(window.webpackJsonp=window.webpackJsonp||[]).push([[463],{485:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e94\u5341\u4e09\u8282 \u53d8\u8eab",content:"<article><h2>\u7b2c\u4e94\u5341\u4e09\u8282 \u53d8\u8eab</h2><p>\u743c\u6069\u51fa\u8eab\u5e73\u6c11\uff0c\u5bb6\u91cc\u662f\u5f00\u6cd5\u672f\u6750\u6599\u5546\u5e97\u7684\uff0c\u5c0f\u65f6\u5019\u4ed6\u6bcf\u5929\u7684\u4e00\u4e2a\u91cd\u8981\u5de5\u4f5c\uff0c\u5c31\u662f\u548c\u59d0\u59d0\u73ca\u5609\u4e00\u8d77\u53bb\u9634\u9b42\u57ce\u5916\u91c7\u96c6\u5404\u79cd\u6750\u6599\u3002\u5e7d\u5f71\u754c\u90a3\u79cd\u5730\u65b9\u9ed1\u6697\u9634\u68ee\uff0c\u6700\u9002\u5408\u86c7\u866b\u751f\u957f\uff0c\u57ce\u5185\u8fd8\u7f62\u4e86\uff0c\u57ce\u5916\u8349\u4e1b\u91cc\u6f5c\u4f0f\u7740\u4e00\u5806\u5f71\u73af\u86c7\uff0c\u56e0\u4e3a\u86c7\u4fe1\u548c\u86c7\u76ae\u90fd\u662f\u633a\u503c\u94b1\u7684\u9b54\u6cd5\u6750\u6599\uff0c\u9002\u5408\u7528\u4e8e\u65bd\u5c55\u5e7b\u672f\uff0c\u6240\u4ee5\u662f\u743c\u6069\u59d0\u5f1f\u4fe9\u91cd\u70b9\u6355\u6349\u7684\u5bf9\u8c61\u3002\u8bf4\u201c\u5929\u5929\u6349\u86c7\u201d\u81ea\u7136\u662f\u5938\u5f20\uff0c\u4f46\u786e\u5b9e\u9694\u6bb5\u65f6\u95f4\u5c31\u80fd\u902e\u5230\u4e00\u6761\uff0c\u76f4\u5230\u743c\u6069\u8fdb\u5165\u5deb\u5e08\u5b66\u6821\u4e3a\u6b62\uff0c\u4ed6\u7684\u6355\u86c7\u6218\u7ee9\u6ca1\u6709\u4e00\u767e\u4e5f\u6709\u516b\u5341\u4e86\uff0c\u53ef\u8c13\u662f\u7ecf\u9a8c\u4e30\u5bcc\u3002</p><p>\u5b89\u535a\u91cc\u7267\u5e08\u5982\u679c\u53d8\u6210\u522b\u7684\u6d77\u602a\uff0c\u743c\u6069\u8fd8\u771f\u6ca1\u529e\u6cd5\uff0c\u4f46\u53d8\u6210\u86c7\u90a3\u5c31\u662f\u81ea\u5bfb\u6b7b\u8def\uff0c\u6b63\u4e2d\u743c\u6069\u4e0b\u6000\u3002\u8d81\u7740\u8eb2\u95ea\u95ea\u7535\u7684\u95f4\u9699\uff0c\u4ed6\u6084\u6084\u4ece\u957f\u888d\u5185\u4fa7\u53e3\u888b\u91cc\u53d6\u4e86\u4e00\u70b9\u7edb\u73e0\u8349\u7c89\u672b\u653e\u8fdb\u53e3\u4e2d\u3002\u7edb\u73e0\u8349\u662f\u4e00\u79cd\u53ea\u751f\u957f\u5728\u5e7d\u5f71\u754c\u7684\u690d\u7269\uff0c\u4f1a\u7ed3\u73cd\u73e0\u5927\u5c0f\u7684\u9c9c\u7ea2\u679c\u7c92\uff0c\u9634\u5e72\u540e\u78e8\u6210\u7c89\u672b\uff0c\u53ef\u4ee5\u4f5c\u4e3a\u591a\u79cd\u6cd5\u672f\u7684\u65bd\u6cd5\u6750\u6599\uff0c\u8fd8\u80fd\u7528\u6765\u8c03\u914d\u89e3\u6bd2\u5242\u3002\u5982\u679c\u76f4\u63a5\u6eb6\u89e3\u4e8e\u6c34\uff0c\u5219\u5bf9\u86c7\u7c7b\u6709\u5f88\u5f3a\u7684\u6740\u4f24\u529b\u3002\u743c\u6069\u6ca1\u7a7a\u53d6\u6c34\uff0c\u53ea\u80fd\u76f4\u63a5\u7528\u553e\u6cab\u6eb6\u89e3\u4e86\uff0c\u7b49\u86c7\u6e38\u5230\u8fd1\u524d\u4e00\u53e3\u55b7\u51fa\uff0c\u679c\u7136\u594f\u6548\u3002</p><p>\u81f3\u4e8e\u4ed6\u62ff\u6cd5\u6756\u5f53\u68cd\u5b50\u7838\u7684\u90a3\u4e00\u8bb0\uff0c\u540c\u6837\u4e5f\u662f\u6709\u95e8\u9053\u7684\u3002\u5bf9\u4ed8\u6bd2\u86c7\uff0c\u5e38\u4eba\u90fd\u77e5\u9053\u8981\u6253\u4e03\u5bf8\uff0c\u5176\u5b9e\u9664\u6b64\u4e4b\u5916\uff0c\u86c7\u8fd8\u6709\u53e6\u5916\u7684\u7f69\u95e8\uff0c\u4fd7\u79f0\u201c\u4e09\u5bf8\u201d\uff0c\u5c31\u662f\u4f4d\u4e8e\u86c7\u5934\u540e\u9762\u7684\u810a\u690e\u90e8\u4f4d\uff0c\u4e00\u65e6\u51fb\u4e2d\u86c7\u5c31\u4f1a\u762b\u75ea\u6389\uff0c\u62ac\u4e0d\u8d77\u5934\uff0c\u81ea\u7136\u4e5f\u6ca1\u6cd5\u518d\u4f24\u4eba\uff0c\u743c\u6069\u521a\u624d\u6253\u4e2d\u7684\u5c31\u662f\u8fd9\u4e2a\u4f4d\u7f6e\u3002</p><p>\u602a\u86c7\u867d\u7136\u6328\u4e86\u91cd\u51fb\uff0c\u8f6f\u8f6f\u762b\u5012\uff0c\u4f46\u5e76\u672a\u6c14\u7edd\uff0c\u743c\u6069\u634f\u7740\u86c7\u5934\u5c06\u5b83\u63d0\u8d77\uff0c\u6b63\u8981\u5f80\u5730\u4e0a\u6454\u6253\uff0c\u5c31\u542c\u5f97\u4e00\u58f0\u96f7\u9706\u6012\u559d\u3002\u4ed6\u8fde\u5fd9\u62ac\u5934\uff0c\u53ea\u89c1\u90a3\u540d\u53eb\u5361\u65af\u56fe\u7684\u5149\u5934\u4e2d\u5e74\u4eba\u5927\u8e0f\u6b65\u903c\u8fd1\uff0c\u53cc\u624b\u865a\u63e1\uff0c\u4e00\u56e2\u70c8\u7130\u65cb\u98ce\u5e73\u5730\u5347\u8d77\uff0c\u547c\u5578\u7740\u671d\u7740\u743c\u6069\u63a8\u8fc7\u6765\u3002</p><p>\u743c\u6069\u6b63\u5f85\u95ea\u907f\uff0c\u4e00\u9053\u900f\u660e\u5c04\u7ebf\u4ece\u4ed6\u8eab\u540e\u6253\u6765\uff0c\u51fb\u4e2d\u70c8\u7130\u65cb\u98ce\uff0c\u5c06\u5b83\u5b8c\u5168\u51bb\u7ed3\u4f4f\u4e86\u3002\u56de\u5934\u4e00\u770b\uff0c\u539f\u6765\u662f\u5965\u5609\u83b1\u65af\u4e0d\u77e5\u4f55\u65f6\u53c8\u62ff\u51fa\u4e86\u5979\u90a3\u672c\u5927\u4e66\uff0c\u7ffb\u5f00\u5728\u5176\u4e2d\u4e00\u9875\uff0c\u521a\u624d\u7684\u6781\u5730\u5c04\u7ebf\u5e94\u8be5\u5c31\u662f\u4ece\u4e2d\u53d1\u51fa\u3002\u5361\u65af\u56fe\u8138\u8272\u4e00\u6c89\uff0c\u53cc\u624b\u63a5\u8fde\u53d8\u5e7b\u624b\u52bf\uff0c\u70c8\u7130\u3001\u65cb\u98ce\u3001\u95ea\u7535\u4ece\u4ed6\u6307\u5c16\u4ea4\u9519\u5c04\u51fa\uff0c\u51dd\u805a\u6210\u5404\u79cd\u6740\u4f24\u529b\u5de8\u5927\u7684\u6cd5\u672f\uff0c\u4f46\u5965\u5609\u83b1\u65af\u4e0d\u614c\u4e0d\u5fd9\uff0c\u5feb\u901f\u7ffb\u5f00\u4e66\u7684\u4e00\u9875\u53c8\u4e00\u9875\uff0c\u91ca\u653e\u51fa\u5404\u79cd\u4e0d\u540c\u7684\u9632\u5fa1\u6cd5\u672f\uff0c\u5c3d\u6570\u62b5\u6321\u4f4f\u4e86\u3002</p><p>\u773c\u89c1\u4e00\u65f6\u95f4\u65e0\u6cd5\u653b\u7834\u5965\u5609\u83b1\u65af\u7684\u963b\u62e6\uff0c\u5361\u65af\u56fe\u5634\u89d2\u4e00\u626f\uff0c\u9732\u51fa\u65e0\u6bd4\u72f0\u72de\u7684\u8868\u60c5\uff0c\u50cf\u662f\u505a\u4e86\u67d0\u4e2a\u51b3\u5b9a\u3002\u4ed6\u53f3\u624b\u731b\u7136\u62ac\u8d77\uff0c\u9065\u9065\u671d\u7740\u5b89\u535a\u91cc\u7267\u5e08\u53d8\u6210\u7684\u602a\u86c7\u865a\u7a7a\u4e00\u6293\u3002\u743c\u6069\u53ea\u89c9\u624b\u4e2d\u5267\u9707\uff0c\u90a3\u6761\u539f\u672c\u8f6f\u8db4\u8db4\u7684\u86c7\u50cf\u662f\u7a81\u7136\u88ab\u7535\u51fb\u822c\uff0c\u731b\u70c8\u5730\u98a4\u52a8\u8d77\u6765\u3002\u4ed6\u5fc3\u4e2d\u5927\u60ca\uff0c\u6b63\u8981\u653e\u624b\u6254\u6389\u5b83\uff0c\u5c31\u89c1\u86c7\u80cc\u7684\u76ae\u8089\u5982\u6ce2\u6d6a\u822c\u5411\u4e24\u65c1\u5256\u5206\uff0c\u50cf\u662f\u88ab\u5229\u5203\u4ece\u5185\u90e8\u5212\u5f00\u4e00\u822c\uff0c\u5374\u6ca1\u6709\u534a\u6ef4\u8840\u6d8c\u51fa\uff0c\u800c\u662f\u65e0\u6570\u9053\u7ec6\u5c0f\u95ea\u7535\u4ece\u4e2d\u98de\u5c04\u800c\u8d77\uff0c\u4ea4\u9519\u7f20\u7ed5\u6210\u4e00\u652f\u5149\u8292\u95ea\u70c1\u7684\u77ed\u77db\uff0c\u7167\u7740\u743c\u6069\u5f53\u80f8\u75be\u523a\u3002</p><p>\u5657\uff01</p><p>\u8fd9\u4e00\u51fb\u6765\u5f97\u592a\u8fc7\u7a81\u7136\uff0c\u743c\u6069\u6839\u672c\u8fd8\u6ca1\u53cd\u5e94\u8fc7\u6765\uff0c\u95ea\u7535\u77db\u5df2\u7ecf\u6df1\u6df1\u523a\u8fdb\u4e86\u8eab\u4f53\uff0c\u603b\u7b97\u4ed6\u4e0b\u610f\u8bc6\u5730\u504f\u4e86\u504f\uff0c\u907f\u5f00\u5fc3\u810f\u8981\u5bb3\u90e8\u4f4d\u3002\u7ed5\u662f\u5982\u6b64\uff0c\u4ed6\u4e5f\u662f\u80f8\u53e3\u5267\u75db\uff0c\u773c\u524d\u9635\u9635\u53d1\u9ed1\uff0c\u610f\u8bc6\u4e2d\u5929\u65cb\u5730\u8f6c\uff0c\u53ea\u662f\u7528\u5c3d\u5168\u90e8\u529b\u6c14\u624d\u52c9\u5f3a\u652f\u6491\u7740\u8eab\u4f53\uff0c\u6ca1\u6709\u7acb\u523b\u5012\u4e0b\u3002</p><p>\u8299\u857e\u72c4\u82b1\u5bb9\u60e8\u767d\uff0c\u4e0d\u987e\u4e00\u5207\u5730\u4e0a\u524d\u5c06\u4ed6\u62b1\u4f4f\uff0c\u773c\u6cea\u6b62\u4e0d\u4f4f\u5730\u6d8c\u51fa\uff0c\u60f3\u8981\u8bf4\u8bdd\uff0c\u54bd\u5589\u5374\u50cf\u662f\u88ab\u627c\u4f4f\uff0c\u53d1\u4e0d\u51fa\u534a\u70b9\u58f0\u97f3\u3002\u5c31\u8fde\u4e00\u76f4\u9547\u5b9a\u7684\u8299\u8389\u5a05\u4e5f\u53d8\u4e86\u8138\u8272\uff0c\u6025\u5fd9\u53d6\u51fa\u5723\u5fbd\uff0c\u671d\u743c\u6069\u8eab\u4e0a\u91ca\u653e\u4e86\u4e00\u4e2a\u5f3a\u5fc3\u672f\u3002\u8fd9\u662f\u591c\u5973\u58eb\u8d50\u4e88\u4fe1\u5f92\u7684\u72ec\u6709\u795e\u672f\uff0c\u6548\u679c\u7c7b\u4f3c\u4e8e\u7ed9\u4eba\u6253\u5174\u594b\u5242\uff0c\u526f\u4f5c\u7528\u5f88\u5f3a\uff0c\u4f46\u5728\u77ed\u65f6\u95f4\u5185\u786e\u5b9e\u6709\u6781\u5f3a\u7684\u6cbb\u7597\u6548\u679c\u3002\u743c\u6069\u539f\u672c\u5df2\u7ecf\u63a5\u8fd1\u610f\u8bc6\u6da3\u6563\uff0c\u88ab\u8fd9\u9053\u6cd5\u672f\u4e00\u62cd\uff0c\u987f\u65f6\u4e00\u80a1\u6696\u6d41\u81ea\u5fc3\u5e95\u6d8c\u8d77\uff0c\u7cbe\u795e\u4e3a\u4e4b\u4e00\u632f\u3002</p><p>\u201c\u653e\u624b\uff01\u201d\u4ed6\u7528\u529b\u6323\u8131\u8299\u857e\u72c4\u7684\u6000\u62b1\uff0c\u5c06\u5979\u731b\u5730\u63a8\u5f00\u3002</p><p>\u5c0f\u5973\u5b69\u6014\u4f4f\uff0c\u4e0d\u77e5\u9053\u4ed6\u4e3a\u4f55\u5bf9\u81ea\u5df1\u5982\u6b64\u7c97\u66b4\uff0c\u4e0d\u77e5\u9053\u662f\u81ea\u5df1\u505a\u9519\u4e86\u4ec0\u4e48\u60f9\u4ed6\u751f\u6c14\u2014\u2014\u4f46\u5728\u4e0b\u4e00\u79d2\u949f\uff0c\u5979\u4fbf\u77e5\u9053\u4e86\u7b54\u6848\u3002\u63d2\u5728\u743c\u6069\u80f8\u8154\u5185\u7684\u77ed\u77db\u5267\u70c8\u9707\u98a4\u7740\uff0c\u5f3a\u5927\u7684\u7535\u6d41\u81ea\u5b83\u7684\u8868\u9762\u4f20\u51fa\u6765\uff0c\u77ac\u95f4\u6269\u81f3\u5deb\u5e08\u5168\u8eab\uff0c\u773c\u770b\u5c31\u8981\u5c06\u4ed6\u4e9f\u6210\u7126\u70ad\uff0c\u4e00\u5c42\u9634\u5f71\u5149\u819c\u9661\u7136\u51fa\u73b0\uff0c\u8986\u76d6\u5728\u743c\u6069\u7684\u76ae\u80a4\u8868\u9762\uff0c\u963b\u9694\u4e86\u6240\u6709\u653b\u51fb\u3002</p><p>\u5343\u94a7\u4e00\u53d1\u4e4b\u9645\uff0c\u743c\u6069\u6fc0\u6d3b\u4e86\u838e\u5c14\u5723\u5fbd\uff0c\u4ed6\u54ac\u7d27\u7259\u5173\uff0c\u731b\u7136\u4f38\u51fa\u53cc\u624b\uff0c\u7d27\u7d27\u63e1\u4f4f\u4e86\u63d2\u5728\u81ea\u5df1\u80f8\u8154\u5185\u7684\u77ed\u77db\uff0c\u900f\u660e\u7684\u9634\u5f71\u706b\u7130\u81ea\u638c\u5fc3\u6d8c\u51fa\uff0c\u7ffb\u817e\u71c3\u70e7\uff0c\u50cf\u662f\u8981\u5c06\u5b83\u953b\u70bc\u7194\u5316\u3002\u5149\u5934\u4e2d\u5e74\u4eba\u8d77\u521d\u8fd8\u4e0d\u751a\u5728\u610f\uff0c\u5b9a\u775b\u518d\u770b\u4fbf\u5927\u5403\u4e00\u60ca\uff0c\u8138\u4e0a\u987f\u65f6\u53d8\u4e86\u989c\u8272\u3002</p><p>\u201c\u5f71\u706b\uff01\u201d</p><p>\u4ed6\u4e00\u628a\u626f\u4e0b\u8499\u5728\u5de6\u773c\u4e0a\u7684\u9ed1\u8272\u773c\u7f69\uff0c\u9732\u51fa\u7a7a\u6d1e\u7684\u773c\u7736\uff0c\u70c8\u7130\u548c\u95ea\u7535\u5728\u5176\u4e2d\u56de\u65cb\uff0c\u53d1\u51fa\u9690\u9690\u8f70\u9e23\u3002\u4e00\u80a1\u5de8\u5927\u7684\u5438\u529b\u4ece\u773c\u7736\u4e2d\u53d1\u51fa\uff0c\u671d\u7740\u743c\u6069\u51f6\u731b\u88ad\u6765\uff0c\u6cbf\u9014\u7a7a\u6c14\u88ab\u9ad8\u901f\u62bd\u79bb\uff0c\u5f62\u6210\u87ba\u65cb\u98d3\u98ce\uff0c\u51e0\u53ea\u6b63\u5728\u56f4\u653b\u9b54\u50cf\u7684\u6d77\u602a\u9760\u5f97\u592a\u8fd1\uff0c\u7728\u773c\u95f4\u4fbf\u88ab\u5377\u4e86\u8fdb\u53bb\uff0c\u6d88\u5931\u5f97\u65e0\u5f71\u65e0\u8e2a\u3002</p><p>\u5965\u5609\u83b1\u65af\u8fde\u7eed\u7ffb\u52a8\u4e66\u9875\uff0c\u4e03\u9053\u77f3\u5899\u6084\u65e0\u58f0\u606f\u5730\u4ece\u5730\u9762\u4f9d\u6b21\u5347\u8d77\uff0c\u6321\u5728\u98d3\u98ce\u7684\u524d\u8fdb\u8def\u7ebf\u4e0a\u3002\u4e09\u79d2\u949f\u540e\uff0c\u5b83\u4eec\u88ab\u5c3d\u6570\u51fb\u6e83\uff0c\u4f46\u8fd9\u5df2\u7ecf\u4e3a\u743c\u6069\u4e89\u53d6\u5230\u4e86\u8db3\u591f\u7684\u65f6\u95f4\u3002\u77ed\u77db\u88ab\u5f71\u706b\u541e\u566c\uff0c\u7194\u70bc\u6b86\u5c3d\uff0c\u53ea\u7559\u4e0b\u80f8\u524d\u6df1\u6df1\u7684\u4f24\u53e3\uff0c\u5deb\u5e08\u731b\u7136\u62ac\u8d77\u5934\uff0c\u53cc\u773c\u4e0d\u77e5\u4f55\u65f6\u5df2\u7ecf\u53d8\u6210\u707f\u70c2\u94f6\u8272\uff0c\u51b0\u51b7\u800c\u4e0d\u5e26\u4e00\u4e1d\u611f\u60c5\u5730\u51dd\u89c6\u7740\u547c\u5578\u800c\u6765\u7684\u87ba\u65cb\u98d3\u98ce\uff0c\u4e0d\u95ea\u4e0d\u907f\uff0c\u5728\u4ed6\u80cc\u540e\u7684\u865a\u7a7a\u4e2d\uff0c\u65e0\u6570\u95ea\u7535\u5f7c\u6b64\u7ea0\u7f20\uff0c\u51dd\u805a\u6210\u51e0\u767e\u4e0a\u5343\u5177\u534e\u4e3d\u7684\u6b66\u5668\uff0c\u547c\u5578\u7740\uff0c\u94fa\u5929\u76d6\u5730\u5730\u4fef\u51b2\u8f70\u70b8\u4e0b\u6765\u3002</p><p>\u8f70\u9686\u9686\u9686\u9686\uff01</p><p>\u87ba\u65cb\u98d3\u98ce\u51fb\u4e2d\u4e86\u743c\u6069\u7684\u80f8\u53e3\uff0c\u7a7f\u900f\u8299\u8389\u5a05\u521a\u521a\u4e3a\u4ed6\u521b\u9020\u51fa\u7684\u9b54\u6cd5\u62a4\u76fe\uff0c\u5c06\u4ed6\u6574\u4e2a\u4eba\u6253\u98de\u8d77\u6765\uff0c\u6454\u843d\u5728\u4e8c\u5341\u5c3a\u5916\u3002\u8165\u751c\u7684\u8840\u4ece\u5634\u91cc\u6d8c\u51fa\uff0c\u4ed6\u80fd\u591f\u6e05\u695a\u611f\u89c9\u5230\u81ea\u5df1\u80ba\u8151\u53d7\u4e86\u91cd\u521b\uff0c\u80f8\u53e3\u808b\u9aa8\u5e94\u8be5\u4e5f\u65ad\u4e86\u51e0\u6839\uff0c\u5267\u75db\u4e00\u9635\u9635\u94bb\u5fc3\u525c\u9aa8\uff0c\u547c\u5438\u90fd\u53d8\u5f97\u65e0\u6bd4\u8270\u96be\u3002\u5e78\u597d\u6b64\u524d\u5965\u5609\u83b1\u65af\u5851\u6210\u4e03\u9053\u77f3\u5899\uff0c\u62b5\u6d88\u4e86\u98d3\u98ce\u5927\u90e8\u5206\u7684\u5a01\u529b\uff0c\u8299\u8389\u5a05\u7684\u795e\u672f\u53c8\u963b\u4e86\u4e00\u963b\uff0c\u518d\u52a0\u4e0a\u743c\u6069\u5728\u957f\u888d\u5185\u7a7f\u4e86\u4f0a\u5361\u6c99\u57ce\u77ee\u4eba\u5de5\u5320\u5927\u5e08\u6253\u9020\u7684\u79d8\u94f6\u9501\u7532\uff0c\u867d\u7136\u4e0d\u80fd\u51cf\u8f7b\u98d3\u98ce\u4e2d\u8574\u542b\u7684\u9b54\u6cd5\u4f24\u5bb3\uff0c\u4f46\u5bf9\u7269\u7406\u51b2\u51fb\u8fd8\u662f\u6709\u5f88\u5f3a\u7684\u62b5\u5fa1\u6548\u679c\u3002\u4e09\u91cd\u4fdd\u62a4\uff0c\u603b\u7b97\u662f\u52c9\u5f3a\u625b\u4e0b\u4e86\u8fd9\u4e00\u51fb\uff0c\u6ca1\u6709\u5f53\u573a\u6302\u6389\u3002</p><p>\u5728\u743c\u6069\u88ab\u51fb\u98de\u7684\u540c\u65f6\uff0c\u95ea\u7535\u51dd\u6210\u7684\u6b66\u5668\u4e4b\u96e8\u4e5f\u5b8c\u5168\u503e\u6cfb\u5230\u4e86\u5361\u65af\u56fe\u7684\u5934\u4e0a\u3002\u5de8\u5927\u7684\u7206\u70b8\u58f0\u63a5\u8fde\u4e0d\u65ad\u5730\u5f7c\u6b64\u54cd\u8d77\uff0c\u545b\u4eba\u7684\u70df\u5c18\u7ffb\u817e\u5f25\u6f2b\uff0c\u5c06\u4ed6\u7684\u9b41\u68a7\u8eab\u8eaf\u5b8c\u5168\u7b3c\u7f69\u3002\u8db3\u8db3\u8fc7\u4e86\u8fd1\u534a\u5206\u949f\uff0c\u70df\u5c18\u65b9\u624d\u6e10\u6e10\u5e73\u606f\uff0c\u5361\u65af\u56fe\u7684\u7ad9\u7acb\u4e4b\u5904\u539f\u672c\u662f\u5e73\u6574\u5730\u9762\uff0c\u73b0\u5728\u5374\u51fa\u73b0\u4e00\u4e2a\u6df1\u5751\uff0c\u5751\u5e95\u4e00\u7247\u72fc\u7c4d\uff0c\u4eff\u4f5b\u88ab\u51e0\u5341\u67b6\u8f70\u70b8\u673a\u521a\u521a\u8f6e\u756a\u8e42\u8e8f\u8fc7\u4e00\u822c\uff0c\u4ed6\u672c\u4eba\u7ad9\u5728\u5751\u5e95\uff0c\u5f62\u5bb9\u72fc\u72c8\uff0c\u7070\u5934\u571f\u8138\uff0c\u8179\u90e8\u53f3\u4fa7\u548c\u5927\u817f\u4e0a\u8840\u8089\u4e00\u7247\u6a21\u7cca\uff0c\u663e\u7136\u53d7\u4e86\u91cd\u521b\uff0c\u4f46\u90fd\u4e0d\u662f\u81f4\u547d\u4f24\u3002</p><p>\u201c\u6df7\u8d26\uff01\u201d</p><p>\u4ed6\u5486\u54ee\u7740\uff0c\u9732\u51fa\u767d\u68ee\u68ee\u7684\u7259\u9f7f\uff0c\u8138\u4e0a\u808c\u8089\u626d\u66f2\uff0c\u8868\u60c5\u72f0\u72de\u65e0\u6bd4\u3002\u7d2b\u8272\u706b\u7130\u5728\u5de6\u773c\u7736\u4e2d\u70c8\u70c8\u71c3\u70e7\uff0c\u65cb\u98ce\u73af\u7ed5\u8eab\u5468\uff0c\u5c06\u4ed6\u7f13\u7f13\u6258\u8d77\uff0c\u901f\u5ea6\u5f88\u6162\uff0c\u5374\u4e1d\u6beb\u6ca1\u6709\u67d4\u7f13\u7684\u611f\u89c9\uff0c\u66f4\u50cf\u662f\u731b\u517d\u5728\u84c4\u52bf\u5f85\u53d1\u3002\u201c\u4f60\u4ece\u54ea\u91cc\u5f97\u6765\u7684\u96f7\u9706\u795e\u529b\uff1f\u201d\u4ed6\u51dd\u89c6\u7740\u743c\u6069\uff0c\u5389\u58f0\u559d\u95ee\u3002</p><p>\u8fd9\u4e2a\u95ee\u9898\u663e\u7136\u4e0d\u4f1a\u5f97\u5230\u56de\u7b54\uff0c\u56e0\u4e3a\u743c\u6069\u5df2\u7ecf\u8fde\u8bf4\u8bdd\u7684\u529b\u6c14\u90fd\u6ca1\u6709\uff0c\u60f3\u5750\u8d77\u6765\u90fd\u529e\u4e0d\u5230\u3002\u8299\u857e\u72c4\u8dd1\u5230\u4ed6\u8eab\u65c1\uff0c\u53c8\u4e0d\u6562\u79fb\u52a8\u4ed6\uff0c\u624b\u8db3\u65e0\u63aa\uff0c\u8299\u8389\u5a05\u76f8\u5bf9\u9547\u5b9a\u8bb8\u591a\uff0c\u4e00\u4e2a\u63a5\u4e00\u4e2a\u795e\u672f\u4ece\u5723\u5fbd\u4e2d\u53d1\u51fa\uff0c\u8f6c\u6362\u6210\u6cbb\u7597\u672f\uff0c\u6ce8\u5165\u743c\u6069\u4f53\u5185\u3002\u5979\u7684\u9020\u8be3\u8fd8\u4e0d\u591f\u6cbb\u7597\u5982\u6b64\u4e25\u91cd\u7684\u4f24\u52bf\uff0c\u4f46\u81f3\u5c11\u53ef\u4ee5\u7f13\u89e3\u75bc\u75db\uff0c\u5c3d\u53ef\u80fd\u4fee\u590d\u8eab\u4f53\u53d7\u635f\u7684\u91cd\u8981\u90e8\u4f4d\uff0c\u8ba9\u60c5\u5f62\u4e0d\u81f3\u4e8e\u8fdb\u4e00\u6b65\u6076\u5316\u3002</p><p>\u5361\u65af\u56fe\u81ea\u5751\u5e95\u6d6e\u4e0a\u5730\u9762\uff0c\u7f13\u7f13\u5411\u524d\u8e0f\u51fa\uff0c\u56e0\u4e3a\u5927\u817f\u53d7\u4f24\u7684\u7f18\u6545\uff0c\u4ed6\u8d70\u8d77\u8def\u6765\u4e00\u7638\u4e00\u62d0\uff0c\u901f\u5ea6\u5f88\u6162\uff0c\u59ff\u52bf\u4e5f\u9887\u4e3a\u6ed1\u7a3d\uff0c\u5374\u7ed9\u4eba\u4ee5\u6c89\u91cd\u7684\u538b\u8feb\u611f\uff0c\u4eff\u4f5b\u4e00\u5ea7\u5927\u5c71\u6b63\u8fce\u9762\u63a8\u6765\u3002\u5965\u5609\u83b1\u65af\u5feb\u901f\u7ffb\u52a8\u4e66\u9875\uff0c\u63a5\u8fde\u91ca\u653e\u51fa\u51e0\u4e2a\u6cd5\u672f\uff0c\u4f46\u4e3e\u624b\u6295\u8db3\u4e4b\u95f4\uff0c\u4fbf\u88ab\u4ed6\u8f7b\u63cf\u6de1\u5199\u5730\u51fb\u7834\u4e86\u3002\u98d3\u98ce\u3001\u95ea\u7535\u548c\u70c8\u7130\u5728\u8eab\u5468\u53d8\u5e7b\u51fa\u73b0\uff0c\u9ad8\u901f\u65cb\u8f6c\uff0c\u5361\u65af\u56fe\u968f\u624b\u4e00\u6293\uff0c\u638c\u5fc3\u51fa\u73b0\u4e86\u4e00\u67c4\u70c8\u7130\u77db\uff0c\u7167\u7740\u743c\u6069\u4fbf\u98de\u63b7\u8fc7\u6765\uff0c\u8981\u5c06\u4ed6\u9489\u6b7b\u5728\u5730\u4e0a\u3002</p><p>\u201c\u9632\u5fa1\u80fd\u91cf\u4f24\u5bb3\u2014\u2014\u706b\u7130\uff01\u201d</p><p>\u5371\u6025\u5173\u5934\uff0c\u8299\u857e\u72c4\u62ff\u51fa\u4e86\u60ca\u4eba\u7684\u52c7\u6c14\uff0c\u5979\u6321\u5728\u743c\u6069\u8eab\u524d\uff0c\u4ee5\u5e73\u751f\u6700\u5feb\u7684\u901f\u5ea6\u5b8c\u6210\u9632\u5fa1\u5492\u8bed\u3002\u8d64\u7ea2\u8272\u7684\u5149\u76fe\u5728\u5979\u9762\u524d\u51fa\u73b0\uff0c\u88ab\u51fb\u788e\uff0c\u4f46\u8fd8\u662f\u6210\u529f\u5c06\u75be\u901f\u5c04\u6765\u7684\u70c8\u7130\u77db\u6321\u4f4f\u4e86\u3002\u5361\u65af\u56fe\u7709\u6bdb\u5fae\u6311\uff0c\u731b\u7136\u4fef\u8eab\u4e00\u62f3\u7838\u5730\uff0c\u65e0\u5f62\u7684\u9707\u6ce2\u6e17\u5165\u5730\u4e0b\uff0c\u5374\u4e0d\u5411\u56db\u9762\u6269\u6563\uff0c\u800c\u662f\u51dd\u6210\u7b14\u76f4\u4e00\u9053\u5411\u524d\u51b2\u6765\u3002\u8299\u857e\u72c4\u54ac\u7740\u5634\u5507\uff0c\u731b\u7136\u5012\u8f6c\u624b\u4e2d\u7684\u8c61\u7259\u6cd5\u6756\uff0c\u5c06\u9876\u7aef\u89e6\u5730\uff0c\u5176\u4e0a\u9576\u5d4c\u7684\u4e09\u9897\u541b\u738b\u4e4b\u6cea\u540c\u65f6\u5149\u8292\u56db\u5c04\u3002\u9707\u6ce2\u51f6\u731b\u6251\u6765\uff0c\u649e\u4e0a\u6cd5\u6756\uff0c\u767b\u65f6\u88ab\u5b9d\u77f3\u5c3d\u6570\u5438\u6536\u4e86\u8fdb\u53bb\uff0c\u53ea\u6709\u8f7b\u5fae\u7684\u7834\u788e\u58f0\u54cd\u8d77\u3002</p><p>\u201c\u54a6\uff1f\u201d</p><p>\u5361\u65af\u56fe\u60ca\u8bb6\u5931\u58f0\uff0c\u4ed6\u521a\u624d\u53d1\u51fa\u7684\u662f\u5730\u9707\u672f\uff0c\u5728\u795e\u672f\u4e2d\u7b97\u662f\u63a5\u8fd1\u9876\u7ea7\u7684\u9b54\u6cd5\u4e86\u3002\u867d\u7136\u4e3a\u4e86\u7cbe\u786e\u63a7\u5236\u9707\u6ce2\u7684\u65b9\u5411\uff0c\u5a01\u529b\u6709\u5f88\u5927\u524a\u5f31\uff0c\u4f46\u4e5f\u4e0d\u662f\u5bfb\u5e38\u4eba\u80fd\u591f\u62b5\u6321\u7684\uff0c\u5982\u679c\u62ff\u5deb\u5e08\u7684\u5965\u672f\u6765\u7c7b\u6bd4\u7684\u8bdd\uff0c\u81f3\u5c11\u4e5f\u662f\u89e3\u79bb\u672f\u8fd9\u79cd\u5c42\u7ea7\uff0c\u6ca1\u60f3\u5230\u8299\u857e\u72c4\u5c45\u7136\u8f7b\u8f7b\u5de7\u5de7\u5c31\u5316\u89e3\u4e86\uff0c\u8fd9\u53ef\u662f\u5927\u5927\u51fa\u4e4e\u4ed6\u7684\u610f\u6599\u4e4b\u5916\u3002\u96be\u9053\u8bf4\uff0c\u8fd9\u4e2a\u770b\u8d77\u6765\u5a07\u602f\u602f\u7684\u5c0f\u5973\u5b69\uff0c\u5c45\u7136\u662f\u6df1\u85cf\u4e0d\u9732\u7684\u5927\u5deb\u5e08\uff1f</p><p>\u4e8b\u5b9e\u81ea\u7136\u5e76\u975e\u5982\u6b64\uff0c\u8299\u857e\u72c4\u5728\u9b54\u6cd5\u4e0a\u7684\u9020\u8be3\u5f88\u4f4e\uff0c\u5927\u81f4\u53ea\u76f8\u5f53\u4e8e\u9634\u9b42\u57ce\u5deb\u5e08\u5b66\u6821\u6bd5\u4e1a\u65b0\u751f\u7684\u6c34\u51c6\u2014\u2014\u800c\u4e14\u8fd8\u4e0d\u662f\u4f18\u79c0\u7684\u90a3\u79cd\u3002\u4f46\u5979\u7cbe\u7814\u7684\u662f\u201c\u6df1\u571f\u9884\u8a00\u201d\uff0c\u53c8\u5728\u5e7d\u6697\u5730\u57df\u5446\u8fc7\u4e00\u5e74\u65f6\u95f4\uff0c\u522b\u7684\u65b9\u9762\u4e0d\u884c\uff0c\u5bf9\u5927\u5730\u7684\u5730\u8109\u80fd\u91cf\u5374\u81ea\u6709\u4e00\u79cd\u683c\u5916\u7684\u9ed8\u5951\u548c\u4eb2\u548c\uff0c\u6240\u4ee5\u624d\u80fd\u591f\u52c9\u5f3a\u6321\u4e0b\u5730\u9707\u6ce2\uff0c\u8fd9\u5176\u4e2d\u8fd8\u6709\u5f88\u5927\u4e00\u90e8\u5206\u662f\u90a3\u4e09\u9897\u541b\u738b\u4e4b\u6cea\u5b9d\u77f3\u7684\u529f\u52b3\u3002</p><p>\u5361\u65af\u56fe\u4e0d\u89e3\u5176\u6545\uff0c\u4f46\u4e5f\u4e0d\u53bb\u591a\u60f3\uff0c\u624b\u6307\u4e00\u5f39\uff0c\u53c8\u4e00\u9053\u70c8\u7130\u65cb\u98ce\u81ea\u5730\u5e95\u6276\u6447\u5347\u8d77\uff0c\u547c\u5578\u7740\u671d\u8299\u857e\u72c4\u5377\u6765\u3002\u5c0f\u5973\u5b69\u9762\u9732\u6050\u60e7\u4e4b\u8272\uff0c\u5979\u660e\u663e\u65e0\u6cd5\u62b5\u5fa1\u8fd9\u6837\u5f3a\u5927\u7684\u6cd5\u672f\uff0c\u4f46\u5374\u54ac\u7d27\u7259\u5173\uff0c\u53cc\u76ee\u4e00\u95ed\uff0c\u4e0d\u8eb2\u4e0d\u95ea\uff0c\u5c45\u7136\u662f\u8981\u7528\u8eab\u4f53\u6765\u505a\u743c\u6069\u7684\u5c4f\u969c\u3002</p><p>\u201c\u8299\u857e\u72c4\uff01\u201d</p><p>\u773c\u89c1\u59b9\u59b9\u5371\u5728\u65e6\u5915\uff0c\u8299\u8389\u5a05\u5927\u60ca\u5931\u8272\uff0c\u4f46\u5979\u7684\u795e\u672f\u5df2\u7ecf\u5c3d\u6570\u8f6c\u5316\u4e3a\u6cbb\u7597\u672f\u7528\u5728\u743c\u6069\u8eab\u4e0a\uff0c\u6b64\u65f6\u5c31\u8fde\u4e00\u4e2a\u6700\u4f4e\u9636\u7684\u9632\u5fa1\u9b54\u6cd5\u90fd\u65e0\u6cd5\u91ca\u653e\u3002\u5979\u6b63\u8981\u4e0d\u987e\u4e00\u5207\u5730\u51b2\u4e0a\u524d\uff0c\u9661\u7136\u611f\u5e94\u5230\u8eab\u540e\u7684\u7a7a\u6c14\u9aa4\u7136\u626d\u66f2\uff0c\u6709\u5f3a\u529b\u7684\u9b54\u6cd5\u6ce2\u52a8\u91ca\u653e\uff0c\u7d27\u63a5\u7740\uff0c\u4e00\u4e2a\u4eba\u5f71\u51ed\u7a7a\u51fa\u73b0\u5728\u8299\u857e\u72c4\u7684\u9762\u524d\u3002</p><p>\u90a3\u662f\u743c\u6069\u3002</p><p>\u4ed6\u8270\u96be\u5730\u4e3e\u8d77\u53f3\u624b\uff0c\u65e0\u540d\u6307\u4e0a\u7684\u767d\u91d1\u6212\u6307\u5fae\u5fae\u6cdb\u5149\uff0c\u50cf\u662f\u5f88\u5bfb\u5e38\u7684\u5149\u7ebf\u6298\u5c04\u3002\u70c8\u7130\u65cb\u98ce\u547c\u5578\u800c\u6765\uff0c\u773c\u770b\u5c31\u8981\u5c06\u4ed6\u548c\u8299\u857e\u72c4\u541e\u566c\uff0c\u4f46\u5728\u4e0b\u4e00\u77ac\u95f4\u5b83\u9aa4\u7136\u6539\u53d8\u65b9\u5411\uff0c\u4ee5\u66f4\u5feb\u7684\u901f\u5ea6\u5012\u8f6c\uff0c\u53cd\u5f39\u56de\u53bb\u3002</p><p>\u201c\u54fc\uff01\u201d</p><p>\u70c8\u7130\u65cb\u98ce\u53cd\u5f39\u7684\u901f\u5ea6\u867d\u5feb\uff0c\u4f46\u8fd8\u4e0d\u8db3\u4ee5\u8ba9\u5361\u65af\u56fe\u6ca1\u6709\u534a\u70b9\u53cd\u5e94\u65f6\u95f4\uff0c\u4ed6\u7684\u7a7a\u6d1e\u773c\u7736\u4e2d\u5149\u8292\u4e00\u95ea\uff0c\u67d0\u79cd\u65e0\u5f62\u7684\u529b\u91cf\u4ece\u4e2d\u6563\u53d1\u51fa\u6765\uff0c\u786c\u751f\u751f\u5c06\u70c8\u7130\u65cb\u98ce\u201c\u5b9a\u201d\u5728\u4e86\u534a\u8def\uff0c\u8fde\u8f6c\u52a8\u90fd\u505c\u6b62\u4e86\uff0c\u63a5\u7740\u4fbf\u6162\u6162\u7184\u706d\u4e86\u3002\u5361\u65af\u56fe\u518d\u5ea6\u62ac\u8d77\u624b\u81c2\uff0c\u4e94\u6307\u865a\u5f20\uff0c\u50cf\u662f\u8981\u4f7f\u7528\u67d0\u79cd\u65b0\u7684\u6cd5\u672f\u3002</p><p>\u5728\u5bf9\u9762\uff0c\u743c\u6069\u5954\u8dd1\u8d77\u6765\uff0c\u671d\u7740\u5361\u65af\u56fe\u53d1\u52a8\u51b2\u950b\u3002</p><p>\u4ed6\u7684\u4f24\u52bf\u663e\u7136\u6ca1\u6709\u590d\u539f\uff0c\u867d\u7136\u7ecf\u8fc7\u8299\u8389\u5a05\u7684\u5168\u529b\u6cbb\u7597\uff0c\u76ae\u8089\u5df2\u7ecf\u5b8c\u5168\u6108\u5408\uff0c\u81f3\u5c11\u4ece\u8868\u9762\u4e0a\u770b\u662f\u5982\u6b64\uff0c\u4f46\u80ba\u8151\u3001\u9aa8\u9abc\u7684\u53d7\u521b\u4e0d\u662f\u90a3\u4e48\u5bb9\u6613\u75ca\u6108\u7684\uff0c\u66f4\u522b\u63d0\u56e0\u5927\u91cf\u5931\u8840\u800c\u5bfc\u81f4\u7684\u865a\u5f31\u3002\u7136\u800c\u4ed6\u7684\u52a8\u4f5c\u5374\u534a\u70b9\u4e0d\u6162\u2014\u2014\u4e0d\u4f46\u4e0d\u6162\uff0c\u7b80\u76f4\u53ef\u4ee5\u8bf4\u662f\u5feb\u5f97\u60ca\u4eba\uff0c\u51e0\u4e4e\u90fd\u53ef\u4ee5\u5ab2\u7f8e\u732b\u5973\u4e86\u3002\u5361\u65af\u56fe\u7684\u6cd5\u672f\u8fd8\u672a\u5b8c\u6210\uff0c\u743c\u6069\u5df2\u7ecf\u51b2\u5230\u8fd1\u524d\uff0c\u4e8c\u8bdd\u4e0d\u8bf4\u4e00\u62f3\u4fbf\u671d\u4ed6\u8138\u4e0a\u7838\u6765\u3002</p><p>\u201c\u627e\u6b7b\uff01\u201d</p><p>\u5361\u65af\u56fe\u6012\u559d\u4e00\u58f0\uff0c\u653e\u5f03\u5df2\u7ecf\u63a5\u8fd1\u5b8c\u6210\u7684\u6cd5\u672f\uff0c\u62ac\u624b\u67b6\u4f4f\u743c\u6069\u7684\u62f3\u5934\u3002\u4ed6\u5f53\u5e74\u662f\u5760\u661f\u6d77\u4e0a\u5a01\u540d\u5353\u8457\u7684\u5927\u6d77\u76d7\uff0c\u8981\u8bba\u8fd1\u8eab\u683c\u6597\u53ef\u662f\u6781\u6709\u81ea\u4fe1\uff0c\u66fe\u7ecf\u6709\u8fc7\u548c\u5de8\u4eba\u89d2\u6597\u800c\u53d6\u80dc\u7684\u5149\u8363\u6218\u7ee9\uff0c\u522b\u8bf4\u743c\u6069\u53ea\u662f\u4e2a\u5c0f\u5deb\u5e08\uff0c\u65e0\u8bba\u4f53\u683c\u8fd8\u662f\u4e2a\u5934\u90fd\u548c\u4ed6\u660e\u663e\u4e0d\u5728\u4e00\u4e2a\u6863\u6b21\u4e0a\uff0c\u53ef\u8c13\u662f\u80dc\u5238\u5728\u63e1\u3002\u7136\u800c\u53cc\u81c2\u4e00\u4ea4\uff0c\u5149\u5934\u7267\u5e08\u9661\u7136\u6253\u4e86\u4e2a\u5bd2\u98a4\uff0c\u65e0\u53ef\u6291\u5236\u7684\u6050\u60e7\u81ea\u5fc3\u5e95\u6d8c\u8d77\uff0c\u4ee4\u4ed6\u5168\u8eab\u6c57\u6bdb\u90fd\u76f4\u7ad6\u8d77\u6765\uff0c\u4eff\u4f5b\u6b63\u9762\u5bf9\u7740\u7684\u4e0d\u662f\u4eba\u7c7b\uff0c\u800c\u662f\u4e00\u5934\u8fdc\u53e4\u5de8\u517d\u3002</p><p>\u540e\u9000\uff01</p><p>\u7267\u5e08\u7684\u610f\u8bc6\u8fd8\u6ca1\u53cd\u5e94\u8fc7\u6765\uff0c\u8eab\u4f53\u5df2\u7ecf\u672c\u80fd\u5730\u505a\u51fa\u4e86\u5224\u65ad\uff0c\u7136\u800c\u592a\u8fdf\u4e86\u3002\u743c\u6069\u5389\u543c\u4e00\u58f0\uff0c\u53cc\u773c\u4e0d\u77e5\u4f55\u65f6\u5df2\u7ecf\u53d8\u6210\u8840\u7ea2\uff0c\u8eab\u8eaf\u9aa4\u7136\u9f13\u80c0\u8d77\u6765\uff0c\u5bbd\u5927\u7684\u957f\u888d\u556a\u5730\u88ab\u6491\u5f97\u56db\u5206\u4e94\u88c2\uff0c\u9732\u51fa\u5792\u7ed3\u6210\u5757\u7684\u5065\u58ee\u808c\u8089\uff0c\u4ed6\u5f13\u8d77\u8eab\uff0c\u4e24\u6392\u9ed1\u8272\u9aa8\u523a\u81ea\u80cc\u90e8\u5494\u5494\u5f39\u51fa\uff0c\u4ed6\u7684\u6307\u5c16\u957f\u51fa\u5c16\u9510\u6307\u7532\uff0c\u5bd2\u5149\u51db\u51db\uff0c\u4eff\u4f5b\u5229\u5203\u94a2\u5200\u3002\u8f6c\u773c\u4e4b\u95f4\uff0c\u539f\u672c\u4fca\u79c0\u7684\u5c11\u5e74\u53d8\u6210\u4e86\u4e00\u53ea\u72f0\u72de\u53ef\u6016\u7684\u602a\u517d\u3002</p><p>\u7136\u540e\u602a\u517d\u4e00\u722a\u51fb\u51fa\u3002</p></article>"}}}]);
