(window.webpackJsonp=window.webpackJsonp||[]).push([[463],{486:function(p){p.exports={chapterTitle:null,title:"\u7b2c463\u7ae0 \u771f\u6b63\u7684\u75db\u82e6",content:"<article><h2>\u7b2c463\u7ae0 \u771f\u6b63\u7684\u75db\u82e6</h2><p>\u3000\u3000\u201c\u600e\u4e48\u4f1a\u8fd9\u6837\uff0c\u597d\u597d\u7684\u8d70\u8def\u5c31\u628a\u811a\u7ed9\u5d34\u88c2\u4e86\uff1f\u4f1a\u4e0d\u4f1a\u8fd9\u6bb5\u65f6\u95f4\u7f3a\u9499\uff1f\u201d\u987e\u5efa\u840d\u5c06\u88c5\u6ee1\u9aa8\u5934\u6c64\u7684\u4fdd\u6e29\u74f6\u9012\u7ed9\u665a\u6674\u5bb6\u7684\u4fdd\u59c6\uff0c\u201c\u6211\u6253\u7535\u8bdd\u544a\u8bc9\u4f60\u5988\u4e86\uff0c\u4f60\u5988\u665a\u4e0a\u5750\u8239\u56de\u6765\uff0c\u660e\u5929\u4e0b\u5348\u624d\u80fd\u5230\u6d77\u5dde\u3002\u201d\u53c8\u5bf9\u5510\u5b66\u8c26\u62b1\u6028\uff0c\u201c\u4f60\u4eec\u5e02\u653f\u5e9c\u600e\u4e48\u7ba1\u4ea4\u901a\u7684\uff0c\u597d\u7aef\u7aef\u7684\u5927\u9a6c\u8def\u4e0a\uff0c\u600e\u4e48\u4f1a\u6709\u534a\u622a\u7816\u5934\uff0c\u5e02\u5bb9\u3001\u5e02\u653f\u90fd\u662f\u5403\u4ec0\u4e48\u5e72\u996d\u7684\uff0c\u4f60\u8fd9\u4e2a\u5e02\u957f\u662f\u600e\u4e48\u5f53\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u987e\u59e8\uff0c\u4f60\u8fd8\u662f\u62b1\u6028\u6211\u8d70\u8def\u6ca1\u957f\u773c\u775b\u5427\u2026\u2026\u201d\u5f20\u606a\u80cc\u4e0b\u57ab\u7740\u539a\u57ab\u5b50\uff0c\u5e8a\u524d\u6401\u7740\u6a2a\u677f\uff0c\u811a\u9694\u5728\u4e0a\u9762\uff0c\u5fc3\u865a\u7684\u8bf4\u3002</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u4f4f\u5728\u533b\u9662\uff0c\u591c\u91cc\u5c31\u62d6\u7740\u6253\u4e0a\u77f3\u818f\u7684\u4f24\u817f\u56de\u5230\u5bb6\u91cc\uff0c\u6253\u7b97\u597d\u597d\u4f11\u517b\u4e00\u4e2a\u6708\u624d\u8bf4\u3002\u53f3\u811a\u8e1d\u8f7b\u5fae\u9aa8\u88c2\uff0c\u5f20\u606a\u4e5f\u6ca1\u6253\u7b97\u5927\u8086\u5ba3\u626c\uff0c\u4f46\u662f27\u65e5\u4e0b\u5348\uff0c\u65b0\u9526\u56ed\u4e8c\u9053\u95e8\u5c97\u5185\u5c31\u505c\u6ee1\u5c0f\u8f66\uff0c\u4e0d\u77e5\u60c5\u7684\u4eba\u89c1\u8fd9\u9635\u5f0f\u8fd8\u4ee5\u4e3a\u5c0f\u533a\u91cc\u6709\u4eba\u957f\u51fa\u7b2c\u4e09\u53ea\u811a\u5462\u3002</p><p>\u3000\u3000\u5f20\u606a\u8eba\u5728\u5e8a\u4e0a\uff0c\u5f88\u65e0\u5948\u7684\u770b\u7740\u56f4\u5728\u5e8a\u8fb9\u7684\u4eba\uff0c\u8bf4\u9053\uff1a\u201c\u8d70\u8def\u5d34\u4e86\u811a\u800c\u5df2\uff0c\u5927\u5bb6\u767e\u5fd9\u4e4b\u4e2d\u8fc7\u6765\u770b\u671b\uff0c\u5185\u5fc3\u94ed\u611f\uff0c\u600e\u4e48\u597d\u610f\u601d\u2026\u2026\u201d\u626d\u5934\u770b\u5230\u53f6\u5efa\u658c\u5728\u8be1\u5f02\u7684\u7b11\uff0c\u53f9\u4e86\u4e00\u53e3\u6c14\u8bf4\u9053\uff0c\u201c\u53f6\u54e5\uff0c\u5e78\u707e\u4e50\u7978\u7684\u8868\u60c5\u662f\u4e0d\u662f\u53ef\u4ee5\u63a9\u9970\u5f97\u66f4\u597d\u4e00\u4e9b\uff1f\u201d</p><p>\u3000\u3000\u201c\u6709\u8fd9\u4e48\u660e\u663e\uff1f\u201d\u53f6\u5efa\u658c\u95ee\u9053\uff0c\u53c8\u6307\u7740\u8c22\u665a\u6674\u8bf4\uff0c\u201c\u8c22\u603b\u7684\u8868\u60c5\u4e5f\u5f88\u5e78\u707e\u4e50\u7978\u554a\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e0d\u4f1a\u9a97\u4ed6\u4eec\u8bf4\u8e29\u5239\u8f66\u7528\u529b\u8fc7\u731b\uff0c\u4e5f\u4e0d\u4f1a\u8bf4\u4ece\u9732\u53f0\u56f4\u5899\u8df3\u4e0b\u6765\u2014\u2014\u6015\u522b\u4eba\u4f1a\u7a76\u6839\u95ee\u5e95\u2014\u2014\u8ddf\u5085\u4fca\u4e24\u4eba\u54ac\u6b7b\u4e86\u8bf4\u662f\u8d70\u8def\u4e0d\u5c0f\u5fc3\u5d34\u788e\u7816\u4e0a\u4e86\uff0c\u800c\u4e14\u662f\u8ddf\u5085\u4fca\u5728\u4e00\u8d77\u3002</p><p>\u3000\u3000\u5f20\u606a\u6628\u5929\u811a\u5d34\u540e\u8ddf\u8bb8\u601d\u5728\u7535\u8bdd\u91cc\u8bf4\u8fc7\u4ed6\u8ddf\u9648\u9759\u5728\u4e00\u8d77\uff0c\u5f53\u65f6\u53f6\u5efa\u658c\u3001\u5b59\u9759\u9999\u3001\u5b59\u5c1a\u4e49\u3001\u82cf\u6d25\u4e1c\u7b49\u4eba\u90fd\u5728\u65c1\u8fb9\u542c\u7740\uff0c\u53ea\u5f53\u5f20\u606a\u5b9e\u9645\u4e0a\u662f\u8ddf\u9648\u9759\u5728\u4e00\u8d77\u5d34\u4e86\u811a\u5374\u62ff\u5085\u4fca\u5f53\u501f\u53e3\u767e\u822c\u63a9\u9970\uff0c\u81ea\u7136\u5c31\u7b11\u8d77\u6765\u5f88\u8be1\u5f02\uff0c\u5012\u6ca1\u6709\u4eba\u540c\u60c5\u4ed6\u5c06\u811a\u7ed9\u5d34\u4e86\u3002</p><p>\u3000\u3000\u8bb8\u601d\u5f53\u7136\u80fd\u660e\u767d\u771f\u6b63\u7684\u539f\u56e0\u662f\u4ec0\u4e48\uff0c\u53ea\u662f\u5979\u8fd9\u65f6\u5019\u4e0d\u65b9\u4fbf\u6765\u770b\u5f20\u606a\uff1b\u8c22\u665a\u6674\u77e5\u9053\u5f20\u606a\u6628\u5929\u4e0b\u5348\u8ddf\u8bb8\u601d\u5728\u4e00\u8d77\uff0c\u5979\u5f53\u65f6\u8fd8\u4e0d\u77e5\u9053\u5f20\u606a\u6709\u9047\u5230\u9648\u9759\uff0c\u5c31\u76f4\u63a5\u6253\u7535\u8bdd\u95ee\u8bb8\u601d\u600e\u4e48\u56de\u4e8b\uff0c\u8bb8\u601d\u72b9\u72b9\u8c6b\u8c6b\u7684\u3001\u5431\u5431\u5514\u5514\u7684\u5c31\u7ed9\u665a\u6674\u5c06\u5b9e\u60c5\u5957\u4e86\u8fc7\u53bb\u3002\u8c22\u665a\u6674\u7684\u7b11\u5bb9\u81ea\u7136\u66f4\u8be1\u5f02\uff0c\u5728\u522b\u4eba\u773c\u91cc\u8fd8\u6b63\u5e38\uff0c\u5f20\u606a\u591a\u5c11\u80fd\u660e\u767d\u8be1\u5f02\u7684\u5473\u9053\u3002</p><p>\u3000\u3000\u5f53\u7136\u4e5f\u6709\u5b8c\u5168\u7ed9\u5f20\u606a\u7684\u9b3c\u8bdd\u552c\u4f4f\u7684\uff0c\u987e\u5efa\u840d\u5c31\u662f\u5176\u4e2d\u7684\u4e00\u4e2a\uff0c\u5f20\u606a\u672c\u4e0d\u60f3\u8ba9\u4ed6\u7238\u5988\u77e5\u9053\u811a\u5d34\u4e86\u7684\u4e8b\uff0c\u4e0d\u8fc7\u4e3b\u52a8\u901a\u98ce\u62a5\u4fe1\u7684\u4e5f\u4e0d\u6b62\u987e\u5efa\u840d\u4e00\u4e2a\uff0c\u4ed6\u5c0f\u53d4\u5f20\u77e5\u975e\u4e5f\u6253\u7535\u8bdd\u53bb\u4e86\u65b0\u829c\u3002</p><p>\u3000\u3000\u6253\u4e0a\u77f3\u818f\uff0c\u811a\u6b62\u75db\u4e4b\u540e\uff0c\u5f20\u606a\u5012\u4e0d\u8ba4\u4e3a\u8fd9\u662f\u4e00\u4ef6\u5012\u9709\u7684\u4e8b\u60c5\uff0c\u5fc3\u91cc\u8fd8\u6709\u4e9b\u7a83\u559c\uff0c\u53ef\u4ee5\u501f\u673a\u5c06\u7410\u788e\u7684\u4e8b\u60c5\u90fd\u63a8\u6389\uff0c\u5b89\u5b89\u9759\u9759\u7684\u5728\u5bb6\u4fee\u8eab\u517b\u6027\uff0c\u6b63\u597d\u7ed9\u81ea\u5df1\u72e0\u72e0\u7684\u653e\u4e00\u56de\u957f\u5047\u3002\u4f46\u662f\u53ea\u5b89\u9759\u7684\u4eab\u53d7\u4e86\u4e00\u4e2a\u4e0a\u5348\u7684\u9759\u8c27\u65f6\u5149\uff0c\u4e2d\u5348\u665a\u6674\u8fc7\u6765\u4e0e\u5979\u52a9\u624b\u5f20\u5ead\u8fc7\u6765\uff0c\u770b\u5230\u5f20\u606a\u53f3\u811a\u6253\u7740\u77f3\u818f\u8eba\u5728\u5e8a\u4e0a\u770b\u90d1\u4f0a\u5065\u7684\u53e4\u60d1\u4ed4\u4e4b\u300a\u731b\u9f99\u8fc7\u6c5f\u300b\u3002\u8bf4\u5b9e\u8bdd\uff0c\u300a\u731b\u9f99\u8fc7\u6c5f\u300b\u91cc\u90d1\u4f0a\u5065\u6f14\u7684\u9648\u6d69\u5357\u8868\u73b0\u5f88\u6709\u9650\uff0c\u9648\u5c0f\u6625\u6f14\u7684\u5c71\u9e21\u5374\u662f\u6d53\u58a8\u91cd\u5f69\uff0c\u56e0\u4e3a\u5927\u5b66\u65f6\u6709\u4eba\u8bf4\u5f20\u606a\u7684\u8138\u6bd4\u90d1\u4f0a\u5065\u8fd8\u6709\u5473\u9053\uff0c\u6240\u4ee5\u5f20\u606a\u770b\u7740\u90d1\u4f0a\u5065\u7684\u7535\u5f71\u7279\u522b\u6709\u4ee3\u5165\u611f\uff0c\u8fd9\u65f6\u5019\u91cd\u6e29\u300a\u731b\u9f99\u8fc7\u6c5f\u300b\u6709\u4e9b\u4e0d\u8fc7\u763e\uff0c\u867d\u7136\u662f\u5085\u4fca\u7167\u4ed6\u610f\u601d\u62ff\u6765\u6700\u65b0\u7684\u6e2f\u53f0\u7247\u3002\u665a\u6674\u4e0e\u5f20\u5ead\u4e0a\u697c\u63a8\u5f00\u95e8\u65f6\uff0c\u5f20\u606a\u6b63\u6253\u7535\u8bdd\u7ed9\u5085\u4fca\uff0c\u8ba9\u4ed6\u53bb\u627e\u53e4\u60d1\u4ed4\u7cfb\u5217\u7b2c\u4e00\u90e8\u300a\u4eba\u5728\u6c5f\u6e56\u300b\u7684\u789f\u7247\uff0c\u4ed6\u8fd8\u8bb0\u5f97\u90a3\u90e8\u7535\u5f71\u7684\u4e00\u53e5\u5f88\u8457\u540d\u7684\u53f0\u8bcd\uff0c\u6b63\u5728\u7535\u8bdd\u91cc\u8ddf\u5085\u4fca\u80e1\u5439\uff1a\u201c\u51fa\u6765\u6df7\u8981\u8bb2\u4fe1\u7528\uff0c\u8bf4\u8fc7\u8ba9\u4ed6\u5168\u5bb6\u6b7b\u5149\uff0c\u5c31\u8ba9\u4ed6\u5168\u5bb6\u6b7b\u5149\u3002\u201d\u90a3\u662f\u5434\u9547\u5b87\u6f14\u7684\u9753\u5764\u7684\u53f0\u8bcd\u3002</p><p>\u3000\u3000\u770b\u5230\u5f20\u606a\u53f3\u811a\u6253\u7740\u77f3\u818f\uff0c\u53c8\u542c\u5230\u4ed6\u5634\u91cc\u8bf4\u51fa\u5982\u6b64\u532a\u6c14\u5341\u8db3\u7684\u8bdd\uff0c\u665a\u6674\u4eff\u4f5b\u6328\u4e86\u4e00\u8bb0\u95f7\u68cd\u50bb\u5728\u90a3\u91cc\uff0c\u665a\u6674\u7684\u79c1\u4eba\u52a9\u7406\u5f20\u5ead\u5219\u5f53\u573a\u8138\u5413\u5f97\u715e\u767d\u3002\u5f20\u606a\u8d39\u52b2\u89e3\u91ca\u4e86\u534a\u5929\uff0c\u76f4\u5230\u5085\u4fca\u5c06\u789f\u7247\u62ff\u8fc7\u6765\uff0c\u5f20\u606a\u4ece\u789f\u7247\u91cc\u641c\u7d22\u5230\u8fd9\u53e5\u8bdd\uff0c\u665a\u6674\u624d\u5982\u91ca\u91cd\u8d1f\uff1a\u5f20\u606a\u4e0d\u662f\u56e0\u4e3a\u5728\u5916\u9762\u53d7\u5230\u6b3a\u8d1f\u8981\u706d\u4eba\u6ee1\u95e8\u3002</p><p>\u3000\u3000\u5c31\u8fd9\u6837\uff0c\u5927\u4e2d\u5348\u7684\uff0c\u6d88\u606f\u5c31\u4f20\u4e86\u51fa\u53bb\uff0c\u516c\u53f8\u91cc\u7684\u4eba\u5012\u4e5f\u7b97\u4e86\uff0c\u5f20\u606a\u6253\u7535\u8bdd\u8bf4\u8fc7\u6ca1\u4e8b\u4e0d\u8981\u8fc7\u6765\uff0c\u5c31\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u3001\u5468\u590d\u3001\u8521\u7eef\u5a1f\u966a\u5b59\u5c1a\u4e49\u6765\u4e86\uff0c\u672c\u6765\u5c31\u6709\u5f88\u591a\u4e8b\u60c5\u8981\u5f20\u606a\u51b3\u5b9a\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u8fd9\u6bb5\u65f6\u95f4\u7b97\u662f\u5728\u6d77\u5dde\u6e21\u5047\uff0c\u5b59\u9759\u9999\u5de5\u4f5c\u65f6\uff0c\u4ed6\u5c31\u8981\u81ea\u5df1\u627e\u5730\u65b9\u6253\u53d1\u65f6\u95f4\uff0c\u76db\u946b\u7535\u5668\u5728\u6d77\u5dde\u897f\u57ce\u533a\u6709\u4e00\u5bb6\u5bb6\u7535\u5356\u573a\uff0c\u4ed6\u7559\u5728\u6d77\u5dde\uff0c\u897f\u57ce\u5bb6\u7535\u5356\u573a\u7684\u603b\u7ecf\u7406\u6574\u65e5\u63d0\u5fc3\u540a\u80c6\uff0c\u504f\u504f\u53f6\u5efa\u658c\u53c8\u4e0d\u53bb\u897f\u57ce\u5356\u573a\u89c6\u5bdf\uff1b\u5b59\u9759\u9999\u3001\u6c5f\u9edb\u513f\uff0c\u4ee5\u53ca\u98de\u9e3f\u5e7f\u544a\u7684\u8001\u603b\u656c\u98de\u9e3f\uff1b\u8fd9\u4e9b\u4eba\u90fd\u8dd1\u4e86\u8fc7\u6765\u3002</p><p>\u3000\u3000\u5e02\u91cc\uff0c\u5f20\u606a\u4e5f\u4e0d\u4fbf\u6253\u7535\u8bdd\u56de\u7edd\u4ec0\u4e48\uff0c\u5510\u5b66\u8c26\u3001\u91d1\u56fd\u6d77\u3001\u675c\u5c0f\u5c71\u3001\u5b8b\u57f9\u660e\u3001\u9ec4\u67f1\u5168\u3001\u8bb8\u745e\u5e73\u3001\u5e02\u653f\u5e9c\u79d8\u4e66\u957f\u803f\u5efa\u5fe0\u7b49\u4eba\u6765\u4e86\uff0c\u5ba3\u4f20\u90e8\u957f\u987e\u8000\u626d\u626d\u634f\u634f\u7684\u8dd1\u8fc7\u6765\uff0c\u8bf4\u662f\u6070\u5de7\u8def\u8fc7\u5c31\u8fdb\u6765\u770b\u4e00\u773c\u3002</p><p>\u3000\u3000\u5c0f\u53d4\u3001\u5c0f\u5a76\u3001\u5927\u8205\u3001\u5927\u8205\u5988\uff0c\u8fd8\u6709\u5728\u6d77\u5dde\u5927\u5b66\u8bfb\u5927\u4e8c\u7684\u8868\u59b9\u6881\u541b\u8339\u6b63\u597d\u4e0b\u5348\u6ca1\u6709\u8bfe\u4e5f\u8fc7\u6765\u4e86\u3002</p><p>\u3000\u3000\u5802\u4f2f\u5f20\u77e5\u5fae\u4e0d\u77e5\u9053\u4ece\u90a3\u91cc\u5f97\u6765\u6d88\u606f\uff0c\u4ed6\u77e5\u9053\u81ea\u5df1\u6ca1\u6709\u8138\u8fc7\u6765\uff0c\u8ba9\u513f\u5b50\u5f20\u9038\u501f\u4e86\u8f86\u8f66\u4e00\u8d77\u5c06\u5f20\u606a\u4ed6\u5976\u5976\u9001\u4e86\u8fc7\u6765\u3002\u8fd9\u4e24\u5e74\u4e24\u5bb6\u7684\u5173\u7cfb\u5f88\u758f\u8fdc\u4e86\uff0c\u5f20\u77e5\u5fae\u7ed9\u4e1c\u793e\u53bf\u6c11\u653f\u5c40\u5f00\u9664\u516c\u804c\uff0c\u5c31\u4ec0\u4e48\u90fd\u4e0d\u662f\u4e86\uff0c\u6bd4\u4e00\u822c\u4eba\u8fd8\u8981\u843d\u9b44\uff1b\u534a\u751f\u7684\u79ef\u84c4\u90fd\u586b\u8fdb\u4e86\u7ed9\u632a\u7528\u516c\u6b3e\u6240\u9020\u6210\u7684\u6d1e\uff0c\u8fd8\u6b20\u5f20\u77e5\u975e\u51e0\u5341\u4e07\uff0c\u867d\u7136\u5f20\u77e5\u975e\u4e0d\u4f1a\u6307\u671b\u4ed6\u8fd8\u8fd9\u51e0\u5341\u4e07\u94b1\uff0c\u4f46\u662f\u5f20\u77e5\u975e\u4ed6\u8001\u5a46\u5374\u4e0d\u662f\u592a\u624b\u8f6f\u7684\u89d2\u8272\uff0c\u9047\u5230\u5927\u4f2f\u5f20\u77e5\u5fae\uff0c\u603b\u4f1a\u63d0\u5230\u8fd9\u7b14\u501f\u6b3e\u3002\u5f20\u77e5\u975e\u5728\u8001\u5bb6\u627f\u5305\u4e86\u51e0\u4ea9\u9c7c\u5858\uff0c\u8bf4\u662f\u53bb\u5e74\u9c7c\u82d7\u4f3a\u5019\u4e0d\u5f53\uff0c\u8fd8\u586b\u4e86\u51e0\u5343\u5757\u94b1\u8fdb\u53bb\uff1b\u5f20\u9038\u5728\u4e1c\u793e\u53bf\u91cc\u7684\u4e00\u5bb6\u516c\u53f8\u5f53\u4e1a\u52a1\u5458\uff0c\u8c08\u4e86\u4e00\u4e2a\u5973\u670b\u53cb\uff0c\u5acc\u5f03\u4ed6\u5bb6\u5883\u5dee\uff0c\u5e74\u524d\u8ba2\u4e86\u5a5a\u8fd8\u662f\u8ddf\u4eba\u8dd1\u4e86\uff0c\u7b97\u662f\u843d\u9b44\u5230\u6781\u4e86\u3002</p><p>\u3000\u3000\u5510\u5a67\u4e0b\u5348\u8ddf\u5b66\u6821\u8bf7\u4e86\u5047\u2026\u2026</p><p>\u3000\u3000\u597d\u5427\uff0c\u5f20\u606a\u7684\u5367\u5ba4\uff0c\u5728\u4e5d\u516d\u5e74\u7684\u6d77\u5dde\uff0c\u7b97\u662f\u6781\u5927\u7684\uff0c\u4e09\u5341\u591a\u5e73\u7c73\uff0c\u8fd9\u65f6\u5019\u5374\u663e\u5f97\u5f88\u62e5\u6324\uff0c\u5f20\u606a\u6240\u671f\u5f85\u7684\u9759\u8c27\u65f6\u5149\u5c31\u8fd9\u6837\u7ed9\u7834\u574f\u6b86\u5c3d\uff0c\u4ed6\u8fd8\u8981\u7ec6\u5fc3\u8ddf\u6bcf\u4e00\u4e2a\u4eba\u590d\u8ff0\u4ed6\u5728\u8def\u4e0a\u8e29\u5230\u788e\u7816\u5d34\u811a\u7684\u8c0e\u8a00\u7ec6\u8282\u4ee5\u6ee1\u8db3\u4ed6\u4eec\u7684\u597d\u5947\u5fc3\u3002\u542c\u5230\u4ed6\u5988\u706b\u6025\u706b\u7206\u7684\u4ece\u65b0\u829c\u8d76\u56de\u6765\uff0c\u5f20\u606a\u6068\u4e0d\u5f97\u4f24\u811a\u9a6c\u4e0a\u75ca\u6108\u3002</p><p>\u3000\u3000\u4ed6\u7684\u6298\u78e8\u8fd8\u6ca1\u6709\u7ed3\u675f\uff0c\u5367\u5ba4\u91cc\u7684\u7535\u8bdd\u94c3\u54cd\u8d77\u6765\uff0c\u665a\u6674\u62ff\u8d77\u6765\uff0c\u542c\u4e86\u4e00\u4f1a\u513f\u5bf9\u5f20\u606a\u8bf4\uff1a\u201c\u5b66\u6821\u5f88\u5173\u5fc3\u4f60\u7684\u60c5\u51b5\uff0c\u4f60\u4eec\u6821\u957f\u8ddf\u73ed\u4e3b\u4efb\u8fd8\u6709\u540c\u5b66\u4ee3\u8868\u4e00\u4f1a\u513f\u8981\u8fc7\u6765\u770b\u671b\u4f60\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u4fa7\u7740\u5934\u95ee\u6b63\u60a0\u7136\u770b\u6700\u65b0\u65f6\u88c5\u6742\u5fd7\u7684\u5510\u5a67\uff1a\u201c\u6211\u6709\u591a\u4e45\u6ca1\u53bb\u5b66\u6821\u4e86\uff0c\u4ed6\u4eec\u600e\u4e48\u53ef\u80fd\u8fd8\u8bb0\u5f97\u6709\u6211\u8fd9\u4e2a\u5b66\u751f\uff1f\u201d</p><p>\u3000\u3000\u5510\u5a67\u7f8e\u4e3d\u7684\u773c\u7738\u5b50\u64a9\u4e86\u64a9\uff0c\u8bf4\u9053\uff1a\u201c\u674e\u8001\u5e08\u6628\u5929\u8fd8\u95ee\u8d77\u4f60\u4f1a\u8003\u600e\u4e48\u529e\u5462\uff0c\u4f60\u662f\u6709\u5f88\u4e45\u6ca1\u53bb\u5b66\u6821\u4e86\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u5bf9\u665a\u6674\u8bf4\uff1a\u201c\u80fd\u5e2e\u6211\u62d2\u7edd\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u4ed6\u4eec\u597d\u50cf\u5728\u8def\u4e0a\u6253\u6765\u7684\u7535\u8bdd\u2026\u2026\u201d\u665a\u6674\u4e3a\u96be\u7684\u8bf4\u3002</p><p>\u3000\u3000\u8fd9\u4e0d\u662f\u4e0d\u7ed9\u4ed6\u62d2\u7edd\u7684\u673a\u4f1a\u5417\uff1f\u5f20\u606a\u65e0\u529b\u7684\u70b9\u70b9\u5934\uff0c\u8fd9\u65f6\u5019\u4ed6\u5976\u5976\u7aef\u4e00\u7897\u9ed1\u4e4e\u4e4e\u7684\u6c64\u836f\u8fc7\u6765\uff0c\u4eba\u6ca1\u5230\u5e8a\u524d\uff0c\u6d53\u91cd\u7684\u4e2d\u836f\u5473\u5c31\u6251\u9f3b\u800c\u6765\uff1a\u201c\u5c0f\u606a\uff0c\u5c06\u836f\u559d\u4e0b\u53bb\uff0c\u8d70\u5f97\u6025\uff0c\u4e34\u65f6\u53ea\u6765\u5f97\u53ca\u6293\u4e24\u4ed8\u836f\uff0c\u5f88\u8865\u9aa8\u5934\u7684\uff0c\u7b49\u4f1a\u513f\u8ba9\u53f8\u673a\u5e08\u5085\u53bb\u836f\u5e97\u518d\u6293\u51e0\u5341\u4ed8\u8fc7\u6765\uff0c\u542c\u8bf4\u8ddf\u9aa8\u5934\u7096\u6c64\u836f\u6548\u6700\u597d\uff0c\u201d\u8f6c\u5934\u8ddf\u987e\u5efa\u840d\u8bf4\uff0c\u201c\u963f\u5a67\u5988\u5988\uff0c\u7096\u9aa8\u5934\u6c64\u8fd9\u4e8b\u5c31\u4ea4\u7ed9\u6211\u6765\u505a\uff0c\u6211\u95f2\u7740\u4e5f\u95f2\u7740\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u618b\u7740\u6c14\u5c06\u6c64\u836f\u559d\u4e0b\u53bb\uff0c\u76b1\u7740\u7709\u5934\u5b9e\u5728\u65e0\u6cd5\u60f3\u8c61\u8981\u662f\u8fd9\u836f\u8ddf\u9aa8\u5934\u6df7\u7096\u4f1a\u600e\u6837\u7684\u6298\u78e8\u4eba\uff0c\u60f3\u7740\u5085\u4fca\u4ed6\u8001\u5a46\u674e\u660e\u745c\u5728\u5e02\u4e2d\u836f\u516c\u53f8\uff0c\u60f3\u7740\u662f\u4e0d\u662f\u53ef\u4ee5\u627e\u5979\u5077\u6881\u6362\u67f1\u5f04\u4e9b\u6ecb\u9634\u58ee\u9633\u7684\u4e2d\u836f\u6765\u7cca\u5f04\u4ed6\u5976\u5976\uff1b\u770b\u7740\u5510\u5a67\u5728\u7b11\uff0c\u5c06\u8fd8\u5269\u4e0b\u4e00\u53e3\u6c64\u836f\u7684\u7897\u9012\u5230\u5979\u5634\u8fb9\uff1a\u201c\u4f60\u5c1d\u5c1d\uff0c\u662f\u6709\u70b9\u82e6\uff0c\u4e0d\u8fc7\u82e6\u540e\u8fd8\u6709\u4e9b\u7518\u751c\u5473\uff0c\u5947\u602a\uff01\u201d\u5510\u5a67\u5634\u51d1\u5230\u7897\u8fb9\uff0c\u60f3\u5c0f\u62bf\u4e86\u4e00\u53e3\u5c1d\u5c1d\uff0c\u54ea\u91cc\u60f3\u5230\u5f20\u606a\u5728\u5979\u814b\u4e0b\u4e00\u6320\uff0c\u5c06\u5269\u4e0b\u7684\u4e00\u53e3\u6c64\u836f\u90fd\u704c\u5230\u5979\u5634\u91cc\u3002</p><p>\u3000\u3000\u5510\u5a67\u79c0\u7709\u90fd\u5feb\u62e7\u5230\u4e00\u5757\u53bb\uff0c\u8981\u62a2\u5f20\u606a\u624b\u91cc\u7684\u7897\u5410\u56de\u53bb\uff0c\u5f20\u606a\u54ea\u91cc\u4f1a\u8ba9\u5979\u5f97\u901e\uff0c\u98de\u5feb\u5c06\u7897\u585e\u5230\u7ad9\u5728\u5e8a\u53f3\u8fb9\u5510\u5b66\u8c26\u7684\u624b\u91cc\u3002\u5510\u5a67\u8981\u8fc7\u6765\u62a2\u5979\u7238\u624b\u91cc\u7684\u7897\uff0c\u5510\u5b66\u8c26\u5c06\u7897\u522b\u5728\u8eab\u540e\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u5f20\u606a\u5c06\u4e00\u6574\u7897\u90fd\u559d\u4e0b\u53bb\uff0c\u4f60\u5c31\u559d\u4e00\u53e3\u6015\u4ec0\u4e48\u2026\u2026\u201d\u5510\u5a67\u542c\u4e86\u8fd9\u8bdd\uff0c\u60f3\u7740\u5f20\u606a\u6240\u5c1d\u7684\u82e6\u81ea\u5df1\u4e5f\u5e94\u8be5\u53bb\u4f53\u4f1a\uff0c\u5c31\u4e0d\u518d\u62a2\u7897\uff0c\u52aa\u529b\u5c06\u82e6\u5230\u6781\u70b9\u7684\u6c64\u836f\u559d\u4e0b\u6765\uff0c\u8fc7\u4e86\u597d\u4e45\u7709\u5934\u624d\u8212\u5c55\u5f00\u6765\uff0c\u5410\u4e86\u4e00\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u82e6\uff01\u201d\u5927\u5bb6\u54c8\u54c8\u5927\u7b11\u3002</p><p>\u3000\u3000\u5510\u5b66\u8c26\u770b\u7740\u5c4b\u5b50\u91cc\u6324\u6ee1\u4eba\uff0c\u7b11\u7740\u5bf9\u5f20\u606a\u8bf4\uff1a\u201c\u770b\u4f60\u6d3b\u8e66\u4e71\u8df3\u7684\uff0c\u5927\u5bb6\u4e0b\u5348\u90fd\u8fd8\u6709\u4e8b\uff0c\u5c31\u4e0d\u966a\u4f60\u627e\u53d1\u65f6\u5149\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u5c31\u76fc\u671b\u7740\u5510\u5b66\u8c26\u5e2e\u4ed6\u8bf4\u8fd9\u53e5\u8bdd\uff0c\u6323\u624e\u7740\u8981\u4e0b\u5e8a\u9001\u5ba2\u3002</p></article>"}}}]);
