(window.webpackJsonp=window.webpackJsonp||[]).push([[464],{486:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e94\u5341\u56db\u8282 \u65a9\u6740",content:"<article><h2>\u7b2c\u4e94\u5341\u56db\u8282 \u65a9\u6740</h2><p>\u556a\u5730\u4e00\u58f0\uff0c\u9c9c\u8840\u98de\u6e85\uff0c\u9b41\u68a7\u7684\u5149\u5934\u4e2d\u5e74\u7267\u5e08\u88ab\u743c\u6069\u4e00\u722a\u62bd\u4e2d\uff0c\u5982\u9640\u87ba\u822c\u5728\u7a7a\u4e2d\u65cb\u98de\u8d77\u6765\uff0c\u56db\u9053\u957f\u957f\u7684\u4f24\u53e3\u4ece\u5de6\u80a9\u4e00\u76f4\u5ef6\u4f38\u5230\u8179\u90e8\uff0c\u76ae\u5f00\u8089\u7efd\uff0c\u6df1\u53ef\u89c1\u9aa8\u3002\u743c\u6069\u7684\u8eaf\u4f53\u548c\u56db\u80a2\u5df2\u7ecf\u5b8c\u5168\u53d8\u6210\u602a\u517d\uff0c\u5934\u90e8\u4f9d\u7136\u8fd8\u662f\u4eba\u7c7b\u65f6\u7684\u5f62\u6001\uff0c\u4f46\u90a3\u8d64\u7ea2\u53cc\u773c\u4e2d\u6ee1\u8574\u7684\u72c2\u66b4\u6740\u6c14\uff0c\u5374\u5728\u8fd9\u79cd\u9c9c\u660e\u5bf9\u6bd4\u4e4b\u4e0b\u663e\u5f97\u683c\u5916\u9a87\u4eba\u3002\u4ed6\u5486\u54ee\u7740\uff0c\u7c97\u58ee\u7684\u540e\u80a2\u8e6c\u5730\u53d1\u529b\uff0c\u4e00\u8dc3\u800c\u8d77\uff0c\u95ea\u7535\u822c\u5728\u534a\u7a7a\u4e2d\u8ffd\u4e0a\u4e86\u7267\u5e08\uff0c\u53cc\u722a\u9ad8\u4e3e\u731b\u529b\u63d2\u4e0b\u3002</p><p>\u5361\u65af\u56fe\u4eba\u5728\u7a7a\u4e2d\uff0c\u907f\u65e0\u53ef\u907f\uff0c\u800c\u4e14\u521a\u624d\u743c\u6069\u90a3\u4e00\u722a\u4f3c\u4e4e\u8fd8\u9644\u5e26\u4e86\u8bc5\u5492\u6548\u679c\uff0c\u4ee4\u4ed6\u6682\u65f6\u5168\u8eab\u50f5\u786c\uff0c\u52a8\u5f39\u4e0d\u5f97\u3002\u773c\u770b\u96be\u9003\u5f00\u819b\u7834\u809a\u4e4b\u5384\uff0c\u4ed6\u9661\u7136\u4f4e\u8bf5\u4e00\u58f0\uff0c\u87ba\u65cb\u98d3\u98ce\u5239\u90a3\u51fa\u73b0\uff0c\u5c06\u7267\u5e08\u5377\u5165\u5176\u4e2d\u3002\u743c\u6069\u4e00\u722a\u51fb\u4e0b\u5374\u6253\u4e86\u4e2a\u7a7a\uff0c\u4ed6\u843d\u4e0b\u5730\u6765\uff0c\u6b63\u5f85\u8ffd\u51fb\uff0c\u5c31\u89c1\u5361\u65af\u56fe\u4ece\u98d3\u98ce\u91cc\u8dc3\u51fa\uff0c\u671d\u7740\u743c\u6069\u865a\u7a7a\u4e00\u6307\u6307\u6765\u3002</p><p>\u201c\u7070\u98de\u70df\u706d\uff01\u201d</p><p>\u88ab\u8fd9\u9053\u8db3\u4ee5\u77ac\u95f4\u6bd9\u6740\u516c\u725b\u7684\u6cd5\u672f\u6253\u4e2d\uff0c\u743c\u6069\u53ea\u662f\u8eab\u4f53\u6643\u4e86\u6643\uff0c\u968f\u5373\u4fbf\u82e5\u65e0\u5176\u4e8b\uff0c\u5486\u54ee\u7740\u518d\u5ea6\u731b\u6251\u4e0a\u6765\u3002\u5361\u65af\u56fe\u5fc3\u4e2d\u51db\u7136\uff0c\u8fde\u8fde\u540e\u9000\uff0c\u53c8\u53d1\u51fa\u51e0\u9053\u5f3a\u529b\u795e\u672f\uff0c\u5b83\u4eec\u5168\u90fd\u51c6\u786e\u547d\u4e2d\uff0c\u7136\u800c\u9664\u4e86\u8ba9\u76ee\u6807\u7684\u524d\u8fdb\u901f\u5ea6\u8fdf\u6ede\u4e86\u534a\u79d2\u949f\uff0c\u518d\u6ca1\u6709\u9020\u6210\u5176\u4ed6\u534a\u70b9\u4f24\u5bb3\u3002</p><p>\u8f6c\u773c\u4e4b\u95f4\uff0c\u7267\u5e08\u5df2\u7ecf\u88ab\u743c\u6069\u8ffd\u4e0a\uff0c\u5229\u722a\u53c8\u95ea\u7535\u822c\u5f53\u5934\u6293\u4e0b\u6765\u3002\u773c\u89c1\u65e0\u4ece\u95ea\u907f\uff0c\u5361\u65af\u56fe\u7834\u91dc\u6c89\u821f\u5730\u6012\u543c\u4e00\u58f0\uff0c\u53f3\u81c2\u50cf\u5439\u6c14\u822c\u9661\u7136\u81a8\u80c0\u4e86\u597d\u51e0\u500d\uff0c\u6574\u6761\u8896\u5b50\u88ab\u6491\u5f97\u7247\u7247\u788e\u88c2\uff0c\u5f80\u4e0a\u4e00\u683c\u67b6\u4f4f\u4e86\u602a\u517d\u7684\u5229\u722a\u3002</p><p>\u743c\u6069\u7684\u8d64\u773c\u4e2d\u5149\u8292\u95ea\u70c1\uff0c\u624b\u8155\u90e8\u4f4d\u9661\u7136\u5411\u524d\u5ef6\u4f38\uff0c\u7a81\u7136\u591a\u957f\u51fa\u4e00\u622a\uff0c\u7d27\u63a5\u7740\u7ffb\u624b\u6293\u4e0b\u3002\u5361\u65af\u56fe\u5389\u58f0\u60e8\u53eb\uff0c\u8138\u4e0a\u8d6b\u7136\u591a\u51fa\u56db\u9053\u722a\u75d5\uff0c\u539f\u672c\u5b8c\u597d\u7684\u53f3\u773c\u4e2d\u6c61\u8840\u8ff8\u6d41\uff0c\u773c\u73e0\u88ab\u751f\u751f\u63d2\u788e\uff0c\u4ed6\u539f\u672c\u5c31\u662f\u72ec\u773c\u9f99\uff0c\u8fd9\u4e0b\u5219\u5f7b\u5e95\u6210\u4e86\u778e\u5b50\u3002\u743c\u6069\u4e00\u51fb\u5f97\u624b\uff0c\u8d81\u7740\u5bf9\u65b9\u5267\u75db\u5206\u795e\u4e4b\u9645\uff0c\u53cc\u722a\u4e00\u9519\u626d\u4f4f\u4e86\u7267\u5e08\u7684\u53f3\u81c2\uff0c\u7528\u529b\u4e00\u7ede\u3002</p><p>\u5494\u5494\u5494\u5494\u5494\u5494\uff01\u4e00\u8fde\u4e32\u6e05\u8106\u7684\u9aa8\u9abc\u788e\u88c2\u58f0\u7206\u8d77\uff0c\u7267\u5e08\u7684\u534a\u622a\u80f3\u818a\uff0c\u81ea\u624b\u8098\u4ee5\u4e0b\u88ab\u7ede\u6210\u788e\u5757\uff0c\u50cf\u4e2a\u7834\u5e03\u888b\u822c\u8f6f\u8f6f\u5782\u4e0b\u3002\u743c\u6069\u72de\u7b11\u4e00\u58f0\uff0c\u653e\u5f00\u624b\u81c2\uff0c\u4e00\u722a\u7167\u7740\u5361\u65af\u56fe\u7684\u5fc3\u810f\u6293\u6765\u3002</p><p>\u6b7b\u5427\uff01</p><p>\u743c\u6069\u51f6\u72e0\u5730\u5636\u543c\u7740\uff0c\u6253\u7b97\u5c06\u8fd9\u4e2a\u53ef\u6076\u654c\u4eba\u7684\u5fc3\u810f\u638f\u51fa\uff0c\u5c06\u8eab\u4f53\u6495\u6210\u788e\u7247\uff0c\u4ed6\u4ece\u672a\u50cf\u8fd9\u6837\u6e34\u671b\u8fc7\u6740\u622e\uff0c\u6e34\u671b\u7740\u70ed\u817e\u817e\u7684\u9c9c\u8840\uff0c\u6e34\u671b\u6b23\u8d4f\u9762\u4e34\u6b7b\u4ea1\u7684\u6050\u60e7\uff0c\u54c1\u5c1d\u88ab\u6740\u5bb3\u7684\u7075\u9b42\uff0c\u90a3\u4e00\u5b9a\u662f\u4e16\u754c\u4e0a\u6700\u7f8e\u5999\u7684\u6ecb\u5473\u5427\u3002</p><p>\u6740\u6389\u773c\u524d\u8fd9\u5bb6\u4f19\uff0c\u518d\u6740\u6389\u5176\u4ed6\u5bb6\u4f19\uff0c\u6740\u6389\u8fd9\u5ea7\u5927\u6bbf\u91cc\u7684\u6240\u6709\u4eba\uff0c\u6740\u6389\u4e00\u5207\u8fdb\u5165\u89c6\u7ebf\u4e4b\u5185\u7684\u4eba\uff0c\u6740\u5149\u8fd9\u4e2a\u4e16\u754c\u2026\u2026</p><p>\u55e4\uff01</p><p>\u773c\u524d\u5149\u5f71\u9aa4\u7136\u4ea4\u9519\uff0c\u4e00\u4e2a\u4f1f\u5cb8\u7684\u6de1\u84dd\u8272\u8eab\u5f71\u51ed\u7a7a\u51fa\u73b0\u5728\u9762\u524d\u3002\u4ed6\u8fd8\u6ca1\u53cd\u5e94\u8fc7\u6765\u53d1\u751f\u4e86\u4ec0\u4e48\uff0c\u722a\u5b50\u5df2\u7ecf\u6309\u539f\u8ba1\u5212\u51fb\u51fa\u3002\u4e00\u58f0\u8f7b\u54cd\uff0c\u5982\u88c2\u8584\u7eb8\uff0c\u743c\u6069\u7684\u5229\u722a\u6df1\u6df1\u523a\u5165\u8840\u8089\u4e4b\u4e2d\uff0c\u51e0\u4e4e\u5c06\u6574\u4e2a\u8eaf\u4f53\u7a7f\u900f\u3002\u84dd\u8272\u9c9c\u8840\u50cf\u55b7\u6cc9\u4e00\u6837\u6e85\u5c04\u51fa\u6765\uff0c\u51b7\u51bd\u5982\u51b0\uff0c\u6d12\u5f97\u4ed6\u6ee1\u8138\u90fd\u662f\u3002</p><p>\u84dd\u8840\uff1f\u51b0\u51b7\uff1f</p><p>\u53d8\u6210\u602a\u517d\u4e4b\u540e\uff0c\u529b\u6c14\u5927\u589e\uff0c\u884c\u52a8\u5982\u98ce\uff0c\u4f46\u8111\u7b4b\u5374\u660e\u663e\u8fdf\u949d\u4e86\u5f88\u591a\uff0c\u4ee5\u81f3\u4e8e\u4ed6\u770b\u5230\u773c\u524d\u7684\u5c38\u4f53\u624d\u660e\u767d\u8fc7\u6765\uff0c\u90a3\u5e76\u4e0d\u662f\u5149\u5934\u7267\u5e08\uff0c\u800c\u662f\u971c\u5de8\u4eba\u3002\u5c31\u5728\u80f8\u819b\u5373\u5c06\u88ab\u5229\u722a\u6495\u5f00\u7684\u90a3\u4e00\u5239\u90a3\uff0c\u5361\u65af\u56fe\u7528\u4e86\u67d0\u79cd\u624b\u6bb5\uff0c\u5c45\u7136\u5c06\u6b63\u5728\u8fdc\u5904\u548c\u62c9\u52a0\u7b49\u4eba\u6df7\u6218\u7684\u971c\u5de8\u4eba\u9694\u7a7a\u77ac\u95f4\u201c\u79fb\u201d\u8fc7\u6765\uff0c\u9694\u5728\u8eab\u524d\u505a\u76fe\u724c\uff0c\u6321\u4e0b\u4e86\u8fd9\u81f4\u547d\u4e00\u51fb\u3002\u81ea\u5df1\u5219\u501f\u673a\u540e\u9000\uff0c\u62c9\u5f00\u4e86\u4e00\u5927\u6bb5\u8ddd\u79bb\u3002</p><p>\u971c\u5de8\u4eba\u7684\u8eab\u6750\u8f83\u4e4b\u4eba\u7c7b\u9ad8\u5f97\u591a\uff0c\u743c\u6069\u8fd9\u4e00\u722a\u539f\u672c\u662f\u5954\u7740\u5fc3\u810f\u53bb\u7684\uff0c\u5b9e\u9645\u6253\u4e2d\u7684\u5374\u662f\u8179\u90e8\u3002\u5de8\u4eba\u751f\u547d\u529b\u5f3a\u97e7\uff0c\u53d7\u6b64\u91cd\u521b\u4f9d\u65e7\u672a\u6b7b\uff0c\u4e24\u53ea\u5de8\u7075\u624b\u638c\u5de6\u53f3\u4e00\u5408\uff0c\u5c06\u5deb\u5e08\u7d27\u7d27\u7b8d\u4f4f\u4e86\u3002\u743c\u6069\u5389\u58f0\u6012\u543c\uff0c\u53cc\u81c2\u4e00\u5206\uff0c\u786c\u751f\u751f\u5c06\u5e9e\u5927\u7684\u971c\u5de8\u4eba\u4ece\u4e2d\u6495\u6210\u4e86\u4e24\u534a\u3002\u84dd\u8840\u55b7\u6e85\u4e86\u4ed6\u4e00\u8eab\uff0c\u539f\u672c\u5c31\u72f0\u72de\u7684\u602a\u517d\u8eab\u8eaf\u8d8a\u53d1\u663e\u5f97\u6050\u6016\uff0c\u8fde\u5728\u540e\u65b9\u89c2\u6218\u7684\u8299\u857e\u72c4\u90fd\u88ab\u5413\u5f97\u5446\u4e86\uff0c\u5018\u82e5\u4e0d\u662f\u8299\u8389\u5a05\u6276\u7740\uff0c\u53ea\u6015\u5c31\u8981\u5f53\u573a\u762b\u8f6f\u5728\u5730\u3002\u743c\u6069\u81ea\u5df1\u5374\u604d\u7136\u672a\u89c9\uff0c\u8db3\u4e0b\u53d1\u529b\u8e6c\u5730\uff0c\u5c31\u8981\u518d\u5ea6\u524d\u51b2\u3002</p><p>\u98ce\u6084\u6084\u4ece\u5730\u9762\u522e\u8d77\uff0c\u51b0\u51b7\u7684\u6c14\u6d41\u81ea\u971c\u5de8\u4eba\u7684\u4e24\u534a\u5c38\u4f53\u4e2d\u6d8c\u51fa\uff0c\u4e92\u76f8\u63a8\u6d8c\u7740\u6269\u6563\u51fa\u53bb\uff0c\u5f62\u6210\u5e9e\u5927\u7684\u6f29\u6da1\uff0c\u8d77\u521d\u7f13\u6162\uff0c\u63a5\u7740\u4fbf\u8d8a\u6765\u8d8a\u5feb\uff0c\u7a7a\u6c14\u4e2d\u7684\u6c34\u73e0\u51dd\u7ed3\u6210\u900f\u660e\u96ea\u82b1\uff0c\u88ab\u72c2\u98ce\u631f\u7740\u98de\u821e\u65cb\u8f6c\u3002\u5361\u65af\u56fe\u7ad9\u5728\u6f29\u6da1\u4e2d\uff0c\u7a33\u7a33\u5c79\u7acb\uff0c\u6c14\u52bf\u5982\u5c71\uff0c\u4e00\u70b9\u7d2b\u7130\u5728\u4ed6\u7a7a\u6d1e\u7684\u5de6\u773c\u7736\u4e2d\u95ea\u70c1\u8df3\u8dc3\u7740\uff0c\u731b\u7136\u201c\u8f70\u201d\u5730\u4e00\u58f0\uff0c\u5267\u70c8\u71c3\u70e7\u8d77\u6765\u3002</p><p>\u7136\u540e\u5468\u56f4\u7684\u4e00\u5207\u90fd\u53d1\u751f\u4e86\u53d8\u5316\u3002</p><p>\u91d1\u78a7\u8f89\u714c\u7684\u5bab\u6bbf\u6d88\u5931\u4e86\uff0c\u5176\u4ed6\u6240\u6709\u7684\u4eba\u4e5f\u90fd\u6d88\u5931\u4e86\uff0c\u8fd9\u662f\u4e00\u7247\u82cd\u832b\u65e0\u9645\u5927\u8352\u539f\uff0c\u4e00\u76f4\u5ef6\u4f38\u5230\u5929\u5730\u7684\u5c3d\u5934\uff0c\u72c2\u98ce\u547c\u5578\u7740\u8086\u8650\u800c\u8fc7\u3002\u743c\u6069\u62ac\u8d77\u5934\uff0c\u770b\u89c1\u8840\u7ea2\u8272\u7684\u5929\u7a7a\uff0c\u51e0\u4e4e\u5c06\u5782\u66ae\u7684\u6b8b\u9633\u5b8c\u5168\u63a9\u76d6\uff0c\u4ed6\u4fbf\u6b63\u7ad9\u5728\u8fd9\u8fbd\u9614\u5927\u5730\u4e4b\u4e0a\uff0c\u5dcd\u5dcd\u5929\u7a79\u4e4b\u4e0b\uff0c\u5b64\u8eab\u4e00\u4eba\u3002</p><p>\u7136\u540e\u5854\u6d1b\u65af\u7267\u5e08\u5728\u524d\u65b9\u51fa\u73b0\u3002</p><p>\u4ed6\u7684\u5f62\u8c61\u53d1\u751f\u4e86\u53d8\u5316\uff0c\u6240\u6709\u7684\u4f24\u52bf\u5168\u90e8\u590d\u539f\uff0c\u5c31\u8fde\u88ab\u743c\u6069\u63d2\u788e\u7684\u53f3\u773c\u4e5f\u91cd\u65b0\u957f\u4e86\u51fa\u6765\uff0c\u5de6\u773c\u4e0a\u5219\u53c8\u6234\u4e0a\u4e86\u9ed1\u8272\u773c\u7f69\u3002\u84dd\u3001\u7eff\u3001\u7d2b\u4e09\u8272\u95ea\u7535\u5728\u4ed6\u8eab\u4fa7\u4e2d\u4ea4\u9519\u7f20\u7ed5\uff0c\u7cc5\u5408\u6210\u4e00\u67c4\u5de8\u578b\u957f\u77db\uff0c\u60ac\u6d6e\u5728\u7a7a\u4e2d\uff0c\u7267\u5e08\u9759\u9759\u7ad9\u7acb\u7740\uff0c\u51dd\u89c6\u743c\u6069\u3002</p><p>\u201c\u77e5\u9053\u8fd9\u662f\u4f55\u5904\u5417\uff0c\u6740\u622e\u8005\uff1f\u201d\u4ed6\u95ee\u3002</p><p>\u743c\u6069\u6ca1\u6709\u56de\u7b54\uff0c\u4ed6\u4e0d\u77e5\u9053\uff0c\u4e5f\u6ca1\u5174\u8da3\u77e5\u9053\uff0c\u4ed6\u53ea\u662f\u5954\u8dd1\u8d77\u6765\uff0c\u518d\u4e00\u6b21\u5730\u53d1\u52a8\u51b2\u950b\u3002\u5361\u65af\u56fe\u51b7\u7b11\uff0c\u4f38\u624b\u63d0\u8d77\u957f\u77db\uff0c\u9065\u9065\u524d\u6307\u3002</p><p>\u5343\u4e07\u9053\u95ea\u7535\u4ece\u7a79\u9876\u5760\u843d\uff0c\u5343\u4e07\u9897\u51b0\u73e0\u88ab\u72c2\u98ce\u5e2d\u5377\u800c\u6765\uff0c\u5343\u4e07\u6735\u70c8\u7130\u5982\u7ea2\u83b2\u822c\u5728\u7a7a\u6c14\u4e2d\u7efd\u653e\uff0c\u5730\u9762\u5267\u70c8\u5730\u9707\u52a8\u8d77\u6765\uff0c\u6447\u6643\u7740\uff0c\u7eb5\u6a2a\u9f9f\u88c2\uff0c\u55b7\u51fa\u6eda\u70eb\u7684\u5ca9\u6d46\u3002\u743c\u6069\u53d7\u4f24\u4e86\uff0c\u4ed6\u88ab\u95ea\u7535\u51fb\u4e2d\uff0c\u88ab\u72c2\u98ce\u548c\u51b0\u73e0\u6495\u88c2\u8eab\u4f53\uff0c\u88ab\u70c8\u7130\u548c\u5ca9\u6d46\u707c\u70e7\u8840\u8089\uff0c\u7136\u800c\u6240\u6709\u8fd9\u4e00\u5207\u90fd\u6ca1\u80fd\u963b\u788d\u4ed6\u7684\u524d\u8fdb\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u4ed6\u7684\u5954\u8dd1\u901f\u5ea6\u8d8a\u6765\u8d8a\u5feb\u3002</p><p>\u6740\u4e86\u4ed6\uff01</p><p>\u6740\u4e86\u4ed6\uff01</p><p>\u6740\u4e86\u4ed6\uff01</p><p>\u575a\u5b9a\u7684\u58f0\u97f3\u5728\u5fc3\u5e95\u4e0d\u65ad\u54cd\u8d77\uff0c\u6210\u4e3a\u4ed6\u6b64\u523b\u8111\u4e2d\u552f\u4e00\u7684\u5ff5\u5934\uff0c\u8eab\u4f53\u7684\u53d7\u4f24\u548c\u5267\u75db\u65e9\u5df2\u611f\u89c9\u4e0d\u5230\uff0c\u6216\u8005\u8bf4\u5b83\u4eec\u6839\u672c\u5c31\u4e0d\u503c\u5f97\u53bb\u5173\u6ce8\u3002\u6b64\u65f6\u6b64\u523b\uff0c\u8fd8\u6709\u4ec0\u4e48\u6bd4\u6740\u4e86\u773c\u524d\u8fd9\u4e2a\u5bb6\u4f19\u66f4\u52a0\u91cd\u8981\u5462\u3002</p><p>\u653b\u51fb\u66f4\u52a0\u731b\u70c8\uff0c\u4f46\u8ddd\u79bb\u4e5f\u8fdb\u4e00\u6b65\u7f29\u77ed\uff0c\u90a3\u5f20\u4ee4\u4eba\u751f\u538c\u7684\u8138\u5df2\u7ecf\u80fd\u591f\u770b\u5f97\u6e05\u6e05\u695a\u695a\u3002\u4e94\u6b65\u3001\u56db\u6b65\u3001\u4e09\u6b65\u3001\u4e24\u6b65\uff0c\u518d\u8de8\u51fa\u4e00\u6b65\uff0c\u81ea\u5df1\u7684\u5229\u722a\u5c31\u80fd\u591f\u5c06\u4ed6\u7684\u5934\u9885\u634f\u788e\uff0c\u5c06\u4ed6\u7684\u8116\u9888\u626d\u65ad\uff0c\u5c06\u4ed6\u6495\u6210\u788e\u7247\u4e86\u3002</p><p>\u95ea\u7535\u957f\u77db\u5e73\u5e73\u523a\u4e86\u8fc7\u6765\u3002</p><p>\u743c\u6069\u4fa7\u8eab\u8eb2\u95ea\uff0c\u7136\u800c\u957f\u77db\u7684\u901f\u5ea6\u5feb\u5f97\u96be\u4ee5\u60f3\u8c61\uff0c\u4e0d\uff0c\u66f4\u51c6\u786e\u5730\u8bf4\u662f\u4e0d\u53ef\u601d\u8bae\uff0c\u5b83\u5728\u201c\u523a\u51fa\u201d\u8fd9\u4e2a\u52a8\u4f5c\u4f5c\u51fa\u7684\u90a3\u4e00\u5239\u90a3\uff0c\u540c\u65f6\u5c31\u5df2\u7ecf\u201c\u523a\u4e2d\u201d\u4e86\u76ee\u6807\u3002\u8fd9\u5df2\u7ecf\u8d85\u8d8a\u4e86\u903b\u8f91\u548c\u9b54\u6cd5\u7684\u6982\u5ff5\uff0c\u53ea\u80fd\u7528\u5947\u8ff9\u6765\u5f62\u5bb9\u3002\u743c\u6069\u4f4e\u4e0b\u5934\uff0c\u770b\u89c1\u81ea\u5df1\u7684\u80f8\u819b\u88ab\u957f\u77db\u6b63\u6b63\u523a\u5165\uff0c\u51b0\u51c9\u7684\u89e6\u611f\u76f4\u900f\u80ba\u8151\uff0c\u50cf\u662f\u8981\u5c06\u4ed6\u6574\u4e2a\u4eba\u51bb\u50f5\u3002</p><p>\u5361\u65af\u56fe\u5f97\u610f\u5730\u72c2\u7b11\u8d77\u6765\uff0c\u7136\u800c\u5728\u4e0b\u4e00\u79d2\u949f\u4ed6\u7684\u7b11\u5bb9\u51dd\u7ed3\u4e86\uff0c\u56e0\u4e3a\u743c\u6069\u518d\u5ea6\u5411\u524d\u8fc8\u51fa\u4e00\u6b65\u3002\u8fd9\u4e2a\u52a8\u4f5c\u8ba9\u4ed6\u7684\u80f8\u8154\u88ab\u5b8c\u5168\u6d1e\u7a7f\uff0c\u957f\u77db\u7a7f\u8fc7\u80ba\u90e8\uff0c\u900f\u4f53\u800c\u8fc7\uff0c\u4f46\u4e5f\u8ba9\u4ed6\u548c\u5361\u65af\u56fe\u4e4b\u95f4\u7684\u8ddd\u79bb\u7f29\u77ed\u2014\u2014\u7f29\u77ed\u5230\u5371\u9669\u7684\u5730\u6b65\u3002</p><p>\u556a\uff01</p><p>\u50cf\u662f\u719f\u900f\u7684\u897f\u74dc\u88ab\u7528\u529b\u62cd\u5f00\uff0c\u9c9c\u7ea2\u7684\u8840\u548c\u4e73\u767d\u8272\u7684\u8111\u6d46\u98de\u6e85\u56db\u5c04\uff0c\u743c\u6069\u4e00\u722a\u5c06\u7267\u5e08\u7684\u5934\u9885\u7838\u6210\u4e86\u788e\u7247\u3002\u5c38\u4f53\u8f6f\u8f6f\u5012\u4e0b\uff0c\u7136\u540e\u8fc5\u901f\u865a\u5316\u3001\u900f\u660e\uff0c\u6d88\u5931\u4e0d\u89c1\u3002\u7d27\u63a5\u7740\uff0c\u5468\u56f4\u7684\u4e00\u5207\u4e5f\u968f\u4e4b\u53d1\u751f\u53d8\u5316\uff0c\u5206\u5d29\u79bb\u6790\uff0c\u6563\u5f52\u865a\u65e0\uff0c\u6062\u590d\u6210\u539f\u672c\u7684\u666f\u8c61\u3002</p><p>\u743c\u6069\u4f9d\u65e7\u662f\u7ad9\u5728\u6d6e\u7a7a\u57ce\u7684\u5bab\u6bbf\u91cc\uff0c\u6240\u6709\u4eba\u90fd\u5904\u4e8e\u539f\u4f4d\uff0c\u6ca1\u6709\u534a\u70b9\u53d8\u5316\uff0c\u521a\u624d\u53d1\u751f\u7684\u4e00\u5207\u4eff\u4f5b\u53ea\u662f\u573a\u68a6\u5e7b\u3002\u7136\u800c\u743c\u6069\u77e5\u9053\u5b83\u5e76\u975e\u68a6\u5e7b\uff0c\u80f8\u8154\u548c\u80ba\u8151\u88ab\u6d1e\u7a7f\u7684\u611f\u89c9\uff0c\u5305\u62ec\u90a3\u79cd\u65e0\u6cd5\u63cf\u8ff0\u7684\u5267\u75db\uff0c\u4f9d\u65e7\u6e05\u695a\u5730\u6b8b\u7559\u5728\u4ed6\u4f53\u5185\uff0c\u800c\u4e14\u539f\u672c\u7ad9\u5728\u5bf9\u9762\u7684\u5854\u6d1b\u65af\u7267\u5e08\u5361\u65af\u56fe\uff0c\u5df2\u7ecf\u6d88\u5931\u4e0d\u89c1\u4e86\u3002</p><p>\u4ed6\u6216\u8bb8\u5df2\u7ecf\u6b7b\u4e86\uff0c\u4f46\u4e5f\u8bb8\u5e76\u6ca1\u6709\u6b7b\uff0c\u8003\u8651\u5230\u743c\u6069\u521a\u624d\u6740\u6b7b\u4ed6\u4e4b\u540e\u5c38\u4f53\u7684\u6d88\u5931\u5f02\u72b6\uff0c\u540e\u9762\u8fd9\u79cd\u53ef\u80fd\u6027\u4f3c\u4e4e\u66f4\u5927\u4e00\u4e9b\u3002\u7136\u800c\u8fd9\u4e9b\u90fd\u65e0\u5173\u7d27\u8981\uff0c\u66f4\u4e0d\u662f\u743c\u6069\u6b64\u65f6\u6240\u8003\u8651\u7684\u95ee\u9898\uff0c\u4ed6\u4f4e\u4f4e\u5486\u54ee\u7740\uff0c\u8f6c\u8fc7\u8eab\u6765\uff0c\u8d64\u7ea2\u53cc\u76ee\u626b\u89c6\u7740\u6240\u6709\u4eba\uff0c\u90a3\u79cd\u72c2\u66b4\u7684\u6740\u6c14\u975e\u4f46\u6ca1\u6709\u56e0\u4e3a\u53d7\u4f24\u800c\u524a\u5f31\uff0c\u53cd\u800c\u66f4\u52a0\u5f3a\u76db\uff0c\u51e0\u4e4e\u5df2\u7ecf\u51dd\u6210\u6709\u5f62\u6709\u8d28\u7684\u5b9e\u4f53\uff0c\u538b\u5f97\u573a\u5185\u8bf8\u4eba\u5168\u90fd\u900f\u4e0d\u8fc7\u6c14\u6765\u3002</p><p>\u201c\u55b5\u545c\uff01\u201d</p><p>\u9762\u5bf9\u538b\u529b\uff0c\u6700\u5148\u4f5c\u51fa\u53cd\u5e94\u7684\u662f\u90a3\u540d\u732b\u5973\uff0c\u5979\u539f\u672c\u5df2\u7ecf\u5c06\u838e\u73de\u514b\u903c\u5f97\u8fde\u8fde\u8d25\u9000\uff0c\u773c\u770b\u5c31\u80fd\u65a9\u843d\u9a6c\u4e0b\uff0c\u8c01\u6599\u56de\u8eab\u4e00\u770b\uff0c\u5361\u65af\u56fe\u5c45\u7136\u6d88\u5931\u4e86\u3002\u60ca\u60e7\u987f\u65f6\u7b3c\u7f69\u5fc3\u5934\uff0c\u4ee4\u5979\u518d\u4e5f\u4e0d\u6562\u604b\u6218\uff0c\u4e00\u722a\u51fb\u51fa\u5c06\u838e\u73de\u514b\u9707\u5f00\uff0c\u968f\u5373\u8f6c\u8eab\u98de\u8dc3\uff0c\u95ea\u7535\u822c\u671d\u5927\u6bbf\u95e8\u53e3\u5954\u53bb\u3002</p><p>\u5979\u539f\u672c\u5c31\u8eab\u624b\u6781\u5feb\uff0c\u6b64\u65f6\u5168\u529b\u5954\u9003\uff0c\u5168\u8eab\u7130\u5149\u817e\u817e\u7efd\u653e\uff0c\u66f4\u8fdb\u4e00\u6b65\u52a0\u5feb\u4e86\u5979\u7684\u901f\u5ea6\u3002\u53ea\u4e00\u7728\u773c\u95f4\uff0c\u732b\u5973\u5c31\u5df2\u7ecf\u51b2\u5230\u95e8\u53e3\uff0c\u773c\u770b\u5c31\u8981\u9003\u51fa\uff0c\u731b\u7136\u4e00\u58f0\u91ce\u517d\u822c\u7684\u5486\u54ee\u81ea\u80cc\u540e\u4f20\u6765\uff0c\u7d27\u63a5\u7740\u4fbf\u662f\u51cc\u5389\u98ce\u58f0\u3002\u743c\u6069\u5df2\u7ecf\u8d76\u4e0a\uff0c\u4e00\u722a\u671d\u5979\u80cc\u540e\u6293\u4e0b\u3002</p><p>\u94ff\uff01</p><p>\u732b\u5973\u53f3\u722a\u5f80\u540e\u4e00\u6325\uff0c\u6b63\u63a5\u4f4f\u743c\u6069\u7684\u653b\u51fb\u3002\u4e24\u722a\u76f8\u4ea4\uff0c\u540c\u65f6\u90fd\u88ab\u8361\u5f00\uff0c\u7ed3\u679c\u5c45\u7136\u5e73\u5206\u79cb\u8272\uff0c\u743c\u6069\u662f\u53d8\u8eab\u6210\u4e86\u6740\u622e\u8005\uff0c\u800c\u732b\u5973\u624b\u4e0a\u4f69\u6234\u7684\u722a\u5957\u662f\u795e\u5668\uff0c\u5176\u5b9e\u8fd8\u8981\u66f4\u80dc\u51fa\u4e00\u7b79\u3002\u53ea\u662f\u5979\u6025\u4e8e\u9003\u547d\uff0c\u4e0d\u6562\u591a\u505c\u7559\uff0c\u501f\u7740\u53cd\u9707\u4e4b\u529b\u987a\u52bf\u524d\u51b2\uff0c\u8dc3\u51fa\u4e86\u5bab\u6bbf\u5927\u95e8\u3002</p><p>\u4fbf\u5728\u6b64\u65f6\uff0c\u4e00\u4e2a\u5e73\u9759\u7684\u5c11\u5973\u58f0\u97f3\u81ea\u5916\u9762\u4f20\u8fdb\u6765\u3002</p><p>\u201c\u51b0\u5c01\uff01\u201d</p><p>\u51b0\u81ea\u865a\u7a7a\u4e2d\u51fa\u73b0\uff0c\u5c06\u8eab\u5728\u534a\u7a7a\u4e2d\u7684\u732b\u5973\u77ac\u95f4\u53d8\u6210\u900f\u660e\u51b0\u96d5\uff0c\u7136\u540e\u7f13\u7f13\u843d\u5730\u3002\u4e24\u4e2a\u4eba\u5e76\u80a9\u4ece\u6bbf\u5916\u8d70\u4e86\u8fdb\u6765\uff0c\u5de6\u4fa7\u662f\u4e2a\u7a7f\u84dd\u8863\u7684\u80cc\u5251\u7537\u5b50\uff0c\u53f3\u4fa7\u5219\u662f\u4e2a\u6817\u8272\u957f\u53d1\u7684\u5973\u5b69\uff0c\u4e00\u88ad\u767d\u8272\u5deb\u5e08\u888d\u4e0a\u7ea4\u5c18\u4e0d\u67d3\uff0c\u5728\u5979\u7684\u5de6\u624b\u4e0a\uff0c\u4e00\u679a\u91d1\u9ec4\u8272\u7684\u6212\u6307\u6b63\u6563\u53d1\u7740\u51db\u51db\u5bd2\u5149\u3002</p></article>"}}}]);
