(window.webpackJsonp=window.webpackJsonp||[]).push([[465],{488:function(p){p.exports={chapterTitle:null,title:"\u7b2c33\u7ae0 \u6076\u4eba\u9700\u6709\u6076\u4eba\u78e8",content:"<article><h2>\u7b2c33\u7ae0 \u6076\u4eba\u9700\u6709\u6076\u4eba\u78e8</h2><p>\u3000\u3000\u521a\u8fdb\u5165\u9ad8\u4e2d\uff0c\u5b66\u6821\u7ec4\u7ec7\u9ad8\u4e00\u65b0\u751f\u8fdb\u884c\u4e3a\u671f\u5341\u5929\u7684\u519b\u8bad\uff0c\u5f20\u606a\u5bf9\u6b64\u5e76\u4e0d\u53cd\u611f\uff0c\u4ed6\u5341\u516d\u5c81\u65f6\uff0c\u867d\u7136\u7ee7\u627f\u7238\u5988\u6f02\u4eae\u7684\u57fa\u56e0\uff0c\u4f46\u662f\u4eba\u8fc7\u4e8e\u524a\u7626\uff0c\u76ae\u80a4\u6d6e\u767d\uff0c\u770b\u4e0a\u53bb\u53ea\u662f\u4fca\u79c0\u5c11\u5e74\uff0c\u8fd8\u7b97\u4e0d\u4e0a\u5341\u8db3\u7684\u9633\u5149\u5e05\u54e5\u3002</p><p>\u3000\u3000\u7ecf\u8fc7\u5341\u5929\u7684\u519b\u8bad\uff0c\u98df\u91cf\u5927\u589e\uff0c\u4eba\u975e\u4f46\u6ca1\u6709\u7ee7\u7eed\u524a\u7626\uff0c\u4f53\u91cd\u8fd8\u589e\u52a0\u4e86\u8bb8\u591a\uff0c\u76ae\u80a4\u6ca1\u6709\u6652\u9ed1\uff0c\u4f9d\u65e7\u767d\u7699\uff0c\u4f46\u8ddf\u4e4b\u524d\u7684\u6d6e\u767d\u4e0d\u540c\uff0c\u6709\u7740\u5065\u5eb7\u7684\u5149\u6cfd\uff0c\u808c\u8089\u4e5f\u53d8\u7ed3\u5b9e\u4e86\uff0c\u773c\u795e\u4e5f\u53d8\u5f97\u9510\u5229\uff0c\u4eba\u770b\u8d77\u6765\u6210\u719f\u591a\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u5bf9\u81ea\u5df1\u73b0\u5728\u7684\u5f62\u8c61\u76f8\u5f53\u6ee1\u610f\uff0c\u5fc3\u60f3\u4ee5\u540e\u8fd8\u8981\u575a\u6301\u4f53\u80b2\u953b\u70bc\u3002</p><p>\u3000\u3000\u6d77\u5dde\u4e00\u4e2d\u7684\u4f20\u7edf\u662f\u5728\u519b\u8bad\u7ed3\u675f\u4e4b\u540e\u5c31\u8981\u8fdb\u884c\u6587\u7406\u5206\u73ed\uff0c\u5f20\u606a\u5bf9\u8ba4\u771f\u5b66\u4e60\u5b9e\u5728\u6ca1\u6709\u4efb\u4f55\u60f3\u6cd5\uff0c\u8fd9\u662f\u4ed6\u7b2c\u4e8c\u6b21\u8fdb\u5165\u6d77\u5dde\u4e00\u4e2d\u8bfb\u9ad8\u4e2d\uff0c\u6839\u672c\u5c31\u6ca1\u6709\u6253\u7b97\u89c4\u89c4\u77e9\u77e9\u7684\u6e21\u8fc7\u4e09\u5e74\u9ad8\u4e2d\u751f\u6daf\u3002\u4ed6\u968f\u624b\u586b\u597d\u6587\u7406\u9009\u73ed\u7684\u7533\u8bf7\u8868\uff0c\u4ea4\u7ed9\u65b0\u751f\u73ed\u7684\u8f85\u5bfc\u5458\uff0c\u5c31\u8d70\u51fa\u6559\u5ba4\u3002</p><p>\u3000\u3000\u60f3\u5230\u519b\u8bad\u8fc7\u540e\u80fd\u4f11\u606f\u4e09\u5929\u518d\u8fdb\u65b0\u7684\u73ed\u7ea7\u4e0a\u8bfe\uff0c\u5f20\u606a\u5fc3\u60c5\u5c31\u5f88\u6109\u5feb\uff0c\u4eff\u4f5b\u771f\u6b63\u8bfb\u9ad8\u4e2d\u65f6\u7684\u5fc3\u60c5\u3002</p><p>\u3000\u3000\u5f20\u606a\u6240\u5728\u7684\u65b0\u751f\u73ed\u6559\u5ba4\u4e0e\u9ad8\u4e8c\u5e74\u7ea7\u7684\u4e00\u90e8\u5206\u73ed\u7ea7\u540c\u5728\u56db\u697c\uff0c\u5f20\u606a\u4f9d\u7740\u680f\u6746\uff0c\u6b63\u4eab\u53d7\u9ec4\u660f\u7684\u4e60\u4e60\u51c9\u98ce\uff0c\u770b\u89c1\u4ece\u9694\u58c1\u6559\u5ba4\u8d70\u51fa\u4e00\u4e2a\u5973\u5b69\u5b50\uff0c\u5973\u5b69\u5b50\u51fa\u6559\u5ba4\u8f6c\u8eab\u5f80\u697c\u68af\u53e3\uff0c\u5f20\u606a\u53ea\u6765\u5f97\u53ca\u77a5\u4e00\u773c\u5979\u7684\u4fa7\u8138\uff0c\u6ca1\u6709\u770b\u6e05\u6b63\u8138\uff0c\u9887\u6709\u7f8e\u5c11\u5973\u7684\u611f\u89c9\uff0c\u6709\u51e0\u5206\u719f\u6089\uff0c\u5fc3\u60f3\u5230\u697c\u4e0b\u7b49\u5510\u5a67\u3001\u675c\u98de\u4e5f\u662f\u4e00\u6837\uff0c\u4fbf\u8ddf\u7740\u4e0b\u4e86\u697c\u3002</p><p>\u3000\u3000\u4e09\u697c\u7684\u697c\u68af\u53e3\uff0c\u6324\u7740\u4e00\u5806\u4eba\uff0c\u4f9d\u7740\u680f\u6746\uff0c\u671d\u7740\u697c\u68af\u53e3\uff0c\u53e3\u773c\u6b6a\u659c\u7684\u4e71\u5439\u53e3\u54e8\uff0c\u5f20\u606a\u5728\u4e00\u4e2d\u6ca1\u5c11\u5e72\u8fc7\u8fd9\u4e8b\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u867d\u7136\u770b\u4e0d\u6e05\u5973\u5b69\u7684\u6b63\u8138\uff0c\u4f46\u662f\u8fd9\u4e48\u591a\u540c\u9053\u4e2d\u4eba\u80af\u5b9a\u7684\u76ee\u5149\u4e0e\u53e3\u54e8\uff0c\u8868\u660e\u8ddf\u7740\u4e0b\u697c\u662f\u4e2a\u4e0d\u9519\u7684\u51b3\u5b9a\u3002</p><p>\u3000\u3000\u4f46\u770b\u5230\u4e07\u5929\u624d\u90a3\u5f20\u4ee4\u4eba\u618e\u6076\u7684\u9762\u5b54\u5728\u4eba\u7fa4\u4e2d\u53e3\u54e8\u5439\u5f97\u6700\u6b22\uff0c\u5f20\u606a\u5fc3\u91cc\u5c31\u5341\u5206\u7684\u4e0d\u723d\uff0c\u81ea\u5df1\u4e0e\u675c\u98de\u8fd8\u597d\u6b79\u53c2\u52a0\u65b0\u751f\u73ed\u7684\u519b\u8bad\uff0c\u8fd9\u5c0f\u5b50\u76f4\u63a5\u8ddf\u9ad8\u5e74\u7ea7\u7684\u4eba\u6df7\u5230\u4e00\u8d77\uff0c\u5f20\u606a\u4e5f\u8ba4\u8bc6\u4ed6\u65c1\u8fb9\u7684\u5c0f\u4e2a\u5b50\u662f\u6d77\u5dde\u6700\u5927\u6c11\u8425\u96c6\u56e2\u9526\u57ce\u96c6\u56e2\u7684\u592a\u5b50\u7237\u8d75\u53f8\u660e\uff0c\u4ed6\u4eec\u4fe9\u4eba\u73b0\u5728\u6076\u540d\u8fd8\u4e0d\u5f70\u663e\uff0c\u5f20\u606a\u5374\u77e5\u9053\u4e24\u4eba\u7ee7\u7eed\u53d1\u5c55\u4e0b\u53bb\u4e00\u5b9a\u4f1a\u53d8\u6210\u4ee4\u4eba\u618e\u6068\u7684\u4eba\u6e23\u3002</p><p>\u3000\u3000\u5fc3\u60f3\u81ea\u5df1\u65e2\u7136\u56de\u5230\u4e5d\u56db\u5e74\uff0c\u5c31\u8981\u5728\u8fd9\u4e24\u4eba\u6210\u4e3a\u4eba\u6e23\u4e4b\u524d\u8ba9\u4ed6\u4eec\u53d8\u6210\u7070\u6e23\u5b50\u624d\u884c\u3002</p><p>\u3000\u3000\u524d\u9762\u90a3\u5973\u5b69\u5b50\u53d7\u4e0d\u4f4f\u6ee1\u773c\u6deb\u90aa\u7684\u76ee\u5149\uff0c\u628a\u5934\u57cb\u4e0b\u53bb\uff0c\u957f\u53d1\u4ece\u4e24\u8fb9\u5206\u5f00\uff0c\u9732\u51fa\u767d\u817b\u5982\u7389\u7684\u8116\u6897\uff0c\u60f3\u8d76\u5feb\u4ece\u4f17\u4eba\u6ce8\u89c6\u91cc\u6d88\u5931\u6389\uff0c\u811a\u4e0b\u4e00\u6025\uff0c\u6ed1\u4e0b\u4e00\u9636\u697c\u68af\uff0c\u5dee\u70b9\u5c31\u6454\u5012\uff0c\u5f15\u5f97\u697c\u68af\u53e3\u4e00\u7fa4\u8272\u72fc\u54c4\u5802\u5927\u7b11\u3002</p><p>\u3000\u3000\u201c\u9648\u5983\u84c9\uff0c\u8981\u4e0d\u8981\u54e5\u54e5\u6276\u4f60\u4e00\u628a\u2026\u2026\u201d\u8d75\u53f8\u660e\u6ee1\u8138\u6deb\u7b11\uff0c\u4ed6\u4e0d\u53bb\u7535\u89c6\u5267\u6f14\u53cd\u89d2\uff0c\u771f\u662f\u57cb\u6ca1\u4ed6\u7684\u4eba\u624d\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e00\u542c\u201c\u9648\u5983\u84c9\u201d\u8fd9\u540d\u5b57\uff0c\u4e5f\u4e0d\u987e\u5979\u5df2\u7ecf\u7ad9\u7a33\u7684\u4e8b\u5b9e\uff0c\u62a2\u4e86\u4e24\u6b65\u697c\u68af\uff0c\u6400\u4f4f\u5979\u7684\u624b\u81c2\uff0c\u5173\u5fc3\u7684\u95ee\uff1a\u201c\u4f60\u6ca1\u4e8b\u5427\uff1f\u201d</p><p>\u3000\u3000\u5973\u5b69\u4fa7\u5934\u65f6\uff0c\u5934\u53d1\u4fa7\u7740\u8138\u988a\u6ed1\u5230\u8033\u540e\uff0c\u9732\u51fa\u4e00\u5f20\u5982\u82b1\u4f3c\u7389\u7684\u5bb9\u989c\u6765\uff0c\u8ba9\u89c1\u8bc6\u8fc7\u8bb8\u601d\u90a3\u98a0\u5012\u4f17\u751f\u4e4b\u7f8e\u7684\u5f20\u606a\u4e5f\u5fc3\u9b42\u5fae\u8361\uff0c\u767d\u7699\u5982\u7389\u8272\u7409\u7483\u7684\u9762\u5bb9\u6e05\u4e3d\u8131\u4fd7\uff0c\u56e0\u521a\u521a\u7684\u60ca\u60f6\u800c\u9762\u5e26\u7f9e\u7ea2\uff0c\u6e05\u6f88\u5982\u77f3\u4e0a\u6e05\u6cc9\u4f3c\u7684\u773c\u7738\uff0c\u8ba9\u5f20\u606a\u5728\u6400\u4f4f\u5979\u7684\u4e00\u53ea\u624b\u81c2\u4e4b\u540e\uff0c\u5fcd\u4e0d\u4f4f\u53c8\u4f38\u624b\u63fd\u4f4f\u5979\u7684\u8170\uff0c\u8fd8\u4e0d\u95ee\u518d\u5173\u5207\u7684\u8bf4\u4e00\u58f0\uff1a\u201c\u8fd9\u91cc\u6709\u4e9b\u6ed1\uff0c\u5783\u573e\u592a\u591a\u7684\u7f18\u6545\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u6ca1\u4e8b\u2026\u2026\u201d\u9648\u5983\u84c9\u8eab\u5b50\u626d\u4e86\u626d\uff0c\u8981\u4ece\u5f20\u606a\u7684\u6000\u62b1\u91cc\u6323\u624e\u51fa\u6765\u3002</p><p>\u3000\u3000\u770b\u4e0a\u53bb\uff0c\u5f20\u606a\u53ea\u662f\u5728\u5979\u5dee\u70b9\u8dcc\u5012\u4e4b\u540e\u8d76\u8fc7\u6765\u76f8\u6276\uff0c\u867d\u7136\u665a\u4e86\u4e00\u70b9\uff0c\u4e5f\u6ca1\u6709\u7ed9\u4eba\u6545\u610f\u5360\u4fbf\u5b9c\u7684\u611f\u89c9\uff0c\u53ea\u662f\u7ed9\u4ed6\u4e00\u624b\u6400\u7740\u624b\u81c2\uff0c\u4e00\u624b\u63fd\u7740\u8170\uff0c\u8fd9\u4e2a\u59ff\u52bf\u592a\u66a7\u6627\u3002</p><p>\u3000\u3000\u201c\u54e5\u4eec\u513f\uff0c\u4f60\u8bf4\u8c01\u5783\u573e\u5462\uff1f\u201d\u8d75\u53f8\u660e\u89c1\u9648\u5983\u84c9\u4e00\u8138\u7f9e\u7ea2\u7684\u4f9d\u5728\u5f20\u606a\u7684\u6000\u91cc\uff0c\u770b\u7740\u8fd9\u4e2a\u4e0d\u8bc6\u62ac\u4e3e\u7684\u5bb6\u4f19\u63fd\u7740\u9648\u5983\u84c9\u7684\u8170\u8fd8\u4e0d\u820d\u5f97\u677e\u624b\uff0c\u201c\u4f60\u4ed6\u5988\u7684\u722a\u5b50\u6293\u54ea\u91cc\uff0c\u4f60\u77e5\u9053\u9648\u5983\u84c9\u662f\u8c01\u7684\u9a6c\u5b50\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u7406\u4ed6\uff0c\u773c\u775b\u76f4\u63a5\u5ffd\u89c6\u4e86\u4ed6\uff0c\u677e\u5f00\u9648\u5983\u84c9\u60ca\u4eba\u5f39\u6027\u7684\u5c0f\u8170\u86ee\u8170\uff0c\u77a5\u4e86\u65c1\u8fb9\u7684\u4e07\u5929\u624d\u4e00\u773c\uff0c\u6487\u5634\u7b11\u4e86\u7b11\uff1a\u201c\u4e07\u5929\u624d\uff0c\u4f60\u5c0f\u5b50\u600e\u4e48\u8fd9\u4e48\u6ca1\u51fa\u606f\uff0c\u5c3d\u627e\u4e9b\u4e0d\u5f00\u773c\u7684\u5783\u573e\u670b\u53cb\uff1f\u201d\u8d81\u7740\u8d75\u53f8\u660e\u53d1\u6123\uff0c\u7529\u624b\u62bd\u4e86\u4ed6\u4e00\u5df4\u638c\u3002\u4ed7\u7740\u6bd4\u8d75\u53f8\u660e\u9ad8\u534a\u5934\uff0c\u4e00\u628a\u6293\u4f4f\u4ed6\u7684\u9886\u53e3\uff0c\u5c06\u4ed6\u63d0\u6e9c\u5230\u8ddf\u524d\uff0c\u89c1\u4ed6\u8fd8\u6ca1\u6709\u4ece\u4e00\u5df4\u638c\u91cc\u6e05\u9192\u8fc7\u6765\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u5144\u5f1f\uff0c\u5c31\u8bf4\u4f60\u662f\u5783\u573e\uff0c\u5f00\u53e3\u5410\u810f\u5b57\u4e4b\u524d\u770b\u770b\u5bf9\u65b9\u662f\u8c01\uff0c\u8fd8\u6709\u544a\u8bc9\u4f60\u4e00\u58f0\uff0c\u9648\u5983\u84c9\u8c01\u7684\u9a6c\u5b50\u90fd\u4e0d\u662f\u3002\u201d</p><p>\u3000\u3000\u56f4\u5728\u697c\u68af\u53e3\u7684\u90a3\u7fa4\u4eba\u90fd\u662f\u8d75\u53f8\u660e\u7684\u72d0\u670b\u72d7\u53cb\uff0c\u89c1\u5f20\u606a\u4e00\u8a00\u4e0d\u5408\u5c31\u7529\u4e86\u8d75\u53f8\u660e\u4e00\u5df4\u638c\uff0c\u90fd\u8499\u4e86\uff0c\u89c1\u4ed6\u56a3\u5f20\u7684\u53e3\u6c14\uff0c\u4f3c\u4e4e\u8ba4\u8bc6\u4e07\u5929\u624d\uff0c\u90fd\u8f6c\u5934\u770b\u5411\u4e07\u5929\u624d\u3002</p><p>\u3000\u3000\u5f20\u606a\u529b\u6c14\u5927\uff0c\u8d75\u53f8\u660e\u8ba9\u4ed6\u6293\u5f97\u9886\u53e3\u53d1\u7d27\uff0c\u547c\u5438\u90fd\u56f0\u96be\uff0c\u4ed6\u5b8c\u5168\u6ca1\u6709\u60f3\u5230\u4e00\u4e2a\u521a\u521a\u5165\u5b66\u7684\u65b0\u751f\uff0c\u6562\u5f53\u7740\u4f17\u4eba\u6247\u81ea\u5df1\u7684\u8033\u5149\uff0c\u53ef\u601c\u5df4\u5df4\u7684\u770b\u7740\u4e07\u5929\u624d\u3002</p><p>\u3000\u3000\u5f20\u606a\u91cd\u65b0\u56de\u5230\u4e5d\u56db\u5e74\uff0c\u53ef\u4e0d\u60f3\u5728\u9ad8\u4e2d\u6821\u56ed\u505a\u4ec0\u4e48\u9738\u738b\uff0c\u90a3\u592a\u5e7c\u7a1a\u4e86\u3002\u5f20\u606a\u77e5\u9053\u4e07\u5929\u624d\u4e0e\u8d75\u53f8\u660e\u8fd9\u4e24\u4e2a\u4eba\u6e23\u5728\u63a5\u4e0b\u6765\u7684\u9ad8\u4e2d\u4e09\u5e74\u751f\u6daf\u91cc\uff0c\u4ed7\u7740\u8001\u5b50\u8001\u6743\u6709\u52bf\uff0c\u505a\u51fa\u5f88\u591a\u8ba9\u4eba\u6df1\u6076\u75db\u7edd\u7684\u6076\u884c\uff0c\u662f\u4ed6\u5988\u6807\u51c6\u7684\u4eba\u6e23\uff0c\u66fe\u5c06\u4e00\u4e2a\u4f4e\u5e74\u7ea7\u7684\u5973\u5b69\u5b50\u62c9\u5230\u5b66\u6821\u540e\u9762\u7684\u5c0f\u6811\u6797\u91cc\u7ed9\u8f6e\u5978\u4e86\uff0c\u4e8b\u60c5\u95f9\u51fa\u6765\uff0c\u4e24\u4eba\u4e5f\u53ea\u662f\u8f6c\u5b66\u800c\u5df2\u3002</p><p>\u3000\u3000\u5f20\u606a\u662f\u60f3\u4e00\u5f00\u59cb\u5c31\u5c06\u4ed6\u4eec\u7684\u56a3\u5f20\u6c14\u7130\u6253\u4e0b\u6765\uff0c\u8ba9\u4ed6\u4eec\u505a\u8d77\u6076\u4e8b\u6765\u4e0d\u81f3\u4e8e\u90a3\u4e48\u8086\u65e0\u5fcc\u60ee\u4e86\uff0c\u81f3\u5c11\u4e0d\u80fd\u8ba9\u5973\u5b69\u88ab\u62d6\u5230\u5c0f\u6811\u6797\u8f6e\u5978\u7684\u8fd9\u79cd\u6076\u884c\u53d1\u751f\u6d77\u5dde\u4e00\u4e2d\u7684\u6821\u56ed\u91cc\u3002</p><p>\u3000\u3000\u4e07\u5929\u624d\u8138\u4e0a\u6709\u4e9b\u6302\u4e0d\u4f4f\uff0c\u867d\u7136\u4ed6\u8001\u5b50\u8ba9\u4ed6\u8ddf\u5f20\u606a\u641e\u597d\u5173\u7cfb\uff0c\u53ef\u662f\u5f20\u606a\u53ea\u8ddf\u675c\u5c0f\u5c71\u7684\u513f\u5b50\u675c\u98de\u51d1\u4e00\u5806\uff0c\u5bf9\u81ea\u5df1\u7231\u7406\u4e0d\u7406\u7684\uff0c\u8fd9\u65f6\u5019\u660e\u77e5\u9053\u8d75\u53f8\u660e\u662f\u81ea\u5df1\u7684\u670b\u53cb\uff0c\u7529\u624b\u5c31\u662f\u4e00\u5df4\u638c\uff0c\u4e5f\u592a\u4e0d\u7ed9\u81ea\u5df1\u9762\u5b50\u4e86\uff0c\u4e0d\u5c31\u5f00\u53e3\u8bf4\u4e86\u4e00\u53e5\u810f\u8bdd\u5417\uff0c\u8fd9\u5e74\u5c81\uff0c\u8c01\u4ed6\u5988\u4e0d\u8bf4\u810f\u8bdd\uff1f</p><p>\u3000\u3000\u201c\u5f20\u606a\uff0c\u4f60\u4eec\u5728\u505a\u4ec0\u4e48\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u626d\u5934\u770b\u53bb\uff0c\u6697\u9a82\u4e00\u58f0\uff1a\u4e3a\u4e86\u538b\u4f4f\u4e07\u5929\u624d\u3001\u8d75\u53f8\u660e\u4ed6\u4eec\u7684\u6c14\u7130\uff0c\u81ea\u5df1\u5374\u6210\u4e86\u574f\u5b66\u751f\u3002\u4e25\u5389\u7684\u65ad\u559d\u4ece\u6559\u52a1\u5904\u4e3b\u4efb\u3001\u53f7\u79f0\u4e00\u4e2d\u7b2c\u4e00\u8273\u5987\u7684\u674e\u829d\u82b3\u5a07\u8273\u7684\u7ea2\u5507\u91cc\u53d1\u51fa\uff0c\u5979\u624b\u91cc\u62ff\u7740\u4e00\u53e0\u4e66\uff0c\u6b63\u62fe\u9636\u800c\u4e0b\uff0c\u675c\u98de\u8ddf\u5728\u540e\u9762\uff0c\u770b\u4ed6\u7684\u773c\u775b\uff0c\u5e94\u8be5\u76ef\u7740\u674e\u829d\u82b3\u4e30\u76c8\u633a\u7fd8\u7684\u81c2\u90e8\u4e0e\u4fee\u76f4\u957f\u817f\u3002</p><p>\u3000\u3000\u6bd4\u8d77\u9648\u5983\u84c9\u7684\u6e05\u4e3d\u8131\u4fd7\uff0c\u5bf9\u4e8e\u6027\u4e8b\u672a\u840c\u7684\u5c11\u5e74\u6765\u8bf4\uff0c\u5374\u662f\u674e\u829d\u82b3\u8fd9\u6837\u7684\u6210\u719f\u8273\u5987\u66f4\u6709\u5438\u5f15\u529b\uff0c\u7ea2\u6da6\u7684\u5634\u5507\uff0c\u5fae\u9686\u7684\u4e0b\u9894\uff0c\u4fee\u957f\u767d\u817b\u7684\u8116\u9888\uff0c\u9ad8\u633a\u7684\u80f8\u90e8\uff0c\u7ea4\u7ec6\u6709\u5f39\u529b\u7684\u8170\u80a2\uff0c\u5bbd\u539a\u6027\u611f\u7684\u81c0\u9acb\uff0c\u65e0\u4e00\u5904\u4e0d\u5177\u6709\u8bf1\u60d1\u529b\uff0c\u8fde\u9886\u53e3\u88ab\u5f20\u606a\u7d27\u7d27\u62fd\u4f4f\u7684\u8d75\u53f8\u660e\u90fd\u5fcd\u4e0d\u4f4f\u6765\u626d\u8fc7\u5934\u6765\u3002</p><p>\u3000\u3000\u201c\u674e\u8001\u5e08\uff0c\u8ddf\u4e07\u5929\u624d\u4ed6\u4eec\u5f00\u73a9\u7b11\u5462\u2026\u2026\u201d\u5f20\u606a\u7b11\u563b\u563b\u7684\u677e\u5f00\u53e3\uff0c\u5c06\u8d75\u53f8\u660e\u7ed9\u6293\u76b1\u7684\u9886\u53e3\u626f\u4e86\u626f\uff0c\u8ba9\u770b\u4e0a\u53bb\u5e73\u6574\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u674e\u829d\u82b3\u8d70\u4e0b\u6765\uff0c\u624d\u770b\u5230\u7ad9\u5728\u697c\u68af\u8f6c\u89d2\u4e0b\u7684\u9648\u5983\u84c9\uff0c\u72d0\u7591\u7684\u770b\u4e86\u5f20\u606a\u4e00\u773c\uff1a\u201c\u771f\u7684\u5728\u73a9\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u4e0d\u4fe1\u5c31\u95ee\u4e07\u5929\u624d\u3002\u201d\u5f20\u606a\u5fc3\u91cc\u6015\u8d75\u53f8\u660e\u6ca1\u79cd\u5c31\u627f\u8ba4\u88ab\u81ea\u5df1\u6253\u4e00\u5df4\u638c\uff0c\u5c06\u96be\u9898\u629b\u4e07\u5929\u624d\u5934\u4e0a\u3002</p><p>\u3000\u3000\u4e07\u5929\u624d\u9762\u5b50\u4e0a\u6302\u4e0d\u4f4f\uff0c\u626d\u5934\u770b\u5411\u680f\u6746\u5916\uff0c\u4e0d\u7406\u4f1a\u674e\u829d\u82b3\u3002</p><p>\u3000\u3000\u674e\u829d\u82b3\u5fc3\u77e5\u7ba1\u4e0d\u4e86\u8fd9\u4e9b\u9ad8\u5e72\u5b50\u5f1f\u3001\u5bcc\u5bb6\u5b50\u5f1f\uff0c\u6709\u4e9b\u7070\u5fc3\u4e27\u6c14\uff0c\u4e5f\u4e0d\u60f3\u591a\u7ba1\uff0c\u4e25\u5389\u7684\u8bf4\u9053\uff1a\u201c\u624d\u521a\u521a\u4e0b\u8bfe\uff0c\u4f60\u4eec\u600e\u4e48\u5c31\u90fd\u5835\u697c\u68af\u53e3\u6765\u4e86\uff0c\u5feb\u6563\u4e86\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u626d\u5934\u5c31\u4e0b\u697c\u68af\uff0c\u5230\u534a\u5c42\u4e4b\u95f4\u7684\u697c\u68af\u62d0\u89d2\uff0c\u56de\u5934\u770b\u4e86\u9648\u5983\u84c9\u4e00\u773c\uff1a\u201c\u4f60\u600e\u4e48\u4e0d\u8d70\u2026\u2026\u201d</p><p>\u3000\u3000\u9648\u5983\u84c9\u4e00\u6123\uff0c\u6307\u6307\u81ea\u5df1\u7684\u9f3b\u5b50\uff0c\u83ab\u540d\u5176\u5999\u7684\u5c31\u8ddf\u7740\u4e0b\u53bb\u3002\u5f20\u606a\u8fd8\u7a7f\u7740\u519b\u8bad\u670d\u88c5\uff0c\u4e00\u770b\u5c31\u77e5\u9053\u662f\u65b0\u751f\uff0c\u9648\u5983\u84c9\u8ddf\u5728\u4ed6\u540e\u9762\uff0c\u5fc3\u91cc\u6709\u4e9b\u5fd0\u5fd1\uff0c\u6015\u4ed6\u8ddf\u8d75\u53f8\u660e\u4e00\u6837\u7ea0\u7f20\u81ea\u5df1\uff0c\u60f3\u5230\u4ed6\u6247\u8d75\u53f8\u660e\u8033\u5149\u7684\u6837\u5b50\uff0c\u53c8\u4e0d\u6562\u5f00\u53e3\u8bf4\u4ec0\u4e48\u3002</p><p>\u3000\u3000\u51fa\u4e86\u697c\u68af\u53e3\uff0c\u5f20\u606a\u7b49\u675c\u98de\u3001\u5510\u5a67\u4e0b\u697c\uff0c\u7b49\u4e86\u4e00\u4f1a\u513f\uff0c\u5374\u89c1\u675c\u98de\u8fd9\u5c0f\u5b50\u5c3e\u968f\u674e\u829d\u82b3\u4ece\u53e6\u4e00\u7aef\u697c\u68af\u53e3\u51fa\u6765\uff0c\u5fcd\u4e0d\u4f4f\u53f9\u6c14\u7684\u6447\u6447\u5934\uff1a\u8fd9\u5c0f\u5b50\u4e0b\u534a\u8f88\u5b50\u6bc1\u5728\u674e\u829d\u82b3\u8eab\u4e0a\u4e5f\u662f\u6d3b\u8be5\u3002\u6ca1\u6709\u5f00\u53e3\u558a\u4ed6\uff0c\u89c1\u9648\u5983\u84c9\u7ad9\u81ea\u5df1\u8eab\u8fb9\uff0c\u95ee\u5979\uff1a\u201c\u4f60\u600e\u4e48\u4e0d\u8d70\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u4e0d\u662f\u627e\u6211\u6709\u4e8b\uff1f\u201d\u9648\u5983\u84c9\u6307\u6307\u81ea\u5df1\u7684\u9f3b\u5b50\uff0c\u624d\u53d1\u73b0\u8fd9\u4e2a\u574f\u7537\u5b69\u957f\u5f97\u771f\u82f1\u4fca\u3002</p><p>\u3000\u3000\u5f20\u606a\u5f20\u5f20\u5634\uff0c\u6ca1\u7b49\u4ed6\u8bf4\u4ec0\u4e48\uff0c\u201c\u54af\u54af\u54af\u201d\uff0c\u978b\u8ddf\u51fb\u5730\u7684\u58f0\u97f3\u5728\u5934\u9876\u54cd\u8d77\uff0c\u62ac\u5934\u770b\u89c1\u7d20\u82b1\u88d9\u5b50\u7684\u4e0b\u6446\u98d8\u8fdb\u89c6\u91ce\uff0c\u63a5\u7740\u662f\u5149\u6ed1\u767d\u5ae9\u7684\u5c0f\u817f\uff0c\u518d\u63a5\u7740\u5c31\u662f\u5510\u5a67\u7cbe\u81f4\u7684\u4fcf\u8138\u63a2\u51fa\u6765\uff1a\u201c\u5f20\u606a\uff0c\u4f60\u4e2a\u8272\u72fc\uff0c\u770b\u4ec0\u4e48\u770b\uff1f\u201d\u63d0\u7740\u88d9\u6446\uff0c\u5c0f\u7ffc\u5c06\u5c0f\u817f\u88f9\u4f4f\uff0c\u8d70\u4e86\u4e0b\u6765\u3002</p><p>\u3000\u3000\u5c31\u4e0b\u5348\u4e00\u4f1a\u513f\u5de5\u592b\uff0c\u5510\u5a67\u8fd8\u5f97\u610f\u628a\u519b\u8bad\u670d\u88c5\u6362\u6210\u957f\u88d9\uff0c\u81ed\u7f8e\u5f97\u5f88\uff0c\u76ae\u80a4\u7ed9\u592a\u9633\u6652\u6210\u871c\u814a\u8272\uff0c\u6709\u5065\u5eb7\u7684\u5149\u6cfd\uff0c\u8ba9\u5979\u770b\u8d77\u6765\u66f4\u52a0\u7684\u4fcf\u4e3d\uff0c\u7709\u773c\u7b11\u5a9a\u52a8\u4eba\u3002</p><p>\u3000\u3000\u5f20\u606a\u6487\u6487\u5634\uff0c\u7b11\u9053\uff1a\u201c\u6ca1\u80f8\u6ca1\u5c41\u80a1\u7684\uff0c\u6709\u4ec0\u4e48\u770b\u5934\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u8fd8\u6562\u8bf4\uff01\u201d\u5510\u5a67\u4e3e\u7740\u5370\u7740\u5361\u901a\u56fe\u6848\u7684\u900f\u660e\u94c5\u7b14\u888b\u5c31\u8981\u7838\u8fc7\u6765\uff0c\u770b\u89c1\u9648\u5983\u84c9\u7ad9\u5728\u4e00\u65c1\uff0c\u5fd9\u6062\u590d\u6dd1\u5973\u72b6\uff0c\u770b\u4e86\u770b\u9648\u5983\u84c9\uff0c\u4fa7\u8fc7\u8eab\u5b50\uff0c\u8f7b\u8f7b\u8e22\u4e86\u8e22\u5f20\u606a\uff1a\u201c\u4f60\u600e\u4e48\u8fd9\u4e48\u5feb\u5c31\u628a\u6211\u4eec\u4e00\u4e2d\u7684\u6821\u82b1\u52fe\u4e0a\u624b\u4e86\uff1f\u201d\u4e00\u76f4\u751f\u6d3b\u5355\u7eaf\u73af\u5883\u4e2d\u7684\u5510\u5a67\u5bf9\u7537\u5973\u4e4b\u60c5\u8fd8\u6ca1\u6709\u5b8c\u5168\u5f00\u7a8d\uff0c\u8bed\u6c14\u5374\u6709\u4e9b\u9178\u5473\u4e86\u3002</p><p>\u3000\u3000\u201c\u6211\u8ddf\u4ed6\u6ca1\u5173\u7cfb\uff0c\u201d\u9648\u5983\u84c9\u8fde\u5fd9\u6446\u624b\uff0c\u201c\u662f\u4ed6\u627e\u6211\u6709\u4e8b\uff0c\u6211\u624d\u7559\u4e0b\u6765\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u5510\u5a67\u521a\u8fdb\u5b66\u6821\u5f15\u8d77\u7684\u8f70\u52a8\uff0c\u53ef\u6bd4\u9648\u5983\u84c9\u5927\u591a\u4e86\uff0c\u5979\u7684\u9752\u6625\u79c0\u7f8e\u4e0d\u6bd4\u9648\u5983\u84c9\u7a0d\u5dee\uff0c\u4f46\u662f\u4e0e\u9648\u5983\u84c9\u5e73\u51e1\u7684\u51fa\u8eab\u4e0d\u540c\uff0c\u5510\u5a67\u7684\u7238\u7238\u662f\u6d77\u5dde\u5e02\u7684\u65b0\u4efb\u5e02\u957f\uff0c\u5728\u5979\u8fdb\u4e00\u4e2d\u4e4b\u524d\uff0c\u6709\u5173\u5979\u7684\u4f20\u95fb\u5c31\u4ece\u6d77\u5e08\u9644\u4e2d\u4f20\u5230\u4e00\u4e2d\uff0c\u5728\u5979\u8fdb\u4e86\u4e00\u4e2d\uff0c\u6821\u82b1\u7684\u8d35\u51a0\u5c31\u843d\u5230\u5979\u7684\u5934\u4e0a\u3002\u4f46\u662f\u663e\u8d6b\u7684\u8eab\u4e16\uff0c\u8ba9\u5979\u770b\u8d77\u6765\u9065\u4e0d\u53ef\u53ca\uff0c\u6ca1\u6709\u54ea\u4e2a\u65e0\u8d56\u5c11\u5e74\u6562\u53bb\u7ea0\u7f20\u5979\uff0c\u5728\u5979\u5fc3\u91cc\uff0c\u8fd8\u662f\u9648\u5983\u84c9\u662f\u8fd9\u6240\u5b66\u6821\u6700\u53d7\u6b22\u7684\u5973\u5b69\u5b50\u3002</p><p>\u3000\u3000\u5728\u8bb8\u601d\u7684\u7edd\u7f8e\u9762\u524d\uff0c\u6e05\u4e3d\u8131\u4fd7\u7684\u9648\u5983\u84c9\u5c31\u7a0d\u5acc\u6e05\u6da9\uff0c\u5c31\u50cf\u8fd8\u6ca1\u6709\u957f\u6210\u719f\u7684\u9752\u679c\u5b50\uff0c\u867d\u7136\u770b\u4e0a\u53bb\u8fd8\u6bd4\u8f83\u53ef\u53e3\u7684\u6837\u5b50\uff0c\u5f20\u606a\u5374\u6ca1\u6709\u592a\u5927\u7684\u5174\u8da3\u54ac\u4e00\u53e3\u3002\u5510\u5a67\u867d\u7136\u4e5f\u672a\u8131\u6e05\u6da9\uff0c\u4f46\u662f\u5728\u4eba\u751f\u8bfb\u6863\u4e4b\u524d\uff0c\u5f20\u606a\u5c31\u4e0e\u5510\u5a67\u6709\u7740\u65e0\u6570\u60c5\u611f\u4e0a\u7684\u7ea0\u845b\uff0c\u867d\u7136\u7edd\u5927\u90e8\u5206\u7684\u4e8b\u60c5\u4e0d\u4f1a\u518d\u53d1\u751f\uff0c\u4f46\u5374\u6df1\u6df1\u5370\u5728\u5f20\u606a\u7684\u7075\u9b42\u6df1\u5904\uff0c\u5f20\u606a\u5bf9\u5510\u5a67\u7684\u611f\u89c9\u8981\u590d\u6742\u4e00\u4e07\u500d\u3002\u9648\u5983\u84c9\u867d\u7136\u6e05\u6da9\uff0c\u4f46\u662f\u8fd9\u679a\u7a0d\u663e\u6e05\u6da9\u65e5\u540e\u5374\u4f1a\u52a8\u4eba\u5fc3\u5f26\u7684\u679c\u5b50\uff0c\u7edd\u4e0d\u80fd\u6bc1\u5728\u4e07\u5929\u624d\u3001\u8d75\u53f8\u660e\u8fd9\u4e24\u4e2a\u4eba\u6e23\u624b\u91cc\uff0c\u5f20\u606a\u644a\u644a\u624b\uff0c\u5bf9\u5979\u8bf4\uff1a\u201c\u6211\u521a\u521a\u5c31\u95ee\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u8d70\uff0c\u6ca1\u4ec0\u4e48\u4e8b\u627e\u4f60\uff0c\u4f60\u73b0\u5728\u4e3a\u4ec0\u4e48\u53c8\u4e0d\u8d70\uff1f\u201d</p><p>\u3000\u3000\u9648\u5983\u84c9\u6123\u4e86\u4e00\u6123\uff0c\u8ba9\u5f20\u606a\u8fd9\u4e48\u4e00\u8bf4\uff0c\u597d\u50cf\u81ea\u5df1\u5728\u7ea0\u7f20\u4ed6\u4f3c\u7684\uff0c\u7a81\u7136\u7f9e\u7ea2\u4e86\u8138\uff0c\u4e0d\u6562\u770b\u5f20\u606a\u3001\u5510\u5a67\uff0c\u5fc3\u91cc\u5bf9\u5f20\u606a\u83ab\u540d\u5176\u5999\u7684\u4e3e\u6b62\uff0c\u6709\u4e9b\u607c\u6012\uff0c\u5374\u4e0d\u6562\u6d41\u9732\u51fa\u6765\uff0c\u626d\u5934\u5c0f\u8dd1\u7684\u4e0b\u4e86\u53f0\u9636\uff0c\u5f80\u6821\u5916\u8d70\u53bb\uff1b</p><p>\u3000\u3000\u770b\u7740\u9648\u5983\u84c9\u7a81\u7136\u7f9e\u7ea2\u8138\u7684\u6837\u5b50\uff0c\u5f20\u606a\u5fc3\u91cc\u5012\u662f\u4e00\u52a8\uff0c\u6ca1\u60f3\u5230\u5c0f\u59ae\u5b50\u8fd8\u662f\u86ee\u6263\u4eba\u5fc3\u5f26\u7684\u3002</p><p>\u3000\u3000\u201c\u6ca1\u4ec0\u4e48\u4e8b\u662f\u4ec0\u4e48\u4e8b\uff1f\u201d\u5510\u5a67\u4e00\u8138\u72d0\u7591\uff0c\u201c\u4f60\u8981\u4e0d\u8bf4\uff0c\u6211\u5c31\u8ddf\u4f60\u5988\u8bf4\u4f60\u521a\u8fdb\u4e00\u4e2d\uff0c\u5c31\u70c2\u4ea4\u5973\u670b\u53cb\u3002\u201d</p><p>\u3000\u3000\u8fd9\u4f1a\u513f\u770b\u89c1\u675c\u98de\u53c8\u8d70\u56de\u6765\uff0c\u95ee\u4ed6\uff1a\u201c\u4f60\u9009\u4ec0\u4e48\u73ed\uff1f\u201d</p><p>\u3000\u3000\u201c\u770b\u60c5\u51b5\u518d\u8bf4\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u563f\u7136\u4e00\u7b11\uff0c\u8fd9\u5341\u591a\u5929\u7684\u65f6\u95f4\uff0c\u675c\u98de\u8ddf\u4ed6\u5173\u7cfb\u597d\u5230\u4e00\u8d77\u5bf9\u674e\u829d\u82b3\u7ed9\u7d27\u8eab\u725b\u4ed4\u88e4\u5305\u5f97\u6d51\u5706\u80a5\u7855\u7684\u5927\u5c41\u80a1\u4e0e\u957f\u817f\u8bc4\u5934\u8bba\u8db3\uff0c\u674e\u829d\u82b3\u521a\u521a\u9001\u8d70\u4e00\u5c4a\u6bd5\u4e1a\u751f\uff0c\u5979\u4f1a\u91cd\u65b0\u6311\u4e00\u4e2a\u9ad8\u4e00\u73ed\u6559\u8bed\u6587\uff0c\u675c\u98de\u662f\u60f3\u5148\u80e1\u4e71\u9009\u4e00\u4e2a\u73ed\uff0c\u7136\u540e\u518d\u8f6c\u5230\u674e\u829d\u82b3\u6559\u7684\u90a3\u4e2a\u73ed\u53bb\u3002</p><p>\u3000\u3000\u675c\u98de\u770b\u7740\u8fdc\u5904\u9648\u5983\u84c9\u8fdc\u53bb\u7684\u80cc\u5f71\uff0c\u5bf9\u5f20\u606a\u563f\u563f\u4e00\u7b11\uff1a\u201c\u4f60\u5c0f\u5b50\u771f\u591f\u72e0\u7684\uff0c\u8d75\u53f8\u660e\u7684\u8138\uff0c\u4f60\u771f\u6562\u4e0b\u624b\u6247\u554a\uff0c\u4e94\u9053\u9c9c\u7ea2\u7684\u624b\u5370\uff0c\u674e\u829d\u82b3\u6123\u662f\u5f53\u6ca1\u770b\u89c1\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4ec0\u4e48\uff0c\u4f60\u6253\u67b6\u2026\u2026\u201d\u5510\u5a67\u5403\u60ca\u7684\u770b\u7740\u5f20\u606a\uff0c\u4eff\u4f5b\u5f20\u606a\u505a\u4e86\u4e00\u4ef6\u4e0d\u53ef\u9976\u6055\u7684\u4e8b\u60c5\uff0c\u201c\u4f60\u600e\u4e48\u53ef\u4ee5\u8ddf\u4eba\u6253\u67b6\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u62cd\u62cd\u8111\u888b\uff0c\u56de\u5230\u4e5d\u56db\u5e74\uff0c\u8eab\u4f53\u867d\u7136\u662f\u5341\u516d\u5c81\u65f6\u7684\u8eab\u4f53\uff0c\u4f46\u662f\u8ddf\u5341\u516d\u5c81\u7684\u5c11\u5973\u7684\u786e\u65e0\u6cd5\u4ea4\u6d41\uff0c\u5510\u5a67\u4ece\u5c0f\u5c31\u53d7\u5f88\u6b63\u7edf\u7684\u6559\u80b2\uff0c\u4e0d\u50cf\u4e00\u822c\u5973\u5b69\u5b50\u770b\u5230\u7537\u5b69\u6253\u67b6\u5c31\u4f1a\u5174\u594b\u5f97\u5c16\u53eb\uff0c\u62c9\u8fc7\u5510\u5a67\u7684\u624b\uff1a\u201c\u6ca1\u4ec0\u4e48\u5927\u60ca\u5c0f\u602a\u7684\uff0c\u8fd9\u4f1a\u513f\u4eba\u591a\uff0c\u5230\u4e00\u8fb9\u8bf4\u7ed9\u4f60\u542c\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u8981\u4e0d\u8bf4\u6e05\u695a\uff0c\u6211\u5c31\u544a\u8bc9\u4f60\u5988\uff0c\u4f60\u5988\u5988\u8ba9\u6211\u770b\u7740\u4f60\u3002\u201d\u5510\u5a67\u4e0d\u60c5\u4e0d\u613f\u7684\u8ba9\u5f20\u606a\u7275\u7740\u624b\u8d70\uff0c\u8fd8\u4e0d\u5fd8\u5a01\u80c1\u4ed6\u3002</p><p>\u3000\u3000\u4e0b\u4e86\u53f0\u9636\uff0c\u5f20\u606a\u62ac\u5934\u770b\u4e86\u770b\uff0c\u4e07\u5929\u624d\u4e0e\u8d75\u53f8\u660e\u6b63\u63a2\u5934\u627e\u4ed6\u5462\uff0c\u770b\u4ed6\u7a81\u7136\u62ac\u5934\u770b\u8fc7\u53bb\uff0c\u614c\u4e71\u7684\u628a\u5934\u7f29\u56de\u53bb\u3002</p><p>\u3000\u3000\u201c\u4e09\u697c\u4e24\u4eba\u770b\u5230\u4e86\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u8c01\u554a\uff1f\u201d\u5510\u5a67\u6ca1\u770b\u6e05\u695a\uff0c\u770b\u6e05\u695a\u4e86\uff0c\u5979\u4e5f\u4e0d\u8ba4\u8bc6\u8d75\u53f8\u660e\u3002</p><p>\u3000\u3000\u201c\u4e24\u4e2a\u4eba\u6e23\uff0c\u201d\u5f20\u606a\u8f7b\u8511\u7684\u5410\u4e86\u4e00\u4e2a\u5b57\u773c\uff0c\u626d\u7740\u5bf9\u675c\u98de\u8bf4\uff0c\u201c\u675c\u98de\uff0c\u4f60\u8ddf\u4e07\u5929\u624d\u540c\u4e00\u6240\u521d\u4e2d\uff0c\u4ed6\u5728\u4f60\u4eec\u5b66\u6821\u505a\u7684\u90a3\u4e9b\u7834\u4e8b\uff0c\u8ddf\u5510\u5a67\u8bf4\u8bf4\u3002\u201d</p><p>\u3000\u3000\u201c\u8fd9\u79cd\u4eba\u6e23\uff0c\u61d2\u5f97\u7406\u4ed6\uff0c\u5e78\u4e8f\u521d\u4e2d\u4e0d\u662f\u8ddf\u6211\u4e00\u4e2a\u73ed\uff0c\u4e0d\u7136\u65e9\u6241\u5f97\u4ed6\u6ee1\u5730\u627e\u7259\uff0c\u8ddf\u4ed6\u540c\u6821\uff0c\u6211\u90fd\u89c9\u5f97\u803b\u8fb1\uff0c\u4ed7\u7740\u4ed6\u7237\u7237\u662f\u5e02\u59d4\u4e66\u8bb0\uff0c\u4ed6\u8001\u5b50\u662f\u5916\u7ecf\u59d4\u4e3b\u4efb\uff0c\u4ed6\u8fde\u4ed6\u7684\u51e0\u4f55\u8001\u5e08\u90fd\u6562\u8c03\u620f\uff0c\u8fd8\u5c06\u4ed6\u73ed\u4e0a\u4e00\u4e2a\u5973\u5b69\u5b50\u7684\u88e4\u5b50\u5f53\u7740\u5168\u73ed\u4eba\u7684\u9762\u6252\u4e0b\u6765\uff0c\u90a3\u5973\u5b69\u540e\u6765\u8f6c\u5b66\u4e86\uff0c\u4ed6\u5012\u4ec0\u4e48\u4e8b\u90fd\u6ca1\u6709\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u600e\u4e48\u53ef\u80fd\u6709\u8fd9\u79cd\u4eba\uff0c\u8001\u5e08\u4e0d\u7ba1\u5427\uff1f\u201d\u5510\u5a67\u54ac\u7259\u5207\u9f7f\uff0c\u89c9\u5f97\u6709\u4e9b\u4e0d\u53ef\u601d\u5f02\u3002</p><p>\u3000\u3000\u201c\u600e\u4e48\u7ba1\uff1f\u5f20\u606a\u521a\u521a\u6247\u8d75\u53f8\u660e\u4e00\u5df4\u638c\uff0c\u8138\u4e0a\u7559\u4e94\u9053\u7ea2\u5370\u5b50\uff0c\u674e\u829d\u82b3\u8001\u5e08\u8fd8\u4e0d\u662f\u88c5\u4f5c\u6ca1\u770b\u89c1\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u89c1\u675c\u98de\u5e74\u7eaa\u867d\u7136\u5c0f\uff0c\u53d7\u5bb6\u5ead\u7684\u5f71\u54cd\uff0c\u5bf9\u8fd9\u79cd\u4e8b\u5012\u4e5f\u770b\u5f97\u900f\u5f7b\u3002</p><p>\u3000\u3000\u201c\u5bf9\u4e86\uff0c\u4f60\u8fd8\u6ca1\u6709\u8bf4\u4e3a\u4ec0\u4e48\u8ddf\u522b\u4eba\u6253\u67b6\u5462\uff1f\u201d\u5510\u5a67\u5012\u4e0d\u5fd8\u7ea0\u6b63\u5f20\u606a\u7684\u884c\u4e3a\u3002</p><p>\u3000\u3000\u201c\u8d75\u53f8\u660e\u8fd9\u4eba\u6e23\uff0c\u6bd4\u4e07\u5929\u624d\u8fd8\u4eba\u6e23\uff0c\u6211\u8fdb\u4e00\u4e2d\u8fd9\u5341\u51e0\u5929\uff0c\u6ca1\u542c\u5230\u6709\u5173\u4ed6\u7684\u4e00\u53e5\u597d\u8bdd\u3002\u6076\u4eba\u8981\u6709\u6076\u4eba\u78e8\uff0c\u4e0d\u7528\u4e00\u5df4\u638c\u72e0\u72e0\u7684\u6247\u6389\u4ed6\u4eec\u7684\u6c14\u7130\uff0c\u4ed6\u4eec\u505a\u8d77\u574f\u4e8b\uff0c\u53ea\u4f1a\u66f4\u8086\u65e0\u5fcc\u60ee\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u521a\u521a\u9648\u5983\u84c9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u662f\u5e02\u957f\u5343\u91d1\u5927\u5c0f\u59d0\uff0c\u6ca1\u4eba\u6562\u6765\u7ea0\u7f20\u4f60\uff0c\u4eba\u5bb6\u9648\u5983\u84c9\u5c31\u4e0d\u540c\u4e86\uff0c\u6211\u6247\u4e86\u8d75\u53f8\u660e\u4e00\u5df4\u638c\uff0c\u53c8\u8ddf\u9648\u5983\u84c9\u4e00\u8d77\u4e0b\u697c\uff0c\u5c31\u662f\u4e3a\u4e86\u4e0d\u8ba9\u8d75\u53f8\u660e\u4ee5\u540e\u7ea0\u7f20\u5979\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u90a3\u4f60\u8fd8\u8bf4\u8ddf\u5979\u6ca1\u5173\u7cfb\uff1f\u201d</p><p>\u3000\u3000\u201c\u786e\u5b9e\u6ca1\u6709\u5173\u7cfb\uff0c\u201d\u5f20\u606a\u62cd\u62cd\u8111\u888b\uff0c\u201c\u53ea\u662f\u4e0d\u60f3\u770b\u4e07\u5929\u624d\u3001\u8d75\u53f8\u660e\u5728\u5b66\u6821\u91cc\u7cdf\u8e4b\u5973\u5b69\u5b50\uff0c\u5f97\uff0c\u4f60\u8981\u4e0d\u4fe1\uff0c\u4ee5\u540e\u8fd9\u4e8b\u5c31\u4ea4\u7ed9\u675c\u98de\u53bb\u505a\uff0c\u201d\u62cd\u4e86\u62cd\u675c\u98de\u7684\u80a9\u8180\uff0c\u201c\u8d75\u53f8\u660e\u4eca\u5929\u7ed9\u6211\u843d\u4e86\u9762\u5b50\uff0c\u4e00\u65f6\u6298\u817e\u4e0d\u8d77\u6765\uff0c\u4e07\u5929\u624d\u5c31\u4ea4\u7ed9\u4f60\u6536\u62fe\u3002\u201d</p><p>\u3000\u3000\u201c\u884c\uff0c\u65e9\u770b\u4ed6\u4e0d\u987a\u773c\u4e86\uff0c\u627e\u5230\u78b4\u513f\u518d\u8bf4\u3002\u201d\u675c\u98de\u6487\u6487\u5634\uff0c\u4e00\u4ed8\u6ee1\u4e0d\u5728\u4e4e\u7684\u795e\u60c5\u3002</p><p>\u3000\u3000\u4e09\u4eba\u5e76\u80a9\u5f80\u6821\u5916\u8d70\uff0c\u5510\u5a67\u7684\u5988\u5988\u987e\u5efa\u840d\u5728\u6821\u95e8\u53e3\u7b49\uff0c\u540e\u9762\u505c\u7740\u5e02\u653f\u5e9c\u7684\u8f66\uff0c\u60f3\u5fc5\u7279\u610f\u6765\u63a5\u5510\u5a67\u56de\u5bb6\u3002</p><p>\u3000\u3000\u5f20\u606a\u8981\u53bb\u79df\u7684\u623f\u95f4\u62ff\u6362\u6d17\u8863\u670d\u56de\u5bb6\uff0c\u5c31\u6ca1\u6709\u5750\u8fdb\u8f66\u53bb\uff0c\u8ba9\u5510\u5a67\u8ddf\u5979\u5988\u5988\u5148\u8d70\u3002</p><p>\u3000\u3000\u5f20\u606a\u5f00\u53e3\u60f3\u95ee\u675c\u98de\u53bb\u4e0d\u53bb\u4ed6\u90a3\u91cc\u73a9\u4e00\u4f1a\u513f\uff0c\u8f6c\u5934\u770b\u89c1\u8bb8\u601d\u98ce\u59ff\u7ef0\u7ea6\u7684\u7ad9\u5728\u8857\u5bf9\u9762\uff0c\u5fc3\u91cc\u83ab\u540d\u7684\u4e00\u8f6f\uff0c\u51e0\u4e4e\u4e0d\u4e89\u6c14\u7684\u8981\u843d\u4e0b\u6cea\u6765\uff0c\u5bf9\u675c\u98de\u8bf4\uff0c\u201c\u6211\u4e34\u65f6\u6709\u4e8b\uff0c\u660e\u5929\u518d\u8054\u7cfb\u3002\u201d\u4e22\u4e0b\u675c\u98de\uff0c\u5f80\u8bb8\u601d\u8dd1\u8fc7\u53bb\u3002</p></article>"}}}]);
