(window.webpackJsonp=window.webpackJsonp||[]).push([[466],{488:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e94\u5341\u516d\u8282 \u8981\u79bb\u5f00",content:"<article><h2>\u7b2c\u4e94\u5341\u516d\u8282 \u8981\u79bb\u5f00</h2><p>\u9ed1\u6697\u3002</p><p>\u65e0\u8fb9\u65e0\u9645\u7684\u9ed1\u6697\uff0c\u4eff\u4f5b\u6c89\u6c89\u591c\u5e55\u7b3c\u7f69\u4e16\u754c\uff0c\u743c\u6069\u72ec\u81ea\u7ad9\u7acb\uff0c\u832b\u7136\u56db\u987e\uff0c\u4e0d\u77e5\u81ea\u5df1\u8eab\u5728\u4f55\u65b9\u3002\u98ce\u7c0c\u7c0c\u5730\u522e\u8d77\uff0c\u900f\u9aa8\u6f88\u9ad3\uff0c\u4ee4\u4eba\u904d\u4f53\u751f\u5bd2\uff0c\u4ed6\u52aa\u529b\u601d\u7d22\u7740\uff0c\u60f3\u8981\u641e\u6e05\u695a\u81ea\u5df1\u7684\u5904\u5883\uff0c\u5374\u4ec0\u4e48\u90fd\u60f3\u4e0d\u51fa\uff0c\u4ec0\u4e48\u90fd\u8bb0\u4e0d\u8d77\uff0c\u610f\u8bc6\u4e2d\u4e00\u7247\u7a7a\u767d\u3002</p><p>\u7136\u540e\u4e00\u53ea\u89e6\u624b\u602a\u51fa\u73b0\u4e86\u3002</p><p>\u5706\u6eda\u6eda\u7684\u8eab\u8eaf\u5982\u7bee\u7403\u573a\u822c\u5e9e\u5927\uff0c\u6697\u84dd\u8272\u7684\u76ae\u80a4\u6cdb\u7740\u6bb7\u6bb7\u7684\u5149\u6cfd\uff0c\u56db\u53ea\u5c0f\u773c\u73e0\u4ece\u4e0a\u5230\u4e0b\u4e00\u5b57\u6392\u5f00\uff0c\u5495\u565c\u8f6c\u52a8\uff0c\u5341\u516d\u6839\u9ed1\u8272\u89e6\u624b\u6c14\u52bf\u6c79\u6c79\u5730\u4e0a\u4e0b\u6325\u821e\u7740\uff0c\u671d\u7740\u743c\u6069\u5feb\u901f\u79fb\u52a8\u8fc7\u6765\u3002\u743c\u6069\u5413\u4e86\u4e00\u8df3\uff0c\u672c\u80fd\u5730\u540e\u9000\uff0c\u8f6c\u8eab\u9003\u8dd1\uff0c\u53cc\u817f\u5374\u50cf\u662f\u704c\u4e86\u94c5\u4f3c\u7684\u51e0\u4e4e\u8fc8\u4e0d\u52a8\u811a\u6b65\u3002\u4ed6\u6c14\u5598\u5401\u5401\uff0c\u53ea\u542c\u5f97\u540e\u9762\u201c\u5693\u3001\u5693\u3001\u5693\u3001\u5693\u201d\u7684\u6469\u64e6\u58f0\u8d8a\u6765\u8d8a\u8fd1\uff0c\u8d8a\u6765\u8d8a\u6e05\u6670\uff0c\u60c5\u77e5\u662f\u90a3\u53ea\u89e6\u624b\u602a\u5df2\u7ecf\u903c\u8fd1\uff0c\u5fc3\u4e2d\u8d8a\u53d1\u60ca\u60f6\uff0c\u811a\u4e0b\u4e00\u7eca\u4fbf\u5411\u524d\u8dcc\u5012\uff0c\u773c\u770b\u5c31\u8981\u6454\u8db4\u4e0b\uff0c\u5374\u88ab\u4e00\u53cc\u7ea4\u7ea4\u7389\u624b\u6276\u4f4f\u4e86\u3002\u4ed6\u62ac\u8d77\u8138\uff0c\u6b63\u89c1\u5c11\u5973\u7684\u6e29\u5a49\u7b11\u5bb9\u3002</p><p>\u201c\u59d0\u59d0\uff0c\u5feb\u8dd1\uff01\u201d</p><p>\u743c\u6069\u987e\u4e0d\u4e0a\u89e3\u91ca\uff0c\u5306\u5fd9\u6293\u4f4f\u5c11\u5973\u7684\u624b\uff0c\u8981\u62c9\u5979\u4e00\u8d77\u9003\u8dd1\u3002\u5c11\u5973\u5fae\u5fae\u7b11\u7740\uff0c\u8f7b\u8f7b\u6323\u8131\u4e86\uff0c\u201c\u6ca1\u4e8b\u7684\uff0c\u5c0f\u5f1f\uff0c\u522b\u5bb3\u6015\uff0c\u201d\u5979\u67d4\u58f0\u8bf4\uff0c\u201c\u6709\u59d0\u59d0\u5728\u5462\u3002\u201d</p><p>\u201c\u53ef\u662f\u2026\u2026\u201d</p><p>\u201c\u5618\uff01\u201d</p><p>\u73ca\u5609\u7ad6\u8d77\u624b\u6307\uff0c\u963b\u6b62\u4e86\u743c\u6069\u60f3\u8bf4\u7684\u8bdd\uff0c\u5149\u8292\u5728\u5979\u7684\u638c\u5fc3\u6eda\u52a8\u6d41\u6ea2\uff0c\u6c47\u805a\u6210\u94f6\u8272\u7684\u91d1\u5c5e\u957f\u7b1b\uff0c\u5979\u770b\u4e86\u773c\u7d27\u8ffd\u8fc7\u6765\u7684\u89e6\u624b\u602a\uff0c\u5c06\u957f\u7b1b\u8d34\u5728\u5507\u8fb9\u5f00\u59cb\u5439\u594f\u3002</p><p>\u7480\u74a8\u7684\u661f\u8fb0\u5728\u591c\u5e55\u4e2d\u51fa\u73b0\uff0c\u95ea\u70c1\u751f\u8f89\uff0c\u7136\u540e\u7eb7\u7eb7\u5760\u843d\uff0c\u707f\u70c2\u5982\u96e8\u3002\u5c11\u5973\u6a2a\u5439\u94f6\u7b1b\uff0c\u5728\u8fd9\u661f\u8fb0\u5149\u96e8\u4e2d\u4fcf\u751f\u751f\u5730\u7ad9\u7acb\u7740\uff0c\u4e03\u9053\u534e\u5f69\u675f\u5e26\u73af\u7ed5\u8eab\u5468\uff0c\u9ed1\u53d1\u5982\u7011\uff0c\u7d2b\u88d9\u98d8\u98d8\uff0c\u7f8e\u4e3d\u7684\u9762\u5bb9\u4e0a\u5149\u534e\u83b9\u6da6\uff0c\u604d\u7136\u4eff\u4f5b\u795e\u660e\u3002\u89e6\u624b\u602a\u60e8\u568e\u7740\uff0c\u5168\u8eab\u5230\u5904\u5192\u51fa\u9752\u70df\uff0c\u5f88\u5feb\u4fbf\u88ab\u6d88\u706d\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u534a\u70b9\u75d5\u8ff9\u4e0d\u7559\u3002</p><p>\u201c\u59d0\u59d0\uff0c\u597d\u5389\u5bb3\u2026\u2026\u201d</p><p>\u743c\u6069\u770b\u5f97\u76ee\u77aa\u53e3\u5446\uff0c\u534a\u664c\u624d\u53cd\u5e94\u8fc7\u6765\uff0c\u4ed6\u7531\u8877\u5730\u8d5e\u53f9\u7740\uff0c\u751a\u81f3\u5185\u5fc3\u90fd\u9690\u9690\u6709\u4e9b\u5ac9\u5992\u3002\u8fd9\u6837\u6c14\u52bf\u6062\u5b8f\u7684\u9b54\u6cd5\uff0c\u5df2\u7ecf\u5b8c\u5168\u8d85\u51fa\u4e86\u5e38\u8bc6\u7684\u8303\u7574\uff0c\u53ea\u80fd\u7528\u5947\u8ff9\u6765\u5f62\u5bb9\u4e86\uff0c\u59d0\u59d0\u662f\u4ec0\u4e48\u65f6\u5019\u53d8\u5f97\u5982\u6b64\u5389\u5bb3\u4e86\u5462\uff0c\u81ea\u5df1\u90fd\u4eff\u4f5b\u4e0d\u8ba4\u8bc6\u4e86\u4f3c\u7684\u3002</p><p>\u7b1b\u58f0\u6d88\u5931\u4e86\uff0c\u661f\u5149\u4e5f\u968f\u4e4b\u9eef\u6de1\uff0c\u5c11\u5973\u4f9d\u65e7\u5fae\u5fae\u7b11\u7740\uff0c\u743c\u6069\u6b63\u60f3\u4f38\u624b\u53bb\u62b1\u5979\uff0c\u604d\u60da\u4e4b\u95f4\uff0c\u5979\u7684\u9762\u5bb9\u7a81\u7136\u6539\u53d8\u4e86\uff0c\u8f6e\u5ed3\u66f4\u52a0\u5706\u6da6\u67d4\u548c\uff0c\u66f4\u52a0\u6210\u719f\uff0c\u9ed1\u8272\u7684\u77b3\u5b54\u4e5f\u53d8\u6210\u4e86\u7d2b\u8272\uff0c\u95ea\u70c1\u7740\u6444\u4eba\u5fc3\u9b42\u7684\u7f8e\u4e3d\uff0c\u5374\u8ba9\u743c\u6069\u7684\u5fc3\u9aa4\u7136\u5982\u5760\u51b0\u7a96\u3002</p><p>\u201c\u4f60\u2026\u2026\u201d</p><p>\u201c\u55ef\uff0c\u4e0d\u8ba4\u8bc6\u4e86\u5417\uff1f\u4e0a\u6b21\u5728\u795e\u6bbf\u91cc\uff0c\u4f60\u89c1\u8fc7\u6211\u7684\u96d5\u50cf\u5427\uff0c\u201d\u5c11\u5973\u2014\u2014\u6216\u8005\u8bf4\u5973\u5b50\u7b11\u610f\u76c8\u76c8\u5730\u8bf4\uff0c\u201c\u6211\u662f\u963f\u62c9\u838e\xb7\u5766\u8212\u5c14\uff0c\u5f88\u9ad8\u5174\u8ba4\u8bc6\u4f60\u5462\u3002\u201d</p><p>\u201c\u53ef\u662f\uff0c\u59d0\u59d0\u2026\u2026\u201d</p><p>\u201c\u59d0\u59d0\u5df2\u7ecf\u4e0d\u5b58\u5728\u4e86\u554a\uff0c\u201d\u5973\u5b50\u5fae\u7b11\u7740\u56de\u7b54\uff0c\u201c\u4ece\u6b64\u4ee5\u540e\uff0c\u4e16\u754c\u4e0a\u5c31\u6ca1\u6709\u73ca\u5609\uff0c\u53ea\u6709\u963f\u62c9\u838e\u3002\u201d</p><p>\u201c\u80e1\u8bf4\u516b\u9053\uff01\u201d\u743c\u6069\u6012\u543c\u8d77\u6765\uff0c\u201c\u59d0\u59d0\u5c31\u662f\u59d0\u59d0\uff0c\u73ca\u5609\u5c31\u662f\u73ca\u5609\uff0c\u624d\u4e0d\u662f\u4ec0\u4e48\u738b\u540e\uff0c\u4e0d\u662f\u4ec0\u4e48\u89c1\u9b3c\u7684\u963f\u62c9\u838e\u2014\u2014\u201d</p><p>\u5973\u5b50\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u8eab\u5f71\u7f13\u7f13\u6de1\u53bb\uff0c\u6d88\u5931\u5728\u9ed1\u6697\u4e2d\u3002\u743c\u6069\u8d76\u4e0a\u6765\uff0c\u60f3\u8981\u5c06\u5979\u6293\u4f4f\uff0c\u5374\u5df2\u7ecf\u6251\u4e86\u4e2a\u7a7a\u3002\u4ed6\u5598\u606f\u7740\uff0c\u53ea\u89c9\u96be\u4ee5\u5f62\u5bb9\u7684\u6012\u6c14\u81ea\u5fc3\u5e95\u52c3\u53d1\u6d8c\u8d77\uff0c\u7b80\u76f4\u50cf\u70c8\u706b\u822c\u718a\u718a\u71c3\u70e7\uff0c\u8981\u5c06\u4e00\u5207\u788d\u773c\u7684\u4e1c\u897f\u711a\u70e7\u6b86\u5c3d\uff0c\u7136\u800c\u89e6\u76ee\u6240\u53ca\uff0c\u5c3d\u662f\u832b\u832b\u865a\u7a7a\uff0c\u6df7\u6c8c\u9ed1\u6697\uff0c\u8fde\u4e2a\u53d1\u6cc4\u7684\u5bf9\u8c61\u90fd\u6ca1\u6709\u3002</p><p>\u201c\u547c\uff01\u201d</p><p>\u4ed6\u70e6\u8e81\u5730\u5410\u4e86\u53e3\u6c14\uff0c\u8111\u4e2d\u6df7\u4e71\u65e0\u6bd4\uff0c\u800c\u4e14\u8fd8\u5728\u9690\u9690\u4f5c\u75bc\u3002\u6b63\u90c1\u95f7\u95f4\uff0c\u731b\u7136\u811a\u4e0b\u5267\u70c8\u9707\u52a8\uff0c\u5468\u56f4\u7684\u7a7a\u95f4\u50cf\u88ab\u6572\u788e\u7684\u73bb\u7483\u4e00\u822c\u788e\u88c2\u3001\u584c\u9677\uff0c\u5206\u5d29\u79bb\u6790\uff0c\u4ed6\u7684\u8eab\u4f53\u6b62\u4e0d\u4f4f\u5730\u4e0b\u5760\uff0c\u4e0b\u5760\uff0c\u901f\u5ea6\u8d8a\u6765\u8d8a\u5feb\uff0c\u6700\u7ec8\u4ee4\u4ed6\u6655\u7729\u8fc7\u53bb\u3002</p><p>\u6d51\u6d51\u5669\u5669\u4e2d\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u4eff\u4f5b\u662f\u6f2b\u957f\u5c81\u6708\uff0c\u4e5f\u4eff\u4f5b\u53ea\u662f\u77ed\u6682\u77ac\u95f4\uff0c\u610f\u8bc6\u6084\u65e0\u58f0\u606f\u5730\u8fd4\u8fd8\u743c\u6069\u7684\u8eab\u4f53\uff0c\u8ba9\u4ed6\u518d\u5ea6\u611f\u89c9\u5230\u81ea\u5df1\u7684\u201c\u5b58\u5728\u201d\u3002\u968f\u4e4b\u800c\u6765\u7684\u4fbf\u662f\u4ee4\u4eba\u7a92\u606f\u7684\u5267\u75db\uff0c\u4eff\u4f5b\u6f6e\u6c34\u822c\u6c79\u6d8c\u88ad\u81f3\uff0c\u5168\u8eab\u4e0a\u4e0b\u50cf\u662f\u88ab\u51e0\u767e\u5934\u7280\u725b\u6216\u8005\u5927\u8c61\u8f6e\u756a\u8e29\u8e0f\u8fc7\uff0c\u6bcf\u4e00\u5904\u6bcf\u4e00\u5bf8\u90fd\u6210\u4e86\u788e\u6e23\u822c\uff0c\u90a3\u79cd\u611f\u89c9\u5b9e\u975e\u4eba\u7c7b\u7684\u8bed\u8a00\u6240\u80fd\u63cf\u7ed8\u5f62\u5bb9\uff0c\u8db3\u4ee5\u8ba9\u6700\u575a\u5f3a\u7684\u4eba\u5d29\u6e83\u3002\u743c\u6069\u4e0d\u7531\u81ea\u4e3b\u5730\u54ac\u7d27\u7259\u5173\uff0c\u5267\u70c8\u98a4\u6296\u8d77\u6765\uff0c\u77ed\u77ed\u534a\u79d2\u949f\u91cc\u5168\u8eab\u5df2\u7ecf\u88ab\u51b7\u6c57\u6d78\u900f\u3002</p><p>\u6b63\u5f53\u4ed6\u7684\u795e\u667a\u518d\u5ea6\u604d\u60da\uff0c\u4ee5\u4e3a\u81ea\u5df1\u4f1a\u6d3b\u751f\u751f\u75db\u6b7b\u7684\u5173\u5934\uff0c\u6709\u67d0\u4e2a\u51b0\u51c9\u575a\u786c\u7684\u4e1c\u897f\uff0c\u8868\u9762\u5149\u6ed1\uff0c\u50cf\u662f\u4e00\u9897\u73e0\u5b50\uff0c\u8f7b\u8f7b\u62b5\u5728\u7709\u5fc3\u4e0a\uff0c\u7136\u540e\u201c\u878d\u201d\u4e86\u8fdb\u6765\u3002\u7d27\u63a5\u7740\uff0c\u4e00\u80a1\u6696\u6d41\u81ea\u7709\u5fc3\u5904\u6d8c\u51fa\uff0c\u6e38\u8f6c\u5168\u8eab\uff0c\u743c\u6069\u611f\u89c9\u5230\u81ea\u5df1\u4eff\u4f5b\u88ab\u548c\u7166\u7684\u9633\u5149\u7167\u5c04\uff0c\u75bc\u75db\u5982\u79ef\u96ea\u822c\u6025\u901f\u6eb6\u5316\u3001\u6d88\u9000\uff0c\u5239\u90a3\u95f4\u6d88\u5931\u5f97\u65e0\u5f71\u65e0\u8e2a\u3002</p><p>\u5f97\u6551\u4e86\u3002</p><p>\u743c\u6069\u677e\u4e86\u53e3\u6c14\uff0c\u7741\u5f00\u773c\u775b\uff0c\u9996\u5148\u6620\u5165\u773c\u5e18\u7684\u662f\u4e00\u5f20\u51b7\u51b0\u51b0\u7684\u8138\uff0c\u4ee5\u53ca\u7d2b\u8272\u53cc\u7738\uff0c\u5012\u662f\u628a\u4ed6\u5413\u4e86\u4e00\u8df3\uff0c\u201c\u5927\u5965\u672f\u5e08\u9601\u4e0b\uff1f\u201d</p><p>\u7ad9\u5728\u4ed6\u65c1\u8fb9\u7684\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u661f\u9668\u57ce\u7684\u57ce\u4e3b\u5965\u5609\u83b1\u65af\u5973\u58eb\uff0c\u5979\u9762\u65e0\u8868\u60c5\u5730\u5c06\u624b\u6307\u4ece\u743c\u6069\u7684\u7709\u5fc3\u79fb\u5f00\uff0c\u5c45\u9ad8\u4e34\u4e0b\u5730\u4fef\u89c6\u7740\u4ed6\u3002\u5979\u662f\u4e2a\u5e7d\u7075\uff0c\u53ea\u6709\u865a\u50cf\uff0c\u6ca1\u6709\u5b9e\u4f53\uff0c\u4f46\u7ad9\u5728\u90a3\u91cc\u5374\u538b\u8feb\u611f\u5341\u8db3\uff0c\u4ee4\u4eba\u65e0\u6cd5\u5ffd\u7565\uff0c\u65e0\u6cd5\u8f7b\u89c6\u3002\u743c\u6069\u5b9a\u4e86\u5b9a\u795e\uff0c\u9759\u9759\u95ed\u5634\u7b49\u5f85\u7740\uff0c\u51c6\u5907\u8046\u542c\u5927\u5965\u672f\u5e08\u7684\u8bad\u793a\u3002\u8ba9\u4ed6\u5fd0\u5fd1\u4e0d\u5b89\u7684\u662f\uff0c\u5965\u5609\u83b1\u65af\u5973\u58eb\u7684\u8138\u8272\u4f3c\u4e4e\u4e0d\u662f\u5f88\u53cb\u5584\u2026\u2026</p><p>\u5e78\u597d\uff0c\u5965\u5609\u83b1\u65af\u5e76\u6ca1\u6709\u548c\u743c\u6069\u591a\u505a\u4ea4\u6d41\u7684\u610f\u601d\uff0c\u5979\u5728\u743c\u6069\u8138\u4e0a\u89c2\u5bdf\u4e86\u51e0\u79d2\u79cd\uff0c\u5fae\u5fae\u70b9\u5934\uff0c\u7136\u540e\u4fbf\u8f6c\u8eab\u671d\u95e8\u5916\u8d70\u53bb\u3002\u201c\u836f\u5df2\u7ecf\u7ed9\u4f60\u4e86\uff0c\u5341\u5206\u949f\u540e\u8ba9\u4ed6\u559d\u4e00\u534a\uff0c\u4e00\u5c0f\u65f6\u540e\u518d\u559d\u5269\u4e0b\u4e00\u534a\u3002\u7136\u540e\u62ff\u9524\u5b50\u5728\u4ed6\u8111\u888b\u4e0a\u7528\u529b\u6572\u4e00\u4e0b\uff0c\u8ba9\u4ed6\u7761\u4e00\u89c9\u5c31\u597d\u4e86\u3002\u8bb0\u4f4f\u6211\u7684\u8bdd\uff0c\u5c0f\u4e2b\u5934\u3002\u201d</p><p>\u201c\u597d\u7684\uff0c\u554a\uff1f\u9524\u5b50\u2026\u2026\u54e6\uff0c\u8c22\u8c22\u60a8\u3002\u201d</p><p>\u8bed\u65e0\u4f26\u6b21\u7684\u9053\u8c22\uff0c\u6b63\u662f\u8299\u857e\u72c4\u7684\u5a07\u602f\u751c\u7f8e\u7684\u55d3\u97f3\uff0c\u5979\u521a\u624d\u7ad9\u5728\u5965\u5609\u83b1\u65af\u7684\u4fa7\u540e\u65b9\uff0c\u8ddd\u79bb\u7a0d\u8fdc\uff0c\u53c8\u88ab\u906e\u6321\uff0c\u6240\u4ee5\u743c\u6069\u4e0d\u66fe\u770b\u89c1\u5979\u3002\u5965\u5609\u83b1\u65af\u79bb\u5f00\u540e\uff0c\u8299\u857e\u72c4\u5173\u4e0a\u623f\u95e8\uff0c\u5750\u5728\u5e8a\u8fb9\u770b\u7740\u743c\u6069\u3002\u201c\u600e\u4e48\u6837\uff1f\u201d\u5979\u5173\u5207\u5730\u95ee\uff0c\u201c\u8eab\u4e0a\u8fd8\u75db\u5417\uff1f\u201d</p><p>\u743c\u6069\u770b\u770b\u56db\u5468\uff0c\u53d1\u73b0\u81ea\u5df1\u8eab\u5904\u4e00\u4e2a\u5f88\u5c0f\u7684\u623f\u95f4\u91cc\uff0c\u50cf\u662f\u5367\u5ba4\u3002\u4ed6\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u52a8\u4e86\u52a8\u80f3\u818a\uff0c\u53c8\u6162\u6162\u626d\u4e86\u626d\u8116\u9888\uff0c\u7136\u540e\u7d22\u6027\u8df3\u4e0b\u5e8a\uff0c\u6d3b\u52a8\u4e86\u51e0\u4e0b\uff0c\u4ed6\u53ea\u89c9\u81ea\u5df1\u6b64\u523b\u4e0d\u4f46\u795e\u6e05\u6c14\u723d\uff0c\u800c\u4e14\u56db\u80a2\u767e\u9ab8\u4e2d\u90fd\u6ee1\u8574\u7740\u5145\u6c9b\u6d3b\u529b\uff0c\u65e0\u8bba\u7cbe\u795e\u8fd8\u662f\u8eab\u4f53\u72b6\u6001\u90fd\u662f\u524d\u6240\u672a\u6709\u5730\u597d\uff0c\u7b80\u76f4\u53ef\u4ee5\u7528\u201c\u5b8c\u7f8e\u201d\u6765\u5f62\u5bb9\u3002\u201c\u4e0d\u9519\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6211\u73b0\u5728\u611f\u89c9\u597d\u6781\u4e86\u3002\u201d</p><p>\u201c\u771f\u7684\uff0c\u592a\u597d\u4e86\uff0c\u201d\u8299\u857e\u72c4\u4e5f\u9ad8\u5174\u8d77\u6765\uff0c\u201c\u6211\u8fd8\u4e00\u76f4\u5728\u62c5\u5fc3\u2026\u2026\u201d</p><p>\u201c\u6211\u59d0\u59d0\u5462\uff1f\u201d\u743c\u6069\u6253\u65ad\u4e86\u5979\u7684\u8bdd\uff0c\u4ed6\u62b1\u6b49\u5730\u7b11\u7b11\uff0c\u201c\u5979\u600e\u4e48\u6837\u4e86\uff1f\u201d</p><p>\u201c\u554a\uff0c\u73ca\u5609\u59d0\u59d0\u6ca1\u4e8b\uff0c\u4f60\u522b\u62c5\u5fc3\uff0c\u201d\u8299\u857e\u72c4\u8bf4\uff0c\u201c\u5979\u4e00\u76f4\u7761\u7740\uff0c\u5965\u5609\u83b1\u65af\u5973\u58eb\u8bf4\u5979\u592a\u7d2f\u4e86\uff0c\u9700\u8981\u4f11\u606f\uff0c\u5927\u6982\u660e\u5929\u65e9\u4e0a\u5c31\u80fd\u591f\u9192\u8fc7\u6765\u3002\u201d</p><p>\u743c\u6069\u7a0d\u7a0d\u677e\u4e86\u53e3\u6c14\uff0c\u201c\u5979\u5728\u54ea\u91cc\uff0c\u6211\u53bb\u2026\u2026\u201d</p><p>\u8bdd\u97f3\u672a\u843d\uff0c\u4e00\u9635\u731b\u70c8\u7684\u9707\u52a8\u7a81\u7136\u88ad\u6765\uff0c\u8299\u857e\u72c4\u5750\u5728\u5e8a\u8fb9\uff0c\u5012\u8fd8\u7f62\u4e86\uff0c\u743c\u6069\u56e0\u4e3a\u662f\u7ad9\u7740\u7684\uff0c\u731d\u4e0d\u53ca\u9632\u4e4b\u4e0b\uff0c\u9669\u4e9b\u5931\u53bb\u91cd\u5fc3\u6454\u5012\u3002\u201c\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u4ed6\u60ca\u95ee\u3002</p><p>\u201c\u62c9\u52a0\u5148\u751f\u4ed6\u4eec\u6b63\u5728\u8c03\u8bd5\u5bc6\u745f\u80fd\u6838\uff0c\u6253\u7b97\u8ba9\u57ce\u5e02\u6d6e\u51fa\u6d77\u9762\uff0c\u4f46\u597d\u50cf\u8fdb\u5c55\u4e0d\u592a\u987a\u5229\uff0c\u201d\u8299\u857e\u72c4\u8bf4\uff0c\u201c\u5965\u5609\u83b1\u65af\u5973\u58eb\u8b66\u544a\u8bf4\u5728\u6b64\u671f\u95f4\u5927\u5bb6\u90fd\u5446\u5728\u623f\u95f4\u91cc\uff0c\u4e0d\u8981\u5728\u5916\u9762\u8d70\u52a8\uff0c\u5426\u5219\u5f88\u5bb9\u6613\u51fa\u4e8b\u7684\u3002\u201d</p><p>\u201c\u5514\uff0c\u8fd9\u6837\u554a\u3002\u201d</p><p>\u743c\u6069\u4e0d\u60a6\u5730\u6447\u6447\u5934\uff0c\u4f46\u653e\u5f03\u4e86\u7acb\u523b\u51fa\u95e8\u53bb\u627e\u73ca\u5609\u7684\u5ff5\u5934\uff0c\u65e2\u7136\u5728\u522b\u4eba\u7684\u5730\u76d8\u4e0a\uff0c\u4ed6\u4e0d\u6253\u7b97\u5192\u9669\u3002\u521a\u624d\u7684\u68a6\u5883\u4ed6\u8bb0\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u73ca\u5609\u7a81\u7136\u53d8\u6210\u4e86\u963f\u62c9\u838e\uff0c\u8fd9\u8ba9\u4ed6\u5fc3\u60c5\u975e\u5e38\u4e4b\u7cdf\u7cd5\uff0c\u751a\u81f3\u6709\u4e9b\u5fd0\u5fd1\u4e0d\u5b89\u3002\u4e0d\u8fc7\u8bf4\u5230\u5e95\uff0c\u7ec8\u7a76\u4e5f\u53ea\u662f\u4e2a\u68a6\u7f62\u4e86\uff0c\u6ca1\u5fc5\u8981\u592a\u5f53\u771f\uff0c\u81ea\u5df1\u8fd8\u66fe\u7ecf\u68a6\u89c1\u8fc7\u5efa\u7acb\u8d77\u4e86\u6d6e\u7a7a\u57ce\uff0c\u628a\u6b23\u5e03\u548c\u8475\u9732\u90fd\u63a8\u5012\u4e86\u5462\uff0c\u60f3\u60f3\u66f4\u662f\u8352\u8bde\u65e0\u7a3d\u4e86\u3002</p><p>\u201c\u4f60\u59d0\u59d0\u5462\uff1f\u838e\u73de\u514b\u5462\uff1f\u201d\u4ed6\u95ee\uff0c\u201c\u90fd\u6ca1\u4e8b\u5427\u3002\u201d</p><p>\u201c\u90fd\u8fd8\u597d\u3002\u201d</p><p>\u53d8\u6210\u602a\u517d\u4e4b\u540e\uff0c\u743c\u6069\u7684\u795e\u667a\u5c31\u5df2\u7ecf\u57fa\u672c\u4e27\u5931\uff0c\u6b64\u540e\u6240\u53d1\u751f\u7684\u4e00\u5207\uff0c\u4ed6\u8111\u5b50\u91cc\u6700\u591a\u53ea\u6709\u6a21\u7cca\u5370\u8c61\uff0c\u5f88\u591a\u751a\u81f3\u8fde\u5370\u8c61\u90fd\u6ca1\u6709\uff0c\u5b8c\u5168\u662f\u7a7a\u767d\u3002\u8299\u857e\u72c4\u663e\u7136\u4e5f\u6e05\u695a\uff0c\u6240\u4ee5\u63a5\u7740\u5c31\u8f7b\u58f0\u544a\u8bc9\u4ed6\u4e4b\u540e\u53d1\u751f\u7684\u4e8b\u60c5\uff0c\u5305\u62ec\u4ed6\u662f\u5982\u4f55\u53d8\u8eab\u6210\u602a\u517d\uff0c\u5982\u4f55\u6253\u5012\u90a3\u540d\u53eb\u505a\u5361\u65af\u56fe\u7684\u5149\u5934\u7267\u5e08\uff0c\u4ee5\u53ca\u4f0a\u65af\u5854\u548c\u601d\u601d\u4e24\u4eba\u7684\u7a81\u7136\u51fa\u73b0\uff0c\u601d\u601d\u5c06\u4ed6\u51bb\u6210\u51b0\u96d5\u3002\u5979\u5e76\u6ca1\u6709\u89c1\u5230\u90a3\u4f4d\u201c\u5361\u5c14\u201d\uff0c\u56e0\u4e3a\u5728\u6b64\u4e4b\u524d\u5965\u5609\u83b1\u65af\u5c31\u628a\u5979\u548c\u8299\u8389\u5a05\uff0c\u4ee5\u53ca\u838e\u73de\u514b\u4e09\u4eba\u9001\u53bb\u4e86\u67d0\u4e2a\u534a\u4f4d\u9762\uff0c\u8fc7\u4e86\u5f88\u4e45\u624d\u88ab\u653e\u56de\u6765\u3002\u5f53\u5979\u4eec\u91cd\u8fd4\u7684\u65f6\u5019\uff0c\u4f0a\u65af\u5854\u548c\u601d\u601d\u90fd\u5df2\u7ecf\u4e0d\u89c1\u4e86\uff0c\u5927\u6bbf\u91cc\u53ea\u5269\u4e0b\u5965\u5609\u83b1\u65af\u4e00\u4eba\u3002\u63a5\u7740\u62c9\u52a0\u7b49\u4eba\u88ab\u53eb\u4e86\u8fdb\u6765\uff0c\u5728\u5965\u5609\u83b1\u65af\u7684\u5429\u5490\u4e0b\u5206\u5934\u53bb\u505a\u5584\u540e\u4e8b\u5b9c\uff0c\u8bf4\u8981\u542f\u52a8\u5bc6\u745f\u80fd\u6838\uff0c\u8ba9\u661f\u9668\u57ce\u4ece\u6d77\u5e95\u98de\u8d77\u6765\u3002\u6700\u540e\u5965\u5609\u83b1\u65af\u7528\u9b54\u6cd5\u878d\u5316\u4e86\u51b0\u5757\uff0c\u5c06\u743c\u6069\u653e\u51fa\u3002</p><p>\u743c\u6069\u5728\u88ab\u51b0\u5c01\u671f\u95f4\u5df2\u7ecf\u6062\u590d\u4e86\u4eba\u5f62\uff0c\u6309\u9053\u7406\u8bf4\u4ed6\u53d7\u4f24\u4e0d\u662f\u5f88\u91cd\uff0c\u53ea\u662f\u808b\u9aa8\u6298\u65ad\u4e86\u51e0\u6839\uff0c\u76ae\u8089\u5916\u4f24\u5219\u65e9\u5c31\u5728\u8299\u8389\u5a05\u7684\u795e\u672f\u6cbb\u7597\u4e0b\u5b8c\u5168\u6062\u590d\u4e86\u3002\u7136\u800c\u4e8b\u5b9e\u662f\u4ed6\u5f53\u65f6\u547c\u5438\u5fae\u5f31\uff0c\u5168\u8eab\u51b0\u51c9\uff0c\u5fc3\u8df3\u8fd1\u4e4e\u505c\u6b62\uff0c\u968f\u65f6\u90fd\u6709\u53ef\u80fd\u4e00\u547d\u545c\u547c\u3002\u5982\u679c\u5728\u9634\u9b42\u57ce\u6216\u8005\u522b\u7684\u5730\u65b9\uff0c\u9047\u5230\u8fd9\u79cd\u60c5\u5f62\u53ef\u4ee5\u8d76\u7d27\u53bb\u795e\u6bbf\u8bf7\u7267\u5e08\uff0c\u4f46\u6b64\u5904\u662f\u4e0a\u4e07\u91cc\u6df1\u7684\u6d77\u5e95\uff0c\u5bc6\u95ed\u7684\u6d6e\u7a7a\u57ce\uff0c\u552f\u4e00\u4e00\u4e2a\u7267\u5e08\u662f\u8299\u8389\u5a05\uff0c\u8fd8\u5df2\u7ecf\u7528\u5b8c\u4e86\u5168\u90e8\u795e\u672f\u3002\u53cc\u80de\u80ce\u59d0\u59b9\u548c\u838e\u73de\u514b\u90fd\u662f\u624b\u8db3\u65e0\u63aa\uff0c\u5b8c\u5168\u4e0d\u77e5\u9053\u8be5\u600e\u4e48\u529e\u597d\u3002\u603b\u7b97\u4ed6\u547d\u4e0d\u8be5\u7edd\uff0c\u6709\u4e00\u4f4d\u5927\u5965\u672f\u5e08\u5728\u573a\uff0c\u5965\u5609\u83b1\u65af\u4f3c\u4e4e\u4e0d\u662f\u5f88\u60c5\u613f\uff0c\u4f46\u6700\u7ec8\u8fd8\u662f\u51fa\u624b\u6551\u4e86\u743c\u6069\u3002\u6574\u4e2a\u4e8b\u60c5\u7684\u7ecf\u8fc7\u4fbf\u5927\u81f4\u5982\u6b64\u4e86\u3002</p><p>\u201c\u8299\u8389\u5a05\u8bf4\u5979\u4f7f\u7528\u795e\u672f\u8fc7\u5ea6\uff0c\u7cbe\u795e\u75b2\u5026\uff0c\u73b0\u5728\u6b63\u5728\u4f11\u606f\uff0c\u201d\u8299\u857e\u72c4\u8bf4\uff0c\u201c\u838e\u73de\u514b\u53d7\u4e86\u4f24\uff0c\u4e5f\u5728\u4f11\u606f\u3002\u201d</p><p>\u201c\u838e\u73de\u514b\u53d7\u4f24\u4e86\uff1f\u201d</p><p>\u201c\u55ef\uff0c\u548c\u90a3\u4e2a\u732b\u5973\u6218\u6597\u7684\u65f6\u5019\u53d7\u4e86\u4f24\uff0c\u4e0d\u8fc7\u4e0d\u662f\u5f88\u91cd\uff0c\u4e0d\u8981\u7d27\u7684\u3002\u201d</p><p>\u838e\u73de\u514b\u662f\u6076\u9b54\uff0c\u81ea\u6108\u6062\u590d\u529b\u5f3a\uff0c\u5982\u679c\u53ea\u662f\u53d7\u70b9\u8f7b\u4f24\uff0c\u5012\u786e\u5b9e\u4e0d\u5fc5\u62c5\u5fc3\u3002\u8fd9\u4e00\u8d9f\u6d77\u5e95\u4e4b\u65c5\u53ef\u8c13\u662f\u5927\u5927\u51fa\u4e4e\u610f\u6599\u4e4b\u5916\uff0c\u65e2\u60ca\u4e14\u9669\uff0c\u6076\u6218\u8fde\u8fde\u3002\u743c\u6069\u4e00\u884c\u4eba\u53d7\u4f24\u7684\u53d7\u4f24\uff0c\u6655\u8ff7\u7684\u6655\u8ff7\uff0c\u7cbe\u75b2\u529b\u5c3d\u7684\u7cbe\u75b2\u529b\u5c3d\uff0c\u76f8\u6bd4\u8f83\u8d77\u6765\uff0c\u5012\u662f\u8299\u857e\u72c4\u7684\u72b6\u6001\u6700\u597d\u4e86\u3002</p><p>\u201c\u5bf9\u4e86\uff0c\u4f60\u81ea\u5df1\u6ca1\u4e8b\u5427\uff0c\u201d\u743c\u6069\u7a81\u7136\u60f3\u4e86\u8d77\u6765\uff0c\u201c\u6211\u8bb0\u5f97\u90a3\u4e2a\u5bb6\u4f19\u671d\u4f60\u53d1\u4e86\u4e00\u51fb\u5730\u9707\u672f\uff0c\u88ab\u4f60\u6321\u4f4f\u4e86\uff0c\u4f46\u90a3\u4e2a\u6cd5\u672f\u5f88\u5f3a\u7684\u2026\u2026\u201d</p><p>\u201c\u6211\u6ca1\u4e8b\u5566\uff0c\u201d\u8299\u857e\u72c4\u5fae\u7b11\u7740\uff0c\u201c\u4f60\u770b\uff0c\u4e00\u70b9\u90fd\u6ca1\u53d7\u4f24\u3002\u201d</p><p>\u5c0f\u5973\u5b69\u63a5\u7740\u89e3\u91ca\uff0c\u5728\u743c\u6069\u7ed9\u4ed6\u7684\u90a3\u4efd\u201c\u6df1\u571f\u9884\u8a00\u201d\u7684\u9b54\u6cd5\u7b14\u8bb0\u4e2d\uff0c\u4fbf\u8bb0\u8f7d\u4e86\u4e00\u79cd\u88ab\u79f0\u4e4b\u4e3a\u201c\u7a33\u56fa\u5927\u5730\u201d\u7684\u80fd\u529b\uff0c\u80fd\u591f\u5bf9\u6297\u6216\u7f13\u89e3\u5404\u79cd\u81ea\u7136\u6216\u4eba\u4e3a\u5bfc\u81f4\u7684\u5730\u5f62\u5730\u8c8c\u53d8\u52a8\uff0c\u4f8b\u5982\u584c\u65b9\u3001\u5c71\u5d29\u3001\u5730\u9707\u7b49\u3002\u65bd\u6cd5\u8005\u7684\u80fd\u529b\u8d8a\u5f3a\uff0c\u7a33\u56fa\u5927\u5730\u7684\u6548\u679c\u81ea\u7136\u8d8a\u597d\uff0c\u8299\u857e\u72c4\u81ea\u8eab\u529b\u91cf\u4e0d\u8db3\uff0c\u4f46\u5979\u5f53\u65f6\u501f\u52a9\u5bb6\u4f20\u6cd5\u6756\u4e0a\u7684\u4e09\u9897\u541b\u738b\u4e4b\u6cea\u5b9d\u77f3\u6765\u5438\u7eb3\u9707\u6ce2\uff0c\u6240\u4ee5\u5b89\u7136\u65e0\u4e8b\uff0c\u4e0d\u66fe\u53d7\u4f24\u3002</p><p>\u201c\u90a3\u5c31\u597d\u3002\u201d</p><p>\u786e\u8ba4\u4e86\u8eab\u8fb9\u5973\u5b69\u5b50\u4eec\u90fd\u5b89\u5168\u65e0\u4e8b\uff0c\u743c\u6069\u7684\u5fc3\u4fbf\u7b97\u662f\u653e\u4e86\u4e0b\u6765\u3002\u4f46\u4e0d\u77e5\u600e\u4e48\u56de\u4e8b\uff0c\u4ed6\u603b\u89c9\u5f97\u8299\u857e\u72c4\u6709\u4e9b\u4e0d\u5bf9\u52b2\uff0c\u6216\u8005\u8bf4\uff0c\u4e3e\u6b62\u795e\u8272\u95f4\u6709\u4e9b\u4e0d\u592a\u81ea\u7136\uff0c\u50cf\u662f\u6709\u4ec0\u4e48\u5fc3\u4e8b\u53c8\u4e0d\u6562\u8868\u9732\u51fa\u6765\uff0c\u5728\u6781\u529b\u63a9\u9970\u7740\u3002\u201c\u600e\u4e48\u4e86\uff0c\u5c0f\u4e2b\u5934\uff0c\u201d\u4ed6\u7b11\u7740\u95ee\uff0c\u201c\u5728\u60f3\u4ec0\u4e48\u5462\uff1f\u201d</p><p>\u201c\u6ca1\u2026\u2026\u6ca1\u4ec0\u4e48\u554a\uff0c\u201d\u5c0f\u5973\u5b69\u614c\u5fd9\u8bf4\uff0c\u201c\u53ea\u662f\u2014\u2014\u5bf9\u4e86\uff0c\u743c\u6069\uff0c\u4f60\u600e\u4e48\u4f1a\u7a81\u7136\u53d8\u6210\u2026\u2026\u53d8\u6210\u90a3\u79cd\u6837\u5b50\uff1f\u201d</p><p>\u201c\u539f\u6765\u662f\u56e0\u4e3a\u8fd9\u4e2a\u554a\uff0c\u88ab\u5413\u5230\u4e86\uff1f\u201d\u743c\u6069\u5fc3\u4e0b\u91ca\u7136\uff0c\u6e29\u67d4\u629a\u6478\u7740\u5979\u7684\u8138\u86cb\uff0c\u201c\u6ca1\u4e8b\uff0c\u522b\u62c5\u5fc3\uff0c\u90a3\u662f\u610f\u5916\u800c\u5df2\u3002\u201d</p><p>\u201c\u610f\u5916\uff1f\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u4ed6\u53d8\u6210\u602a\u517d\u7684\u4e8b\u60c5\uff0c\u81ea\u5df1\u662f\u538b\u6839\u8bb0\u4e0d\u6e05\u695a\u4e86\uff0c\u4f46\u8299\u857e\u72c4\u548c\u4ed6\u5728\u4e00\u8d77\u65f6\u95f4\u4e45\uff0c\u77e5\u9053\u4ed6\u7684\u4e60\u60ef\uff0c\u521a\u624d\u8bb2\u8ff0\u7684\u65f6\u5019\u90fd\u662f\u5c3d\u53ef\u80fd\u8be6\u5c3d\uff0c\u6240\u6709\u7ec6\u8282\uff0c\u5305\u62ec\u6bcf\u4e00\u53e5\u8bdd\u751a\u81f3\u6bcf\u4e2a\u52a8\u4f5c\uff0c\u5979\u90fd\u5c3d\u53ef\u80fd\u8bf4\u5f97\u6e05\u6e05\u695a\u695a\u3002\u743c\u6069\u542c\u5979\u63cf\u8ff0\u7684\u602a\u517d\u6a21\u6837\uff0c\u7565\u4e00\u601d\u7d22\uff0c\u5927\u81f4\u4fbf\u5fc3\u4e2d\u6709\u6570\u4e86\u3002</p><p>\u201c\u6740\u622e\u8005\u3002\u201d</p><p>\u7c7b\u4f3c\u7684\u5f62\u8c61\uff0c\u743c\u6069\u4ee5\u524d\u66fe\u7ecf\u89c1\u8fc7\u2014\u2014\u5728\u5de8\u9b54\u5c71\u8109\u7684\u65f6\u5019\uff0c\u838e\u73de\u514b\u548c\u6885\u83f2\u65af\u4f53\u5185\u7206\u51fa\u7684\u6740\u622e\u4e4b\u795e\u5df4\u5c14\u7684\u865a\u5f71\uff0c\u5c31\u662f\u90a3\u526f\u5c0a\u5bb9\u3002\u6b64\u5916\uff0c\u8299\u857e\u72c4\u8fd8\u542c\u89c1\u601d\u601d\u5bf9\u4f0a\u65af\u5854\u8bf4\u201c\u539f\u6765\u8fd9\u5bb6\u4f19\u4e5f\u662f\u5df4\u5c14\u5b50\u55e3\u201d\uff0c\u5f7c\u6b64\u5370\u8bc1\uff0c\u66f4\u52a0\u786e\u5b9a\u65e0\u7591\u4e86\u3002</p><p>\u4ee5\u524d\u548c\u6885\u83f2\u65af\u95f2\u8c08\u7684\u65f6\u5019\uff0c\u5c11\u5973\u4e5f\u66fe\u7ecf\u63d0\u53ca\u8fc7\uff0c\u5df4\u5c14\u5b50\u55e3\u56e0\u4e3a\u90aa\u795e\u8840\u8109\u7684\u5173\u7cfb\uff0c\u62e5\u6709\u53d8\u8eab\u6210\u201c\u6740\u622e\u8005\u201d\u7684\u80fd\u529b\u3002\u6240\u8c13\u6740\u622e\u8005\uff0c\u4fbf\u662f\u5df4\u5c14\u7684\u795e\u76f8\u4e4b\u4e00\uff0c\u662f\u529b\u5927\u65e0\u6bd4\u7684\u602a\u517d\u3002\u4f46\u53ea\u6709\u6781\u5c11\u6570\u5df4\u5c14\u5b50\u55e3\u662f\u80fd\u591f\u81ea\u5df1\u6709\u610f\u8bc6\u5730\u63a7\u5236\u53d8\u8eab\uff0c\u7edd\u5927\u591a\u6570\u90fd\u662f\u81ea\u5df1\u6ca1\u6cd5\u63a7\u5236\uff0c\u53ea\u80fd\u5728\u6fd2\u6b7b\u72b6\u6001\u4e0b\u6fc0\u53d1\u8840\u7edf\u7684\u81ea\u6211\u4fdd\u62a4\u673a\u5236\uff0c\u5b8c\u6210\u53d8\u8eab\u3002\u53ef\u60dc\u7684\u662f\uff0c\u7edd\u5927\u90e8\u5206\u5df4\u5c14\u5b50\u55e3\uff0c\u538b\u6839\u5c31\u6ca1\u6709\u7ecf\u5386\u201c\u6fd2\u6b7b\u201d\u8fd9\u4e2a\u9636\u6bb5\uff0c\u76f4\u63a5\u88ab\u4eba\u4ece\u201c\u751f\u201d\u77ac\u95f4\u53d8\u6210\u4e86\u201c\u6b7b\u201d\uff0c\u6240\u4ee5\u4e5f\u5c31\u5230\u6b7b\u90fd\u6ca1\u53d8\u8fc7\u8eab\u3002\u743c\u6069\u4e0d\u662f\u5df4\u5c14\u5b50\u55e3\uff0c\u4f46\u4ed6\u5148\u540e\u5438\u6536\u8fc7\u838e\u73de\u514b\u548c\u83f2\u5a05\u7684\u6740\u622e\u795e\u529b\uff0c\u5176\u5b9e\u4e5f\u53ef\u4ee5\u7b97\u662f\u4e86\u3002\u8fd9\u6b21\u88ab\u90a3\u5149\u5934\u7267\u5e08\u6253\u5f97\u534a\u6b7b\uff0c\u81ea\u6211\u4fdd\u62a4\u673a\u5236\u4fbf\u81ea\u52a8\u6fc0\u53d1\uff0c\u5f53\u7136\uff0c\u8fd9\u4e5f\u662f\u56e0\u4e3a\u6709\u8299\u857e\u72c4\u62fc\u547d\u62b5\u6297\uff0c\u4e3a\u4ed6\u4e89\u53d6\u5230\u4e86\u8db3\u591f\u7684\u65f6\u95f4\uff0c\u5426\u5219\u8fd8\u6ca1\u7b49\u53d8\u8eab\u6210\u529f\u53ea\u6015\u5c31\u88ab\u5e72\u6389\u4e86\u3002</p><p>\u201c\u5438\u6536\u4e86\u5df4\u5c14\u7684\u795e\u529b\uff0c\u6ca1\u60f3\u5230\u8fd8\u4f1a\u6709\u8fd9\u79cd\u526f\u4f5c\u7528\uff0c\u5389\u5bb3\u662f\u591f\u5389\u5bb3\u4e86\uff0c\u5374\u672a\u514d\u592a\u4e0d\u7f8e\u578b\u2026\u2026\u201d</p><p>\u743c\u6069\u6697\u81ea\u53d1\u7740\u7262\u9a9a\uff0c\u4f46\u6ca1\u6709\u8bf4\u51fa\u53e3\uff0c\u8fd9\u4ef6\u4e8b\u7275\u626f\u590d\u6742\uff0c\u5982\u679c\u8981\u5411\u8299\u857e\u72c4\u89e3\u91ca\u6e05\u695a\u7684\u8bdd\uff0c\u5c31\u8981\u5c06\u5f88\u591a\u79d8\u5bc6\u90fd\u66b4\u9732\u51fa\u6765\u3002\u4ed6\u65e2\u7136\u4f4f\u53e3\u4e0d\u8bf4\uff0c\u8299\u857e\u72c4\u4e5f\u4e56\u5de7\u5730\u4e0d\u518d\u8ffd\u95ee\u3002\u5979\u770b\u770b\u65f6\u95f4\uff0c\u62ff\u8d77\u5965\u5609\u83b1\u65af\u7559\u4e0b\u7684\u90a3\u74f6\u836f\u6c34\uff0c\u5012\u4e86\u4e00\u534a\u5728\u676f\u4e2d\uff0c\u743c\u6069\u6b63\u8981\u4f38\u624b\u53bb\u63a5\uff0c\u5374\u88ab\u5979\u8eb2\u5f00\uff0c\u81ea\u5df1\u4e00\u53e3\u559d\u4e86\u4e0b\u53bb\u3002</p><p>\u201c\u2026\u2026\u4f60\u5e72\u561b\uff1f\u201d</p><p>\u743c\u6069\u83ab\u540d\u5176\u5999\uff0c\u4f46\u4ed6\u5f88\u5feb\u5c31\u77e5\u9053\u4e86\u7b54\u6848\u3002\u8299\u857e\u72c4\u559d\u4e0b\u836f\u6c34\uff0c\u5374\u5e76\u672a\u54bd\u4e0b\uff0c\u800c\u662f\u542b\u5728\u53e3\u4e2d\uff0c\u7136\u540e\u4e3b\u52a8\u8d34\u8fc7\u6765\uff0c\u4fcf\u8138\u626c\u8d77\uff0c\u6ee1\u662f\u7f9e\u6da9\u7ea2\u6655\uff0c\u7cbe\u81f4\u7684\u6a31\u5507\u4eff\u4f5b\u9c9c\u5ae9\u82b1\u74e3\uff0c\u6b63\u7b49\u5f85\u7740\u7537\u4eba\u7684\u91c7\u6458\u3002\u743c\u6069\u7a0d\u7a0d\u6709\u4e9b\u610f\u5916\uff0c\u4f46\u4e5f\u4e0d\u53bb\u591a\u60f3\uff0c\u4f4e\u5934\u543b\u4e0a\u5979\u7684\u5c0f\u5634\uff0c\u8ba9\u5979\u5c06\u836f\u6c34\u4e00\u70b9\u70b9\u5ea6\u5165\u81ea\u5df1\u53e3\u4e2d\uff0c\u987a\u4fbf\u54c1\u5c1d\u5c11\u5973\u9999\u6ed1\u67d4\u8f6f\u7684\u820c\u5c16\u3002</p><p>\u201c\u743c\u6069\uff0c\u6211\u60f3\u8981\u2026\u2026\u7ed9\u6211\u2026\u2026\u201d</p><p>\u5c11\u5973\u5462\u5583\u7740\uff0c\u767d\u7699\u5982\u7389\u7684\u808c\u80a4\u5df2\u7ecf\u88ab\u60c5\u6b32\u70e7\u6210\u4e86\u73ab\u7470\u8272\uff0c\u8bf1\u4eba\u7684\u4f53\u9999\u88ab\u70ed\u529b\u84b8\u53d1\uff0c\u900f\u8863\u800c\u51fa\u3002\u5979\u7d27\u7d27\u62b1\u7740\u743c\u6069\uff0c\u4eb2\u543b\u7740\u4ed6\u7684\u8138\u988a\u548c\u8116\u9888\uff0c\u89e3\u5f00\u4ed6\u7684\u886c\u886b\u7ebd\u6263\uff0c\u6574\u4e2a\u4eba\u5c31\u50cf\u662f\u4e00\u56e2\u718a\u718a\u71c3\u70e7\u7684\u70c8\u706b\uff0c\u524d\u6240\u672a\u6709\u7684\u4e3b\u52a8\uff0c\u50cf\u662f\u8981\u628a\u538b\u6291\u5df2\u4e45\u7684\u6fc0\u60c5\u548c\u6e34\u671b\u5168\u90fd\u53d1\u6cc4\u51fa\u6765\u3002\u6ca1\u6709\u4efb\u4f55\u7537\u6027\u80fd\u591f\u6297\u62d2\u8fd9\u6837\u7684\u8bf1\u60d1\uff0c\u743c\u6069\u66f4\u4e0d\u80fd\uff0c\u51e0\u4e4e\u662f\u4e0b\u610f\u8bc6\u5730\uff0c\u4ed6\u4e00\u628a\u6495\u5f00\u5c11\u5973\u7684\u8863\u88d9\uff0c\u5c06\u5979\u7c97\u66b4\u5730\u6309\u5012\u5728\u5e8a\u4e0a\uff0c\u53cc\u817f\u5927\u5927\u5206\u5f00\uff0c\u7136\u540e\u5f84\u76f4\u523a\u5165\u3002</p><p>\u4e0b\u4f53\u88ab\u6495\u88c2\u7684\u4e1d\u4e1d\u5267\u75db\u8ba9\u5c11\u5973\u5634\u5507\u7d27\u54ac\uff0c\u6cea\u6c34\u76c8\u76c8\uff0c\u7136\u800c\u5979\u54c0\u557c\u7740\uff0c\u54ed\u6ce3\u7740\uff0c\u53cc\u81c2\u5374\u7d27\u7d27\u62b1\u7740\u7537\u4eba\u7684\u8116\u9888\uff0c\u8170\u80a2\u5f80\u4e0a\u52aa\u529b\u5730\u8fce\u5408\u7740\uff0c\u8ba9\u4ed6\u5728\u81ea\u5df1\u7684\u8eab\u4f53\u91cc\u8086\u610f\u51b2\u649e\uff0c\u4eff\u4f5b\u51f6\u731b\u7684\u6d6a\u6f6e\uff0c\u65e0\u4f11\u65e0\u6b62\u3002\u743c\u6069\u611f\u89c9\u81ea\u5df1\u7684\u7cbe\u529b\u524d\u6240\u672a\u6709\u5730\u65fa\u76db\u5145\u6c9b\uff0c\u50cf\u662f\u65e0\u8bba\u591a\u5c11\u6b21\u90fd\u4e0d\u4f1a\u6ee1\u8db3\u4f3c\u7684\u3002\u4ed6\u4eec\u5728\u5e8a\u4e0a\u7ffb\u6eda\u7740\uff0c\u53d8\u6362\u7740\u5404\u79cd\u4e0d\u540c\u7684\u59ff\u52bf\uff0c\u505a\u4e86\u4e00\u6b21\u53c8\u4e00\u6b21\u3002\u743c\u6069\u8f6e\u6d41\u4eab\u7528\u5c11\u5973\u7684\u6a31\u5507\u548c\u82b1\u5f84\uff0c\u5b83\u4eec\u7684\u6ecb\u5473\u662f\u90a3\u6837\u7684\u7f8e\u5999\uff0c\u7d27\u81f4\u3001\u67d4\u8f6f\u3001\u706b\u70ed\uff0c\u8ba9\u4ed6\u6d41\u8fde\u5fd8\u8fd4\uff0c\u820d\u4e0d\u5f97\u7247\u523b\u6682\u79bb\u3002</p><p>\u5f53\u6c99\u6f0f\u663e\u793a\u65f6\u95f4\u5df2\u7ecf\u8fc7\u53bb\u56db\u4e2a\u5c0f\u65f6\u540e\uff0c\u7b4b\u75b2\u529b\u5c3d\u7684\u4e24\u4eba\u7ec8\u4e8e\u505c\u4e86\u4e0b\u6765\uff0c\u5598\u606f\u58f0\u6e10\u6e10\u5e73\u606f\u3002\u743c\u6069\u62b1\u7740\u8f6f\u7ef5\u7ef5\u762b\u5728\u81ea\u5df1\u6000\u4e2d\u7684\u8d64\u88f8\u5c11\u5973\uff0c\u624b\u5728\u5149\u6d01\u7684\u810a\u80cc\u4e0a\u8f7b\u8f7b\u629a\u6478\uff0c\u5634\u5507\u4eb2\u543b\u7740\u5979\u7684\u989d\u5934\uff0c\u201c\u600e\u4e48\u4eca\u5929\u8fd9\u4e48\u4e3b\u52a8\uff1f\u201d\u4ed6\u53d6\u7b11\uff0c\u201c\u4f60\u4ee5\u524d\u53ef\u90fd\u662f\u5bb3\u7f9e\u5f97\u8981\u547d\u3002\u201d</p><p>\u201c\u56e0\u4e3a\u2026\u2026\u201d\u5c11\u5973\u4f4e\u58f0\u8bf4\uff0c\u201c\u56e0\u4e3a\u8fd9\u6216\u8bb8\u662f\u6700\u540e\u4e00\u6b21\u4e86\u561b\u3002\u201d</p></article>"}}}]);
