(window.webpackJsonp=window.webpackJsonp||[]).push([[468],{491:function(p){p.exports={chapterTitle:null,title:"\u7b2c468\u7ae0 \u51fa\u5934\u693d\u5b50",content:"<article><h2>\u7b2c468\u7ae0 \u51fa\u5934\u693d\u5b50</h2><p>\u3000\u3000\u542c\u7740\u5b59\u9759\u8499\u5bf9\u5979\u5916\u7956\u7236\u7684\u8fd9\u4e00\u756a\u8bc4\u4ef7\uff0c\u5f20\u606a\u5c0f\u5c0f\u7684\u611f\u6168\u4e86\u4e00\u4e0b\uff0c\u845b\u666f\u8bda\u5728\u534e\u4eba\u5546\u5708\u6709\u7740\u6781\u9ad8\u7684\u58f0\u8a89\uff0c\u8fd9\u51e0\u5929\u7684\u62a5\u7eb8\u5bf9\u4ed6\u7684\u4eba\u751f\u8bc4\u4ef7\u90fd\u662f\u63a8\u5d07\u4e4b\u6781\uff0c\u4f46\u662f\u4ed6\u5bf9\u5bb6\u4eba\u7684\u82db\u523b\u4ee5\u53ca\u601d\u60f3\u7684\u4fdd\u5b88\u4e5f\u8ba9\u4ed6\u9887\u6709\u6307\u8d23\uff0c\u4e5f\u4f7f\u4ed6\u5728\u751f\u524d\u4fdd\u6301\u7684\u5a01\u4e25\u5728\u4ed6\u79bb\u4e16\u7684\u90a3\u4e00\u523b\u5c31\u8f70\u7136\u5d29\u584c\uff0c\u8fd9\u65f6\u5019\u845b\u5bb6\u4eba\u6068\u4e0d\u5f97\u5c31\u5c06\u4ed6\u6574\u4e2a\u7684\u90fd\u5fd8\u6389\u3002</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u91cc\u4f1a\u60f3\u81ea\u5df1\u5bf9\u5802\u5144\u5f20\u5955\u4f1a\u4e0d\u4f1a\u592a\u82db\u523b\u4e86\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u5b59\u5c1a\u4e49\u8f7b\u8f7b\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u6ca1\u6709\u8d23\u5907\u5c0f\u5973\u513f\u4e71\u8bf4\u8bdd\uff0c\u53ea\u8bf4\u9053\uff1a\u201c\u6211\u8ddf\u4f60\u53d4\u5230\u73b0\u5728\u90fd\u6ca1\u6709\u5403\u665a\u996d\uff0c\u6211\u4eec\u5c31\u5728\u9694\u58c1\u8c08\u4e8b\u60c5\uff0c\u4f60\u5e2e\u6211\u4eec\u7b80\u5355\u641e\u4e9b\u5403\u7684\u4e1c\u897f\u6765\u3002\u201d</p><p>\u3000\u3000\u201c\u7b80\u5355\u5c31\u53ea\u6709\u5143\u5bb5\u6216\u8005\u6c34\u997a\u4e86\u2026\u2026\u201d\u5b59\u9759\u6aac\u624b\u6307\u62b5\u7740\u4e0b\u5df4\u601d\u8003\u7740\uff0c\u5728\u5979\u7236\u4eb2\u9762\u524d\uff0c\u5f20\u626c\u6cfc\u8fa3\u7684\u6027\u683c\u6709\u6240\u6536\u655b\uff0c\u5012\u6709\u51e0\u5206\u7eaf\u771f\u5973\u5b69\u7684\u6a21\u6837\uff0c\u672a\u65bd\u7c89\u9edb\uff0c\u6e05\u6c34\u8299\u84c9\u5341\u5206\u5a07\u7f8e\u3002</p><p>\u3000\u3000\u201c\u90a3\u5c31\u5143\u5bb5\u5427\uff0c\u6211\u76f8\u4fe1\u4f60\u4e0b\u5143\u5bb5\u8fd8\u662f\u6709\u6c34\u5e73\u7684\u3002\u201d</p><p>\u3000\u3000\u5b59\u5c1a\u4e49\u8fd9\u4e48\u5938\u4e00\u53e5\uff0c\u5b59\u9759\u6aac\u5c31\u6709\u4e9b\u65e0\u5730\u81ea\u5bb9\u4e86\u3002</p><p>\u3000\u3000\u5f71\u6e7e\u56ed\u5927\u697c\u91cc\u5c31\u6709\u8d85\u7ea7\u5546\u573a\uff0c\u5f20\u606a\u4e0e\u53f6\u5efa\u658c\u53bb\u540a\u5501\u65f6\uff0c\u5b59\u9759\u6aac\u62c9\u7740\u5979\u59d0\u8fd8\u6709\u8bb8\u601d\u9664\u4e86\u4e70\u4e00\u4e9b\u9152\u5e97\u516c\u5bd3\u672a\u63d0\u4f9b\u7684\u751f\u6d3b\u7528\u54c1\u4e4b\u5916\uff0c\u8fd8\u4e70\u4e86\u4e9b\u901f\u51bb\u98df\u54c1\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u4ece\u51b0\u7bb1\u91cc\u62ff\u51fa\u5143\u5bb5\uff0c\u8bb8\u601d\u8981\u8fc7\u53bb\u5e2e\u5fd9\uff0c\u5b59\u9759\u6aac\u8bf4\u9053\uff1a\u201c\u6211\u5bf9\u81ea\u5df1\u4e0b\u5143\u5bb5\u7684\u6c34\u5e73\u8fd8\u6709\u4e9b\u81ea\u4fe1\u2026\u2026\u201d\u53c8\u95ee\u8bb8\u601d\uff0c\u201c\u4f60\u600e\u4e48\u4e0d\u8fc7\u53bb\u8ddf\u4ed6\u4eec\u8c08\u4e8b\u60c5\uff1f\u201d</p><p>\u3000\u3000\u8bb8\u601d\u606c\u6de1\u7684\u7b11\u4e86\u7b11\uff1a\u201c\u966a\u5b59\u59d0\u8ddf\u4f60\u804a\u5929\u6bd4\u8f83\u6709\u8da3\u2026\u2026\u201d\u5750\u56de\u6c99\u53d1\u4e0e\u5b59\u9759\u9999\u4e00\u8d77\u770b\u9999\u6e2f\u7684\u5a31\u4e50\u8282\u76ee\u3002</p><p>\u3000\u3000\u5143\u5bb5\u4e0b\u597d\uff0c\u5b59\u9759\u6aac\u7aef\u5230\u9694\u58c1\u53bb\uff0c\u5085\u4fca\u5728\u91cc\u9762\u5e2e\u5979\u6253\u5f00\u95e8\uff0c\u5c31\u542c\u89c1\u5f20\u606a\u5728\u8bf4\uff1a\u201c\u6211\u770b\u5609\u4fe1\u5b9e\u4e1a\u660e\u5929\u5c31\u53d1\u5e03\u516c\u544a\uff0c\u4e0e\u845b\u5bb6\u5212\u6e05\u754c\u9650\uff0c\u4ed6\u4eec\u8981\u6210\u4e3a\u70c2\u6ce5\u6f6d\u5c31\u6210\u4e3a\u70c2\u6ce5\u6f6d\u597d\u4e86\uff0c\u8fc7\u53bb\uff0c\u4e0d\u7ba1\u6211\u4eec\u505a\u518d\u5927\u7684\u52aa\u529b\uff0c\u522b\u4eba\u90fd\u4f1a\u5c06\u5609\u4fe1\u5b9e\u4e1a\u770b\u6210\u845b\u6c0f\u5bb6\u65cf\u4f01\u4e1a\u7684\u4e00\u5458\uff0c\u770b\u6210\u662f\u5609\u4fe1\u96c6\u56e2\u7684\u5916\u56f4\u4f01\u4e1a\uff0c\u6211\u60f3\u73b0\u5728\u5c31\u662f\u5609\u4fe1\u5b9e\u4e1a\u8d70\u51fa\u8fd9\u4e2a\u9634\u5f71\u7684\u65f6\u673a\u2026\u2026\u201d</p><p>\u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u5609\u4fe1\u5b9e\u4e1a\u4e0e\u845b\u5bb6\u7684\u5173\u7cfb\u4e5f\u4ec5\u9650\u4e8e\u4e24\u540d\u5927\u80a1\u4e1c\uff1a\u845b\u660e\u5fb7\u662f\u845b\u666f\u8bda\u7684\u4f84\u5b50\u3001\u5b59\u5c1a\u4e49\u662f\u845b\u666f\u8bda\u7684\u5973\u5a7f\uff0c\u5728\u8d22\u52a1\u5173\u7cfb\u4e0a\u5374\u65e9\u5df2\u7ecf\u4e0e\u845b\u5bb6\u5272\u88c2\u3002\u845b\u666f\u8bda\u665a\u5e74\u9010\u6e10\u7684\u5c06\u4e00\u4e9b\u975e\u4e3b\u8425\u4e1a\u52a1\u7684\u8d44\u4ea7\u62e8\u5230\u5bb6\u65cf\u6210\u5458\u540d\u4e0b\uff0c\u5609\u4fe1\u96c6\u56e2\u53ea\u4fdd\u7559\u5730\u4ea7\u3001\u7535\u5b50\u3001\u6a61\u80f6\u5236\u9020\u7b49\u6838\u5fc3\u4e1a\u52a1\u8d44\u4ea7\uff0c\u4e0d\u8fc7\u4e3a\u4e86\u501f\u52a9\u845b\u5bb6\u7684\u58f0\u52bf\uff0c\u50cf\u5609\u4fe1\u5b9e\u4e1a\u8fd9\u6837\u7684\u975e\u4e3b\u8425\u4e1a\u52a1\u516c\u53f8\u4e5f\u4e0d\u4f1a\u523b\u610f\u7684\u63d0\u9192\u6295\u8d44\u8005\u5176\u8d22\u52a1\u4e0a\u5df2\u7ecf\u4e0e\u845b\u5bb6\u5f7b\u5e95\u5272\u65ad\u5173\u7cfb\uff0c\u751a\u81f3\u5c06\u8fd9\u4e9b\u5173\u952e\u6d88\u606f\u8f7b\u63cf\u6de1\u5199\u4e00\u756a\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u53d1\u5e03\u6f84\u6e05\u516c\u544a\uff0c\u4e0e\u845b\u5bb6\u5212\u6e05\u754c\u9650\uff0c\u53ef\u4ee5\u907f\u514d\u845b\u666f\u8bda\u79bb\u4e16\u53ca\u5176\u5b50\u5973\u4e89\u593a\u9057\u4ea7\u6240\u5e26\u6765\u7684\u6076\u52a3\u5f71\u54cd\uff0c\u4e5f\u662f\u4e3a\u4e86\u4ece\u6839\u672c\u4e0a\u8ba9\u5609\u4fe1\u5b9e\u4e1a\u4ece\u5609\u4fe1\u96c6\u56e2\u7684\u9634\u5f71\u4e0b\u8d70\u51fa\u6765\uff0c\u4e3a\u4e0b\u4e00\u6b65\u5b9e\u65bd\u589e\u53d1\u5e76\u8d2d\u6574\u4f53\u7f6e\u5165\u7231\u8fbe\u7535\u5b50\u7684\u789f\u673a\u8d44\u4ea7\u505a\u51c6\u5907\uff0c\u82e5\u6709\u9700\u8981\uff0c\u66f4\u6539\u516c\u53f8\u6ce8\u518c\u540d\u79f0\u4e5f\u5728\u6240\u4e0d\u60dc\u3002</p><p>\u3000\u3000\u8fd9\u4e48\u505a\u4e5f\u6709\u4e00\u4e2a\u574f\u5904\u3002</p><p>\u3000\u3000\u845b\u666f\u8bda\u7684\u79bb\u4e16\u4ee4\u5609\u4fe1\u96c6\u56e2\u65d7\u4e0b\u516d\u5bb6\u4e0a\u5e02\u516c\u53f8\u80a1\u4ef7\u72c2\u8dcc\uff0c\u845b\u660e\u4fe1\u5f53\u524d\u7684\u8981\u52a1\u662f\u7a33\u5b9a\u6295\u8d44\u4eba\u5bf9\u845b\u5bb6\u4f01\u4e1a\u7684\u4fe1\u5fc3\uff0c\u7edd\u5bf9\u4e0d\u5e0c\u671b\u5bb6\u65cf\u5185\u90e8\u7684\u6fc0\u70c8\u77db\u76fe\u66b4\u9732\u5728\u5a92\u4f53\u4e0e\u516c\u4f17\u9762\u524d\uff0c\u5609\u4fe1\u5b9e\u4e1a\u5728\u8fd9\u4e2a\u654f\u611f\u65f6\u523b\u53d1\u5e03\u5212\u6e05\u754c\u9650\u7684\u516c\u544a\uff0c\u845b\u660e\u4fe1\u5927\u6982\u4f1a\u6709\u80cc\u540e\u7ed9\u6345\u4e00\u5200\u7684\u611f\u89c9\u3002</p><p>\u3000\u3000\u5b59\u5c1a\u4e49\u6709\u4e9b\u72b9\u8c6b\uff1a\u201c\u5bb6\u65cf\u7684\u4e8b\u4e5f\u7792\u4e0d\u8fc7\u592a\u4e45\uff0c\u4e8c\u623f\u3001\u4e09\u623f\u7684\u90a3\u4e9b\u4eba\u90fd\u5f00\u59cb\u642c\u51fa\u539f\u6765\u7684\u5b85\u5b50\uff0c\u76f8\u4fe1\u9999\u6e2f\u5a92\u4f53\u5f88\u5feb\u5c31\u6709\u62a5\u9053\uff0c\u6211\u4eec\u53ef\u4ee5\u5728\u9999\u6e2f\u5a92\u4f53\u62a5\u9053\u4e4b\u540e\u518d\u53d1\u5e03\u6f84\u6e05\u516c\u544a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u90a3\u5c31\u662f\u88ab\u52a8\u56de\u5e94\u4e86\u2026\u2026\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u5fc3\u91cc\u5374\u60f3\uff1a\u4e5d\u516d\u5e74\u9999\u6e2f\u7684\u72d7\u4ed4\u961f\u4f3c\u4e4e\u6bd4\u5341\u5e74\u4e4b\u540e\u7a0d\u5dee\u4e00\u4e9b\uff0c\u55c5\u89c9\u4e5f\u7a0d\u8fdf\u949d\u4e86\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u845b\u660e\u5fb7\u4e0e\u845b\u660e\u4fe1\u4e4b\u95f4\u7684\u77db\u76fe\u6bd4\u8f83\u5c16\u9510\uff0c\u5f53\u521d\u5c31\u662f\u845b\u666f\u8bda\u4e3a\u4e86\u5f25\u8865\u4ed6\u8fd9\u4e00\u7cfb\u7ed9\u845b\u5bb6\u505a\u51fa\u7684\u8d21\u732e\uff0c\u624d\u5c06\u65d7\u4e0b\u5609\u4fe1\u5b9e\u4e1a\u7ea615\uff05\u7684\u80a1\u6743\u8f6c\u5230\u4ed6\u540d\u4e0b\uff0c\u5e76\u5c06\u5609\u4fe1\u5b9e\u4e1a\u4ea4\u7ed9\u4ed6\u7ba1\u7406\uff0c\u4f46\u662f\u4ece\u90a3\u4e4b\u540e\uff0c\u957f\u623f\u957f\u5b50\u845b\u660e\u4fe1\u7ba1\u7406\u7684\u5609\u4fe1\u7535\u5b50\u5374\u4e0d\u62e9\u624b\u6bb5\u7684\u8ddf\u5609\u4fe1\u5b9e\u4e1a\u6076\u6027\u7ade\u4e89\u8d77\u6765\uff0c\u4e00\u5ea6\u5c06\u5609\u4fe1\u5b9e\u4e1a\u903c\u5230\u7834\u4ea7\u7684\u8fb9\u7f18\u3002</p><p>\u3000\u3000\u6709\u673a\u4f1a\u80fd\u5728\u80cc\u540e\u6345\u845b\u660e\u4fe1\u4e00\u5200\uff0c\u845b\u660e\u5fb7\u4e00\u5b9a\u613f\u610f\uff0c\u800c\u4e14\u8fd8\u8981\u8ba9\u845b\u660e\u4fe1\u77e5\u9053\u5c31\u662f\u4ed6\u6345\u7684\u8fd9\u4e00\u5200\u3002</p><p>\u3000\u3000\u845b\u660e\u5fb7\u8bf4\u9053\uff1a\u201c\u786e\u4fdd\u589e\u53d1\u5e76\u8d2d\u662f\u6211\u4eec\u7684\u91cd\u5fc3\uff0c\u5176\u4ed6\u4ec0\u4e48\u6709\u7684\u6ca1\u7684\uff0c\u90fd\u4e0d\u662f\u91cd\u70b9\u2026\u2026\u201d\u8a00\u4e0b\u4e4b\u610f\uff0c\u5373\u4f7f\u73b0\u5728\u8dd1\u8fc7\u53bb\u6247\u845b\u660e\u4fe1\u4e00\u8bb0\u8033\u5149\u90fd\u65e0\u6240\u8c13\u3002\u845b\u660e\u4fe1\u8fd9\u4e9b\u5e74\u4ed7\u7740\u957f\u623f\u957f\u5b50\u7684\u8eab\u4efd\u88ab\u5a92\u4f53\u8a89\u4e3a\u5609\u4fe1\u63a5\u73ed\u4eba\uff0c\u4ed6\u4f55\u65f6\u5c06\u5176\u4ed6\u4eba\u653e\u5728\u773c\u91cc\uff1f</p><p>\u3000\u3000\u5b59\u9759\u6aac\u5c06\u5143\u5bb5\u653e\u5230\u6c99\u53d1\u95f4\u7684\u80e1\u6843\u6728\u77ee\u51e0\u4e0a\uff0c\u5979\u4e0d\u6e05\u695a\u8be6\u60c5\uff0c\u4f46\u662f\u542c\u5230\u80fd\u8ddf\u845b\u5bb6\u5212\u6e05\u754c\u9650\uff0c\u65e0\u8bba\u5982\u4f55\u90fd\u8981\u652f\u6301\u7684\uff1a\u201c\u65e9\u5c31\u8be5\u8ddf\u8fd9\u4e9b\u5212\u6e05\u754c\u9650\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u77e5\u9053\u4ec0\u4e48\u5728\u8fd9\u91cc\u80e1\u8bf4\u516b\u9053\uff1f\u201d\u5b59\u5c1a\u4e49\u62cd\u4e86\u62cd\u5973\u513f\u7684\u8111\u888b\u3002</p><p>\u3000\u3000\u201c\u4f60\u4eec\u4ec0\u4e48\u90fd\u4e0d\u8ba9\u6211\u77e5\u9053\uff0c\u6211\u80fd\u77e5\u9053\u4ec0\u4e48\uff1f\u201d\u5b59\u9759\u6aac\u59d4\u5c48\u7684\u8bf4\u9053\u3002</p><p>\u3000\u3000\u5f20\u606a\u8ddf\u53f6\u5efa\u658c\u5fc3\u91cc\u6e05\u695a\u6b64\u65f6\u7684\u5b59\u9759\u6aac\u7edd\u5bf9\u662f\u4f2a\u88c5\u7684\uff0c\u5374\u504f\u504f\u770b\u4e0d\u5230\u5979\u5a07\u67d4\u55d4\u6028\u4e2d\u6709\u4e1d\u6beb\u505a\u4f5c\u7684\u75d5\u8ff9\uff0c\u53ea\u80fd\u65e0\u5948\u7684\u5bf9\u671b\u4e00\u773c\uff0c\u5b59\u9759\u6aac\u626e\u53ef\u601c\u8981\u8d56\u5728\u8fd9\u91cc\uff0c\u603b\u4e0d\u80fd\u5c06\u5979\u8f87\u51fa\u53bb\u6216\u8005\u5e72\u8106\u4e0d\u8bf4\u4ec0\u4e48\u3002</p><p>\u3000\u3000\u5f20\u606a\u63c9\u4e86\u63c9\u809a\u5b50\uff0c\u95ee\u53f6\u5efa\u658c\uff1a\u201c\u665a\u996d\u5403\u4e86\u4e0d\u5c11\uff0c\u8fd9\u4f1a\u513f\u600e\u4e48\u4e5f\u997f\u4e86\uff1f\u201d</p><p>\u3000\u3000\u53f6\u5efa\u658c\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u5947\u602a\uff0c\u53ef\u80fd\u6700\u8fd1\u7684\u80c3\u53e3\u7279\u522b\u597d\uff0c\u8fd8\u662f\u8bf4\u5c0f\u59b9\u4e0b\u5143\u5bb5\u7684\u6c34\u5e73\u5f88\u80fd\u52fe\u5f15\u8d77\u522b\u4eba\u7684\u98df\u6b32\uff1f\u201d\u5bf9\u5b59\u9759\u6aac\u8bf4\u9053\uff0c\u201c\u5c0f\u59b9\uff0c\u4f60\u80fd\u4e0d\u80fd\u5e2e\u6211\u4eec\u4e5f\u4e0b\u4e24\u7897\u5143\u5bb5\uff1f\u201d</p><p>\u3000\u3000\u5b59\u9759\u6aac\u8d81\u4eba\u4e0d\u6ce8\u610f\u77aa\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u4e0d\u7518\u5fc3\u5230\u9694\u58c1\u5c4b\u4e0b\u5143\u5bb5\u53bb\u4e86\uff0c\u7b49\u5979\u4e0b\u597d\u5143\u5bb5\u8fc7\u6765\uff0c\u5f20\u606a\u6070\u5de7\u7ad9\u8d77\u6765\u8bf4\uff1a\u201c\u90a3\u5c31\u8fd9\u6837\uff0c\u660e\u5929\u4e00\u65e9\uff0c\u5085\u603b\u5c31\u53ec\u5f00\u8bb0\u8005\u4f1a\u2026\u2026\u201d\u8dd1\u8fc7\u6765\u63a5\u8fc7\u5b59\u9759\u6aac\u624b\u91cc\u7684\u5143\u5bb5\uff0c\u79f0\u8d5e\u9053\uff0c\u201c\u771f\u662f\u4e0d\u9519\uff0c\u95fb\u7740\u5c31\u662f\u9999\uff0c\u53ef\u80fd\u6709\u4e9b\u8d76\u65f6\u95f4\uff0c\u600e\u4e48\u770b\u8d77\u6765\u6709\u4e9b\u5939\u751f\u7684\u611f\u89c9\uff1f\u201d\u516c\u5bd3\u91cc\u6709\u5168\u5957\u7684\u7076\u5177\u3001\u708a\u5177\u7b49\u751f\u6d3b\u7528\u5177\uff0c\u4e0d\u8fc7\u5f20\u606a\u8fd8\u662f\u62ff\u5230\u8bb8\u601d\u5979\u4eec\u623f\u91cc\u5c06\u5143\u5bb5\u53c8\u716e\u4e86\u4e24\u5206\u949f\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u845b\u660e\u4fe1\u7684\u5de6\u773c\u7a9d\u6709\u4e9b\u7ea2\u80bf\uff0c\u6577\u8fc7\u51b0\u888b\u8fd8\u662f\u6709\u4e9b\u75bc\uff0c\u5fc3\u91cc\u5fff\u5fff\u4e0d\u5e73\uff0c\u867d\u7136\u7acb\u9a6c\u5c31\u5c06\u53cd\u5e94\u6162\u4e86\u534a\u62cd\u5bb3\u4ed6\u7ed9\u4e8c\u623f\u8001\u5927\u845b\u660e\u793c\u4e00\u62f3\u6253\u4e2d\u773c\u7a9d\u7684\u4fdd\u9556\u8f9e\u9000\uff0c\u4f46\u662f\u4ed6\u5fc3\u91cc\u7684\u6c14\u6124\u5f88\u96be\u6d88\u5e73\uff0c\u53ea\u662f\u8003\u8651\u5230\u6b64\u65f6\u845b\u5bb6\u7684\u5185\u90e8\u77db\u76fe\u6fc0\u5316\u4f1a\u5f71\u54cd\u4ed6\u5750\u7a33\u5609\u4fe1\u96c6\u56e2\u8463\u4e8b\u957f\u517c\u603b\u88c1\u7684\u4f4d\u5b50\uff0c\u6709\u4ec0\u4e48\u6012\u6c14\u8fd9\u65f6\u5019\u90fd\u5f97\u5fcd\u4e86\u3002</p><p>\u3000\u3000\u201c\u6211\u4e0d\u4ecb\u610f\u4f60\u62ff\u51fa\u81ea\u5df1\u7684\u94b1\u53bb\u6295\u8d44\u4e00\u4e9b\u4ea7\u4e1a\uff0c\u4f46\u662f\u79d1\u738b\u6709\u4ec0\u4e48\u5b9e\u529b\u8ddf\u5609\u4fe1\u7535\u5b50\u5408\u4f5c\uff1f\u201d\u845b\u660e\u4fe1\u7ad9\u5728\u7a7f\u8863\u955c\u524d\u770b\u5de6\u773c\u7a9d\u7ea2\u80bf\u7684\u7a0b\u5ea6\uff0c\u5fc3\u91cc\u60f3\u54ed\u80bf\u773c\u775b\u4e5f\u5c31\u8fd9\u6a21\u6837\uff0c\u53ea\u662f\u53f3\u773c\u7a9d\u592a\u4e0d\u8c10\u8c03\uff0c\u662f\u4e0d\u662f\u8981\u81ea\u5df1\u5f80\u8fd9\u91cc\u6253\u4e00\u62f3\uff1f\u901a\u8fc7\u7a7f\u8863\u955c\u770b\u7740\u7ad9\u5728\u4ed6\u8eab\u540e\u7684\u513f\u5b50\uff0c\u201c\u5609\u4fe1\u7535\u5b50\u6295\u8d44\u4e00\u5bb6\u516c\u53f8\uff0c\u4f1a\u53ea\u5728\u90a3\u5bb6\u4f01\u4e1a\u53ea\u5360\u670910\uff05\u6216\u800520\uff05\u7684\u80a1\u4efd\uff1f\u201d</p><p>\u3000\u3000\u201c\u4ed6\u4eec\u521b\u5efa\u79d1\u738b\u5df2\u7ecf\u4ed8\u51fa\u4e00\u4e9b\u4ee3\u4ef7\u4e0e\u7cbe\u529b\uff0c\u5728\u4ed6\u4eec\u611f\u53d7\u5230\u5609\u4fe1\u7535\u5b50\u7684\u5b9e\u529b\u4e4b\u524d\uff0c\u60f3\u8981\u4ed6\u4eec\u8ba9\u51fa\u66f4\u591a\u7684\u80a1\u4efd\uff0c\u53ea\u6015\u6709\u4e9b\u96be\u5ea6\uff0c\u7238\u7238\uff0c\u4f60\u4e0d\u662f\u7ecf\u5e38\u544a\u8bc9\u6211\uff0c\u996d\u8981\u4e00\u53e3\u4e00\u53e3\u7684\u5403\u5417\uff1f\u201d\u845b\u836b\u5747\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u5475\u5475\u2026\u2026\u201d\u8fd9\u5927\u6982\u662f\u845b\u660e\u4fe1\u4eca\u665a\u4e0a\u542c\u5230\u60df\u4e00\u8ba9\u4ed6\u5fc3\u91cc\u7a0d\u597d\u8fc7\u4e9b\u7684\u8bdd\uff0c\u201c\u4ed6\u4eec\u65e2\u7136\u80fd\u5728\u5341\u591a\u5e74\u7684\u65f6\u95f4\u91cc\u5728\u5185\u5730\u521b\u4e0b\u8fd9\u4e48\u5927\u7684\u8d22\u5bcc\uff0c\u4e5f\u662f\u4e0d\u7b80\u5355\u7684\u4eba\uff0c\u8981\u662f\u5728\u5e73\u65f6\uff0c\u6211\u5012\u53ef\u4ee5\u62bd\u51fa\u4e9b\u65f6\u95f4\u8ddf\u4ed6\u4eec\u804a\u4e00\u804a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u773c\u4e0b\u789f\u673a\u4ea7\u4e1a\u5728\u5185\u5730\u5f88\u706b\u7206\uff0c\u800c\u4e14\u4e1c\u5357\u4e9a\u3001\u5357\u4e9a\u7b49\u5730\u533a\u7684\u65b0\u5174\u5e02\u573a\uff0c\u90fd\u6709\u6781\u5927\u7684\u6f5c\u529b\uff0c\u5374\u8fd8\u662f\u521a\u521a\u5f00\u53d1\u751a\u81f3\u8fd8\u662f\u6ca1\u6709\u5f00\u53d1\u8fc7\u7684\u5904\u5973\u5730\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u542c\u8d77\u6765\u86ee\u6709\u5438\u5f15\u529b\uff0c\u4f46\u662f\u8fd9\u65f6\u5019\u8c01\u6709\u5fc3\u601d\u7ba1\u8fd9\u4e2a\uff1f\u8981\u4e0d\u8fd9\u6837\uff0c\u8fc7\u4e00\u4e24\u4e2a\u6708\uff0c\u7b49\u8fd9\u8fb9\u7684\u4e8b\u60c5\u5e73\u606f\u4e86\uff0c\u518d\u8ddf\u4ed6\u4eec\u5177\u4f53\u8c08\u5408\u4f5c\u7684\u4e8b\u60c5\uff1f\u201d</p><p>\u3000\u3000\u201c\u90a3\u8981\u7b49\u5230\u4ec0\u4e48\u65f6\u5019\uff1f\u9ec4\u82b1\u83dc\u90fd\u51c9\u4e86\u3002\u201d\u845b\u836b\u5747\u4e0d\u6ee1\u7684\u8bf4\u9053\uff0c\u201c\u4f60\u8981\u662f\u4e0d\u540c\u610f\uff0c\u90a3\u6211\u5c31\u8003\u8651\u4e2a\u4eba\u51fa\u8d44\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4ed6\u4eec\u8981\u662f\u613f\u610f\uff0c\u6211\u4e5f\u4e0d\u4ecb\u610f\u3002\u201d</p><p>\u3000\u3000\u79d1\u738b\u627e\u4e0a\u95e8\u6765\u5408\u4f5c\uff0c\u5c31\u662f\u770b\u4e2d\u5609\u4fe1\u7535\u5b50\u5728\u6280\u672f\u4e0a\u7684\u5f3a\u52b2\u5b9e\u529b\u4e0e\u5728\u4e1c\u5357\u4e9a\u6e20\u9053\u4e0a\u7684\u8d44\u6e90\uff0c\u53ea\u8ddf\u845b\u836b\u5747\u4e2a\u4eba\u5408\u4f5c\uff0c\u5c31\u5931\u51fa\u6b64\u884c\u6700\u5927\u7684\u76ee\u7684\uff1b\u845b\u660e\u4fe1\u80fd\u6267\u638c\u5609\u4fe1\u96c6\u56e2\u5341\u4e94\u5e74\uff0c\u8fd9\u4e9b\u9053\u9053\u8fd8\u662f\u4e00\u773c\u5c31\u80fd\u770b\u51fa\u6765\u7684\u3002</p><p>\u3000\u3000\u845b\u836b\u5747\u4e5f\u4e0d\u662f\u4e0d\u660e\u767d\u5176\u4e2d\u7684\u9053\u7406\uff0c\u4f46\u662f\u4e8b\u5b9e\u8bf4\u51fa\u6765\u591a\u5c11\u8ba9\u4eba\u96be\u4ee5\u63a5\u53d7\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u8c22\u6c49\u9756\u3001\u8c22\u5251\u5357\u4ed6\u4eec\u4e00\u884c\u4eba\u4f4f\u5728\u79bb\u5f00\u9999\u6e2f\u957f\u8475\u533b\u5b66\u9662\u4e0d\u8fdc\u7684\u9999\u6e2f\u827e\u7f8e\u56fd\u9645\u9152\u5e97\uff0c\u672c\u60f3\u501f\u540a\u5501\u7684\u673a\u4f1a\uff0c\u4e0e\u5609\u4fe1\u96c6\u56e2\u5b9e\u9645\u4e0a\u7684\u638c\u95e8\u4eba\u845b\u660e\u4fe1\u8bf4\u4e0a\u8bdd\uff0c\u6ca1\u60f3\u5230\u6628\u591c\u53bb\u7075\u5802\u540a\u5501\u5374\u9047\u4e0a\u845b\u5bb6\u5185\u90e8\u7684\u62f3\u811a\u7eb7\u4e89\uff0c\u770b\u5230\u845b\u660e\u4fe1\u7ed9\u90a3\u4e2a\u845b\u836b\u5747\u53eb\u4e09\u53d4\u7684\u4e2d\u5e74\u7537\u4eba\u4e00\u62f3\u6253\u5728\u773c\u7a9d\u4e0a\uff0c\u4e00\u65c1\u7684\u4fdd\u9556\u53d1\u5e94\u8fdf\u949d\u4e86\u4e24\u4e09\u79d2\u624d\u51b2\u4e0a\u53bb\u5c06\u53d1\u751f\u80a2\u4f53\u51b2\u7a81\u7684\u4e24\u4e2a\u4eba\u62c9\u5f00\uff0c\u5374\u8ba9\u845b\u660e\u4fe1\u5c41\u80a1\u4e0a\u591a\u6328\u4e86\u4e00\u811a\u3002</p><p>\u3000\u3000\u8fd9\u79cd\u60c5\u5f62\u81ea\u7136\u6ca1\u6709\u673a\u4f1a\u4e0a\u524d\u8ddf\u845b\u660e\u4fe1\u8bf4\u4e0a\u8bdd\uff1b\u5bf9\u4e8e\u845b\u660e\u4fe1\u6765\u8bf4\uff0c\u773c\u4e0b\u6700\u91cd\u8981\u7684\u4e8b\u60c5\u662f\u5904\u7406\u5bb6\u65cf\u5185\u90e8\u7684\u7ea0\u7eb7\uff0c\u672a\u5fc5\u6709\u5174\u8da3\u5173\u6ce8\u5408\u4f5c\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u5bf9\u4e8e\u79d1\u738b\u6765\u8bf4\uff0c\u773c\u4e0b\u5374\u662f\u5206\u79d2\u5fc5\u4e89\u7684\u65f6\u523b\uff0c\u5df2\u7ecf\u843d\u540e\u7231\u8fbe\u7535\u5b50\u592a\u591a\uff0c\u6ca1\u6709\u8010\u5fc3\u518d\u7b49\u4e0a\u4e24\u4e09\u4e2a\u6708\u7684\u65f6\u95f4\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u966a\u8c22\u6c49\u9756\u8fd9\u6b21\u501f\u540a\u5501\u4e4b\u540d\u5230\u9999\u6e2f\uff0c\u5012\u4e0d\u4f1a\u60f3\u7740\u5728\u5609\u4fe1\u7535\u5b50\u8fd9\u4e00\u68f5\u5927\u6811\u4e0a\u540a\u6b7b\u3002\u4e5d\u516d\u5e74\uff0c\u9999\u6e2f\u7535\u5b50\u4ea7\u4e1a\u5f00\u59cb\u5411\u73e0\u4e09\u89d2\u5730\u533a\u8f6c\u79fb\uff0c\u7814\u53d1\u4e2d\u5fc3\u4e5f\u8ddf\u7740\u5411\u73e0\u4e09\u89d2\u5730\u533a\u8f6c\u79fb\uff0c\u4f46\u662f\u4ea7\u4e1a\u7684\u91cd\u5fc3\u8fd8\u6ca1\u6709\u5b8c\u5168\u4ece\u9999\u6e2f\u79fb\u51fa\uff0c\u8fd8\u6709\u4e00\u4e9b\u76f8\u5f53\u6709\u5b9e\u529b\u7684\u7535\u5b50\u4f01\u4e1a\u8fd8\u7559\u5728\u9999\u6e2f\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u5230\u9999\u6e2f\u4e4b\u524d\u6392\u4e86\u4e00\u4efd\u540d\u5355\uff0c\u8003\u8651\u5230\u4e0e\u5609\u4fe1\u7535\u5b50\u7684\u5408\u4f5c\u6ca1\u6709\u8fdb\u5c55\uff0c\u5c31\u8981\u4e3b\u52a8\u8ddf\u9999\u6e2f\u5176\u4ed6\u7684\u7535\u5b50\u4f01\u4e1a\u8054\u7cfb\u3002</p><p>\u3000\u3000\u968f\u884c\u4eba\u5458\u5df2\u7ecf\u4e0e\u9999\u6e2f\u88d5\u6210\u7535\u5b50\u96c6\u56e2\u8054\u7cfb\u597d\uff0c\u8c22\u5251\u5357\u5230\u4e8c\u53d4\u8c22\u6c49\u9756\u7684\u623f\u95f4\u544a\u8bc9\u4ed6\u4e00\u58f0\uff0c\u6572\u5f00\u95e8\uff0c\u770b\u89c1\u4e8c\u53d4\u5750\u5728\u6c99\u53d1\u4e0a\u770b\u7535\u89c6\uff0c\u4ed6\u5c31\u7ad9\u5728\u7384\u5173\u5904\u6ca1\u6709\u8fdb\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u4e8c\u53d4\uff0c\u6211\u5c31\u53bb\u88d5\u6210\u7535\u5b50\uff0c\u4f60\u4e0a\u5348\u7559\u5728\u5bbe\u9986\u4f11\u606f\u4e00\u4e0b\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u5148\u8fc7\u6765\u966a\u6211\u770b\u4e00\u4f1a\u513f\u65b0\u95fb\u2026\u2026\u201d\u8c22\u6c49\u9756\u62cd\u4e86\u62cd\u8eab\u8fb9\u7684\u6c99\u53d1\uff0c\u8ba9\u4ed6\u5750\u8fc7\u53bb\uff0c\u201c\u65f6\u95f4\u8fd8\u65e9\uff0c\u8981\u6709\u4e9b\u8010\u5fc3\uff0c\u8fd9\u4e2a\u662f\u9999\u6e2f\u7684\u65e0\u7ebf\u7535\u89c6\u53f0\u5427\uff0c\u65b0\u95fb\u8282\u76ee\u4e0d\u50cf\u5185\u5730\u5047\u5927\u7a7a\u3002\u9664\u4e86\u845b\u5bb6\uff0c\u6211\u4eec\u5728\u9999\u6e2f\u6ca1\u6709\u4ec0\u4e48\u4eba\u8109\u57fa\u7840\uff0c\u505a\u4ec0\u4e48\u4e8b\u60c5\u90fd\u6f2b\u65e0\u5934\u7eea\uff0c\u5750\u4e0b\u6765\u770b\u4e00\u770b\u7535\u89c6\u65b0\u95fb\u8fd8\u662f\u6709\u597d\u5904\u7684\uff0c\u6216\u8bb8\u80fd\u627e\u5230\u5934\u7eea\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4e2d\u592e\u7535\u89c6\u53f0\u7684\u65b0\u95fb\u8054\u64ad\u4f60\u4e0d\u4e00\u6837\u5929\u5929\u575a\u6301\u5728\u770b\uff1f\u201d</p><p>\u3000\u3000\u201c\u65b0\u95fb\u8054\u64ad\u7684\u5185\u5bb9\u867d\u7136\u5f88\u67af\u71e5\u4e4f\u5473\uff0c\u53ef\u4ee5\u8bf4\u662f\u653f\u7b56\u6027\u7684\u65b0\u95fb\u8282\u76ee\uff0c\u4f46\u5185\u5730\u672c\u8eab\u5c31\u662f\u4e00\u4e2a\u653f\u7b56\u6027\u5f88\u5f3a\u7684\u5e02\u573a\uff0c\u65b0\u95fb\u8054\u64ad\u4f60\u80fd\u4e0d\u770b\uff1f\u201d\u8c22\u6c49\u9756\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u770b\u4e86\u770b\u624b\u8868\uff0c\u8fd8\u6709\u4e00\u4e9b\u65f6\u95f4\uff0c\u4fbf\u5750\u4e86\u4e0b\u6765\uff0c\u662f\u65e0\u7ebf\u65b0\u95fb\u53f0\uff0c\u6b63\u64ad\u653e\u5468\u661f\u9a70\u79fb\u6c11\u52a0\u62ff\u5927\u7533\u8bf7\u88ab\u52a0\u62ff\u5927\u9a7b\u6e2f\u516c\u7f72\u4e13\u5458\u62d2\u7b7e\u7684\u65b0\u95fb\uff0c\u8c22\u5251\u5357\u5bf9\u8fd9\u7c7b\u5a31\u4e50\u65b0\u95fb\u4e00\u5411\u4e0d\u611f\u5174\u8da3\uff0c\u800c\u504f\u504f\u9999\u6e2f\u7684\u5a92\u4f53\u4e0e\u516c\u4f17\u5374\u559c\u6b22\u8ffd\u9010\u6b64\u7c7b\u7684\u65b0\u95fb\uff0c\u8c22\u5251\u5357\u6ca1\u60f3\u5230\u4ed6\u4e8c\u53d4\u5012\u662f\u559c\u6b22\u770b\u8fd9\u4e9b\u516b\u5366\u3002\u4ed6\u6b63\u8981\u627e\u501f\u53e3\u5148\u8d70\uff0c\u5c4f\u5e55\u4e0b\u65b9\u6eda\u52a8\u6253\u51fa\u4e00\u884c\u5b57\u5e55\u9884\u544a\u4e0b\u4e00\u5219\u65b0\u95fb\u7684\u5185\u5bb9\uff1a\u5609\u4fe1\u5b9e\u4e1a\u603b\u88c1\u5085\u5bb6\u4fca\u53ec\u5f00\u8bb0\u8005\u4f1a\u4e0e\u845b\u6c0f\u5bb6\u65cf\u4f01\u4e1a\u5212\u6e05\u754c\u9650\u2026\u2026</p><p>\u3000\u3000\u8c22\u5251\u5357\u770b\u4e86\u8fd9\u884c\u5b57\u5e55\u6709\u4e9b\u53d1\u6123\uff0c\u4e0d\u53ef\u7f6e\u4fe1\u7684\u770b\u7740\u8c22\u6c49\u9756\uff0c\u95ee\u9053\uff1a\u201c\u4e8c\u53d4\uff0c\u4ed6\u4eec\u60f3\u505a\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u4e5f\u624d\u770b\u5230\u65b0\u95fb\u9884\u544a\uff0c\u6709\u4e9b\u8010\u5fc3\uff0c\u201d\u8c22\u6c49\u9756\u8bf4\u9053\uff0c\u201c\u845b\u660e\u4fe1\u6628\u5929\u7ed9\u6253\u4e00\u62f3\uff0c\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5fb7\u597d\u50cf\u4e5f\u662f\u51b7\u773c\u65c1\u89c2\u7684\uff0c\u4e0d\u8fc7\u8fd9\u79cd\u516c\u5f00\u5212\u6e05\u754c\u9650\u561b\u2026\u2026\u5c31\u8981\u770b\u4ed6\u4eec\u600e\u4e48\u5212\u6e05\u6cd5\u4e86\u3002\u201d</p><p>\u3000\u3000\u5c4f\u5e55\u91cc\u7684\u955c\u5934\uff0c\u4f3c\u4e4e\u662f\u5085\u5bb6\u4fca\u51fa\u95e8\u7ed9\u5a92\u4f53\u8bb0\u8005\u56f4\u4f4f\u88ab\u8feb\u505c\u4e0b\u6765\u53d1\u8868\u4e00\u901a\u58f0\u660e\uff0c\u8c22\u5251\u5357\u77e5\u9053\u8fd9\u4e0d\u8fc7\u662f\u4f5c\u79c0\uff0c\u845b\u666f\u8bda\u4e00\u6b7b\uff0c\u7275\u52a8\u9999\u6e2f\u5a92\u4f53\u7684\u5fc3\u601d\uff0c\u5085\u5bb6\u4fca\u8fd8\u8fdc\u8fdc\u6210\u4e3a\u4e0d\u4e86\u7126\u70b9\uff0c\u4e0d\u8fc7\u4ed6\u5bf9\u5a92\u4f53\u53d1\u8868\u7684\u58f0\u660e\uff0c\u5374\u80fd\u8ba9\u4ed6\u6210\u4e3a\u5a92\u4f53\u7684\u7126\u70b9\u3002</p><p>\u3000\u3000\u5085\u5bb6\u4fca\u5ba3\u79f0\u539f\u845b\u6c0f\u5bb6\u65cf\u540d\u4e0b\u62e5\u6709\u5609\u4fe1\u5b9e\u4e1a\u8fd130\uff05\u7684\u80a1\u4efd\u65e9\u5728\u845b\u666f\u8bda\u751f\u524d\u5c31\u8f6c\u79fb\u5230\u845b\u660e\u5fb7\u3001\u5b59\u5c1a\u4e49\u4e2a\u4eba\u540d\u4e0b\uff0c\u8fd9\u5728\u4e4b\u524d\u7684\u8d22\u5b63\u516c\u544a\u4e2d\u5df2\u7ecf\u6279\u9732\uff0c\u5609\u4fe1\u5b9e\u4e1a\u8fd1\u4e00\u5e74\u6765\u7ecf\u8425\u80fd\u9010\u6e10\u8d70\u51fa\u56f0\u5883\uff0c\u6b63\u56e0\u4e3a\u662f\u8d22\u52a1\u4e0a\u3001\u7ecf\u8425\u4e0a\u4e0e\u845b\u6c0f\u5b8c\u5168\u8131\u79bb\u5173\u7cfb\u540e\u53d6\u5f97\u7684\u6210\u5c31\uff0c\u5e76\u8868\u793a\u5609\u4fe1\u5b9e\u4e1a\u7684\u7ecf\u8425\u6d3b\u52a8\u4e0d\u4f1a\u53d7\u5230\u8fd9\u6b21\u4e8b\u4ef6\u7684\u5e72\u6270\uff0c\u516c\u53f8\u4e5f\u4f1a\u62e9\u65e5\u53d1\u5e03\u4e5d\u516d\u5e74\u7b2c\u56db\u8d22\u5b63\u76c8\u5229\u5927\u5e45\u9884\u589e\u7684\u516c\u544a\uff0c\u76c8\u5229\u9884\u589e\u4e3b\u8981\u6765\u81ea\u4e8e\u4e1c\u5357\u533a\u789f\u673a\u4e1a\u52a1\u7684\u5c55\u5f00\uff0c\u5e76\u8868\u793a\u4ece\u8fd9\u4e2a\u6708\u5f00\u59cb\uff0c\u516c\u53f8\u4f1a\u8ffd\u52a0\u5bf9\u4e1c\u5357\u4e9a\u5730\u533a\u789f\u673a\u4e1a\u52a1\u7684\u6295\u8d44\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u4ed6\u4eec\u6216\u8bb8\u4f1a\u5173\u6ce8\u5085\u5bb6\u4fca\u58f0\u660e\u4e2d\u6709\u5173\u5f71\u789f\u673a\u7684\u5185\u5bb9\uff0c\u4e0d\u8fc7\u9999\u6e2f\u5a92\u4f53\u5374\u66f4\u5173\u6ce8\u58f0\u660e\u4e2d\u8981\u4e0e\u845b\u6c0f\u5bb6\u65cf\u4f01\u4e1a\u5212\u6e05\u754c\u9650\u7684\u8868\u8fbe\u2026\u2026</p><p>\u3000\u3000\u201c\u845b\u660e\u4fe1\u7b97\u662f\u80cc\u540e\u7ed9\u6345\u4e86\u4e00\u5200\uff1f\u201d\u8c22\u5251\u5357\u7591\u60d1\u4e0d\u89e3\u7684\u95ee\u4ed6\u4e8c\u53d4\uff0c\u8fd9\u5219\u58f0\u660e\u7b80\u76f4\u5c31\u662f\u5c06\u845b\u5bb6\u5185\u90e8\u7684\u77db\u76fe\u5411\u5a92\u4f53\u3001\u516c\u4f17\u516c\u5f00\uff1b\u8fd9\u504f\u504f\u53c8\u662f\u845b\u660e\u4fe1\u8fd9\u65f6\u5019\u6700\u4e0d\u613f\u610f\u770b\u5230\u7684\u3002</p><p>\u3000\u3000\u8c22\u6c49\u9756\u624b\u6258\u7740\u4e0b\u5df4\uff0c\u6ca1\u6709\u7acb\u5373\u56de\u7b54\u8c22\u5251\u5357\uff0c\u53ea\u662f\u95ee\u4ed6\uff1a\u201c\u8fd9\u5219\u58f0\u660e\uff0c\u5609\u4fe1\u5b9e\u4e1a\u6709\u4ec0\u4e48\u7acb\u7aff\u89c1\u5f71\u7684\u597d\u5904\uff1f\u201d</p><p>\u3000\u3000\u201c\u4ece\u845b\u6c0f\u5bb6\u65cf\u5185\u90e8\u7eb7\u4e89\u7684\u6ce5\u6f6d\u4e2d\u8131\u8eab\u51fa\u6765\uff0c\u7ef4\u6301\u6295\u8d44\u4eba\u5bf9\u5609\u4fe1\u5b9e\u4e1a\u7684\u4fe1\u5fc3\uff0c\u201d\u8c22\u5251\u5357\u8f6c\u77ac\u95f4\u5c31\u80fd\u60f3\u660e\u767d\u8fd9\u5219\u58f0\u660e\u6240\u8981\u4f20\u8fbe\u7ed9\u516c\u4f17\u7684\u610f\u56fe\uff0c\u201c\u53ea\u662f\u4ed6\u4eec\u4f3c\u4e4e\u592a\u64cd\u4e4b\u8fc7\u6025\u4e86\u2026\u2026\u201d\u845b\u6c0f\u5bb6\u65cf\u5185\u90e8\u7684\u77db\u76fe\uff0c\u906e\u662f\u906e\u4e0d\u4f4f\u7684\uff0c\u9999\u6e2f\u5a92\u4f53\u8bb0\u8005\u7684\u80fd\u529b\uff0c\u8c22\u5251\u5357\u5fc3\u91cc\u6709\u6570\uff0c\u8fdf\u65e9\u4f1a\u5c06\u845b\u5bb6\u5185\u90e8\u56e0\u8d22\u4ea7\u4e89\u593a\u800c\u4ea7\u751f\u7684\u7ea0\u7eb7\u62ab\u9732\u51fa\u6765\uff0c\u4f46\u662f\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5fb7\u4f3c\u4e4e\u6ca1\u6709\u5fc5\u8981\u5f53\u8fd9\u4e2a\u51fa\u5934\u693d\u5b50\uff0c\u5b9e\u9645\u4e0a\u5df2\u7ecf\u8131\u5f00\u5173\u7cfb\uff0c\u5373\u4f7f\u4e00\u65f6\u4f1a\u53d7\u5230\u4e9b\u5f71\u54cd\uff0c\u4e5f\u80fd\u5f88\u5feb\u4f1a\u626d\u8f6c\u8fc7\u6765\uff1b\u4e00\u65f6\u7684\u80a1\u4ef7\u6ce2\u52a8\uff0c\u5bf9\u5927\u80a1\u4e1c\u6765\u8bf4\u5e94\u8be5\u6ca1\u6709\u592a\u5927\u7684\u5f71\u54cd\u3002</p><p>\u3000\u3000\u201c\u4ece\u54ea\u91cc\u80fd\u67e5\u8be2\u5609\u4fe1\u96c6\u56e2\u65d7\u4e0b\u4e0a\u5e02\u516c\u53f8\u7684\u80a1\u4ef7\uff1f\u201d\u8c22\u6c49\u9756\u95ee\u9053\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u770b\u4e86\u770b\u624b\u8868\uff0c\u8bf4\u9053\uff1a\u201c\u9999\u6e2f\u80a1\u5e02\u5f00\u76d8\u6bd4\u56fd\u5185\u8981\u665a\u4e00\u4e9b\uff0c\u8fd8\u6709\u534a\u4e2a\u5c0f\u65f6\u624d\u5f00\u76d8\u2026\u2026\u4f46\u662f\u80fd\u770b\u51fa\u8fd9\u5219\u58f0\u660e\u5bf9\u845b\u6c0f\u7684\u5176\u4ed6\u5bb6\u65cf\u4f01\u4e1a\u6709\u5f88\u5927\u7684\u8d1f\u9762\u5f71\u54cd\u3002\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u201d\u8c22\u6c49\u9756\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u6211\u4eec\u662f\u4e0d\u662f\u5e94\u8be5\u518d\u53bb\u62dc\u8bbf\u845b\u660e\u4fe1\u4e00\u4e0b\uff1f\u6211\u60f3\u4ed6\u770b\u5230\u8fd9\u5219\u65b0\u95fb\u5e94\u8be5\u4f1a\u5f88\u751f\u6c14\u5427\uff01\u201d</p></article>"}}}]);
