(window.webpackJsonp=window.webpackJsonp||[]).push([[469],{491:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e00\u8282 \u4ea4\u6613",content:"<article><h2>\u7b2c\u4e00\u8282 \u4ea4\u6613</h2><p>\u62c9\u52a0\u897f\u4e9a\u66fc\u7279\u65af\u7684\u5fc3\u60c5\u73b0\u5728\u5f88\u597d\uff0c\u975e\u5e38\u597d\uff0c\u597d\u5f97\u4e00\u584c\u7cca\u6d82\u3002</p><p>\u81ea\u4ece\u63a5\u4e0b\u5bfb\u627e\u661f\u9668\u57ce\u7684\u4efb\u52a1\uff0c\u6765\u5230\u6851\u6bd4\u4e9a\u540e\uff0c\u574f\u8fd0\u6c14\u4f3c\u4e4e\u5c31\u4e00\u76f4\u4f34\u968f\u7740\u4ed6\uff0c\u63a5\u8fde\u4e0d\u65ad\u7684\u632b\u6298\u548c\u6253\u51fb\uff0c\u8ba9\u4ed6\u7070\u5fc3\u4e27\u6c14\u5f97\u51e0\u4e4e\u60f3\u81ea\u6740\u2014\u2014\u4f46\u73b0\u5728\u4ed6\u7ec8\u4e8e\u8f6c\u8fd0\u4e86\u3002</p><p>\u867d\u7136\u5b8c\u5168\u8bb0\u4e0d\u6e05\u81ea\u5df1\u662f\u600e\u4e48\u5e72\u6389\u4e86\u6d77\u795e\u5b89\u535a\u91cc\uff0c\u4e5f\u534a\u70b9\u60f3\u4e0d\u8d77\u81ea\u5df1\u662f\u600e\u4e48\u6253\u9000\u4e86\u5149\u5934\u72ec\u773c\u7267\u5e08\u5361\u65af\u56fe\u7b49\u4eba\uff0c\u751a\u81f3\u90fd\u4e0d\u8bb0\u5f97\u81ea\u5df1\u4e00\u884c\u4eba\u4e3a\u4f55\u4f1a\u4e56\u4e56\u7b49\u5f85\u5728\u5927\u6bbf\u4e4b\u5916\u2014\u2014\u4f46\u8fd9\u4e9b\u5168\u90fd\u4e0d\u91cd\u8981\uff0c\u4e0d\u5173\u952e\uff0c\u4e0d\u9700\u8981\u5728\u610f\u3002\u53ea\u9700\u8981\u77e5\u9053\u4e00\u4ef6\u4e8b\u5c31\u8db3\u591f\u4e86\uff1a\u5965\u5609\u83b1\u65af\u7b54\u5e94\u5c06\u5979\u7684\u661f\u9668\u57ce\u79fb\u4ea4\u7ed9\u9634\u9b42\u57ce\u4e3b\u590f\u591a\u201c\u4ee3\u4e3a\u4fdd\u7ba1\u201d\u3002</p><p>\u4ee3\u4e3a\u4fdd\u7ba1\u4e5f\u7f62\uff0c\u76f4\u63a5\u8f6c\u8ba9\u4e5f\u7f62\uff0c\u53cd\u6b63\u9634\u9b42\u57ce\u5b9e\u9645\u62ff\u5230\u4e86\u3002\u4e00\u5ea7\u5b8c\u6574\u7684\u6d6e\u7a7a\u57ce\uff0c\u4e00\u9897\u8fd0\u4f5c\u826f\u597d\u7684\u5bc6\u745f\u80fd\u6838\uff0c\u8fd9\u610f\u5473\u7740\u4ec0\u4e48\uff0c\u522b\u4eba\u6216\u8bb8\u4e0d\u77e5\u9053\uff0c\u4f5c\u4e3a\u9634\u9b42\u57ce\u9ad8\u9636\u5deb\u5e08\u7684\u62c9\u52a0\u800c\u8a00\u5374\u662f\u6e05\u695a\u5f97\u4e0d\u80fd\u518d\u6e05\u695a\u3002\u9634\u9b42\u57ce\u4e4b\u6240\u4ee5\u80fd\u591f\u4ee5\u4e00\u57ce\u4e4b\u5730\u4fef\u77b0\u5927\u9646\uff0c\u5174\u98ce\u4f5c\u6d6a\u800c\u5c79\u7acb\u4e0d\u5012\uff0c\u4e00\u534a\u662f\u56e0\u4e3a\u6709\u5927\u6279\u7cbe\u82f1\u4eba\u624d\uff0c\u53e6\u4e00\u534a\u4fbf\u662f\u56e0\u4e3a\u6709\u6d6e\u7a7a\u57ce\u505a\u4f9d\u6258\uff0c\u6709\u5bc6\u745f\u80fd\u6838\u8425\u9020\u51fa\u7684\u5f3a\u5927\u4e3b\u573a\u4f18\u52bf\uff0c\u5305\u62ec\u8ff7\u9501\u3001\u5404\u79cd\u51c6\u9b54\u6cd5\u7269\u54c1\uff0c\u4ee5\u53ca\u67d0\u4e9b\u552f\u6709\u5728\u5bc6\u745f\u80fd\u6838\u7684\u652f\u6301\u4e0b\u624d\u80fd\u91ca\u653e\u51fa\u7684\u6cd5\u672f\u3002\u5982\u4eca\u591a\u4e86\u4e00\u5ea7\u6d6e\u7a7a\u57ce\uff0c\u5c31\u76f8\u5f53\u73a9\u5373\u65f6\u6218\u7565\u6e38\u620f\uff0c\u5929\u4e0a\u6389\u4e0b\u6765\u4e00\u4e2a\u5206\u57fa\u5730\uff0c\u5176\u4ef7\u503c\u53ef\u60f3\u800c\u77e5\u3002\u800c\u4f5c\u4e3a\u6b64\u6b21\u884c\u52a8\u7684\u8d1f\u8d23\u4eba\uff0c\u62c9\u52a0\u81ea\u7136\u662f\u9996\u529f\uff0c\u5347\u804c\u664b\u9636\u4ec0\u4e48\u7684\u5168\u4e0d\u5728\u8bdd\u4e0b\uff0c\u81f3\u4e8e\u6298\u635f\u4e86\u51e0\u4e2a\u4eba\u624b\uff0c\u5728\u8fd9\u6837\u5927\u7684\u529f\u52b3\u9762\u524d\uff0c\u9876\u591a\u88ab\u65a5\u8d23\u51e0\u53e5\uff0c\u8c61\u5f81\u6027\u7ed9\u4e2a\u5904\u5206\uff0c\u5c31\u5b8c\u5168\u65e0\u9700\u5728\u610f\u4e86\u3002</p><p>\u65e2\u7136\u6765\u641c\u5bfb\u6d6e\u7a7a\u57ce\uff0c\u81ea\u7136\u4e5f\u505a\u597d\u4e86\u76f8\u5e94\u7684\u51c6\u5907\uff0c\u53c8\u5f97\u5230\u5965\u5609\u83b1\u65af\u7684\u6307\u70b9\uff0c\u7ecf\u8fc7\u6574\u6574\u5341\u4e8c\u4e2a\u5c0f\u65f6\u7684\u53cd\u590d\u5c1d\u8bd5\u548c\u4e0d\u65ad\u5931\u8d25\uff0c\u603b\u7b97\u662f\u6682\u65f6\u6027\u5730\u5c06\u5bc6\u745f\u80fd\u6838\u548c\u5f71\u9b54\u7f51\u6210\u529f\u8054\u7ed3\uff0c\u539f\u672c\u7480\u74a8\u707f\u70c2\u7684\u767d\u5149\u5185\u655b\uff0c\u53d8\u6210\u4e86\u6697\u7d2b\u7684\u6c34\u6676\u8272\uff0c\u4e00\u6ce2\u6ce2\u5e9e\u5927\u5230\u4e0d\u53ef\u601d\u8bae\u7684\u80fd\u91cf\u5411\u5916\u6ce2\u7eb9\u72b6\u6563\u53d1\u51fa\u53bb\u3002\u6d6e\u7a7a\u57ce\u9707\u52a8\u7740\uff0c\u6447\u6643\u7740\uff0c\u50cf\u662f\u4ece\u6c89\u7761\u4e2d\u82cf\u9192\uff0c\u6700\u540e\u5f00\u59cb\u5189\u5189\u4e0a\u5347\uff0c\u8d8a\u6765\u8d8a\u5feb\u3002\u6309\u7167\u76ee\u524d\u8fd9\u79cd\u901f\u5ea6\uff0c\u5927\u6982\u518d\u8fc7\u4e03\u516b\u4e2a\u5c0f\u65f6\u5c31\u80fd\u56de\u5230\u6d77\u9762\u4e0a\u4e86\u3002\u8131\u79bb\u4e86\u6d77\u5e95\u73af\u5883\uff0c\u9884\u8a00\u9b54\u6cd5\u4fbf\u4e0d\u53d7\u5e72\u6270\uff0c\u5230\u65f6\u5019\u518d\u4f20\u8baf\u56de\u9634\u9b42\u57ce\uff0c\u672c\u90e8\u81ea\u7136\u4f1a\u53e6\u6d3e\u5927\u961f\u4eba\u9a6c\u6765\u63a5\u6536\u57ce\u6c60\uff0c\u4e00\u5207\u5c31\u7b97\u662f\u5927\u529f\u544a\u6210\u3002</p><p>\u9664\u6b64\u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u6869\u4ee4\u62c9\u52a0\u5341\u5206\u9ad8\u5174\u7684\u4e8b\u60c5\uff0c\u4fbf\u662f\u4ed6\u89c1\u5230\u4e86\u81ea\u5df1\u5fc3\u76ee\u4e2d\u7684\u5076\u50cf\u3002</p><p>\u62c9\u52a0\u51fa\u8eab\u4e8e\u4e00\u4e2a\u4ee5\u7cbe\u7814\u5851\u80fd\u672f\u800c\u8457\u79f0\u7684\u5deb\u5e08\u5bb6\u65cf\uff0c\u5374\u901a\u8fc7\u81ea\u5df1\u7684\u4e0d\u61c8\u575a\u6301\uff0c\u6700\u7ec8\u5927\u5668\u665a\u6210\uff0c\u6210\u4e3a\u4e00\u540d\u6770\u51fa\u7684\u9884\u8a00\u5e08\u3002\u6240\u6709\u4eba\u90fd\u79f0\u8d5e\u4ed6\u7684\u52aa\u529b\uff0c\u4f46\u5f88\u5c11\u6709\u4eba\u77e5\u9053\u7684\u662f\uff0c\u62c9\u52a0\u771f\u6b63\u7684\u68a6\u60f3\uff0c\u5e76\u4e0d\u4ec5\u4ec5\u662f\u6210\u4e3a\u9884\u8a00\u5e08\uff0c\u800c\u662f\u6210\u4e3a\u4e00\u540d\u5360\u661f\u5e08\u3002\u516d\u5c81\u7684\u65f6\u5019\uff0c\u4ed6\u5728\u5bb6\u65cf\u8001\u5b85\u7684\u4ed3\u5e93\u89d2\u843d\u91cc\u627e\u5230\u4e00\u672c\u5360\u661f\u5e08\u7684\u4f20\u8bb0\uff0c\u88ab\u6df1\u6df1\u5438\u5f15\u4e86\uff0c\u800c\u5965\u5609\u83b1\u65af\u4f5c\u4e3a\u8010\u745f\u745e\u5c14\u5e1d\u56fd\u5386\u53f2\u4e0a\u6700\u8457\u540d\u7684\u4e24\u4f4d\u5360\u661f\u5e08\u4e4b\u4e00\uff0c\u5979\u7684\u4f20\u5947\u7ecf\u5386\u5728\u4e66\u4e2d\u5360\u4e86\u8fd1\u4e4e\u4e09\u5206\u4e4b\u4e00\u7684\u7bc7\u5e45\uff0c\u4e5f\u4ece\u6b64\u6210\u4e3a\u4e86\u62c9\u52a0\u7ae5\u5e74\u7684\u7f8e\u597d\u61a7\u61ac\u3002\u800c\u6b64\u65f6\u6b64\u523b\uff0c\u4ed6\u7684\u5076\u50cf\u4fbf\u6d3b\u751f\u751f\u2014\u2014\u597d\u5427\uff0c\u56e0\u4e3a\u5df2\u7ecf\u662f\u5e7d\u7075\uff0c\u6240\u4ee5\u5e94\u8be5\u662f\u201c\u6829\u6829\u5982\u751f\u201d\u2014\u2014\u5730\u7ad9\u5728\u81ea\u5df1\u9762\u524d\u3002</p><p>\u5c31\u50cf\u662f\u9752\u6da9\u7684\u7537\u5b69\u9047\u4e0a\u6697\u604b\u5df2\u4e45\u7684\u5973\u5b50\uff0c\u90a3\u79cd\u6fc0\u52a8\u4e2d\u6df7\u5408\u7740\u5fd0\u5fd1\u4e0d\u5b89\uff0c\u5fc3\u8df3\u901f\u5ea6\u4f3c\u4e4e\u90fd\u6bd4\u5e73\u65f6\u52a0\u5feb\u4e86\u51e0\u500d\uff0c\u8fde\u8bf4\u8bdd\u90fd\u6709\u4e9b\u7ed3\u7ed3\u5df4\u5df4\u8d77\u6765\u3002\u5e78\u597d\u5965\u5609\u83b1\u65af\u538b\u6839\u5c31\u6ca1\u6ce8\u610f\u5230\u4ed6\u7684\u5f02\u6837\uff0c\u4e4b\u6240\u4ee5\u53eb\u4ed6\u8fc7\u6765\uff0c\u53ea\u662f\u4e3a\u4e86\u8be2\u95ee\u81ea\u4ece\u8010\u745f\u745e\u5c14\u706d\u4ea1\u540e\uff0c\u8fd9\u4e00\u5343\u591a\u5e74\u6765\u53d1\u751f\u7684\u79cd\u79cd\u5927\u4e8b\u3002\u62c9\u52a0\u7cbe\u719f\u5386\u53f2\uff0c\u535a\u5b66\u5e7f\u77e5\uff0c\u867d\u7136\u5fc3\u60c5\u7d27\u5f20\uff0c\u4f46\u56de\u7b54\u8d77\u95ee\u9898\u6765\u5012\u4e5f\u6d41\u5229\u81f3\u6781\uff0c\u5168\u65e0\u6ede\u788d\uff0c\u8ba9\u5965\u5609\u83b1\u65af\u9887\u4e3a\u6ee1\u610f\u3002</p><p>\u4ea4\u8c08\u4e86\u4e00\u6bb5\u65f6\u95f4\uff0c\u62c9\u52a0\u5fc3\u60c5\u6e10\u6e10\u7a33\u5b9a\uff0c\u6b63\u51c6\u5907\u8bb2\u8ff0\u52a8\u8361\u5e74\u4ee3\uff0c\u7a81\u7136\u770b\u89c1\u743c\u6069\u4ece\u5916\u9762\u8d70\u4e86\u8fdb\u6765\uff0c\u8138\u8272\u9634\u6c89\uff0c\u660e\u663e\u538b\u6291\u7740\u6012\u6c14\u3002\u62c9\u52a0\u5fc3\u4e2d\u5947\u602a\uff0c\u6b63\u5f85\u8be2\u95ee\uff0c\u4fbf\u89c1\u5965\u5609\u83b1\u65af\u5fae\u5fae\u62ac\u624b\uff0c\u8ba9\u4ed6\u4e0b\u53bb\u3002</p><p>\u62c9\u52a0\u4f9d\u8a00\u9000\u51fa\uff0c\u623f\u95f4\u91cc\u53ea\u5269\u4e0b\u4e00\u4e2a\u4eba\u7c7b\u548c\u4e00\u4e2a\u5e7d\u7075\u3002\u201c\u600e\u4e48\uff1f\u201d\u5965\u5609\u83b1\u65af\u5750\u5728\u6905\u4e2d\uff0c\u4f4e\u5934\u7ffb\u7740\u5979\u90a3\u672c\u5de8\u5927\u7684\u4e66\uff0c\u6f2b\u4e0d\u7ecf\u5fc3\u5730\u95ee\uff0c\u201c\u5170\u5c3c\u65af\u7279\u5148\u751f\u6765\u627e\u6211\uff0c\u6709\u4ec0\u4e48\u6307\u6559\u7684\u5417\uff1f\u201d</p><p>\u743c\u6069\u6df1\u6df1\u5438\u53e3\u6c14\uff0c\u4e0d\u7406\u4f1a\u5979\u8bdd\u8bed\u4e2d\u7684\u5632\u8bbd\u610f\u5473\uff0c\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u903c\u8feb\u8299\u857e\u72c4\u548c\u8299\u8389\u5a05\u79bb\u5f00\uff1f\u201d\u4ed6\u76f4\u622a\u4e86\u5f53\u5730\u95ee\u3002</p><p>\u201c\u4f60\u662f\u6307\u90a3\u5bf9\u53cc\u80de\u80ce\u5417\uff1f\u201d</p><p>\u201c\u662f\u3002\u201d</p><p>\u201c\u54e6\uff0c\u5f88\u7b80\u5355\u554a\uff0c\u6211\u89c9\u5f97\u5979\u4eec\u5f88\u591a\u4f59\uff0c\u5f88\u788d\u4e8b\uff0c\u201d\u5965\u5609\u83b1\u65af\u8bf4\uff0c\u201c\u4f60\u4e0d\u89c9\u5f97\u81ea\u5df1\u8eab\u8fb9\u7684\u5973\u5b69\u5b50\u592a\u591a\u4e86\u5417\uff1f\u592a\u591a\u4e86\u4e0d\u597d\uff0c\u6240\u4ee5\u6211\u66ff\u4f60\u6253\u53d1\u4e24\u4e2a\u8d70\u3002\u201d</p><p>\u201c\u2026\u2026\u4f60\uff01\u201d</p><p>\u743c\u6069\u7ec8\u4e8e\u6309\u637a\u4e0d\u4f4f\u6012\u6c14\uff0c\u97f3\u8c03\u9661\u7136\u63d0\u9ad8\u8d77\u6765\u3002\u4ed6\u82cf\u9192\u4e4b\u540e\uff0c\u548c\u8299\u857e\u72c4\u4e91\u96e8\u6b22\u597d\uff0c\u6fc0\u60c5\u7f20\u7ef5\uff0c\u6b63\u6000\u62b1\u6e29\u9999\u8f6f\u7389\u822c\u7684\u7f8e\u4eba\u88f8\u4f53\uff0c\u56de\u5473\u54c1\u5c1d\u521a\u624d\u7684\u9ad8\u6f6e\u4f59\u97f5\uff0c\u7a81\u7136\u542c\u5230\u5c0f\u5973\u5b69\u8bf4\u201c\u6216\u8bb8\u662f\u6700\u540e\u4e00\u6b21\u4e86\u201d\uff0c\u767b\u65f6\u5413\u4e86\u4e00\u8df3\uff0c\u8fde\u5fd9\u95ee\u5230\u5e95\u600e\u4e48\u56de\u4e8b\u3002\u8299\u857e\u72c4\u521a\u8bf4\u51e0\u53e5\uff0c\u5373\u4fbf\u6ce3\u4e0d\u6210\u58f0\uff0c\u54ed\u5f97\u4eff\u4f5b\u68a8\u82b1\u5e26\u96e8\uff0c\u743c\u6069\u65e2\u662f\u7126\u6025\uff0c\u53c8\u662f\u601c\u60dc\uff0c\u95ee\u4e86\u534a\u664c\uff0c\u603b\u7b97\u624d\u542c\u5c0f\u5973\u5b69\u65ad\u65ad\u7eed\u7eed\u628a\u7ecf\u8fc7\u8bf4\u660e\u767d\u3002\u539f\u6765\u5f53\u65f6\u743c\u6069\u91cd\u4f24\u6fd2\u6b7b\uff0c\u65e0\u6cd5\u53ef\u6551\uff0c\u8299\u857e\u72c4\u81ea\u7136\u662f\u7126\u6025\u4e07\u5206\uff0c\u5728\u573a\u7684\u4eba\u4e2d\uff0c\u5965\u5609\u83b1\u65af\u662f\u5927\u5965\u672f\u5e08\uff0c\u89c1\u8bc6\u6700\u5e7f\uff0c\u529b\u91cf\u6700\u5f3a\uff0c\u5c0f\u5973\u5b69\u81ea\u7136\u5c31\u6c42\u5230\u5979\u5934\u4e0a\u3002\u5965\u5609\u83b1\u65af\u5012\u4e5f\u723d\u5feb\uff0c\u8bf4\u6551\u4eba\u53ef\u4ee5\uff0c\u4f46\u6709\u4e2a\u6761\u4ef6\uff0c\u4fbf\u662f\u4e00\u7b49\u5230\u6d6e\u7a7a\u57ce\u56de\u5230\u4e4c\u62c9\u65af\u76ae\u5c14\uff0c\u8299\u857e\u72c4\u548c\u8299\u8389\u5a05\u59d0\u59b9\u4e24\u4eba\u4fbf\u8981\u79bb\u5f00\u743c\u6069\uff0c\u4ece\u6b64\u518d\u4e5f\u4e0d\u80fd\u89c1\u9762\u3002</p><p>\u82e5\u5728\u5e73\u65f6\uff0c\u8981\u8299\u857e\u72c4\u79bb\u5f00\u743c\u6069\uff0c\u5c31\u7b97\u6740\u4e86\u5979\u53ea\u6015\u4e5f\u4f11\u60f3\u3002\u4f46\u6b64\u65f6\u743c\u6069\u6027\u547d\u60ac\u4e8e\u4e00\u7ebf\uff0c\u552f\u6709\u5965\u5609\u83b1\u65af\u8bf4\u5979\u6709\u6cd5\u80fd\u6551\uff0c\u65e0\u53ef\u5948\u4f55\u4e4b\u4e0b\uff0c\u8299\u857e\u72c4\u53ea\u80fd\u7b54\u5e94\u4e86\u8fd9\u4e2a\u6761\u4ef6\u3002\u4e4b\u540e\u7684\u4e8b\u60c5\u5c31\u5f88\u7b80\u5355\uff0c\u5965\u5609\u83b1\u65af\u7528\u67d0\u79cd\u65b9\u6cd5\u6551\u6d3b\u4e86\u743c\u6069\uff0c\u6240\u4ee5\u63a5\u4e0b\u6765\u8f6e\u5230\u8299\u857e\u72c4\u548c\u8299\u8389\u5a05\u9075\u5b88\u627f\u8bfa\uff0c\u5f85\u6d6e\u7a7a\u57ce\u98de\u51fa\u6d77\u9762\uff0c\u8fd4\u56de\u4e4c\u62c9\u65af\u76ae\u5c14\u4e4b\u540e\uff0c\u5979\u4eec\u5c31\u8981\u79bb\u5f00\u4e86\u3002\u800c\u4e14\u6309\u7167\u7ea6\u5b9a\u662f\u201c\u518d\u4e5f\u4e0d\u89c1\u9762\u201d\uff0c\u6240\u4ee5\u8299\u857e\u72c4\u624d\u4f1a\u8bf4\u662f\u201c\u6700\u540e\u4e00\u6b21\u201d\u4e86\u3002</p><p>\u743c\u6069\u542c\u5b8c\u7ecf\u8fc7\uff0c\u767b\u65f6\u52c3\u7136\u5927\u6012\u3002\u5982\u679c\u662f\u73ca\u5609\u6216\u8005\u6885\u83f2\u65af\u63d0\u51fa\u7c7b\u4f3c\u7684\u8981\u6c42\uff0c\u4ed6\u90fd\u8fd8\u80fd\u7406\u89e3\uff0c\u5927\u5bb6\u662f\u60c5\u654c\u561b\u2014\u2014\u5f53\u7136\u7406\u89e3\u5f52\u7406\u89e3\uff0c\u662f\u5426\u7167\u529e\u53c8\u662f\u53e6\u5916\u4e00\u56de\u4e8b\u3002\u4f46\u5965\u5609\u83b1\u65af\u4e00\u4e2a\u521a\u8ba4\u8bc6\u7684\u5916\u4eba\uff0c\u51ed\u4ec0\u4e48\u8981\u81ea\u5df1\u7684\u5973\u4eba\u79bb\u5f00\uff1f\u8fd9\u4e5f\u672a\u514d\u592a\u5c82\u6709\u6b64\u7406\u4e86\u5427\u3002</p><p>\u4ed6\u5f53\u5373\u8981\u6765\u627e\u5965\u5609\u83b1\u65af\u7b97\u8d26\uff0c\u7ed3\u679c\u4e00\u51fa\u95e8\u4fbf\u53d1\u73b0\u81ea\u5df1\u9677\u5165\u4e86\u8ff7\u5bab\u4e4b\u4e2d\u3002\u5927\u6982\u662f\u56e0\u4e3a\u5f53\u65f6\u6b63\u5728\u8c03\u8bd5\u5bc6\u745f\u80fd\u6838\uff0c\u6574\u4e2a\u6d6e\u7a7a\u57ce\u5185\u7684\u9b54\u6cd5\u90fd\u5904\u4e8e\u7d0a\u4e71\u72b6\u6001\uff0c\u8fde\u5468\u56f4\u73af\u5883\u90fd\u5728\u4e0d\u65ad\u5730\u626d\u66f2\u53d8\u5e7b\u3002\u4e0d\u77e5\u8bef\u6253\u8bef\u649e\u4e86\u591a\u4e45\uff0c\u7a81\u7136\u4e00\u5207\u7a33\u5b9a\u4e0b\u6765\uff0c\u743c\u6069\u51ed\u7740\u8bb0\u5fc6\u8d70\u5230\u80fd\u6838\u5ba4\uff0c\u679c\u7136\u89c1\u5230\u4e86\u8fd9\u4f4d\u5e7d\u7075\u5927\u5965\u672f\u5e08\u3002</p><p>\u201c\u6211\u8eab\u8fb9\u5973\u5b69\u5b50\u662f\u591a\u662f\u5c11\uff0c\u5173\u4f60\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d\u743c\u6069\u8d28\u95ee\uff0c\u201c\u4f60\u51ed\u4ec0\u4e48\u66ff\u6211\u6253\u53d1\u8d70\uff1f\u201d</p><p>\u201c\u4e0d\u5173\u6211\u7684\u4e8b\u60c5\u5417\uff1f\u6216\u8bb8\u5427\uff0c\u201d\u5965\u5609\u83b1\u65af\u6162\u60a0\u60a0\u5730\u8bf4\uff0c\u201c\u4e0d\u8fc7\u5170\u5c3c\u65af\u7279\u5148\u751f\uff0c\u4f60\u559c\u6b22\u73ca\u5609\u5bf9\u5427\u3002\u201d</p><p>\u201c\u662f\uff0c\u600e\u4e48\u4e86\u3002\u201d</p><p>\u201c\u771f\u5fc3\u7231\u5979\uff1f\u201d</p><p>\u201c\u662f\u3002\u201d</p><p>\u201c\u4e00\u5fc3\u4e00\u610f\uff1f\u201d\u5965\u5609\u83b1\u65af\u5632\u8bbd\u5730\u95ee\uff0c\u201c\u8fd8\u662f\u8bf4\u4f60\u5bf9\u6bcf\u4e00\u4f4d\u5973\u5b69\u5b50\u90fd\u662f\u4e00\u5fc3\u4e00\u610f\uff1f\u201d</p><p>\u201c\u2026\u2026\u8fd9\u4e0d\u5173\u4f60\u7684\u4e8b\u60c5\u3002\u201d</p><p>\u201c\u771f\u7684\uff1f\u201d\u5965\u5609\u83b1\u65af\u53cd\u95ee\u3002</p><p>\u201c\u4f60\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u743c\u6069\u8b66\u60d5\u8d77\u6765\uff0c\u4ed6\u4ece\u5bf9\u65b9\u7684\u8bed\u6c14\u4e2d\u54c1\u5c1d\u51fa\u4e00\u4e1d\u4e0d\u7965\u7684\u610f\u5473\u3002</p><p>\u201c\u6ca1\u4ec0\u4e48\uff0c\u6211\u53ea\u662f\u60f3\u544a\u8bc9\u4f60\uff0c\u6211\u662f\u4f60\u59d0\u59d0\u7684\uff0c\u55ef\uff0c\u6211\u662f\u5979\u7684\u8001\u5e08\uff0c\u201d\u5965\u5609\u83b1\u65af\u8bf4\uff0c\u201c\u73b0\u5728\u4f60\u89c9\u5f97\u6211\u6709\u6ca1\u6709\u8d44\u683c\u6765\u7ba1\u5462\uff1f\u201d</p><p>\u201c\u7b11\u8bdd\uff0c\u201d\u743c\u6069\u5927\u58f0\u8bf4\uff0c\u201c\u6211\u59d0\u59d0\u4ece\u6765\u5c31\u4e0d\u8ba4\u8bc6\u4f60\uff0c\u4ec0\u4e48\u65f6\u5019\u6709\u4f60\u8fd9\u4e2a\u8001\u5e08\uff01\u201d</p><p>\u201c\u54e6\uff0c\u771f\u7684\u8fd8\u6ca1\u660e\u767d\u5417\uff1f\u8fd8\u662f\u8bf4\uff0c\u5176\u5b9e\u5fc3\u91cc\u65e9\u5c31\u5df2\u7ecf\u660e\u767d\u4e86\uff0c\u4f46\u662f\u4e0d\u80af\u627f\u8ba4\u5462\uff1f\u201d\u5965\u5609\u83b1\u65af\u8f7b\u58f0\u7b11\u7740\uff0c\u201c\u6216\u8005\u8bf4\uff0c\u4e0d\u6562\u9762\u5bf9\u73b0\u5b9e\u5462\uff1f\u201d</p><p>\u201c\u6211\u4e0d\u77e5\u9053\u4f60\u5728\u9b3c\u626f\u4ec0\u4e48\u3002\u201d</p><p>\u201c\u662f\u5417\uff1f\u4eb2\u773c\u76ee\u7779\u4e86\u90a3\u4e00\u9053\u5760\u661f\u672f\uff0c\u4f60\u96be\u9053\u5c31\u6ca1\u6709\u60f3\u8fc7\uff0c\u4e3a\u4ec0\u4e48\u4f60\u4eb2\u7231\u7684\u59d0\u59d0\u80fd\u591f\u91ca\u653e\u8fd9\u6837\u5f3a\u5927\u7684\u6cd5\u672f\u5462\uff1f\u201d</p><p>\u201c\u2026\u2026\u6211\u4e0d\u77e5\u9053\u3002\u201d</p><p>\u201c\u54c8\uff0c\u6ca1\u5173\u7cfb\uff0c\u5170\u5c3c\u65af\u7279\uff0c\u201d\u5965\u5609\u83b1\u65af\u8bf4\uff0c\u201c\u4f60\u77e5\u9053\u4e5f\u597d\uff0c\u4e0d\u77e5\u9053\u4e5f\u597d\uff0c\u627f\u8ba4\u4e5f\u7f62\uff0c\u4e0d\u627f\u8ba4\u4e5f\u7f62\uff0c\u90fd\u65e0\u6240\u8c13\u3002\u90a3\u4e48\u8bf4\u5f97\u7b80\u5355\u70b9\uff0c\u6211\u73b0\u5728\u5c31\u662f\u4e0d\u559c\u6b22\u5979\u4eec\uff0c\u8981\u5979\u4eec\u8d70\uff0c\u4f60\u53c8\u60f3\u600e\u4e48\u6837\u5462\uff1f\u201d\u5979\u4fa7\u8fc7\u8138\uff0c\u77a5\u7740\u743c\u6069\uff0c\u201c\u4f60\u6012\u6c14\u51b2\u51b2\u6765\u627e\u6211\uff0c\u662f\u60f3\u548c\u6211\u6253\u4e00\u67b6\u5417\uff1f\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u6253\u67b6\u81ea\u7136\u4e0d\u53ef\u80fd\uff0c\u4e0d\u8bf4\u5bf9\u65b9\u521a\u521a\u6551\u4e86\u81ea\u5df1\uff0c\u597d\u6b79\u662f\u6551\u547d\u6069\u4eba\uff0c\u743c\u6069\u4e0d\u6562\u592a\u8fc7\u65e0\u793c\u3002\u5c31\u7b97\u771f\u8981\u52a8\u624b\uff0c\u5979\u53ef\u662f\u8d27\u771f\u4ef7\u5b9e\u7684\u5927\u5965\u672f\u5e08\uff0c\u867d\u7136\u5982\u4eca\u6210\u4e86\u5e7d\u7075\uff0c\u4f46\u4e5f\u4e0d\u662f\u743c\u6069\u8fd9\u79cd\u7a0b\u5ea6\u7684\u89d2\u8272\u80fd\u591f\u5bf9\u4ed8\u7684\u3002\u6253\u67b6\uff1f\u90a3\u4e0d\u662f\u81ea\u53d6\u5176\u8fb1\u4e48\u3002</p><p>\u201c\u4e0d\u670d\u6c14\u662f\u4e48\uff1f\u201d\u5965\u5609\u83b1\u65af\u7ec8\u4e8e\u62ac\u8d77\u5934\u6765\uff0c\u6b63\u89c6\u7740\u743c\u6069\uff0c\u201c\u5c0f\u5b50\uff0c\u8001\u5b9e\u544a\u8bc9\u4f60\uff0c\u5982\u679c\u6362\u4e86\u6211\u662f\u4f60\u59d0\u59d0\uff0c\u6211\u65e9\u5c31\u628a\u90a3\u5bf9\u53cc\u80de\u80ce\u7ed9\u6740\u4e86\uff0c\u5e72\u8106\u5229\u843d\uff0c\u624d\u6ca1\u5174\u8da3\u8fd8\u8c08\u4ec0\u4e48\u6761\u4ef6\u3002\u6211\u8fd9\u6b21\u662f\u770b\u5728\u4f60\u59d0\u59d0\u7684\u4efd\u4e0a\uff0c\u4e0d\u60f3\u4f60\u592a\u8fc7\u96be\u582a\uff0c\u6240\u4ee5\u7ed9\u90a3\u5bf9\u5c0f\u4e2b\u5934\u4e00\u6b21\u673a\u4f1a\u3002\u4f60\u8981\u8bb2\u9053\u7406\u662f\u5417\uff0c\u90a3\u6211\u5c31\u548c\u4f60\u8bb2\u9053\u7406\uff0c\u201d\u5979\u6307\u4e86\u6307\u743c\u6069\uff0c\u201c\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u548c\u4f60\u6ca1\u5173\u7cfb\uff0c\u548c\u4f60\u59d0\u59d0\u4e5f\u6ca1\u5173\u7cfb\uff0c\u5355\u7eaf\u5c31\u662f\u6211\u548c\u5979\u4eec\u4e4b\u95f4\u505a\u4e86\u4e2a\u4ea4\u6613\u3002\u6211\u6551\u4f60\u7684\u547d\uff0c\u5979\u4eec\u8d70\u4eba\uff0c\u4e24\u53a2\u60c5\u613f\uff0c\u516c\u5e73\u5408\u7406\u3002\u73b0\u5728\u6211\u5b8c\u6210\u4e86\u6211\u7684\u5de5\u4f5c\uff0c\u8be5\u5979\u4eec\u5c65\u884c\u627f\u8bfa\u3002\u4f60\u95ee\u6211\u6709\u4ec0\u4e48\u8d44\u683c\u7ba1\u4f60\u7684\u4e8b\u60c5\u2014\u2014\u90a3\u4e48\u53cd\u8fc7\u6765\uff0c\u4f60\u53c8\u6709\u4ec0\u4e48\u8d44\u683c\uff0c\u6765\u7ba1\u6211\u548c\u522b\u4eba\u505a\u7684\u4ea4\u6613\uff1f\u201d</p><p>\u743c\u6069\u4e00\u65f6\u6014\u4f4f\uff0c\u54d1\u53e3\u65e0\u8a00\u3002</p><p>\u201c\u6211\u4e0d\u559c\u6b22\u4f60\uff0c\u4e0d\u8fc7\u65e2\u7136\u5979\u559c\u6b22\u4e86\uff0c\u90a3\u4e48\u6211\u4e5f\u5c31\u6ca1\u4ec0\u4e48\u8bdd\u8bf4\uff0c\u201d\u5965\u5609\u83b1\u65af\u8bf4\uff0c\u201c\u4f46\u662f\u6211\u4e5f\u660e\u767d\u544a\u8bc9\u4f60\uff0c\u6211\u4e0d\u4f1a\u5141\u8bb8\u4f60\u505a\u4efb\u4f55\u5bf9\u4e0d\u8d77\u5979\u7684\u4e8b\u60c5\u3002\u90a3\u4e2a\u9b45\u9b54\u548c\u4f60\u6709\u771f\u540d\u5951\u7ea6\uff0c\u6211\u662f\u6ca1\u529e\u6cd5\u5bf9\u4ed8\uff0c\u6682\u65f6\u7b97\u4e86\uff0c\u4f46\u90a3\u5bf9\u53cc\u80de\u80ce\u5fc5\u987b\u79bb\u5f00\uff0c\u201d\u5979\u51b7\u51b7\u770b\u7740\u743c\u6069\uff0c\u201c\u5951\u7ea6\u6210\u7acb\uff0c\u5c31\u5f97\u9075\u5b88\uff0c\u5979\u4eec\u5982\u679c\u4e0d\u8d70\uff0c\u90a3\u5c31\u662f\u6bc1\u7ea6\uff0c\u5c31\u662f\u84c4\u610f\u6b3a\u8bc8\uff0c\u522b\u602a\u6211\u4e0d\u5ba2\u6c14\u2014\u2014\u8fd8\u6709\u522b\u7684\u95ee\u9898\u5417\uff1f\u201d</p></article>"}}}]);
