(window.webpackJsonp=window.webpackJsonp||[]).push([[470],{493:function(p){p.exports={chapterTitle:null,title:"\u7b2c334\u7ae0 \u91cd\u5927\u4e8b\u9879\u62a5\u5907",content:"<article><h2>\u7b2c334\u7ae0 \u91cd\u5927\u4e8b\u9879\u62a5\u5907</h2><p>\u3000\u3000\u5230\u4e5d\u516b\u5e74\u624d\u4f1a\u542f\u7528\u65b0\u7684\u7701\u653f\u5e9c\u529e\u516c\u5927\u697c\uff0c\u4e5d\u516d\u5e74\u7684\u4e1c\u6d77\u7701\u653f\u5e9c\u529e\u516c\u5927\u697c\u8fd8\u662f\u4e94\u5341\u5e74\u4ee3\u5efa\u8d77\u7684\u4f1a\u5802\u5f0f\u5efa\u7b51\uff0c\u8fc7\u9053\u91cc\u5149\u7ebf\u6666\u6697\uff0c\u6709\u7740\u5e74\u6df1\u65e5\u4e45\u7684\u6c89\u8150\u6c14\u5473\uff0c\u5f20\u606a\u8d70\u8fc7\u65e0\u6570\u7684\u653f\u5e9c\u90e8\u95e8\uff0c\u4f3c\u4e4e\u90fd\u6709\u8fd9\u79cd\u611f\u89c9\uff0c\u5373\u4f7f\u662f\u65b0\u5efa\u7684\u653f\u5e9c\u673a\u5173\u5927\u697c\u91cc\uff0c\u4e5f\u6446\u8131\u4e0d\u51fa\u8fd9\u79cd\u611f\u89c9\uff0c\u8fd9\u5927\u6982\u662f\u56fd\u5185\u5b98\u573a\u7ed9\u4eba\u7279\u6709\u7684\u611f\u89c9\u5427\u3002</p><p>\u3000\u3000\u7701\u653f\u5e9c\u79d8\u4e66\u957f\u9646\u6587\u592b\u4e5f\u4e0d\u559c\u6b22\u8fd9\u91cc\u7684\u6c14\u5473\uff0c\u611f\u89c9\u65f6\u95f4\u4e45\u4e86\u4eba\u90fd\u4f1a\u53d1\u9709\uff0c\u4ed6\u559c\u6b22\u5c06\u529e\u516c\u5ba4\u7684\u95e8\u7559\u4e00\u9053\u7f1d\uff0c\u8fd9\u6837\u80fd\u770b\u89c1\u6709\u6ca1\u6709\u4eba\u8fdb\u51fa\u5f90\u5b66\u5e73\u7684\u529e\u516c\u5ba4\u3002</p><p>\u3000\u3000\u8ddf\u6240\u6709\u7684\u9886\u5bfc\u5e72\u90e8\u4e00\u6837\uff0c\u5f90\u5b66\u5e73\u4e5f\u66f4\u4fe1\u4efb\u4ed6\u4ece\u7701\u653f\u6cd5\u59d4\u5e26\u8fc7\u6765\u7684\u79d8\u4e66\u674e\u4e49\u6c5f\uff0c\u4e0e\u9646\u6587\u592b\u59cb\u7ec8\u9694\u7740\u4e00\u5c42\u3002\u8fd9\u4e00\u70b9\uff0c\u5bf9\u9646\u6587\u592b\u6700\u81f4\u547d\u4e86\uff0c\u4ed6\u5728\u7701\u76f4\u673a\u5173\u91cc\u6162\u6162\u722c\u5230\u7701\u653f\u5e9c\u79d8\u4e66\u957f\u7684\u4f4d\u7f6e\u4e0a\uff0c\u53ea\u5728\u5347\u526f\u79d8\u4e66\u957f\u4e4b\u524d\u5230\u65b0\u829c\u5e02\u6302\u4e86\u4e24\u5e74\u7684\u804c\uff0c\u4ed6\u5728\u7701\u76f4\u673a\u5173\u91cc\u5dee\u4e0d\u591a\u662f\u5230\u5934\u4e86\uff0c\u4e0a\u4e0d\u80fd\u4e0a\uff0c\u4e0b\u4e0d\u80fd\u4e0b\uff0c\u6700\u7406\u60f3\u7684\u51fa\u8def\u5c31\u662f\u4e0b\u53bb\u505a\u4e00\u4efb\u5e02\u59d4\u4e66\u8bb0\uff0c\u4ed6\u8ddf\u9676\u664b\u4e66\u8bb0\u4e0d\u719f\u6089\uff0c\u5f90\u5b66\u5e73\u4e0d\u5e2e\u4ed6\u8bf4\u8bdd\uff0c\u7b49\u4e0b\u4e00\u4efb\u7701\u957f\uff0c\u4ed6\u5c31\u9519\u8fc7\u4e0b\u53bb\u7684\u65f6\u673a\u4e86\uff0c\u8fc7\u51e0\u5e74\u8bf4\u4e0d\u5b9a\u4f1a\u7ed9\u8e22\u5230\u7701\u653f\u534f\u3001\u7701\u4eba\u5927\u53bb\u3002</p><p>\u3000\u3000\u9646\u6587\u592b\u770b\u5230\u674e\u4e49\u6c5f\u4ece\u5f90\u5b66\u5e73\u7684\u529e\u516c\u5ba4\u8d70\u51fa\u53bb\u53c8\u9886\u7740\u51e0\u4e2a\u4eba\u8d70\u8fdb\u53bb\uff0c\u4ece\u95e8\u7f1d\u91cc\u6ca1\u6709\u770b\u6e05\u695a\u662f\u8c01\uff0c\u4ed6\u63a8\u51fa\u95e8\uff0c\u770b\u89c1\u4e00\u5904\u7684\u6731\u5c0f\u519b\u5728\u8fc7\u9053\u91cc\u5f20\u671b\u3002\u4e09\u661f\u4e0e\u9999\u96ea\u6d77\u5408\u8d44\u4e00\u4e8b\uff0c\u6700\u521d\u5c31\u662f\u7ed9\u8fd9\u4e2a\u6731\u5c0f\u519b\u5728\u7ecf\u8d38\u59d4\u6345\u51fa\u6765\u7684\uff0c\u5f90\u5b66\u5e73\u540e\u6765\u505a\u4e86\u6279\u793a\u6253\u56de\u91cd\u65b0\u8c08\u5224\uff0c\u5408\u8d44\u8c08\u5224\u91cd\u542f\u4e86\u56db\u4e2a\u6708\uff0c\u8fd8\u6ca1\u6709\u8c08\u51fa\u65b0\u7684\u7ed3\u679c\u3002\u6731\u5c0f\u519b\u662f\u674e\u4e49\u6c5f\u8bf4\u8fdb\u7701\u653f\u5e9c\u529e\u516c\u5385\u7684\uff0c\u9646\u6587\u592b\u8d77\u521d\u4ee5\u4e3a\u6731\u5c0f\u519b\u4e0e\u5f90\u5b66\u5e73\u6709\u4ec0\u4e48\u5173\u7cfb\uff0c\u540e\u6765\u53d1\u73b0\u5f90\u5b66\u5e73\u5e76\u4e0d\u8ba4\u8bc6\u4ed6\uff0c\u53ea\u662f\u674e\u4e49\u6c5f\u5bf9\u4ed6\u6709\u4e9b\u7167\u987e\uff0c\u4e5f\u4ec5\u6b64\u800c\u5df2\u3002</p><p>\u3000\u3000\u4e0a\u56de\u5f20\u606a\u5230\u7701\u653f\u5e9c\u529e\u516c\u5927\u697c\u6765\uff0c\u6731\u5c0f\u519b\u6070\u5de7\u7ed9\u9063\u5230\u5916\u9762\u53bb\u529e\u4e8b\uff0c\u6ca1\u673a\u4f1a\u51d1\u5de7\u8d76\u4e0a\u5f20\u606a\u4e0e\u5f90\u5b66\u5e73\u4f1a\u9762\u7684\u673a\u4f1a\uff0c\u521a\u521a\u770b\u89c1\u674e\u4e49\u6c5f\u9886\u7740\u51e0\u4e2a\u4eba\u8fdb\u5f90\u5b66\u5e73\u7684\u529e\u516c\u5ba4\uff0c\u5f20\u606a\u5728\u91cc\u9762\uff0c\u65b0\u4efb\u7684\u6797\u4e1a\u5385\u5385\u957f\u6c5f\u6d9b\u4e5f\u5728\u91cc\u9762\uff0c\u53e6\u5916\u4e00\u4e2a\u4e2d\u5e74\u4eba\uff0c\u770b\u76f8\u8c8c\u4e0e\u5f20\u606a\u5f88\u50cf\uff0c\u60f3\u7740\u83ab\u4e0d\u662f\u5f20\u606a\u7684\u957f\u8f88\u3002</p><p>\u3000\u3000\u5f20\u606a\u7684\u80cc\u666f\u795e\u79d8\u5f97\u5f88\uff0c\u6731\u5c0f\u519b\u3001\u4ee4\u5c0f\u71d5\u4ed6\u4eec\u90fd\u5728\u731c\u6d4b\u4ed6\u7684\u5bb6\u5ead\u80cc\u666f\uff0c\u53ea\u662f\u4ed6\u4eec\u63a5\u89e6\u7684\u5c42\u6b21\u5f88\u4f4e\uff0c\u5f20\u606a\u4e0d\u8ddf\u4ed6\u4eec\u660e\u8bf4\uff0c\u4ed6\u4eec\u54ea\u91cc\u80fd\u731c\u5f97\u5230\uff1f</p><p>\u3000\u3000\u6731\u5c0f\u519b\u4fbf\u60f3\u7740\u65f6\u4e0d\u65f6\u5230\u8fc7\u9053\u91cc\u8f6c\u4e00\u8f6c\uff0c\u60f3\u7740\u7b49\u674e\u4e49\u6c5f\u8ddf\u5f20\u606a\u4ed6\u4eec\u51fa\u5f90\u5b66\u5e73\u7684\u529e\u516c\u5ba4\uff0c\u88c5\u4f5c\u51d1\u5de7\u78b0\u4e0a\u3002\u6ca1\u60f3\u5230\u5728\u8fc7\u9053\u91cc\u6643\u8361\u7684\u6837\u5b50\u7ed9\u9646\u6587\u592b\u770b\u5728\u773c\u91cc\uff0c\u5fc3\u91cc\u4e00\u865a\uff0c\u4f4e\u5934\u60f3\u8eb2\u56de\u529e\u516c\u5ba4\u53bb\u3002</p><p>\u3000\u3000\u9646\u6587\u592b\u62db\u547c\u5524\u4f4f\u4ed6\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u6731\uff0c\u6211\u8fd9\u91cc\u6709\u4efd\u6587\u4ef6\uff0c\u4f60\u5e2e\u6211\u8f6c\u7ed9\u6cd5\u5236\u529e\u2026\u2026\u201d\u7701\u653f\u5e9c\u529e\u516c\u5385\u4e5f\u6302\u7701\u6cd5\u5236\u529e\u7684\u724c\u5b50\uff0c\u6cd5\u5236\u529e\u51e0\u4e2a\u5904\u5ba4\u5c31\u5728\u8fc7\u9053\u7684\u90a3\u5934\uff0c\u96be\u5f97\u9646\u6587\u592b\u4e0d\u8d23\u5907\u4ed6\uff0c\u8fd8\u5f00\u53e3\u5524\u4ed6\u505a\u4e8b\uff0c\u6731\u5c0f\u519b\u795e\u60c5\u4e00\u632f\uff0c\u8ddf\u7740\u8fdb\u4e86\u9646\u592b\u6587\u7684\u529e\u516c\u5ba4\u3002</p><p>\u3000\u3000\u201c\u770b\u5230\u674e\u5904\u957f\u9886\u7740\u8c01\u8fdb\u5f90\u7701\u957f\u7684\u529e\u516c\u5ba4\u4e86\uff1f\u201d\u9646\u6587\u592b\u5c06\u6587\u4ef6\u9012\u7ed9\u6731\u5c0f\u519b\uff0c\u770b\u4f3c\u65e0\u610f\u7684\u95ee\u4e86\u4e00\u53e5\u3002</p><p>\u3000\u3000\u6731\u5c0f\u519b\u5fc3\u91cc\u4e00\u60ca\uff0c\u4e0d\u7ba1\u54ea\u4e2a\u673a\u5173\uff0c\u6700\u662f\u8ba8\u538c\u4e0b\u9762\u4eba\u79c1\u4e0b\u91cc\u5077\u7aa5\u3001\u63e3\u6d4b\u9886\u5bfc\u7684\u4e8b\u60c5\uff0c\u8ba9\u9646\u6587\u592b\u770b\u4f3c\u65e0\u610f\u7684\u4e00\u95ee\uff0c\u6731\u5c0f\u519b\u624b\u5fc3\u90fd\u5413\u51fa\u6c57\uff0c\u8fd8\u4ee5\u4e3a\u4ed6\u5f80\u5f90\u5b66\u5e73\u529e\u516c\u5ba4\u65b9\u5411\u5f20\u671b\u7684\u6837\u5b50\u90fd\u8ba9\u9646\u6587\u592b\u770b\u4e86\u53bb\uff0c\u4ed6\u5fd9\u89e3\u91ca\u9053\uff1a\u201c\u4e0e\u674e\u5904\u957f\uff0c\u8fd8\u6709\u6797\u4e1a\u5385\u6c5f\u5385\u957f\u8fdb\u5f90\u7701\u957f\u529e\u516c\u5ba4\u7684\u4eba\u91cc\uff0c\u6709\u4e00\u4e2a\u662f\u6211\u8ba4\u8bc6\u7684\uff0c\u6211\u8fd8\u4ee5\u4e3a\u770b\u9519\u4e86\uff0c\u6240\u4ee5\u5230\u8fc7\u9053\u91cc\u6765\u770b\u770b\uff0c\u4ed6\u4eec\u90fd\u5df2\u7ecf\u8fdb\u53bb\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u54e6\u2026\u2026\u201d\u9646\u6587\u592b\u8f7b\u8f7b\u5e94\u4e86\u4e00\u58f0\uff0c\u53c8\u95ee\u4e86\u4e00\u53e5\uff0c\u201c\u4e5f\u662f\u6797\u4e1a\u5385\u7684\u5e72\u90e8\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e0d\u662f\uff0c\u662f\u4e00\u4e2a\u670b\u53cb\uff0c\u4ed6\u5e74\u7eaa\u8f7b\u7740\u5462\uff0c\u8fd8\u5728\u8bfb\u4e66\u3002\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u4f60\u662f\u8bf4\u5f20\u606a\u554a\uff0c\u4f60\u8ba4\u8bc6\u5f20\u606a\uff1f\u201d</p><p>\u3000\u3000\u4e0a\u56de\u5f20\u606a\u966a\u5f90\u5b66\u5e73\u4e58\u76f4\u5347\u98de\u673a\u89c6\u5bdf\u5c0f\u6c5f\u4e2d\u4e0a\u6e38\u7684\u5929\u7136\u6797\u73b0\u72b6\uff0c\u9646\u6587\u592b\u5fc3\u91cc\u4e00\u76f4\u5f88\u5947\u602a\uff0c\u90a3\u6b21\u89c6\u5bdf\u4e4b\u540e\uff0c\u6797\u4e1a\u5385\u7684\u5934\u5934\u8111\u8111\u90fd\u6362\u4e86\u4e00\u4e2a\u904d\uff0c\u53ea\u4e0d\u8fc7\u540e\u6765\u7684\u4e8b\u60c5\uff0c\u5f90\u5b66\u5e73\u90fd\u4ea4\u7ed9\u674e\u4e49\u6c5f\u53bb\u529e\uff0c\u65b0\u5149\u7eb8\u4e1a\u8fd8\u6ca1\u6709\u51fa\u73b0\u5728\u6b63\u5f0f\u7684\u6587\u4ef6\u91cc\uff0c\u9646\u6587\u592b\u5bf9\u5177\u4f53\u7684\u60c5\u51b5\u4e0d\u662f\u5f88\u6e05\u695a\u3002</p><p>\u3000\u3000\u201c\u8ba4\u8bc6\uff0c\u4e24\u5e74\u524d\u5c31\u8ba4\u8bc6\u4e86\uff0c\u5e78\u4e8f\u5f20\u606a\u4ecb\u7ecd\uff0c\u6211\u624d\u8ba4\u8bc6\u674e\u5904\u957f\u7684\u3002\u201d</p><p>\u3000\u3000\u9646\u6587\u592b\u70b9\u70b9\u5934\uff0c\u6ca1\u6709\u518d\u8ddf\u6731\u5c0f\u519b\u591a\u8bf4\u4ec0\u4e48\uff0c\u8ba9\u4ed6\u5c06\u6587\u4ef6\u62ff\u51fa\u53bb\u3002</p><p>\u3000\u3000\u4ed6\u5750\u5728\u529e\u516c\u684c\u524d\u7684\u6c99\u53d1\u4e0a\u60f3\u4e86\u4e00\u4f1a\u513f\uff0c\u770b\u6765\u53bb\u5e74\u51ac\u5929\u9999\u96ea\u6d77\u3001\u4e09\u661f\u5408\u8d44\u6848\u88ab\u5f90\u5b66\u5e73\u9a73\u56de\u7684\u90a3\u4ef6\u4e8b\u4e5f\u4e0d\u662f\u90a3\u4e48\u7b80\u5355\u3002\u8fd9\u4e2a\u60f3\u6cd5\uff0c\u4ed6\u53ea\u4f1a\u85cf\u5728\u5fc3\u91cc\u3002</p><p>\u3000\u3000\u529e\u516c\u684c\u4e0a\u7535\u8bdd\u54cd\u8d77\uff0c\u662f\u5f90\u5b66\u5e73\u7684\u7535\u8bdd\uff0c\u5524\u4ed6\u8fc7\u53bb\u4e00\u4e0b\u3002</p><p>\u3000\u3000\u9646\u6587\u592b\u6574\u4e86\u6574\u8863\u895f\uff0c\u8ba9\u81ea\u5df1\u770b\u8d77\u6765\u7cbe\u795e\u4e00\u4e9b\uff0c\u8d70\u51fa\u5f90\u5b66\u5e73\u7684\u529e\u516c\u5ba4\uff0c\u4ed6\u7b2c\u4e00\u6b21\u89c1\u5f20\u77e5\u884c\uff0c\u4f46\u662f\u770b\u5230\u5f20\u77e5\u884c\u5c31\u77e5\u9053\u662f\u4ed6\uff0c\u4e5d\u56db\u5e74\u5510\u5b66\u8c26\u6848\u5f97\u4ee5\u7ea0\u6b63\u7684\u5173\u952e\u4eba\u7269\uff0c\u4e0d\u8fc7\u7701\u68c0\u5bdf\u9662\u91cc\u4f20\u8a00\u5f20\u77e5\u884c\u7684\u513f\u5b50\u5f20\u606a\u5728\u90a3\u6b21\u5ba1\u6848\u4e2d\u8d77\u4e86\u5173\u952e\u4f5c\u7528\u3002</p><p>\u3000\u3000\u201c\u5f90\u7701\u957f\uff0c\u60a8\u627e\u6211\u4ec0\u4e48\u4e8b\uff1f\u201d</p><p>\u3000\u3000\u9646\u6587\u592b\u53c8\u8ddf\u6c5f\u6d9b\u3001\u5f20\u77e5\u884c\u70b9\u5934\u793a\u610f\u3002</p><p>\u3000\u3000\u201c\u6211\u521a\u521a\u8ddf\u9648\u76ca\u65b0\u4e66\u8bb0\u901a\u8fc7\u7535\u8bdd\uff0c\u77e5\u884c\u4e0e\u5f20\u606a\u6709\u4e9b\u6750\u6599\u8981\u8ddf\u9648\u76ca\u65b0\u4e66\u8bb0\u5f53\u9762\u6c47\u62a5\uff0c\u9ebb\u70e6\u4f60\u9886\u4ed6\u4eec\u53bb\u627e\u9648\u76ca\u65b0\u4e66\u8bb0\u2026\u2026\u201d</p><p>\u3000\u3000\u9648\u76ca\u65b0\u662f\u7701\u7eaa\u59d4\u4e66\u8bb0\uff0c\u9646\u6587\u592b\u8fd8\u4ee5\u4e3a\u6d77\u5dde\u53c8\u51fa\u4ec0\u4e48\u5927\u6848\u4e86\uff0c\u52b3\u5f20\u77e5\u884c\u7236\u5b50\u518d\u6b21\u5230\u7701\u91cc\u76f4\u63a5\u4e3e\u62a5\u3002</p><p>\u3000\u3000\u5f90\u5b66\u5e73\u5c06\u9646\u6587\u592b\u773c\u91cc\u7684\u7591\u60d1\u770b\u5728\u773c\u91cc\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u8fd8\u6709\u4e00\u4efd\u540c\u6837\u7684\u6750\u6599\uff0c\u8fd8\u8981\u9ebb\u70e6\u4f60\u627e\u4eba\u9001\u5230\u7ec4\u7ec7\u90e8\u53bb\uff0c\u8fd9\u4efd\u6750\u6599\u8981\u4ea4\u7ed9\u9f9a\u90e8\u957f\u2026\u2026\u201d</p><p>\u3000\u3000\u8fd9\u662f\u5173\u4e8e\u5f20\u606a\u540d\u4e0b\u6240\u6709\u5173\u8054\u516c\u53f8\u7684\u8be6\u7ec6\u8d44\u6599\uff0c\u5f20\u77e5\u884c\u5c31\u7b97\u60f3\u5e73\u7ea7\u8c03\u51fa\u6d77\u5dde\uff0c\u751a\u81f3\u60f3\u5f80\u4e0a\u8d70\u4e00\u6b65\uff0c\u90fd\u8981\u8fc7\u8fd9\u9053\u5ba1\u67e5\u3001\u62a5\u5907\u624b\u7eed\u3002</p><p>\u3000\u3000\u5f80\u7ec4\u7ec7\u90e8\u9001\u6750\u6599\u8fd8\u597d\u7406\u89e3\u4e00\u4e9b\uff0c\u6d77\u5dde\u5e02\u59d4\u4eca\u5929\u4e0a\u5348\u521a\u521a\u63a8\u8350\u5f20\u77e5\u884c\u5230\u7701\u59d4\u515a\u6821\u6625\u5b63\u53bf\u5904\u7ea7\u5e72\u90e8\u8fdb\u4fee\u73ed\u63d2\u73ed\u5b66\u4e60\uff0c\u63d2\u73ed\u63a8\u8350\u6bd4\u8f83\u7f55\u89c1\uff0c\u4f46\u662f\u4e5f\u89c1\u602a\u4e0d\u602a\uff0c\u53ea\u662f\u5230\u8fdb\u4fee\u73ed\u5b66\u4e60\u800c\u5df2\uff0c\u53bf\u5904\u7ea7\u5e72\u90e8\u7684\u5c97\u4f4d\u5b89\u6392\uff0c\u6700\u7ec8\u8fd8\u662f\u8981\u770b\u5404\u4e2a\u5e02\u91cc\u81ea\u5df1\u7684\u5b89\u6392\uff0c\u53ea\u662f\u5f20\u77e5\u884c\u7684\u8eab\u4efd\uff0c\u5bf9\u7701\u653f\u5e9c\u529e\u516c\u5385\u7684\u4eba\u6765\u8bf4\uff0c\u6709\u4e9b\u654f\u611f\uff0c\u5f80\u7ec4\u7ec7\u90e8\u9001\u6750\u6599\uff0c\u8bf4\u4e0d\u5b9a\u662f\u8981\u5f80\u4e0a\u8df3\u4e00\u7ea7\uff0c\u624d\u4f1a\u63a5\u53d7\u67d0\u79cd\u7a0b\u5e8f\u4e0a\u7684\u5ba1\u67e5\u3002\u4f46\u662f\u4e3a\u4ec0\u4e48\u8fd9\u4efd\u6750\u6599\u540c\u65f6\u9001\u4e00\u4efd\u7ed9\u9648\u76ca\u65b0\uff1f</p><p>\u3000\u30001995\u5e74\uff0c\u51fa\u53f0\u515a\u653f\u673a\u5173\u53bf\uff08\u5904\uff09\u7ea7\u4ee5\u4e0a\u9886\u5bfc\u5e72\u90e8\u6536\u5165\u7533\u62a5\u7684\u89c4\u5b9a\uff0c\u53ea\u662f\u5f3a\u8c03\u515a\u653f\u673a\u5173\u9886\u5bfc\u5e72\u90e8\u7684\u4e2a\u4eba\u6536\u5165\uff0c\u5e76\u6ca1\u6709\u5c06\u5bb6\u5ead\u6536\u5165\u754c\u5b9a\u5728\u5185\uff0c\u4f46\u662f\u50cf\u5f20\u77e5\u884c\u3001\u5f20\u606a\u8fd9\u79cd\u60c5\u51b5\uff0c\u662f\u5c5e\u4e8e\u9886\u5bfc\u5e72\u90e8\u4e2a\u4eba\u76f8\u5173\u7684\u91cd\u5927\u4e8b\u9879\uff0c\u867d\u7136\u4e5d\u516d\u5e74\u8fd8\u6ca1\u6709\u5f3a\u5236\u62a5\u5907\u7684\u89c4\u5b9a\uff0c\u4f46\u662f\u5f20\u77e5\u884c\u60f3\u5728\u4ed5\u9014\u6709\u6240\u8fdb\u6b65\uff0c\u4e3b\u52a8\u5411\u6709\u5173\u90e8\u95e8\u62a5\u5907\u662f\u5fc5\u9700\u7684\uff0c\u56e0\u4e3a\u6ca1\u6709\u5f3a\u5236\u62a5\u5907\u7684\u8981\u6c42\uff0c\u6240\u4ee5\u6ca1\u6709\u6d77\u5dde\u5e02\u7ec4\u7ec7\u90e8\u95e8\u7533\u62a5\uff0c\u800c\u662f\u76f4\u63a5\u5411\u7701\u7ec4\u7ec7\u90e8\u3001\u7eaa\u68c0\u90e8\u95e8\u62a5\u5907\u3002</p><p>\u3000\u3000\u9646\u6587\u592b\u5fc3\u91cc\u7ea0\u7ed3\u60f3\u4e0d\u900f\u5f7b\uff0c\u5fc3\u75d2\u75d2\u6320\u7740\u76f4\u96be\u53d7\uff0c\u770b\u6765\u81ea\u5df1\u867d\u7136\u6574\u65e5\u5728\u5f90\u5b66\u5e73\u8eab\u4e0a\u8f6c\u60a0\uff0c\u5176\u5b9e\u5e76\u6ca1\u6709\u878d\u5165\u8fdb\u5f90\u5b66\u5e73\u7684\u5708\u5b50\u91cc\u53bb\uff0c\u60f3\u60f3\u4e5f\u771f\u662f\u5931\u8d25\u3002</p><p>\u3000\u3000\u5f85\u9646\u6587\u592b\u9886\u7740\u5f20\u77e5\u884c\u3001\u5f20\u606a\u79bb\u5f00\u529e\u516c\u5ba4\uff0c\u5f90\u5b66\u5e73\u5bf9\u674e\u4e49\u6c5f\u8bf4\u9053\uff1a\u201c\u4f60\u95ee\u95ee\u770b\uff0c\u9676\u664b\u4e66\u8bb0\u5728\u4e0d\u5728\u5bb6\uff1f\u201d</p><p>\u3000\u3000\u674e\u4e49\u6c5f\u6253\u7535\u8bdd\u8054\u7cfb\u9676\u664b\u7684\u79d8\u4e66\uff0c\u7701\u59d4\u4e66\u8bb0\u9676\u664b\u4eba\u5c31\u5728\u5927\u697c\u91cc\u3002</p><p>\u3000\u3000\u5f90\u5b66\u5e73\u5bf9\u6c5f\u6d9b\u8bf4\u9053\uff1a\u201c\u65b0\u5149\u7eb8\u4e1a\u7684\u60c5\u51b5\uff0c\u4f60\u6bd4\u6211\u6e05\u695a\uff0c\u6709\u4e9b\u95ee\u9898\uff0c\u4f60\u8ddf\u6211\u4e00\u8d77\u5411\u9676\u664b\u4e66\u8bb0\u6c47\u62a5\u4e00\u4e0b\u3002\u201d</p><p>\u3000\u3000\u5177\u4f53\u7684\u8d44\u6599\uff0c\u6c5f\u6d9b\u5c31\u662f\u521a\u521a\u5728\u8fd9\u91cc\u624d\u770b\u5230\uff0c\u4ed6\u603b\u4e0d\u80fd\u8bf4\u81ea\u5df1\u8d77\u521d\u628a\u65b0\u5149\u7eb8\u4e1a\u7ed9\u5ffd\u89c6\u6389\u4e86\uff0c\u5f90\u5b66\u5e73\u8fd9\u4e48\u6025\u5207\u7684\u8981\u5411\u9676\u664b\u6c47\u62a5\u6e05\u695a\uff0c\u4e00\u65b9\u9762\u662f\u8fd9\u4e9b\u4e8b\u60c5\u8ddf\u6d77\u88d5\u516c\u53f8\u6709\u4e9b\u7ea0\u845b\uff0c\u5f90\u5b66\u5e73\u60f3\u6e05\u8005\u81ea\u6e05\u4e0d\u662f\u90a3\u4e48\u5bb9\u6613\uff0c\u56fd\u4eba\u90fd\u4e60\u60ef\u628a\u4e8b\u60c5\u5f80\u574f\u7684\u65b9\u9762\u8003\u8651\uff0c\u6709\u5fc5\u8981\u89e3\u91ca\uff0c\u6700\u4e3b\u8981\u7684\u5927\u6982\u662f\u5e2e\u5f20\u77e5\u884c\u8bf4\u8bdd\u5427\u3002\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u5f20\u77e5\u884c\u9a6c\u4e0a\u8131\u79bb\u6d77\u5dde\u5e02\u6700\u597d\u7684\u65b9\u6cd5\u5c31\u662f\u8fdb\u7701\u59d4\u515a\u6821\u5b66\u4e60\uff0c\u7136\u540e\u53e6\u884c\u5b89\u6392\u804c\u52a1\uff0c\u4e00\u4e2a\u6b63\u5904\u7ea7\u5b98\u5458\u7684\u804c\u52a1\u5b89\u6392\u5728\u54ea\u91cc\u9700\u8981\u7701\u91cc\u4e00\u4e8c\u628a\u624b\u715e\u6709\u4ecb\u4e8b\u7684\u8ba8\u8bba\uff1f</p><p>\u3000\u3000\u96be\u9053\u662f\u60f3\u518d\u6b21\u7834\u683c\u63d0\u62e8\u5f20\u77e5\u884c\uff1f</p><p>\u3000\u3000\u5f20\u77e5\u884c\u53c2\u52a0\u5b8c\u8fdb\u4fee\u73ed\uff0c\u4e5f\u7b97\u5728\u6b63\u5904\u7ea7\u7684\u4f4d\u5b50\u5e72\u6ee1\u4e24\u5e74\uff0c\u8ddd\u79bb\u6b63\u5f0f\u63d0\u62d4\u7684\u5e74\u9650\u8fd8\u5dee\u90a3\u4e48\u4e00\u70b9\uff0c\u7834\u683c\u63d0\u62d4\u4e5f\u4e0d\u662f\u591a\u5927\u4e8b\u60c5\u3002\u73b0\u5728\u7701\u90e8\u7ea7\u5b98\u5458\u5dee\u4e0d\u591a\u6709\u4e00\u534a\u4ee5\u4e0a\u7684\u4eba\u6709\u7834\u683c\u63d0\u62d4\u7684\u7ecf\u5386\uff0c\u4eab\u53d7\u4e24\u6b21\u3001\u4e09\u6b21\u7834\u683c\u5f85\u9047\u7684\u4e5f\u5927\u6709\u4eba\u5728\u3002</p><p>\u3000\u3000\u6d77\u5dde\u63a7\u80a1\u9887\u6709\u58f0\u8272\uff0c\u7701\u59d4\u515a\u62a5\u4e5f\u6b63\u5f0f\u520a\u4e13\u9898\u8ba8\u8bba\u8fc7\u6d77\u5dde\u63a7\u80a1\u7684\u6a21\u5f0f\uff0c\u4ece\u5510\u5b66\u8c26\u6848\u4ee5\u6765\uff0c\u5f20\u77e5\u884c\u5728\u7701\u91cc\u7684\u66b4\u5149\u7387\u6bd4\u4e00\u822c\u7684\u53bf\u5904\u7ea7\u5e72\u90e8\u8981\u9ad8\u591a\u4e86\uff0c\u81f3\u5c11\u9676\u664b\u4e66\u8bb0\u77e5\u9053\u8fd9\u4e2a\u4eba\u3002</p><p>\u3000\u3000\u8981\u771f\u6307\u671b\u65b0\u5149\u7eb8\u4e1a\u5e2e\u7740\u5f25\u8865\u7701\u6797\u4e1a\u7cfb\u7edf\u7684\u7bd3\u5b50\uff0c\u54ea\u80fd\u4e0d\u7ed9\u4e00\u70b9\u7cd6\u5403\uff1f\u4e0d\u7ed9\u4e2a\u526f\u5385\u3001\u526f\u5e02\u7684\u4f4d\u7f6e\uff0c\u5012\u663e\u5f97\u7701\u91cc\u592a\u541d\u556c\u4e86\u3002\u6c5f\u6d9b\u8fd9\u4e48\u60f3\u7740\uff0c\u6709\u4e9b\u7fa1\u6155\u5f20\u77e5\u884c\u7684\u597d\u8fd0\u6c14\uff0c\u4e5f\u5b9e\u5728\u60f3\u4e0d\u660e\u767d\u5f20\u606a\u8fd9\u4e48\u4e2a\u5c11\u5e74\uff0c\u600e\u4e48\u80fd\u505a\u51fa\u8fd9\u5927\u7684\u4e8b\u60c5\u6765\uff1f</p></article>"}}}]);
