(window.webpackJsonp=window.webpackJsonp||[]).push([[471],{493:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u8282 \u5bf9\u4e0d\u8d77",content:"<article><h2>\u7b2c\u4e09\u8282 \u5bf9\u4e0d\u8d77</h2><p>\u627f\u8bfa\u8fd9\u79cd\u4e1c\u897f\u662f\u4e0d\u80fd\u8f7b\u6613\u4f5c\u51fa\u7684\uff0c\u4e00\u65e6\u4f5c\u51fa\uff0c\u90a3\u5c31\u8981\u5151\u73b0\u3002\u867d\u7136\u8bf4\u4e16\u4e8b\u65e0\u5e38\uff0c\u672a\u6765\u83ab\u6d4b\uff0c\u8c01\u4e5f\u4e0d\u80fd\u767e\u5206\u4e4b\u767e\u4fdd\u8bc1\uff0c\u4f46\u65e2\u7136\u6562\u4f5c\u627f\u8bfa\uff0c\u591a\u591a\u5c11\u5c11\u5fc3\u4e2d\u6709\u70b9\u5e95\u3002\u7136\u800c\u5f53\u743c\u6069\u8131\u53e3\u800c\u51fa\u7684\u65f6\u5019\uff0c\u4ed6\u5fc3\u4e2d\u5176\u5b9e\u5e76\u6ca1\u6709\u534a\u5206\u628a\u63e1\u3002</p><p>\u73b0\u5728\u662f1374DR\u7684\u4e00\u6708\u4e0b\u65ec\uff0c\u8299\u857e\u72c4\u5341\u516d\u5c81\uff0c\u53bb\u5e74\u5341\u4e00\u6708\u4e0a\u65ec\u521a\u521a\u5ea6\u8fc7\u751f\u65e5\uff0c\u800c\u743c\u6069\u8bb8\u8bfa\u7684\u662f\u5728\u5979\u5341\u516b\u5c81\u751f\u65e5\u65f6\u5a36\u5979\u3002\u6362\u53e5\u8bdd\u8bf4\uff0c\u5176\u5b9e\u6ca1\u6709\u4e24\u5e74\uff0c\u53ea\u6709\u4e8c\u5341\u4e8c\u4e2a\u6708\u3002\u4e8c\u5341\u4e8c\u4e2a\u6708\uff0c\u8bf4\u957f\u4e0d\u957f\u8bf4\u77ed\u4e0d\u77ed\uff0c\u8981\u60f3\u80fd\u591f\u6392\u9664\u969c\u788d\uff0c\u5b9e\u73b0\u76ee\u6807\uff0c\u96be\u5ea6\u662f\u975e\u5e38\u9ad8\u7684\uff0c\u522b\u7684\u4e0d\u8bf4\uff0c\u4e8c\u5341\u4e8c\u4e2a\u6708\u540e\u743c\u6069\u5c31\u80fd\u6253\u5f97\u8fc7\u5965\u5609\u83b1\u65af\u4e48\uff1f</p><p>\u4f46\u743c\u6069\u5df2\u7ecf\u522b\u65e0\u9009\u62e9\u3002</p><p>\u4ed6\u4e0d\u53ef\u80fd\u771f\u7684\u653e\u5f03\u8299\u857e\u72c4\uff0c\u65e0\u8bba\u8bf4\u7231\u4e5f\u597d\uff0c\u559c\u6b22\u4e5f\u7f62\uff0c\u751a\u81f3\u5355\u7eaf\u7684\u5360\u6709\u6b32\u4e5f\u7f62\uff0c\u603b\u4e4b\u5c31\u662f\u4e0d\u53ef\u80fd\uff0c\u8fd9\u4e00\u70b9\u786e\u5b9a\u65e0\u7591\u3002\u6b64\u65f6\u529b\u4e0d\u5982\u4eba\uff0c\u90a3\u53ea\u597d\u6682\u4e14\u5fcd\u8010\uff0c\u4f46\u5fcd\u8010\u7ec8\u7a76\u662f\u6709\u9650\u5ea6\u7684\uff0c\u4ed6\u4e0d\u53ef\u80fd\u8ba9\u8299\u857e\u72c4\u6c38\u8fdc\u7b49\u4e0b\u53bb\u3002\u4e24\u5e74\u7684\u65f6\u95f4\u8fd9\u5df2\u7ecf\u662f\u6781\u9650\uff0c\u66f4\u957f\u7684\u8bdd\uff0c\u5c0f\u5973\u5b69\u4f1a\u5d29\u6e83\u6389\u5427\u3002</p><p>\u5fc5\u987b\u7ed9\u5979\u5e0c\u671b\uff0c\u54ea\u6015\u662f\u865a\u5047\u7684\u5e0c\u671b\uff0c\u5979\u624d\u80fd\u591f\u652f\u6491\u4e0b\u53bb\u3002</p><p>\u201c\u6b7b\u8001\u592a\u5a46\uff0c\u5c45\u7136\u6562\u5a01\u80c1\u6211\uff0c\u5927\u5965\u672f\u5e08\u53c8\u600e\u4e48\u6837\uff0c\u53d8\u6210\u9b3c\u4e86\u8fd8\u8fd9\u4e48\u56a3\u5f20\u3002\u8428\u739b\u65af\u7279\u6bd4\u4f60\u5f3a\u5427\uff0c\u5f53\u65e5\u5728\u6df1\u6e0a\u65ad\u57df\u9547\u91cc\uff0c\u4ed6\u6263\u7740\u827e\u5f25\u8587\u6765\u903c\u6211\u5c31\u8303\uff0c\u8f6c\u8fc7\u5934\u7167\u6837\u70b8\u4e86\u4ed6\u4e2a\u7c89\u8eab\u788e\u9aa8\uff0c\u201d\u743c\u6069\u54ac\u7740\u7259\u9f7f\uff0c\u5728\u5fc3\u91cc\u7ed9\u81ea\u5df1\u6253\u6c14\uff0c\u201c\u6211\u662f1372DR\u7684\u56db\u6708\u5e95\u51fa\u9634\u9b42\u57ce\uff0c\u5230\u73b0\u5728\u4e5f\u624d\u6b63\u597d\u4e8c\u5341\u4e8c\u4e2a\u6708\uff0c\u4ece\u521d\u51fa\u8305\u5e90\u7684\u83dc\u9e1f\u53d8\u6210\u4e86\u9ad8\u9636\u5deb\u5e08\uff0c\u529b\u91cf\u5f3a\u5927\u4e86\u4f55\u6b62\u5341\u500d\u3002\u518d\u8fc7\u4e8c\u5341\u4e8c\u4e2a\u6708\uff0c\u600e\u89c1\u5f97\u6211\u5c31\u4e0d\u80fd\u628a\u4f60\u6253\u8db4\u4e0b\uff0c\u5927\u5bb6\u8d70\u7740\u77a7\uff01\u201d</p><p>\u743c\u6069\u7684\u5904\u4e8b\u51c6\u5219\uff0c\u662f\u4eba\u4e0d\u72af\u6211\uff0c\u6211\u4e0d\u72af\u4eba\uff0c\u5927\u5bb6\u76f8\u5b89\u65e0\u4e8b\u5c31\u597d\uff1b\u5982\u679c\u662f\u8f7b\u5fae\u7684\u5192\u72af\u6216\u8005\u65e0\u5fc3\u4e4b\u5931\uff0c\u4ed6\u4e5f\u90fd\u80fd\u5c06\u5c31\u5bb9\u5fcd\uff0c\u4e00\u7b11\u800c\u8fc7\uff1b\u4f46\u5982\u679c\u662f\u4e25\u91cd\u7684\u5192\u72af\uff0c\u8d8a\u8fc7\u90a3\u9053\u754c\u9650\uff0c\u4ed6\u4fbf\u4f1a\u4e0d\u62e9\u624b\u6bb5\u5730\u62a5\u590d\uff0c\u751a\u81f3\u7f6e\u4eba\u4e8e\u6b7b\u5730\uff1b\u5982\u679c\u662f\u60f3\u6740\u4ed6\uff0c\u6216\u8005\u4f24\u5bb3\u73ca\u5609\u8fd9\u79cd\u6700\u4e25\u91cd\u7684\u60c5\u5f62\uff0c\u4ed6\u5c31\u4f1a\u6740\u4f60\u5168\u5bb6\uff0c\u706d\u4f60\u6ee1\u95e8\u3002\u5965\u5609\u83b1\u65af\u5f3a\u884c\u628a\u53cc\u80de\u80ce\u8d76\u8d70\uff0c\u5bf9\u4e8e\u743c\u6069\u6765\u8bf4\uff0c\u8fd9\u5df2\u7ecf\u8d8a\u8fc7\u4e86\u4ed6\u5fc3\u91cc\u7684\u90a3\u9053\u754c\u9650\uff0c\u5c5e\u4e8e\u4e25\u91cd\u7684\u5192\u72af\u884c\u4e3a\u3002\u65e2\u7136\u5982\u6b64\uff0c\u4ed6\u5c31\u4e00\u5b9a\u4f1a\u62a5\u590d\uff0c\u800c\u4e14\u662f\u52a0\u500d\u7684\u62a5\u590d\u3002</p><p>\u8d70\u7740\u77a7\u7f62\u3002</p><p>\u5728\u5fc3\u91cc\u4f5c\u51fa\u5ba3\u8a00\u4e4b\u540e\uff0c\u743c\u6069\u6df1\u6df1\u547c\u5438\uff0c\u8c03\u6574\u5fc3\u6001\uff0c\u5f00\u59cb\u63a5\u53d7\u8fd9\u4e2a\u4e8b\u5b9e\u3002\u8299\u857e\u72c4\u548c\u8299\u8389\u5a05\u7684\u79bb\u5f00\u5df2\u7ecf\u662f\u786e\u5b9a\u4e86\uff0c\u4e5f\u597d\uff0c\u81ea\u5df1\u5c31\u50cf\u662f\u4e2a\u707e\u661f\uff0c\u8d70\u5230\u54ea\u91cc\u90fd\u662f\u98ce\u9669\uff0c\u800c\u4e14\u662f\u9ad8\u98ce\u9669\uff0c\u660e\u660e\u662f\u51fa\u6765\u516c\u8d39\u65c5\u6e38\u90fd\u80fd\u78b0\u5230\u795e\u7947\u8981\u780d\uff0c\u53cc\u80de\u80ce\u5b9e\u529b\u592a\u5f31\uff0c\u81ea\u4fdd\u90fd\u52c9\u5f3a\uff0c\u5e26\u5728\u8eab\u8fb9\u4e5f\u786e\u5b9e\u4e0d\u5b89\u5168\uff0c\u5c31\u8ba9\u5979\u4eec\u5148\u56de\u9634\u9b42\u57ce\u4f11\u606f\u4f11\u606f\u5427\u3002\u53cd\u6b63\u8fd9\u5ea7\u661f\u9668\u57ce\u56de\u5230\u5cb8\u8fb9\u540e\uff0c\u9634\u9b42\u57ce\u603b\u90e8\u5c31\u4f1a\u6d3e\u4eba\u8fc7\u6765\u63a5\u6536\uff0c\u62c9\u52a0\u7b49\u4eba\u5e94\u8be5\u4e5f\u8981\u56de\u57ce\u590d\u547d\uff0c\u5230\u65f6\u5019\u987a\u8def\u56de\u53bb\uff0c\u5b89\u5168\u4e5f\u6709\u4fdd\u969c\u3002</p><p>\u8fd9\u4e48\u4e00\u60f3\uff0c\u5fc3\u6001\u7b97\u662f\u7a0d\u7a0d\u5e73\u7f13\uff0c\u867d\u7136\u660e\u77e5\u9053\u8fd9\u5176\u5b9e\u662f\u81ea\u6211\u5b89\u6170\uff0c\u4f46\u4eba\u5728\u65e0\u5948\u7684\u65f6\u5019\uff0c\u4e5f\u53ea\u80fd\u5982\u6b64\u4e86\u3002</p><p>\u5728\u8299\u857e\u72c4\u7684\u8138\u4e0a\u8f7b\u8f7b\u4e00\u543b\uff0c\u743c\u6069\u8d77\u8eab\u51fa\u95e8\u3002\u6d6e\u7a7a\u57ce\u6301\u7eed\u4e0a\u5347\uff0c\u901f\u5ea6\u8d8a\u6765\u8d8a\u5feb\uff0c\u5018\u82e5\u4e0d\u662f\u6709\u9b54\u6cd5\u4fdd\u62a4\uff0c\u57ce\u91cc\u7684\u4eba\u53ea\u6015\u90fd\u8981\u88ab\u9ad8\u538b\u7ed9\u538b\u6241\u4e86\u3002\u8299\u857e\u72c4\u7559\u5728\u623f\u95f4\u91cc\uff0c\u5f97\u5230\u743c\u6069\u7684\u627f\u8bfa\uff0c\u5979\u7684\u5fc3\u60c5\u4e5f\u4e00\u4e0b\u5b50\u53d8\u5f97\u597d\u4e86\u8d77\u6765\uff0c\u867d\u7136\u5373\u5c06\u7684\u79bb\u522b\u4f9d\u65e7\u8ba9\u5979\u611f\u4f24\uff0c\u4f46\u4eba\u5c31\u662f\u8fd9\u6837\uff0c\u4e00\u65e6\u5bf9\u672a\u6765\u6709\u4e86\u7f8e\u597d\u9884\u671f\uff0c\u773c\u524d\u7684\u8270\u96be\u9669\u963b\u4e5f\u5c31\u663e\u5f97\u4e0d\u90a3\u4e48\u788d\u773c\u4e86\u3002\u201c\u5341\u516b\u5c81\u751f\u65e5\uff0c\u4e24\u5e74\u2026\u2026\u4e0d\uff0c\u4e8c\u5341\u4e8c\u4e2a\u6708\uff0c\u201d\u5979\u5728\u5fc3\u4e2d\u9ed8\u81ea\u5ff5\u7740\uff0c\u201c\u6ca1\u5173\u7cfb\uff0c\u5f88\u5feb\u7684\uff0c\u7b49\u7b49\u5c31\u8fc7\u53bb\u4e86\u3002\u201d</p><p>\u867d\u7136\u4e5f\u4f1a\u60f3\uff0c\u8fd9\u6837\u77ed\u6682\u7684\u65f6\u95f4\uff0c\u743c\u6069\u80fd\u591f\u514b\u670d\u5965\u5609\u83b1\u65af\u8fd9\u9053\u969c\u788d\u5417\uff1f\u80fd\u591f\u514b\u670d\u5404\u79cd\u5df2\u77e5\u672a\u77e5\u7684\u56f0\u96be\uff0c\u5982\u671f\u6765\u5a36\u81ea\u5df1\u5417\uff1f\u4f3c\u4e4e\u5f88\u96be\u554a\u2014\u2014\u4f46\u6240\u6709\u8fd9\u79cd\u5ff5\u5934\uff0c\u5168\u90fd\u662f\u4e00\u95ea\u800c\u8fc7\uff0c\u968f\u5373\u5c31\u88ab\u6b7b\u6b7b\u5730\u538b\u5728\u4e86\u5fc3\u5e95\uff0c\u57cb\u5728\u4e86\u610f\u8bc6\u7684\u6700\u6df1\u5904\uff0c\u4e0d\u518d\u53bb\u60f3\uff0c\u4e0d\u53bb\u89e6\u53ca\u3002</p><p>\u65e2\u7136\u4ed6\u8bf4\u53ef\u4ee5\uff0c\u90a3\u5c31\u4e00\u5b9a\u53ef\u4ee5\uff1b\u65e2\u7136\u4ed6\u8bf4\u4f1a\u6765\uff0c\u90a3\u5c31\u4e00\u5b9a\u4f1a\u6765\uff1b\u4ed6\u8bb8\u8bfa\u4e86\uff0c\u90a3\u5c31\u662f\u5df2\u7ecf\u662f\u4e8b\u5b9e\uff0c\u662f\u672a\u6765\u5fc5\u5b9a\u4f1a\u53d1\u751f\u7684\u4e8b\u5b9e\uff0c\u81ea\u5df1\u53ea\u9700\u8981\u9759\u9759\u7b49\u5f85\uff0c\u7b49\u5f85\u90a3\u5e78\u798f\u7684\u65f6\u523b\u5230\u6765\u4fbf\u53ef\u4ee5\u4e86\u5462\u3002\u5bf9\u81ea\u5df1\u7684\u7537\u4eba\uff0c\u8981\u5145\u6ee1\u4fe1\u5fc3\u554a\u3002</p><p>\u5c0f\u5973\u5b69\u5fae\u5fae\u7b11\u7740\uff0c\u900f\u7740\u53d1\u81ea\u5185\u5fc3\u7684\u751c\u871c\uff0c\u5979\u770b\u770b\u65f6\u95f4\uff0c\u8d76\u7d27\u6253\u5f00\u9b54\u6cd5\u4e66\uff0c\u5f00\u59cb\u6bcf\u5929\u7684\u4f8b\u884c\u6e29\u4e60\u3002\u4ed6\u5728\u52aa\u529b\uff0c\u81ea\u5df1\u4e5f\u8981\u52aa\u529b\uff0c\u5230\u65f6\u5019\u4e00\u5b9a\u8981\u8ba9\u4ed6\u522e\u76ee\u76f8\u770b\uff0c\u4e0d\u80fd\u6c38\u8fdc\u90fd\u662f\u4ed6\u7684\u62d6\u7d2f\uff0c\u6c38\u8fdc\u90fd\u8981\u4ed6\u7167\u987e\u5440\u3002</p><p>\u90a3\u4e00\u523b\uff0c\u5979\u7684\u4fcf\u4e3d\u9762\u5e9e\u4e0a\u795e\u91c7\u6d41\u8f6c\uff0c\u7f8e\u4e3d\u5f97\u8ba9\u4eba\u65e0\u6cd5\u63cf\u8ff0\u3002</p><p>\u203b\u203b\u203b</p><p>\u743c\u6069\u8d70\u51fa\u623f\u95f4\uff0c\u5148\u662f\u53bb\u770b\u8299\u8389\u5a05\uff0c\u4ed6\u4e8b\u5148\u5df2\u7ecf\u95ee\u8fc7\u8299\u857e\u72c4\uff0c\u77e5\u9053\u4f4d\u7f6e\u3002\u8d70\u5230\u623f\u95f4\u5916\u6572\u4e86\u6572\u95e8\uff0c\u53ea\u542c\u5f97\u91cc\u9762\u4e00\u9635\u5fd9\u4e71\uff0c\u8fc7\u4e86\u534a\u664c\u95e8\u624d\u6253\u5f00\uff0c\u53ea\u89c1\u8299\u8389\u5a05\u8863\u88f3\u4e0d\u6574\uff0c\u4e24\u988a\u6655\u7ea2\uff0c\u661f\u7738\u4e2d\u90a3\u79cd\u6625\u8272\u6b32\u6ef4\uff0c\u4fbf\u662f\u767d\u75f4\u4e5f\u770b\u7684\u51fa\u6765\u3002\u743c\u6069\u5fc3\u4e2d\u8be7\u5f02\uff0c\u8d70\u8fdb\u623f\u95f4\u5374\u53c8\u6ca1\u53d1\u73b0\u522b\u4eba\uff0c\u770b\u5e8a\u4e0a\u75d5\u8ff9\uff0c\u55c5\u7740\u7a7a\u6c14\u4e2d\u9178\u9178\u751c\u751c\u7684\u6c14\u5473\uff0c\u5927\u7ea6\u4e5f\u731c\u5230\u600e\u4e48\u56de\u4e8b\u3002\u4ed6\u4e00\u5f00\u59cb\u83ab\u540d\u5176\u5999\uff0c\u5fc3\u60f3\u4f60\u4e0d\u662f\u5e94\u8be5\u5728\u4f11\u606f\u4e48\uff0c\u600e\u4e48\u7a81\u7136\u53d1\u6625\uff0c\u7136\u540e\u624d\u53cd\u5e94\u8fc7\u6765\u3002\u81ea\u5df1\u6b64\u524d\u548c\u8299\u857e\u72c4\u4e00\u573a\u5927\u6218\uff0c\u8ddd\u79bb\u5982\u6b64\u4e4b\u8fd1\uff0c\u4f5c\u4e3a\u59d0\u59d0\u7684\u8299\u8389\u5a05\u600e\u4e48\u53ef\u80fd\u4e0d\u53d7\u5f71\u54cd\u3002\u95ee\u9898\u662f\u53cc\u80de\u80ce\u7684\u5fc3\u7075\u611f\u5e94\uff0c\u867d\u7136\u8bf4\u662f\u8bf4\u611f\u540c\u8eab\u53d7\uff0c\u7ec8\u7a76\u8fd8\u662f\u5dee\u4e86\u4e00\u5c42\uff0c\u5c31\u50cf\u662f\u9694\u9774\u6414\u75d2\uff0c\u4e0d\u662f\u771f\u6b63\u7684\u4f53\u9a8c\uff0c\u5b9e\u5728\u6309\u637a\u4e0d\u4f4f\u4e4b\u4e0b\uff0c\u53ea\u597d\u81ea\u5df1\u89e3\u51b3\u95ee\u9898\u4e86\u3002\u6ca1\u60f3\u5230\u6b63\u597d\u743c\u6069\u8fc7\u6765\uff0c\u5f53\u573a\u649e\u7834\uff0c\u5979\u867d\u7136\u5e73\u65f6\u9547\u5b9a\uff0c\u7ec8\u7a76\u8fd8\u662f\u4e2a\u5341\u516d\u5c81\u7684\u5c11\u5973\uff0c\u8138\u4e0a\u987f\u65f6\u706b\u8fa3\u8fa3\u5730\u53d1\u70e7\uff0c\u7f9e\u5f97\u65e0\u53ef\u81ea\u6291\u3002</p><p>\u5973\u5b69\u5b50\u5a07\u7f9e\u6a21\u6837\uff0c\u539f\u672c\u5c31\u6700\u4ee4\u7537\u4eba\u52a8\u5fc3\uff0c\u8299\u8389\u5a05\u53c8\u662f\u6807\u51c6\u7684\u7f8e\u5973\uff0c\u770b\u5f97\u743c\u6069\u4e0b\u8eab\u8822\u8822\u6b32\u52a8\uff0c\u4f3c\u4e4e\u53c8\u6709\u575a\u633a\u7684\u8ff9\u8c61\u3002\u4ed6\u81ea\u5df1\u90fd\u5413\u4e86\u4e00\u8df3\uff0c\u4e0d\u4e45\u524d\u624d\u5728\u8299\u857e\u72c4\u8eab\u4e0a\u53d1\u6cc4\u8fc7\u597d\u51e0\u6b21\uff0c\u6ca1\u9053\u7406\u4f1a\u8fd9\u4e48\u5f3a\u5427\uff0c\u600e\u4e48\u611f\u89c9\u9669\u6b7b\u8fd8\u751f\u4e00\u8d9f\uff0c\u81ea\u5df1\u7684\u4f53\u529b\u9661\u7136\u95f4\u589e\u5f3a\u4e86\u8bb8\u591a\u4f3c\u7684\uff0c\u7cbe\u795e\u4e5f\u660e\u663e\u6bd4\u4ee5\u524d\u66f4\u52a0\u65fa\u76db\u3002\u603b\u7b97\u5fc3\u91cc\u8fd8\u6709\u4e8b\uff0c\u514b\u5236\u7740\u6ca1\u6709\u6251\u4e0a\u53bb\u63a8\u5012\u3002</p><p>\u5176\u5b9e\u5728\u743c\u6069\u5fc3\u91cc\uff0c\u867d\u7136\u662f\u53cc\u80de\u80ce\uff0c\u4f46\u8299\u8389\u5a05\u7684\u5730\u4f4d\u8fdc\u8fdc\u6bd4\u4e0d\u4e0a\u59b9\u59b9\u3002\u8299\u857e\u72c4\u662f\u4ed6\u5728\u8fd9\u4e2a\u4e16\u754c\u4e0a\u7684\u521d\u604b\uff0c\u662f\u7b2c\u4e00\u4e2a\u5973\u5b69\u5b50\uff0c\u867d\u7136\u66fe\u7ecf\u5206\u5f00\uff0c\u4f46\u6700\u7ec8\u53c8\u91cd\u5f52\u4e8e\u597d\uff0c\u771f\u8981\u8bba\u8d77\u6765\uff0c\u5927\u6982\u4ec5\u6b21\u4e8e\u73ca\u5609\u548c\u6885\u83f2\u65af\u5728\u743c\u6069\u5fc3\u76ee\u4e2d\u7684\u91cd\u8981\u6027\u3002\u81f3\u4e8e\u8299\u8389\u5a05\uff0c\u66f4\u591a\u662f\u4f5c\u4e3a\u201c\u8299\u857e\u72c4\u7684\u59d0\u59d0\u201d\u800c\u5b58\u5728\uff0c\u5173\u7cfb\u4e00\u76f4\u5e73\u5e73\u751a\u81f3\u4e0d\u4f73\uff0c\u8fde\u63a8\u5012\u90fd\u662f\u88ab\u838e\u5c14\u5f53\u505a\u751f\u65e5\u793c\u7269\u9001\u6765\u7684\uff0c\u81ea\u7136\u4e5f\u5c31\u8c08\u4e0d\u4e0a\u591a\u5c11\u611f\u60c5\u4e86\u3002\u4e4b\u6240\u4ee5\u4f1a\u628a\u5979\u5217\u4e3a\u81ea\u5df1\u7684\u5973\u4eba\u4e4b\u4e00\uff0c\u4e00\u65b9\u9762\u662f\u5355\u7eaf\u7684\u5360\u6709\u6b32\uff0c\u53e6\u4e00\u65b9\u9762\u4e5f\u662f\u4e3a\u4e86\u907f\u514d\u88ab\u95f4\u63a5\u6234\u7eff\u5e3d\u5b50\u5427\u2026\u2026</p><p>\u4ed6\u7b80\u5355\u628a\u521a\u624d\u53d1\u751f\u7684\u4e8b\u60c5\u7ecf\u8fc7\u544a\u8bc9\u8299\u8389\u5a05\uff0c\u4e5f\u8bf4\u4e86\u4ed6\u5bf9\u8299\u857e\u72c4\u7684\u627f\u8bfa\u3002\u8299\u8389\u5a05\u542c\u4e86\uff0c\u5fae\u5fae\u51b7\u7b11\uff0c\u4f46\u4ee4\u4eba\u610f\u5916\u5730\u4e5f\u6ca1\u6709\u8bf4\u4ec0\u4e48\uff0c\u66f4\u6ca1\u6709\u50cf\u5e73\u65f6\u90a3\u6837\u51b7\u8a00\u5632\u8bbd\u3002\u201c\u6211\u77e5\u9053\u4e86\uff0c\u6211\u4f1a\u7167\u987e\u5979\u7684\uff0c\u201d\u5979\u6700\u540e\u8bf4\uff0c\u201c\u53ea\u5e0c\u671b\u4f60\u80fd\u8bb0\u7740\u4f60\u7684\u627f\u8bfa\u3002\u201d</p><p>\u201c\u6211\u4f1a\u7684\u3002\u201d</p><p>\u743c\u6069\u70b9\u4e86\u70b9\u5934\uff0c\u79bb\u5f00\u8299\u8389\u5a05\u7684\u623f\u95f4\uff0c\u5728\u8d70\u5eca\u91cc\u7ad9\u4e86\u7247\u523b\uff0c\u51b3\u5b9a\u53bb\u770b\u770b\u59d0\u59d0\u3002\u867d\u7136\u8299\u857e\u72c4\u8bf4\u6ca1\u4e8b\uff0c\u770b\u5965\u5609\u83b1\u65af\u7684\u6837\u5b50\u5e94\u8be5\u4e5f\u6ca1\u4e8b\uff0c\u4f46\u7ec8\u7a76\u653e\u5fc3\u4e0d\u4e0b\u3002</p><p>\u51e0\u4e2a\u4eba\u7684\u623f\u95f4\u5176\u5b9e\u90fd\u5728\u4e00\u8d77\uff0c\u8ddd\u79bb\u4e0d\u8fdc\uff0c\u8f6c\u8fc7\u4e24\u4e2a\u62d0\u89d2\u5c31\u5230\u3002\u73ca\u5609\u7684\u623f\u95f4\u95e8\u865a\u63a9\u7740\uff0c\u743c\u6069\u5f84\u76f4\u63a8\u5f00\u8d70\u8fdb\u53bb\uff0c\u5374\u610f\u5916\u53d1\u73b0\u59d0\u59d0\u5df2\u7ecf\u9192\u4e86\uff0c\u6b63\u9760\u5750\u5728\u5e8a\u5934\uff0c\u817f\u4e0a\u88f9\u7740\u6bdb\u6bef\uff0c\u53cc\u624b\u6367\u7740\u4e00\u676f\u70ed\u6c14\u817e\u817e\u7684\u5496\u5561\uff0c\u800c\u5e7d\u7075\u5927\u5965\u672f\u5e08\u5973\u58eb\u6b63\u60a0\u95f2\u5730\u5750\u5728\u65c1\u8fb9\u7684\u4e00\u5f20\u8eba\u6905\u4e2d\uff0c\u50cf\u662f\u5728\u548c\u73ca\u5609\u804a\u5929\u95f2\u8c08\u3002</p><p>\u770b\u89c1\u743c\u6069\u8d70\u8fdb\u6765\uff0c\u5965\u5609\u83b1\u65af\u7709\u5934\u5fae\u76b1\uff0c\u201c\u8fdb\u522b\u4eba\u7684\u623f\u95f4\uff0c\u96be\u9053\u4e0d\u5e94\u8be5\u5148\u6572\u95e8\u5417\uff1f\u201d\u5979\u5f88\u4e0d\u5ba2\u6c14\u5730\u8bf4\uff0c\u201c\u592a\u6ca1\u6559\u517b\u4e86\u5427\uff0c\u5170\u5c3c\u65af\u7279\u3002\u201d</p><p>\u743c\u6069\u7b11\u4e86\u4e00\u7b11\uff0c\u201c\u8fd9\u662f\u6211\u59d0\u59d0\u7684\u623f\u95f4\uff0c\u4e0d\u662f\u522b\u4eba\u7684\u623f\u95f4\uff0c\u5927\u5965\u672f\u5e08\u9601\u4e0b\uff0c\u201d\u4ed6\u9488\u950b\u76f8\u5bf9\uff0c\u201c\u518d\u8bf4\u4e86\uff0c\u5c31\u7b97\u6211\u6709\u4ec0\u4e48\u5931\u793c\u4e4b\u5904\uff0c\u90a3\u4e5f\u662f\u6211\u59d0\u59d0\u624d\u6709\u8d44\u683c\u8bf4\uff0c\u4f60\u4e00\u4e2a\u5916\u4eba\uff0c\u4e0d\u5e94\u8be5\u8d8a\u4fce\u4ee3\u5e96\u5427\u3002\u201d</p><p>\u201c\u54c8\uff0c\u5c0f\u5b50\uff0c\u6709\u80c6\u6c14\u554a\uff0c\u83ab\u975e\u2026\u2026\u201d</p><p>\u201c\u4f60\u4eec\u4e24\u4e2a\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u73ca\u5609\u53ca\u65f6\u63d2\u8bdd\u6253\u65ad\uff0c\u5979\u7684\u58f0\u97f3\u5f88\u865a\u5f31\uff0c\u5374\u4eff\u4f5b\u8574\u542b\u7740\u5f3a\u5927\u7684\u529b\u91cf\uff0c\u201c\u600e\u4e48\u89c1\u9762\u5c31\u5435\u67b6\u2014\u2014\u53d1\u751f\u4ec0\u4e48\u4e8b\u60c5\u4e86\u662f\u5417\uff1f\u201d</p><p>\u201c\u6ca1\u4ec0\u4e48\u3002\u201d\u743c\u6069\u8bf4\uff0c\u4ed6\u4e0d\u592a\u60f3\u8ba9\u59d0\u59d0\u53c2\u4e0e\u8fdb\u8fd9\u4ef6\u4e8b\u60c5\u4e2d\u6765\u3002</p><p>\u201c\u4e0d\u6562\u8bf4\u5417\uff1f\u201d\u5965\u5609\u83b1\u65af\u5374\u6ca1\u8fd9\u79cd\u987e\u8651\uff0c\u5979\u51b7\u7b11\u7740\uff0c\u201c\u5f88\u7b80\u5355\uff0c\u73ca\u5609\uff0c\u6211\u521a\u521a\u628a\u90a3\u5bf9\u53cc\u80de\u80ce\u6253\u53d1\u8d70\u4e86\uff0c\u6240\u4ee5\u8fd9\u5c0f\u5b50\u770b\u6211\u4e0d\u987a\u773c\u5427\u3002\u201d</p><p>\u201c\u53cc\u80de\u80ce\uff1f\u4f60\u662f\u8bf4\u8299\u8389\u5a05\u548c\u8299\u857e\u72c4\uff1f\u201d\u73ca\u5609\u5fae\u5fae\u4e00\u60ca\uff0c\u201c\u4f60\u628a\u5979\u4eec\u8d76\u8d70\u4e86\uff1f\u201d</p><p>\u201c\u73b0\u5728\u8fd8\u6ca1\u8d70\u3002\u201d</p><p>\u201c\u5230\u5e95\u600e\u4e48\u56de\u4e8b\uff1f\u201d</p><p>\u5965\u5609\u83b1\u65af\u7b80\u5355\u628a\u4e8b\u60c5\u8bf4\u4e86\u904d\uff0c\u73ca\u5609\u6c89\u541f\u7247\u523b\uff0c\u201c\u6211\u60f3\u548c\u6211\u5f1f\u5f1f\u8c08\u8c08\uff0c\u201d\u5979\u5bf9\u5965\u5609\u83b1\u65af\u8bf4\uff0c\u201c\u53ef\u5426\u2026\u2026\u201d</p><p>\u201c\u5f53\u7136\uff0c\u6211\u5148\u544a\u9000\uff0c\u201d\u5965\u5609\u83b1\u65af\u6e29\u548c\u5730\u7b11\u4e86\u7b11\uff0c\u7ad9\u8d77\u8eab\u6765\uff0c\u76f4\u63a5\u98d8\u51fa\u95e8\uff0c\u201c\u6ce8\u610f\u4f11\u606f\uff0c\u201d\u5979\u6700\u540e\u53ee\u5631\uff0c\u201c\u522b\u592a\u7d2f\u3002\u201d</p><p>\u201c\u55ef\uff0c\u8c22\u8c22\u3002\u201d</p><p>\u73ca\u5609\u5fae\u7b11\u7740\uff0c\u76ee\u9001\u5965\u5609\u83b1\u65af\u79bb\u53bb\uff0c\u7136\u540e\u793a\u610f\u743c\u6069\u5173\u4e0a\u623f\u95e8\uff0c\u62cd\u62cd\u5e8a\u6cbf\uff0c\u8ba9\u4ed6\u5728\u81ea\u5df1\u8eab\u8fb9\u5750\u4e0b\u3002</p><p>\u201c\u5bf9\u4e0d\u8d77\uff0c\u5c0f\u5f1f\u3002\u201d\u5979\u8f7b\u58f0\u8bf4\u3002</p><p>\u201c\u59d0\u59d0\u5e72\u561b\u8bf4\u5bf9\u4e0d\u8d77\u554a\uff0c\u201d\u743c\u6069\u6709\u4e9b\u65e0\u7cbe\u6253\u91c7\uff0c\u201c\u8fd9\u4e8b\u60c5\u548c\u4f60\u53c8\u6ca1\u5173\u7cfb\u3002\u201d</p><p>\u201c\u6211\u4e8b\u5148\u662f\u4e0d\u77e5\u9053\uff0c\u4f46\u5979\u786e\u5b9e\u662f\u4e3a\u4e86\u6211\u624d\u8fd9\u4e48\u505a\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u6240\u4ee5\u6211\u8981\u8bf4\u5bf9\u4e0d\u8d77\u3002\u4f46\u662f\uff0c\u5c0f\u5f1f\uff0c\u4f60\u8981\u660e\u767d\u6211\u5230\u5e95\u662f\u4e3a\u4ec0\u4e48\u8bf4\u5bf9\u4e0d\u8d77\u3002\u201d</p><p>\u201c\u59d0\u59d0\u4ec0\u4e48\u610f\u601d\uff1f\u201d</p><p>\u201c\u6211\u8bf4\u5bf9\u4e0d\u8d77\uff0c\u662f\u56e0\u4e3a\u6211\u77e5\u9053\uff0c\u5979\u7684\u505a\u6cd5\u8ba9\u4f60\u5f88\u96be\u582a\uff0c\u800c\u4e14\u5f88\u4f24\u4eba\uff0c\u201d\u73ca\u5609\u67d4\u58f0\u8bf4\uff0c\u201c\u59d0\u59d0\u77e5\u9053\uff0c\u4f5c\u4e3a\u4e00\u4e2a\u7537\u5b69\u5b50\uff0c\u5982\u679c\u8fde\u81ea\u5df1\u559c\u6b22\u7684\u4eba\u90fd\u6ca1\u6709\u80fd\u529b\u4fdd\u62a4\uff0c\u90a3\u662f\u975e\u5e38\u2026\u2026\u975e\u5e38\u4ee4\u4eba\u96be\u4ee5\u63a5\u53d7\u7684\u4e8b\u60c5\u3002\u5979\u7684\u505a\u6cd5\u4f1a\u8ba9\u4f60\u89c9\u5f97\u7f9e\u8fb1\uff0c\u8ba9\u4f60\u81ea\u5c0a\u5fc3\u53d7\u4f24\u5bb3\uff0c\u8fd9\u662f\u4e0d\u5bf9\u7684\u3002\u6240\u4ee5\u59d0\u59d0\u8981\u66ff\u5979\u5411\u4f60\u9053\u6b49\uff0c\u8bf4\u5bf9\u4e0d\u8d77\u3002\u201d</p><p>\u201c\u4f46\u662f\u5462\uff0c\u5c0f\u5f1f\uff0c\u201d\u5979\u7684\u8138\u8272\u4e25\u8083\u8d77\u6765\uff0c\u201c\u6211\u4e0d\u8d5e\u540c\u5979\u7684\u884c\u4e8b\u65b9\u6cd5\uff0c\u4f46\u5e76\u4e0d\u662f\u8bf4\u6211\u8ba4\u4e3a\u5979\u505a\u9519\u4e86\uff0c\u6211\u4e5f\u4e0d\u662f\u56e0\u6b64\u800c\u5411\u4f60\u9053\u6b49\uff0c\u201d\u5979\u5766\u7387\u5730\u770b\u7740\u743c\u6069\uff0c\u201c\u59d0\u59d0\u559c\u6b22\u4f60\uff0c\u8fd9\u70b9\u4f60\u5f88\u6e05\u695a\u3002\u7231\u60c5\u8fd9\u79cd\u4e1c\u897f\uff0c\u5bb9\u4e0d\u5f97\u7b2c\u4e09\u4e2a\u4eba\uff0c\u8fd9\u70b9\u4f60\u4e5f\u5e94\u8be5\u5f88\u6e05\u695a\u3002\u59d0\u59d0\u4e00\u5fc3\u4e00\u610f\u5bf9\u4f60\uff0c\u4f46\u4f60\u8eab\u8fb9\u5374\u6709\u4e00\u5927\u5806\u5176\u4ed6\u5973\u5b69\u5b50\uff0c\u8fd9\u8ba9\u59d0\u59d0\u5f88\u4e0d\u9ad8\u5174\uff0c\u8fd9\u70b9\u4f60\u4e5f\u5e94\u8be5\u5f88\u6e05\u695a\uff0c\u4f46\u4f60\u8fd8\u662f\u505a\u4e86\uff0c\u800c\u4e14\u6ca1\u6709\u6539\u6b63\u7684\u610f\u601d\uff0c\u201d\u5979\u987f\u4e86\u987f\uff0c\u201c\u5728\u8fd9\u4ef6\u4e8b\u4e0a\uff0c\u59d0\u59d0\u6ca1\u6709\u534a\u70b9\u5bf9\u4e0d\u8d77\u4f60\uff0c\u662f\u4f60\u5bf9\u4e0d\u8d77\u59d0\u59d0\uff0c\u662f\u4e0d\u662f\uff1f\u201d</p><p>\u201c\u2026\u2026\u662f\u3002\u201d</p><p>\u4ece\u5c0f\u5230\u5927\uff0c\u76f8\u5904\u5341\u51e0\u5e74\uff0c\u743c\u6069\u4e60\u60ef\u4e86\u6e29\u67d4\u5a49\u4e3d\u7684\u59d0\u59d0\uff0c\u4ece\u6ca1\u542c\u8fc7\u73ca\u5609\u7528\u8fd9\u79cd\u8bed\u6c14\u548c\u4ed6\u8bf4\u8bdd\u3002\u867d\u7136\u8c08\u4e0d\u4e0a\u591a\u4e48\u4e25\u5389\uff0c\u4f46\u5374\u900f\u7740\u51db\u51db\u7684\u5bd2\u610f\uff0c\u8ba9\u4ed6\u7981\u4e0d\u4f4f\u7684\u8138\u4e0a\u53d1\u70e7\uff0c\u80cc\u4e0a\u5192\u6c57\uff0c\u65e2\u7f9e\u4e14\u607c\uff0c\u5374\u53c8\u534a\u70b9\u4e0d\u6562\u53d1\u4f5c\uff0c\u53ea\u80fd\u95f7\u95f7\u5730\u5e94\u4e86\u4e00\u58f0\u3002</p><p>\u89c1\u4ed6\u8fd9\u526f\u6a21\u6837\uff0c\u73ca\u5609\u4e0d\u7531\u5f97\u53c8\u6709\u51e0\u5206\u5fc3\u8f6f\uff0c\u201c\u7b97\u5566\uff0c\u53c8\u4e0d\u662f\u4e00\u5929\u4e24\u5929\u7684\u4e8b\u60c5\u4e86\uff0c\u201d\u5979\u53f9\u4e86\u53e3\u6c14\uff0c\u201c\u4e5f\u662f\u59d0\u59d0\u4e00\u76f4\u90fd\u7eb5\u5bb9\u7740\u4f60\uff0c\u5ba0\u7740\u4f60\uff0c\u8ba9\u4f60\u53d8\u672c\u52a0\u5389\uff0c\u5c31\u77e5\u9053\u6b3a\u8d1f\u81ea\u5df1\u59d0\u59d0\u3002\u201d</p><p>\u201c\u5bf9\u4e0d\u8d77\u554a\uff0c\u59d0\u59d0\u3002\u201d</p><p>\u201c\u5973\u5b69\u5b50\u4e00\u5806\u4e00\u5806\u5f80\u5bb6\u5e26\uff0c\u73b0\u5728\u8fd8\u8bf4\u5bf9\u4e0d\u8d77\u6709\u4ec0\u4e48\u7528\uff1f\u201d\u73ca\u5609\u54fc\u4e86\u4e00\u58f0\uff0c\u201c\u4e0d\u8bf4\u4e86\uff0c\u8bf4\u8fd9\u6b21\u7684\u4e8b\u60c5\u5427\u3002\u4f60\u60f3\u7559\u4e0b\u5979\u4eec\uff1f\u201d</p><p>\u201c\u6211\u2026\u2026\u201d</p><p>\u201c\u8981\u4e0d\u8981\u6211\u53bb\u627e\u5965\u5609\u83b1\u65af\u5973\u58eb\u8bf4\u8bf4\u770b\uff0c\u201d\u73ca\u5609\u54ac\u4e86\u54ac\u5634\u5507\uff0c\u201c\u6211\u6c42\u60c5\u7684\u8bdd\uff0c\u5979\u5e94\u8be5\u4f1a\u7b54\u5e94\u3002\u201d</p><p>\u201c\u4e0d\u7528\u4e86\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4e0d\u7528\u4e86\uff0c\u8ba9\u5979\u4eec\u8d70\u5427\u3002\u201d</p><p>\u73ca\u5609\u6084\u6084\u677e\u4e86\u53e3\u6c14\uff0c\u5374\u53c8\u6709\u4e9b\u597d\u5947\uff0c\u201c\u4e3a\u4ec0\u4e48\u5462\uff0c\u4f60\u4e0d\u5e0c\u671b\u5979\u4eec\u7559\u4e0b\u5417\uff1f\u201d</p><p>\u201c\u5e0c\u671b\uff0c\u4e0d\u8fc7\u8fd9\u4ef6\u4e8b\u60c5\u548c\u59d0\u59d0\u6ca1\u5173\u7cfb\u2026\u2026\u55ef\uff0c\u6709\u5173\u7cfb\uff0c\u6240\u4ee5\u5c31\u66f4\u4e0d\u80fd\u518d\u59d4\u5c48\u59d0\u59d0\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u53e6\u5916\uff0c\u59d0\u59d0\uff0c\u6211\u4e5f\u60f3\u8bf4\u51e0\u53e5\u8bdd\u3002\u201d</p><p>\u201c\u55ef\uff0c\u4f60\u8bf4\u3002\u201d</p><p>\u201c\u6211\u559c\u6b22\u59d0\u59d0\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u771f\u7684\u559c\u6b22\u3002\u201d</p><p>\u201c\u55ef\uff0c\u6211\u77e5\u9053\u554a\uff0c\u201d\u73ca\u5609\u7b11\u7740\uff0c\u201c\u8fd9\u70b9\u59d0\u59d0\u4ece\u6765\u5c31\u6ca1\u6000\u7591\u8fc7\u5462\u3002\u201d</p><p>\u201c\u6211\u559c\u6b22\u59d0\u59d0\uff0c\u60f3\u8ba9\u59d0\u59d0\u6c38\u8fdc\u90fd\u9ad8\u5174\uff0c\u4f46\u662f\u6709\u4e9b\u4e8b\u60c5\uff0c\u786e\u5b9e\u662f\u6211\u5bf9\u4e0d\u8d77\u59d0\u59d0\uff0c\u60f9\u59d0\u59d0\u751f\u6c14\uff0c\u201d\u743c\u6069\u63a5\u7740\u8bf4\uff0c\u201c\u6211\u5f88\u7f9e\u6127\uff0c\u4e0d\u77e5\u9053\u8be5\u600e\u4e48\u8bf4\uff0c\u4e5f\u6ca1\u4ec0\u4e48\u53ef\u8fa9\u89e3\u7684\u3002\u6709\u4e9b\u4e8b\u60c5\uff0c\u6709\u4e9b\u4eba\uff0c\u6211\u77e5\u9053\u5982\u679c\u653e\u5f03\u6389\uff0c\u4f1a\u8ba9\u59d0\u59d0\u975e\u5e38\u9ad8\u5174\uff0c\u53ef\u662f\u6211\u6ca1\u6cd5\u2026\u2026\u6211\u5f88\u96be\u653e\u5f03\uff0c\u201d\u4ed6\u5782\u4e0b\u76ee\u5149\uff0c\u201c\u6211\u77e5\u9053\u6211\u7684\u505a\u6cd5\u5f88\u6076\u52a3\uff0c\u4f46\u6211\u6ca1\u529e\u6cd5\u2026\u2026\u4e0d\uff0c\u662f\u6211\u4e0d\u60c5\u613f\u53bb\u6539\u6b63\u3002\u59d0\u59d0\u8d23\u5907\u6211\uff0c\u6211\u65e0\u8bdd\u53ef\u8bf4\uff0c\u59d0\u59d0\u751f\u6211\u7684\u6c14\uff0c\u90a3\u4e5f\u662f\u7406\u6240\u5e94\u5f53\u3002\u6211\u53ea\u80fd\u8bf4\uff0c\u6211\u4f1a\u5c3d\u529b\u53bb\u505a\uff0c\u5c3d\u529b\u8ba9\u59d0\u59d0\u9ad8\u5174\uff0c\u4f46\u662f\u80fd\u591f\u505a\u5230\u4ec0\u4e48\u7a0b\u5ea6\uff0c\u6211\u65e0\u6cd5\u4fdd\u8bc1\uff0c\u6216\u8005\u8bf4\u73b0\u5728\u65e0\u6cd5\u4fdd\u8bc1\u3002\u201d</p><p>\u73ca\u5609\u9759\u9759\u770b\u7740\u4ed6\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u7a81\u7136\u8f7b\u58f0\u7b11\u4e86\u8d77\u6765\u3002</p><p>\u201c\u8fd9\u7b97\u662f\u644a\u724c\u5417\uff1f\u5c0f\u5f1f\uff1f\u201d</p><p>\u201c\u4e0d\u662f\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u53ea\u662f\u8ddf\u59d0\u59d0\u8bf4\u51e0\u53e5\u5fc3\u91cc\u8bdd\u3002\u201d</p><p>\u201c\u8fd9\u6837\u554a\uff0c\u786e\u5b9e\u4f60\u5f88\u4e45\u6ca1\u6709\u548c\u59d0\u59d0\u8bf4\u5fc3\u91cc\u8bdd\u4e86\u5462\uff0c\u201d\u73ca\u5609\u5fae\u7b11\u7740\uff0c\u629a\u6478\u4ed6\u7684\u8138\u988a\uff0c\u201c\u6211\u7684\u5f1f\u5f1f\uff0c\u771f\u7684\u957f\u5927\u4e86\u5462\u3002\u201d</p></article>"}}}]);
