(window.webpackJsonp=window.webpackJsonp||[]).push([[472],{494:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u56db\u8282 \u6211\u53cd\u5bf9",content:"<article><h2>\u7b2c\u56db\u8282 \u6211\u53cd\u5bf9</h2><p>\u201c\u4e0d\u662f\u957f\u5927\uff0c\u4e5f\u4e0d\u662f\u6210\u719f\uff0c\u59d0\u59d0\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u53ea\u662f\u4e0d\u60f3\u518d\u8fd9\u6837\u7ee7\u7eed\u4e0b\u53bb\u4e86\u3002\u201d</p><p>\u201c\u90a3\u60f3\u53d8\u6210\u4ec0\u4e48\u6837\u5462\uff1f\u201d\u73ca\u5609\u67d4\u58f0\u95ee\u3002</p><p>\u743c\u6069\u832b\u7136\uff0c\u6447\u6447\u5934\uff0c\u201c\u4e0d\u77e5\u9053\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u603b\u4e4b\uff0c\u5c31\u662f\u89c9\u5f97\u81ea\u5df1\u73b0\u5728\u7684\u72b6\u6001\u5f88\u7cdf\u7cd5\uff0c\u5f88\u5dee\u52b2\uff0c\u8ba9\u81ea\u5df1\u96be\u53d7\uff0c\u8ba9\u8eab\u8fb9\u7684\u4eba\u4e5f\u4e0d\u8212\u670d\u3002\u6240\u4ee5\u2026\u2026\u6709\u4e9b\u4e1c\u897f\uff0c\u6709\u4e9b\u4e8b\u60c5\uff0c\u5e94\u8be5\u6709\u6240\u6539\u53d8\u4e86\u5427\u3002\u201d</p><p>\u201c\u8fd9\u6837\u554a\u3002\u201d</p><p>\u73ca\u5609\u4f4e\u4f4e\u5730\u81ea\u8a00\u81ea\u8bed\uff0c\u7136\u540e\u5ae3\u7136\u5fae\u7b11\uff0c\u4f38\u51fa\u53cc\u81c2\uff0c\u201c\u6765\uff0c\u8ba9\u59d0\u59d0\u62b1\u62b1\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u743c\u6069\u4f0f\u5728\u59d0\u59d0\u6e29\u6696\u7684\u6000\u62b1\u91cc\uff0c\u611f\u89c9\u81ea\u5df1\u7684\u5934\u53d1\u88ab\u8f7b\u8f7b\u629a\u6478\u3002\u201c\u90a3\u4e48\uff0c\u5c0f\u5f1f\uff0c\u201d\u73ca\u5609\u8f7b\u58f0\u95ee\uff0c\u201c\u5bf9\u59d0\u59d0\u7684\u7231\u604b\uff0c\u4e5f\u4f1a\u6539\u53d8\u5417\uff1f\u201d</p><p>\u201c\u4e0d\u4f1a\u3002\u201d\u743c\u6069\u7acb\u523b\u56de\u7b54\u3002</p><p>\u201c\u771f\u7684\uff1f\u201d</p><p>\u201c\u771f\u7684\u3002\u201d</p><p>\u201c\u6c38\u8fdc\u90fd\u4e0d\u4f1a\uff1f\u201d\u5c11\u5973\u8ffd\u95ee\uff0c\u201c\u6c38\u8fdc\u90fd\u8fd9\u6837\u7231\u59d0\u59d0\uff1f\u201d</p><p>\u201c\u6c38\u8fdc\u3002\u201d</p><p>\u201c\u55ef\uff0c\u59d0\u59d0\u4e5f\u4e00\u6837\uff0c\u4f1a\u6c38\u8fdc\u7231\u4f60\uff0c\u201d\u73ca\u5609\u4eb2\u543b\u7740\u4ed6\u7684\u5934\u53d1\uff0c\u201c\u597d\u5427\uff0c\u201d\u5979\u8bf4\uff0c\u957f\u957f\u8212\u4e86\u53e3\u6c14\uff0c\u50cf\u662f\u53f9\u606f\uff0c\u53c8\u50cf\u662f\u5982\u91ca\u91cd\u8d1f\uff0c\u201c\u90a3\u5c31\u8ba9\u6211\u4eec\u90fd\u5c3d\u529b\u53bb\u505a\u5427\uff0c\u770b\u770b\u6700\u540e\u80fd\u505a\u5230\u4ec0\u4e48\u5730\u6b65\u3002\u201d</p><p>\u201c\u55ef\uff0c\u201d\u743c\u6069\u4f4e\u58f0\u8bf4\uff0c\u201c\u8c22\u8c22\u59d0\u59d0\u3002\u201d</p><p>\u59d0\u5f1f\u4e24\u4eba\u4ece\u5c0f\u4e00\u8d77\u957f\u5927\uff0c\u76f8\u5904\u5341\u51e0\u5e74\uff0c\u867d\u7136\u4e0d\u50cf\u8299\u857e\u72c4\u548c\u8299\u8389\u5a05\u90a3\u6837\u80fd\u591f\u5fc3\u7075\u611f\u5e94\uff0c\u4f46\u4e5f\u81ea\u6709\u5176\u9ed8\u5951\u3002\u7b80\u5355\u51e0\u53e5\u8bdd\uff0c\u5f7c\u6b64\u7684\u610f\u601d\u90fd\u5df2\u7ecf\u6e05\u6e05\u695a\u695a\u3002\u743c\u6069\u5e76\u4e0d\u662f\u5728\u644a\u724c\uff0c\u4ed6\u53ea\u662f\u9f13\u8d77\u52c7\u6c14\u9762\u5bf9\u8d8a\u6765\u8d8a\u4e25\u5cfb\u7684\u73b0\u5b9e\uff0c\u5766\u5766\u767d\u767d\u5730\u5c06\u81ea\u5df1\u7684\u60f3\u6cd5\u544a\u8bc9\u73ca\u5609\uff1a\u4ed6\u7231\u73ca\u5609\uff0c\u4f46\u4ed6\u4e0d\u613f\u4e3a\u4e86\u59d0\u59d0\u653e\u5f03\u5176\u4ed6\u5973\u5b69\u5b50\uff0c\u73b0\u5728\u4ed6\u552f\u4e00\u80fd\u505a\u7684\uff0c\u552f\u4e00\u80fd\u591f\u627f\u8bfa\u7684\uff0c\u53ea\u662f\u201c\u5c3d\u529b\u201d\uff0c\u5c3d\u529b\u8ba9\u59d0\u59d0\u9ad8\u5174\u3002\u73ca\u5609\u7684\u56de\u7b54\u5219\u662f\uff1a\u5979\u7231\u743c\u6069\uff0c\u6240\u4ee5\u96be\u4ee5\u63a5\u53d7\u5f1f\u5f1f\u88ab\u5176\u4ed6\u5973\u5b69\u5b50\u5206\u4eab\uff0c\u5979\u65e0\u6cd5\u4e0d\u5ac9\u5992\uff0c\u65e0\u6cd5\u4e0d\u751f\u6c14\uff0c\u4f46\u5979\u540c\u6837\u627f\u8bfa\u4f1a\u5c3d\u529b\u53bb\u505a\uff0c\u5c3d\u529b\u5730\u53bb\u514b\u5236\uff0c\u53bb\u5bb9\u5fcd\uff0c\u53bb\u8fc1\u5c31\uff0c\u4f46\u5230\u5e95\u80fd\u514b\u5236\u5230\u4ec0\u4e48\u5730\u6b65\uff0c\u5bb9\u5fcd\u5230\u4ec0\u4e48\u7a0b\u5ea6\uff0c\u8fc1\u5c31\u5230\u4ec0\u4e48\u65f6\u5019\uff0c\u5979\u4e5f\u4e0d\u77e5\u9053\u3002</p><p>\u8fd9\u4e0d\u662f\u627f\u8bfa\uff0c\u56e0\u4e3a\u8c01\u4e5f\u65e0\u6cd5\u7ed9\u5bf9\u65b9\u4fdd\u8bc1\uff0c\u4e5f\u4e0d\u662f\u8a93\u8a00\uff0c\u56e0\u4e3a\u8c01\u4e5f\u65e0\u6cd5\u786e\u5b9a\u660e\u5929\u81ea\u5df1\u4f1a\u4e0d\u4f1a\u6539\u53d8\u5fc3\u610f\u3002\u8fd9\u53ea\u662f\u5766\u767d\u5730\u8bf4\u51fa\u81ea\u5df1\u7684\u5fc3\u610f\uff0c\u5e73\u9759\u5730\u9762\u5bf9\u5df2\u7ecf\u53d1\u751f\u7684\u73b0\u5b9e\uff0c\u5f7c\u6b64\u7b54\u5e94\u201c\u5c3d\u529b\u800c\u4e3a\u201d\u3002\u7ed3\u5c40\u7a76\u7adf\u4f1a\u5982\u4f55\uff0c\u8c01\u4e5f\u4e0d\u77e5\u9053\uff0c\u4f46\u81f3\u5c11\u6b64\u65f6\u6b64\u523b\uff0c\u5f7c\u6b64\u90fd\u8fd8\u7231\u604b\u7740\u5bf9\u65b9\uff0c\u5f7c\u6b64\u90fd\u5bf9\u5bf9\u65b9\u7684\u5fc3\u610f\u786e\u4fe1\u65e0\u7591\uff0c\u81f3\u5c11\u6b64\u65f6\u6b64\u523b\uff0c\u5f7c\u6b64\u90fd\u65e0\u6cd5\u6492\u624b\uff0c\u5f7c\u6b64\u90fd\u820d\u4e0d\u5f97\u653e\u5f03\u3002</p><p>\u90a3\u5c31\u8fd9\u6837\u5427\u3002</p><p>\u623f\u95f4\u91cc\u9759\u6084\u6084\u7684\uff0c\u4e00\u65f6\u95f4\u8c01\u90fd\u6ca1\u6709\u518d\u8bf4\u8bdd\u3002\u5ffd\u7136\u95f4\uff0c\u73ca\u5609\u683c\u683c\u7b11\u8d77\u6765\uff0c\u6253\u7834\u4e86\u6c89\u5bc2\uff0c\u201c\u5c0f\u5f1f\uff0c\u5728\u52a8\u4ec0\u4e48\u574f\u5fc3\u601d\u554a\u3002\u201d\u5979\u5a07\u55d4\u3002</p><p>\u201c\u55ef\uff1f\u6ca1\u6709\u554a\u3002\u201d</p><p>\u201c\u53ef\u662f\u4f60\u90a3\u5730\u65b9\u2026\u2026\u5728\u9876\u7740\u59d0\u59d0\u5462\u3002\u201d</p><p>\u201c\u554a\u2026\u2026\u54e6\uff01\u201d</p><p>\u81ea\u4ece\u82cf\u9192\u4e4b\u540e\uff0c\u743c\u6069\u5c31\u4e00\u76f4\u89c9\u5f97\u81ea\u5df1\u7684\u8eab\u4f53\u6709\u4e9b\u4e0d\u5bf9\u52b2\uff0c\u65e0\u8bba\u662f\u4f53\u529b\u8fd8\u662f\u7cbe\u795e\uff0c\u90fd\u660e\u663e\u6bd4\u4ee5\u524d\u8981\u5f3a\u58ee\u65fa\u76db\u8bb8\u591a\u3002\u521a\u548c\u8299\u857e\u72c4\u7f20\u7ef5\u4e86\u51e0\u4e2a\u5c0f\u65f6\uff0c\u5728\u5c0f\u5973\u5b69\u7684\u7f8e\u5999\u5a07\u81a3\u91cc\u53d1\u6cc4\u4e86\u4e00\u6b21\u53c8\u4e00\u6b21\uff0c\u672c\u5e94\u8be5\u5b8c\u5168\u75b2\u8f6f\u624d\u5bf9\uff0c\u7ed3\u679c\u5728\u8299\u8389\u5a05\u7684\u623f\u95f4\u91cc\uff0c\u88ab\u5979\u5a07\u7f9e\u795e\u6001\u5438\u5f15\uff0c\u987f\u65f6\u4fbf\u53c8\u96c4\u98ce\u518d\u632f\u4e86\u3002\u5982\u4eca\u88ab\u6f02\u4eae\u59d0\u59d0\u62b1\u5728\u6000\u91cc\uff0c\u8138\u57cb\u8fdb\u6e29\u9999\u8f6f\u7389\u7684\u4e30\u6ee1\u80f8\u90e8\uff0c\u6e05\u5e7d\u51b0\u751c\u7684\u5c11\u5973\u4f53\u9999\u4e1d\u4e1d\u5165\u9f3b\uff0c\u867d\u7136\u5fc3\u4e2d\u6709\u4e8b\uff0c\u4e0b\u4f53\u5374\u4e0d\u7531\u81ea\u4e3b\u5730\u633a\u8d77\u6765\uff0c\u786c\u786c\u5730\u62b5\u5728\u73ca\u5609\u7684\u5927\u817f\u5185\u4fa7\uff0c\u5373\u4fbf\u9694\u7740\u6bdb\u6bef\u90fd\u80fd\u6e05\u695a\u5730\u611f\u89c9\u5230\u90a3\u79cd\u6eda\u70eb\u7684\u70ed\u5ea6\u3002</p><p>\u743c\u6069\u7565\u89c9\u5c34\u5c2c\uff0c\u4f46\u65e2\u7136\u662f\u5728\u73ca\u5609\u9762\u524d\uff0c\u5012\u4e5f\u65e0\u9700\u63a9\u9970\u3002\u59d0\u5f1f\u4fe9\u4e4b\u95f4\u7684\u6c14\u6c1b\u539f\u672c\u6709\u4e9b\u5fae\u5999\uff0c\u88ab\u8fd9\u4e00\u6253\u832c\uff0c\u53cd\u800c\u53d8\u5f97\u8f7b\u677e\u4e0b\u6765\u3002\u201c\u56e0\u4e3a\u5b83\u559c\u6b22\u59d0\u59d0\u554a\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u6545\u610f\u633a\u4e86\u633a\u8170\uff0c\u91cd\u91cd\u5730\u9876\u4e86\u4e00\u4e0b\u73ca\u5609\uff0c\u201c\u6240\u4ee5\u4e00\u770b\u5230\u59d0\u59d0\u5c31\u4f1a\u786c\u8d77\u6765\u561b\u3002\u201d</p><p>\u201c\u624d\u4e0d\u662f\uff0c\u201d\u73ca\u5609\u6485\u7740\u5634\uff0c\u201c\u5b83\u662f\u53ea\u8981\u89c1\u5230\u6f02\u4eae\u5973\u5b69\uff0c\u5c31\u90fd\u4f1a\u8fd9\u4e48\u795e\u6c14\u6d3b\u73b0\u5427\uff0c\u624d\u4e0d\u662f\u56e0\u4e3a\u6211\u5462\u3002\u201d</p><p>\u201c\u53ef\u662f\uff0c\u59d0\u59d0\u5c31\u662f\u6f02\u4eae\u5973\u5b69\u554a\uff0c\u800c\u4e14\u662f\u4e16\u754c\u4e0a\u6700\u6f02\u4eae\u7684\u5973\u5b69\u3002\u201d</p><p>\u867d\u7136\u4f9d\u65e7\u662f\u5e26\u7740\u918b\u610f\u7684\u53e3\u543b\uff0c\u4f46\u8bed\u6c14\u4e2d\u5176\u5b9e\u5e76\u6ca1\u6709\u591a\u5c11\u771f\u6b63\u7684\u62b1\u6028\uff0c\u66f4\u591a\u662f\u5728\u6492\u5a07\u3002\u73ca\u5609\u4e0d\u662f\u5bfb\u5e38\u5973\u5b69\u5b50\uff0c\u8bdd\u5df2\u7ecf\u8bf4\u5230\u8fd9\u4e2a\u5730\u6b65\uff0c\u518d\u7ee7\u7eed\u7ea0\u7f20\u4e5f\u6ca1\u6709\u610f\u4e49\uff0c\u4e0d\u5982\u6682\u65f6\u653e\u4e0b\u3002\u6240\u4ee5\u743c\u6069\u7684\u56de\u7b54\u4e5f\u5c31\u6062\u590d\u4e86\u5f80\u5e38\u7684\u8f7b\u677e\u81ea\u5728\uff0c\u4eff\u4f5b\u534a\u5f00\u73a9\u7b11\u3002\u4f46\u4ed6\u6240\u8bf4\u7684\u8bdd\u4e5f\u786e\u5b9e\u53d1\u81ea\u771f\u5fc3\uff0c\u5728\u6240\u89c1\u8fc7\u7684\u5973\u5b69\u5b50\u4e2d\uff0c\u73ca\u5609\u6beb\u65e0\u7591\u95ee\u662f\u6700\u6f02\u4eae\u7684\u2014\u2014\u597d\u5427\uff0c\u6216\u8bb8\u6bd4\u6df1\u6e0a\u65ad\u57df\u9547\u7684\u7ea2\u8272\u5bff\u8863\u7565\u900a\u4e00\u7ebf\uff0c\u95ee\u9898\u5728\u4e8e\u540e\u8005\u4e0d\u662f\u5973\u5b69\u5b50\uff0c\u662f\u4eba\u5996\u2026\u2026</p><p>\u73ca\u5609\u751c\u751c\u7b11\u7740\uff0c\u8d1d\u9f7f\u5728\u743c\u6069\u7684\u8033\u5782\u4e0a\u8f7b\u8f7b\u54ac\u556e\uff0c\u7559\u4e0b\u4e24\u6392\u7ec6\u7ec6\u7684\u7259\u5370\u3002\u201c\u5c0f\u574f\u86cb\uff0c\u201d\u5979\u5728\u8033\u8fb9\u8bf4\uff0c\u5410\u6c14\u82e5\u5170\uff0c\u201c\u8bf4\uff0c\u662f\u4e0d\u662f\u4ece\u5c0f\u5c31\u60f3\u7740\u6b3a\u8d1f\u59d0\u59d0\u554a\uff1f\u201d</p><p>\u201c\u662f\u554a\uff0c\u56e0\u4e3a\u59d0\u59d0\u5c0f\u65f6\u5019\u5c31\u90a3\u4e48\u6f02\u4eae\u561b\uff0c\u201d\u743c\u6069\u7b11\u7740\u56de\u7b54\uff0c\u201c\u6574\u5929\u6709\u4e2a\u6f02\u4eae\u59d0\u59d0\u5728\u9762\u524d\u6643\u6765\u6643\u53bb\uff0c\u4e0d\u7ba1\u662f\u54ea\u4e2a\u5f1f\u5f1f\u4e5f\u90fd\u6ca1\u6cd5\u4e0d\u8d77\u5fc3\u601d\u5427\u3002\u201d</p><p>\u201c\u90a3\u4ec0\u4e48\u65f6\u5019\u5c31\u5f00\u59cb\u59d0\u59d0\u52a8\u5fc3\u601d\u5462\uff1f\u201d\u73ca\u5609\u95ee\u3002</p><p>\u201c\u8fd9\u4e2a\u8bf4\u4e0d\u597d\u5462\uff0c\u53cd\u6b63\u662f\u5f88\u65e9\u5f88\u65e9\u5427\u3002\u201d</p><p>\u201c\u5927\u6982\u51e0\u5c81\u7684\u65f6\u5019\u5462\uff1f\u201d</p><p>\u201c\u4e0d\u8bb0\u5f97\u4e86\uff0c\u603b\u4e4b\u2026\u2026\u55ef\uff0c\u81f3\u5c11\u5728\u53bb\u5b66\u6821\u4e4b\u524d\uff0c\u5c31\u5df2\u7ecf\u660e\u786e\u4e86\u2018\u81ea\u5df1\u8ff7\u604b\u59d0\u59d0\u2019\u8fd9\u4e2a\u4e8b\u5b9e\u5427\u3002\u201d</p><p>\u201c\u79bd\u517d\uff01\u201d\u73ca\u5609\u4fcf\u8138\u901a\u7ea2\uff0c\u5550\u4e86\u5f1f\u5f1f\u4e00\u53e3\uff0c\u201c\u53bb\u5b66\u6821\u4e4b\u524d\uff1f\u90a3\u65f6\u5019\u4f60\u624d\u591a\u5927\u554a\uff1f\u90a3\u65f6\u5019\u59d0\u59d0\u4e5f\u624d\u4e0d\u5230\u5341\u5c81\u5427\uff0c\u4f60\u5c31\u5df2\u7ecf\u6253\u4e3b\u610f\u4e86\uff1f\u201d</p><p>\u201c\u6ca1\u529e\u6cd5\u554a\uff0c\u201d\u743c\u6069\u5f88\u59d4\u5c48\u5730\u8bf4\uff0c\u201c\u8c01\u8ba9\u59d0\u59d0\u8fd8\u4e0d\u5230\u5341\u5c81\uff0c\u5c31\u5df2\u7ecf\u662f\u4e2a\u6807\u81f4\u7f8e\u4eba\u5462\u3002\u201d</p><p>\u201c\u54fc\uff0c\u5949\u627f\u59d0\u59d0\u4e5f\u662f\u6ca1\u6709\u7528\u7684\uff0c\u201d\u73ca\u5609\u88c5\u4f5c\u4e25\u5389\u7684\u6837\u5b50\u8bf4\uff0c\u4f46\u7709\u773c\u95f4\u5374\u5c3d\u662f\u63a9\u4e0d\u4f4f\u7684\u7b11\u610f\uff0c\u201c\u6240\u4ee5\uff0c\u59d0\u59d0\u662f\u4f60\u559c\u6b22\u7684\u7b2c\u4e00\u4e2a\u5973\u5b69\u5b50\uff0c\u5bf9\u4e0d\u5bf9\uff1f\u201d</p><p>\u201c\u5f53\u7136\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u59d0\u59d0\u662f\u7b2c\u4e00\u4e2a\u3002\u201d</p><p>\u201c\u4e5f\u662f\u6700\u559c\u6b22\u7684\uff0c\u5bf9\u4e0d\u5bf9\uff1f\u201d</p><p>\u743c\u6069\u70b9\u5934\u3002</p><p>\u201c\u8fd9\u8fd8\u5dee\u4e0d\u591a\u3002\u201d\u73ca\u5609\u6ee1\u610f\u5730\u8bf4\uff0c\u201c\u5c31\u7b97\u2026\u2026\u5c31\u7b97\u59d0\u59d0\u4e0d\u80fd\u6210\u4e3a\u4f60\u552f\u4e00\u559c\u6b22\u7684\u5973\u5b69\u5b50\uff0c\u4e5f\u4e00\u5b9a\u8981\u662f\u7b2c\u4e00\u4e2a\uff0c\u800c\u4e14\u8981\u662f\u6700\u559c\u6b22\u7684\u90a3\u4e2a\u3002\u4f60\u7684\u59d0\u59d0\uff0c\u53ef\u662f\u4ece\u6765\u5c31\u4e0d\u80fd\u6bd4\u522b\u4eba\u5dee\u7684\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u201c\u800c\u4e14\uff0c\u522b\u7684\u5973\u5b69\u5b50\u80fd\u505a\u5230\u7684\uff0c\u59d0\u59d0\u4e5f\u90fd\u53ef\u4ee5\u54df\uff0c\u201d\u5c11\u5973\u5a07\u5a9a\u800c\u53c8\u4e0d\u65e0\u81ea\u8d1f\u5730\u8bf4\uff0c\u201c\u5c31\u7b97\u662f\u73b0\u5728\u4e0d\u4f1a\u4e5f\u6ca1\u5173\u7cfb\uff0c\u59d0\u59d0\u5b66\u4e1c\u897f\u53ef\u662f\u5f88\u5feb\u7684\u3002\u201d</p><p>\u8bf4\u7740\u81ea\u4fe1\u7684\u5ba3\u8a00\uff0c\u73ca\u5609\u4fa7\u8fc7\u8eab\u4f53\uff0c\u5c06\u6000\u4e2d\u7684\u743c\u6069\u653e\u5728\u6795\u4e0a\uff0c\u4eb2\u543b\u4ed6\u7684\u8138\u988a\uff0c\u7136\u540e\u4e00\u70b9\u70b9\u5f80\u4e0b\uff0c\u6162\u6162\u94bb\u8fdb\u6bef\u5b50\u91cc\u3002\u743c\u6069\u611f\u89c9\u5230\u59d0\u59d0\u8131\u4e0b\u81ea\u5df1\u7684\u5185\u8863\uff0c\u8138\u988a\u8d34\u5728\u5927\u817f\u6839\u90e8\uff0c\u4f3c\u4e4e\u72b9\u8c6b\u4e86\u7247\u523b\uff0c\u7136\u540e\u4e00\u70b9\u70b9\u5730\u541e\u5165\uff0c\u52aa\u529b\u5730\u542e\u5438\u8d77\u6765\uff0c\u67d4\u8f6f\u7684\u820c\u5c16\u4e0d\u65ad\u8214\u8210\u3002\u6bd5\u7adf\u5df2\u7ecf\u4e0d\u662f\u7b2c\u4e00\u6b21\uff0c\u6709\u8fc7\u7ecf\u9a8c\uff0c\u5979\u7684\u6280\u672f\u660e\u663e\u6bd4\u524d\u6b21\u8fdb\u6b65\u4e86\u4e00\u4e9b\uff0c\u867d\u7136\u8fd8\u662f\u663e\u5f97\u5f88\u751f\u6da9\u3002\u743c\u6069\u8212\u670d\u5730\u4eab\u53d7\u4e86\u5927\u7ea6\u4e8c\u5341\u5206\u949f\uff0c\u7136\u540e\u793a\u610f\u73ca\u5609\u505c\u4e0b\u6765\u3002\u201c\u53ef\u4ee5\u4e86\uff0c\u59d0\u59d0\u3002\u201d\u4ed6\u8bf4\u3002</p><p>\u201c\u5514\uff1f\u201d\u73ca\u5609\u5947\u602a\uff0c\u201c\u4e0d\u8981\u59d0\u59d0\u5e2e\u4f60\u54ac\u51fa\u6765\u5417\uff1f\u201d</p><p>\u53ef\u662f\u59d0\u59d0\u4f60\u505a\u4e0d\u5230\u554a\uff0c\u743c\u6069\u5728\u5fc3\u91cc\u8bf4\uff0c\u4f46\u8bdd\u5230\u5634\u8fb9\u5219\u53d8\u6210\u4e86\u53e6\u5916\u4e00\u53e5\uff0c\u201c\u4e0d\u884c\u5566\uff0c\u59d0\u59d0\u518d\u54ac\u4e0b\u53bb\u6211\u5c31\u8981\u5fcd\u4e0d\u4f4f\u4e86\u3002\u201d</p><p>\u201c\u90a3\u4e0d\u597d\u5417\uff1f\u201d\u73ca\u5609\u4f9d\u65e7\u4e0d\u89e3\uff0c\u201c\u4f60\u4e0d\u662f\u4e00\u76f4\u90fd\u5f88\u559c\u6b22\u5728\u5973\u5b69\u5b50\u5634\u91cc\u2026\u2026\u5514\uff0c\u90a3\u4e2a\u2026\u2026\u201d</p><p>\u201c\u4e0d\u597d\uff0c\u201d\u743c\u6069\u4e00\u672c\u6b63\u7ecf\u5730\u8bf4\uff0c\u201c\u56e0\u4e3a\u6211\u66f4\u559c\u6b22\u5728\u59d0\u59d0\u7684\u8eab\u4f53\u91cc\u53d1\u5c04\u51fa\u6765\u3002\u201d</p><p>\u201c\u9a97\u4eba\uff01\u4f60\u660e\u660e\u5c31\u66f4\u559c\u6b22\u5973\u5b69\u5b50\u7528\u5634\uff0c\u8299\u857e\u72c4\u8ddf\u6211\u8bf4\u8fc7\u2026\u2026\u201d</p><p>\u8bdd\u8bf4\u5230\u4e00\u534a\uff0c\u73ca\u5609\u81ea\u77e5\u5931\u8a00\uff0c\u6084\u6084\u62ac\u773c\u770b\u743c\u6069\uff0c\u53d1\u73b0\u4ed6\u5e76\u672a\u56e0\u4e3a\u8299\u857e\u72c4\u7684\u540d\u5b57\u88ab\u63d0\u8d77\u800c\u6709\u4ec0\u4e48\u5f02\u6837\u3002\u201c\u597d\u5566\uff0c\u90fd\u542c\u4f60\u7684\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u59d0\u59d0\u662f\u4f60\u7684\u5973\u4eba\u561b\u3002\u201d</p><p>\u5979\u94bb\u51fa\u6bef\u5b50\uff0c\u8ba9\u5f1f\u5f1f\u62b1\u5728\u6000\u4e2d\uff0c\u95ed\u4e0a\u773c\u775b\u548c\u4ed6\u63a5\u543b\uff0c\u547c\u5438\u6e10\u6e10\u53d8\u5f97\u6025\u4fc3\uff0c\u808c\u80a4\u7684\u6e29\u5ea6\u4e5f\u5728\u5feb\u901f\u4e0a\u5347\u3002\u743c\u6069\u629a\u6478\u7740\u59d0\u59d0\u7684\u8eab\u4f53\uff0c\u719f\u7ec3\u5730\u6311\u9017\u7740\u5979\uff0c\u8bba\u53ca\u8fd9\u65b9\u9762\u7684\u7ecf\u9a8c\uff0c\u4ed6\u80dc\u8fc7\u73ca\u5609\u4f55\u6b62\u767e\u500d\uff0c\u5f88\u5feb\u4fbf\u5c06\u5c11\u5973\u9017\u5f04\u5f97\u5a07\u5598\u5401\u5401\uff0c\u8138\u988a\u706b\u7ea2\u5982\u70e7\uff0c\u4e0d\u7531\u81ea\u4e3b\u7d27\u5e76\u7684\u817f\u80a1\u95f4\u5df2\u7ecf\u662f\u6eaa\u6c34\u6f7a\u6f7a\uff0c\u6cdb\u6ee5\u6210\u707e\u3002</p><p>\u201c\u5c0f\u5f1f\uff0c\u201d\u5979\u54ac\u7740\u5634\u5507\uff0c\u4f4e\u4f4e\u5598\u606f\u7740\uff0c\u201c\u59d0\u59d0\u53d7\u4e0d\u4e86\u5566\uff0c\u5feb\u70b9\u2026\u2026\u201d</p><p>\u201c\u5feb\u70b9\u4ec0\u4e48\uff1f\u201d\u743c\u6069\u6545\u610f\u95ee\u3002</p><p>\u201c\u574f\u86cb\uff01\u201d\u73ca\u5609\u5a07\u5a9a\u5730\u6a2a\u4e86\u4ed6\u4e00\u773c\uff0c\u73af\u81c2\u5c06\u4ed6\u8116\u9888\u6402\u4f4f\uff0c\u201c\u5feb\u70b9\u2026\u2026\u6765\u6b3a\u8d1f\u59d0\u59d0\uff0c\u5230\u59d0\u59d0\u91cc\u9762\u6765\u2026\u2026\u201d</p><p>\u201c\u4e0d\u884c\uff0c\u59d0\u59d0\u8981\u6c42\u6211\u624d\u884c\u3002\u201d</p><p>\u201c\u6c42\u4f60\u4e86\uff0c\u5c0f\u5f1f\uff0c\u59d0\u59d0\u6295\u964d\u597d\u4e0d\u597d\uff0c\u201d\u5c11\u5973\u4e56\u4e56\u5730\u8868\u793a\u5c48\u670d\uff0c\u201c\u59d0\u59d0\u6295\u964d\u4e86\uff0c\u6c42\u4f60\u6765\u6b3a\u8d1f\u59d0\u59d0\u3002\u201d</p><p>\u201c\u8fd9\u8fd8\u5dee\u4e0d\u591a\uff0c\u201d\u743c\u6069\u54ac\u7740\u5979\u7684\u8033\u5782\uff0c\u201c\u59d0\u59d0\u9080\u8bf7\uff0c\u90a3\u6211\u5c31\u6b23\u7136\u4ece\u547d\u4e86\u3002\u201d</p><p>\u4ed6\u5c06\u59d0\u59d0\u538b\u5728\u8eab\u4e0b\uff0c\u8dea\u5728\u53cc\u817f\u95f4\uff0c\u8c03\u6574\u597d\u59ff\u52bf\uff0c\u7136\u540e\u4e00\u70b9\u70b9\u5730\u7f13\u7f13\u8fdb\u5165\u3002\u56e0\u4e3a\u5df2\u7ecf\u6709\u5145\u5206\u6da6\u6ed1\u7684\u5173\u7cfb\uff0c\u73ca\u5609\u4ec5\u4ec5\u53ea\u662f\u4e00\u5f00\u59cb\u6709\u4e9b\u80c0\u75db\uff0c\u5f88\u5feb\u4fbf\u9002\u5e94\u8fc7\u6765\u3002\u8003\u8651\u5230\u59d0\u59d0\u521a\u521a\u82cf\u9192\uff0c\u8eab\u4f53\u53ef\u80fd\u6ca1\u6709\u5b8c\u5168\u6062\u590d\uff0c\u743c\u6069\u7684\u52a8\u4f5c\u975e\u5e38\u6e29\u67d4\uff0c\u8fd9\u4e5f\u5bfc\u81f4\u65f6\u95f4\u53d8\u5f97\u66f4\u957f\uff0c\u4f46\u6c89\u6d78\u5728\u6b22\u597d\u4e2d\u7684\u4e24\u4eba\u8c01\u4e5f\u4e0d\u5728\u610f\uff0c\u8fde\u6d6e\u7a7a\u57ce\u5df2\u7ecf\u51b2\u51fa\u6d77\u9762\uff0c\u505c\u6b62\u4e0a\u5347\uff0c\u5728\u7a7a\u4e2d\u60ac\u505c\u4e0b\u6765\u90fd\u6ca1\u53d1\u89c9\u3002\u5728\u4e00\u8f6e\u731b\u70c8\u51b2\u523a\u4e4b\u540e\uff0c\u73ca\u5609\u7d27\u7d27\u6293\u7740\u743c\u6069\uff0c\u6307\u5c16\u6df1\u6df1\u6390\u5165\u80cc\u90e8\u808c\u8089\uff0c\u8eab\u4f53\u65e0\u6cd5\u6291\u5236\u5730\u7d27\u7ef7\u7740\uff0c\u98a4\u6296\u7740\uff0c\u6700\u7ec8\u518d\u5ea6\u88ab\u9001\u4e0a\u4e86\u5dc5\u5cf0\u3002</p><p>\u201c\u59d0\u59d0\u8212\u670d\u5417\uff1f\u201d\u743c\u6069\u5598\u606f\u7740\u4ece\u5c11\u5973\u8eab\u4e0a\u7ffb\u4e0b\u6765\uff0c\u8fde\u573a\u6fc0\u6218\uff0c\u4ed6\u53ea\u89c9\u81ea\u5df1\u5934\u6655\u773c\u82b1\uff0c\u8f7b\u58f0\u95ee\u6000\u4e2d\u7684\u5c11\u5973\u611f\u53d7\uff0c\u800c\u73ca\u5609\u5df2\u7ecf\u5168\u8eab\u762b\u8f6f\uff0c\u8fde\u7741\u773c\u7684\u529b\u6c14\u90fd\u6ca1\u6709\uff0c\u5fae\u5fae\u70b9\u4e86\u70b9\u5934\uff0c\u7b97\u662f\u56de\u7b54\u3002\u4e24\u4eba\u4f11\u606f\u826f\u4e45\uff0c\u603b\u7b97\u662f\u6e10\u6e10\u6062\u590d\u4e86\u70b9\u4f53\u529b\uff0c\u73ca\u5609\u6162\u6162\u7741\u5f00\u773c\u775b\uff0c\u770b\u7740\u743c\u6069\uff0c\u7f9e\u6da9\u5730\u7b11\u4e86\u7b11\u3002\u201c\u59d0\u59d0\u2026\u2026\u521a\u624d\u7684\u6837\u5b50\u662f\u4e0d\u662f\u5f88\u653e\u8361\u554a\uff1f\u201d</p><p>\u201c\u662f\u5f88\u7f8e\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u7f8e\u6781\u4e86\u3002\u201d</p><p>\u73ca\u5609\u7b11\u7740\uff0c\u8138\u4e0a\u5c3d\u662f\u5e78\u798f\u795e\u60c5\uff0c\u8fc7\u4e86\u7247\u523b\uff0c\u5979\u7a81\u7136\u60f3\u8d77\u4ef6\u4e8b\u60c5\u6765\uff0c\u201c\u54e6\uff0c\u5bf9\u4e86\uff0c\u5c0f\u5f1f\uff0c\u6709\u4e2a\u597d\u6d88\u606f\u5dee\u70b9\u5fd8\u4e86\u544a\u8bc9\u4f60\uff0c\u201d\u5979\u9887\u4e3a\u5174\u594b\u5730\u8bf4\uff0c\u201c\u5965\u5609\u83b1\u65af\u5973\u58eb\u8bf4\u60f3\u6536\u6211\u505a\u5b66\u751f\u5462\u3002\u201d</p><p>\u743c\u6069\u6014\u4e86\u534a\u664c\uff0c\u7136\u540e\u6162\u6162\u6447\u5934\u3002</p><p>\u201c\u600e\u4e48\u4e86\uff1f\u201d\u73ca\u5609\u4e0d\u89e3\u3002</p><p>\u201c\u6211\u53cd\u5bf9\u8fd9\u4ef6\u4e8b\uff0c\u59d0\u59d0\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u575a\u51b3\u53cd\u5bf9\u3002\u201d</p></article>"}}}]);
