(window.webpackJsonp=window.webpackJsonp||[]).push([[474],{497:function(p){p.exports={chapterTitle:null,title:"\u7b2c474\u7ae0 \u4e09\u4eba\u9152\u540e\u5171\u7720",content:"<article><h2>\u7b2c474\u7ae0 \u4e09\u4eba\u9152\u540e\u5171\u7720</h2><p>\u3000\u3000\u201c\u771f\u5fc3\u8bdd\u5f53\u7136\u4e0d\u53ef\u80fd\u73a9\uff0c\u5979\u4e5f\u77e5\u9053\u6211\u4f1a\u606c\u4e0d\u77e5\u803b\u7684\u80e1\u8bf4\u516b\u9053\uff0c\u8bf4\u5b9a\u8981\u73a9\u5927\u5192\u9669\uff0c\u559d\u4e0b\u4e00\u676f\u9152\uff0c\u5c31\u5c06\u5e0c\u671b\u5bf9\u65b9\u505a\u7684\u4e8b\u60c5\u5199\u4e0b\u6765\uff0c\u7b49\u4e24\u4eba\u5c06\u4e00\u74f6\u5e72\u7ea2\u559d\u5b8c\uff0c\u4ece\u5bf9\u65b9\u5199\u4e0b\u7684\u90a3\u4e9b\u4e8b\u60c5\u91cc\u6311\u4e00\u4ef6\u81ea\u5df1\u4ee5\u4e3a\u6700\u8f7b\u677e\u7684\u4e8b\u53bb\u505a\u5c31\u53ef\u4ee5\u4e86\u2026\u2026\u201d\u5f20\u606a\u5728\u7535\u68af\u91cc\uff0c\u7b11\u7740\u8ddf\u5b59\u5c1a\u4e49\u3001\u8bb8\u601d\u89e3\u91ca\u4e0b\u5348\u53d1\u751f\u7684\u4e8b\u60c5\uff0c\u201c\u6211\u4e0d\u77e5\u9053\u5979\u4f1a\u600e\u4e48\u6574\u6211\uff0c\u53cd\u6b63\u6211\u77e5\u9053\u5979\u4f1a\u5c06\u6211\u625b\u5728\u67b6\u5b50\u4e0a\u4e0b\u4e0d\u4e86\u53f0\uff0c\u4e5f\u4f30\u8ba1\u5979\u4ee5\u4e3a\u6211\u4f1a\u5f88\u6709\u9a91\u58eb\u7cbe\u795e\u7684\u4e0d\u4f1a\u592a\u4e3a\u96be\u5979\uff0c\u8fd8\u6709\u53ef\u80fd\u5979\u4ee5\u4e3a\u6211\u8fd9\u4e2a\u4e61\u5df4\u4f6c\u672a\u5fc5\u77e5\u9053\u9999\u6e2f\u4eba\u73a9\u5f97\u591a\u75af\u72c2\uff0c\u6bd5\u7adf\u4e24\u4e2a\u4eba\u559d\u9152\uff0c\u53c8\u4e0d\u662f\u4e00\u7fa4\u4eba\u75af\u73a9\u3002\u6211\u5c31\u6bcf\u559d\u5b8c\u559d\u4e00\u676f\uff0c\u5c31\u5199\u4e0b\u2018\u6211\u505a\u5565\u4f60\u505a\u5565\u2019\u8fd9\u53e5\u8bdd\uff0c\u4f60\u4eec\u77e5\u4e0d\u77e5\u9053\uff0c\u51fa\u5356\u8272\u76f8\u4e5e\u8ba8\u662f\u5979\u5199\u7684\u90a3\u4e48\u591a\u6761\u4e2d\u6700\u4e0d\u51fa\u683c\u7684\u4e00\u6761\u2026\u2026\u201d</p><p>\u3000\u3000\u5927\u5bb6\u542c\u4e86\u90fd\u54c8\u54c8\u5927\u7b11\uff0c\u8bb8\u601d\u7b11\u5f97\u6342\u7740\u809a\u5b50\uff0c\u5fcd\u4e0d\u4f4f\u597d\u5947\u7684\u95ee\u5f20\u606a\uff1a\u201c\u90a3\u9759\u6aac\u5176\u4ed6\u8fd8\u5199\u4e86\u4ec0\u4e48\u4e8b\u60c5\u8ba9\u4f60\u505a\uff1f\u201d</p><p>\u3000\u3000\u5b59\u9759\u6aac\u94f6\u7259\u54ac\u7259\u7740\u5ae3\u7ea2\u7684\u5634\u5507\uff0c\u77aa\u773c\u770b\u7740\u5f20\u606a\uff0c\u5a01\u80c1\u4ed6\u8bf4\uff1a\u201c\u4f60\u6562\u8bf4\uff01\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e3e\u624b\u6447\u6447\u5934\uff1a\u201c\u6211\u4e0d\u8bf4\uff0c\u6211\u73b0\u5728\u4e0d\u8bf4\uff0c\u53cd\u6b63\u4f60\u4e5f\u6ca1\u6709\u9009\u62e9\u505a\uff0c\u518d\u8bf4\u4f60\u521d\u8877\u4e5f\u662f\u8981\u9677\u5bb3\u6211\u800c\u5df2\uff0c\u4f60\u6015\u4ec0\u4e48\u4e22\u8138\u7684\uff1f\u201d</p><p>\u3000\u3000\u51fa\u4e86\u7535\u68af\uff0c\u53f6\u5efa\u658c\u6253\u7535\u8bdd\u8fc7\u6765\uff0c\u4ed6\u4e0e\u5b59\u9759\u9999\u5df2\u7ecf\u4e0b\u4e86\u98de\u673a\uff0c\u5f20\u606a\u81ea\u7136\u5c06\u5f97\u610f\u6d0b\u6d0b\u7684\u5c06\u5b59\u9759\u6aac\u4e0b\u5348\u5728\u5e7f\u573a\u4e0a\u5f53\u821e\u5973\u7684\u4e8b\u60c5\u5927\u8086\u5ba3\u626c\u7684\u4e00\u756a\uff0c\u542c\u7740\u624b\u673a\u91cc\u53f6\u5efa\u658c\u6291\u4e0d\u4f4f\u7684\u7206\u7b11\u58f0\uff0c\u5b59\u9759\u6aac\u8981\u8fc7\u6765\u62a2\u5f20\u606a\u7684\u624b\u673a\uff0c\u53ef\u60dc\u6ca1\u6709\u4eba\u642d\u7406\u5979\u3002</p><p>\u3000\u3000\u7528\u8fc7\u665a\u9910\uff0c\u5f20\u606a\u5728\u5b59\u5c1a\u4e49\u5c4b\u91cc\u8c08\u4e8b\u60c5\uff0c\u7b49\u665a\u4e0a\u5b59\u5c1a\u4e49\u7684\u59bb\u5b50\u845b\u660e\u73cf\u56de\u6765\uff0c\u5b59\u5c1a\u4e49\u53c8\u5c06\u5b59\u9759\u6aac\u62ff\u51fa\u6765\u597d\u597d\u53d6\u7b11\u4e86\u4e00\u756a\uff0c\u845b\u660e\u73cf\u6491\u7740\u809a\u5b50\u7b11\u4e86\u534a\u664c\uff0c\u624d\u611f\u6168\u8bf4\u9053\uff1a\u201c\u8981\u662f\u5979\u4f6c\u7237\u5728\u4e16\uff0c\u77e5\u9053\u8fd9\u4e8b\u4e00\u5b9a\u4f1a\u5927\u53d1\u96f7\u9706\u7684\u3002\u201d</p><p>\u3000\u3000\u845b\u666f\u8bda\u7684\u53bb\u4e16\uff0c\u5927\u6982\u6240\u6709\u4eba\u90fd\u6709\u5fc3\u5934\u7ed9\u63ed\u53bb\u4e00\u5c42\u9634\u5f71\u7684\u611f\u89c9\u3002</p><p>\u3000\u3000\u8bb8\u601d\u6572\u95e8\u8fdb\u6765\u8bf4\uff1a\u201c\u660e\u73e0\u53f0\u6b63\u5728\u64ad\u65b0\u95fb\uff0c\u6709\u8bf4\u5230\u4e0b\u5348\u7684\u4e8b\u60c5\u2026\u2026\u201d</p><p>\u3000\u3000\u5b59\u9759\u6aac\u62a2\u8fc7\u9065\u63a7\u5668\uff0c\u56b7\u7740\u4e0d\u8bb8\u770b\uff0c\u845b\u660e\u73cf\u5374\u594b\u529b\u4ece\u5b59\u9759\u6aac\u7684\u6495\u626f\u4e2d\u6323\u624e\u7740\u8fc7\u53bb\u5c06\u7535\u89c6\u6253\u5f00\uff0c\u4e00\u5b9a\u8981\u770b\u770b\u4e0b\u5348\u5230\u5e95\u662f\u600e\u6837\u7684\u76db\u51b5\uff0c\u53ef\u60dc\u65b0\u95fb\u5df2\u7ecf\u64ad\u8fc7\u53bb\uff0c\u95ee\u8bb8\u601d\uff1a\u201c\u65b0\u95fb\u8bf4\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u8fd8\u8bb0\u5f97\u64ad\u62a5\u65f6\u7684\u65b0\u95fb\u6807\u9898\uff0c\u201d\u8bb8\u601d\u7b11\u7740\u8bf4\uff0c\u201c\u2018\u5f71\u6e7e\u56ed\u524d\uff0c\u5bcc\u5bb6\u5973\u4e3a\u52a9\u6b8b\u57fa\u91d1\u732e\u8eab\u884c\u4e3a\u827a\u672f\u2019\uff0c\u8bf4\u4e0d\u5b9a\u660e\u5929\u7684\u5468\u520a\u4f1a\u6709\u62a5\u9053\uff01\u201d</p><p>\u3000\u3000\u5f20\u606a\u6307\u7740\u81ea\u5df1\u7684\u9f3b\u5934\u95ee\uff1a\u201c\u6709\u63d0\u5230\u6211\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e0b\u5348\u4f60\u6839\u672c\u5c31\u662f\u4e00\u4e2a\u9053\u5177\u554a\uff0c\u65b0\u95fb\u91cc\u4f1a\u4ecb\u7ecd\u9053\u5177\u5417\uff1f\u201d\u8bb8\u601d\u8ba5\u7b11\u4ed6\u8bf4\u3002</p><p>\u3000\u3000\u60f3\u60f3\u4e5f\u662f\uff0c\u5c31\u7b97\u6700\u540e\u5171\u821e\u65f6\uff0c\u81ea\u5df1\u4e5f\u662f\u57fa\u672c\u7ad9\u5728\u90a3\u91cc\u4e0d\u52a8\uff0c\u5b59\u9759\u6aac\u90a3\u4ee4\u4eba\u60ca\u8273\u7684\u821e\u59ff\u624d\u771f\u6b63\u7684\u5f81\u670d\u522b\u4eba\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u5374\u662f\u53c8\u7f9e\u53c8\u6124\uff0c\u8fc7\u6765\u6293\u8fc7\u5f20\u606a\u7684\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u518d\u8fc7\u53bb\u559d\u9152\uff01\u201d</p><p>\u3000\u3000\u201c\u8fd8\u73a9\u771f\u5fc3\u8bdd\u5927\u5192\u9669\uff1f\u201d\u5f20\u606a\u62ac\u5934\u7b11\u7740\u95ee\u3002</p><p>\u3000\u3000\u201c\u5bb3\u6211\u5f53\u4e86\u4e00\u4e0b\u5348\u7684\u821e\u5973\uff0c\u966a\u6211\u8fd8\u6709\u8bb8\u601d\u559d\u9152\u603b\u53ef\u4ee5\u5427\u3002\u201d\u5b59\u9759\u6aac\u8fde\u62d6\u5e26\u62fd\u7684\u8981\u5c06\u5f20\u606a\u62d6\u8d77\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u65e0\u5948\uff0c\u9080\u5b59\u5c1a\u4e49\u592b\u5987\u4e00\u8d77\u8fc7\u53bb\u559d\u9152\uff0c\u5b59\u5c1a\u4e49\u6447\u6447\u5934\u8bf4\uff1a\u201c\u4f60\u4eec\u5e74\u8f7b\u4eba\u5728\u4e00\u8d77\u5c31\u53ef\u4ee5\u4e86\uff0c\u6211\u8fd8\u662f\u4e60\u60ef\u5341\u70b9\u949f\u51c6\u65f6\u7761\u89c9\u7684\u3002\u201d</p><p>\u3000\u3000\u5b59\u9759\u6aac\u8f6c\u8eab\u5230\u4e66\u623f\u9152\u67dc\u62ff\u51fa\u4e24\u74f6\u5e72\u9091\uff0c\u8ddf\u5979\u7238\u5b59\u5c1a\u4e49\u8bf4\uff1a\u201c\u4e24\u74f6\u9152\u501f\u6211\u4eec\u2026\u2026\u201d</p><p>\u3000\u3000\u542c\u5230\u5b59\u5c1a\u4e49\u5fc3\u75bc\u7684\u5438\u6c14\u58f0\uff0c\u5f20\u606a\u8f6c\u5934\u770b\u4e86\u770b\u9152\u6807\uff0c\u4e0d\u662f\u5bfb\u5e38\u7684\u8f69\u5c3c\u8bd7\u3001\u8def\u6613\u3001\u9a6c\u7239\u5229\u4e4b\u7c7b\u7684\u540d\u9152\uff0c\u4ed6\u4e5f\u7422\u78e8\u4e0d\u900f\u9152\u6cd5\u4e0a\u7684\u6cd5\u6587\u9152\u540d\u662f\u4ec0\u4e48\u610f\u601d\uff0c\u9152\u6807\u4e0a\u6709X\xb7O\u6807\u5fd7\uff0c\u90a3\u5c31\u8868\u660e\u662f\u8fd9\u4e2a\u9152\u5e84\u6240\u4ea7\u7684\u7279\u7ea7\u9152\uff0c\u5b59\u5c1a\u4e49\u642c\u51fa\u4e4b\u524d\u4f4f\u7684\u5b85\u5b50\uff0c\u5bb6\u5177\u4e4b\u7c7b\u7684\u4e1c\u897f\u5e76\u6ca1\u6709\u5168\u90e8\u642c\u51fa\uff0c\u642c\u51fa\u6765\u53ea\u662f\u505a\u51fa\u4e00\u4e2a\u59ff\u6001\u800c\u5df2\uff1b\u4e0d\u8fc7\u4e66\u7c4d\u4e0e\u9152\uff0c\u4ed6\u90fd\u8bf7\u4e13\u4e1a\u7684\u516c\u53f8\u642c\u5230\u8fd9\u95f4\u516c\u5bd3\u6765\uff0c\u8fd9\u4e24\u74f6\u9152\u4f30\u8ba1\u662f\u5b59\u5c1a\u4e49\u7684\u591a\u5e74\u73cd\u85cf\u3002</p><p>\u3000\u3000\u6709\u8fd9\u79cd\u9152\u53ef\u5c1d\uff0c\u5f20\u606a\u5012\u4e0d\u4ecb\u610f\u7ed9\u5b59\u9759\u6aac\u704c\u9189\uff0c\u5b59\u5c1a\u4e49\u53c8\u72b9\u8c6b\u8d77\u6765\uff0c\u8fd9\u4e24\u74f6\u4ed6\u90fd\u8fd8\u6253\u7b97\u7559\u5230\u67d0\u4e2a\u7279\u6b8a\u7eaa\u5ff5\u65e5\u6253\u5f00\u6765\u5c1d\u4e00\u5c1d\uff0c\u4e5f\u8981\u7ad9\u8d77\u6765\u8ddf\u7740\u53bb\u559d\u9152\uff0c\u5b59\u9759\u6aac\u5374\u5b8c\u5168\u6ca1\u6709\u53ef\u601c\u4ed6\u7684\u610f\u601d\uff1a\u201c\u7238\uff0c\u4f60\u4e0d\u8981\u6765\u4e86\uff0c\u4f60\u8fd9\u4e2a\u5927\u9152\u9b3c\uff0c\u4f60\u4e00\u8fc7\u6765\uff0c\u6211\u4eec\u8fd8\u80fd\u559d\u591a\u5c11\u3002\u201d</p><p>\u3000\u3000\u5b59\u5c1a\u4e49\u5c31\u8fd9\u6837\u53ef\u601c\u5df4\u5df4\u7684\u770b\u7740\u5f20\u606a\u3001\u8bb8\u601d\u8fd8\u6709\u4ed6\u5973\u513f\u51fa\u4e86\u623f\u95f4\u3002</p><p>\u3000\u3000\u8bb8\u601d\u8fd9\u95f4\u516c\u5bd3\u53ea\u6709\u5c0f\u5385\uff0c\u5367\u5ba4\u5f88\u5bbd\u655e\uff0c\u843d\u5730\u7a97\u53f0\uff0c\u7a97\u53f0\u4e00\u89d2\u653e\u7740\u85e4\u7f16\u77ee\u51e0\u4e0e\u6728\u96d5\u8499\u9e8b\u76ae\u7684\u5c0f\u5706\u5f27\u6905\uff0c\u5750\u5728\u90a3\u91cc\u80fd\u591f\u770b\u89c1\u6708\u5149\u4e0b\u7684\u5927\u6d77\u4e0e\u5cb8\u8fb9\u7684\u6930\u6811\u6797\u3002\u5728\u8bb8\u601d\u5367\u5ba4\u91cc\u559d\u9152\uff0c\u6c1b\u56f4\u6bd4\u5230\u9876\u697c\u7684\u9152\u5427\u8fd8\u8981\u6e29\u99a8\u3002</p><p>\u3000\u3000\u62e7\u5f00\u6175\u61d2\u9761\u9761\u7684\u97f3\u4e50\uff0c\u623f\u95f4\u91cc\u706f\u5173\u4e0a\uff0c\u6708\u5149\u6d12\u8fdb\u6765\uff0c\u660e\u4eae\u5982\u65e7\uff0c\u4eba\u5374\u5982\u6d78\u5728\u6d77\u6c34\u91cc\u3002</p><p>\u3000\u3000\u5f20\u606a\u62c4\u7740\u62d0\u6756\u62ff\u6765\u6c34\u6676\u9152\u676f\u4e0e\u5f00\u74f6\u5668\uff0c\u5206\u522b\u5012\u6ee1\u4e09\u4e2a\u9152\u676f\uff0c\u4e09\u4eba\u56f4\u7740\u85e4\u7f16\u77ee\u51e0\u800c\u5750\u3002</p><p>\u3000\u3000\u201c\u8981\u662f\u59e5\u7237\u5728\uff0c\u53d1\u751f\u4eca\u5929\u4e0b\u5348\u8fd9\u6837\u7684\u4e8b\u60c5\uff0c\u56de\u53bb\u94c1\u5b9a\u8981\u4f1a\u7ed9\u5927\u8bad\u4e00\u987f\uff0c\u6709\u4e9b\u5373\u4f7f\u60f3\u53db\u9006\u4e00\u4e9b\uff0c\u5fc3\u91cc\u8fd8\u662f\u6709\u4e9b\u9634\u5f71\u7684\u2026\u2026\u201d\u5b59\u9759\u6aac\u611f\u6982\u4e86\u4e00\u53e5\uff0c\u63a5\u7740\u8138\u8272\u5c31\u53d8\u4e86\uff0c\u6076\u72e0\u72e0\u7684\u76ef\u7740\u5f20\u606a\uff0c\u201c\u4e0d\u4ee5\u4e3a\u8fd9\u4ef6\u4e8b\u8fd9\u4e48\u5c31\u7b97\u4e86\uff01\u201d</p><p>\u3000\u3000\u5f20\u606a\u5408\u638c\u62dc\u62dc\uff0c\u5b09\u76ae\u7b11\u8138\u7684\u8bf4\u9053\uff1a\u201c\u613f\u8d4c\u670d\u8f93\uff0c\u4f60\u8981\u8bb0\u5f97\uff0c\u6211\u4eca\u5929\u52df\u96c6\u5230\u7684\u5584\u6b3e\u53ef\u6bd4\u4f60\u8981\u591a\u554a\uff0c\u4f60\u8981\u8bb0\u4f4f\uff0c\u6211\u8fd8\u53ef\u4ee5\u8981\u6c42\u4f60\u505a\u4e00\u4ef6\u4e8b\u3002\u201d</p><p>\u3000\u3000\u201c\u90a3\u4e0d\u516c\u5e73\uff0c\u6211\u8d62\u8fc7\u4f60\u53c8\u6ca1\u6709\u4ec0\u4e48\u6761\u4ef6\u3002\u201d</p><p>\u3000\u3000\u201c\u6ca1\u4ec0\u4e48\u516c\u5e73\u4e0d\u516c\u5e73\u7684\uff0c\u8c01\u8ba9\u4f60\u90a3\u4e48\u81ea\u4fe1\u7684\uff0c\u8ba4\u5b9a\u81ea\u5df1\u4e0d\u53ef\u80fd\u8f93\u7ed9\u6211\uff1f\u201d\u5f20\u606a\u60a0\u7136\u81ea\u5f97\u7684\u4e3e\u7740\u9152\u676f\uff0c\u201c\u4f60\u786c\u662f\u8981\u8d56\u6389\uff0c\u90a3\u6211\u4e5f\u6ca1\u529e\u6cd5\u2026\u2026\u201d</p><p>\u3000\u3000\u5b59\u9759\u6aac\u54ac\u7740\u5634\u5507\uff0c\u8ddf\u5f20\u606a\u7ea0\u7f20\u7740\u8fd9\u4e2a\u8bdd\u9898\uff0c\u4e00\u8f88\u5b50\u90fd\u627e\u4e0d\u56de\u8fd9\u4e2a\u8fc7\u7ed3\uff0c\u4ed6\u4ee5\u540e\u53ef\u4ee5\u540d\u6b63\u8a00\u987a\u7684\u8d56\u6389\u554a\u3002\u5979\u624b\u81c2\u6491\u5728\u77ed\u51e0\u4e0a\uff0c\u8eab\u5b50\u524d\u503e\uff0c\u5979\u8fd8\u7a7f\u7740\u90a3\u4ef6\u9ed1\u8272\u7684\u4f4e\u80f8\u665a\u88c5\uff0c\u633a\u7acb\u5728\u80f8\u524d\u7684\u90a3\u5bf9\u98a4\u60a0\u60a0\u7684\u4e73\u5cf0\u968f\u7740\u8eab\u5b50\u524d\u503e\u9732\u51fa\u66f4\u591a\u7684\u767d\u817b\uff0c\u53cc\u5cf0\u95f4\u662f\u5e7d\u6df1\u7729\u76ee\u7684\u4e73\u5cf0\uff1a\u201c\u4e0b\u5348\u90a3\u5973\u5b69\u5b50\u5230\u5e95\u662f\u8c01\uff0c\u770b\u4e0d\u51fa\u4f60\u5c0f\u5b50\u5f88\u8ba8\u5973\u5b69\u5b50\u559c\u6b22\u554a\uff0c\u4eba\u5bb6\u53ea\u4e3a\u4e86\u80fd\u8ddf\u4f60\u804a\u5929\u5c31\u638f\u51fa\u5341\u4e8c\u4e07\u6e2f\u5143\u5462\u3002\u201d</p><p>\u3000\u3000\u201c\u90fd\u8bf4\u4e86\u5f88\u591a\u904d\u4e86\uff0c\u5c31\u662f\u4e0a\u56de\u5230\u9999\u6e2f\u6765\uff0c\u98de\u673a\u7684\u90bb\u5ea7\u800c\u5df2\uff0c\u8bb8\u601d\u59d0\u4e5f\u8ba4\u8bc6\u2026\u2026\u201d</p><p>\u3000\u3000\u8bb8\u601d\u7a7f\u7740\u8584\u7f8a\u7ed2\u886b\uff0c\u80f8\u3001\u8170\u7684\u66f2\u7ebf\u7ed9\u52fe\u52d2\u5f97\u6027\u611f\u65e0\u6bd4\uff0c\u5979\u5ae3\u7136\u7b11\u7740\uff0c\u5c06\u5f20\u606a\u5728\u98de\u673a\u4e0a\u5403\u96ea\u7cd5\u6076\u5fc3\u90a3\u5973\u5b69\u5b50\u7684\u4e8b\u60c5\u8bf4\u7ed9\u5b59\u9759\u6aac\u542c\u3002</p><p>\u3000\u3000\u201c\u8fd9\u4e5f\u884c\uff01\u201d\u5b59\u9759\u6aac\u8be7\u5f02\u7684\u8bf4\uff0c\u6273\u7740\u8bb8\u601d\u7684\u80a9\u8180\u7b11\u7740\u8bf4\uff0c\u201c\u5b8c\u86cb\u4e86\uff0c\u8fd9\u6837\u90fd\u80fd\u884c\uff0c\u6211\u770b\u5f20\u606a\u8fd9\u5bb6\u4f19\u4ee5\u540e\u4e00\u5b9a\u662f\u4e2a\u4f1a\u8ba9\u5f88\u591a\u5973\u5b69\u5b50\u65e2\u6068\u53c8\u7231\u7684\u82b1\u82b1\u516c\u5b50\uff0c\u201d\u62ff\u8d64\u7740\u7684\u7389\u5ae9\u9c9c\u8db3\u53bb\u8e22\u5f20\u606a\u7684\u5c0f\u817f\uff0c\u51b0\u51c9\u7684\u811a\u8dbe\u8e6d\u5f97\u5f20\u606a\u5c0f\u817f\u75d2\u75d2\u7684\uff0c\u201c\u8bf4\u4e0d\u5b9a\u90a3\u5973\u5b69\u5b50\u4f1a\u4e3b\u52a8\u8fc7\u6765\u5e2e\u4f60\u8865\u4e60\u6cd5\u6587\u5462\uff0c\u4f60\u8981\u4e3b\u52a8\u4e00\u70b9\u554a\uff0c\u770b\u90a3\u5973\u5b69\u5b50\u51fa\u624b\uff0c\u5bb6\u91cc\u5c11\u8bf4\u4e5f\u8981\u6709\u5341\u51e0\u4e8c\u5341\u4ebf\u7684\u8d44\u4ea7\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6211\u9700\u8981\u727a\u7272\u8272\u76f8\u5403\u996d\uff1f\u201d\u5f20\u606a\u4e0d\u5c51\u7684\u8bf4\uff0c\u62bd\u624b\u5c06\u5b59\u9759\u6aac\u4e0d\u5b89\u5206\u7684\u767d\u5ae9\u7389\u8db3\u6253\u5f00\u3002</p><p>\u3000\u3000\u8bb8\u601d\u7b11\u7740\u4e0d\u505c\uff0c\u5f20\u606a\u8eab\u4e0a\u6240\u8868\u73b0\u51fa\u6765\u7684\u6210\u719f\u4e0e\u5929\u771f\u7684\u6c14\u606f\u6d51\u7136\u5929\u6210\uff0c\u8d22\u5bcc\u4e0e\u6743\u52bf\u65e2\u6ca1\u6709\u8ba9\u4ed6\u7684\u6027\u683c\u98de\u626c\u8dcb\u6248\u4e00\u5206\uff0c\u4e5f\u6ca1\u6709\u8ba9\u4ed6\u7684\u6027\u683c\u66f4\u4e16\u4fd7\u9634\u6697\uff0c\u5982\u6b64\u7684\u6d12\u8131\u81ea\u7136\uff0c\u5373\u4f7f\u505a\u4e0d\u6210\u604b\u4eba\uff0c\u8ddf\u4ed6\u5728\u4e00\u8d77\uff0c\u4e5f\u662f\u4e00\u4e2a\u8ba9\u4eba\u611f\u5230\u975e\u5e38\u8212\u670d\u7684\u670b\u53cb\uff0c\u8fd9\u5927\u6982\u662f\u4ed6\u6700\u5177\u9b45\u529b\u7684\u5730\u65b9\u5427\u3002</p><p>\u3000\u3000\u4e09\u4e2a\u4eba\u5750\u5728\u6708\u5149\u4e0b\u8fb9\u559d\u8fb9\u804a\uff0c\u4e3b\u8981\u662f\u542c\u5b59\u9759\u6aac\u503e\u8bc9\u5979\u5728\u845b\u5bb6\u5927\u5b85\u6240\u627f\u53d7\u7684\u5fc3\u7406\u9634\u5f71\uff0c\u4e5f\u96be\u602a\u5979\u7684\u6027\u683c\u4f1a\u50cf\u706b\u7130\u4e00\u6837\u7684\u5982\u6b64\u70ed\u70c8\uff0c\u5979\u8981\u662f\u4e0d\u8fd9\u4e48\u71c3\u70e7\u7740\uff0c\u5728\u8fd9\u5c42\u51b0\u51b7\u7684\u9634\u5f71\u4e0b\uff0c\u4e5f\u53ea\u80fd\u7070\u5bc2\u65e0\u5149\u7684\u751f\u5b58\u7740\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u5230\u6700\u540e\u5c31\u559d\u9189\u4e86\uff0c\u5979\u4e00\u4eba\u5c31\u5dee\u4e0d\u591a\u559d\u4e86\u4e00\u74f6\u5e72\u9091\uff0c\u8eba\u5728\u8bb8\u601d\u7684\u6000\u91cc\u9189\u610f\u6726\u80e7\uff0c\u8fd8\u8dd1\u56de\u53bb\u5c06\u5979\u4ece\u5c0f\u8ddf\u5979\u59d0\u7684\u5f71\u96c6\u62ff\u8fc7\u6765\u7ffb\u7ed9\u5f20\u606a\u3001\u8bb8\u601d\u770b\uff0c\u4e00\u4f1a\u513f\u5c31\u7761\u5dee\u5728\u8bb8\u601d\u7684\u819d\u76d6\u4e0a\u3002\u5f20\u606a\u8ddf\u8bb8\u601d\u5408\u529b\u5c06\u762b\u5012\u7684\u5b59\u9759\u6aac\u4e22\u5e8a\u4e0a\u53bb\uff0c\u5b59\u9759\u6aac\u660f\u7761\u5982\u6ce5\uff0c\u8fd8\u53d1\u51fa\u7ec6\u5fae\u7684\u547c\u58f0\u3002\u5f20\u606a\u65e0\u5948\u7684\u7b11\u4e86\u7b11\uff0c\u518d\u4e0e\u8bb8\u601d\u5728\u6708\u4e0b\u5c0f\u914c\uff0c\u4e5f\u4e0d\u62c5\u5fc3\u5b59\u9759\u6aac\u4f1a\u9192\u6765\uff0c\u5c06\u8bb8\u601d\u6402\u5728\u6000\u91cc\uff0c\u8fd9\u65f6\u5019\u5728\u9999\u6e2f\u4e5f\u53ea\u6709\u5c06\u5b59\u9759\u6aac\u704c\u9189\u4e24\u4eba\u624d\u80fd\u6e29\u5b58\u7247\u523b\u3002\u8bb8\u601d\u9152\u91cf\u5f88\u5c0f\uff0c\u5f20\u606a\u4e5f\u5dee\u4e0d\u591a\u559d\u4e0b\u4e00\u74f6\uff0c\u5230\u6700\u540e\u5374\u662f\u4ed6\u8eba\u5728\u8bb8\u601d\u7684\u5927\u817f\u4e0a\uff0c\u4e00\u8d77\u770b\u7740\u7a97\u5916\u7684\u6708\u5149\u3002</p><p>\u3000\u3000\u660f\u6c89\u6c89\u4e0d\u77e5\u4f55\u65f6\u7761\u53bb\uff0c\u534a\u591c\u9192\u6765\uff0c\u5934\u6709\u4e9b\u75bc\uff0c\u4ed6\u5c06\u8bb8\u601d\u6402\u5728\u6000\u91cc\uff0c\u80cc\u540e\u8fd8\u538b\u7740\u4e00\u4e2a\u5a07\u8f6f\u7684\u8eab\u5b50\uff0c\u611f\u89c9\u5230\u5979\u7684\u4e73\u623f\u7d27\u8d34\u7740\u81ea\u5df1\u7684\u80cc\u810a\uff0c\u4e73\u623f\u5f88\u9971\u6ee1\uff0c\u90a3\u611f\u89e6\u5230\u7684\u5f39\u6027\u8ba9\u4eba\u5fc3\u60ca\u8089\u8df3\u3001\u9b42\u8361\u795e\u79fb\uff0c\u4e00\u53ea\u7389\u85d5\u4f3c\u7684\u767d\u5ae9\u624b\u81c2\u6401\u5728\u81ea\u5df1\u8170\u4e0a\uff0c\u9f3b\u7aef\u4f20\u6765\u4e24\u79cd\u622a\u7136\u4e0d\u540c\u7684\u5973\u4eba\u9999\uff0c\u8bb8\u601d\u7684\u6c14\u606f\u5e7d\u9759\u5982\u7a97\u5916\u7684\u6708\u5149\u7167\u5728\u5979\u5bc2\u9759\u7edd\u7f8e\u7684\u8138\u4e0a\uff0c\u5b59\u9759\u6aac\u7684\u6c14\u606f\u5374\u662f\u90a3\u6837\u7684\u70bd\u70c8\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e00\u76f4\u68a6\u60f3\u7740\u4ec0\u4e48\u65f6\u5019\u8ddf\u4e24\u4e2a\u7edd\u8272\u7684\u5973\u4eba\u540c\u5e8a\u5171\u6795\uff0c\u4e00\u4e0d\u5c0f\u5fc3\u5c31\u6210\u4e86\u3002</p></article>"}}}]);
