(window.webpackJsonp=window.webpackJsonp||[]).push([[476],{498:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u516b\u8282 \u65b0\u65c5\u7a0b",content:"<article><h2>\u7b2c\u516b\u8282 \u65b0\u65c5\u7a0b</h2><p>\u6885\u83f2\u65af\u8bf4\u8bdd\u7684\u8bed\u6c14\u6709\u70b9\u5947\u602a\uff0c\u4f46\u743c\u6069\u5e76\u6ca1\u6709\u592a\u5728\u610f\uff0c\u5206\u522b\u591a\u65f6\u3001\u4e00\u76f4\u6302\u5ff5\u7684\u60c5\u4eba\uff0c\u7a81\u7136\u6765\u4fe1\u8bf4\u5979\u60f3\u4f60\uff0c\u8fd9\u79cd\u5e78\u798f\u611f\u2014\u2014\u800c\u4e14\u8fd8\u662f\u53cc\u4efd\u7684\u2014\u2014\u8db3\u4ee5\u4ee4\u4efb\u4f55\u7537\u4eba\u9676\u9189\uff0c\u5ffd\u7565\u6389\u4e00\u5207\u5f02\u5e38\u3002</p><p>\u662f\u8be5\u53bb\u89c1\u5979\u4eec\u4e86\u3002</p><p>\u6b64\u6b21\u51fa\u884c\u6709\u4e09\u4e2a\u76ee\u7684\uff0c\u5176\u4e00\u662f\u516c\u52a1\uff0c\u5176\u4e8c\u662f\u627e\u90a3\u4f4d\u5f17\u6717\u897f\u65af\u79d1\uff0c\u6c42\u8bc1\u7236\u6bcd\u53bb\u4e16\u7684\u771f\u76f8\uff0c\u5176\u4e09\u5219\u662f\u548c\u6885\u83f2\u65af\u4e0e\u51db\u4f1a\u5408\u3002\u5982\u4eca\u516c\u52a1\u5df2\u7ecf\u641e\u5b9a\uff0c\u8fde\u6d6e\u7a7a\u57ce\u90fd\u5230\u624b\uff1b\u5f17\u6717\u897f\u65af\u79d1\u610f\u5916\u8eab\u6545\uff0c\u7ebf\u7d22\u6682\u65f6\u65ad\u4e86\uff0c\u53ea\u5269\u4e0b\u6700\u540e\u4e00\u6869\u4e8b\u6ca1\u505a\u3002\u6885\u83f2\u65af\u548c\u51db\u662f\u5728\u963f\u65af\u5361\u7279\u62c9\u4e0e\u743c\u6069\u5206\u5f00\u7684\uff0c\u7b97\u8d77\u6765\u5df2\u7ecf\u4e00\u4e2a\u591a\u6708\u6ca1\u89c1\u4e86\uff0c\u7740\u5b9e\u662f\u60f3\u5ff5\u5f97\u7d27\u3002</p><p>\u521a\u624d\u743c\u6069\u6b63\u8981\u53d1\u52a8\u4f20\u9001\uff0c\u88ab\u6885\u83f2\u65af\u7a81\u7136\u4f20\u8baf\u800c\u6253\u65ad\uff0c\u8fd9\u4e2a\u6cd5\u672f\u5c31\u7b97\u662f\u767d\u767d\u6d6a\u8d39\u6389\u4e86\uff0c\u5e78\u597d\u4ed6\u4e0d\u6b62\u51c6\u5907\u4e86\u4e00\u4e2a\u3002\u51e0\u79d2\u949f\u540e\uff0c\u4ed6\u56de\u5230\u73ca\u5609\u7684\u623f\u95f4\uff0c\u53d1\u73b0\u59d0\u59d0\u5df2\u7ecf\u7a7f\u8863\u8d77\u8eab\uff0c\u6b63\u5750\u5728\u6905\u4e2d\u4e13\u5fc3\u81f4\u5fd7\u5730\u8bfb\u4e66\u3002\u4ee4\u4ed6\u60ca\u8bb6\u7684\u662f\uff0c\u73ca\u5609\u8bfb\u7684\u90a3\u672c\u4e66\u4ed6\u5f88\u773c\u719f\uff0c\u5f88\u5927\uff0c\u6d45\u7eff\u8272\u91d1\u5c5e\u5c01\u9762\uff0c\u6ca1\u6709\u4e66\u540d\uff0c\u53ea\u753b\u7740\u4e00\u6735\u7389\u767d\u8272\u6800\u5b50\u82b1\uff0c\u6b63\u662f\u5965\u5609\u83b1\u65af\u90a3\u672c\u300a\u547d\u8fd0\u957f\u591c\u300b\uff0c\u636e\u5965\u6c83\u8bf4\u662f\u6b27\u8d1d\u4f26\u9001\u7684\u5b9a\u60c5\u4fe1\u7269\u3002</p><p>\u201c\u54a6\uff0c\u8fd9\u672c\u4e66\u4e0d\u662f\u5979\u7684\u5417\uff1f\u201d</p><p>\u201c\u662f\u554a\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u5979\u8ba9\u6211\u5148\u9605\u8bfb\uff0c\u6709\u4e0d\u61c2\u7684\u5730\u65b9\u518d\u5411\u5979\u8bf7\u6559\u3002\u201d</p><p>\u201c\u2026\u2026\u6709\u8fd9\u4e48\u6559\u5b66\u751f\u7684\u5417\uff0c\u592a\u4e0d\u8d1f\u8d23\u4e86\u5427\u3002\u201d</p><p>\u743c\u6069\u6709\u70b9\u607c\u706b\uff0c\u73ca\u5609\u5bf9\u9b54\u6cd5\u4e0d\u7b97\u662f\u5b8c\u5168\u7684\u5916\u884c\u2014\u2014\u4e8b\u5b9e\u4e0a\uff0c\u53ea\u8981\u662f\u9634\u9b42\u57ce\u7684\u4eba\uff0c\u54ea\u6015\u7956\u7956\u8f88\u8f88\u90fd\u662f\u5e73\u6c11\uff0c\u4e5f\u4e0d\u53ef\u80fd\u662f\u5b8c\u5168\u7684\u9b54\u6cd5\u5916\u884c\uff0c\u591a\u591a\u5c11\u5c11\u603b\u7565\u6709\u6240\u77e5\u2014\u2014\u4f46\u6bd5\u7adf\u4e0d\u662f\u50cf\u4ed6\u4e00\u6837\u63a5\u53d7\u8fc7\u6b63\u89c4\u7684\u5b66\u9662\u6559\u80b2\uff0c\u57fa\u7840\u662f\u975e\u5e38\u8584\u5f31\u7684\u3002\u5965\u5609\u83b1\u65af\u5bf9\u5979\u7528\u8fd9\u79cd\u201c\u81ea\u5df1\u5b66\u4e60\u3001\u8001\u5e08\u89e3\u7591\u201d\u7684\u6559\u5b66\u65b9\u6cd5\uff0c\u7b80\u76f4\u5c31\u662f\u8bef\u4eba\u5b50\u5f1f\u561b\u3002\u4ed6\u60f3\u4e86\u60f3\uff0c\u4ece\u73ca\u5609\u624b\u4e2d\u53d6\u8fc7\u4e66\uff0c\u7c97\u7565\u7ffb\u4e86\u7ffb\uff0c\u53d1\u73b0\u6bcf\u4e00\u9875\u4e0a\u90fd\u8bb0\u8f7d\u7740\u4e00\u4e2a\u6cd5\u672f\u7684\u8be6\u7ec6\u8d44\u6599\uff0c\u5305\u62ec\u539f\u7406\u3001\u5492\u6587\u3001\u65bd\u6cd5\u8981\u7d20\u3001\u6cd5\u672f\u6548\u679c\u7b49\u7b49\uff0c\u4e3b\u8981\u96c6\u4e2d\u5728\u9884\u8a00\u3001\u5e7b\u672f\u3001\u9644\u9b54\u8fd9\u4e09\u5b66\u6d3e\uff0c\u4e5f\u5373\u662f\u8010\u745f\u65f6\u4ee3\u7684\u201c\u5fc3\u7075\u7cfb\u201d\u8303\u7574\u3002</p><p>\u73b0\u5728\u5927\u9646\u901a\u884c\u7684\u9b54\u6cd5\u5b66\u662f\u4ece\u8010\u745f\u65f6\u4ee3\u6f14\u53d8\u800c\u6765\uff0c\u4f46\u53c8\u6709\u6240\u4e0d\u540c\uff0c\u5176\u4e2d\u4e00\u5927\u533a\u522b\u5c31\u662f\u5728\u9b54\u6cd5\u5b66\u6d3e\u7684\u5212\u5206\u4e0a\u3002\u73b0\u4eca\u7684\u9b54\u6cd5\u5b66\u88ab\u5212\u5206\u4e3a\u516b\u5927\u7cfb\uff1a\u5492\u6cd5\u3001\u53d8\u5316\u3001\u9632\u62a4\u3001\u5e7b\u672f\u3001\u9644\u9b54\u3001\u5851\u80fd\u3001\u4ea1\u7075\u548c\u9884\u8a00\uff0c\u800c\u5728\u8010\u745f\u65f6\u4ee3\u5e76\u6ca1\u6709\u8fd9\u4e48\u7cbe\u7ec6\uff0c\u53ea\u662f\u7c97\u7565\u5730\u5206\u4e3a\uff1a\u521b\u80fd\u3001\u8f6c\u5316\u548c\u5fc3\u7075\u4e09\u5927\u7cfb\uff0c\u5176\u4e2d\u5fc3\u7075\u7cfb\u5927\u81f4\u76f8\u5f53\u4e8e\u73b0\u5728\u5e7b\u672f\u3001\u9644\u9b54\u548c\u9884\u8a00\u4e09\u7cfb\u7684\u603b\u548c\u3002\u5965\u5609\u83b1\u65af\u662f\u5360\u661f\u5e08\uff0c\u6309\u7167\u73b0\u4ee3\u7684\u6807\u51c6\u5c5e\u4e8e\u9884\u8a00\u5e08\u7684\u4e00\u79cd\uff0c\u4f46\u5176\u5b9e\u5979\u771f\u6b63\u7684\u5934\u8854\u662f\u201c\u5fc3\u7075\u5e08\u201d\uff0c\u867d\u7136\u6700\u64c5\u957f\u7684\u662f\u9884\u8a00\u672f\uff0c\u4f46\u5e7b\u672f\u3001\u9644\u9b54\u672f\u4e5f\u5c5e\u4e8e\u5979\u7684\u4e13\u4e1a\u8303\u7574\uff0c\u540c\u6837\u662f\u5f88\u7cbe\u901a\u7684\u3002</p><p>\u73ca\u5609\u5728\u97f3\u4e50\u4e0a\u6709\u6781\u9ad8\u5929\u8d4b\uff0c\u88ab\u8ba4\u4e3a\u6709\u5e0c\u671b\u6210\u4e3a\u541f\u6e38\u8bd7\u4eba\u3002\u800c\u541f\u6e38\u8bd7\u4eba\u4e3b\u8981\u64c5\u957f\u7684\u4fbf\u662f\u4ee5\u97f3\u5f8b\u6765\u5851\u6210\u9b54\u6cd5\uff0c\u5f71\u54cd\u4ed6\u4eba\uff0c\u504f\u91cd\u4e8e\u5fc3\u7075\u7cfb\u7684\u8303\u7574\u3002\u5965\u5609\u83b1\u65af\u8ba9\u5979\u9605\u8bfb\u8fd9\u4e9b\uff0c\u4ece\u4e13\u4e1a\u65b9\u5411\u4e0a\u6765\u8bf4\u662f\u5012\u662f\u5f88\u6b63\u786e\u7684\uff0c\u4f46\u5bf9\u4e8e\u73ca\u5609\u8fd9\u79cd\u534a\u5916\u884c\uff0c\u4e0d\u5148\u6559\u57fa\u7840\u7406\u8bba\uff0c\u76f4\u63a5\u5c31\u5b66\u5177\u4f53\u7684\u6cd5\u672f\uff0c\u8fd9\u672a\u514d\u592a\u8de8\u8d8a\u5f0f\u4e86\u3002</p><p>\u201c\u59d0\u59d0\u90fd\u80fd\u770b\u61c2\u5417\uff1f\u201d\u4ed6\u5c06\u4e66\u8fd8\u7ed9\u73ca\u5609\u3002</p><p>\u201c\u8fd8\u884c\uff0c\u5927\u81f4\u90fd\u80fd\u770b\u61c2\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u6709\u4e9b\u5730\u65b9\u7b2c\u4e00\u773c\u770b\u4e0d\u660e\u767d\uff0c\u518d\u770b\u4e24\u904d\u5c31\u81ea\u7136\u800c\u7136\u5730\u61c2\u4e86\u3002\u201d</p><p>\u201c\u2026\u2026\u59d0\u59d0\u5929\u8d4b\u771f\u9ad8\u3002\u201d</p><p>\u73ca\u5609\u5fae\u5fae\u4e00\u7b11\uff0c\u7ee7\u7eed\u770b\u4e66\u3002\u743c\u6069\u72b9\u8c6b\u7247\u523b\uff0c\u201c\u59d0\u59d0\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6709\u4ef6\u4e8b\u60c5\u8981\u548c\u4f60\u5546\u91cf\u3002\u201d</p><p>\u201c\u55ef\uff1f\u201d</p><p>\u201c\u827e\u5f25\u8587\u521a\u521a\u4f20\u8baf\u8fc7\u6765\uff0c\u5979\u5df2\u7ecf\u5230\u4e86\u5854\u745f\u8c37\u3002\u201d</p><p>\u201c\u6240\u4ee5\u5462\uff1f\u201d\u73ca\u5609\u62ac\u8d77\u5934\uff0c\u7b11\u610f\u76c8\u76c8\u5730\u770b\u7740\u4ed6\u3002</p><p>\u201c\u6211\u60f3\u53bb\u89c1\u5979\u3002\u201d\u743c\u6069\u76f4\u622a\u4e86\u5f53\u5730\u8bf4\u3002</p><p>\u201c\u55ef\uff0c\u201d\u73ca\u5609\u70b9\u70b9\u5934\uff0c\u7b11\u5bb9\u4e0d\u51cf\uff0c\u201c\u597d\u554a\uff0c\u4e00\u8d77\u53bb\u5427\u3002\u59d0\u59d0\u4e5f\u5f88\u4e45\u6ca1\u89c1\u8fc7\u827e\u5f25\u8587\u4e86\uff0c\u5f88\u60f3\u5ff5\u5979\u5462\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u867d\u7136\u77e5\u9053\u73ca\u5609\u4e0d\u592a\u53ef\u80fd\u56e0\u6b64\u53d1\u6012\uff0c\u4f46\u5979\u6001\u5ea6\u5982\u6b64\u6e29\u548c\u751a\u81f3\u53ef\u4ee5\u8bf4\u662f\u79ef\u6781\uff0c\u4f9d\u65e7\u8fd8\u662f\u51fa\u4e4e\u743c\u6069\u7684\u610f\u6599\u4e4b\u5916\uff0c\u4ee5\u81f3\u4e8e\u8ba9\u4ed6\u90fd\u7a81\u7136\u6709\u79cd\u83ab\u540d\u7684\u6050\u60e7\u611f\uff0c\u770b\u7740\u73ca\u5609\u7684\u707f\u70c2\u7b11\u989c\uff0c\u80cc\u4e0a\u5374\u5bd2\u6c14\u55d6\u55d6\u76f4\u5192\u3002\u6709\u53e5\u8bdd\u600e\u4e48\u8bf4\u6765\u7740\u7684\uff0c\u7269\u4e4b\u53cd\u5e38\u5373\u4e3a\u5996\u2026\u2026</p><p>\u7b97\u4e86\uff0c\u53cd\u6b63\u603b\u6709\u8fd9\u4e00\u5929\u7684\u3002</p><p>\u201c\u4eca\u5929\u5df2\u7ecf\u5f88\u665a\u4e86\uff0c\u660e\u5929\u518d\u4f11\u606f\u4e00\u5929\uff0c\u540e\u5929\u52a8\u8eab\uff0c\u59d0\u59d0\u89c9\u5f97\u53ef\u4ee5\u5417\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u201c\u4f60\u5b89\u6392\u5c31\u662f\uff0c\u201d\u73ca\u5609\u7b11\u7b11\uff0c\u201c\u51fa\u95e8\u5728\u5916\u8fd9\u4e9b\u4e8b\u60c5\uff0c\u59d0\u59d0\u542c\u4f60\u7684\u2026\u2026\u54e6\uff0c\u5bf9\u4e86\uff0c\u6211\u5dee\u70b9\u5fd8\u4e86\uff0c\u5c0f\u5f1f\uff0c\u540e\u5929\u6050\u6015\u4e0d\u80fd\u52a8\u8eab\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u743c\u6069\u5947\u602a\u3002</p><p>\u201c\u8001\u5e08\u8bf4\uff0c\u5979\u8981\u5c06\u81ea\u5df1\u7684\u9b42\u5668\u4ece\u5bc6\u745f\u80fd\u6838\u79fb\u5230\u8fd9\u672c\u4e66\u4e0a\u6765\uff0c\u8fd9\u4e2a\u8fc7\u7a0b\u5927\u6982\u9700\u8981\u56db\u5230\u4e94\u5929\u3002\u201d</p><p>\u201c\u5979\u8981\u79fb\u9b42\uff1f\u201d</p><p>\u5965\u5609\u83b1\u65af\u5df2\u7ecf\u6b7b\u53bb\uff0c\u6b64\u65f6\u7684\u72b6\u6001\u662f\u201c\u5e7d\u7075\u201d\uff0c\u6216\u8005\u53eb\u201c\u9b3c\u9b42\u201d\uff0c\u662f\u4e00\u79cd\u5f88\u7279\u6b8a\u7684\u4ea1\u7075\u5f62\u6001\uff0c\u5b83\u6709\u7075\u89c9\uff0c\u6709\u795e\u667a\uff0c\u4fdd\u7559\u751f\u524d\u7684\u8bb0\u5fc6\uff0c\u4f46\u5374\u65e0\u6cd5\u81ea\u7531\u6d3b\u52a8\u3002\u56e0\u4e3a\u5e7d\u7075\u5b8c\u5168\u6ca1\u6709\u5b9e\u4f53\uff0c\u53ea\u662f\u88ab\u8d1f\u80fd\u91cf\u4fb5\u67d3\u7684\u7075\u9b42\uff0c\u5b83\u4eec\u82e5\u60f3\u5728\u8fd9\u7269\u8d28\u754c\u5177\u73b0\u7559\u9a7b\uff0c\u5c31\u5fc5\u987b\u6709\u4e00\u4e2a\u201c\u7acb\u8db3\u4e4b\u5730\u201d\u2014\u2014\u8fd9\u4e2a\u7acb\u8db3\u4e4b\u5730\uff0c\u5728\u9b54\u6cd5\u5b66\u4e0a\u79f0\u4e4b\u4e3a\u201c\u9b42\u5668\u201d\uff0c\u53ef\u80fd\u662f\u67d0\u4ef6\u5668\u7269\uff0c\u4e5f\u6709\u53ef\u80fd\u662f\u67d0\u5757\u7ecf\u8fc7\u9b54\u6cd5\u5904\u7406\u7684\u5730\u57df\uff0c\u751a\u81f3\u6709\u53ef\u80fd\u662f\u67d0\u4e2a\u751f\u7269\uff08\u8fd9\u79cd\u60c5\u51b5\u6781\u5176\u7f55\u89c1\uff09\u3002\u9b42\u5668\u65e2\u662f\u4f9d\u6258\u548c\u51ed\u501f\uff0c\u540c\u65f6\u4e5f\u662f\u675f\u7f1a\u548c\u9650\u5236\uff0c\u50cf\u743c\u6069\u4ee5\u524d\u5728\u70db\u5821\u78b0\u5230\u7684\u94f6\u9f99\u5e7d\u7075\uff0c\u4fbf\u662f\u88ab\u675f\u7f1a\u5728\u90a3\u4e2a\u5730\u4e0b\u5893\u7a74\uff0c\u65e0\u6cd5\u79bb\u5f00\u3002</p><p>\u901a\u5e38\u60c5\u51b5\u4e0b\uff0c\u5e7d\u7075\u4e00\u65e6\u9009\u62e9\uff08\u6216\u8005\u88ab\u8feb\u9009\u62e9\uff09\u9b42\u5668\u4e4b\u540e\uff0c\u4fbf\u6c38\u751f\u76f8\u4f34\uff0c\u65e0\u6cd5\u5206\u79bb\u3002\u4f46\u67d0\u4e9b\u5f3a\u5927\u7684\u5e7d\u7075\uff0c\u636e\u8bf4\u80fd\u591f\u5c06\u81ea\u5df1\u4ece\u4e00\u4e2a\u9b42\u5668\u79fb\u52a8\u5230\u53e6\u5916\u4e00\u4e2a\u9b42\u5668\u4e0a\uff0c\u8fd9\u5c31\u662f\u6240\u8c13\u7684\u201c\u79fb\u9b42\u201d\u3002\u636e\u73ca\u5609\u8bf4\uff0c\u5965\u5609\u83b1\u65af\u53d8\u6210\u5e7d\u7075\u540e\uff0c\u539f\u672c\u662f\u7528\u5bc6\u745f\u80fd\u6838\u4f5c\u4e3a\u9b42\u5668\uff0c\u73b0\u5728\u5219\u662f\u6253\u7b97\u5c06\u9b42\u5668\u53d8\u66f4\u4e3a\u5979\u624b\u4e0a\u8fd9\u672c\u300a\u547d\u8fd0\u957f\u591c\u300b\uff0c\u8fd9\u81ea\u7136\u9700\u8981\u82b1\u8d39\u4e00\u6bb5\u65f6\u95f4\u4e86\u3002</p><p>\u201c\u660e\u767d\u4e86\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u90a3\u5c31\u7b49\u5979\u79fb\u9b42\u5b8c\u6210\u4e86\u518d\u52a8\u8eab\u5427\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>1374DR\u51b0\u722a\u4e4b\u6708\uff08\u8d39\u4f26\u7684\u4e8c\u6708\uff09\u7b2c\u4e09\u65e5\uff0c\u9634\u9b42\u57ce\u70bc\u91d1\u5b66\u9662\u7684\u4e24\u4f4d\u9662\u957f\u4e4b\u4e00\uff0c\u9634\u9b42\u738b\u5b50\u739b\u63d0\u514b\xb7\u5766\u8212\u5c14\u62b5\u8fbe\u661f\u9668\u57ce\uff0c\u5c06\u5b83\u7684\u5bc6\u745f\u80fd\u6838\u4e0e\u5f71\u9b54\u7f51\u5b8c\u5168\u8054\u7ed3\uff0c\u9ad8\u9ad8\u98de\u884c\u5728\u6851\u6bd4\u4e9a\u4e0a\u7a7a\uff0c\u8ddd\u79bb\u5730\u9762\u4e09\u5343\u4e94\u767e\u82f1\u5c3a\uff0c\u6210\u4e3a\u5982\u4eca\u8d39\u4f26\u5927\u9646\u4e0a\u5df2\u77e5\u7684\u7b2c\u4e8c\u5ea7\u6d6e\u7a7a\u57ce\u3002\u4e03\u8272\u5149\u5e26\u4ea4\u9519\u7f16\u7ec7\u6210\u7684\u8ff7\u9501\u5168\u529b\u5f00\u542f\uff0c\u5728\u9633\u5149\u7167\u5c04\u4e0b\u53d1\u51fa\u70ab\u4e3d\u8679\u534e\uff0c\u4e0e\u84dd\u5929\u78a7\u6d77\u76f8\u4e89\u8f89\uff0c\u5411\u6574\u4e2a\u5927\u9646\u5ba3\u793a\u7740\u8010\u745f\u9057\u6c11\u7684\u590d\u56fd\u68a6\u60f3\u3002</p><p>\u540c\u4e00\u65f6\u95f4\uff0c\u743c\u6069\u4e00\u884c\u4eba\u884c\u9a76\u5728\u524d\u5f80\u5854\u745f\u8c37\u7684\u8def\u4e0a\u3002</p><p>\u81ea\u5760\u661f\u6d77\u4e0a\u6d8c\u6765\u7684\u5bd2\u6d41\u5e2d\u5377\u4e86\u5927\u534a\u4e2a\u6851\u6bd4\u4e9a\uff0c\u5929\u7a7a\u4e2d\u751a\u81f3\u98d8\u8d77\u4e86\u7ec6\u7ec6\u96ea\u82b1\u3002\u548c\u6765\u65f6\u4e00\u6837\uff0c\u4ed6\u4eec\u4e58\u5750\u7740\u5bbd\u655e\u7684\u56db\u8f6e\u9a6c\u8f66\uff0c\u800c\u4e14\u6bd4\u6765\u65f6\u4e58\u5750\u7684\u90a3\u8f86\u66f4\u52a0\u8c6a\u534e\u8212\u9002\uff0c\u8fd9\u662f\u5965\u5609\u83b1\u65af\u7684\u6536\u85cf\u54c1\u4e4b\u4e00\uff0c\u8f66\u53a2\u662f\u7528\u6c89\u9999\u6728\u5236\u6210\uff0c\u5916\u58c1\u9644\u7740\u5404\u79cd\u9632\u62a4\u9b54\u6cd5\uff0c\u5185\u58c1\u5219\u6709\u523b\u7740\u8c03\u8282\u6e29\u5ea6\u7684\u7b26\u7eb9\uff0c\u6240\u4ee5\u5916\u9762\u867d\u7136\u5bd2\u98ce\u547c\u5578\uff0c\u6ef4\u6c34\u6210\u51b0\uff0c\u8f66\u53a2\u5185\u5374\u662f\u6e29\u6696\u5982\u6625\u3002\u5730\u677f\u4e0a\u57ab\u7740\u539a\u539a\u7684\u6bdb\u6bef\uff0c\u743c\u6069\u56db\u80a2\u8212\u5c55\uff0c\u8eba\u5728\u4e00\u5806\u67d4\u8f6f\u7684\u6d77\u7ef5\u57ab\u4e2d\uff0c\u60ec\u610f\u5f97\u51e0\u4e4e\u60f3\u8981\u7f8e\u7f8e\u7761\u4e0a\u4e00\u89c9\u3002</p><p>\u201c\u59d0\u59d0\uff0c\u6211\u597d\u56f0\u3002\u201d</p><p>\u201c\u56f0\u4e86\u5c31\u7761\u5427\u3002\u201d\u5750\u5728\u65c1\u8fb9\u7684\u5c11\u5973\u6b63\u4e13\u5fc3\u81f4\u5fd7\u5730\u770b\u4e66\uff0c\u987a\u53e3\u56de\u7b54\u3002</p><p>\u201c\u53ef\u662f\u6795\u5934\u4e0d\u591f\u67d4\u8f6f\u3002\u201d</p><p>\u201c\u4e0d\u591f\u8f6f\uff1f\u201d</p><p>\u201c\u55ef\uff0c\u6240\u4ee5\u2026\u2026\u8ba9\u6211\u5728\u59d0\u59d0\u817f\u4e0a\u7761\u4f1a\u597d\u4e0d\u597d\u3002\u201d</p><p>\u201c\u4e0d\u597d\uff0c\u201d\u73ca\u5609\u677f\u7740\u8138\uff0c\u201c\u4e56\u4e56\u7761\uff0c\u4e0d\u8981\u6253\u6270\u59d0\u59d0\u3002\u201d</p><p>\u201c\u6211\u4fdd\u8bc1\u4e56\u4e56\u5730\u8eba\u7740\uff0c\u4e0d\u4f1a\u6253\u6270\u59d0\u59d0\u7684\uff0c\u201d\u743c\u6069\u722c\u8fc7\u6765\uff0c\u8dea\u5750\u5728\u73ca\u5609\u65c1\u8fb9\uff0c\u201c\u597d\u4e0d\u597d\u561b\u3002\u201d</p><p>\u73ca\u5609\u53f9\u4e86\u53e3\u6c14\uff0c\u201c\u597d\u5566\uff0c\u771f\u662f\u6015\u4e86\u4f60\uff0c\u6c38\u8fdc\u957f\u4e0d\u5927\u7684\u5c0f\u5b69\u5b50\u4f3c\u7684\u3002\u201d</p><p>\u5979\u5c06\u4e66\u653e\u5230\u4e00\u65c1\uff0c\u743c\u6069\u8d76\u5feb\u51d1\u8fc7\u6765\uff0c\u5c06\u6795\u5728\u59d0\u59d0\u7684\u5927\u817f\u4e0a\u3002\u201c\u59d0\u59d0\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u8033\u6735\u91cc\u75d2\u3002\u201d</p><p>\u201c\u77e5\u9053\u5566\uff0c\u7ed9\u4f60\u638f\u8033\u6735\u5c31\u662f\u3002\u201d</p><p>\u73ca\u5609\u4ece\u8170\u95f4\u94a5\u5319\u4e32\u4e0a\u53d6\u4e0b\u4e00\u4e2a\u7ec6\u957f\u7684\u91d1\u8272\u8033\u52fa\uff0c\u7528\u624b\u5e15\u8f7b\u8f7b\u64e6\u4e86\u64e6\uff0c\u4ed4\u7ec6\u7ed9\u743c\u6069\u6e05\u6d01\u8033\u5b54\u3002\u201c\u5c0f\u5f1f\uff0c\u8212\u670d\u5417\uff1f\u201d\u5979\u67d4\u58f0\u95ee\u3002</p><p>\u201c\u55ef\u3002\u201d\u743c\u6069\u8ff7\u8ff7\u7cca\u7cca\u56de\u7b54\uff0c\u5df2\u7ecf\u6709\u4e9b\u5026\u610f\u4e0a\u6d8c\uff0c\u59d0\u59d0\u7684\u4f53\u9999\uff0c\u603b\u662f\u90a3\u6837\u8ba9\u4eba\u5b89\u5fc3\u3002</p><p>\u201c\u56f0\u4e86\uff1f\u201d</p><p>\u201c\u55ef\uff0c\u6709\u70b9\u3002\u201d</p><p>\u201c\u6628\u665a\u592a\u7d2f\u4e86\u5427\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u548c\u5979\u4eec\u4e00\u5171\u51e0\u6b21\uff1f\u201d</p><p>\u201c\u4e0d\u8bb0\u5f97\u4e86\uff0c\u516d\u4e03\u6b21\u5427\u2014\u2014\u5443\uff0c\u201d\u743c\u6069\u8bdd\u5df2\u51fa\u53e3\u624d\u53cd\u5e94\u8fc7\u6765\uff0c\u201c\u59d0\u59d0\u2026\u2026\u201d</p><p>\u201c\u522b\u4e71\u52a8\uff0c\u5f88\u5371\u9669\u7684\uff0c\u5c0f\u5fc3\u8033\u819c\u88ab\u5f04\u7834\uff0c\u201d\u73ca\u5609\u62cd\u4e86\u62cd\u4ed6\u7684\u5934\uff0c\u201c\u4f60\u7d27\u5f20\u4ec0\u4e48\uff0c\u59d0\u59d0\u53ea\u662f\u95ee\u95ee\u800c\u5df2\uff0c\u53c8\u6ca1\u627e\u4f60\u7b97\u8d26\u3002\u201d</p><p>\u201c\u53ef\u662f\u2026\u2026\u5728\u59d0\u59d0\u9762\u524d\u8bf4\u8fd9\u4e2a\uff0c\u603b\u662f\u89c9\u5f97\u4f1a\u4e0d\u597d\u610f\u601d\u561b\u3002\u201d</p><p>\u743c\u6069\u6628\u591c\u662f\u548c\u83ab\u59ae\u5361\u59d0\u59b9\u4fe9\u4e00\u8d77\u5ea6\u8fc7\u7684\uff0c\u6216\u8bb8\u662f\u56e0\u4e3a\u5373\u5c06\u79bb\u522b\u7684\u7f18\u6545\uff0c\u4e0d\u4ec5\u4ec5\u8299\u857e\u72c4\uff0c\u4fbf\u662f\u8299\u8389\u5a05\u4e5f\u6e29\u987a\u4e56\u5de7\u4e86\u8bb8\u591a\u3002\u4e09\u4eba\u5728\u5e8a\u4e0a\u6574\u6574\u6298\u817e\u4e86\u4e00\u591c\uff0c\u6700\u540e\u7ec8\u4e8e\u7b4b\u75b2\u529b\u5c3d\uff0c\u76f8\u62e5\u7761\u53bb\uff0c\u4e00\u76f4\u5230\u4e2d\u5348\u65f6\u5206\u624d\u9192\u8fc7\u6765\u3002\u743c\u6069\u81ea\u7136\u6ca1\u6307\u671b\u8fd9\u4ef6\u4e8b\u80fd\u7792\u8fc7\u73ca\u5609\uff0c\u4f46\u88ab\u59d0\u59d0\u5f53\u9762\u63d0\u8d77\u6765\uff0c\u603b\u8fd8\u662f\u6709\u4e9b\u5c34\u5c2c\u3002</p><p>\u4fbf\u5728\u6b64\u65f6\uff0c\u4e00\u76f4\u9ad8\u901f\u75be\u9a70\u7684\u9a6c\u8f66\u901f\u5ea6\u7a81\u7136\u505c\u4e0b\u6765\uff0c\u73ca\u5609\u731d\u4e0d\u53ca\u9632\uff0c\u88ab\u60ef\u6027\u5e26\u5f97\u5f80\u540e\u4e00\u4ef0\uff0c\u5e78\u597d\u6709\u539a\u539a\u7684\u57ab\u5b50\u6321\u4f4f\u3002\u201c\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u5979\u5947\u602a\uff0c\u4fbf\u89c1\u8f66\u53a2\u58c1\u53d8\u5f97\u5982\u955c\u9762\u822c\u900f\u660e\uff0c\u5176\u4e0a\u5149\u5f71\u4ea4\u9519\uff0c\u6620\u51fa\u9a6c\u8f66\u524d\u65b9\u7684\u60c5\u666f\uff1a\u56db\u5177\u50f5\u5c38\u6447\u6447\u6643\u6643\u5730\u4ece\u65c1\u8fb9\u8349\u4e1b\u4e2d\u722c\u8d77\uff0c\u6321\u4f4f\u53bb\u8def\u3002</p><p>\u201c\u89c1\u9b3c\u4e86\uff0c\u8fd9\u91cc\u600e\u4e48\u4f1a\u6709\u4ea1\u7075\u2026\u2026\u201d</p><p>\u743c\u6069\u6115\u7136\u4e0d\u89e3\uff0c\u4ed6\u4eec\u6b64\u65f6\u5df2\u7ecf\u79bb\u5f00\u6851\u6bd4\u4e9a\uff0c\u8fdb\u5165\u5f13\u8c37\u7684\u9886\u5730\u3002\u5f13\u8c37\u662f\u8c37\u5730\u5341\u4e00\u9547\u4e4b\u4e00\uff0c\u800c\u4e14\u662f\u6bd4\u8f83\u5f3a\u76db\u7684\u4e00\u4e2a\uff0c\u548c\u9634\u5f71\u8c37\u3001\u5854\u745f\u8c37\u5c5e\u4e8e\u540c\u7ea7\uff0c\u636e\u8bf4\u6cbb\u7406\u5f97\u975e\u5e38\u4e0d\u9519\u3002\u5c31\u7b97\u4e0d\u63d0\u4ec0\u4e48\u591c\u4e0d\u95ed\u6237\u9053\u4e0d\u62fe\u9057\uff0c\u603b\u4e5f\u4e0d\u5e94\u8be5\u9752\u5929\u767d\u65e5\u6717\u6717\u4e7e\u5764\uff0c\u5927\u8def\u4e0a\u90fd\u6709\u4ea1\u7075\u6253\u52ab\u5427\u3002</p><p>\u4ed6\u6b63\u8981\u8d77\u8eab\uff0c\u73ca\u5609\u8f7b\u8f7b\u6309\u4f4f\uff0c\u201c\u522b\u52a8\uff0c\u8033\u6735\u8fd8\u6ca1\u638f\u5b8c\u5462\u2014\u2014\u742a\u5a05\uff0c\u53bb\u5e72\u6389\u5b83\u4eec\u3002\u201d</p><p>\u968f\u7740\u5979\u7684\u547d\u4ee4\uff0c\u539f\u672c\u8737\u7f29\u5728\u89d2\u843d\u91cc\u547c\u547c\u5927\u7761\u7684\u9ed1\u732b\u95ea\u7535\u822c\u8dc3\u51fa\u8f66\u53a2\uff0c\u4e00\u843d\u5730\u4fbf\u5316\u4f5c\u5c16\u8033\u957f\u5c3e\u7684\u5c11\u5973\uff0c\u53cc\u624b\u722a\u5957\u5bd2\u5149\u51db\u51db\uff0c\u55b5\u545c\u4f4e\u543c\u4e00\u58f0\uff0c\u671d\u7740\u90a3\u56db\u540d\u50f5\u5c38\u731b\u6251\u8fc7\u53bb\u3002</p></article>"}}}]);
