(window.webpackJsonp=window.webpackJsonp||[]).push([[476],{499:function(p){p.exports={chapterTitle:null,title:"\u7b2c476\u7ae0 \u597d\u5904",content:"<article><h2>\u7b2c476\u7ae0 \u597d\u5904</h2><p>\u3000\u3000\u5f20\u606a\u4e0e\u8bb8\u601d\u3001\u5b59\u5c1a\u4e49\u7b49\u4eba\u53bb\u673a\u573a\u89c1\u8fc7\u5728\u9999\u6e2f\u8f6c\u673a\u56de\u4e1c\u6d77\u7684\u5f90\u5b66\u5e73\u4e4b\u540e\uff0c\u5c06\u5468\u6e38\u3001\u5b8b\u5fd7\u4f51\u622a\u4e0b\u6765\uff0c\u4e00\u8d77\u5750\u8f66\u8fd4\u56de\u5f71\u6e7e\u56ed\uff0c\u521a\u5230\u5f71\u6e7e\u56ed\u9152\u5e97\u516c\u5bd3\uff0c\u5c31\u63a5\u5230\u845b\u660e\u5fb7\u7684\u7535\u8bdd\uff0c\u8bf4\u662f\u5609\u4fe1\u7535\u5b50\u4e0e\u79d1\u738b\u7535\u5668\u3001\u9526\u57ce\u96c6\u56e2\u6b63\u5f0f\u7b7e\u7f72\u4e09\u65b9\u5408\u4f5c\u534f\u8bae\uff0c\u7535\u8bdd\u91cc\u8bf4\u4e0d\u6e05\u695a\uff0c\u5f20\u606a\u8bf7\u845b\u660e\u5fb7\u5230\u5f71\u6e7e\u56ed\u9152\u5e97\u516c\u5bd3\u6765\u3002</p><p>\u3000\u3000\u8c22\u6c49\u9756\u3001\u8c22\u5251\u5357\u3001\u9648\u9759\u4e09\u4eba\u662f12\u67089\u65e5\u4e58\u540c\u4e00\u73ed\u98de\u673a\u62b5\u8fbe\u9999\u6e2f\u7684\uff0c\u845b\u666f\u8bda\u7684\u846c\u793c11\u65e5\u7ed3\u675f\uff0c\u5373\u4f7f\u4ed6\u4eec\u4e4b\u524d\u5c31\u5408\u4f5c\u6709\u8fc7\u63a5\u89e6\uff0c\u4e5f\u8003\u8651\u5230\u8c22\u5bb6\u6293\u4f4f\u9526\u57ce\u8d44\u91d1\u5468\u8f6c\u56f0\u96be\u7684\u95ee\u9898\u6765\u903c\u8feb\u8d75\u9526\u8363\u5c48\u670d\uff0c\u4e0d\u8fc7\u53ea\u7528\u5341\u5929\u7684\u65f6\u95f4\u8c08\u5b8c\u6240\u6709\u7684\u5408\u4f5c\u7ec6\u8282\u95ee\u9898\uff0c\u4e5f\u4e0d\u80fd\u4e0d\u627f\u8ba4\u4ed6\u4eec\u7684\u901f\u5ea6\u8fd8\u662f\u591f\u5feb\u7684\u3002</p><p>\u3000\u3000\u5b59\u5c1a\u4e49\u592b\u5987\u660e\u786e\u8868\u793a\u7ee7\u627f\u5bb6\u65cf\u8d22\u4ea7\u65f6\u653e\u5f03\u5609\u4fe1\u7535\u5b50\u7684\u80a1\u4efd\uff0c\u4e5f\u5f97\u5230\u5176\u4ed6\u4eba\u7684\u540c\u610f\uff0c\u6240\u4ee5\u8d75\u9526\u8363\u98de\u6765\u9999\u6e2f\u3001\u8c22\u6c49\u9756\u3001\u8c22\u5251\u5357\u4e0e\u5609\u4fe1\u7535\u5b50\u8c08\u5224\u7684\u7ec6\u8282\uff0c\u4ed6\u4eec\u5c31\u4e0d\u662f\u6e05\u695a\u3002</p><p>\u3000\u3000\u845b\u660e\u5fb7\u9664\u4e86\u8ddf\u845b\u660e\u4fe1\u52bf\u5982\u6c34\u706b\uff0c\u4e0e\u845b\u5bb6\u5176\u4ed6\u6210\u5458\u7684\u5173\u7cfb\u5341\u5206\u878d\u6d3d\uff0c\u6240\u4ee5\u8fd9\u8fb9\u638c\u63e1\u4ed6\u4eec\u8c08\u5224\u7684\u8fdb\u5ea6\u8fd8\u662f\u6613\u5982\u53cd\u638c\u7684\u3002</p><p>\u3000\u3000\u9526\u57ce\u5f7b\u5e95\u4ece\u79d1\u738b\u7535\u5668\u9000\u51fa\uff0c\u5c06\u624b\u4e2d\u6240\u6301\u79d1\u4efd\u6089\u6570\u8f6c\u8ba9\u7ed9\u5609\u4fe1\u7535\u5b50\uff0c\u5609\u4fe1\u7535\u5b50\u63a5\u53d7\u9526\u57ce\u624b\u91cc\u7684\u80a1\u4efd\uff0c\u7ee7\u7eed\u589e\u8d44\u79d1\u738b\uff0c\u5c06\u6301\u80a1\u6bd4\u4f8b\u6bd4\u4f8b\u63d0\u9ad8\u523040\uff05\uff0c\u5927\u96c5\u96c6\u56e2\u6301\u80a1\u8c03\u6574\u523016\uff05\uff0c\u8c22\u5bb6\u4ee5\u6b63\u6cf0\u3001\u6d77\u6cf0\u4ee5\u53ca\u8c22\u77bb\u4e2a\u4eba\u7684\u540d\u4e49\u6301\u80a144\uff05\uff0c\u4f46\u56e0\u5927\u96c5\u96c6\u56e2\u5bf9\u6d77\u6cf0\u6709\u6295\u8d44\uff0c\u5b9e\u9645\u6301\u80a1\u4e3a20\uff05\u5de6\u53f3\u3002</p><p>\u3000\u3000\u5f20\u606a\u5bf9\u8fd9\u4e9b\u4e0d\u5173\u5fc3\uff0c\u53ea\u662f\u60f3\u77e5\u9053\u4ed6\u4eec\u7684\u5408\u4f5c\u534f\u8bae\u4e2d\u5bf9\u4e1c\u5357\u4e9a\u5730\u533a\u789f\u673a\u5e02\u573a\u5f00\u53d1\u7684\u5185\u5bb9\uff0c\u8fd8\u6709\u5c31\u662f\u9526\u57ce\u8f6c\u8ba9\u624b\u91cc\u79d1\u738b30\uff05\u7684\u80a1\u6743\u7ed9\u5609\u4fe1\u7535\u5b50\uff0c\u56de\u7b3c\u4e86\u591a\u5c11\u8d44\u91d1\u3002</p><p>\u3000\u3000\u201c\u516b\u5343\u4e07\uff01\u201d\u845b\u660e\u5fb7\u8bf4\u9053\uff0c\u201c\u9526\u57ce\u8f6c\u8ba9\u80a1\u4ef7\u662f\u516b\u5343\u4e07\u6e2f\u5143\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8d75\u9526\u8363\u809a\u5b50\u91cc\u5927\u6982\u618b\u7740\u4e00\u56e2\u706b\uff0c\u201d\u5f20\u606a\u629a\u7740\u989d\u5934\u7b11\u4e86\u8d77\u6765\uff0c\u5468\u6e38\u6700\u8fd1\u8fd9\u6bb5\u65f6\u95f4\u4e00\u76f4\u5728\u65e5\u672c\uff0c\u800c\u4e14\u4ed6\u4e5d\u516d\u5e74\u4e4b\u540e\u5c31\u5f88\u5c11\u76f4\u63a5\u63a5\u89e6\u7231\u8fbe\u7684\u4e8b\u52a1\uff0c\u5f20\u606a\u5c06\u4e8b\u60c5\u7684\u524d\u56e0\u540e\u679c\u89e3\u91ca\u7ed9\u4ed6\u542c\uff0c\u201c\u6211\u4f30\u7b97\u9526\u57ce\u524d\u540e\u6295\u5165\u79d1\u738b\u7684\u8d44\u91d1\u5dee\u4e0d\u591a\u5c31\u6709\u4e03\u5343\u4e07\uff0c30\uff05\u7684\u80a1\u6743\uff0c\u6211\u770b\u79d1\u738b\u6210\u7acb\u81f3\u4eca\uff0c\u8fd930\uff05\u80a1\u6743\u76f4\u63a5\u4ea7\u751f\u7684\u76c8\u5229\u5c31\u8fdc\u8fdc\u4e0d\u6b62\u8fd9\u4e2a\u6570\u2026\u2026\u201d</p><p>\u3000\u3000\u845b\u660e\u5fb7\u7b11\u7740\u8bf4\uff1a\u201c\u5609\u4fe1\u7535\u5b50\u4ece\u9526\u57ce\u624b\u91cc\u83b7\u5f9730\uff05\u7684\u80a1\u6743\uff0c\u5bf9\u79d1\u738b\u8d44\u4ea7\u7684\u4f30\u503c\u4e0d\u8db3\u4e09\u4ebf\u5343\u4e07\uff1b\u53e610\uff05\u80a1\u6743\u7684\u83b7\u5f97\uff0c\u5374\u662f\u4ee5\u56db\u4ebf\u7684\u4f30\u503c\u6c34\u5e73\u5411\u79d1\u738b\u6ce8\u5165\u8fd1\u4e94\u5343\u4e07\u6e2f\u5143\u7684\u8d44\u91d1\uff0c\u644a\u767d\u4e86\uff0c\u8fd9\u7b14\u4ea4\u6613\uff0c\u9526\u57ce\u5c31\u8981\u635f\u5931\u4e86\u56db\u5343\u4e07\uff0c\u5012\u4e0d\u6653\u5f97\u8fd9\u7b14\u4ea4\u6613\u662f\u5982\u4f55\u4fc3\u6210\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8d75\u9526\u8363\u4e5f\u662f\u7ed9\u903c\u4e0a\u7edd\u8def\uff0c\u8fc7\u6cb3\u62c6\u6865\uff0c\u8c22\u5bb6\u80cc\u540e\u5bf9\u76df\u53cb\u4e0b\u624b\u53ef\u4ece\u6765\u90fd\u4e0d\u4f1a\u624b\u6148\u624b\u8f6f\uff01\u201d\u5f20\u606a\u5c06\u8d75\u9526\u8363\u5c06\u9526\u57ce\u4e09\u5343\u4e07\u8d37\u6b3e\u632a\u7ed9\u79d1\u738b\u4f7f\u7528\u906d\u5230\u94f6\u884c\u50ac\u8ba8\u7684\u4e8b\u60c5\u8bf4\u7ed9\u845b\u660e\u5fb7\u3001\u5b59\u5c1a\u4e49\u4ed6\u4eec\u542c\uff1b\u9999\u6e2f\u91d1\u73b0\u5236\u5ea6\u76f8\u5bf9\u4e25\u5bc6\u5f97\u591a\uff0c\u82e5\u6709\u6f0f\u6d1e\u7ed9\u5bf9\u624b\u62ff\u6765\u653b\u51fb\u4e5f\u662f\u5e38\u6709\u7684\u4e8b\u60c5\uff0c\u5546\u573a\u4ece\u6765\u90fd\u4e0d\u662f\u6e29\u60c5\u8109\u8109\u7684\uff0c\u4e0d\u8fc7\u8c22\u5bb6\u7528\u4eba\u5373\u6254\u3001\u8fc7\u6865\u62c6\u6865\u7684\u72e0\u51b3\uff0c\u5012\u662f\u8ba9\u4ed6\u4eec\u5618\u550f\u4e0d\u5df2\u3002</p><p>\u3000\u3000\u201c\u65e2\u7136\u8fdd\u89c4\u632a\u7528\u906d\u5230\u50ac\u8ba8\uff0c\u90a3\u8fd9\u7b14\u8d37\u6b3e\uff0c\u9526\u6e56\u603b\u8981\u5148\u8fd8\u6389\u7684\uff0c\u201d\u5468\u6e38\u6e05\u695a\u5f20\u606a\u603b\u4f53\u4e0a\u7684\u90e8\u7f72\uff0c\u603b\u4e0d\u4f1a\u5e0c\u671b\u9526\u57ce\u6709\u597d\u65e5\u5b50\u8fc7\u7684\uff0c\u201c\u8981\u662f\u6d88\u606f\u4f20\u51fa\u6765\uff0c\u8fd8\u5269\u4e0b\u7684\u90a3\u4e94\u5343\u4e07\uff0c\u9526\u57ce\u8fd8\u80fd\u4fdd\u4f4f\u591a\u5c11\u5c31\u4e0d\u5f97\u800c\u77e5\u4e86\uff0c\u8fd9\u5927\u6982\u662f\u5c4b\u6f0f\u504f\u9022\u8fde\u591c\u96e8\u5427\uff01\u201d\u8fdd\u89c4\u632a\u7528\u8d37\u6b3e\u906d\u5230\u94f6\u884c\u50ac\u8ba8\uff0c\u6d88\u606f\u4e00\u65e6\u4f20\u51fa\u53bb\uff0c\u52bf\u5fc5\u5f15\u8d77\u5176\u4ed6\u503a\u6743\u4eba\u7684\u6050\u614c\uff0c\u90fd\u4e00\u8d77\u8fc7\u6765\u8ffd\u8ba8\u503a\u52a1\uff0c\u800c\u9526\u57ce\u96c6\u56e2\u6700\u91cd\u8981\u7684\u4e00\u5757\u8d44\u4ea7\uff0c\u8fd8\u662f\u6c99\u7530\u897f\u7247\u8fdb\u5ea6\u7f13\u6162\u3001\u8fdf\u8fdf\u672a\u89c1\u5b8c\u5de5\u7684\u5546\u4e1a\u5730\u4ea7\uff0c\u9664\u4e86\u90a3\u4e9b\u5546\u4e1a\u5730\u4ea7\u5f00\u53d1\u5b8c\u6210\u987a\u5229\u51fa\u552e\uff0c\u4e0d\u7136\u9526\u57ce\u662f\u65e0\u6cd5\u5f7b\u5e95\u89e3\u51b3\u4ed6\u4eec\u7684\u8d22\u52a1\u5371\u673a\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u5fae\u5fae\u4e00\u7b11\uff0c\u8fd9\u4e2a\u95ee\u9898\u5c31\u6ca1\u6709\u5fc5\u8981\u5728\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5fb7\u9762\u524d\u8ba8\u8bba\uff0c\u95ee\u8d77\u5609\u4fe1\u7535\u5b50\u4e0e\u79d1\u738b\u5408\u4f5c\u534f\u8bae\u91cc\u5bf9\u4e1c\u5357\u4e9a\u5730\u533a\u789f\u673a\u5e02\u573a\u5f00\u53d1\u7684\u5185\u5bb9\u3002</p><p>\u3000\u3000\u201c\u4f1a\u5728\u9999\u6e2f\u72ec\u7acb\u6ce8\u518c\u5206\u516c\u53f8\u6765\u5f00\u53d1\u4e1c\u5357\u4e9a\u5730\u533a\u7684\u789f\u673a\u5e02\u573a\uff0c\u5404\u5bb6\u6309\u6bd4\u4f8b\u6ce8\u8d44\u4e24\u4ebf\u6e2f\u5143\u2026\u2026\u7531\u90a3\u5c0f\u5b50\u6765\u8d1f\u8d23\u3002\u201d</p><p>\u3000\u3000\u592a\u8be6\u7ec6\u7684\u5185\u5bb9\u5c31\u6d89\u53ca\u5230\u5546\u4e1a\u673a\u5bc6\uff0c\u845b\u660e\u5fb7\u4e5f\u65e0\u6cd5\u83b7\u77e5\uff0c\u4f46\u662f\u77e5\u9053\u8fd9\u4e9b\u5185\u5bb9\u4e5f\u5df2\u7ecf\u8db3\u591f\u4e86\uff0c\u5f20\u606a\u5fc3\u60f3\uff1a\u845b\u660e\u5fb7\u5634\u91cc\u7684\u201c\u90a3\u5c0f\u5b50\u201d\u5c31\u662f\u6307\u845b\u836b\u5747\uff0c\u8fd9\u4e5f\u5f88\u6b63\u5e38\uff0c\u6bd5\u7adf\u662f\u5251\u6865\u5b66\u6210\u5f52\u6765\u7684\u9ad8\u6750\u751f\uff0c\u5728\u845b\u6c0f\u4e0b\u5c5e\u7684\u4f01\u4e1a\u4e5f\u53ae\u6df7\u8fc7\u8fd1\u56db\u5e74\u7684\u65f6\u95f4\uff0c\u81f3\u5c11\u5f97\u5230\u201c\u6709\u76f8\u5f53\u80fd\u529b\u201d\u7684\u8bc4\u4ef7\uff0c\u53c8\u4e0e\u8c22\u5251\u5357\u6709\u4e00\u6bb5\u65f6\u95f4\u7684\u540c\u7a97\u4e4b\u8c0a\uff0c\u4ed6\u6765\u8d1f\u8d23\u79d1\u738b\u7684\u6d77\u5916\u5206\u516c\u53f8\uff0c\u8fd8\u771f\u662f\u9002\u5408\u3002</p><p>\u3000\u3000\u4e1c\u5357\u4e9a\u5730\u533a\u7684\u4eba\u53e3\u89c4\u6a21\u7ea6\u4e3a\u56fd\u5185\u4e00\u534a\uff0c\u6f5c\u529b\u7684\u789f\u673a\u5e02\u573a\u4e5f\u5dee\u4e0d\u591a\u4e3a\u56fd\u5185\u4e00\u534a\uff0c\u4f46\u662f\u5206\u5c5e\u4e0d\u540c\u7684\u5730\u533a\u4e0e\u56fd\u5bb6\uff0c\u7ba1\u7406\u4e0a\u7684\u96be\u5ea6\u4f1a\u589e\u52a0\u4e00\u4e9b\uff0c\u4f46\u4e0d\u4f1a\u589e\u52a0\u592a\u591a\uff1b\u6d77\u5916\u5206\u516c\u53f8\u8bbe\u5728\u9999\u6e2f\u8fd8\u662f\u6709\u597d\u5904\uff0c\u9999\u6e2f\u4e0d\u4ec5\u662f\u5168\u7403\u4e3a\u6570\u4e0d\u591a\u7684\u91d1\u878d\u4e2d\u5fc3\u4e4b\u4e00\uff0c\u66f4\u662f\u534e\u4eba\u5546\u5708\u7684\u60df\u4e00\u91d1\u878d\u4e2d\u5fc3\uff0c\u5bf9\u4e1c\u5357\u4e9a\u5404\u4e2a\u56fd\u5bb6\u4e0e\u5730\u533a\u7684\u5e45\u5c04\u80fd\u529b\u6781\u5f3a\u3002</p><p>\u3000\u3000\u82e5\u662f\u751f\u4ea7\u57fa\u5730\u8bbe\u5728\u5185\u5730\uff0c\u53ea\u8981\u5728\u9999\u6e2f\u6d77\u5916\u4ee3\u7406\u603b\u90e8\uff0c\u5c31\u80fd\u7ec4\u5efa\u8986\u76d6\u6574\u4e2a\u4e1c\u5357\u4e9a\u533a\u57df\u7684\u6e20\u9053\u5546\u7f51\u7edc\uff0c\u4f46\u662f\u7531\u4e8e\u5404\u56fd\u5173\u7a0e\u7684\u4fdd\u62a4\u58c1\u5792\u4f5c\u7528\uff0c\u8fdb\u5165\u5404\u56fd\u5e02\u573a\u7684\u4ea7\u54c1\u8981\u52a0\u4e0a\u5173\u7a0e\u6210\u672c\uff0c\u9500\u4ef7\u81ea\u7136\u8981\u9ad8\u8bb8\u591a\u3002\u6700\u5408\u9002\u7684\u529e\u6cd5\u5c31\u662f\u5229\u7528\u4e1c\u5357\u4e9a\u5730\u533a\u4e0e\u56fd\u5bb6\u8054\u76df\u4e4b\u95f4\u7535\u5b50\u4ea7\u54c1\u7684\u5173\u7a0e\u4e92\u60e0\u6761\u4ef6\u5c06\u751f\u4ea7\u57fa\u5730\u8bbe\u5728\u4e1c\u76df\u7684\u67d0\u4e2a\u56fd\u5bb6\u4e0e\u5730\u533a\uff0c\u5c31\u53ef\u4ee5\u5927\u5e45\u5ea6\u7684\u964d\u4f4e\u5173\u7a0e\u6240\u5e26\u6765\u7684\u989d\u5916\u6210\u672c\u3002</p><p>\u3000\u3000\u201c\u5609\u4fe1\u7535\u5b50\u5728\u9a6c\u6765\u897f\u4e9a\u3001\u65b0\u52a0\u5761\u90fd\u6709\u5de5\u5382\uff0c\u8fd9\u4e9b\u5de5\u5382\uff0c\u53ef\u4ee5\u501f\u7ed9\u79d1\u738b\u7528\uff0c\u836b\u5747\u8d44\u5386\u867d\u7136\u6d45\u4e00\u4e9b\uff0c\u6211\u60f3\u4ed6\u8001\u5b50\u4f1a\u5728\u80cc\u540e\u5e2e\u4ed6\u51fa\u4e9b\u529b\u6c14\uff0c\u518d\u5229\u7528\u845b\u5bb6\u5728\u4e1c\u5357\u4e9a\u7684\u5f71\u54cd\u529b\uff0c\u7ec4\u5efa\u4ee3\u7406\u6e20\u9053\u4f53\u7cfb\u4e5f\u4e0d\u4f1a\u592a\u56f0\u96be\uff0c\u201d\u5b59\u5c1a\u4e49\u8bf4\u9053\uff1a\u201c\u63a5\u4e0b\u6765\u5c31\u8981\u770b\u4ed6\u4eec\u600e\u4e48\u6d12\u94b1\u5c06\u644a\u5b50\u94fa\u5f00\u4e86\uff0c\u80fd\u60f3\u8c61\u4ed6\u4eec\u7684\u52a8\u4f5c\u4e0d\u4f1a\u592a\u6162\u2026\u2026\u201d\u5b59\u5c1a\u4e49\u672a\u8bf4\u900f\u7684\u610f\u601d\u662f\u8bf4\u4ed6\u4eec\u8fd9\u8fb9\u4e5f\u53ef\u4ee5\u9002\u5f53\u7684\u52a0\u5feb\u4e00\u4e0b\u901f\u5ea6\u3002</p><p>\u3000\u3000\u51c6\u5907\u5de5\u4f5c\u5b8c\u6210\uff0c\u8981\u5c06\u644a\u5b50\u94fa\u5f00\u5c31\u662f\u89e3\u51b3VCD\u789f\u7247\u7684\u7247\u6e90\u95ee\u9898\u3001\u9ad8\u5f3a\u5ea6\u7684\u5e02\u573a\u5ba3\u4f20\u3001\u4ea7\u80fd\u6269\u5f20\uff0c\u7247\u6e90\u95ee\u9898\u597d\u89e3\u51b3\uff0c\u6709\u56fd\u5185\u7684\u7ecf\u9a8c\u53ef\u4ee5\u501f\u9274\uff0c\u53ea\u8981\u5e02\u573a\u5ba3\u4f20\u5230\u4f4d\uff0c\u53ef\u4ee5\u9884\u89c1\u789f\u673a\u4e0e\u789f\u7247\u5e02\u573a\u80fd\u540c\u6b65\u6253\u5f00\uff0c\u5e02\u573a\u5ba3\u4f20\u4e0e\u4ea7\u80fd\u6269\u5f20\u5219\u610f\u8bc6\u7740\u76f4\u63a5\u7684\u5de8\u989d\u6295\u8d44\u3002</p><p>\u3000\u3000\u5f20\u606a\u88c5\u6a21\u4f5c\u6837\u7684\u8bf4\uff1a\u201c\u6211\u4eec\u7684\u786e\u4e5f\u8981\u52a0\u5feb\u4e00\u4e0b\u901f\u5ea6\uff0c\u4e0d\u8fc7\u603b\u90e8\u7684\u8d44\u91d1\u4e5f\u5f88\u7d27\u5f20\uff0c\u4e0d\u4ec5\u4e0d\u80fd\u52a8\uff0c\u789f\u673a\u4e1a\u52a1\u5728\u4e1c\u5357\u4e9a\u5730\u533a\u5df2\u7ecf\u4ea7\u751f\u5229\u6da6\uff0c\u8fd9\u4e9b\u5229\u6da6\u8fd8\u8981\u6e90\u6e90\u4e0d\u65ad\u7684\u63d0\u53d6\u56de\u6765\uff0c\u5f00\u5c55\u4e1a\u52a1\u6240\u9700\u7684\u8d44\u91d1\u6211\u770b\u8fd8\u662f\u8ba9\u9a7b\u5404\u5730\u7684\u5b50\u516c\u53f8\u79ef\u6781\u5411\u5f53\u5730\u94f6\u884c\u7533\u8bf7\u4fe1\u7528\u62c5\u4fdd\u8d37\u6b3e\u2026\u2026\u845b\u5148\u751f\u4e0e\u4e1c\u5357\u4e9a\u7684\u534e\u5546\u5173\u7cfb\u90fd\u4e0d\u9519\u5427\uff1f\u201d</p><p>\u3000\u3000\u845b\u660e\u5fb7\u671d\u5b59\u5c1a\u4e49\u770b\u4e86\u4e00\u773c\u3002</p><p>\u3000\u3000\u5b59\u5c1a\u4e49\u65e0\u5948\u7684\u7b11\u4e86\u7b11\uff1a\u201c\u606a\u5c11\u8fd8\u771f\u662f\u5f88\u56fa\u6267\u7684\u4e00\u4e2a\u4eba\uff0c\u5373\u4f7f\u53ef\u80fd\u4f1a\u9519\u8fc7\u538b\u5236\u5bf9\u624b\u7684\u673a\u4f1a\uff0c\u4e5f\u4e0d\u653e\u5f03\u81ea\u5df1\u7684\u5224\u65ad\u2026\u2026\u201d</p><p>\u3000\u3000\u5bf9\u4e1c\u5357\u4e9a\u7ecf\u6d4e\u52bf\u6001\u7684\u62c5\u5fe7\u662f\u4e00\u56de\u4e8b\uff0c\u4f46\u662f\u5b9e\u9645\u9762\u4e34\u6765\u81ea\u7ade\u4e89\u5bf9\u624b\u7684\u5e02\u573a\u538b\u529b\uff0c\u53c8\u662f\u53e6\u5916\u4e00\u56de\u4e8b\uff0c\u5b59\u5c1a\u4e49\u7684\u8003\u8651\u662f\u5b8c\u5168\u6709\u9053\u7406\u7684\u3002</p><p>\u3000\u3000\u5609\u4fe1\u5b9e\u4e1a\u5728\u83f2\u5f8b\u5bbe\u4e0e\u8d8a\u5357\u90fd\u5efa\u6709\u751f\u4ea7\u57fa\u5730\uff0c\u5b8c\u5168\u53ef\u4ee5\u89c4\u907f\u4e1c\u5357\u4e9a\u5404\u4e2a\u56fd\u5bb6\u4e0e\u5730\u533a\u95f4\u7684\u5173\u7a0e\u58c1\u5792\uff0c\u6e20\u9053\u5546\u7f51\u7edc\u4e5f\u5dee\u4e0d\u591a\u5efa\u7acb\uff0c\u8981\u4e0d\u662f\u660e\u77e5\u9053\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u6781\u96be\u907f\u514d\uff0c\u5f20\u606a\u4e5f\u4f1a\u5728\u4e5d\u4e03\u5e74\u5c31\u5927\u529b\u5f00\u53d1\u4e1c\u5357\u4e9a\u7684\u789f\u673a\u5e02\u573a\u3002</p><p>\u3000\u3000\u73b0\u5728\uff0c\u5f20\u606a\u4f1a\u53cd\u590d\u62ff\u53f6\u81fb\u6c11\u6587\u7ae0\u91cc\u7684\u7406\u8bba\u6765\u529d\u8bf4\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5fb7\uff0c\u4ed6\u7b11\u7740\u8bf4\uff1a\u201c\u6ca1\u6709\u529e\u6cd5\uff0c\u4e0d\u649e\u5357\u5899\u4e0d\u56de\u5934\u7684\u786c\u6485\u813e\u6c14\uff0c\u8f7b\u6613\u52a8\u6447\uff0c\u4e0d\u4f1a\u6709\u592a\u6df1\u523b\u7684\u53cd\u601d\uff0c\u52a8\u6447\u7684\u5374\u662f\u5bf9\u81ea\u5df1\u7684\u4fe1\u5fc3\u2026\u2026\u5f53\u7136\uff0c\u4e3a\u4e86\u914d\u5408\u589e\u53d1\u5e76\u8d2d\u65b9\u6848\u7684\u9700\u8981\u62c9\u5347\u5609\u4fe1\u5b9e\u4e1a\u7684\u80a1\u4ef7\uff0c\u5e76\u8981\u5c06\u79d1\u738b\u3001\u65b0\u79d1\u3001\u6b65\u6b65\u9ad8\u3001\u7231\u591a\u7b49\u789f\u673a\u5382\u5546\u7684\u6ce8\u610f\u529b\u90fd\u5438\u5f15\u5230\u4e1c\u5357\u4e9a\u5730\u533a\u53bb\uff0c\u51cf\u8f7b\u56fd\u5185\u5e02\u573a\u7684\u538b\u529b\uff0c\u6211\u4eec\u7684\u786e\u4e5f\u8981\u505a\u4e9b\u8868\u9762\u6587\u7ae0\u2026\u2026\u7231\u8fbe\u7535\u5b50\u4e0e\u5609\u4fe1\u5b9e\u4e1a\u90fd\u5206\u522b\u516c\u5e03\u8981\u8054\u5408\u52a0\u5927\u5bf9\u4e1c\u5357\u4e9a\u5e02\u573a\u7684\u6295\u8d44\uff0c\u7279\u522b\u662f\u5609\u4fe1\u5b9e\u4e1a\u662f\u4ee5\u516c\u544a\u7684\u5f62\u5f0f\u5411\u9999\u6e2f\u6295\u8d44\u8005\u516c\u5e03\u8fd9\u4e00\u51b3\u5b9a\uff0c\u90a3\u5c31\u4e0d\u80fd\u4ec5\u662f\u8bf4\u8bf4\u800c\u5df2\u2026\u2026\u201d\u5f20\u606a\u626f\u5230\u8fd9\u91cc\uff0c\u624d\u95ee\u5b59\u5c1a\u4e49\uff0c\u201c\u5b59\u5148\u751f\uff0c\u4f60\u8bf4\u662f\u4e0d\u662f\u8fd9\u56de\u4e8b\uff1f\u201d</p><p>\u3000\u3000\u201c\u90a3\u4e5f\u53ea\u662f\u8868\u9762\u6587\u7ae0\u800c\u5df2\u2026\u2026\u201d\u5b59\u5c1a\u4e49\u89c1\u5f20\u606a\u7684\u6001\u5ea6\u8fd8\u662f\u4e00\u8d2f\u7684\u575a\u6301\uff0c\u53ea\u5f97\u65e0\u5948\u7684\u7b11\u7b11\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u81ea\u6709\u4ed6\u7684\u6253\u7b97\uff0c\u5c31\u50cf\u4ed6\u521a\u521a\u8bf4\u7684\uff0c\u4ed6\u5efa\u8bae\u5609\u4fe1\u5b9e\u4e1a\u5728\u4e1c\u5357\u4e9a\u5730\u533a\u5f00\u5c55\u789f\u673a\u4e1a\u52a1\u8981\u5c3d\u53ef\u80fd\u7684\u5229\u7528\u5f53\u5730\u7684\u4e2d\u957f\u671f\u8d37\u6b3e\uff0c\u800c\u789f\u673a\u4e1a\u52a1\u6240\u4ea7\u751f\u7684\u5229\u6da6\u4e5f\u8981\u53ca\u65f6\u62bd\u53d6\u51fa\u6765\uff0c\u589e\u52a0\u516c\u53f8\u7684\u8d44\u91d1\u6d41\u52a8\u6027\u3002</p><p>\u3000\u3000\u8fd9\u6837\u4e00\u6765\uff0c\u5c31\u51b3\u5b9a\u5609\u4fe1\u5b9e\u4e1a\u5728\u4e1c\u5357\u4e9a\u5730\u533a\u5f00\u5c55\u789f\u673a\u4e1a\u52a1\u5c31\u4e0d\u53ef\u80fd\u80fd\u5feb\u8d77\u6765\uff0c\u56e0\u4e3a\u5609\u4fe1\u5b9e\u4e1a\u4ece\u4e1c\u5357\u4e9a\u5341\u4e2a\u56fd\u5bb6\u3001\u5730\u533a\u5404\u878d\u8d44\u51e0\u5343\u4e07\u4e0e\u4ece\u9999\u6e2f\u91d1\u878d\u5e02\u573a\u76f4\u63a5\u878d\u8d44\u51e0\u4ebf\u76f8\u6bd4\uff0c\u590d\u6742\u7a0b\u5ea6\u7edd\u5bf9\u8981\u589e\u52a0\u6570\u500d\u4ee5\u4e0a\uff0c\u4ec5\u8fd9\u4e00\u9879\uff0c\u5c31\u5c06\u5609\u4fe1\u5b9e\u4e1a\u539f\u6709\u7684\u7ba1\u7406\u4eba\u5458\u81ea\u5085\u5bb6\u4fca\u4ee5\u4e0b\u90fd\u65e0\u6687\u987e\u53ca\u5176\u4ed6\u7684\u4e8b\u52a1\u3002</p><p>\u3000\u3000\u4e0d\u53ef\u5426\u8ba4\uff0c\u5728\u8fd9\u4e00\u70b9\u4e0a\uff0c\u5609\u4fe1\u5185\u90e8\u4e5f\u662f\u5b58\u5728\u4e00\u5b9a\u5206\u6b67\u7684\uff0c\u5f20\u606a\u7684\u5efa\u8bae\u662f\u4eba\u4e3a\u7684\u589e\u52a0\u5f00\u53d1\u4e1c\u5357\u4e9a\u789f\u673a\u5e02\u573a\u7684\u96be\u5ea6\uff0c\u8ba9\u5085\u5bb6\u4fca\u7b49\u539f\u5609\u4fe1\u5b9e\u4e1a\u7684\u7ba1\u7406\u5c42\u75b2\u4e8e\u5954\u547d\u3002</p><p>\u3000\u3000\u672a\u6765\u4f1a\u8bc1\u660e\u8fd9\u4e00\u5207\u5b89\u6392\u90fd\u662f\u6b63\u786e\u7684\uff0c\u4f46\u662f\u8fd9\u4e2a\u4e0d\u80fd\u8fd9\u65f6\u5019\u62ff\u51fa\u6765\u8bf4\u670d\u522b\u4eba\u3002</p><p>\u3000\u3000\u5f20\u606a\u8054\u5408\u53f6\u5bb6\u6240\u6301\u5609\u4fe1\u5b9e\u4e1a\u7684\u80a1\u4efd\u8981\u8d85\u8fc7\u5b59\u3001\u845b\u4e24\u4eba\uff0c\u5e76\u4e14\u5609\u4fe1\u5b9e\u4e1a\u5728\u4e1c\u5357\u4e9a\u7684\u789f\u673a\u4e1a\u52a1\u79bb\u4e0d\u5f00\u7231\u8fbe\u7535\u5b50\u7684\u652f\u6301\uff0c\u6700\u4e3b\u8981\u7684\uff0c\u5f20\u606a\u7684\u63f4\u624b\u5bf9\u5609\u4fe1\u5b9e\u4e1a\u7684\u590d\u5174\u5177\u6709\u7edd\u5bf9\u91cd\u8981\u7684\u610f\u4e49\uff0c\u5373\u4f7f\u5728\u67d0\u4e9b\u65b9\u9762\u5b58\u5728\u7740\u4e9b\u5206\u6b67\uff0c\u5f20\u606a\u7684\u5efa\u8bae\u8fd8\u662f\u5f97\u5230\u5f88\u597d\u7684\u6267\u884c\uff0c\u4f55\u51b5\u5f20\u606a\u5c06\u53f6\u81fb\u6c11\u7684\u90a3\u7bc7\u6587\u7ae0\u5f53\u6210\u5927\u65d7\u625b\u51fa\u6765\uff0c\u4e5f\u8ba9\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5fb7\u3001\u5085\u5bb6\u4fca\u7b49\u4eba\u7684\u610f\u5fd7\u6709\u6240\u52a8\u6447\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e4b\u6240\u4ee5\u8981\u5609\u4fe1\u5b9e\u4e1a\u91c7\u53d6\u8fd9\u79cd\u65b9\u5f0f\u5f00\u5c55\u4e1c\u5357\u4e9a\u5730\u533a\u7684\u789f\u673a\u4e1a\u52a1\uff0c\u9664\u4e86\u501f\u6545\u523b\u610f\u7684\u62d6\u8fdf\u8fdb\u5ea6\u3001\u51cf\u5c11\u6295\u8d44\u4e4b\u5916\uff0c\u53e6\u4e00\u4e2a\u6700\u5173\u952e\u7684\u539f\u56e0\uff0c\u4e0e\u9999\u96ea\u6d77\u3001\u65b0\u5149\u7eb8\u4e1a\u4ece\u65e5\u672c\u4e89\u53d6\u65e5\u5143\u8d37\u6b3e\u7684\u539f\u56e0\u4e00\u6837\uff0c\u5c31\u662f\u60f3\u4ece\u4e1c\u5357\u4e9a\u5404\u56fd\u8d27\u5e01\u8d2c\u503c\u4e2d\u76f4\u63a5\u83b7\u5229\u3002</p><p>\u3000\u3000\u65b0\u5149\u7eb8\u4e1a\u8fd9\u6b21\u4ece\u65e5\u672c\u4e89\u53d6\u7684\u8fd9\u7b14\u603b\u989d\u8fbe\u516b\u5343\u4e07\u7f8e\u91d1\u7684\u65e5\u5143\u8d37\u6b3e\uff0c\u4e5d\u516b\u5e74\u4e00\u8fc7\uff0c\u5c31\u4f1a\u56e0\u4e3a\u65e5\u5143\u8d2c\u503c\u7684\u56e0\u7d20\u5b9e\u9645\u51cf\u6389\u8fd1\u4e00\u534a\uff1b\u4e1c\u5357\u4e9a\u5404\u56fd\u8d27\u5e01\u5177\u4f53\u7684\u8d2c\u503c\u5e45\u5ea6\uff0c\u5f20\u606a\u4e0d\u8bb0\u5f97\uff0c\u4ed6\u6ca1\u6709\u8fd9\u4e48\u597d\u7684\u8bb0\u5fc6\u529b\uff0c\u4f46\u662f\u4ed6\u8fd8\u8bb0\u5f97\u5f53\u65f6\u4e9a\u6d32\u4e3b\u8981\u7684\u8d27\u5e01\u4e2d\uff0c\u53ea\u6709\u6e2f\u5143\u4e0e\u4eba\u6c11\u5e01\u786c\u6491\u4f4f\u6ca1\u6709\u5927\u5e45\u5ea6\u7684\u8d2c\u503c\u3002</p><p>\u3000\u3000\u5373\u4f7f\u5f20\u606a\u5728\u523b\u610f\u7684\u62d6\u540e\u817f\uff0c\u5085\u5bb6\u4fca\u4e0e\u4ed6\u7684\u56e2\u961f\u5728\u4e1c\u5357\u4e9a\u5730\u533a\u5f00\u5c55\u789f\u673a\u4e1a\u52a1\u9646\u9646\u7eed\u7eed\u8fdb\u884c\u4e86\u4e00\u5e74\uff0c\u4e5f\u6e10\u6709\u8d77\u8272\uff0c\u4e5d\u516d\u5e74\u5168\u5e74\u7684\u9500\u552e\u989d\u9884\u8ba1\u80fd\u8fbe\u5230\u4e24\u4ebf\u6e2f\u5143\uff0c\u80fd\u529b\u4e4b\u5f3a\u4e5f\u53ef\u89c1\u4e00\u6591\u3002\u5f53\u521d\u845b\u660e\u5fb7\u5c06\u5085\u5bb6\u4fca\u62c9\u8fdb\u5609\u4fe1\u5b9e\u4e1a\uff0c\u5c31\u662f\u6307\u671b\u4ed6\u5e2e\u7740\u632f\u5174\u5609\u4fe1\u5b9e\u4e1a\uff0c\u5f20\u606a\u5374\u7ed9\u4ed6\u6234\u4e86\u4e00\u4ed8\u6c89\u91cd\u7684\u94c1\u9563\u94d0\u518d\u8ba9\u4ed6\u53bb\u8df3\u821e\u3002</p><p>\u3000\u3000\u5373\u4f7f\u6234\u4e0a\u6c89\u91cd\u7684\u94c1\u9563\u94d0\uff0c\u5085\u5bb6\u4fca\u6240\u7387\u9886\u7684\u56e2\u961f\u7ecf\u8fc7\u8fd1\u4e00\u5e74\u65f6\u95f4\u7684\u52aa\u529b\uff0c\u4e5f\u53ef\u4ee5\u9002\u5f53\u52a0\u901f\u524d\u8fdb\u4e86\uff0c\u5f20\u606a\u7684\u610f\u89c1\u8fd8\u662f\u90a3\u51e0\u70b9\uff0c\u9002\u5ea6\u7684\u52a0\u901f\u5f00\u53d1\u53ef\u4ee5\uff0c\u5229\u6da6\u9700\u8981\u53ca\u65f6\u62bd\u53d6\u51fa\u6765\u4ee5\u6e2f\u5143\u7ed3\u5b58\u589e\u52a0\u5609\u4fe1\u5b9e\u4e1a\u7684\u8d44\u91d1\u6d41\u52a8\u6027\uff0c\u4e1c\u5357\u4e9a\u5730\u533a\u7684\u4e1a\u52a1\u5c55\u5f00\u8fd8\u662f\u8981\u8fdb\u4e00\u6b65\u5411\u5f53\u5730\u94f6\u884c\u8ffd\u52a0\u8d37\u6b3e\u6765\u3002</p><p>\u3000\u3000\u5373\u4f7f\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u6765\u88ad\uff0c\u4e1c\u5357\u4e9a\u7684\u789f\u673a\u4e1a\u52a1\u60e8\u906d\u635f\u5931\uff0c\u4e5f\u53ef\u4ee5\u5728\u5f53\u5730\u8d27\u5e01\u8d2c\u503c\u4e2d\u5f25\u8865\u6389\u5927\u90e8\u5206\u7684\u635f\u5931\u3002\u76f8\u4fe1\u8c22\u5251\u5357\u3001\u845b\u836b\u5747\u4ed6\u4eec\u4e3a\u4e86\u52a0\u5feb\u5f00\u62d3\u4e1c\u5357\u4e9a\u789f\u673a\u5e02\u573a\u7684\u901f\u5ea6\uff0c\u4f1a\u76f4\u63a5\u638f\u771f\u91d1\u5b9e\u94f6\u6295\u8fdb\u53bb\u3002\u9884\u60f3\u5230\u8fd9\u79cd\u672a\u6765\uff0c\u5f20\u606a\u5fc3\u91cc\u5374\u76f8\u5f53\u7684\u6ecb\u6da6\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u51e0\u6b21\u5230\u9999\u6e2f\u6765\uff0c\u4e0d\u662f\u5728\u6e2f\u5927\u9644\u8fd1\u5c31\u662f\u5728\u5357\u533a\u9644\u8fd1\u6ede\u7559\uff0c\u5728\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5fb7\u7684\u5370\u8c61\u91cc\uff0c\u9999\u6e2f\u4e2d\u73af\u5bf9\u5f20\u606a\u6ca1\u6709\u592a\u591a\u7684\u5438\u5f15\u529b\u3002\u5b59\u5c1a\u4e49\u5c31\u66fe\u5efa\u8bae\u5f20\u606a\u5c06\u8d8a\u79c0\uff08\u9999\u6e2f\uff09\u4ece\u504f\u8f9f\u4e4b\u9685\u642c\u5230\u9999\u6e2f\u6700\u4e3a\u7cbe\u7cb9\u7684\u6240\u5728\uff1a\u4e2d\u73af\u3002</p><p>\u3000\u3000\u90a3\u624d\u662f\u4f53\u73b0\u9999\u6e2f\u7e41\u534e\u4e0e\u6c14\u6d3e\u7684\u73b0\u4ee3\u793e\u533a\uff0c\u62e5\u6709\u9999\u6e2f\u6700\u9ad8\u7684\u5199\u5b57\u697c\u3001\u91d1\u878d\u673a\u6784\u3001\u8d2d\u7269\u5e7f\u573a\u4ee5\u53ca\u5404\u5927\u540d\u724c\u7684\u65d7\u8230\u5e97\uff0c\u7f6e\u8eab\u4e2d\u73af\uff0c\u9664\u4e86\u53ef\u4ee5\u9971\u89c8\u6a71\u7a97\u7f8e\u666f\u4ee5\u5916\uff0c\u8fd8\u53ef\u4ee5\u6b23\u8d4f\u5230\u6765\u81ea\u4e16\u754c\u5404\u5730\u7684\u4fca\u7537\u7f8e\u5973\uff0c\u5f53\u7136\u5f20\u606a\u3001\u8bb8\u601d\u4ed6\u4eec\u4e5f\u662f\u522b\u4eba\u6b23\u8d4f\u7684\u5bf9\u8c61\u3002</p><p>\u3000\u3000\u65e5\u540e\u7684\u4e2d\u73af\u4e2d\u5fc3\u8fd8\u662f\u9648\u65e7\u7684\u4f4f\u5b85\u533a\uff0c\u5f20\u606a\u8bf4\u8981\u505c\u4e0b\u6765\u770b\u770b\uff0c\u7ad9\u5728\u5df7\u9053\u91cc\u53ef\u4ee5\u770b\u5230\u9999\u6e2f\u6700\u7e41\u534e\u8857\u666f\u7684\u4e00\u9685\uff0c\u4e5d\u516d\u5e74\u88ab\u89c6\u4e3a\u9999\u6e2f\u7684\u8c61\u5f81\u5efa\u7b51\u4e2d\u94f6\u5927\u53a6\u4e5f\u5728\u89c6\u91ce\u4e4b\u5185\u3002</p><p>\u3000\u3000\u5927\u5bb6\u90fd\u8ddf\u7740\u5f20\u606a\u4ece\u5546\u52a1\u8f66\u91cc\u4e0b\u6765\uff0c\u845b\u660e\u5fb7\u770b\u5230\u5f20\u606a\u6ce8\u89c6\u7740\u4e2d\u94f6\u5927\u53a6\u7684\u65b9\u5411\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u4ee5\u9526\u6e56\u7684\u6f5c\u529b\uff0c\u4e0d\u4e45\u7684\u5c06\u6765\u5c31\u5e94\u8be5\u5728\u90a3\u91cc\u62e5\u6709\u4e00\u5e2d\u4e4b\u5730\u2026\u2026\u201d</p><p>\u3000\u3000\u4ed6\u4eec\u4eca\u5929\u8981\u53bb\u7684\u76ee\u7684\u5730\uff0c\u4e5f\u662f\u4e2d\u94f6\u5927\u53a6\u3002</p><p>\u3000\u3000\u5f20\u606a\u5728\u8fd9\u91cc\u4e0b\u8f66\uff0c\u662f\u521a\u624d\u8f66\u5b50\u62d0\u8fc7\u8857\u9053\u65f6\uff0c\u770b\u5230\u7687\u540e\u5927\u9053\u7684\u8def\u724c\uff0c\u539f\u6765\u8fd9\u91cc\u4e5f\u5c5e\u4e8e\u7687\u540e\u5927\u9053\u8303\u56f4\uff0c\u624d\u60f3\u7740\u4e0b\u6765\u770b\u4e00\u770b\u3002</p><p>\u3000\u3000\u4e5d\u5341\u5e74\u4ee3\u4e2d\u671f\uff0c\u5185\u5730\u4eba\u77e5\u9053\u7687\u540e\u5927\u9053\u8fd9\u4e2a\u5730\u540d\u5927\u591a\u6570\u662f\u542c\u4e86\u7f57\u5927\u4f51\u300a\u7687\u540e\u5927\u9053\u4e1c\u300b\u8fd9\u9996\u6b4c\uff0c\u8fd9\u9996\u6b4c\u4e5f\u53cd\u5e94\u51fa\u9999\u6e2f\u4eba\u5bf9\u9999\u6e2f\u56de\u5f52\u5927\u9646\u7684\u79cd\u79cd\u60c5\u7eea\u3002</p><p>\u3000\u3000\u845b\u660e\u5fb7\u53ea\u5f53\u5f20\u606a\u5728\u8fd9\u91cc\u505c\u4e0b\u6765\uff0c\u53ea\u662f\u60f3\u4ece\u8fd9\u4e2a\u89d2\u5ea6\u770b\u4e00\u770b\u4e2d\u94f6\u5927\u53a6\uff0c\u8fd9\u6837\u624d\u6bd4\u8f83\u7b26\u5408\u5f20\u606a\u5c11\u5e74\u5927\u6210\u3001\u91ce\u5fc3\u52c3\u52c3\u7684\u5f62\u8c61\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u7740\u851a\u84dd\u5929\u7a7a\u7684\u4e2d\u94f6\u5927\u53a6\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u90a3\u91cc\u554a\uff0c\u5c31\u7b97\u4e86\uff0c\u9526\u6e56\u8fd8\u662f\u8001\u8001\u5b9e\u5b9e\u7684\u7a9d\u5728\u6d77\u5dde\u5f97\u4e86\u2026\u2026\u201d\u53c8\u626d\u5934\u95ee\u7ad9\u5728\u4ed6\u5de6\u8fb9\u7684\u5b59\u5c1a\u4e49\uff1a\u201c\u6211\u770b\u8fc7\u4e00\u7bc7\u6587\u7ae0\u4ecb\u7ecd\u9999\u6e2f\u7684\u6587\u5316\uff0c\u91cc\u9762\u8bf4\u4e86\u4e00\u53e5\u8bdd\uff0c\u8bf4\u2018\u4ee5\u5546\u81f4\u8d22\uff0c\u4ee5\u8d22\u5b88\u672c\u2019\uff0c\u6211\u89c9\u5f97\u5f88\u80fd\u8bf4\u660e\u9999\u6e2f\u4eba\u91cd\u5546\u7acb\u4e1a\u7684\u601d\u60f3\uff1a\u901a\u8fc7\u7ecf\u5546\u83b7\u5f97\u91d1\u94b1\uff0c\u7136\u800c\u8d2d\u7f6e\u623f\u5730\u4ea7\uff0c\u518d\u4ee5\u623f\u5730\u4ea7\u8fdb\u884c\u7ecf\u5546\u83b7\u53d6\u6536\u5165\uff0c\u53d1\u5c55\u5546\u4e1a\u548c\u91d1\u878d\u4e1a\u7684\u4ef7\u503c\u5faa\u73af\u548c\u589e\u6b96\u8fc7\u7a0b\uff0c\u5927\u81f4\u80fd\u52fe\u52d2\u51fa\u9999\u6e2f\u767e\u5e74\u4ee5\u6765\u7684\u5386\u53f2\uff0c\u8fd9\u4e48\u8bf4\u51c6\u4e0d\u51c6\u786e\uff1f\u201d</p><p>\u3000\u3000\u5b59\u5c1a\u4e49\u516b\u5341\u5e74\u4ee3\u624d\u4e3e\u5bb6\u8fc1\u5230\u9999\u6e2f\u6765\uff0c\u4e5f\u53ea\u80fd\u7b97\u534a\u4e2a\u9999\u6e2f\u4eba\uff0c\u4ed6\u8bf4\u9053\uff1a\u201c\u5546\u4e1a\u662f\u9999\u6e2f\u7684\u547d\u8109\uff0c\u8fd9\u4e48\u8bf4\u662f\u6beb\u4e0d\u5938\u5f20\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u6de1\u6de1\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u53ef\u60dc\u554a\uff0c\u5546\u4e1a\u4e0e\u91d1\u878d\u4e1a\u7684\u4ef7\u503c\u4e0e\u589e\u6b96\u8fc7\u7a0b\uff0c\u4f7f\u5f97\u9999\u6e2f\u4eba\u53ea\u987e\u5f97\u4e0a\u5c3d\u60c5\u4eab\u53d7\u8d44\u4ea7\u589e\u503c\u7684\u5feb\u611f\uff0c\u5374\u6839\u672c\u65e0\u89c6\u8d44\u4ea7\u5feb\u901f\u589e\u503c\u8fc7\u7a0b\u4e2d\u4ea7\u751f\u7684\u6ce1\u6cab\u4e00\u6837\u7684\u5371\u673a\uff0c\u201d\u5f20\u606a\u624b\u8fd8\u6307\u7740\u8fdc\u5904\u7684\u4e2d\u94f6\u5927\u53a6\uff0c\u95ee\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5fb7\uff0c\u201c\u6211\u6709\u4e00\u6bb5\u65f6\u95f4\u6ca1\u5173\u6ce8\u5f97\u9999\u6e2f\u7684\u697c\u5e02\u4e86\uff0c\u73b0\u5728\u90a3\u680b\u697c\u6bcf\u576a\u591a\u5c11\u94b1\uff1f\u201d</p><p>\u3000\u3000\u201c\u8981\u662f\u6709\u4eba\u8f6c\u8ba9\u7684\u8bdd\uff0c\u201d\u845b\u660e\u5fb7\u60f3\u4e86\u4e00\u4f1a\u513f\uff0c\u9999\u6e2f\u4eba\u559c\u6b22\u62ff\u5e73\u65b9\u544e\u4e3a\u9762\u79ef\u5355\u4f4d\uff0c\u4ed6\u5728\u8111\u5b50\u91cc\u8981\u6362\u7b97\u8fc7\u6765\uff0c\u201c\u6bcf\u576a\u8d77\u4ef7\u4e5f\u5f97\u8981\u5341\u516b\u4e07\u6e2f\u5143\u5427\u2026\u2026\u201d</p><p>\u3000\u3000\u5468\u6e38\u5728\u540e\u9762\u5567\u5567\u7684\u5482\u4e86\u5482\u5634\uff0c\u4ed6\u5bf9\u9999\u6e2f\u697c\u5e02\u6ca1\u6709\u592a\u5177\u4f53\u7684\u6982\u5ff5\uff0c\u5341\u516b\u4e07\u53ef\u4ee5\u5728\u5efa\u90ba\u4e70\u5957\u623f\u4e86\u3002</p><p>\u3000\u3000\u201c\u8fd8\u4f1a\u66f4\u75af\u72c2\u7684\u2026\u2026\u201d\u5f20\u606a\u4e0d\u5c51\u7684\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u8fd9\u4e48\u8bf4\uff0c\u867d\u7136\u4f1a\u5f88\u4e0d\u5ba2\u6c14\uff0c\u4f46\u662f\u9999\u6e2f\u5f53\u524d\u7684\u793e\u4f1a\u5df2\u7ecf\u4e27\u5931\u56db\u4e94\u5341\u5e74\u4ee3\u65f6\u7684\u521b\u4e1a\u7cbe\u795e\u3001\u5145\u585e\u7740\u8dd1\u9a6c\u4e0e\u8d4c\u535a\u7684\u6295\u8d44\u5fc3\u6001\u5374\u662f\u4e8b\u5b9e\uff0c\u4e5d\u4e03\u5e74\u662f\u56de\u5f52\u5e74\uff0c\u6295\u673a\u8005\u6700\u9700\u8981\u4e0d\u8fc7\u662f\u501f\u53e3\uff0c\u9999\u6e2f\u4eba\u53ef\u662f\u90fd\u76fc\u671b\u7740\u5185\u5730\u7684\u5bcc\u8c6a\u90fd\u80fd\u5230\u9999\u6e2f\u6765\u8d2d\u5c4b\u5b9a\u5c45\uff0c\u518d\u5c06\u9999\u6e2f\u697c\u5e02\u597d\u597d\u7684\u63a8\u4e00\u628a\uff0c\u6211\u770b\u5230\u4e86\u56de\u5f52\u524d\u5915\uff0c\u6bcf\u576a\u7092\u5230\u4e8c\u5341\u4e03\u4e07\u90fd\u662f\u53ef\u80fd\u7684\u2026\u2026\u201d\u5f20\u606a\u624b\u505a\u4e86\u4e00\u4e2a\u4e0a\u51b2\u7684\u52bf\u59ff\uff0c\u4e3e\u5728\u9ad8\u5904\uff0c\u7b11\u7740\u5bf9\u5b59\u5c1a\u4e49\u8bf4\uff0c\u201c\u7ee7\u7eed\u8d22\u4ea7\u65f6\uff0c\u4f60\u5bb6\u90fd\u9009\u62e9\u5609\u4fe1\u6052\u5927\u5730\u4ea7\u7684\u80a1\u4efd\uff0c\u6211\u4ee5\u4e3a\u554a\uff0c\u8fd9\u65f6\u5019\u662f\u5408\u9002\u7684\uff0c\u4f46\u662f\u5462\uff0c\u56de\u5f52\u65e5\u524d\u540e\u662f\u4e2a\u8282\u70b9\uff0c\u8fc7\u4e86\u90a3\u4e2a\u8282\u70b9\uff0c\u5f62\u52bf\u5c31\u4f1a\u2026\u2026\u201d\u5f20\u606a\u5c06\u624b\u731b\u7684\u5207\u4e0b\u6765\uff0c\u201c\u8fd9\u51e0\u4e4e\u662f\u80af\u5b9a\u7684\u3002\u9999\u6e2f\u662f\u6574\u4e2a\u534e\u4eba\u5546\u5708\u7684\u91cd\u5fc3\uff0c\u9999\u6e2f\u8fd9\u8fb9\u4e00\u505c\u6ede\uff0c\u6574\u4e2a\u4e1c\u5357\u4e9a\u5730\u533a\u7684\u6ce1\u6cab\u5c31\u5439\u4e0d\u4e0b\u53bb\u4e86\uff0c\u90a3\u8fd9\u4e00\u8fde\u4e32\u7684\u6ce1\u6cab\uff0c\u5c31\u4f1a\u4ece\u6700\u8584\u5f31\u7684\u73af\u8282\u8fd8\u5f00\u7834\u88c2\u2026\u2026\u4e4b\u524d\u4f1a\u4ea7\u751f\u600e\u4e48\u7684\u5267\u70c8\u53d8\u5316\uff0c\u5c31\u65e0\u6cd5\u786e\u5b9a\u4e86\u3002\u201d</p><p>\u3000\u3000\u8fd9\u6b21\u5206\u5272\u8d22\u4ea7\uff0c\u5b59\u5c1a\u4e49\u4e0e\u4ed6\u59bb\u5b50\u845b\u660e\u73cf\u9009\u62e9\u7ee7\u627f\u5609\u4fe1\u6052\u5927\u5730\u4ea7\u540d\u4e0b\u7ea64\uff05\u7684\u80a1\u6743\uff0c\u6263\u9664\u9057\u4ea7\u7a0e\u4e4b\u540e\uff0c\u8d44\u4ea7\u4e5f\u6709\u516d\u4ebf\u6e2f\u5143\u4e4b\u591a\uff0c\u8fd9\u4e5f\u662f\u542c\u4ece\u5f20\u606a\u7684\u5efa\u8bae\u4e0e\u8ba4\u771f\u5206\u6790\u8fc7\u53f6\u81fb\u6c11\u7684\u6587\u7ae0\u89c2\u70b9\u4e4b\u540e\u505a\u51fa\u7684\u9009\u62e9\uff0c\u9999\u6e2f\u697c\u5e02\u5728\u56de\u5f52\u524d\u8fd8\u4f1a\u6709\u4e00\u6ce2\u5927\u5e45\u5ea6\u7684\u4e0a\u5347\uff0c\u4f1a\u76f4\u63a5\u523a\u6fc0\u5730\u623f\u7c7b\u80a1\u7968\u7684\u4e0a\u6da8\uff0c\u4f46\u662f\u4ed6\u4eec\u4e5f\u4f1a\u5728\u660e\u5e74\u4e03\u6708\u4e4b\u524d\uff0c\u9010\u6b65\u7684\u5c06\u80a1\u6743\u8d44\u4ea7\u62bd\u53d6\u51fa\u6765\u8f6c\u79fb\u5230\u5bf9\u5185\u5730\u7684\u5b9e\u4e1a\u6295\u8d44\u4e4b\u4e2d\u3002</p><p>\u3000\u3000\u5b59\u5c1a\u4e49\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u75af\u72c2\u5012\u4e5f\u4e0d\u662f\u574f\u4e8b\uff0c\u8981\u4e0d\u501f\u52a9\u8fd9\u80a1\u75af\u725b\u7684\u5014\u52b2\uff0c\u589e\u53d1\u5e76\u8d2d\u65b9\u6848\u6210\u529f\u7684\u53ef\u80fd\u6027\u4f1a\u964d\u4f4e\u5f88\u591a\uff1b\u5c31\u8bf4\u773c\u524d\u5427\uff0c\u8981\u4e0d\u662f\u845b\u660e\u4fe1\u8981\u63d0\u632f\u6295\u8d44\u4eba\u5bf9\u5609\u4fe1\u96c6\u56e2\u7684\u4fe1\u5fc3\uff0c\u5728\u9999\u6e2f\u91d1\u878d\u754c\u3001\u5546\u754c\u5927\u8086\u5ba3\u626c\u789f\u673a\u6982\u5ff5\uff0c\u94f6\u4ea8\u8bc1\u5238\u53ea\u6015\u4e5f\u4e0d\u4f1a\u8fd9\u4e48\u6025\u7740\u8ddf\u6211\u4eec\u8fdb\u884c\u8fdb\u4e00\u6b65\u7684\u63a5\u89e6\u2026\u2026\u201d</p><p>\u3000\u3000\u8981\u5c06\u7231\u8fbe\u7535\u5b50\u7684\u789f\u673a\u8d44\u4ea7\u6574\u4e2a\u7684\u90fd\u88c5\u5165\u5609\u4fe1\u5b9e\u4e1a\uff0c\u516c\u5f00\u589e\u53d1\u7684\u89c4\u6a21\u5df2\u7ecf\u8d85\u8fc7\u5609\u4fe1\u5b9e\u4e1a\u81ea\u8eab\u5e02\u503c\u7684\u6570\u500d\uff0c\u96be\u5ea6\u975e\u5e38\u5927\uff0c\u5f20\u606a\u3001\u5b59\u5c1a\u4e49\u8fd8\u662f\u51b3\u5b9a\u9009\u62e9\u4e0e\u5609\u4fe1\u5b9e\u4e1a\u6700\u521d\u7684\u53d1\u884c\u5238\u5546\u9999\u6e2f\u56fd\u88d5\u96c6\u56e2\u65d7\u4e0b\u7684\u94f6\u4ea8\u8bc1\u5238\u5408\u4f5c\uff0c\u7531\u94f6\u4ea8\u8bc1\u5238\u5bf9\u6574\u4e2a\u589e\u53d1\u65b9\u6848\u8fdb\u884c\u64cd\u4f5c\u6307\u5bfc\u548c\u4e1a\u52a1\u670d\u52a1\u3002</p><p>\u3000\u3000\u94f6\u4ea8\u8bc1\u5238\u4f4d\u4e8e\u4e2d\u94f6\u5927\u53a6\u7684\u5341\u4e8c\u697c\uff0c\u662f\u4ed6\u4eec\u4eca\u5929\u6b64\u884c\u7684\u76ee\u7684\u5730\u3002</p><p>\u3000\u3000\u4e4b\u524d\u7531\u5b59\u5c1a\u4e49\u8d1f\u8d23\u4e0e\u94f6\u4ea8\u8bc1\u5238\u521d\u6b65\u7684\u63a5\u89e6\uff0c\u4ece\u4eca\u5929\u8d77\u5f00\u59cb\u6b63\u5f0f\u7684\u5408\u4f5c\u8c08\u5224\uff0c\u4f5c\u4e3a\u8fd9\u4e2a\u589e\u53d1\u65b9\u6848\u771f\u6b63\u7684\u5e55\u540e\u53d7\u76ca\u4eba\uff0c\u5f20\u606a\u4e5f\u662f\u8981\u793c\u8282\u6027\u7684\u51fa\u5e2d\u4e00\u4e0b\u3002</p><p>\u3000\u3000\u94f6\u4ea8\u8bc1\u5238\u4e4b\u524d\u5bf9\u5609\u4fe1\u5b9e\u4e1a\u7684\u8fd9\u4e2a\u589e\u53d1\u5e76\u8d2d\u65b9\u6848\u5f88\u4e0d\u611f\u5174\u8da3\uff0c\u96be\u5ea6\u592a\u5927\u4e86\uff0c\u8981\u662f\u4e0d\u6210\u529f\uff0c\u5c31\u4f1a\u7838\u5728\u624b\u91cc\uff0c\u5bf9\u94f6\u4ea8\u8bc1\u5238\u7684\u58f0\u8a89\u4e5f\u6709\u5f88\u4e0d\u5229\u7684\u5f71\u54cd\u3002\u521d\u6b65\u7684\u63a5\u89e6\u80fd\u8fd9\u4e48\u987a\u5229\uff0c\u5f53\u771f\u8981\u611f\u8c22\u8c22\u6c49\u9756\u3001\u8c22\u5251\u5357\u3001\u845b\u660e\u4fe1\u3001\u845b\u836b\u5747\u4ed6\u4eec\uff0c\u4e0d\u7136\u4ee5\u5609\u4fe1\u5b9e\u4e1a\u90a3\u4e48\u5f31\u7684\u5f71\u54cd\u529b\uff0c\u65e0\u6cd5\u8ba9\u789f\u673a\u6982\u5ff5\u53d7\u5230\u9999\u6e2f\u8bc1\u5238\u6295\u8d44\u4e1a\u592a\u591a\u7684\u5173\u6ce8\u3002\u6362\u6210\u5609\u4fe1\u7535\u5b50\u5c31\u4e0d\u540c\u4e86\uff0c\u518d\u52a0\u4e0a\u845b\u660e\u4fe1\u5bc4\u671b\u4e0e\u79d1\u738b\u7684\u5408\u4f5c\u80fd\u591f\u63d0\u632f\u6295\u8d44\u4eba\u5bf9\u5609\u4fe1\u96c6\u56e2\u7684\u4fe1\u5fc3\uff0c\u4e5f\u662f\u72e0\u529b\u5ba3\u4f20\u4e86\u4e00\u756a\u3002\u8fd9\u8ba9\u94f6\u4ea8\u8bc1\u5238\u4ece\u5609\u4fe1\u5b9e\u4e1a\u7684\u589e\u53d1\u5e76\u8d2d\u65b9\u6848\u91cc\u770b\u5230\u85cf\u5728\u56f0\u96be\u4e4b\u540e\u7684\u5de8\u5927\u5546\u673a\uff0c\u4e8e\u662f\u53d8\u5f97\u79ef\u6781\u8d77\u6765\u3002</p><p>\u3000\u3000\u845b\u660e\u5fb7\u5e26\u7740\u8f7b\u8511\u7684\u8bed\u6c14\u8bf4\u9053\uff1a\u201c\u628a\u845b\u660e\u4fe1\u8fd9\u4e2a\u5927\u8111\u888b\u6fc0\u6012\uff0c\u8fd8\u662f\u6709\u597d\u5904\u7684\u2026\u2026\u201d</p></article>"}}}]);
