(window.webpackJsonp=window.webpackJsonp||[]).push([[478],{500:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u8282 \u7d2b\u6587",content:"<article><h2>\u7b2c\u5341\u8282 \u7d2b\u6587</h2><p>\u4e16\u754c\u4e0a\u7d2b\u53d1\u7684\u4eba\u5f88\u5c11\uff0c\u7d2b\u53d1\u7684\u5973\u5b50\u66f4\u5c11\uff0c\u6709\u8fd9\u7eda\u4e3d\u593a\u76ee\u7684\u7d2b\u53d1\uff0c\u5e76\u4e14\u80fd\u591f\u957f\u5230\u76f4\u5782\u811a\u8e1d\u7684\u5973\u5b50\uff0c\u4f30\u8ba1\u5168\u4e16\u754c\u4e5f\u6ca1\u51e0\u4f4d\uff0c\u800c\u743c\u6069\u6070\u6070\u5c31\u8ba4\u8bc6\u4e00\u4e2a\uff0c\u524d\u4e0d\u4e45\u624d\u521a\u521a\u6253\u8fc7\u4ea4\u9053\u3002</p><p>\u6563\u63d0\u5c14\u5821\u7684\u519b\u4e8b\u7edf\u5e05\u6b4c\u66e6\u96c5\u3002</p><p>\u5979\u5148\u662f\u7387\u519b\u653b\u4e0b\u4e86\u8fdc\u5c71\u5e02\uff0c\u743c\u6069\u5f53\u65f6\u5728\u57ce\u5899\u4e0a\uff0c\u548c\u5979\u8fdc\u8fdc\u6253\u8fc7\u7167\u9762\uff0c\u8fd8\u89c9\u5f97\u5979\u7684\u6c14\u8d28\u548c\u6885\u83f2\u65af\u5f88\u6709\u8fd1\u4f3c\u4e4b\u5904\u3002\u540e\u6765\u6b4c\u66e6\u96c5\u8ffd\u6740\u8fdc\u5c71\u5e02\u7684\u80d6\u57ce\u4e3b\u5f17\u96f7\u65af\uff0c\u548c\u743c\u6069\u6253\u4e86\u4e00\u67b6\uff0c\u6700\u7ec8\u88ab\u5f71\u706b\u6240\u4f24\uff0c\u6218\u8d25\u9000\u8d70\u3002\u6b64\u540e\u743c\u6069\u8fdb\u5165\u6851\u6bd4\u4e9a\u5883\u5185\uff0c\u53cc\u65b9\u4fbf\u518d\u6ca1\u89c1\u8fc7\uff0c\u6ca1\u60f3\u5230\u8f6c\u6765\u8f6c\u53bb\uff0c\u5728\u8fd9\u91cc\u53c8\u78b0\u4e0a\u4e86\u3002</p><p>\u201c\u5979\u6765\u8fd9\u91cc\u505a\u4ec0\u4e48\uff1f\u201d</p><p>\u743c\u6069\u770b\u4e86\u770b\u5979\u7684\u5468\u56f4\uff0c\u6ca1\u53d1\u73b0\u6709\u4ec0\u4e48\u4f8d\u536b\u968f\u4ece\uff0c\u4e5f\u6ca1\u770b\u89c1\u5979\u90a3\u5339\u9ed1\u8272\u72ec\u89d2\u517d\uff0c\u50cf\u662f\u5355\u8eab\u4e00\u4eba\uff0c\u6b63\u4e13\u5fc3\u81f4\u5fd7\u5730\u6311\u9009\u4e00\u4e9b\u5973\u5b69\u5b50\u7528\u7684\u9970\u54c1\u3002\u5ffd\u7136\u4e4b\u95f4\uff0c\u5979\u50cf\u662f\u611f\u5e94\u5230\u88ab\u4eba\u7684\u76ee\u5149\u6ce8\u89c6\u4f3c\u7684\uff0c\u8f6c\u8eab\u671d\u8fd9\u8fb9\u671b\u8fc7\u6765\uff0c\u743c\u6069\u5413\u4e86\u4e00\u8df3\uff0c\u8fde\u5fd9\u4fa7\u8fc7\u8eab\uff0c\u8eb2\u5728\u8f66\u53a2\u58c1\u540e\u3002\u7d2b\u53d1\u5973\u5b50\u770b\u4e86\u9a6c\u8f66\u4e24\u773c\uff0c\u4f3c\u4e4e\u6ca1\u53d1\u73b0\u4ec0\u4e48\u5f02\u72b6\uff0c\u4fbf\u56de\u8eab\u7ee7\u7eed\u6311\u9009\uff0c\u6700\u7ec8\u4e70\u4e86\u51e0\u4ef6\u4e1c\u897f\uff0c\u5305\u597d\u4e4b\u540e\u4ed8\u6b3e\uff0c\u7136\u540e\u79bb\u5f00\u3002</p><p>\u201c\u8ddf\u7740\u5979\u3002\u201d\u743c\u6069\u5bf9\u742a\u5a05\u8bf4\uff0c\u540e\u8005\u70b9\u70b9\u5934\uff0c\u77ac\u95f4\u53d8\u5316\u6210\u732b\u5f62\uff0c\u8dc3\u51fa\u8f66\u53a2\uff0c\u4e00\u8def\u5c3e\u968f\u800c\u53bb\u3002</p><p>\u521a\u624d\u7d2b\u53d1\u5973\u5b50\u8f6c\u8eab\u7684\u65f6\u5019\uff0c\u743c\u6069\u77a5\u89c1\u4e86\u9762\u5bb9\uff0c\u786e\u5b9a\u662f\u6b4c\u66e6\u96c5\u65e0\u7591\u3002\u5bf9\u4e8e\u5979\u4e3a\u4f55\u4f1a\u5728\u6b64\u5730\u51fa\u73b0\uff0c\u743c\u6069\u9887\u4e3a\u597d\u5947\uff0c\u751a\u81f3\u6000\u7591\u662f\u5426\u548c\u8def\u4e0a\u51fa\u73b0\u7684\u4ea1\u7075\u6709\u5173\uff0c\u8fd8\u662f\u8bf4\u6563\u5854\u6797\u4f1a\u653b\u5360\u8fdc\u5c71\u57ce\u4e4b\u540e\uff0c\u53c8\u5f00\u59cb\u6253\u5f13\u8c37\u7684\u4e3b\u610f\u4e86\uff1f\u672c\u60f3\u4e0d\u7ba1\u8fd9\u4e8b\uff0c\u8f6c\u5ff5\u4e00\u60f3\u5982\u679c\u6563\u5854\u6797\u4f1a\u5f53\u771f\u8981\u5bf9\u5f13\u8c37\u4e0d\u5229\uff0c\u5854\u745f\u8c37\u5c31\u5728\u4e34\u8fd1\uff0c\u52bf\u5fc5\u4f1a\u53d7\u5f71\u54cd\uff0c\u867d\u8bf4\u5854\u745f\u8c37\u4e5f\u4e0d\u662f\u4ed6\u5bb6\uff0c\u4f46\u5374\u662f\u6885\u83f2\u65af\u5bb6\u3002</p><p>\u8fd9\u79cd\u6f5c\u4f0f\u8ffd\u8e2a\u7684\u4e8b\u60c5\uff0c\u539f\u672c\u838e\u73de\u514b\u6700\u64c5\u957f\uff0c\u4f46\u5979\u548c\u6b4c\u66e6\u96c5\u4ea4\u8fc7\u624b\uff0c\u6253\u8fc7\u7167\u9762\uff0c\u4e07\u4e00\u88ab\u8ba4\u51fa\u6765\u5c31\u9ebb\u70e6\uff0c\u6b4c\u66e6\u96c5\u6709\u8fc7\u76ee\u4e0d\u5fd8\u4e4b\u80fd\uff0c\u743c\u6069\u662f\u4eb2\u8eab\u9886\u6559\u8fc7\u7684\uff0c\u6240\u4ee5\u53ea\u80fd\u8ba9\u732b\u5973\u53bb\u4e86\u3002</p><p>\u5927\u7ea6\u8fc7\u4e86\u5341\u591a\u5206\u949f\uff0c\u9ed1\u732b\u8fd4\u56de\u8f66\u4e2d\uff0c\u53d8\u56de\u4eba\u5f62\uff0c\u671d\u73ca\u5609\u8f7b\u8f7b\u53eb\u4e86\u51e0\u58f0\u3002\u5979\u4e0d\u662f\u54d1\u5df4\uff0c\u4f46\u4e0d\u4f1a\u8bf4\u8bdd\uff0c\u53ea\u80fd\u50cf\u732b\u4e00\u6837\u545c\u53eb\uff0c\u743c\u6069\u81ea\u7136\u662f\u542c\u4e0d\u61c2\u7684\uff0c\u4f46\u56e0\u4e3a\u5965\u5609\u83b1\u65af\u7684\u6cd5\u672f\uff0c\u73ca\u5609\u53ef\u4ee5\u76f4\u63a5\u660e\u767d\u610f\u601d\u3002\u201c\u90a3\u540d\u7d2b\u53d1\u5973\u5b50\u76f4\u63a5\u8fdb\u4e86\u9886\u4e3b\u5b98\u90b8\uff0c\u201d\u73ca\u5609\u7ffb\u8bd1\u7740\u732b\u8bed\uff0c\u201c\u95e8\u53e3\u536b\u5175\u6ca1\u6709\u963b\u62e6\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d</p><p>\u80fd\u591f\u81ea\u7531\u8fdb\u51fa\u5f13\u8c37\u9886\u4e3b\u7684\u5b98\u90b8\uff0c\u8bc1\u660e\u5173\u7cfb\u975e\u540c\u4e00\u822c\uff0c\u8fd9\u662f\u5355\u7eaf\u7684\u79c1\u4ea4\uff0c\u8fd8\u662f\u610f\u5473\u7740\u6563\u5854\u6797\u4f1a\u548c\u5f13\u8c37\u4e4b\u95f4\uff0c\u5df2\u7ecf\u4eb2\u5bc6\u5230\u4e86\u4e00\u5b9a\u7a0b\u5ea6\u5462\u3002</p><p>\u201c\u8d70\u5427\uff0c\u201d\u743c\u6069\u6700\u540e\u8bf4\uff0c\u201c\u5148\u53bb\u5854\u745f\u8c37\u518d\u8bf4\u3002\u201d</p><p>\u4e00\u884c\u4eba\u7ee7\u7eed\u4e0a\u8def\uff0c\u987a\u987a\u5f53\u5f53\u5730\u79bb\u5f00\u4e86\u5f13\u6865\u9547\uff0c\u8e0f\u4e0a\u524d\u5f80\u5854\u745f\u8c37\u7684\u5546\u9053\uff0c\u8fd9\u6b21\u5c31\u518d\u4e5f\u6ca1\u6709\u9047\u5230\u4ea1\u7075\u6216\u8005\u5f3a\u76d7\u62e6\u622a\u4e86\uff0c\u4e00\u8def\u98ce\u5e73\u6d6a\u9759\u3002\u73ca\u5609\u7ee7\u7eed\u770b\u4e66\uff0c\u743c\u6069\u5219\u5c06\u5934\u6795\u5728\u5979\u817f\u4e0a\u4f11\u606f\uff0c\u6b63\u89c9\u6709\u4e9b\u5026\u610f\uff0c\u5ffd\u7136\u542c\u5230\u73ca\u5609\u8f7b\u8f7b\u54a6\u4e86\u4e00\u58f0\uff0c\u201c\u5c0f\u5f1f\uff0c\u4f60\u6765\u770b\uff0c\u201d\u5c11\u5973\u8bf4\uff0c\u201c\u8fd9\u662f\u4ec0\u4e48\uff1f\u201d</p><p>\u743c\u6069\u8d77\u8eab\u770b\u53bb\uff0c\u53d1\u73b0\u662f\u4e00\u53ea\u6d45\u7eff\u8272\u7684\u4e66\u7b7e\uff0c\u6b63\u9759\u9759\u5939\u5728\u4e66\u9875\u4e2d\u95f4\uff0c\u4e0a\u9762\u753b\u7740\u4e00\u53ea\u6a21\u6837\u5341\u5206\u795e\u6c14\u7684\u706b\u9e1f\uff0c\u9e70\u9996\u9ad8\u51a0\uff0c\u8d64\u7fbd\u5f69\u5c3e\uff0c\u505a\u5c55\u7fc5\u6b32\u98de\u4e4b\u5f62\uff0c\u867d\u7136\u53ea\u662f\u4e00\u5e45\u56fe\uff0c\u4f46\u90a3\u987e\u76fc\u4e4b\u95f4\u7684\u5a01\u4e25\u534e\u8d35\u6c14\u5ea6\u5374\u662f\u8dc3\u7136\u7eb8\u4e0a\u3002\u201c\u8fd9\u53ea\u9e1f\u597d\u6f02\u4eae\u554a\uff0c\u800c\u4e14\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u79c0\u7709\u5fae\u8e59\uff0c\u601d\u7d22\u7740\uff0c\u201c\u89c9\u5f97\u597d\u50cf\u5728\u54ea\u91cc\u89c1\u5230\u8fc7\u4f3c\u7684\u3002\u201d</p><p>\u4f60\u5728\u786e\u5b9e\u89c1\u8fc7\uff0c\u743c\u6069\u5fc3\u91cc\u8bf4\uff0c\u5728\u68a6\u91cc\u3002</p><p>\u201c\u8fd9\u662f\u2018\u51e4\u51f0\u2019\uff0c\u201d\u4ed6\u544a\u8bc9\u59d0\u59d0\uff0c\u201c\u4e00\u79cd\u795e\u9e1f\u3002\u201d</p><p>\u8bdd\u521a\u51fa\u53e3\uff0c\u743c\u6069\u9661\u7136\u60f3\u8d77\uff1a\u5728\u8fd9\u4e2a\u4e16\u754c\u4e0a\uff0c\u51e4\u51f0\u5e76\u975e\u5b9e\u9645\u5b58\u5728\u7684\u751f\u7269\uff0c\u800c\u662f\u4f0a\u739b\u65af\u5361\u5e1d\u56fd\u7687\u5ba4\u5947\u68b0\u5e08\u6240\u5d07\u62dc\u7684\u4e24\u5927\u56fe\u817e\u4e4b\u4e00\uff0c\u65c1\u4eba\u5e76\u4e0d\u77e5\u6653\u3002\u5728\u8010\u745f\u8bed\u4e2d\u6839\u672c\u6ca1\u6709\u201c\u51e4\u51f0\u201d\u8fd9\u4e2a\u8bcd\u3002\u90a3\u4e48\u5965\u5609\u83b1\u65af\u8fd9\u672c\u4e66\u91cc\uff0c\u4e3a\u4f55\u4f1a\u6709\u51e4\u51f0\u7684\u56fe\u753b\uff1f\u96be\u4e0d\u6210\u5979\u548c\u4f0a\u739b\u65af\u5361\u6709\u4ec0\u4e48\u5173\u8054\u4e0d\u6210\uff1f\u8fd8\u662f\u8bf4\uff0c\u9001\u5979\u8fd9\u672c\u4e66\u7684\u6b27\u8d1d\u4f26\u4e0e\u4f0a\u739b\u65af\u5361\u6709\u5173\uff1f\u8bb0\u5f97\u4ee5\u524d\u5965\u6c83\u66fe\u7ecf\u65e0\u610f\u95f4\u63d0\u53ca\u8fc7\u4e00\u53e5\uff0c\u8bf4\u6b27\u8d1d\u4f26\u5f88\u50cf\u4f0a\u739b\u65af\u5361\u7684\u7687\u5ba4\u5947\u68b0\u5e08\u2026\u2026</p><p>\u4ed6\u6b63\u81ea\u6c89\u601d\uff0c\u73ca\u5609\u53c8\u6709\u4e86\u65b0\u53d1\u73b0\uff0c\u5c11\u5973\u5c06\u4e66\u7b7e\u62ff\u8d77\u6765\u89c2\u770b\uff0c\u968f\u610f\u4e00\u7ffb\uff0c\u53d1\u73b0\u539f\u6765\u80cc\u9762\u4e5f\u5bc6\u5bc6\u9ebb\u9ebb\u5730\u523b\u7740\u4e00\u5806\u5947\u602a\u7684\u56fe\u6848\u3002\u201c\u8fd9\u4e9b\u53c8\u662f\u4ec0\u4e48\uff1f\u201d\u5979\u95ee\u743c\u6069\uff0c\u201c\u9b54\u6cd5\u7b26\u6587\u5417\uff1f\u201d</p><p>\u201c\u6211\u770b\u770b\u3002\u201d</p><p>\u743c\u6069\u4ece\u59d0\u59d0\u624b\u4e2d\u63a5\u8fc7\u4e66\u7b7e\uff0c\u653e\u5728\u773c\u524d\u4ed4\u7ec6\u7aef\u8be6\u3002\u4e66\u7b7e\u662f\u957f\u65b9\u5f62\uff0c\u5f88\u5c0f\uff0c\u5927\u81f4\u76f8\u5f53\u4e8e\u98df\u6307\u548c\u4e2d\u6307\u5e76\u62e2\u7684\u9762\u79ef\uff0c\u575a\u786c\u5e73\u6ed1\uff0c\u4eff\u4f5b\u94a2\u94c1\uff0c\u4f46\u53c8\u89e6\u624b\u6e29\u6da6\uff0c\u4e0d\u50cf\u662f\u91d1\u5c5e\u3002\u81f3\u4e8e\u80cc\u9762\u90a3\u4e9b\u56fe\u6848\uff0c\u5e76\u975e\u9b54\u6cd5\u7b26\u6587\uff0c\u800c\u662f\u67d0\u79cd\u8c61\u5f62\u6587\u5b57\uff0c\u743c\u6069\u662f\u534a\u4e2a\u90fd\u4e0d\u8ba4\u8bc6\uff0c\u4f46\u4ed6\u6b64\u524d\u89c1\u8fc7\u7c7b\u4f3c\u7684\u4e1c\u897f\u3002</p><p>\u5728\u6df1\u6e0a\u65ad\u57df\u9547\uff0c\u7ea2\u8272\u5bff\u8863\u62ff\u51fa\u6765\u7684\u90a3\u679a\u4f0a\u739b\u65af\u5361\u7687\u5ba4\u5947\u68b0\u5e08\u7684\u767d\u7389\u5370\u7ae0\u4e0a\uff0c\u5c31\u523b\u7740\u7c7b\u4f3c\u7684\u6587\u5b57\u3002\u540e\u6765\u548c\u6885\u83f2\u65af\u63d0\u8d77\uff0c\u6885\u83f2\u65af\u8bf4\u5f88\u53ef\u80fd\u662f\u4f20\u8bf4\u4e2d\u4ec5\u5728\u4f0a\u739b\u65af\u5361\u5e1d\u56fd\u7687\u5ba4\u5185\u90e8\u4f7f\u7528\u7684\u79d8\u5bc6\u6587\u5b57\uff0c\u743c\u6069\u5fc3\u91cc\u975e\u5e38\u6000\u7591\u5b83\u662f\u7532\u9aa8\u6587\uff0c\u4f46\u65e0\u6cd5\u786e\u5b9a\u3002</p><p>\u201c\u771f\u53e4\u602a\uff0c\u201d\u73ca\u5609\u6258\u7740\u816e\uff0c\u201c\u5b57\u4e0d\u50cf\u5b57\uff0c\u753b\u4e0d\u50cf\u753b\uff0c\u53ef\u6211\u770b\u7740\u603b\u89c9\u5f97\u6709\u4e9b\u773c\u719f\u4f3c\u7684\u3002\u201d</p><p>\u201c\u8fd9\u662f\u79cd\u6587\u5b57\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u4ed6\u5b57\u659f\u53e5\u914c\u5730\u5411\u73ca\u5609\u89e3\u91ca\uff0c\u201c\u53ef\u80fd\u6e90\u81ea\u67d0\u4e2a\u975e\u5e38\u8fdc\u53e4\uff0c\u6bd4\u8010\u745f\u5e1d\u56fd\u66f4\u4e45\u8fdc\u7684\u6587\u660e\uff0c\u4f0a\u739b\u65af\u5361\u5e1d\u56fd\u3002\u6211\u4e0d\u61c2\u5b83\u7684\u610f\u601d\uff0c\u4e0d\u8fc7\u827e\u5f25\u8587\u6709\u53ef\u80fd\u8ba4\u8bc6\u2026\u2026\u201d</p><p>\u201c\u8fd9\u662f\u7d2b\u6587\u3002\u201d\u4e00\u4e2a\u51b7\u51b7\u7684\u58f0\u97f3\u7a81\u7136\u63d2\u8fdb\u6765\uff0c\u6253\u65ad\u4e86\u743c\u6069\u7684\u8bdd\u3002</p><p>\u73ca\u5609\u62ac\u5934\u770b\u53bb\uff0c\u53d1\u73b0\u5965\u5609\u83b1\u65af\u4e0d\u77e5\u4f55\u65f6\u5df2\u7ecf\u51fa\u73b0\u5728\u65c1\u8fb9\uff0c\u8138\u8272\u4e0d\u662f\u5f88\u597d\u770b\uff0c\u743c\u6069\u8d76\u5feb\u8bc6\u8da3\u5730\u5c06\u4e66\u7b7e\u8fd8\u7ed9\u5979\u3002\u201c\u8001\u5e08\uff0c\u7d2b\u6587\u662f\u4ec0\u4e48\u554a\uff1f\u201d\u73ca\u5609\u95ee\u3002</p><p>\u201c\u4ed6\u521a\u624d\u4e0d\u662f\u8bf4\u4e86\u5417\uff0c\u4f0a\u739b\u65af\u5361\u7684\u6587\u5b57\uff0c\u201d\u5965\u5609\u83b1\u65af\u8bf4\uff0c\u201c\u4f0a\u739b\u65af\u5361\u7684\u7687\u5ba4\u4ee5\u7d2b\u8272\u4e3a\u5c1a\uff0c\u8fd9\u79cd\u6587\u5b57\u662f\u5728\u7687\u5ba4\u5185\u90e8\u4f7f\u7528\uff0c\u6240\u4ee5\u79f0\u4e4b\u4e3a\u7d2b\u6587\u3002\u201d</p><p>\u201c\u90a3\u8fd9\u4e0a\u9762\u7684\u7d2b\u6587\u662f\u6b27\u8d1d\u4f26\u5148\u751f\u5199\u7684\uff1f\u201d\u73ca\u5609\u7b11\u563b\u563b\u5730\u95ee\uff0c\u201c\u662f\u4ec0\u4e48\u610f\u601d\u554a\uff1f\u201d</p><p>\u5979\u5df2\u7ecf\u4ece\u743c\u6069\u90a3\u91cc\u77e5\u9053\uff0c\u8fd9\u672c\u547d\u8fd0\u957f\u591c\u662f\u6b27\u8d1d\u4f26\u9001\u7ed9\u5965\u5609\u83b1\u65af\u7684\u5b9a\u60c5\u4fe1\u7269\uff0c\u65e2\u7136\u5982\u6b64\uff0c\u8fd9\u9875\u4e66\u7b7e\u4e0a\u6240\u5199\u7740\u7684\uff0c\u53ea\u6015\u662f\u4ec0\u4e48\u60c5\u8bdd\u5427\uff0c\u7136\u800c\u5965\u5609\u83b1\u65af\u7684\u53cd\u5e94\u5374\u51fa\u4e4e\u5979\u7684\u610f\u6599\u4e4b\u5916\u3002</p><p>\u201c\u4e0d\u662f\u4ed6\u5199\u7684\u3002\u201d\u5927\u5965\u672f\u5e08\u6447\u6447\u5934\u3002</p><p>\u201c\u554a\uff1f\u201d</p><p>\u201c\u8fd9\u662f\u4f0a\u739b\u65af\u5361\u7684\u4e00\u9996\u796d\u8bd7\uff0c\u7687\u5ba4\u796d\u7940\u5148\u7956\u7684\u65f6\u5019\u4f1a\u5531\u9882\uff0c\u81f3\u4e8e\u610f\u601d\u4e48\uff0c\u4e0d\u592a\u5bb9\u6613\u7ffb\u8bd1\uff0c\u201d\u5965\u5609\u83b1\u65af\u77a5\u4e86\u4e66\u7b7e\u4e00\u773c\uff0c\u201c\u5927\u81f4\u5c31\u662f\u8bf4\uff1a\u5929\u4e0a\u7684\u795e\u660e\u6d3e\u6765\u4e00\u53ea\u9e1f\uff0c\u558f\uff0c\u5c31\u662f\u8fd9\u53ea\u51e4\u51f0\uff0c\u751f\u4e0b\u4e00\u4e2a\u4eba\uff0c\u5efa\u7acb\u4e86\u4e00\u4e2a\u5e1d\u56fd\uff0c\u540d\u5b57\u53eb\u505a\uff0c\u5514\uff0c\u8fd9\u4e2a\u97f3\u600e\u4e48\u53d1\u6765\u7740\u2026\u2026\u201d</p><p>\u201c\u5546\uff0c\u201d\u743c\u6069\u6162\u6162\u8bf4\uff0c\u201c\u5929\u547d\u7384\u9e1f\uff0c\u964d\u800c\u751f\u5546\u3002\u201d</p><p>\u73ca\u5609\u83ab\u540d\u5176\u5999\u5730\u770b\u7740\u4ed6\uff0c\u4e0d\u77e5\u9053\u4ed6\u5728\u8bf4\u4ec0\u4e48\uff0c\u5965\u5609\u83b1\u65af\u5374\u662f\u795e\u8272\u5fae\u53d8\uff0c\u201c\u4f60\u8ba4\u8bc6\u7d2b\u6587\uff1f\u201d</p><p>\u201c\u4e0d\u8ba4\u8bc6\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u53ea\u662f\u6070\u597d\u77e5\u9053\u8fd9\u9996\u8bd7\u3002\u201d</p><p>\u201c\u4f60\u77e5\u9053\uff1f\u201d\u5965\u5609\u83b1\u65af\u51b7\u7b11\uff0c\u201c\u90a3\u540e\u9762\u5462\uff1f\u201d</p><p>\u201c\u5929\u547d\u7384\u9e1f\uff0c\u964d\u800c\u751f\u5546\uff0c\u5b85\u6bb7\u571f\u8292\u8292\u3002\u53e4\u5e1d\u547d\u6b66\u6c64\uff0c\u6b63\u57df\u5f7c\u56db\u65b9\u3002\u65b9\u547d\u53a5\u540e\uff0c\u5944\u6709\u4e5d\u6709\u3002\u5546\u4e4b\u5148\u540e\uff0c\u53d7\u547d\u4e0d\u6b86\uff0c\u5728\u6b66\u4e01\u5b59\u5b50\u3002\u6b66\u4e01\u5b59\u5b50\uff0c\u6b66\u738b\u9761\u4e0d\u80dc\u3002\u9f99\u65c2\u5341\u4e58\uff0c\u5927\u7ce6\u662f\u627f\u3002\u90a6\u757f\u5343\u91cc\uff0c\u7ef4\u6c11\u6240\u6b62\uff0c\u8087\u57df\u5f7c\u56db\u6d77\u3002\u56db\u6d77\u6765\u5047\uff0c\u6765\u5047\u7941\u7941\uff0c\u666f\u5458\u7ef4\u6cb3\u3002\u6bb7\u53d7\u547d\u54b8\u5b9c\uff0c\u767e\u7984\u662f\u4f55\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u73ca\u5609\u5df2\u7ecf\u5b8c\u5168\u6014\u4f4f\uff0c\u5979\u542c\u4e0d\u61c2\u743c\u6069\u5728\u8bf4\u4ec0\u4e48\uff0c\u4f46\u5374\u53c8\u89c9\u5f97\u65e0\u6bd4\u719f\u6089\uff0c\u90a3\u4e9b\u964c\u751f\u7684\u53d1\u97f3\u6c47\u5408\u5728\u4e00\u8d77\uff0c\u81ea\u6709\u4e00\u79cd\u52a8\u4eba\u5fc3\u9b44\u7684\u8282\u594f\u97f5\u5f8b\uff0c\u4eff\u4f5b\u662f\u66fe\u7ecf\u542c\u8fc7\u5343\u904d\u4e07\u904d\u7684\u6062\u5b8f\u5723\u6b4c\u3002\u5965\u5609\u83b1\u65af\u5374\u662f\u795e\u8272\u53d8\u5e7b\u4e0d\u5b9a\uff0c\u65e2\u662f\u60ca\u8bb6\uff0c\u53c8\u4f3c\u604d\u7136\uff0c\u751a\u81f3\u8fd8\u7565\u5e26\u7740\u4e00\u4e1d\u8ba5\u5632\u51b7\u7b11\uff0c\u5979\u4e0a\u4e0b\u6253\u91cf\u7740\u743c\u6069\uff0c\u50cf\u662f\u770b\u602a\u7269\u4e00\u822c\u3002</p><p>\u201c\u770b\u6765\uff0c\u6211\u4eec\u5e94\u8be5\u627e\u4e2a\u65f6\u95f4\u597d\u597d\u8c08\u4e00\u8c08\u3002\u201d\u5979\u6700\u540e\u5782\u4e0b\u773c\u76ae\uff0c\u4f4e\u58f0\u8bf4\u3002</p><p>\u201c\u6b63\u6709\u6b64\u610f\u3002\u201d\u743c\u6069\u56de\u7b54\u3002</p><p>\u5965\u5609\u83b1\u65af\u53c8\u770b\u4e86\u4ed6\u4e00\u773c\uff0c\u6ca1\u6709\u7ee7\u7eed\u8fd9\u4e2a\u8bdd\u9898\uff0c\u5979\u793a\u610f\u73ca\u5609\u5c06\u4e66\u9012\u8fc7\u6765\uff0c\u7528\u53f3\u624b\u5728\u5c01\u9762\u4e0a\u8f7b\u8f7b\u629a\u6469\u4e86\u51e0\u4e0b\uff0c\u9ed8\u8bf5\u5492\u6587\u3002\u51e0\u5341\u9053\u7ec6\u5fae\u7684\u7d2b\u8272\u5149\u7ebf\u4ece\u4e66\u4e2d\u53d1\u51fa\uff0c\u5728\u7a7a\u6c14\u4e2d\u7eb5\u6a2a\u4ea4\u9519\uff0c\u7f16\u7ec7\u6210\u4e00\u67c4\u5251\uff0c\u8d77\u521d\u53ea\u662f\u7ebf\u6761\u7ec4\u6210\u7684\u8f6e\u5ed3\uff0c\u6e10\u6e10\u6210\u4e3a\u5b8c\u6574\u5f71\u50cf\uff0c\u6700\u7ec8\u5b8c\u5168\u5177\u73b0\u4e3a\u5b9e\u4f53\u3002\u5965\u5609\u83b1\u65af\u4f38\u624b\u53d6\u4e0b\uff0c\u9012\u7ed9\u73ca\u5609\u3002</p><p>\u90a3\u662f\u4e00\u67c4\u523a\u5251\uff0c\u62a4\u624b\u5f88\u5bbd\uff0c\u5251\u8eab\u975e\u5e38\u7ec6\u7a84\uff0c\u4e2d\u95f4\u5f00\u7740\u4e00\u9053\u8840\u69fd\uff0c\u5251\u5c16\u95ea\u70c1\u7740\u950b\u5229\u5149\u6cfd\uff0c\u4eff\u4f5b\u5371\u9669\u6bd2\u86c7\u3002\u743c\u6069\u5f00\u542f\u5965\u672f\u89c6\u89c9\uff0c\u7136\u540e\u60ca\u8bb6\u5730\u53d1\u73b0\u5b83\u7684\u7075\u5149\u5f3a\u5ea6\u5b8c\u5168\u8db3\u4ee5\u8dfb\u8eab\u795e\u5668\u4e4b\u5217\uff0c\u5927\u7ea6\u4ecb\u4e4e\u4e8e\u201c\u6b21\u7b49\u201d\u4e0e\u201c\u9ad8\u7b49\u201d\u4e4b\u95f4\uff0c\u9b54\u6cd5\u80fd\u91cf\u4ece\u950b\u5203\u4e2d\u4e1d\u4e1d\u6d41\u51fa\uff0c\u73af\u7ed5\u5251\u4f53\uff0c\u4e0d\u65ad\u53d8\u5e7b\u6210\u6697\u7ea2\u706b\u7130\u548c\u51b0\u84dd\u96ea\u82b1\u7684\u5f62\u6001\u3002</p><p>\u201c\u4e00\u5343\u591a\u5e74\u6ca1\u7528\u5b83\uff0c\u90fd\u6709\u4e9b\u751f\u758f\u4e86\uff0c\u201d\u5965\u5609\u83b1\u65af\u8bf4\uff0c\u201c\u7b2c\u4e00\u6b21\u5c45\u7136\u5931\u624b\uff0c\u5dee\u70b9\u6d6a\u8d39\u4e86\u90a3\u67c4\u51b0\u65a7\uff0c\u5e78\u597d\u7b2c\u4e8c\u6b21\u6210\u529f\u4e86\u3002\u8bd5\u8bd5\u770b\u5427\uff0c\u5e94\u8be5\u5408\u624b\u3002\u201d</p><p>\u201c\u53ef\u662f\u2026\u2026\u201d\u73ca\u5609\u7b28\u62d9\u5730\u63a5\u8fc7\u523a\u5251\uff0c\u201c\u8001\u5e08\uff0c\u6211\u6ca1\u5b66\u8fc7\u5251\u672f\u554a\u3002\u201d</p><p>\u201c\u6211\u77e5\u9053\uff0c\u6240\u4ee5\u6211\u4f1a\u6559\u4f60\u7684\uff0c\u201d\u5965\u5609\u83b1\u65af\u8bf4\uff0c\u201c\u53cd\u6b63\u4e5f\u4e0d\u662f\u7b2c\u4e00\u6b21\u4e86\u3002\u201d</p><p>\u201c\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u6ca1\u4ec0\u4e48\uff0c\u6211\u662f\u8bf4\uff0c\u4ee5\u524d\u6211\u4e5f\u6559\u8fc7\u522b\u4eba\u5251\u672f\uff0c\u6709\u7ecf\u9a8c\u7684\u3002\u201d</p><p>\u201c\u8001\u5e08\u539f\u6765\u8fd8\u662f\u5251\u672f\u9ad8\u624b\u554a\u3002\u201d</p><p>\u201c\u90a3\u81ea\u7136\uff0c\u201d\u5965\u5609\u83b1\u65af\u6de1\u6de1\u4e00\u7b11\uff0c\u201c\u6211\u53ef\u662f\u7528\u5251\u6740\u8fc7\u795e\u7684\u3002\u201d</p><p>\u201c\u662f\u5417\uff0c\u8001\u5e08\u771f\u5389\u5bb3\u554a\uff0c\u201d\u73ca\u5609\u4e00\u8138\u5d07\u62dc\u7684\u795e\u60c5\uff0c\u201c\u662f\u54ea\u4e2a\u795e\u5462\uff1f\u201d</p><p>\u201c\u7a46\u7f55\u745e\u5fb7\u90a3\u8fb9\u7684\u4e00\u4e2a\u4e8c\u6d41\u5c0f\u795e\uff0c\u8bf4\u4e86\u4f60\u4e5f\u4e0d\u77e5\u9053\uff0c\u201d\u5965\u5609\u83b1\u65af\u8bf4\uff0c\u201c\u957f\u7740\u4e2a\u72d7\u5934\uff0c\u5947\u5f62\u602a\u72b6\u7684\uff0c\u540d\u5b57\u8bb0\u4e0d\u592a\u6e05\u695a\u4e86\uff0c\u597d\u50cf\u662f\u963f\u5974\u65af\uff1f\u963f\u6bd4\u5974\uff1f\u8fd8\u662f\u53f2\u52aa\u6bd4\uff1f\u201d</p><p>\u201c\u53f2\u52aa\u6bd4\u8fd9\u540d\u5b57\u5f88\u53ef\u7231\u554a\u3002\u201d</p><p>\u5965\u5609\u83b1\u65af\u7b11\u4e86\u7b11\uff0c\u6b63\u5f85\u8bf4\u8bdd\uff0c\u731b\u7136\u95f4\u8138\u8272\u9aa4\u53d8\uff0c\u4ee5\u6781\u5feb\u7684\u901f\u5ea6\u7ffb\u5f00\u300a\u547d\u8fd0\u957f\u591c\u300b\uff0c\u6253\u5f00\u5230\u5176\u4e2d\u4e00\u9875\uff0c\u8f7b\u559d\u4e00\u58f0\u3002\u707f\u70c2\u7684\u4e03\u5f69\u8679\u5149\u81ea\u4e66\u9875\u4e2d\u5c04\u51fa\uff0c\u8f70\u7136\u56db\u5c04\uff0c\u8f6c\u773c\u95f4\u5f62\u6210\u4e86\u4e00\u4e2a\u76f4\u5f84\u7ea6\u5341\u82f1\u5c3a\u7684\u534a\u7403\u5f62\u62a4\u7f69\uff0c\u5c06\u6574\u4e2a\u9a6c\u8f66\u7b3c\u7f69\u5728\u5176\u4e2d\u3002\u743c\u6069\u548c\u73ca\u5609\u8fd8\u4e0d\u77e5\u9053\u53d1\u751f\u4e86\u4ec0\u4e48\uff0c\u7d27\u63a5\u7740\u5c31\u542c\u5230\u5934\u9876\u4e0a\u8f70\u7136\u96f7\u9e23\uff0c\u9707\u8033\u6b32\u804b\uff0c\u8fde\u7ef5\u4e0d\u7edd\uff0c\u8fde\u5927\u5730\u90fd\u5728\u9690\u9690\u6447\u6643\uff0c\u4ece\u8f66\u53a2\u7a97\u53e3\u671b\u53bb\uff0c\u53ea\u89c1\u539f\u672c\u6674\u6717\u7684\u5929\u7a7a\u5df2\u7ecf\u9ed1\u4e91\u6eda\u6eda\uff0c\u6210\u5343\u4e0a\u4e07\u7684\u94f6\u8272\u95ea\u7535\u5728\u5176\u4e2d\u7a7f\u68ad\u76d8\u65cb\uff0c\u7136\u540e\u201c\u5494\u5693\u201d\u4e00\u58f0\uff0c\u50cf\u5343\u4e07\u9053\u523a\u773c\u7684\u5229\u5251\uff0c\u671d\u7740\u9a6c\u8f66\u5288\u4e0b\u6765\u3002</p></article>"}}}]);
