(window.webpackJsonp=window.webpackJsonp||[]).push([[478],{501:function(p){p.exports={chapterTitle:null,title:"\u7b2c478\u7ae0 \u98ce\u96ea\u591c",content:"<article><h2>\u7b2c478\u7ae0 \u98ce\u96ea\u591c</h2><p>\u3000\u3000\u98de\u56de\u5185\u5730\uff0c\u5f20\u606a\u51b3\u5b9a\u5f53\u591c\u7559\u5728\u7701\u57ce\uff0c\u8bb8\u601d\u3001\u5468\u6e38\u7b49\u966a\u7740\u5b59\u5c1a\u4e49\u3001\u6768\u5f8b\u9526\u7b49\u524d\u5f80\u6d77\u5dde\uff0c\u6d77\u5dde\u90a3\u8fb9\u8fd8\u6709\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u7b49\u4eba\u8d1f\u8d23\u63a5\u5f85\u3002</p><p>\u3000\u3000\u5f90\u5b66\u5e73\u8fc7\u4e24\u5929\u5c31\u8981\u53bb\u5317\u4eac\u8d74\u4efb\u4e86\uff0c\u6240\u4ee5\u5f20\u606a\u6025\u7740\u4ece\u9999\u6e2f\u8d76\u56de\u6765\uff0c\u5728\u5f90\u5b66\u5e73\u6b63\u5f0f\u5378\u4efb\u4e4b\u524d\u89c1\u4ed6\u4e00\u9762\u3002\u9876\u66ff\u5f90\u5b66\u5e73\u7684\u662f\u5e38\u52a1\u526f\u7701\u957f\u674e\u8fdc\u6e56\uff0c\u674e\u8fdc\u6e56\u4e24\u5e74\u524d\u4ece\u60e0\u5c71\u5e02\u5e02\u59d4\u4e66\u8bb0\u4efb\u4e0a\u8c03\u5230\u7701\u91cc\u51fa\u4efb\u5e38\u52a1\u526f\u7701\u957f\uff0c\u7ec4\u7ec7\u4e0a\u5c31\u662f\u5b89\u6392\u4ed6\u8fc7\u4e24\u5e74\u63a5\u66ff\u6587\u5f81\u74a7\u7684\u7701\u957f\u4f4d\u5b50\uff0c\u8c01\u66fe\u60f3\u5230\u4ed6\u521a\u8c03\u5230\u7701\u91cc\u6587\u5f81\u74a7\u5c31\u56e0\u4e3a\u4e01\u5411\u5c71\u7684\u95ee\u9898\u4e0d\u5f97\u4e0d\u63d0\u524d\u9000\u4e86\u4e0b\u53bb\uff0c\u5f90\u5b66\u5e73\u63d2\u5728\u4ed6\u524d\u9762\u5148\u5f53\u4e86\u7701\u957f\u3002</p><p>\u3000\u3000\u5f20\u606a\u4ece\u6765\u90fd\u4e0d\u76f8\u4fe1\u8868\u9762\u4e0a\u7684\u4e00\u56e2\u548c\u6c14\uff0c\u8981\u8bf4\u674e\u8fdc\u6e56\u5bf9\u5f90\u5b66\u5e73\u6321\u4e86\u4ed6\u4e24\u5e74\u800c\u5fc3\u4e2d\u6beb\u65e0\u82a5\u8482\uff0c\u5f20\u606a\u4e5f\u4e0d\u4f1a\u76f8\u4fe1\uff0c\u8981\u8bf4\u5f90\u5b66\u5e73\u63d0\u524d\u79bb\u5f00\u4e1c\u6d77\u674e\u8fdc\u6e56\u6ca1\u6709\u5728\u6697\u4e2d\u51fa\u529b\uff0c\u5f20\u606a\u4e5f\u4e0d\u4f1a\u76f8\u4fe1\u3002\u6709\u77db\u76fe\u624d\u6709\u653f\u6cbb\u5b58\u5728\u7684\u5fc5\u8981\uff0c\u7167\u987e\u5230\u5f90\u5b66\u5e73\u7684\u611f\u53d7\uff0c\u5f20\u606a\u4ece\u6765\u90fd\u6ca1\u6709\u4e3b\u52a8\u53bb\u63a5\u89e6\u674e\u8fdc\u6e56\uff0c\u8fd9\u65f6\u5019\u8d76\u8fc7\u53bb\u5df4\u7ed3\u4e5f\u6709\u4e9b\u4ea1\u7f8a\u8865\u7262\u4e86\uff0c\u6216\u8bb8\u8fd9\u4e48\u505a\u5728\u7701\u59d4\u4e66\u8bb0\u9676\u664b\u773c\u91cc\u4e5f\u4f1a\u6709\u8fc7\u5ea6\u8ba8\u5de7\u4e4b\u5acc\uff0c\u653f\u6cbb\u5e76\u975e\u610f\u5473\u7740\u65e0\u9650\u5236\u7684\u5de6\u53f3\u9022\u8fce\u3002</p><p>\u3000\u3000\u4e5d\u5341\u5e74\u4e2d\u671f\uff0c\u56fd\u5185\u7684\u8d44\u672c\u52bf\u529b\u8fd8\u8981\u5728\u653f\u6cbb\u52bf\u529b\u7684\u7f1d\u9699\u95f4\u751f\u5b58\uff0c\u8fd9\u4e5f\u662f\u65e0\u5948\u7684\u4e8b\u5b9e\u3002</p><p>\u3000\u3000\u5f90\u5b66\u5e73\u79bb\u5f00\u4e1c\u6d77\uff0c\u4e1c\u6d77\u5c31\u6210\u4e3a\u9676\u664b\u4e0e\u674e\u8fdc\u6e56\u5e76\u5b58\u7684\u65f6\u4ee3\uff0c\u674e\u8fdc\u6e56\u5e74\u7eaa\u521a\u8fc7\u4e94\u5341\uff0c\u5916\u5a92\u8bc4\u4ef7\u56fd\u5185\u7684\u653f\u6cbb\u4eba\u7269\u65f6\uff0c\u5c31\u5c06\u4ed6\u5f52\u5165\u56fd\u5185\u65b0\u751f\u4ee3\u7684\u653f\u6cbb\u52bf\u529b\u4e4b\u5217\uff0c\u867d\u7136\u8bf4\u9676\u664b\u5728\u4e1c\u6d77\u4f4d\u5c0a\u6743\u91cd\uff0c\u4f46\u662f\u5c81\u6708\u4e0d\u9976\u4eba\uff0c\u4ed6\u4eca\u5e74\u516d\u5341\u4e09\u5c81\uff0c\u4e0d\u7ba1\u4ed6\u80fd\u4e0d\u80fd\u5728\u4e24\u5e74\u4e4b\u5185\u8df3\u5230\u4e2d\u592e\uff0c\u4e24\u5e74\u4e4b\u540e\uff0c\u4e1c\u6d77\u7701\u59d4\u4e66\u8bb0\u7684\u5b98\u8854\u4f1a\u843d\u5230\u4ed6\u4eba\u5934\u4e0a\uff0c\u51e0\u4e4e\u6210\u5b9a\u5c40\u4e86\uff0c\u4e0d\u8fc7\u674e\u8fdc\u6e56\u4f3c\u4e4e\u8fd8\u6ca1\u6709\u8d44\u5386\u5728\u4e24\u5e74\u540e\u5c31\u76f4\u63a5\u9876\u66ff\u9676\u664b\uff0c\u8fd9\u4e48\u8bf4\u6765\uff0c\u8fd8\u771f\u662f\u5f90\u5b66\u5e73\u803d\u6401\u4e86\u4ed6\u4e24\u5e74\u7684\u65f6\u95f4\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4e0b\u5348\u4e09\u70b9\u949f\uff0c\u5929\u7a7a\u9634\u6c89\uff0c\u6709\u5982\u5915\u9633\u6c89\u5165\u5730\u5e73\u7ebf\u4e4b\u540e\u7684\u660f\u669d\uff0c\u5929\u6c14\u9884\u62a5\u8bf4\u591c\u91cc\u8981\u4e0b\u96ea\uff0c\u8fdc\u5904\u673a\u573a\u8dd1\u9053\u4e0a\u7a7a\u7684\u706f\u5149\u95ea\u70c1\uff0c\u8d77\u964d\u7684\u98de\u673a\u6709\u4e9b\u6a21\u7cca\uff0c\u5f20\u606a\u5750\u5728\u79bb\u5f00\u673a\u573a\u7684\u8f7f\u8f66\u4e0a\uff0c\u674e\u4e49\u6c5f\u8fc7\u6765\u63a5\u7684\u673a\u3002</p><p>\u3000\u3000\u674e\u4e49\u6c5f\u5230\u6d77\u5dde\u62c5\u4efb\u57ce\u5357\u533a\u59d4\u526f\u4e66\u8bb0\u3001\u4ee3\u533a\u957f\u7684\u51b3\u8bae\u5df2\u7ecf\u4e0b\u6765\uff0c\u4ed6\u8981\u7b49\u5f90\u5b66\u5e73\u79bb\u5f00\u4e1c\u6d77\u4e4b\u540e\uff0c\u518d\u53bb\u6d77\u5dde\u8d76\u4efb\u3002</p><p>\u3000\u3000\u201c\u5e02\u91cc\u8981\u5c06\u57ce\u5357\u533a\u5357\u90ca\u7684\u4e09\u4e2a\u9547\u3001\u4e24\u4e2a\u5de5\u4e1a\u56ed\u533a\u5355\u72ec\u62bd\u51fa\u6765\u641e\u4e00\u4e2a\u7701\u7ea7\u5f00\u53d1\u533a\uff1f\u201d\u674e\u4e49\u6c5f\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u54e6\uff0c\u5e02\u91cc\u597d\u50cf\u662f\u6709\u8fd9\u610f\u601d\uff0c\u201d\u5f20\u606a\u5c06\u89c6\u91ce\u4ece\u7a97\u5916\u660f\u6c89\u7684\u98ce\u666f\u91cc\u6536\u56de\u6765\uff0c\u201c\u4e5f\u6ca1\u6709\u6700\u540e\u786e\u5b9a\uff0c\u674e\u54e5\u65e9\u5c31\u5173\u5fc3\u8d77\u6d77\u5dde\u7684\u4e8b\u60c5\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4e5f\u5c31\u56de\u56fd\u540e\u786e\u5207\u77e5\u9053\u8981\u53bb\u6d77\u5dde\uff0c\u624d\u60f3\u7740\u529f\u8bfe\u8981\u63d0\u524d\u505a\u4e00\u4e9b\u7684\uff0c\u4e0d\u7136\u5f00\u5c55\u5de5\u4f5c\u5c31\u592a\u88ab\u52a8\u4e86\u3002\u6234\u6587\u8fdb\u662f\u600e\u4e48\u6837\u7684\u4e00\u4e2a\u4eba\uff0c\u600e\u4e48\u4e0d\u8ddf\u5e02\u91cc\u4e89\u4e00\u4e89\uff1f\u5e02\u91cc\u8c03\u6574\u4e3b\u57ce\u533a\u89c4\u5212\u8349\u6848\uff0c\u5c06\u758f\u6e2f\u6cb3\u4ee5\u897f\u7684\u6c99\u7530\u90fd\u5212\u5165\u897f\u57ce\u533a\uff0c\u5c31\u8ba9\u57ce\u5357\u533a\u7684\u7f29\u5c0f\u4e86\u4e00\u5757\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6234\u6587\u8fdb\u554a\uff0c\u6211\u8fd8\u6ca1\u6709\u63a5\u89e6\u8fc7\uff0c\u4ed6\u5728\u65b0\u8054\u53bf\u505a\u53bf\u957f\u65f6\u542c\u8bf4\u505a\u5de5\u4f5c\u8fd8\u662f\u5f88\u6709\u4e00\u624b\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u674e\u4e49\u6c5f\u5230\u57ce\u5357\u533a\u4e0d\u662f\u76f4\u63a5\u5f53\u4e66\u8bb0\u800c\u662f\u62c5\u4efb\u526f\u4e66\u8bb0\u3001\u4ee3\u533a\u957f\uff0c\u5fc3\u91cc\u591a\u5c11\u5c31\u6709\u4e9b\u5931\u843d\u4e86\u3002</p><p>\u3000\u3000\u4e5d\u5341\u5e74\u4ee3\u4e2d\u540e\u671f\uff0c\u653f\u5e9c\u5b98\u5458\u6700\u91cd\u8981\u7684\u653f\u7ee9\u8fd8\u662f\u5730\u65b9\u4e0a\u7684\u7ecf\u6d4e\u5b9e\u7ee9\uff0c\u57ce\u5357\u533a\u6709\u65b0\u6865\u9547\u7684\u9020\u7eb8\u5de5\u4e1a\u56ed\u3001\u76ca\u9686\u9547\u7684\u7535\u5b50\u5de5\u4e1a\u56ed\uff0c\u4e5d\u516d\u5e74\u5404\u9879\u7ecf\u6d4e\u6570\u636e\u2014\u2014\u62db\u5546\u5f15\u8d44\u3001\u5de5\u4e1a\u6295\u8d44\u3001\u5de5\u4e1a\u751f\u4ea7\u603b\u503c\u3001\u5730\u65b9\u8d22\u653f\u589e\u6536\u2014\u2014\u90fd\u8981\u8fdc\u8fdc\u8d85\u8fc7\u6d77\u5dde\u5e02\u91cc\u7684\u5176\u4ed6\u53bf\u533a\uff0c\u674e\u4e49\u6c5f\u4e4b\u524d\u5bf9\u5230\u57ce\u5357\u533a\u62c5\u4efb\u4e00\u4e8c\u628a\u624b\u90fd\u76f8\u5f53\u7684\u671f\u5f85\uff0c\u6bd4\u5176\u4ed6\u533a\u53bf\u66f4\u5bb9\u6613\u51fa\u6210\u7ee9\uff0c\u6d77\u5dde\u5e02\u91cc\u7684\u8fd9\u4e00\u756a\u8c03\u6574\uff0c\u8981\u5c06\u57ce\u5357\u533a\u7ecf\u6d4e\u6700\u4e3a\u4f9d\u91cd\u7684\u4e24\u4e2a\u5de5\u4e1a\u56ed\u533a\u5212\u51fa\u57ce\u5357\u533a\uff0c\u674e\u4e49\u6c5f\u5fc3\u91cc\u591a\u5c11\u6709\u4e9b\u9e21\u98de\u86cb\u6253\u7684\u5931\u843d\u3002</p><p>\u3000\u3000\u674e\u4e49\u6c5f\u8fd9\u65f6\u5019\u5728\u8f66\u91cc\u63d0\u51fa\u8fd9\u4e9b\u4e8b\u60c5\uff0c\u5f20\u606a\u80fd\u660e\u767d\u4ed6\u7684\u5fc3\u601d\uff0c\u4e14\u4e0d\u8bf4\u5f20\u606a\u81ea\u5df1\u4e5f\u5e0c\u671b\u65b0\u6865\u3001\u8c61\u5c71\u3001\u76ca\u9686\u4e09\u4e2a\u9547\u80fd\u72ec\u7acb\u51fa\u6765\u7ecf\u8425\uff0c\u8fd9\u65f6\u5019\u674e\u4e49\u6c5f\u8fd8\u6ca1\u6709\u5230\u6d77\u5dde\uff0c\u5c31\u6025\u7740\u4e89\u8fd9\u4e9b\uff0c\u591a\u5c11\u6709\u4e9b\u6025\u5207\u4e86\u3002\u5f20\u606a\u4e5f\u65e0\u6cd5\u8bf4\u4ec0\u4e48\uff0c\u5e94\u4ed8\u7684\u51e0\u53e5\uff0c\u5c31\u8f6c\u79fb\u5230\u5176\u4ed6\u8bdd\u9898\u4e0a\u4e86\u3002</p><p>\u3000\u3000\u65b0\u8054\u53bf\u662f\u6d77\u5dde\u5404\u65b9\u9762\u90fd\u76f8\u5f53\u9760\u540e\u7684\u4e00\u4e2a\u53bf\uff0c\u6234\u6587\u8fdb\u4ece\u65b0\u8054\u8c03\u5230\u57ce\u5357\u5f53\u4e66\u8bb0\uff0c\u6839\u672c\u5c31\u65e0\u6ca1\u6709\u592a\u591a\u7684\u8d44\u683c\u5728\u57ce\u5357\u533a\u65b0\u89c4\u5212\u4e0a\u53d1\u8a00\uff0c\u4e5f\u4e0d\u662f\u4ed6\u6027\u683c\u8f6f\u5f31\u7ed9\u522b\u4eba\u6b3a\u8d1f\u3002</p><p>\u3000\u3000\u5b8b\u57f9\u660e\u6b63\u5f0f\u8c03\u5230\u5e02\u91cc\u62c5\u4efb\u5e38\u52a1\u526f\u5e02\u957f\u3001\u6234\u6587\u8fdb\u8c03\u5230\u57ce\u5357\u533a\u62c5\u4efb\u4e66\u8bb0\u3001\u4ee3\u7406\u53bf\u957f\u662f\u5f20\u606a\u8fd9\u6bb5\u65f6\u95f4\u5728\u9999\u6e2f\u65f6\u53d1\u751f\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5728\u65b0\u6885\u82d1\u5f90\u5b66\u5e73\u5bb6\u5403\u7684\u665a\u996d\uff0c\u5f90\u5b66\u5e73\u804a\u7684\u6700\u591a\u8fd8\u662f\u7701\u91cc\u7684\u7ecf\u6d4e\u95ee\u9898\uff0c\u4e1c\u6d77\u8054\u5408\u94a2\u94c1\u9879\u76ee\u8c08\u6210\uff0c\u4e1c\u6d77\u8054\u5408\u94a2\u94c1\u96c6\u56e2\u5c06\u5f62\u6210\u56fd\u5185\u7b2c\u4e94\u5927\u7684\u94a2\u94c1\u751f\u4ea7\u57fa\u5730\uff0c\u4f46\u662f\u8fd9\u8fd8\u4e0d\u80fd\u6539\u53d8\u7701\u5185\u94a2\u94c1\u751f\u4ea7\u5206\u6563\u7684\u5c40\u9762\uff0c\u8fd8\u8981\u8fdb\u4e00\u6b65\u7684\u52a0\u5f3a\u94a2\u94c1\u4ea7\u80fd\u7684\u91cd\u7ec4\u6574\u5408\uff0c\u5f62\u6210\u56fd\u5185\u7b2c\u4e09\u3001\u751a\u81f3\u7b2c\u4e8c\u7684\u94a2\u94c1\u751f\u4ea7\u89c4\u6a21\uff1b\u91cd\u578b\u5316\u662f\u94a2\u94c1\u4f01\u4e1a\u53d1\u5c55\u7684\u8d8b\u52bf\u3002\u8fd8\u6709\u52a0\u901f\u7701\u9ad8\u901f\u516c\u8def\u7f51\u3001\u6cbf\u6c5f\u3001\u6cbf\u6d77\u6e2f\u53e3\u7b49\u57fa\u7840\u8bbe\u65bd\u7684\u5efa\u8bbe\u3001\u7701\u5185\u4ea7\u4e1a\u7ed3\u6784\u7684\u8c03\u6574\u7b49\u7b49\u95ee\u9898\uff0c\u5f90\u5b66\u5e73\u8c08\u4e86\u5f88\u591a\uff0c\u4e4b\u6240\u4ee5\u8c08\u8fd9\u4e48\u591a\uff0c\u8fd8\u662f\u6709\u6015\u674e\u8fdc\u6e56\u9876\u66ff\u4ed6\u7684\u4f4d\u5b50\u4e4b\u540e\u53e6\u641e\u4e00\u5957\u7684\u62c5\u5fe7\u3002</p><p>\u3000\u3000\u8fd9\u4e2a\u662f\u6781\u6709\u53ef\u80fd\u7684\uff0c\u56fd\u5185\u4e5d\u5341\u5e74\u4ee3\uff0c\u51e0\u4e4e\u662f\u4e00\u4efb\u5730\u65b9\u653f\u5e9c\u4e00\u5957\u5730\u65b9\u89c4\u5212\uff0c\u7ee7\u4efb\u8005\u4f3c\u4e4e\u8ba4\u4e3a\u6309\u7167\u524d\u4efb\u7684\u601d\u8def\u8d70\u4e0b\u53bb\uff0c\u5373\u4f7f\u5927\u6709\u6210\u6548\uff0c\u5927\u90e8\u5206\u7684\u529f\u52b3\u90fd\u4f1a\u5f52\u5230\u524d\u4efb\u7684\u540d\u4e0b\uff0c\u6240\u4ee5\u7ee7\u4efb\u8005\u90fd\u975e\u5e38\u5fe0\u8bda\u7684\u4f53\u73b0\u51fa\u201c\u601d\u8def\u5f00\u62d3\u3001\u79ef\u6781\u521b\u65b0\u201d\u7684\u7cbe\u795e\uff0c\u8fd9\u5728\u5730\u5e02\u4e00\u7ea7\u7684\u57ce\u5e02\u5efa\u8bbe\u4e0e\u53d1\u5c55\u4e2d\u8868\u73b0\u5c24\u5176\u7684\u660e\u663e\u3002\u5012\u4e5f\u4e0d\u662f\u8bf4\u7701\u4e00\u7ea7\u6ca1\u6709\u8fd9\u65b9\u9762\u7684\u5f0a\u7aef\uff0c\u53ea\u662f\u666e\u901a\u6c11\u4f17\u5f88\u96be\u76f4\u63a5\u4f53\u4f1a\u7701\u91cc\u7684\u671d\u4e09\u66ae\u56db\u3002</p><p>\u3000\u3000\u7edf\u8f96\u6574\u4e2a\u533a\u57df\u5185\u7684\u7ecf\u6d4e\u6d3b\u52a8\uff0c\u7701\u91cc\u638c\u63e1\u7684\u8d44\u6e90\u4e5f\u5f88\u6709\u9650\uff0c\u8fd9\u4e2a\u591a\u4e86\uff0c\u90a3\u4e2a\u5c31\u5c11\u4e86\uff0c\u91cd\u70b9\u652f\u6301\u54ea\u4e00\u65b9\u9762\uff0c\u54ea\u4e00\u65b9\u9762\u53ef\u4ee5\u7a0d\u7a0d\u653e\u7f13\u4e00\u4e9b\uff0c\u53ea\u8981\u6709\u8fd9\u6837\u7684\u8c03\u6574\uff0c\u5c31\u5df2\u7ecf\u5f71\u54cd\u5230\u6574\u4e2a\u533a\u57df\u7684\u7ecf\u6d4e\u5b89\u6392\u3002</p><p>\u3000\u3000\u5f90\u5b66\u5e73\u6709\u8fd9\u6837\u7684\u62c5\u5fe7\uff0c\u90a3\u662f\u518d\u6b63\u5e38\u4e0d\u8fc7\uff0c\u53ea\u662f\u65e0\u6cd5\u660e\u8bf4\u800c\u5df2\uff0c\u5f20\u606a\u4e5f\u65e0\u53ef\u5948\u4f55\uff0c\u6709\u4e9b\u4e8b\u60c5\u662f\u4ed6\u5b8c\u5168\u65e0\u6cd5\u63d2\u624b\u7684\u3002</p><p>\u3000\u3000\u4ece\u65b0\u6885\u82d1\u51fa\u6765\uff0c\u5df2\u7ecf\u5feb\u5230\u51cc\u6668\u5341\u4e8c\u70b9\u4e86\uff0c\u5728\u56de\u72ee\u5b50\u56ed\u7684\u522b\u5885\u4e4b\u524d\uff0c\u5f20\u606a\u60f3\u7740\u53bb1978\u5750\u4e00\u5750\u3002\u5b59\u9759\u6aac12\u670816\u65e5\u5c31\u79bb\u5f00\u9999\u6e2f\u56de\u5230\u7701\u57ce\uff0c\u5728\u9999\u6e2f\u7684\u90a3\u6bb5\u65f6\u95f4\uff0c\u5f20\u606a\u81ea\u8ba4\u4e3a\u4e0e\u5b59\u9759\u6aac\u6539\u5584\u4e86\u5173\u7cfb\uff0c\u5929\u6c14\u9634\u6c89\u7684\u591c\u665a\uff0c\u521a\u521a\u8c08\u8bba\u8fc7\u6c89\u91cd\u7684\u8bdd\u9898\uff0c\u4e0d\u6653\u5f97\u4ec0\u4e48\u65f6\u5019\u7761\u610f\u624d\u4f1a\u964d\u4e34\u5230\u610f\u8bc6\u65f6\uff0c\u53bb\u9152\u5427\u559d\u676f\u9152\uff0c\u5012\u662f\u4e0d\u9519\u7684\u9009\u62e9\u3002</p><p>\u3000\u3000\u8f66\u5230\u5b66\u5e9c\u5df7\uff0c\u5f20\u606a\u4e0b\u8f66\u65f6\uff0c\u624d\u7a81\u7136\u53d1\u73b0\u5929\u7a7a\u4e0b\u8d77\u96ea\u6765\uff0c\u4ed6\u4e4b\u524d\u5728\u8f66\u91cc\u4e00\u76f4\u5728\u60f3\u4e8b\u60c5\uff1a\u5f90\u5b66\u5e73\u4e00\u8d70\uff0c\u5c40\u52bf\u5c31\u9661\u7136\u590d\u6742\u8d77\u6765\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u95ee\u5085\u4fca\uff1a\u201c\u8bb8\u8001\u5e08\u4ed6\u4eba\u5728\u54ea\u91cc\uff1f\u201d</p><p>\u3000\u3000\u5f88\u591a\u4eba\u4e0e\u4e8b\uff0c\u5f20\u606a\u90fd\u4e0d\u5927\u5173\u5fc3\uff0c\u5085\u4fca\u5012\u4e0d\u4ec5\u4ec5\u53ea\u662f\u4fdd\u9556\u517c\u53f8\u673a\u4e86\uff0c\u4e5f\u9010\u6e10\u62c5\u5f53\u8d77\u52a9\u624b\u7684\u89d2\u8272\uff0c\u50cf\u9526\u6e56\u65d7\u4e0b\u5e9e\u5927\u3001\u590d\u6742\u7684\u4eba\u4e8b\u52a8\u5411\uff0c\u5085\u4fca\u603b\u8981\u5e2e\u5f20\u606a\u7559\u610f\u7684\u3002\u5085\u4fca\u9ad8\u4e2d\u65f6\u5b66\u4e60\u4f18\u5f02\uff0c\u53ea\u662f\u5bb6\u8d2b\u4e3a\u4e86\u51cf\u8f7b\u5bb6\u91cc\u7684\u8d1f\u62c5\u624d\u53bb\u53c2\u519b\uff0c\u540e\u6765\u56e0\u4e3a\u674e\u660e\u745c\u53d7\u8fb1\u7684\u4e8b\u60c5\u64c5\u81ea\u56de\u5bb6\u4f24\u4eba\u8fdb\u4e86\u76d1\u72f1\uff0c\u51fa\u72f1\u540e\u627e\u4e0d\u5230\u5de5\u4f5c\u4e5f\u53ea\u80fd\u8ddf\u7740\u76db\u9752\u53ae\u6df7\u3002\u5f20\u606a\u8fd8\u95ee\u8d77\u674e\u660e\u745c\uff0c\u4ed6\u4eec\u662f\u4e0d\u662f\u9ad8\u4e2d\u65f6\u5c31\u5904\u5bf9\u8c61\uff0c\u674e\u660e\u745c\u7b11\u7740\u544a\u8bc9\u5f20\u606a\u8bf4\u5085\u4fca\u5728\u5b66\u6821\u65f6\u4eba\u50bb\u5929\u771f\uff0c\u53c2\u519b\u5012\u662f\u5199\u8fc7\u51e0\u5c01\u4fe1\u5f80\u6765\uff0c\u4e5f\u591a\u662f\u8ba8\u8bba\u5b66\u4e60\u4e0a\u7684\u4e8b\u60c5\uff0c\u5085\u4fca\u5f53\u65f6\u5728\u90e8\u961f\u91cc\u662f\u51c6\u5907\u8003\u519b\u6821\uff1b\u6709\u4e00\u6b21\u5f20\u606a\u53bb\u5085\u4fca\u5bb6\u505a\u5ba2\uff0c\u8d76\u5de7\u674e\u660e\u745c\u5728\u5bb6\u6536\u62fe\u5c4b\u5b50\uff0c\u6709\u673a\u4f1a\u770b\u5230\u5085\u4fca\u5728\u90e8\u961f\u91cc\u8ddf\u674e\u660e\u745c\u5bc4\u7684\u60c5\u4e66\uff0c\u9760\uff0c\u54ea\u91cc\u662f\u60c5\u8282\uff0c\u7b80\u76f4\u5c31\u662f\u4e00\u5c01\u95ee\u9898\u7b54\u7591\uff0c\u8fde\u79f0\u547c\u4e0a\u90fd\u606d\u606d\u656c\u656c\u7684\u5199\u4e0a\u201c\u674e\u660e\u745c\u540c\u5b66\u201d\u4e94\u4e2a\u5b57\uff0c\u8ba9\u4eba\u604d\u7136\u4ee5\u4e3a\u662f\u6587\u9769\u65f6\u671f\u5199\u6210\u7684\u3002</p><p>\u3000\u3000\u201c\u524d\u4e9b\u5929\u542c\u8bf4\u8bb8\u8001\u5e08\u5728\u4e0a\u9633\u8fdb\u5c71\u91cc\u65f6\u6dcb\u4e86\u4e00\u573a\u96e8\u6709\u4e9b\u611f\u5192\uff0c\u6ca1\u6709\u56de\u6d77\u5dde\uff0c\u800c\u662f\u56de\u7701\u57ce\u4f11\u606f\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u8bb8\u8001\u5e08\u5728\u7701\u57ce\u554a\uff0c\u8fd9\u4e48\u665a\u5c31\u7b97\u4e86\uff0c\u660e\u5929\u53bb\u627e\u4ed6\u3002\u201d</p><p>\u3000\u3000\u96ea\u5f80\u8116\u5b50\u91cc\u94bb\uff0c\u51b0\u51c9\u7684\uff0c\u5f20\u606a\u5c06\u9886\u5b50\u7ad6\u8d77\u6765\uff0c\u5f80\u5b66\u5e9c\u5df7\u91cc\u8d70\u53bb\uff0c\u5085\u4fca\u8981\u5230\u5df7\u5b50\u80cc\u540e\u627e\u5730\u65b9\u5c06\u8f66\u5b50\u505c\u4e0b\u6765\u3002</p><p>\u3000\u3000\u5230\u5b66\u671f\u672b\u4e86\uff0c\u6df1\u591c\u7684\u5b66\u5e9c\u5df7\u5012\u662f\u6bd4\u5f80\u5e38\u66f4\u70ed\u95f9\u4e00\u4e9b\uff0c\u4e60\u60ef\u5230\u5b66\u671f\u672b\u901a\u5bb5\u6e29\u4e66\u7684\u5b66\u751f\uff0c\u51cc\u6668\u65f6\u5206\u4f1a\u6709\u5f88\u591a\u4eba\u5230\u5b66\u5e9c\u5df7\u4e70\u4e9b\u5403\u98df\u586b\u586b\u809a\u5b50\uff0c\u5bb6\u5883\u7a0d\u597d\u7684\u4e00\u4e9b\u5b66\u751f\uff0c\u5c31\u76f4\u63a5\u4f1a\u5728\u5496\u5561\u5427\u3001\u9152\u5427\u91cc\u6e29\u4e66\uff0c\u8fd9\u65f6\u5019\uff0c\u5b66\u6821\u5bbf\u820d\u4e0e\u6559\u5ba4\u91cc\u90fd\u6ca1\u6709\u7a7a\u8c03\uff0c\u5efa\u90ba\u7684\u51ac\u5929\u53c8\u683c\u5916\u7684\u6e7f\u5bd2\uff0c\u5929\u5bd2\u5730\u51bb\u7684\uff0c\u7684\u786e\u6ca1\u6709\u6bd4\u64ad\u653e\u7740\u8f7b\u7f13\u8f7b\u97f3\u4e50\u7684\u5496\u5561\u5427\u3001\u9152\u5427\u66f4\u9002\u5408\u6e29\u4e66\u7684\u5730\u65b9\u4e86\uff0c\u9152\u5427\u901a\u5e38\u5728\u8fd9\u6bb5\u65f6\u95f4\u4e5f\u4e0d\u518d\u5608\u6742\u55a7\u95f9\uff0c\u8fd9\u4e3b\u8981\u5f52\u529f\u4e8e\u5b66\u5e9c\u5df7\u5c3e\u7684\u97f3\u4e50\u5b66\u9662\u7684\u5b66\u751f\uff0c\u97f3\u4e50\u5b66\u9662\u90a3\u4e9b\u957f\u76f8\u6f02\u4eae\u7684\u5973\u5b69\u5b50\u5927\u6982\u662f\u8fd9\u5ea7\u57ce\u5e02\u6700\u65e9\u840c\u751f\u5c0f\u8d44\u610f\u8bc6\u7684\u7fa4\u4f53\uff0c\u5b66\u5e9c\u5df7\u897f\u9762\u7684\u5e08\u5927\u4e5f\u4e0d\u843d\u540e\u592a\u591a\uff0c\u5012\u662f\u6700\u8d1f\u76db\u540d\u7684\u4e1c\u6d77\u5927\u5b66\u5219\u4e61\u571f\u6c14\u606f\u6d53\u90c1\u4e00\u4e9b\u3002\u90a3\u4e9b\u4ece\u4e1c\u6d77\u5927\u5b66\u6bd5\u4e1a\u53c8\u5728\u8fd9\u5ea7\u57ce\u5e02\u6df7\u6210\u7cbe\u82f1\u4eba\u58eb\u7684\u5bb6\u4f19\u4eec\u6025\u4e8e\u4e14\u4e60\u60ef\u5230\u5b66\u5e9c\u5df7\u6765\u6d41\u8fde\u7684\u5fc3\u60c5\uff0c\u5012\u662f\u5f88\u80fd\u591f\u7406\u89e3\u3002</p><p>\u3000\u3000\u8fd9\u4e48\u8bf4\u5427\uff0c\u97f3\u4e50\u5b66\u9662\u3001\u5e08\u5927\u7f8e\u5973\u5bf9\u4e1c\u6d77\u5927\u5b66\u5728\u8bfb\u7684\u8d2b\u56f0\u5b66\u5b50\u90fd\u4e0d\u5927\u611f\u5174\u8da3\uff0c\u4f46\u662f\u5bf9\u90a3\u4e9b\u5b66\u6210\u8e0f\u5165\u793e\u4f1a\u7684\u4e1c\u5927\u5b66\u5b50\u5012\u8fd8\u662f\u9752\u7750\u6709\u52a0\u3002</p><p>\u3000\u3000\u8fd9\u73a9\u827a\u8ddf\u7092\u80a1\u76f8\u5dee\u65e0\u51e0\uff0c\u6f5c\u529b\u80a1\u5728\u5e95\u90e8\u8707\u4f0f\u65f6\uff0c\u5373\u4f7f\u6709\u4eba\u76f8\u4fe1\u8fd9\u662f\u6f5c\u529b\u80a1\uff0c\u4e5f\u5f88\u5c11\u6709\u4eba\u5403\u8fdb\u7684\uff0c\u8fd9\u5927\u6982\u8ddf\u56fd\u4eba\u6027\u60c5\u6d6e\u8e81\u3001\u7f3a\u4e4f\u8db3\u591f\u7684\u8010\u5fc3\u6709\u5173\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u63a8\u95e8\u8d70\u8fdb1978\uff0c\u56e0\u5bd2\u51b7\u800c\u7ef7\u7d27\u7684\u8eab\u4f53\uff0c\u8fdb\u5165\u6e29\u6696\u5982\u6625\u7684\u5ba4\u5185\uff0c\u5c31\u4e00\u4e0b\u5b50\u7f13\u548c\u8d77\u6765\uff0c\u9152\u5427\u91cc\u4e5f\u6ca1\u6709\u592a\u591a\u4eba\uff0c\u5df2\u7ecf\u8fc7\u4e86\u5341\u4e8c\u70b9\uff0c\u8212\u7f13\u7684\u8f7b\u97f3\u4e50\u5728\u8033\u6735\u4f20\u8361\uff0c\u5b59\u9759\u6aac\u63cf\u7740\u6d45\u7d2b\u8272\u7684\u773c\u5f71\u5374\u5750\u5728\u67dc\u53f0\u540e\u770b\u4e66\uff0c\u5f20\u606a\u4f38\u624b\u62ff\u8fc7\u6765\u4e00\u770b\uff0c\u5927\u8dcc\u773c\u955c\uff0c\u7adf\u7136\u662f\u300a\u793e\u4f1a\u4e3b\u4e49\u7ecf\u6d4e\u5b66\u6982\u8bba\u300b\uff0c\u8fd8\u7ed9\u5b59\u9759\u6aac\uff1a\u201c\u4f60\u4eec\u97f3\u4e50\u5b66\u9662\u821e\u7f8e\u7cfb\u7684\u5b66\u751f\u9700\u8981\u5b66\u8fd9\u4e2a\uff1f\u201d</p><p>\u3000\u3000\u201c\u5413\u6211\u4e00\u8df3\uff0c\u4f60\u5b66\u4eba\u505a\u8d3c\u554a\uff1f\u201d\u5b59\u9759\u6aac\u62cd\u4e86\u62cd\u80f8\u53e3\uff0c\u201c\u4f60\u4eec\u4ec0\u4e48\u65f6\u5019\u56de\u6765\u7684\uff0c\u4f60\u7684\u7b2c\u4e09\u6761\u817f\u5462\uff1f\u201d</p><p>\u3000\u3000\u5b59\u9759\u6aac\u95ee\u8d77\u7b2c\u4e09\u6761\u817f\uff0c\u5f20\u606a\u9664\u4e86\u60f3\u8d77\u62d0\u6756\u4e4b\u5916\uff0c\u8fd8\u60f3\u53e6\u5916\u4e00\u6839\u4e1c\u897f\uff0c\u4e0d\u8fc7\u60f3\u5230\u8c03\u620f\u5b59\u9759\u6aac\u672a\u5fc5\u6709\u4ec0\u4e48\u597d\u4e0b\u573a\uff0c\u5c31\u563f\u563f\u7b11\u4e86\u4e00\u4e0b\uff0c\u5c41\u80a1\u632a\u5750\u5230\u5427\u53f0\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u628a\u53f6\u54e5\u7684\u9152\u62ff\u51fa\u6765\u79c1\u5206\u6389\u2026\u2026\u4e0d\u8fc7\u4f60\u4e4b\u524d\u5077\u559d\u7684\u9152\u4e0d\u80fd\u7b97\u6211\u5934\u4e0a\u3002\u201d</p><p>\u3000\u3000\u201c\u8ddf\u5973\u4eba\u65a4\u65a4\u8ba1\u8f83\uff0c\u4f60\u591a\u5927\u51fa\u606f\uff1f\u201d\u5b59\u9759\u6aac\u4fef\u8eab\u671d\u9152\u67dc\u91cc\u62ff\u51fa\u53ea\u5269\u4e0b\u5927\u534a\u74f6\u7684\u829d\u534e\u58eb\uff0c\u201c\u5c31\u5269\u4e0b\u534a\u74f6\u4e86\uff0c\u559d\u5149\u8fd9\u534a\u74f6\uff0c\u8fd9\u9152\u67dc\u5c31\u7a7a\u4e86\uff0c\u4f60\u662f\u4e0d\u662f\u8003\u8651\u5728\u655d\u9152\u5427\u4eab\u53d7\u4e86\u4e00\u4e0b\u7279\u6b8a\u7684\u5f85\u9047\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u7b97\u4e86\uff0c\u9152\u8d3c\u96be\u9632\u3002\u201d</p><p>\u3000\u3000\u5b59\u9759\u9999\u5c06\u9152\u5427\u4ea4\u7ed9\u5b59\u9759\u6aac\uff0c\u53f6\u5efa\u658c\u5c31\u6781\u5c11\u8fc7\u6765\uff0c\u5f20\u606a\u8bb0\u5f97\u4e0a\u56de\u8fc7\u6765\u65f6\uff0c\u8fd8\u6709\u4e03\u516b\u74f6\u9876\u7ea7\u7684\u829d\u534e\u58eb\uff0c\u60f3\u5230\u5b59\u9759\u6aac\u7684\u9152\u91cf\uff0c\u5927\u6982\u4e5f\u9700\u8981\u65f6\u65f6\u953b\u70bc\u5427\u3002</p><p>\u3000\u3000\u5f20\u606a\u5750\u5728\u5427\u53f0\u524d\uff0c\u8f6c\u8eab\u5f80\u91cc\u770b\u53bb\uff0c\u770b\u89c1\u6797\u51b0\u8ddf\u5979\u90a3\u4e2a\u957f\u76f8\u6587\u5f31\u7684\u7537\u670b\u53cb\u5728\u4e00\u8d77\uff0c\u5c31\u5750\u5728\u5927\u5385\u4e00\u89d2\u6e29\u4e66\uff0c\u4e0e\u6797\u51b0\u4ea4\u60c5\u6cdb\u6cdb\uff0c\u5f20\u606a\u5012\u6ca1\u6709\u60f3\u7740\u523b\u610f\u53bb\u6253\u62db\u547c\uff0c\u95ee\u5b59\u9759\u6aac\uff1a\u201c\u53e6\u4e24\u4e2a\u4eba\u5462\uff0c\u600e\u4e48\u4eca\u5929\u53ea\u770b\u5230\u4f60\uff1f\u201d</p><p>\u3000\u3000\u201c\u5979\u4eec\u56de\u53bb\u4e13\u5fc3\u590d\u4e60\u4e86\uff0c\u8fd9\u51e0\u5929\u90fd\u4e0d\u5728\uff0c\u636e\u8bf4\u662f\u4e1c\u6d77\u56db\u5927\u540d\u6355\u7684\u8bfe\u2026\u2026\u201d</p><p>\u3000\u3000\u6797\u51b0\u4e00\u76f4\u5728\u51c6\u5907\u8003\u7814\uff0c\u4e00\u822c\u7a0b\u5ea6\u7684\u8003\u8bd5\u96be\u4e0d\u5012\u5979\uff0c\u8fd9\u65f6\u5019\u5927\u6982\u4e5f\u53ea\u662f\u5728\u8ddf\u5979\u7537\u670b\u53cb\u5728\u770b\u8003\u7814\u7684\u4e66\uff1b\u8bb8\u7ef4\u3001\u4ee4\u5c0f\u71d5\u5e73\u65e5\u5c31\u6ca1\u6709\u5fc3\u601d\u770b\u4e66\uff0c\u8fd9\u65f6\u5019\u8981\u662f\u5728\u9152\u5427\u91cc\u6e29\u4e66\uff0c\u5012\u4e5f\u4e0d\u80fd\u5750\u7740\u4e0d\u7ba1\u9152\u5427\u91cc\u7684\u4e8b\u60c5\uff0c\u8fd8\u4e0d\u5982\u7d22\u6027\u5728\u6559\u5ba4\u6216\u8005\u5bbf\u820d\u91cc\u6e29\u4e66\u3002</p><p>\u3000\u3000\u5085\u4fca\u8d70\u8fdb\u6765\uff0c\u544a\u8bc9\u5f20\u606a\u8bb8\u9e3f\u4f2f\u521a\u6253\u7535\u8bdd\u6765\uff1a\u201c\u8bb8\u8001\u5e08\u6015\u6253\u6270\u4f60\u8ddf\u5f90\u7701\u957f\u8bf4\u8bdd\uff0c\u7b49\u5230\u73b0\u5728\u624d\u7ed9\u6211\u6253\u7535\u8bdd\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4ed6\u77e5\u9053\u6211\u56de\u6765\uff1f\u201d\u5f20\u606a\u62cd\u62cd\u8111\u888b\uff0c\u201c\u4ed6\u5728\u7701\u57ce\u4e5f\u5929\u5929\u53bb\u516c\u53f8\uff0c\u771f\u662f\u8f9b\u82e6\u4ed6\uff0c\u4ed6\u65e2\u7136\u6ca1\u7761\uff0c\u4f60\u5f00\u8f66\u9001\u6211\u53bb\u89c1\u4ed6\u3002\u201d</p><p>\u3000\u3000\u201c\u8bb8\u8001\u5e08\u559c\u6b22\u559d\u9152\uff0c\u6211\u63a5\u4ed6\u8fc7\u6765\u559d\u9152\u4e0d\u5c31\u884c\u4e86\uff1f\u201d\u5085\u4fca\u5efa\u8bae\u9053\u3002</p><p>\u3000\u3000\u201c\u4e5f\u884c\u3002\u201d\u5f20\u606a\u770b\u7740\u90a3\u5927\u534a\u74f6\u829d\u534e\u58eb\u5df2\u7ecf\u8ba9\u5b59\u9759\u6aac\u5012\u5165\u4e24\u53ea\u9ad8\u811a\u676f\u5b50\u91cc\uff0c\u7b11\u7740\u8ddf\u5b59\u9759\u6aac\u8bf4\uff0c\u201c\u6211\u4eec\u8d76\u7d27\u5c06\u8fd9\u4e24\u676f\u9152\u559d\u5b8c\uff0c\u7b49\u4f1a\u513f\u6765\u7684\u90a3\u4e2a\u4eba\u662f\u771f\u9152\u9b3c\uff0c\u4f60\u8fd9\u91cc\u4e5f\u518d\u6ca1\u6709\u8fd9\u4e48\u597d\u7684\u9152\u62db\u5f85\u4ed6\u2026\u2026\u201d</p><p>\u3000\u3000\u8fd9\u79cd\u829d\u534e\u58eb\u6709\u56db\u5341\u4e09\u5ea6\uff0c700\u6beb\u5347\u88c5\uff0c\u5269\u4e0b\u5927\u534a\u74f6\uff0c\u5dee\u4e0d\u591a\u4e5f\u6709\u8fd1\u4e00\u65a4\u7684\u91cf\uff0c\u5f20\u606a\u901a\u5e38\u559c\u6b22\u52a0\u7eaf\u51c0\u6c34\u4e0e\u51b0\u5151\u7740\u559d\uff0c\u4e0d\u8fc7\u9e21\u5c3e\u9152\u4e5f\u6781\u5c11\u559d\u3002</p><p>\u3000\u3000\u7b49\u5230\u8bb8\u9e3f\u4f2f\u8d76\u8fc7\u6765\uff0c\u5f20\u606a\u4e0e\u5b59\u9759\u6aac\u5404\u81ea\u5c06\u676f\u4e2d\u9152\u559d\u5c3d\uff0c\u5f20\u606a\u7761\u5f97\u5168\u8eab\u6696\u6d0b\u6d0b\u7684\uff0c\u5b59\u9759\u6aac\u4fcf\u8138\u8273\u7ea2\uff0c\u773c\u775b\u91cc\u6d6e\u51fa\u4e09\u5206\u9189\u610f\uff0c\u8fd9\u65f6\u5019\u9152\u5427\u91cc\u4e5f\u6ca1\u6709\u4ec0\u4e48\u4e8b\u60c5\uff0c\u591a\u662f\u6e29\u4e66\u7684\u5b66\u751f\uff0c\u8fd8\u6709\u4e24\u540d\u6253\u5de5\u7684\u5b66\u751f\u8d1f\u8d23\u7167\u5e94\u5c31\u591f\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u542c\u8bb8\u9e3f\u4f2f\u8fdb\u6765\u6709\u4e9b\u54b3\u55fd\uff1a\u201c\u8eab\u4f53\u8fd8\u6ca1\u6709\u6062\u590d\uff0c\u600e\u4e48\u4e0d\u591a\u4f11\u606f\uff1f\u201d</p><p>\u3000\u3000\u201c\u53ea\u662f\u611f\u5192\uff0c\u5dee\u4e0d\u591a\u597d\u4e86\uff0c\u8fd8\u6709\u4e9b\u54b3\u55fd\u7f62\u4e86\uff0c\u201d\u8bb8\u9e3f\u4f2f\u4e0d\u4ecb\u610f\u7684\u6325\u4e86\u6325\uff0c\u201c\u518d\u591a\u559d\u4e24\u676f\u9152\u5c31\u597d\u4e86\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u95ee\u5b59\u9759\u6aac\uff1a\u201c\u6c64\u529b\u6c34\u6e29\u4e00\u4e9b\uff0c\u52a0\u67e0\u6aac\u52a0\u91d1\u9152\uff0c\u518d\u52a0\u4e9b\u8702\u871c\uff0c\u5e2e\u8bb8\u8001\u5e08\u8c03\u4e00\u676f\u91d1\u6c64\u529b\u2026\u2026\u201d\u53c8\u8ddf\u8bb8\u9e3f\u4f2f\u89e3\u91ca\uff0c\u201c\u91d1\u9152\u662f\u9e21\u5c3e\u9152\u7684\u516d\u5927\u57fa\u9152\u4e4b\u4e00\uff0c\u6d0b\u9b3c\u5b50\u90fd\u559c\u6b22\u8c03\u6210\u9e21\u5c3e\u9152\u559d\uff0c\u636e\u8bf4\u91d1\u9152\u8fd9\u4e48\u8c03\u80fd\u7f13\u89e3\u611f\u5192\u75c7\u72b6\uff0c\u5929\u5bd2\u5730\u51bb\u7684\u8bf7\u4f60\u4ece\u5bbe\u9986\u51fa\u6765\uff0c\u4f60\u7684\u611f\u5192\u518d\u52a0\u91cd\uff0c\u6211\u5c31\u7f6a\u5927\u4e86\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u8bb8\u9e3f\u4f2f\u5c31\u5750\u5728\u5427\u53f0\u7684\u4e00\u89d2\u8bf4\u8bdd\uff0c\u5f90\u5b66\u5e73\u79bb\u5f00\u4e1c\u6d77\uff0c\u674e\u8fdc\u6e56\u9876\u66ff\u5f90\u5b66\u6e56\u62c5\u4efb\u7701\u957f\uff0c\u5c40\u52bf\u5c31\u590d\u6742\u4e86\uff0c\u5f20\u606a\u6709\u4e9b\u770b\u4e0d\u900f\u5f7b\uff0c\u8bb8\u9e3f\u4f2f\u8bf4\u9053\uff1a\u201c\u5f90\u5b66\u5e73\u8d70\u65e9\u4e86\u4e9b\uff0c\u4e0d\u7136\u5c31\u80fd\u7ef4\u6301\u6d77\u5dde\u7684\u5c40\u9762\u4e0d\u53d8\u3002\u770b\u4e0a\u53bb\u5bf9\u5510\u5b66\u8c26\u4e0d\u5927\u516c\u5e73\uff0c\u4f46\u662f\u6b64\u65f6\u7684\u5468\u5bcc\u660e\u5df2\u7ecf\u6ca1\u6709\u80fd\u529b\u6dfb\u4ec0\u4e48\u4e71\u4e86\uff0c\u7ef4\u6301\u8fd9\u6837\u7684\u5c40\u9762\u5bf9\u5927\u5bb6\u90fd\u662f\u6709\u597d\u5904\u7684\u3002\u773c\u4e0b\uff0c\u6700\u5927\u7684\u53ef\u80fd\u5c31\u662f\u5468\u5bcc\u660e\u660e\u5e74\u9000\u5c45\u4e8c\u7ebf\u53bb\u4eba\u5927\uff0c\u5510\u5b66\u8c26\u7406\u6240\u5f53\u7136\u7684\u5f53\u4e66\u8bb0\uff0c\u7701\u91cc\u4e0d\u4f1a\u5e0c\u671b\u6d77\u5dde\u7684\u4e66\u8bb0\u3001\u5e02\u957f\u548c\u548c\u7766\u772d\u3001\u4e00\u56e2\u548c\u6c14\uff0c\u90a3\u5c31\u4f1a\u7a7a\u964d\u4e00\u4e2a\u5e02\u957f\u5230\u6d77\u5dde\uff0c\u5510\u5b66\u8c26\u867d\u7136\u5982\u613f\u5f53\u4e0a\u5e02\u59d4\u4e66\u8bb0\uff0c\u4f46\u662f\u5374\u8981\u7ed9\u4eba\u5927\u4e3b\u4efb\u4e0e\u5e02\u957f\u5939\u5728\u4e2d\u95f4\uff0c\u8fd9\u4e2a\u5c40\u9762\u4f1a\u6bd4\u8f83\u96be\u53d7\u2026\u2026\u201d</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u8fd8\u4e0d\u80fd\u8ddf\u522b\u4eba\u8bf4\u5f90\u5b66\u5e73\u6709\u4e1c\u5c71\u518d\u8d77\u7684\u673a\u4f1a\uff0c\u53f6\u81fb\u6c11\u5728\u653f\u6cbb\u4e0a\u8981\u6d3b\u8dc3\u8d77\u6765\u4e5f\u8981\u9700\u8981\u4e00\u6bb5\u65f6\u95f4\uff0c\u8981\u662f\u8fd9\u6bb5\u65f6\u95f4\u8ba9\u5c0f\u4eba\u5f97\u5fd7\u4e5f\u7740\u5b9e\u4f1a\u8ba9\u4eba\u90c1\u95f7\uff0c\u7279\u522b\u662f\u56fd\u5185\u6709\u4eba\u8d70\u8336\u51c9\u7684\u4f20\u7edf\uff0c\u5f90\u5b66\u5e73\u8fc1\u53bb\u7684\u804c\u52a1\u770b\u4e0a\u53bb\u5df2\u7ecf\u6ca1\u4ec0\u4e48\u524d\u9014\uff0c\u8c01\u6653\u5f97\u7701\u91cc\u4f1a\u4e0d\u4f1a\u6709\u8c01\u8e66\u51fa\u6765\u62bd\u9526\u6e56\u51e0\u68cd\u5b50\uff1f\u5f20\u606a\u90fd\u6253\u7b97\u5b8c\u6210\u5609\u4fe1\u5b9e\u4e1a\u7684\u589e\u53d1\u5e76\u8d2d\u6848\u4e4b\u540e\uff0c\u8001\u8001\u5b9e\u5b9e\u7684\u8fc7\u4e0a\u4e00\u4e24\u5e74\u7684\u5927\u5b66\u751f\u6d3b\uff0c\u4e0d\u518d\u6405\u98ce\u6405\u96e8\u4e86\u3002</p><p>\u3000\u3000\u201c\u4f60\u600e\u4e48\u5728\u7701\u57ce\uff1f\u201d\u6797\u51b0\u6536\u62fe\u4e1c\u897f\u51c6\u5907\u56de\u53bb\uff0c\u770b\u5230\u5f20\u606a\u5750\u5427\u53f0\u7684\u4e00\u89d2\uff0c\u6b23\u559c\u7684\u5927\u6253\u62db\u547c\u3002</p><p>\u3000\u3000\u201c\u521a\u521a\u8fc7\u6765\uff0c\u770b\u5230\u4f60\u4eec\u6e29\u4e66\u592a\u8ba4\u771f\uff0c\u6ca1\u5fcd\u5fc3\u6253\u6405\u4f60\u4eec\u3002\u201d\u5f20\u606a\u80cc\u9760\u7740\u5427\u53f0\uff0c\u8ddf\u6797\u51b0\u6253\u62db\u547c\u3002</p><p>\u3000\u3000\u5bd2\u6684\u4e86\u51e0\u53e5\uff0c\u4e5f\u6ca1\u89c1\u6797\u51b0\u8ddf\u5979\u7537\u670b\u53cb\u4ed8\u8d26\u5c31\u8d70\u4e86\u51fa\u53bb\uff0c\u5f20\u606a\u8fd8\u8bb0\u5f97\u6797\u51b0\u5979\u7537\u670b\u53cb\u66fe\u9119\u5937\u6709\u94b1\u4eba\u7684\u6076\u8da3\u5473\u5462\u3002</p><p>\u3000\u3000\u5dee\u4e0d\u591a\u5230\u6253\u70ca\u7684\u65f6\u95f4\uff0c\u9152\u5427\u91cc\u7684\u4eba\u9646\u7eed\u79bb\u53bb\u3002</p><p>\u3000\u3000\u5f20\u606a\u89c1\u65f6\u95f4\u5dee\u4e0d\u591a\uff0c\u8ba9\u5085\u4fca\u5148\u9001\u8bb8\u9e3f\u4f2f\u56de\u5bbe\u9986\uff0c\u8ba9\u8bb8\u9e3f\u4f2f\u660e\u5929\u966a\u4ed6\u4e00\u8d77\u56de\u6d77\u5dde\u4f11\u606f\u51e0\u5929\uff0c\u7b49\u8eab\u4f53\u5b8c\u5168\u6062\u590d\u4e86\u518d\u8bf4\u3002</p><p>\u3000\u3000\u4e24\u540d\u6253\u5de5\u7684\u5b66\u751f\u6536\u62fe\u5b8c\u5148\u8d70\uff0c\u5f20\u606a\u95ee\u5b59\u9759\u6aac\uff1a\u201c\u6211\u5148\u9001\u4f60\u56de\u516c\u5bd3\uff1f\u201d</p><p>\u3000\u3000\u201c\u516c\u5bd3\u91cc\u7684\u7a7a\u8c03\u574f\u4e86\uff0c\u4f4f\u91cc\u9762\u51b7\u51b0\u51b0\u7684\uff0c\u4f60\u9001\u6211\u56de\u5bbf\u820d\u5427\u3002\u201d</p><p>\u3000\u3000\u6bcf\u5230\u671f\u672b\u90a3\u4e24\u4e09\u5468\u7684\u65f6\u95f4\uff0c\u4e1c\u5927\u3001\u5e08\u5927\u3001\u97f3\u4e50\u5b66\u9662\u7684\u5bbf\u820d\u697c\u5c31\u4e0d\u4f1a\u4e00\u5230\u65f6\u95f4\u70b9\u5c31\u51c6\u65f6\u9501\u95e8\uff0c\u5728\u6559\u5ba4\u91cc\u901a\u5bb5\u6e29\u4e66\u7684\u4eba\u5f88\u591a\uff0c\u65f6\u4e0d\u65f6\u6709\u4eba\u4f1a\u56de\u5bbf\u820d\uff0c\u7ba1\u7406\u5458\u4e5f\u6015\u9ebb\u70e6\uff0c\u66f4\u65e0\u6cd5\u4e25\u683c\u575a\u6301\u539f\u5148\u7684\u4f5c\u606f\u5236\u5ea6\u3002</p><p>\u3000\u3000\u4e0d\u8fc7\u60f3\u5230\u4e0a\u56de\u9001\u5b59\u9759\u6aac\u56de\u5bbf\u820d\u7684\u60c5\u5f62\uff0c\u90a3\u8bb0\u5fc6\u603b\u4e0d\u80fd\u7b97\u592a\u7f8e\u597d\uff0c\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u4f60\u4e0d\u4f1a\u518d\u6361\u4e00\u5757\u7816\u628a\u4f60\u4eec\u5bbf\u820d\u5927\u95e8\u7838\u788e\u8d56\u6211\u5934\u4e0a\u6765\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u77a7\u4f60\u591a\u5927\u7684\u80c6\u5b50\uff0c\u201d\u5b59\u9759\u6aac\u4e0d\u5c51\u7684\u8bf4\uff0c\u201c\u6211\u4eec\u5bbf\u820d\u6709\u4e2a\u5973\u5b69\u5b50\u4eca\u5929\u591c\u4e0d\u5f52\u5bbf\uff0c\u6709\u5f20\u5e8a\u7a7a\u7740\uff0c\u4f60\u6709\u80c6\u5b50\u4eca\u5929\u665a\u4e0a\u5c31\u7761\u6211\u4eec\u5bbf\u820d\u91cc\uff0c\u6211\u4eec\u5bbf\u820d\u6709\u4e24\u4e2a\u7f8e\u5973\uff0c\u4f60\u4e5f\u89c1\u8fc7\uff0c\u4f60\u6709\u672c\u4e8b\u4e24\u4e2a\u7f8e\u5973\u4eca\u665a\u5c31\u90fd\u4fbf\u5b9c\u4f60\u2026\u2026\u201d</p><p>\u3000\u3000\u5b59\u9759\u6aac\u53bb\u5e74\u5e95\u5728\u9999\u6e2f\u4e3e\u529e\u751f\u65e5\u5bb4\u8bf7\uff0c\u8bf7\u4e86\u597d\u51e0\u4e2a\u6f02\u4eae\u7684\u5973\u5b69\u5b50\uff0c\u5f20\u606a\u4e5f\u7b97\u662f\u8ba4\u8bc6\uff0c\u53ea\u662f\u5634\u998b\u7684\u54bd\u4e86\u4e00\u53e3\u553e\u6db2\uff0c\u8981\u771f\u4ee5\u4e3a\u591c\u91cc\u5077\u5077\u7761\u5728\u97f3\u4e50\u5b66\u9662\u7684\u5973\u751f\u5bbf\u820d\u80fd\u5360\u4e86\u4ec0\u4e48\u4fbf\u5b9c\u90a3\u5c31\u592a\u50bb\u4e86\uff0c\u7ed9\u5b59\u9759\u6aac\u8fd9\u90aa\u6076\u7684\u5973\u4eba\u8981\u631f\u7740\u62cd\u4e0b\u4ec0\u4e48\u88f8\u7167\u4e5f\u8bf4\u4e0d\u5b9a\u3002</p><p>\u3000\u3000\u5f20\u606a\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u9001\u4f60\u56de\u5bbf\u820d\u5427\uff0c\u591c\u91cc\u7761\u4f60\u4eec\u5bbf\u820d\uff0c\u6211\u7684\u786e\u6ca1\u80c6\u5b50\u3002\u201d</p><p>\u3000\u3000\u51fa\u4e86\u9152\u5427\uff0c\u624d\u53d1\u73b0\u5730\u4e0a\u5df2\u7ecf\u79ef\u4e86\u539a\u539a\u4e00\u5c42\u96ea\uff0c\u5341\u4e8c\u70b9\u5de6\u53f3\u5f00\u59cb\u4e0b\u96ea\uff0c\u624d\u6709\u4e24\u4e2a\u5c0f\u65f6\uff0c\u5df2\u7ecf\u76f8\u5f53\u539a\u4e86\u3002\u96ea\u82b1\u8fd8\u5728\u5317\u98ce\u7684\u631f\u88f9\u4e0b\u4e71\u6765\uff0c\u5b59\u9759\u6aac\u5750\u7740\u9e45\u9ec4\u8272\u7684\u7f8a\u7ed2\u5916\u5957\uff0c\u91cc\u9762\u7a7f\u7740\u9488\u7ec7\u957f\u88d9\uff0c\u5916\u5957\u4e0e\u9488\u7ec7\u957f\u88d9\u6446\u90fd\u5728\u819d\u76d6\u4e0a\u4e09\u5bf8\uff0c\u957f\u88d9\u91cc\u7a7f\u7740\u9ad8\u5f39\u529b\u88e4\u889c\uff0c\u867d\u8bf4\u662f\u6027\u611f\u65e0\u7aef\uff0c\u4f46\u662f\u5728\u98ce\u96ea\u91cc\uff0c\u8fd8\u662f\u771f\u591f\u53d7\u7684\uff0c\u62b1\u7740\u53cc\u81c2\u745f\u745f\u53d1\u6296\uff0c\u5f80\u524d\u8d70\u4e86\u4e24\u6b65\uff0c\u6ed1\u4e86\u4e00\u4e0b\uff0c\u5c31\u7ad9\u5728\u90a3\u91cc\u8ba9\u5f20\u606a\u6328\u8fc7\u6765\uff0c\u6402\u4f4f\u4ed6\u7684\u80f3\u81c2\u3002</p><p>\u3000\u3000\u5f20\u606a\u4ed6\u4eec\u4ece\u9999\u6e2f\u76f4\u63a5\u56de\u6765\uff0c\u9999\u6e2f\u4eca\u5929\u767d\u5929\u7684\u6c14\u6e29\u8fd8\u6709\u4e8c\u5341\u5ea6\uff0c\u4f46\u662f\u5efa\u90ba\u9ec4\u660f\u65f6\u5206\u5c31\u5df2\u7ecf\u662f\u96f6\u4e0b\u56db\u4e94\u5ea6\uff0c\u8fd9\u5317\u98ce\u5939\u96ea\u4e0b\u6765\uff0c\u4f30\u8ba1\u53c8\u964d\u4e86\u51e0\u5ea6\uff0c\u5f20\u606a\u5c31\u6bd4\u5728\u9999\u6e2f\u591a\u7a7f\u4e86\u4e00\u4ef6\u98ce\u8863\uff0c\u5728\u8f66\u91cc\u6216\u8005\u5ba4\u5185\uff0c\u90fd\u4e0d\u4f1a\u89c9\u5f97\u51b7\uff0c\u8fd9\u4f1a\u513f\u4e00\u51fa\u6765\uff0c\u5bd2\u98ce\u523a\u9aa8\uff0c\u7a7f\u7740\u5355\u76ae\u978b\uff0c\u9677\u5230\u96ea\u91cc\uff0c\u96ea\u6d78\u8fc7\u889c\u5b50\uff0c\u811a\u8e1d\u90fd\u662f\u51b0\u7684\uff0c\u8fd8\u597d\u5728\u9152\u5427\u91cc\u559d\u4e86\u4e0d\u5c11\u9152\u3002</p><p>\u3000\u3000\u8def\u4e0a\u79ef\u96ea\u8fd9\u4e48\u539a\uff0c\u60f3\u5fc5\u5085\u4fca\u5f00\u8f66\u4e5f\u56f0\u96be\uff0c\u80cc\u7740\u98ce\u7ed9\u4ed6\u6253\u4e86\u7535\u8bdd\uff0c\u8f66\u5b50\u679c\u7136\u5728\u8def\u4e0a\u9f9f\u884c\uff0c\u5c31\u8ddf\u4ed6\u8bf4\uff1a\u201c\u9001\u8bb8\u8001\u5e08\u56de\u5bbe\u9986\uff0c\u4f60\u4e5f\u4f4f\u5bbe\u9986\u91cc\u5427\uff0c\u591c\u91cc\u5f00\u8f66\u592a\u5371\u9669\uff0c\u4f60\u4e0d\u7528\u56de\u6765\u63a5\u6211\u4e86\uff0c\u6211\u81ea\u5df1\u627e\u5730\u65b9\u7761\u2026\u2026\u201d</p></article>"}}}]);
