(window.webpackJsonp=window.webpackJsonp||[]).push([[479],{501:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u4e00\u8282 \u906d\u9047\u6218",content:"<article><h2>\u7b2c\u5341\u4e00\u8282 \u906d\u9047\u6218</h2><p>\u6c89\u96f7\u8f70\u9e23\uff0c\u5929\u5730\u4eff\u4f5b\u90fd\u4e3a\u4e4b\u9707\u8361\u6447\u6643\uff0c\u5343\u4e07\u9053\u95ea\u7535\u5316\u4f5c\u5229\u5251\u7a7f\u900f\u4e4c\u4e91\uff0c\u75be\u98ce\u66b4\u96e8\u822c\u51fb\u4e0b\uff0c\u58f0\u52bf\u60ca\u4eba\u3002\u4f46\u8679\u5149\u6cd5\u7403\u662f\u9632\u5fa1\u9b54\u6cd5\u4e2d\u7684\u5dc5\u5cf0\uff0c\u80fd\u591f\u963b\u9694\u4e00\u5207\u5f62\u5f0f\u7684\u653b\u51fb\uff0c\u9664\u975e\u7528\u5927\u88c2\u89e3\u5f3a\u884c\u5c06\u5b83\u5d29\u6563\uff0c\u6216\u8005\u7528\u4e03\u79cd\u5bf9\u5e94\u7684\u9b54\u6cd5\u5c42\u5c42\u62b5\u6d88\uff0c\u5426\u5219\u5355\u51ed\u95ea\u7535\u8f70\u70b8\uff0c\u90a3\u662f\u4e0d\u53ef\u80fd\u6253\u7834\u7684\u3002</p><p>\u77ed\u77ed\u51e0\u79d2\u949f\uff0c\u8679\u5149\u7f69\u5df2\u7ecf\u627f\u53d7\u4e86\u4e0a\u767e\u9053\u95ea\u7535\u7684\u8f70\u70b8\uff0c\u5b83\u4e0d\u4f46\u6beb\u53d1\u65e0\u4f24\uff0c\u53cd\u800c\u5149\u534e\u6108\u76db\uff0c\u5728\u8868\u9762\u6d41\u8f6c\u5982\u6c34\u3002\u743c\u6069\u6697\u81ea\u677e\u4e86\u53e3\u6c14\uff0c\u6b63\u5f85\u8bf4\u8bdd\uff0c\u7a81\u7136\u542c\u89c1\u5965\u5609\u83b1\u65af\u8f7b\u8f7b\u201c\u54a6\u201d\u4e86\u4e00\u58f0\u3002</p><p>\u201c\u600e\u4e48\u4e86\uff0c\u8001\u5e08\uff1f\u201d\u73ca\u5609\u95ee\u3002</p><p>\u201c\u8fd9\u4e9b\u95ea\u7535\u2026\u2026\u201d\u5979\u6c89\u541f\u7740\uff0c\u201c\u597d\u50cf\u4e0d\u662f\u7279\u5730\u9488\u5bf9\u6211\u4eec\u800c\u6765\u7684\u3002\u201d</p><p>\u201c\u554a\uff1f\u201d</p><p>\u88ab\u5965\u5609\u83b1\u65af\u4e00\u63d0\u9192\uff0c\u743c\u6069\u548c\u73ca\u5609\u62ac\u5934\u518d\u770b\uff0c\u679c\u7136\u610f\u8bc6\u5230\u4e0d\u5bf9\u52b2\u3002\u4e00\u5f00\u59cb\u7684\u65f6\u5019\uff0c\u56e0\u4e3a\u662f\u4ece\u5730\u9762\u5f80\u7a7a\u4e2d\u4ef0\u671b\uff0c\u81ea\u6709\u4e00\u79cd\u89c6\u89c9\u8bef\u5dee\uff0c\u52a0\u4e0a\u731d\u7136\u9047\u88ad\uff0c\u672c\u80fd\u5730\u4ee5\u4e3a\u843d\u4e0b\u7684\u95ea\u7535\u5168\u662f\u5954\u7740\u81ea\u5df1\u6765\uff0c\u4f46\u73b0\u5728\u53d1\u73b0\u5e76\u975e\u5982\u6b64\u3002</p><p>\u8c37\u5730\u4e4b\u6240\u4ee5\u53eb\u505a\u8c37\u5730\uff0c\u987e\u540d\u601d\u4e49\u4fbf\u77e5\u9053\uff0c\u6b64\u5730\u591a\u5c71\u5cad\uff0c\u591a\u5ce1\u8c37\uff0c\u5730\u5f62\u975e\u5e38\u5d0e\u5c96\uff0c\u743c\u6069\u7b49\u4eba\u73b0\u5728\u6240\u5904\u7684\u4f4d\u7f6e\u662f\u4e00\u5ea7\u5c71\u7684\u897f\u5357\u8fb9\uff0c\u800c\u8fd9\u5ea7\u5c71\u4fbf\u662f\u5f13\u8c37\u548c\u5854\u745f\u8c37\u7684\u5206\u754c\u7ebf\uff0c\u4e1c\u5317\u8fb9\u4fbf\u662f\u5854\u745f\u8c37\u3002\u7a7a\u4e2d\u5760\u4e0b\u7684\u95ea\u7535\uff0c\u4e03\u6210\u4ee5\u4e0a\u90fd\u843d\u5230\u5c71\u7684\u53e6\u4e00\u8fb9\u53bb\u4e86\uff0c\u4f59\u4e0b\u4e09\u6210\u6563\u4e71\u8ff8\u5c04\uff0c\u5176\u5b9e\u6ca1\u6709\u660e\u663e\u9488\u5bf9\u7684\u76ee\u6807\uff0c\u66f4\u50cf\u662f\u63a7\u5236\u4e0d\u4f4f\u7684\u7ed3\u679c\uff0c\u800c\u51fb\u4e2d\u743c\u6069\u7b49\u4eba\u7684\uff0c\u66f4\u662f\u8fd9\u4e09\u6210\u4e2d\u7684\u4e00\u6210\u90fd\u4e0d\u5230\u3002\u8fd9\u6837\u770b\u6765\uff0c\u786e\u5b9e\u5c31\u50cf\u5965\u5609\u83b1\u65af\u6240\u8a00\uff0c\u4ed6\u4eec\u5e76\u4e0d\u662f\u88ab\u653b\u51fb\u7684\u771f\u6b63\u76ee\u6807\uff0c\u800c\u662f\u6070\u597d\u8fdb\u5165\u8fd9\u4e2a\u533a\u57df\uff0c\u7ed3\u679c\u906d\u53d7\u6c60\u9c7c\u4e4b\u6b83\uff0c\u88ab\u6ce2\u53ca\u4e86\u3002</p><p>\u90a3\u4e48\u771f\u6b63\u88ab\u653b\u51fb\u7684\u4eba\u662f\u8c01\uff1f</p><p>\u743c\u6069\u597d\u5947\u5fc3\u8d77\uff0c\u60f3\u8981\u53bb\u770b\u4e2a\u7a76\u7adf\uff0c\u4f46\u4ed6\u4eec\u73b0\u5728\u88ab\u8679\u5149\u6cd5\u7403\u7f69\u5728\u4e2d\u95f4\uff0c\u56fa\u7136\u963b\u9694\u4e86\u4e00\u5207\u5916\u6765\u653b\u51fb\uff0c\u5374\u4e5f\u540c\u65f6\u628a\u91cc\u9762\u7684\u4eba\u5c01\u9501\u5728\u5185\uff0c\u65e0\u6cd5\u79fb\u52a8\u3002\u5e78\u597d\u5965\u5609\u83b1\u65af\u4f3c\u4e4e\u4e5f\u5bf9\u8fd9\u4ef6\u4e8b\u4ea7\u751f\u4e86\u5174\u8da3\uff0c\u5979\u8f7b\u629a\u4e66\u7684\u5c01\u9762\uff0c\u9ed8\u8bf5\u5492\u6587\uff0c\u6574\u4e2a\u8679\u5149\u7403\u88ab\u5de8\u5927\u7684\u529b\u91cf\u62c9\u626f\uff0c\u5feb\u901f\u5411\u4e0a\u98de\u8d77\uff0c\u8f6c\u773c\u4e4b\u95f4\u5df2\u7ecf\u5347\u5230\u4e86\u8ddd\u79bb\u5730\u9762\u5927\u7ea6\u56db\u767e\u82f1\u5c3a\u7684\u9ad8\u5ea6\uff0c\u8db3\u4ee5\u8d8a\u8fc7\u5c71\u810a\uff0c\u6e05\u695a\u770b\u89c1\u5c71\u7684\u53e6\u5916\u4e00\u8fb9\u53d1\u751f\u7684\u60c5\u5f62\u3002</p><p>\u7136\u540e\u743c\u6069\u5012\u5438\u4e86\u4e00\u53e3\u51b7\u6c14\u3002</p><p>\u5728\u5c71\u811a\u4e0b\uff0c\u4e00\u573a\u6fc0\u6218\u6b63\u5728\u8fdb\u884c\u3002\u743c\u6069\u770b\u89c1\u4e86\u6f2b\u5c71\u904d\u91ce\u7684\u4ea1\u7075\u5927\u519b\uff0c\u89e6\u76ee\u6240\u53ca\u5c3d\u662f\u9752\u9ed1\u8272\u7684\u50f5\u5c38\u548c\u767d\u68ee\u68ee\u7684\u9ab7\u9ac5\uff0c\u7387\u9886\u5b83\u4eec\u7684\u5219\u662f\u9b54\u9b42\u5c38\uff0c\u5176\u603b\u6570\u91cf\u7edd\u5bf9\u5df2\u7ecf\u8d85\u8fc7\u4e86\u4e24\u5343\u540d\uff0c\u800c\u88ab\u8fd9\u4ea1\u7075\u5927\u519b\u5305\u56f4\u5728\u4e2d\u95f4\u7684\uff0c\u662f\u51e0\u5341\u4e2a\u4eba\u7c7b\u6b66\u58eb\u7ec4\u6210\u7684\u5706\u9635\uff0c\u4ed6\u4eec\u5168\u90fd\u7a7f\u7740\u5bd2\u5149\u95ea\u95ea\u7684\u91d1\u5c5e\u94e0\u7532\uff0c\u5c06\u81ea\u5df1\u5305\u88f9\u6210\u94c1\u7f50\u5934\uff0c\u624b\u4e2d\u6325\u821e\u7740\u5de8\u5251\u548c\u6218\u9524\uff0c\u7aed\u529b\u62b5\u5fa1\u7740\u4ea1\u7075\u65e0\u4f11\u65e0\u6b62\u7684\u51b2\u51fb\u3002\u5728\u5706\u9635\u5185\u4fa7\uff0c\u4e03\u516b\u4e2a\u7267\u5e08\u6a21\u6837\u7684\u4eba\u6b63\u9ad8\u4e3e\u5723\u5fbd\uff0c\u5168\u529b\u4ee5\u8d74\u5730\u66ff\u524d\u65b9\u7684\u6218\u58eb\u589e\u52a0\u5404\u79cd\u6cbb\u7597\u3001\u9632\u62a4\u548c\u5f3a\u5316\u795e\u672f\u3002\u800c\u5728\u5706\u9635\u4e0a\u65b9\uff0c\u4e00\u4f4d\u5168\u8eab\u51b0\u84dd\u8272\u7684\u5341\u7ffc\u5929\u4f7f\u6b63\u9ad8\u4e3e\u6218\u9524\uff0c\u5343\u4e07\u9053\u707f\u70c2\u91d1\u5149\u4ece\u9524\u4e0a\u53d1\u51fa\uff0c\u6c47\u805a\u6210\u4e00\u9762\u5de8\u5927\u7684\u91d1\u8272\u5149\u76fe\u3002\u65e0\u6570\u9053\u95ea\u7535\u81ea\u58a8\u6c41\u822c\u7684\u4e91\u5c42\u4e2d\u7eb7\u7eb7\u51fb\u4e0b\uff0c\u6253\u5728\u91d1\u8272\u5149\u76fe\u4e0a\uff0c\u7136\u540e\u88ab\u6084\u65e0\u58f0\u606f\u5730\u5438\u6536\u4e86\uff0c\u6ca1\u6709\u5bf9\u4e0b\u65b9\u7684\u4eba\u9020\u6210\u4efb\u4f55\u4f24\u5bb3\u3002</p><p>\u201c\u54df\uff0c\u4e00\u4f4d\u5fa1\u5929\u4f7f\u554a\uff0c\u201d\u5965\u5609\u83b1\u65af\u8bf4\uff0c\u201c\u6ca1\u60f3\u5230\u963f\u66fc\u7eb3\u5854\u90a3\u4e2a\u8001\u5bb6\u4f19\u5b8c\u86cb\u4e4b\u540e\uff0c\u8fd8\u80fd\u5728\u7269\u8d28\u754c\u770b\u5230\u8fd9\u6837\u9ad8\u9636\u7684\u5929\u4f7f\u3002\u201d</p><p>\u5fa1\u5929\u4f7f\u662f\u5929\u5802\u5c71\u4e03\u9636\u795e\u4f8d\u4e2d\u7684\u7b2c\u4e09\u9636\uff0c\u4ec5\u6b21\u4e8e\u66e6\u5929\u4f7f\u548c\u5f8b\u5929\u4f7f\uff0c\u786e\u5b9e\u5df2\u7ecf\u662f\u975e\u5e38\u9ad8\u9636\u7684\u5b58\u5728\u4e86\uff0c\u800c\u4e14\u6700\u64c5\u957f\u5b88\u62a4\u3002\u5982\u679c\u8bf4\u66e6\u5929\u4f7f\u662f\u8bf8\u795e\u4f8d\u4e2d\u6700\u950b\u5229\u7684\u5251\uff0c\u90a3\u4e48\u5fa1\u5929\u4f7f\u4fbf\u662f\u6700\u575a\u56fa\u7684\u76fe\u3002\u867d\u7136\u548c\u6240\u6709\u7684\u5f02\u754c\u5b58\u5728\u4e00\u6837\uff0c\u8000\u5929\u4f7f\u53d7\u5230\u7269\u8d28\u754c\u5f3a\u5927\u7684\u4f4d\u9762\u538b\u5236\uff0c\u53ea\u80fd\u53d1\u6325\u51fa\u539f\u672c\u5b9e\u529b\u7684\u4e24\u4e09\u6210\uff0c\u4f46\u8fd9\u5df2\u7ecf\u8db3\u4ee5\u62b5\u5fa1\u6f2b\u5929\u96f7\u9706\u4e86\u3002</p><p>\u66f4\u91cd\u8981\u7684\u662f\uff0c\u5fa1\u5929\u4f7f\u6765\u81ea\u5929\u5802\u5c71\uff0c\u800c\u5929\u5802\u5c71\u662f\u4ee5\u63d0\u5c14\u4e3a\u9996\u7684\u201c\u4e09\u5723\u201d\uff08\u6b63\u4e49\u4e4b\u795e\u63d0\u5c14\u3001\u6b89\u96be\u4e4b\u795e\u4f0a\u5c14\u739b\u7279\u3001\u5fe0\u8bda\u4e4b\u795e\u6258\u59c6\uff09\u548c\u5b88\u62a4\u4e4b\u795e\u6d77\u59c6\u7684\u795e\u57df\u6240\u5728\u3002\u6d77\u59c6\u662f\u5b64\u5bb6\u5be1\u4eba\uff0c\u6ca1\u6709\u795e\u4f8d\uff0c\u5929\u5802\u5c71\u7684\u6240\u6709\u5929\u4f7f\u5168\u90fd\u662f\u4e09\u5723\u5c5e\u4e0b\uff0c\u4e5f\u5373\u662f\u63d0\u5c14\u5c5e\u4e0b\u3002\u63d0\u5c14\u5c5e\u4e0b\u7684\u9ad8\u9636\u5929\u4f7f\u51fa\u73b0\u5728\u7269\u8d28\u754c\uff0c\u800c\u6b64\u5730\u662f\u5854\u745f\u8c37\uff0c\u662f\u63d0\u5c14\u6559\u4f1a\u8c37\u5730\u533a\u7684\u603b\u90e8\u6240\u5728\u2014\u2014\u5982\u6b64\u4e00\u6765\uff0c\u8fd9\u7fa4\u88ab\u4ea1\u7075\u56f4\u653b\u7684\u4eba\u7c7b\u8eab\u4efd\uff0c\u4e5f\u5c31\u5927\u4f53\u731c\u60f3\u5f97\u51fa\u4e86\u3002</p><p>\u743c\u6069\u4e0b\u610f\u8bc6\u5730\u62ac\u8d77\u5de6\u624b\uff0c\u62c7\u6307\u6309\u4f4f\u4e2d\u6307\u4e0a\u6234\u7740\u7684\u5b9d\u77f3\u6212\u6307\uff0c\u4f46\u60f3\u4e86\u60f3\u53c8\u653e\u4e0b\u4e86\u3002\u4ed6\u626b\u89c6\u7740\u6218\u573a\uff0c\u7136\u540e\u627e\u5230\u4e86\u4ea1\u7075\u5927\u519b\u7684\u7edf\u5e05\uff0c\u90a3\u662f\u4e00\u5904\u65ad\u5d16\u5ced\u58c1\u4e0a\u51f9\u8fdb\u53bb\u7684\u6d1e\u7a9f\uff0c\u4ea1\u7075\u5e08\u5c31\u8eb2\u5728\u5176\u4e2d\u7684\u9634\u5f71\u91cc\u3002\u56e0\u4e3a\u8ddd\u79bb\u592a\u8fdc\uff0c\u4e00\u5f00\u59cb\u770b\u4e0d\u6e05\u695a\u3002\u5f53\u743c\u6069\u4e3a\u81ea\u5df1\u65bd\u52a0\u4e86\u589e\u5f3a\u89c6\u529b\u7684\u6cd5\u672f\u540e\uff0c\u4ed6\u987f\u65f6\u5012\u5438\u4e86\u53e3\u6c14\uff0c\u56e0\u4e3a\u90a3\u662f\u4e00\u4e2a\u5b8c\u5168\u8d85\u51fa\u60f3\u50cf\u7684\u602a\u7269\uff0c\u57fa\u672c\u8f6e\u5ed3\u662f\u4eba\u7c7b\u5f62\u6001\uff0c\u53f3\u534a\u8fb9\u8138\u6b63\u5e38\uff0c\u751a\u81f3\u79f0\u5f97\u4e0a\u4e30\u6ee1\u7ea2\u6da6\uff0c\u4f46\u5de6\u534a\u8fb9\u8138\u6ca1\u6709\u534a\u70b9\u76ae\u8089\uff0c\u53ea\u6709\u68ee\u68ee\u767d\u9aa8\uff0c\u773c\u7736\u4e2d\u8df3\u8dc3\u7740\u78a7\u8367\u8367\u7684\u9b3c\u706b\uff1b\u4ed6\u7684\u5de6\u81c2\u5b8c\u597d\u65e0\u635f\uff0c\u63e1\u7740\u4e00\u67c4\u9ed1\u8272\u9aa8\u6756\uff0c\u53f3\u81c2\u5374\u53ea\u5269\u534a\u622a\uff0c\u624b\u8098\u4ee5\u4e0b\u63a5\u7740\u6781\u5176\u7c97\u5927\u7684\u9aa8\u81c2\uff0c\u4ece\u5f62\u6001\u4e0a\u770b\u7edd\u975e\u4eba\u7c7b\uff0c\u800c\u662f\u67d0\u79cd\u91ce\u517d\uff0c\u7ad9\u7acb\u65f6\u624b\u81c2\u81ea\u7136\u5782\u4e0b\uff0c\u6307\u5173\u8282\u80fd\u591f\u89e6\u5230\u5730\u9762\u3002\u4ed6\u81ea\u8170\u90e8\u4ee5\u4e0b\u5168\u662f\u767d\u9aa8\uff0c\u5e26\u7740\u5927\u5927\u5c0f\u5c0f\u7684\u5012\u94a9\u5c16\u523a\uff0c\u521d\u770b\u4ee5\u4e3a\u662f\u7a7f\u7740\u4e00\u4ef6\u9aa8\u94e0\uff0c\u4ed4\u7ec6\u518d\u770b\u4fbf\u53d1\u73b0\u5176\u5b9e\u5c31\u662f\u8eaf\u4f53\u672c\u8eab\u3002\u957f\u957f\u7684\u6697\u7ea2\u820c\u5934\u5728\u53e3\u4e2d\u4f38\u7f29\u541e\u5410\uff0c\u5c31\u50cf\u9b54\u9b42\u5c38\u4e00\u822c\u3002</p><p>\u8fd9\u5230\u5e95\u662f\u6d3b\u4eba\uff0c\u8fd8\u662f\u4ea1\u7075\uff1f</p><p>\u5965\u5609\u83b1\u65af\u4e5f\u987a\u7740\u4ed6\u7684\u76ee\u5149\u770b\u8fc7\u6765\uff0c\u201c\u8eaf\u4f53\u4ea1\u7075\u5316\u5417\uff0c\u8fd9\u5c31\u662f\u6240\u8c13\u7684\u2018\u82cd\u767d\u9886\u4e3b\u2019\u5427\uff0c\u6ca1\u60f3\u5230\u6234\u745f\u5fb7\u90a3\u75af\u5b50\u7684\u8bdd\u8fd8\u771f\u6709\u4eba\u4fe1\u554a\uff0c\u201d\u5979\u4f4e\u58f0\u81ea\u8bed\uff0c\u201c\u800c\u4e14\u5c45\u7136\u8fd8\u771f\u6210\u529f\u4e86\uff0c\u4e86\u4e0d\u8d77\uff0c\u771f\u662f\u4e86\u4e0d\u8d77\u3002\u201d</p><p>\u743c\u6069\u7709\u5934\u8df3\u4e86\u8df3\uff0c\u6ca1\u6709\u8bf4\u8bdd\u3002</p><p>\u201c\u6234\u745f\u5fb7\u662f\u8c01\u554a\uff1f\u201d\u73ca\u5609\u5728\u4e00\u65c1\u597d\u5947\u5730\u95ee\u3002</p><p>\u201c\u4e00\u4e2a\u75af\u5b50\u3002\u201d\u5965\u5609\u83b1\u65af\u56de\u7b54\u3002</p><p>\u8fd9\u4e2a\u56de\u7b54\u592a\u8fc7\u4e8e\u8a00\u7b80\u610f\u8d45\uff0c\u663e\u7136\u4e0d\u80fd\u6ee1\u8db3\u73ca\u5609\u7684\u8981\u6c42\uff0c\u6240\u4ee5\u743c\u6069\u53ea\u597d\u505a\u8865\u5145\u89e3\u91ca\u3002\u8fd9\u4f4d\u540c\u5b66\u662f\u201c\u8010\u745f\u4e4b\u7236\u201d\u4f0a\u5965\u52d2\u59c6\u7684\u5f97\u610f\u95e8\u5f92\uff0c\u6781\u5ea6\u6c89\u8ff7\u4e8e\u4ea1\u7075\u672f\uff0c\u6700\u8457\u540d\u7684\u4e8b\u8ff9\u662f\u53d1\u660e\u4e86\u8f6c\u5316\u5deb\u5996\u7684\u65b9\u6cd5\uff0c\u628a\u81ea\u5df1\u53d8\u6210\u4e86\u8d39\u4f26\u5386\u53f2\u4e0a\u7b2c\u4e00\u4f4d\u5deb\u5996\uff0c\u4ece\u6b64\u83b7\u5f97\u4e86\u201c\u75af\u5b50\u201d\u7684\u79f0\u53f7\u2014\u2014\u8981\u77e5\u9053\uff0c\u8010\u745f\u65f6\u4ee3\u7684\u5927\u5965\u672f\u5e08\u4eec\u662f\u53ef\u4ee5\u7528\u201c\u4f0a\u5965\u52d2\u59c6\u4e4b\u957f\u751f\u672f\u201d\u5ef6\u547d\u7684\uff0c\u8f7b\u677e\u5c31\u80fd\u6d3b\u4e2a\u51e0\u767e\u5e74\u4e0a\u5343\u5e74\uff0c\u4ece\u6765\u6ca1\u6709\u8c01\u5403\u9971\u4e86\u6491\u7740\uff0c\u597d\u597d\u7684\u6d3b\u4eba\u4e0d\u5f53\u53bb\u505a\u4ea1\u7075\u3002\u6b64\u5916\u6234\u745f\u5fb7\u8fd8\u662f\u591a\u79cd\u4ea1\u7075\u672f\u7684\u53d1\u660e\u8005\uff0c\u6700\u8457\u540d\u7684\u4fbf\u662f\u201c\u6b7b\u4ea1\u4e00\u6307\u201d\u548c\u201c\u5973\u5996\u4e4b\u568e\u201d\u3002</p><p>\u5982\u6b64\u8f89\u714c\u7684\u4eba\u751f\uff0c\u7ed3\u5c40\u5374\u5f88\u4e0d\u4ee4\u4eba\u6b23\u6170\u3002\u4f5c\u4e3a\u5deb\u5996\uff0c\u6234\u745f\u5fb7\u5148\u751f\u81ea\u7136\u62e5\u6709\u547d\u5323\uff0c\u8fd9\u6837\u81f3\u5173\u91cd\u8981\u7684\u4e1c\u897f\u7406\u5e94\u79d8\u5bc6\u6536\u85cf\u3002\u7136\u800c\u8fd9\u4f4d\u5deb\u5996\u59cb\u7956\u60a3\u6709\u4e25\u91cd\u7684\u5f3a\u8feb\u75c7\uff0c\u8fd9\u662f\u5fc3\u7406\u75be\u75c5\uff0c\u6240\u4ee5\u53d8\u6210\u4ea1\u7075\u540e\u7167\u6837\u5b58\u5728\uff0c\u4ed6\u603b\u89c9\u5f97\u628a\u547d\u5323\u653e\u5728\u4efb\u4f55\u5730\u65b9\u90fd\u4e0d\u591f\u5b89\u5168\u2014\u2014\u66f4\u51c6\u786e\u5730\u8bf4\uff0c\u4e00\u5207\u4ed6\u89c6\u7ebf\u6240\u4e0d\u80fd\u53ca\u7684\u5730\u65b9\u90fd\u662f\u4e0d\u5b89\u5168\u7684\uff0c\u6700\u540e\u4ed6\u7ec8\u4e8e\u60f3\u51fa\u4e86\u4e2a\u597d\u4e3b\u610f\uff1a\u628a\u547d\u5323\uff08\u4e00\u679a\u84dd\u5b9d\u77f3\uff09\u505a\u6210\u540a\u5760\u6302\u5728\u8116\u5b50\u4e0a\u3002</p><p>\u7ed3\u679c\u5728\u4e00\u6b21\u51b3\u6597\u4e2d\uff0c\u4ed6\u88ab\u4eba\u7528\u5f3a\u5927\u7684\u6cd5\u672f\u8f70\u788e\u4e86\u9aa8\u67b6\u2014\u2014\u8fde\u540c\u4ed6\u6f02\u4eae\u7684\u547d\u5323\u540a\u5760\u3002</p><p>\u201c\u4ed6\u8fd9\u4e48\u5389\u5bb3\u8fd8\u88ab\u4eba\u6740\u4e86\uff1f\u201d\u73ca\u5609\u60ca\u8bb6\uff0c\u201c\u5bf9\u624b\u662f\u8c01\u554a\uff1f\u201d</p><p>\u201c\u5443\uff0c\u8fd9\u4e2a\u4e48\u2026\u2026\u201d</p><p>\u201c\u5c31\u662f\u8fd9\u5c0f\u5b50\u7684\u6b7b\u80d6\u5b50\u8001\u5e08\u3002\u201d\u5965\u5609\u83b1\u65af\u8bf4\u3002</p><p>\u743c\u6069\u8038\u8038\u80a9\u3002\u4ed6\u5bf9\u8010\u745f\u65f6\u4ee3\u7684\u5927\u5965\u672f\u5e08\u4e86\u89e3\u5176\u5b9e\u5f88\u5c11\uff0c\u6234\u745f\u5fb7\u53c8\u4e0d\u662f\u50cf\u5927\u5730\u5148\u77e5\u3001\u8010\u745f\u4e4b\u7236\u3001\u65c5\u8005\u90a3\u79cd\u9876\u7ea7\u4eba\u7269\uff0c\u4e13\u4e1a\u9886\u57df\u66f4\u4e0d\u5bf9\u53e3\uff0c\u539f\u672c\u662f\u5e94\u8be5\u4e0d\u77e5\u9053\u6b64\u4eba\u7684\u2014\u2014\u4e4b\u6240\u4ee5\u77e5\u9053\u5f97\u8fd9\u4e48\u8be6\u7ec6\uff0c\u5c31\u662f\u56e0\u4e3a\u5965\u6c83\u7ecf\u5e38\u63d0\u8d77\u3002\u8981\u77e5\u9053\uff0c\u5f53\u65f6\u5965\u6c83\u624d\u521a\u521a\u6210\u4e3a\u5927\u5965\u672f\u5e08\uff0c\u6234\u745f\u5fb7\u5374\u662f\u6210\u540d\u5df2\u4e45\u7684\u8001\u524d\u8f88\uff0c\u51b3\u6597\u524d\u6240\u6709\u4eba\u90fd\u8ba4\u4e3a\u5965\u6c83\u5fc5\u6b7b\u65e0\u7591\uff0c\u7ed3\u679c\u4ee4\u4ed6\u4eec\u5927\u8dcc\u773c\u955c\u3002\u8fd9\u4ef6\u4e8b\u4e5f\u56e0\u6b64\u6210\u4e3a\u5965\u6c83\u4e00\u751f\u4e2d\u6700\u5f97\u610f\u7684\u8d44\u672c\uff0c\u52a8\u8f84\u5c31\u62ff\u6765\u5411\u743c\u6069\u70ab\u8000\uff0c\u542c\u5f97\u8033\u6735\u90fd\u5feb\u957f\u8327\u4e86\u3002</p><p>\u6234\u745f\u5fb7\u4e00\u751f\u6709\u8bf8\u591a\u53d1\u660e\uff0c\u6b64\u5916\u8fd8\u6709\u66f4\u591a\u6ca1\u6709\u6765\u5f97\u53ca\u4ed8\u8bf8\u5b9e\u8df5\u7684\u521b\u610f\uff0c\u5965\u5609\u83b1\u65af\u521a\u624d\u6240\u8bf4\u7684\u201c\u82cd\u767d\u9886\u4e3b\u201d\u4fbf\u662f\u5176\u4e2d\u4e4b\u4e00\u3002\u5176\u539f\u7406\u662f\u901a\u8fc7\u79fb\u690d\u7684\u65b9\u6cd5\uff0c\u5c06\u4ea1\u7075\u5e08\u7684\u8840\u8089\u4e4b\u8eaf\uff0c\u5305\u62ec\u56db\u80a2\u3001\u4e94\u5b98\u3001\u80ba\u8151\u751a\u81f3\u5fc3\u810f\u90fd\u66ff\u6362\u4e3a\u7ecf\u8fc7\u5404\u79cd\u9b54\u6cd5\u5f3a\u5316\u7684\u4ea1\u7075\u5668\u5b98\uff0c\u4ece\u800c\u83b7\u5f97\u5404\u79cd\u5f3a\u608d\u80fd\u529b\uff0c\u4f46\u672c\u8d28\u4e0a\u8fd8\u662f\u751f\u8005\uff0c\u4e0d\u662f\u4ea1\u7075\uff0c\u56e0\u6b64\u4e5f\u6ca1\u6709\u4ea1\u7075\u7684\u5404\u79cd\u7f3a\u9677\uff0c\u4e0d\u6015\u8d85\u5ea6\u3002\u6234\u745f\u5fb7\u4e4b\u540e\uff0c\u7ecf\u8fc7\u5386\u4ee3\u4ea1\u7075\u5e08\u4eec\u7684\u4e0d\u61c8\u52aa\u529b\u7814\u7a76\u548c\u8bd5\u9a8c\uff0c\u6700\u7ec8\u5728\u8428\u739b\u65af\u7279\u624b\u4e0a\u96c6\u5176\u5927\u6210\uff0c\u771f\u6b63\u5b8c\u6210\u8fd9\u9879\u53d1\u660e\u3002\u5f53\u5e74\u9f99\u5deb\u6559\u58f0\u52bf\u6700\u76db\u7684\u65f6\u5019\uff0c\u8428\u739b\u65af\u7279\u624b\u4e0b\u6709\u5341\u4e03\u4f4d\u82cd\u767d\u9886\u4e3b\uff0c\u6bcf\u4e00\u4f4d\u90fd\u662f\u8d6b\u8d6b\u6709\u540d\u7684\u89d2\u8272\u3002\u540e\u6765\u5927\u534a\u88ab\u6740\uff0c\u4f59\u515a\u9500\u58f0\u533f\u8ff9\uff0c\u6ca1\u60f3\u5230\u5728\u8fd9\u91cc\u5c45\u7136\u4f1a\u78b0\u89c1\u4e00\u4e2a\u3002</p><p>\u201c\u9b54\u9b42\u5c38\u3001\u82cd\u767d\u9886\u4e3b\u2026\u2026\u5df2\u7ecf\u53ef\u4ee5\u767e\u5206\u4e4b\u767e\u786e\u5b9a\u548c\u8428\u739b\u65af\u7279\u6709\u5173\u4e86\u3002\u201d</p><p>\u82cd\u767d\u9886\u4e3b\u4f3c\u4e4e\u5e76\u6ca1\u6709\u6ce8\u610f\u5230\u8fdc\u5904\u6709\u4eba\u7aa5\u4f3a\uff0c\u4ed6\u6b63\u4e13\u5fc3\u81f4\u5fd7\u5730\u6325\u821e\u7740\u9aa8\u6756\uff0c\u5583\u5583\u5ff5\u8bf5\u9634\u90aa\u7684\u5492\u6587\uff0c\u8d8a\u6765\u8d8a\u591a\u7684\u9b54\u9b42\u5c38\u3001\u50f5\u5c38\u548c\u9ab7\u9ac5\u4ece\u5730\u5e95\u722c\u8d77\uff0c\u52a0\u5165\u9635\u5217\uff0c\u4eff\u4f5b\u65e0\u7a77\u65e0\u5c3d\u3002\u4ee4\u743c\u6069\u60ca\u8be7\u7684\u662f\uff0c\u90a3\u4e9b\u50f5\u5c38\u5f53\u4e2d\uff0c\u6709\u4e0d\u5c11\u8eab\u6750\u7ea4\u7ec6\uff0c\u6709\u7740\u957f\u957f\u5c16\u8033\uff0c\u5206\u660e\u5c31\u662f\u7cbe\u7075\uff0c\u800c\u975e\u4eba\u7c7b\u3002</p><p>\u5deb\u5e08\u80fd\u53ec\u5524\u4ea1\u7075\uff0c\u4f46\u4e5f\u4e0d\u662f\u51ed\u7a7a\u53ec\u5524\uff0c\u603b\u8981\u6709\u6750\u6599\uff0c\u4e5f\u5c31\u662f\u5c38\u4f53\u3002\u5982\u4eca\u743c\u6069\u6240\u770b\u5230\u7684\u4ea1\u7075\u603b\u6570\u5927\u7ea6\u5df2\u7ecf\u8d85\u8fc7\u4e09\u5343\uff0c\u5176\u4e2d\u7ea6\u6709\u516b\u6210\u4ee5\u4e0a\u662f\u7cbe\u7075\u2014\u2014\u6362\u53e5\u8bdd\u8bf4\uff1a\u8fd9\u4e2a\u5730\u65b9\u81f3\u5c11\u63a9\u57cb\u4e86\u4e24\u5343\u56db\u767e\u591a\u540d\u7cbe\u7075\u7684\u5c38\u4f53\u3002</p><p>\u201c\u8fd9\u91cc\u600e\u4e48\u4f1a\u6709\u8fd9\u4e48\u591a\u7cbe\u7075\u5c38\u4f53\uff1f\u201d\u743c\u6069\u4e0d\u7981\u95ee\u3002</p><p>\u201c\u6b63\u5e38\uff0c\u8fd9\u91cc\u662f\u8c37\u5730\u561b\uff0c\u201d\u5965\u5609\u83b1\u65af\u8bf4\uff0c\u201c\u9f99\u65cf\u548c\u7cbe\u7075\u65cf\u7684\u6700\u7ec8\u4e00\u6218\u5c31\u662f\u5728\u8fd9\u91cc\u7206\u53d1\u7684\u3002\u6b64\u540e\u7cbe\u7075\u5728\u6b64\u5730\u5efa\u56fd\uff0c\u53c8\u5386\u7ecf\u4e86\u51e0\u5343\u5e74\u3002\u6574\u4e2a\u8c37\u5730\u4e0b\u9762\u57cb\u7740\u7684\u7cbe\u7075\u5c38\u9aa8\uff0c\u5c11\u8bf4\u4e5f\u6709\u51e0\u5341\u4e07\u5427\u3002\u7cbe\u7075\u7684\u5c38\u9aa8\u53c8\u5f88\u96be\u8150\u70c2\uff0c\u62ff\u6765\u53ec\u5524\u4ea1\u7075\u6700\u5408\u9002\u4e0d\u8fc7\u4e86\u3002\u201d</p><p>\u201c\u2026\u2026\u539f\u6765\u5982\u6b64\u3002\u201d</p><p>\u4ed6\u4eec\u8fdc\u8fdc\u89c2\u6218\u804a\u5929\uff0c\u60a0\u95f2\u65e0\u6bd4\uff0c\u8eab\u5904\u6218\u5c40\u4e2d\u7684\u4eba\u4eec\u53ef\u5c31\u6ca1\u8fd9\u4e48\u8f7b\u677e\u81ea\u5728\u4e86\u3002\u7ec4\u6210\u5706\u9635\u7684\u6b66\u58eb\u548c\u7267\u5e08\uff0c\u968f\u4fbf\u4e00\u4e2a\u90fd\u662f\u5927\u9646\u4e0a\u582a\u79f0\u7cbe\u82f1\u7684\u4eba\u7269\uff0c\u6240\u4ee5\u624d\u80fd\u5728\u8fd9\u4ea1\u7075\u6d77\u4e2d\u575a\u6301\u8fd9\u4e48\u4e45\uff0c\u4f46\u8fd9\u7ec8\u7a76\u4e0d\u662f\u529e\u6cd5\u3002\u4eba\u662f\u4f1a\u7d2f\u7684\uff0c\u4ea1\u7075\u5374\u4e0d\u4f1a\uff0c\u5b83\u4eec\u88ab\u90aa\u6076\u7684\u4ea1\u7075\u672f\u64cd\u7eb5\uff0c\u4ece\u6c38\u7720\u4e2d\u82cf\u9192\uff0c\u4ece\u5730\u5e95\u722c\u51fa\uff0c\u524d\u8d74\u540e\u7ee7\u5730\u52a0\u5165\u524d\u8fdb\u7684\u961f\u5217\uff0c\u4e00\u6ce2\u6ce2\u5730\u88ab\u5316\u4f5c\u98de\u7070\uff0c\u5288\u6210\u788e\u7247\u2014\u2014\u4f46\u540e\u6765\u8005\u7ee7\u7eed\u524d\u8fdb\uff0c\u65e0\u4f11\u65e0\u6b62\uff0c\u65e0\u7a77\u65e0\u5c3d\u3002\u8fd9\u5c31\u50cf\u62b5\u6321\u7740\u6d2a\u6c34\u7684\u5927\u5824\uff0c\u53ef\u80fd\u4e00\u76f4\u90fd\u8868\u73b0\u5f97\u575a\u56fa\u65e0\u6bd4\uff0c\u4f46\u968f\u7740\u6d2a\u6c34\u7684\u4e0d\u65ad\u51b2\u5237\uff0c\u7ec8\u7a76\u4f1a\u51fa\u73b0\u6f0f\u6d1e\uff0c\u800c\u53ea\u8981\u51fa\u73b0\u4e00\u4e2a\u6f0f\u6d1e\uff0c\u539f\u672c\u770b\u4f3c\u4e00\u76f4\u826f\u597d\u7684\u5f62\u52bf\u4fbf\u4f1a\u6025\u8f6c\u76f4\u4e0b\uff0c\u5168\u5c40\u7684\u6e83\u8d25\u8fd1\u5728\u773c\u524d\u3002</p><p>\u8981\u6446\u8131\u8fd9\u79cd\u56f0\u5883\uff0c\u6700\u7b80\u5355\u7684\u65b9\u6cd5\u5c31\u662f\u6253\u5012\u8eab\u4e3a\u53ec\u5524\u8005\u7684\u82cd\u767d\u9886\u4e3b\uff0c\u81f3\u5c11\u53ef\u4ee5\u65ad\u7edd\u654c\u4eba\u7684\u540e\u7eed\u8865\u5145\u3002\u7136\u800c\u4ece\u88ab\u56f4\u653b\u8005\u7684\u65b9\u5411\u770b\u8fc7\u53bb\uff0c\u82cd\u767d\u9886\u4e3b\u85cf\u8eab\u7684\u5730\u65b9\u6070\u597d\u662f\u89c6\u7ebf\u6b7b\u89d2\uff0c\u6839\u672c\u4e0d\u53ef\u80fd\u88ab\u53d1\u73b0\uff0c\u5c31\u7b97\u53d1\u73b0\u4e86\u4e5f\u5f88\u96be\u9694\u7740\u5982\u6b64\u8fdc\u7684\u8ddd\u79bb\u8fdb\u884c\u653b\u51fb\u3002\u5fa1\u5929\u4f7f\u80fd\u591f\u98de\u884c\uff0c\u5012\u662f\u8db3\u4ee5\u627f\u62c5\u6b64\u4efb\uff0c\u4f46\u94fa\u5929\u76d6\u5730\u8f70\u4e0b\u7684\u96f7\u9706\u8ba9\u4ed6\u6839\u672c\u4e0d\u53ef\u80fd\u79bb\u5f00\u534a\u6b65\u3002</p><p>\u743c\u6069\u6b63\u8003\u8651\u662f\u5426\u8981\u51fa\u624b\u76f8\u52a9\uff0c\u539f\u672c\u4e0d\u65ad\u51fb\u4e0b\u7684\u96f7\u9706\u7a81\u7136\u505c\u4e86\uff0c\u7d27\u63a5\u7740\u91cd\u91cd\u4e4c\u4e91\u5411\u56db\u65b9\u63a8\u6d8c\uff0c\u73b0\u51fa\u4e00\u5757\u6697\u9752\u8272\u7684\u5929\u7a7a\u3002\u72c2\u98ce\u547c\u5578\u5377\u8d77\uff0c\u51dd\u805a\u6210\u4e00\u4e2a\u4eba\u5f71\uff0c\u90a3\u662f\u4e2a\u8eab\u6750\u9b41\u68a7\u7684\u4e2d\u5e74\u4eba\uff0c\u5149\u5934\u3001\u72ec\u773c\u3001\u7edc\u816e\u80e1\u987b\uff0c\u7a7f\u7740\u7ed8\u6709\u95ea\u7535\u8840\u7eb9\u7684\u84dd\u767d\u8272\u957f\u888d\uff0c\u7a33\u7a33\u60ac\u6d6e\u5728\u9ad8\u7a7a\u4e2d\uff0c\u6b63\u662f\u4e0a\u6b21\u5728\u661f\u9668\u57ce\u548c\u743c\u6069\u4ea4\u624b\u8fc7\u7684\u5361\u65af\u56fe\u3002\u4ed6\u88ab\u743c\u6069\u523a\u778e\u7684\u53f3\u773c\u5df2\u7ecf\u5b8c\u5168\u590d\u539f\u4e86\uff0c\u5de6\u773c\u4e0a\u4f9d\u65e7\u8fd8\u662f\u6234\u7740\u9ed1\u8272\u773c\u7f69\u3002</p><p>\u201c\u662f\u8fd9\u5bb6\u4f19\u2026\u2026\u201d</p><p>\u201c\u5f53\u7136\u662f\u4ed6\uff0c\u201d\u5965\u5609\u83b1\u65af\u6de1\u6de1\u8bf4\uff0c\u201c\u53ea\u6709\u67ef\u8428\u7684\u9009\u6c11\u624d\u6709\u8fd9\u62db\u2018\u5343\u96f7\u4e07\u9706\u2019\u3002\u54e6\uff0c\u90a3\u5bb6\u4f19\u73b0\u5728\u6539\u540d\u53eb\u2026\u2026\u201d</p><p>\u201c\u5854\u6d1b\u65af\uff01\u201d</p><p>\u5965\u5609\u83b1\u65af\u8bdd\u8fd8\u6ca1\u8bf4\u5b8c\uff0c\u5c31\u542c\u5f97\u8fdc\u5904\u9ad8\u7a7a\u4e2d\u7684\u5361\u65af\u56fe\u4e00\u58f0\u5389\u543c\uff0c\u5de6\u773c\u4e0a\u7684\u9ed1\u8272\u773c\u7f69\u81ea\u884c\u70b8\u5f00\uff0c\u95ea\u7535\u3001\u98d3\u98ce\u548c\u70c8\u7130\u5728\u7a7a\u6d1e\u7684\u773c\u7736\u4e2d\u4ea4\u9519\u95ea\u70c1\uff0c\u4e00\u5c0a\u5de8\u5927\u865a\u50cf\u4ece\u80cc\u540e\u5347\u8d77\uff0c\u90a3\u662f\u4e2a\u9ed1\u8272\u957f\u53d1\u3001\u6ee1\u8138\u80e1\u987b\u7684\u5f6a\u5f62\u5927\u6c49\uff0c\u53cc\u773c\u91d1\u7130\u7206\u71c3\uff0c\u567c\u556a\u95ea\u70c1\u7740\u707f\u70c2\u95ea\u7535\uff0c\u7a7f\u7740\u9ed1\u8272\u7684\u534a\u8eab\u94e0\u7532\uff0c\u624b\u6301\u4e09\u8282\u957f\u6756\uff0c\u795e\u60c5\u4e0d\u6012\u81ea\u5a01\u3002\u5361\u65af\u56fe\u53cc\u624b\u62ac\u8d77\uff0c\u7f13\u7f13\u5408\u62e2\uff0c\u53ea\u89c1\u4ed6\u5468\u56f4\u7684\u91cd\u91cd\u4e4c\u4e91\u4e4b\u4e2d\uff0c\u84dd\u8272\u3001\u7eff\u8272\u548c\u7d2b\u8272\u95ea\u7535\u873f\u8712\u6e38\u52a8\uff0c\u4ea4\u9519\u7f20\u7ed5\uff0c\u9010\u6e10\u6c47\u5408\u5728\u4e00\u8d77\uff0c\u51dd\u805a\u6210\u4e00\u652f\u5de8\u5927\u7684\u94f6\u767d\u8272\u957f\u77db\uff0c\u77db\u8eab\u7f13\u7f13\u65cb\u8f6c\u7740\uff0c\u5bf9\u51c6\u4e86\u5730\u9762\u4e0a\u7684\u5706\u9635\uff0c\u867d\u7136\u8fd8\u672a\u771f\u6b63\u51fb\u4e0b\uff0c\u4f46\u90a3\u79cd\u94fa\u5929\u76d6\u5730\u800c\u6765\u7684\u78c5\u7934\u6c14\u52bf\uff0c\u5c31\u8fde\u8ddd\u79bb\u8fdc\u8fdc\u7684\u65c1\u89c2\u8005\u90fd\u611f\u53d7\u5230\u4e86\u5f3a\u70c8\u7684\u538b\u8feb\u3002</p><p>\u743c\u6069\u80fd\u591f\u6e05\u695a\u5730\u610f\u8bc6\u5230\uff0c\u5bf9\u65b9\u8fd9\u6b21\u7684\u653b\u51fb\u7684\u5a01\u529b\u975e\u6bd4\u5bfb\u5e38\uff0c\u5018\u82e5\u6362\u7b97\u6210\u5deb\u5e08\u7684\u5965\u672f\uff0c\u7edd\u5bf9\u662f\u8d85\u8d8a\u73b0\u5b58\u4e5d\u9636\u9b54\u6cd5\uff0c\u66f4\u52a0\u51cc\u9a7e\u4e4b\u4e0a\u7684\u5b58\u5728\u3002\u88ab\u8fd9\u9053\u95ea\u7535\u77db\u6b63\u9762\u51fb\u4e2d\uff0c\u5c31\u7b97\u662f\u7406\u8bba\u4e0a\u80fd\u963b\u9694\u4e00\u5207\u653b\u51fb\u7684\u8679\u5149\u6cd5\u7403\uff0c\u4e5f\u4e00\u5b9a\u4f1a\u5d29\u6e83\u7684\u5427\u3002</p><p>\u5361\u65af\u56fe\u53c8\u662f\u4e00\u58f0\u5389\u559d\uff0c\u80cc\u540e\u865a\u50cf\u624b\u4e2d\u7684\u4e09\u8282\u6756\u9661\u7136\u5206\u79bb\uff0c\u5316\u4f5c\u4e09\u9053\u65cb\u98ce\uff0c\u63a8\u52a8\u7740\u95ea\u7535\u77db\u8f70\u7136\u51fb\u4e0b\u3002\u5fa1\u5929\u4f7f\u9996\u5f53\u5176\u51b2\uff0c\u4ed6\u6602\u9996\u6ce8\u89c6\u7740\u957f\u77db\uff0c\u4f53\u578b\u6025\u5267\u6da8\u5927\uff0c\u80cc\u540e\u7684\u4e94\u5bf9\u51b0\u84dd\u5149\u7ffc\u91cd\u91cd\u8212\u5f20\uff0c\u65e0\u6570\u7ec6\u5c0f\u7684\u91d1\u8272\u5149\u73af\u4ece\u4e2d\u5347\u8d77\uff0c\u5c06\u56e0\u4e3a\u4e4c\u4e91\u7b3c\u7f69\u800c\u9ed1\u6697\u6c89\u6c89\u7684\u5929\u5730\u95f4\u7167\u8000\u5f97\u5149\u660e\u4e00\u7247\u3002\u5fa1\u5929\u4f7f\u4fbf\u50b2\u7136\u5c79\u7acb\u5728\u8fd9\u5149\u660e\u4e4b\u4e2d\uff0c\u9ad8\u9ad8\u4e3e\u8d77\u624b\u4e2d\u5de8\u5927\u6218\u9524\uff0c\u8fce\u4e0a\u4e86\u9ad8\u901f\u5c04\u6765\u7684\u957f\u77db\u3002</p><p>\u201c\u8f70\uff01\u201d</p><p>\u4e24\u80a1\u5f3a\u5927\u7684\u529b\u91cf\u78b0\u649e\uff0c\u7206\u53d1\u51fa\u65e0\u4e0e\u4f26\u6bd4\u7684\u5de8\u54cd\uff0c\u5fa1\u5929\u4f7f\u4ece\u4e00\u5f00\u59cb\u5c31\u843d\u4e86\u4e0b\u98ce\uff0c\u4ed6\u7684\u6218\u9524\u5728\u4e00\u77ac\u95f4\u88ab\u51fb\u6210\u7c89\u788e\uff0c\u95ea\u7535\u77db\u6df1\u6df1\u523a\u8fdb\u5fa1\u5929\u4f7f\u7684\u80f8\u819b\uff0c\u5c06\u4ed6\u6574\u4e2a\u8eab\u4f53\u9ad8\u901f\u63a8\u5411\u5730\u9762\u3002\u773c\u770b\u5fa1\u5929\u4f7f\u5c31\u8981\u88ab\u9489\u5728\u5730\u4e0a\uff0c\u4ed6\u9661\u7136\u95f4\u5f20\u53e3\u6012\u543c\uff0c\u80cc\u540e\u4e94\u5bf9\u707f\u70c2\u5149\u7ffc\u50cf\u624b\u81c2\u822c\u4e00\u8d77\u5408\u62e2\uff0c\u5c06\u95ea\u7535\u77db\u5b8c\u5168\u5305\u88f9\u5728\u5176\u4e2d\u3002\u70c8\u7130\u4ece\u5fa1\u5929\u4f7f\u7684\u6bcf\u4e2a\u6bdb\u5b54\u4e2d\u55b7\u5c04\u51fa\u6765\uff0c\u539f\u672c\u9ad8\u901f\u4e0b\u5760\u4e4b\u52bf\u88ab\u77ac\u95f4\u6b62\u4f4f\uff0c\u53cd\u800c\u5189\u5189\u4e0a\u5347\uff0c\u50cf\u662f\u4e00\u8f6e\u51b0\u84dd\u8272\u7684\u65ed\u65e5\u3002\u5f53\u5347\u5230\u516d\u767e\u5c3a\u9ad8\u5ea6\u65f6\uff0c\u65ed\u65e5\u731b\u7136\u7206\u70b8\u5f00\u6765\u3002</p><p>\u6f8e\u6e43\u7684\u6c14\u6d6a\u5316\u4f5c\u547c\u5578\u72c2\u98ce\uff0c\u5fa1\u5929\u4f7f\u4e5f\u597d\uff0c\u95ea\u7535\u77db\u4e5f\u7f62\uff0c\u90fd\u6d88\u5931\u5f97\u65e0\u5f71\u65e0\u8e2a\u3002\u5343\u4e07\u70b9\u94f6\u767d\u8272\u548c\u51b0\u84dd\u8272\u7684\u5149\u8292\u50cf\u66b4\u96e8\u4e00\u822c\u4ece\u5929\u7a7a\u4e2d\u6d12\u843d\uff0c\u6240\u6709\u88ab\u6e85\u5230\u7684\u4ea1\u7075\u90fd\u5728\u77ac\u95f4\u5316\u4f5c\u55e4\u55e4\u767d\u70df\uff0c\u5706\u9635\u4e2d\u7684\u6b66\u58eb\u548c\u7267\u5e08\u4eec\u540c\u6837\u4e5f\u53d7\u5230\u4f24\u5bb3\uff0c\u4f46\u8f7b\u5fae\u5f97\u591a\uff0c\u800c\u4e14\u5728\u6cbb\u7597\u795e\u672f\u7684\u4f5c\u7528\u4e0b\u5f88\u5feb\u4fbf\u6062\u590d\u4e86\u3002\u5361\u65af\u56fe\u4f9d\u65e7\u7ad9\u7acb\u5728\u9ad8\u7a7a\u4e4b\u4e2d\uff0c\u5374\u662f\u8eab\u4f53\u50f5\u76f4\uff0c\u52a8\u4e5f\u4e0d\u52a8\uff0c\u50cf\u662f\u88ab\u5b9a\u4f4f\u4e86\u4e00\u822c\uff0c\u80cc\u540e\u7684\u865a\u50cf\u4e5f\u6e10\u6e10\u900f\u660e\uff0c\u76f4\u81f3\u5b8c\u5168\u6d88\u5931\u4e0d\u89c1\u3002</p><p>\u201c\u662f\u65f6\u5019\u4e86\u3002\u201d\u8fdc\u5904\u89c2\u6218\u7684\u8bf8\u4eba\u4e2d\uff0c\u5965\u5609\u83b1\u65af\u7a81\u7136\u5f00\u53e3\u8bf4\u3002</p><p>\u201c\u4ec0\u4e48\u662f\u65f6\u5019\u4e86\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u201c\u4f60\u53ef\u4ee5\u4f20\u8baf\u8ba9\u90a3\u4f4d\u6885\u83f2\u65af\u5c0f\u59d0\u6765\u4e86\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u5965\u5609\u83b1\u65af\u7684\u8bdd\u5b9e\u5728\u662f\u592a\u51fa\u4e4e\u610f\u6599\uff0c\u743c\u6069\u4e00\u65f6\u95f4\u6014\u4f4f\uff0c\u90fd\u4e0d\u77e5\u9053\u8be5\u5982\u4f55\u56de\u7b54\u3002\u201c\u4e00\u5f00\u59cb\u4f60\u4e0d\u6562\u8ba9\u5979\u6765\u5192\u9669\uff0c\u4e0d\u80af\u901a\u77e5\u5979\uff0c\u73b0\u5728\u5df2\u7ecf\u65e0\u9700\u987e\u5fcc\u4e86\u5427\uff0c\u201d\u5927\u5965\u672f\u5e08\u6de1\u6de1\u8bf4\uff0c\u201c\u90a3\u4e2a\u5149\u5934\u5df2\u7ecf\u62db\u6570\u7528\u5c3d\uff0c\u5343\u96f7\u4e07\u9706\u653e\u51fa\u6765\uff0c\u4e0d\u5728\u5e8a\u4e0a\u8eba\u4e2a\u5341\u5929\u534a\u6708\u522b\u60f3\u52a8\u5f39\uff0c\u73b0\u5728\u5c31\u662f\u5e9f\u4eba\u4e00\u4e2a\u3002\u8fd9\u91cc\u53c8\u6ca1\u6709\u522b\u7684\u654c\u4eba\uff0c\u53ea\u8981\u518d\u5e72\u6389\u90a3\u4e2a\u82cd\u767d\u9886\u4e3b\uff0c\u4e00\u5207\u95ee\u9898\u5c31\u90fd\u89e3\u51b3\u4e86\u3002\u201d</p><p>\u95ee\u9898\u90fd\u89e3\u51b3\u5012\u4e5f\u4e0d\u81f3\u4e8e\uff0c\u81f3\u5c11\u8fd8\u6709\u90a3\u6f2b\u5c71\u904d\u91ce\u7684\u4ea1\u7075\u3002\u4f46\u6ca1\u4e86\u4e3b\u63a7\u8005\uff0c\u8fd9\u4e9b\u6742\u5175\u5c31\u597d\u5bf9\u4ed8\u591a\u4e86\u3002\u743c\u6069\u4e0d\u89e3\u5965\u5609\u83b1\u65af\u4e3a\u4f55\u4f1a\u8bf4\u8fd9\u4e9b\u8bdd\uff0c\u4f46\u81f3\u5c11\u9053\u7406\u662f\u6b63\u786e\u7684\uff0c\u867d\u7136\u603b\u8fd8\u662f\u89c9\u5f97\u6709\u70b9\u602a\u602a\u7684\u2026\u2026\u4e5f\u5f88\u6b63\u5e38\u4e86\uff0c\u4ee5\u5965\u5609\u83b1\u65af\u548c\u73ca\u5609\u7684\u5173\u7cfb\uff0c\u5979\u63d0\u5230\u6885\u83f2\u65af\uff0c\u8bed\u6c14\u4e0d\u602a\u624d\u662f\u4e0d\u6b63\u5e38\u3002</p><p>\u5965\u5609\u83b1\u65af\u63a7\u5236\u8679\u5149\u7403\u9732\u51fa\u4e00\u4e2a\u7f3a\u53e3\uff0c\u743c\u6069\u76ee\u6d4b\u8ddd\u79bb\uff0c\u7136\u540e\u91ca\u653e\u4f20\u9001\u672f\uff0c\u4ed6\u7684\u8eab\u5f71\u77ac\u95f4\u8de8\u8d8a\u865a\u7a7a\uff0c\u51fa\u73b0\u5728\u82cd\u767d\u9886\u4e3b\u85cf\u8eab\u6240\u5728\u7684\u6d1e\u7a9f\u4e2d\u3002\u8679\u5149\u7403\u91cd\u65b0\u5408\u62e2\uff0c\u5965\u5609\u83b1\u65af\u653e\u4e0b\u4e66\uff0c\u51b7\u7b11\u4e00\u58f0\u3002</p><p>\u201c\u8001\u5e08\uff0c\u4f60\u2026\u2026\u7b11\u4ec0\u4e48\uff1f\u201d\u73ca\u5609\u4e0d\u89e3\u3002</p><p>\u201c\u6ca1\u4ec0\u4e48\u3002\u201d</p><p>\u73ca\u5609\u79c0\u7709\u8e59\u8d77\uff0c\u5979\u542c\u51fa\u4e86\u5965\u5609\u83b1\u65af\u8bed\u6c14\u4e2d\u7684\u51b7\u5389\uff0c\u201c\u90a3\u4e2a\u4ec0\u4e48\u82cd\u767d\u9886\u4e3b\u5f88\u5389\u5bb3\u662f\u5417\uff0c\u96be\u9053\u743c\u6069\u548c\u827e\u5f25\u8587\u8054\u624b\u90fd\u5bf9\u4ed8\u4e0d\u4e86\uff1f\u201d</p><p>\u201c\u90a3\u5012\u4e0d\u81f3\u4e8e\uff0c\u201d\u5965\u5609\u83b1\u65af\u8fdc\u8fdc\u770b\u7740\u743c\u6069\uff0c\u201c\u5982\u679c\u90a3\u4e2a\u53eb\u6885\u83f2\u65af\u7684\u5973\u5b69\u5b50\u771f\u6709\u4f60\u8bf4\u7684\u90a3\u4e48\u5389\u5bb3\uff0c\u5bf9\u4ed8\u8fd9\u4e2a\u4ea1\u7075\u5e08\u662f\u7ef0\u7ef0\u6709\u4f59\u4e86\uff0c\u201d\u5979\u7b11\u4e86\u7b11\uff0c\u201c\u53ea\u662f\u4e0d\u77e5\u9053\uff0c\u5982\u679c\u518d\u52a0\u4e24\u6761\u9aa8\u9f99\u4f1a\u5982\u4f55\u5462\uff1f\u201d</p></article>"}}}]);
