(window.webpackJsonp=window.webpackJsonp||[]).push([[48],{71:function(p){p.exports={chapterTitle:null,title:"\u7b2c48\u7ae0 \u603b\u4ee3\u7406\u5546\u7684\u4ee3\u8868",content:"<article><h2>\u7b2c48\u7ae0 \u603b\u4ee3\u7406\u5546\u7684\u4ee3\u8868</h2><p>\u3000\u3000\u5468\u4e94\u4e0b\u5348\u7684\u7b2c\u4e00\u8282\u8bfe\u662f\u674e\u829d\u82b3\u7684\u8bed\u6587\u8bfe\uff0c\u521a\u7b49\u4e0a\u8bfe\u7684\u94c3\u58f0\u6572\u54cd\uff0c\u5f20\u606a\u5c31\u6342\u7740\u809a\u5b50\u51fa\u4e86\u6559\u5ba4\uff0c\u5728\u5395\u6240\u6253\u4e86\u4e2a\u8f6c\uff0c\u56de\u5230\u6559\u5ba4\u8ddf\u6b63\u9886\u7740\u5927\u5bb6\u6717\u8bfb\u8bfe\u6587\u7684\u674e\u829d\u82b3\u8bf7\u4e86\u75c5\u5047\uff0c\u51fa\u6821\u95e8\u62e6\u4e86\u4e00\u8f86\u51fa\u79df\u8f66\u76f4\u5954\u65b0\u6d77\u901a\u5927\u53a6\u3002</p><p>\u3000\u3000\u529e\u4e8b\u5904\u6324\u4e86\u8bb8\u591a\u4eba\uff0c\u4ed6\u4eec\u624b\u91cc\u90fd\u634f\u7740\u4eca\u5929\u7684\u6d77\u5dde\u65e5\u62a5\uff0c\u770b\u6765\u6d77\u5dde\u65e5\u62a5\u7684\u534a\u7248\u5e7f\u544a\u6548\u679c\u4e0d\u9519\uff0c\u4e34\u65f6\u627e\u6765\u7684\u5b66\u751f\u96f6\u5de5\uff0c\u6b63\u7a77\u4e8e\u5e94\u4ed8\uff0c\u4eca\u5929\u7684\u5e7f\u544a\u63ed\u9732\u8c1c\u5e95\uff0c\u73b0\u5728\u8fd8\u627e\u4e0a\u95e8\u6765\u7684\u54a8\u8be2\u8005\uff0c\u5df2\u4e0d\u5355\u7eaf\u662f\u597d\u5947\u5fc3\u65fa\u76db\u7684\u5e02\u6c11\uff0c\u6709\u6f5c\u5728\u7684\u7ec8\u7aef\u9500\u552e\u5546\uff0c\u8fd8\u6709\u5f88\u591a\u662f\u5bf9\u6570\u5b57\u624b\u673a\u611f\u5174\u8da3\u7684\u5927\u6b3e\u5927\u8155\u4eec\u3002</p><p>\u3000\u3000\u867d\u7136\u6b64\u6b21\u5e7f\u544a\u7684\u76ee\u7684\u5728\u4e8e\u5438\u5f15\u6f5c\u5728\u7684\u7ec8\u7aef\u9500\u552e\u5546\uff0c\u5bf9\u4e8e\u90a3\u4e9b\u6253\u7535\u8bdd\u6765\u54a8\u8be2\u7684\u5927\u6b3e\u5927\u8155\u4eec\uff0c\u8fd8\u8981\u5efa\u7acb\u5b8c\u5907\u7684\u8d44\u6599\u5361\uff0c\u5982\u679c\u80fd\u76f4\u63a5\u5c06\u6700\u7ec8\u7528\u6237\u8d44\u6e90\u4e5f\u638c\u63e1\u5728\u81ea\u5df1\u624b\u91cc\uff0c\u4e89\u53d6\u4ee3\u7406\u6743\u65f6\uff0c\u65e0\u7591\u5bf9\u8c08\u5224\u66f4\u4e3a\u6709\u5229\u3002</p><p>\u3000\u3000\u6309\u7167\u8bae\u5b9a\u597d\u7684\u5de5\u4f5c\u7a0b\u5e8f\uff0c\u5c06\u6f5c\u5728\u7684\u7ec8\u7aef\u9500\u552e\u5546\u68b3\u7406\u51fa\u6765\uff0c\u5148\u8fdb\u884c\u6280\u672f\u626b\u76f2\uff0c\u7136\u540e\u5206\u522b\u7531\u6768\u4e91\u3001\u848b\u8587\u4ed6\u4eec\u6328\u4e2a\u63a5\u5f85\u6765\u8bbf\u8005\u5546\u8c08\u63a5\u4e0b\u6765\u7684\u5408\u4f5c\u610f\u5411\uff0c\u8bb8\u601d\u8d1f\u8d23\u5168\u5c40\u7edf\u7b79\u3002</p><p>\u3000\u3000\u5f20\u606a\u8d70\u8fdb\u5927\u529e\u516c\u5ba4\uff0c\u770b\u89c1\u5218\u660e\u8f89\u6b63\u9001\u4e00\u4e2a\u4e2d\u5e74\u4eba\u51fa\u4f1a\u8bae\u5ba4\uff0c\u7b11\u5bb9\u6ee1\u9762\u7684\u8ddf\u4e2d\u5e74\u4eba\u544a\u522b\u3002\u5f20\u606a\u671d\u5218\u660e\u8f89\u62db\u624b\u6253\u4e86\u58f0\u62db\u547c\uff0c\u5218\u660e\u8f89\u5c0f\u6b65\u5feb\u8d70\u8fc7\u6765\uff0c\u8d34\u7740\u4ed6\u7684\u8033\u6735\u8bf4\uff1a\u201c\u76db\u946b\u516c\u53f8\u7684\u4e1a\u52a1\u4ee3\u8868\u5927\u4e2d\u5348\u5c31\u8d76\u8fc7\u6765\u4e86\uff0c\u5728\u8bb8\u7ecf\u7406\u7684\u529e\u516c\u5ba4\u91cc\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u8ddf\u5218\u660e\u8f89\u4ed6\u4eec\u660e\u8bf4\u81ea\u5df1\u8fd8\u662f\u5b66\u751f\uff0c\u529e\u4e8b\u5904\u8fd9\u8fb9\u7684\u4e8b\u60c5\u4e3b\u8981\u7531\u8bb8\u601d\u8d1f\u8d23\u5904\u7406\u3002\u653e\u5b66\u540e\uff0c\u5f20\u606a\u624d\u80fd\u8d76\u8fc7\u6765\u6c47\u5408\uff0c\u8ba8\u8bba\u603b\u7ed3\u4e00\u5929\u7684\u5de5\u4f5c\u60c5\u51b5\u3002</p><p>\u3000\u3000\u5218\u660e\u8f89\u4ed6\u4eec\u4e5f\u6ca1\u6709\u591a\u60f3\uff0c\u6bd5\u7adf\u5f20\u606a\u521a\u8ddf\u4ed6\u4eec\u63a5\u89e6\u65f6\u7684\u8868\u73b0\uff0c\u662f\u4ed6\u4eec\u81ea\u77e5\u4e0d\u53ca\u7684\uff0c\u5e74\u9f84\u5e76\u4e0d\u662f\u95ee\u9898\uff0c\u4f55\u51b5\u4ed6\u4eec\u4e00\u76f4\u90fd\u6ca1\u6709\u60f3\u5230\u5f20\u606a\u6b64\u65f6\u624d\u5341\u516d\u5c81\u3002\u540e\u4e3a\u77e5\u9053\u8fd9\u4e00\u4e8b\u5b9e\uff0c\u90fd\u60ca\u8bb6\u7684\u8bf4\uff1a\u201c\u4e0d\u770b\u5f20\u603b\u7684\u8138\uff0c\u542c\u4ed6\u8bf4\u8bdd\u7684\u8bed\u6c14\uff0c\u54ea\u91cc\u50cf\u4e09\u5341\u5c81\u4ee5\u4e0b\u7684\uff0c\u54e6\uff0c\u5bf9\u4e86\uff0c\u58f0\u97f3\u6709\u4e9b\u7a1a\u5ae9\u2026\u2026\u201d</p><p>\u3000\u3000\u4e5d\u56db\u5e74\u90a3\u4f1a\u513f\uff0c\u53d1\u5bb6\u81f4\u5bcc\u7684\u6587\u76f2\u3001\u534a\u6587\u76f2\uff0c\u6bd4\u6709\u5b66\u5386\u7684\u77e5\u8bc6\u5206\u5b50\u8981\u591a\u5f97\u591a\uff0c\u9020\u6210\u4eba\u4eec\u89c2\u5ff5\u4e0a\u5e76\u4e0d\u91cd\u89c6\u6559\u80b2\uff0c\u4e2d\u5b66\u5c31\u7f00\u5b66\u3001\u5bb6\u91cc\u7ed9\u94b1\u5f00\u516c\u53f8\u7684\u8d25\u5bb6\u5b50\uff0c\u5728\u6d77\u5dde\u4e5f\u4e0d\u5c11\u89c1\u3002\u518d\u8bf4\u4e5d\u56db\u5e74\u90a3\u4f1a\u513f\u9ad8\u6821\u8fd8\u6ca1\u6709\u5f00\u59cb\u6269\u62db\uff0c\u4e2d\u4e13\u3001\u9ad8\u4e2d\u6bd5\u4e1a\u5c31\u51fa\u6765\u5de5\u4f5c\u7684\u5c0f\u9752\u5de5\uff0c\u6ee1\u8857\u90fd\u662f\uff0c\u5f20\u606a\u770b\u4e0a\u53bb\u6bd4\u4ed6\u4eec\u8fd8\u8981\u6210\u719f\u4e9b\u3002</p><p>\u3000\u3000\u5f20\u606a\u542c\u5218\u660e\u8f89\u8bf4\u76db\u946b\u516c\u53f8\u7684\u4ee3\u8868\u5df2\u7ecf\u6765\u4e86\uff0c\u5411\u8bb8\u601d\u7684\u7ecf\u7406\u5ba4\u8d70\u8fc7\u53bb\uff0c\u53c8\u542c\u89c1\u5218\u660e\u8f89\u5728\u540e\u9762\u8bf4\uff1a\u201c\u76db\u946b\u7684\u4ee3\u8868\uff0c\u8138\u8272\u597d\u50cf\u6709\u4e9b\u4e25\u8083\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u63a8\u5f00\u8bb8\u601d\u7ecf\u7406\u5ba4\u7684\u95e8\u3002\u76db\u946b\u516c\u53f8\u7684\u4e1a\u52a1\u4ee3\u8868\u6b63\u80cc\u5bf9\u7740\u81ea\u5df1\uff0c\u957f\u53d1\u675f\u5728\u80a9\u540e\uff0c\u9732\u51fa\u7684\u8116\u9888\u80a4\u8272\u767d\u7699\uff0c\u7a7f\u7740\u6d0b\u7070\u8272\u7684\u7ec6\u6761\u7eb9\u886c\u886b\uff0c\u524a\u80a9\u3001\u7ec6\u8170\u3001\u8eab\u6750\u7ea4\u7ec6\uff0c\u4e0b\u8eab\u7a7f\u7740\u7b52\u88d9\uff0c\u4e1d\u889c\u4e0e\u88d9\u6446\u95f4\u9732\u51fa\u4e00\u622a\u767d\u817b\u5982\u7389\u7684\u817f\u808c\uff0c\u5f20\u606a\u8bb0\u7740\u4e0a\u6b21\u8054\u7edc\u65f6\uff0c\u8fd9\u4e2a\u5973\u4eba\u53eb\u53f6\u5c0f\u6850\uff0c\u5979\u6ca1\u6709\u610f\u8bc6\u5f20\u606a\u8d70\u8fdb\u6765\uff0c\u5728\u5bf9\u8bb8\u601d\u8bf4\u8bdd\uff1a\u201c\u4f60\u4eec\u4e0d\u662f\u7231\u7acb\u4fe1\u5728\u6d77\u5dde\u7684\u4ee3\u7406\u5546\uff0c\u8c01\u6388\u6743\u8ba9\u4f60\u4eec\u5728\u5a92\u4f53\u4e0a\u767b\u8fd9\u6837\u7684\u5e7f\u544a\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u770b\u5230\u5979\u624b\u8fb9\u7684\u529e\u516c\u684c\u4e0a\u653e\u7740\u51e0\u4efd\u62a5\u7eb8\uff0c\u6700\u4e0a\u9762\u5c31\u662f\u4eca\u5929\u7684\u6d77\u5dde\u65e5\u62a5\u3002</p><p>\u3000\u3000\u201c\u767b\u5e7f\u544a\u9700\u8981\u6388\u6743\u5417\uff1f\u201d\u5f20\u606a\u6572\u4e86\u6572\u95e8\u3002</p><p>\u3000\u3000\u8bb8\u601d\u62ac\u8d77\u5934\uff0c\u9732\u51fa\u8ff7\u4eba\u7684\u5fae\u7b11\uff0c\u663e\u7136\u5979\u5df2\u7ecf\u7ed9\u8fd9\u5973\u4eba\u6405\u4e86\u597d\u4e00\u4f1a\u513f\uff0c\u6709\u4e9b\u75b2\u60eb\u4e86\u3002</p><p>\u3000\u3000\u53f6\u5c0f\u6850\u8b66\u89c9\u7684\u56de\u8fc7\u5934\u6765\uff0c\u4e8c\u5341\u4e09\u56db\u5c81\uff0c\u5bb9\u8c8c\u6e05\u4e3d\uff0c\u8be7\u5f02\u7684\u95ee\uff1a\u201c\u4ed6\u662f\u8c01\uff1f\u201d</p><p>\u3000\u3000\u201c\u6210\u4e3a\u7231\u7acb\u4fe1\u5728\u6d77\u5dde\u7684\u4ee3\u7406\u5546\uff0c\u662f\u6211\u7684\u51b3\u5b9a\uff0c\u4e0a\u6b21\u662f\u6211\u8ddf\u4f60\u901a\u7684\u7535\u8bdd\u3002\u201d</p><p>\u3000\u3000\u201c\u5f20\u606a\u2026\u2026\u201d\u53f6\u5c0f\u6850\u7591\u60d1\u7684\u770b\u7740\u5f20\u606a\uff0c\u4e0e\u4e4b\u524d\u6240\u5f62\u6210\u7684\u5370\u8c61\u6709\u76f8\u5f53\u5927\u7684\u5dee\u8ddd\uff0c\u542c\u7535\u8bdd\u91cc\u7684\u58f0\u97f3\u3001\u63aa\u8f9e\uff0c\u662f\u90a3\u79cd\u4e09\u5341\u5c81\u51fa\u5934\u3001\u5404\u65b9\u9762\u90fd\u5e94\u8be5\u76f8\u5f53\u51fa\u8272\u7684\u7537\u4eba\uff0c\u5b9e\u9645\u770b\u8d77\u6765\u600e\u4e48\u8fd9\u4e48\u5e74\u8f7b\uff1f</p><p>\u3000\u3000\u5f20\u606a\u8d70\u8fc7\u53bb\uff0c\u62ff\u8fc7\u684c\u4e0a\u7684\u62a5\u7eb8\uff0c\u662f\u6d77\u5dde\u65e5\u62a5\u6700\u8fd1\u4e09\u5929\u7684\u62a5\u7eb8\uff0c\u90fd\u6298\u5230\u7b2c\u4e09\u7248\u4e0a\uff1a\u201c\u5f0a\u4eba\u5f20\u606a\uff0c\u606a\u5b88\u4fe1\u4e49\u7684\u606a\uff0c\u4e0e\u8bb8\u601d\u4e00\u540c\u8d1f\u8d23\u8fd9\u8fb9\u7684\u4e8b\u52a1\u3002\u6211\u60f3\u95ee\u53f6\u5c0f\u59d0\u4e00\u53e5\u8bdd\uff1a\u767b\u8fd9\u6837\u7684\u5e7f\u544a\uff0c\u4e00\u5b9a\u9700\u8981\u6388\u6743\u5417\uff1f\u6cd5\u5f8b\u4e0a\u5e76\u6ca1\u6709\u660e\u6587\u89c4\u5b9a\u9700\u8981\u6388\u6743\uff0c\u6469\u6258\u7f57\u62c9\u516c\u53f8\u5012\u662f\u53ef\u544a\u6211\u4eec\u8bfd\u8c24\uff0c\u56e0\u4e3a\u6211\u4eec\u5728\u5e7f\u544a\u91cc\u5f88\u80af\u5b9a\u8bf4\u4ed6\u4eec\u7684\u2018\u5927\u54e5\u5927\u2019\u5f88\u5feb\u5c31\u4f1a\u88ab\u6dd8\u6c70\u6389\u3002\u201d</p><p>\u3000\u3000\u201c\u6388\u6743\u2026\u2026\u8c01\u770b\u5230\u8fd9\u4efd\u62a5\u7eb8\uff0c\u90fd\u4f1a\u8bef\u4ee5\u4e3a\u4f60\u4eec\u662f\u7231\u7acb\u4fe1\u5728\u6d77\u5dde\u7684\u4ee3\u7406\u5546\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u5e76\u4e0d\u662f\uff0c\u201d\u53f6\u5c0f\u6850\u8fdf\u7591\u7684\u8bf4\uff0c\u201c\u8fd9\u4e2a\u8ddf\u7535\u89c6\u91cc\u5e38\u64ad\u7684\u67d0\u4e9b\u60c5\u8282\u7c7b\u4f3c\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8bc8\u9a97\uff1f\u201d\u5f20\u606a\u7b11\u4e86\u8d77\u6765\uff0c\u5c06\u4e09\u4efd\u62a5\u7eb8\u5e73\u644a\u5230\u529e\u516c\u684c\u4e0a\uff0c\u201c\u6a21\u62df\u4fe1\u53f7\u901a\u8baf\u6280\u672f\u843d\u540e\uff0c\u6807\u51c6\u5316\u7a0b\u5ea6\u4f4e\uff0c\u6ce8\u5b9a\u8981\u88ab\u5148\u8fdb\u7684\u3001\u6807\u51c6\u5316\u7a0b\u5ea6\u66f4\u9ad8\u7684\u6570\u5b57\u901a\u8baf\u6280\u672f\u6240\u53d6\u4ee3\uff0c\u6211\u4eec\u575a\u4fe1\u8fd9\u70b9\uff0c\u4e5f\u662f\u8fd9\u4e2a\u5e7f\u544a\u6240\u8868\u8fbe\u7684\u4e3b\u8981\u4fe1\u606f\u3002\u800c\u4e14\uff0c\u6211\u4eec\u8ba4\u4e3a\u7231\u7acb\u4fe1\u5728\u6570\u5b57\u901a\u8baf\u5e02\u573a\u4e0a\u6709\u8d85\u8d8a\u6469\u6258\u7f57\u62c9\u7684\u53ef\u80fd\uff0c\u8fd9\u4e5f\u662f\u5e7f\u544a\u6240\u8981\u8868\u8fbe\u7684\u4fe1\u606f\uff0c\u5ba3\u4f20\u6570\u5b57\u901a\u8baf\u6280\u672f\u800c\u5df2\uff0c\u8bf4\u6210\u516c\u76ca\u5e7f\u544a\u4e5f\u53ef\u4ee5\u554a\u3002\u201d</p><p>\u3000\u3000\u8d85\u8d8a\u6469\u6258\u7f57\u62c9\uff1f\u53f6\u5c0f\u6850\u7ed9\u5f20\u606a\u81ea\u4fe1\u7684\u8bed\u6c14\u5413\u4f4f\uff0c\u5c31\u7b97\u4f5c\u4e3a\u7231\u7acb\u4fe1\u7684\u534e\u4e1c\u5730\u533a\u603b\u4ee3\u7406\u5546\uff0c\u76db\u946b\u516c\u53f8\u5185\u90e8\u4e5f\u6ca1\u6709\u8ba4\u4e3a\u7231\u7acb\u4fe1\u6709\u5728\u901a\u8baf\u5e02\u573a\u8d85\u8d8a\u6469\u6258\u7f57\u62c9\u7684\u53ef\u80fd\uff0c\u8981\u77e5\u9053\u5728\u4e5d\u56db\u5e74\u6469\u6258\u7f57\u62c9\u5360\u56fd\u5185\u901a\u4fe1\u5e02\u573a\u768490\uff05\u4ee5\u4e0a\u3002</p><p>\u3000\u3000\u7231\u7acb\u4fe1\u4e00\u76f4\u5230\u4e5d\u4e03\u5e74\u624d\u5728\u56fd\u5185\u901a\u8baf\u5e02\u573a\u8d85\u8d8a\u6469\u6258\u7f57\u62c9\uff0c\u800c\u6469\u6258\u7f57\u62c9\u5728\u524d\u4e00\u5e74\u5e02\u573a\u4efd\u989d\u8fd8\u8d85\u8fc770\uff05\uff0c\u4e1a\u5185\u79f0\u8fd9\u4e3a\u901a\u8baf\u5e02\u573a\u7684\u795e\u8bdd\u3002</p><p>\u3000\u3000\u8fd9\u4e9b\u4e8b\u60c5\u73b0\u5728\u65e0\u6cd5\u89e3\u91ca\uff0c\u5f20\u606a\u8bf4\u8fd9\u4e9b\u4e5f\u4e0d\u662f\u8981\u5c06\u53f6\u5c0f\u6850\u7ed9\u552c\u4f4f\uff0c\u5173\u952e\u8981\u8bf4\u670d\u5979\u76f8\u4fe1\u529e\u4e8b\u5904\u6709\u80fd\u529b\u62c5\u4efb\u7231\u7acb\u4fe1\u5728\u6d77\u5dde\u7684\u6e20\u9053\u5206\u9500\u5546\u3002</p><p>\u3000\u3000\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u767b\u8fd9\u5219\u5e7f\u544a\u7684\u76ee\u7684\uff0c\u4e0d\u662f\u8bc8\u9a97\uff0c\u800c\u662f\u5bfb\u6c42\u5408\u4f5c\u8005\u3002\u6b63\u5982\u4e4b\u524d\u4e0e\u4f60\u5728\u7535\u8bdd\u8054\u7edc\u4e2d\u6240\u900f\u9732\u7684\u4fe1\u606f\u4e00\u6837\uff0c\u6211\u4eec\u53ea\u662f\u6d77\u88d5\u516c\u53f8\u5728\u6d77\u5dde\u7684\u529e\u4e8b\u5904\uff0c\u80fd\u8c03\u7528\u7684\u8d44\u91d1\u6709\u9650\uff0c\u65e0\u6cd5\u8fbe\u5230\u4f60\u4eec\u516c\u53f8\u5927\u91cf\u94fa\u8d27\u7684\u8d44\u91d1\u8981\u6c42\uff0c\u4f46\u662f\u6211\u4eec\u4e0d\u60f3\u653e\u5f03\u8fd9\u4e2a\u673a\u4f1a\uff0c\u6240\u4ee5\u767b\u51fa\u8fd9\u5219\u5e7f\u544a\uff0c\u5728\u6d77\u5dde\u5e7f\u6cdb\u7684\u5bfb\u6c42\u5408\u4f5c\u8005\u2026\u2026\u201d\u5f20\u606a\u6307\u7740\u5927\u5385\u91cc\u7ad9\u7740\u7684\u4eba\u7fa4\uff0c\u201c\u53f6\u5c0f\u59d0\u770b\u5230\u6ca1\u6709\uff1f\u8fd9\u5219\u5e7f\u544a\u8ba9\u4ed6\u4eec\u610f\u8bc6\u5230\u6570\u5b57\u901a\u8baf\u5e26\u6765\u7684\u5546\u673a\uff0c\u4ed6\u4eec\u4e2d\u5f88\u591a\u4eba\u90fd\u662f\u7ecf\u8425\u5bfb\u547c\u673a\u7684\u5c0f\u7ecf\u8425\u5546\uff0c\u5bf9\u8fd9\u7c7b\u5546\u673a\u5f88\u654f\u611f\uff0c\u4f46\u662f\u4ed6\u4eec\u6216\u8005\u6ca1\u6709\u5b9e\u529b\u72ec\u81ea\u53bb\u62ff\u7231\u7acb\u4fe1\u7684\u4ee3\u7406\u6743\uff0c\u6216\u8005\u89c9\u5f97\u73b0\u5728\u5c31\u8fdb\u5165\u6570\u5b57\u624b\u673a\u5e02\u573a\uff0c\u98ce\u9669\u6709\u4e9b\u592a\u5927\uff0c\u6211\u4eec\u7684\u76ee\u7684\u5c31\u662f\u628a\u4ed6\u4eec\u805a\u96c6\u8d77\u6765\uff0c\u6210\u4e3a\u6211\u4eec\u7684\u5408\u4f5c\u8005\uff0c\u4f5c\u4e3a\u5408\u4f5c\u8005\uff0c\u6c47\u805a\u8d77\u6765\uff0c\u4ed6\u4eec\u65e2\u53ef\u4ee5\u63d0\u4f9b\u94fa\u8d27\u6240\u9700\u7684\u5927\u91cf\u8d44\u91d1\uff0c\u53c8\u662f\u5f62\u6210\u6700\u7ec8\u7684\u9500\u552e\u7f51\u7edc\u2026\u2026\u201d</p><p>\u3000\u3000\u53f6\u5c0f\u6850\u540e\u6765\u56de\u5fc6\u6b64\u65f6\u7684\u60c5\u5f62\u8bf4\uff1a\u201c\u5f53\u65f6\u6ca1\u60f3\u5230\u5f20\u606a\u53ea\u662f\u521a\u8bfb\u9ad8\u4e2d\u7684\u5c0f\u5c41\u5b69\uff0c\u542c\u5230\u4ed6\u7684\u90a3\u4e00\u756a\u5b8f\u7bc7\u5927\u8bba\uff0c\u5c31\u5f7b\u5e95\u8499\u4e86\u2026\u2026\u201d</p></article>"}}}]);
