(window.webpackJsonp=window.webpackJsonp||[]).push([[484],{506:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u516d\u8282 \u63d0\u9192",content:"<article><h2>\u7b2c\u5341\u516d\u8282 \u63d0\u9192</h2><p>\u201c\u4e03\u74e3\u76fe\uff01\u201d</p><p>\u5965\u5609\u83b1\u65af\u5feb\u901f\u5ff5\u8bf5\u7b80\u6d01\u7684\u5492\u6587\uff0c\u7d2b\u8272\u5149\u7ebf\u4ece\u4e66\u9875\u4e2d\u53d1\u51fa\uff0c\u6295\u5f71\u6210\u4e03\u7247\u7f8e\u4e3d\u82b1\u74e3\uff0c\u968f\u5373\u5177\u73b0\u4e3a\u5b9e\u4f53\u3002\u5979\u5f20\u53e3\u8f7b\u8f7b\u4e00\u5439\uff0c\u4e03\u7247\u82b1\u74e3\u50cf\u88ab\u65e0\u5f62\u7684\u4e1d\u7ebf\u7275\u52a8\u7740\uff0c\u8fde\u73af\u98de\u8d77\uff0c\u8fce\u4e0a\u4e86\u6fc0\u5c04\u800c\u6765\u7684\u51e4\u51f0\u3002</p><p>\u51e0\u4e4e\u662f\u6beb\u65e0\u60ac\u5ff5\u5730\uff0c\u51e4\u51f0\u51fb\u6e83\u4e86\u7b2c\u4e00\u7247\u82b1\u74e3\uff0c\u70c8\u7130\u5c06\u5b83\u77ac\u95f4\u711a\u70e7\u6b86\u5c3d\uff0c\u7136\u540e\u662f\u7b2c\u4e8c\u7247\u3001\u7b2c\u4e09\u7247\u3001\u7b2c\u56db\u7247\u3001\u7b2c\u4e94\u7247\uff0c\u5728\u51fb\u7a7f\u7b2c\u516d\u7247\u82b1\u74e3\u65f6\u7ec8\u4e8e\u7a0d\u7a0d\u505c\u6ede\u4e86\u4e00\u77ac\u95f4\u3002\u5965\u5609\u83b1\u65af\u4f4e\u58f0\u5ff5\u4e86\u4e2a\u8bcd\uff0c\u6700\u540e\u4e00\u7247\u82b1\u74e3\u4e0a\u9661\u7136\u5149\u8292\u56db\u5c04\uff0c\u65e0\u8fb9\u65e0\u9645\u5730\u6269\u5c55\u5f00\u6765\uff0c\u5316\u4f5c\u539a\u91cd\u7684\u57ce\u5899\u3002\u51e4\u51f0\u5468\u8eab\u70c8\u7130\u731b\u6da8\uff0c\u53e3\u4e2d\u53d1\u51fa\u4e00\u9053\u65e0\u5f62\u7684\u97f3\u6ce2\uff0c\u5c06\u57ce\u5899\u9707\u5f97\u7c89\u788e\u3002</p><p>\u8d81\u7740\u8fd9\u4e2a\u95f4\u9699\uff0c\u5965\u5609\u83b1\u65af\u8fdc\u8fdc\u9000\u5f00\uff0c\u5979\u62ac\u8d77\u53f3\u624b\uff0c\u4e00\u679a\u56db\u56db\u65b9\u65b9\u7684\u767d\u7389\u5370\u73ba\u5728\u638c\u5fc3\u6d6e\u73b0\uff0c\u900f\u660e\u7684\u8d64\u7ea2\u5149\u7130\u8426\u7ed5\u5176\u4e0a\uff0c\u8d6b\u7136\u53c8\u4fbf\u662f\u4e00\u5934\u5c0f\u5c0f\u7684\u51e4\u51f0\u3002\u201c\u771f\u662f\u7684\uff0c\u4e00\u4e0d\u7559\u795e\u5c31\u66b4\u8d70\u4e86\uff0c\u4e3a\u4eba\u7236\u6bcd\u771f\u662f\u4e0d\u5bb9\u6613\uff0c\u201d\u5927\u5965\u672f\u5e08\u65e0\u5948\u5730\u53f9\u7740\u6c14\uff0c\u201c\u5e78\u597d\u4e0a\u6b21\u9047\u5230\u5361\u5c14\uff0c\u62ff\u5230\u4e86\u8fd9\u4e1c\u897f\uff0c\u5426\u5219\u8fd8\u771f\u9ebb\u70e6\u4e86\u3002\u201d</p><p>\u5979\u62ac\u624b\u4e00\u6307\uff0c\u5370\u73ba\u98de\u5c04\u800c\u51fa\uff0c\u8fce\u9762\u649e\u5165\u51e4\u51f0\u4f53\u5185\u3002\u4eff\u4f5b\u65f6\u95f4\u7a81\u7136\u51dd\u56fa\uff0c\u539f\u672c\u6b63\u9ad8\u901f\u51b2\u6765\u7684\u51e4\u51f0\u88ab\u201c\u5b9a\u683c\u201d\u5728\u7a7a\u4e2d\uff0c\u817e\u817e\u7130\u5149\u5feb\u901f\u5730\u9eef\u6de1\u4e0b\u6765\uff0c\u73b0\u51fa\u73ca\u5609\u7684\u8eab\u5f62\u3002\u5979\u53cc\u76ee\u7d27\u95ed\uff0c\u4e0d\u8a00\u4e0d\u52a8\uff0c\u4e00\u56e2\u7d2b\u8272\u706b\u7130\u5728\u7709\u5fc3\u8df3\u8dc3\u95ea\u70c1\u7740\uff0c\u53d8\u5e7b\u6210\u5404\u79cd\u51e4\u51f0\u5f62\u6001\uff0c\u6700\u7ec8\u878d\u5165\u8eab\u4f53\uff0c\u6d88\u5931\u4e0d\u89c1\u3002\u5965\u5609\u83b1\u65af\u8212\u4e86\u53e3\u6c14\uff0c\u56de\u5934\u518d\u770b\uff0c\u53d1\u73b0\u6885\u83f2\u65af\u5df2\u7ecf\u548c\u743c\u6069\u6c47\u5408\u5230\u4e00\u5904\u3002\u201c\u7b97\u4e86\u3002\u201d\u5979\u6447\u4e86\u6447\u5934\uff0c\u4f38\u624b\u62c9\u4f4f\u73ca\u5609\uff0c\u56de\u5230\u8679\u5149\u7403\u4e2d\uff0c\u7136\u540e\u7f13\u7f13\u964d\u843d\u3002</p><p>\u5bf9\u8fd9\u8fb9\u53d1\u751f\u7684\u4e00\u5207\uff0c\u743c\u6069\u548c\u6885\u83f2\u65af\u4e00\u65e0\u6240\u77e5\uff0c\u4ed6\u4eec\u9a91\u7740\u72ec\u89d2\u517d\u671d\u5730\u9762\u98de\u53bb\uff0c\u6253\u7b97\u63f4\u52a9\u6b63\u88ab\u56f4\u653b\u7684\u4eba\u4eec\u3002\u968f\u7740\u82cd\u767d\u9886\u4e3b\u88ab\u65a9\u6740\uff0c\u4ea1\u7075\u4e00\u65b9\u4e27\u5931\u4e86\u9886\u8896\u548c\u540e\u7eed\u90e8\u961f\uff0c\u539f\u672c\u5c31\u662f\u8d25\u5c40\u5df2\u5b9a\uff0c\u521a\u624d\u8f89\u9633\u62a4\u7b26\u5927\u5c55\u795e\u5a01\uff0c\u5723\u5149\u666e\u7167\u5927\u5730\uff0c\u53c8\u6467\u6bc1\u4e86\u56db\u5206\u4e4b\u4e09\u4ee5\u4e0a\u7684\u50f5\u5c38\u9ab7\u9ac5\uff0c\u53ea\u5269\u4e0b\u4e00\u4e9b\u6b8b\u5175\u6e38\u52c7\uff0c\u5728\u9b54\u9b42\u5c38\u7684\u5e26\u9886\u4e0b\u5404\u81ea\u4e3a\u6218\uff0c\u751a\u81f3\u56db\u5904\u9003\u7a9c\uff0c\u5df2\u7ecf\u6784\u4e0d\u6210\u4ec0\u4e48\u5a01\u80c1\u3002\u5f53\u592a\u9633\u5feb\u8981\u843d\u5c71\u65f6\uff0c\u8fd9\u573a\u751f\u8005\u4e0e\u6b7b\u8005\u7684\u6218\u6597\u7ec8\u4e8e\u7ed3\u675f\u4e86\u3002</p><p>\u548c\u743c\u6069\u6599\u60f3\u5f97\u4e00\u6837\uff0c\u8fd9\u4e9b\u88ab\u4ea1\u7075\u653b\u51fb\u7684\u4eba\u786e\u5b9e\u662f\u63d0\u5c14\u6559\u4f1a\u7684\u795e\u804c\u4eba\u5458\uff0c\u5305\u62ec\u4e5d\u540d\u7267\u5e08\u3001\u4e8c\u5341\u56db\u540d\u795e\u6bbf\u536b\u5175\uff0c\u8fd8\u6709\u4e00\u540d\u5723\u6b66\u58eb\u3002\u539f\u6765\u524d\u4e9b\u65e5\u5b50\uff0c\u63d0\u5c14\u6559\u4f1a\u6df1\u6c34\u57ce\u6559\u533a\u7684\u5927\u4e3b\u6559\u6765\u5854\u745f\u8c37\u62dc\u8bbf\uff0c\u524d\u65e5\u8d77\u7a0b\u524d\u5f80\u56e0\u5e03\u56fe\uff0c\u8fd9\u4e9b\u4eba\u4fbf\u662f\u672c\u5730\u6559\u4f1a\u6d3e\u51fa\u7684\u62a4\u9001\u4eba\u5458\uff0c\u6ca1\u60f3\u5230\u5728\u8fd4\u56de\u9014\u4e2d\u906d\u9047\u4ea1\u7075\u88ad\u51fb\uff0c\u7ed3\u679c\u5c31\u5f04\u6210\u4e86\u8fd9\u526f\u6837\u5b50\u3002</p><p>\u201c\u8fd9\u91cc\u65e2\u7136\u66fe\u7ecf\u662f\u57cb\u9aa8\u65e0\u6570\u7684\u53e4\u6218\u573a\uff0c\u4ea1\u7075\u5e08\u6765\u8fd9\u91cc\u505a\u4ec0\u4e48\uff0c\u5012\u4e5f\u5f88\u6b63\u5e38\u4e86\uff0c\u201d\u743c\u6069\u95ee\u4e00\u540d\u795e\u6bbf\u536b\u5175\uff0c\u201c\u4f46\u662f\u90a3\u4e2a\u5149\u5934\u7267\u5e08\u53c8\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d</p><p>\u201c\u5149\u5934\u7267\u5e08\uff1f\u201d\u795e\u6bbf\u536b\u5175\u4e0d\u89e3\u3002</p><p>\u201c\u5c31\u662f\u5728\u5929\u4e0a\u653e\u96f7\u7535\u7684\u90a3\u5bb6\u4f19\u4e86\uff0c\u540d\u53eb\u5361\u65af\u56fe\u7684\u2014\u2014\u5bf9\u4e86\uff0c\u201d\u743c\u6069\u62ac\u5934\u5f80\u5929\u4e0a\u770b\u53bb\uff0c\u201c\u5947\u602a\uff0c\u90a3\u5bb6\u4f19\u600e\u4e48\u4e0d\u89c1\u4e86\uff1f\u9003\u4e86\uff1f\u201d</p><p>\u81ea\u4ece\u548c\u82cd\u767d\u9886\u4e3b\u4ea4\u4e0a\u624b\uff0c\u63a5\u7740\u53c8\u662f\u9aa8\u9f99\uff0c\u743c\u6069\u538b\u6839\u65e0\u6687\u5173\u6ce8\u5176\u4ed6\uff0c\u76f4\u5230\u6b64\u65f6\u624d\u731b\u7136\u60f3\u8d77\u90a3\u4e2a\u5149\u5934\u7267\u5e08\u6765\u3002\u6b63\u5f85\u5bfb\u627e\uff0c\u4e00\u4e2a\u719f\u6089\u7684\u58f0\u97f3\u4ece\u80cc\u540e\u4f20\u6765\uff0c\u201c\u4ed6\u5728\u8fd9\u91cc\u3002\u201d</p><p>\u743c\u6069\u56de\u8fc7\u8eab\uff0c\u53d1\u73b0\u662f\u838e\u73de\u514b\uff0c\u5de6\u624b\u63d0\u7740\u77ed\u5251\uff0c\u53f3\u624b\u4e0a\u5219\u62ce\u7740\u4e00\u4e2a\u4eba\u5934\uff0c\u8116\u9888\u65ad\u53e3\u5904\u8fd8\u5728\u6ef4\u7b54\u6d41\u8840\uff0c\u6b63\u662f\u743c\u6069\u521a\u521a\u63d0\u53ca\u7684\u5361\u65af\u56fe\u3002\u5979\u8138\u8272\u6709\u4e9b\u82cd\u767d\uff0c\u8eab\u4e0a\u7684\u6076\u9b54\u76ae\u7532\u5df2\u7ecf\u5b8c\u5168\u7834\u635f\uff0c\u8d64\u88f8\u7684\u53f3\u81c2\u4e0a\u6709\u4e24\u9053\u6df1\u6df1\u8840\u75d5\uff0c\u663e\u7136\u521a\u521a\u7ecf\u5386\u4e86\u4e00\u573a\u6076\u6218\u3002\u795e\u6bbf\u536b\u5175\u4e00\u5f00\u59cb\u4ee5\u4e3a\u838e\u73de\u514b\u662f\u540d\u4eba\u7c7b\u5973\u5b50\uff0c\u5e76\u672a\u5728\u610f\uff0c\u4f46\u7d27\u63a5\u7740\u4fbf\u53cd\u5e94\u8fc7\u6765\u3002</p><p>\u201c\u6076\u9b54\uff01\u201d</p><p>\u4ed6\u4e0b\u610f\u8bc6\u5730\u62d4\u5251\u5c31\u8981\u653b\u51fb\uff0c\u4f46\u88ab\u6885\u83f2\u65af\u53ca\u65f6\u5236\u6b62\u4e86\u3002\u201c\u662f\u6211\u7684\u719f\u4eba\uff0c\u201d\u5979\u6de1\u6de1\u8bf4\uff0c\u201c\u4e0d\u7528\u5927\u60ca\u5c0f\u602a\u3002\u201d</p><p>\u201c\u554a\uff1f\u53ef\u662f\u2026\u2026\u5979\u662f\u4e2a\u6076\u9b54\uff01\u201d</p><p>\u201c\u662f\u554a\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u795e\u6bbf\u536b\u5175\u77a0\u76ee\u7ed3\u820c\uff0c\u4e0d\u77e5\u9053\u8be5\u5982\u4f55\u628a\u5bf9\u8bdd\u7ee7\u7eed\u8fdb\u884c\u4e0b\u53bb\u3002\u6885\u83f2\u65af\u4e0d\u53bb\u7406\u4ed6\uff0c\u8f6c\u8fc7\u8138\u671d\u838e\u73de\u514b\u5fae\u5fae\u4e00\u7b11\uff0c\u70b9\u5934\u793a\u610f\uff0c\u201c\u4e0b\u5348\u597d\uff0c\u838e\u73de\u514b\u3002\u201d</p><p>\u201c\u9a6c\u9a6c\u864e\u864e\u5427\uff0c\u201d\u9b45\u9b54\u8038\u8038\u80a9\uff0c\u7528\u4e00\u79cd\u6f2b\u4e0d\u7ecf\u5fc3\u7684\u53e3\u6c14\u56de\u7b54\uff0c\u201c\u5012\u662f\u4f60\u8d8a\u6765\u8d8a\u5389\u5bb3\u4e86\u554a\uff0c\u827e\u5f25\u8587\uff0c\u201d\u5979\u4e0d\u65e0\u5ac9\u5992\u5730\u8bf4\uff0c\u201c\u90a3\u4e00\u4e0b\u77ac\u79fb\uff0c\u5e94\u8be5\u5c31\u662f\u6240\u8c13\u7684\u2018\u51a5\u6b65\u2019\u5427\u3002\u201d</p><p>\u201c\u55ef\uff0c\u51a5\u6b65\uff0c\u201d\u6885\u83f2\u65af\u56de\u7b54\uff0c\u770b\u7740\u5728\u5730\u4e0a\u6eda\u52a8\u7684\u4eba\u5934\uff0c\u201c\u4f60\u4e5f\u5f88\u5f3a\u554a\uff0c\u5c45\u7136\u80fd\u6740\u6389\u2018\u98ce\u773c\u2019\u3002\u201d</p><p>\u201c\u5475\u3002\u201d</p><p>\u838e\u73de\u514b\u51b7\u7b11\u4e00\u58f0\uff0c\u6ca1\u6709\u63a5\u8bdd\u3002\u743c\u6069\u5728\u65c1\u8fb9\u542c\u89c1\u5bf9\u8bdd\uff0c\u6709\u4e9b\u5947\u602a\u5979\u4eec\u6240\u8bf4\u7684\u201c\u51a5\u6b65\u201d\u662f\u4ec0\u4e48\uff0c\u4f46\u4e5f\u6ca1\u6709\u95ee\uff0c\u8fd9\u91cc\u4eba\u591a\u53e3\u6742\uff0c\u4e0d\u9002\u5408\u8bf4\u8bdd\uff0c\u4ee5\u540e\u6709\u65f6\u95f4\u518d\u5355\u72ec\u95ee\u6885\u83f2\u65af\u5c31\u662f\u3002\u6bd4\u8d77\u8fd9\u4e2a\uff0c\u4ed6\u6709\u66f4\u5173\u5fc3\u7684\u95ee\u9898\uff0c\u201c\u4f60\u600e\u4e48\u5728\u8fd9\u91cc\uff0c\u73ca\u5609\u5462\uff1f\u201d</p><p>\u838e\u73de\u514b\u53cd\u624b\u6307\u4e86\u6307\uff0c\u201c\u5728\u540e\u9762\u3002\u201d</p><p>\u201c\u554a\uff0c\u539f\u6765\u73ca\u5609\u59d0\u59d0\u4e5f\u6765\u4e86\u5417\uff0c\u201d\u6885\u83f2\u65af\u9732\u51fa\u65e0\u6bd4\u707f\u70c2\u7684\u7f8e\u4e3d\u7b11\u5bb9\uff0c\u201c\u597d\u4e45\u6ca1\u89c1\u4e86\uff0c\u6211\u5f88\u60f3\u5ff5\u5979\u5462\u3002\u201d</p><p>\u201c\u5443\uff0c\u662f\u554a\u2026\u2026\u5bf9\u4e86\uff0c\u827e\u5f25\u8587\uff0c\u4f60\u521a\u624d\u8bf4\u8fd9\u5bb6\u4f19\u662f\u4ec0\u4e48\u2018\u98ce\u773c\u2019\uff0c\u4f60\u8ba4\u8bc6\u4ed6\u5417\uff1f\u201d</p><p>\u201c\u5f53\u7136\u8ba4\u8bc6\uff0c\u2018\u98ce\u773c\u2019\u5361\u65af\u56fe\uff0c\u5728\u6574\u4e2a\u5760\u661f\u6d77\u5730\u533a\u4e5f\u7b97\u662f\u8d6b\u8d6b\u6709\u540d\u7684\u4eba\u7269\u2014\u2014\u987a\u4fbf\u8bf4\u53e5\uff0c\u743c\u6069\uff0c\u201d\u5c11\u5973\u751c\u751c\u5fae\u7b11\uff0c\u201c\u4f60\u8f6c\u79fb\u8bdd\u9898\u7684\u6280\u672f\uff0c\u8fd8\u662f\u534a\u70b9\u90fd\u6ca1\u6709\u8fdb\u6b65\u554a\u3002\u201d</p><p>\u201c\u2026\u2026\u597d\u50cf\u662f\u5427\u3002\u201d</p><p>\u838e\u73de\u514b\u5728\u65c1\u8fb9\u5fcd\u4e0d\u51fa\u7b11\u51fa\u58f0\u6765\uff0c\u88ab\u743c\u6069\u607c\u7f9e\u6210\u6012\u5730\u72e0\u72e0\u77aa\u4e86\u4e00\u773c\uff0c\u5979\u505a\u4e86\u4e2a\u9b3c\u8138\uff0c\u7528\u5507\u8bed\u65e0\u58f0\u5730\u56de\u7b54\uff1a\u201c\u4f60\u81ea\u627e\u7684\u3002\u201d</p><p>\u5c31\u5728\u6b64\u65f6\uff0c\u4e00\u4f4d\u7a7f\u7740\u5168\u8eab\u94e0\u7532\uff0c\u5927\u7ea6\u4e09\u5341\u4f59\u5c81\u7684\u91d1\u53d1\u7537\u5b50\u8d70\u8fc7\u6765\uff0c\u4ed6\u770b\u89c1\u838e\u73de\u514b\u65f6\u6014\u4e86\u6014\uff0c\u5728\u9b45\u9b54\u624b\u4e2d\u63d0\u7740\u7684\u4eba\u5934\u4e0a\u7aef\u8be6\u4e86\u4e24\u79d2\u949f\uff0c\u7d27\u63a5\u7740\u4fbf\u82e5\u65e0\u5176\u4e8b\u3002\u6885\u83f2\u65af\u663e\u7136\u8ba4\u8bc6\u4ed6\uff0c\u5f88\u968f\u610f\u5730\u62ac\u4e86\u62ac\u624b\uff0c\u7b97\u662f\u6253\u62db\u547c\u3002\u201c\u771f\u72fc\u72c8\u554a\uff0c\u5e0c\u6b27\uff0c\u201d\u5979\u61d2\u61d2\u5730\u8bf4\uff0c\u201c\u5802\u5802\u5723\u6b66\u58eb\uff0c\u5c45\u7136\u88ab\u4e00\u7fa4\u4ea1\u7075\u903c\u8feb\u6210\u8fd9\u4e2a\u6837\u5b50\uff0c\u4f20\u51fa\u53bb\u7684\u8bdd\uff0c\u4f1a\u4e25\u91cd\u6709\u635f\u5e9e\u7f57\u5bb6\u65cf\u7684\u76db\u540d\u5427\u3002\u201d</p><p>\u201c\u5e9e\u7f57\u5bb6\u65cf\u7684\u76db\u540d\uff1f\u4e16\u754c\u4e0a\u8fd8\u6709\u8fd9\u79cd\u4e1c\u897f\u5417\uff1f\u201d\u5e0c\u6b27\u5927\u7b11\uff0c\u201c\u5c31\u7b97\u6709\uff0c\u4e5f\u65e9\u5c31\u5df2\u7ecf\u88ab\u6211\u8fd9\u4e2a\u4e0d\u6210\u5668\u7684\u5bb6\u4f19\u8d25\u574f\u6b86\u5c3d\u4e86\u5427\uff0c\u201d\u4ed6\u770b\u5411\u743c\u6069\uff0c\u70b9\u5934\u81f4\u610f\uff0c\u201c\u5982\u679c\u6211\u6ca1\u731c\u9519\u7684\u8bdd\uff0c\u8fd9\u4f4d\u4e00\u5b9a\u662f\u6765\u81ea\u9634\u9b42\u57ce\u7684\u5170\u5c3c\u65af\u7279\u5148\u751f\u4e86\u3002\u201d</p><p>\u201c\u662f\u6211\u2026\u2026\u201d</p><p>\u201c\u5170\u5c3c\u65af\u7279\u5148\u751f\u8fd9\u6b21\u6765\u5854\u745f\u8c37\uff0c\u6253\u7b97\u4f4f\u4e0a\u4e00\u6bb5\u65f6\u95f4\u5417\uff1f\u201d\u5e0c\u6b27\u6709\u4e9b\u65e0\u793c\u5730\u6253\u65ad\u4e86\u743c\u6069\u7684\u8bdd\u3002</p><p>\u201c\u5443\uff0c\u5e94\u8be5\u4f1a\u5446\u4e0a\u4e00\u9635\u5b50\u5427\u3002\u201d</p><p>\u5e0c\u6b27\u70b9\u70b9\u5934\uff0c\u53e3\u6c14\u53d8\u5f97\u975e\u5e38\u4e25\u8083\uff0c\u201c\u90a3\u4e48\uff0c\u4f5c\u4e3a\u5149\u660e\u6b63\u5927\u7684\u5723\u6b66\u58eb\uff0c\u5c3d\u7ba1\u5f88\u4e0d\u60c5\u613f\uff0c\u4f46\u5d07\u9ad8\u7684\u8363\u8a89\u548c\u9053\u5fb7\u611f\uff0c\u8ba9\u6211\u5fc5\u987b\u63d0\u9192\u9601\u4e0b\u4e00\u4ef6\u4e8b\u60c5\u3002\u201d</p><p>\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d\u743c\u6069\u4e0d\u89e3\u5730\u770b\u5411\u6885\u83f2\u65af\uff0c\u5c11\u5973\u663e\u7136\u4e5f\u6709\u4e9b\u83ab\u540d\u5176\u5999\u3002</p><p>\u201c\u5f88\u7b80\u5355\uff0c\u5c31\u662f\u4f60\u8981\u505a\u597d\u65f6\u523b\u8fce\u63a5\u6311\u6218\u7684\u51c6\u5907\uff0c\u5c31\u7b97\u662f\u8d70\u5728\u5927\u8857\u4e0a\uff0c\u4e5f\u4e00\u5b9a\u4f1a\u6709\u65e0\u6570\u4eba\u5411\u4f60\u8981\u6c42\u51b3\u6597\u7684\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u743c\u6069\u6115\u7136\uff0c\u4ed6\u4e0d\u8bb0\u5f97\u81ea\u5df1\u5728\u5854\u745f\u8c37\u6709\u8fd9\u4e48\u591a\u4ec7\u5bb6\u554a\u3002</p><p>\u201c\u56e0\u4e3a\u4f60\u6458\u8d70\u4e86\u5854\u745f\u8c37\u6700\u7f8e\u4e3d\u7684\u9c9c\u82b1\u554a\uff0c\u201d\u5e0c\u6b27\u54c8\u54c8\u5927\u7b11\uff0c\u201c\u4e0d\u77e5\u9053\u5417\uff0c\u5170\u5c3c\u65af\u7279\u5148\u751f\uff0c\u827e\u5f25\u8587\u53ef\u662f\u5168\u5854\u745f\u8c37\u4e24\u4e07\u540d\u5c11\u5e74\u7684\u68a6\u4e2d\u60c5\u4eba\u554a\u3002\u201d</p><p>\u201c\u80e1\u626f\uff0c\u201d\u6885\u83f2\u65af\u53cd\u9a73\uff0c\u201c\u5168\u5854\u745f\u8c37\u4e5f\u624d\u4e09\u4e07\u4eba\u4e0d\u5230\uff0c\u54ea\u6765\u7684\u4e24\u4e07\u540d\u5c11\u5e74\uff1f\u201d</p><p>\u201c\u53ea\u8981\u50cf\u6211\u4e00\u6837\uff0c\u6c38\u8fdc\u4fdd\u6301\u7740\u4e00\u9897\u5145\u6ee1\u7740\u7231\u548c\u6fc0\u60c5\u7684\u5fc3\u7075\uff0c\u90a3\u5c31\u662f\u5c11\u5e74\uff0c\u548c\u5e74\u9f84\u4ec0\u4e48\u7684\u6ca1\u6709\u5173\u7cfb\uff0c\u201d\u5e0c\u6b27\u4e00\u672c\u6b63\u7ecf\u5730\u56de\u7b54\uff0c\u201c\u800c\u4e14\u7537\u5973\u90fd\u5305\u62ec\u2014\u2014\u6709\u5f88\u591a\u5c11\u5973\u90fd\u5bf9\u6211\u8bf4\u5979\u4eec\u6697\u604b\u4f60\u54df\uff0c\u827e\u5f25\u8587\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u743c\u6069\u4e00\u65f6\u95f4\u90fd\u6014\u4f4f\u4e86\uff0c\u4e0d\u77e5\u9053\u8be5\u5982\u4f55\u56de\u7b54\u3002\u6885\u83f2\u65af\u4fcf\u8138\u7f9e\u7ea2\uff0c\u7709\u773c\u95f4\u5374\u662f\u63a9\u4e0d\u4f4f\u7684\u7b11\u610f\uff0c\u201c\u53ea\u4f1a\u8bf4\u4e9b\u65e0\u804a\u7684\u5e9f\u8bdd\u5417\uff0c\u201d\u5979\u6c14\u52bf\u6c79\u6c79\u5730\u53cd\u95ee\uff0c\u201c\u4f60\u7684\u5fa1\u5929\u4f7f\u5462\uff0c\u600e\u4e48\u4e0d\u53ec\u5524\u4ed6\uff1f\u5426\u5219\u5bf9\u4ed8\u8fd9\u4e9b\u4ea1\u7075\u8f7b\u800c\u6613\u4e3e\u5427\u3002\u201d</p><p>\u201c\u4f60\u6765\u665a\u4e86\u5427\uff0c\u201d\u5e0c\u6b27\u8038\u8038\u80a9\uff0c\u201c\u6211\u4e00\u5f00\u59cb\u5c31\u53ec\u5524\u4e86\u4ed6\u554a\uff0c\u8c01\u6599\u5230\u5bf9\u65b9\u8fd8\u6709\u2018\u98ce\u773c\u2019\u8fd9\u4e2a\u76df\u53cb\uff0c\u7528\u5343\u96f7\u4e07\u9706\u628a\u4ed6\u6253\u56de\u53bb\u4e86\u3002\u201d</p><p>\u201c\u8fd9\u6837\u554a\u3002\u201d</p><p>\u6885\u83f2\u65af\u5c94\u5f00\u8bdd\u9898\uff0c\u968f\u5373\u5411\u743c\u6069\u505a\u4ecb\u7ecd\u3002\u539f\u6765\u8fd9\u4f4d\u91d1\u53d1\u7537\u5b50\u540d\u53eb\u5e0c\u6b27\uff0c\u662f\u5e9e\u7f57\u5bb6\u65cf\u7684\u957f\u5b50\uff0c\u548c\u6885\u83f2\u65af\u4e00\u6837\u4e5f\u662f\u5723\u6b66\u58eb\uff0c\u662f\u8fd9\u961f\u63d0\u5c14\u6559\u4f1a\u4eba\u5458\u7684\u9886\u8896\u3002\u56e0\u4e3a\u5929\u8272\u5df2\u665a\uff0c\u52a0\u4e0a\u6709\u591a\u540d\u90e8\u5c5e\u53d7\u4f24\uff0c\u5728\u611f\u8c22\u4e86\u743c\u6069\u7684\u63f4\u624b\u4e4b\u540e\uff0c\u5e0c\u6b27\u7387\u961f\u5148\u884c\u542f\u7a0b\uff0c\u8fd4\u56de\u603b\u90e8\u3002\u800c\u6885\u83f2\u65af\u5219\u7559\u4e0b\u6765\uff0c\u548c\u743c\u6069\u3001\u838e\u73de\u514b\u4e00\u8d77\u5f80\u56de\u8d70\uff0c\u53bb\u63a5\u73ca\u5609\u3002</p><p>\u201c\u5443\uff0c\u6211\u8bf4\uff0c\u838e\u73de\u514b\uff0c\u628a\u8fd9\u4eba\u5934\u6254\u4e86\u5427\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4e00\u76f4\u63d0\u7740\u5b83\uff0c\u592a\u2026\u2026\u8ba9\u4eba\u4e0d\u8212\u670d\u4e86\u3002\u201d</p><p>\u62ce\u7740\u8840\u6dcb\u6dcb\u7684\u4eba\u5934\u8d70\u8def\uff0c\u786e\u5b9e\u662f\u5f88\u8be1\u5f02\uff0c\u4f46\u6885\u83f2\u65af\u5374\u53cd\u5bf9\u743c\u6069\u7684\u610f\u89c1\u3002\u201c\u8fd9\u9897\u4eba\u5934\u503c\u4e94\u5343\u91d1\u5e01\u5462\uff0c\u6254\u4e86\u592a\u53ef\u60dc\u4e86\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u4e0d\u51c6\u6d6a\u8d39\u3002\u201d</p><p>\u201c\u4e94\u5343\u91d1\u5e01\uff1f\u201d\u743c\u6069\u5413\u4e86\u4e00\u8df3\uff0c\u201c\u8fd9\u4e48\u503c\u94b1\uff1f\u8c01\u7ed9\uff1f\u201d</p><p>\u201c\u9001\u5230\u9634\u5f71\u8c37\uff0c\u4ea4\u7ed9\u90a3\u91cc\u7684\u7ad6\u7434\u624b\uff0c\u4f60\u5c31\u80fd\u62ff\u5230\u94b1\u4e86\uff0c\u653e\u5fc3\u4e0d\u4f1a\u88ab\u706d\u53e3\u7684\uff0c\u201d\u6885\u83f2\u65af\u7b11\u7740\uff0c\u201c\u7ad6\u7434\u624b\u540c\u76df\u867d\u7136\u884c\u4e8b\u4e71\u4e03\u516b\u7cdf\uff0c\u4f46\u57fa\u672c\u7684\u4fe1\u8a89\u8fd8\u662f\u6709\u7684\u3002\u8fd9\u5bb6\u4f19\u53ef\u662f\u5854\u6d1b\u65af\u7684\u4e09\u5927\u9009\u6c11\u4e4b\u4e00\u554a\uff0c\u600e\u4e48\u4e0d\u503c\u8fd9\u4e2a\u4ef7\u3002\u201d</p><p>\u201c\u5854\u6d1b\u65af\u7684\u4e09\u5927\u9009\u6c11\uff1f\u201d</p><p>\u201c\u662f\u554a\u3002\u201d</p><p>\u8def\u4e0a\u5de6\u53f3\u65e0\u4e8b\uff0c\u6885\u83f2\u65af\u4fbf\u7b80\u5355\u5730\u628a\u5361\u65af\u56fe\u7684\u60c5\u51b5\u8bf4\u4e86\u4e00\u4e0b\u3002\u8981\u8bba\u8d77\u6765\uff0c\u6b64\u4eba\u4e5f\u7b97\u5f97\u4e0a\u662f\u4e2a\u4f20\u5947\uff0c\u4ed6\u539f\u672c\u5e76\u4e0d\u662f\u5854\u6d1b\u65af\u7684\u7267\u5e08\uff0c\u8fde\u4fe1\u5f92\u90fd\u4e0d\u662f\u2014\u2014\u4ed6\u662f\u5760\u661f\u6d77\u4e0a\u5a01\u540d\u8d6b\u8d6b\u96c4\u9738\u4e00\u65b9\u7684\u5927\u6d77\u76d7\uff0c\u4fe1\u4ef0\u7684\u795e\u7947\u662f\u6d77\u6218\u4e4b\u795e\u7ef4\u5c14\u5bc7\u3002\u5341\u516d\u5e74\u524d\uff0c\u8bf8\u795e\u4e34\u51e1\uff0c\u7ef4\u5c14\u5bc7\u964d\u4e34\u5230\u5760\u661f\u6d77\u7684\u4e00\u5ea7\u6d77\u5c9b\u4e0a\uff0c\u5361\u65af\u56fe\u8fd8\u53bb\u671d\u89d0\u8fc7\u3002\u4e0d\u5e78\u7684\u662f\uff0c\u5f53\u65f6\u6d77\u795e\u5b89\u535a\u91cc\u4e5f\u964d\u4e34\u5230\u5760\u661f\u6d77\u4e0a\uff0c\u4e24\u4f4d\u795e\u7947\u76f8\u9047\u3001\u51b2\u7a81\u3001\u6218\u6597\uff0c\u6700\u7ec8\u4ee5\u5b89\u535a\u91cc\u7684\u83b7\u80dc\u800c\u544a\u7ec8\u3002\u7ef4\u5c14\u5bc7\u88ab\u6467\u6bc1\u4e86\uff0c\u4ed6\u6b8b\u4f59\u7684\u795e\u529b\u672c\u8d28\u9644\u5728\u5361\u65af\u56fe\u8eab\u4e0a\uff0c\u9003\u9041\u5230\u701a\u571f\u7684\u695a\u62c9\u683c\u57ce\uff0c\u7136\u540e\u5728\u4e00\u5bb6\u5993\u9662\u91cc\u906d\u9047\u4e86\u98ce\u66b4\u4e4b\u795e\u7684\u5723\u8005\u3002</p><p>\u5854\u6d1b\u65af\u541e\u566c\u4e86\u7ef4\u5c14\u5bc7\u7684\u6b8b\u4f59\u672c\u8d28\uff0c\u5374\u51fa\u4eba\u610f\u6599\u5730\u7559\u4e86\u5361\u65af\u56fe\u4e00\u547d\uff0c\u4e0d\u4ec5\u5982\u6b64\uff0c\u4ed6\u4f3c\u4e4e\u5bf9\u8fd9\u4f4d\u6d77\u76d7\u9887\u4e3a\u6b23\u8d4f\uff0c\u62db\u63fd\u4ed6\u6210\u4e3a\u81ea\u5df1\u7684\u90e8\u5c5e\u3002\u5361\u65af\u56fe\u6539\u6362\u95e8\u5ead\u540e\uff0c\u4e00\u8def\u98de\u9ec4\u817e\u8fbe\uff0c\u77ed\u77ed\u51e0\u5e74\u4e2d\u5c31\u5347\u5230\u4e86\u5730\u533a\u4e3b\u6559\u7684\u4f4d\u7f6e\u3002\u540e\u6765\u57281368DR\u7684\u6df1\u79cb\uff0c\u5854\u6d1b\u65af\u4eb2\u81ea\u964d\u4e34\u51e1\u95f4\uff0c\u53ec\u96c6\u4fe1\u5f92\uff0c\u5f53\u4f17\u6316\u53bb\u5361\u65af\u56fe\u7684\u5de6\u773c\uff0c\u8d50\u4e88\u795e\u529b\uff0c\u5ba3\u5e03\u4ed6\u4ece\u6b64\u6210\u4e3a\u81ea\u5df1\u7684\u9009\u6c11\u3002\u5728\u6b64\u4e4b\u524d\uff0c\u5854\u6d1b\u65af\u5df2\u7ecf\u6709\u4e24\u540d\u9009\u6c11\uff0c\u5361\u65af\u56fe\u4fbf\u662f\u7b2c\u4e09\u4f4d\uff0c\u53d7\u5c01\u4e3a\u201c\u98d3\u98ce\u4f7f\u201d\u3002</p><p>\u5854\u6d1b\u65af\u662f\u4e2a\u6df7\u4e71\u72c2\u66b4\u7684\u90aa\u795e\uff0c\u6559\u4f1a\u52bf\u529b\u867d\u7136\u5e9e\u5927\uff0c\u7ec4\u7ec7\u5374\u975e\u5e38\u6563\u3002\u4e0d\u540c\u5730\u533a\u7684\u6559\u4f1a\uff0c\u9664\u4e86\u90fd\u5c0a\u5949\u540c\u4e00\u4f4d\u795e\u660e\u4e4b\u5916\uff0c\u795e\u804c\u4eba\u5458\u7684\u79f0\u53f7\u3001\u4f4d\u9636\uff0c\u795e\u6bbf\u7684\u683c\u5c40\uff0c\u796d\u7940\u7684\u4eea\u5f0f\uff0c\u751a\u81f3\u8fde\u6559\u4e49\u90fd\u5f80\u5f80\u4e0d\u540c\uff0c\u5f7c\u6b64\u95f4\u4e5f\u57fa\u672c\u6ca1\u4ec0\u4e48\u4e0a\u4e0b\u96b6\u5c5e\u5173\u7cfb\uff0c\u5404\u81ea\u4e3a\u653f\u3002\u5361\u65af\u56fe\u6210\u4e3a\u9009\u6c11\u4e4b\u540e\uff0c\u81ea\u53f7\u201c\u98ce\u773c\u201d\uff0c\u5f00\u59cb\u5bf9\u6559\u4f1a\u8fdb\u884c\u5927\u529b\u6574\u987f\uff0c\u9887\u6709\u6210\u6548\u3002\u5728\u4ed6\u7684\u7387\u9886\u4e0b\uff0c\u5854\u6d1b\u65af\u6559\u4f1a\u4e00\u53cd\u6b64\u524d\u7684\u56a3\u5f20\u4f5c\u98ce\uff0c\u53d8\u5f97\u66f4\u4f4e\u8c03\uff0c\u66f4\u7406\u6027\uff0c\u4e5f\u66f4\u6709\u6548\u7387\u3002\u4ece\u8868\u9762\u4e0a\u770b\uff0c\u8fd9\u51e0\u5e74\u98ce\u66b4\u4e4b\u795e\u7684\u5f71\u54cd\u529b\u4f3c\u4e4e\u5927\u5927\u8870\u9000\uff0c\u4f46\u6885\u83f2\u65af\u770b\u8fc7\u7684\u8d44\u6599\u5374\u663e\u793a\uff1a\u6574\u4e2a\u5760\u661f\u6d77\u7684\u6cbf\u5cb8\u533a\u57df\uff0c\u6709\u56db\u5206\u4e4b\u4e00\u7684\u6d77\u6e2f\u5df2\u7ecf\u843d\u5165\u5854\u6d1b\u65af\u6559\u4f1a\u7684\u79d8\u5bc6\u638c\u63e1\u4e2d\uff0c\u8fdb\u51fa\u8d38\u6613\u5b8c\u5168\u4e3a\u5176\u6240\u628a\u6301\uff0c\u5176\u4e2d\u5229\u6da6\u4e0d\u53ef\u4f30\u91cf\u3002\u7531\u6b64\u53ef\u89c1\u8fd9\u4f4d\u51fa\u8eab\u6d77\u76d7\u7684\u201c\u98ce\u773c\u201d\u5361\u65af\u56fe\u624d\u7565\u975e\u51e1\uff0c\u5854\u6d1b\u65af\u63d0\u62d4\u4ed6\u5f53\u9009\u6c11\uff0c\u4e5f\u786e\u5b9e\u662f\u6709\u8bc6\u4eba\u4e4b\u660e\u3002\u4ece\u8fd9\u70b9\u6765\u8bf4\uff0c\u7ad6\u7434\u624b\u540c\u76df\u5f00\u51fa\u4e94\u5343\u91d1\u5e01\u7684\u60ac\u8d4f\uff0c\u534a\u70b9\u90fd\u4e0d\u9ad8\u3002</p><p>\u201c\u5514\uff0c\u770b\u6765\u53c8\u7ed3\u4e0b\u4e00\u4e2a\u4ec7\u654c\u4e86\u3002\u201d</p><p>\u5361\u65af\u56fe\u65e2\u7136\u6709\u5982\u6b64\u6df1\u539a\u80cc\u666f\uff0c\u5982\u4eca\u4ed6\u88ab\u6740\u4e86\uff0c\u5854\u6d1b\u65af\u6559\u4f1a\u5c82\u4f1a\u5c31\u6b64\u5584\u7f62\u7518\u4f11\u3002\u98ce\u66b4\u4e4b\u795e\u662f\u9ad8\u7b49\u795e\u4e4b\u4e00\uff0c\u5982\u679c\u53ef\u4ee5\u7684\u8bdd\uff0c\u743c\u6069\u5b9e\u5728\u4e0d\u60f3\u60f9\u4e0a\u8fd9\u79cd\u5f3a\u654c\u3002\u4e0d\u8fc7\u65e2\u7136\u4e8b\u5df2\u81f3\u6b64\uff0c\u4e5f\u5c31\u65e0\u6240\u8c13\u4e86\uff0c\u987a\u5176\u81ea\u7136\u5427\u3002</p><p>\u8d70\u4e86\u5927\u7ea6\u4e8c\u5341\u5206\u949f\uff0c\u743c\u6069\u770b\u89c1\u4e86\u505c\u5728\u9053\u8def\u65c1\u8fb9\u7684\u4e00\u8f86\u9a6c\u8f66\u3002\u4e24\u5339\u62c9\u8f66\u7684\u9a6c\u901a\u4f53\u7389\u767d\uff0c\u5934\u9ad8\u817f\u957f\uff0c\u770b\u8d77\u6765\u795e\u4fca\u65e0\u6bd4\uff0c\u9a7e\u8f95\u7684\u8f66\u592b\u5219\u4f4e\u7740\u5934\uff0c\u9762\u5bb9\u6728\u7136\uff0c\u7a7f\u7740\u4e00\u8eab\u7070\u6251\u6251\u7684\u8863\u670d\u6beb\u4e0d\u8d77\u773c\uff0c\u4f46\u743c\u6069\u77e5\u9053\u5b83\u4eec\u5176\u5b9e\u90fd\u4e0d\u662f\u6d3b\u7269\uff0c\u800c\u662f\u9b54\u50cf\uff0c\u7c7b\u4f3c\u4ed6\u7684\u8428\u74e6\u68cb\u3002\u743c\u6069\u8d70\u8fd1\uff0c\u6b63\u8981\u8bf4\u8bdd\uff0c\u65c1\u8fb9\u7684\u6885\u83f2\u65af\u7a81\u7136\u54a6\u4e86\u4e00\u58f0\uff0c\u6302\u5728\u5979\u80f8\u53e3\u7684\u8f89\u9633\u62a4\u7b26\u81ea\u884c\u9707\u52a8\u8d77\u6765\uff0c\u53d1\u51fa\u523a\u773c\u5149\u8292\u3002</p><p>\u201c\u8f66\u53a2\u91cc\u6709\u4ea1\u7075\uff01\u201d</p><p>\u5c11\u5973\u8138\u8272\u5fae\u53d8\uff0c\u4f38\u624b\u62d4\u51fa\u94f6\u5251\uff0c\u5c31\u8981\u653b\u8fdb\u53bb\u3002\u743c\u6069\u5148\u662f\u4e00\u6014\uff0c\u968f\u5373\u53cd\u5e94\u8fc7\u6765\uff0c\u8fde\u5fd9\u5c06\u5979\u62c9\u4f4f\u3002\u201c\u6ca1\u4e8b\uff0c\u201d\u4ed6\u89e3\u91ca\uff0c\u201c\u662f\u4f4d\u5e7d\u7075\u5927\u5965\u672f\u5e08\u2026\u2026\u201d</p><p>\u8bdd\u97f3\u672a\u843d\uff0c\u5e7d\u7075\u5927\u5965\u672f\u5e08\u5df2\u7ecf\u81ea\u884c\u51fa\u73b0\u5728\u9762\u524d\uff0c\u5979\u9762\u65e0\u8868\u60c5\u5730\u770b\u4e86\u743c\u6069\u4e00\u773c\uff0c\u968f\u5373\u5c06\u76ee\u5149\u79fb\u5230\u6885\u83f2\u65af\u8138\u4e0a\u3002\u201c\u5965\u5609\u83b1\u65af\u5973\u58eb\uff0c\u201d\u743c\u6069\u5411\u6885\u83f2\u65af\u4ecb\u7ecd\uff0c\u201c\u73ca\u5609\u7684\u8001\u5e08\u3002\u8fd9\u4f4d\u662f\u2026\u2026\u201d</p><p>\u201c\u827e\u5f25\u8587\xb7\u6885\u83f2\u65af\uff0c\u201d\u6e05\u695a\u611f\u53d7\u5230\u5bf9\u65b9\u7684\u654c\u610f\uff0c\u5c11\u5973\u67d4\u7f8e\u5730\u5fae\u7b11\u7740\uff0c\u975e\u5e38\u81ea\u7136\u5730\u4f38\u624b\u633d\u4f4f\u743c\u6069\u7684\u80f3\u818a\uff0c\u548c\u4ed6\u5e76\u80a9\u7ad9\u5728\u4e00\u8d77\uff0c\u201c\u8ba4\u8bc6\u60a8\u5f88\u9ad8\u5174\u3002\u201d</p></article>"}}}]);
