(window.webpackJsonp=window.webpackJsonp||[]).push([[484],{507:function(p){p.exports={chapterTitle:null,title:"\u7b2c347\u7ae0 \u540e\u987e\u4e4b\u5fe7",content:"<article><h2>\u7b2c347\u7ae0 \u540e\u987e\u4e4b\u5fe7</h2><p>\u3000\u3000\u90ed\u5efa\u5f53\u7136\u77e5\u9053\u9676\u884c\u5065\u5230\u60e0\u5c71\u4e0d\u6b62\u53d9\u65e7\u8fd9\u4e48\u7b80\u5355\uff0c\u5728\u9676\u884c\u5065\u627e\u4ed6\u53d9\u65e7\u7684\u7b2c\u4e8c\u5929\uff0c\u6781\u5c11\u5728\u9999\u96ea\u6d77\u9732\u9762\u3001\u5374\u53c8\u662f\u4e2d\u65b9\u8c08\u5224\u7ec4\u7ec4\u957f\u7684\u5f20\u94c1\u68ee\u51fa\u73b0\u5728\u5382\u91cc\uff0c\u627e\u5230\u4ed6\u4eec\u51e0\u4e2a\u5382\u91cc\u7684\u8c08\u5224\u7ec4\u7684\u6210\u5458\u5f00\u4f1a\uff0c\u4e1c\u626f\u897f\u626f\uff0c\u6700\u7ec8\u8fd8\u662f\u626f\u5230\u9676\u884c\u5065\u7684\u5934\u4e0a\uff0c\u5f20\u94c1\u68ee\u5728\u4f1a\u8bae\u4e0a\u5927\u653e\u5282\u8bcd\uff1a\u201c\u8c01\u8981\u505a\u7b2c\u4e8c\u4e2a\u9676\u884c\u5065\uff0c\u522b\u602a\u6211\u4e0d\u5ba2\u6c14\uff0c\u90a3\u65f6\u5c31\u4e0d\u662f\u8bf7\u4ed6\u79bb\u5f00\u60e0\u5c71\u8fd9\u4e48\u7b80\u5355\u4e86\u3002\u4e1c\u4e61\u7acb\u6c11\u670d\u88c5\u5382\u7684\u8001\u677f\uff0c\u5927\u5bb6\u90fd\u542c\u8bf4\u8fc7\u5427\uff0c\u56e0\u4e3a\u670d\u88c5\u6269\u5efa\u672a\u7ecf\u5ba1\u6279\uff0c\u5c41\u5927\u7684\u4e8b\uff0c\u7ed9\u6293\u8d77\u6765\u5224\u4e86\u5341\u5e74\uff0c\u80cc\u540e\u7684\u6545\u4e8b\u5c31\u4e0d\u53bb\u8bf4\u4ed6\uff0c\u8bf4\u8fd9\u4e2a\u4f8b\u5b50\uff0c\u5c31\u662f\u8981\u544a\u8bc9\u4f60\u4eec\uff0c\u6cd5\u5f8b\u8981\u662f\u8ba4\u771f\u8d77\u6765\uff0c\u5c31\u4f1a\u53eb\u4f60\u4eec\u5bb3\u6015\uff0c\u8fd8\u6709\u4ec0\u4e48\u884c\u4e3a\u6bd4\u7834\u574f\u62db\u5546\u5f15\u8d44\u66f4\u6076\u52a3\uff1f\u201d</p><p>\u3000\u3000\u9676\u884c\u5065\u6ca1\u6709\u8ddf\u90ed\u5efa\u660e\u8bf4\u4ed6\u4eec\u5728\u6885\u6797\u8d4f\u6885\u65f6\u7ed9\u5f20\u94c1\u68ee\u770b\u5230\uff1b\u5f20\u94c1\u68ee\u5f53\u7136\u4e0d\u4f1a\u8fd9\u65f6\u5019\u65e0\u7f18\u65e0\u6545\u7684\u63d0\u8d77\u9676\u884c\u5065\u6765\uff0c\u8fd8\u8bf4\u8fd9\u4e48\u91cd\u7684\u8bdd\uff0c\u90ed\u5efa\u80fd\u80af\u5b9a\u5f20\u94c1\u68ee\u77e5\u9053\u9676\u884c\u5065\u5230\u60e0\u5c71\u6765\u4e86\uff0c\u8fd8\u77e5\u9053\u9676\u884c\u5065\u5230\u60e0\u5c71\u6765\u7684\u76ee\u7684\u4e0d\u7b80\u5355\uff0c\u4e0d\u7136\u4ed6\u4e0d\u4f1a\u8fd9\u4e48\u6c14\u6025\u8d25\u574f\u3002</p><p>\u3000\u3000\u90ed\u5efa\u56de\u53bb\u540e\u67e5\u4e86\u4e00\u4e9b\u5609\u4fe1\u5b9e\u4e1a\u7684\u8d44\u6599\uff0c\u624d\u77e5\u9053\u5f20\u94c1\u68ee\u4e3a\u4ec0\u4e48\u5982\u6b64\u6c14\u6025\u8d25\u574f\uff0c\u4ed6\u662f\u60e0\u5c71\u7684\u5730\u5934\u86c7\uff0c\u4e00\u4e2a\u8ba9\u4ed6\u6068\u4e4b\u5165\u9aa8\u7684\u4eba\u56de\u5230\u60e0\u5c71\uff0c\u4ed6\u5374\u4e0d\u80fd\u5948\u5176\u4f55\uff0c\u5982\u4f55\u53eb\u4ed6\u4e0d\u6c14\u6025\u8d25\u574f\uff1f</p><p>\u3000\u3000\u80fd\u67e5\u5230\u7684\u8d44\u6599\uff0c\u5609\u4fe1\u5b9e\u4e1a\u662f\u9999\u6e2f\u845b\u5bb6\u5609\u4fe1\u96c6\u56e2\u540d\u4e0b\u7684\u4e0a\u5e02\u516c\u53f8\u4e4b\u4e00\uff0c\u4e5f\u662f\u845b\u5bb6\u65d7\u4e0b\u4e1a\u7ee9\u6700\u5dee\u5f3a\u4eba\u610f\u7684\u4e0a\u5e02\u516c\u53f8\uff0c\u5728\u83f2\u5f8b\u5bbe\u3001\u8d8a\u5357\u4ee5\u53ca\u5185\u5730\u90fd\u6709\u5de5\u5382\uff0c\u80a1\u4ef7\u957f\u671f\u5728\u4f4e\u4f4d\u5f98\u5f8a\uff0c\u5012\u662f\u4e09\u6708\u521d\u7684\u4e00\u6b21\u5b9a\u5411\u589e\u53d1\uff0c\u8ba9\u5609\u4fe1\u5b9e\u4e1a\u5728\u9999\u6e2f\u80a1\u5e02\u6380\u8d77\u4e9b\u6ce2\u6f9c\u3002</p><p>\u3000\u3000\u5411\u8d8a\u79c0\uff08\u9999\u6e2f\uff09\u6709\u9650\u516c\u53f8\u5b9a\u5411\u589e\u53d125\uff05\u7684\u80a1\u7968\uff0c\u7528\u4e8e\u6536\u8d2d\u8d8a\u79c0\uff08\u9999\u6e2f\uff09\u65d7\u4e0b\u7684\u90e8\u5206\u4e1a\u52a1\u4e0e\u8d44\u4ea7\uff0c\u5305\u62ec\u7231\u8fbe\u7535\u5b50\u5728\u4e1c\u5357\u4e9a\u7684\u5546\u6807\u4f7f\u7528\u6743\u3001\u5f71\u789f\u673a\u5236\u9020\u6280\u672f\u53ca\u4e00\u90e8\u5206\u73b0\u91d1\uff0c\u53ea\u662f\u5f71\u789f\u673a\u624d\u5728\u5185\u5730\u5927\u70ed\uff0c\u5728\u9999\u6e2f\u7684\u5f71\u54cd\u5c0f\u4e00\u4e9b\uff0c\u5609\u4fe1\u5b9e\u4e1a\u7684\u80a1\u4ef7\u5012\u6ca1\u6709\u75af\u72c2\u7684\u4e0a\u6da8\uff0c\u4e0d\u8fc7\u8fde\u540c\u542f\u52a8\u589e\u53d1\u6848\u8c08\u5224\u7684\u65f6\u95f4\uff0c\u5609\u4fe1\u5b9e\u4e1a\u4e5f\u6709\u8fd1\u4e00\u500d\u7684\u6da8\u5e45\u3002</p><p>\u3000\u3000\u589e\u53d1\u6848\u6d89\u53ca\u7231\u8fbe\u7535\u5b50\u5728\u4e1c\u5357\u4e9a\u7684\u4e1a\u52a1\uff0c\u90ed\u5efa\u5b8c\u5168\u6ca1\u6709\u60f3\u5230\u5f20\u606a\u51fa\u793a\u540d\u7247\u4e0a\u7684\u8d8a\u79c0\uff08\u9999\u6e2f\uff09\u7adf\u7136\u4e0e\u56fd\u5185\u6b63\u5927\u70ed\u7684\u7231\u8fbe\u7535\u5b50\u6709\u5173\u8054\u3002</p><p>\u3000\u3000\u8981\u662f\u4e0d\u77e5\u9053\u5609\u4fe1\u5b9e\u4e1a\u80cc\u5730\u91cc\u7684\u5173\u7cfb\uff0c\u8981\u4e0d\u662f\u5f20\u606a\u6697\u793a\u7701\u957f\u5f90\u5b66\u5e73\u8fc7\u95ee\u4e09\u661f\u4e0e\u9999\u96ea\u6d77\u7684\u5408\u8d44\u6848\u5e76\u975e\u51fa\u4e8e\u5076\u7136\uff0c\u542c\u4e86\u5f20\u94c1\u68ee\u8d64\u88f8\u88f8\u5e26\u6709\u5a01\u80c1\u7684\u8bdd\uff0c\u90ed\u5efa\u8bf4\u4e0d\u5b9a\u4e0d\u4f1a\u518d\u8ddf\u9676\u884c\u5065\u63a5\u89e6\uff0c\u6b64\u65f6\u5374\u4e0d\u4f1a\u5c06\u5f20\u94c1\u68ee\u7684\u8bdd\u5f53\u771f\uff0c\u53ea\u662f\u8003\u8651\u5230\u5b9e\u5728\u6ca1\u6709\u5fc5\u8981\u8ddf\u5f20\u94c1\u68ee\u8d77\u51b2\u7a81\uff0c\u8fd8\u662f\u79c1\u4e0b\u91cc\u4e0e\u5f20\u606a\u4ed6\u4eec\u4fdd\u6301\u63a5\u89e6\u4e3a\u597d\u3002</p><p>\u3000\u3000\u8fde\u7eed\u56db\u4e2a\u665a\u4e0a\u90fd\u5728\u8c08\u5408\u8d44\u4e0e\u4f01\u4e1a\u7ba1\u7406\u7684\u8bdd\u9898\uff0c\u7b2c\u4e09\u5929\u66fe\u8587\u7684\u5c0f\u5b69\u53d1\u9ad8\u70ed\u5230\u533b\u9662\u6302\u6c34\u5c31\u6ca1\u6709\u53c2\u52a0\uff0c\u5176\u4ed6\u65f6\u5019\uff0c\u4e09\u4eba\u6bcf\u5929\u5382\u91cc\u4e0b\u73ed\u5c31\u76f4\u63a5\u53bb\u60e0\u5c71\u5927\u9152\u5e97\uff0c\u591c\u91cc\u5403\u8fc7\u591c\u5bb5\uff0c\u90fd\u8981\u5230\u51cc\u6668\u4e00\u4e24\u70b9\u624d\u7531\u5085\u4fca\u5f00\u8f66\u9001\u4ed6\u4eec\u56de\u5bb6\uff0c\u4e0d\u4ec5\u5f20\u606a\uff0c\u9676\u884c\u5065\u3001\u5085\u5bb6\u4fca\u5728\u4f01\u4e1a\u7ba1\u7406\u4e0a\u4e5f\u5f88\u6709\u81ea\u5df1\u7684\u89c1\u89e3\uff0c\u5927\u5bb6\u8c08\u5f97\u975e\u5e38\u6295\u673a\u3002\u7b2c\u56db\u5929\u665a\u4e0a\uff0c\u5f20\u606a\u544a\u8bc9\u90ed\u5efa\u4ed6\u4eec\u6b64\u6b21\u7684\u610f\u56fe\uff1a\u201c\u6211\u4eec\u5e0c\u671b\u5609\u4fe1\u5b9e\u4e1a\u80fd\u53d6\u66ff\u4e09\u661f\u4e0e\u9999\u96ea\u6d77\u5408\u8d44\u2026\u2026\u201d</p><p>\u3000\u3000\u5373\u4f7f\u6709\u8fd9\u6837\u7684\u731c\u6d4b\uff0c\u5f20\u606a\u8fd9\u4e48\u5feb\u3001\u8fd9\u4e48\u76f4\u622a\u4e86\u5f53\u7684\u8bf4\u51fa\u6765\uff0c\u90ed\u5efa\u4ed6\u4eec\u8fd8\u662f\u5403\u4e86\u4e00\u60ca\u3002</p><p>\u3000\u3000\u90ed\u5efa\u7591\u60d1\u7684\u8bf4\uff1a\u201c\u8981\u8c08\u5408\u8d44\uff0c\u4f3c\u4e4e\u627e\u6211\u4eec\u6ca1\u6709\u591a\u5927\u7684\u7528\u5904\uff0c\u6211\u4eec\u867d\u7136\u4e5f\u662f\u4e2d\u65b9\u8c08\u5224\u7ec4\u7684\u6210\u5458\uff0c\u4f46\u6446\u9970\u7684\u4f5c\u7528\u5c45\u591a\u3002\u201d</p><p>\u3000\u3000\u201c\u4e0d\uff0c\u201d\u5f20\u606a\u6de1\u5b9a\u4ece\u5bb9\u7684\u8bf4\u9053\uff0c\u201c\u4f60\u4eec\u662f\u80fd\u771f\u6b63\u4ee3\u8868\u9999\u96ea\u6d77\u7684\u4eba\uff0c\u53ea\u6709\u4e0e\u4f60\u4eec\u4ea4\u6d41\u4e4b\u540e\uff0c\u6211\u4eec\u624d\u80fd\u62ff\u51fa\u6700\u5927\u7b26\u5408\u5609\u4fe1\u5b9e\u4e1a\u3001\u9999\u96ea\u6d77\u53cc\u65b9\u5229\u76ca\u7684\u5408\u8d44\u65b9\u6848\u6765\uff0c\u81f3\u4e8e\u5176\u4ed6\u4e8b\u60c5\uff0c\u4e0d\u8fc7\u662f\u5c14\u865e\u6211\u8bc8\u7684\u52fe\u5f53\u800c\u5df2\u2026\u2026\u201d\u5f20\u606a\u4ece\u8bb8\u601d\u624b\u63a5\u8fc7\u4e00\u4efd\u6587\u4ef6\uff0c\u9012\u5230\u90ed\u5efa\u4ed6\u4eec\u9762\u524d\uff0c\u201c\u8fd9\u662f\u5408\u8d44\u65b9\u6848\u7684\u8349\u7a3f\uff0c\u5728\u6765\u60e0\u5c71\u4e4b\u524d\uff0c\u6211\u4eec\u65e9\u62df\u4e86\u4e00\u4efd\u65b9\u6848\uff0c\u4e0e\u4f60\u4eec\u4e00\u5e2d\u8a00\uff0c\u53d7\u76ca\u826f\u591a\uff0c\u6709\u5f88\u5927\u7684\u4fee\u6539\uff0c\u6211\u60f3\u4f60\u4eec\u66f4\u80fd\u533a\u522b\u54ea\u4e00\u79cd\u5408\u8d44\u65b9\u6848\u5bf9\u9999\u96ea\u6d77\u66f4\u6709\u5229\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u7684\u8bdd\u5012\u662f\u8ba9\u90ed\u5efa\u5f88\u52a8\u5fc3\uff0c\u80fd\u6709\u53e6\u5916\u4e00\u5bb6\u4f01\u4e1a\u8df3\u51fa\u6765\u8ddf\u4e09\u661f\u7ade\u4e89\uff0c\u7edd\u5bf9\u662f\u9999\u96ea\u6d77\u53d7\u76ca\uff0c\u4e0d\u7136\u8ddf\u4e09\u661f\u7684\u8c08\u5224\u4e5f\u4e0d\u4f1a\u50f5\u6301\u5230\u73b0\u5728\u4e00\u70b9\u90fd\u6ca1\u6709\u8fdb\u5c55\u4e86\u3002\u4f46\u662f\u4ed6\u6709\u987e\u5fcc\uff0c\u4ed6\u987e\u5fcc\u5f20\u94c1\u68ee\u8fd9\u4e2a\u4eba\u3002\u81f3\u4e8e\u80cc\u540e\u6709\u6ca1\u6709\u732b\u817b\uff0c\u4ed6\u4e0d\u77e5\u9053\uff0c\u6240\u4ee5\u4e0d\u80fd\u80e1\u4e71\u731c\u6d4b\uff0c\u4f46\u662f\u5f20\u94c1\u68ee\u5374\u662f\u6781\u529b\u652f\u6301\u4e0e\u4e09\u661f\u5408\u8d44\u7684\uff0c\u56e0\u4e3a\u4e09\u661f\u7b54\u5e94\u4ed6\u6765\u62c5\u4efb\u5408\u8d44\u5de5\u5382\u7684\u4e2d\u65b9\u603b\u7ecf\u7406\u3002</p><p>\u3000\u3000\u90ed\u5efa\u95ee\u9053\uff1a\u201c\u5609\u4fe1\u4e0e\u9999\u96ea\u6d77\u5408\u8d44\uff0c\u5e02\u91cc\u6307\u6d3e\u5f20\u94c1\u68ee\u5230\u5408\u8d44\u5de5\u5382\u62c5\u4efb\u4e2d\u65b9\u603b\u7ecf\u7406\uff0c\u4f60\u4eec\u6ca1\u6709\u610f\u89c1\uff1f\u201d</p><p>\u3000\u3000\u201c\u8981\u80fd\u5408\u8d44\u6210\u529f\uff0c\u6211\u4eec\u5e0c\u671b\u7531\u5609\u4fe1\u5b9e\u4e1a\u63a7\u80a1\uff0c\u65e2\u7136\u662f\u4ee5\u73b0\u4ee3\u4f01\u4e1a\u5236\u5ea6\u4e3a\u76ee\u6807\uff0c\u7ba1\u7406\u5c42\u90fd\u5f97\u7531\u8463\u4e8b\u4f1a\u59d4\u4efb\uff0c\u54ea\u6709\u4e2a\u522b\u80a1\u4e1c\u76f4\u63a5\u6307\u6d3e\u7ba1\u7406\u4eba\u5458\u7684\u9053\u7406\uff1f\u4ed6\u4eec\u63a8\u8350\uff0c\u5012\u662f\u53ef\u4ee5\u8ba4\u771f\u8003\u8651\uff0c\u53ea\u8981\u5f20\u94c1\u68ee\u591f\u8d44\u683c\uff0c\u80fd\u4e0e\u6211\u4eec\u7684\u9676\u603b\u5408\u62cd\uff0c\u8bf7\u4ed6\u4e5f\u672a\u5c1d\u4e0d\u53ef\u3002\u201d</p><p>\u3000\u3000\u90ed\u5efa\u770b\u4e86\u9676\u884c\u5065\u4e00\u773c\uff0c\u5fc3\u60f3\u4ed6\u8981\u80fd\u51fa\u4efb\u5408\u8d44\u540e\u9999\u96ea\u6d77\u7684\u603b\u7ecf\u7406\uff0c\u5bf9\u9999\u96ea\u6d77\u81ea\u7136\u6709\u5229\uff0c\u53ea\u662f\u5f20\u94c1\u68ee\u5fc5\u4e0d\u4f1a\u5c48\u5c45\u4ed6\u4e0b\uff0c\u5982\u6b64\u4e00\u6765\uff0c\u4e0d\u7ba1\u5f20\u94c1\u68ee\u662f\u4e0d\u662f\u8ddf\u4e09\u661f\u65e9\u6709\u52fe\u7ed3\uff0c\u4ed6\u90fd\u4e0d\u4f1a\u652f\u6301\u9999\u96ea\u6d77\u4e0e\u5609\u4fe1\u5b9e\u4e1a\u5408\u8d44\u3002\u90ed\u5efa\u72b9\u8c6b\u7684\u8bf4\uff1a\u201c\u5f20\u94c1\u68ee\u53ef\u4e0d\u662f\u4e00\u4e2a\u4f1a\u56e0\u516c\u5fd8\u79c1\u7684\u4eba\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u77e5\u9053\u90ed\u5efa\u7684\u987e\u8651\uff0c\u5609\u4fe1\u4e2d\u9014\u4ecb\u5165\uff0c\u8981\u662f\u8c08\u4e0d\u6210\uff0c\u62cd\u62cd\u5c41\u80a1\u5c31\u8d70\u4e86\uff0c\u4e00\u70b9\u540e\u60a3\u90fd\u6ca1\u6709\uff0c\u4f46\u662f\u90ed\u5efa\u4ed6\u4eec\u8fd8\u8981\u7559\u5728\u60e0\u5c71\u3001\u7559\u5728\u9999\u96ea\u6d77\u7ee7\u7eed\u8ba8\u751f\u6d3b\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u6de1\u6de1\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u80fd\u7ed9\u4f60\u4eec\u4ec0\u4e48\u627f\u8bfa\uff0c\u90a3\u5c31\u6210\u4e86\u79c1\u7acb\u534f\u8bae\u4e86\u3002\u6211\u6ca1\u6709\u89c1\u8fc7\u5f20\u94c1\u68ee\u8fd9\u4e2a\u4eba\uff0c\u5bf9\u4ed6\u4e5f\u4e0d\u4e86\u89e3\uff0c\u4f46\u662f\u6211\u53ef\u4ee5\u544a\u8bc9\u4f60\u4eec\uff0c\u4ed6\u53ea\u624b\u906e\u4e0d\u4e86\u6574\u4e2a\u5929\u3002\u201d</p><p>\u3000\u3000\u90ed\u5efa\u95ee\u9053\uff1a\u201c\u6709\u4e00\u4e2a\u95ee\u9898\u4e00\u76f4\u60f3\u95ee\u5f20\u5148\u751f\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u4eec\u8bf4\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u7231\u8fbe\u7535\u5b50\u4e0e\u8d8a\u79c0\u516c\u53f8\u662f\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d</p><p>\u3000\u3000\u201c\u5927\u5bb6\u5f00\u8bda\u5e03\u516c\u7684\u7545\u8c08\u8fd9\u4e9b\u5929\uff0c\u8fd9\u4e00\u70b9\u5012\u4e0d\u9700\u7792\u4f60\u4eec\uff0c\u660e\u9762\u4e0a\uff0c\u8d8a\u79c0\uff08\u9999\u6e2f\uff09\u53ea\u662f\u5411\u7231\u8fbe\u7535\u5b50\u4ee3\u7406\u9500\u552eTI\u516c\u53f8\u7684\u89e3\u7801\u677f\uff0c\u5b9e\u9645\u4e0a\uff0c\u4e24\u5bb6\u516c\u53f8\u7684\u521b\u529e\u4eba\u662f\u76f8\u540c\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u90fd\u662f\u5f20\u5148\u751f\u4f60\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u5012\u4e5f\u4e0d\u80fd\u8fd9\u4e48\u8bf4\uff0c\u5927\u5bb6\u7684\u52aa\u529b\uff0c\u53ea\u662f\u6211\u5360\u4e86\u5f88\u5927\u7684\u4fbf\u5b9c\u2026\u2026\u201d\u5f20\u606a\u5f88\u5e73\u9759\u7684\u8bf4\u7740\uff0c\u201c\u9676\u884c\u5065\u53bb\u5e74\u65e0\u6cd5\u5728\u60e0\u5c71\u7acb\u8db3\uff0c\u5728\u53bb\u5609\u4fe1\u4e4b\u524d\u5c31\u5728\u7231\u8fbe\u7535\u5b50\u5e2e\u5fd9\u3002\u201d</p><p>\u3000\u3000\u90ed\u5efa\u70b9\u70b9\u5934\uff0c\u5f20\u606a\u8fd9\u4e48\u8bf4\u7684\u610f\u601d\uff0c\u4ed6\u662f\u660e\u767d\u7684\uff0c\u5609\u4fe1\u5b9e\u4e1a\u4ecb\u5165\u5931\u8d25\uff0c\u4ed6\u4eec\u5728\u60e0\u5c71\u65e0\u6cd5\u7acb\u8db3\uff0c\u8fd8\u53ef\u4ee5\u9009\u62e9\u53bb\u6d77\u5dde\u7684\u7231\u8fbe\u7535\u5b50\uff0c\u6ca1\u6709\u540e\u987e\u4e4b\u5fe7\uff0c\u5012\u662f\u53ef\u4ee5\u4e3a\u9999\u96ea\u6d77\u7684\u5229\u76ca\u4e89\u4e00\u4e89\uff0c\u4ed6\u8bf4\u9053\uff1a\u201c\u8fd9\u4efd\u65b9\u6848\uff0c\u6211\u4eec\u53ef\u4e0d\u53ef\u5e26\u56de\u53bb\u770b\uff1f\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u56db\u6708\u5341\u4e09\u65e5\uff0c\u542c\u8d77\u6765\uff0c\u5bf9\u5f20\u94c1\u68ee\u7edd\u4e0d\u662f\u4ec0\u4e48\u597d\u65e5\u5b50\uff0c\u6682\u505c\u4e00\u5468\u3001\u660e\u5929\u5c31\u8981\u5f00\u59cb\u4e0e\u97e9\u65b9\u4ee3\u8868\u8fdb\u884c\u7b2c\u5341\u4e09\u6b21\u7684\u8c08\u5224\uff0c\u5f20\u94c1\u68ee\u5c06\u8c08\u5224\u7ec4\u7684\u6210\u5458\u53ec\u96c6\u8d77\u6765\uff0c\u53ec\u5f00\u4e00\u4e2a\u5185\u90e8\u4f1a\u8bae\uff0c\u7edf\u4e00\u4e00\u4e0b\u601d\u60f3\uff0c\u4e3a\u4e86\u4f7f\u8fd9\u6b21\u7edf\u4e00\u601d\u60f3\u66f4\u6709\u6548\u679c\uff0c\u5730\u70b9\u6ca1\u6709\u9009\u62e9\u5728\u5382\u5b50\u91cc\uff0c\u800c\u662f\u501f\u7528\u5e02\u653f\u5e9c\u529e\u516c\u5927\u697c\u7684\u4f1a\u8bae\u5ba4\uff0c\u5e02\u91cc\u9664\u4e86\u4ed6\u4e4b\u5916\uff0c\u8fd8\u6709\u5e02\u8f7b\u5de5\u5c40\u7684\u526f\u5c40\u957f\u8d39\u884c\u4f1f\u7b49\u4eba\uff0c\u9999\u96ea\u6d77\u7535\u51b0\u7bb1\u5382\u6709\u516b\u4eba\uff0c\u53ef\u8c13\u9635\u5bb9\u5e9e\u5927\u3002</p><p>\u3000\u3000\u6240\u8c13\u7684\u7edf\u4e00\u601d\u60f3\uff0c\u5c31\u662f\u5728\u8c08\u5224\u65f6\u4e0d\u5f97\u968f\u4fbf\u53d1\u8a00\uff0c\u66f4\u4e0d\u80fd\u4e0e\u97e9\u65b9\u4ee3\u8868\u4e89\u6267\u3002\u8d39\u884c\u4f1f\u5012\u662f\u4e0d\u7ba1\uff0c\u53ea\u8981\u65b9\u6848\u5728\u5e02\u91cc\u3001\u7701\u91cc\u80fd\u901a\u8fc7\uff0c\u600e\u4e48\u8c08\u90fd\u884c\u3002\u97e9\u65b9\u4ee3\u8868\u5728\u539f\u6765\u7ed9\u7701\u91cc\u9a73\u56de\u7684\u65b9\u6848\u57fa\u7840\u4e0a\u5bf8\u6b65\u4e0d\u8ba9\uff0c\u65e0\u975e\u662f\u8003\u9a8c\u60e0\u5c71\u5e02\u4e0e\u7701\u91cc\u62db\u5546\u5f15\u8d44\u7684\u8010\u5fc3\uff0c\u5f20\u94c1\u68ee\u7adf\u7136\u8fd8\u8981\u7edf\u4e00\u601d\u60f3\u3001\u7981\u6b62\u8c08\u5224\u7ec4\u5176\u4ed6\u6210\u5458\u4e0e\u97e9\u65b9\u4ee3\u8868\u4e89\u6267\uff0c\u771f\u4e0d\u6653\u5f97\u4ed6\u662f\u4e2d\u65b9\u4ee3\u8868\uff0c\u8fd8\u662f\u97e9\u65b9\u4ee3\u8868\u3002</p><p>\u3000\u3000\u8d39\u884c\u4f1f\u5fc3\u4e0d\u5728\u7109\u7684\u5750\u5728\u90a3\u91cc\uff0c\u6ce8\u610f\u5230\u5de6\u624b\u8fb9\u7684\u90ed\u5efa\u5728\u7ffb\u4e00\u4efd\u6587\u4ef6\uff0c\u53ea\u5f53\u4ed6\u4e5f\u542c\u817b\u4e86\u5f20\u94c1\u68ee\u7684\u5e9f\u8bdd\uff0c\u770b\u522b\u7684\u6587\u4ef6\u6253\u53d1\u65f6\u95f4\uff0c\u6b6a\u8fc7\u5934\u6765\u770b\u4e86\u770b\uff1a\u201c\u770b\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u90ed\u5efa\u4e0d\u52a8\u58f0\u8272\u7684\u5c06\u5609\u4fe1\u5b9e\u4e1a\u63d0\u51fa\u7684\u5408\u8d44\u65b9\u6848\u9012\u7ed9\u8d39\u884c\u4f1f\uff0c\u8d39\u884c\u4f1f\u8d8a\u770b\u8d8a\u5fc3\u60ca\uff0c\u8d76\u5fd9\u5c06\u6587\u4ef6\u63a8\u5230\u90ed\u5efa\u9762\u524d\uff0c\u4eff\u4f5b\u662f\u53ea\u70eb\u624b\u7684\u5c71\u828b\u3002\u6025\u5207\u4e4b\u4e0b\uff0c\u52a8\u4f5c\u672a\u514d\u60f6\u7136\u4e86\u4e00\u4e9b\uff0c\u5f15\u8d77\u5f20\u94c1\u68ee\u7684\u6ce8\u610f\u3002</p><p>\u3000\u3000\u201c\u8001\u8d39\u5728\u770b\u4ec0\u4e48\u4e1c\u897f\uff0c\u8fd9\u4e48\u6d25\u6d25\u6709\u5473\uff1f\u201d\u5f20\u94c1\u68ee\u5e74\u524d\u521a\u63d0\u7684\u526f\u5904\uff0c\u8d39\u884c\u4f1f\u5219\u662f\u8001\u6cb9\u6761\uff0c\u53ea\u8981\u4ed6\u80fd\u5728\u8c08\u5224\u65f6\u4e00\u76f4\u4fdd\u6301\u6c89\u9ed8\uff0c\u4e5f\u7531\u5f97\u4ed6\u5728\u4e0b\u9762\u641e\u8fd9\u4e9b\u6ca1\u7528\u7684\u5c0f\u52a8\u4f5c\uff0c\u8fd9\u4e5f\u53ea\u662f\u968f\u53e3\u4e00\u95ee\u3002</p><p>\u3000\u3000\u201c\u90ed\u5382\u957f\u8ba9\u6211\u770b\u7684\u4e1c\u897f\uff0c\u6211\u5c31\u770b\u770b\u800c\u5df2\u2026\u2026\u201d</p><p>\u3000\u3000\u8d39\u884c\u4f1f\u6025\u7740\u6487\u6e05\u81ea\u5df1\uff0c\u624d\u5f15\u8d77\u5f20\u94c1\u68ee\u7684\u6ce8\u610f\u3002</p><p>\u3000\u3000\u5bf9\u90ed\u5efa\uff0c\u5f20\u94c1\u68ee\u5c31\u6ca1\u6709\u90a3\u4e48\u5ba2\u6c14\u4e86\uff0c\u76f4\u63a5\u8bf4\u9053\uff1a\u201c\u73b0\u5728\u5f00\u4f1a\uff0c\u770b\u4ec0\u4e48\u4e1c\u897f\uff0c\u62ff\u7ed9\u6211\u770b\u770b\uff0c\u662f\u4e0d\u662f\u8ddf\u660e\u5929\u8c08\u5224\u6709\u5173\u7684\u4e1c\u897f\uff1f\u4e3a\u4ec0\u4e48\u8c08\u5224\u8981\u4e0d\u6210\u529f\uff0c\u504f\u504f\u5c31\u662f\u4f60\u4eec\u9999\u96ea\u6d77\u81ea\u5df1\u7684\u4eba\u4e0d\u4e0a\u5fc3\u2026\u2026\u201d</p><p>\u3000\u3000\u90ed\u5efa\u8f7b\u8f7b\u5410\u4e86\u4e00\u53e3\u6c14\uff0c\u5c06\u6587\u4ef6\u9012\u8fc7\u53bb\uff1a\u201c\u8fd9\u4efd\u6750\u6599\u6b63\u60f3\u5728\u4f1a\u4e0a\u63d0\u51fa\u6765\uff0c\u5f20\u5904\u957f\u8981\u770b\uff0c\u90a3\u4f60\u5c31\u5148\u770b\u770b\u5427\u3002\u201d</p><p>\u3000\u3000\u5f20\u94c1\u68ee\u63a5\u8fc7\u6587\u4ef6\uff0c\u624d\u7ffb\u4e86\u4e24\u9875\u5c31\u52c3\u7136\u8272\u53d8\uff0c\u6076\u72e0\u72e0\u7684\u76ef\u7740\u90ed\u5efa\uff0c\u5927\u58f0\u5486\u54ee\uff1a\u201c\u6211\u65e9\u5c31\u8b66\u544a\u8fc7\u4f60\u4eec\uff0c\u8c01\u8981\u60f3\u505a\u7b2c\u4e8c\u4e2a\u9676\u884c\u5065\uff0c\u662f\u8981\u4ed8\u51fa\u4ee3\u4ef7\u7684\uff0c\u8fd9\u4efd\u4e1c\u897f\u63d0\u4ec0\u4e48\u63d0\uff01\u201d\u201c\u54d7\u201d\u7684\u6495\u6210\u4e24\u534a\uff0c\u53c8\u62fc\u547d\u6495\u4e86\u51e0\u4e0b\uff0c\u5c06\u7eb8\u7247\u90fd\u4e22\u5230\u90ed\u5efa\u7684\u8138\u4e0a\u3002</p></article>"}}}]);
