(window.webpackJsonp=window.webpackJsonp||[]).push([[485],{508:function(p){p.exports={chapterTitle:null,title:"\u7b2c348\u7ae0 \u80cc\u540e\u7684\u4eba",content:"<article><h2>\u7b2c348\u7ae0 \u80cc\u540e\u7684\u4eba</h2><p>\u3000\u3000\u5f20\u94c1\u68ee\u4eff\u4f5b\u7ed9\u6349\u4f4f\u75db\u811a\u4e00\u6837\u7684\u7a81\u7136\u5931\u63a7\uff0c\u66b4\u8df3\u5982\u96f7\u7684\u5c06\u6587\u4ef6\u6495\u6210\u788e\u7247\u6254\u5230\u90ed\u5efa\u7684\u8138\u4e0a\uff0c\u96ea\u767d\u7684\u7eb8\u7247\u6d12\u4e86\u4e00\u5730\u3002</p><p>\u3000\u3000\u5728\u5ea7\u7684\u8c08\u5224\u7ec4\u7684\u5176\u4ed6\u6210\u5458\u90fd\u89c9\u5f97\u592a\u7a81\u5140\uff0c\u4e0d\u6653\u5f97\u90ed\u5efa\u62ff\u7ed9\u5f20\u94c1\u68ee\u770b\u7684\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u7adf\u7136\u80fd\u8fd9\u4e48\u5f7b\u5e95\u7684\u6fc0\u52aa\u5f20\u94c1\u68ee\uff0c\u770b\u7740\u5f20\u94c1\u68ee\u77aa\u5927\u773c\u775b\u76ef\u7740\u90ed\u5efa\uff0c\u597d\u50cf\u8981\u5c06\u4ed6\u5403\u4e0b\u53bb\u4e00\u6837\u3002\u6709\u4eba\u60f3\u62fe\u8d77\u5730\u4e0a\u7684\u788e\u7eb8\u7247\uff0c\u60f3\u770b\u7a76\u7adf\u662f\u4ec0\u4e48\u4e1c\u897f\u3002</p><p>\u3000\u3000\u201c\u770b\u4ec0\u4e48\u770b\uff1f\u201d\u5f20\u94c1\u68ee\u731b\u7136\u4e00\u62cd\u684c\u5b50\uff0c\u53c8\u5413\u4e86\u4f17\u4eba\u4e00\u8df3\u3002</p><p>\u3000\u3000\u4e5f\u6709\u4eba\u4e0d\u4e70\u4ed6\u8d26\u7684\uff0c\u9999\u96ea\u6d77\u4e0b\u5c5e\u51b7\u67dc\u5206\u5382\u7684\u5382\u957f\u6797\u56fd\u680b\u6162\u60a0\u60a0\u7684\u5c06\u51e0\u5f20\u788e\u7eb8\u7247\u6446\u5230\u684c\u4e0a\uff0c\u60f3\u62fc\u8d77\u6765\u770b\u770b\u5230\u5e95\u662f\u4ec0\u4e48\u6750\u6599\uff0c\u8bf4\u9053\uff1a\u201c\u90ed\u5382\u957f\u96be\u9053\u62ff\u51fa\u6765\u7684\u662f\u53cd\u653f\u5e9c\u7684\u6750\u6599\uff0c\u8981\u771f\u662f\uff0c\u4e5f\u4e0d\u8be5\u6495\u788e\uff0c\u53ef\u4ee5\u5f53\u8bc1\u636e\u4fdd\u7559\u4e0b\u6765\uff0c\u6495\u4e86\u5e72\u561b\u2026\u2026\u201d\u4ed6\u662f\u5168\u56fd\u52b3\u6a21\uff0c\u53c8\u662f\u7701\u653f\u534f\u59d4\u5458\uff0c\u51b7\u67dc\u5206\u5382\u539f\u6765\u662f\u72ec\u7acb\u7684\u60e0\u5c71\u5236\u51b7\u8bbe\u5907\u5382\uff0c\u4e5d\u4e8c\u5e74\u624d\u8ba9\u5e02\u91cc\u5e76\u5165\u9999\u96ea\u6d77\u5382\uff0c\u9996\u5148\u4ed6\u5bf9\u9999\u96ea\u6d77\u7684\u611f\u60c5\u4e0d\u6bd4\u90ed\u5efa\u4ed6\u4eec\u6d53\u539a\uff0c\u4ed6\u5e73\u65f6\u53ea\u8d1f\u8d23\u51b7\u67dc\u5382\u7684\u751f\u4ea7\uff0c\u5bf9\u5e02\u573a\u4e5f\u4e0d\u662f\u90a3\u4e48\u654f\u611f\uff0c\u5bf9\u97e9\u65b9\u63d0\u51fa\u7684\u5408\u8d44\u6761\u4ef6\uff0c\u867d\u7136\u89c9\u5f97\u6709\u4e9b\u82db\u523b\uff0c\u5012\u4e5f\u6ca1\u6709\u592a\u5f3a\u70c8\u7684\u62b5\u5236\u60c5\u7eea\uff1b\u518d\u8bf4\uff0c\u4eba\u90fd\u662f\u6709\u79c1\u5fc3\u7684\uff0c\u5408\u8d44\u4e4b\u540e\uff0c\u8fdb\u5165\u5408\u8d44\u5de5\u5382\uff0c\u4e2a\u4eba\u6536\u5165\u80fd\u63d0\u9ad8\u4e00\u622a\u3002\u5012\u662f\u4ed6\u5bf9\u5f20\u94c1\u68ee\u8fd9\u4eba\u4e0d\u559c\u6b22\uff0c\u53ea\u662f\u5e73\u65e5\u65f6\u8c28\u5b88\u201c\u5b81\u53ef\u5f97\u7f6a\u541b\u5b50\u3001\u4e0d\u53ef\u5f97\u7f6a\u5c0f\u4eba\u201d\u7684\u4fe1\u6761\uff0c\u4e0d\u8ddf\u5f20\u94c1\u68ee\u8d77\u51b2\u7a81\uff0c\u4f46\u662f\u90ed\u5efa\u597d\u6b79\u662f\u9999\u96ea\u6d77\u7684\u526f\u5382\u957f\uff0c\u7adf\u7136\u7ed9\u5f20\u94c1\u68ee\u5c06\u7eb8\u7247\u6254\u4ed6\u8138\u4e0a\uff0c\u90ed\u5efa\u7ed9\u5f20\u94c1\u68ee\u7a81\u7136\u7684\u5931\u63a7\u641e\u5f97\u6709\u4e9b\u53d1\u8499\uff0c\u4ed6\u5374\u5148\u770b\u4e0d\u8fc7\u53bb\u4e86\u3002</p><p>\u3000\u3000\u201c\u6797\u56fd\u680b\uff01\u201d\u5f20\u94c1\u68ee\u65e0\u6cd5\u63a7\u5236\u4ed6\u7684\u60c5\u7eea\uff0c\u5609\u4fe1\u5b9e\u4e1a\u4ecb\u5165\u662f\u4ed6\u6240\u80fd\u9884\u6599\u5230\u7684\u6700\u574f\u60c5\u5f62\uff0c\u4ed6\u6ca1\u6709\u60f3\u5230\u5609\u4fe1\u5b9e\u4e1a\u65e9\u5c31\u8ddf\u8c08\u5224\u7ec4\u5185\u90e8\u6210\u5458\u6709\u52fe\u7ed3\uff0c\u8fd8\u4e8f\u4ed6\u4e00\u800c\u518d\u3001\u518d\u800c\u4e09\u7684\u7edf\u4e00\u601d\u60f3\uff0c\u90ed\u5efa\u7684\u884c\u5f84\u8ba9\u4ed6\u4ee5\u5f80\u6240\u52aa\u529b\u7ad6\u8d77\u7684\u6743\u5a01\u8f70\u7136\u5d29\u584c\uff0c\u53c8\u8ba9\u4ed6\u4e07\u5206\u7684\u6050\u60e7\uff0c\u4f46\u662f\u6797\u56fd\u680b\u7684\u884c\u4e3a\u5374\u786e\u5b9e\u4f24\u4e86\u4ed6\u7684\u81ea\u5c0a\u5fc3\uff0c\u58f0\u97f3\u6beb\u4e0d\u8282\u5236\u7684\u63d0\u9ad8\u4e86\u516b\u5ea6\uff0c\u201c\u4e0d\u8981\u7ed9\u4f60\u8138\u4e0d\u8981\u8138\uff0c\u4f60\u5f53\u81ea\u5df1\u662f\u8c01\uff1f\u90ed\u5efa\u8981\u505a\u7b2c\u4e8c\u4e2a\u9676\u884c\u5065\uff0c\u4f60\u8981\u8ddf\u7740\u4ed6\u4e00\u8d77\u7834\u574f\u62db\u5546\u5f15\u8d44\uff01\u201d</p><p>\u3000\u3000\u6797\u56fd\u680b\u6c14\u5f97\u8138\u4e0a\u8089\u76f4\u8df3\uff0c\u5c06\u684c\u524d\u7684\u788e\u6587\u4ef6\u63a8\u5230\u5730\u4e0a\uff1a\u201c\u8fd9\u9b3c\u52b3\u5b50\u4e1c\u897f\u8c01\u8981\u770b\uff01\u8c01\u7834\u574f\u62db\u5546\u5f15\u8d44\uff0c\u4e5f\u4e0d\u662f\u4f60\u8fd9\u4e2a\u5976\u6ce1\u8bf4\u7684\u7b97\u7684\u3002\u201d\u624b\u6491\u7740\u684c\u5b50\u7ad9\u8d77\u6765\u5c31\u5f80\u5916\u8d70\u3002</p><p>\u3000\u3000\u201c\u4f60\u7ed9\u6211\u7ad9\u4f4f\uff01\u201d\u5f20\u94c1\u68ee\u8ba9\u4ed6\u4e00\u53e5\u201c\u5976\u6ce1\u201d\u6233\u5230\u75db\u811a\uff0c\u8df3\u8fc7\u6765\u8981\u6293\u6797\u56fd\u680b\uff0c\u201c\u4f60\u8bf4\u4ec0\u4e48\uff0c\u4f60\u8bec\u8511\u6211\u4ec0\u4e48\uff0c\u6211\u8ddf\u4f60\u5230\u5468\u5e02\u957f\u524d\u8bc4\u7406\u53bb\u3002\u201d\u6797\u56fd\u680b\u817f\u811a\u4e0d\u6bd4\u5f20\u94c1\u68ee\u7075\u6d3b\uff0c\u4e24\u4eba\u5ea7\u4f4d\u6328\u5f97\u53c8\u8fd1\uff0c\u6ca1\u7b49\u4ed6\u6253\u5f00\u4f1a\u8bae\u5ba4\u7684\u95e8\u51fa\u53bb\uff0c\u5c31\u7ed9\u5f20\u94c1\u68ee\u6525\u4f4f\u8863\u9886\u2026\u2026</p><p>\u3000\u3000\u6b63\u95f9\u5f97\u4e0d\u53ef\u5f00\u4ea4\uff0c\u4f1a\u8bae\u5ba4\u7684\u95e8\u7ed9\u4eba\u4ece\u5916\u9762\u63a8\u5f00\uff1a\u201c\u5f00\u4f1a\u7684\u58f0\u97f3\u80fd\u4e0d\u80fd\u5c0f\u4e00\u4e9b\uff0c\u6574\u5c42\u697c\u90fd\u542c\u89c1\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u5c0f\u4f60\u5988\u2026\u2026\u201d\u5f20\u94c1\u68ee\u6b63\u5728\u6c14\u5934\u4e0a\uff0c\u810f\u8bdd\u8131\u53e3\u800c\u51fa\uff0c\u7b49\u770b\u89c1\u63a8\u95e8\u7684\u4eba\u662f\u8d75\u9633\u4e66\u8bb0\u7684\u79d8\u4e66\u9a6c\u9510\u5df2\u7ecf\u6765\u4e0d\u53ca\u6536\u53e3\uff0c\u8d75\u9633\u4e0e\u5e02\u59d4\u79d8\u4e66\u957f\u90dd\u5efa\u8bbe\u7ad9\u5728\u95e8\u5916\uff0c\u4fe9\u4eba\u7684\u8138\u90fd\u9634\u9634\u7684\uff0c\u5c24\u5176\u662f\u8d75\u9633\u7684\u8138\u9634\u6c89\u5f97\u66f4\u53ef\u6015\uff0c\u60f3\u5fc5\u4ed6\u4eec\u5728\u95e8\u5916\u542c\u4e86\u597d\u4e00\u4f1a\u513f\u3002\u8d75\u9633\u4e66\u8bb0\u8eab\u8fb9\u8fd8\u6709\u51e0\u4e2a\u4eba\uff0c\u5f20\u94c1\u68ee\u5f53\u65f6\u5728\u6885\u6797\u8fb9\u7684\u56fd\u9053\u4e0a\u6ca1\u6709\u770b\u89c1\u53f6\u5efa\u658c\u7684\u8138\uff0c\u4f46\u662f\u540e\u6765\u770b\u8fc7\u4ed6\u7684\u7167\u7247\uff0c\u6240\u4ee5\u8ba4\u51fa\u53f6\u5efa\u658c\uff0c\u53f6\u5efa\u658c\u65c1\u8fb9\u6bd4\u4ed6\u8981\u5e74\u8f7b\u5f97\u591a\u7684\u9752\u5e74\uff0c\u5e94\u8be5\u5c31\u662f\u5f20\u606a\u4e86\u3002</p><p>\u3000\u3000\u90a3\u56fd\u9053\u8fb9\u4e88\u4eba\u60ca\u8273\u4e4b\u611f\u7684\u8bb8\u601d\uff0c\u5f20\u94c1\u68ee\u518d\u6ca1\u6709\u5fc3\u60c5\u6b23\u8d4f\u4f73\u5bb9\uff0c\u8fd9\u5f53\u513f\uff0c\u4ed6\u817f\u90fd\u6709\u4e9b\u53d1\u8f6f\u4e86\uff0c\u90fd\u4e0d\u6562\u4e3b\u52a8\u8ddf\u8d75\u9633\u7684\u79d8\u4e66\u9a6c\u9510\u9053\u6b49\u3002</p><p>\u3000\u3000\u201c\u4ec0\u4e48\u5de5\u4f5c\u4f5c\u98ce\uff0c\u5927\u5435\u5927\u56b7\u7684\uff0c\u8981\u5435\u90fd\u7ed9\u6211\u51fa\u53bb\u5435\uff01\u201d\u8d75\u9633\u6b63\u966a\u53f6\u5efa\u658c\u3001\u5f20\u606a\u53c2\u89c2\u5e02\u59d4\u5e02\u653f\u5e9c\u529e\u516c\u5927\u697c\uff0c\u6ca1\u60f3\u5230\u8fd9\u4e00\u5e55\u95f9\u5267\u7ed9\u5ba2\u4eba\u770b\u5230\uff0c\u4e22\u5c3d\u60e0\u5c71\u7684\u8138\uff0c\u538b\u4f4f\u5fc3\u91cc\u7684\u6012\u706b\uff0c\u4f4e\u58f0\u8bad\u65a5\u3002</p><p>\u3000\u3000\u5f20\u94c1\u68ee\u5413\u5f97\u4e0d\u6562\u52a8\u5f39\uff0c\u5e02\u59d4\u79d8\u4e66\u957f\u8d6b\u5efa\u8bbe\u8d70\u8fc7\u6765\uff0c\u538b\u4f4e\u58f0\u97f3\u8bad\u9053\uff1a\u201c\u8fd8\u4e0d\u90fd\u51fa\u53bb\uff01\u201d</p><p>\u3000\u3000\u5f20\u94c1\u68ee\u4e0d\u6562\u8fa9\u9a73\u4ec0\u4e48\uff0c\u53ea\u5f97\u5148\u51fa\u53bb\u518d\u8bf4\uff0c\u4e0e\u5f20\u606a\u9519\u8eab\u800c\u8fc7\u65f6\uff0c\u770b\u5230\u4ed6\u5634\u89d2\u8fb9\u6d6e\u8d77\u7684\u6d45\u7b11\uff0c\u53ea\u662f\u5c06\u5934\u6487\u5f00\u4e0d\u770b\u800c\u5df2\u3002</p><p>\u3000\u3000\u90ed\u5efa\u4ed6\u4eec\u5c06\u5730\u4e0a\u7684\u788e\u7eb8\u7247\u62fe\u8d77\u6765\uff0c\u4e5f\u8ddf\u7740\u51fa\u53bb\uff0c\u8d75\u9633\u4e66\u8bb0\u5728\u573a\uff0c\u591a\u8bf4\u4e00\u53e5\u5c31\u662f\u627e\u9a82\u7684\uff0c\u4e0d\u8fc7\u770b\u5230\u5f20\u94c1\u68ee\u6709\u5982\u4e27\u5bb6\u4e4b\u72ac\uff0c\u5fc3\u91cc\u975e\u5e38\u7684\u75db\u5feb\u3002</p><p>\u3000\u3000\u4e24\u5929\u524d\uff0c\u5f20\u606a\u624d\u8bf4\u5f20\u94c1\u68ee\u53ea\u624b\u906e\u4e0d\u4f4f\u5168\u90e8\u7684\u5929\uff0c\u6ca1\u60f3\u5230\u5f20\u94c1\u68ee\u7684\u62a5\u5e94\u8fd8\u771f\u662f\u5feb\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u94c1\u68ee\u5f53\u7136\u6ca1\u6709\u5230\u529e\u516c\u697c\u5916\u9762\u4e0e\u90ed\u5efa\u3001\u6797\u56fd\u680b\u4ed6\u4eec\u7ee7\u7eed\u5435\uff0c\u7ed9\u8d76\u51fa\u4f1a\u8bae\u5ba4\uff0c\u60f6\u6050\u4e0d\u5b89\u7684\u8dd1\u5230\u5468\u747e\u745c\u529e\u516c\u5ba4\uff0c\u5c06\u4e8b\u60c5\u8bf4\u4e86\u4e00\u901a\uff0c\u4e5f\u4e0d\u6562\u9690\u7792\u4ec0\u4e48\uff0c\u53ea\u662f\u5c06\u90ed\u5efa\u3001\u6797\u56fd\u680b\u8bf4\u5f97\u9669\u6076\u4e86\u4e00\u4e9b\uff1a\u201c\u4ed6\u4eec\u7eaf\u7cb9\u60f3\u8981\u7834\u574f\u5e02\u91cc\u7684\u62db\u5546\u5f15\u8d44\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8d75\u9633\u4e66\u8bb0\u6709\u5ba2\u4eba\u5728\uff0c\u4f60\u5bf9\u9a6c\u79d8\u4e66\u8bf4\u7c97\u53e3\uff0c\u8ba9\u9a6c\u79d8\u4e66\u3001\u8d75\u9633\u4e66\u8bb0\u600e\u4e48\u4e0b\u53f0\uff1f\u4f60\u7b49\u4f1a\u513f\u53bb\u627e\u9a6c\u79d8\u4e66\u9053\u6b49\uff0c\u518d\u5230\u8d75\u9633\u4e66\u8bb0\u90a3\u91cc\u6df1\u523b\u68c0\u8ba8\u4e00\u4e0b\u3002\u201d</p><p>\u3000\u3000\u201c\u90a3\u5408\u8d44\u7684\u4e8b\u60c5\u600e\u4e48\u529e\uff0c\u4ed6\u4eec\u662f\u8981\u8d70\u8d75\u9633\u4e66\u8bb0\u7684\u8def\u7ebf\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6ca1\u6709\u7684\u4e8b\u4e0d\u8981\u778e\u731c\u6d4b\u3002\u201d</p><p>\u3000\u3000\u201c\u53bb\u5e74\u65b9\u6848\u7ed9\u5f90\u5b66\u5e73\u9a73\u56de\uff0c\u4ed6\u4eec\u5c31\u662f\u6709\u9884\u8c0b\u7684\uff0c\u8fd9\u4e4b\u524d\u662f\u731c\u6d4b\uff0c\u4f46\u662f\u4e8b\u60c5\u5df2\u7ecf\u5230\u8fd9\u6b65\uff0c\u8fd8\u662f\u731c\u6d4b\u5417\uff1f\u201d\u5f20\u94c1\u68ee\u5fcd\u4e0d\u4f4f\u4e89\u4e86\u4e00\u53e5\u3002</p><p>\u3000\u3000\u5468\u747e\u745c\u5012\u662f\u6ca1\u6709\u602a\u4ed6\uff0c\u8f7b\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u505a\u4e8b\u7684\u65b9\u6cd5\u6709\u4e9b\u95ee\u9898\u554a\uff0c\u5408\u8d44\u7684\u4e8b\u60c5\u62d6\u5f97\u8fd9\u4e48\u4e45\uff0c\u80fd\u6709\u7b2c\u4e8c\u5bb6\u4f01\u4e1a\u6709\u8ddf\u9999\u96ea\u6d77\u5408\u8d44\u7684\u610f\u613f\uff0c\u5e02\u91cc\u600e\u4e48\u53ef\u4ee5\u62d2\u7edd\uff1f\u4f60\u8981\u662f\u4e0d\u95f9\uff0c\u4e8b\u60c5\u90fd\u4e0d\u8fd8\u4f60\u7684\u638c\u63e1\u4e4b\u4e2d\u5417\uff1f\u8be5\u600e\u4e48\u8c08\uff0c\u5408\u8d44\u65b9\u6848\u8be5\u600e\u4e48\u8bc4\u4f30\uff0c\u4e0d\u90fd\u662f\u8c08\u5224\u7ec4\u51b3\u5b9a\u5417\uff1f\u8d75\u9633\u4e66\u8bb0\u5411\u6765\u4e0d\u63d2\u624b\u5e02\u653f\u5e9c\u7684\u4e8b\uff0c\u90a3\u662f\u4ed6\u4e00\u76f4\u6ca1\u6709\u63d2\u624b\u7684\u501f\u53e3\uff0c\u73b0\u5728\u6709\u4e9b\u88ab\u52a8\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u94c1\u68ee\u4e00\u6123\uff0c\u60f3\u8d77\u79bb\u5f00\u4f1a\u8bae\u5ba4\u91cc\u5f20\u606a\u5634\u89d2\u72e1\u8bc8\u7684\u6d45\u7b11\uff0c\u5fcd\u4e0d\u4f4f\u62b1\u6028\uff1a\u201c\u90a3\u4e2a\u5f20\u606a\u8ba9\u9999\u96ea\u6d77\u7684\u90a3\u4e2a\u526f\u5382\u957f\u7ed9\u6211\u641e\u7a81\u7136\u88ad\u51fb\uff0c\u4ed6\u4eec\u662f\u6709\u9884\u8c0b\u7684\uff0c\u5c31\u662f\u60f3\u6211\u5728\u8d75\u4e66\u8bb0\u9762\u524d\u51fa\u4e11\uff0c\u4e5f\u662f\u4e00\u65f6\u7ed9\u6c14\u660f\u4e86\u5934\uff0c\u4f46\u662f\u6797\u56fd\u680b\u8bf4\u90a3\u4e48\u6076\u52a3\u7684\u8bdd\uff0c\u90a3\u662f\u5728\u653b\u51fb\u5468\u5e02\u957f\u4f60\u554a\uff0c\u6211\u54ea\u91cc\u80fd\u63a7\u5236\u5f97\u4f4f\u4e0d\u751f\u6c14\uff1f\u201d</p><p>\u3000\u3000\u201c\u8d75\u9633\u4e66\u8bb0\u7684\u529e\u516c\u5ba4\u5728\u5341\u4e8c\u697c\uff0c\u4ed6\u4eec\u80fd\u77e5\u9053\u4f60\u4ec0\u4e48\u65f6\u5019\u5931\u63a7\uff0c\u6b63\u597d\u5f15\u7740\u8d75\u9633\u4e66\u8bb0\u90a3\u65f6\u7ecf\u8fc7\u4f1a\u8bae\u5ba4\u5916\uff1f\u6ca1\u6709\u7684\u4e8b\uff0c\u4e0d\u8981\u80e1\u4e71\u60f3\uff0c\u201d\u5468\u747e\u745c\u4e5f\u6709\u4e9b\u6068\u94c1\u4e0d\u6210\u94a2\uff0c\u4e0d\u8fc7\u8981\u7167\u987e\u4ed6\u7684\u60c5\u7eea\uff0c\u5b89\u6170\u4ed6\u8bf4\uff0c\u201c\u5148\u60f3\u60f3\u628a\u773c\u524d\u7684\u5c40\u52bf\u6536\u62fe\u597d\u7f62\uff0c\u5b9e\u5728\u6ca1\u6709\u529e\u6cd5\uff0c\u5408\u8d44\u516c\u53f8\u7684\u603b\u7ecf\u7406\uff0c\u4e0d\u5f53\u4e5f\u7f62\uff0c\u7b49\u4ee5\u540e\u6709\u597d\u7684\u4f4d\u7f6e\uff0c\u6211\u518d\u5b89\u6392\u4f60\u8fc7\u53bb\u3002\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u5f20\u94c1\u68ee\u8fdf\u7591\u7740\uff0c\u5468\u747e\u745c\u8fd9\u8bdd\u662f\u793a\u5f31\u4e86\u3002</p><p>\u3000\u3000\u5468\u747e\u745c\u89c1\u5f20\u94c1\u68ee\u8fd9\u4ed8\u6837\u5b50\uff0c\u95ee\u4ed6\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u6536\u4eba\u5bb6\u597d\u5904\u4e86\uff1f\u201d</p><p>\u3000\u3000\u5f20\u94c1\u68ee\u4e00\u60ca\uff0c\u5fd9\u77e2\u53e3\u5426\u8ba4\uff1a\u201c\u6ca1\u6709\uff0c\u7edd\u5bf9\u6ca1\u6709\u2026\u2026\u201d</p><p>\u3000\u3000\u5468\u747e\u745c\u76ef\u7740\u5f20\u94c1\u68ee\u7684\u8138\u770b\u4e86\u4e00\u4f1a\u513f\uff0c\u624d\u6325\u624b\u8ba9\u4ed6\u79bb\u5f00\u3002\u5750\u5728\u6905\u5b50\u4e0a\u60f3\u4e86\u4e00\u4f1a\u513f\uff0c\u5c06\u524d\u540e\u7684\u4e8b\u60c5\u4ed4\u7ec6\u56de\u5473\u4e86\u4e00\u4e0b\uff0c\u62ff\u8d77\u684c\u4e0a\u7684\u7535\u8bdd\uff0c\u62d4\u4e86\u4e00\u4e2a\u7701\u57ce\u7684\u7535\u8bdd\u3002</p><p>\u3000\u3000\u201c\u747e\u745c\uff0c\u600e\u4e48\u7a81\u7136\u60f3\u5230\u65e0\u6240\u4e8b\u4e8b\u7684\u5927\u54e5\u6765\u4e86\uff1f\u201d\u7535\u8bdd\u90a3\u5934\u662f\u4e2a\u5e26\u7740\u4e9b\u6c99\u54d1\u7684\u58f0\u97f3\u3002</p><p>\u3000\u3000\u201c\u53ea\u662f\u60f3\u8ddf\u5927\u54e5\u8bf4\u4e00\u58f0\uff0c\u5f90\u5b66\u5e73\u672a\u5fc5\u662f\u5927\u54e5\u60f3\u7684\u90a3\u79cd\u4eba\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u5728\u5b66\u6821\u91cc\u592a\u957f\u65f6\u95f4\u4e86\uff0c\u8111\u5b50\u90fd\u6709\u4e9b\u8fdf\u949d\u4e86\uff0c\u5f90\u5b66\u5e73\u600e\u4e48\u4e0d\u662f\u6211\u6240\u60f3\u7684\u90a3\u79cd\u4eba\uff1f\u201d</p><p>\u3000\u3000\u201c\u5927\u54e5\u4e4b\u524d\u8bf4\u8fc7\u5f90\u5b66\u5e73\u2018\u521a\u5219\u6613\u6298\u2019\uff0c\u73b0\u5728\u770b\u6765\uff0c\u4ed6\u4e5f\u767e\u6298\u6210\u7ed5\u6307\u67d4\u4e86\uff0c\u8fd9\u4e24\u5e74\u53d1\u751f\u5f88\u591a\u4e8b\u60c5\uff0c\u5c31\u8fde\u8c22\u665a\u6674\u90fd\u8ddf\u8c22\u5bb6\u6495\u7834\u8138\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u770b\u4e0d\u900f\u7684\u4e1c\u897f\u591a\u4e86\uff0c\u6211\u8ddf\u4ed6\u8ba4\u8bc6\u4e86\u4e09\u5341\u5e74\uff0c\u4e00\u8d77\u7ed9\u4e0b\u653e\u5230\u6d77\u57ce\u519c\u573a\uff0c\u6211\u8fd8\u6551\u8fc7\u4ed6\u4e00\u547d\uff0c\u4ed6\u6700\u540e\u8fd8\u4e0d\u662f\u575a\u6301\u8981\u67e5\u6211\uff1f\u8981\u4e0d\u662f\u4e0a\u9762\u6709\u4eba\u5e72\u6d89\uff0c\u4ed6\u4f1a\u5bb9\u6211\u56de\u5b66\u6821\u6559\u4e66\u4e3a\u751f\uff1f\u8fd9\u662f\u6211\u7b2c\u4e00\u6b21\u770b\u4e0d\u900f\u4ed6\u3002\u6ca1\u60f3\u5230\u90a3\u4ef6\u6848\u5b50\u7ed3\u675f\uff0c\u4ed6\u4e5f\u9634\u9b42\u4e0d\u6563\u7684\u8ddf\u7740\u4e5f\u5230\u4e1c\u6d77\u6765\uff0c\u65e2\u7136\u6709\u7b2c\u4e00\u6b21\u770b\u4e0d\u900f\uff0c\u63a5\u4e0b\u6765\u770b\u4e0d\u900f\uff0c\u4e5f\u4e0d\u662f\u4ec0\u4e48\u5947\u602a\u7684\u4e8b\u60c5\u3002\u7a76\u7adf\u53d1\u751f\u4ec0\u4e48\u4e8b\u4e86\uff0c\u8ba9\u4f60\u60f3\u8d77\u8fd9\u65f6\u5019\u8981\u8ddf\u6211\u8bf4\u5f90\u5b66\u5e73\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e09\u661f\u4e0e\u9999\u96ea\u6d77\u5408\u8d44\u7684\u4e8b\u60c5\u5927\u54e5\u6709\u6ca1\u6709\u542c\u8bf4\u8fc7\uff1f\u201d</p><p>\u3000\u3000\u201c\u6709\u542c\u8bf4\u8fc7\uff0c\u5408\u8d44\u65b9\u6848\u8ba9\u5f90\u5b66\u5e73\u7ed9\u6321\u4e86\u56de\u53bb\u3002\u4e0d\u8fc7\u8c22\u6c49\u9756\u4e0a\u56de\u5230\u6211\u8fd9\u91cc\u6765\u505a\u5ba2\uff0c\u8c08\u4e86\u5f88\u591a\u4e8b\u60c5\uff0c\u5374\u662f\u6ca1\u6709\u63d0\u6b63\u6cf0\u96c6\u56e2\u8ddf\u8fd9\u5408\u8d44\u7684\u4e8b\u60c5\u6709\u5173\u7cfb\u3002\u201d</p><p>\u3000\u3000\u201c\u8ddf\u6b63\u6cf0\u96c6\u56e2\u662f\u6ca1\u6709\u5173\u7cfb\uff0c\u53ea\u662f\u60f3\u8ddf\u5927\u54e5\u8bf4\uff0c\u5f90\u5b66\u5e73\u662f\u6709\u5229\u76ca\u7275\u626f\u624d\u63d2\u624b\u8fd9\u4ef6\u4e8b\u7684\u3002\u201d</p><p>\u3000\u3000\u201c\u90a3\u4f60\u8bf4\u6765\u542c\u542c\uff0c\u6211\u4e5f\u60f3\u770b\u770b\u5f90\u5b66\u5e73\u73b0\u5728\u53d8\u6210\u4ec0\u4e48\u6837\u5b50\u4e86\u3002\u201d</p></article>"}}}]);
