(window.webpackJsonp=window.webpackJsonp||[]).push([[488],{511:function(p){p.exports={chapterTitle:null,title:"\u7b2c350\u7ae0 \u9488\u950b\u76f8\u5bf9",content:"<article><h2>\u7b2c350\u7ae0 \u9488\u950b\u76f8\u5bf9</h2><p>\u3000\u3000\u5f20\u606a\u521d\u6765\u65f6\u8c26\u548c\u6c89\u9759\uff0c\u4e0e\u5468\u747e\u745c\u4e00\u89c1\u9762\u5c31\u9510\u6c14\u523a\u4eba\uff0c\u6beb\u4e0d\u72b9\u8c6b\u7684\u53cd\u51fb\u5468\u747e\u745c\u5bf9\u8bb8\u601d\u7684\u6311\u7554\u3002\u8d75\u9633\u5012\u6ca1\u6709\u89c1\u8fc7\u5468\u747e\u745c\u5f53\u573a\u7ed9\u4eba\u62ff\u8bdd\u9876\u4f4f\u7684\u60c5\u5f62\uff0c\u5fc3\u91cc\u7545\u5feb\uff0c\u4e5f\u4e0d\u5e2e\u5979\u89e3\u56f4\uff0c\u53ea\u5728\u65c1\u8fb9\u9759\u770b\u7740\u5979\u7684\u8138\u5fae\u5fae\u6da8\u7ea2\uff0c\u60f3\u5fc5\u5979\u5fc3\u91cc\u6781\u4e3a\u4e0d\u723d\u5427\u3002</p><p>\u3000\u3000\u5f20\u606a\u6c89\u9759\u7684\u6ce8\u89c6\u7740\u5468\u747e\u745c\uff0c\u4e24\u5bb6\u90fd\u5df2\u7ecf\u6495\u7834\u8138\uff0c\u81ea\u7136\u4e0d\u7528\u7ed9\u5468\u747e\u745c\u4ec0\u4e48\u597d\u8138\u8272\u3002</p><p>\u3000\u3000\u7ed9\u5f20\u606a\u8fd9\u4e00\u6fc0\uff0c\u5468\u747e\u745c\u6c14\u5f97\u5dee\u70b9\u95ed\u8fc7\u6c14\u53bb\uff0c\u597d\u4e00\u4f1a\u513f\u624d\u7f13\u8fc7\u795e\uff0c\u6062\u590d\u9547\u5b9a\u7684\u795e\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u5609\u4fe1\u5b9e\u4e1a\u7684\u5408\u8d44\u65b9\u6848\uff0c\u5f20\u79d8\u4e66\u8ddf\u6211\u63d0\u8d77\u8fc7\uff0c\u5728\u9999\u96ea\u6d77\u4e0e\u4e09\u661f\u7684\u5408\u8d44\u8c08\u5224\u8d70\u5230\u6700\u540e\u4e00\u6b65\u4e4b\u65f6\uff0c\u5609\u4fe1\u5b9e\u4e1a\u7a81\u7136\u4ecb\u5165\uff0c\u603b\u7ed9\u4eba\u592a\u7a81\u5140\u7684\u611f\u89c9\u3002\u800c\u4e14\uff0c\u5f20\u79d8\u4e66\u662f\u8c08\u5224\u7ec4\u7684\u7ec4\u957f\uff0c\u5408\u8d44\u65b9\u6848\u5012\u662f\u6bd4\u4e0b\u9762\u4eba\u665a\u770b\u5230\uff0c\u5fc3\u91cc\u81ea\u7136\u6709\u4e9b\u4e0d\u75db\u5feb\u3002\u6211\u770b\u5f20\u79d8\u4e66\u5012\u4e0d\u4f1a\u62d2\u7edd\u5584\u610f\u7684\u6295\u8d44\uff0c\u9999\u96ea\u6d77\u5408\u8d44\u4e00\u4e8b\uff0c\u5e02\u91cc\u5df2\u7ecf\u5c06\u6743\u529b\u4e0b\u653e\u5230\u8c08\u5224\u7ec4\uff0c\u81f3\u4e8e\u6b22\u4e0d\u6b22\u8fce\u5609\u4fe1\u5b9e\u4e1a\u7684\u4ecb\u5165\uff0c\u4e00\u5207\u8fd8\u8981\u770b\u8c08\u5224\u7ec4\u7684\u610f\u89c1\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e0d\u660e\u767d\u5468\u747e\u745c\u4e3a\u4ec0\u4e48\u8981\u628a\u5f20\u94c1\u68ee\u653e\u5230\u706b\u4e0a\u70e4\u3002\u5468\u747e\u745c\u8fd9\u756a\u8bdd\uff0c\u867d\u7136\u5c06\u8d75\u9633\u4e0e\u5979\u81ea\u5df1\u90fd\u6487\u51fa\u5408\u8d44\u8c08\u5224\u4e4b\u5916\uff0c\u5979\u5374\u53ef\u4ee5\u901a\u8fc7\u5f20\u94c1\u68ee\u7ee7\u7eed\u63a7\u5236\u8c08\u5224\u7684\u8fdb\u5ea6\uff0c\u4f46\u662f\u4eca\u5929\u7684\u60c5\u5f62\u5df2\u7ecf\u4e0d\u540c\uff0c\u8c08\u5224\u7ec4\u4e4b\u5185\uff0c\u5f20\u94c1\u68ee\u672c\u65e0\u5a01\u4fe1\uff0c\u53ea\u662f\u4f9d\u4ed7\u5468\u747e\u745c\u7684\u5f3a\u529b\u652f\u6301\uff0c\u4eca\u5929\u8fd9\u4e00\u95f9\uff0c\u53ea\u6015\u8ba9\u4ed6\u6700\u540e\u4e00\u70b9\u5a01\u4fe1\u90fd\u8361\u7136\u65e0\u5b58\uff0c\u8fd8\u4e0e\u5176\u4ed6\u6210\u5458\u641e\u5f97\u52bf\u5982\u6c34\u706b\uff1b\u8c08\u5224\u7ec4\u4e4b\u5916\uff0c\u4ed6\u53ea\u662f\u65e0\u8db3\u8f7b\u91cd\u7684\u5c0f\u4eba\u7269\u800c\u5df2\uff0c\u8981\u518d\u65e0\u7406\u53d6\u95f9\uff0c\u8d75\u9633\u4f1a\u5fc3\u6148\u624b\u8f6f\uff1f\u9664\u975e\u97e9\u56fd\u4e09\u661f\u505a\u51fa\u91cd\u5927\u8ba9\u6b65\uff0c\u63d0\u51fa\u4e0e\u5609\u4fe1\u5b9e\u4e1a\u76f8\u8fd1\u7684\u5408\u8d44\u65b9\u6848\uff0c\u5b9e\u5728\u60f3\u8c61\u4e0d\u51fa\u5f20\u94c1\u68ee\u80fd\u53d1\u6325\u4ec0\u4e48\u4f5c\u7528\u3002</p><p>\u3000\u3000\u65e2\u7136\u5468\u747e\u745c\u8981\u5c06\u5f20\u94c1\u68ee\u653e\u5230\u706b\u4e0a\u70e4\uff0c\u90a3\u4e5f\u7531\u7740\u5979\uff0c\u6bd5\u7adf\u662f\u5979\u7684\u4eba\uff0c\u5979\u81ea\u5df1\u4f1a\u5904\u7406\uff0c\u5f20\u606a\u7ec8\u4ece\u6c99\u53d1\u4e0a\u7ad9\u8d77\u6765\uff0c\u5bf9\u7740\u5468\u747e\u745c\uff0c\u8d75\u9633\u8bf4\u9053\uff1a\u201c\u90a3\u5c31\u597d\uff0c\u8d75\u4e66\u8bb0\u3001\u5468\u5e02\u957f\uff0c\u6211\u76f8\u4fe1\u8c08\u5224\u7ec4\u4f1a\u5fe0\u5b9e\u7684\u7ef4\u62a4\u60e0\u5c71\u5e02\u4e0e\u9999\u96ea\u6d77\u7684\u5229\u76ca\u7684\u3002\u6211\u4eec\uff0c\u4e0d\u4ec5\u9999\u6e2f\u845b\u660e\u5fb7\u5148\u751f\u3001\u5b59\u5c1a\u4e49\u5148\u751f\uff0c\u4ea6\u6709\u53f6\u5bb6\u53f6\u7956\u8303\u8001\u5148\u751f\uff0c\u4e4b\u6240\u4ee5\u9009\u62e9\u652f\u6301\u5609\u4fe1\u5b9e\u4e1a\u4ecb\u5165\u9999\u96ea\u6d77\uff0c\u867d\u7136\u6709\u7a81\u5140\uff0c\u4f46\u6700\u7ec8\u4e4b\u7f18\u7531\uff0c\u8fd8\u662f\u4e0d\u5e0c\u671b\u8fd9\u4e00\u8457\u540d\u7684\u6c11\u65cf\u54c1\u724c\u5c31\u8fd9\u4e48\u8f7b\u6613\u7684\u6b92\u843d\u3002\u6211\u4eec\u5728\u5408\u8d44\u65b9\u6848\u91cc\uff0c\u63d0\u51fa\u4e09\u6761\u6709\u5229\u9999\u96ea\u6d77\u7684\u6761\u4ef6\uff0c\u5176\u4e00\uff0c\u4ee5\u53d1\u5c55\u9999\u96ea\u6d77\u54c1\u724c\u5c79\u7acb\u4e16\u754c\u4f01\u4e1a\u4e4b\u6797\u4e3a\u5df1\u4efb\uff0c\u5609\u4fe1\u5b9e\u4e1a\u627f\u8bfa\u4e0d\u4f1a\u5728\u5173\u8054\u4f01\u4e1a\u53d1\u5c55\u5176\u4ed6\u54c1\u724c\u7684\u767d\u7535\u4ea7\u54c1\uff1b\u5176\u4e8c\uff0c\u9999\u96ea\u6d77\u4e3a\u56fd\u5185\u9a70\u540d\u5546\u6807\uff0c\u5176\u5546\u4e1a\u4ef7\u503c\u4e0d\u5bb9\u5ffd\u89c6\uff0c\u5609\u4fe1\u5b9e\u4e1a\u613f\u610f\u8c08\u5224\u65f6\u5408\u5e76\u8ba1\u5165\u5408\u8d44\u8d44\u4ea7\uff1b\u5176\u4e09\uff0c\u65e0\u8bba\u662f\u73b0\u5728\uff0c\u8fd8\u662f\u5c06\u6765\uff0c\u5609\u4fe1\u5b9e\u4e1a\u5728\u60e0\u5c71\u7684\u6295\u8d44\u989d\u5ea6\u4e0d\u4f1a\u4f4e\u4e8e\u97e9\u56fd\u4e09\u661f\u3002\u9999\u6e2f\u56de\u5f52\u5728\u5373\uff0c\u7531\u6e2f\u8d44\u5165\u80a1\u5408\u8d44\uff0c\u7ec8\u7a76\u662f\u56fd\u4eba\u4e4b\u4f01\u4e1a\uff0c\u60e0\u5c71\u5e02\u653f\u5e9c\u62db\u5546\u5f15\u8d44\u4e4b\u9645\uff0c\u96be\u9053\u4e0d\u8003\u8651\u6276\u6301\u4e00\u4e0b\u6c11\u65cf\u4ea7\u4e1a\uff1f\u201d</p><p>\u3000\u3000\u5468\u747e\u745c\u5634\u89d2\u6d6e\u51fa\u4e00\u4e1d\u51b7\u7b11\uff0c\u6ca1\u6709\u8bf4\u4ec0\u4e48\u3002\u8d75\u9633\u5012\u89c9\u5f97\u5f20\u606a\u5f88\u6709\u6f14\u8bb2\u7684\u624d\u534e\uff0c\u53c8\u9ad8\u4e3e\u6c11\u65cf\u4f01\u4e1a\u632f\u5174\u7684\u5927\u65d7\uff0c\u8ba9\u4eba\u89c9\u5f97\u62d2\u7edd\u4ed6\u4eec\u7684\u5408\u8d44\u65b9\u6848\uff0c\u7b80\u76f4\u5c31\u662f\u6c11\u65cf\u7684\u7f6a\u4eba\uff0c\u53c8\u5c06\u845b\u5bb6\u3001\u53f6\u5bb6\u63a8\u5230\u524d\u9762\uff0c\u5468\u5bb6\u3001\u8c22\u5bb6\u4e0e\u4ed6\u6709\u9699\uff0c\u96be\u9053\u8fd8\u8981\u56e0\u6b64\u7275\u626f\u5230\u845b\u5bb6\u3001\u53f6\u5bb6\u8eab\u4e0a\u53bb\uff1f\u8d75\u9633\u5012\u60f3\u770b\u770b\u5468\u747e\u745c\u4f1a\u5982\u4f55\u7684\u80e1\u6405\u86ee\u7f20\u3002</p><p>\u3000\u3000\u8d75\u9633\u4e5f\u4e0d\u542b\u7cca\uff0c\u5f53\u5373\u8868\u6001\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4ee3\u8868\u60e0\u5c71\u5e02\u6b22\u8fce\u5609\u4fe1\u5b9e\u4e1a\u5230\u60e0\u5c71\u6765\u6295\u8d44\uff0c\u5468\u5e02\u957f\uff0c\u5173\u4e8e\u5408\u8d44\u7684\u4e8b\u60c5\uff0c\u4f60\u4eb2\u81ea\u7ed9\u8c08\u5224\u7ec4\u6253\u58f0\u62db\u547c\uff0c\u8ba9\u5609\u4fe1\u5b9e\u4e1a\u4e0e\u4e09\u661f\u516c\u5e73\u7ade\u4e89\uff0c\u5e02\u91cc\u7edd\u4e0d\u504f\u8892\u54ea\u4e00\u5bb6\uff1f\u201d</p><p>\u3000\u3000\u201c\u516c\u5e73\u7ade\u4e89\u90a3\u662f\u80af\u5b9a\u8981\u7684\uff0c\u201d\u5468\u747e\u745c\u8bf4\u9053\uff1a\u201c\u5609\u4fe1\u5b9e\u4e1a\u63d0\u51fa\u7684\u65b9\u6848\u9887\u4e3a\u52a8\u4eba\uff0c\u4f46\u662f\u4ee5\u5609\u4fe1\u5b9e\u4e1a\u7684\u5b9e\u529b\uff0c\u80fd\u4e0d\u80fd\u8ba9\u8fd9\u4e09\u6761\u9010\u4e00\u5b9e\u73b0\uff0c\u5374\u662f\u8c08\u5224\u7ec4\u8981\u4ed4\u7ec6\u8003\u8861\u7684\u2026\u2026\u201d\u5468\u747e\u745c\u5012\u662f\u4e0d\u7b28\uff0c\u4e00\u4e0b\u5b50\u5c31\u6293\u4f4f\u5609\u4fe1\u5b9e\u4e1a\u6700\u5927\u7684\u5f31\u70b9\uff0c\u201c\u62db\u5546\u5f15\u8d44\uff0c\u4e5f\u4e0d\u7eaf\u7cb9\u662f\u5438\u5f15\u8d44\u91d1\uff0c\u8fd8\u662f\u8981\u501f\u9274\u5b66\u4e60\u5916\u8d44\u4f01\u4e1a\u7684\u5148\u8fdb\u6280\u672f\u4e0e\u7ba1\u7406\u7ecf\u9a8c\uff0c\u4e3a\u4e86\u8fd9\u4e2a\u7f18\u6545\uff0c\u5373\u4f7f\u4ed8\u51fa\u4e9b\u4ee3\u4ef7\uff0c\u4e5f\u662f\u503c\u5f97\u7684\uff0c\u4e0e\u4e09\u661f\u5408\u8d44\uff0c\u9999\u96ea\u6d77\u6216\u8bb8\u6709\u4e9b\u4e0d\u5229\uff0c\u4f46\u662f\u5408\u8d44\u5de5\u5382\u5728\u60e0\u5c71\uff0c\u62db\u5de5\uff0c\u662f\u62db\u60e0\u5c71\u7684\u5e02\u6c11\uff0c\u7a0e\u6536\uff0c\u662f\u5165\u60e0\u5c71\u7684\u8d22\u653f\uff0c\u8fd9\u90fd\u662f\u8c08\u5224\u7ec4\u6240\u8981\u8003\u8651\u7684\u95ee\u9898\uff0c\u800c\u4e0d\u662f\u5634\u5df4\u4e0a\u8bf4\u7684\u52a8\u542c\u3002\u201d</p><p>\u3000\u3000\u8d75\u9633\u8bf4\u9053\uff1a\u201c\u5468\u5e02\u957f\u80fd\u5168\u76d8\u8003\u8651\uff0c\u90a3\u662f\u518d\u597d\u4e0d\u8fc7\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u591a\u8bf4\u4ec0\u4e48\uff0c\u5728\u8fd9\u91cc\u6ca1\u6709\u4ec0\u4e48\u597d\u8fa9\u9a73\u7684\uff0c\u5468\u747e\u745c\u663e\u7136\u4e5f\u4e0d\u60f3\u5728\u8d75\u9633\u7684\u529e\u516c\u5ba4\u591a\u505c\u7559\uff0c\u65cb\u5373\u79bb\u5f00\u3002</p><p>\u3000\u3000\u665a\u4e0a\uff0c\u5728\u60e0\u5c71\u5357\u90ca\u9e21\u9996\u5c71\u4e0a\u7684\u4e00\u5904\u540d\u4e3a\u7d2b\u7af9\u9601\u7684\u5c71\u5e84\u9152\u5e97\u5bb4\u8bf7\u8d75\u9633\uff0c\u539f\u6253\u7b97\u5728\u60e0\u5c71\u5927\u9152\u5e97\u8bf7\u5bb4\uff0c\u8d75\u9633\u8bf4\u60e0\u5c71\u5927\u9152\u5e97\u4eba\u591a\u773c\u6742\uff0c\u4e0d\u5982\u7d2b\u7af9\u9601\u6e05\u9759\u3002\u5f20\u606a\u3001\u53f6\u5efa\u658c\u5230\u60e0\u5c71\u6765\u90fd\u6ca1\u6709\u542c\u8bf4\u8fc7\u4ec0\u4e48\u7d2b\u7af9\u9601\uff0c\u65e2\u7136\u8d75\u9633\u63a8\u8350\uff0c\u7109\u6709\u4e0d\u7ed9\u9762\u5b50\u7684\u9053\u7406\uff1f</p><p>\u3000\u3000\u53bb\u9a71\u8f66\u524d\u5f80\uff0c\u76d8\u5c71\u516c\u8def\u4e0a\u53bb\uff0c\u662f\u5904\u5e7d\u9759\u7684\u6240\u5728\uff0c\u5404\u5904\u75d5\u8ff9\u90fd\u5f88\u65b0\uff0c\u662f\u5bb6\u65b0\u5f00\u6ca1\u6709\u591a\u4e45\u7684\u9152\u5e97\uff0c\u5374\u6ca1\u6709\u6d6e\u6d45\u7684\u611f\u89c9\uff0c\u4f7f\u4eba\u89c9\u5f97\u4e00\u5982\u7d2b\u7af9\u9601\u7684\u603b\u7ecf\u7406\u8d75\u6893\u8eab\u4e0a\u6240\u6563\u53d1\u51fa\u6765\u7684\u6c14\u8d28\uff0c\u8f9b\u6893\u4e0e\u5b59\u9759\u9999\u5e74\u7eaa\u76f8\u4eff\uff0c\u4e0d\u8fc7\u8d75\u6893\u7684\u5bb9\u989c\u66f4\u52a0\u59a9\u8273\u4e00\u4e9b\uff0c\u90fd\u662f\u7f8e\u4e3d\u7684\u5973\u4eba\u3002\u867d\u8bf4\u8bb8\u601d\u7684\u5bb9\u8c8c\u66f4\u52a0\u7cbe\u81f4\u4e00\u4e9b\uff0c\u4f46\u662f\u5979\u4eca\u5929\u8ba9\u5468\u747e\u745c\u7684\u8bdd\u523a\u5230\u5fc3\u91cc\uff0c\u6b64\u65f6\u8fd8\u90c1\u90c1\u96be\u89e3\uff0c\u795e\u8272\u6709\u4e9b\u9eef\u6de1\uff0c\u9759\u9759\u7684\u7ad9\u5728\u5f20\u606a\u7684\u8eab\u8fb9\uff0c\u8bdd\u5f88\u5c11\uff0c\u5f20\u606a\u539f\u6765\u8ba9\u5979\u5148\u56de\u60e0\u5c71\u5927\u9152\u5e97\u4f11\u606f\uff0c\u53ea\u662f\u5979\u89c9\u5f97\u5728\u5f20\u606a\u4f1a\u66f4\u5b89\u5fc3\u4e00\u4e9b\u3002\u5230\u4e86\u8fd9\u5904\u5e7d\u9759\u7684\u6240\u5728\uff0c\u624d\u7a0d\u7a0d\u7f13\u4e00\u53e3\u6c14\uff0c\u4f4e\u58f0\u5bf9\u5f20\u606a\u8bf4\uff1a\u201c\u60e0\u5c71\u5927\u9152\u5e97\u8fc7\u4e8e\u6d6e\u4e3d\uff0c\u8981\u662f\u4f4f\u5728\u8fd9\u91cc\u4fbf\u597d\u4e86\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u8f7b\u8f7b\u7684\u63e1\u4e86\u63e1\u5979\u7684\u624b\u3002</p><p>\u3000\u3000\u7d2b\u7af9\u9601\u4eca\u665a\u6ca1\u4ec0\u4e48\u5ba2\u4eba\uff0c\u542c\u8d75\u6893\u8bf4\u662f\u542c\u5230\u8d75\u9633\u8981\u8fc7\u6765\u5403\u996d\uff0c\u5176\u4ed6\u4eba\u518d\u6253\u7535\u8bdd\u6765\u9884\u7ea6\u65f6\uff0c\u4fbf\u63a8\u8bf4\u5df2\u7ecf\u9884\u7ea6\u6ee1\u4e86\u3002</p><p>\u3000\u3000\u5165\u5e2d\u65f6\uff0c\u8d75\u6893\u4e5f\u5165\u5e2d\u966a\u540c\u3002</p><p>\u3000\u3000\u8d75\u9633\u80fd\u5c06\u4ed6\u4eec\u9886\u5230\u8fd9\u91cc\u6765\u5403\u996d\uff0c\u771f\u662f\u610f\u5916\u4e4b\u559c\uff0c\u4f46\u662f\u8d75\u9633\u4e0e\u5468\u747e\u745c\u5728\u60e0\u5c71\u73a9\u5e73\u8861\u8fd9\u4e48\u4e45\uff0c\u5927\u6982\u4e5f\u4e0d\u4f1a\u4e3a\u4e86\u5408\u8d44\u7684\u4e8b\u8ddf\u5468\u747e\u745c\u76f4\u63a5\u8d77\u51b2\u7a81\uff0c\u8fd9\u4e48\u770b\u6765\uff0c\u5f20\u94c1\u68ee\u8fd8\u771f\u662f\u5f88\u5173\u952e\u7684\u4e00\u4e2a\u4eba\u7269\u3002</p><p>\u3000\u3000\u5e2d\u95f4\uff0c\u5f20\u606a\u95ee\u7d2b\u7af9\u9601\u6709\u6ca1\u6709\u5ba2\u623f\uff0c\u8d75\u9633\u66ff\u8d75\u6893\u56de\u7b54\u8bf4\uff1a\u201c\u4e00\u822c\u4eba\u5012\u662f\u4e0d\u63a8\u8350\u7684\uff0c\u7d2b\u7af9\u9601\u8fd8\u6709\u4e0d\u9519\u7684\u6e29\u6cc9\u2026\u2026\u201d</p><p>\u3000\u3000\u53f6\u5efa\u658c\u7b2c\u4e8c\u5929\u8d77\u65e9\u5c31\u79bb\u5f00\u60e0\u5c71\uff0c\u5b59\u9759\u9999\u4e2d\u5348\u624d\u79bb\u5f00\u60e0\u5c71\u3002\u90a3\u8f86\u94f6\u7070\u8272\u7684Vs40\u867d\u7136\u4e0d\u662f\u5f88\u9ad8\u6863\u7684\u8f66\uff0c\u4f46\u662f\u5728\u60e0\u5c71\u8fd8\u6ca1\u6709\u770b\u5230\u540c\u6837\u7684\u8f66\u578b\uff0c\u592a\u8fc7\u663e\u773c\u4e86\uff0c\u5f20\u606a\u5c31\u6258\u5b59\u9759\u9999\u5c06\u8f66\u5f00\u5230\u7701\u57ce\u53bb\uff0c\u4ed6\u4f1a\u4e0e\u8bb8\u601d\u5728\u60e0\u5c71\u518d\u7559\u51e0\u5929\uff0c\u770b\u770b\u8c08\u5224\u7684\u60c5\u5f62\uff0c\u518d\u51b3\u5b9a\u53bb\u7559\u3002\u9676\u884c\u5065\u3001\u5085\u5bb6\u4fca\u7ee7\u7eed\u4f4f\u5728\u60e0\u5c71\u5927\u9152\u5e97\uff0c\u5f20\u606a\u5219\u4e0e\u8bb8\u601d\u5219\u4f4f\u8fdb\u7d2b\u7af9\u82d1\u91cc\uff0c\u4e00\u5904\u72ec\u7acb\u7684\u7528\u5c3d\u5fc3\u601d\u7684\u5c0f\u9662\u5b50\uff0c\u6e29\u6cc9\u6c34\u76f4\u63a5\u5f15\u5165\u9662\u5b50\u91cc\uff0c\u8fd8\u6258\u8d75\u9633\u7684\u5173\u7cfb\uff0c\u501f\u4e86\u7d2b\u7af9\u9601\u7684\u4e00\u8f86\u8f66\u7528\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u56db\u6708\u5341\u516b\u65e5\uff0c\u5728\u5404\u65b9\u9762\u7684\u63a8\u52a8\u4e4b\u4e0b\uff0c\u65b0\u5149\u7eb8\u4e1a\u4e0e\u7701\u6797\u4e1a\u5385\u3001\u7701\u56fd\u6709\u6797\u573a\u7ba1\u7406\u5c40\u7b7e\u7f72\u4e00\u7cfb\u5217\u7684\u5408\u4f5c\u534f\u8bae\uff0c\u65b0\u5149\u7eb8\u4e1a\u76f4\u63a5\u589e\u8d44\u63a7\u80a1\u539f\u56fd\u6709\u6797\u573a\u7ba1\u7406\u5c40\u6240\u5c5e\u7684\u79cd\u82d7\u57fa\u5730\uff0c\u5f52\u7701\u56fd\u6709\u6797\u573a\u7ba1\u7406\u5c40\u76f4\u63a5\u7ba1\u8f96\u7684\u516d\u5bb6\u56fd\u6709\u6797\u573a\uff0c\u4ee5\u6797\u5730\u4f7f\u7528\u6743\u5165\u80a1\u3001\u65b0\u5149\u7eb8\u4e1a\u4ee5\u73b0\u91d1\u5165\u80a1\uff0c\u5408\u8d44\u7ec4\u5efa\u8425\u6797\u516c\u53f8\uff0c\u8fd9\u4e00\u5408\u4f5c\u6a21\u5f0f\u5c06\u8fc5\u901f\u590d\u5236\u5230\u5e02\u53bf\u4e24\u7ea7\u7684\u56fd\u6709\u6797\u573a\uff0c\u65b0\u5149\u7eb8\u4e1a\u5c06\u5728\u4e24\u5e74\u65f6\u95f4\u91cc\u5411\u8fd9\u4e9b\u5408\u8d44\u5206\u516c\u53f8\u6295\u5165\u603b\u989d\u8fbe\u4e24\u4e2a\u4ebf\u7684\u8d44\u91d1\uff0c\u914d\u5408\u7701\u91cc\u7684\u516c\u76ca\u6797\u79cd\u690d\u8ba1\u5212\uff0c\u4e89\u53d6\u5728\u4e5d\u516b\u5e74\u4e4b\u524d\u6d88\u9664\u56fd\u6709\u6797\u573a\u5185\u7684\u5b9c\u6797\u8352\u5c71\u3002\u8425\u6797\u516c\u53f8\u5c06\u5438\u7eb3\u7701\u68ee\u5de5\u4f01\u4e1a\u5206\u6d41\u4e0b\u5c97\u7684\u6797\u4e1a\u5de5\u4eba\uff0c\u4ee5\u56db\u5341\u4e07\u4ea9\u7684\u8425\u6797\u89c4\u6a21\uff0c\u53ef\u4ee5\u5438\u7eb3\u4e24\u4e09\u5343\u540d\u6797\u4e1a\u5de5\u4eba\u3002\u8fd9\u8fd8\u4ec5\u4ec5\u662f\u7b2c\u4e00\u6b65\uff0c\u65b0\u5149\u7eb8\u4e1a\u4e0e\u7701\u6797\u4e1a\u5385\u8054\u5408\u5411\u5916\u516c\u544a\uff0c\u65b0\u5149\u7eb8\u4e1a\u63a5\u4e0b\u6765\u8fd8\u5c06\u4e0e\u5c0f\u6c5f\u4e2d\u4e0a\u6e38\u5730\u533a\u7684\u5404\u5730\u653f\u5e9c\u4e0e\u6797\u4e1a\u5c40\u5408\u4f5c\uff0c\u4e24\u5e74\u89c4\u5212\u5185\u7684\u8425\u6797\u9762\u79ef\u66f4\u662f\u9ad8\u8fbe\u767e\u4e07\u4ea9\u4e4b\u591a\uff0c\u5e76\u5728\u4e2d\u4e0a\u6e38\u5730\u533a\u6797\u6e90\u5730\u9644\u8fd1\u62e9\u5730\u7b79\u5efa\u56fd\u5185\u6700\u5927\u7684\u6728\u6d46\u751f\u4ea7\u57fa\u5730\u3002</p><p>\u3000\u3000\u4e09\u6708\u521d\uff0c\u7701\u91cc\u81ea\u66b4\u6797\u4e1a\u7cfb\u7edf\u6570\u5b57\u9020\u5047\u5927\u6848\uff0c\u5f15\u8d77\u6781\u5927\u7684\u9707\u52a8\uff0c\u7701\u6797\u4e1a\u5385\u3001\u5404\u5730\u6797\u4e1a\u5c40\u5b98\u5458\u8c03\u6574\u7684\u52a8\u4f5c\u4e4b\u5927\uff0c\u662f\u4e1c\u6d77\u7701\u5386\u6765\u5c11\u89c1\uff0c\u81f3\u5c11\u8ba9\u539f\u6797\u4e1a\u7cfb\u7edf\u5185\u5b58\u5728\u7684\u90a3\u5c42\u5229\u76ca\u94fe\u6761\u5f7b\u5e95\u7684\u652f\u79bb\u7834\u788e\uff0c\u800c\u4e14\u7701\u7eaa\u68c0\u90e8\u95e8\u53c8\u7740\u91cd\u67e5\u5904\u6797\u4e1a\u7cfb\u7edf\u5185\u7684\u8d2a\u6c61\u8150\u8d25\u6848\u5b50\uff0c\u8c01\u4e5f\u4e0d\u6653\u5f97\u4f1a\u626f\u51fa\u591a\u5927\u98ce\u6ce2\u3002\u7701\u5e02\u4e24\u7ea7\u77e5\u5230\u5185\u5e55\u7684\u5b98\u5458\uff0c\u81ea\u7136\u77e5\u9053\u65b0\u5149\u7eb8\u4e1a\u6b64\u65f6\u7684\u52a8\u4f5c\uff0c\u6709\u4e3a\u7701\u6797\u4e1a\u7cfb\u7edf\u5f25\u6f0f\u8865\u7f3a\u7684\u610f\u5473\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u542c\u5230\u8fd9\u4e2a\u6d88\u606f\uff0c\u5b8c\u5168\u4e0d\u6e05\u695a\u5f20\u606a\u51fa\u4e8e\u600e\u4e48\u7684\u5224\u65ad\u624d\u8fc8\u51fa\u5982\u6b64\u5927\u7684\u6b65\u4f10\uff0c\u5c31\u7b97\u4e3a\u5f90\u5b66\u5e73\u7684\u653f\u7ee9\u8003\u7ee9\uff0c\u4e5f\u6ca1\u6709\u5fc5\u987b\u62ff\u51e0\u4ebf\u3001\u51e0\u5341\u4ebf\u7684\u8d44\u91d1\u7838\u8fdb\u53bb\uff0c\u5c31\u7b97\u8981\u4e0a\u6797\u7eb8\u4e00\u4f53\u5316\u9879\u76ee\uff0c\u4ee5\u65b0\u5149\u7eb8\u4e1a\u7684\u9020\u7eb8\u4ea7\u80fd\uff0c\u4e5f\u53ea\u9700\u8981\u4e8c\u4e09\u5341\u4e07\u4ea9\u7684\u6797\u5730\u5c31\u8db3\u591f\u4e86\u3002</p><p>\u3000\u3000\u5c31\u7b97\u4e09\u6708\u521d\u4e0d\u4ece\u9526\u6e56\u64a4\u80a1\uff0c\u5c31\u65f6\u5019\u4e5f\u4f1a\u7ed9\u903c\u7740\u4ece\u9526\u6e56\u64a4\u80a1\u7684\uff0c\u8c22\u5251\u5357\u7b2c\u4e00\u6b21\u770b\u4e0d\u900f\u5f20\u606a\u7684\u610f\u56fe\u3002</p></article>"}}}]);
