(window.webpackJsonp=window.webpackJsonp||[]).push([[489],{512:function(p){p.exports={chapterTitle:null,title:"\u7b2c489\u7ae0 \u79d1\u7814\u5b9e\u9a8c\u57fa\u5730",content:"<article><h2>\u7b2c489\u7ae0 \u79d1\u7814\u5b9e\u9a8c\u57fa\u5730</h2><p>\u3000\u3000\u65b0\u79d1\u7535\u5b50\u6b64\u65f6\u6709\u5982\u6ca1\u5934\u7684\u82cd\u8747\u3002</p><p>\u3000\u3000\u65af\u9ad8\u67cf\u3001TI\u4e0e\u98de\u5229\u6d66\u3001\u7231\u8fbe\u8054\u5408\u5f00\u53d1\u8d85\u7ea7VCD\uff0c\u663e\u7136\u662f\u5c06\u7d22\u5c3c\u8e22\u51fa\u5c40\u53bb\uff0c\u65b0\u79d1\u7535\u5b50\u4e00\u76f4\u4ee5\u6765\u90fd\u5927\u89c4\u6a21\u7684\u91c7\u7528\u7d22\u5c3c\u7684\u673a\u82af\uff0c\u4e5f\u5c06\u53d7\u5230\u6781\u5927\u7684\u5f71\u54cd\u3002\u5f53\u521d\u5c31\u662f\u4e3a\u4e86\u6446\u8131\u98de\u5229\u6d66\u5bf9\u673a\u82af\u7684\u5784\u65ad\u63a7\u5236\uff0c\u624d\u8f6c\u5411\u4e0e\u7d22\u5c3c\u5408\u4f5c\uff1b\u4e3a\u4e86\u6446\u8131\u65af\u9ad8\u67cf\u3001TI\u5bf9\u89e3\u7801\u677f\u7684\u5784\u65ad\u63a7\u5236\uff0c\u624d\u8f6c\u5411\u652f\u6301ESS\u72ec\u7acb\u5f00\u53d1\u65b0\u7684\u89e3\u7801\u82af\u7247\uff0c\u773c\u4e0b\u8fd9\u6240\u6709\u7684\u52aa\u529b\u90fd\u82b1\u4e3a\u6ce1\u5f71\uff0c\u8fd8\u8981\u8003\u8651\u5230\u53ef\u80fd\u6765\u81ea\u65af\u9ad8\u67cf\u3001TI\u4e0e\u98de\u5229\u6d66\u7684\u8054\u5408\u5c01\u6740\u3002</p><p>\u3000\u3000\u5df2\u7ecf\u6ca1\u6709\u591a\u4f59\u7684\u7cbe\u529b\u53bb\u5173\u5fc3\u6210\u6c49\u748b\u3001\u8096\u664b\u6210\u4e24\u4eba\u4e86\uff0c\u6216\u8bb8\u65e0\u4eba\u5fcd\u5fc3\u9762\u5bf9\u53d7\u632b\u66f4\u51c4\u60e8\u7684ESS\uff0c\u65b0\u79d1\u7535\u5b50\u4f3c\u4e4e\u60f3\u523b\u610f\u5fd8\u6389ESS\u7684\u5b58\u5728\uff0c\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u65b0\u79d1\u4e5f\u5f80\u8fd9\u4e2a\u9879\u76ee\u91cc\u6295\u5165\u4e8c\u5341\u4e07\u7f8e\u5143\u7684\u8d44\u91d1\u3002</p><p>\u3000\u3000\u4ece\u5f88\u591a\u6e20\u9053\u90fd\u80fd\u67e5\u5230\u6210\u6c49\u748b\u3001\u8096\u664b\u6210\u6240\u4e58\u7684\u822a\u73ed\uff0c\u786e\u8ba4\u6210\u6c49\u748b\u3001\u8096\u664b\u6210\u4e24\u4eba\u6765\u5185\u5730\u7684\u822a\u73ed\u4e4b\u540e\uff0c\u4e01\u69d0\u5c31\u5e26\u7740\u53f8\u673a\u8d76\u5230\u5efa\u90ba\u51c6\u5907\u5c06\u4eba\u4ece\u65b0\u79d1\u7535\u5b50\u624b\u91cc\u7ed9\u622a\u4e0b\u6765\uff0c\u6ca1\u60f3\u5230\u5230\u673a\u573a\u540e\u6ca1\u6709\u770b\u5230\u65b0\u79d1\u7535\u5b50\u7684\u4eba\uff0c\u4ed6\u4eec\u53c8\u6ca1\u6709\u89c1\u8fc7\u6210\u6c49\u748b\u3001\u8096\u664b\u6210\u957f\u4ec0\u4e48\u6a21\u6837\uff0c\u6025\u7740\u627e\u6765\u4e00\u53ea\u7eb8\u7bb1\u5b50\uff0c\u6495\u4e0b\u4e00\u7247\u7eb8\u677f\u5199\u4e0b\u6210\u6c49\u748b\u7684\u540d\u5b57\u3002</p><p>\u3000\u3000\u6210\u6c49\u748b\u3001\u8096\u664b\u6210\u6ca1\u6709\u542c\u8bf4\u8fc7\u9526\u6e56\u7535\u5b50\u6280\u672f\u7814\u7a76\u9662\u7684\u540d\u5b57\uff0c\u4f46\u662f\u7231\u8fbe\u7535\u5b50\u7edd\u4e0d\u4f1a\u964c\u751f\uff0c\u4ed6\u4eec\u8fd8\u4e0d\u77e5\u9053\u5185\u60c5\uff0c\u4f46\u662f\u4ece\u516c\u5f00\u7684\u8d44\u6599\u6765\u770b\uff0c\u7231\u8fbe\u7535\u5b50\u5373\u4f7f\u4e0d\u662f\u4e3b\u8981\u7684\u7f6a\u9b41\u7978\u9996\uff0c\u4e5f\u662f\u91cd\u8981\u7684\u5e2e\u51f6\uff0c\u4e0d\u8fc7\u5728\u6280\u672f\u5f00\u53d1\u9886\u57df\uff0c\u4e5f\u8c08\u4e0d\u4e0a\u4ec0\u4e48\u5bbf\u6028\uff0c\u4f5c\u4e3a\u56fd\u5185\u7684\u4f01\u4e1a\uff0c\u4e5f\u505a\u5230\u8fd9\u4e00\u6b65\uff0c\u76f8\u53cd\u4f1a\u8ba9\u4eba\u656c\u4f69\uff0c\u867d\u7136ESS\u7684\u7ed3\u5c40\u51c4\u60e8\u4e86\u4e9b\u3002</p><p>\u3000\u3000\u6210\u6c49\u748b\u3001\u8096\u664b\u6210\u4e0d\u660e\u767d\u4e01\u69d0\u7684\u6765\u610f\uff0c\u8fd8\u662f\u5f88\u6709\u98ce\u5ea6\u7684\u4e0e\u4ed6\u63e1\u624b\uff0c\u4e01\u69d0\u9080\u8bf7\u4ed6\u4eec\u5230\u6d77\u5dde\u53bb\uff0c\u4ed6\u4eec\u6ca1\u770b\u5230\u65b0\u79d1\u7535\u5b50\u6709\u4eba\u8fc7\u6765\uff0c\u5fc3\u60f3\u6b64\u884c\u7684\u76ee\u7684\u5730\u4e5f\u6b63\u662f\u8981\u53bb\u6d77\u5dde\u770b\u8fd8\u6709\u6ca1\u6709ESS\u751f\u5b58\u7684\u673a\u4f1a\uff0c\u4fbf\u540c\u610f\u4e01\u69d0\u7684\u9080\u8bf7\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u30001\u670813\u65e5\uff0c\u65b0\u79d1\u3001\u6b65\u6b65\u9ad8\u3001\u7231\u591a\u3001\u4e07\u5229\u8fbe\u3001\u91d1\u6b63\u7b49\u789f\u673a\u6570\u5341\u5bb6\u5382\u5546\u4ee3\u8868\u90fd\u9f50\u805a\u6d77\u5dde\uff0c\u4e89\u53d6\u53c2\u52a0\u65b0\u789f\u673a\u6d4b\u8bd5\u7684\u540d\u989d\u3002</p><p>\u3000\u3000\u6d77\u5dde\u5e02\u7684\u4e2d\u5c0f\u789f\u673a\u4f01\u4e1a\u4e5f\u70ed\u95f9\u6210\u4e00\u9505\u6cb8\u7ca5\uff0c\u65b0\u5143\u7535\u5668\u8001\u603b\u4e01\u6587\u7965\u7275\u5934\u8054\u7edc\u8fd9\u4e9b\u4e2d\u5c0f\u789f\u673a\u4f01\u4e1a\u4e2d\u5728\u5404\u65b9\u9762\u90fd\u8f83\u4e3a\u51fa\u8272\u7684\u516d\u5bb6\u4f01\u4e1a\u51c6\u5907\u8054\u8425\u5e76\u80a1\uff0c\u5171\u540c\u62a2\u53d6\u9884\u7559\u7ed9\u6d77\u5dde\u672c\u5730\u4f01\u4e1a\u90a3\u4e00\u4e2a\u6d4b\u8bd5\u540d\u989d\u3002</p><p>\u3000\u3000\u8fd9\u516d\u5bb6\u789f\u673a\u4f01\u4e1a\u5176\u5de5\u5382\u76f8\u90bb\u3001\u90fd\u5efa\u6210\u76ca\u9686\u9547\u7535\u5b50\u5de5\u4e1a\u533a\u7684\u5317\u7247\uff0c\u6709\u4e24\u5bb6\u4f01\u4e1a\u4e0e\u65b0\u5143\u7535\u5668\u4e00\u6837\uff0c\u90fd\u662f\u7231\u8fbe\u7535\u5b50\u7684\u96f6\u90e8\u4ef6\u4f9b\u5e94\u5546\uff1b\u5f53\u7136\uff0c\u5f53\u4ed6\u4eec\u6d89\u8db3\u789f\u673a\u4ea7\u4e1a\u4e4b\u540e\uff0c\u7231\u8fbe\u7535\u5b50\u4e5f\u76f8\u5e94\u51cf\u5c11\u5728\u4ed6\u4eec\u90a3\u91cc\u7684\u96f6\u90e8\u4ef6\u91c7\u8d2d\u91cf\u3002\u4e0d\u7ba1\u600e\u4e48\u6765\u8bf4\uff0c\u8fd9\u51e0\u5bb6\u4f01\u4e1a\u8054\u8425\u5e76\u80a1\uff0c\u786e\u5b9e\u6709\u8bb8\u591a\u5148\u5929\u4e0a\u7684\u4f18\u52bf\u3002</p><p>\u3000\u3000\u4e01\u6587\u7965\u8fd9\u4e24\u5929\u4e5f\u591f\u5fd9\u4e71\u7684\uff0c\u4e0d\u4ec5\u8981\u534f\u8c03\u3001\u5546\u8ba8\u4e03\u5bb6\u789f\u673a\u4f01\u4e1a\u8054\u8425\u5e76\u8d2d\u3001\u8d44\u4ea7\u91cd\u7ec4\u3001\u8d44\u6e90\u91cd\u5408\u7684\u4e8b\u60c5\uff0c\u8fd8\u8981\u5bf9\u7231\u8fbe\u7535\u5b50\u5c55\u5f00\u516c\u5173\u653b\u52bf\u3002</p><p>\u3000\u3000\u5176\u5b9e\u76f8\u5173\u7684\u8c03\u8bd5\u5de5\u4f5c\uff0c\u9526\u6e56\u7535\u5b50\u6280\u672f\u7814\u7a76\u9662\u3001\u6cf0\u5fd2\u5149\u7535\u7814\u7a76\u9662\u3001\u7231\u8fbe\u7535\u5b50\u7814\u53d1\u4e2d\u5fc3\u90fd\u62bd\u8c03\u6280\u672f\u529b\u91cf\u4e0e\u65af\u9ad8\u67cf\u3001TI\u3001\u98de\u5229\u6d66\u3001\u660e\u4fca\u7535\u5b50\u7684\u6280\u672f\u4eba\u5458\u4e00\u76f4\u90fd\u5728\u4e0d\u677e\u61c8\u7684\u8fdb\u884c\u4e2d\u3002\u641e\u51fa\u8fd9\u4e48\u5927\u7684\u52a8\u9759\uff0c\u4e5f\u662f\u5f20\u606a\u4e3a\u4e86\u589e\u52a0\u7231\u8fbe\u7535\u5b50\u7684\u5f71\u54cd\u529b\uff1b\u5bf9\u4e8e\u65af\u9ad8\u67cf\u3001TI\u7b49\u516c\u53f8\u6765\u8bf4\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u635f\u5931\uff0c\u4e50\u4e8e\u914d\u5408\u3002</p><p>\u3000\u3000\u8fd9\u79cd\u8f6f\u5e7f\u544a\uff0c\u5728\u516c\u4f17\u5fc3\u4e2d\u7ad6\u7acb\u7231\u8fbe\u7535\u5b50\u6280\u672f\u9886\u5148\u7684\u826f\u597d\u5f62\u8c61\uff0c\u8981\u6bd4\u786c\u5e7f\u544a\u66f4\u6709\u4f5c\u7528\u3002\u4e00\u822c\u8bf4\u6765\uff0c\u786c\u5e7f\u544a\u66f4\u591a\u7684\u662f\u63d0\u9ad8\u54c1\u724c\u6216\u4f01\u4e1a\u7684\u77e5\u540d\u5ea6\uff0c\u8981\u63d0\u9ad8\u7f8e\u8a89\u5ea6\uff0c\u5219\u9700\u8981\u6301\u7eed\u4e0d\u65ad\u7684\u52aa\u529b\u3002\u7231\u8fbe\u5728\u56fd\u5185\u7684\u77e5\u540d\u5ea6\u5df2\u7ecf\u63d0\u9ad8\u5230\u4e00\u4e2a\u76f8\u5f53\u7684\u6c34\u51c6\u4e4b\u4e0a\uff0c\u63d0\u9ad8\u7f8e\u8a89\u5ea6\uff0c\u4e5f\u662f\u7231\u8fbe\u7535\u5b50\u5e02\u573a\u90e8\u5f53\u524d\u7684\u4e3b\u8981\u52aa\u529b\u65b9\u5411\u3002\u4e0d\u8fc7\u5f20\u606a\u5bf9\u7231\u8fbe\u7684\u54c1\u724c\u8fdb\u884c\u4e86\u5206\u62c6\uff0c\u76ee\u524d\u7231\u8fbe\u7535\u5b50\u5546\u6807\u4e4b\u4e0b\u53ea\u6709\u789f\u673a\u7c7b\u4ea7\u54c1\u7684\u51a0\u540d\u6743\u5c5e\u4e8e\u7231\u8fbe\u7535\u5b50\uff0c\u5176\u4f59\u4e00\u6982\u5f52\u4e8e\u9526\u6e56\u7535\u5b50\u6280\u672f\u7814\u7a76\u9662\uff0c\u6240\u4ee5\u5728\u5e02\u573a\u5ba3\u4f20\u53d1\u751f\u7684\u8d39\u7528\u4e0a\uff0c\u54c1\u724c\u5ba3\u4f20\u7684\u8d39\u7528\u4ece\u7814\u7a76\u9662\u7684\u8d26\u4e0a\u8d70\uff0c\u529f\u80fd\u6027\u7684\u3001\u5177\u4f53\u4ea7\u54c1\u7684\u5e02\u573a\u5ba3\u4f20\u8d39\u7528\u624d\u4ece\u7231\u8fbe\u7535\u5b50\u7684\u8d26\u4e0a\u8d70\uff0c\u53ea\u6709\u8fdb\u884c\u8fd9\u6837\u7684\u8d22\u52a1\u5212\u6e05\u4e4b\u540e\uff0c\u624d\u80fd\u987a\u5229\u7684\u5c06\u789f\u673a\u4e1a\u52a1\u6253\u5305\u5356\u7ed9\u5609\u4fe1\u5b9e\u4e1a\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u8fd9\u4e00\u5929\uff0c\u5f20\u606a\u6709\u4e24\u95e8\u4f1a\u8003\u79d1\u76ee\u7684\u8003\u8bd5\u8981\u53c2\u52a0\uff0c\u674e\u4e49\u6c5f\u5728\u7701\u59d4\u7ec4\u7ec7\u90e8\u4e8c\u5904\u526f\u5904\u957f\u7684\u966a\u540c\u4e0b\u6b63\u5f0f\u5411\u6d77\u5dde\u5e02\u59d4\u62a5\u9053\uff0c\u7b80\u5355\u7684\u6b22\u8fce\u4eea\u5f0f\u8fc7\u540e\uff0c\u5c31\u7531\u5e02\u59d4\u7ec4\u7ec7\u90e8\u6768\u65ed\u5347\u3001\u5e02\u59d4\u5e38\u59d4\u3001\u5e38\u59d4\u526f\u5e02\u957f\u5b8b\u57f9\u660e\u966a\u540c\u4e0b\uff0c\u76f4\u63a5\u5230\u57ce\u5357\u533a\u653f\u5e9c\u5c65\u4efb\uff0c\u4e0e\u5df2\u7ecf\u8d74\u4efb\u7684\u533a\u59d4\u4e66\u8bb0\u6234\u6587\u8fdb\u642d\u73ed\u5b50\u3002</p><p>\u3000\u3000\u665a\u4e0a\uff0c\u5f20\u606a\u3001\u5b8b\u57f9\u660e\u3001\u675c\u5c0f\u5c71\u3001\u8bb8\u745e\u5e73\u7b49\u4eba\u8981\u4e3a\u674e\u4e49\u6c5f\u63a5\u98ce\u6d17\u5c18\uff0c\u8fd9\u65f6\u5019\uff0c\u4ed6\u4eec\u8fd8\u4e0d\u77e5\u9053\u91d1\u56fd\u6d77\u5df2\u7ecf\u7ed9\u5185\u5b9a\u6210\u65b0\u4e00\u4efb\u7701\u516c\u5b89\u5385\u5385\u957f\u3001\u7701\u653f\u6cd5\u59d4\u526f\u4e66\u8bb0\u7684\u4eba\u9009\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u7231\u8fbe\u7535\u5b50\u7684\u8d44\u4ea7\u8bc4\u6d4b\u3001\u7ba1\u7406\u6539\u5584\u7b49\u5de5\u4f5c\u4e5f\u6b63\u6709\u6761\u4e0d\u7d0a\u7684\u8fdb\u884c\u7740\u3002</p><p>\u3000\u3000\u8bf4\u8d77\u6765\uff0c\u5f20\u606a\u8981\u5904\u7406\u7684\u5927\u4e8b\u60c5\u591f\u591a\u4e86\uff0c\u4e0d\u8fc7\u4ed6\u4ece\u8003\u573a\u51fa\u6765\uff0c\u6700\u5148\u505a\u7684\u4e8b\u60c5\uff0c\u5c31\u662f\u7ed9\u5510\u5a67\u62c9\u5230\u64cd\u573a\u4e0a\u5bf9\u8003\u5377\u7684\u7b54\u6848\u3002</p><p>\u3000\u3000\u9ad8\u4e09\u7684\u8fd9\u6b21\u4f1a\u8003\u79d1\u76ee\u4e0e\u9ad8\u8003\u79d1\u76ee\u76f8\u540c\uff0c\u5bf9\u4e8e\u4e00\u4e2d\u7684\u666e\u901a\u5b66\u751f\u6765\u8bf4\uff0c\u4e0d\u5b58\u5728\u4e00\u70b9\u70b9\u7684\u96be\u5ea6\uff0c\u4f46\u662f\u5bf9\u5f20\u606a\u6765\u8bf4\u5374\u4e0d\u540c\u7684\uff0c\u7279\u522b\u662f\u6570\u5b66\u3002</p><p>\u3000\u3000\u5929\u6c14\u5c1a\u597d\uff0c\u8fd9\u51e0\u5929\u6c14\u6e29\u6709\u4e9b\u56de\u6696\uff0c\u91cd\u65b0\u56de\u5230\u96f6\u5ea6\u4ee5\u4e0a\uff0c\u6ca1\u6709\u98ce\uff0c\u706b\u7ea2\u7684\u5915\u9633\u8fd8\u60ac\u5728\u534a\u7a7a\u4e2d\uff0c\u4eba\u5750\u5728\u64cd\u573a\u7684\u8349\u576a\u4e0a\uff0c\u611f\u89c9\u8fd8\u7b97\u8212\u5766\u3002\u5f20\u606a\u81ea\u7136\u81ea\u4fe1\u6ee1\u6ee1\u7684\u8bf4\u6570\u5b66\u79d1\u76ee\u7684\u4f1a\u8003\u6ca1\u6709\u95ee\u9898\uff0c\u5510\u5a67\u5374\u6df1\u8868\u6000\u7591\uff0c\u6628\u5929\u62ff\u6a21\u62df\u8bd5\u5377\u7ed9\u5f20\u606a\u505a\uff0c\u4e5f\u5c31\u5728\u53ca\u683c\u7ebf\u4e0a\u5f98\u5f8a\u3002\u4e24\u4eba\u5750\u5728\u8349\u576a\u4e0a\u5bf9\u5b8c\u7b54\u6848\uff0c\u53d1\u73b0\u8981\u662f\u5012\u9709\u9047\u5230\u4e00\u4e2a\u4e25\u683c\u7684\u8bc4\u5377\u8001\u5e08\uff0c\u90a3\u5c31\u7b49\u7740\u8865\u8003\u5427\u3002</p><p>\u3000\u3000\u201c\u4e07\u4e00\u5168\u6821\u5c31\u4f60\u4e00\u4e2a\u4eba\u53c2\u52a0\u8865\u8003\uff0c\u4f1a\u4e0d\u4f1a\u5f88\u96be\u8fc7\uff1f\u201d\u5510\u5a67\u6709\u4e9b\u53ef\u601c\u516e\u516e\u7684\u770b\u7740\u5f20\u606a\u3002</p><p>\u3000\u3000\u201c\u73b0\u5728\u5c31\u5f88\u96be\u8fc7\u4e86\uff0c\u201d\u5f20\u606a\u6cae\u4e27\u7740\u8138\uff0c\u201c\u82b1\u4e86\u597d\u4e9b\u5929\u590d\u4e60\u6765\u7740\uff0c\u8981\u4e0d\u4f60\u5728\u8fd9\u91cc\u4eb2\u6211\u4e00\u4e0b\uff0c\u8ba9\u6211\u5fc3\u60c5\u597d\u8fc7\u4e00\u4e9b\u3002\u201d</p><p>\u3000\u3000\u201c\u624d\u4e0d\u4fe1\u4f60\u5462\uff0c\u201d\u5510\u5a67\u54ac\u7740\u5634\u5507\uff0c\u51dd\u7738\u770b\u7740\u5f20\u606a\uff0c\u201c\u4f60\u5728\u9999\u6e2f\u5446\u4e86\u90a3\u4e48\u591a\u5929\uff0c\u8003\u4e0d\u8fc7\u90fd\u662f\u4f60\u81ea\u5df1\u6d3b\u8be5\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u563f\u7136\u7b11\u4e86\u7b11\uff0c\u8fd9\u4e9b\u5929\u624b\u5934\u7684\u4e8b\u60c5\u4e5f\u7684\u786e\u591a\u4e86\u4e9b\uff0c\u8fde\u65b0\u829c\u7684\u8001\u7239\u8001\u5a18\u90fd\u6ca1\u7a7a\u642d\u7406\uff0c\u4fbf\u8bf4\u5bd2\u5047\u91cc\u4e0e\u5510\u5a67\u4e00\u8d77\u53bb\u65b0\u829c\u4f4f\u51e0\u5929\uff0c\u7136\u540e\u518d\u4e0e\u665a\u6674\u3001\u82b7\u5f64\u4e00\u8d77\u53bb\u5317\u4eac\u966a\u5f90\u5b66\u5e73\u8fc7\u6625\u8282\u3002\u5982\u6b64\u4e00\u6765\uff0c\u5510\u5a67\u5fc3\u91cc\u7684\u90a3\u70b9\u6028\u6c14\u4e5f\u4e00\u6d88\u800c\u7a7a\uff0c\u4e0d\u8fc7\u53c8\u62c5\u5fc3\u8d77\u5979\u5988\u4e0d\u540c\u610f\u5979\u8ddf\u7740\u5f20\u606a\u5728\u5916\u6d41\u6d6a\u8fd9\u4e48\u591a\u5929\uff0c\u771f\u662f\u6101\u715e\u4eba\u3002</p><p>\u3000\u3000\u591c\u91cc\u5148\u5230\u897f\u57ce\u996d\u5e97\u7ed9\u674e\u4e49\u6c5f\u63a5\u98ce\uff0c\u674e\u4e49\u6c5f\u53ea\u8eab\u6765\u6d77\u5dde\u8d74\u4efb\uff0c\u751f\u6d3b\u8d77\u5c45\u6682\u65f6\u5b89\u6392\u5728\u57ce\u5357\u533a\u653f\u5e9c\u81ea\u5df1\u7684\u62db\u5f85\u6240\u91cc\uff0c\u603b\u4e0d\u4f1a\u59d4\u5c48\u4e86\u4ed6\u3002</p><p>\u3000\u3000\u5403\u8fc7\u665a\u996d\uff0c\u5f20\u606a\u8fd8\u6709\u522b\u7684\u4e8b\u60c5\uff0c\u4ed6\u7279\u610f\u5c06\u4ed6\u5c0f\u53d4\u5f20\u77e5\u975e\u62c9\u8fc7\u6765\uff0c\u63a5\u4e0b\u6765\u8981\u6709\u4ec0\u4e48\u6d3b\u52a8\u5c31\u7531\u5c0f\u53d4\u4e0e\u8bb8\u745e\u5e73\u8d1f\u8d23\u966a\u540c\uff0c\u675c\u5c0f\u5c71\u3001\u5b8b\u57f9\u660e\u4e5f\u662f\u5403\u8fc7\u996d\u5c31\u8d70\u3002</p><p>\u3000\u3000\u597d\u51e0\u62e8\u4eba\u5728\u6d77\u5dde\uff0c\u5c31\u50cf\u9648\u4fe1\u751f\u3001\u827e\u9ed8\u3001\u5f20\u4e9a\u5e73\u3001\u8d75\u6709\u4f26\u7b49\u4eba\u90fd\u662f\u91cd\u91cf\u7ea7\u7684\uff0c\u5373\u4f7f\u5b59\u5c1a\u4e49\u3001\u6768\u5f8b\u9526\u7b49\u4eba\uff0c\u5206\u91cf\u4e5f\u4e0d\u8f7b\uff0c\u597d\u5728\u4ed6\u4eec\u7406\u89e3\u8fd9\u8fb9\u7684\u5fd9\u788c\uff0c\u5728\u6d77\u5dde\u65f6\u95f4\u957f\u4e86\uff0c\u4e5f\u4e0d\u9700\u8981\u7231\u8fbe\u6d3e\u4eba\u966a\u540c\u3002\u90a3\u4e9b\u95fb\u8baf\u800c\u6765\u540c\u884c\u789f\u673a\u5382\u5546\u4ee3\u8868\u4eec\uff0c\u7231\u8fbe\u4e5f\u8981\u505a\u597d\u62db\u5f85\u5de5\u4f5c\uff0c\u518d\u8bf4\u4ed6\u4eec\u4e5f\u4f1a\u79c1\u4e0b\u91cc\u60f3\u529e\u6cd5\u8ddf\u9648\u4fe1\u751f\u3001\u827e\u9ed8\u3001\u5f20\u4e9a\u5e73\u3001\u8d75\u6709\u4f26\u7b49\u4eba\u63a5\u89e6\uff0c\u81f3\u5c11\u5728\u4ed6\u4eec\u770b\u6765\uff0c\u6d4b\u8bd5\u540d\u989d\u7684\u843d\u5b9e\uff0c\u9648\u4fe1\u751f\u4e0e\u827e\u9ed8\u4e24\u4eba\u66f4\u6709\u51b3\u5b9a\u6743\u3002</p><p>\u3000\u3000\u8fd9\u4e9b\u4eba\u6682\u65f6\u4e0d\u53bb\u7ba1\u4ed6\uff0c\u5f20\u606a\u60f3\u7740\u8981\u4e0e\u4e01\u69d0\u63a5\u56de\u6765\u7684\u6210\u6c49\u748b\u3001\u8096\u664b\u6210\u5148\u89c1\u4e0a\u4e00\u9762\u3002</p><p>\u3000\u3000\u7845\u8c37\u662f\u5168\u7403\u4fe1\u606f\u4ea7\u4e1a\u6280\u672f\u3001\u4eba\u624d\u7684\u96c6\u4e2d\u5730\uff0c\u5c3d\u7ba1\u4e16\u754c\u5404\u56fd\u6709\u4e0d\u5c11\u79d1\u6280\u8f83\u53d1\u8fbe\u5730\u533a\uff0c\u4e3a\u4e86\u66f4\u5feb\u7684\u4fc3\u8fdb\u9ad8\u65b0\u6280\u672f\u4e0e\u7ecf\u6d4e\u7684\u53d1\u5c55\uff0c\u90fd\u8bd5\u56fe\u5efa\u7acb\u8d77\u81ea\u5df1\u7684\u7845\u8c37\uff0c\u5982\u7f8e\u56fd\u6ce2\u58eb\u987f\u7684\u7b2c\u4e8c\u7845\u8c37\u3001\u65e5\u672c\u7845\u8c37\u3001\u97e9\u56fd\u7845\u8c37\uff0c\u56fd\u5185\u4e5f\u4e0d\u4f8b\u5916\uff0c\u6709\u5317\u4eac\u4e2d\u5173\u6751\u7845\u8c37\u3001\u4e0a\u6d77\u6d66\u4e1c\u7845\u8c37\u3001\u5c31\u8fde\u6d59\u6c5f\u676d\u5dde\u4e5f\u6709\u4e00\u4e2a\u201c\u5929\u5802\u7845\u8c37\u201d\uff0c\u8fd9\u4e9b\u590d\u5236\u54c1\uff0c\u5f53\u7136\u8fdc\u8fdc\u4e0d\u80fd\u4e0e\u52a0\u5229\u798f\u5c3c\u4e9a\u5dde\u7684\u7845\u8c37\u76f8\u63d0\u5e76\u8bba\u3002\u636e\u7edf\u8ba1\uff0c\u56fd\u5185\u6e05\u534e\u5927\u5b66\u3001\u4e2d\u79d1\u5927\u7b49\u4e00\u6d41\u7406\u5de5\u7c7b\u9662\u6821\u7684\u4f18\u79c0\u6bd5\u4e1a\u751f\u4e2d\uff0c\u5dee\u4e0d\u591a\u6709\u4e00\u534a\u4eba\u6216\u65e9\u6216\u8fdf\u7684\u90fd\u8981\u51fa\u56fd\uff0c\u5176\u4e2d\u4e3b\u8981\u6d41\u5411\u5c31\u662f\u7f8e\u56fd\uff0c\u5305\u62ec\u7845\u8c37\u8fd1\u4e00\u4e07\u5bb6\u9ad8\u79d1\u6280\u516c\u53f8\u3002</p><p>\u3000\u3000\u867d\u7136\u9526\u6e56\u53ef\u4ee5\u63d0\u4f9b\u76f8\u5bf9\u4e30\u539a\u7684\u85aa\u8d44\u633d\u7559\u4e00\u90e8\u5206\u4eba\u624d\uff0c\u4f46\u662f\u9ad8\u79d1\u6280\u98de\u901f\u53d1\u5c55\uff0c\u6280\u672f\u66f4\u65b0\u5c42\u51fa\u4e0d\u7a77\uff0c\u6280\u672f\u4eba\u5458\u82e5\u4e0d\u80fd\u575a\u6301\u4e0d\u61c8\u7684\u5b66\u4e60\u3001\u63d0\u9ad8\uff0c\u7a0d\u6709\u61c8\u6020\uff0c\u660e\u5929\u5c31\u53ef\u80fd\u88ab\u6dd8\u6c70\uff1b\u9664\u4e86\u5c06\u7cbe\u82f1\u6280\u672f\u4eba\u624d\u633d\u7559\u5230\u65d7\u4e0b\uff0c\u9526\u6e56\u8fd8\u9700\u8981\u4e00\u4e2a\u4f9b\u65d7\u4e0b\u6280\u672f\u4eba\u5458\u4e0d\u65ad\u5b66\u4e60\u3001\u4e0e\u4e16\u754c\u6700\u65b0\u7684\u4fe1\u606f\u6280\u672f\u8fdb\u884c\u4ea4\u6d41\u7684\u5e73\u53f0\u4e0e\u6e20\u9053\uff0c\u6240\u4ee5\u5f20\u606a\u51b3\u5b9a\u5148\u5728\u7845\u8c37\u76f4\u63a5\u6210\u7acb\u79d1\u7814\u5b9e\u9a8c\u57fa\u5730\u3002</p></article>"}}}]);
