(window.webpackJsonp=window.webpackJsonp||[]).push([[49],{71:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e03\u8282 \u6e56\u8fb9",content:"<article><h2>\u7b2c\u4e03\u8282 \u6e56\u8fb9</h2><p>\u4e09\u5929\u4e4b\u540e\uff0c\u8001\u4eba\u53bb\u4e16\u3002\u6b64\u65f6\u743c\u6069\u4e00\u884c\u4eba\u5df2\u7ecf\u7a7f\u8fc7\u9ed1\u8def\uff0c\u8eb2\u8fc7\u6563\u5854\u6797\u4f1a\u7684\u641c\u6355\uff0c\u6b63\u5f80\u897f\u5317\u7684\u57c3\u65af\u8003\u57ce\u524d\u8fdb\u3002</p><p>\u56e0\u4e3a\u5df2\u7ecf\u63d0\u524d\u544a\u77e5\u65cf\u4eba\uff0c\u5b89\u6392\u597d\u540e\u4e8b\uff0c\u8001\u4eba\u7684\u53bb\u4e16\u5e76\u6ca1\u6709\u5f15\u8d77\u591a\u5927\u7684\u6ce2\u52a8\u3002\u5728\u6309\u8d1d\u6234\u86ee\u65cf\u7684\u4e60\u4fd7\u5c06\u8001\u4eba\u5c31\u5730\u5b89\u846c\u540e\uff0c\u5927\u5bb6\u7ee7\u7eed\u4e0a\u8def\u3002</p><p>\u743c\u6069\u6682\u65f6\u6210\u4e3a\u8fd9\u652f\u8d1d\u6234\u86ee\u65cf\u90e8\u843d\u7684\u65cf\u957f\uff0c\u8fd9\u662f\u89c4\u77e9\uff0c\u56e0\u4e3a\u4ed6\u73b0\u5728\u638c\u63e1\u7740\u90a3\u5f20\u7f8a\u76ae\u5730\u56fe\u2014\u2014\u4e5f\u5c31\u662f\u65cf\u957f\u7684\u4fe1\u7269\u3002\u4e0d\u8fc7\u53ea\u662f\u6682\u65f6\uff0c\u743c\u6069\u4e5f\u548c\u8001\u4eba\u4e8b\u5148\u8bf4\u597d\uff0c\u53ea\u8981\u5230\u4e86\u57c3\u65af\u8003\u57ce\uff0c\u5927\u5bb6\u5c31\u5404\u8d70\u5404\u7684\u8def\uff0c\u8fd9\u652f\u8d1d\u6234\u86ee\u65cf\u662f\u518d\u627e\u5deb\u5e08\u6765\u7acb\u65b0\u65cf\u957f\u4e5f\u597d\uff0c\u662f\u5e76\u5165\u5176\u4ed6\u90e8\u843d\u4e5f\u597d\uff0c\u90fd\u4e0d\u5173\u743c\u6069\u7684\u4e8b\u60c5\u4e86\u3002</p><p>\u56e0\u4e3a\u53ea\u662f\u4e34\u65f6\u5408\u4f5c\uff0c\u743c\u6069\u4e5f\u61d2\u4e8e\u548c\u8fd9\u4e9b\u8d1d\u6234\u86ee\u65cf\u6df1\u5165\u6253\u4ea4\u9053\u3002\u4ed6\u6bcf\u65e5\u7684\u5de5\u4f5c\uff0c\u4fbf\u662f\u7528\u5730\u56fe\u5bfb\u627e\u9644\u8fd1\u7684\u7eff\u6d32\uff0c\u5269\u4e0b\u6765\u7684\u4e8b\u60c5\u81ea\u7136\u6709\u4eba\u505a\uff0c\u4e5f\u65e0\u9700\u4ed6\u8fd9\u4e2a\u4e34\u65f6\u65cf\u957f\u5173\u5fc3\u3002</p><p>\u8001\u4eba\u53bb\u4e16\u524d\u53e6\u5916\u8fd8\u505a\u4e86\u4ef6\u597d\u4e8b\uff0c\u5c31\u662f\u6559\u4f1a\u4e86\u743c\u6069\u8bf4\u901a\u7528\u8bed\u3002</p><p>\u901a\u7528\u8bed\u786e\u5b9e\u975e\u5e38\u7b80\u5355\u6613\u5b66\uff0c\u96be\u602a\u80fd\u98ce\u884c\u5927\u9646\uff0c\u743c\u6069\u53ea\u5b66\u4e86\u4e09\u5929\uff0c\u5c31\u5df2\u7ecf\u57fa\u672c\u638c\u63e1\uff0c\u81f3\u5c11\u65e5\u5e38\u7684\u4ea4\u6d41\u8c08\u8bdd\u53ef\u4ee5\u5927\u4f53\u5e94\u4ed8\u4e86\u3002\u5b66\u8bed\u8a00\u90fd\u662f\u8fd9\u6837\uff0c\u638c\u63e1\u5927\u4f53\u7684\u8bed\u6cd5\u3001\u53d1\u97f3\u89c4\u5219\u548c\u57fa\u7840\u7684\u8bcd\u6c47\u91cf\u4e4b\u540e\uff0c\u4ee5\u540e\u5c31\u53ef\u4ee5\u81ea\u5df1\u6162\u6162\u719f\u7ec3\u63d0\u9ad8\u4e86\u3002</p><p>\u81f3\u4e8e\u90a3\u4e09\u4e2a\u7267\u5e08\u548c\u5973\u9a91\u58eb\uff0c\u5bf9\u8d1d\u6234\u86ee\u65cf\u8fd9\u8fb9\u7684\u4e8b\u60c5\u4e5f\u5e76\u4e0d\u5982\u4f55\u5173\u5fc3\u2014\u2014\u6216\u8005\u8bf4\uff0c\u5e76\u4e0d\u8868\u73b0\u5f97\u5f88\u5173\u5fc3\u3002\u8d70\u8def\u65f6\uff0c\u4ed6\u4eec\u4e0d\u5373\u4e0d\u79bb\u5730\u8ddf\u5728\u540e\u9762\uff1b\u5bbf\u8425\u65f6\uff0c\u4ed6\u4eec\u5355\u72ec\u627e\u4e00\u5757\u50fb\u9759\u5730\u65b9\uff1b\u5c31\u8fde\u8fd9\u8fb9\u8001\u65cf\u957f\u53bb\u4e16\uff0c\u743c\u6069\u6682\u65f6\u7ee7\u4f4d\uff0c\u4ed6\u4eec\u4e5f\u90fd\u6ca1\u6709\u8868\u73b0\u51fa\u534a\u5206\u7684\u60ca\u8bb6\uff0c\u95ee\u90fd\u4e0d\u95ee\u4e00\u58f0\u3002</p><p>\u4e0d\u8fc7\uff0c\u743c\u6069\u5f53\u4e0a\u4e34\u65f6\u65cf\u957f\u540e\uff0c\u548c\u5973\u9a91\u58eb\u7684\u63a5\u89e6\u5012\u7a0d\u7a0d\u591a\u4e86\u4e00\u70b9\u2014\u2014\u56e0\u4e3a\u6bcf\u5929\u63a2\u8def\u7684\u5de5\u4f5c\u90fd\u662f\u7531\u5979\u6765\u627f\u62c5\u7684\u3002\u6bcf\u5929\u4e2d\u5348\uff0c\u743c\u6069\u4f7f\u7528\u5730\u56fe\uff0c\u5bfb\u627e\u9644\u8fd1\u7684\u7eff\u6d32\uff0c\u7136\u540e\u5973\u9a91\u58eb\u4fbf\u6309\u7740\u65b9\u5411\u53bb\u641c\u5bfb\uff0c\u786e\u5b9a\u51c6\u786e\u7684\u65b9\u4f4d\uff0c\u56de\u6765\u901a\u77e5\u5927\u5bb6\u3002</p><p>\u867d\u7136\u5982\u6b64\uff0c\u5f7c\u6b64\u8fd8\u662f\u6ca1\u4ec0\u4e48\u8bf4\u8bdd\u7684\u673a\u4f1a\uff0c\u5973\u9a91\u58eb\u6709\u4e9b\u51b0\u51b7\uff0c\u6216\u8005\u8bf4\uff0c\u5bf9\u4ec0\u4e48\u90fd\u51b7\u51b7\u6de1\u6de1\u7684\uff0c\u4eff\u4f5b\u6f20\u4e0d\u5173\u5fc3\uff0c\u6781\u5c11\u8bf4\u8bdd\u2014\u2014\u5e76\u975e\u5355\u5355\u5bf9\u743c\u6069\u662f\u5982\u6b64\uff0c\u5c31\u8fde\u5bf9\u5979\u7684\u90a3\u4e09\u4e2a\u7267\u5e08\u540c\u4f34\u90fd\u662f\u5982\u6b64\u3002</p><p>\u4ed6\u4eec\u786e\u5b9e\u5e94\u8be5\u662f\u8981\u53bb\u9001\u67d0\u4ef6\u4e1c\u897f\u2014\u2014\u662f\u4e00\u4e2a\u8fd1\u4f3c\u5c0f\u5706\u76fe\u5927\u5c0f\u7684\u7403\u4f53\uff0c\u88ab\u9ed1\u5e03\u4e25\u5bc6\u5305\u88f9\u7740\uff0c\u7531\u90a3\u4e2a\u7ea2\u5934\u53d1\u7684\u5170\u68ee\u5fb7\u5c14\u7267\u5e08\u4fdd\u7ba1\uff0c\u80cc\u5728\u80cc\u4e0a\uff0c\u5403\u996d\u7761\u89c9\u7247\u523b\u4e0d\u79bb\u8eab\u3002\u8001\u4eba\u544a\u8bc9\u743c\u6069\uff0c\u8fd9\u4e2a\u7267\u5e08\u5e94\u8be5\u662f\u8fd9\u56db\u4eba\u4e2d\u7684\u9996\u9886\uff0c\u540d\u53eb\u5766\u7eb3\u65af\u3002\u53e6\u5916\u4e00\u540d\u5170\u68ee\u5fb7\u5c14\u7267\u5e08\u662f\u5766\u7eb3\u65af\u7684\u526f\u624b\uff0c\u683c\u5170\u7279\uff0c\u90a3\u540d\u63d0\u5c14\u7267\u5e08\u540d\u53eb\u83f2\u62c9\u987f\uff0c\u4ece\u8863\u9970\u4e0a\u6765\u770b\uff0c\u8fd9\u4e09\u4eba\u5728\u5404\u81ea\u7684\u6559\u4f1a\u4e2d\u4f4d\u9636\u80af\u5b9a\u90fd\u4e0d\u4f4e\u3002</p><p>\u81f3\u4e8e\u90a3\u4e2a\u5973\u9a91\u58eb\uff0c\u743c\u6069\u5374\u4e00\u76f4\u4e0d\u77e5\u9053\u5979\u7684\u540d\u5b57\u3002\u56e0\u4e3a\u5176\u4ed6\u4e09\u4e2a\u7267\u5e08\u90fd\u53eb\u5979\u201c\u795e\u9009\u8005\u201d\uff0c\u8fd9\u663e\u7136\u662f\u4e00\u79cd\u79f0\u547c\uff0c\u8c61\u5f81\u7740\u5c0a\u5d07\u7684\u8eab\u4efd\uff0c\u5374\u80af\u5b9a\u4e0d\u662f\u540d\u5b57\u3002</p><p>\u201c\u795e\u9009\u8005\uff1f\u201d\u743c\u6069\u7422\u78e8\u7740\u8fd9\u4e2a\u540d\u5b57\uff0c\u4ece\u5b57\u9762\u610f\u601d\u6765\u770b\uff0c\u5e94\u8be5\u662f\u8bf4\u8fd9\u4e2a\u5973\u9a91\u58eb\u662f\u88ab\u795e\u7947\u6240\u4eb2\u81ea\u9009\u4e2d\u7684\u51e1\u95f4\u4ee3\u8a00\u4eba\u2014\u2014\u5982\u679c\u8fd9\u4e48\u8bf4\u7684\u8bdd\uff0c\u5012\u4e5f\u80fd\u89e3\u91ca\u4e3a\u4f55\u5979\u5c0f\u5c0f\u5e74\u7eaa\u5c31\u80fd\u62e5\u6709\u5982\u6b64\u5f3a\u608d\u7684\u529b\u91cf\u3002</p><p>\u4e0d\u8fc7\uff0c\u4e00\u4e2a\u770b\u8d77\u6765\u4e0d\u5230\u4e8c\u5341\u5c81\u7684\u5c0f\u5973\u5b69\uff0c\u80fd\u6709\u4ec0\u4e48\u80fd\u529b\uff0c\u88ab\u795e\u7947\u5982\u6b64\u770b\u91cd\uff1f</p><p>\u4e00\u8def\u4e0a\u5e73\u9759\u65e0\u4e8b\uff0c\u518d\u4e5f\u6ca1\u6709\u9047\u5230\u9aa8\u866b\u8fd9\u79cd\u5de8\u5927\u7684\u4ea1\u7075\u602a\u7269\uff0c\u5012\u6709\u4e00\u4e9b\u6c99\u6f20\u51f6\u732b\u4e4b\u7c7b\u7684\uff0c\u8d1d\u6234\u86ee\u65cf\u4eba\u7528\u4e00\u8f6e\u4e71\u7bad\u5c31\u6253\u53d1\u4e86\u3002\u56e0\u4e3a\u5df2\u7ecf\u8fdc\u79bb\u9ed1\u8def\uff0c\u6240\u4ee5\u8fde\u6563\u5854\u6797\u4f1a\u7684\u8ffd\u5175\u4e5f\u6d88\u5931\u4e86\u3002\u7f8a\u76ae\u5730\u56fe\u786e\u5b9e\u5f88\u597d\u7528\uff0c\u603b\u80fd\u51c6\u786e\u5730\u6307\u793a\u51fa\u9644\u8fd1\u7684\u7eff\u6d32\u3002\u57fa\u672c\u4e0a\uff0c\u5e73\u5747\u6bcf\u9694\u4e24\u4e09\u5929\uff0c\u5c31\u80fd\u6709\u4e00\u4e2a\u665a\u4e0a\u5728\u8349\u5730\u4e0a\u5b89\u8425\u624e\u5be8\uff0c\u8865\u7ed9\u996e\u6c34\u3002</p><p>\u5927\u7ea6\u8d70\u4e86\u5927\u534a\u4e2a\u6708\uff0c\u4ece\u5730\u56fe\u4e0a\u770b\uff0c\u8ddd\u79bb\u57c3\u65af\u8003\u5df2\u7ecf\u5f88\u8fd1\u4e86\uff0c\u5927\u7ea6\u518d\u6709\u4e24\u5929\u8def\u7a0b\u4fbf\u5230\u3002\u5230\u65f6\u5019\u5927\u5bb6\u4fbf\u8981\u5206\u9053\u626c\u9573\uff0c\u5404\u8d70\u5404\u7684\u8def\u3002\u743c\u6069\u81ea\u53bb\u535a\u5f97\u4e4b\u95e8\u7ed9\u90a3\u4e2a\u53eb\u201c\u62c9\u6c83\u514b\u201d\u7684\u8001\u5deb\u5e08\u9001\u4fe1\uff0c\u8d1d\u6234\u86ee\u65cf\u81ea\u751f\u81ea\u706d\uff0c\u81f3\u4e8e\u90a3\u4e09\u4e2a\u7267\u5e08\u548c\u5973\u9a91\u58eb\uff0c\u5c31\u4e0d\u77e5\u9053\u5230\u5e95\u8981\u53bb\u54ea\u91cc\u4e86\uff0c\u4ed6\u4eec\u53e3\u98ce\u5f88\u7d27\uff0c\u65e0\u8bba\u600e\u4e48\u65c1\u6572\u4fa7\u51fb\u90fd\u4e0d\u80af\u5410\u9732\u534a\u70b9\u3002</p><p>\u743c\u6069\u867d\u7136\u5bf9\u90a3\u4e2a\u5973\u9a91\u58eb\u5f88\u611f\u5174\u8da3\uff0c\u4f46\u5bf9\u65b9\u662f\u4e2a\u51b0\u7f8e\u4eba\u2014\u2014\u800c\u4e14\u8fd8\u662f\u4e2a\u529b\u91cf\u5982\u6b64\u5f3a\u608d\u7684\u51b0\u7f8e\u4eba\uff0c\u4e00\u8def\u4e0a\u8fde\u642d\u8baa\u7684\u673a\u4f1a\u90fd\u6ca1\u6709\uff0c\u8eab\u8fb9\u53c8\u6709\u4e09\u4e2a\u4e00\u770b\u5c31\u6b63\u4e49\u611f\u8fc7\u5269\u7684\u7537\u6027\u7267\u5e08\u3002\u743c\u6069\u60f3\u6765\u60f3\u53bb\uff0c\u8fd8\u662f\u51b3\u5b9a\u653e\u5f03\uff0c\u4e0d\u6253\u4ec0\u4e48\u4e3b\u610f\u5427\u3002\u65e9\u65e9\u53bb\u535a\u5f97\u4e4b\u95e8\uff0c\u628a\u8fd9\u4e00\u8d9f\u5dee\u4f7f\u529e\u5b8c\uff0c\u56de\u9634\u9b42\u57ce\u53bb\u89c1\u73ca\u5609\u624d\u662f\u6b63\u4e8b\u3002\u7f8e\u5973\u54ea\u91cc\u6ca1\u6709\uff0c\u4e5f\u4e0d\u7f3a\u8fd9\u4e00\u4e2a\u3002</p><p>\u203b\u203b\u203b</p><p>\u8fd9\u4e00\u5929\u508d\u665a\uff0c\u5973\u9a91\u58eb\u7b56\u9a6c\u56de\u6765\uff0c\u8bf4\u524d\u65b9\u627e\u5230\u4e86\u7eff\u6d32\u3002\u4f17\u4eba\u4e60\u4ee5\u4e3a\u5e38\uff0c\u5230\u4e86\u5730\u65b9\uff0c\u50cf\u5f80\u65e5\u4e00\u6837\u5b89\u8425\u624e\u5be8\uff0c\u5404\u81ea\u4f11\u606f\u3002</p><p>\u743c\u6069\u72ec\u81ea\u4e00\u4eba\u5728\u5e10\u7bf7\u91cc\uff0c\u7761\u5230\u534a\u591c\u9192\u6765\uff0c\u603b\u89c9\u5f97\u8eab\u4e0a\u96be\u53d7\u3002\u8fd9\u5927\u534a\u4e2a\u6708\u6765\u5728\u6c99\u6f20\u91cc\u884c\u8d70\uff0c\u53ef\u4ee5\u7b97\u5f97\u4e0a\u662f\u6709\u751f\u4ee5\u6765\u6700\u8270\u82e6\u7684\u4e00\u6b21\u65c5\u884c\u3002\u70c8\u65e5\u708e\u708e\uff0c\u98ce\u6c99\u5439\u522e\uff0c\u8fd9\u4e9b\u4e5f\u90fd\u7f62\u4e86\uff0c\u4e60\u60ef\u5c31\u597d\uff0c\u4f46\u5929\u5929\u8fd9\u6837\u8dcb\u6d89\uff0c\u6c57\u6e7f\u91cd\u8863\uff0c\u5b9e\u5728\u5f88\u60f3\u6d17\u4e2a\u6fa1\u3002</p><p>\u201c\u7b97\u4e86\uff0c\u53cd\u6b63\u518d\u6709\u51e0\u5929\u5c31\u51fa\u6c99\u6f20\u4e86\u3002\u201d</p><p>\u4ed6\u7ffb\u4e86\u4e2a\u8eab\uff0c\u51c6\u5907\u518d\u5ea6\u7761\u53bb\uff0c\u5374\u600e\u4e48\u4e5f\u7761\u4e0d\u7740\u3002\u8fd9\u6b21\u51fa\u6765\u5f97\u592a\u4ed3\u4fc3\uff0c\u8fde\u672c\u4e66\u90fd\u6ca1\u5e26\uff0c\u5426\u5219\u8fd8\u53ef\u4ee5\u6d88\u9063\u3002\u8bf4\u8d77\u6765\uff0c\u8fd9\u4e2a\u4e16\u754c\u867d\u7136\u9b54\u6cd5\u53d1\u8fbe\uff0c\u79d1\u6280\u6c34\u51c6\u5374\u5e73\u5e73\uff0c\u6ca1\u6709\u7535\u8111\uff0c\u6ca1\u6709\u7535\u89c6\uff0c\u6ca1\u6709MP3\uff0c\u66f4\u6ca1\u6709\u4e92\u8054\u7f51\uff0c\u5404\u79cd\u5a31\u4e50\u90fd\u6ca1\u6709\uff0c\u7740\u5b9e\u4ee4\u4eba\u4e27\u6c14\u3002</p><p>\u6b63\u767e\u65e0\u804a\u8d56\u95f4\uff0c\u7a81\u7136\u60f3\u4e86\u8d77\u6765\uff0c\u4eca\u5929\u8fd9\u7247\u7eff\u6d32\u9760\u5317\u90e8\uff0c\u4e0d\u662f\u6709\u4e2a\u5c0f\u6e56\u4e48\u3002\u56e0\u4e3a\u5927\u5bb6\u90fd\u8981\u5728\u6e56\u91cc\u6c72\u6c34\uff0c\u743c\u6069\u4e5f\u4e0d\u6562\u53bb\u6d17\u6fa1\uff0c\u4f46\u5982\u4eca\u5df2\u7ecf\u662f\u534a\u591c\uff0c\u8be5\u51c6\u5907\u7684\u4e5f\u90fd\u51c6\u5907\u597d\u4e86\uff0c\u660e\u5929\u4e00\u65e9\u5c31\u8981\u4e0a\u8def\u3002\u73b0\u5728\u53bb\u6d17\u4e2a\u6fa1\uff0c\u5e94\u8be5\u4e5f\u4ec0\u4e48\u5927\u5173\u7cfb\u5427\u3002</p><p>\u4e00\u5ff5\u53ca\u6b64\uff0c\u8d76\u5fd9\u4ece\u5305\u88f9\u91cc\u7ffb\u51fa\u4e00\u5957\u65b0\u8863\u670d\u6765\u3002\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u603b\u7b97\u8fd8\u6709\u70b9\u4eba\u60c5\u5473\uff0c\u867d\u7136\u4e0d\u7531\u5206\u8bf4\u5730\u5c31\u628a\u743c\u6069\u6253\u53d1\u5230\u8fd9\u5927\u6c99\u6f20\u91cc\u6765\u4e86\uff0c\u4f46\u8fd8\u8bb0\u5f97\u66ff\u4ed6\u51c6\u5907\u4e86\u8def\u4e0a\u6362\u7684\u8863\u670d\uff0c\u8fde\u5deb\u5e08\u888d\u90fd\u5907\u4e86\u597d\u51e0\u4ef6\u3002</p><p>\u6361\u4e86\u4e00\u5957\u6293\u5728\u624b\u4e0a\uff0c\u743c\u6069\u51fa\u4e86\u5e10\u7bf7\uff0c\u501f\u7740\u6708\u5149\u4fbf\u5f80\u90a3\u5c0f\u6e56\u8d70\u53bb\u3002\u6b64\u65f6\u5df2\u7ecf\u662f\u534a\u591c\uff0c\u6240\u6709\u4eba\u8d76\u4e86\u4e00\u5929\u7684\u8def\uff0c\u90fd\u5728\u81ea\u5df1\u5e10\u7bf7\u91cc\u6c89\u7761\u3002\u660e\u5929\u4e00\u65e9\uff0c\u4fbf\u53c8\u8981\u51c6\u5907\u542f\u7a0b\u4e86\uff0c\u8fd9\u79cd\u65e5\u5b50\u53ef\u771f\u8f9b\u82e6\u3002</p><p>\u4e0d\u8fc7\u603b\u7b97\u5feb\u71ac\u8fc7\u53bb\u4e86\uff0c\u7b97\u7b97\u8def\u7a0b\uff0c\u5982\u679c\u987a\u5229\u7684\u8bdd\uff0c\u540e\u5929\u80fd\u5230\u57c3\u65af\u8003\u3002\u542c\u8001\u4eba\u8bf4\uff0c\u57c3\u65af\u8003\u4ee5\u524d\u662f\u8010\u745f\u9057\u6c11\u548c\u8d1d\u6234\u86ee\u65cf\u4eba\u5c45\u4f4f\u6240\u5728\uff0c\u540e\u6765\u88ab\u4e00\u7fa4\u4eba\u9762\u72ee\u8d76\u4e86\u51fa\u6765\u3002\u5982\u4eca\u5c31\u662f\u56e0\u4e3a\u4eba\u9762\u72ee\u548c\u8d1d\u6234\u86ee\u65cf\u5ba3\u5e03\u548c\u89e3\uff0c\u5e76\u4e14\u8ba9\u51fa\u57c3\u65af\u8003\u57ce\uff0c\u6240\u4ee5\u9ed1\u8def\u6cbf\u7ebf\u7684\u8d1d\u6234\u86ee\u65cf\u90e8\u843d\u7eb7\u7eb7\u5317\u4e0a\u800c\u6765\u3002</p><p>\u8bf4\u5230\u8fd9\u4e2a\uff0c\u5176\u5b9e\u743c\u6069\u5fc3\u4e2d\u5f88\u6709\u7591\u8651\u3002\u4ee5\u524d\u5728\u5deb\u5e08\u5b66\u6821\u7684\u65f6\u5019\uff0c\u8bfe\u7a0b\u91cc\u4e5f\u6709\u201c\u602a\u7269\u5b66\u201d\uff0c\u8bb2\u8ff0\u5404\u79cd\u5343\u5947\u767e\u602a\u7684\u602a\u7269\uff0c\u5176\u4e2d\u4e5f\u63d0\u5230\u8fd9\u4eba\u9762\u72ee\u3002\u5982\u679c\u743c\u6069\u8bb0\u5f97\u4e0d\u9519\uff0c\u4eba\u9762\u72ee\u5e94\u8be5\u662f\u4e00\u79cd\u975e\u5e38\u72e1\u8bc8\u3001\u90aa\u6076\u800c\u4e14\u4e0d\u5b88\u4fe1\u7528\u7684\u602a\u7269\uff0c\u5b83\u4eec\u770b\u8d77\u6765\u50cf\u662f\u5916\u8868\u4fca\u6717\u7684\u4eba\u7c7b\u548c\u5f3a\u58ee\u7684\u72ee\u5b50\u5408\u4f53\u800c\u6210\uff0c\u4e0a\u534a\u8eab\u4eba\uff0c\u4e0b\u534a\u8eab\u72ee\u5b50\u2014\u2014\u4f46\u636e\u8bf4\u4eba\u9762\u72ee\u7684\u8d35\u65cf\u5374\u662f\u534a\u4eba\u534a\u86c7\u7684\u3002\u666e\u901a\u7684\u4eba\u9762\u72ee\u662f\u5f3a\u58ee\u7684\u6218\u58eb\uff0c\u72e1\u733e\u4f46\u5e76\u4e0d\u806a\u660e\uff1b\u8d35\u65cf\u5374\u5f80\u5f80\u90fd\u6709\u5f88\u9ad8\u7684\u667a\u529b\uff0c\u4f1a\u8bf4\u5f88\u591a\u79cd\u8bed\u8a00\uff0c\u800c\u4e14\u5927\u591a\u90fd\u6709\u4e9b\u9b54\u6cd5\u5929\u8d4b\uff0c\u5176\u4e2d\u4e0d\u4e4f\u9ad8\u660e\u7684\u5deb\u5e08\u6216\u8005\u672f\u58eb\u3002\u4e0d\u8fc7\u548c\u9634\u9b42\u57ce\u4e2d\u6b63\u89c4\u57f9\u8bad\u51fa\u6765\u7684\u5deb\u5e08\u4e0d\u540c\uff0c\u4eba\u9762\u72ee\u4e2d\u7684\u5deb\u5e08\u548c\u672f\u58eb\uff0c\u5927\u591a\u90fd\u53ea\u7cbe\u901a\u5f88\u6709\u9650\u7684\u51e0\u79cd\u9b54\u6cd5\uff0c\u4e3b\u8981\u662f\u5728\u7cbe\u795e\u63a7\u5236\u548c\u5236\u9020\u5e7b\u8c61\u65b9\u9762\u6bd4\u8f83\u64c5\u957f\uff0c\u5bf9\u4e8e\u5176\u4ed6\u9b54\u6cd5\u57fa\u672c\u4e00\u7a8d\u4e0d\u901a\u3002</p><p>\u4eba\u9762\u72ee\u9a71\u8d76\u8ffd\u6740\u8d1d\u6234\u86ee\u65cf\uff0c\u8fd9\u4e2a\u6ca1\u4ec0\u4e48\u5947\u602a\u7684\uff0c\u6b63\u5982\u4eba\u7c7b\u4f1a\u9a71\u8d76\u8ffd\u6740\u5730\u7cbe\u4e00\u822c\u2014\u2014\u4f46\u4f60\u76f8\u4fe1\u4eba\u4f1a\u548c\u5730\u7cbe\u548c\u89e3\u4e48\uff1f</p><p>\u743c\u6069\u662f\u4e0d\u592a\u76f8\u4fe1\u7684\uff0c\u4ed6\u4e5f\u66fe\u7ecf\u5411\u8001\u4eba\u63d0\u51fa\u8fc7\u8fd9\u4e2a\u95ee\u9898\u3002\u8001\u4eba\u6c89\u9ed8\u7740\uff0c\u4e0d\u8bf4\u8bdd\uff0c\u8fc7\u4e86\u5f88\u4e45\u624d\u56de\u7b54\u8bf4\uff1a\u201c\u5176\u5b9e\u6211\u4eec\u4e5f\u4e0d\u662f\u5f88\u76f8\u4fe1\uff0c\u4f46\u5317\u8fb9\u7684\u90e8\u843d\u6d3e\u4f7f\u8005\u4f20\u6d88\u606f\u8fc7\u6765\uff0c\u5e94\u8be5\u662f\u4e0d\u5047\u7684\u3002\u518d\u8bf4\uff0c\u6211\u4eec\u4e5f\u6ca1\u522b\u7684\u5730\u65b9\u53ef\u53bb\u4e86\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\uff1f\u6563\u5854\u6797\u4f1a\u53ea\u662f\u628a\u9ed1\u8def\u6cbf\u7ebf\u7ed9\u5c01\u4e86\u3002\u6c99\u6f20\u8fd9\u4e48\u5927\uff0c\u672a\u5fc5\u4e00\u5b9a\u8981\u53bb\u5317\u8fb9\u554a\u3002\u201d</p><p>\u8001\u4eba\u6447\u5934\uff0c\u89e3\u91ca\u4e86\u4e00\u756a\uff0c\u743c\u6069\u8fd9\u624d\u77e5\u9053\u3002\u539f\u6765\u5927\u6c99\u6f20\u5927\u4f53\u4e0a\u5206\u6210\u4e94\u5927\u533a\u57df\u3002\u4e2d\u95f4\u7684\u9ed1\u8def\u6cbf\u7ebf\u533a\u57df\uff0c\u5df2\u7ecf\u88ab\u6563\u5854\u6797\u4f1a\u7262\u7262\u5360\u636e\uff1b\u4e1c\u5317\u90e8\u5c5e\u4e8e\u6218\u8725\u4eba\u7684\u5730\u76d8\uff0c\u8d1d\u6234\u86ee\u65cf\u4e0d\u6562\u63d2\u8db3\uff1b\u897f\u5357\u90e8\u662f\u201c\u5251\u6f20\u5730\u533a\u201d\uff0c\u53c8\u88ab\u79f0\u4e3a\u201c\u865a\u65e0\u4e4b\u5730\u201d\uff0c\u4ece\u65e0\u534a\u5757\u7eff\u6d32\u51fa\u73b0\uff0c\u662f\u771f\u6b63\u7684\u7edd\u5883\u6b7b\u5730\u3002</p><p>\u201c\u53ea\u6709\u5f80\u897f\u5317\u8d70\uff0c\u56de\u6211\u4eec\u7684\u6545\u5c45\u4e86\u3002\u201d\u8001\u4eba\u8bf4\u3002</p><p>\u201c\u7b49\u7b49\uff0c\u4f60\u521a\u624d\u8fd8\u6f0f\u4e86\u4e00\u5757\u3002\u4e1c\u5357\u8fd9\u90e8\u5206\u5462\uff0c\u8fd9\u5757\uff0c\u53eb\u5e72\u6e34\u6c99\u6d32\u7684\u5730\u65b9\u3002\u201d</p><p>\u201c\u8fd9\u91cc\uff0c\u5982\u4eca\u5df2\u7ecf\u662f\u9634\u9b42\u57ce\u7684\u5730\u76d8\u4e86\u5427\u3002\u201d\u8001\u4eba\u6162\u6162\u8bf4\uff0c\u770b\u7740\u743c\u6069\u3002\u663e\u7136\uff0c\u4ed6\u65e9\u5c31\u9690\u7ea6\u731c\u6d4b\u5230\u743c\u6069\u7684\u6765\u5386\u8eab\u4efd\u3002</p><p>\u8bdd\u8bf4\u5230\u8fd9\u79cd\u5730\u6b65\uff0c\u743c\u6069\u4e5f\u6ca1\u4ec0\u4e48\u53ef\u8bf4\u7684\uff0c\u53cd\u6b63\u8bf4\u5230\u5e95\uff0c\u8d1d\u6234\u86ee\u65cf\u7684\u4e8b\u60c5\u8ddf\u4ed6\u8fd9\u4e2a\u5916\u4eba\u65e0\u5173\u3002\u4e0d\u8fc7\u662f\u6682\u65f6\u5408\u4f5c\uff0c\u5171\u540c\u8d70\u51fa\u8fd9\u5927\u6c99\u6f20\u7f62\u4e86\uff0c\u4e00\u65e6\u5230\u4e86\u57c3\u65af\u8003\uff0c\u7acb\u523b\u5404\u8d70\u5404\u7684\u8def\u3002</p><p>\u6447\u6447\u5934\uff0c\u4e0d\u60f3\u8fd9\u4e9b\u4e71\u4e03\u516b\u7cdf\u7684\u4e8b\u60c5\uff0c\u743c\u6069\u5feb\u6b65\u671d\u6e56\u8fb9\u8d70\u53bb\u3002\u6c99\u6f20\u7684\u591c\u91cc\u5f88\u51b7\uff0c\u4f30\u8ba1\u6e56\u6c34\u4e5f\u4f1a\u5f88\u51b0\uff0c\u4e0d\u8fc7\u8eab\u4e0a\u5b9e\u5728\u592a\u96be\u53d7\uff0c\u8feb\u5207\u6d17\u6fa1\u7684\u6b32\u671b\u538b\u5012\u4e86\u4e00\u5207\u3002</p><p>\u773c\u770b\u5c31\u8981\u5feb\u5230\u6e56\u8fb9\uff0c\u8033\u4e2d\u7a81\u7136\u4f20\u6765\u7ec6\u5fae\u7684\u54d7\u54d7\u6c34\u58f0\u3002\u6b64\u65f6\u662f\u6df1\u591c\uff0c\u5468\u56f4\u4e07\u7c41\u4ff1\u5bc2\uff0c\u743c\u6069\u4fee\u4e60\u5185\u529f\u591a\u5e74\uff0c\u867d\u7136\u6210\u5c31\u5e73\u5e73\uff0c\u4f46\u542c\u529b\u8f83\u5e38\u4eba\u654f\u9510\uff0c\u6c34\u58f0\u867d\u7136\u5f88\u8f7b\uff0c\u5374\u6e05\u695a\u5730\u4f20\u5230\u4ed6\u8033\u4e2d\u6765\u3002</p><p>\u201c\u8fd9\u4e48\u665a\u4e86\uff0c\u8c01\u8fd8\u5728\u6e56\u8fb9\u6c72\u6c34\uff1f\u4e0d\u5bf9\uff0c\u8fd9\u58f0\u97f3\u2026\u2026\u662f\u6709\u4eba\u5728\u6d17\u6fa1\uff1f\u201d</p><p>\u5ff5\u5934\u95ea\u8fc7\uff0c\u7ec6\u788e\u7684\u6c34\u58f0\u8d8a\u6765\u8d8a\u6e05\u6670\uff0c\u743c\u6069\u66f4\u52a0\u786e\u5b9a\u4e86\u81ea\u5df1\u7684\u5224\u65ad\u3002\u6ca1\u60f3\u5230\u5c45\u7136\u6709\u4eba\u548c\u81ea\u5df1\u5fc3\u610f\u76f8\u901a\uff0c\u4e5f\u8d81\u7740\u534a\u591c\u6765\u6d17\u6fa1\uff0c\u5012\u4e5f\u7b97\u662f\u6709\u7f18\u2026\u2026\u7b49\u4e00\u4e0b\uff1f\u4ed6\u7a81\u7136\u53cd\u5e94\u8fc7\u6765\uff0c\u4e0d\u4f1a\u662f\u90a3\u4e2a\u5973\u9a91\u58eb\u5427\uff0c\u81ea\u5df1\u8fd0\u6c14\u8fd9\u4e48\u597d\uff1f</p><p>\u5bf9\u554a\uff0c\u5973\u5b69\u5b50\u751f\u6027\u597d\u6d01\uff1b\u7537\u5b50\u5341\u5929\u534a\u6708\u4e0d\u6d17\u6fa1\u662f\u5e38\u4e8b\uff0c\u5973\u5b69\u5b50\u4e09\u5929\u4e0d\u6d17\u6d74\u5c31\u5929\u584c\u5730\u9677\u4e86\u3002\u5982\u4eca\u5927\u5bb6\u5728\u6c99\u6f20\u91cc\u8d70\u4e86\u5927\u534a\u4e2a\u6708\uff0c\u81ea\u5df1\u8fd9\u79cd\u5bb6\u4f19\u90fd\u5fcd\u53d7\u4e0d\u4e86\uff0c\u8981\u534a\u591c\u6e9c\u51fa\u6765\u6d17\u6fa1\uff0c\u90a3\u4e2a\u5973\u9a91\u58eb\u4e00\u5929\u5230\u665a\u7a7f\u7740\u5168\u8eab\u94e0\u7532\uff0c\u5e94\u8be5\u6bd4\u81ea\u5df1\u66f4\u89c9\u95f7\u70ed\u96be\u53d7\u5427\u3002</p><p>\u743c\u6069\u5c4f\u4f4f\u547c\u5438\uff0c\u8e51\u624b\u8e51\u811a\u5730\u8d70\u8fc7\u53bb\u3002\u770b\u89c1\u6e56\u8fb9\u6709\u4e00\u4e2a\u5c0f\u571f\u4e18\uff0c\u4ed6\u4f0f\u4e0b\u8eab\uff0c\u722c\u884c\u7740\uff0c\u79fb\u5230\u5c0f\u571f\u4e18\u540e\u9762\uff0c\u63a2\u51fa\u5934\u6765\u4e00\u770b\uff0c\u4e0d\u7981\u6709\u4e9b\u5931\u671b\u3002</p><p>\u4ed6\u731c\u5bf9\u4e86\u5f00\u5934\uff0c\u6ca1\u6709\u731c\u4e2d\u7ed3\u5c40\u2014\u2014\u786e\u5b9e\u662f\u90a3\u4e2a\u5973\u9a91\u58eb\uff0c\u4f46\u5374\u4e0d\u662f\u6765\u6d17\u6fa1\u7684\u3002</p><p>\u5979\u4e0d\u8fc7\u662f\u5728\u6e56\u8fb9\u6d17\u81ea\u5df1\u90a3\u628a\u5de8\u5927\u7684\u94f6\u5251\u3002</p></article>"}}}]);
