(window.webpackJsonp=window.webpackJsonp||[]).push([[490],{512:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u4e8c\u8282 \u6885\u83f2\u65af\u7684\u7d27\u5f20",content:"<article><h2>\u7b2c\u4e8c\u5341\u4e8c\u8282 \u6885\u83f2\u65af\u7684\u7d27\u5f20</h2><p>\u201c\u5979\u4e3a\u4ec0\u4e48\u5728\u8fd9\u91cc\u2026\u2026\u8bf4\u6765\u8bdd\u957f\uff0c\u6709\u7a7a\u518d\u8ddf\u4f60\u89e3\u91ca\uff0c\u201d\u743c\u6069\u6709\u4e9b\u65e0\u5948\u5730\u8038\u8038\u80a9\uff0c\u201c\u53cd\u6b63\u6709\u4e00\u70b9\uff0c\u4f60\u662f\u53ef\u4ee5\u653e\u5fc3\u7684\u3002\u201d</p><p>\u201c\u653e\u5fc3\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u6211\u6ca1\u6253\u5979\u7684\u4e3b\u610f\u3002\u201d</p><p>\u6885\u83f2\u65af\u7b11\u8d77\u6765\uff0c\u201c\u4f60\u662f\u4e0d\u6562\u5427\u3002\u201d</p><p>\u742a\u5a05\u4e0d\u7b97\u7edd\u8272\uff0c\u4f46\u4e5f\u79f0\u5f97\u4e0a\u7f8e\u4eba\uff0c\u8eab\u6bb5\u7a88\u7a95\uff0c\u9762\u5bb9\u4fcf\u4e3d\uff0c\u800c\u4e14\u505a\u4e3a\u732b\u5973\uff0c\u6bd4\u8d77\u5176\u4ed6\u5973\u5b50\u800c\u8a00\u66f4\u591a\u4e86\u4e00\u4efd\u730e\u5947\u611f\u3002\u743c\u6069\u5bf9\u4ec0\u4e48\u732b\u8033\u5a18\u4e4b\u7c7b\u7684\u5b58\u5728\u6ca1\u6709\u7279\u522b\u55dc\u597d\uff0c\u4f46\u5982\u679c\u6709\u673a\u4f1a\u7684\u8bdd\uff0c\u5012\u4e5f\u4e0d\u4ecb\u610f\u5c1d\u5c1d\u9c9c\uff0c\u589e\u52a0\u7ecf\u9a8c\u3002\u4f46\u95ee\u9898\u5c31\u51fa\u5728\u8fd9\u4e2a\u201c\u732b\u201d\u4e0a\uff0c\u742a\u5a05\u662f\u517d\u5316\u4eba\uff0c\u4e5f\u5373\u662f\u8bf4\u5979\u539f\u672c\u662f\u4eba\u7c7b\uff0c\u5f97\u4e86\u517d\u5316\u75c7\uff08\u5177\u4f53\u8bf4\u662f\u732b\u5316\u75c7\uff09\uff0c\u6240\u4ee5\u624d\u53d8\u6210\u4e86\u732b\u5973\u2014\u2014\u800c\u517d\u5316\u75c7\u8fd9\u79cd\u4e1c\u897f\u662f\u4f1a\u4f20\u67d3\u7684\uff0c\u4f20\u64ad\u9014\u5f84\u6700\u5e38\u89c1\u7684\u662f\u8840\u6db2\uff0c\u5176\u6b21\u4fbf\u662f\u6027\u4ea4\u2026\u2026</p><p>\u5f53\u7136\uff0c\u5e76\u4e0d\u662f\u8bf4\u743c\u6069\u628a\u742a\u5a05\u63a8\u5012\uff0c\u5c31\u4e00\u5b9a\u4f1a\u611f\u67d3\u4e0a\u517d\u5316\u75c7\uff0c\u8fd9\u662f\u6709\u6982\u7387\u7684\uff0c\u5177\u4f53\u6570\u503c\u56e0\u4eba\u800c\u5f02\uff0c\u4f46\u54ea\u6015\u662f\u5343\u5206\u4e4b\u4e00\u7684\u6982\u7387\uff0c\u743c\u6069\u4e5f\u4e0d\u60f3\u5192\u8fd9\u4e2a\u9669\u3002\u4ed6\u53ef\u4e0d\u60f3\u4e00\u591c\u98ce\u6d41\uff0c\u7b2c\u4e8c\u5929\u65e9\u4e0a\u8d77\u5e8a\u53d1\u73b0\u81ea\u5df1\u957f\u51fa\u732b\u8033\u6735\u548c\u5c3e\u5df4\uff0c\u90a3\u592a\u53ef\u6015\u4e86\u3002\u6216\u8bb8\u8fd9\u4e5f\u662f\u5965\u5609\u83b1\u65af\u4f1a\u7559\u4e0b\u742a\u5a05\uff0c\u5e76\u4e14\u628a\u5979\u5b89\u6392\u5728\u73ca\u5609\u8eab\u8fb9\u7684\u539f\u56e0\u4e4b\u4e00\u5427\uff0c\u53cd\u6b63\u743c\u6069\u53ea\u80fd\u770b\uff0c\u4e0d\u6562\u5403\uff0c\u4e0d\u4f1a\u7ed9\u73ca\u5609\u589e\u52a0\u6f5c\u5728\u7684\u7ade\u4e89\u5bf9\u624b\u3002</p><p>\u8fd9\u4e2a\u4e16\u754c\u9b54\u6cd5\u53d1\u8fbe\uff0c\u5f88\u591a\u75be\u75c5\u90fd\u80fd\u591f\u7528\u795e\u672f\u76f4\u63a5\u6cbb\u7597\uff0c\u4f46\u517d\u5316\u75c7\u662f\u4e2a\u4f8b\u5916\uff0c\u518d\u9ad8\u660e\u7684\u7267\u5e08\u7528\u518d\u5f3a\u529b\u7684\u795e\u672f\u4e5f\u65e0\u6cd5\u795b\u9664\u3002\u53ea\u6709\u6708\u5973\u795e\u7684\u6559\u4f1a\u636e\u8bf4\u638c\u63e1\u4e86\u4e00\u79cd\u79d8\u5bc6\u65b9\u6cd5\uff0c\u80fd\u591f\u6cbb\u6108\u517d\u5316\u75c7\uff0c\u8ba9\u60a3\u8005\u6062\u590d\u539f\u72b6\u3002\u8fd9\u79cd\u4f20\u8a00\u662f\u5426\u53ef\u9760\u5c1a\u672a\u53ef\u77e5\uff0c\u5c31\u7b97\u5f53\u771f\u5c5e\u5b9e\uff0c\u743c\u6069\u4e5f\u4e0d\u53ef\u80fd\u4e0a\u95e8\u6c42\u52a9\uff0c\u4ed6\u4e00\u4e2a\u838e\u5c14\u9009\u6c11\uff0c\u9634\u9b42\u57ce\u5deb\u5e08\uff0c\u8dd1\u53bb\u82cf\u4f26\u795e\u6bbf\uff0c\u90a3\u4e0d\u662f\u81ea\u5bfb\u6b7b\u8def\u4e48\u3002</p><p>\u5176\u5b9e\u6885\u83f2\u65af\u6628\u65e5\u4e5f\u89c1\u8fc7\u742a\u5a05\uff0c\u53ea\u4e0d\u8fc7\uff0c\u5979\u5f53\u65f6\u770b\u89c1\u7684\u662f\u4e00\u53ea\u8737\u7f29\u5728\u8f66\u53a2\u89d2\u843d\u91cc\u547c\u547c\u5927\u7761\u7684\u9ed1\u732b\uff0c\u4ee5\u4e3a\u662f\u73ca\u5609\u517b\u7684\u5ba0\u7269\uff0c\u6240\u4ee5\u4e0d\u66fe\u5728\u610f\u3002\u5f53\u7136\u5979\u8fd9\u4e2a\u60f3\u6cd5\u4e5f\u5e76\u6ca1\u6709\u9519\uff0c\u742a\u5a05\u76ee\u524d\u7684\u5730\u4f4d\uff0c\u786e\u5b9e\u5c31\u662f\u73ca\u5609\u7684\u5ba0\u7269\uff0c\u517c\u804c\u4fdd\u9556\u3002</p><p>\u5982\u4eca\u770b\u6765\uff0c\u5979\u53c8\u591a\u4e86\u4e00\u9879\u5de5\u4f5c\uff0c\u5c31\u662f\u966a\u7ec3\u3002</p><p>\u5373\u4fbf\u4ee5\u743c\u6069\u8fd9\u79cd\u5916\u884c\u4eba\u7684\u773c\u5149\u770b\u6765\uff0c\u73ca\u5609\u7684\u8fdb\u653b\u4e5f\u662f\u7834\u7efd\u767e\u51fa\uff0c\u6301\u5251\u7684\u59ff\u52bf\u5012\u8fd8\u7b97\u50cf\u6a21\u50cf\u6837\uff0c\u4f46\u6bcf\u4e00\u6b21\u523a\u51fb\u90fd\u660e\u663e\u7f3a\u4e4f\u529b\u9053\uff0c\u901f\u5ea6\u4e5f\u4e0d\u591f\u5feb\u3002\u5018\u82e5\u662f\u5f53\u771f\u4ea4\u624b\u7684\u8bdd\uff0c\u53ea\u6015\u4e00\u7167\u9762\u5c31\u88ab\u742a\u5a05\u6253\u5012\u4e86\u3002\u4f46\u73b0\u5728\u662f\u5728\u7ec3\u4e60\uff0c\u4e0d\u662f\u5b9e\u6218\uff0c\u742a\u5a05\u663e\u7136\u88ab\u9650\u5b9a\u4e86\u4e0d\u51c6\u53cd\u51fb\uff0c\u751a\u81f3\u4e0d\u80fd\u95ea\u907f\uff0c\u53ea\u80fd\u9632\u5fa1\uff0c\u6240\u4ee5\u5c31\u6bd4\u8f83\u90c1\u95f7\u4e86\u3002\u5373\u4fbf\u5982\u6b64\uff0c\u5979\u4f9d\u65e7\u8fd8\u662f\u5f88\u8f7b\u677e\u5c31\u5316\u89e3\u4e86\u73ca\u5609\u7684\u5168\u90e8\u653b\u52bf\uff0c\u4e24\u53ea\u722a\u5957\u5728\u8eab\u524d\u7eb5\u6a2a\u98de\u821e\u7740\uff0c\u5c06\u5bf9\u624b\u523a\u6765\u7684\u6bcf\u4e00\u5251\u90fd\u7a33\u7a33\u5730\u683c\u5f00\u3002</p><p>\u94ff\uff01</p><p>\u73ca\u5609\u53c8\u4e00\u6b21\u51fa\u5251\uff0c\u7136\u540e\u53c8\u4e00\u6b21\u6beb\u65e0\u60ac\u5ff5\u5730\u88ab\u722a\u5957\u6321\u4e86\u56de\u6765\u3002\u5de8\u5927\u7684\u5b9e\u529b\u5dee\u8ddd\uff0c\u8ba9\u5979\u6839\u672c\u5c31\u6ca1\u6709\u4e00\u4e1d\u673a\u4f1a\u653b\u7834\u732b\u5973\u7684\u9632\u5fa1\u3002\u5979\u8f7b\u8f7b\u5730\u5598\u606f\u7740\uff0c\u80f8\u53e3\u4e00\u9635\u9635\u8d77\u4f0f\uff0c\u8138\u988a\u901a\u7ea2\uff0c\u9b13\u89d2\u4e0a\u5df2\u7ecf\u80fd\u591f\u770b\u5230\u6676\u83b9\u7684\u6c57\u6ef4\uff0c\u663e\u7136\u4e5f\u5df2\u7ecf\u5f88\u75b2\u5026\u3002\u5c3d\u7ba1\u5982\u6b64\uff0c\u5979\u5e76\u6ca1\u6709\u6253\u7b97\u505c\u4e0b\u6765\u4f11\u606f\uff0c\u800c\u662f\u518d\u5ea6\u4e3e\u8d77\u5251\uff0c\u6307\u5411\u742a\u5a05\u3002</p><p>\u7136\u540e\u5979\u5530\u5730\u523a\u51fa\u3002</p><p>\u8fd9\u4e00\u5251\u901f\u5ea6\u5f88\u5feb\uff0c\u529b\u9053\u4e5f\u8db3\uff0c\u89d2\u5ea6\u4e0d\u7b97\u5201\u94bb\uff0c\u4f46\u5982\u679c\u662f\u743c\u6069\u6765\u5e94\u4ed8\u7684\u8bdd\uff0c\u53ea\u6015\u8fd8\u771f\u8981\u624b\u5fd9\u811a\u4e71\u3002\u53ef\u60dc\u73ca\u5609\u7684\u5bf9\u624b\u5e76\u4e0d\u662f\u743c\u6069\uff0c\u800c\u662f\u742a\u5a05\u3002\u732b\u5973\u9ed8\u4e0d\u4f5c\u58f0\u5730\u53cc\u722a\u4e00\u5408\uff0c\u4eff\u4f5b\u4e00\u5bf9\u94c1\u94b3\uff0c\u7cbe\u51c6\u5730\u4fbf\u5c06\u523a\u5251\u5c01\u4f4f\u4e86\u3002</p><p>\u773c\u770b\u8fd9\u4e00\u5251\u53c8\u8981\u50cf\u4e4b\u524d\u4e00\u6837\u5f92\u52b3\u65e0\u529f\uff0c\u73ca\u5609\u9661\u7136\u624b\u8155\u7ffb\u8f6c\uff0c\u7ec6\u957f\u7684\u5251\u8eab\u4ee5\u6781\u9ad8\u7684\u9891\u7387\u98a4\u52a8\u7740\uff0c\u786c\u751f\u751f\u5730\u4ece\u4e24\u53ea\u4e25\u4e1d\u5408\u7f1d\u7684\u722a\u5957\u4e2d\u7a7f\u8fc7\uff0c\u4eff\u4f5b\u4e00\u6761\u94f6\u5149\u95ea\u95ea\u7684\u6bd2\u86c7\u626d\u66f2\u7740\u8eab\u4f53\uff0c\u4ee5\u8be1\u5f02\u7684\u89d2\u5ea6\u500f\u5ffd\u4ece\u6d1e\u7a74\u4e2d\u7a9c\u51fa\uff0c\u5f20\u5f00\u7360\u7259\u6251\u5411\u732b\u5973\u3002</p><p>\u742a\u5a05\u5b8c\u5168\u6ca1\u6709\u6599\u5230\u8fd9\u79cd\u53d8\u5316\uff0c\u4f46\u5979\u7ec8\u7a76\u53cd\u5e94\u7075\u654f\uff0c\u5343\u94a7\u4e00\u53d1\u4e4b\u9645\u8eab\u4f53\u504f\u8f6c\uff0c\u907f\u5f00\u8981\u5bb3\u3002\u73ca\u5609\u60ca\u547c\u4e00\u58f0\uff0c\u523a\u5251\u5df2\u7ecf\u76f4\u63a0\u800c\u8fc7\uff0c\u55e4\u5730\u5212\u7834\u76ae\u7532\uff0c\u5728\u732b\u5973\u7684\u53f3\u81c2\u4e0a\u5207\u5f00\u4e00\u9053\u6d45\u6d45\u4f24\u53e3\u3002\u9b54\u6cd5\u706b\u7130\u548c\u5bd2\u51b0\u7684\u5149\u8292\u4ea4\u9519\u95ea\u8fc7\uff0c\u4f24\u53e3\u5904\u7684\u8840\u8089\u77ac\u95f4\u53d8\u5f97\u7126\u9ed1\u51dd\u56fa\uff0c\u50cf\u662f\u88ab\u9ad8\u6e29\u707c\u70e7\u8fc7\uff0c\u8868\u9762\u5374\u53c8\u7b3c\u7f69\u7740\u4e00\u5c42\u6de1\u84dd\u8272\u51b0\u971c\u3002\u800c\u5728\u51b0\u971c\u4e4b\u4e2d\uff0c\u51e0\u7f15\u9ed1\u6c14\u6b63\u9690\u9690\u6d41\u52a8\uff0c\u50cf\u86c7\u4e00\u6837\u671d\u732b\u5973\u7684\u4f53\u5185\u6e17\u5165\u3002\u742a\u5a05\u4e00\u76f4\u5e73\u6de1\u6728\u7136\u7684\u9762\u5bb9\u65e0\u6cd5\u6291\u5236\u5730\u626d\u66f2\u8d77\u6765\uff0c\u50cf\u662f\u906d\u53d7\u5230\u96be\u4ee5\u60f3\u8c61\u7684\u75db\u82e6\uff0c\u6c57\u73e0\u6eda\u6eda\u4e4b\u4e0b\uff0c\u8eab\u4f53\u98a4\u7740\u6296\uff0c\u4f46\u4f9d\u7136\u662f\u4e00\u8a00\u4e0d\u53d1\u3002</p><p>\u201c\u5bf9\u4e0d\u8d77\u5bf9\u4e0d\u8d77\uff0c\u201d\u73ca\u5609\u614c\u614c\u5f20\u5f20\u5730\u9053\u6b49\uff0c\u5979\u81ea\u5df1\u90fd\u6ca1\u60f3\u5230\u4f1a\u51fa\u73b0\u8fd9\u79cd\u60c5\u5f62\uff0c\u201c\u6ca1\u4e8b\u5427\u2026\u2026\u201d</p><p>\u201c\u597d\u4e86\uff0c\u4eca\u5929\u7684\u5251\u672f\u8bad\u7ec3\u5230\u6b64\u4e3a\u6b62\u3002\u201d\u7ad9\u5728\u8fdc\u5904\u7684\u5965\u5609\u83b1\u65af\u53ca\u65f6\u53d1\u8bdd\uff0c\u5979\u7ffb\u5f00\u4e66\uff0c\u767d\u5149\u4ece\u5176\u4e2d\u4e00\u9875\u53d1\u51fa\uff0c\u5c04\u5728\u742a\u5a05\u7684\u4f24\u53e3\u4e0a\u3002\u51b0\u971c\u88ab\u77ac\u95f4\u6d88\u878d\u4e86\uff0c\u706b\u7130\u707c\u70e7\u7684\u75d5\u8ff9\u4e5f\u6d88\u5931\u4e0d\u89c1\uff0c\u8840\u6d41\u6dcc\u51fa\u6765\uff0c\u5148\u662f\u7d2b\u9ed1\uff0c\u6162\u6162\u8f6c\u6210\u7ea2\u8272\uff0c\u6700\u540e\u6e10\u6e10\u6b62\u4f4f\u3002\u5965\u5609\u83b1\u65af\u77a5\u4e86\u7ad9\u5728\u95e8\u53e3\u7684\u743c\u6069\u548c\u6885\u83f2\u65af\u4e00\u773c\uff0c\u5c06\u4e66\u4ea4\u7ed9\u73ca\u5609\u3002</p><p>\u201c\u4f11\u606f\u4e00\u5c0f\u65f6\uff0c\u4e0b\u5348\u4e00\u70b9\u949f\u7ee7\u7eed\u3002\u201d\u5979\u8bf4\uff0c\u7136\u540e\u6d88\u5931\u3002</p><p>\u73ca\u5609\u5c06\u523a\u5251\u201c\u7d2b\u8346\u201d\u653e\u5728\u300a\u547d\u8fd0\u957f\u591c\u300b\u7684\u5c01\u9762\u4e0a\uff0c\u9ed8\u8bf5\u5492\u6587\uff0c\u5b9e\u4f53\u5251\u8eab\u968f\u5373\u865a\u5316\uff0c\u9690\u5165\u4e66\u4e2d\u3002\u5979\u638f\u51fa\u624b\u5e15\u64e6\u4e86\u64e6\u989d\u5934\u6c57\u6c34\uff0c\u62ac\u8d77\u5934\uff0c\u6b63\u8981\u548c\u742a\u5a05\u8bf4\u8bdd\uff0c\u8fd9\u65f6\u5019\u624d\u53d1\u73b0\u95e8\u53e3\u7684\u4e24\u4eba\u3002</p><p>\u201c\u4e0a\u5348\u597d\uff0c\u827e\u5f25\u8587\u3002\u201d\u73ca\u5609\u7b11\u772f\u772f\u5730\u548c\u6885\u83f2\u65af\u6253\u62db\u547c\u3002</p><p>\u6885\u83f2\u65af\u7b11\u7740\u70b9\u5934\u793a\u610f\uff0c\u5374\u6709\u4e9b\u795e\u601d\u4e0d\u5c5e\u7684\u6837\u5b50\u3002\u743c\u6069\u5fc3\u4e2d\u5947\u602a\uff0c\u4f46\u4e5f\u6ca1\u591a\u95ee\uff0c\u201c\u8be5\u5403\u5348\u996d\u4e86\uff0c\u59d0\u59d0\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u4eca\u5929\u662f\u827e\u5f25\u8587\u4eb2\u81ea\u4e0b\u53a8\u5462\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u5766\u767d\u5730\u8bf4\uff0c\u6885\u83f2\u65af\u7684\u53a8\u827a\u786e\u5b9e\u4e0d\u7b97\u9ad8\u660e\uff0c\u867d\u7136\u4e5f\u4e0d\u7b97\u7279\u522b\u5dee\uff0c\u5927\u81f4\u4e0a\u5c5e\u4e8e\u4e2d\u7b49\u504f\u4e0b\u7684\u6c34\u51c6\u3002\u586b\u9971\u809a\u5b50\u662f\u6ca1\u95ee\u9898\uff0c\u4f46\u8272\u9999\u5473\u4ec0\u4e48\u7684\uff0c\u90a3\u5c31\u4e0d\u80fd\u82db\u6c42\u4e86\u3002\u800c\u4e14\u5979\u4e0d\u4f1a\u505a\u4efb\u4f55\u8089\u7c7b\u98df\u54c1\uff0c\u4e00\u684c\u5b50\u5168\u662f\u6e05\u6de1\u7d20\u83dc\uff0c\u8ba9\u743c\u6069\u4e0d\u514d\u6709\u4e9b\u5931\u671b\u3002\u4e0d\u8fc7\u4ed6\u4e5f\u5b9e\u5728\u662f\u997f\u4e86\uff0c\u987e\u4e0d\u4e0a\u6311\u4e09\u62e3\u56db\u3002</p><p>\u5403\u5b8c\u5348\u996d\uff0c\u73ca\u5609\u56de\u623f\u95f4\u4f11\u606f\uff0c\u5979\u6709\u5348\u7761\u7684\u4e60\u60ef\uff0c\u53ea\u8981\u6709\u6761\u4ef6\u90fd\u4f1a\u5c3d\u91cf\u5c0f\u61a9\u7247\u523b\uff0c\u742a\u5a05\u81ea\u7136\u966a\u4f34\u7740\u5979\u3002\u517d\u5316\u4eba\u7684\u81ea\u6108\u80fd\u529b\u5f88\u5f3a\uff0c\u867d\u7136\u6bd4\u4e0d\u4e0a\u5438\u8840\u9b3c\u90a3\u4e48\u53d8\u6001\uff0c\u4f46\u6bd4\u5bfb\u5e38\u4eba\u7c7b\u8981\u4f18\u79c0\u5f97\u591a\uff0c\u6ca1\u8fc7\u534a\u4e2a\u5c0f\u65f6\uff0c\u732b\u5973\u7684\u4f24\u53e3\u5c31\u5df2\u7ecf\u57fa\u672c\u6108\u5408\u4e86\uff0c\u53ea\u7559\u4e0b\u4e00\u9053\u6de1\u6de1\u7684\u75a4\u75d5\u3002</p><p>\u5c3d\u7ba1\u5982\u6b64\uff0c\u73ca\u5609\u90a3\u4e00\u5251\u8fd8\u662f\u7ed9\u743c\u6069\u548c\u6885\u83f2\u65af\u7559\u4e0b\u4e86\u6df1\u523b\u5370\u8c61\uff0c\u4ee5\u81f3\u4e8e\u4ed6\u4eec\u8d70\u5728\u8def\u4e0a\u90fd\u8fd8\u5728\u8ba8\u8bba\u8fd9\u4e2a\u8bdd\u9898\u3002</p><p>\u201c\u73ca\u5609\u59d0\u59d0\u90a3\u628a\u5251\u5f88\u4e86\u4e0d\u8d77\u554a\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u706b\u7130\u3001\u5bd2\u51b0\uff0c\u8fd8\u6709\u865a\u5f31\u8bc5\u5492\uff0c\u4e00\u51fb\u80fd\u591f\u540c\u65f6\u9644\u5e26\u4e09\u79cd\u9b54\u6cd5\u6548\u679c\uff0c\u8fd9\u5df2\u7ecf\u8db3\u4ee5\u79f0\u4e4b\u4e3a\u795e\u5668\u4e86\u5427\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u653b\u51fb\u65f6\u4f1a\u9644\u5e26\u9b54\u6cd5\u6548\u679c\u7684\u6b66\u5668\uff0c\u5728\u8fd9\u4e2a\u4e16\u754c\u4e0a\u5e76\u4e0d\u7f55\u89c1\uff0c\u5927\u57ce\u5e02\u7684\u9b54\u6cd5\u5546\u5e97\u91cc\u5f80\u5f80\u90fd\u6709\u51fa\u552e\u3002\u4f46\u6b63\u5982\u6885\u83f2\u65af\u6240\u8a00\uff0c\u80fd\u540c\u65f6\u9644\u5e26\u4e09\u79cd\u4e0d\u540c\u7c7b\u578b\u4e0d\u540c\u5b66\u6d3e\u9b54\u6cd5\u6548\u679c\u7684\u6b66\u5668\uff0c\u90a3\u53ef\u5c31\u592a\u96be\u5f97\u4e86\u3002\u751a\u81f3\u53ef\u4ee5\u8bf4\uff0c\u8fd9\u5df2\u7ecf\u8fdd\u80cc\u4e86\u901a\u884c\u7684\u9b54\u6cd5\u5b66\u539f\u7406\u3002</p><p>\u201c\u4f60\u4ece\u54ea\u91cc\u5f04\u5230\u90a3\u628a\u5251\u7684\uff1f\u201d\u6885\u83f2\u65af\u95ee\uff0c\u201c\u523a\u5251\u672c\u8eab\u5c31\u662f\u975e\u5e38\u5c11\u89c1\u7684\u6b66\u5668\u554a\u3002\u201d</p><p>\u201c\u548c\u6211\u6ca1\u5173\u7cfb\uff0c\u662f\u5965\u5609\u83b1\u65af\u5973\u58eb\u9001\u7ed9\u5979\u7684\u3002\u201d</p><p>\u201c\u96be\u602a\uff0c\u201d\u6885\u83f2\u65af\u70b9\u5934\uff0c\u201c\u591a\u91cd\u9b54\u6cd5\u6548\u679c\u590d\u5408\uff0c\u867d\u7136\u4e0d\u7b26\u5408\u73b0\u5728\u7684\u9b54\u6cd5\u5b66\u539f\u7406\uff0c\u4f46\u5982\u679c\u662f\u5728\u5343\u5e74\u524d\u7684\u8010\u745f\u65f6\u4ee3\uff0c\u5012\u4e5f\u4e0d\u7b97\u4ec0\u4e48\u5427\u3002\u201d</p><p>\u201c\u662f\u554a\u3002\u201d</p><p>\u201c\u90a3\u8fd9\u4ef6\u5462\uff1f\u201d\u6885\u83f2\u65af\u6478\u4e86\u6478\u8116\u9888\u4e0a\u7684\u8f89\u9633\u62a4\u7b26\uff0c\u201c\u4e5f\u662f\u4f60\u4ece\u661f\u9668\u57ce\u91cc\u62ff\u6765\u7684\uff1f\u201d</p><p>\u201c\u4e0d\u662f\uff0c\u201d\u743c\u6069\u6447\u5934\uff0c\u201c\u8fd9\u662f\u6211\u90a3\u4f4d\u80d6\u5b50\u8001\u5e08\u9001\u4f60\u7684\u793c\u7269\u3002\u201d</p><p>\u201c\u90a3\u5bb6\u4f19\u554a\u3002\u201d</p><p>\u5c11\u5973\u7684\u8138\u4e0a\u660e\u663e\u9732\u51fa\u538c\u6076\u4e4b\u8272\uff0c\u4f46\u4e5f\u5e76\u6ca1\u6709\u518d\u591a\u8bf4\u4ec0\u4e48\u3002\u201c\u743c\u6069\uff0c\u201d\u6c89\u541f\u4e86\u534a\u664c\uff0c\u6885\u83f2\u65af\u5ffd\u7136\u5f00\u53e3\uff0c\u201c\u4f60\u786e\u5b9a\u73ca\u5609\u59d0\u59d0\u4ee5\u524d\u4ece\u6ca1\u5b66\u8fc7\u5251\u672f\uff1f\u201d</p><p>\u201c\u5f53\u7136\u786e\u5b9a\u3002\u201d</p><p>\u201c\u662f\u4e48\uff0c\u90a3\u771f\u5947\u602a\u2026\u2026\u201d</p><p>\u201c\u600e\u4e48\u4e86\u3002\u201d\u743c\u6069\u4e0d\u89e3\u3002</p><p>\u201c\u73ca\u5609\u59d0\u59d0\u6700\u540e\u523a\u51fa\u7684\u90a3\u4e00\u5251\uff0c\u201d\u6885\u83f2\u65af\u987f\u4e86\u987f\uff0c\u659f\u914c\u7740\u8a00\u8f9e\uff0c\u201c\u975e\u5e38\u975e\u5e38\u4e4b\u2026\u2026\u8001\u7ec3\uff0c\u6b63\u5e38\u60c5\u51b5\u4e0b\uff0c\u6ca1\u6709\u51e0\u5341\u5e74\u7684\u82e6\u7ec3\u662f\u4e0d\u53ef\u80fd\u529e\u5230\u7684\u3002\u201d</p><p>\u201c\u662f\u5417\uff1f\u201d</p><p>\u6885\u83f2\u65af\u6ca1\u6709\u6ce8\u610f\u743c\u6069\u8bed\u6c14\u4e2d\u7684\u4e9b\u8bb8\u5f02\u6837\uff0c\u201c\u6b63\u5e38\u60c5\u51b5\u4e0b\u662f\u5982\u6b64\u3002\u201d\u5979\u8bf4\u3002</p><p>\u201c\u5de7\u5408\u5427\uff0c\u201d\u743c\u6069\u7b11\u8d77\u6765\uff0c\u201c\u6211\u4ee5\u524d\u53ef\u4ece\u6765\u6ca1\u89c1\u8fc7\u5979\u63e1\u5251\u5462\uff0c\u4e00\u6b21\u90fd\u6ca1\u6709\u3002\u201d</p><p>\u201c\u6709\u53ef\u80fd\u5427\uff0c\u201d\u6885\u83f2\u65af\u70b9\u70b9\u5934\uff0c\u52c9\u5f3a\u8d5e\u540c\u743c\u6069\u7684\u8bf4\u6cd5\uff0c\u201c\u867d\u8bf4\u8fd9\u79cd\u6982\u7387\u6781\u5c0f\uff0c\u4f46\u4e5f\u786e\u5b9e\u662f\u5b58\u5728\u7684\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u6c89\u9ed8\u7247\u523b\uff0c\u743c\u6069\u8f7b\u58f0\u95ee\uff0c\u201c\u771f\u7684\u5f88\u5389\u5bb3\u4e48\uff0c\u827e\u5f25\u8587\uff1f\u201d</p><p>\u201c\u4ec0\u4e48\u771f\u7684\u5f88\u5389\u5bb3\uff1f\u201d\u6885\u83f2\u65af\u83ab\u540d\u5176\u5999\u3002</p><p>\u201c\u73ca\u5609\u6700\u540e\u90a3\u4e00\u5251\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u771f\u7684\u5f88\u5389\u5bb3\u662f\u4e48\uff1f\u201d</p><p>\u201c\u5f88\u5389\u5bb3\uff0c\u201d\u6885\u83f2\u65af\u786e\u5b9a\uff0c\u201c\u4e8b\u5b9e\u4e0a\uff0c\u5982\u679c\u4e0d\u662f\u5979\u7684\u6b65\u6cd5\u6ca1\u80fd\u914d\u5408\u4e0a\uff0c\u742a\u5a05\u73b0\u5728\u5df2\u7ecf\u662f\u4e2a\u6b7b\u4eba\u4e86\u3002\u201d</p><p>\u201c\u5982\u679c\u662f\u4f60\u5462\uff0c\u201d\u743c\u6069\u5047\u8bbe\uff0c\u201c\u6613\u5730\u800c\u5904\u7684\u8bdd\uff0c\u4f60\u80fd\u63a5\u4e0b\u8fd9\u4e00\u5251\u5427\u3002\u201d</p><p>\u201c\u6709\u9632\u5907\u7684\u8bdd\uff0c\u5e94\u8be5\u6ca1\u95ee\u9898\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u4f46\u5982\u679c\u6ca1\u6709\u9632\u5907\uff0c\u90a3\u4e48\u2026\u2026\u5f88\u5371\u9669\u3002\u201d</p><p>\u201c\u5514\u3002\u201d</p><p>\u201c\u6240\u4ee5\u4f60\u5f88\u7d27\u5f20\uff1f\u201d</p><p>\u201c\u6211\u7d27\u5f20\uff1f\u201d\u6885\u83f2\u65af\u6115\u7136\u53cd\u95ee\uff0c\u201c\u6709\u5417\uff1f\u201d</p><p>\u743c\u6069\u70b9\u70b9\u5934\u3002</p><p>\u5c11\u5973\u7ad9\u5b9a\u811a\u6b65\uff0c\u9759\u9759\u5730\u60f3\u4e86\u7247\u523b\uff0c\u7136\u540e\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u88ab\u4f60\u8fd9\u4e48\u4e00\u8bf4\uff0c\u597d\u50cf\u771f\u6709\u70b9\u5462\u3002\u201d</p><p>\u201c\u90a3\u662f\u4e3a\u4ec0\u4e48\u5462\uff1f\u201d</p><p>\u201c\u4e0d\u77e5\u9053\uff0c\u4e0d\u8fc7\u5462\uff0c\u743c\u6069\uff0c\u544a\u8bc9\u4f60\u4e00\u4ef6\u4e8b\uff0c\u201d\u6885\u83f2\u65af\u7b11\u7740\u770b\u5411\u4ed6\uff0c\u201c\u6211\u66fe\u7ecf\u8bbe\u60f3\u8fc7\u4e00\u79cd\u573a\u666f\u54e6\uff0c\u5f88\u50bb\u5f88\u50bb\u7684\u573a\u666f\u3002\u5c31\u662f\u6709\u4e00\u5929\uff0c\u73ca\u5609\u59d0\u59d0\u5bf9\u6211\u8bf4\uff0c\u6211\u4eec\u6765\u51b3\u6597\u5427\uff0c\u80dc\u5229\u8005\u7559\u4e0b\uff0c\u5931\u8d25\u8005\u79bb\u5f00\u3002\u6211\u7b54\u5e94\u4e86\uff0c\u4e8e\u662f\u51b3\u6597\u3002\u201d</p><p>\u201c\u771f\u50bb\u3002\u201d\u743c\u6069\u8bc4\u4ef7\u3002</p><p>\u201c\u662f\u554a\uff0c\u81ea\u5df1\u90fd\u89c9\u5f97\u50bb\uff0c\u5e7c\u7a1a\u6781\u4e86\uff0c\u50cf\u5c0f\u5b69\u5b50\u7684\u60f3\u6cd5\uff0c\u201d\u5c11\u5973\u5fae\u5fae\u7b11\u7740\uff0c\u201c\u4f46\u662f\u771f\u7684\u8fd9\u4e48\u60f3\u8fc7\u54e6\uff0c\u4e0d\u9a97\u4f60\u3002\u201d</p><p>\u201c\u5979\u600e\u4e48\u4f1a\u662f\u4f60\u7684\u5bf9\u624b\uff0c\u8fd9\u79cd\u51b3\u6597\u6839\u672c\u6beb\u65e0\u60ac\u5ff5\u5427\u3002\u201d</p><p>\u201c\u672a\u5fc5\u554a\uff0c\u201d\u6885\u83f2\u65af\u5f88\u8ba4\u771f\u5730\u8bf4\uff0c\u201c\u5982\u679c\u6700\u540e\u90a3\u4e00\u5251\u4e0d\u662f\u5de7\u5408\uff0c\u800c\u662f\u5979\u7684\u771f\u5b9e\u672c\u9886\uff0c\u90a3\u4e48\u6211\u53ef\u4ee5\u65ad\u8a00\uff0c\u5979\u7edd\u5bf9\u662f\u8fd9\u4e2a\u4e16\u754c\u4e0a\u7b2c\u4e00\u6d41\u7684\u5251\u58eb\uff0c\u5b8c\u5168\u6709\u548c\u6211\u4e00\u6218\u7684\u8d44\u683c\u2014\u2014\u5f53\u7136\uff0c\u6700\u7ec8\u8fd8\u662f\u6211\u4f1a\u8d62\u3002\u201d</p><p>\u201c\u4e0d\u4f1a\u6709\u90a3\u4e00\u5929\u7684\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u50cf\u662f\u5411\u5c11\u5973\u4fdd\u8bc1\uff0c\u53c8\u50cf\u662f\u81ea\u8a00\u81ea\u8bed\uff0c\u201c\u800c\u4e14\u2026\u2026\u201d\u4ed6\u6df1\u6df1\u5438\u4e86\u53e3\u6c14\uff0c\u4eff\u4f5b\u4e0b\u5b9a\u51b3\u5fc3\uff0c\u201c\u5c31\u7b97\u771f\u7684\u6709\u90a3\u4e00\u5929\uff0c\u827e\u5f25\u8587\uff0c\u4f60\u8981\u9762\u5bf9\u7684\uff0c\u53ea\u6015\u4e5f\u4e0d\u4f1a\u662f\u7b2c\u4e00\u6d41\u7684\u5251\u58eb\u3002\u201d</p><p>\u201c\u90a3\u662f\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u662f\u7b2c\u4e00\u6d41\u7684\u5deb\u5e08\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4e00\u4f4d\u5927\u5965\u672f\u5e08\u3002\u201d</p></article>"}}}]);
