(window.webpackJsonp=window.webpackJsonp||[]).push([[492],{514:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u56db\u8282 \u597d\u4eba\u5361",content:"<article><h2>\u7b2c\u4e8c\u5341\u56db\u8282 \u597d\u4eba\u5361</h2><p>\u5929\u5730\u5f00\u8f9f\uff0c\u5149\u6697\u6218\u4e89\uff0c\u65e0\u6570\u795e\u660e\u81ea\u6df7\u6c8c\u4e2d\u8bde\u751f\uff0c\u53c8\u5982\u6d41\u661f\u822c\u9668\u706d\uff0c\u5de8\u9f99\u548c\u7cbe\u7075\u76f8\u7ee7\u7edf\u6cbb\u5927\u5730\uff0c\u7136\u540e\u76f8\u7ee7\u5d29\u6e83\uff0c\u76f4\u81f3\u8010\u745f\u745e\u5c14\u5efa\u56fd\uff0c\u5176\u95f4\u5171\u8ba1\u4e00\u4e07\u4e94\u5343\u5e74\uff0c\u88ab\u5927\u5730\u5148\u77e5\u963f\u8f9b\u591a\u547d\u540d\u4e3a\u201c\u7b2c\u4e00\u7eaa\u201d\uff0c\u53c8\u79f0\u4e3a\u201c\u4f20\u8bf4\u65f6\u4ee3\u201d\u6216\u8005\u201c\u521b\u59cb\u65f6\u4ee3\u201d\u3002</p><p>\u8010\u745f\u745e\u5c14\u5e1d\u56fd\u4f20\u627f\u4e09\u5343\u4e94\u767e\u5e74\uff0c\u8bde\u751f\u4e86\u707f\u70c2\u7729\u76ee\u7684\u9b54\u6cd5\u6587\u660e\uff0c\u7ff1\u7fd4\u5929\u5b87\u7684\u6d6e\u7a7a\u57ce\u662f\u5927\u5965\u672f\u5e08\u541b\u4e34\u4e16\u754c\u7684\u8bc1\u660e\u3002\u6700\u540e\u4e00\u573a\u6d69\u52ab\uff0c\u5e1d\u56fd\u7684\u8f89\u714c\u5f7b\u5e95\u7ed3\u675f\u4e86\u3002\u540c\u6837\u6839\u636e\u963f\u8f9b\u591a\u7684\u547d\u540d\uff0c\u8fd9\u6bb5\u65f6\u95f4\u88ab\u79f0\u4e3a\u201c\u7b2c\u4e8c\u7eaa\u201d\uff0c\u53c8\u540d\u201c\u82f1\u96c4\u65f6\u4ee3\u201d\u3002</p><p>\u968f\u7740\u7b2c\u4e8c\u7eaa\u7684\u5e37\u5e55\u5f90\u5f90\u964d\u4e0b\uff0c\u7b2c\u4e09\u7eaa\u6084\u7136\u6765\u4e34\u3002\u8010\u745f\u745e\u5c14\u9668\u706d\u4e4b\u540e\uff0c\u5929\u754c\u5723\u5149\u904d\u7167\u5927\u5730\uff0c\u795e\u660e\u8d70\u4e0a\u524d\u53f0\uff0c\u6210\u4e3a\u51e1\u95f4\u7684\u771f\u6b63\u4e3b\u5bb0\u3002\u5c24\u5176\u662f\u52a8\u8361\u5e74\u4ee3\u4e4b\u540e\uff0c\u795e\u660e\u5bf9\u7269\u8d28\u754c\u7684\u63a7\u5236\u9aa4\u7136\u589e\u5f3a\uff0c\u51e0\u4e4e\u8fbe\u5230\u4e86\u7406\u8bba\u4e0a\u6240\u80fd\u5b58\u5728\u7684\u9876\u5cf0\u3002\u65f6\u81f3\u4eca\u65e5\uff0c\u6574\u4e2a\u8d39\u4f26\u5927\u9646\u51e0\u4e4e\u65e0\u5904\u6ca1\u6709\u6559\u4f1a\uff0c\u65e0\u5730\u4e0d\u5efa\u795e\u6bbf\uff0c\u65e0\u4eba\u4e0d\u662f\u4fe1\u5f92\u3002\u51e1\u4eba\u4ece\u51fa\u751f\u5230\u6b7b\u4ea1\uff0c\u4e43\u81f3\u4e8e\u6b7b\u540e\u5f52\u5bbf\uff0c\u90fd\u5728\u795e\u660e\u7684\u638c\u63e1\u4e4b\u4e2d\u3002\u4e5f\u6b63\u56e0\u4e3a\u5982\u6b64\uff0c\u7b2c\u4e09\u7eaa\u53c8\u88ab\u79f0\u4e3a\u201c\u795e\u660e\u65f6\u4ee3\u201d\u3002</p><p>\u201c\u7b2c\u56db\u7eaa\u7684\u8bf4\u6cd5\uff0c\u6700\u65e9\u51fa\u81ea\u5927\u9884\u8a00\u5e08\u963f\u5170\u591a\u7684\u8457\u4f5c\u300a\u8bf8\u4e16\u7eaa\u300b\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u963f\u5170\u591a\u6cbf\u7528\u5927\u5730\u5148\u77e5\u6709\u5173\u7b2c\u4e00\u7eaa\u548c\u7b2c\u4e8c\u7eaa\u7684\u8bf4\u6cd5\uff0c\u63d0\u51fa\u4e86\u7b2c\u4e09\u7eaa\u2018\u795e\u660e\u65f6\u4ee3\u2019\u3002\u5e76\u4e14\u5728\u300a\u8bf8\u4e16\u7eaa\u300b\u7684\u6700\u540e\u4e00\u9875\uff0c\u7528\u9690\u6666\u7684\u8bd7\u53e5\u6697\u793a\u4e86\u7b2c\u4e09\u7eaa\u7684\u7ec8\u7ed3\u548c\u7b2c\u56db\u7eaa\u7684\u5230\u6765\u3002\u201d</p><p>\u201c\u90a3\u7b2c\u56db\u7eaa\u662f\u4ec0\u4e48\u65f6\u4ee3\u5462\uff1f\u201d\u743c\u6069\u95ee\uff0c\u201c\u795e\u660e\u65f6\u4ee3\u7ed3\u675f\u4e86\uff0c\u63a5\u4e0b\u6765\u662f\u4ec0\u4e48\uff1f\u6076\u9b54\u65f6\u4ee3\uff1f\u9b54\u9b3c\u65f6\u4ee3\uff1f\u201d</p><p>\u6885\u83f2\u65af\u7b11\u7740\u6447\u6447\u5934\uff0c\u201c\u8fd9\u4e2a\u6211\u5c31\u4e0d\u77e5\u9053\u4e86\u3002\u7b2c\u56db\u7eaa\u7684\u8bf4\u6cd5\uff0c\u6211\u4e5f\u662f\u524d\u51e0\u5929\u624d\u542c\u5927\u4e3b\u6559\u63d0\u8d77\uff0c\u5c31\u77e5\u9053\u8fd9\u4e9b\u3002\u4f60\u5982\u679c\u6709\u5174\u8da3\uff0c\u6709\u7a7a\u6211\u5e26\u4f60\u53bb\u62dc\u8bbf\u5927\u4e3b\u6559\uff0c\u4f60\u76f4\u63a5\u95ee\u4ed6\u5427\u3002\u201d</p><p>\u201c\u55ef\uff0c\u597d\u554a\u3002\u201d</p><p>\u5bf9\u4e8e\u8fd9\u4f4d\u6885\u83f2\u65af\u53e3\u4e2d\u5e38\u5e38\u63d0\u53ca\u7684\u63d0\u5c14\u5927\u4e3b\u6559\uff0c\u743c\u6069\u8fd8\u771f\u7684\u662f\u633a\u597d\u5947\uff0c\u5f88\u60f3\u80fd\u591f\u89c1\u4e0a\u4e00\u9762\uff0c\u770b\u770b\u5230\u5e95\u662f\u4f55\u7b49\u4e86\u4e0d\u8d77\u7684\u4eba\u7269\u3002\u4e0d\u8fc7\u8fd9\u79cd\u4e8b\u60c5\u5012\u4e0d\u5fc5\u5fd9\u4e8e\u4e00\u65f6\uff0c\u53cd\u6b63\u5bf9\u65b9\u53ea\u662f\u4e2a\u8001\u5934\u5b50\uff0c\u53c8\u4e0d\u662f\u6f02\u4eae\u59d0\u59d0\uff0c\u65e0\u9700\u7740\u6025\uff0c\u8fd8\u662f\u5148\u966a\u8eab\u8fb9\u7684\u5973\u670b\u53cb\u901b\u8857\u6bd4\u8f83\u8981\u7d27\u3002</p><p>\u4e24\u4eba\u8d70\u8fc7\u77f3\u6865\uff0c\u8fdb\u4e86\u7279\u52a0\u5c14\u9547\u3002\u6216\u8bb8\u56e0\u4e3a\u662f\u521a\u8fc7\u5348\u996d\u65f6\u95f4\u7684\u7f18\u6545\uff0c\u8857\u9053\u4e0a\u7684\u884c\u4eba\u5e76\u4e0d\u662f\u5f88\u591a\uff0c\u743c\u6069\u4e00\u8def\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u4e1c\u5f20\u897f\u671b\uff0c\u8ba9\u6885\u83f2\u65af\u83ab\u540d\u5176\u5999\uff0c\u201c\u600e\u4e48\u4e86\uff1f\u201d\u5979\u95ee\uff0c\u201c\u4f60\u5728\u770b\u4ec0\u4e48\u554a\u3002\u201d</p><p>\u201c\u6211\u6015\u6709\u4e00\u7fa4\u4eba\u8df3\u51fa\u6765\u8981\u548c\u6211\u51b3\u6597\u554a\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5e9e\u7f57\u5148\u751f\u4e0d\u662f\u8fd9\u4e48\u544a\u8beb\u6211\u7684\u4e48\u3002\u201d</p><p>\u5c11\u5973\u6014\u4e86\u6014\uff0c\u5657\u54e7\u4e00\u58f0\u7b11\u51fa\u6765\uff0c\u201c\u5e0c\u6b27\u90a3\u5bb6\u4f19\u9a97\u4f60\u7684\u5566\u3002\u51b3\u6597\u8fd9\u79cd\u4e8b\u60c5\uff0c\u5854\u745f\u8c37\u7684\u6cd5\u5f8b\u65e9\u5728\u4e00\u767e\u4e03\u5341\u591a\u5e74\u524d\u5c31\u7981\u6b62\u4e86\u3002\u4ed6\u5f00\u4e2a\u73a9\u7b11\u800c\u5df2\uff0c\u4f60\u4e5f\u5f53\u771f\uff1f\u201d</p><p>\u201c\u2026\u2026\u592a\u8fc7\u5206\u4e86\uff01\u5bb3\u5f97\u6211\u63d0\u5fc3\u540a\u80c6\u4e86\u534a\u5929\uff0c\u4e0b\u6b21\u89c1\u5230\u4e00\u5b9a\u8981\u627e\u4ed6\u7b97\u5e10\u2014\u2014\u201d</p><p>\u201c\u5170\u5c3c\u65af\u7279\u5148\u751f\u8981\u627e\u8c01\u7b97\u5e10\u5417\uff1f\u201d</p><p>\u743c\u6069\u8bdd\u97f3\u672a\u843d\uff0c\u4e00\u4e2a\u719f\u6089\u7684\u7537\u6027\u58f0\u97f3\u4fbf\u4ece\u80cc\u540e\u54cd\u8d77\uff0c\u56de\u8eab\u770b\u53bb\u6b63\u662f\u5e0c\u6b27\uff0c\u8eab\u65c1\u8fd8\u6709\u4e24\u4e2a\u5973\u5b69\u5b50\uff0c\u4e00\u9ad8\u4e00\u77ee\uff0c\u957f\u5f97\u90fd\u633a\u6f02\u4eae\uff0c\u7709\u773c\u5c24\u5176\u7cbe\u81f4\u3002\u201c\u4e0b\u5348\u597d\uff0c\u4e24\u4f4d\uff0c\u201d\u4ed6\u7b11\u7740\u6253\u62db\u547c\uff0c\u201c\u521a\u624d\u5728\u804a\u4ec0\u4e48\u5462\uff1f\u6211\u597d\u50cf\u542c\u5230\u81ea\u5df1\u7684\u540d\u5b57\u4e86\u554a\u3002\u201d</p><p>\u201c\u54e6\uff0c\u6ca1\u4ec0\u4e48\uff0c\u4ed6\u8bf4\u5f88\u60f3\u627e\u4e2a\u673a\u4f1a\u63cd\u4f60\u4e00\u987f\uff0c\u201d\u6885\u83f2\u65af\u671d\u5e0c\u6b27\u4ee5\u53ca\u4ed6\u7684\u4e24\u4f4d\u5973\u4f34\u70b9\u70b9\u5934\uff0c\u201c\u4e0b\u5348\u597d\uff0c\u59ae\u53ef\u3001\u8309\u8389\u5a05\uff0c\u8fd9\u662f\u51c6\u5907\u53bb\u90ca\u6e38\u5417\uff1f\u201d</p><p>\u201c\u662f\u554a\uff0c\u4eca\u5929\u96be\u5f97\u4f11\u5047\uff0c\u5929\u6c14\u53c8\u8fd9\u4e48\u597d\uff0c\u4e0d\u53bb\u91ce\u5916\u8d70\u8d70\u5b9e\u5728\u592a\u53ef\u60dc\u4e86\u3002\u4e00\u8d77\u53bb\u5417\uff0c\u4e24\u4f4d\uff1f\u201d</p><p>\u201c\u4e0d\u4e86\uff0c\u6211\u4eec\u8fd8\u6709\u4e8b\u3002\u201d</p><p>\u4ea4\u8c08\u51e0\u53e5\uff0c\u5e0c\u6b27\u5148\u884c\u79bb\u53bb\u3002\u743c\u6069\u770b\u7740\u4ed6\u4e00\u624b\u63fd\u7740\u4e00\u4f4d\u5c11\u5973\uff0c\u5de6\u62e5\u53f3\u62b1\uff0c\u4e0d\u7531\u5f97\u6709\u4e9b\u5947\u602a\uff0c\u201c\u827e\u5f25\u8587\uff0c\u5723\u6b66\u58eb\u7684\u6212\u5f8b\u91cc\uff0c\u96be\u9053\u6ca1\u6709\u7981\u6b62\u8fd9\u79cd\u811a\u8e0f\u4e24\u6761\u8239\u7684\u884c\u4e3a\u5417\uff1f\u201d</p><p>\u201c\u4f60\u60c5\u6211\u613f\uff0c\u53c8\u4e0d\u662f\u91cd\u5a5a\uff0c\u8c01\u7ba1\u5f97\u7740\uff0c\u201d\u6885\u83f2\u65af\u6de1\u6de1\u8bf4\uff0c\u4f46\u663e\u7136\u5979\u5bf9\u5e0c\u6b27\u7684\u505a\u6cd5\u4e5f\u5e76\u4e0d\u662f\u5341\u5206\u6b23\u8d4f\uff0c\u201c\u5176\u5b9e\u2026\u2026\u4ee5\u524d\u4ed6\u4e5f\u4e0d\u662f\u8fd9\u6837\u7684\u3002\u201d</p><p>\u201c\u554a\uff0c\u662f\u5417\uff1f\u201d\u743c\u6069\u8be7\u5f02\uff0c\u201c\u4ee5\u524d\u4ed6\u662f\u4ec0\u4e48\u6837\u7684\uff1f\u201d</p><p>\u201c\u4f60\u6240\u80fd\u60f3\u8c61\u7684\u6700\u6807\u51c6\u7684\u5723\u6b66\u58eb\u7684\u5f62\u8c61\uff0c\u5c31\u662f\u4ed6\u4ee5\u524d\u7684\u6837\u5b50\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u4e25\u8c28\u3001\u8ba4\u771f\u3001\u4e00\u4e1d\u4e0d\u82df\uff0c\u5076\u5c14\u5f00\u4e2a\u73a9\u7b11\uff0c\u5f80\u5f80\u8fd8\u662f\u8d85\u7ea7\u51b7\u7684\u90a3\u79cd\uff0c\u8ba9\u4eba\u534a\u5929\u53cd\u5e94\u4e0d\u8fc7\u6765\u3002\u201d</p><p>\u201c\u90a3\u600e\u4e48\u53d8\u6210\u73b0\u5728\u8fd9\u6837\u4e86\uff1f\u201d\u743c\u6069\u8d8a\u53d1\u597d\u5947\u3002</p><p>\u201c\u88ab\u6253\u51fb\u4e86\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u4ed6\u66fe\u7ecf\u559c\u6b22\u4e0a\u4e86\u4e00\u4e2a\u5973\u5b69\u5b50\uff0c\u6697\u604b\u4e86\u6574\u6574\u5341\u5e74\uff0c\u7ec8\u4e8e\u6709\u4e00\u5929\u9f13\u8db3\u52c7\u6c14\u53bb\u8868\u767d\uff0c\u7ed3\u679c\u88ab\u62d2\u7edd\u4e86\u3002\u90a3\u4e2a\u5973\u5b69\u5b50\u5bf9\u4ed6\u8bf4\u4e86\u4e00\u4e9b\u8bdd\uff0c\u5177\u4f53\u5185\u5bb9\u6211\u4e0d\u77e5\u9053\uff0c\u53cd\u6b63\u6700\u540e\u4e00\u53e5\u8bdd\u662f\uff1a\u6211\u4e00\u76f4\u53ea\u628a\u4f60\u5f53\u54e5\u54e5\u770b\u5f85\u3002\u201d</p><p>\u201c\u2026\u2026\u539f\u6765\u662f\u88ab\u53d1\u597d\u4eba\u5361\u4e86\u3002\u201d</p><p>\u201c\u597d\u4eba\u5361\uff1f\u201d\u6885\u83f2\u65af\u6ca1\u542c\u61c2\uff0c\u201c\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d</p><p>\u201c\u6ca1\uff0c\u6ca1\u4ec0\u4e48\u3002\u201d</p><p>\u56e0\u4e3a\u611f\u60c5\u632b\u6298\uff0c\u5bfc\u81f4\u6027\u60c5\u5927\u53d8\uff0c\u8fd9\u79cd\u6545\u4e8b\u542c\u8d77\u6765\u867d\u7136\u8001\u5957\uff0c\u4f46\u73b0\u5b9e\u4e2d\u786e\u5b9e\u4e5f\u5e38\u89c1\uff0c\u4e0d\u8db3\u4e3a\u5947\u3002\u4e0d\u8fc7\u8bf4\u5b9e\u8bdd\uff0c\u5e0c\u6b27\u76f8\u8c8c\u5802\u5802\uff0c\u5b8c\u5168\u79f0\u5f97\u4e0a\u82f1\u4fca\uff0c\u53c8\u662f\u5e9e\u7f57\u5bb6\u65cf\u7684\u957f\u5b50\uff0c\u53ef\u8c13\u51fa\u8eab\u540d\u95e8\uff0c\u81ea\u5df1\u66f4\u662f\u63d0\u5c14\u7684\u5723\u6b66\u58eb\uff08\u63d0\u5c14\u6559\u4f1a\u4e2d\uff0c\u5723\u6b66\u58eb\u5730\u4f4d\u6781\u9ad8\uff0c\u8d85\u8d8a\u7edd\u5927\u90e8\u5206\u795e\u804c\u4eba\u5458\uff0c\u4ec5\u6b21\u4e8e\u6781\u5c11\u6570\u5927\u4e3b\u6559\u548c\u957f\u8001\uff09\uff0c\u6709\u8fd9\u6837\u7684\u6761\u4ef6\uff0c\u75f4\u60c5\u8ffd\u6c42\u5341\u5e74\uff0c\u7ed3\u679c\u8fd8\u662f\u88ab\u4eba\u7ed9\u53d1\u5361\u4e86\u3002\u4e0d\u77e5\u9053\u90a3\u4f4d\u5973\u5b69\u5b50\u53c8\u662f\u4f55\u7b49\u7684\u7edd\u8272\u7f8e\u4eba\uff0c\u773c\u5149\u5982\u6b64\u4e4b\u9ad8\uff0c\u6216\u8005\u2026\u2026\u662f\u65e9\u5c31\u5df2\u7ecf\u5fc3\u6709\u6240\u5c5e\uff1f</p><p>\u201c\u90fd\u4e0d\u662f\uff0c\u8fd9\u5176\u4e2d\u53e6\u6709\u539f\u56e0\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u5f53\u7136\u786e\u5b9e\u662f\u4e86\u4e0d\u5f97\u7684\u5927\u7f8e\u4eba\uff0c\u4ece1362DR\u52301370DR\uff0c\u63a5\u8fde\u56db\u5c4a\u2018\u661f\u4e4b\u82b1\u2019\u5934\u8854\u7684\u5f97\u4e3b\u90fd\u662f\u5979\u5462\u3002\u201d</p><p>\u201c\u54c7\uff0c\u5389\u5bb3\u554a\u3002\u201d</p><p>\u201c\u52a8\u5fc3\u4e86\u5427\uff1f\u201d\u5c11\u5973\u767d\u4e86\u4ed6\u4e00\u773c\uff0c\u201c\u542c\u6211\u8fd9\u4e48\u4e00\u8bf4\uff0c\u662f\u4e0d\u662f\u8feb\u4e0d\u53ca\u5f85\u5730\u60f3\u89c1\u89c1\u672c\u4eba\u554a\uff0c\u4f60\u8fd9\u4e2a\u5927\u8272\u72fc\u3002\u201d</p><p>\u201c\u6ca1\u6709\u6ca1\u6709\uff0c\u201d\u743c\u6069\u8d76\u7d27\u5426\u8ba4\uff0c\u201c\u6709\u4f60\u5728\u8eab\u8fb9\uff0c\u6211\u600e\u4e48\u8fd8\u4f1a\u60f3\u89c1\u522b\u7684\u5973\u4eba\u3002\u201d</p><p>\u201c\u9a97\u4eba\uff0c\u624d\u4e0d\u4fe1\uff0c\u201d\u6885\u83f2\u65af\u54fc\u4e86\u58f0\uff0c\u201c\u7537\u4eba\u90fd\u4e00\u6837\uff0c\u542c\u5230\u7f8e\u5973\u5c31\u4e24\u773c\u653e\u5149\u3002\u54ea\u6015\u8eab\u8fb9\u5df2\u7ecf\u6709\u4e86\u6700\u597d\u7684\uff0c\u603b\u8fd8\u662f\u60f3\u7740\u66f4\u591a\u2014\u2014\u4e0d\u8fc7\u4f60\u60f3\u89c1\u4e5f\u89c1\u4e0d\u5230\uff0c\u5979\u65e9\u5c31\u79bb\u5f00\u5854\u745f\u8c37\u4e86\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d</p><p>\u201c\u5f88\u5931\u671b\u5427\uff1f\u201d</p><p>\u201c\u6ca1\u6709\uff0c\u771f\u7684\u6ca1\u6709\uff0c\u201d\u743c\u6069\u6446\u6446\u624b\uff0c\u201c1362DR\u5c31\u83b7\u5f97\u661f\u4e4b\u82b1\uff0c\u73b0\u5728\u90fd1374DR\u4e86\uff0c\u4f30\u8ba1\u8be5\u6709\u4e09\u5341\u5c81\u4e86\u5427\uff0c\u6211\u662f\u559c\u6b22\u7f8e\u5973\uff0c\u4f46\u4e0d\u559c\u6b22\u8001\u5973\u4eba\u554a\u3002\u201d</p><p>\u201c\u6211\u4e5f\u4f1a\u53d8\u8001\u554a\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u58f0\u97f3\u5e73\u9759\u4e2d\u5374\u900f\u51fa\u51db\u51bd\u6740\u6c14\uff0c\u201c\u4e5f\u5c31\u662f\u8bf4\uff0c\u518d\u8fc7\u5341\u51e0\u5e74\uff0c\u7b49\u6211\u4e5f\u4f1a\u53d8\u6210\u8001\u5973\u4eba\uff0c\u4f60\u5c31\u4e0d\u4f1a\u518d\u559c\u6b22\u6211\u4e86\u662f\u5427\u3002\u201d</p><p>\u201c\u4e0d\uff0c\u6211\u4e0d\u662f\u8fd9\u4e2a\u610f\u601d\u2014\u2014\u201d</p><p>\u201c\u660e\u660e\u5c31\u662f\u8fd9\u4e2a\u610f\u601d\uff01\u201d</p><p>\u201c\u2026\u2026\u6211\u9519\u4e86\u3002\u201d</p><p>\u5e74\u9f84\u548c\u8870\u8001\uff0c\u6c38\u8fdc\u662f\u5973\u4eba\u6700\u5fcc\u8bb3\u7684\u8bdd\u9898\uff0c\u743c\u6069\u4e00\u65f6\u5931\u8a00\uff0c\u53ea\u597d\u8d76\u7d27\u9053\u6b49\uff0c\u597d\u5728\u6885\u83f2\u65af\u4f3c\u4e4e\u5e76\u6ca1\u6709\u5f53\u771f\u751f\u6c14\u2014\u2014\u6216\u8005\u8bf4\uff0c\u5979\u5e76\u6ca1\u6709\u771f\u7684\u5728\u610f\u3002\u201c\u5bf9\u4e86\uff0c\u827e\u5f25\u8587\uff0c\u201d\u743c\u6069\u8f6c\u79fb\u8bdd\u9898\uff0c\u201c\u6211\u597d\u50cf\u542c\u8bf4\u8fc7\uff0c\u63d0\u5c14\u2026\u2026\u5443\uff0c\u6b63\u4e49\u4e4b\u795e\u7684\u5723\u6b66\u58eb\uff0c\u4e5f\u662f\u53ef\u4ee5\u50cf\u9009\u6c11\u4e00\u6837\u9752\u6625\u6c38\u9a7b\u7684\u5427\u3002\u201d</p><p>\u201c\u90a3\u662f\u2018\u5723\u5316\u2019\uff0c\u548c\u9009\u6c11\u8fd8\u662f\u4e0d\u4e00\u6837\u7684\u3002\u201d</p><p>\u9009\u6c11\u501f\u52a9\u795e\u529b\uff0c\u80fd\u591f\u6781\u5927\u5730\u5ef6\u957f\u5bff\u547d\uff0c\u540c\u65f6\u5ef6\u7f13\u8870\u8001\uff0c\u76f8\u5f53\u4e8e\u201c\u7a00\u91ca\u201d\uff0c\u628a\u539f\u672c\u7684\u4e00\u4efd\u65f6\u95f4\u53d8\u6210\u5341\u4efd\u751a\u81f3\u767e\u4efd\uff1b\u5723\u5316\u5219\u662f\u63d0\u5c14\u5723\u6b66\u58eb\u7684\u4e00\u79cd\u795e\u8d50\u80fd\u529b\uff0c\u5e76\u4e0d\u4f1a\u5ef6\u957f\u5bff\u547d\uff0c\u800c\u662f\u8ba9\u8eab\u4f53\u673a\u80fd\u6c38\u8fdc\u4fdd\u6301\u5728\u6700\u5dc5\u5cf0\u65f6\u7684\u72b6\u6001\uff0c\u5bb9\u989c\u5219\u7ef4\u6301\u83b7\u5f97\u5723\u5316\u65f6\u7684\u6a21\u6837\uff0c\u81f3\u6b7b\u4e0d\u53d8\u3002</p><p>\u201c\u5723\u5316\u662f\u6700\u9ad8\u9636\u5723\u6b66\u58eb\u624d\u80fd\u83b7\u5f97\u7684\u795e\u6069\uff0c\u6211\u662f\u6ca1\u6709\u7684\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u4f46\u6211\u662f\u795e\u5b50\uff0c\u4ee5\u62e5\u6709\u7684\u795e\u529b\u603b\u91cf\u800c\u8bba\uff0c\u751a\u81f3\u80dc\u8fc7\u4f4e\u7ea7\u795e\u660e\uff0c\u957f\u751f\u4e0d\u6b7b\u529e\u4e0d\u5230\uff0c\u5982\u679c\u53ea\u662f\u4fdd\u6301\u5bb9\u989c\u4e0d\u8001\uff0c\u90a3\u662f\u8f7b\u800c\u6613\u4e3e\u7684\u4e8b\u60c5\u3002\u201d</p><p>\u201c\u90a3\u4f60\u751f\u6c14\u4ec0\u4e48\u554a\u3002\u201d</p><p>\u201c\u6211\u662f\u751f\u6c14\u4f60\u8fd9\u79cd\u559c\u65b0\u538c\u65e7\u7684\u5ff5\u5934\uff01\u201d</p><p>\u201c\u597d\u5427\uff0c\u8fd8\u662f\u6211\u9519\u4e86\u2026\u2026\u54a6\uff0c\u8fd9\u662f\u54ea\u91cc\uff1f\u201d</p><p>\u4e0d\u77e5\u4e0d\u89c9\u95f4\uff0c\u743c\u6069\u5df2\u7ecf\u8ddf\u7740\u6885\u83f2\u65af\u8d70\u5230\u4e86\u4e00\u5ea7\u9662\u5b50\u524d\uff0c\u6b8b\u7834\u7684\u56f4\u5899\u548c\u6742\u8349\u4e1b\u751f\u7684\u5ead\u9662\uff0c\u4ee5\u53ca\u9508\u8ff9\u6591\u6591\u7684\u5927\u94c1\u95e8\uff0c\u90fd\u6e05\u695a\u8868\u660e\u6b64\u5904\u5df2\u7ecf\u8352\u5e9f\u591a\u5e74\uff0c\u7406\u5e94\u65e0\u4eba\u5c45\u4f4f\u3002\u201c\u4f60\u5728\u8fd9\u7b49\u6211\u4e00\u4f1a\uff0c\u201d\u5c11\u5973\u5bf9\u743c\u6069\u8bf4\uff0c\u4e00\u8fb9\u5c06\u8116\u9888\u4e0a\u7684\u8f89\u9633\u62a4\u7b26\u53d6\u4e0b\uff0c\u5c0f\u5fc3\u5730\u653e\u8fdb\u6000\u91cc\uff0c\u201c\u6211\u8fdb\u53bb\u4e00\u8d9f\uff0c\u5f88\u5feb\u5c31\u51fa\u6765\u3002\u201d</p><p>\u743c\u6069\u6b63\u8981\u7b54\u5e94\uff0c\u4e00\u4e2a\u67af\u6da9\u65e0\u6bd4\uff0c\u9690\u7ea6\u5e26\u7740\u523a\u8033\u6469\u64e6\u7684\u58f0\u97f3\u7a81\u7136\u5728\u4e24\u4eba\u7684\u8033\u8fb9\u54cd\u8d77\uff0c\u201c\u4ed6\u662f\u4f60\u7684\u670b\u53cb\uff1f\u201d</p><p>\u6885\u83f2\u65af\u6014\u4e86\u6014\uff0c\u201c\u662f\u3002\u201d</p><p>\u743c\u6069\u62ac\u8d77\u5934\uff0c\u60f3\u8981\u5bfb\u627e\u58f0\u97f3\u7684\u6765\u6e90\uff0c\u5374\u4e00\u65e0\u6240\u83b7\uff0c\u4f46\u4ed6\u80fd\u591f\u6e05\u695a\u5730\u611f\u89c9\u5230\u81ea\u5df1\u6b63\u5728\u88ab\u6765\u81ea\u6697\u4e2d\u7684\u76ee\u5149\u6240\u6ce8\u89c6\u2014\u2014\u800c\u4e14\u90a3\u76ee\u5149\u4e2d\u5e76\u4e0d\u5305\u542b\u4e1d\u6beb\u6e29\u6696\u548c\u5584\u610f\u3002\u201c\u8ba9\u4ed6\u4e5f\u8fdb\u6765\u5427\uff0c\u201d\u51e0\u79d2\u949f\u540e\uff0c\u90a3\u4e2a\u58f0\u97f3\u53c8\u5728\u8033\u8fb9\u54cd\u8d77\uff0c\u201c\u6211\u5df2\u7ecf\u5f88\u4e45\u6ca1\u6709\u89c1\u8fc7\u8fd9\u4e48\u5e74\u8f7b\u7684\u5deb\u5e08\u4e86\u3002\u201d</p><p>\u201c\u554a\uff1f\u53ef\u662f\u2026\u2026\u201d</p><p>\u5927\u7ea6\u662f\u5bf9\u65b9\u7684\u63d0\u8bae\u592a\u8fc7\u4e8e\u51fa\u4e4e\u610f\u6599\uff0c\u6885\u83f2\u65af\u660e\u663e\u6709\u4e9b\u60ca\u8bb6\uff0c\u5979\u770b\u5411\u743c\u6069\uff0c\u7528\u76ee\u5149\u5f81\u8be2\u7740\u4ed6\u7684\u610f\u601d\uff0c\u800c\u540e\u8005\u8038\u8038\u80a9\uff0c\u8868\u793a\u65e0\u6240\u8c13\u3002</p><p>\u201c\u8d70\u5427\uff0c\u201d\u5c11\u5973\u7565\u4e00\u6c89\u541f\uff0c\u968f\u5373\u505a\u51fa\u51b3\u5b9a\uff0c\u201c\u4f60\u8ddf\u7740\u6211\u3002\u201d</p><p>\u95e8\u5728\u4ed6\u4eec\u9762\u524d\u81ea\u884c\u6253\u5f00\u4e86\uff0c\u53d1\u51fa\u5431\u5440\u5431\u5440\u7684\u96be\u542c\u58f0\u97f3\uff0c\u50cf\u662f\u957f\u4e45\u6ca1\u6709\u4f7f\u7528\uff0c\u5df2\u7ecf\u673d\u574f\u3002\u6885\u83f2\u65af\u63e1\u7740\u743c\u6069\u7684\u624b\uff0c\u5e26\u7740\u4ed6\u8d70\u8fdb\u623f\u5b50\uff0c\u7136\u540e\u7830\u5730\u4e00\u58f0\uff0c\u95e8\u5728\u80cc\u540e\u53c8\u81ea\u5df1\u5173\u4e0a\u4e86\uff0c\u5ba4\u5185\u987f\u65f6\u4e00\u7247\u9ed1\u6697\u3002\u7d27\u63a5\u7740\uff0c\u7eff\u8272\u3001\u7d2b\u8272\u548c\u6d45\u84dd\u8272\u7684\u706b\u7130\u540c\u65f6\u5728\u5730\u9762\u71c3\u8d77\uff0c\u50cf\u86c7\u4e00\u6837\u6e38\u8d70\u7740\uff0c\u63cf\u7ed8\u6210\u4e00\u4e2a\u7ed3\u6784\u7b80\u5355\u7684\u9b54\u6cd5\u9635\u3002</p><p>\u201c\u7ad9\u7a33\uff01\u201d\u6885\u83f2\u65af\u63d0\u9192\u3002</p><p>\u8bdd\u97f3\u672a\u843d\uff0c\u743c\u6069\u5df2\u7ecf\u611f\u89c9\u811a\u4e0b\u4e00\u7a7a\uff0c\u539f\u672c\u575a\u5b9e\u7684\u5730\u9762\u50cf\u662f\u53d8\u6210\u4e86\u865a\u65e0\uff0c\u6574\u4e2a\u4eba\u5239\u90a3\u95f4\u5760\u843d\u4e0b\u53bb\u3002\u4ed6\u65e9\u6709\u5fc3\u7406\u51c6\u5907\uff0c\u867d\u60ca\u4e0d\u4e71\uff0c\u6b63\u5f85\u6fc0\u53d1\u6d6e\u7a7a\u672f\uff0c\u4e00\u5f00\u59cb\u90a3\u4e2a\u67af\u6da9\u7684\u58f0\u97f3\u7a81\u7136\u53c8\u5728\u8033\u8fb9\u54cd\u8d77\uff0c\u201c\u6b62\uff01\u201d</p><p>\u968f\u7740\u8fd9\u4e00\u58f0\u5410\u51fa\uff0c\u743c\u6069\u53d1\u73b0\u81ea\u5df1\u518d\u5ea6\u8e0f\u4e0a\u5b9e\u5730\uff0c\u4f46\u5df2\u7ecf\u4e0d\u518d\u662f\u90a3\u95f4\u623f\u5c4b\u4e2d\uff0c\u66f4\u50cf\u662f\u4e2a\u5730\u4e0b\u5ba4\uff0c\u5934\u9876\u3001\u5899\u58c1\u4ee5\u53ca\u811a\u4e0b\uff0c\u90fd\u662f\u88ab\u7cbe\u7ec6\u6253\u78e8\u8fc7\u7684\u77f3\u5757\uff0c\u5e73\u6ed1\u5982\u955c\u3002\u5730\u4e0b\u5ba4\u4e0d\u5927\uff0c\u88ab\u5404\u79cd\u4ed6\u53eb\u4e0d\u51fa\u540d\u5b57\u7684\u5947\u602a\u4eea\u5668\u5806\u5f97\u6ee1\u6ee1\u5f53\u5f53\uff0c\u51e0\u4e4e\u6ca1\u6709\u7ed9\u4eba\u7559\u4e0b\u8d70\u8def\u7684\u901a\u9053\u3002\u6885\u83f2\u65af\u4f9d\u65e7\u5728\u4ed6\u8eab\u65c1\uff0c\u800c\u5728\u524d\u65b9\u76f8\u8ddd\u5341\u4f59\u5c3a\u5904\uff0c\u4e00\u4e2a\u6d88\u7626\u65e0\u6bd4\u7684\u4eba\u5f71\u4ece\u4eea\u5668\u5806\u4e2d\u6162\u6162\u76f4\u8d77\u8170\u6765\uff0c\u7ea2\u5149\u95ea\u70c1\u7684\u53cc\u773c\u51dd\u89c6\u7740\u743c\u6069\u3002\u6c34\u6676\u540a\u706f\u7684\u5149\u8292\u6d12\u5728\u4ed6\u7684\u9ab7\u9ac5\u8138\u4e0a\uff0c\u53cd\u5c04\u51fa\u8c61\u7259\u822c\u7684\u4e73\u767d\u8272\u5fae\u5149\u3002</p><p>\u743c\u6069\u5012\u5438\u4e86\u4e00\u53e3\u51b7\u6c14\u3002</p><p>\u201c\u5deb\u5996\uff01\u201d</p></article>"}}}]);
