(window.webpackJsonp=window.webpackJsonp||[]).push([[493],{515:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u4e94\u8282 \u5927\u957f\u8001",content:"<article><h2>\u7b2c\u4e8c\u5341\u4e94\u8282 \u5927\u957f\u8001</h2><p>\u743c\u6069\u5e76\u4e0d\u662f\u4e4b\u524d\u6ca1\u89c1\u8fc7\u5deb\u5996\u2014\u2014\u4e8b\u5b9e\u4e0a\u4ed6\u7ecf\u5e38\u78b0\u89c1\uff0c\u800c\u4e14\u8fd8\u4e2a\u4e2a\u90fd\u662f\u9876\u7ea7\u89d2\u8272\u3002\u7b2c\u4e00\u6b21\u51fa\u9634\u9b42\u57ce\u4ed6\u5c31\u9047\u5230\u4e86\u62c9\u6c83\u514b\uff0c\u56de\u7a0b\u7684\u9014\u4e2d\u53c8\u78b0\u89c1\u4e86\u5965\u6c83\uff0c\u8fd9\u4e24\u4f4d\u90fd\u662f\u5b58\u5728\u4e8e\u6b64\u4e16\u8d85\u8fc7\u4e24\u5343\u5e74\u7684\u5927\u5965\u672f\u5e08\uff1b\u63a5\u7740\u53bb\u585e\u5c14\uff0c\u53c8\u78b0\u5230\u4e86\u8428\u624e\u65af\u5766\uff0c\u8fd9\u4f4d\u662f\u7ea2\u888d\u5deb\u5e08\u7684\u9886\u8896\uff1b\u56de\u5230\u9634\u9b42\u57ce\u6ca1\u591a\u4e45\uff0c\u53bb\u4e86\u5e7d\u6697\u5730\u57df\uff0c\u8fd9\u6b21\u649e\u4e0a\u7684\u662f\u4e00\u4f4d\u7075\u5438\u602a\u5deb\u5996\uff0c\u867d\u7136\u6bd4\u4e0d\u5f97\u524d\u51e0\u4f4d\u540d\u58f0\u663e\u8d6b\uff0c\u4e5f\u66fe\u7ecf\u662f\u5965\u7075\u591a\u5c14\u7684\u957f\u8001\u4e4b\u4e00\uff1b\u63a5\u7740\u5728\u5e7d\u6697\u5730\u57df\u5f85\u7740\uff0c\u4e00\u4e0d\u5c0f\u5fc3\u5760\u5165\u4e0b\u5c42\u754c\uff0c\u7ed3\u679c\u5c45\u7136\u649e\u4e0a\u4e86\u8428\u739b\u65af\u7279\uff0c\u6614\u65e5\u540d\u9707\u5927\u9646\u7684\u9f99\u5deb\u6559\u4e4b\u4e3b\u3002</p><p>\u65e2\u7136\u5df2\u7ecf\u6709\u5982\u6b64\u201c\u4e30\u5bcc\u591a\u5f69\u201d\u7684\u7ecf\u5386\uff0c\u6309\u9053\u7406\u8bf4\u743c\u6069\u5bf9\u5deb\u5996\u8fd9\u79cd\u5b58\u5728\u5df2\u7ecf\u53f8\u7a7a\u89c1\u60ef\u4e86\u624d\u5bf9\uff0c\u7136\u800c\u8fd9\u4e00\u6b21\uff0c\u4ed6\u8fd8\u662f\u7ed3\u7ed3\u5b9e\u5b9e\u5730\u5403\u4e86\u4e00\u60ca\u3002\u56e0\u4e3a\uff0c\u8fd9\u91cc\u662f\u5854\u745f\u8c37\uff0c\u662f\u63d0\u5c14\u6559\u4f1a\u8c37\u5730\u6559\u533a\u7684\u5927\u603b\u90e8\u6240\u5728\u554a\u3002</p><p>\u80fd\u591f\u8eb2\u5728\u6b63\u4e49\u4e4b\u795e\u7684\u773c\u76ae\u5e95\u4e0b\uff0c\u8fd9\u4f4d\u5deb\u5996\u7684\u9690\u9041\u80fd\u529b\u4e5f\u672a\u514d\u592a\u5f3a\u4e86\u5427\u2014\u2014\u4e0d\uff0c\u4e0d\u5bf9\uff0c\u8fd9\u5730\u65b9\u5e76\u975e\u5076\u7136\u53d1\u73b0\uff0c\u800c\u662f\u6885\u83f2\u65af\u4e3b\u52a8\u8981\u6765\u7684\uff0c\u773c\u524d\u7684\u5deb\u5996\u5c31\u662f\u5979\u8981\u627e\u7684\u4eba\uff0c\u800c\u4e14\u542c\u4ed6\u4eec\u5bf9\u8bdd\uff0c\u5f88\u660e\u663e\u662f\u65e7\u8bc6\u3002\u5982\u6b64\u8bf4\u6765\uff0c\u83ab\u975e\u8fd9\u540d\u5deb\u5996\u548c\u5979\u4e00\u6837\uff0c\u4e5f\u662f\u63d0\u5c14\u6559\u4f1a\u7684\u6210\u5458\uff1f</p><p>\u4e0d\u81f3\u4e8e\u5427\u2026\u2026</p><p>\u867d\u8bf4\u63d0\u5c14\u662f\u6b63\u4e49\u4e4b\u795e\uff0c\u4e0d\u50cf\u5170\u68ee\u5fb7\u5c14\uff08\u6668\u66e6\u4e4b\u795e\uff09\u6216\u8005\u514b\u5170\u6c83\uff08\u6b7b\u795e\uff09\u90a3\u6837\u5bf9\u4ea1\u7075\u6df1\u6076\u75db\u7edd\uff0c\u4f46\u5deb\u5996\u8fd9\u79cd\u5b58\u5728\uff0c\u56e0\u4e3a\u53d7\u5230\u8d1f\u80fd\u91cf\u7684\u4fb5\u67d3\uff0c\u65e0\u8bba\u751f\u524d\u6709\u591a\u4e48\u5584\u826f\uff0c\u9ed1\u6697\u548c\u90aa\u6076\u90fd\u662f\u5b83\u6700\u7ec8\u7684\u5f52\u5bbf\u3002\u6240\u4ee5\u4ece\u672c\u8d28\u4e0a\u8bf4\uff0c\u5deb\u5996\u548c\u5584\u795e\u5c31\u662f\u5b8c\u5168\u5bf9\u7acb\uff0c\u65e0\u6cd5\u76f8\u5bb9\u7684\u3002\u552f\u4e00\u4f8b\u5916\u7684\u662f\u201c\u9ad8\u7b49\u5deb\u5996\u201d\uff0c\u4f8b\u5982\u62c9\u6c83\u514b\u624b\u4e0b\u7684\u90a3\u4f4d\u745e\u54c8\u683c\u62c9\u65af\u5148\u751f\uff0c\u5728\u8f6c\u5316\u65f6\u4f7f\u7528\u4e86\u66f4\u4e25\u82db\u66f4\u5371\u9669\u7684\u7279\u6b8a\u65b9\u6cd5\uff0c\u4ece\u800c\u80fd\u591f\u4fdd\u6301\u539f\u672c\u7684\u5584\u826f\u5929\u6027\u3002\u4f46\u666e\u901a\u5deb\u5996\u548c\u9ad8\u7b49\u5deb\u5996\u7684\u533a\u522b\u662f\u5f88\u660e\u663e\u7684\uff1a\u524d\u8005\u773c\u7736\u91cc\u7684\u5149\u8292\u662f\u523a\u773c\u7684\u6a58\u7ea2\u8272\uff0c\u540e\u8005\u5219\u662f\u5fae\u5f31\u7684\u94f6\u767d\u8272\u3002\u800c\u7ad9\u5728\u743c\u6069\u9762\u524d\u7684\u8fd9\u4f4d\uff0c\u7a7a\u6d1e\u773c\u7736\u91cc\u6cdb\u7740\u7684\u662f\u4e0d\u6298\u4e0d\u6263\u7684\u7ea2\u5149\uff0c\u6b63\u662f\u6700\u6807\u51c6\u7684\u90aa\u6076\u5deb\u5996\u65e0\u7591\u2014\u2014\u9664\u975e\u4ed6\u6234\u4e86\u80fd\u591f\u53d8\u8272\u7684\u9690\u5f62\u773c\u955c\u3002</p><p>\u8fd9\u662f\u600e\u4e48\u4e00\u56de\u4e8b\u554a\uff1f</p><p>\u4ed6\u8fd8\u6ca1\u53cd\u5e94\u8fc7\u6765\uff0c\u65c1\u8fb9\u6885\u83f2\u65af\u548c\u5deb\u5996\u7684\u4ea4\u8c08\u5df2\u7ecf\u5f00\u59cb\u4e86\u3002\u201c\u6c49\u5bc6\u5c14\u987f\u957f\u8001\uff0c\u201d\u5979\u7a0d\u7a0d\u8eac\u8eab\uff0c\u6709\u4e9b\u968f\u610f\u5730\u884c\u4e86\u4e2a\u793c\uff0c\u201c\u4e0b\u5348\u597d\u3002\u201d</p><p>\u957f\u8001\uff1f</p><p>\u743c\u6069\u542c\u5230\u4e86\u8fd9\u4e2a\u8bcd\uff0c\u4ed6\u7684\u601d\u7ef4\u7a0d\u7a0d\u53d8\u5f97\u6e05\u6670\u4e86\u4e00\u4e9b\u3002\u548c\u6885\u83f2\u65af\u5728\u4e00\u8d77\u5f85\u4e45\u4e86\uff0c\u5bf9\u63d0\u5c14\u6559\u4f1a\u7684\u4f4d\u9636\u4f53\u7cfb\u4e5f\u7b97\u662f\u6709\u6240\u4e86\u89e3\u3002\u5927\u4e3b\u6559\u6700\u9ad8\uff0c\u662f\u4e00\u4e2a\u6559\u533a\u7684\u9886\u8896\uff0c\u53c8\u79f0\u201c\u8861\u5e73\u8005\u201d\uff0c\u5176\u4e0b\u662f\u526f\u4e3b\u6559\uff0c\u901a\u5e38\u662f\u4e24\u81f3\u56db\u540d\uff0c\u79f0\u201c\u660e\u5bdf\u8005\u201d\uff0c\u518d\u5176\u4e0b\u662f\u56db\u7ea7\u6b63\u5f0f\u7267\u5e08\uff0c\u5206\u522b\u662f\u201c\u5236\u6cd5\u8005\u201d\u3001\u201c\u6301\u5f8b\u8005\u201d\u3001\u201c\u88c1\u5224\u8005\u201d\u3001\u201c\u5b88\u62a4\u8005\u201d\uff0c\u6700\u540e\u5219\u662f\u89c1\u4e60\u7267\u5e08\u201c\u8bf5\u4e60\u8005\u201d\uff0c\u4e00\u5171\u4e03\u9636\u3002\u5728\u6b64\u4e4b\u5916\uff0c\u53c8\u6709\u201c\u5723\u6b66\u58eb\u201d\u548c\u201c\u5927\u957f\u8001\u201d\uff0c\u72ec\u7acb\u4e8e\u4e03\u9636\u4e4b\u5916\uff0c\u524d\u8005\u662f\u795e\u660e\u9009\u4e2d\u7684\u6218\u58eb\uff0c\u540e\u8005\u5219\u5f80\u5f80\u662f\u5fb7\u9ad8\u671b\u5c0a\u7684\u8001\u524d\u8f88\u3002\u6885\u83f2\u65af\u521a\u521a\u79f0\u547c\u8fd9\u540d\u5deb\u5996\u4e3a\u201c\u6c49\u5bc6\u5c14\u987f\u957f\u8001\u201d\uff0c\u90a3\u4e48\u4ed6\u679c\u7136\u662f\u63d0\u5c14\u6559\u4f1a\u7684\u6210\u5458\u4e86\uff0c\u800c\u4e14\u5730\u4f4d\u8fd8\u975e\u5e38\u9ad8\u2014\u2014\u5927\u957f\u8001\u56e0\u4e3a\u8d44\u5386\u8f88\u5206\u7684\u5173\u7cfb\uff0c\u867d\u7136\u540d\u4e49\u4e0a\u7684\u4f4d\u9636\u53ea\u7b49\u540c\u4e8e\u526f\u4e3b\u6559\uff0c\u5176\u5b9e\u5f80\u5f80\u662f\u5927\u4e3b\u6559\u89c1\u4e86\u90fd\u8981\u793c\u656c\u7684\u89d2\u8272\u3002</p><p>\u53ea\u4e0d\u8fc7\uff0c\u63d0\u5c14\u5c45\u7136\u4f1a\u6311\u4e00\u4e2a\u5deb\u5996\u505a\u5927\u957f\u8001\uff0c\u770b\u6765\u6b63\u4e49\u4e4b\u795e\u679c\u7136\u662f\u4e2a\u778e\u5b50\u2026\u2026</p><p>\u201c\u4e0d\u7528\u8868\u73b0\u5f97\u8fd9\u4e48\u60ca\u8bb6\uff0c\u5e74\u8f7b\u4eba\uff0c\u201d\u4eff\u4f5b\u770b\u7a7f\u4e86\u743c\u6069\u7684\u5fc3\u601d\uff0c\u5deb\u5996\u6162\u60a0\u60a0\u5730\u5f00\u53e3\uff0c\u201c\u5df4\u5c14\u7684\u5973\u513f\u80fd\u505a\u5723\u6b66\u58eb\uff0c\u6211\u4e00\u4e2a\u5deb\u5996\u957f\u8001\u6709\u4ec0\u4e48\u5947\u602a\u7684\u3002\u6b8b\u75be\u4eba\u7684\u5fc3\u7406\uff0c\u4ece\u6765\u90fd\u662f\u6bd4\u8f83\u53e6\u7c7b\uff0c\u8ba9\u4eba\u6349\u6478\u4e0d\u900f\uff0c\u65e0\u9700\u592a\u5728\u610f\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u6240\u8c13\u6b8b\u75be\u4eba\u4e91\u4e91\uff0c\u6beb\u65e0\u7591\u95ee\u662f\u6307\u63d0\u5c14\u4e86\u3002\u56e0\u4e3a\u6839\u636e\u6559\u4f1a\u5178\u7c4d\u8bb0\u8f7d\uff0c\u6b63\u4e49\u4e4b\u795e\u6bcf\u6b21\u5728\u51e1\u95f4\u663e\u5723\uff0c\u90fd\u662f\u4ee5\u53cc\u76ee\u5168\u76f2\u3001\u53f3\u624b\u9f50\u8155\u800c\u65ad\u7684\u91cd\u5ea6\u6b8b\u75be\u5f62\u8c61\u51fa\u73b0\u3002\u5deb\u5996\u7684\u8bdd\uff0c\u5012\u4e5f\u4e0d\u80fd\u8bf4\u5168\u7136\u6ca1\u6709\u9053\u7406\uff0c\u4f46\u5c45\u7136\u4ee5\u8fd9\u79cd\u53e3\u6c14\u63d0\u53ca\u81ea\u5df1\u4fe1\u4ef0\u7684\u795e\u7947\uff0c\u8bf4\u4e25\u91cd\u70b9\u662f\u4eb5\u6e0e\uff0c\u8bf4\u8f7b\u70b9\u4e5f\u662f\u4e0d\u656c\u5427\u3002</p><p>\u4f46\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u8fd9\u540d\u5deb\u5996\u7684\u957f\u8001\u8eab\u4efd\uff0c\u770b\u6765\u662f\u786e\u5b9a\u65e0\u7591\u4e86\u3002</p><p>\u6885\u83f2\u65af\u9002\u65f6\u63d2\u8bdd\uff0c\u628a\u743c\u6069\u4ece\u77a0\u76ee\u7ed3\u820c\u7684\u77f3\u5316\u72b6\u6001\u4e2d\u89e3\u653e\u51fa\u6765\uff0c\u201c\u957f\u8001\uff0c\u5bf9\u90a3\u4e24\u4ef6\u4e1c\u897f\u7684\u5206\u6790\u6709\u7ed3\u679c\u4e86\u5417\uff1f\u201d\u5979\u95ee\uff0c\u201c\u5927\u4e3b\u6559\u6b63\u7b49\u5f85\u60a8\u7684\u6d88\u606f\u5462\u3002\u201d</p><p>\u201c\u6709\u4e86\u6709\u4e86\uff0c\u201d\u5deb\u5996\u8bf4\uff0c\u201c\u548c\u4f60\u9884\u60f3\u7684\u4e00\u6837\u3002\u201d</p><p>\u4ed6\u4ece\u9ed1\u8272\u957f\u888d\u4e2d\u4f38\u51fa\u53f3\u624b\uff0c\u767d\u68ee\u68ee\u7684\u9aa8\u638c\u4e2d\u6258\u7740\u4e24\u4ef6\u4e1c\u897f\uff1a\u4e00\u67c4\u9aa8\u6756\u548c\u4e00\u679a\u6212\u6307\uff0c\u5728\u706f\u5149\u4e0b\u6298\u5c04\u7740\u51b7\u5e7d\u5e7d\u7684\u5fae\u5149\u3002\u743c\u6069\u4e0d\u8ba4\u8bc6\u6212\u6307\uff0c\u5374\u89c9\u5f97\u9aa8\u6756\u6709\u4e9b\u773c\u719f\uff0c\u518d\u770b\u4e24\u773c\u60f3\u4e86\u8d77\u6765\uff1a\u8fd9\u4e0d\u5c31\u662f\u6628\u5929\u5e72\u6389\u7684\u90a3\u540d\u82cd\u767d\u9886\u4e3b\u624b\u4e2d\u62ff\u7740\u7684\u6cd5\u6756\u4e48\u3002\u5f53\u65f6\u4ed6\u5fd9\u7740\u7528\u98d3\u98ce\u963b\u6321\u9aa8\u9f99\uff0c\u82cd\u767d\u9886\u4e3b\u662f\u6885\u83f2\u65af\u89e3\u51b3\u7684\uff0c\u6218\u5229\u54c1\u81ea\u7136\u4e5f\u843d\u5165\u5979\u624b\u4e2d\uff0c\u743c\u6069\u538b\u6839\u6ca1\u8fc7\u95ee\uff0c\u51e0\u4e4e\u90fd\u7ed9\u5fd8\u4e86\u3002</p><p>\u50cf\u662f\u5f97\u5230\u4ec0\u4e48\u6307\u4ee4\uff0c\u9aa8\u6756\u4ece\u4ed6\u7684\u638c\u5fc3\u7f13\u7f13\u98de\u8d77\uff0c\u60ac\u6d6e\u5728\u8ddd\u79bb\u5730\u9762\u7ea6\u516b\u5c3a\u7684\u9ad8\u5ea6\u3002\u7136\u540e\u5deb\u5996\u62ac\u8d77\u811a\uff0c\u201c\u7830\u201d\u5730\u4e00\u58f0\uff0c\u91cd\u91cd\u8e39\u5728\u4ed6\u65c1\u8fb9\u7684\u4e00\u5ea7\u534a\u692d\u5706\u5f62\u7684\u5947\u602a\u4eea\u5668\u4e0a\u3002\u201c\u5580\u5587\u5580\u5587\u5580\u5587\u201d\u7684\u52a8\u9759\u987f\u65f6\u5728\u5730\u4e0b\u5ba4\u7684\u6bcf\u4e2a\u89d2\u843d\u91cc\u54cd\u8d77\uff0c\u50cf\u662f\u6709\u4e00\u5927\u5806\u9f7f\u8f6e\u540c\u65f6\u5f00\u59cb\u8f6c\u52a8\uff0c\u800c\u4e14\u901f\u5ea6\u8d8a\u6765\u8d8a\u5feb\u3002\u51e0\u79d2\u949f\u540e\uff0c\u4e00\u9053\u7897\u53e3\u7c97\u7684\u6de1\u7eff\u8272\u5706\u5f62\u5149\u67f1\u9aa4\u7136\u81ea\u5934\u9876\u659c\u659c\u5c04\u4e0b\uff0c\u5c06\u9aa8\u6756\u7f69\u5728\u5176\u4e2d\u3002</p><p>\u88ab\u7eff\u5149\u4e00\u7167\uff0c\u539f\u672c\u4e4c\u9ed1\u5982\u58a8\u7684\u9aa8\u6756\u50cf\u662f\u892a\u8272\u4e86\u4f3c\u7684\uff0c\u8868\u9762\u989c\u8272\u5feb\u901f\u6de1\u5316\uff0c\u76f4\u81f3\u53d8\u6210\u65e0\u8272\u900f\u660e\uff0c\u4eff\u4f5b\u6c34\u6676\uff0c\u80fd\u591f\u6e05\u6e05\u695a\u695a\u770b\u89c1\u5185\u90e8\u57cb\u6709\u4e00\u6839\u7ea4\u7ec6\u5982\u53d1\u4e1d\u7684\u94f6\u7ebf\uff0c\u90a3\u662f\u6cd5\u6756\u7684\u201c\u82af\u201d\u3002\u5deb\u5996\u201c\u5657\u201d\u5730\u5410\u51fa\u4e00\u53e3\u5bd2\u6c14\uff0c\u53cd\u624b\u4e00\u62f3\u6253\u5728\u80cc\u540e\u6b63\u65b9\u5f62\u7684\u9ed1\u5323\u5b50\u4e0a\uff0c\u968f\u7740\u9686\u9686\u58f0\u54cd\uff0c\u5934\u9876\u53c8\u6709\u4e24\u9053\u5149\u67f1\u7167\u4e0b\uff0c\u4e00\u84dd\u4e00\u7d2b\uff0c\u548c\u539f\u672c\u7684\u6d45\u7eff\u8272\u5149\u67f1\u4ea4\u53c9\uff0c\u5c06\u9aa8\u6756\u7f69\u4f4f\u3002\u5728\u5deb\u5996\u8eab\u540e\u5149\u6ed1\u5982\u955c\u7684\u77f3\u58c1\u4e0a\uff0c\u9aa8\u6756\u7684\u6295\u5f71\u51fa\u73b0\u4e86\uff0c\u5b83\u5728\u4e0d\u65ad\u5730\u5feb\u901f\u653e\u5927\uff0c\u5341\u500d\u3001\u767e\u500d\u3001\u5343\u500d\u3001\u4e07\u500d\uff0c\u90a3\u6839\u7ec6\u7ec6\u7684\u94f6\u7ebf\u5df2\u7ecf\u53d8\u5f97\u6bd4\u6210\u5e74\u4eba\u7684\u624b\u81c2\u8fd8\u7c97\uff0c\u7136\u540e\u743c\u6069\u548c\u6885\u83f2\u65af\u7ec8\u4e8e\u770b\u6e05\u695a\u5b83\u4e0a\u9762\u539f\u6765\u8fd8\u523b\u7740\u4e00\u4e2a\u56fe\u6848\uff0c\u4e0d\u8fc7\u8fd8\u770b\u4e0d\u6e05\u695a\u5230\u5e95\u662f\u4ec0\u4e48\u3002</p><p>\u6295\u5f71\u7ee7\u7eed\u653e\u5927\uff0c\u8ba9\u90a3\u4e2a\u56fe\u6848\u6700\u7ec8\u5360\u636e\u4e86\u6574\u4e2a\u5899\u58c1\uff0c\u73b0\u5728\u7ec8\u4e8e\u80fd\u591f\u770b\u6e05\u695a\u4e86\uff0c\u7136\u800c\u743c\u6069\u8fd8\u662f\u6ca1\u770b\u61c2\u3002\u611f\u89c9\u50cf\u662f\u526f\u62bd\u8c61\u753b\uff0c\u800c\u4e14\u6bd4\u6bd5\u52a0\u7d22\u8fd8\u6bd5\u52a0\u7d22\uff0c\u7ebf\u6761\u975e\u5e38\u6742\u4e71\uff0c\u52c9\u5f3a\u80fd\u8fa8\u8ba4\u51fa\u6709\u67c4\u77ed\u5200\u3001\u6709\u4e2a\u6708\u4eae\uff0c\u4f3c\u4e4e\u8fd8\u6709\u5f20\u4eba\u8138\u2014\u2014\u6216\u8005\u662f\u9a6c\u8138\uff0c\u56e0\u4e3a\u786e\u5b9e\u592a\u957f\u3002</p><p>\u5e78\u597d\u5deb\u5996\u6ca1\u6709\u51fa\u8c1c\u9898\u7684\u6253\u7b97\uff0c\u4ed6\u7d27\u63a5\u7740\u5c31\u8bf4\u51fa\u4e86\u7b54\u6848\uff0c\u201c\u8fd9\u662f\u4e4c\u6885\u57ce\u8010\u8bfa\u5bb6\u65cf\u7684\u5bb6\u5fbd\uff0c\u201d\u4ed6\u5bf9\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u7b2c\u4e09\u4ee3\u5bb6\u4e3b\u52a0\u56fe\u7d22\u5728535DR\u7ed8\u5236\u7684\u3002\u90a3\u5bb6\u4f19\u662f\u4e2a\u5929\u624d\u4ea1\u7075\u5e08\uff0c\u4f46\u5728\u7ed8\u753b\u4e0a\u7684\u6c34\u5e73\u771f\u662f\u60e8\u4e0d\u5fcd\u7779\uff0c\u504f\u504f\u53c8\u81ea\u4ee5\u4e3a\u662f\u3002\u540e\u6765\u7b2c\u4e94\u4ee3\u5bb6\u4e3b\u96c5\u67ef\u5b9e\u5728\u770b\u4e0d\u987a\u773c\uff0c\u5728614DR\u5bf9\u5bb6\u5fbd\u8fdb\u884c\u4e86\u5927\u5e45\u5ea6\u4fee\u6539\uff0c\u53d8\u6210\u4e86\u8fd9\u6837\u2014\u2014\u201d\u5deb\u5996\u62ac\u8d77\u624b\u6307\uff0c\u5728\u7a7a\u6c14\u4e2d\u6f66\u8349\u5730\u753b\u4e86\u51e0\u7b14\uff0c\u4e5f\u4e0d\u7ba1\u522b\u4eba\u6709\u6ca1\u6709\u770b\u6e05\u695a\uff0c\u201c\u6240\u4ee5\u8fd9\u4e2a\u5bb6\u5fbd\u771f\u6b63\u7684\u4f7f\u7528\u65f6\u95f4\uff0c\u53ea\u6709\u4ece535DR\u5230614DR\u4e4b\u95f4\u7684\u4e03\u5341\u4e5d\u5e74\uff0c\u6b64\u524d\u8fd8\u6ca1\u51fa\u73b0\uff0c\u6b64\u540e\u5c31\u6539\u6389\u4e86\uff0c\u771f\u662f\u77ed\u547d\u54ea\u2026\u2026\u201d</p><p>\u201c\u957f\u8001\u2014\u2014\u201d\u6885\u83f2\u65af\u6253\u65ad\u5deb\u5996\u7684\u957f\u7bc7\u5927\u8bba\u3002</p><p>\u201c\u597d\u5427\u597d\u5427\uff0c\u6211\u77e5\u9053\u4f60\u6ca1\u5174\u8da3\u542c\u8fd9\u4e9b\uff0c\u73b0\u5728\u7684\u5e74\u8f7b\u4eba\u771f\u662f\u6c89\u4e0d\u4f4f\u6c14\uff0c\u201d\u5deb\u5996\u5583\u5583\u62b1\u6028\u7740\uff0c\u201c\u8a00\u5f52\u6b63\u4f20\uff0c\u8fd9\u67c4\u6cd5\u6756\u4e3b\u4f53\u662f\u7528\u9f99\u7684\u808b\u9aa8\u78e8\u5236\u800c\u6210\uff0c\u4ece\u6c14\u5473\u5224\u65ad\u50cf\u662f\u9ed1\u9f99\uff0c\u6756\u82af\u662f\u4e00\u6839\u8001\u5e74\u96c4\u6027\u72ec\u89d2\u517d\u7684\u989d\u53d1\uff0c\u6756\u7aef\u7684\u7403\u5f62\u82cd\u84dd\u77f3\u91cc\u9762\u56da\u7981\u7740\u516b\u53ea\u591c\u5f71\u3002\u53e6\u5916\uff0c\u6cd5\u6756\u4e0a\u523b\u6709\u5341\u516d\u4e2a\u8fde\u73af\u91cd\u53e0\u7684\u62db\u9b42\u6cd5\u9635\uff0c\u53ef\u4ee5\u8ba9\u4f7f\u7528\u8005\u7684\u4ea1\u7075\u62db\u5524\u6cd5\u672f\u6548\u679c\u589e\u5f3a\u56db\u500d\uff0c\u53cd\u51b2\u529b\u51cf\u5c11\u5230\u516b\u5206\u4e4b\u4e00\uff0c\u5982\u679c\u4e00\u6b21\u6027\u6fc0\u53d1\u7684\u8bdd\uff0c\u8fde\u9aa8\u9f99\u8fd9\u79cd\u5927\u5bb6\u4f19\u90fd\u80fd\u64cd\u7eb5\u3002\u4f46\u662f\u6cd5\u6756\u4e0a\u6709\u4e2a\u9690\u853d\u7684\u7981\u5236\uff0c\u5728\u8fd9\u91cc\uff0c\u201d\u5deb\u5996\u6307\u4e86\u6307\u6cd5\u6756\u7684\u672b\u7aef\uff0c\u201c\u8ba9\u5b83\u53ea\u80fd\u88ab\u6709\u8010\u8bfa\u5bb6\u65cf\u8840\u7edf\u7684\u5deb\u5e08\u6240\u4f7f\u7528\uff0c\u5982\u679c\u843d\u5230\u5916\u4eba\u624b\u91cc\uff0c\u5c31\u53ea\u662f\u4e2a\u5e9f\u7269\uff0c\u4ec0\u4e48\u80fd\u529b\u90fd\u6ca1\u6709\u3002\u6240\u4ee5\u6628\u5929\u4f60\u4eec\u78b0\u5230\u7684\u90a3\u4e2a\u82cd\u767d\u9886\u4e3b\uff0c\u5e94\u8be5\u5c31\u662f\u8010\u8bfa\u7684\u540e\u88d4\u3002\u201d</p><p>\u201c\u53ef\u662f\uff0c\u4e4c\u6885\u57ce\u7684\u8010\u8bfa\u5bb6\u65cf\uff0c\u4e0d\u662f\u5728\u5341\u516d\u5e74\u524d\u5c31\u88ab\u6211\u4eec\u2026\u2026\u201d</p><p>\u201c\u554a\uff0c\u5927\u6982\u662f\u6f0f\u7f51\u4e4b\u9c7c\u5427\u3002\u201d</p><p>\u201c\u90a3\u679a\u6212\u6307\u5462\uff1f\u201d\u6885\u83f2\u65af\u95ee\uff0c\u201c\u4e5f\u6709\u8010\u8bfa\u5bb6\u65cf\u7684\u5fbd\u8bb0\uff1f\u201d</p><p>\u201c\u4e0d\u662f\u3002\u201d\u5deb\u5996\u8bf4\u7740\uff0c\u5c06\u6212\u6307\u5f80\u7a7a\u6c14\u4e2d\u4e00\u653e\uff0c\u5ff5\u4e86\u51e0\u53e5\u5492\u8bed\uff0c\u900f\u660e\u6c34\u6c7d\u5728\u6212\u6307\u7684\u524d\u65b9\u51fa\u73b0\uff0c\u51dd\u56fa\u6210\u578b\uff0c\u4eff\u4f5b\u4e00\u9762\u68f1\u955c\u3002\u90a3\u679a\u6212\u6307\u539f\u672c\u666e\u666e\u901a\u901a\uff0c\u9ed1\u6c89\u6c89\u7684\u6beb\u4e0d\u8d77\u773c\uff0c\u50cf\u662f\u4e2a\u94c1\u73af\uff0c\u4f46\u73b0\u5728\u900f\u8fc7\u68f1\u955c\u518d\u770b\uff0c\u987f\u65f6\u5c31\u53d8\u4e86\u6837\u3002\u8868\u9762\u7684\u9eef\u6de1\u6f6e\u6c34\u822c\u892a\u53bb\uff0c\u9732\u51fa\u9690\u85cf\u5176\u4e2d\u7684\u4e00\u4e2a\u5c0f\u5c0f\u7684\u5fbd\u7ae0\uff0c\u5deb\u5996\u518d\u6b21\u5c06\u5b83\u6295\u5f71\u5230\u77f3\u58c1\u4e0a\uff0c\u7136\u540e\u653e\u5927\u6570\u5341\u500d\u3002</p><p>\u9aa8\u9f99\uff1f\u4e0d\uff0c\u4e0d\u5bf9\u3002</p><p>\u5899\u58c1\u4e0a\u51fa\u73b0\u7684\u662f\u4e00\u53ea\u9ab7\u9ac5\u5de8\u9f99\uff0c\u7c97\u770b\u786e\u5b9e\u5f88\u50cf\u6628\u5929\u521a\u521a\u89c1\u8fc7\u7684\u9aa8\u9f99\uff0c\u4f46\u4ed4\u7ec6\u5206\u8fa8\u4fbf\u6709\u533a\u522b\u3002\u5b83\u7684\u773c\u7736\u5e76\u975e\u5b8c\u5168\u7a7a\u6d1e\uff0c\u800c\u662f\u88ab\u4e00\u7247\u6d53\u91cd\u7684\u9634\u5f71\u7b3c\u7f69\uff0c\u7ec6\u82e5\u9488\u5c16\u7684\u7d2b\u5149\u4ece\u4e2d\u70b9\u70b9\u900f\u51fa\uff0c\u50cf\u662f\u591c\u7a7a\u4e2d\u523a\u7834\u4e4c\u4e91\u900f\u4e0b\u7684\u661f\u8fb0\uff0c\u800c\u975e\u9aa8\u9f99\u90a3\u79cd\u7ea2\u5149\u70c1\u70c1\u3002\u743c\u6069\u8bb0\u5f97\u4ee5\u524d\u5728\u4e66\u4e0a\u8bfb\u5230\u8fc7\u8fd9\u79cd\u5f62\u8c61\u63cf\u8ff0\uff0c\u4e0d\u662f\u9aa8\u9f99\uff0c\u800c\u5e94\u8be5\u662f\u2026\u2026</p><p>\u201c\u9f99\u5deb\u5996\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u8fd9\u662f\u8428\u739b\u65af\u7279\u7684\u6807\u8bb0\u3002\u201d</p><p>\u201c\u5bf9\uff0c\u8428\u739b\u65af\u7279\u6210\u529f\u53d1\u660e\u7b2c\u4e00\u5934\u9f99\u5deb\u5996\u590f\u845b\u745e\u62c9\u4e4b\u540e\uff0c\u5c31\u6539\u7528\u5b83\u505a\u81ea\u5df1\u7684\u5fbd\u8bb0\uff0c\u6240\u4ee5\u73b0\u5728\u5f88\u6e05\u695a\u4e86\uff0c\u8010\u8bfa\u5bb6\u65cf\u7684\u4f59\u515a\uff0c\u6295\u9760\u4e86\u8428\u739b\u65af\u7279\uff0c\u53d8\u6210\u82cd\u767d\u9886\u4e3b\u4e4b\u540e\u56de\u6765\u6253\u7b97\u590d\u4ec7\uff0c\u7ed3\u679c\u53c8\u88ab\u4f60\u5e72\u6389\u4e86\u2014\u2014\u5c31\u662f\u8fd9\u6837\u3002\u201d</p><p>\u5deb\u5996\u4e00\u8fb9\u8bf4\u7740\uff0c\u4e00\u8fb9\u5c06\u9aa8\u6756\u548c\u6212\u6307\u6536\u56de\u638c\u4e2d\uff0c\u7136\u540e\u63b7\u8fd8\u7ed9\u6885\u83f2\u65af\u3002\u201c\u95f2\u8bdd\u8bf4\u5b8c\uff0c\u8be5\u8c08\u8c08\u6b63\u4e8b\u4e86\uff0c\u743c\u6069\xb7\u5170\u5c3c\u65af\u7279\uff0c\u6765\u81ea\u9634\u9b42\u57ce\u7684\u5deb\u5e08\u5bf9\u5427\uff0c\u201d\u4ed6\u5fae\u5fae\u4f4e\u5934\uff0c\u4fef\u89c6\u7740\u743c\u6069\uff0c\u773c\u7736\u4e2d\u7684\u7ea2\u5149\u8f70\u7136\u7efd\u653e\uff0c\u8d8a\u53d1\u663e\u5f97\u90aa\u5f02\u8feb\u4eba\uff0c\u4e2d\u5fc3\u5374\u4eff\u4f5b\u6709\u4e24\u70b9\u94f6\u661f\u5728\u8df3\u8dc3\uff0c\u201c\u4f60\u5f88\u8ba9\u6211\u5403\u60ca\u554a\u3002\u201d</p><p>\u201c\u5514\uff1f\u201d\u743c\u6069\u83ab\u540d\u5176\u5999\u3002</p><p>\u201c\u4e0d\u6ee1\u4e8c\u5341\u5c81\uff0c\u5df2\u7ecf\u51dd\u6210\u771f\u540d\uff0c\u4f60\u5728\u5965\u672f\u4e4b\u9053\u4e0a\u7684\u8fdb\u5883\u4e4b\u901f\uff0c\u5b9e\u5728\u662f\u6211\u751f\u5e73\u6240\u7f55\u89c1\u3002\u9634\u9b42\u57ce\u7684\u9b54\u6cd5\u6559\u80b2\uff0c\u7740\u5b9e\u6709\u4e00\u5957\u554a\uff0c\u201d\u5deb\u5996\u81ea\u987e\u81ea\u5730\u8bf4\uff0c\u201c\u6709\u6ca1\u6709\u5174\u8da3\u548c\u6211\u6253\u4e00\u67b6\u5462\uff1f\u743c\u6069\uff0c\u4e5f\u8ba9\u6211\u8fd9\u4e2a\u57cb\u5728\u5730\u4e0b\u51e0\u767e\u5e74\u7684\u8001\u9aa8\u5934\uff0c\u89c1\u8bc6\u89c1\u8bc6\u4f20\u8bf4\u4e2d\u53e4\u8010\u745f\u5e1d\u56fd\u7684\u7cbe\u5999\u6280\u827a\u5427\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u51fa\u95e8\u5728\u5916\u5192\u9669\uff0c\u96be\u514d\u9047\u5230\u6311\u6218\uff0c\u743c\u6069\u548c\u4eba\u52a8\u624b\u4e5f\u4e0d\u662f\u7b2c\u4e00\u6b21\uff0c\u4f46\u8fd9\u4e48\u76f4\u767d\u7684\u9080\u6218\uff0c\u5012\u8fd8\u771f\u662f\u5c11\u89c1\u3002\u6885\u83f2\u65af\u4e5f\u662f\u9887\u4e3a\u610f\u5916\uff0c\u6b63\u5f85\u963b\u62e6\uff0c\u773c\u89d2\u4f59\u5149\u5374\u77a5\u89c1\u5deb\u5996\u534a\u7b3c\u5728\u8896\u4e2d\u7684\u5de6\u624b\u6781\u9690\u853d\u5730\u6bd4\u5212\u4e86\u4e00\u4e0b\uff0c\u5979\u6014\u4e86\u6014\uff0c\u4fbf\u4e0d\u518d\u8bf4\u8bdd\u3002</p><p>\u201c\u5982\u4f55\uff1f\u201d\u5deb\u5996\u7d27\u8ffd\u4e0d\u820d\uff0c\u201c\u4e0d\u53cd\u5bf9\u7684\u8bdd\uff0c\u6211\u5c31\u5f53\u4f60\u9ed8\u8ba4\u4e86\u5462\u3002\u201d</p><p>\u743c\u6069\u770b\u770b\u6885\u83f2\u65af\uff0c\u89c1\u5979\u6ca1\u6709\u53cd\u5bf9\u7684\u610f\u601d\uff0c\u4fbf\u70b9\u4e86\u70b9\u5934\u3002\u201c\u4e3b\u968f\u5ba2\u4fbf\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u8bf7\u5427\u3002\u201d</p><p>\u201c\u597d\u6781\u4e86\uff0c\u201d\u5deb\u5996\u7acb\u523b\u8bf4\uff0c\u201c\u4e0d\u8fc7\u8fd9\u91cc\u592a\u72ed\u7a84\uff0c\u6362\u4e2a\u5730\u65b9\u5427\u3002\u7ad9\u7740\u522b\u52a8\uff0c\u653e\u8f7b\u677e\uff0c\u522b\u62b5\u6297\uff0c\u743c\u6069\u3002\u201d</p><p>\u4ed6\u62ac\u624b\u4e00\u6307\uff0c\u56db\u9053\u7075\u7ebf\u4ece\u9aa8\u8282\u4e2d\u53d1\u51fa\uff0c\u4eff\u4f5b\u7ef3\u7d22\u822c\u5c06\u743c\u6069\u7f20\u4f4f\uff0c\u5bc6\u5bc6\u531d\u531d\uff0c\u77ac\u95f4\u53d8\u6210\u4e00\u4e2a\u5f69\u8272\u5149\u8327\u3002\u51e0\u79d2\u949f\u540e\uff0c\u5149\u8327\u6563\u53bb\uff0c\u743c\u6069\u5df2\u7ecf\u6d88\u5931\u4e86\u3002</p><p>\u201c\u955c\uff01\u201d</p><p>\u968f\u7740\u5deb\u5996\u7684\u771f\u8a00\u5410\u51fa\uff0c\u4e00\u5f20\u5de8\u5927\u7684\u5149\u955c\u5728\u7a7a\u6c14\u4e2d\u51fa\u73b0\uff0c\u5b83\u7684\u8868\u9762\u5148\u662f\u6a21\u7cca\u4e0d\u6e05\uff0c\u8272\u5f69\u7f24\u7eb7\uff0c\u4e91\u906e\u96fe\u7f69\uff0c\u4f46\u5f88\u5feb\u5c31\u53d8\u5f97\u6e05\u6670\u8d77\u6765\u3002\u6885\u83f2\u65af\u770b\u89c1\u4e86\u4e00\u5ea7\u5706\u73af\u5f62\u7684\u5efa\u7b51\uff0c\u5728\u5efa\u7b51\u4e2d\u5fc3\u7684\u5e7f\u573a\u4e0a\uff0c\u4e24\u4e2a\u8eab\u5f71\u6b63\u9065\u9065\u76f8\u5bf9\u7ad9\u7acb\u7740\uff0c\u4e00\u4e2a\u662f\u743c\u6069\uff0c\u53e6\u4e00\u4e2a\u5219\u662f\u5deb\u5996\u3002</p><p>\u6ca1\u9519\uff0c\u5c31\u662f\u6b64\u523b\u6b63\u7ad9\u5728\u6885\u83f2\u65af\u8eab\u65c1\u7684\u5deb\u5996\uff0c\u88ab\u79f0\u4f5c\u201c\u6c49\u5bc6\u5c14\u987f\u957f\u8001\u201d\u7684\u5947\u602a\u5bb6\u4f19\uff0c\u8eab\u6750\u3001\u8863\u9970\u3001\u76f8\u8c8c\u2014\u2014\u597d\u5427\uff0c\u4e00\u526f\u9ab7\u9ac5\u9aa8\u67b6\u6ca1\u4ec0\u4e48\u76f8\u8c8c\u53ef\u8a00\uff0c\u4f46\u5c31\u8fde\u9aa8\u9abc\u4e0a\u7684\u7eb9\u8def\u88c2\u9699\u3001\u8272\u6cfd\u6697\u6591\uff0c\u90fd\u662f\u4e00\u6a21\u4e00\u6837\uff0c\u7edd\u65e0\u534a\u70b9\u533a\u522b\u3002</p><p>\u201c\u4f60\u6b20\u6211\u4e00\u4e2a\u89e3\u91ca\uff0c\u957f\u8001\u3002\u201d\u6885\u83f2\u65af\u8bf4\u3002</p><p>\u201c\u5514\uff1f\u201d\u5deb\u5996\u88c5\u50bb\uff0c\u201c\u4ec0\u4e48\u89e3\u91ca\u3002\u201d</p><p>\u201c\u5982\u679c\u6211\u6ca1\u8bb0\u9519\u7684\u8bdd\uff0c\u4f60\u4e0d\u4f46\u8ba4\u8bc6\u9634\u9b42\u57ce\u7684\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\uff0c\u8fd8\u66fe\u7ecf\u548c\u4ed6\u4e00\u8d77\u7ed3\u4f34\u5192\u9669\u8fc7\uff0c\u6240\u8c13\u2018\u60f3\u89c1\u8bc6\u89c1\u8bc6\u53e4\u8010\u745f\u5e1d\u56fd\u9b54\u6cd5\u6280\u827a\u2019\u7684\u8bf4\u6cd5\uff0c\u5b8c\u5168\u662f\u4e2a\u501f\u53e3\u5427\uff0c\u201d\u6885\u83f2\u65af\u51b7\u9759\u5730\u6307\u51fa\u5deb\u5996\u8c0e\u8a00\u4e2d\u7684\u7834\u7efd\uff0c\u201c\u4e5f\u522b\u8ddf\u6211\u8bf4\u4ec0\u4e48\u4f60\u95f2\u7740\u65e0\u804a\uff0c\u5c31\u662f\u60f3\u627e\u4e2a\u4eba\u6253\u4e00\u67b6\u89e3\u95f7\u4e4b\u7c7b\u7684\u8bdd\u2014\u2014\u4f60\u4eec\u4ea1\u7075\u54ea\u6709\u2018\u65e0\u804a\u2019\u8fd9\u79cd\u89c1\u9b3c\u7684\u6982\u5ff5\u3002\u201d</p><p>\u201c\u55e4\uff0c\u5f53\u7740\u7537\u670b\u53cb\u7684\u9762\u88c5\u6dd1\u5973\uff0c\u4e00\u8f6c\u8eab\u5c31\u8fd9\u4e48\u8bf4\u8bdd\u4e0d\u5ba2\u6c14\u5417\uff0c\u201d\u5deb\u5996\u4e0d\u6ee1\u5730\u8bf4\uff0c\u201c\u771f\u662f\u6ca1\u6709\u793c\u8c8c\u554a\uff0c\u827e\u5f25\u8587\u3002\u201d</p><p>\u201c\u522b\u8f6c\u79fb\u8bdd\u9898\uff01\u201d</p><p>\u201c\u597d\u5566\u597d\u5566\uff0c\u522b\u8fd9\u4e48\u51f6\uff0c\u544a\u8bc9\u4f60\u5c31\u662f\u4e86\uff0c\u201d\u5deb\u5996\u8bf4\uff0c\u201c\u521a\u624d\u6211\u7528\u4e86\u2018\u547d\u8fd0\u4e4b\u773c\u2019\u770b\u4ed6\u3002\u201d</p><p>\u201c\u4ec0\u4e48\uff0c\u4f60\u7adf\u7136\u2026\u2026\u201d\u4ece\u6765\u90fd\u662f\u9547\u5b9a\u81ea\u82e5\u7684\u5723\u6b66\u58eb\u4e5f\u7981\u4e0d\u4f4f\u8138\u4e0a\u5fae\u5fae\u53d8\u8272\uff0c\u201c\u4f60\u770b\u5230\u4e86\u4ec0\u4e48\uff1f\u201d</p><p>\u5deb\u5996\u770b\u7740\u5149\u955c\uff0c\u91cc\u9762\u7684\u4e24\u4e2a\u4eba\u5df2\u7ecf\u5f00\u59cb\u4ea4\u624b\uff0c\u4ed6\u7684\u4e0a\u4e0b\u989a\u9aa8\u6d3b\u52a8\u7740\uff0c\u7528\u4e00\u79cd\u6885\u83f2\u65af\u4ece\u672a\u542c\u8fc7\u7684\u8bed\u8a00\uff0c\u5410\u51fa\u4e00\u4e2a\u5979\u5b8c\u5168\u964c\u751f\u7684\u8bcd\u3002</p><p>\u201c\u7fd4\u9f99\u3002\u201d</p></article>"}}}]);
