(window.webpackJsonp=window.webpackJsonp||[]).push([[493],{516:function(p){p.exports={chapterTitle:null,title:"\u7b2c493\u7ae0 \u9152\u5165\u5589",content:"<article><h2>\u7b2c493\u7ae0 \u9152\u5165\u5589</h2><p>\u3000\u3000\u5f20\u606a\u53f3\u817f\u6454\u9aa8\u88c2\u65f6\uff0c\u5f20\u5955\u5728\u697c\u4e0b\u5ba2\u5385\u5e72\u5750\u4e86\u4e00\u5929\uff0c\u90a3\u5929\u5f20\u606a\u6ca1\u6709\u89c1\u5f20\u5955\u4ed6\u4eba\uff1b\u8fd9\u662f\u8ddd\u4e0a\u6b21\u76f8\u89c1\u4e00\u5e74\u534a\u4e4b\u540e\u7684\u89c1\u9762\u5427\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u5c0f\u53d4\u5faa\u7740\u58f0\u97f3\u8d70\u8fc7\u6765\uff0c\u8fd9\u7247\u533a\u57df\u7684\u5e73\u623f\u6253\u773c\u770b\u4e0a\u53bb\u5c31\u662f\u6751\u6c11\u5efa\u6765\u51fa\u79df\u7684\uff0c\u8fd8\u7a0d\u7a0d\u6574\u9f50\u4e00\u8d77\uff0c\u4e00\u680b\u6392\u5c4b\u53ef\u80fd\u8fde\u7740\u6709\u5341\u95f4\u516b\u95f4\u623f\uff0c\u5c4b\u9762\u8986\u76d6\u5f69\u94a2\u74e6\uff0c\u5899\u8eab\u7ea2\u7816\u780c\u597d\u62b9\u7740\u77f3\u7070\u3002\u8fd9\u79cd\u7b80\u6613\u623f\u4e00\u95f4\u4e00\u4e2a\u6708\u7684\u79df\u91d1\u90fd\u4e0d\u8981\u4e09\u4e94\u5341\u5757\uff0c\u4e5f\u6709\u9644\u8fd1\u7eba\u7ec7\u5382\u79df\u6765\u5f53\u804c\u5de5\u5bbf\u820d\u7684\u3002</p><p>\u3000\u3000\u6d77\u5dde\u63a7\u80a1\u5c06\u6d77\u68c9\u51e0\u95f4\u5382\u90fd\u8fc1\u5230\u5317\u5173\u7684\u68c9\u7eba\u7ec7\u5de5\u4e1a\u56ed\u91cc\uff0c\u8fd9\u9644\u8fd1\u7684\u7eba\u7ec7\u5382\u5c31\u591a\u4e86\u8d77\u6765\uff0c\u4e3a\u4e86\u8282\u7ea6\u82cf\u52a8\u6210\u672c\uff0c\u62db\u5de5\u5c31\u591a\u7528\u5916\u5730\u5230\u6d77\u5dde\u7684\u6253\u5de5\u59b9\uff0c\u8d70\u5230\u8fd9\u4e00\u7247\uff0c\u5e74\u8f7b\u7684\u5973\u5b69\u5b50\u5012\u5f88\u591a\uff0c\u8863\u7740\u6734\u7d20\uff0c\u4e5f\u6709\u51e0\u4e2a\u76f8\u8c8c\u7aef\u5e84\u7684\u2014\u2014\u4e0d\u50cf\u4e03\u516b\u5e74\u540e\uff0c\u76f8\u8c8c\u7aef\u5e84\u7684\u90fd\u8dd1\u5230\u6d17\u6d74\u4e2d\u5fc3\u3001\u7f8e\u5bb9\u5c4b\u3001\u6ce1\u811a\u623f\u8fd9\u4e9b\u5730\u65b9\u6253\u5de5\u53bb\u4e86\u3002</p><p>\u3000\u3000\u5f20\u5955\u8ddf\u4e00\u4e2a\u5973\u5b69\u7ad9\u5728\u623f\u524d\u7684\u4e95\u8fb9\uff0c\u811a\u4e0b\u6709\u53ea\u5927\u7ea2\u5851\u6599\u76c6\uff0c\u91cc\u9762\u6d78\u7740\u6ce1\u6c34\u7684\u8863\u670d\uff0c\u5f20\u5955\u4e0e\u4e00\u5e74\u534a\u524d\u76f8\u6bd4\uff0c\u8138\u4e0a\u5c11\u4e86\u4e9b\u8f7b\u6d6e\u5473\uff0c\u5634\u5507\u4e0a\u6709\u4e9b\u9aed\u987b\uff0c\u4e0b\u9894\u6709\u4e9b\u80e1\u5b50\u6e23\uff0c\u7a7f\u7740\u673a\u4fee\u5de5\u7684\u673a\u5e06\u5e03\u5de5\u4f5c\u670d\uff0c\u6cbe\u6ee1\u6cb9\u6e0d\uff0c\u5f62\u8c61\u6709\u4e9b\u908b\u9062\uff0c\u5927\u6982\u5728\u9644\u8fd1\u7eba\u7ec7\u5382\u627e\u5230\u673a\u4fee\u7684\u5de5\u4f5c\uff0c\u4ed6\u6b63\u5c06\u4e00\u4ef6\u6d78\u6e7f\u7684\u8863\u670d\u4ece\u90a3\u5973\u5b69\u5b50\u7684\u624b\u91cc\u62a2\u4e0b\u6765\uff0c\u8981\u63a8\u90a3\u5973\u5b69\u8fdb\u5c4b\u53bb\u3002</p><p>\u3000\u3000\u4e5f\u5c31\u662f\u4e0a\u56de\u4e0e\u5f20\u5955\u5728\u5b66\u6821\u5927\u95e8\u524d\u89c1\u9762\u65f6\uff0c\u5f20\u606a\u89c1\u8fc7\u7684\u90a3\u5973\u5b69\uff0c\u4e0e\u4e0a\u6b21\u76f8\u6bd4\uff0c\u7626\u5f97\u5389\u5bb3\uff0c\u98a7\u9aa8\u7a81\u51fa\u6765\uff0c\u51e0\u4e4e\u8ba4\u4e0d\u5f97\uff0c\u8138\u8272\u82cd\u767d\uff0c\u6ca1\u6709\u4e00\u70b9\u8840\u8272\uff0c\u6b64\u65f6\u7684\u5979\u5012\u7b97\u4e0d\u4e0a\u6f02\u4eae\uff0c\u7a81\u663e\u51fa\u773c\u775b\u53c8\u5927\u53c8\u4eae\uff0c\u542c\u5c0f\u53d4\u8bf4\u8fd9\u5973\u5b69\u624d\u6d41\u8fc7\u4ea7\u6ca1\u591a\u4e45\uff0c\u5e94\u8be5\u662f\u8eab\u4f53\u4e00\u76f4\u90fd\u6ca1\u6709\u6062\u590d\u8fc7\u6765\u3002</p><p>\u3000\u3000\u90a3\u5973\u5b69\u4e0d\u60f3\u56de\u5c4b\u91cc\uff0c\u7ed9\u5f20\u5955\u63ea\u7740\u80f3\u818a\u5f80\u5c4b\u91cc\u63a8\uff0c\u5934\u5374\u626d\u8fc7\u6765\uff0c\u60f3\u8bf4\u4ec0\u4e48\u6765\u7740\uff0c\u770b\u5230\u5f20\u77e5\u975e\u4e0e\u5f20\u606a\uff0c\u6123\u5728\u90a3\u91cc\u3002</p><p>\u3000\u3000\u5f20\u5955\u8fd9\u624d\u89c9\u5f97\u5f02\u5e38\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u6709\u4e9b\u53d1\u6123\uff0c\u65e2\u641e\u4e0d\u6e05\u4ed6\u4eec\u7684\u6765\u610f\uff0c\u4e5f\u4e0d\u613f\u5c31\u81ea\u5df1\u7684\u5883\u51b5\u591a\u8bf4\u4ec0\u4e48\uff0c\u5c31\u6c89\u9ed8\u5728\u90a3\u91cc\u3002</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u8bf4\u8bdd\uff0c\u4ed6\u4e0e\u5c0f\u53d4\u542c\u5230\u5f20\u5955\u7684\u58f0\u97f3\u4e4b\u540e\uff0c\u53c8\u542c\u5230\u4ed6\u4eec\u4fe9\u4eba\u7684\u5bf9\u8bdd\uff0c\u539f\u672c\u4e0d\u60f3\u8d70\u8fc7\u6765\u8ba9\u5f20\u5955\u96be\u582a\uff0c\u4f46\u662f\u542c\u5230\u8fd9\u5973\u5b69\u5b50\u8eab\u4f53\u4e0d\u597d\uff0c\u4e24\u4eba\u7684\u751f\u6d3b\u53c8\u8fd9\u4e48\u8270\u96be\uff0c\u8eab\u4f53\u8981\u662f\u4e8f\u4e86\u5c31\u6b64\u843d\u4e0b\u75c5\u6839\uff0c\u90a3\u5c31\u540e\u6094\u83ab\u53ca\u4e86\u3002</p><p>\u3000\u3000\u201c\u6211\u8ddf\u5f20\u606a\u521a\u5de7\u7ecf\u8fc7\u8fd9\u91cc\uff0c\u5c31\u8fc7\u6765\u770b\u770b\uff0c\u542c\u522b\u4eba\u8bf4\u8d77\u5728\u8fd9\u91cc\u770b\u5230\u8fc7\u4f60\u4eec\uff0c\u4f60\u8ddf\u4f60\u7238\u7684\u4e8b\u60c5\uff0c\u6211\u4eec\u7ba1\u4e0d\u7740\uff0c\u201d\u5f20\u77e5\u975e\u8bf4\u9053\uff0c\u4ece\u88e4\u515c\u638f\u51fa\u4e00\u53ea\u4fe1\u5c01\u585e\u5f20\u5955\u624b\u91cc\uff0c\u5f20\u5955\u624b\u7f29\u56de\u53bb\uff0c\u53c8\u585e\u5973\u5b69\u5b50\u624b\u91cc\uff0c\u5bf9\u5f20\u5955\u8bf4\uff0c\u201c\u6211\u8ddf\u5f20\u606a\u8eab\u4e0a\u6ca1\u5e26\u591a\u5c11\u94b1\uff0c\u5c31\u8fd9\u4e48\u4e9b\uff0c\u4f60\u8981\u60f3\u5145\u4ec0\u4e48\u7537\u4eba\uff0c\u8fd9\u94b1\u4f60\u5c31\u5148\u7ed9\u6211\u6536\u4e0b\uff0c\u4ee5\u540e\u8fd8\u7ed9\u6211\u4eec\u4e5f\u884c\u3002\u201d</p><p>\u3000\u3000\u90a3\u5973\u5b69\u770b\u7740\u5f20\u5955\uff0c\u72b9\u8c6b\u7740\u4e0d\u6653\u5f97\u5f53\u4e0d\u5f53\u5c06\u4fe1\u5c01\u6536\u4e0b\u3002\u5f20\u5955\u77a5\u4e86\u4e00\u773c\u5f20\u606a\uff0c\u53c8\u770b\u7740\u4ed6\u53d4\u5f20\u77e5\u975e\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8981\u5c06\u95fb\u5a34\u7167\u987e\u597d\uff1b\u6211\u5df2\u7ecf\u8ddf\u5f20\u5bb6\u6ca1\u4ec0\u4e48\u74dc\u845b\uff0c\u4e5f\u4e0d\u4f1a\u4e22\u5f20\u5bb6\u7684\u8138\uff0c\u8fd9\u94b1\u6211\u4f1a\u8fd8\u7ed9\u4f60\u4eec\u3002\u201d</p><p>\u3000\u3000\u201c\u8fd9\u79cd\u4e8b\uff0c\u5982\u4eba\u996e\u6c34\uff0c\u51b7\u6696\u81ea\u77e5\uff0c\u53ea\u8981\u4f60\u81ea\u5df1\u77e5\u8db3\u5c31\u884c\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u81f3\u4e8e\u5f20\u5bb6\u7684\u8138\uff0c\u4e5f\u4e0d\u662f\u4f60\u8bf4\u60f3\u4e22\u5c31\u80fd\u4e22\u7684\uff0c\u4e0d\u7528\u4f60\u62c5\u5fc3\u8fd9\u4e9b\u6709\u7684\u6ca1\u7684\u3002\u201d\u5c06\u624b\u63d2\u5728\u88e4\u515c\u91cc\uff0c\u95ee\u5c0f\u53d4\uff1a\u201c\u6211\u4eec\u662f\u4e0d\u662f\u8d70\uff1f\u201d</p><p>\u3000\u3000\u5c31\u50cf\u5f20\u606a\u6240\u8bf4\uff0c\u8fd9\u4e8b\u5982\u4eba\u996e\u6c34\u3001\u51b7\u6696\u81ea\u77e5\uff0c\u5f20\u77e5\u975e\u62bf\u7740\u5634\u5507\u5482\u4e86\u4e00\u4e0b\uff0c\u5012\u662f\u8f7b\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u60f3\u8bf4\u4ec0\u4e48\u5374\u6ca1\u6709\u8bf4\u51fa\u53e3\uff0c\u8f6c\u8eab\u8ddf\u5f20\u606a\u8d70\u4e86\u3002</p><p>\u3000\u3000\u8fd9\u7247\u6c11\u623f\u91cc\u7684\u8def\u62d0\u5f2f\u62b9\u89d2\uff0c\u8d70\u56de\u53bb\u53c8\u627e\u4e86\u597d\u4e00\u9635\u5b50\uff0c\u624d\u627e\u5230\u505c\u8f66\u7684\u5730\u65b9\uff0c\u5750\u8fdb\u8f66\u91cc\uff0c\u5f20\u606a\u5bf9\u5c0f\u53d4\u8bf4\u9053\uff1a\u201c\u4e16\u6545\u8fd9\u6f6d\u70c2\u6ce5\uff0c\u9677\u8fdb\u53bb\u5bb9\u6613\uff0c\u8981\u518d\u94bb\u51fa\u6765\uff0c\u592a\u96be\uff1b\u94bb\u51fa\u6765\uff0c\u5c31\u4ec0\u4e48\u90fd\u660e\u767d\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u600e\u4e48\u7a81\u7136\u6709\u8fd9\u4e9b\u611f\u6168\uff0c\u201d\u5f20\u77e5\u975e\u5c06\u8f66\u53d1\u52a8\u8d77\u6765\uff0c\u201c\u5e0c\u671b\u4ed6\u4eec\u4fe9\u4e2a\u80fd\u660e\u767d\u8fc7\u6765\uff1f\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u7a81\u7136\u6709\u4e9b\u611f\u6168\u800c\u5df2\uff0c\u201d\u5f20\u606a\u5e94\u4e86\u4e00\u53e5\uff0c\u201c\u4e16\u4e8b\u6d1e\u660e\u7686\u5b66\u95ee\uff0c\u4eba\u60c5\u51b7\u6696\u5373\u6587\u7ae0\uff0c\u4eba\u4e0e\u4eba\u4e4b\u95f4\u4e5f\u5c31\u5dee\u8fd9\u4e48\u4e00\u70b9\u3002\u201d</p><p>\u3000\u3000\u5f20\u77e5\u975e\u4e0d\u80fd\u786e\u5b9a\u5f20\u606a\u5230\u5e95\u662f\u5728\u8bf4\u4ec0\u4e48\uff0c\u4ee5\u4ed6\u7684\u5e74\u7eaa\u8bf4\u8fd9\u4e9b\u8bdd\uff0c\u672a\u5fc5\u592a\u6ca7\u6851\u4e86\uff0c\u5f53\u7136\uff0c\u6216\u8bb8\u662f\u5929\u8d4b\u5427\uff0c\u6709\u4e9b\u4eba\u609f\u6027\u597d\uff0c\u5c31\u7b97\u6ca1\u6709\u78e8\u783a\u4e5f\u4f1a\u7efd\u653e\u5149\u8292\u7684\uff0c\u5f20\u606a\u8981\u662f\u505a\u4e0d\u5230\u8fd9\u4e00\u70b9\uff0c\u4e5f\u4e0d\u4f1a\u6709\u773c\u4e0b\u7684\u6210\u5c31\u3002\u8bf4\u5b9e\u8bdd\uff0c\u50cf\u4ed6\u4eec\u90a3\u4e00\u8f88\u4eba\u5dee\u4e0d\u591a\u90fd\u5403\u8fc7\u82e6\u3001\u53d7\u8fc7\u632b\u6298\uff0c\u4f46\u662f\u80fd\u4e0d\u80fd\u4ece\u8fd9\u4e9b\u82e6\u3001\u8fd9\u4e9b\u632b\u6298\u91cc\u5f97\u5230\u4e9b\u4ec0\u4e48\uff0c\u5c31\u8981\u770b\u5404\u4eba\u7684\u609f\u6027\u4e86\u3002\u5f20\u77e5\u975e\u5fc3\u91cc\u60f3\uff1a\u5927\u54e5\u5c31\u662f\u90a3\u79cd\u8d70\u4e0d\u51fa\u6765\u7684\u4eba\u5427\uff0c\u5012\u5e0c\u671b\u5f20\u5955\u80fd\u56e0\u6b64\u53d8\u5f97\u66f4\u575a\u97e7\u4e00\u4e9b\uff0c\u5c06\u8eab\u4e0a\u7684\u6d6e\u8e81\u6d17\u6389\u3002</p><p>\u3000\u3000\u5728\u90a3\u7247\u6c11\u623f\u91cc\u6478\u4e86\u534a\u5929\u8def\uff0c\u627e\u5230\u8f66\uff0c\u66ae\u8272\u6e10\u669d\uff0c\u5f20\u77e5\u975e\u62c9\u7740\u5f20\u606a\u53bb\u5317\u5173\u4e00\u5bb6\u540d\u4e3a\u732b\u513f\u80e1\u5b50\u7684\u7f8a\u8089\u5e97\u5403\u7f8a\u8089\uff0c\u5f20\u606a\u5174\u81f4\u5012\u662f\u975e\u5e38\u6d53\uff0c\u62c9\u7740\u5c0f\u53d4\u559d\u8d77\u9152\u6765\uff0c\u662f\u6d77\u5dde\u81ea\u4ea7\u7684\u90a3\u79cd\u7cef\u7c73\u9648\u9152\uff0c\u4e00\u7897\u4e00\u5757\u94b1\u7684\u7cef\u7c73\u9648\uff0c\u52a0\u732a\u6cb9\u6d78\u8fc7\uff0c\u5165\u53e3\u7ef5\u8f6f\u9999\u751c\uff0c\u540e\u52b2\u5374\u4e0d\u4e9a\u4f4e\u5ea6\u767d\u9152\u3002</p><p>\u3000\u3000\u5f20\u606a\u5728\u60f3\u5f20\u5955\u7684\u6027\u683c\uff0c\u591a\u5c11\u7ed9\u4e16\u6545\u6d78\u67d3\uff0c\u6709\u4e9b\u7ea8\u7ed4\u5b50\u5f1f\u7684\u5c0f\u813e\u6c14\uff0c\u5012\u4e5f\u4e0d\u81f3\u4e8e\u4e0d\u6653\u5f97\u5206\u5bf8\uff0c\u4ed6\u7684\u4eba\u751f\u82e5\u4e0d\u88ab\u81ea\u5df1\u4ecb\u5165\uff0c\u4e5f\u5c31\u5982\u6b64\uff0c\u6216\u8bb8\u5e73\u6de1\u65e0\u5e38\uff0c\u4e5f\u4e0d\u9700\u8981\u5fcd\u53d7\u4ec0\u4e48\u78e8\u783a\uff1b\u5012\u662f\u6ca1\u6709\u60f3\u5230\u4ed6\u73b0\u5728\u4f1a\u8d70\u5f97\u8fd9\u4e48\u8f9b\u82e6\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0d\u7531\u5728\u60f3\u81ea\u5df1\u7684\u524d\u4e16\u4eba\u751f\uff0c\u5230\u5e95\u662f\u8d70\u51fa\u6765\uff0c\u8fd8\u662f\u6ca1\u6709\u8d70\u51fa\u6765\uff1f\u5e94\u8be5\u6ca1\u6709\u5427\u3002</p><p>\u3000\u3000\u559d\u4e86\u597d\u4e9b\u9152\uff0c\u5f20\u606a\u4e0e\u5c0f\u53d4\u90fd\u5f00\u4e0d\u4e86\u8f66\uff0c\u4e0d\u5f97\u4e0d\u6253\u7535\u8bdd\u5524\u5085\u4fca\u8fc7\u6765\u63a5\u4ed6\u4eec\u3002\u8dcc\u8dcc\u649e\u649e\u5750\u5230\u8f66\u91cc\uff0c\u9152\u52b2\u4e0a\u6765\uff0c\u610f\u8bc6\u5f00\u59cb\u5728\u98d8\uff1a\u81ea\u5df1\u5f53\u65f6\u4e0d\u662f\u4e5f\u6709\u4e00\u79cd\u4e0d\u7ba1\u5979\u53d8\u4ec0\u4e48\u6837\u5b50\u3001\u751a\u81f3\u5b81\u53ef\u5979\u53d8\u6210\u4e11\u516b\u602a\u4e5f\u8981\u5c06\u5979\u7559\u5728\u8eab\u8fb9\u7684\u5ff5\u5934\u5417\uff1f\u53ea\u662f\u5ff5\u5934\u800c\u5df2\uff0c\u751a\u81f3\u8fde\u5979\u65e5\u540e\u8fc7\u5f97\u597d\u4e0d\u597d\u90fd\u6ca1\u6709\u52c7\u6c14\u53bb\u5173\u5fc3\u3002\u8111\u5b50\u91cc\u53c8\u95ea\u8fc7\u524d\u4e16\u8bb8\u601d\u90a3\u4ee4\u4eba\u770b\u4e86\u4f1a\u751f\u51fa\u7edd\u671b\u60c5\u7eea\u7684\u773c\u795e\uff0c\u5728\u90a3\u77ac\u95f4\u4e0d\u662f\u4e5f\u6709\u53ef\u4ee5\u4e3a\u5979\u820d\u5f03\u4e00\u5207\u7684\u5ff5\u5934\u5417\uff1f\u8fd9\u6bb5\u610f\u8bc6\u4e4b\u540e\uff0c\u5f20\u606a\u53ea\u8bb0\u5f97\u5410\u4e86\uff0c\u5374\u4e0d\u6653\u5f97\u662f\u4e0d\u662f\u5410\u5230\u8f66\u4e0a\u3002</p><p>\u3000\u3000\u518d\u6b21\u9192\u8fc7\u6765\u65f6\uff0c\u5934\u75bc\u6b32\u88c2\uff0c\u8eba\u5728\u5e8a\u4e0a\uff0c\u7a97\u5e18\u62c9\u7740\uff0c\u53ea\u6709\u5fae\u5f31\u7684\u5149\u900f\u8fc7\u7a97\u5e18\u5e03\u900f\u8fdb\u6765\uff0c\u5f20\u606a\u6478\u7d22\u7740\u8981\u5750\u8d77\u6765\uff0c\u624b\u6491\u5230\u4e00\u6367\u987a\u6ed1\u7684\u957f\u53d1\u4e0a\u3002</p><p>\u3000\u3000\u201c\u554a\uff01\u201d\u8bb8\u601d\u5403\u75db\u8f7b\u547c\u8d77\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u8d76\u5fd9\u62ac\u8d77\u624b\uff0c\u5fae\u5149\u91cc\u53ea\u8fa8\u5f97\u6e05\u5f62\u72b6\uff0c\u5f20\u606a\u8fd8\u662f\u770b\u6e05\u8bb8\u601d\u7684\u773c\u775b\u3002</p><p>\u3000\u3000\u201c\u4f60\u9192\u4e86\uff1f\u201d\u8bb8\u601d\u6478\u7d22\u5c06\u5e8a\u5934\u67dc\u4e0a\u7684\u53f0\u706f\u6253\u5f00\uff0c\u7ec6\u58f0\u7ec6\u6c14\u7684\u95ee\u5f20\u606a\uff0c\u201c\u600e\u4e48\u5c31\u559d\u8fd9\u4e48\u591a\u9152\u5462\uff0c\u8981\u4e0d\u8981\u559d\u4e9b\u6e29\u6c34\uff1f\u201d\u8bb8\u601d\u8d77\u8eab\u8981\u53bb\u5012\u5f00\u6c34\uff0c\u5e8a\u5934\u67dc\u4e0a\u51c6\u5907\u7684\u6c34\u90fd\u5df2\u7ecf\u51b0\u51c9\u3002</p><p>\u3000\u3000\u201c\u8ba9\u6211\u62b1\u4e00\u4f1a\u513f\u3002\u201d</p><p>\u3000\u3000\u201c\u554a\uff01\u201d\u8bb8\u601d\u6709\u4e9b\u4e0d\u660e\u6240\u4ee5\u7684\u7ad9\u5728\u90a3\u91cc\u3002</p><p>\u3000\u3000\u5f20\u606a\u62b1\u7740\u5979\u7684\u8170\u80a2\uff0c\u62b1\u5f97\u7d27\u7d27\u7684\uff0c\u8111\u6d77\u91cc\u603b\u4f1a\u95ea\u8fc7\u524d\u4e16\u8bb8\u601d\u5728\u5ead\u5ba1\u4e0a\u6d41\u9732\u51fa\u7684\u7edd\u671b\u7684\u773c\u795e\uff0c\u501f\u7740\u672a\u6563\u7684\u9152\u52b2\uff0c\u524d\u4e16\u7684\u60c5\u7eea\u878d\u8fdb\u6765\uff0c\u63ea\u5fc3\u75db\u5f97\u5389\u5bb3\u3002</p><p>\u3000\u3000\u8bb8\u601d\u80fd\u611f\u89c9\u5230\u5f20\u606a\u6d53\u70c8\u7684\u60c5\u7eea\uff0c\u5374\u4e0d\u6653\u5f97\u8fd9\u80a1\u60c5\u7eea\u4ece\u54ea\u91cc\u55b7\u6d8c\u51fa\u6765\uff0c\u4efb\u4ed6\u5c06\u81ea\u5df1\u7d27\u7d27\u62b1\u7740\uff0c\u8f7b\u8f7b\u6469\u6332\u7740\u4ed6\u7684\u5934\uff0c\u8212\u89e3\u4ed6\u6d53\u70c8\u7684\u60c5\u7eea\u3002</p><p>\u3000\u3000\u597d\u4e86\u4e00\u4f1a\u513f\uff0c\u5f20\u606a\u6536\u62fe\u8d77\u6765\u60c5\u7eea\uff0c\u6f31\u8fc7\u53e3\uff0c\u704c\u4e0b\u4e24\u5927\u676f\u6e29\u5f00\u6c34\uff0c\u5c31\u611f\u89c9\u8212\u670d\u591a\u4e86\uff0c\u5934\u75bc\u4e5f\u7f13\u89e3\u4e86\uff0c\u95ee\u8bb8\u601d\uff1a\u201c\u4f60\u600e\u4e48\u5728\u8fd9\u91cc\uff0c\u6211\u89c9\u5f97\u6211\u5410\u8fc7\u4e86\uff0c\u5374\u4e0d\u8bb0\u5f97\u5410\u54ea\u91cc\u7684\uff0c\u6ca1\u5410\u4f60\u8eab\u4e0a\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u8ddf\u665a\u6674\u59d0\u5728\u4e00\u8d77\uff0c\u542c\u8bf4\u4f60\u559d\u591a\u4e86\uff0c\u4e0d\u653e\u5fc3\u8fc7\u6765\u5c31\u4e00\u8d77\u6765\u770b\u770b\uff0c\u4f60\u5012\u662f\u6ca1\u5410\u6211\u8eab\u4e0a\uff0c\u7b49\u5929\u4eae\u4f60\u8ddf\u665a\u6674\u59d0\u9053\u6b49\u5427\uff0c\u4f60\u7684\u624b\u8fd8\u6293\u9519\u5730\u65b9\u4e86\u3002\u201d\u8bb8\u601d\u8bf4\u9053\uff0c\u201c\u6211\u6015\u4f60\u591c\u91cc\u9192\u6765\u6ca1\u4eba\u7167\u987e\uff0c\u5c31\u7559\u4e86\u4e0b\u6765\u3002\u201d</p><p>\u3000\u3000\u201c\u554a\uff0c\u201d\u5f20\u606a\u6ca1\u6562\u95ee\u6293\u9519\u665a\u6674\u4ec0\u4e48\u5730\u65b9\u4e86\uff0c\u542c\u8bb8\u601d\u8fd9\u4e48\u8bf4\uff0c\u4e5f\u6ca1\u6562\u770b\u5979\u7684\u773c\u775b\uff0c\u53ea\u89e3\u91ca\u9053\uff0c\u201c\u90a3\u9152\u8fd8\u771f\u4e0d\u9519\uff0c\u5728\u5e97\u91cc\u559d\u7740\u6ca1\u6709\u4ec0\u4e48\u611f\u89c9\uff0c\u7a0d\u6709\u4e9b\u9189\u610f\uff0c\u4ee5\u4e3a\u51fa\u6765\u5439\u4e00\u5439\u98ce\u5c31\u597d\uff0c\u6ca1\u60f3\u5230\u7ad9\u8d77\u6765\u9152\u52b2\u5c31\u6d8c\u5230\u5934\u4e0a\uff0c\u6211\u5c0f\u53d4\u9152\u91cf\u90a3\u4e48\u5927\u7684\u4eba\uff0c\u90fd\u8981\u6276\u7740\u684c\u5b50\u624d\u80fd\u7ad9\u7a33\uff0c\u4ed6\u600e\u4e48\u6837\uff1f\u201d</p><p>\u3000\u3000\u201c\u5085\u4fca\u5148\u9001\u4ed6\u56de\u5bb6\u7684\uff0c\u4f60\u56de\u6765\u540e\uff0c\u4ed6\u8fd8\u6253\u8fc7\u7535\u8bdd\u95ee\u4f60\u600e\u4e48\u6837\uff0c\u4ed6\u90a3\u65f6\u9192\u9152\u4e86\uff0c\u8bf4\u4f60\u559d\u5f97\u6bd4\u4ed6\u51f6\u3002\u201d</p><p>\u3000\u3000\u201c\u563f\u563f\u3002\u201d\u5f20\u606a\u5f80\u5e8a\u90a3\u8fb9\u632a\u4e86\u632a\uff0c\u8ba9\u8bb8\u601d\u94bb\u8fdb\u6765\uff0c\u5c06\u5979\u6402\u5728\u6000\u91cc\uff0c\u5c06\u4e0b\u5348\u8003\u8fc7\u8bd5\u4e0e\u5c0f\u53d4\u4e00\u8d77\u53bb\u770b\u5f20\u5955\u7684\u4e8b\u60c5\u544a\u8bc9\u8bb8\u601d\u3002</p><p>\u3000\u3000\u5c06\u706f\u5173\u4e0a\uff0c\u8bb8\u601d\u6795\u7740\u5f20\u606a\u7684\u624b\u81c2\uff0c\u53ea\u662f\u9759\u9759\u7684\u542c\u7740\uff0c\u773c\u7738\u5b50\u5728\u591c\u91cc\u6cdb\u8d77\u4e00\u5c42\u5149\uff0c\u51dd\u89c6\u7740\u5f20\u606a\uff0c\u542c\u5230\u6700\u540e\uff0c\u5fcd\u4e0d\u4f4f\u6df1\u6df1\u7684\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u826f\u4e45\u624d\u8bf4\u9053\uff1a\u201c\u8fd9\u5973\u5b69\u5b50\u80fd\u9047\u5230\u5f20\u5955\uff0c\u5012\u4e5f\u4e0d\u9519\uff0c\u53ea\u662f\u4f60\u4e3a\u4ec0\u4e48\u559d\u8fd9\u4e48\u9152\uff0c\u4e3a\u8fd9\u4e8b\uff1f\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u60f3\u8d77\u6211\u505a\u8fc7\u7684\u4e00\u4e2a\u68a6\uff0c\u4e00\u4e2a\u65e0\u6cd5\u633d\u56de\u8ba9\u4eba\u9192\u6765\u4ecd\u75db\u5fc3\u4e0d\u5df2\u7684\u68a6\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4ec0\u4e48\u68a6\uff1f\u201d</p><p>\u3000\u3000\u5c31\u7b97\u62ff\u68a6\u505a\u6643\u5b50\uff0c\u5f20\u606a\u4e5f\u4e0d\u5fcd\u5fc3\u5c06\u8bb8\u601d\u5728\u524d\u4e16\u7684\u906d\u9047\u8bf4\u7ed9\u5979\u542c\uff0c\u63fd\u8fc7\u5979\u5c0f\u5de7\u7684\u5934\u9885\uff0c\u8ba9\u5979\u7684\u8138\u86cb\u8d34\u7740\u81ea\u5df1\u7684\u80f8\u53e3\uff0c\u8bf4\u9053\uff1a\u201c\u53ea\u662f\u4e00\u4e2a\u8ba9\u4eba\u9192\u6765\u4e5f\u75db\u5fc3\u4e0d\u5df2\u7684\u68a6\uff0c\u8bf4\u4e86\u90fd\u8ba9\u611f\u5230\u5fc3\u75db\uff0c\u8fd8\u662f\u4e0d\u8bf4\u4e86\u3002\u201d</p></article>"}}}]);
