(window.webpackJsonp=window.webpackJsonp||[]).push([[495],{518:function(p){p.exports={chapterTitle:null,title:"\u7b2c495\u7ae0 \u4e00\u5757\u80a5\u8089",content:"<article><h2>\u7b2c495\u7ae0 \u4e00\u5757\u80a5\u8089</h2><p>\u3000\u3000\u5e02\u91cc\u5bf9\u57ce\u533a\u533a\u57df\u89c4\u5212\u8fdb\u884c\u4e86\u5c40\u90e8\u8c03\u6574\uff0c\u5f20\u77e5\u975e\u4e5f\u89c9\u5f97\u5b8f\u8fdc\u5b9e\u4e1a\u53ef\u4ee5\u52a0\u52a0\u901f\u4e86\uff0c\u8fd9\u4e24\u5e74\uff0c\u9664\u4e86\u5b89\u7f6e\u623f\u9879\u76ee\u8d21\u732e\u5927\u91cf\u7684\u5229\u6da6\u4ee5\u6765\uff0c\u76d7\u7248\u88e2\u4e5f\u6e90\u6e90\u4e0d\u65ad\u7684\u8f93\u9001\u5229\u6da6\uff0c\u9664\u4e86\u5f80\u4e16\u7eaa\u9910\u996e\u5a31\u4e50\u96c6\u56e2\u91cc\u8fdb\u884c\u6295\u8d44\u4e4b\u5916\uff0c\u4ed6\u624b\u5934\u8fd8\u79ef\u6512\u4e00\u4e9b\u8d44\u91d1\uff0c\u53ef\u4ee5\u505a\u4e9b\u5927\u52a8\u4f5c\uff0c\u5c31\u8fc7\u6765\u627e\u5f20\u606a\u5546\u91cf\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd8\u5728\u62c5\u5fc3\u4ed6\u4e0e\u665a\u6674\u7684\u4e8b\u60c5\u6709\u6ca1\u6709\u7ed9\u8bb8\u601d\u7aa5\u7834\uff0c\u5c0f\u53d4\u7545\u53d9\u5b8f\u8fdc\u5b9e\u4e1a\u672a\u6765\u7684\u53d1\u5c55\u89c4\u5212\uff0c\u4ed6\u5c31\u6ca1\u6709\u592a\u96c6\u4e2d\u6ce8\u610f\u529b\u53bb\u542c\uff0c\u542c\u4e86\u534a\u5929\uff0c\u624d\u6709\u4e9b\u53d1\u6123\u4e86\u8bf4\uff1a\u201c\u54e6\uff0c\u4f60\u624b\u91cc\u5012\u662f\u79ef\u6512\u4e86\u4e0d\u5c11\u94b1\u554a\u3002\u201d</p><p>\u3000\u3000\u201c\u8ddf\u4f60\u6ca1\u6cd5\u6bd4\u2026\u2026\u201d\u5f20\u77e5\u975e\u563f\u7136\u4e00\u8bf4\uff0c\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u4e24\u4eba\u5728\u7231\u8fbe\u7535\u5b50\u6301\u6709\u7684\u80a1\u6743\u90fd\u503c\u4e24\u5343\u4e07\u7f8e\u91d1\uff0c\u5f20\u77e5\u975e\u7684\u786e\u4e0d\u80fd\u5c06\u4ed6\u7684\u6210\u7ee9\u62ff\u51fa\u6765\u5728\u5f20\u606a\u9762\u524d\u70ab\u8000\u3002</p><p>\u3000\u3000\u201c\u90a3\u4e5f\u4e0d\u9519\u4e86\u3002\u8d75\u9526\u8363\u5728\u6d77\u5dde\u96c4\u8d77\u4e86\u8fd9\u4e48\u591a\u5e74\uff0c\u65d7\u4e0b\u4e5f\u5c31\u4e09\u4e94\u4ebf\uff0c\u4ed6\u8fd9\u6b21\u7ed9\u8c22\u5bb6\u6324\u51fa\u79d1\u738b\uff0c\u9e21\u86cb\u5c31\u90fd\u6446\u5728\u5546\u4e1a\u5730\u4ea7\u4e00\u53ea\u7bee\u5b50\u91cc\u4e86\uff0c\u4e0d\u8fc7\u9526\u57ce\u5730\u4ea7\u7684\u644a\u5b50\u94fa\u5f97\u592a\u5927\uff0c\u4ec5\u6c99\u7530\u897f\u7247\u5728\u5efa\u672a\u5b8c\u5de5\u7684\u5546\u4e1a\u5730\u4ea7\u9879\u76ee\u5c31\u6709\u4e09\u5341\u4e07\u5e73\u65b9\u7c73\u4e4b\u591a\uff0c\u8fd8\u4e0d\u7b97\u4ed6\u624b\u91cc\u634f\u7740\u7684\u51e0\u767e\u4ea9\u4f4d\u4e8e\u6c99\u7530\u897f\u7247\u7684\u571f\u5730\uff0c\u8fd9\u4e9b\u8d44\u4ea7\u8981\u662f\u90fd\u80fd\u8ba9\u4ed6\u6298\u6210\u73b0\u91d1\uff0c\u8fd8\u6e05\u8d37\u6b3e\uff0c\u4ed6\u5c31\u8981\u6210\u4e3a\u6d77\u5dde\u6709\u53f2\u4ee5\u6765\u7b2c\u4e00\u4e2a\u8d85\u5341\u4ebf\u7684\u5bcc\u8c6a\u4e86\u2026\u2026\u201d\u5f20\u606a\u8fd9\u4e48\u8bf4\u7684\u65f6\u5019\uff0c\u663e\u7136\u628a\u81ea\u5df1\u7ed9\u5ffd\u7565\u6389\u4e86\uff0c\u201c\u53ef\u60dc\u554a\uff0c\u4ed6\u624b\u91cc\u7684\u6d41\u52a8\u8d44\u91d1\u5df2\u7ecf\u4e0d\u8d85\u8fc7\u4e94\u5343\u4e07\uff0c\u8981\u8ba9\u4e09\u5341\u4e07\u5e73\u65b9\u7c73\u7684\u5546\u4e1a\u5730\u4ea7\u5efa\u6210\u5b8c\u6210\uff0c\u8fd8\u6709\u51e0\u767e\u4ea9\u7684\u597d\u5730\u8981\u6025\u5f85\u5f00\u53d1\uff0c\u8fd8\u6709\u4ece\u5efa\u884c\u3001\u4ece\u65b0\u52a0\u5761\u56fd\u5bb6\u5f00\u53d1\u94f6\u884c\u83b7\u5f97\u51e0\u4e2a\u4ebf\u7684\u8d37\u6b3e\u90fd\u8981\u53ca\u65f6\u652f\u4ed8\u6708\u606f\uff0c\u5927\u6982\u968f\u968f\u4fbf\u4fbf\u7684\u7ed9\u52a0\u4e00\u6839\u7a3b\u8349\u5c31\u8981\u8ba9\u4ed6\u6447\u4e09\u6447\u5427\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u77e5\u975e\u542c\u5230\u8fd9\u91cc\u773c\u775b\u91cc\u6d41\u9732\u51fa\u5f02\u6837\u7684\u795e\u91c7\uff0c\u8c01\u90fd\u77e5\u9053\u8fd9\u65f6\u5019\u52a0\u628a\u52b2\uff0c\u5c06\u9526\u57ce\u51fb\u57ae\uff0c\u7136\u540e\u4f4e\u4ef7\u5403\u8fdb\u9526\u57ce\u540d\u4e0b\u7684\u8fd9\u4e9b\u4f18\u8d28\u8d44\u4ea7\uff0c\u624d\u662f\u6700\u8bf1\u4eba\u7684\u4e70\u5356\u3002</p><p>\u3000\u3000\u201c\u600e\u4e48\u6837\uff0c\u6709\u6ca1\u6709\u5174\u8da3\uff1f\u201d\u5f20\u77e5\u975e\u5174\u594b\u7684\u95ee\u3002</p><p>\u3000\u3000\u201c\u6ca1\u5174\u8da3\u3002\u201d\u5f20\u606a\u65a9\u9489\u622a\u94c1\u7684\u8bf4\u3002</p><p>\u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff0c\u4f60\u8fd8\u80fd\u5bb9\u5fcd\u8d75\u9526\u8363\u7ee7\u7eed\u6a2a\u7740\u4e00\u4ed8\u81ed\u8138\u5728\u6d77\u5dde\u8000\u6b66\u626c\u5a01\uff1f\u201d\u5f20\u77e5\u975e\u7591\u60d1\u7684\u95ee\uff0c\u201c\u8fd8\u662f\u8bf4\u4f60\u5bf9\u8fd9\u5757\u80a5\u8089\u4e0d\u611f\u5174\u8da3\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4e2a\uff0c\u8d75\u9526\u8363\u5df2\u7ecf\u7ed9\u8c22\u5bb6\u6b3a\u8d1f\u591f\u60e8\u7684\uff0c\u201d\u5f20\u606a\u8138\u4e0a\u9732\u51fa\u4e0d\u5fcd\u7684\u795e\u8272\uff0c\u7a0d\u505c\u7247\u523b\uff0c\u53c8\u8bf4\u9053\uff0c\u201c\u6253\u843d\u6c34\u72d7\uff0c\u6211\u5f53\u7136\u4e5f\u662f\u613f\u610f\u505a\u7684\u2026\u2026\u53ea\u4e0d\u8fc7\u554a\uff0c\u6211\u89c9\u5f97\u9526\u57ce\u96c6\u56e2\u5df2\u7ecf\u4e0d\u662f\u843d\u6c34\u72d7\u4e86\uff0c\u800c\u662f\u522b\u4eba\u773c\u91cc\u7684\u80a5\u8089\u3002\u4e0d\u4ec5\u662f\u4f60\u4eec\u773c\u91cc\u7684\u2026\u2026\u6211\u8fd8\u5728\u60f3\uff0c\u8c22\u6c49\u9756\u3001\u8c22\u5251\u5357\u5c06\u845b\u660e\u4fe1\u7236\u5b50\u62c9\u5230\u8fd9\u4e2a\u6e38\u620f\u91cc\u6765\uff0c\u845b\u660e\u4fe1\u624b\u91cc\u8fd8\u638c\u63e1\u7740\u5609\u4fe1\u6052\u5927\u5730\u4ea7\u96c6\u56e2\uff0c200\u4ebf\u6e2f\u5143\u5e02\u503c\u7684\u5609\u4fe1\u6052\u5927\u5730\u4ea7\uff0c\u6b63\u6cf0\u96c6\u56e2\u65d7\u4e0b\u4e5f\u6709\u5730\u4ea7\u516c\u53f8\uff0c\u4f60\u8bf4\u4ed6\u4eec\u5c31\u6ca1\u6709\u5728\u76ef\u7740\u8fd9\u5757\u80a5\u8089\uff1f\u201d</p><p>\u3000\u3000\u9999\u6e2f\u697c\u5e02\u867d\u7136\u706b\u7206\u5f02\u5e38\uff0c\u4f46\u662f\u6e2f\u5c9b\u5730\u5904\u72ed\u7a84\uff0c\u4e5f\u6ca1\u6709\u591a\u5c11\u571f\u5730\u4f9b\u5730\u4ea7\u516c\u53f8\u5206\u98df\uff0c\u5373\u4f7f\u6ca1\u6709\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\uff0c\u5609\u4fe1\u6052\u5927\u5730\u4ea7\u8fd9\u6837\u7684\u9999\u6e2f\u5730\u4ea7\u5de8\u9cc4\u90fd\u4f1a\u5411\u5185\u5730\u8f6c\u79fb\u3002\u8c22\u6c49\u9756\u52a8\u7528\u5173\u7cfb\u4fc3\u4f7f\u5efa\u884c\u5411\u9526\u57ce\u50ac\u8ba8\u90a3\u7b14\u8fdd\u89c4\u632a\u7528\u7684\u8d37\u6b3e\u5c06\u9526\u57ce\u6324\u51fa\u79d1\u738b\uff0c\u8fd9\u5df2\u7ecf\u7ed9\u6349\u895f\u89c1\u8098\u7684\u9526\u57ce\u8116\u5b50\u5957\u4e0a\u7ef3\u7d22\uff0c\u8981\u8bf4\u8c22\u6c49\u9756\u6ca1\u6709\u770b\u5230\u9526\u57ce\u65d7\u4e0b\u90a3\u4e9b\u4f18\u79c0\u5730\u4ea7\u8d44\u4ea7\uff0c\u5f20\u606a\u6253\u6b7b\u90fd\u4e0d\u4fe1\u3002</p><p>\u3000\u3000\u8c22\u6c49\u9756\u3001\u845b\u660e\u4fe1\u4ed6\u4eec\u4e0d\u6025\u7740\u52a8\u624b\uff0c\u53ea\u4e0d\u8fc7\u662f\u79d1\u738b\u7684\u4e8b\u60c5\u8fd8\u6ca1\u6709\u6446\u5e73\uff0c\u6240\u4ee5\u4ed6\u4eec\u624d\u4e0d\u4f1a\u7126\u6025\u7740\u53bb\u6253\u8349\u60ca\u86c7\u3002</p><p>\u3000\u3000\u5f20\u77e5\u975e\u89c6\u91ce\u5c40\u9650\u5728\u6d77\u5dde\uff0c\u683c\u5c40\u6bd5\u7adf\u8981\u5c11\u4e00\u4e9b\uff0c\u542c\u5f20\u606a\u8fd9\u4e48\u63d0\u9192\uff0c\u5012\u89c9\u5f97\u6709\u8fd9\u53ef\u80fd\uff1a\u201c\u4ed6\u4eec\u8d39\u5c3d\u5fc3\u601d\u8981\u5c06\u82cf\u9038\u7fa4\u642c\u5230\u6d77\u5dde\u6765\uff0c\u4e3b\u8981\u662f\u4e3a\u4e86\u5bf9\u9526\u57ce\u4e0b\u624b\uff0c\u4e5f\u662f\u4e3a\u4e86\u9632\u6b62\u9526\u57ce\u843d\u5165\u5176\u4ed6\u4eba\u7684\u5634\u91cc\uff1f\u201d</p><p>\u3000\u3000\u201c\u5e94\u8be5\u662f\u7684\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u4ed6\u4eec\u603b\u4e0d\u4f1a\u5c06\u82cf\u9038\u7fa4\u642c\u5230\u6d77\u5dde\u6765\u76f4\u63a5\u9488\u5bf9\u7231\u8fbe\u7535\u5b50\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u7231\u8fbe\u7535\u5b50\u4e13\u6ce8\u505a\u5b9e\u4e1a\uff0c\u4e3a\u653f\u5e9c\u505a\u8d21\u732e\uff0c\u53c8\u4e0d\u7ed9\u653f\u5e9c\u6dfb\u9ebb\u70e6\uff0c\u5c31\u7b97\u82cf\u9038\u7fa4\u60f3\u5728\u9e21\u86cb\u91cc\u6311\u9aa8\u5934\u4e5f\u96be\uff0c\u8c22\u6c49\u9756\u3001\u845b\u660e\u4fe1\u3001\u82cf\u9038\u7fa4\u3001\u5468\u5bcc\u660e\u8fd9\u4e9b\u4eba\u5e94\u8be5\u90fd\u662f\u8001\u72d0\u72f8\uff0c\u4e5f\u4e0d\u4f1a\u641e\u4ec0\u4e48\u610f\u6c14\u4e4b\u4e89\uff0c\u8981\u662f\u771f\u5c06\u82cf\u9038\u7fa4\u642c\u5230\u6d77\u5dde\u6765\uff0c\u5bf9\u9526\u57ce\u4e0b\u624b\u7684\u53ef\u80fd\u6027\u5f88\u5927\u3002\u201d\u5f20\u77e5\u975e\u8dd1\u8fc7\u6765\u65f6\u6ee1\u5fc3\u5174\u594b\uff0c\u8ddf\u5f20\u606a\u8ba8\u8bba\u5230\u8fd9\u91cc\uff0c\u5b9e\u5728\u6ca1\u6709\u673a\u4f1a\u4ece\u8c22\u6c49\u9756\u3001\u845b\u660e\u4fe1\u8fd9\u4e9b\u4eba\u5634\u91cc\u5c06\u80a5\u8089\u62a2\u8fc7\u6765\uff0c\u53c8\u4e0d\u7531\u7070\u5fc3\u6cae\u4e27\u8d77\u6765\uff0c\u5fcd\u4e0d\u4f4f\u6c14\u6124\u7684\u8bf4\uff0c\u201c\u8fd9\u4e48\u4e00\u5757\u80a5\u8089\uff0c\u603b\u4e0d\u80fd\u4fbf\u5b9c\u4ed6\u4eec\u8fd9\u4e9b\u4eba\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5475\u5475\uff0c\u201d\u5f20\u606a\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u6211\u5bf9\u8fd9\u4e8b\u6ca1\u6709\u592a\u5927\u7684\u5174\u8da3\uff0c\u4f60\u627e\u90b5\u81f3\u521a\u5546\u91cf\u53bb\uff0c\u8bf4\u4e0d\u5b9a\u6709\u673a\u4f1a\u5c06\u80a5\u8089\u7ed9\u62a2\u56de\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u600e\u4e48\u62a2\uff1f\u201d\u5f20\u77e5\u975e\u597d\u5947\u7684\u95ee\uff0c\u201c\u6309\u4f60\u5206\u6790\u7684\uff0c\u5468\u5bcc\u660e\u9000\u5230\u4eba\u5927\uff0c\u82cf\u9038\u7fa4\u5230\u6d77\u5dde\u6765\u62c5\u5e02\u957f\uff0c\u5468\u5bcc\u660e\u3001\u82cf\u9038\u7fa4\u4e24\u4eba\u5939\u7740\u5510\u5b66\u8c26\uff0c\u5e02\u91cc\u53ef\u80fd\u53c8\u8981\u8ba9\u4ed6\u4eec\u5360\u4e0a\u98ce\u4e86\uff1b\u5609\u4fe1\u6052\u5927\u5730\u4ea7\u62ab\u7740\u6e2f\u8d44\u7684\u5916\u8863\uff0c\u5b9e\u529b\u53c8\u5f3a\u5927\u5230\u60ca\u4eba\uff0c\u4f60\u8981\u5bf9\u8fd9\u5757\u80a5\u8089\u6709\u5174\u8da3\uff0c\u6211\u4eec\u8fd8\u53ef\u4ee5\u8bd5\u4e00\u8bd5\uff0c\u4f60\u8981\u6ca1\u5174\u8da3\uff0c\u5c31\u51ed\u6211\u8ddf\u90b5\u81f3\u521a\u4ed6\u4eec\uff0c\u5b9e\u5728\u6380\u4e0d\u8d77\u4ec0\u4e48\u5927\u6d6a\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8d75\u9526\u8363\u5bf9\u8c22\u3001\u845b\u5df2\u7ecf\u8d77\u4e86\u8b66\u60d5\u4e4b\u5fc3\uff0c\u4e0d\u4f1a\u8f7b\u6613\u5c31\u8303\uff0c\u8c22\u3001\u845b\u6700\u5927\u7684\u53ef\u80fd\u662f\u5229\u7528\u5de8\u989d\u503a\u6743\u538b\u57ae\u9526\u57ce\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u9526\u57ce\u624b\u91cc\u8fd8\u6709\u51e0\u767e\u4ea9\u5730\u6ca1\u6709\u5f00\u53d1\uff0c\u6211\u770b\u4e0d\u7528\u6211\u4eec\u50ac\u4fc3\uff0c\u4ed6\u4eec\u90a3\u8fb9\u4e5f\u4f1a\u6697\u4e2d\u4f7f\u52b2\uff0c\u903c\u8feb\u9526\u57ce\u5f00\u53d1\u8fd9\u4e9b\u5730\u5757\uff0c\u52a0\u5927\u4ed6\u4eec\u7684\u503a\u52a1\u8d1f\u62c5\u2026\u2026\u82e5\u662f\u6700\u540e\u901a\u8fc7\u6536\u8d2d\u503a\u6743\u5c06\u9526\u57ce\u8fd9\u5757\u80a5\u8089\u5403\u4e0b\u6765\uff0c\u5bf9\u4f60\u4eec\u6765\u8bf4\u8fd8\u662f\u6709\u673a\u4f1a\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u9526\u57ce\u65d7\u4e0b\u7684\u5730\u5757\u5168\u90e8\u5f00\u53d1\uff0c\u603b\u8d44\u4ea7\u89c4\u6a21\u4f30\u8ba1\u8981\u8d85\u8fc7\u5341\u4e94\u516d\u4ebf\u2014\u2014\u5f53\u7136\u5e02\u573a\u4f30\u503c\u8fd8\u8981\u5e9e\u5927\u5f97\u591a\u2014\u2014\u800c\u9526\u57ce\u7684\u51c0\u8d44\u4ea7\u4e0d\u4f1a\u8d85\u8fc7\u4e94\u4ebf\uff0c\u5dee\u4e0d\u591a\u4f1a\u5f62\u6210\u5341\u4ebf\u5de6\u53f3\u7684\u8d1f\u503a\uff0c\u8c22\u6c49\u9756\u3001\u845b\u660e\u4fe1\u7b49\u8981\u8fc5\u901f\u541e\u4e0b\u9526\u57ce\uff0c\u5dee\u4e0d\u591a\u4e5f\u8981\u52a8\u7528\u5341\u4e2a\u4ebf\u7684\u8d44\u91d1\u624d\u6709\u628a\u63e1\u3002</p><p>\u3000\u3000\u65e0\u8bba\u662f\u6b63\u6cf0\u8fd8\u662f\u5927\u96c5\uff0c\u8fd1\u5e74\u5185\u90fd\u65e0\u6cd5\u62bd\u53d6\u8fd9\u4e48\u5e9e\u5927\u7684\u8d44\u91d1\uff0c\u60df\u6709\u4f9d\u8d56\u4e8e\u5609\u4fe1\u6052\u5927\uff1b\u4f46\u662f\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u6765\u88ad\uff0c\u5609\u4fe1\u6052\u5927\u5373\u4f7f\u4e0d\u4f1a\u53d7\u5230\u60e8\u91cd\u7684\u6253\u51fb\uff0c\u6295\u8d44\u4e5f\u4f1a\u53d8\u5f97\u66f4\u52a0\u8c28\u614e\uff0c\u8fd9\u5e76\u4e0d\u662f\u845b\u660e\u4fe1\u4e2a\u4eba\u6301\u6709\u5609\u4fe1\u6052\u592725\uff05\u7684\u80a1\u6743\u5c31\u80fd\u63a7\u5236\u7684\uff0c\u5b59\u5c1a\u4e49\u592b\u5987\u8fd8\u6301\u6709\u5609\u4fe1\u6052\u59273\uff05\u7684\u80a1\u7968\u5462\u3002</p><p>\u3000\u3000\u53ea\u8981\u5609\u4fe1\u6052\u5927\u5bf9\u8fd9\u7b14\u4e70\u4e70\u4ea7\u751f\u7591\u8651\uff0c\u673a\u4f1a\u5c31\u4f1a\u843d\u5165\u522b\u4eba\u624b\u91cc\uff1b\u50cf\u5c0f\u53d4\u4ed6\u4eec\u8fd9\u65f6\u5019\u53ea\u662f\u8981\u505a\u597d\u51c6\u5907\u7b49\u5f85\u673a\u4f1a\u800c\u5df2\u3002</p><p>\u3000\u3000\u6709\u4e9b\u7ec6\u8282\u65e0\u6cd5\u89e3\u91ca\u900f\u5f7b\uff0c\u5f20\u606a\u53ea\u8ddf\u4ed6\u5c0f\u53d4\u8bf4\u9053\uff1a\u201c\u73b0\u5728\u8ba9\u8d75\u9526\u8363\u8ddf\u4ed6\u4eec\u72d7\u54ac\u72d7\u597d\u4e86\uff0c\u4f60\u4eec\u8fd9\u8fb9\u505a\u597d\u51c6\u5907\uff0c\u7b49\u4ed6\u4eec\u4e24\u8d25\u4ff1\u4f24\uff0c\u6216\u8bb8\u6709\u6361\u73b0\u6210\u7684\u4fbf\u5b9c\u3002\u201d</p><p>\u3000\u3000\u201c\u5f53\u7136\uff0c\u9526\u57ce\u8fd9\u5757\u80a5\u8089\uff0c\u6211\u4eec\u662f\u6709\u4e9b\u52a8\u5fc3\uff0c\u4e0d\u8fc7\u90a3\u79bb\u6211\u4eec\u592a\u9065\u8fdc\u4e86\uff0c\u6211\u4e00\u76f4\u6709\u5728\u8003\u8651\u554a\uff0c\u773c\u4e0b\u505a\u597d\u81ea\u5df1\u7684\u4e8b\u60c5\u6700\u662f\u91cd\u8981\u7684\u2026\u2026\u201d\u5f20\u77e5\u975e\u8fd9\u65f6\u5019\u624d\u8f6c\u5230\u4eca\u5929\u8fc7\u6765\u7684\u6b63\u9898\u4e0a\uff0c\u201c\u4e39\u4e95\u5df7\u90a3\u8fb9\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u7b49\u7b49\uff0c\u201d\u5f20\u606a\u542c\u51fa\u5473\u513f\u6765\u4e86\uff0c\u201c\u6ca1\u6709\u8054\u5408\u5916\u4eba\u4e00\u8d77\u5751\u81ea\u5df1\u5bb6\u4f84\u5b50\u7684\u9053\u7406\u5427\uff1f\u201d</p><p>\u3000\u3000\u5f20\u77e5\u975e\u563f\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u600e\u4e48\u4f1a\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u624d\u4e0d\u4fe1\u2026\u2026\u201d\u5f20\u606a\u62ff\u8d77\u4e66\u684c\u4e0a\u7684\u7535\u8bdd\u673a\uff0c\u62e8\u4e86\u90b5\u81f3\u521a\u7684\u624b\u673a\u53f7\uff0c\u62e8\u901a\u7535\u8bdd\uff1a\u201c\u8001\u90b5\uff0c\u4f60\u4eba\u5728\u54ea\u91cc\u554a\uff1f\u4f60\u8ddf\u6211\u5c0f\u53d4\u5546\u91cf\u7684\u4e8b\uff0c\u4ed6\u90fd\u8ddf\u6211\u8bf4\u4e86\uff0c\u4f60\u6709\u6ca1\u6709\u7a7a\u9a6c\u4e0a\u5230\u65b0\u9526\u56ed\u6765\u4e00\u4e0b\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u597d\uff0c\u597d\uff0c\u597d\uff0c\u6211\u9a6c\u4e0a\u5c31\u5230\uff0c\u4e94\u5206\u949f\u2026\u2026\u201d\u90b5\u81f3\u521a\u4ece\u7535\u8bdd\u673a\u91cc\u4f20\u51fa\u6765\u7684\u58f0\u97f3\u6709\u4e9b\u5927\uff0c\u5750\u5728\u4e66\u684c\u5bf9\u9762\u7684\u5f20\u77e5\u975e\u90fd\u542c\u5f97\u4e00\u6e05\u4e8c\u695a\u3002</p><p>\u3000\u3000\u5f20\u77e5\u975e\u65e0\u5948\u7684\u6447\u5934\u7b11\u7740\u8bf4\uff1a\u201c\u8fd9\u5c0f\u5b50\u2026\u2026\u90a3\u5c31\u7b49\u4ed6\u8fc7\u6765\u518d\u8c08\u5427\u3002\u201d</p><p>\u3000\u3000\u90b5\u81f3\u521a\u8fd8\u771f\u8d76\u5728\u4e94\u5206\u949f\u5185\u8d76\u5230\u4e86\uff0c\u8d76\u5230\u624d\u77e5\u9053\u5f20\u77e5\u975e\u5230\u65b0\u9526\u4e00\u4e2a\u5c0f\u65f6\u5c31\u8ba8\u8bba\u9526\u57ce\u7684\u95ee\u9898\u4e86\uff0c\u4ed6\u4eec\u7684\u4e8b\u60c5\u8fd8\u6ca1\u6709\u6765\u5f97\u53ca\u8c08\u3002</p><p>\u3000\u3000\u90b5\u81f3\u521a\u8146\u7740\u8138\u8bf4\uff1a\u201c\u6b64\u65f6\u5012\u662f\u80fd\u770b\u51fa\u606a\u5c11\u7684\u9ad8\u77bb\u8fdc\u77a9\uff0c\u5f53\u521d\u8fd8\u771f\u6ca1\u6709\u8c01\u770b\u5f97\u4e0a\u5b89\u7f6e\u623f\u9879\u76ee\uff0c\u4e24\u5e74\u56db\u4e94\u5343\u4e07\uff0c\u6570\u94b1\u90fd\u624b\u53d1\u8f6f\uff0c\u65e9\u77e5\u9053\u5982\u6b64\uff0c\u4e5f\u4e0d\u4f1a\u5c3d\u4fbf\u5b9c\u4f60\u5c0f\u53d4\u554a\uff1b\u6c99\u7530\u897f\u7247\u7684\u5546\u4e1a\u5730\u4ea7\uff0c\u53ea\u80fd\u7b97\u662f\u8d26\u9762\u4e0a\u7684\u6e38\u620f\uff0c\u4e5f\u5c31\u662f\u9526\u57ce\u7684\u5e9c\u5929\u5927\u53a6\u9500\u552e\u60c5\u51b5\u826f\u597d\uff0c\u540e\u6765\u5e02\u91cc\u7981\u6b62\u7092\u4f5c\u697c\u82b1\uff0c\u5546\u4e1a\u5730\u4ea7\u5c31\u6ca1\u6709\u591a\u5927\u8d77\u8272\uff0c\u6211\u624b\u91cc\u62ff\u7740\u7684\u4e94\u91cc\u6eaa\u5934\u7684\u90a3\u680b\u5546\u4e1a\u697c\u76d8\u5927\u6982\u518d\u6709\u4e24\u4e09\u4e2a\u6708\u5c31\u80fd\u5efa\u6210\uff0c\u53ea\u662f\u5bf9\u5e02\u573a\u524d\u666f\u6ca1\u6709\u592a\u5927\u7684\u628a\u63e1\uff0c\u524d\u4e9b\u65f6\u95f4\u8ddf\u4f60\u5c0f\u53d4\u7ecf\u8fc7\u65b0\u6d77\u901a\uff0c\u60f3\u7740\u9526\u6e56\u73b0\u5728\u7adf\u7136\u8fd8\u7a9d\u5728\u65b0\u6d77\u901a\u5927\u53a6\u7684\u5341\u4e8c\u697c\uff0c\u603b\u89c9\u5f97\u6709\u4e9b\u7a9d\u56ca\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u7a9d\u56ca\u4e0d\u7a9d\u56ca\uff0c\u4e0d\u662f\u4f60\u8bf4\u4e86\u5c31\u7b97\u7684\uff0c\u201d\u5f20\u606a\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u60f3\u8981\u9526\u6e56\u4e70\u4e0b\u4f60\u90a3\u680b\u697c\u5e2e\u4f60\u5957\u73b0\uff1f\u201d</p><p>\u3000\u3000\u201c\u563f\u563f\uff0c\u201d\u90b5\u81f3\u521a\u7b11\u7740\u8bf4\uff0c\u201c\u9526\u6e56\u4e5f\u7684\u786e\u4e0d\u80fd\u603b\u7a9d\u5728\u65ee\u65ef\u91cc\uff0c\u603b\u8981\u5728\u6d77\u5dde\u6709\u4e00\u5ea7\u5c5e\u4e8e\u9526\u6e56\u81ea\u5df1\u7684\u9ad8\u697c\u5427\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u65b0\u6d77\u901a\u5927\u53a6\u4e5d\u4e8c\u5e74\u5efa\u6210\u65f6\uff0c\u597d\u6b79\u8fd8\u662f\u6d77\u5dde\u5e02\u7684\u7b2c\u4e00\u9ad8\u697c\uff0c\u8fd9\u65f6\u5019\u7adf\u7136\u7ed9\u4f60\u6c61\u8511\u6210\u65ee\u65ef\u89d2\u843d\uff0c\u597d\u50cf\u4f60\u90a3\u680b\u697c\u76d8\u4e5f\u6ca1\u6709\u65b0\u6d77\u901a\u9ad8\uff1f\u201d</p></article>"}}}]);
