(window.webpackJsonp=window.webpackJsonp||[]).push([[496],{519:function(p){p.exports={chapterTitle:null,title:"\u7b2c358\u7ae0 \u79d1\u738b\u7535\u5b50",content:"<article><h2>\u7b2c358\u7ae0 \u79d1\u738b\u7535\u5b50</h2><p>\u3000\u3000\u63d0\u5230\u5965\u8fd0\u91d1\u724c\u52a9\u5b66\u7684\u6784\u601d\uff0c\u4e5f\u6709\u4eba\u63d0\u51fa\uff1a\u8981\u662f\u56e2\u961f\u9879\u76ee\u83b7\u5f97\u91d1\u724c\uff0c\u662f\u6309\u9879\u76ee\u8ba1\u7b97\u8fd8\u662f\u6309\u4eba\u6570\u8ba1\u7b97\uff1f\u8fd9\u5176\u4e2d\u4ea7\u751f\u7684\u8d39\u7528\u5dee\u8ddd\u5f88\u5927\u3002</p><p>\u3000\u3000\u7537\u8db3\u3001\u7537\u84dd\u8981\u80fd\u593a\u51a0\uff0c\u518d\u7ed9\u4ed6\u4eec\u4e00\u5343\u5e74\u7684\u65f6\u95f4\u90fd\u4e0d\u591f\uff0c\u56e2\u4f53\u9879\u76ee\u4e00\u5411\u4e0d\u662f\u56fd\u5185\u7684\u5f3a\u9879\uff0c\u6709\u4e9b\u53cc\u4eba\u9879\u76ee\uff0c\u5c31\u6ca1\u6709\u5fc5\u8981\u65a4\u65a4\u8ba1\u8f83\uff0c\u603b\u4e4b\u5c06\u53ef\u4f9b\u5e02\u573a\u7092\u4f5c\u7684\u4ef7\u503c\u5c3d\u53ef\u80fd\u6316\u6398\u900f\u5c31\u53ef\u4ee5\u4e86\u3002</p><p>\u3000\u3000\u9664\u4e86\u6350\u5efa\u5e0c\u671b\u5c0f\u5b66\u4e4b\u5916\uff0c\u5f20\u606a\u8fd8\u8ba1\u5212\u7740\u62ff\u51fa\u4e00\u90e8\u5206\u73b0\u91d1\u6765\u91cd\u5956\u83b7\u5f97\u91d1\u724c\u7684\u5965\u8fd0\u51a0\u519b\uff0c\u8fd9\u6837\u624d\u80fd\u5c06\u83b7\u5f97\u91d1\u724c\u7684\u8fd0\u52a8\u5458\u4e34\u65f6\u62c9\u6765\u7ed9\u7231\u8fbe\u7535\u5b50\u505a\u4e00\u56de\u5f62\u8c61\u4ee3\u8a00\u4eba\u3002</p><p>\u3000\u3000\u5f20\u606a\u3001\u82cf\u6d25\u4e1c\u4e0e\u6768\u4e91\u3001\u5218\u660e\u8f89\u5728\u5e02\u573a\u90e8\u529e\u516c\u5ba4\u8ba8\u8bba\u5230\u4e2d\u5348\u5341\u4e8c\u70b9\uff0c\u7c97\u7565\u7684\u62df\u4e86\u4e00\u4e2a\u6846\u67b6\uff0c\u8ba9\u6768\u4e91\u9a6c\u4e0a\u627e\u4eba\u62df\u5b9a\u8be6\u7ec6\u7684\u65b9\u6848\u3002\u79bb\u5965\u8fd0\u4f1a\u4e5f\u53ea\u6709\u4e24\u4e2a\u591a\u6708\u7684\u65f6\u95f4\uff0c\u786e\u4fdd\u8fd0\u52a8\u5458\u83b7\u5f97\u91d1\u724c\u540e\u4f1a\u914d\u5408\u5e02\u573a\u90e8\u5ba3\u4f20\u9020\u52bf\uff0c\u8981\u8ddf\u4f53\u80b2\u4e3b\u7ba1\u90e8\u95e8\u6253\u597d\u4ea4\u9053\uff0c\u8fd9\u6b21\u6700\u597d\u8fd8\u80fd\u5c06\u592e\u89c6\u62c9\u4e0b\u6c34\u914d\u5408\u7092\u4f5c\uff0c\u9700\u8981\u6c9f\u901a\u7684\u5730\u65b9\u5f88\u591a\uff0c\u65f6\u95f4\u4e0a\u4e5f\u4e0d\u5bbd\u88d5\u3002</p><p>\u3000\u3000\u4e2d\u5348\u5c31\u5728\u5382\u533a\u5916\u65b0\u5efa\u7684\u9910\u5385\u7528\u9910\uff0c\u5e95\u5c42\u662f\u76f8\u5bf9\u4ef7\u683c\u4f4e\u5ec9\u7684\u81ea\u9009\u5feb\u9910\uff0c\u4e8c\u5c42\u6709\u7092\u83dc\u4f9b\u5e94\uff0c\u4e5f\u6709\u81ea\u52a9\u9910\u53ef\u4ee5\u9009\u62e9\u3002</p><p>\u3000\u3000\u5f20\u606a\u96be\u5f97\u5728\u8fd9\u8fb9\u7684\u5382\u533a\u51fa\u73b0\uff0c\u5403\u5348\u996d\u65f6\uff0c\u848b\u8587\u3001\u9c81\u5e86\u751f\u7b49\u4eba\u90fd\u8fc7\u6765\u51d1\u70ed\u95f9\uff0c\u8fd9\u4e48\u4e00\u6765\uff0c\u5c31\u7279\u522b\u7684\u5f15\u4eba\u77a9\u76ee\uff0c\u5f20\u606a\u996d\u524d\u53bb\u4e86\u4e00\u8d9f\u5395\u6240\u89e3\u624b\uff0c\u6ca1\u6709\u8fdb\u5395\u6240\u542c\u89c1\u91cc\u9762\u6709\u4eba\u5728\u8bae\u8bba\u4ed6\uff1a\u201c\u8fd9\u9e1f\u4eba\u662f\u8c01\uff0c\u51e0\u5927\u5de8\u5934\u90fd\u51fa\u6765\u966a\u4ed6\u5403\u996d\uff0c\u8be5\u4e0d\u4f1a\u662f\u4ec0\u4e48\u9ad8\u5e72\u5b50\u5f1f\u60f3\u8fdb\u6211\u4eec\u516c\u53f8\u5427\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u90fd\u4e0d\u597d\u610f\u601d\u8fdb\u53bb\u8ddf\u4eba\u5bb6\u6253\u7167\u9762\uff0c\u53ea\u5f97\u618b\u7740\u5c3f\u8f6c\u8eab\u56de\u53bb\u5403\u996d\u3002</p><p>\u3000\u3000\u5403\u8fc7\u4e2d\u996d\uff0c\u5f20\u606a\u4e0e\u82cf\u6d25\u4e1c\u4e00\u8d77\u56de\u5e02\u91cc\uff0c\u76ca\u9686\u9547\u4e0e\u5f00\u53d1\u533a\u7684\u53c9\u8def\u53e3\u7ad6\u7740\u79d1\u738b\u7535\u5b50\u7684\u6807\u8bc6\u8def\u724c\uff0c\u6307\u5411\u4ece\u53c9\u8def\u53e3\u8fc7\u53bb\u5c31\u662f\u9526\u57ce\u4e0e\u6b63\u6cf0\u5408\u8d44\u6210\u7acb\u7684\u5f71\u789f\u673a\u7ec4\u88c5\u751f\u4ea7\u57fa\u5730\u3002</p><p>\u3000\u3000\u201c\u53bb\u79d1\u738b\u770b\u770b\u2026\u2026\u201d\u5f20\u606a\u4e34\u65f6\u8d77\u610f\uff0c\u8ba9\u9a6c\u6d77\u9f99\u5012\u8f66\u8f7d\u4ed6\u4e0e\u82cf\u6d25\u4e1c\u5148\u53bb\u79d1\u738b\u7535\u5b50\u770b\u4e00\u773c\uff0c\u201c\u6bd5\u7adf\u662f\u5bb6\u95e8\u53e3\u7684\u7ade\u4e89\u8005\uff0c\u8981\u7ed9\u4e00\u4e9b\u5c0a\u91cd\u7684\uff0c\u79d1\u738b\u7535\u5b50\u7684\u603b\u7ecf\u7406\u8c22\u77bb\uff0c\u662f\u4e00\u4e2a\u5f88\u4f1a\u5728\u80cc\u5730\u91cc\u505a\u624b\u811a\u7684\u4eba\uff0c\u4f60\u4ee5\u540e\u4f1a\u89c1\u8bc6\u5230\u4ed6\u7684\u624b\u6bb5\u2026\u2026\u201d\u5f20\u606a\u7b11\u7740\u5bf9\u82cf\u6d25\u4e1c\u8bf4\u3002</p><p>\u3000\u3000\u201c\u4ece\u79d1\u738b\u7684\u96f6\u914d\u4ef6\u91c7\u8d2d\u91cf\u63a8\u7b97\uff0c\u79d1\u738b\u4e94\u6708\u4efd\u7684\u4ea7\u91cf\u5927\u6982\u5728\u4e09\u5343\u53f0\u5de6\u53f3\uff0c\u4e3b\u8981\u901a\u8fc7\u6d77\u6cf0\u5728\u7701\u5185\u7684\u7535\u5668\u5356\u573a\u9500\u552e\uff0c\u770b\u7740\u4ed6\u4eec\u4e0d\u65ad\u8ffd\u52a0\u6295\u5165\u7684\u6001\u52bf\uff0c\u9500\u552e\u5e94\u8be5\u4e0d\u9519\uff0c\u4e5f\u5e94\u8be5\u5f88\u6709\u4fe1\u5fc3\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8fd9\u65f6\u5019\u6324\u8fdb\u6765\uff0c\u6ca1\u6709\u4e00\u5b9a\u7684\u4fe1\u5fc3\u600e\u4e48\u884c\uff1f\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u201c\u76f8\u5bf9\u4e00\u822c\u7684\u6574\u673a\u7ec4\u88c5\u5546\uff0c\u4ed6\u4eec\u8fd8\u6709\u5f88\u5927\u7684\u4f18\u52bf\uff0c\u53ea\u8981\u4ed6\u4eec\u80fd\u7a0d\u7a0d\u63a7\u5236\u4e00\u4e0b\u8d2a\u5ff5\uff0c\u5e94\u8be5\u80fd\u8d5a\u86ee\u591a\u7684\u3002\u201d</p><p>\u3000\u3000\u201c\u8fd9\u6b21\u5468\u747e\u745c\u7684\u79d8\u4e66\u95f9\u51fa\u6848\u5b50\uff0c\u5468\u747e\u745c\u5e94\u8be5\u8981\u8d1f\u4e9b\u8d23\u4efb\uff0c\u4ed6\u4eec\u6216\u8bb8\u4f1a\u6536\u655b\u4e00\u4e0b\u3002\u201d\u82cf\u6d25\u4e1c\u8bf4\u9053\u3002</p><p>\u3000\u3000\u5f20\u606a\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u73b0\u5728\u65b0\u95fb\u5c01\u9501\u5f97\u5389\u5bb3\uff0c\u5f20\u94c1\u68ee\u7684\u6848\u5b50\u53ea\u5728\u60e0\u5c71\u4f20\u5f97\u5f88\u5f00\u3001\u5f71\u54cd\u6076\u52a3\uff0c\u5916\u5730\u7684\u98ce\u58f0\u5c31\u5f88\u5c0f\u4e86\u3002\u5468\u747e\u745c\u4f1a\u8c03\u51fa\u60e0\u5c71\uff0c\u8d1f\u8d23\u4efb\u5012\u662f\u4e0d\u4f1a\uff0c\u4ed6\u4eec\u80cc\u540e\u7684\u90a3\u5c42\u7f51\u5bc6\u7740\u5462\u3002\u5468\u747e\u745c\u8c03\u51fa\u60e0\u5c71\uff0c\u4f46\u662f\u6b63\u6cf0\u96c6\u56e2\u5728\u60e0\u5c71\u7acb\u8db3\u5c06\u8fd1\u5341\u5e74\uff0c\u5df2\u7ecf\u5728\u5f53\u5730\u6839\u6df1\u8482\u56fa\uff0c\u4e0d\u9700\u8981\u5468\u747e\u745c\u8fd9\u5c42\u76f4\u63a5\u7684\u4fdd\u62a4\u4f1e\uff0c\u4e00\u76f4\u5468\u747e\u745c\u5728\u5176\u4ed6\u5730\u65b9\u83b7\u5f97\u5b9e\u6743\uff0c\u53cd\u800c\u6210\u4e86\u6b63\u6cf0\u96c6\u56e2\u5411\u5916\u6269\u5f20\u7684\u4e00\u4e2a\u901a\u9053\uff0c\u73b0\u5728\u4ed6\u4eec\u4e3a\u5468\u747e\u745c\u7684\u51fa\u8def\u800c\u6709\u4e9b\u614c\u4e71\uff0c\u7b49\u5468\u747e\u745c\u7684\u4e8b\u60c5\u5b9a\u4e0b\u6765\uff0c\u4f60\u8bf4\u4ed6\u4eec\u4f1a\u4e0d\u4f1a\u6536\u655b\uff1f\u201d\u5f20\u606a\u6de1\u6de1\u7684\u7b11\u7740\uff0c\u201c\u5bf9\u6211\u4eec\u6765\u8bf4\uff0c\u9999\u96ea\u6d77\u5728\u60e0\u5c71\u53d1\u5c55\u6f5c\u5728\u7684\u6700\u5927\u969c\u788d\u4e0d\u5728\u4e86\uff0c\u4e5f\u4e0d\u662f\u4e00\u4e2a\u574f\u6d88\u606f\u3002\u201d</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u542c\u5f20\u606a\u8fd9\u4e48\u8bf4\uff0c\u6697\u81ea\u601d\u91cf\u4e86\u4e00\u756a\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u5012\u662f\u6ca1\u60f3\u5230\u8fd9\u80cc\u540e\u7684\u7ea0\u7f20\u4f1a\u8fd9\u4e48\u590d\u6742\u2026\u2026\u201d</p><p>\u3000\u3000\u9526\u57ce\u4e0e\u6b63\u6cf0\u5408\u8d44\u7684\u79d1\u738b\u7535\u5b50\u662f\u4ee5\u9526\u57ce\u65d7\u4e0b\u4e00\u5bb6\u5c0f\u7535\u5b50\u5143\u4ef6\u5382\u4e3a\u57fa\u7840\u6210\u7acb\u7684\uff0c\u4f4d\u4e8e\u5f00\u53d1\u533a\u7684\u897f\u7aef\uff0c\u4e0e\u76ca\u9686\u9547\u5c31\u7d27\u6328\u7740\uff0c\u4e0e\u7535\u5b50\u5de5\u4e1a\u56ed\u53ea\u6709\u4e24\u516c\u91cc\u5de6\u53f3\u7684\u8ddd\u79bb\uff0c\u539f\u5148\u7684\u5382\u5b50\u4e0e\u6700\u521d\u7684\u58f0\u8fbe\u7535\u5b50\u89c4\u6a21\u76f8\u4eff\uff0c\u5382\u5b50\u5357\u4fa7\u7684\u4e00\u7247\u5730\uff0c\u5927\u7ea6\u6709\u4e24\u4e09\u767e\u4ea9\u7684\u5149\u666f\uff0c\u73b0\u5728\u5df2\u7ecf\u662f\u7e41\u5fd9\u7684\u5de5\u5730\u3002\u5382\u533a\u9053\u8def\u5df2\u7ecf\u65bd\u5de5\u5b8c\u6bd5\uff0c\u5382\u623f\u4e5f\u5df2\u7ecf\u6253\u4e0b\u5730\u57fa\uff0c\u60f3\u5fc5\u8d44\u91d1\u6295\u5165\u7684\u529b\u5ea6\u5f88\u5927\uff0c\u624d\u4f1a\u6709\u8fd9\u4e48\u5feb\u7684\u8fdb\u5ea6\u3002</p><p>\u3000\u3000\u89c4\u6a21\u867d\u7136\u4e0e\u7535\u5b50\u5de5\u4e1a\u533a\u65e0\u6cd5\u76f8\u6bd4\uff0c\u4f46\u662f\u4ec5\u4f5c\u4e3a\u6574\u673a\u7ec4\u88c5\u5de5\u5382\uff0c\u79d1\u738b\u7535\u5b50\u7684\u89c4\u6a21\u5df2\u7ecf\u4e0d\u5c0f\uff0c\u4ee5\u6b63\u6cf0\u96c6\u56e2\u8c22\u5bb6\u90a3\u51e0\u4eba\u8fd8\u7b97\u4e0d\u5dee\u7684\u773c\u5149\uff0c\u60f3\u5fc5\u65e9\u5c31\u8003\u8651\u5230\u53d1\u5c55\u6280\u672f\u3001\u964d\u4f4e\u751f\u4ea7\u6210\u672c\u7684\u91cd\u8981\u6027\u3002</p><p>\u3000\u3000\u5f53\u521d\uff0c\u4e01\u69d0\u5c31\u770b\u5230\u5f71\u789f\u673a\u751f\u4ea7\u4f01\u4e1a\u53d1\u5c55\u7684\u4e09\u6761\u8def\u7ebf\uff0c\u7b2c\u4e00\u6761\u8def\u6700\u7b80\u5355\uff0c\u51e0\u4e4e\u4e0d\u9700\u8981\u6280\u672f\u6295\u5165\uff0c\u53ea\u662f\u91c7\u8d2d\u6210\u719f\u7684\u7ec4\u4ef6\u4e0e\u914d\u5957\u7684\u6574\u673a\u6280\u672f\u65b9\u6848\u8fdb\u884c\u7ec4\u88c5\uff1b\u7b2c\u4e8c\u6761\u8def\u5c31\u662f\u5177\u5907\u4e00\u5b9a\u7684\u6280\u672f\u7814\u7a76\u80fd\u529b\uff0c\u80fd\u591f\u6574\u5408\u597d\u73b0\u6709\u7684\u7ec4\u4ef6\uff0c\u5f00\u53d1\u51fa\u66f4\u5168\u9762\u3001\u66f4\u5b9e\u7528\u7684\u6027\u80fd\uff0c\u5728\u5143\u7ec4\u4ef6\u9009\u62e9\u4e0a\u4e5f\u6709\u66f4\u5927\u7684\u81ea\u7531\u4e3b\uff1b\u7b2c\u4e09\u6761\u8def\uff0c\u53ef\u4ee5\u8bf4\u4e00\u53ea\u811a\u4ecb\u5165\u7ec4\u4ef6\u7684\u751f\u4ea7\uff0c\u5f20\u606a\u4f30\u8ba1\u9664\u4e86\u7231\u8fbe\u7535\u5b50\u63d0\u524d\u8c0b\u5c40\u4e4b\u5916\uff0c\u4e5f\u53ea\u6709\u65b0\u79d1\u7535\u5b50\u6709\u6280\u672f\u5b9e\u529b\u8d70\u7b2c\u4e09\u6761\u8def\uff0c\u79d1\u738b\u7535\u5b50\u773c\u4e0b\u4f30\u8ba1\u4f1a\u9009\u62e9\u8d70\u7b2c\u4e8c\u6761\u8def\uff0c\u6295\u5165\u4e00\u5b9a\u7684\u6280\u672f\u529b\u91cf\u8fdb\u884c\u7814\u53d1\uff0c\u4f46\u662f\u4e3b\u8981\u8fd8\u662f\u4f9d\u8d56\u624b\u5e02\u573a\u624b\u6bb5\u3002</p><p>\u3000\u3000\u7ed5\u7740\u79d1\u738b\u7535\u5b50\u7684\u65b0\u65e7\u5382\u533a\u770b\u4e86\u4e00\u5708\uff0c\u5f20\u606a\u7565\u77e5\u4ed6\u4eec\u7684\u91ce\u5fc3\uff0c\u4e00\u65f6\u95f4\u5012\u4e5f\u65e0\u6cd5\u8bc4\u5224\uff0c\u6bd4\u8d77\u65b0\u79d1\u7535\u5b50\u3001\u6b65\u6b65\u9ad8\uff0c\u79d1\u738b\u8fd8\u4e0d\u80fd\u7b97\u7231\u8fbe\u7535\u5b50\u7684\u5a01\u80c1\uff0c\u4ed6\u4eec\u73b0\u5728\u7684\u57fa\u7840\u8fd8\u6709\u4e9b\u5f31\u3002</p><p>\u3000\u3000\u6253\u7b97\u79bb\u5f00\u65f6\uff0c\u770b\u89c1\u4e00\u8f86\u9ed1\u8272\u7684\u5954\u9a70\u4ece\u5382\u533a\u9a76\u51fa\u6765\u3002</p><p>\u3000\u3000\u662f\u8c22\u77bb\u7684\u8f66\uff0c\u8fd8\u662f\u8c22\u77bb\u5f53\u521d\u79bb\u5f00\u6d77\u88d5\u516c\u53f8\u4e4b\u524d\u79c1\u4eba\u8d2d\u7f6e\u7684\u5954\u9a70190\uff0c\u6d77\u88d5\u516c\u53f8\u7ed9\u4ed6\u641e\u5f97\u4e00\u584c\u7cca\u6d82\uff0c\u4ed6\u5374\u4e70\u4e86\u8fd9\u8f66\u3002</p><p>\u3000\u3000\u5954\u9a70\u8f66\u7684\u8f66\u901f\u5f88\u5feb\uff0c\u4e00\u8f6c\u773c\u5c31\u4ece\u773c\u524d\u9a76\u8fc7\uff0c\u5f20\u606a\u62bf\u7740\u5634\uff0c\u8d75\u9526\u8363\u4e0e\u5468\u5bcc\u660e\u6452\u5f03\u524d\u5acc\uff0c\u91cd\u65b0\u52fe\u7ed3\u5728\u4e00\u8d77\uff0c\u8c22\u5bb6\u529f\u4e0d\u53ef\u6ca1\uff0c\u542c\u8bf4\u5468\u5bcc\u660e\u7684\u5973\u513f\u5468\u7ea2\u6885\u4e0e\u5979\u4e08\u592b\u5728\u65b0\u6cf0\u5e02\u529e\u7684\u516c\u53f8\u76f8\u5f53\u7684\u7ea2\u706b\uff0c\u5f88\u53d7\u8c22\u5bb6\u7684\u7167\u987e\u3002</p><p>\u3000\u3000\u201c\u5e02\u573a\u98ce\u4e91\u6e10\u8d77\uff0c\u5374\u610f\u5473\u7740VCD\u5f71\u789f\u673a\u5e02\u573a\u683c\u5c40\u5728\u5efa\u6210\u7684\u6700\u521d\u5c31\u57cb\u4e0b\u5d29\u6e83\u7684\u6839\u5b50\uff0c\u201d\u5f20\u606a\u7f13\u6162\u7684\u8bf4\u9053\uff0c\u201c\u4eca\u540e\u51e0\u5e74\u7684\u53d1\u5c55\u8109\u7edc\u4e5f\u80fd\u770b\u6e05\u695a\uff0c\u5148\u662f\u5e7f\u544a\u5927\u6218\uff0c\u540e\u662f\u4ef7\u683c\u5927\u6218\uff0c\u4f7f\u5f97\u6574\u673a\u7ec4\u88c5\u5382\u5546\u7684\u5229\u6da6\u7a7a\u95f4\u8fc5\u901f\u538b\u7f29\uff0c\u6700\u540e\u4ece\u7fa4\u9cc4\u7ede\u6740\u7684\u8840\u8165\u65f6\u4ee3\u6b8b\u5b58\u4e0b\u6765\u7684\u6574\u673a\u5382\u5546\u6700\u7ec8\u4e5f\u53ea\u80fd\u62ff\u5230\u6bd4\u52a0\u5de5\u8d39\u9ad8\u4e0d\u4e86\u591a\u5c11\u7684\u5fae\u8584\u5229\u6da6\uff0c\u4e0d\u6653\u5f97\u65e5\u540e\u5728\u7fa4\u9cc4\u9c7c\u7ede\u6740\u65f6\u671f\u751f\u5b58\u4e0b\u6765\u7684\u6574\u673a\u5382\u5546\u4f1a\u4e0d\u4f1a\u5c06\u4e5d\u516d\u5e74\u89c6\u4e3a\u5f71\u789f\u673a\u7684\u9ec4\u91d1\u65f6\u4ee3\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u79d1\u738b\u6216\u8bb8\u80fd\u591f\u6293\u5f97\u4f4f\u9ec4\u91d1\u65f6\u4ee3\u7684\u5c3e\u5df4\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4ed6\u4eec\u672a\u5fc5\u80fd\u6293\u5f97\u4f4f\uff0c\u6b63\u6cf0\u96c6\u56e2\u5728\u79d1\u738b\u4e0a\u6295\u5165\u4e0d\u5c11\uff0c\u4ec5\u6bcf\u4e2a\u6708\u4e09\u56db\u5343\u53f0\u7684\u4ea7\u91cf\uff0c\u65e0\u6cd5\u8ba9\u4ed6\u4eec\u6ee1\u8db3\u7684\uff0c\u201d\u5f20\u606a\u8f7b\u8511\u7684\u4e00\u7b11\uff0c\u201c\u6211\u4eec\u5012\u4e5f\u5e0c\u671b\u5f71\u789f\u673a\u7684\u9ec4\u91d1\u65f6\u4ee3\u80fd\u5c3d\u53ef\u80fd\u5ef6\u957f\uff0c\u81f3\u5c11\u4e5d\u516d\u5e74\uff0c\u7231\u8fbe\u7535\u5b50\u5360\u636e\u592e\u89c6\u6807\u738b\u8fd9\u4e2a\u5e02\u573a\u5ba3\u4f20\u7684\u81f3\u9ad8\u70b9\uff0c\u5728\u5e02\u573a\u4e0a\u7684\u5730\u4f4d\uff0c\u8fd8\u4e0d\u6015\u5176\u4ed6\u5382\u5546\u6765\u4e89\uff0c\u4f46\u662f\u6709\u4e86\u7231\u8fbe\u7535\u5b50\u7684\u793a\u8303\u4f5c\u7528\uff0c\u4e5d\u4e03\u5e74\u7684\u592e\u89c6\u6807\u738b\u4e89\u593a\u4e4b\u60e8\u70c8\u53ef\u60f3\u800c\u77e5\uff0c\u90a3\u6216\u8bb8\u4f1a\u662f\u5e7f\u544a\u5927\u6218\u7684\u8d77\u7aef\uff0c\u5e02\u573a\u5ba3\u4f20\u6210\u672c\u5c06\u4f1a\u6fc0\u589e\uff0c\u63a5\u4e0b\u6765\u5c31\u5e94\u8be5\u662f\u4ef7\u683c\u5927\u6218\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u90a3\u7231\u8fbe\u7535\u5b50\u4eca\u5e74\u8fd8\u4e89\u4e0d\u4e89\u7b2c\u4e09\u5c4a\u7684\u592e\u89c6\u5e7f\u544a\u6807\u738b\uff1f\u201d\u82cf\u6d25\u4e1c\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u65f6\u95f4\u8fd8\u65e9\u5462\uff0c\u4eca\u5e74\u592e\u89c6\u7684\u5e7f\u544a\u62db\u6807\u5927\u4f1a\u5e94\u8be5\u8fd8\u4f1a\u9009\u5728\u5341\u4e00\u6708\u4efd\u8fdb\u884c\uff0c\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u201c\u8fd9\u65f6\u5019\u8ba8\u8bba\u8fd9\u4e2a\u8fd8\u65e9\u4e86\u4e9b\uff0c\u5230\u65f6\u5019\u518d\u8bf4\u5427\u3002\u201d</p><p>\u3000\u3000\u53bb\u5e74\u5341\u4e00\u6708\u4efd\uff0c\u5f20\u606a\u4e00\u4e3e\u638f\u51fa\u516b\u5343\u591a\u4e07\u62ff\u4e0b\u7b2c\u4e8c\u5c4a\u592e\u89c6\u5e7f\u544a\u6807\u738b\uff0c\u5728\u82cf\u6d25\u4e1c\u5f53\u65f6\u7684\u773c\u775b\u91cc\uff0c\u8fd8\u662f\u6781\u4e0d\u53ef\u9884\u6599\u524d\u666f\u7684\u4e00\u62db\u9669\u68cb\uff0c\u534a\u5e74\u7684\u65f6\u95f4\u8fc7\u53bb\u4e86\uff0c\u518d\u56de\u8fc7\u5934\u6765\u770b\uff0c\u5f20\u606a\u5f53\u65f6\u770b\u4f3c\u4e00\u610f\u5b64\u884c\u7684\u51b3\u5b9a\u662f\u5982\u6b64\u7684\u660e\u667a\uff0c\u662f\u5982\u6b64\u7684\u4ee4\u4eba\u53f9\u670d\uff0c\u4ed6\u4f3c\u4e4e\u80fd\u770b\u5230\u5176\u4ed6\u4eba\u6240\u65e0\u6cd5\u51c6\u786e\u9884\u89c1\u7684\u672a\u6765\u4e00\u6837\u3002</p><p>\u3000\u3000\u89c1\u5f20\u606a\u4e0d\u613f\u591a\u8c08\u4e5d\u4e03\u5e74\u592e\u89c6\u6807\u738b\u7684\u8bdd\u9898\uff0c\u82cf\u6d25\u4e1c\u7b11\u4e86\u7b11\uff0c\u5728\u4ed6\u770b\u6765\uff0c\u5f20\u606a\u8fd8\u662f\u90a3\u79cd\u559c\u6b22\u5230\u6700\u540e\u624d\u7ed9\u4eba\u60ca\u559c\u7684\u90a3\u7c7b\u4eba\uff0c\u5341\u4e00\u6708\u4efd\u592e\u89c6\u624d\u53ec\u5f00\u5e7f\u544a\u62db\u6807\u5927\u4f1a\uff0c\u8fd9\u65f6\u5019\u8ba8\u8bba\uff0c\u4e5f\u771f\u662f\u65e9\u4e86\u4e9b\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\u8bf4\uff1a\u201c\u6211\u4eec\u773c\u4e0b\u7d27\u8981\u7684\uff0c\u5c31\u662f\u5c3d\u53ef\u80fd\u5728\u9ad8\u5229\u6da6\u65f6\u671f\u591a\u635e\u4e9b\u94b1\uff0c\u5c06\u7231\u8fbe\u7535\u5b50\u7684\u751f\u4ea7\u4f53\u7cfb\u5b8c\u5584\u8d77\u6765\uff0c\u800c\u8001\u4e01\u8d1f\u8d23\u7684\u6280\u672f\u7814\u53d1\u4e2d\u5fc3\uff0c\u624d\u662f\u6211\u4eec\u624b\u91cc\u6700\u91cd\u8981\u4e00\u5f20\u724c\uff0c\u6211\u5012\u8ba4\u4e3a\u73b0\u5728\u53ef\u4ee5\u8ba9\u6280\u672f\u7814\u53d1\u4e2d\u5fc3\u72ec\u7acb\u51fa\u6765\uff0c\u7136\u540e\u627e\u4e2a\u9002\u5f53\u7684\u65f6\u673a\uff0c\u5c06\u5f71\u789f\u673a\u4e1a\u52a1\u51fa\u552e\u7ed9\u5609\u4fe1\u5b9e\u4e1a\u3002\u201d</p><p>\u3000\u3000\u201c\u9002\u5f53\u7684\u65f6\u673a\u662f\u6307\u9ad8\u5229\u6da6\u65f6\u95f4\u5373\u5c06\u7ed3\u675f\u7684\u65f6\u5019\uff1f\u201d\u82cf\u6d25\u4e1c\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u662f\u554a\uff0c\u201d\u5f20\u606a\u72e1\u9ee0\u7684\u4e00\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u6700\u8d5a\u94b1\u7684\u65f6\u671f\u5373\u5c06\u8fc7\u53bb\uff0c\u8d81\u7740\u9ad8\u4ef7\u51fa\u552e\u624d\u80fd\u8ba9\u5229\u76ca\u6700\u5927\u5316\uff0c\u518d\u8bf4\u4e86\uff0c\u51fa\u552e\u7ed9\u5609\u4fe1\u5b9e\u4e1a\uff0c\u4e5f\u4e0d\u662f\u5b8c\u5168\u5c31\u8131\u79bb\u51fa\u6211\u4eec\u7684\u638c\u63e1\uff0c\u53ea\u662f\u5609\u4fe1\u5b9e\u4e1a\u8981\u6062\u590d\u5728\u9999\u6e2f\u80a1\u5e02\u516c\u5f00\u53d1\u884c\u80a1\u7968\u7b79\u96c6\u8d44\u91d1\u7684\u80fd\u529b\uff0c\u8fd8\u6709\u76f8\u5f53\u957f\u7684\u4e00\u6bb5\u8def\u8981\u8d70\u2026\u2026\u201d</p></article>"}}}]);
