(window.webpackJsonp=window.webpackJsonp||[]).push([[497],{520:function(p){p.exports={chapterTitle:null,title:"\u7b2c359\u7ae0 \u8d85\u7ea7VCD",content:"<article><h2>\u7b2c359\u7ae0 \u8d85\u7ea7VCD</h2><p>\u3000\u3000\u7231\u8fbe\u7535\u5b50\u7684\u6210\u529f\uff0c\u6ce8\u5b9a\u4e5d\u4e03\u5e74\u7684\u592e\u89c6\u6807\u738b\u4e89\u593a\u6ce8\u5b9a\u4f1a\u5f88\u70ed\u95f9\uff0c\u800c\u4e14\u7231\u8fbe\u7535\u5b50\u4eca\u5e74\u7684\u5de8\u5927\u6210\u529f\u5bf9\u540c\u4e1a\u7684\u5f71\u789f\u673a\u7ec4\u88c5\u5382\u5546\u6709\u7740\u66f4\u5f3a\u7684\u793a\u8303\u4f5c\u7528\uff0c\u4e5d\u4e03\u5e74\u592e\u89c6\u5e7f\u544a\u6807\u738b\u7684\u4e89\u593a\u70ed\u70b9\uff0c\u5e94\u8be5\u5728\u5f71\u789f\u673a\u54c1\u724c\u5382\u5546\u4e4b\u95f4\u8fdb\u884c\uff0c\u53bb\u5e74\u91c7\u53d6\u7684\u662f\u4e00\u6b21\u6027\u7684\u6697\u6807\u62a5\u4ef7\u5f62\u5f0f\uff0c\u6ca1\u6709\u592a\u591a\u4ef7\u683c\u4e89\u593a\u7684\u4f59\u5730\uff0c\u5927\u5bb6\u53ea\u662f\u63a8\u6d4b\u522b\u4eba\u7684\u5e95\u4ef7\u7136\u540e\u4e00\u6b21\u6027\u7684\u62a5\u51fa\u81ea\u5df1\u7684\u5f00\u4ef7\u3002</p><p>\u3000\u3000\u4eca\u5e74\u592e\u89c6\u7684\u5e7f\u544a\u4fe1\u606f\u90e8\u5f88\u53ef\u80fd\u4f1a\u91c7\u53d6\u660e\u6807\u7ade\u593a\u7684\u5f62\u5f0f\uff0c\u90a3\u73b0\u573a\u5c06\u60e8\u70c8\u65e0\u6bd4\u3002</p><p>\u3000\u3000\u5f20\u606a\u9884\u8ba1\u4ece\u5341\u4e00\u6708\u4efd\u5f00\u59cb\uff0c\u5f71\u789f\u673a\u5e02\u573a\u5c31\u4f1a\u6380\u8d77\u5e7f\u544a\u6218\u7684\u785d\u70df\u6218\u706b\uff0c\u800c\u4e14\u98de\u5229\u6d66\u4e0a\u6d77\u5de5\u5382\u4e8c\u671f\u5de5\u7a0b\u4e0e\u7d22\u5c3c\u673a\u82af\u5de5\u5382\u7684\u6295\u4ea7\uff0c\u5f71\u789f\u673a\u7684\u4ea7\u80fd\u5df2\u7ecf\u4e0d\u5b58\u5728\u6700\u91cd\u8981\u7684\u4e00\u9053\u74f6\u9888\uff0c\u5e7f\u544a\u5927\u6218\u4f1a\u6700\u5927\u9650\u5ea6\u7684\u6316\u6398\u8fd9\u4e2a\u5e02\u573a\uff0c\u4f46\u662f\u5e02\u573a\u5bb9\u91cf\u5e76\u4e0d\u662f\u53ef\u4ee5\u65e0\u9650\u5236\u7684\u6269\u5f20\u4e0b\u53bb\uff0c\u800c\u5f71\u789f\u673a\u7ec4\u88c5\u91cf\u7684\u6fc0\u589e\uff0c\u4f1a\u8fc5\u901f\u6539\u53d8\u4f9b\u9700\u5e73\u8861\uff0c\u63a5\u4e0b\u6765\uff0c\u5c31\u4f1a\u6380\u8d77\u4f18\u80dc\u52a3\u6c70\u3001\u4e89\u593a\u5e02\u573a\u4efd\u989d\u7684\u4ef7\u683c\u5927\u6218\uff0c\u5f71\u789f\u673a\u8d85\u9ad8\u5229\u6da6\u7684\u9ec4\u91d1\u65f6\u671f\u5927\u6982\u53ea\u80fd\u7ef4\u6301\u5230\u660e\u5e74\u5165\u79cb\uff0c\u800c\u4e14\u5728\u6b64\u4e4b\u524d\uff0c\u9500\u4ef7\u5c31\u4f1a\u9010\u6e10\u7684\u6ed1\u843d\u3002</p><p>\u3000\u3000\u4ef7\u683c\u5927\u6218\u4e00\u7ecf\u5c55\u5f00\uff0c\u6ca1\u6709\u4efb\u4f55\u4e00\u5bb6\u6574\u673a\u5382\u5546\u80fd\u7f6e\u8eab\u4e8b\u5916\uff0c\u7231\u8fbe\u7535\u5b50\u4e5f\u4e0d\u80fd\uff0c\u53ea\u6709\u8ddf\u7740\u964d\u4ef7\u4fc3\u9500\uff0c\u800c\u5e7f\u544a\u5927\u6218\u4f1a\u6781\u9ad8\u7684\u63d0\u5347\u8425\u9500\u6210\u672c\uff0c\u4e24\u76f8\u6324\u538b\uff0c\u5229\u6da6\u7a7a\u95f4\u5c31\u4f1a\u53d8\u5f97\u5f88\u5c0f\uff0c\u7531\u76db\u8f6c\u8870\uff0c\u76f4\u63a5\u8fdb\u5165\u8584\u5229\u6216\u65e0\u5229\u7684\u9ed1\u6697\u65f6\u671f\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u7231\u8fbe\u7535\u5b50\u7684\u60c5\u51b5\u4f1a\u597d\u5f97\u591a\uff0c\u5728\u6280\u672f\u4e0a\u6301\u7eed\u7684\u3001\u5148\u884c\u7684\u6295\u5165\uff0c\u5728\u5176\u4ed6\u5382\u5546\u8fd8\u6709\u7422\u78e8\u5f71\u789f\u673a\u5165\u95e8\u6280\u672f\u7684\u65f6\u5019\uff0c\u7231\u8fbe\u7535\u5b50\u65e9\u5df2\u7ecf\u5f00\u53d1\u51fa\u53ef\u4ee5\u6839\u636e\u6240\u9009\u83dc\u5355\u64ad\u653e\u9ad8\u6e05\u6670\u5ea6\u9759\u6b62\u56fe\u50cf\u7684VCD2.0\u7248\u672c\uff0c\u53e6\u5916\u8fd8\u5411\u96f6\u914d\u4ef6\u5382\u5546\u63d0\u4f9b\u6280\u672f\u652f\u6301\uff0c\u6301\u7eed\u7684\u63d0\u9ad8\u96f6\u914d\u4ef6\u7684\u8d28\u91cf\u3001\u964d\u4f4e\u751f\u4ea7\u6210\u672c\uff08\u5728\u6d77\u5dde\u5730\u533a\u7684\u5f71\u789f\u673a\u6574\u673a\u7ec4\u88c5\u5382\u5546\u5305\u62ec\u79d1\u738b\u7535\u5b50\u5728\u5185\uff0c\u90fd\u4eab\u53d7\u5230\u7231\u8fbe\u7535\u5b50\u7684\u597d\u5904\uff09\u3002\u7231\u8fbe\u7535\u5b50\u5728\u9ad8\u7aef\u5f71\u789f\u673a\u4e0a\u65e0\u4eba\u7ade\u4e89\uff0c\u4e2d\u4f4e\u6863\u5f71\u789f\u673a\u6709\u7740\u6bd4\u522b\u4eba\u66f4\u4f4e\u7684\u751f\u4ea7\u6210\u672c\uff0c\u6700\u91cd\u8981\u7684\u4e00\u70b9\uff0c\u53ea\u8981\u4e0a\u6e38\u65af\u9ad8\u67cf\u3001TI\u89e3\u7801\u677f\u7684\u6280\u672f\u5784\u65ad\u4e0d\u88ab\u7b2c\u4e09\u5bb6\u5382\u5546\u6253\u7834\uff0c\u9999\u6e2f\u516c\u53f8\u8fd8\u53ef\u4ee5\u4eab\u53d7\u5927\u7ea6\u5360\u6574\u4e2a\u89e3\u7801\u677f\u5e02\u573a20\uff05\u7684\u5784\u65ad\u5229\u6da6\u3002</p><p>\u3000\u3000\u5728\u522b\u4eba\u6253\u5f97\u5934\u7834\u8840\u6d41\u4e4b\u65f6\uff0c\u7231\u8fbe\u7535\u5b50\u8fd8\u53ef\u4ee5\u4eab\u53d7\u7a33\u5b9a\u7684\u5229\u6da6\uff0c\u53ea\u662f\u4e0d\u80fd\u8ddf\u524d\u671f\u7684\u66b4\u5229\u76f8\u6bd4\u7f62\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u7684\u8ba1\u5212\uff0c\u662f\u5728\u660e\u5e74\u5165\u79cb\u3001\u5f71\u789f\u673a\u884c\u4e1a\u7531\u76db\u8f6c\u8870\u4e4b\u524d\uff0c\u5c06\u7231\u8fbe\u7535\u5b50\u7684\u5f71\u789f\u673a\u4e1a\u52a1\u6253\u5305\u51fa\u552e\u7ed9\u5609\u4fe1\u5b9e\u4e1a\u3002\u6b64\u65f6\uff0c\u5f71\u789f\u673a\u4ea7\u4e1a\u6b63\u8fdb\u5165\u5168\u9762\u7e41\u8363\u7684\u9636\u6bb5\uff0c\u53ea\u662f\u6709\u4e9b\u8fc7\u5ea6\u7e41\u8363\u4e86\u800c\u5df2\uff0c\u7231\u8fbe\u7535\u5b50\u6709\u4e1a\u7ee9\u4e0e\u8d85\u9ad8\u5229\u6da6\u652f\u6491\uff0c\u5728\u6700\u5927\u5229\u6da6\u5df2\u7ecf\u62bd\u51fa\u6765\u4e4b\u65f6\uff0c\u8fd8\u5b8c\u5168\u53ef\u4ee5\u5356\u51fa\u6700\u597d\u7684\u4ef7\u683c\uff0c\u8fd9\u6837\u7684\u4e8b\u60c5\u4f55\u4e50\u800c\u4e0d\u4e3a\uff1f</p><p>\u3000\u3000\u5f20\u606a\u63a7\u5236\u7740\u7231\u8fbe\u7535\u5b50\u6700\u591a\u7684\u80a1\u6743\uff0c\u81ea\u7136\u4e5f\u662f\u8fd9\u9879\u4ea4\u6613\u6700\u5927\u7684\u53d7\u76ca\u4eba\u3002</p><p>\u3000\u3000\u5609\u4fe1\u5b9e\u4e1a\u672c\u8eab\u6ca1\u6709\u8db3\u591f\u7684\u8d44\u91d1\u5b8c\u6210\u8fd9\u6837\u7684\u6536\u8d2d\uff0c\u5728\u80a1\u5e02\u516c\u5f00\u53d1\u884c\u80a1\u7968\u7b79\u96c6\u8d44\u91d1\u662f\u76f8\u5bf9\u53ef\u884c\u7684\u4e00\u6761\u8def\u3002</p><p>\u3000\u3000\u5728\u65f6\u673a\u4e0a\uff0c\u660e\u5e74\u5165\u79cb\u53c8\u662f\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u5168\u9762\u7206\u53d1\u7684\u65f6\u5019\uff0c\u5728\u6b64\u4e4b\u524d\uff0c\u9999\u6e2f\u80a1\u5e02\u6b63\u7531\u4e8e\u56de\u5f52\u4e3b\u9898\u800c\u5904\u4e8e\u516b\u4e03\u5e74\u80a1\u707e\u6765\u6700\u5927\u7684\u7e41\u8363\u671f\uff0c\u4e2d\u8d44\u7ea2\u7b79\u80a1\u5728\u9999\u6e2f\u80a1\u5e02\u53d7\u5230\u6781\u5927\u7684\u8ffd\u6367\u3002\u8fd9\u65f6\u5019\u9ad8\u4ef7\u53d1\u884c\u80a1\u7968\u7b79\u96c6\u8d44\u91d1\u6536\u8d2d\u7231\u8fbe\u7535\u5b50\u7684\u5f71\u789f\u673a\u4e1a\u52a1\uff0c\u53ea\u9700\u8f6c\u8ba9\u516c\u53f8\u8f83\u5c11\u7684\u80a1\u6743\uff0c\u5374\u80fd\u7b79\u96c6\u66f4\u591a\u7684\u8d44\u91d1\u3002</p><p>\u3000\u3000\u5f20\u606a\u5b9e\u9645\u63a7\u5236\u7740\u5609\u4fe1\u5b9e\u4e1a\u56db\u5206\u4e4b\u4e00\u5f3a\u7684\u80a1\u4efd\uff0c\u5609\u4fe1\u5b9e\u4e1a\u5728\u9999\u6e2f\u80a1\u5e02\u9ad8\u4ef7\u53d1\u884c\u80a1\u7968\uff0c\u5f20\u606a\u4e5f\u662f\u6700\u5927\u7684\u5f97\u76ca\u8005\u3002</p><p>\u3000\u3000\u8981\u5b8c\u6210\u8fd9\u4e2a\u8ba1\u5212\uff0c\u5c31\u8981\u63d0\u524d\u5c06\u7231\u8fbe\u7535\u5b50\u7684\u5f71\u789f\u673a\u4e1a\u52a1\u5206\u79bb\u51fa\u6765\u3002</p><p>\u3000\u3000\u867d\u7136\u5728\u5e74\u524d\u7231\u8fbe\u7535\u5b50\u5c31\u63d0\u51fa\u591a\u5143\u5316\u7ecf\u8425\u7684\u7406\u5ff5\uff0c\u4f46\u662f\u73b0\u5b9e\u4e2d\u8fd8\u6ca1\u6709\u8fdb\u884c\u90a3\u4e00\u6b65\uff0c\u524d\u671f\u53ea\u662f\u6280\u672f\u6269\u5f20\uff0c\u4e3b\u8425\u4e1a\u52a1\u8fd8\u662f\u5f71\u789f\u673a\uff0c\u5728\u5f20\u606a\u770b\u6765\uff0c\u6700\u91cd\u8981\u7684\u8d44\u4ea7\u5374\u662f\u6280\u672f\u7814\u53d1\u4e2d\u5fc3\uff0c\u7231\u8fbe\u7535\u5b50\u4ee5\u540e\u80fd\u6709\u591a\u5927\u7684\u53d1\u5c55\uff0c\u5c31\u8981\u770b\u6280\u672f\u7814\u53d1\u4e2d\u5fc3\u6295\u5165\u8fd9\u4e48\u591a\u4e4b\u540e\uff0c\u80fd\u505a\u51fa\u591a\u5927\u7684\u6210\u7ee9\u3002</p><p>\u3000\u3000\u4e1a\u52a1\u5206\u79bb\uff0c\u4e3b\u8981\u662f\u5c06\u6280\u672f\u7814\u53d1\u4e2d\u5fc3\u5206\u79bb\u51fa\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0a\u5348\u53c2\u89c2\u5b8c\u7535\u5b50\u5de5\u4e1a\u56ed\u533a\u4e4b\u540e\uff0c\u5403\u8fc7\u4e2d\u996d\uff0c\u5230\u9526\u57ce\u4e0e\u6b63\u6cf0\u5408\u8d44\u6210\u7acb\u7684\u79d1\u738b\u7535\u5b50\u7684\u5de5\u5382\u8f6c\u4e86\u4e00\u5708\uff0c\u5c31\u76f4\u63a5\u53bb\u4f4d\u4e8e\u57ce\u5c71\u8def\u7684\u5143\u5668\u4ef6\u5206\u516c\u53f8\uff0c\u6280\u672f\u7814\u7a76\u4e2d\u5fc3\u8fd8\u7559\u5728\u90a3\u91cc\uff0c\u539f\u6765\u4e09\u89d2\u5f62\u7684\u79d1\u7814\u697c\u73b0\u5728\u5168\u90e8\u7ed9\u4e86\u6280\u672f\u4e2d\u5fc3\u4f7f\u7528\u3002</p><p>\u3000\u3000\u660e\u5e74\u5165\u79cb\u4e4b\u524d\u5c31\u8981\u5c06\u5f71\u789f\u673a\u4e1a\u52a1\u6574\u4f53\u51fa\u552e\u7ed9\u5609\u4fe1\u5b9e\u4e1a\uff0c\u65f6\u95f4\u5c3d\u53ef\u80fd\u5b89\u6392\u5728\u5165\u590f\u4e4b\u524d\u3002</p><p>\u3000\u3000\u76ee\u524d\uff0c\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u5728\u6cf0\u56fd\u3001\u83f2\u5f8b\u5bbe\u7b49\u5df2\u7ecf\u521d\u89c1\u7aef\u502a\uff0c\u53ea\u662f\u8fd8\u6ca1\u6709\u5f15\u8d77\u8db3\u591f\u7684\u6ce8\u610f\uff0c\u5f20\u606a\u4e5f\u4e0d\u4fbf\u8bf4\u660e\u9009\u62e9\u51fa\u552e\u5f71\u789f\u673a\u4e1a\u52a1\u7684\u65f6\u673a\u662f\u8ddf\u4e8e\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u6709\u7740\u5bc6\u5207\u7684\u5173\u7cfb\uff0c\u7279\u522b\u662f\u65f6\u95f4\u4e0a\u8981\u5361\u51c6\u3002\u63d0\u524d\u592a\u591a\uff0c\u4e2d\u8d44\u7ea2\u7b79\u80a1\u8fd8\u6ca1\u6709\u5230\u8fbe\u5386\u53f2\u7684\u9ad8\u4f4d\uff0c\u4e0d\u80fd\u6316\u53d6\u6700\u5927\u7684\u5229\u6da6\uff1b\u5ef6\u540e\u592a\u4e45\uff0c\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u5168\u9762\u7206\u53d1\uff0c\u80a1\u5e02\u91cc\u6050\u614c\u5f25\u6f2b\uff0c\u5609\u4fe1\u5b9e\u4e1a\u516c\u5f00\u589e\u53d1\u65b0\u80a1\u7684\u8ba1\u5212\u6781\u53ef\u80fd\u4f1a\u6d41\u4ea7\u3002</p><p>\u3000\u3000\u5728\u65e2\u5b9a\u7684\u8ba1\u5212\u91cc\uff0c\u5f20\u606a\u4e0b\u5348\u5230\u6280\u672f\u4e2d\u5fc3\u78b0\u5934\uff0c\u662f\u6280\u672f\u4e2d\u5fc3\u7814\u5236\u51fa\u6700\u65b0\u4e00\u4ee3\u7684\u5f71\u789f\u673a\uff0c\u662f\u53bb\u542c\u4e01\u69d0\u732e\u5b9d\u7684\uff0c\u4e0d\u8fc7\u5230\u6280\u672f\u4e2d\u5fc3\u4e4b\u540e\uff0c\u4ed6\u7ed9\u665a\u6674\u3001\u5468\u6e38\u3001\u8521\u88d5\u5a1f\u3001\u848b\u8587\u3001\u9c81\u5e86\u751f\u3001\u6768\u4e91\u3001\u5468\u4e00\u5e73\u4ed6\u4eec\u6253\u4e86\u7535\u8bdd\uff0c\u665a\u4e0a\u4e00\u8d77\u5403\u996d\uff0c\u5403\u996d\u65f6\u4e00\u8d77\u8ba8\u8bba\u6280\u672f\u7814\u53d1\u4e2d\u5fc3\u5206\u62c6\u7684\u95ee\u9898\u3002</p><p>\u3000\u3000\u201c\u6bd4\u8d77VCD2.0\uff0c\u6700\u65b0\u7684\u6837\u673a\u5728\u529f\u80fd\u4e0a\u6709\u4e00\u4e2a\u660e\u663e\u7684\u4e0a\u5347\uff0c\u53ef\u4ee5\u8bf4\u5728\u539f\u6765VCD\u7684\u57fa\u7840\u4e0a\u6709\u4e00\u4e2a\u8d28\u7684\u98de\u8dc3\uff0c\u201d\u4e3a\u9999\u96ea\u6d77\u7684\u4e8b\u60c5\uff0c\u5f20\u606a\u5728\u9999\u6e2f\u3001\u60e0\u5c71\u4e4b\u95f4\u5954\u6ce2\u4e86\u4e24\u4e2a\u6708\uff0c\u4e4b\u524d\u53c8\u4e3a\u5c0f\u6c5f\u4e2d\u4e0a\u6e38\u690d\u6797\u7684\u4e8b\u60c5\u5fd9\u788c\uff0c\u5b8c\u5168\u987e\u4e0d\u4e0a\u7231\u8fbe\u7535\u5b50\u8fd9\u8fb9\uff0c\u4e01\u69d0\u8fd9\u6b21\u5f88\u60f3\u8ba9\u6280\u672f\u7814\u53d1\u4e2d\u5fc3\u7684\u5de5\u4f5c\u9707\u64bc\u4e00\u4e0b\u5f20\u606a\u7684\uff0c\u201c\u8fd9\u6b21\u7684\u6539\u8fdb\uff0c\u975e\u5e38\u7684\u5927\uff0c\u6700\u5927\u7684\u6539\u8fdb\u5728\u4e8e\uff0c\u4e0d\u540c\u4e8eVCD\u91c7\u7528MPEG-1\u4f5c\u4e3a\u89c6\u9891\u5b58\u50a8\u683c\u5f0f\uff0c\u65b0\u673a\u578b\u91c7\u7528MPEG-2\u4f5c\u4e3a\u89c6\u9891\u5b58\u50a8\u683c\u5f0f\uff0c\u76f8\u6bd4VCD250\u7ebf\u7684\u6c34\u5e73\u6e05\u6670\u5ea6\uff0c\u65b0\u673a\u578b\u63d0\u9ad8\u5230350\u7ebf\u4ee5\u4e0a\uff0c\u2026\u2026\u6211\u4eec\u5f00\u53d1\u51faSmartstream\u6d41\u7545\u6280\u672f\uff0c\u4e0d\u4ec5\u5177\u5907\u66f4\u5f3a\u5927\u7684\u64ad\u653e\u80fd\u529b\uff0c\u9488\u5bf9\u76ee\u524d\u56fd\u5185\u76d7\u7248\u789f\u5e02\u573a\u6df7\u4e71\u7684\u5c40\u9762\uff0c\u6b64\u9879\u6280\u672f\u5bf9\u789f\u7247\u7684\u7ea0\u9519\u80fd\u529b\u4e5f\u662f\u8d85\u5f3a\u7684\uff1b\u91c7\u7528\u53ef\u53d8\u901f\u7387\u7f16\u7801\uff0c\u4f18\u5316\u56fe\u50cf\u89e3\u7801\u7cfb\u7edf\uff0c\u6d88\u9664\u4ee5\u5f80\u4ea7\u54c1\u5feb\u901f\u8fd0\u52a8\u56fe\u50cf\u6240\u5f15\u8d77\u7684\u9a6c\u8d5b\u7f3a\u9677\uff0c\u6e05\u6670\u5ea6\u66f4\u662f\u63d0\u9ad8\u4e86\u4e00\u4e2a\u53f0\u9636\uff1b\u5728\u64ad\u653e\u529f\u80fd\u4e0a\uff0c\u5177\u5907\u66f4\u591a\u6837\u7684\u65b9\u5f0f\uff0c\u53ef\u4ee5\u524d\u540e\u6162\u653e\u3001\u5feb\u901f\u68c0\u7d22\u3001\u9010\u5e27\u64ad\u653e\uff0c\u8fd8\u53ef\u4ee5\u8fdb\u884c\u4e5d\u753b\u9762\u9884\u89c8\u5e76\u5bf9\u753b\u9762\u8fdb\u884c\u53d8\u7126\u5904\u7406\uff0c\u5e76\u53ef\u4ee5\u5bf9\u6574\u5f20\u789f\u7247\u6216\u5176\u4e2d\u7684\u5355\u66f2\u3001\u5355\u6bb5\u5185\u5bb9\u8fdb\u91cd\u590d\u64ad\u653e\u5e76\u5177\u5907\u968f\u673a\u6216\u5faa\u73af\u64ad\u653e\u7684\u529f\u80fd\u3002\u6700\u65b0\u6837\u673a\u7684\u4e3b\u8981\u63a7\u5236\u8f6f\u4ef6\u80fd\u591f\u8bc6JAVA\u548cHTML\u8ba1\u7b97\u673a\u8bed\u8a00\uff0c\u5927\u5927\u52a0\u5f3a\u53cc\u5411\u4ea4\u4e92\u6027\u80fd\uff0c\u9884\u7559\u8f6f\u786c\u4ef6\u63a5\u53e3\uff0c\u914d\u4ee5\u76f8\u5e94\u7684\u5916\u8054\u8bbe\u5907\uff0c\u8fd8\u53ef\u4ee5\u6210\u4e3a\u4eba\u673a\u4e92\u52a8\u7684\u5b66\u4e60\u5e73\u53f0\u3001\u6e38\u620f\u5e73\u53f0\u2026\u2026\u201d\u4e01\u69d0\u505c\u987f\u4e86\u4e00\u4f1a\u513f\uff0c\u867d\u7136\u4e00\u8fb9\u6f14\u793a\u4e00\u8fb9\u8bb2\u89e3\uff0c\u4f46\u662f\u5f88\u591a\u7684\u672f\u8bed\uff0c\u4ee5\u53ca\u7ec6\u5fae\u529f\u80fd\u7684\u6539\u8fdb\uff0c\u5f20\u606a\u8981\u4ed4\u7ec6\u7406\u89e3\u8d77\u6765\uff0c\u8fd8\u662f\u6709\u4e00\u5b9a\u7684\u56f0\u96be\uff0c\u6700\u540e\uff0c\u4ed6\u4e0d\u65e0\u81ea\u8c6a\u7684\u8bf4\uff0c\u201c\u65b0\u673a\u578b\uff0c\u6bd4\u56fd\u5916\u6b63\u5728\u641e\u7684DVD\u5dee\u4e0d\u4e86\u591a\u5c11\uff0c\u53ea\u8981\u62a2\u5148\u4e00\u6b65\u63a8\u884c\uff0c\u53d6\u4ee3DVD\u4e5f\u4e0d\u662f\u6ca1\u6709\u53ef\u80fd\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8d85\u7ea7VCD\u2026\u2026\u201d\u5f20\u606a\u60f3\u8d77\u4e5d\u4e03\u5e74\u4e0b\u534a\u5e74\uff0c\u65b0\u79d1\u7535\u5b50\u4e0e\u7231\u591a\u7b49\u56fd\u5185\u5f71\u789f\u673a\u5382\u5546\u641e\u51fa\u6765\u7684SVCD\u4e0eCVD\u4e24\u79cd\u65b0\u5f71\u789f\u673a\u6807\u51c6\u4e4b\u4e89\uff0c\u4e00\u76f4\u4e89\u5230\u4e5d\u516b\u5e74\uff0c\u624d\u7531\u56fd\u5bb6\u4fe1\u606f\u4ea7\u4e1a\u90e8\u5236\u5b9a\u7edf\u4e00\u7684\u8d85\u7ea7VCD\u6807\u51c6\uff0c\u663e\u7136\u4e01\u69d0\u4ed6\u4eec\u63d0\u524d\u4e00\u5e74\u591a\u7814\u5236\u8fd9\u79cd\u660e\u663e\u4f18\u4e8eVCD\u7684\u65b0\u578b\u5f71\u789f\u673a\uff0c\u6280\u672f\u7814\u53d1\u4e2d\u5fc3\u7684\u5de5\u4f5c\u7684\u786e\u5f88\u51fa\u8272\uff0c\u5728\u56fd\u5185\u820d\u5f97\u6280\u672f\u6295\u5165\uff0c\u56de\u62a5\u8fd8\u662f\u5f88\u53ef\u89c2\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u5634\u91cc\u7a81\u7136\u5192\u51fa\u8fd9\u4e48\u4e00\u4e2a\u8bcd\uff0c\u4e01\u69d0\u3001\u82cf\u6d25\u4e1c\u4e5f\u662f\u4e00\u6123\uff0c\u5f53\u521d\u8fd8\u62c5\u5fc3\u6280\u672f\u6027\u7684\u4e1c\u897f\u6709\u4e9b\u8d39\u89e3\uff0c\u6ca1\u6709\u60f3\u5230\u4ed6\u7acb\u5373\u60f3\u51fa\u4e00\u4e2a\u8d34\u5207\u4e8e\u65b0\u673a\u578b\u7684\u540d\u79f0\uff0c\u82cf\u6d25\u4e1c\u8bf4\u9053\uff1a\u201c\u8d85\u7ea7VCD\u8fd9\u4e2a\u540d\u79f0\u6b63\u5408\u65b0\u673a\u578b\u7684\u7279\u70b9\uff0c\u6211\u4eec\u5185\u90e8\u7814\u7a76\u8fc7\uff0c\u8d85\u7ea7VCD\u5b8c\u5168\u53ef\u4ee5\u53d6\u4ee3VCD\uff0c\u800c\u4e14Smartstream\u6d41\u7545\u6280\u672f\u4e0e\u53ef\u53d8\u901f\u7387\u7f16\u7801\u662f\u6211\u4eec\u72ec\u6709\u7684\u4e13\u5229\u6280\u672f\u2026\u2026\u81f3\u4e8e\u80fd\u4e0d\u80fd\u53d6\u4ee3DVD\uff0c\u4e5f\u503c\u5f97\u8ba4\u771f\u7684\u7814\u7a76\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201cDVD\u5149\u76d8\u7684\u6807\u51c6\uff0c\u4e5d\u4e94\u5e74\u5e95\u5c31\u7531\u7d22\u5c3c\u3001\u4e1c\u829d\u7b49\u4f01\u4e1a\u5236\u5b9a\u51fa\u6765\uff0c\u98de\u5229\u6d66\u3001\u5148\u950b\u3001\u6c64\u59c6\u900a\u7b49\u7535\u5b50\u5de5\u4e1a\u7684\u56fd\u9645\u5de8\u5934\u90fd\u53c2\u4e0eDVD\u673a\u578b\u7684\u7814\u5236\uff0c\u4e0d\u8981\u8bf4\u8d85\u7ea7VCD\u7684\u6280\u672f\u6bd4DVD\u8fd8\u5dee\u4e00\u4e9b\uff0c\u5c31\u7b97\u6bd4DVD\u7684\u6280\u672f\u8fd8\u8981\u8fdb\u6b65\u4e00\u4e9b\uff0c\u4e5f\u4e0d\u53ef\u80fd\u53d6\u4ee3DVD\uff0c\u9664\u4e86\u6280\u672f\u7684\u5148\u8fdb\u6027\uff0c\u5e02\u573a\u7ade\u4e89\u4e5f\u662f\u6700\u5173\u952e\u7684\u56e0\u7d20\u4e4b\u4e00\u3002\u6280\u672f\u6027\uff0c\u5c31\u4e0d\u53bb\u8ba8\u8bba\u5b83\uff0c\u6280\u672f\u4e2d\u5fc3\u5185\u90e8\u6709\u6743\u8861\uff0c\u5c31\u5e02\u573a\u7ade\u4e89\u80fd\u529b\u8fd9\u4e00\u70b9\uff0c\u5c31\u7b97\u5c06\u5168\u56fd\u7684\u7535\u5b50\u5de5\u4e1a\u5382\u5546\u90fd\u805a\u96c6\u8d77\u6765\uff0c\u90fd\u65e0\u6cd5\u4e0e\u90a3\u4e9b\u56fd\u9645\u7535\u5b50\u5de5\u4e1a\u5de8\u5934\u6297\u8861\u7684\u2026\u2026\u53d6\u4ee3DVD\u8fd9\u4e00\u70b9\uff0c\u5c31\u4e0d\u8981\u8d39\u5fc3\u53bb\u8003\u8651\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u4e48\u8bf4\uff0c\u4e01\u69d0\u3001\u82cf\u6d25\u4e1c\u5fcd\u4e0d\u4f4f\u6709\u4e9b\u6cc4\u6c14\uff0c\u4ed6\u4eec\u5f53\u521d\u53d6\u5f97\u6280\u672f\u4e0a\u7684\u7a81\u7834\uff0c\u8fd8\u662f\u5174\u594b\u4e86\u597d\u4e00\u9635\u5b50\u3002</p></article>"}}}]);
