(window.webpackJsonp=window.webpackJsonp||[]).push([[498],{521:function(p){p.exports={chapterTitle:null,title:"\u7b2c36\u7ae0 \u665a\u6674\u7684\u610f\u5916\u6765\u8bbf",content:"<article><h2>\u7b2c36\u7ae0 \u665a\u6674\u7684\u610f\u5916\u6765\u8bbf</h2><p>\u3000\u3000\u60f3\u4e0d\u5230\u82b7\u5f64\u8fd9\u65f6\u5019\u4f1a\u5230\u6d77\u5dde\u6765\uff0c\u5f20\u606a\u5174\u594b\u7684\u653e\u4e0b\u7535\u8bdd\uff0c\u5f88\u62b1\u6b49\u7684\u5bf9\u8bb8\u601d\u8bf4\uff1a\u201c\u8bb8\u601d\u59d0\uff0c\u5bb6\u91cc\u6765\u5ba2\u4eba\u4e86\uff0c\u4e0d\u5f97\u4e0d\u56de\u53bb\uff0c\u660e\u5929\u6211\u6765\u627e\u4f60\u3002\u201d</p><p>\u3000\u3000\u201c\u56de\u53bb\u5427\uff0c\u201d\u8bb8\u601d\u5a34\u9759\u7684\u7ad9\u5728\u90a3\u91cc\uff0c\u7b11\u4e86\u7b11\uff0c\u201c\u660e\u5929\uff0c\u8bb8\u601d\u59d0\u8fd8\u8981\u51fa\u53bb\u627e\u5de5\u4f5c\u5462\uff0c\u4e0b\u56de\u4e13\u95e8\u8bf7\u4f60\u5403\u996d\u3002\u201d</p><p>\u3000\u3000\u542c\u5230\u8bb8\u601d\u6253\u7b97\u53bb\u627e\u5de5\u4f5c\uff0c\u5c31\u77e5\u9053\u6ca1\u628a\u81ea\u5df1\u7684\u8bdd\u5f53\u56de\u4e8b\uff0c\u5f20\u606a\u82e6\u607c\u7684\u7b11\u4e86\u7b11\uff0c\u4e0d\u591a\u8bf4\u4ec0\u4e48\uff0c\u53ea\u8bf4\u4e86\u53e5\uff1a\u201c\u521a\u56de\u6765\uff0c\u8981\u4e0d\u4f60\u5148\u6b47\u51e0\u5929\uff0c\u793e\u4f1a\u8c03\u67e5\u7684\u4e8b\uff0c\u8bb0\u5f97\u5e2e\u6211\u505a\u3002\u201d\u8bb8\u601d\u6a2a\u4e86\u4ed6\u4e00\u773c\uff1a\u201c\u6211\u4f1a\u62bd\u7a7a\u505a\u7684\u3002\u201d\u5f20\u606a\u4e0d\u6653\u5f97\u8bb8\u601d\u4f1a\u4e0d\u4f1a\u8ba4\u771f\u53bb\u8c03\u67e5\u65b0\u5149\u9020\u7eb8\u5382\u7684\u8d44\u6599\uff0c\u8ddf\u8bb8\u601d\u7236\u4eb2\u6253\u4e86\u58f0\u62db\u547c\uff0c\u5c31\u62e6\u4e86\u4e00\u8f86\u51fa\u79df\u8f66\uff0c\u5f80\u673a\u5173\u5927\u9662\u8d76\u53bb\u3002</p><p>\u3000\u3000\u4e0b\u4e86\u51fa\u79df\u8f66\uff0c\u5f20\u606a\u98de\u5954\u7740\u4e0a\u697c\uff0c\u8fdb\u95e8\u5c31\u5c06\u6251\u8fdb\u6000\u91cc\u7684\u5c0f\u82b7\u5f64\u62b1\u8d77\u6765\uff0c\u5728\u5979\u7c89\u96d5\u7389\u7422\u7684\u5c0f\u8138\u4e0a\u72e0\u72e0\u4eb2\u4e86\u4e00\u53e3\uff0c\u89c1\u82b7\u5f64\u7684\u5988\u5988\u8c22\u665a\u6674\u5750\u5728\u6c99\u53d1\u4e0a\uff0c\u8ddf\u5988\u5988\u804a\u5929\uff0c\u62db\u547c\u9053\uff1a\u201c\u665a\u6674\u59d0\uff0c\u4f60\u600e\u4e48\u4eca\u5929\u5230\u6d77\u5dde\u6765\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u6b21\u8fc7\u6765\u628a\u516c\u53f8\u5728\u6d77\u5dde\u7684\u4e1a\u52a1\u7ed9\u7ed3\u4e86\uff0c\u82b7\u5f64\u542c\u8bf4\u6211\u6765\u6d77\u5dde\uff0c\u94bb\u5230\u8f66\u91cc\u5c31\u4e0d\u80af\u4e0b\u6765\uff0c\u6240\u4ee5\u4e00\u5230\u6d77\u5dde\uff0c\u5c31\u5148\u5954\u4f60\u5bb6\u6765\u4e86\u3002\u201d</p><p>\u3000\u3000\u8c22\u665a\u6674\u5c06\u5802\u5144\u8c22\u77bb\u8d76\u51fa\u6d77\u88d5\u516c\u53f8\uff0c\u624d\u53d1\u73b0\u6d77\u88d5\u516c\u53f8\u65e9\u6210\u4e86\u5343\u75ae\u767e\u5b54\u7684\u70c2\u644a\u5b50\uff0c\u4e0d\u5f97\u4e0d\u6536\u7f29\u4e1a\u52a1\u8fdb\u884c\u5927\u6574\u987f\u3002\u81ea\u4ece\u8f66\u7978\u53d1\u751f\u4e4b\u540e\uff0c\u6d77\u5dde\u529e\u4e8b\u5904\u8fd9\u8fb9\u66f4\u662f\u4e71\u4e86\u4e00\u56e2\u7cdf\uff0c\u4e1a\u52a1\u4eba\u5458\u5dee\u4e0d\u591a\u90fd\u64a4\u79bb\u4e86\uff0c\u8c22\u665a\u6674\u4fbf\u60f3\u5148\u5c06\u8fd9\u91cc\u7684\u4e1a\u52a1\u7ed9\u7ed3\u675f\u6389\uff0c\u66f4\u4f55\u51b5\u8f66\u7978\u662f\u5728\u6d77\u5dde\u53d1\u751f\u7684\uff0c\u8c22\u665a\u6674\u8e0f\u4e0a\u6d77\u5dde\u7684\u571f\u5730\uff0c\u5fc3\u91cc\u603b\u6709\u9690\u90c1\u3002</p><p>\u3000\u3000\u5f20\u606a\u81ea\u7136\u6653\u5f97\u8c22\u665a\u6674\u51fa\u4e8e\u4ec0\u4e48\u7406\u7531\u8981\u628a\u6d77\u5dde\u529e\u4e8b\u5904\u7ed9\u64a4\u6389\uff0c\u4f46\u662f\u4ed6\u4e0d\u5e0c\u671b\u5982\u6b64\uff0c\u9887\u4e3a\u9057\u61be\u7684\u8bf4\uff1a\u201c\u554a\uff0c\u6211\u8fd8\u4ee5\u4e3a\u665a\u6674\u59d0\u6709\u529e\u4e8b\u5904\u5728\u6d77\u5dde\uff0c\u53ef\u4ee5\u5e38\u5e26\u82b7\u5f64\u6765\u770b\u6211\u5462\u2026\u2026\u4e0d\u8fc7\u8fd8\u597d\uff0c\u6211\u8fd8\u662f\u53ef\u4ee5\u7ecf\u5e38\u62bd\u7a7a\u53bb\u7701\u57ce\u770b\u82b7\u5f64\uff0c\u9ad8\u4e00\u7684\u529f\u8bfe\u4e0d\u7b97\u7d27\u3002\u201d</p><p>\u3000\u3000\u8c22\u665a\u6674\u542c\u4e86\u5f20\u606a\u7684\u8bdd\uff0c\u5fae\u5fae\u4e00\u6014\uff0c\u770b\u89c1\u82b7\u5f64\u7d27\u7d27\u6402\u7740\u5c0f\u606a\u7684\u8116\u5b50\uff0c\u5fc3\u91cc\u60f3\uff1a\u8981\u8ba9\u82b7\u5f64\u518d\u6b21\u5f00\u53e3\u8bf4\u8bdd\uff0c\u5f7b\u5e95\u6d88\u9664\u8f66\u7978\u5e26\u7ed9\u5979\u7684\u5fc3\u7406\u6050\u60e7\uff0c\u5c0f\u606a\u65e0\u7591\u662f\u6700\u80fd\u5e2e\u52a9\u5979\u7684\u4eba\uff0c\u4f46\u662f\u5c0f\u606a\u8bfb\u9ad8\u4e2d\u540e\uff0c\u5b66\u4e60\u5c31\u4f1a\u5f88\u7d27\u5f20\uff0c\u4e5f\u6ca1\u6709\u6cd5\u5b50\u7ecf\u5e38\u5230\u7701\u57ce\u6765\u3002</p><p>\u3000\u3000\u8c22\u665a\u6674\u5728\u6b64\u4e4b\u524d\uff0c\u4e00\u5fc3\u60f3\u5c06\u6d77\u5dde\u7684\u4e1a\u52a1\u7ed9\u7ed3\u675f\u6389\uff0c\u4ee5\u540e\u4e0d\u7528\u5230\u6d77\u5dde\u6765\uff0c\u542c\u4e86\u5f20\u606a\u7684\u8fd9\u53e5\u8bdd\uff0c\u53c8\u72b9\u8c6b\u8d77\u6765\uff0c\u6bd5\u7adf\u5bf9\u82b7\u5f64\u6765\u8bf4\uff0c\u8fd9\u4e0d\u662f\u6700\u597d\u7684\u51b3\u5b9a\uff1a\u201c\u529e\u4e8b\u5904\u7684\u4eba\u90fd\u64a4\u56de\u53bb\u4e86\uff0c\u60f3\u4e0d\u7ed3\u675f\u4e5f\u4e0d\u884c\u4e86\uff0c\u4e0d\u8fc7\u6211\u8fd8\u53ef\u4ee5\u5e26\u82b7\u5f64\u5e38\u5230\u6d77\u5dde\u6765\u770b\u4f60\u7684\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u89c1\u5979\u5173\u6389\u8fd9\u8fb9\u529e\u4e8b\u5904\u7684\u51b3\u5fc3\u4e5f\u4e0d\u575a\u51b3\uff0c\u987a\u7740\u5979\u7684\u53e3\u6c14\u8bf4\uff1a\u201c\u6211\u6709\u4e00\u4e2a\u670b\u53cb\u5728\u627e\u5de5\u4f5c\uff0c\u672c\u6765\u6253\u7b97\u4ecb\u7ecd\u7ed9\u665a\u6674\u59d0\uff0c\u73b0\u5728\u5c31\u7b97\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u4ec0\u4e48\u670b\u53cb\uff1f\u201d\u6881\u683c\u73cd\u5bf9\u513f\u5b50\u4ea4\u4ec0\u4e48\u670b\u53cb\uff0c\u603b\u662f\u5f88\u7d27\u5f20\uff0c\u201c\u8bf4\u7684\u8ddf\u5927\u4eba\u4f3c\u7684\uff0c\u8fd8\u8981\u5e2e\u670b\u53cb\u4ecb\u7ecd\u5de5\u4f5c\uff0c\u4f60\u5148\u628a\u81ea\u5df1\u7684\u5b66\u4e60\u641e\u597d\uff0c\u4e0d\u8981\u4e71\u4ea4\u4ec0\u4e48\u670b\u53cb\u3002\u201d</p><p>\u3000\u3000\u8c22\u665a\u6674\u7b11\u7740\u8bf4\uff1a\u201c\u5c0f\u606a\u505a\u4e8b\u6700\u6709\u5206\u5bf8\uff0c\u4e0d\u4f1a\u4e71\u4ea4\u670b\u53cb\uff0c\u770b\u4ed6\u5728\u8fd9\u591a\u534a\u4e2a\u6708\u7684\u8868\u73b0\uff0c\u53ea\u6015\u6211\u4eec\u6210\u5e74\u4eba\uff0c\u90fd\u6ca1\u51e0\u4e2a\u6bd4\u5f97\u4e0a\u4ed6\uff0c\u82b7\u5f64\u4ed6\u7237\u7237\u8fd8\u8bf4\uff0c\u5c0f\u606a\u957f\u5927\uff0c\u6307\u5b9a\u6709\u5927\u51fa\u606f\u5462\u3002\u201d\u53c8\u8f6c\u8fc7\u5934\u5bf9\u5f20\u606a\u8bf4\uff0c\u201c\u90a3\u8ba9\u6211\u60f3\u60f3\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u9887\u4e3a\u9057\u61be\uff0c\u8c22\u665a\u6674\u53ea\u662f\u8bf4\u8ba9\u5979\u60f3\u60f3\uff0c\u800c\u4e0d\u662f\u8bf4\u5148\u89c1\u81ea\u5df1\u670b\u53cb\u4e00\u9762\u624d\u60f3\u60f3\uff0c\u53ef\u89c1\u5979\u6ca1\u6709\u8f6c\u53d8\u5fc3\u610f\u3002</p><p>\u3000\u3000\u7238\u7238\u665a\u4e0a\u6709\u5e94\u916c\uff0c\u5f20\u606a\u4e0e\u5988\u5988\u966a\u8c22\u665a\u6674\u3001\u82b7\u5f64\u5230\u5c0f\u533a\u524d\u9762\u7684\u9152\u5e97\u5403\u8fc7\u665a\u996d\uff0c\u53c8\u56de\u5230\u5bb6\u91cc\u804a\u4e86\u5f88\u4e45\uff0c\u53ea\u5988\u5988\u5728\u573a\uff0c\u53c8\u4e0d\u4fbf\u591a\u804a\u6d77\u88d5\u516c\u53f8\u7684\u4e8b\u60c5\uff0c\u4e34\u5230\u6700\u540e\uff0c\u8fd8\u662f\u82b7\u5f64\u4e0d\u80af\u5b9a\uff0c\u8fde\u8bf4\u5e26\u9a97\u7684\u624d\u8ba9\u5979\u677e\u5f00\u624b\uff0c\u5f20\u606a\u7684T\u6064\u886b\u7ed9\u6293\u5f97\u76b1\u5df4\u5df4\u7684\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u7b2c\u4e8c\u5929\u4e00\u5927\u65e9\uff0c\u8c22\u665a\u6674\u53c8\u5e26\u7740\u82b7\u5f64\u767b\u95e8\u4e86\uff0c\u5bf9\u5f20\u606a\u8bf4\uff1a\u201c\u80fd\u4e0d\u80fd\u89c1\u89c1\u4f60\u7684\u670b\u53cb\uff1f\u201d\u5979\u770b\u5230\u82b7\u5f64\u5bf9\u5c0f\u606a\u90a3\u4e48\u4f9d\u8d56\uff0c\u4e3a\u4e86\u82b7\u5f64\u80fd\u5c3d\u5feb\u5f00\u53e3\u8bf4\u8bdd\uff0c\u4e5f\u8981\u5e38\u5f80\u6d77\u5dde\u8dd1\uff0c\u5c31\u7b97\u4e0d\u6025\u7740\u5728\u6d77\u5dde\u53d1\u5c55\u4e1a\u52a1\uff0c\u7528\u4e2a\u4eba\u628a\u529e\u4e8b\u5904\u4fdd\u7559\u4e0b\u6765\u4e5f\u662f\u597d\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u60f3\u5230\u8fc7\u4e86\u4e00\u591c\uff0c\u4e8b\u60c5\u53c8\u6709\u4e86\u8f6c\u673a\uff0c\u80af\u5b9a\u662f\u5c0f\u82b7\u5f64\u7684\u5173\u7cfb\uff0c\u8ba9\u665a\u6674\u59d0\u6447\u6446\u4e0d\u5b9a\u3002</p><p>\u3000\u3000\u8bb8\u601d\u5bb6\u6ca1\u6709\u7535\u8bdd\uff0c\u7535\u8bdd\u6253\u5230\u8bb8\u601d\u5bb6\u5df7\u5b50\u53e3\u7684\u4fbf\u5229\u5e97\uff0c\u62dc\u6258\u4eba\u53bb\u558a\u8bb8\u601d\u3002\u5f20\u606a\u8bb0\u5f97\u8bb8\u601d\u6628\u5929\u8bf4\u8981\u53bb\u627e\u5de5\u4f5c\uff0c\u5c31\u6015\u5979\u5df2\u7ecf\u51fa\u4e86\u95e8\uff0c\u90a3\u53ea\u6709\u5230\u52b3\u52a8\u529b\u5e02\u573a\u5835\u5979\u53bb\u4e86\u3002\u4e5d\u56db\u5e74\uff0c\u6d77\u5dde\u5e02\u8fd8\u6ca1\u6709\u4f9b\u4eba\u5458\u6d41\u901a\u7684\u4eba\u624d\u5e02\u573a\uff0c\u53ea\u6709\u4e00\u4e2a\u5c0f\u89c4\u6a21\u7684\u52b3\u52a8\u529b\u5e02\u573a\uff0c\u4e3b\u8981\u4e3a\u8fd9\u4e24\u5e74\u6765\u4ece\u4f01\u4e1a\u7cbe\u51cf\u4e0b\u6765\u7684\u4e0b\u5c97\u804c\u5de5\u670d\u52a1\u3002</p><p>\u3000\u3000\u201c\u4e00\u5927\u65e9\u6709\u4ec0\u4e48\u4e8b\uff1f\u201d\u8bb8\u601d\u5fae\u5fae\u6c14\u5598\uff0c\u4e5d\u56db\u5e74\u7535\u8bdd\u8d39\u4e0d\u4fbf\u5b9c\uff0c\u8bb8\u601d\u4ece\u5bb6\u6025\u51b2\u51b2\u7684\u8dd1\u5230\u5df7\u5b50\u53e3\uff0c\u5f00\u53e3\u8bf4\u8bdd\uff0c\u6c14\u90fd\u987a\u4e0d\u4e0a\u6765\u3002</p><p>\u3000\u3000\u201c\u5e2e\u4f60\u8054\u7cfb\u4e0a\u5de5\u4f5c\u7684\u4e8b\uff0c\u4e0a\u5348\u4f60\u62bd\u7a7a\u51fa\u6765\u4e00\u4e0b\uff0c\u89c1\u9762\u5408\u610f\u5c31\u80fd\u5b9a\u4e0b\u6765\u3002\u201d</p><p>\u3000\u3000\u201c\u600e\u4e48\u53ef\u80fd\uff1f\u201d\u8bb8\u601d\u5728\u7535\u8bdd\u90a3\u5934\u4e0d\u76f8\u4fe1\u5f20\u606a\u7684\u8bdd\uff0c\u201c\u6211\u6628\u5929\u5c31\u968f\u53e3\u4e00\u8bf4\uff0c\u4f60\u80fd\u5e2e\u6211\u627e\u4ec0\u4e48\u5de5\u4f5c\uff1f\u201d</p><p>\u3000\u3000\u8bb8\u601d\u7684\u5de5\u4f5c\u5f53\u7136\u4e0d\u6210\u95ee\u9898\uff0c\u5c0f\u53d4\u5f20\u77e5\u975e\u521a\u5c06\u516c\u53f8\u642c\u5230\u5e02\u91cc\u6765\uff0c\u6b63\u5343\u5934\u4e07\u7eea\u7684\u5fd9\u4e71\u7740\uff0c\u968f\u4fbf\u585e\u4e00\u4e2a\u4eba\uff0c\u5b8c\u5168\u4e0d\u6210\u95ee\u9898\uff0c\u5173\u952e\u5f20\u606a\u53e6\u6709\u6253\u7b97\u3002</p><p>\u3000\u3000\u201c\u591a\u5c11\u4e0d\u53ef\u80fd\u7684\u4e8b\u60c5\u90fd\u53d1\u751f\u4e86\uff0c\u4f60\u518d\u4fe1\u6211\u4e00\u56de\u3002\u201d\u5f20\u606a\u4e0d\u8ddf\u8bb8\u601d\u591a\u8bf4\uff0c\u7ea6\u597d\u4e5d\u70b9\u5230\u524d\u95e8\u7684\u80af\u5fb7\u57fa\u5e97\u89c1\u9762\uff0c\u5c31\u628a\u7535\u8bdd\u7ed9\u653e\u4e0b\u6765\u4e86\uff0c\u62b1\u7740\u82b7\u5f64\u56de\u5230\u5ba2\u5385\u3002</p><p>\u3000\u3000\u6881\u683c\u73cd\u8ddf\u665a\u6674\u5728\u5ba2\u5385\u91cc\u8bf4\u8bdd\uff0c\u89c1\u5c0f\u606a\u6253\u5b8c\u7535\u8bdd\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u4f60\u624d\u591a\u5927\u7684\u4eba\uff0c\u600e\u4e48\u60f3\u7740\u8981\u5e2e\u670b\u53cb\u4ecb\u7ecd\u5de5\u4f5c\uff1f\u8fd9\u4e2a\u4eba\u53ef\u9760\u4e0d\u53ef\u9760\uff0c\u4f60\u665a\u6674\u59d0\u7684\u516c\u53f8\uff0c\u4f60\u4e0d\u80fd\u968f\u968f\u4fbf\u4fbf\u7684\u5c31\u4ecb\u7ecd\u4e00\u4e2a\u4eba\u8fdb\u53bb\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u5c06\u82b7\u5f64\u653e\u81ea\u5df1\u7684\u819d\u76d6\u4e0a\uff0c\u5bf9\u665a\u6674\u59d0\u8bf4\uff1a\u201c\u665a\u6674\u59d0\u89c9\u5f97\u6211\u53ef\u4e0d\u53ef\u9760\uff1f\u201d</p><p>\u3000\u3000\u665a\u6674\u7b11\u7740\u8bf4\uff1a\u201c\u672c\u6765\u60f3\u628a\u8fd9\u8fb9\u7684\u4e8b\u90fd\u7ed9\u7ed3\u4e86\uff0c\u5f88\u591a\u4e1a\u52a1\u90fd\u653e\u5f03\u4e86\uff0c\u5269\u4e0b\u4e5f\u6ca1\u6709\u591a\u5c11\u4e8b\uff0c\u628a\u529e\u4e8b\u5904\u4fdd\u7559\u7740\uff0c\u4e5f\u6709\u501f\u53e3\u7ecf\u5e38\u5e26\u7740\u82b7\u5f64\u6765\u770b\u5979\u5c0f\u606a\u54e5\u54e5\uff0c\u6881\u59d0\uff0c\u5c0f\u606a\u505a\u4e8b\u5f88\u8ba9\u4eba\u653e\u5fc3\u7684\u3002\u201d</p><p>\u3000\u3000\u77a7\u8fd9\u4e48\u79f0\u547c\u4e71\u7684\uff0c\u8c22\u665a\u6674\u59d0\u521a\u6ee1\u4e09\u5341\u5c81\uff0c\u98ce\u97f5\u97f6\u534e\uff0c\u6b63\u662f\u6d51\u8eab\u6563\u53d1\u6210\u719f\u5973\u4eba\u9b44\u529b\u7684\u5e74\u9f84\uff0c\u5f20\u606a\u89c9\u5f97\u5524\u5979\u665a\u6674\u59d0\uff0c\u6bd4\u5524\u5979\u8c22\u59e8\uff0c\u66f4\u8d34\u5979\u5fc3\u610f\u3002\u4e0e\u8bb8\u601d\u7ea6\u597d\u4e5d\u70b9\uff0c\u65f6\u95f4\u8fd8\u65e9\uff0c\u4f46\u662f\u5f88\u591a\u60f3\u6cd5\u8981\u5148\u6c9f\u901a\u597d\u624d\u884c\u3002</p><p>\u3000\u3000\u5f20\u606a\u62b1\u7740\u82b7\u5f64\u5148\u4e0b\u4e86\u697c\uff0c\u770b\u7740\u8c22\u665a\u6674\u4ece\u540e\u9762\u8ddf\u4e0a\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6d77\u88d5\u516c\u53f8\u505a\u5230\u4eca\u5929\u7684\u89c4\u6a21\u5f88\u4e0d\u5bb9\u6613\uff0c\u6211\u77e5\u9053\u6d77\u88d5\u516c\u53f8\u76ee\u524d\u6709\u4e9b\u56f0\u96be\uff0c\u4f46\u90fd\u662f\u6682\u65f6\u7684\uff0c\u53ea\u8981\u8c22\u665a\u6674\u6709\u51b3\u5fc3\uff0c\u76f8\u4fe1\u5f88\u5feb\u5c31\u80fd\u628a\u516c\u53f8\u7406\u987a\u3002\u5f53\u7136\uff0c\u6211\u4e5f\u80fd\u7406\u89e3\u665a\u6674\u59d0\u653e\u5f03\u6d77\u5dde\u4e1a\u52a1\u662f\u51fa\u4e8e\u4ec0\u4e48\u8003\u8651\uff0c\u4f46\u662f\u8fd9\u4e48\u597d\u7684\u8d44\u6e90\u4e0d\u53bb\u5145\u5206\u7684\u5229\u7528\u8d77\u6765\uff0c\u6211\u8fd8\u662f\u89c9\u5f97\u6709\u4e9b\u53ef\u60dc\uff0c\u5982\u679c\u665a\u6674\u59d0\u4fe1\u4efb\u6211\u7684\u8bdd\uff0c\u201d\u5f20\u606a\u8bf4\u5230\u8fd9\u91cc\u505c\u987f\u4e86\u4e00\u4e0b\uff0c\u89c1\u8c22\u665a\u6674\u773c\u795e\u6709\u4e9b\u98d8\u5ffd\uff0c\u77e5\u9053\u63d0\u8fd9\u6837\u7684\u5efa\u8bae\uff0c\u4f1a\u51fa\u4e4e\u5979\u7684\u610f\u6599\uff0c\u201c\u6211\u80fd\u4e0d\u80fd\u5229\u7528\u8fd9\u4e9b\u8d44\u6e90\uff1f\u201d</p></article>"}}}]);
