(window.webpackJsonp=window.webpackJsonp||[]).push([[5],{27:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u56db\u8282 \u5669\u8017\u3001\u53d1\u70e7\u548c\u521d\u543b",content:"<article><h2>\u7b2c\u56db\u8282 \u5669\u8017\u3001\u53d1\u70e7\u548c\u521d\u543b</h2><p>\u51b2\u8fdb\u6765\u7684\u4eba\uff0c\u743c\u6069\u548c\u73ca\u5609\u90fd\u8ba4\u8bc6\uff0c\u53eb\u5361\u65af\u7279\u7f57\uff0c\u4e00\u4e2a\u4e2d\u5e74\u4eba\uff0c\u548c\u4ed6\u4eec\u4f4f\u540c\u4e00\u6761\u8857\u4e0a\uff0c\u7b97\u662f\u8857\u574a\u90bb\u5c45\uff0c\u5e73\u5e38\u89c1\u4e86\u9762\uff0c\u4e5f\u4f1a\u70b9\u5934\u6253\u4e2a\u62db\u547c\u3002</p><p>\u4ed6\u6c14\u5598\u5401\u5401\u3002</p><p>\u201c\u73ca\u5609\u3001\u743c\u6069\uff0c\u201d\u4ed6\u53eb\u7740\u59d0\u5f1f\u4fe9\u7684\u540d\u5b57\uff0c\u201c\u51fa\u4e8b\u4e86\uff01\u201d</p><p>\u786e\u5b9e\u662f\u51fa\u4e8b\u4e86\u3002</p><p>\u7ed3\u5a5a\u7eaa\u5ff5\u65e5\uff0c\u743c\u6069\u7684\u7236\u6bcd\u53bb\u901b\u8857\u4e86\uff0c\u8fd9\u672c\u662f\u4ef6\u597d\u4e8b\u3002\u7136\u800c\u6240\u8c13\u5929\u6709\u4e0d\u6d4b\u98ce\u4e91\uff0c\u4ed6\u4eec\u6070\u597d\u649e\u4e0a\u4e86\u4e24\u4f4d\u5deb\u5e08\uff0c\u800c\u4e14\u662f\u4e24\u4f4d\u813e\u6c14\u66b4\u8e81\u7684\u5deb\u5e08\u3002</p><p>\u8fd9\u4e24\u4f4d\u5deb\u5e08\uff0c\u4f3c\u4e4e\u662f\u6709\u4e9b\u4ec7\u6028\uff0c\u4e5f\u53ef\u80fd\u5c31\u662f\u5355\u7eaf\u7684\u5f7c\u6b64\u770b\u4e0d\u987a\u773c\uff0c\u603b\u4e4b\uff0c\u4ed6\u4eec\u65e9\u5c31\u4e0d\u5408\u3002\u8fd9\u6b21\u4e0d\u77e5\u600e\u4e48\u641e\u7684\uff0c\u4e24\u4f4d\u5c0a\u656c\u7684\u5deb\u5e08\u5927\u4eba\u5c45\u7136\u90fd\u6765\u901b\u8857\uff0c\u800c\u4e14\u5728\u8857\u5934\u649e\u89c1\u4e86\u3002</p><p>\u649e\u89c1\u4e5f\u5c31\u7f62\u4e86\uff0c\u504f\u504f\u8fd8\u4e89\u5435\u8d77\u6765\uff1b\u4e89\u5435\u8d77\u6765\u4e5f\u5c31\u7f62\u4e86\uff0c\u504f\u504f\u8fd8\u6253\u8d77\u6765\u3002\u4e24\u4f4d\u5deb\u5e08\u7684\u51b3\u6597\uff0c\u81ea\u7136\u6bd4\u6218\u58eb\u4eec\u7684\u5200\u5251\u780d\u6740\u534e\u4e3d\u5f97\u591a\u2014\u2014\u534e\u4e3d\u7684\u540e\u679c\uff0c\u5c31\u662f\u5468\u56f4\u4e00\u5708\u4eba\u6ca6\u4e3a\u88ab\u6b83\u53ca\u7684\u6c60\u9c7c\uff0c\u5176\u4e2d\u5305\u62ec\u743c\u6069\u7684\u8001\u7238\u8001\u5988\u3002</p><p>\u743c\u6069\u4e0d\u77e5\u9053\u4ed6\u4eec\u662f\u600e\u4e48\u6b7b\u7684\uff0c\u56e0\u4e3a\u6ca1\u6cd5\u53bb\u52d8\u6d4b\u73b0\u573a\uff0c\u65e9\u88ab\u5c01\u9501\u4e86\u3002\u81f3\u4e8e\u76ee\u51fb\u8005\uff0c\u9760\u5f97\u8fd1\u7684\u90fd\u88ab\u4e00\u8d77\u6b83\u53ca\u4e86\uff0c\u9760\u5f97\u8fdc\u7684\u4e5f\u90fd\u88ab\u4e0b\u4e86\u5c01\u53e3\u4ee4\u3002</p><p>\u5982\u524d\u6240\u8ff0\uff0c\u9634\u9b42\u57ce\u662f\u4e2a\u7b49\u7ea7\u975e\u5e38\u68ee\u4e25\u7684\u5730\u65b9\uff0c\u7531\u5deb\u5e08\u548c\u7267\u5e08\u5171\u540c\u7edf\u6cbb\uff0c\u524d\u8005\u638c\u63e1\u4e16\u4fd7\u6743\u529b\uff0c\u540e\u8005\u638c\u63e1\u5b97\u6559\u4fe1\u4ef0\u3002\u6240\u4ee5\u5deb\u5e08\u4eec\u5728\u5168\u57ce\u5c45\u6c11\u4e2d\u7684\u4f4d\u9636\uff0c\u4e5f\u76f8\u5e94\u5730\u5c5e\u4e8e\u6700\u9ad8\u7ea7\u90a3\u4e00\u7b49\uff0c\u548c\u7267\u5e08\u4eec\u5e76\u5217\uff0c\u800c\u743c\u6069\u7684\u7236\u6bcd\uff0c\u5c5e\u4e8e\u5546\u4eba\uff0c\u662f\u6bd4\u8f83\u4f4e\u7ea7\u7684\u4e00\u7b49\uff0c\u4ec5\u4ec5\u6bd4\u4ec6\u5f79\u548c\u5974\u96b6\u9ad8\u7ea7\u3002\u5f53\u65f6\u5468\u56f4\u6302\u6389\u7684\u5176\u4ed6\u4eba\uff0c\u4e5f\u548c\u743c\u6069\u7684\u7236\u6bcd\u5dee\u4e0d\u591a\uff0c\u6216\u8005\u5546\u4eba\uff0c\u6216\u8005\u5de5\u5320\uff0c\u6216\u8005\u4e00\u822c\u7684\u5e02\u6c11\uff0c\u90fd\u4e0d\u662f\u4ec0\u4e48\u4e0a\u6d41\u793e\u4f1a\u7684\u89d2\u8272\u3002</p><p>\u4e24\u4f4d\u9ad8\u4f4d\u9636\u8005\u51b3\u6597\uff0c\u9020\u6210\u4e00\u7fa4\u4f4e\u4f4d\u9636\u8005\u6b7b\u4ea1\u2014\u2014\u6309\u9634\u9b42\u57ce\u7684\u6cd5\u5f8b\uff0c\u662f\u4e0d\u5fc5\u62b5\u547d\u7684\uff0c\u56e0\u4e3a\u9ad8\u4f4d\u9636\u8005\u6bd4\u4f4e\u4f4d\u9636\u8005\u5c0a\u8d35\u3002\u8fd9\u5176\u5b9e\u548c\u4e2d\u56fd\u5143\u671d\u5dee\u4e0d\u591a\uff0c\u628a\u4eba\u5206\u7b49\u7ea7\uff0c\u4f4d\u9636\u8d8a\u9ad8\u7279\u6743\u8d8a\u5927\u3002</p><p>\u95ee\u9898\u5728\u4e8e\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u9690\u9690\u900f\u7740\u8e4a\u8df7\u3002</p><p>\u9634\u9b42\u57ce\u56fa\u7136\u7b49\u7ea7\u4f4d\u9636\u68ee\u4e25\uff0c\u540c\u65f6\u4e5f\u5f8b\u6cd5\u4e25\u660e\uff0c\u5b9e\u884c\u7684\u53c8\u662f\u534a\u519b\u4e8b\u5316\u7ba1\u7406\uff0c\u5f53\u8857\u6597\u6bb4\u8fd9\u79cd\u4e25\u91cd\u6270\u4e71\u6cbb\u5b89\u7684\u4e8b\u60c5\u662f\u660e\u4ee4\u7981\u6b62\u7684\u3002\u5deb\u5e08\u4eec\u56fa\u7136\u6267\u638c\u5927\u6743\uff0c\u5374\u4e5f\u8fd8\u666e\u904d\u90fd\u7b97\u5f97\u4e0a\u8c28\u614e\u81ea\u5f8b\uff0c\u6781\u5c11\u542c\u8bf4\u6709\u8fdd\u6cd5\u4e71\u7eaa\u4e4b\u4e8b\uff0c\u8981\u8bf4\u8d77\u6765\uff0c\u53cd\u5012\u662f\u7267\u5e08\u6bd4\u8f83\u56a3\u5f20\u8dcb\u6248\u3002</p><p>\u8fd9\u4e2a\u4e16\u754c\u6709\u5f88\u591a\u795e\u7947\uff0c\u76f8\u5e94\u7684\u81ea\u7136\u4e5f\u5c31\u6709\u5f88\u591a\u79cd\u7267\u5e08\u3002\u4f46\u5728\u9634\u9b42\u57ce\u4e2d\uff0c\u53ea\u6709\u4e00\u79cd\uff0c\u5c31\u662f\u6697\u591c\u5973\u795e\u838e\u5c14\u7684\u7267\u5e08\uff0c\u522b\u65e0\u5176\u4ed6\u3002</p><p>\u9634\u9b42\u57ce\u662f\u838e\u5c14\u7684\u7edd\u5bf9\u52bf\u529b\u8303\u56f4\uff0c\u6ca1\u6709\u5176\u4ed6\u4efb\u4f55\u795e\u7947\u80fd\u591f\u63d2\u624b\u3002</p><p>\u838e\u5c14\u662f\u4e00\u4f4d\u90aa\u795e\uff0c\u90aa\u6076\u3001\u72e1\u733e\uff0c\u800c\u4e14\u9690\u9690\u6709\u4e00\u4e9b\u75af\u72c2\uff0c\u5979\u7684\u7267\u5e08\u81ea\u7136\u4e5f\u4e0d\u4f1a\u662f\u9075\u7eaa\u5b88\u6cd5\u7684\u89d2\u8272\u3002\u501a\u4ed7\u7740\u5973\u795e\u7684\u5e87\u62a4\uff0c\u7267\u5e08\u4e0e\u5deb\u5e08\u5e76\u5217\uff0c\u6210\u4e3a\u9634\u9b42\u57ce\u7684\u7edf\u6cbb\u9636\u5c42\uff0c\u4ed6\u4eec\u5f80\u5f80\u66f4\u8511\u89c6\u5f8b\u6cd5\uff0c\u66f4\u5bb9\u6613\u6ecb\u751f\u4e8b\u7aef\u3002</p><p>\u5982\u679c\u662f\u4e24\u4e2a\u7267\u5e08\u5f53\u8857\u51b3\u6597\uff0c\u52c9\u5f3a\u8fd8\u80fd\u8bf4\u5f97\u8fc7\u53bb\uff1b\u4f46\u8fd9\u6b21\u662f\u4e24\u4e2a\u5deb\u5e08\u2014\u2014\u8fd9\u5c31\u6709\u4e9b\u4e0d\u53ef\u601d\u8bae\u4e86\uff0c\u83ab\u975e\u8fd9\u4e24\u4e2a\u5deb\u5e08\u540c\u65f6\u8111\u5b50\u574f\u6389\u4e86\u4e48\u3002</p><p>\u4f46\u73b0\u5728\u601d\u8003\u8fd9\u4e9b\u5df2\u7ecf\u6ca1\u6709\u610f\u4e49\u3002</p><p>\u4eba\u5df2\u7ecf\u6b7b\u4e86\uff0c\u800c\u4f5c\u4e3a\u5e73\u6c11\u767e\u59d3\u5bb6\u7684\u4e24\u4e2a\u5b69\u5b50\uff0c\u743c\u6069\u548c\u73ca\u5609\u552f\u4e00\u6709\u8d44\u683c\uff0c\u4e5f\u6709\u80fd\u529b\u505a\u7684\u4e8b\u60c5\uff0c\u5c31\u662f\u5728\u90bb\u5c45\u7684\u5e2e\u52a9\u4e0b\uff0c\u4e3a\u7236\u6bcd\u6536\u5c38\u3001\u4e0b\u846c\uff0c\u7b49\u5f85\u4e0a\u9762\u5904\u7406\u7ed3\u679c\u3002</p><p>\u9634\u9b42\u57ce\u7684\u884c\u653f\u6548\u7387\u5f88\u9ad8\uff0c\u5f53\u5929\u4e0b\u5348\u6700\u7ec8\u7684\u5904\u7406\u7ed3\u679c\u5c31\u505a\u4e86\u51fa\u6765\uff0c\u5e76\u6ca1\u6709\u51fa\u4e4e\u5927\u5bb6\u610f\u6599\u3002\u5728\u7ed9\u6bcf\u4f4d\u6b7b\u8005\u5bb6\u5c5e\u8d54\u507f\u4e86\u4e00\u7b14\u94b1\u4e4b\u540e\uff0c\u4e24\u4f4d\u8087\u4e8b\u8005\u5c31\u900d\u9065\u6cd5\u5916\u4e86\u3002\u542c\u8bf4\u5deb\u5e08\u5185\u90e8\u4f1a\u6709\u4e00\u4e9b\u60e9\u7f5a\u63aa\u65bd\uff0c\u4f46\u603b\u4e4b\u80af\u5b9a\u6027\u547d\u65e0\u5fe7\u3002\u6bd5\u7adf\uff0c\u4ed6\u4eec\u662f\u5deb\u5e08\uff0c\u662f\u9ad8\u9636\u8005\uff0c\u800c\u6b7b\u8005\u90fd\u662f\u4f4e\u9636\u8005\u3002</p><p>\u9634\u9b42\u57ce\u662f\u4e2a\u7b49\u7ea7\u68ee\u4e25\u7684\u5730\u65b9\u3002</p><p>\u4e8e\u662f\uff0c\u5728\u8ddd\u79bb\u59d0\u5f1f\u4fe9\u7684\u4e03\u5c81\u751f\u65e5\u8fd8\u6709\u4e09\u5929\u7684\u65f6\u5019\uff0c\u4ed6\u4eec\u5931\u53bb\u4e86\u7236\u4eb2\u6bcd\u4eb2\uff0c\u6362\u6765\u7684\u662f\u4e00\u7b14\u94b1\u2014\u2014\u516b\u5341\u91d1\u5e01\u3002</p><p>\u59d0\u59d0\u73ca\u5609\u9762\u65e0\u8868\u60c5\u5730\u4ece\u4e00\u4f4d\u653f\u5e9c\u5b98\u5458\u624b\u4e2d\u63a5\u8fc7\u88c5\u6ee1\u91d1\u5e01\u7684\u53e3\u888b\uff0c\u5173\u4e0a\u4e86\u95e8\uff0c\u628a\u53e3\u888b\u653e\u8fdb\u4e00\u95f4\u67dc\u5b50\u91cc\uff0c\u52a0\u4e0a\u9501\u3002</p><p>\u201c\u62a5\u4ec7\u3002\u201d\u5979\u4f4e\u4f4e\u5730\u8bf4\uff0c\u7136\u540e\u653e\u58f0\u75db\u54ed\u3002</p><p>\u62a5\u4ec7\u3002</p><p>\u203b\u203b\u203b</p><p>\u73ca\u5609\u75c5\u5012\u4e86\u3002</p><p>\u5927\u7ea6\u662f\u56e0\u4e3a\u4f24\u5fc3\u8fc7\u5ea6\uff0c\u53c8\u52a0\u4e0a\u7740\u4e86\u51c9\uff0c\u5f53\u5929\u665a\u4e0a\uff0c\u5c0f\u5973\u5b69\u53d1\u8d77\u70e7\u6765\u3002</p><p>\u5979\u5148\u662f\u6655\u6655\u6c89\u6c89\uff0c\u8eba\u5728\u5e8a\u4e0a\u4f11\u606f\uff0c\u4f46\u5374\u7ffb\u6765\u8986\u53bb\uff0c\u4eff\u4f5b\u603b\u662f\u7761\u4e0d\u7740\u7684\u6837\u5b50\u3002\u743c\u6069\u5f00\u59cb\u672a\u66fe\u5728\u610f\uff0c\u6e10\u6e10\u53d1\u89c9\u4e0d\u5bf9\u52b2\uff0c\u8fde\u5fd9\u8fc7\u53bb\u4e00\u770b\uff0c\u89c1\u73ca\u5609\u7684\u8138\u86cb\u5df2\u7ecf\u70e7\u5f97\u901a\u7ea2\uff0c\u4f38\u624b\u4e00\u6478\uff0c\u989d\u5934\u53d1\u70eb\u5f97\u5389\u5bb3\u3002</p><p>\u201c\u53d1\u70e7\u4e86\u3002\u201d</p><p>\u743c\u6069\u7b2c\u4e00\u53cd\u5e94\u662f\u53bb\u795e\u6bbf\u627e\u7267\u5e08\u3002\u8fd9\u662f\u4e2a\u9b54\u6cd5\u4e16\u754c\uff0c\u9634\u9b42\u57ce\u91cc\u662f\u6ca1\u6709\u533b\u751f\u7684\uff0c\u4e00\u5207\u75c5\u75db\u90fd\u53ef\u4ee5\u7528\u9b54\u6cd5\u6765\u6cbb\u6108\uff0c\u6536\u8d39\u5012\u4e5f\u4e0d\u8d35\u3002\u6216\u8005\u53bb\u4e13\u95e8\u7684\u5546\u5e97\u91cc\u4e70\u6cbb\u7597\u836f\u6c34\uff0c\u4ef7\u683c\u4e5f\u8fd8\u633a\u4fbf\u5b9c\u3002</p><p>\u4f46\u4ed6\u968f\u5373\u53cd\u5e94\u8fc7\u6765\u3002\u9634\u9b42\u57ce\u665a\u4e0a\u662f\u5b9e\u884c\u5bb5\u7981\u7684\uff0c\u50cf\u4ed6\u8fd9\u79cd\u5e73\u6c11\u767e\u59d3\uff0c\u5929\u5927\u7684\u4e8b\u60c5\u4e5f\u4e0d\u51c6\u51fa\u95e8\uff0c\u5982\u679c\u4ed6\u8fd9\u65f6\u5019\u62b1\u7740\u73ca\u5609\u53bb\u795e\u6bbf\uff0c\u8def\u4e0a\u5341\u6709\u516b\u4e5d\u88ab\u536b\u5175\u76f4\u63a5\u5f53\u573a\u683c\u6740\u3002\u5c31\u7b97\u80fd\u5b89\u5168\u5230\u8fbe\u795e\u6bbf\u4e5f\u6ca1\u7528\uff0c\u838e\u5c14\u662f\u6697\u591c\u5973\u795e\uff0c\u5979\u7684\u7267\u5e08\u7d20\u6765\u90fd\u662f\u5728\u665a\u4e0a\u8fdb\u884c\u7948\u7977\uff0c\u4e3e\u884c\u5404\u79cd\u6559\u4f1a\u5185\u90e8\u4eea\u5f0f\uff0c\u6b64\u65f6\u662f\u4e0d\u4f1a\u63a5\u5f85\u75c5\u4eba\u7684\u3002</p><p>\u8be5\u6b7b\uff0c\u600e\u4e48\u529e\u3002</p><p>\u4ed6\u52aa\u529b\u5730\u56de\u5fc6\u7740\u524d\u4e16\u611f\u5192\u53d1\u70e7\u65f6\u7684\u60c5\u5f62\uff0c\u8bb0\u5f97\u597d\u50cf\u66fe\u542c\u8bf4\u8fc7\uff1a\u75c5\u4eba\u53d1\u70e7\u65f6\u9700\u8981\u8fdb\u884c\u964d\u6e29\uff0c\u65b9\u5f0f\u6709\u4e24\u79cd\uff0c\u4e00\u79cd\u5316\u5b66\u964d\u6e29\uff0c\u4fbf\u662f\u5403\u836f\u4e86\u2014\u2014\u4f46\u743c\u6069\u5bb6\u91cc\u73b0\u5728\u6ca1\u6709\uff1b\u53e6\u5916\u4e00\u79cd\u7269\u7406\u964d\u6e29\u65b9\u6cd5\uff0c\u5c31\u662f\u7528\u6bdb\u5dfe\u6d78\u900f\u51b7\u6c34\u6577\u5728\u989d\u5934\u4e0a\uff0c\u7528\u9152\u7cbe\u64e6\u62ed\u8eab\u4f53\uff0c\u4e5f\u53ef\u4ee5\u964d\u6e29\u3002</p><p>\u7236\u4eb2\u751f\u524d\u7231\u559d\u4e24\u53e3\uff0c\u5bb6\u91cc\u9152\u5012\u662f\u6709\u51e0\u74f6\u7684\u3002</p><p>\u743c\u6069\u6253\u6765\u4e00\u76c6\u51b7\u6c34\uff0c\u7528\u6bdb\u5dfe\u6d78\u900f\u4e86\uff0c\u6298\u53e0\u8d77\u6765\uff0c\u6577\u5728\u73ca\u5609\u989d\u5934\u4e0a\u3002\u968f\u5373\u4ece\u7236\u4eb2\u623f\u91cc\u62b1\u51fa\u4e24\u74f6\u9152\u6765\uff0c\u62e7\u5f00\u74f6\u76d6\uff0c\u5012\u5728\u53e6\u5916\u4e00\u5757\u6bdb\u5dfe\u4e0a\uff0c\u51c6\u5907\u7ed9\u73ca\u5609\u64e6\u62ed\u8eab\u4f53\u3002</p><p>\u65e0\u8bba\u4e0a\u8f88\u5b50\u8fd8\u662f\u8fd9\u8f88\u5b50\uff0c\u743c\u6069\u90fd\u6ca1\u4ec0\u4e48\u7167\u987e\u75c5\u4eba\u7684\u7ecf\u9a8c\uff0c\u53c8\u662f\u4e8b\u51fa\u7a81\u7136\uff0c\u4e0d\u514d\u6709\u4e9b\u624b\u5fd9\u811a\u4e71\u3002\u5df2\u7ecf\u5c06\u9152\u6d78\u900f\u6bdb\u5dfe\uff0c\u7136\u540e\u624d\u60f3\u8d77\u6765\uff0c\u8fd8\u6ca1\u628a\u73ca\u5609\u7684\u8863\u670d\u8131\u4e0b\u6765\u5462\u3002</p><p>\u5c06\u6bdb\u5dfe\u653e\u5230\u4e00\u8fb9\uff0c\u5f00\u59cb\u89e3\u5c0f\u5973\u5b69\u7684\u8863\u670d\u3002\u8fd9\u5de5\u4f5c\u5e76\u4e0d\u96be\uff0c\u4ed6\u4ee5\u524d\u53c8\u4e0d\u662f\u6ca1\u8131\u8fc7\u5973\u5b69\u5b50\u8863\u670d\uff0c\u73ca\u5609\u5df2\u7ecf\u53d1\u70e7\u5f97\u6655\u6655\u6c89\u6c89\uff0c\u6beb\u4e0d\u53cd\u6297\u3002\u5f88\u5feb\uff0c\u743c\u6069\u5c31\u6210\u529f\u5c06\u59d0\u59d0\u8131\u5149\u4e86\u3002</p><p>\u9634\u9b42\u57ce\u662f\u6ca1\u6709\u6708\u5149\u7684\u2014\u2014\u6708\u5973\u795e\u82cf\u4f26\u662f\u6697\u591c\u5973\u795e\u838e\u5c14\u7684\u6b7b\u654c\uff0c\u8def\u706f\u7684\u5fae\u5149\u4ece\u7a97\u53e3\u6084\u6084\u900f\u8fdb\u6765\uff0c\u7167\u5728\u73ca\u5609\u7684\u8eab\u4f53\u4e0a\uff0c\u5982\u540c\u6d01\u767d\u65e0\u6687\u7684\u8f6f\u7389\uff0c\u6563\u53d1\u51fa\u71a0\u71a0\u67d4\u5149\uff0c\u4ee4\u4eba\u4e0d\u80dc\u601c\u7231\u3002</p><p>\u7531\u4e8e\u5728\u53d1\u70e7\uff0c\u73ca\u5609\u7761\u5f97\u5e76\u4e0d\u5b89\u7a33\uff0c\u65f6\u5e38\u8f7b\u5fae\u5730\u632a\u52a8\u8eab\u4f53\u3002\u6bcf\u5f53\u5979\u7a0d\u5fae\u52a8\u4e0b\u8eab\u5b50\uff0c\u6e05\u51b7\u706f\u5149\u6295\u5c04\u7684\u90e8\u4f4d\u4fbf\u5fae\u5999\u5730\u6ed1\u884c\u5f00\u6765\uff0c\u904d\u5e03\u8eab\u4f53\u7684\u9634\u5f71\u4ea6\u968f\u4e4b\u53d8\u5f62\uff0c\u6070\u4f3c\u9759\u9759\u6e56\u9762\u4e0a\u8361\u6f3e\u5f00\u6765\u7684\u6c34\u7eb9\u4e00\u6837\u6539\u53d8\u7740\u5f62\u72b6\u3002</p><p>\u968f\u7740\u9634\u5f71\u7684\u53d8\u5e7b\uff0c\u743c\u6069\u7684\u773c\u5149\u7f13\u7f13\u4e0b\u79fb\uff0c\u770b\u89c1\u8170\u95f4\u6d41\u7545\u7684\u66f2\u7ebf\uff0c\u968f\u7740\u547c\u5438\u9759\u9759\u8d77\u4f0f\u7684\u5e73\u6ed1\u7684\u5c0f\u8179\uff0c\u773c\u5149\u518d\u5f80\u4e0b\uff0c\u7d27\u5e76\u7684\u53cc\u817f\u4e2d\u95f4\uff0c\u4eff\u4f5b\u6709\u4e00\u62b9\u7c89\u5ae9\u7684\u5ae3\u7ea2\u2026\u2026</p><p>\u201c\u771f\u6f02\u4eae\u3002\u201d\u4ed6\u5fcd\u4e0d\u4f4f\u8d5e\u53f9\u4e00\u58f0\u3002</p><p>\u4e03\u5c81\u7684\u5c0f\u5973\u5b69\uff0c\u8eab\u4f53\u5176\u5b9e\u8fd8\u57fa\u672c\u6ca1\u6709\u53d1\u80b2\uff0c\u867d\u7136\u6709\u79cd\u7a1a\u5ae9\u7684\u7f8e\u4e3d\uff0c\u5374\u5230\u5e95\u592a\u8fc7\u5355\u7eaf\uff0c\u4e0d\u591f\u751c\u871c\u8bf1\u4eba\uff0c\u743c\u6069\u4e0d\u662f\u841d\u8389\u63a7\uff0c\u4e0a\u8f88\u5b50\u5973\u4eba\u7684\u8eab\u4f53\u4e5f\u89c1\u5f97\u591a\u4e86\uff0c\u81ea\u7136\u4e0d\u4f1a\u771f\u8d77\u4ec0\u4e48\u6b32\u5ff5\u3002\u7a0d\u7a0d\u6b23\u8d4f\u4e86\u51e0\u79d2\u949f\uff0c\u4fbf\u62ff\u8d77\u6bdb\u5dfe\uff0c\u5f00\u59cb\u7f13\u7f13\u64e6\u62ed\u73ca\u5609\u7684\u8eab\u4f53\u3002</p><p>\u8fd9\u79cd\u964d\u6e29\u65b9\u6cd5\u4ed6\u4e5f\u53ea\u662f\u8033\u95fb\uff0c\u5e76\u672a\u5b9e\u8df5\u8fc7\uff0c\u521d\u65f6\u8fd8\u6709\u4e9b\u62c5\u5fc3\uff0c\u4e0d\u77e5\u6548\u679c\u5982\u4f55\u3002\u4f46\u4ed4\u4ed4\u7ec6\u7ec6\u5730\u64e6\u62ed\u4e86\u4e24\u904d\u540e\uff0c\u73ca\u5609\u6216\u8bb8\u662f\u611f\u89c9\u5230\u4e86\u6e05\u51c9\uff0c\u8eab\u4f53\u4e0d\u518d\u90a3\u4e48\u707c\u70ed\uff0c\u6e10\u6e10\u5b89\u9759\u4e0b\u6765\uff0c\u547c\u5438\u4e5f\u53d8\u5f97\u5e73\u7a33\u3002</p><p>\u743c\u6069\u89c1\u8fd9\u65b9\u6cd5\u6709\u6548\uff0c\u5fc3\u5934\u653e\u4e0b\u4e00\u5757\u5927\u77f3\uff0c\u8fde\u5fd9\u5c06\u9152\u90fd\u5012\u4e86\u51fa\u6765\uff0c\u4e00\u904d\u53c8\u4e00\u904d\u5730\u64e6\u62ed\u7740\u73ca\u5609\u7684\u8eab\u4f53\u3002\u4ed6\u5f88\u4ed4\u7ec6\uff0c\u6bcf\u4e00\u5bf8\u808c\u80a4\u90fd\u5c0f\u5fc3\u5730\u64e6\u62ed\u5230\uff0c\u5148\u64e6\u5b8c\u8116\u9888\u3001\u80a9\uff0c\u7136\u540e\u79fb\u5230\u76ee\u524d\u8fd8\u662f\u5e73\u5766\u7684\u80f8\u90e8\uff0c\u6545\u610f\u591a\u505c\u7559\u4e86\u4e00\u4f1a\uff0c\u60f3\u50cf\u6b64\u5904\u5c06\u6765\u4f1a\u5982\u4f55\u7684\u4e30\u8174\u633a\u62d4\uff0c\u7ec8\u4e8e\u604b\u604b\u4e0d\u820d\u5730\u5f80\u4e0b\uff0c\u79fb\u5230\u5c0f\u8179\u3002</p><p>\u64e6\u62ed\u5b8c\u5c0f\u8179\u540e\uff0c\u743c\u6069\u5c06\u73ca\u5609\u7684\u53cc\u817f\u8f7b\u8f7b\u5206\u5f00\uff0c\u7528\u6bdb\u5dfe\u8638\u7740\u9152\uff0c\u4ed4\u7ec6\u64e6\u62ed\u7740\u5927\u817f\u5185\u4fa7\u3002\u4ed6\u77e5\u9053\u8fd9\u91cc\u662f\u5973\u5b69\u5b50\u6700\u5a07\u5ae9\u654f\u611f\u7684\u5730\u65b9\uff0c\u53d7\u4e0d\u5f97\u9152\u7cbe\u523a\u6fc0\uff0c\u6240\u4ee5\u683c\u5916\u5c0f\u5fc3\u7ffc\u7ffc\u3002\u548c\u4ed6\u524d\u4e16\u770b\u8fc7\u7684\u6210\u5e74\u5973\u5b50\u8eab\u4f53\u4e0d\u540c\uff0c\u4e03\u5c81\u7684\u5c0f\u5973\u5b69\uff0c\u5c0f\u8179\u4e0b\u4f9d\u65e7\u662f\u5149\u6d01\u4e00\u7247\uff0c\u6beb\u65e0\u534a\u70b9\u7ed2\u6bdb\uff0c\u770b\u8d77\u6765\u7eaf\u51c0\u65e0\u6bd4\u3002</p><p>\u201c\u4e0d\u77e5\u9053\u957f\u5927\u540e\uff0c\u4f1a\u4e0d\u4f1a\u771f\u662f\u4e2a\u5c0f\u767d\u864e\u5462\uff1f\u201d\u4ed6\u4f4e\u7b11\u7740\u3002</p><p>\u53cd\u590d\u64e6\u62ed\u5168\u8eab\u51e0\u904d\u540e\uff0c\u73ca\u5609\u7684\u547c\u5438\u53d8\u5f97\u5e73\u7f13\u60a0\u957f\uff0c\u4e5f\u4e0d\u518d\u7ffb\u6765\u8986\u53bb\u4e86\u3002</p><p>\u743c\u6069\u957f\u957f\u677e\u4e86\u53e3\u6c14\uff0c\u4f38\u624b\u6478\u6478\u5979\u7684\u989d\u5934\uff0c\u4e5f\u4e0d\u518d\u6eda\u70eb\u4e86\u3002\u770b\u6765\uff0c\u8fd9\u4e00\u5173\u5ea6\u8fc7\u53bb\u4e86\u3002</p><p>\u4ed6\u5c06\u6bdb\u5dfe\u62e7\u5e72\uff0c\u667e\u8d77\uff0c\u628a\u9152\u74f6\u585e\u4e0a\u585e\u5b50\uff0c\u653e\u5230\u4e00\u65c1\uff0c\u5e2e\u73ca\u5609\u628a\u88ab\u5b50\u76d6\u4e0a\uff0c\u7136\u540e\u53bb\u540e\u9662\u628a\u76c6\u91cc\u7684\u6c34\u5012\u6389\u3002\u5f53\u4ed6\u5fd9\u5b8c\u8fd9\u4e00\u5207\u540e\uff0c\u8fd4\u56de\u623f\u95f4\uff0c\u73ca\u5609\u5df2\u7ecf\u6c89\u6c89\u7761\u7740\u3002</p><p>\u743c\u6069\u4e5f\u6709\u4e9b\u7d2f\u4e86\uff0c\u6b63\u51c6\u5907\u722c\u4e0a\u81ea\u5df1\u7684\u4e0a\u94fa\uff0c\u7a81\u7136\u542c\u89c1\u73ca\u5609\u4f3c\u4e4e\u5728\u53eb\u81ea\u5df1\u7684\u540d\u5b57\u3002</p><p>\u4ed6\u6014\u4e86\u4e00\u4e0b\uff0c\u8d34\u8fd1\u95ee\uff1a\u201c\u73ca\u5609\uff1f\u201d</p><p>\u201c\u51b7\u3002\u201d\u73ca\u5609\u542b\u542b\u7cca\u7cca\u5730\u8bf4\u3002</p><p>\u743c\u6069\u4e00\u60ca\uff0c\u4f38\u624b\u8fdb\u88ab\u5b50\uff0c\u6478\u6478\u73ca\u5609\u7684\u8eab\u4f53\uff0c\u53d1\u73b0\u679c\u7136\u4f53\u6e29\u5f88\u4f4e\u3002\u59d0\u5f1f\u4fe9\u5728\u56db\u4e94\u5c81\u4e4b\u524d\uff0c\u90fd\u662f\u7761\u4e00\u5f20\u5e8a\u7684\uff0c\u65f6\u5e38\u808c\u80a4\u76f8\u4eb2\uff0c\u6240\u4ee5\u4ed6\u77e5\u9053\u73ca\u5609\u7d20\u6765\u6709\u70b9\u4f53\u5bd2\uff0c\u8eab\u4f53\u6e29\u5ea6\u6bd4\u81ea\u5df1\u8981\u4f4e\u4e0a\u4e00\u4e9b\uff0c\u6309\u524d\u4e16\u8bb0\u5fc6\u91cc\u7684\u8bf4\u6cd5\uff0c\u6b63\u5e38\u4eba\u7684\u4f53\u6e29\u5e94\u8be5\u662f\u4e09\u5341\u4e03\u5ea6\u5de6\u53f3\uff0c\u73ca\u5609\u5374\u81f3\u591a\u53ea\u6709\u4e09\u5341\u516d\u5ea6\u3002</p><p>\u5982\u4eca\u5374\u6bd4\u5e73\u5e38\u66f4\u51b0\u51c9\u4e86\uff0c\u6307\u5c16\u90fd\u611f\u89c9\u5230\u4e86\u5fae\u5fae\u7684\u5bd2\u610f\u3002</p><p>\u7cdf\u7cd5\uff0c\u96be\u9053\u521a\u624d\u964d\u6e29\u964d\u5f97\u592a\u8fc7\u5934\u4e86\uff1f</p><p>\u743c\u6069\u6709\u4e9b\u4e0d\u77e5\u6240\u63aa\uff0c\u4f46\u73ca\u5609\u663e\u7136\u5f88\u51b7\uff0c\u5979\u7684\u8eab\u4f53\u51b0\u51c9\uff0c\u7d27\u7d27\u8737\u6210\u4e00\u56e2\uff0c\u62b1\u7740\u88ab\u5b50\uff0c\u7259\u9f7f\u5f00\u59cb\u8f7b\u5fae\u6253\u98a4\uff0c\u53d1\u51fa\u54af\u54af\u78b0\u649e\u58f0\uff0c\u5728\u9759\u8c27\u7684\u591c\u91cc\u542c\u8d77\u6765\u683c\u5916\u6e05\u695a\u3002</p><p>\u201c\u51b7\u3002\u201d\u5979\u542b\u7cca\u5730\u547b\u541f\u7740\u3002</p><p>\u5e7d\u5f71\u754c\u539f\u672c\u5c31\u9634\u51b7\uff0c\u8fd9\u4e2a\u4e16\u754c\u53c8\u6ca1\u6709\u4ec0\u4e48\u7535\u6696\u6c14\u4e4b\u7c7b\u7684\u4e1c\u897f\uff0c\u8fde\u592a\u9633\u90fd\u662f\u9b54\u6cd5\u521b\u9020\u7684\u4eff\u5236\u54c1\uff0c\u6ca1\u6709\u591a\u5c11\u70ed\u91cf\u3002\u9634\u9b42\u57ce\u5728\u5e7d\u5f71\u754c\u5446\u4e86\u4e00\u5343\u591a\u5e74\uff0c\u5c45\u6c11\u4ee3\u4ee3\u7e41\u884d\uff0c\u4e5f\u65e9\u5c31\u4e60\u60ef\u4e86\u8fd9\u7ec8\u5e74\u9634\u51b7\u7684\u5929\u6c14\uff0c\u5e76\u65e0\u4ec0\u4e48\u53d6\u6696\u8bbe\u5907\u3002</p><p>\u4e0d\u8fc7\uff0c\u8fd9\u573a\u666f\u4f3c\u4e4e\u5f88\u719f\u6089\u2026\u2026\u7a7f\u8d8a\u4e4b\u524d\uff0c\u5973\u53cb\u62b1\u6028\u51b7\u7684\u65f6\u5019\uff0c\u81ea\u5df1\u662f\u600e\u4e48\u8ba9\u5979\u53d6\u6696\u5462\u2026\u2026</p><p>\u597d\u5427\uff0c\u770b\u6765\u5fc5\u987b\u8ba9\u65e0\u6c61\u67d3\u65e0\u566a\u97f3\u65e2\u5b89\u5168\u53c8\u73af\u4fdd\u6e29\u5ea6\u6700\u9002\u5b9c\u800c\u4e14\u8fd8\u53ef\u4ee5\u62b1\u5728\u6000\u91cc\u7684\u4eba\u8089\u6696\u7089\u4e0a\u573a\u4e86\u3002</p><p>\u743c\u6069\u8131\u6389\u81ea\u5df1\u7684\u8863\u670d\uff0c\u5feb\u901f\u94bb\u8fdb\u88ab\u7a9d\u91cc\uff0c\u62b1\u7740\u73ca\u5609\u3002\u4ed6\u7684\u4f53\u6e29\u6bd4\u73ca\u5609\u9ad8\uff0c\u5c0f\u5973\u5b69\u6b63\u51b7\u5f97\u745f\u745f\u53d1\u6296\uff0c\u4e00\u611f\u89c9\u5230\u6e29\u6696\uff0c\u4fbf\u672c\u80fd\u5730\u6574\u4e2a\u4eba\u90fd\u504e\u8fc7\u6765\uff0c\u50cf\u516b\u722a\u7ae0\u9c7c\u4e00\u6837\u7d27\u7d27\u62b1\u7740\u743c\u6069\u3002\u5979\u8eab\u4f53\u6709\u4e00\u79cd\u5976\u9999\uff0c\u6df7\u5408\u7740\u521a\u624d\u64e6\u62ed\u8eab\u4f53\u7559\u4e0b\u7684\u9152\u6c14\uff0c\u4ee4\u4eba\u8ff7\u9189\u3002</p><p>\u8fd9\u786e\u5b9e\u662f\u4e2a\u5f88\u51b7\u7684\u591c\u665a\u3002</p><p>\u743c\u6069\u7b49\u81ea\u5df1\u7684\u624b\u53d8\u5f97\u6e29\u6696\u4e86\uff0c\u7136\u540e\u8f7b\u8f7b\u5728\u73ca\u5609\u80cc\u4e0a\u629a\u6478\uff0c\u6162\u6162\u5f80\u4e0b\uff0c\u629a\u8fc7\u540e\u8170\uff0c\u63c9\u634f\u7740\u4e24\u74e3\u5c0f\u5de7\u7684\u5706\u81c0\u3002\u201c\u624b\u611f\u5f88\u597d\uff0c\u201d\u4ed6\u5728\u73ca\u5609\u8033\u8fb9\u4f4e\u58f0\u8bf4\uff0c\u201c\u4f60\u662f\u6211\u7684\uff0c\u59d0\u59d0\u3002\u201d</p><p>\u867d\u7136\u4e00\u4e2a\u5fc3\u7406\u4e0a\u662f\u4e8c\u5341\u4f59\u5c81\u7684\u7537\u5b50\uff0c\u53eb\u4e00\u4e2a\u4e03\u5c81\u7684\u5c0f\u5973\u5b69\u505a\u59d0\u59d0\uff0c\u7740\u5b9e\u6709\u4e9b\u602a\u602a\u7684\u611f\u89c9\uff0c\u4f46\u743c\u6069\u5f88\u559c\u6b22\u3002</p><p>\u4ed6\u6e29\u67d4\u5730\u629a\u6478\u73ca\u5609\u5168\u8eab\uff0c\u7528\u6e29\u6696\u7684\u638c\u5fc3\u5e2e\u5979\u9a71\u8d70\u5bd2\u51b7\u3002\u5c0f\u5973\u5b69\u7684\u8eab\u4f53\u6e10\u6e10\u4e0d\u518d\u98a4\u6296\uff0c\u7259\u9f7f\u4e5f\u4e0d\u518d\u6253\u98a4\uff0c\u5979\u7d27\u7d27\u62b1\u7740\u743c\u6069\uff0c\u6795\u7740\u4ed6\u7684\u624b\u81c2\uff0c\u5c06\u8138\u57cb\u5728\u4ed6\u7684\u8116\u9888\u4e0a\uff0c\u547c\u6c14\u5982\u5170\uff0c\u5f04\u5f97\u743c\u6069\u8033\u8fb9\u4e00\u9635\u9635\u75d2\u75d2\u7684\u3002</p><p>\u8be5\u6b7b\uff0c\u4e0a\u8f88\u5b50\u81ea\u5df1\u7684\u8033\u6735\u5c31\u662f\u6700\u654f\u611f\u7684\u5730\u5e26\uff0c\u5e38\u5e38\u5728\u548c\u5973\u53cb\u8c03\u60c5\u65f6\u88ab\u5077\u88ad\uff0c\u600e\u4e48\u7a7f\u8d8a\u8fc7\u6765\uff0c\u6295\u80ce\u8f6c\u4e16\uff0c\u5df2\u7ecf\u6362\u4e86\u4e2a\u8eab\u4f53\uff0c\u4f9d\u7136\u8fd8\u662f\u5982\u6b64\u3002</p><p>\u4e0d\u8fc7\u6682\u65f6\u65e0\u6687\u8003\u8651\u8fd9\u4e9b\uff0c\u4e0d\u80fd\u4efb\u7531\u5c0f\u5973\u5b69\u8fd9\u6837\u6311\u9017\u4e0b\u53bb\uff0c\u5c3d\u7ba1\u5979\u662f\u65e0\u610f\u8bc6\u7684\u3002</p><p>\u743c\u6069\u5fae\u5fae\u7528\u529b\uff0c\u8ba9\u73ca\u5609\u79fb\u5f00\u4e00\u70b9\u3002\u6b64\u65f6\u5df2\u7ecf\u8fc7\u4e86\u665a\u4e0a\u4e5d\u70b9\uff0c\u8def\u706f\u81ea\u52a8\u8f6c\u6697\uff0c\u5feb\u8981\u7184\u706d\uff0c\u4e00\u7247\u9ed1\u6697\u4e4b\u4e2d\uff0c\u5c0f\u5973\u5b69\u9762\u5bb9\u6cdb\u7740\u5fae\u5149\uff0c\u7a1a\u6c14\u800c\u5b89\u8be6\uff0c\u5979\u7684\u776b\u6bdb\u5f88\u957f\uff0c\u5f2f\u5f2f\u7684\uff0c\u5c31\u50cf\u4ee5\u524d\u770b\u8fc7\u7684\u6d0b\u5a03\u5a03\u4f3c\u7684\uff0c\u9f3b\u6881\u79c0\u633a\uff0c\u5c0f\u5634\u975e\u5e38\u7cbe\u81f4\u3002</p><p>\u743c\u6069\u5fcd\u4e0d\u4f4f\u4f4e\u4e0b\u5934\uff0c\u8f7b\u8f7b\u543b\u5728\u73ca\u5609\u5634\u5507\u4e0a\u3002</p><p>\u6216\u8bb8\u662f\u56e0\u4e3a\u9ad8\u70e7\u7684\u7f18\u6545\uff0c\u73ca\u5609\u7684\u5634\u5507\u975e\u5e38\u5e72\u71e5\uff0c\u7565\u5e26\u4e9b\u7c97\u7cd9\u7684\u611f\u89c9\uff0c\u8fdc\u4e0d\u662f\u5e73\u65f6\u90a3\u822c\u5a07\u5ae9\u5982\u82b1\u7684\u6837\u5b50\uff0c\u663e\u7136\u662f\u5931\u6c34\u4e25\u91cd\u3002\u867d\u7136\u5982\u6b64\uff0c\u543b\u8d77\u6765\u5374\u5206\u5916\u6709\u4e00\u79cd\u7279\u522b\u7684\u89e6\u611f\uff0c\u4e5f\u5341\u5206\u8bf1\u4eba\u3002</p><p>\u743c\u6069\u7740\u8ff7\u5730\u543b\u4e86\u5f88\u4e45\uff0c\u7136\u540e\u604b\u604b\u4e0d\u820d\u5730\u5206\u5f00\uff0c\u201c\u8fd9\u662f\u6211\u4eec\u7684\u521d\u543b\uff0c\u201d\u4ed6\u4f4e\u58f0\u5bf9\u719f\u7761\u4e2d\u7684\u5c0f\u5973\u5b69\u8bf4\uff0c\u201c\u6211\u8981\u4f60\u505a\u6211\u7684\u59d0\u59d0\uff0c\u4e5f\u505a\u6211\u7684\u60c5\u4eba\u3002\u201d</p><p>\u73ca\u5609\u6ca1\u6709\u542c\u89c1\u3002</p><p>\u743c\u6069\u8d77\u8eab\u4e0b\u5e8a\uff0c\u5012\u4e86\u676f\u6c34\uff0c\u5148\u559d\u4e86\u4e00\u53e3\uff0c\u4e0d\u54bd\u4e0b\u53bb\uff0c\u542b\u5728\u53e3\u4e2d\uff0c\u7136\u540e\u4fef\u8eab\u543b\u5728\u73ca\u5609\u7684\u5634\u5507\u4e0a\u3002\u5c0f\u5973\u5b69\u6b63\u6709\u4e9b\u6e34\uff0c\u611f\u89c9\u5230\u6c34\u7684\u6e05\u51c9\u6ecb\u6da6\uff0c\u8ff7\u8ff7\u7cca\u7cca\u5730\u5f20\u5f00\u6a31\u5507\uff0c\u743c\u6069\u8d81\u673a\u7528\u820c\u5934\u9876\u5f00\u5979\u7684\u7259\u9f7f\uff0c\u50cf\u5c0f\u9c7c\u4e00\u6837\u6e38\u4e86\u8fdb\u53bb\uff0c\u7ea0\u7f20\u7740\u5979\u7684\u9999\u820c\uff0c\u6311\u9017\u7740\uff0c\u5c0f\u5973\u5b69\u7684\u53e3\u4e2d\u6709\u79cd\u82ac\u82b3\u7684\u5473\u9053\u3002</p><p>\u743c\u6069\u7f13\u7f13\u5c06\u542b\u7740\u7684\u6c34\u5ea6\u5165\u73ca\u5609\u53e3\u4e2d\uff0c\u73ca\u5609\u672c\u80fd\u5730\u541e\u54bd\u7740\uff0c\u5634\u5507\u6e10\u6e10\u6ecb\u6da6\u3002\u5979\u5c06\u743c\u6069\u62b1\u5f97\u66f4\u7d27\uff0c\u65e0\u610f\u8bc6\u5730\u56de\u5e94\u7740\u5f1f\u5f1f\u7684\u70ed\u543b\u3002</p><p>\u6e10\u6e10\u5730\uff0c\u4e24\u4eba\u90fd\u7761\u4e86\u8fc7\u53bb\u3002</p></article>"}}}]);
