(window.webpackJsonp=window.webpackJsonp||[]).push([[503],{526:function(p){p.exports={chapterTitle:null,title:"\u7b2c503\u7ae0 \u53bb\u65b0\u829c\u4f11\u5047",content:"<article><h2>\u7b2c503\u7ae0 \u53bb\u65b0\u829c\u4f11\u5047</h2><p>\u3000\u3000\u8fd9\u662f\u4e00\u4e2a\u60a0\u957f\u7684\u5047\u671f\uff0c\u81f3\u5c11\u79bb\u5f00\u6d77\u5dde\u65f6\uff0c\u5f20\u606a\u662f\u8fd9\u4e48\u60f3\u7684\uff0c\u4e5f\u9009\u62e9\u6700\u60a0\u95f2\u7684\u65b9\u5f0f\u6eaf\u6d41\u800c\u4e0a\u3002</p><p>\u3000\u3000\u5728\u6c5f\u8f6e\u4e0a\uff0c\u4e8c\u5341\u516d\u5c0f\u65f6\u7684\u65c5\u7a0b\uff0c\u7532\u677f\u4e0a\uff0c\u5439\u7740\u6e05\u51b7\u5fae\u5e26\u8165\u5473\u7684\u6c5f\u98ce\uff0c\u4e3a\u4e86\u4e0d\u7ed9\u4e09\u4e2a\u5c0f\u4e2b\u5934\u6559\u8bad\uff0c\u5f20\u606a\u4e0e\u5085\u4fca\u62bd\u70df\u8981\u8eb2\u5230\u8231\u8237\u697c\u68af\u80cc\u540e\u7684\u89d2\u843d\u91cc\uff0c\u60dc\u5bb9\u3001\u60dc\u7fbd\u3001\u82b7\u5f64\u94f6\u94c3\u4f3c\u7684\u7b11\u58f0\u8086\u610f\u6d12\u5230\u6c5f\u6c34\u91cc\uff0c\u674e\u660e\u745c\u3001\u665a\u6674\u8fd8\u6709\u665a\u6674\u7684\u79c1\u4eba\u52a9\u7406\u3001\u90a3\u4e2a\u60f3\u53bb\u7f8e\u56fd\u7559\u5b66\u5374\u6ca1\u6709\u8d70\u6210\u4e0d\u5f97\u4e0d\u6682\u65f6\u627e\u4e2a\u5de5\u4f5c\u517b\u6d3b\u81ea\u5df1\u7684\u5973\u5b69\u5f20\u5ead\u7ad9\u5728\u4e00\u65c1\u804a\u5929\uff0c\u4e5f\u8981\u968f\u65f6\u6ce8\u610f\u4e0d\u8ba9\u4e09\u4e2a\u4e2b\u5934\u79bb\u5f00\u5979\u4eec\u7684\u89c6\u91ce\uff0c\u5510\u5a67\u3001\u9648\u5983\u84c9\u3001\u675c\u98de\u3001\u76db\u590f\u5728\u8231\u5ba4\u91cc\u6253\u724c\uff0c\u5f20\u73ab\u8dea\u5728\u6c99\u53d1\u4e0a\u770b\u7740\u8237\u7a97\u5916\u7684\u5915\u9633\u2026\u2026</p><p>\u3000\u3000\u8981\u5c06\u8fd9\u7406\u89e3\u6210\u65c5\u6e38\u56e2\u4e5f\u53ef\u4ee5\uff0c\u8fd8\u6709\u9a6c\u6d77\u9f99\u4e0e\u4ed6\u521a\u4e0a\u521d\u4e2d\u7684\u5973\u513f\u4e5f\u662f\u65c5\u6e38\u56e2\u7684\u6210\u5458\u3002</p><p>\u3000\u30001\u670828\u65e5\uff0c\u5b59\u5c1a\u4e49\u3001\u6768\u5f8b\u9526\u7b49\u4eba\u8fd4\u56de\u9999\u6e2f\uff0c\u5bf9\u7231\u8fbe\u7535\u5b50\u789f\u673a\u4e1a\u52a1\u8d44\u4ea7\u7684\u5ba1\u8ba1\u4e0e\u8bc4\u4f30\u5de5\u4f5c\u4e5f\u5c31\u662f\u6682\u544a\u4e00\u6bb5\u843d\uff0c1978\u9152\u5427\u5f00\u59cb\u6625\u8282\u524d\u7684\u6b47\u4e1a\uff0c\u8bb8\u7ef4\u966a\u5979\u59d0\u8bb8\u601d\u4e00\u8d77\u524d\u5f80\u9999\u6e2f\uff0c\u8fd9\u4e2a\u6625\u8282\u5979\u4eec\u90fd\u8981\u5728\u9999\u6e2f\u6e21\u8fc7\u3002</p><p>\u3000\u3000\u5f20\u606a\u4ed6\u4eec\u4e00\u884c\u5148\u524d\u65b0\u829c\u4f4f\u51e0\u5929\uff0c\u7136\u540e\u5f20\u606a\u4e0e\u5510\u5a67\u3001\u665a\u6674\u3001\u82b7\u5f64\u53bb\u5317\u4eac\u966a\u5f90\u5b66\u5e73\u3001\u5468\u53d4\u60e0\u592b\u5987\u8fc7\u6625\u8282\uff0c\u5176\u4ed6\u4eba\u5219\u8fd4\u56de\u6d77\u5dde\u3002</p><p>\u3000\u300028\u65e5\u4e0a\u5348\u4ece\u6d77\u5dde\u767b\u8239\uff0c29\u65e5\u4e2d\u5348\u5728\u65b0\u829c\u4e0a\u5cb8\uff0c\u57ce\u5317\u7684\u522b\u5885\u6ca1\u6709\u592a\u591a\u7684\u5ba2\u623f\u5b89\u987f\u8fd9\u4e48\u591a\u4eba\uff0c\u90fd\u5b89\u987f\u5728\u4e16\u7eaa\u9526\u6e56\u9152\u5e97\u91cc\uff0c\u8bdd\u8bf4\u56de\u6765\uff0c\u5c0f\u53d4\u3001\u90b5\u81f3\u521a\u7275\u5934\uff0c\u62c9\u8bb8\u601d\u62ff\u4e39\u4e95\u5df7\u7269\u4e1a\u5165\u80a1\u7684\u5546\u4e1a\u5730\u4ea7\u5f00\u53d1\u516c\u53f8\u76f4\u63a5\u6ce8\u518c\u4e3a\u4e16\u7eaa\u9526\u6e56\u5730\u4ea7\u5f00\u53d1\u96c6\u56e2\uff0c\u662f\u80a1\u4efd\u6709\u9650\u5236\u6027\u8d28\uff0c\u4ee5\u90b5\u81f3\u521a\u624b\u91cc\u7684\u5546\u4e1a\u5730\u4ea7\u5f00\u53d1\u516c\u53f8\u7684\u539f\u73ed\u4eba\u9a6c\u4ee5\u53ca\u4ece\u5b8f\u8fdc\u5b9e\u4e1a\u62bd\u51fa\u4e9b\u4eba\u624b\u505a\u73ed\u5e95\uff0c\u5171\u6ce8\u5165\u8d44\u672c\u91d1\u4e00\u4e2a\u4ebf\u3002\u5f20\u606a\u5012\u4e5f\u6ca1\u6709\u592a\u5173\u5fc3\u8fc7\u90a3\u6863\u5b50\u4e8b\uff0c\u5374\u662f\u5230\u4e86\u65b0\u829c\uff0c\u624d\u60f3\u8d77\u90b5\u81f3\u521a\u8ddf\u5c0f\u53d4\u4ed6\u4eec\u8be5\u4e0d\u4f1a\u5c31\u5c06\u201c\u4e16\u7eaa\u9526\u6e56\u201d\u7684\u540d\u5b57\u5f7b\u5e95\u7684\u7ed9\u5360\u8fc7\u53bb\u4e86\uff1f</p><p>\u3000\u3000\u5f90\u5b66\u5e73\u79bb\u5f00\u4e1c\u6d77\uff0c\u674e\u8fdc\u6e56\u5f53\u4e0a\u7701\u59d4\u526f\u4e66\u8bb0\u3001\u4ee3\u7701\u957f\uff0c\u6682\u65f6\u5bf9\u65b0\u829c\u7684\u5b98\u573a\u5e76\u6ca1\u6709\u76f4\u63a5\u7684\u9707\u52a8\uff0c\u65b0\u829c\u539f\u5e02\u59d4\u526f\u4e66\u8bb0\u3001\u526f\u5e02\u957f\u7f57\u5f52\u6e90\u7ed9\u5224\u5904\u6b7b\u7f13\uff0c\u5e02\u59d4\u79d8\u4e66\u957f\u94b1\u6587\u8d35\u9876\u66ff\u5e38\u52a1\u526f\u5e02\u957f\u7684\u804c\u52a1\uff0c\u5f20\u77e5\u884c\u4f5c\u4e3a\u526f\u5e02\u957f\u53ea\u5206\u7ba1\u65c5\u6e38\u3001\u5de5\u4e1a\u3001\u4f01\u4e1a\u6539\u5236\u7b49\u5230\u5f88\u5c0f\u4e86\u4e00\u5757\u3002\u867d\u8bf4\u5206\u7ba1\u6539\u5236\u529e\uff0c\u4f46\u662f\u4eba\u5728\u65b0\u829c\uff0c\u6ca1\u6709\u4ec0\u4e48\u5f3a\u6709\u529b\u7684\u652f\u6491\uff0c\u4ed6\u53ea\u662f\u5c06\u4e91\u6c60\u9152\u4e1a\u6293\u5728\u624b\u91cc\uff0c\u65b0\u829c\u5176\u4ed6\u7684\u5e02\u5c5e\u4f01\u4e1a\uff0c\u90fd\u6309\u7167\u4e00\u822c\u6b65\u9aa4\u8fdb\u884c\uff0c\u4e0d\u518d\u50cf\u5728\u6d77\u5dde\u65f6\u5192\u8fdb\u3002</p><p>\u3000\u3000\u65b0\u829c\u4eca\u5e74\u53d1\u751f\u8bb8\u591a\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u539f\u5e02\u59d4\u526f\u4e66\u8bb0\u3001\u526f\u5e02\u957f\u3001\u6a2a\u884c\u65b0\u829c\u5b98\u573a\u6709\u5341\u5e74\u4e4b\u4e45\u7684\u7f57\u5f52\u6e90\u7ec8\u4e8e\u8eab\u9677\u7262\u7b3c\u3001\u7275\u51fa\u7684\u7a9d\u72af\u5dee\u4e0d\u591a\u5c06\u65b0\u829c\u58f0\u540d\u6700\u72fc\u85c9\u7684\u4e00\u4f19\u653f\u5e9c\u5b98\u5458\u53ca\u5546\u4eba\u90fd\u4e00\u7f51\u6253\u5c3d\uff0c\u7f57\u5f52\u6e90\u4e4b\u5b50\u7f57\u6587\u4e5f\u56e0\u4e3a\u53c2\u4e0e\u5e76\u7ec4\u7ec7\u8f6e\u5978\u5987\u5973\u7b49\u7f6a\u7ed9\u4ece\u91cd\u5224\u5904\u65e0\u671f\u5f92\u5211\uff0c\u8fd9\u90fd\u662f\u5927\u5feb\u4eba\u5fc3\u7684\u4e00\u4e9b\u4e8b\u60c5\uff0c\u4f7f\u5f97\u65b0\u829c\u7684\u793e\u4f1a\u98ce\u6c14\u4e0e\u6cbb\u5b89\u90fd\u4e3a\u4e4b\u6e05\u8083\uff0c\u5f20\u606a\u6b64\u65f6\u5230\u65b0\u829c\uff0c\u5c31\u4e0d\u4f1a\u62c5\u5fc3\u7ed9\u4e00\u7fa4\u793e\u4f1a\u6df7\u6df7\u56f4\u89c2\u4e86\uff0c\u5e02\u91cc\u7684\u7ecf\u6d4e\u6d3b\u52a8\u6062\u590d\u6b63\u5e38\u79e9\u5e8f\uff0c\u8fd9\u4e9b\u90fd\u7ed9\u5e02\u59d4\u4e66\u8bb0\u6c5f\u4e0a\u5143\u3001\u5e02\u957f\u5218\u7235\u4e24\u4eba\u5e26\u6765\u826f\u597d\u7684\u793e\u4f1a\u58f0\u8a89\u3002\u53ef\u4ee5\u8bf4\uff0c\u5c06\u7f57\u5f52\u6e90\u9001\u8fdb\u7262\u72f1\uff0c\u4ed6\u4eec\u4e24\u4eba\u662f\u6700\u5927\u7684\u5229\u76ca\u83b7\u5f97\u8005\uff0c\u5c31\u7b97\u5f90\u5b66\u5e73\u79bb\u5f00\u65b0\u829c\u4e4b\u540e\uff0c\u6709\u4eba\u5efa\u8bae\u8c03\u6574\u4e00\u4e0b\u5f20\u77e5\u884c\u5206\u7ba1\u7684\u5757\uff0c\u90fd\u7ed9\u4e24\u4eba\u9876\u4e86\u56de\u53bb\u3002\u91cd\u4fee\u57ce\u9632\u6c5f\u5824\u5230\u51ac\u5b63\u67af\u6c34\u671f\u5de5\u7a0b\u6700\u7d27\u8981\u7684\u65f6\u523b\uff0c\u8d44\u91d1\u7d27\u5f20\uff0c\u8fd8\u662f\u4e16\u7eaa\u9526\u6e56\u6697\u4e2d\u6350\u4e86\u4e00\u7b14\u4e94\u767e\u4e07\u7684\u6b3e\u5b50\uff0c\u6c5f\u4e0a\u5143\u81ea\u7136\u80fd\u660e\u767d\u8fd9\u7b14\u94b1\u662f\u8c01\u51fa\u7684\u3002\u5218\u7235\u8d77\u521d\u4e0d\u6653\u5f97\u5f20\u77e5\u884c\u7684\u5e95\u7ec6\uff0c\u82e5\u662f\u73b0\u5728\u8fd8\u4e0d\u77e5\uff0c\u90a3\u4ed6\u8fd9\u4e2a\u5e02\u957f\u505a\u5f97\u5c31\u592a\u65e0\u8c13\u4e86\u3002\u4e14\u4e0d\u8bf4\u5f20\u77e5\u884c\u672c\u4eba\u7684\u80fd\u529b\u4e0e\u4ee5\u5f80\u7684\u653f\u7ee9\uff0c\u4ec5\u4ed6\u80cc\u540e\u7ad9\u7740\u9526\u6e56\u8fd9\u4e00\u70b9\uff0c\u5c31\u4e0d\u662f\u65c1\u4eba\u80fd\u8f7b\u610f\u60f9\u5f97\u8d77\u4ed6\u7684\u3002</p><p>\u3000\u3000\u5f90\u5b66\u5e73\u867d\u7136\u79bb\u5f00\u4e1c\u6d77\u4e86\uff0c\u4f46\u662f\u5f90\u5b66\u5e73\u4e00\u7cfb\u7684\u5b98\u5458\u90fd\u76f8\u5f53\u7684\u7a33\u5065\uff0c\u91d1\u56fd\u6d77\u56de\u5230\u7701\u91cc\u5c65\u4efb\u7701\u653f\u6cd5\u59d4\u526f\u4e66\u8bb0\u3001\u7701\u516c\u5b89\u5385\u5385\u957f\u4e4b\u804c\uff0c\u8fc7\u51e0\u5e74\u8fdb\u7701\u5e38\u59d4\u51e0\u4e4e\u662f\u80af\u5b9a\u7684\u4e8b\u60c5\u3002\u53c8\u542c\u8bf4\u6d77\u5dde\u90a3\u8fb9\u5468\u5bcc\u660e\u4e3b\u52a8\u5411\u7701\u59d4\u63d0\u51fa\u9000\u5c45\u4e8c\u7ebf\u7684\u8bf7\u6c42\uff0c\u867d\u7136\u7701\u59d4\u4e0d\u53ef\u80fd\u8ba9\u5510\u5b66\u8c26\u4e66\u8bb0\u3001\u5e02\u957f\u4e00\u517c\u6311\uff0c\u4f46\u662f\u5510\u5b66\u8c26\u5728\u4ed6\u4eba\u751f\u4e94\u5341\u5c81\u4e4b\u95f4\u5f53\u4e0a\u5e02\u59d4\u4e66\u8bb0\u4e5f\u6210\u5b9a\u5c40\u3002</p><p>\u3000\u3000\u629b\u5f00\u8fd9\u4e9b\u4e0d\u8bba\uff0c\u4e91\u6c60\u9152\u4e1a\u91cd\u7ec4\u4e0e\u6539\u5236\u4e4b\u540e\u53d6\u5f97\u7684\u8fd9\u4e9b\u6210\u7ee9\uff0c\u8fd8\u662f\u9010\u6e10\u56fa\u5de9\u5f20\u77e5\u884c\u5728\u65b0\u829c\u7684\u5730\u4f4d\u3002</p><p>\u3000\u3000\u65b0\u829c\u7684\u5de5\u4e1a\u57fa\u7840\u8584\u5f31\uff0c\u4e91\u6c60\u9152\u4e1a\u5927\u6982\u662f\u65b0\u829c\u5e02\u91cc\u62fc\u62fc\u51d1\u51d1\u60df\u4e00\u8d44\u4ea7\u8fbe\u5c06\u8fd1\u4e24\u4e2a\u4ebf\u7684\u516c\u53f8\u3002\u8d44\u4ea7\u89c4\u6a21\u867d\u7136\u53ef\u89c2\uff0c\u4f46\u662f\u52a0\u8d77\u6765\u7684\u9500\u552e\u989d\u3001\u5229\u6da6\u4ee5\u53ca\u4e0a\u7f34\u7684\u5229\u7a0e\u5374\u975e\u5e38\u7684\u60e8\u6de1\uff0c\u8d1f\u503a\u8fd8\u91cd\u3002\u5f20\u77e5\u884c\u4eb2\u81ea\u64cd\u5200\u4e91\u6c60\u9152\u4e1a\u4e4b\u540e\uff0c\u6df1\u5165\u7684\u8fdb\u884c\u516c\u53f8\u7ba1\u7406\u6539\u9769\u2014\u2014\u5927\u5e45\u5ea6\u7684\u4eba\u4e8b\u8c03\u6574\u4ee5\u53ca\u4e25\u683c\u4eba\u4e8b\u5236\u5ea6\u7684\u5236\u5b9a\u867d\u7136\u60f9\u6765\u8bb8\u591a\u4e89\u8bae\uff0c\u4e00\u6765\u4e91\u6c60\u9152\u4e1a\u7684\u7ea7\u522b\u4e0d\u591f\u3001\u8303\u56f4\u4e0d\u5e7f\uff0c\u8fd9\u4e9b\u4e89\u8bae\u8fd8\u65e0\u6cd5\u7ed9\u8eab\u4e3a\u526f\u5e02\u957f\u7684\u5f20\u77e5\u884c\u589e\u52a0\u4ec0\u4e48\u963b\u529b\u2014\u2014\u9010\u6b65\u7f29\u5c0f\u4f4e\u6863\u767d\u9152\u7684\u4ea7\u80fd\u3001\u6539\u826f\u917f\u9020\u5de5\u827a\u751f\u4ea7\u4e2d\u9ad8\u6863\u767d\u9152\u3001\u5927\u529b\u63a8\u52a8\u5e02\u573a\u5316\u8fd0\u4f5c\uff0c\u867d\u7136\u4e91\u6c60\u9152\u4e1a\u603b\u7684\u4ea7\u80fd\u6ca1\u6709\u63d0\u9ad8\u592a\u591a\uff0c\u4f46\u662f\u9500\u552e\u989d\u5374\u5927\u5e45\u4e0a\u5347\uff0c\u5e02\u91cc\u539f\u5148\u4e0d\u6307\u671b\u4e91\u6c60\u9152\u4e1a\u80fd\u5f88\u5feb\u6709\u4ec0\u4e48\u6210\u6548\uff0c\u4ec5\u534a\u5e74\u7684\u65f6\u95f4\uff0c\u8fd8\u662f\u7ed9\u5e02\u91cc\u4e0a\u7f34\u4ec5\u4e24\u5343\u4e07\u7684\u5229\u7a0e\uff0c\u8ba9\u65b0\u829c\u4eca\u5e74\u6349\u895f\u89c1\u8098\u7684\u5730\u65b9\u8d22\u653f\u7a0d\u7f13\u4e86\u4e00\u53e3\u6c14\u3002</p><p>\u3000\u3000\u5e76\u975e\u6240\u6709\u7684\u56fd\u6709\u4f01\u4e1a\u638c\u95e8\u4eba\u90fd\u788c\u788c\u65e0\u4e3a\u3001\u90fd\u7518\u5fc3\u5f53\u86c0\u866b\uff1b\u5c31\u7b97\u60f3\u5f53\u86c0\u866b\uff0c\u4e5f\u8981\u6709\u4e1c\u897f\u53ef\u4ee5\u86c0\uff0c\u4e3b\u8981\u8fd8\u662f\u4f53\u5236\u7684\u50f5\u5316\uff0c\u4f7f\u5f97\u56fd\u6709\u4f01\u4e1a\u7684\u7ecf\u7406\u3001\u5382\u957f\u4e0d\u6562\u3001\u4e5f\u4e0d\u80fd\u8f7b\u4e3e\u5984\u52a8\uff0c\u4f01\u4e1a\u6ca1\u6709\u6d3b\u529b\u3001\u7ecf\u6d4e\u6548\u76ca\u65e5\u76ca\u6ed1\u5761\u3002</p><p>\u3000\u3000\u4e0d\u80fd\u5426\u8ba4\u699c\u6837\u7684\u4f5c\u7528\uff0c\u4e91\u6c60\u9152\u4e1a\u5c0f\u534a\u5e74\u6765\u53d6\u5f97\u7684\u6210\u7ee9\uff0c\u4e5f\u8ba9\u5e02\u91cc\u7684\u5176\u4ed6\u4e00\u4e9b\u4f01\u4e1a\u8ddf\u7740\u8822\u8822\u6b32\u52a8\uff0c\u5f20\u77e5\u884c\u8d1f\u8d23\u8fd9\u4e00\u5757\uff0c\u867d\u7136\u6ca1\u6709\u5927\u5f20\u65d7\u9f13\uff0c\u4f46\u662f\u8fd8\u662f\u6301\u9f13\u52b1\u7684\u6001\u5ea6\uff0c\u5148\u8ba9\u8fd9\u4e9b\u4f01\u4e1a\u5148\u6d3b\u8dc3\u8d77\u6765\uff0c\u4ee5\u540e\u6539\u5236\u8d77\u6765\uff0c\u4e5f\u4f1a\u51cf\u5c11\u963b\u529b\u3002</p><p>\u3000\u3000\u5f20\u606a\u5f53\u81ea\u5df1\u5230\u65b0\u829c\u6765\u662f\u4f11\u5047\u7684\uff0c\u5373\u4f7f\u4ed6\u7238\u6293\u7740\u4ed6\u8981\u8c08\u4e91\u6c60\u9152\u4e1a\u7684\u4e8b\u60c5\uff0c\u4ed6\u4e5f\u90fd\u5efa\u8bae\u5230\u5929\u4e91\u5c71\u4e0a\u4e00\u8fb9\u6ce1\u7740\u6e29\u6cc9\uff0c\u4e00\u8fb9\u804a\u5929\u3002</p><p>\u3000\u3000\u9ad8\u8038\u5165\u4e91\u7684\u5929\u4e91\u5c71\u6a2a\u5367\u5728\u65b0\u829c\u7684\u897f\u5317\uff0c\u4e1c\u5317\u65b9\u5411\u4e5f\u6709\u9ad8\u5ea6\u4e0d\u4e00\u7684\u5cad\u72b6\u5c71\u5730\uff0c\u5c06\u7edd\u5927\u591a\u6570\u4ece\u5317\u65b9\u5439\u6765\u7684\u5bd2\u6d41\u6321\u5728\u5916\u9762\uff0c\u4f7f\u5f97\u65b0\u829c\u5e02\u5185\u51ac\u5b63\u6c14\u5019\u6e29\u6da6\u5b9c\u4eba\uff0c\u4e0d\u8fc7\u5230\u5929\u4e91\u5c71\u4e0a\u5374\u53c8\u662f\u53e6\u5916\u4e00\u756a\u666f\u81f4\uff0c\u5f20\u606a\u4f4f\u8fdb\u9152\u5e97\uff0c\u5c31\u542c\u536b\u5170\u8bf4\u5c71\u5317\u5761\u5728\u4e0b\u96ea\uff0c\u60f3\u7740\u6ce1\u5728\u6e29\u6cc9\u91cc\uff0c\u770b\u7740\u5929\u7a7a\u96ea\u82b1\u98d8\u821e\u7684\u611f\u89c9\u771f\u662f\u4e0d\u9519\uff0c\u6240\u4ee5\u521a\u5230\u9152\u5e97\uff0c\u5927\u5bb6\u5c31\u51c6\u5907\u7740\u5750\u8f66\u4e0a\u5929\u4e91\u5c71\u6ce1\u6e29\u6cc9\u3002</p><p>\u3000\u3000\u5f20\u77e5\u884c\u8bf4\u9053\uff1a\u201c\u6211\u8fd9\u65f6\u5019\u8dd1\u53bb\u6ce1\u6e29\u6cc9\uff0c\u6210\u4ec0\u4e48\u6837\u5b50\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u6487\u6487\u5634\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u5c31\u662f\u4e91\u6c60\u9152\u4e1a\u53c2\u52a0\u5168\u56fd\u7cd6\u9152\u4f1a\u7684\u4e8b\u60c5\u5417\uff1f\u6211\u5bf9\u8fd9\u4e00\u5757\u4e5f\u4e0d\u719f\uff0c\u4f60\u4eec\u63ea\u4f4f\u6211\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u826f\u8a00\u53ef\u732e\u2026\u2026\u201d</p><p>\u3000\u3000\u8ba8\u4ef7\u8fd8\u4ef7\u7684\u7ed3\u679c\uff0c\u5f20\u77e5\u884c\u6700\u7ec8\u51b3\u5b9a\u5c06\u4e91\u6c60\u9152\u4e1a\u7684\u5c81\u672b\u8336\u4f1a\u8bdd\u642c\u5230\u4e16\u7eaa\u9526\u6e56\u9152\u5e97\u6765\uff0c\u5f20\u606a\u5c06\u4e0a\u5c71\u7684\u65f6\u95f4\u63a8\u5230\u8336\u8bdd\u4f1a\u7ed3\u675f\u4e4b\u540e\u3002</p><p>\u3000\u3000\u79e6\u971c\u534e\u3001\u5b54\u957f\u6cb3\u8fd8\u4e0d\u77e5\u9053\u5f20\u606a\u4eba\u5230\u65b0\u829c\u4e86\uff0c\u8fd8\u5f53\u4e00\u5411\u6263\u95e8\u7684\u5f20\u77e5\u884c\u7ec8\u4e8e\u4f53\u8c05\u5230\u4e0b\u9762\u4eba\u7684\u8f9b\u82e6\uff0c\u624d\u5c06\u516c\u53f8\u7684\u5c81\u672b\u8336\u8bdd\u4f1a\u4ece\u51b7\u51b0\u51b0\u7684\u516c\u53f8\u4f1a\u8bae\u5ba4\u79fb\u5230\u4e16\u7eaa\u9526\u6e56\u5927\u9152\u5e97\u91cc\uff0c\u5750\u8f66\u8fc7\u6765\uff0c\u6b63\u70ed\u60c5\u6d0b\u6ea2\u7684\u8ba8\u8bba\u665a\u4e0a\u51c6\u5907\u5728\u9152\u5e97\u91cc\u5403\u4ec0\u4e48\u597d\u3002</p><p>\u3000\u3000\u5c81\u672b\u8336\u8bdd\u4f1a\uff0c\u9664\u4e86\u4e91\u6c60\u9152\u4e1a\u7684\u7ba1\u7406\u5c42\uff0c\u8fd8\u9080\u8bf7\u5e02\u8f7b\u5de5\u5c40\u3001\u5e02\u7cd6\u9152\u534f\u4f1a\u4ee5\u53ca\u540c\u4e1a\u9152\u5382\u7684\u4e00\u4e9b\u4eba\uff0c\u7528\u610f\u4e5f\u662f\u603b\u7ed3\u4ee5\u5f80\u7684\u7ecf\u9a8c\uff0c\u4e3a\u4e91\u6c60\u9152\u4e1a\u65e5\u540e\u7684\u53d1\u5c55\u732e\u8ba1\u732e\u7b56\u3002</p><p>\u3000\u3000\u79e6\u971c\u534e\u3001\u5b54\u957f\u6cb3\u8d76\u5230\u4e16\u7eaa\u9526\u6e56\uff0c\u8c22\u665a\u6674\u3001\u5510\u5a67\u5979\u4eec\u6b63\u5750\u8f66\u79bb\u5f00\uff0c\u6881\u683c\u73cd\u8fd8\u7279\u610f\u5c06\u536b\u5a76\u2014\u2014\u536b\u5170\u5979\u5988\u62c9\u4e0a\uff1b\u5f20\u606a\u3001\u675c\u98de\u53ea\u6709\u53ef\u601c\u5df4\u5df4\u7684\u770b\u7740\u5979\u4eec\u5174\u9ad8\u91c7\u70c8\u7684\u4e0a\u5c71\u53bb\u3002</p><p>\u3000\u3000\u8c22\u665a\u775b\u3001\u5510\u5a67\u5979\u4eec\u5012\u662f\u4e0d\u4f1a\u4f53\u8c05\u5f20\u606a\u7684\u611f\u53d7\uff0c\u51ac\u5929\u6ce1\u6e29\u6cc9\u7684\u611f\u89c9\u786e\u5b9e\u5f88\u8bf1\u4eba\uff0c\u9664\u4e86\u6ce1\u6e29\u6cc9\uff0c\u5317\u5761\u8fd8\u6709\u4e00\u4e2a\u5c0f\u578b\u7684\u6ed1\u96ea\u573a\uff0c\u542c\u8d77\u6765\u4e5f\u76f8\u5f53\u7684\u8bf1\u4eba\u3002</p><p>\u3000\u3000\u5f20\u606a\u6700\u7ec8\u53ea\u80fd\u5c06\u675c\u98de\u7ed9\u62c9\u4e0b\u6765\u966a\u4ed6\uff0c\u53c2\u52a0\u4e91\u6c60\u9152\u4e1a\u7684\u5c81\u672b\u8336\u8bdd\u4f1a\u3002</p><p>\u3000\u3000\u5b54\u957f\u6cb3\u770b\u5230\u5f20\u606a\uff0c\u4e0b\u9894\u7684\u80e1\u5b50\u6e23\u90fd\u5174\u594b\u5f97\u8981\u7acb\u8d77\u6765\uff0c\u5c71\u4e1c\u6c49\u4eba\uff0c\u5f85\u4eba\u70ed\u60c5\uff0c\u63fd\u8fc7\u5f20\u606a\u7684\u80a9\u8180\uff1a\u201c\u606a\u5c11\u4ec0\u4e48\u65f6\u5019\u5230\u65b0\u829c\u7684\uff0c\u4e5f\u6ca1\u6709\u63d0\u524d\u62db\u547c\u4e00\u58f0\u2026\u2026\u201d\u4e91\u6c60\u9152\u4e1a\u7684\u7ba1\u7406\u5c42\u4e5f\u4e0d\u662f\u4e2a\u4e2a\u90fd\u77e5\u9053\u5f20\u606a\u662f\u526f\u5e02\u957f\u5f20\u77e5\u884c\u7684\u513f\u5b50\uff0c\u5e02\u8f7b\u5de5\u5c40\u4e0e\u7cd6\u9152\u534f\u4f1a\u7684\u4eba\u5219\u66f4\u4e0d\u6e05\u695a\uff0c\u5b54\u957f\u6cb3\u66ff\u4ed6\u4e00\u4e00\u4ecb\u7ecd\u3002</p><p>\u3000\u3000\u8fd9\u4e00\u756a\u4ecb\u7ecd\uff0c\u5404\u4eba\u77ac\u95f4\u521d\u9732\u51fa\u6765\u7684\u53cd\u5e94\u8868\u60c5\u5404\u5f02\uff0c\u60f3\u5fc5\u662f\u590f\u5929\u5728\u65b0\u829c\u60f9\u51fa\u7684\u662f\u975e\u592a\u6df1\u5165\u4eba\u5fc3\u4e86\uff1b\u4e0d\u8fc7\u8f6c\u77ac\u4e4b\u65f6\u90fd\u53d8\u5f97\u975e\u5e38\u7684\u70ed\u60c5\u3002\u5e02\u8f7b\u5de5\u5c40\u3001\u7cd6\u9152\u534f\u4f1a\u90fd\u662f\u5f20\u77e5\u884c\u5206\u7ba1\u7684\u5757\uff0c\u770b\u5230\u8859\u5185\uff0c\u8c01\u90fd\u8fd8\u771f\u4e0d\u80fd\u9a6c\u864e\u3002</p><p>\u3000\u3000\u5f20\u606a\u5012\u4e5f\u4e0d\u4ee5\u4e3a\u610f\uff0c\u4e91\u6c60\u9152\u4e1a\u8fd9\u8fb9\uff0c\u4ed6\u53ea\u8981\u4e0e\u603b\u7ecf\u7406\u79e6\u971c\u534e\u3001\u526f\u603b\u7ecf\u7406\u5b54\u957f\u6cb3\u4fdd\u6301\u63a5\u89e6\u5c31\u597d\uff0c\u5176\u4ed6\u4eba\uff0c\u4ed6\u81ea\u5df1\u4e5f\u90fd\u7231\u7406\u4e0d\u7406\u7684\uff0c\u54ea\u7ba1\u5f97\u4ed6\u4eec\u5bf9\u81ea\u5df1\u6709\u4ec0\u4e48\u60f3\u6cd5\uff1f</p><p>\u3000\u3000\u5f20\u606a\u7ed9\u4eba\u9ad8\u9a6c\u5927\u7684\u5b54\u957f\u6cb3\u63fd\u7740\u8116\u5b50\uff0c\u63a2\u51fa\u5934\u8ddf\u79e6\u971c\u534e\u6253\u62db\u547c\uff1a\u201c\u79e6\u603b\uff0c\u7b49\u8bdd\u8336\u4f1a\u7ed3\u675f\u8bf7\u4f60\u5230\u5929\u4e91\u5c71\u6ce1\u6e29\u6cc9\u53bb\u3002\u201d\u4ed6\u8fd8\u60e6\u8bb0\u7740\u96ea\u4e2d\u6ce1\u6e29\u6cc9\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u201c\u4e0d\u5728\u8fd9\u91cc\u5403\u665a\u996d\uff1f\u201d\u79e6\u971c\u534e\u95ee\u9053\uff0c\u8981\u662f\u665a\u4e0a\u5b89\u6392\u4e86\u9152\u5e2d\uff0c\u4ed6\u4e0e\u5b54\u957f\u6cb3\u5c31\u8d70\u4e0d\u5f00\u3002</p><p>\u3000\u3000\u201c\u6709\u5b89\u6392\u665a\u996d\u5417\uff1f\u201d\u5f20\u606a\u56de\u5934\u627e\u4ed6\u7238\uff0c\u6ca1\u770b\u5230\u4eba\uff0c\u62db\u624b\u5524\u7fdf\u4e39\u9752\u8fc7\u6765\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u7a7f\u7740\u6d0b\u7070\u8272\u5462\u5b50\u5916\u5957\uff0c\u5916\u5957\u4e0b\u9732\u51fa\u7a7f\u7740\u8089\u8272\u9ad8\u5f39\u4e1d\u7684\u88e4\u889c\u4e0e\u5c16\u6839\u6df1\u7d2b\u4eae\u9762\u7684\u9ad8\u6839\u978b\uff0c\u6b63\u7ad9\u5728\u4f1a\u573a\u91cc\u4eb2\u81ea\u5f20\u843d\u4e34\u65f6\u4f1a\u573a\u7684\u5e03\u7f6e\uff0c\u5a07\u8273\u98ce\u60c5\uff0c\u60f9\u5f97\u4eba\u9891\u9891\u5c06\u773c\u795e\u5f80\u5979\u8eab\u4e0a\u4e22\uff0c\u4e00\u65c1\u7684\u79e6\u88d5\u7434\u7a7f\u7740\u9152\u5e97\u96ea\u9752\u8272\u5236\u670d\u73b2\u73d1\u6709\u81f4\u3001\u5bb9\u8c8c\u6e05\u4e3d\uff0c\u4f46\u662f\u6c14\u8d28\u4e0a\u8981\u8f93\u7fdf\u4e39\u9752\u4e00\u622a\uff0c\u7fdf\u4e39\u9752\u662f\u90a3\u79cd\u4e00\u98a6\u4e00\u8e59\u4e4b\u95f4\uff0c\u6d51\u4eba\u90fd\u80fd\u6563\u53d1\u51fa\u9b45\u529b\u7684\u5973\u4eba\uff0c\u8fd9\u6837\u7684\u5973\u4eba\u5927\u6982\u5df2\u7ecf\u8d85\u8d8a\u4e00\u822c\u6f02\u4eae\u5973\u4eba\u7684\u8303\u7574\u5427\u3002</p><p>\u3000\u3000\u5728\u65b0\u829c\uff0c\u7fdf\u4e39\u9752\u4e5f\u662f\u4f20\u5947\u4eba\u7269\uff0c\u5b66\u751f\u65f6\u4ee3\u51fa\u6765\u966a\u9152\u8d5a\u751f\u6d3b\u8d39\uff0c\u9ad8\u4e2d\u6bd5\u4e1a\u7ed9\u62db\u5de5\u8fdb\u5e02\u59d4\u529e\uff0c\u5165\u7f16\u8f6c\u6b63\uff0c\u4e8c\u5341\u56db\u5c81\u5c31\u6210\u4e3a\u65b0\u829c\u6700\u5e74\u8f7b\u7684\u526f\u79d1\u7ea7\uff0c\u5f53\u4e0a\u5e02\u59d4\u62db\u5f85\u5904\u526f\u5904\u957f\u3001\u5e02\u653f\u5e9c\u62db\u5f85\u5bbe\u9986\u7ecf\u7406\uff0c\u7f57\u5f52\u6e90\u3001\u7f57\u6587\u4e00\u6848\uff0c\u5927\u5bb6\u90fd\u4ee5\u4e3a\u5979\u8981\u7ed9\u94b1\u6587\u8d35\u63a8\u51fa\u6765\u5f53\u66ff\u7f6a\u7f8a\uff0c\u516c\u804c\u7ed9\u634b\uff0c\u5374\u534e\u4e3d\u8f6c\u8eab\u5f53\u4e0a\u4e16\u7eaa\u9526\u6e56\u7684\u603b\u7ecf\u7406\uff0c\u4e8c\u5341\u516b\u5c81\u7684\u5973\u4eba\uff0c\u80fd\u505a\u5230\u8fd9\u4e00\u6b65\uff0c\u81ea\u7136\u662f\u4e0d\u7b80\u5355\u3002</p><p>\u3000\u3000\u867d\u7136\u5f88\u591a\u4eba\u90fd\u7b80\u5355\u7684\u5c06\u8fd9\u4e00\u5207\u4e0e\u5979\u8ff7\u4eba\u7684\u5916\u8c8c\u8054\u7cfb\u5728\u4e00\u8d77\uff0c\u8a00\u8bed\u95f4\u4e5f\u9887\u4e3a\u4e0d\u5c51\uff0c\u4e0d\u8fc7\u66f4\u591a\u7684\u4eba\u5374\u5f02\u6837\u7684\u8be7\u5f02\uff1a\u8fd9\u4e00\u6735\u5a07\u6ef4\u6ef4\u7684\u82b1\uff0c\u7adf\u7136\u8c01\u4e5f\u6458\u4e0d\u8d70\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u8d70\u8fc7\u6765\uff0c\u542c\u5f20\u606a\u95ee\u53ca\u665a\u4e0a\u9152\u5e2d\u5b89\u6392\u7684\u4e8b\u60c5\uff0c\u5979\u8bf4\u9053\uff1a\u201c\u5f20\u5e02\u957f\u8bf4\u770b\u4e0b\u5348\u8336\u8bdd\u4f1a\u8fdb\u884c\u7684\u60c5\u51b5\u51b3\u5b9a\uff0c\u603b\u4e0d\u80fd\u4e09\u70b9\u949f\u7ed3\u675f\u8336\u8bdd\u4f1a\u5927\u5bb6\u5728\u8fd9\u91cc\u5750\u7740\u5e72\u7b49\u4e09\u4e2a\u5c0f\u65f6\u5403\u665a\u996d\u5427\u2026\u2026\u201d</p><p>\u3000\u3000\u90a3\u4e9b\u671f\u5f85\u80fd\u635e\u4e00\u987f\u597d\u9152\u3001\u597d\u83dc\u7684\u8f7b\u5de5\u5c40\u3001\u7cd6\u9152\u534f\u4f1a\u7684\u4eba\u542c\u4e86\u7fdf\u4e39\u9752\u8fd9\u7565\u5e26\u8bbd\u523a\u7684\u8bdd\uff0c\u8138\u4e0a\u90fd\u6709\u4e9b\u5fae\u8fa3\uff0c\u4e5f\u5fcd\u4e0d\u4f4f\u6709\u4e9b\u5931\u671b\uff1a\u4ec0\u4e48\u597d\u5904\u90fd\u6ca1\u6709\uff0c\u8d76\u8fc7\u6765\u8d77\u4ec0\u4e48\u52b2\uff1f</p><p>\u3000\u3000\u79e6\u971c\u534e\u5012\u662f\u8001\u8fa3\u4eba\uff0c\u7b11\u7740\u95ee\u5b54\u957f\u6cb3\u8bf4\uff1a\u201c\u5f20\u5e02\u957f\u5429\u5490\u8ba9\u51c6\u5907\u4e86\u4e9b\u5c0f\u793c\u54c1\uff0c\u4f60\u51c6\u5907\u600e\u4e48\u6837\u4e86\uff1f\u201d\u8fd9\u4e48\u4e00\u8bf4\uff0c\u53c8\u5c06\u4e0e\u4f1a\u8005\u7684\u60c5\u7eea\u7ed9\u5e26\u52a8\u8d77\u6765\uff0c\u8fd9\u5e74\u5934\uff0c\u4e0d\u7ed9\u4eba\u5bb6\u4e9b\u4fbf\u5b9c\uff0c\u5c31\u662f\u4e0d\u8fd1\u4eba\u60c5\u3002\u5b89\u6392\u9152\u5e2d\u4e5f\u597d\uff0c\u4e0d\u5b89\u6392\u9152\u5e2d\u4e5f\u597d\uff0c\u90fd\u662f\u4e24\u53ef\u4e4b\u95f4\u7684\u4e8b\u60c5\uff0c\u79e6\u971c\u534e\u3001\u5b54\u957f\u6cb3\u5012\u4e5f\u5934\u75bc\u5403\u5403\u559d\u559d\u592a\u6d6a\u8d39\u65f6\u95f4\uff0c\u5173\u952e\u662f\u65b0\u829c\u559d\u9152\u7684\u98ce\u6c14\u8ba9\u4eba\u5934\u75bc\uff0c\u80fd\u4e0d\u559d\u8fd8\u4e0d\u559d\u7684\u597d\u3002</p><p>\u3000\u3000\u5f20\u77e5\u884c\u4e34\u65f6\u53bb\u5e02\u91cc\u6709\u4e8b\u60c5\uff0c\u8336\u8bdd\u4f1a\u8fdb\u884c\u5230\u534a\u9014\uff0c\u624d\u8d76\u56de\u6765\uff0c\u5bf9\u6b64\uff0c\u8c01\u4e5f\u65e0\u6cd5\u8bf4\u4ec0\u4e48\u3002</p><p>\u3000\u3000\u5f20\u77e5\u884c\u6765\u4e86\u4e4b\u540e\uff0c\u5c31\u7b80\u6d01\u660e\u4e86\u5c06\u8bdd\u9898\u8f6c\u5230\u5373\u5c06\u4e8e\u4e09\u6708\u4e2d\u65ec\u5728\u6210\u90fd\u53ec\u5f00\u7684\u5168\u56fd\u6625\u5b63\u7cd6\u9152\u4ea4\u6613\u4f1a\u4e0a\u6765\u3002</p><p>\u3000\u3000\u4e00\u5e74\u4e8c\u5ea6\u7684\u5168\u56fd\u7cd6\u9152\u4f1a\uff0c\u65e0\u8bba\u6d3b\u52a8\u89c4\u6a21\u3001\u6863\u6b21\u3001\u53c2\u4e0e\u4eba\u7fa4\u3001\u4ea4\u6613\u91cf\u3001\u5f71\u54cd\u529b\u7b49\u65b9\u9762\u800c\u8a00\uff0c\u5747\u53ef\u79f0\u5f97\u4e0a\u662f\u201c\u4e2d\u56fd\u7b2c\u4e00\u76db\u4f1a\u201d\uff0c\u5df2\u7ecf\u8fde\u7eed\u53ec\u5f00\u516d\u5341\u5c4a\u7684\u4ea4\u6613\u4f1a\u8fd1\u5e74\u5c24\u5176\u7684\u706b\u7206\uff0c\u4ea4\u6613\u4f1a\u671f\u95f4\u901a\u5e38\u80fd\u805a\u96c6\u5168\u56fd\u5404\u5730\u6570\u4e07\u5bb6\u7ecf\u9500\u5546\u4e0e\u5382\u5546\uff0c\u5168\u56fd\u7684\u5e02\u573a\u80fd\u4e00\u4e0b\u5b50\u90fd\u805a\u62e2\u5230\u8fd9\u4e2a\u70b9\u4e0a\u6765\u3002\u6bcf\u5e74\u7684\u4e3e\u529e\u57ce\u5e02\u90fd\u6210\u4e86\u4e07\u4f17\u77a9\u76ee\u7684\u7126\u70b9\uff0c\u4e3a\u4e3e\u529e\u57ce\u5e02\u5e26\u6765\u4e86\u5de8\u5927\u7684\u7ecf\u6d4e\u6548\u76ca\u548c\u793e\u4f1a\u6548\u76ca\uff0c\u5927\u5927\u63a8\u52a8\u4e86\u5f53\u5730\u65c5\u6e38\u3001\u670d\u52a1\u4e1a\u7684\u53d1\u5c55\uff0c\u4ece\u8425\u9500\u89d2\u5ea6\u800c\u8a00\uff0c\u7cd6\u9152\u4f1a\u6210\u4e86\u98df\u54c1\u5236\u9020\u4f01\u4e1a\u4ea7\u54c1\u8425\u9500\u6e20\u9053\u62db\u5546\u7684\u91cd\u8981\u201c\u6865\u5934\u5821\u201d\u3002</p><p>\u3000\u3000\u4ea7\u54c1\u80fd\u5728\u7cd6\u9152\u4f1a\u201c\u4e00\u70ae\u8d70\u7ea2\u201d\uff0c\u5c31\u610f\u5473\u7740\u4f01\u4e1a\u8fdb\u5165\u4e86\u5feb\u901f\u53d1\u5c55\u7684\u9ad8\u901f\u8def\uff0c\u81f3\u5c11\u7b49\u4e8e\u6210\u529f\u4e86\u4e00\u5927\u534a\uff0c\u7279\u522b\u662f\u5bf9\u4e8e\u4ece\u4e8b\u5feb\u901f\u6d88\u8d39\u54c1\u9886\u57df\u7684\u4e2d\u5c0f\u578b\u4f01\u4e1a\uff0c\u7cd6\u9152\u4f1a\u62db\u5546\u7684\u610f\u4e49\u5c31\u663e\u5f97\u5c24\u4e3a\u91cd\u8981\u3002</p><p>\u3000\u3000\u4f46\u662f\u7cd6\u9152\u4f1a\u81ea\u4e5d\u5341\u5e74\u4ee3\u521d\u6301\u7eed\u5347\u6e29\u4ee5\u6765\uff0c\u5df2\u7ecf\u51fa\u73b0\u8fc7\u5ea6\u6cdb\u6ee5\u7684\u503e\u5411\uff0c\u5f88\u591a\u4f01\u4e1a\u6bcf\u5e74\u90fd\u201c\u62bc\u5b9d\u201d\u7cd6\u9152\u4f1a\uff0c\u4fe8\u7136\u7cd6\u9152\u4f1a\u5df2\u7ecf\u6210\u4e86\u5546\u5bb6\u7684\u4e00\u4e2a\u201c\u516c\u5171\u8d4c\u573a\u201d\uff0c\u4e00\u8d4c\u5b9a\u8f93\u8d62\uff01\u66f4\u6709\u751a\u8005\u5b81\u53ef\u201c\u501f\u94b1\u201d\u4e5f\u8981\u6765\u201c\u8d4c\u4e00\u628a\u201d\uff0c\u9762\u5bf9\u5168\u56fd\u7684\u5ba2\u5546\uff0c\u5168\u56fd\u7684\u5e02\u573a\u4e00\u4e0b\u5b50\u805a\u62e2\u5230\u4e00\u4e2a\u7126\u70b9\uff0c\u7684\u786e\u662f\u5341\u5206\u96be\u5f97\u7684\u673a\u4f1a\uff0c\u5982\u679c\u89c4\u5212\u597d\u4e86\uff0c\u5927\u628a\u7684\u94f6\u5b50\u5c31\u8f7b\u677e\u5165\u5e10\u4e86\uff0c\u9664\u4e86\u53c2\u5c55\u7684\u5c11\u91cf\u8d39\u7528\uff0c\u4e00\u4e0b\u5b50\u5c31\u201c\u65e5\u8fdb\u6597\u91d1\u201d\u4e86\uff0c\u6709\u7684\u4f01\u4e1a\u96be\u514d\u201c\u627e\u4e0d\u5230\u5317\u201d\u4e86\uff0c\u4e8e\u662f\u7cd6\u9152\u4f1a\u4e5f\u6210\u5c31\u4e86\u8fd9\u4e9b\u201c\u673a\u4f1a\u4e3b\u4e49\u201d\u503e\u5411\u4e25\u91cd\u7684\u201c\u8d4c\u535a\u578b\u201d\u4f01\u4e1a\u3002</p><p>\u3000\u3000\u4e91\u6c60\u9152\u4e1a\u53c2\u52a0\u4e5d\u4e03\u5e74\u6625\u5b63\u7cd6\u9152\u4ea4\u6613\u4f1a\uff0c\u90a3\u662f\u4e00\u5b9a\u7684\uff0c\u800c\u4e14\u8fd9\u6b21\u8981\u9ad8\u8c03\u53c2\u52a0\uff0c\u4f46\u662f\u8981\u907f\u514d\u6ca6\u9677\u4e3a\u673a\u4f1a\u4e3b\u4e49\u503e\u5411\u7684\u8d4c\u535a\u578b\u4f01\u4e1a\uff0c\u5c31\u9700\u8981\u505a\u8bb8\u591a\u7ec6\u81f4\u7f1c\u5bc6\u7684\u5de5\u4f5c\u3002</p><p>\u3000\u3000\u5b54\u957f\u6cb3\u81ea\u4e0a\u6b21\u4e0e\u5f20\u606a\u89c1\u9762\uff0c\u4ed6\u4eba\u5c31\u4e00\u76f4\u5728\u534e\u5317\u3001\u4e1c\u5317\u5404\u57ce\u5e02\u4e4b\u95f4\u98a0\u7c38\u6d41\u79bb\u6253\u5e02\u573a\uff0c\u56de\u65b0\u829c\u7684\u65e5\u5b50\u7d2f\u52a0\u8d77\u6765\u90fd\u5c48\u6307\u53ef\u6570\uff0c\u4e3a\u4e91\u6c60\u9152\u4e1a\u65b0\u5f00\u53d1\u7684\u4e2d\u9ad8\u6863\u9152\u6253\u5f00\u5e02\u573a\u505a\u51fa\u5f88\u5927\u7684\u8d21\u732e\u3002</p><p>\u3000\u3000\u6625\u8282\u524d\u540e\u662f\u5404\u6d88\u8d39\u7c7b\u4ea7\u54c1\u9500\u552e\u7684\u65fa\u5b63\uff0c\u5b54\u957f\u6cb3\u539f\u672c\u6ca1\u6253\u7b97\u56de\u65b0\u829c\uff0c\u4f46\u662f\u53c2\u52a0\u7cd6\u9152\u4f1a\u662f\u4e91\u6c60\u9152\u4e1a\u7ec4\u5efa\u4ee5\u6765\u6700\u5927\u7684\u4e00\u6b21\u5e02\u573a\u6295\u5165\uff0c\u9700\u8981\u4ed6\u56de\u6765\u4eb2\u81ea\u5750\u9547\u3002</p><p>\u3000\u3000\u4eca\u5929\u7684\u8336\u8bdd\u4f1a\u81ea\u7136\u662f\u4e3a\u96c6\u601d\u5e7f\u76ca\uff0c\u7531\u4e8e\u6709\u9080\u8bf7\u540c\u4e1a\u9152\u5382\u7684\u4ee3\u8868\u53c2\u52a0\uff0c\u4e91\u6c60\u9152\u4e1a\u8fd9\u8fb9\u7684\u53d1\u8a00\u5c31\u4e0d\u662f\u5f88\u79ef\u6781\uff0c\u4e3b\u8981\u662f\u5f15\u5bfc\u522b\u4eba\u53d1\u8a00\uff0c\u5012\u662f\u5e02\u8f7b\u5de5\u5c40\u3001\u7cd6\u9152\u534f\u4f1a\u7684\u4eba\u60f3\u5728\u5f20\u77e5\u884c\u9762\u524d\u8868\u73b0\uff0c\u53d1\u8a00\u5f88\u8e0a\u8dc3\uff0c\u542c\u5f97\u5f20\u606a\u8981\u62fc\u547d\u6291\u5236\u778c\u7761\u52b2\u624d\u4e0d\u80fd\u5f53\u4f17\u6253\u54c8\u6b20\uff1a\u6628\u5929\u591c\u91cc\u5728\u6c5f\u8f6e\u4e0a\u7ed9\u5510\u5a67\u5979\u4eec\u62c9\u53bb\u6253\u724c\u7761\u89c9\u592a\u665a\u4e86\uff1b\u4e0d\u8fc7\u4e5f\u7c4d\u6b64\u4e86\u89e3\u57fa\u672c\u7684\u60c5\u51b5\u3002</p><p>\u3000\u3000\u8336\u8bdd\u4f1a\u4e09\u70b9\u534a\u949f\u7ed3\u675f\uff0c\u4e0e\u4f1a\u4eba\u5458\u62ff\u7740\u4e91\u6c60\u9152\u4e1a\u51c6\u5907\u7684\u793c\u54c1\u5404\u81ea\u79bb\u5f00\uff0c\u81ea\u7136\u6ca1\u597d\u610f\u601d\u7559\u4e0b\u6765\u8e6d\u987f\u597d\u9152\u597d\u83dc\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u7740\u65f6\u95f4\uff0c\u8bf4\u9053\uff1a\u201c\u8981\u4e0a\u5c71\u5c31\u8d81\u65e9\uff0c\u4eca\u5929\u5929\u9634\uff0c\u5929\u9ed1\u5f97\u5feb\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u77e5\u884c\u72b9\u8c6b\u7740\uff0c\u5929\u5c71\u4e91\u4e0a\u7684\u5c71\u8def\u662f\u6cbf\u7740\u96e8\u6c34\u9053\u4fee\u7684\uff0c\u6ca1\u6709\u4e34\u5d16\u7684\u9669\u8def\uff0c\u4f46\u662f\u57fa\u7840\u8bbe\u65bd\u5f88\u5dee\uff0c\u8fd8\u662f\u7802\u77f3\u8def\uff0c\u53c8\u6ca1\u6709\u8def\u706f\uff0c\u867d\u7136\u79bb\u5e02\u533a\u4e0d\u5230\u56db\u5341\u516c\u91cc\uff0c\u4f46\u662f\u591c\u91cc\u4e0b\u5c71\u8fd8\u662f\u5f88\u9ebb\u70e6\u3002\u8fd9\u65f6\u95f4\u4e0a\u5c71\uff0c\u6700\u65e9\u660e\u5929\u4e2d\u5348\u624d\u80fd\u56de\u6765\uff0c\u5e74\u5173\u5e02\u91cc\u7684\u4e8b\u60c5\u591a\uff0c\u4e0b\u5348\u4ee5\u53ca\u660e\u5929\u4e0a\u5348\u7684\u65f6\u95f4\u5c31\u803d\u6401\u4e86\u3002</p><p>\u3000\u3000\u201c\u4e0d\u4f1a\u645e\u6311\u5b50\u7684\u9886\u5bfc\uff0c\u5c31\u4e0d\u662f\u5408\u9002\u7684\u9886\u5bfc\uff0c\u201d\u5f20\u606a\u6293\u4f4f\u4ed6\u7238\u7684\u8863\u895f\u8981\u5c06\u4ed6\u62c9\u8fdb\u8f66\u91cc\u6765\uff0c\u201c\u70e6\u5fc3\u4e8b\u662f\u64cd\u4e0d\u5b8c\u7684\uff0c\u4f60\u7ba1\u90a3\u4e48\u591a\u505a\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u5f20\u77e5\u884c\u5012\u4e5f\u6765\u4e0d\u53ca\u72b9\u8c6b\uff0c\u5c31\u7ed9\u534a\u62d6\u7740\u4e0a\u4e86\u8f66\uff0c\u60f3\u60f3\u7a0d\u7a0d\u653e\u677e\u4e00\u4e0b\uff0c\u4e5f\u6ca1\u4ec0\u4e48\u4e0d\u5f53\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5929\u4e91\u5c71\u4e0a\u7684\u6e29\u6cc9\u5bbe\u9986\uff0c\u4e5f\u662f\u4e16\u7eaa\u9910\u996e\u5a31\u4e50\u96c6\u56e2\u2014\u2014\u5f20\u606a\u6000\u7591\u4f1a\u5f88\u5feb\u66f4\u540d\u4e3a\u4e16\u7eaa\u9526\u6e56\u9910\u996e\u5a31\u4e50\u96c6\u56e2\u2014\u2014\u4e0e\u658c\u8001\u5927\u5218\u658c\u7684\u5927\u5174\u5de5\u8d38\u8054\u5408\u6295\u8d44\u7684\u4ea7\u4e1a\uff0c\u65b0\u829c\u7684\u65c5\u6e38\u4e1a\u867d\u7136\u8fd8\u6ca1\u6709\u62c9\u52a8\u8d77\u6765\uff0c\u4f46\u662f\u5230\u5929\u4e91\u5c71\u4e0a\u6e38\u73a9\u4f4f\u5bbf\uff0c\u6e29\u6cc9\u5bbe\u9986\u65e0\u7591\u662f\u6700\u4f73\u7684\u53ef\u80fd\u662f\u5f88\u591a\u4eba\u60df\u4e00\u7684\u9009\u62e9\uff1b\u6e29\u6cc9\u5bbe\u9986\u7684\u751f\u610f\u5012\u4e5f\u4e0d\u574f\u3002</p><p>\u3000\u3000\u65b0\u829c\u7684\u65c5\u6e38\u4e1a\u8981\u771f\u6b63\u62c9\u52a8\u8d77\u6765\uff0c\u8fd8\u8981\u57ce\u5e02\u3001\u666f\u533a\u7684\u57fa\u7840\u8bbe\u65bd\u5efa\u8bbe\u8ddf\u4e0a\u53bb\u624d\u884c\uff0c\u9664\u6709\u79c0\u4e3d\u7684\u98ce\u666f\u3001\u5b9c\u4eba\u7684\u6c14\u5019\u4e4b\u5916\uff0c\u8fd8\u8981\u6709\u5403\u3001\u6709\u559d\u3001\u6709\u73a9\u3001\u6709\u770b\u3001\u6709\u4e70\uff0c\u4ea4\u901a\u8fd8\u9700\u8981\u4fbf\u5229\u3002</p><p>\u3000\u3000\u65b0\u829c\u504f\u4e8e\u4e1c\u6d77\u4e00\u9685\uff0c\u65b0\u829c\u673a\u573a\u8fd8\u53ea\u662f\u5c0f\u578b\u7684\u519b\u7528\u673a\u573a\uff0c\u9ad8\u901f\u516c\u8def\u6ca1\u6709\u4e00\u5bf8\uff0c\u56fd\u9053\u9664\u6b64\u4e0e\u7701\u57ce\u5efa\u90ba\u76f8\u63a5\u4e4b\u5916\uff0c\u5c31\u53ea\u4e0e\u897f\u5317\u8fb9\u7ecf\u6d4e\u53d1\u5c55\u66f4\u52a0\u843d\u540e\u7684\u5730\u533a\u76f8\u901a\uff0c\u94c1\u8def\u4e5f\u53ea\u662f\u652f\u7ebf\uff0c\u5c0f\u6c5f\u822a\u9053\u5ba2\u8fd0\u4e5f\u5c06\u7531\u822a\u7a0b\u65f6\u95f4\u8fc7\u957f\u7ed9\u9010\u6e10\u6dd8\u6c70\u51fa\u5e02\u573a\u3002</p><p>\u3000\u3000\u8fd9\u4e9b\u90fd\u662f\u65b0\u829c\u4e0d\u4ec5\u65c5\u6e38\u7ecf\u6d4e\u8fd8\u6709\u5de5\u4e1a\u53d1\u5c55\u7684\u74f6\u9888\uff0c\u8fd9\u4e9b\u74f6\u9888\u9760\u65b0\u829c\u81ea\u8eab\u52aa\u529b\u5f88\u96be\u7a81\u7834\uff0c\u8fd8\u9700\u8981\u7701\u91cc\u3001\u56fd\u5bb6\u7684\u653f\u7b56\u503e\u659c\u3002\u5f90\u5b66\u5e73\u5e74\u524d\u4ece\u65e5\u672c\u62c9\u6765\u90a3\u4e48\u591a\u653f\u7b56\u6027\u8d37\u6b3e\uff0c\u5176\u4e2d\u5c31\u5305\u62ec\u4ece\u5efa\u90ba\u5230\u65b0\u829c\u7684\u9ad8\u901f\u8def\u9879\u76ee\uff0c\u65b0\u829c\u81f3\u5c11\u53ef\u4ee5\u63d0\u9ad8\u4e09\u5e74\u901a\u4e0a\u9ad8\u901f\uff0c\u800c\u8fd9\u4e09\u5e74\u6070\u6070\u662f\u56fd\u5185\u65c5\u6e38\u7ecf\u6d4e\u9ad8\u901f\u53d1\u5c55\u7684\u4e09\u5e74\uff1b\u9ec4\u91d1\u5468\u5047\u65e5\u7ecf\u6d4e\u62c9\u52a8\u65c5\u6e38\u7ecf\u6d4e\u662f\u4ece\u4e5d\u4e5d\u5e74\u6b63\u5f0f\u542f\u52a8\u4e86\u3002</p><p>\u3000\u3000\u5c71\u4e0b\u94c5\u4e91\u4f4e\u91cd\u3001\u770b\u4f3c\u8981\u4e0b\u96e8\uff0c\u6d77\u62e8\u4e0a\u4e86\u56db\u767e\u7c73\uff0c\u5929\u6c14\u5c31\u8f6c\u4e3a\u6674\u4e86\uff0c\u80fd\u611f\u89c9\u5230\u811a\u4e0b\u7684\u4e91\u5c42\u98d8\u51fa\u96e8\u4e1d\u6765\uff0c\u4e0a\u5c71\u4e4b\u524d\u901a\u7535\u8bdd\u65f6\uff0c\u542c\u8bf4\u5c71\u5317\u5761\u7adf\u7136\u8fd8\u5728\u98d8\u96ea\uff0c\u5c71\u810a\u4e24\u8fb9\u7684\u6e29\u5ea6\u76f8\u5dee\u5341\u5ea6\u4ee5\u4e0a\uff0c\u5012\u771f\u89c9\u5f97\u65b0\u829c\u7684\u5730\u7406\u73af\u5883\u4e0d\u7528\u505a\u65c5\u6e38\uff0c\u771f\u662f\u66b4\u6b84\u5929\u7269\u4e86\u3002</p><p>\u3000\u3000\u5728\u5c71\u4e0b\u6ca1\u6709\u803d\u6401\uff0c\u8d76\u5230\u5c71\u4e0a\u7684\u6e29\u6cc9\u65c5\u9986\u624d\u56db\u70b9\u534a\uff0c\u665a\u6674\u3001\u5510\u5a67\u4e0d\u89c1\u8e2a\u5f71\uff0c\u542c\u8bf4\u4e00\u8fc7\u6765\u5c31\u53bb\u5317\u5761\u7684\u6ed1\u96ea\u573a\u4e86\uff0c\u624b\u673a\u4fe1\u53f7\u8fd8\u6ca1\u6709\u8986\u76d6\u5230\u5c71\u4e0a\u6765\uff0c\u4e5f\u65e0\u6cd5\u8054\u7cfb\u5979\u4eec\uff0c\u53ea\u80fd\u7b49\u5979\u4eec\u73a9\u591f\u4e86\u81ea\u5df1\u56de\u6765\uff0c\u4ed6\u4eec\u5148\u53bb\u6ce1\u6e29\u6cc9\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u8d76\u53bb\u5317\u5761\u7684\u96ea\u573a\uff0c\u4e0d\u7528\u591a\u4e45\u5929\u5c31\u8981\u9ed1\u4e0b\u6765\uff0c\u5f20\u606a\u4ed6\u4eec\u53ea\u6709\u5148\u53bb\u6ce1\u6e29\u6cc9\uff0c\u53cd\u6b63\u79e6\u971c\u534e\u3001\u5b54\u957f\u6cb3\u4e5f\u8feb\u4e0d\u53ca\u5f85\u7684\u7ee7\u7eed\u8c08\u521a\u624d\u8336\u8bdd\u4f1a\u4e0a\u6ca1\u6709\u653e\u5f00\u8c08\u7684\u8bdd\u9898\u3002</p></article>"}}}]);
