(window.webpackJsonp=window.webpackJsonp||[]).push([[505],{528:function(p){p.exports={chapterTitle:null,title:"\u7b2c366\u7ae0 \u6606\u817e\u5728\u7ebf",content:"<article><h2>\u7b2c366\u7ae0 \u6606\u817e\u5728\u7ebf</h2><p>\u3000\u3000\u501f\u7740\u53c2\u52a0\u6881\u519b\u8ba2\u5a5a\u5bb4\u7684\u673a\u4f1a\uff0c\u6881\u6587\u6c5f\u5230\u6d77\u5dde\u627e\u4e01\u6625\u5efa\u6765\u78b0\u8fd0\u6c14\uff0c\u5e0c\u671b\u80fd\u7ed9\u4ed6\u4eec\u51e0\u4e2a\u6e05\u534e\u540c\u7a97\u5efa\u7acb\u7684\u6606\u817e\u5728\u7ebf\u7f51\u7ad9\u62c9\u4e9b\u8d5e\u52a9\u3002</p><p>\u3000\u3000\u56fd\u5185\u95e8\u6237\u7f51\u7ad9\u7684\u76c8\u5229\u9884\u671f\u8981\u5ef6\u8fdf\u5230\u96f6\u4e09\u3001\u96f6\u56db\u5e74\uff0c\u4e4b\u524d\u6700\u91cd\u8981\u7684\u5546\u4e1a\u6295\u8d44\u673a\u4f1a\u5c31\u662f\u5229\u7528\u7b2c\u4e00\u6b21\u7f51\u7edc\u6d6a\u6f6e\u5c06\u7f51\u7ad9\u653e\u5230\u9999\u6e2f\u6216\u7f8e\u56fd\u7b49\u5730\u7684\u8bc1\u5238\u5e02\u573a\u4e0a\u5e02\u5957\u73b0\u3002\u6309\u7167\u8fd9\u79cd\u6a21\u5f0f\uff0c\u5f20\u606a\u771f\u6709\u5fc3\u4ecb\u5165\u4e92\u8054\u7f51\u4ea7\u4e1a\uff0c\u5e94\u8be5\u5230\u4e5d\u516b\u5e74\u524d\u540e\u8054\u5408\u5176\u4ed6\u6295\u8d44\u673a\u6784\u627e\u4e00\u5bb6\u76f8\u5bf9\u6210\u719f\u7684\u7efc\u5408\u6027\u95e8\u6237\u7f51\u7ad9\u8fdb\u884c\u6295\u8d44\uff0c\u7136\u540e\u5305\u88c5\u4e0a\u5e02\uff0c\u5728\u96f6\u4e00\u5e74\u5957\u73b0\u64a4\u51fa\uff0c\u800c\u4e0d\u662f\u5728\u4e5d\u516d\u5e74\u5c31\u4ecb\u5165\u4e00\u5bb6\u8fd8\u53ea\u80fd\u79f0\u4e3a\u96cf\u5f62\u7684\u3001\u529f\u80fd\u5355\u4e00\u72ed\u7a84\u7684\u4e2d\u6587\u7f51\u7ad9\u3002</p><p>\u3000\u3000\u7531\u4e8e\u6881\u6587\u6c5f\u7684\u5173\u7cfb\uff0c\u5f20\u606a\u5012\u613f\u610f\u5148\u671f\u4ee5\u79c1\u4eba\u7684\u540d\u4e49\u6295\u767e\u5341\u4e07\u8fdb\u53bb\u3002\u4e0d\u8fc7\u5728\u6881\u6587\u6c5f\u770b\u6765\uff0c\u7531\u4e8e\u5efa\u7acb\u7f51\u7ad9\u662f\u4ed6\u4eec\u51e0\u4e2a\u6e05\u534e\u540c\u7a97\u7684\u5174\u8da3\uff0c\u4ed6\u5e0c\u671b\u80fd\u62c9\u5230\u516c\u53f8\u7684\u8d5e\u52a9\uff0c\u800c\u4e0d\u5e0c\u671b\u7ed9\u5f20\u606a\u9020\u6210\u4ec0\u4e48\u79c1\u4eba\u4e0a\u7684\u8d1f\u62c5\uff1b\u4ed6\u8fd8\u4e0d\u6e05\u695a\u5f20\u606a\u6b64\u65f6\u7684\u8eab\u5bb6\uff0c\u6709\u5fc3\u62d2\u7edd\u5f20\u606a\u7684\u597d\u610f\u3002</p><p>\u3000\u3000\u5f20\u606a\u8ddf\u4ed6\u8bf4\uff0c\u8981\u4ee5\u7231\u8fbe\u7535\u5b50\u7684\u540d\u4e49\u8d5e\u52a9\u53ef\u4ee5\uff0c\u4f46\u662f\u5728\u5546\u8a00\u5546\uff0c\u9996\u5148\u8981\u770b\u5230\u7231\u8fbe\u7535\u5b50\u5728\u8fd9\u6b21\u8d5e\u52a9\u4e2d\u7684\u5229\u76ca\u624d\u884c\u3002</p><p>\u3000\u3000\u6881\u6587\u6c5f\u867d\u7136\u662f\u6606\u817e\u5728\u7ebf\u7684\u4e3b\u521b\u4eba\u4e4b\u4e00\uff0c\u4f46\u662f\u4ed6\u4e0e\u4e01\u6625\u5efa\u4e00\u6837\uff0c\u5728\u6280\u672f\u4e0a\u9762\u6709\u7740\u4e13\u957f\uff0c\u5bf9\u7f51\u7ad9\u7684\u8fd0\u8425\u8fd8\u6ca1\u6709\u4ec0\u4e48\u7ecf\u9a8c\uff0c\u53ea\u662f\u5174\u8da3\u6240\u81f4\u624d\u4e0e\u4eba\u4e00\u8d77\u5efa\u7acb\u4e86\u8fd9\u4e48\u4e00\u4e2a\u7f51\u7ad9\uff0c\u5bf9\u7f51\u7ad9\u672a\u6765\u7684\u76c8\u5229\u6a21\u5f0f\u5374\u6ca1\u6709\u592a\u591a\u7684\u601d\u8003\u3002</p><p>\u3000\u3000\u81f3\u4e8e\u7231\u8fbe\u7535\u5b50\u80fd\u5728\u8fd9\u6b21\u8d5e\u52a9\u4e2d\u83b7\u5f97\u4ec0\u4e48\u5229\u76ca\uff0c\u6881\u6587\u6c5f\u8fd8\u56de\u7b54\u4e0d\u51fa\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u8ba9\u4e01\u69d0\u4ee5\u516c\u53f8\u7684\u540d\u4e49\u9080\u8bf7\u6606\u817e\u5728\u7ebf\u7684\u51e0\u4e2a\u5b66\u751f\u4e3b\u521b\u4eba\uff0c\u6881\u6587\u6c5f\u7684\u6e05\u534e\u540c\u7a97\u5230\u516c\u53f8\uff0c\u5373\u4f7f\u5728\u7f51\u7ad9\u5efa\u8bbe\u7684\u4e8b\u60c5\u8c08\u4e0d\u51fa\u4ec0\u4e48\u7ed3\u679c\u6765\uff0c\u8fd9\u6837\u7684\u63a5\u89e6\u4e5f\u6709\u5229\u4e8e\u5438\u5f15\u8fd9\u4e9b\u6280\u672f\u4eba\u624d\u5230\u7231\u8fbe\u6765\u5de5\u4f5c\uff0c\u6bd5\u7adf\u6d77\u5dde\u4e0d\u662f\u4e00\u6d41\u7684\u5927\u57ce\u5e02\uff0c\u8981\u5438\u5f15\u8fd9\u4e2a\u884c\u4e1a\u91cc\u7684\u9876\u5c16\u4eba\u624d\uff0c\u9664\u4e86\u4f18\u6e25\u7684\u85aa\u8d44\u4e4b\u5916\uff0c\u8fd8\u8981\u52a8\u4e9b\u5c0f\u5fc3\u601d\u3002</p><p>\u3000\u3000\u6881\u519b\u4e0e\u5b59\u4e3d\u7684\u8ba2\u5a5a\u5bb4\u4e4b\u540e\uff0c\u5c0f\u8205\u5bb6\u5176\u4ed6\u4eba\u90fd\u5148\u56de\u53bb\u4e86\uff0c\u6881\u6587\u6c5f\u6682\u65f6\u5148\u56de\u5b66\u6821\u3002</p><p>\u3000\u3000\u5f20\u606a\u73b0\u5728\u5fc5\u8981\u5c06\u5fc3\u601d\u653e\u5728\u4f1a\u8003\u4e0a\u9762\uff0c\u4e00\u76f4\u5230\u516d\u6708\u5341\u516b\u65e5\uff0c\u6881\u6587\u6c5f\u4e0e\u6606\u817e\u5728\u7ebf\u7684\u5b66\u751f\u4e3b\u521b\u4eba\u5458\uff0c\u624d\u518d\u6b21\u4e58\u706b\u8f66\u62b5\u8fbe\u6d77\u5dde\uff0c\u8fd9\u662f\u4e00\u4e2a\u8ba9\u4ed6\u611f\u89c9\u5230\u4e0d\u601d\u8bae\u7684\u57ce\u5e02\uff0c\u5173\u952e\u5728\u8fd9\u5ea7\u57ce\u5e02\u91cc\u6709\u4ed6\u89c9\u5f97\u4e0d\u53ef\u601d\u8bae\u7684\u8868\u5f1f\u3002</p><p>\u3000\u3000\u4ed6\u90fd\u4e0d\u77e5\u9053\u600e\u4e48\u8ddf\u5176\u4ed6\u4eba\u89e3\u91ca\u8fd9\u4e00\u5207\uff0c\u4fbf\u7d22\u6027\u542b\u7cca\u7684\u89e3\u91ca\u8bf4\u4ed6\u5c0f\u59d1\u592b\u5bb6\u5728\u7231\u8fbe\u7535\u5b50\u6709\u6295\u8d44\uff0c\u5bf9\u4e92\u8054\u7f51\u8fd9\u79cd\u65b0\u9c9c\u4e8b\u7269\u4e5f\u5f88\u611f\u5174\u8da3\uff0c\u5176\u4ed6\u7684\u5012\u6ca1\u6709\u591a\u8bf4\u3002</p><p>\u3000\u3000\u6d77\u5dde\u662f\u4e5d\u4e8c\u5e74\u624d\u901a\u706b\u8f66\u7684\uff0c\u5efa\u7ad9\u7684\u65f6\u95f4\u867d\u7136\u4e0d\u65e9\uff0c\u4f46\u662f\u89c4\u5212\u4e0a\u53ea\u662f\u89c4\u6a21\u4e0d\u5927\u7684\u505c\u9760\u7ad9\uff0c\u5efa\u8bbe\u65f6\uff0c\u5730\u65b9\u80fd\u8865\u8d34\u7684\u7ecf\u8d39\u4e5f\u76f8\u5f53\u6709\u9650\uff0c\u800c\u4e14\u5efa\u8bbe\u5728\u6bd4\u8f83\u8352\u51c9\u7684\u5317\u90ca\uff0c\u8bf4\u53e5\u5b9e\u8bdd\uff0c\u6881\u6587\u6c5f\u7b2c\u4e00\u6b21\u51fa\u8f66\u7ad9\u65f6\uff0c\u770b\u7740\u72ed\u5c0f\u8f66\u7ad9\u5916\u7684\u8352\u51c9\uff0c\u90fd\u5fcd\u4e0d\u4f4f\u6709\u4e9b\u5931\u671b\u7684\u3002</p><p>\u3000\u3000\u73b0\u5728\u9a6c\u5411\u4e1c\u4ed6\u4eec\u51e0\u4e2a\u4eba\uff0c\u4e0e\u6881\u6587\u6c5f\u7b2c\u4e00\u6b21\u5230\u6d77\u5dde\u7684\u611f\u89e6\u5f88\u7c7b\u4f3c\u7684\uff0c\u7ad9\u5728\u6708\u53f0\u4e0a\uff0c\u770b\u7740\u6708\u53f0\u4e0b\u7684\u519c\u7530\uff0c\u90fd\u6000\u7591\u8fd9\u91cc\u4f1a\u770b\u597d\u4e92\u8054\u7f51\u4ea7\u4e1a\u524d\u666f\u7684\u6295\u8d44\u4eba\uff0c\u6bd5\u7adf\u4e92\u8054\u7f51\u5728\u5317\u4eac\u3001\u4e0a\u6d77\u8fd9\u4e9b\u5927\u90fd\u5e02\u90fd\u662f\u5f88\u65f6\u9ae6\u7684\u4e8b\u7269\uff0c\u9a6c\u5411\u4e1c\u5f00\u73a9\u7b11\u8bf4\uff1a\u201c\u4f60\u5c0f\u59d1\u592b\u5c31\u662f\u4f20\u8bf4\u4e2d\u7684\u90a3\u79cd\u571f\u8d22\u4e3b\uff1f\u539f\u5148\u5bf9\u7231\u8fbe\u7535\u5b50\u8fd8\u6709\u4e9b\u671f\u5f85\uff0c\u771f\u6000\u7591\u8fd9\u6837\u7684\u5730\u65b9\u80fd\u51fa\u591a\u4e48\u4f18\u79c0\u7684\u4f01\u4e1a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8fd9\u91cc\u662f\u8352\u51c9\u4e9b\uff0c\u5e02\u91cc\u5012\u4e5f\u4e0d\u592a\u5dee\u2026\u2026\u201d\u6881\u6587\u6c5f\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u201c\u6881\u6587\u6c5f\u3001\u9a6c\u5411\u4e1c\u2026\u2026\u201d</p><p>\u3000\u3000\u6881\u6587\u6c5f\u8f6c\u8fc7\u5934\uff0c\u770b\u89c1\u5f20\u606a\u4e0e\u4e01\u6625\u5efa\u5c31\u7ad9\u5728\u4e0d\u8fdc\u5904\u7684\u51fa\u7ad9\u53e3\uff0c\u4e01\u6625\u5efa\u62db\u624b\u8ba9\u4ed6\u4eec\u8fc7\u53bb\uff0c\u5728\u4ed6\u4eec\u8eab\u8fb9\uff0c\u8fd8\u6709\u4e24\u4e2a\u4eba\uff0c\u4ed6\u5728\u6d77\u5dde\u65f6\uff0c\u8fd8\u6ca1\u6709\u673a\u4f1a\u8ba4\u8bc6\u5085\u4fca\u4e0e\u675c\u98de\u3002</p><p>\u3000\u3000\u6d77\u5dde\u4e00\u4e2d\u4e3a\u4e86\u8ba9\u5b66\u751f\u91cd\u89c6\u8d77\u4f1a\u8003\uff0c\u7279\u610f\u5c06\u671f\u672b\u8003\u8bd5\u62d6\u5230\u4e0b\u5b66\u671f\u5f00\u5b66\u521d\u8fdb\u884c\uff0c\u6ca1\u6709\u671f\u672b\u8003\u8bd5\u7684\u8d1f\u62c5\uff0c\u675c\u98de\u8fd9\u4e9b\u5929\u4fbf\u6574\u65e5\u4e0e\u5f20\u606a\u53ae\u6df7\u5728\u4e00\u8d77\u3002</p><p>\u3000\u3000\u6881\u6587\u6c5f\u540c\u5b66\u773c\u6674\u91cc\u5bf9\u6d77\u5dde\u7684\u5931\u671b\uff0c\u5f20\u606a\u591a\u5c11\u80fd\u770b\u51fa\u4e9b\u6765\uff0c\u6487\u5634\u7b11\u4e86\u7b11\uff0c\u7b49\u7740\u4ed6\u4eec\u8d70\u8fc7\u6765\uff0c\u4e00\u76f4\u8d70\u51fa\u7ad9\u53f0\uff0c\u7ad9\u53f0\u5916\u66f4\u52a0\u8352\u51c9\uff0c\u8fd9\u91cc\u79bb\u5e02\u4e2d\u5fc3\u8fd8\u6709\u56db\u516c\u91cc\uff0c\u9664\u4e86\u7b14\u76f4\u5bbd\u655e\u7684\u5317\u5173\u5927\u9053\uff0c\u51e0\u4e4e\u770b\u4e0d\u5230\u5e26\u6709\u57ce\u5e02\u7279\u5f81\u7684\u5efa\u7b51\u3002\u6d77\u5dde\u5e02\u4e2d\u5fc3\u7684\u8303\u56f4\u53c8\u5c0f\uff0c\u52a0\u4e0a\u8fd9\u4e24\u5e74\u5411\u4e1c\u3001\u5411\u5357\u53d1\u5c55\uff0c\u5317\u9762\u5317\u5173\u533a\u7684\u53d1\u5c55\u66f4\u662f\u6ede\u540e\u3002</p><p>\u3000\u3000\u5f20\u606a\u7ad9\u5728\u53f0\u9636\u4e0a\u7b11\u7740\u8bf4\uff1a\u201c\u4e61\u4e0b\u5730\u65b9\uff0c\u4f60\u4eec\u521a\u624d\u4f1a\u4e0d\u4f1a\u8ba8\u8bba\u7231\u8fbe\u7535\u5b50\u53ea\u662f\u51e0\u4e2a\u571f\u8d22\u4e3b\u6363\u9f13\u8d77\u6765\u7684\uff1f\u201d</p><p>\u3000\u3000\u6881\u6587\u6c5f\u5475\u5475\u4e00\u7b11\uff0c\u4ecb\u7ecd\u5434\u6b63\u519b\u950b\u7b49\u7ed9\u5f20\u606a\u8ba4\u8bc6\u3002</p><p>\u3000\u3000\u6606\u817e\u5728\u7ebf\u7684\u51e0\u4e2a\u4e3b\u521b\u529e\u4eba\uff0c\u5c31\u9a6c\u5411\u4e1c\u4e0e\u6881\u6587\u6c5f\u662f\u672c\u79d1\u751f\uff0c\u5176\u4ed6\u4e09\u4e2a\u90fd\u662f\u5728\u6821\u7684\u7814\u7a76\u751f\uff0c\u53d1\u8d77\u4eba\u4fbf\u662f\u4eca\u5e74\u5c06\u4ece\u6e05\u534e\u6bd5\u4e1a\u7684\u9a6c\u5411\u4e1c\uff0c\u662f\u4ed6\u5728\u5927\u4e09\u65f6\u5c06\u6881\u6587\u6c5f\u4ed6\u4eec\u7ec4\u7ec7\u8d77\u6765\u521b\u7acb\u6606\u817e\u5728\u7ebf\u3002\u6bd4\u8f83\u6881\u6587\u6c5f\u6765\uff0c\u9a6c\u5411\u4e1c\u6709\u7740\u51fa\u8272\u7684\u7ec4\u7ec7\u80fd\u529b\uff0c\u8fd9\u4e00\u70b9\uff0c\u4e01\u6625\u5efa\u4e5f\u8d5e\u4e0d\u7edd\u53e3\uff0c\u4ed6\u5f53\u521d\u5e2e\u4ed6\u4eec\u7684\u5fd9\uff0c\u4e5f\u662f\u62b5\u4e0d\u4f4f\u9a6c\u5411\u4e1c\u5634\u91cc\u4e09\u5bf8\u4e4b\u83b2\u7684\u9f13\u52a8\u3002</p><p>\u3000\u3000\u4e4b\u524d\uff0c\u6606\u817e\u5728\u7ebf\u53ea\u662f\u504f\u5411\u9ad8\u6821\u4f53\u80b2\u7ade\u4f53\u7231\u597d\u8005\u805a\u96c6\u7684BBS\u7f51\u7ad9\uff0c\u4eca\u5e74\u4e09\u6708\u521d\uff0c\u96c5\u864e\u5728\u7eb3\u65af\u8fbe\u514b\u6b63\u5f0f\u4e0a\u5e02\uff0c\u7b2c\u4e00\u5929\u5c31\u521b\u9020\u534e\u5c14\u8857\u7684\u4e00\u6b21\u4f20\u5947\uff0c\u72c2\u70ed\u7684\u4ea4\u6613\uff0c\u4f7f\u5f97\u5e73\u5747\u6bcf\u5c0f\u65f6\u7684\u8f6c\u624b\u8fbe\u516d\u6b21\u4e4b\u591a\u3002\u90a3\u4e00\u5929\u96c5\u864e\u7684\u5e02\u503c\u98da\u5347\u81f3\u516b\u4ebf\u591a\u7f8e\u5143\u3002\u96c5\u864e\u7684\u6210\u529f\uff0c\u6781\u5927\u7684\u9f13\u821e\u4e86\u56fd\u5185\u4e92\u8054\u7f51\u4ea7\u4e1a\u7684\u5148\u9a71\u4eec\uff0c\u4e5f\u662f\u6606\u817e\u5728\u7ebf\u8fd9\u6b21\u6269\u5bb9\u5347\u7ea7\u7684\u6700\u76f4\u63a5\u7684\u539f\u56e0\u3002</p><p>\u3000\u3000\u9664\u4e86\u8f6f\u4ef6\u5347\u7ea7\uff0c\u786c\u4ef6\u8bbe\u5907\u4e5f\u8981\u6269\u5bb9\u5347\u7ea7\uff0c\u6240\u8017\u7684\u8d39\u7528\uff0c\u5c31\u4e0d\u662f\u4ed6\u4eec\u51e0\u4e2a\u4eba\u4ece\u522b\u5904\u63fd\u5de5\u80fd\u8d5a\u56de\u6765\u7684\uff0c\u9a6c\u5411\u4e1c\u9a6c\u4e0a\u5c31\u6bd5\u4e1a\u4e86\uff0c\u4e3a\u4e86\u8fd9\u6b21\u4e8b\uff0c\u5de5\u4f5c\u53ea\u662f\u5f88\u987a\u4fbf\u7684\u9009\u4e86\u5317\u4eac\u7684\u4e00\u6240\u7535\u5b50\u7814\u7a76\u6240\uff0c\u4e4b\u540e\u5c31\u5c06\u6240\u6709\u7684\u7cbe\u529b\u653e\u5728\u6606\u817e\u5728\u7ebf\u4e0a\u3002</p><p>\u3000\u3000\u6606\u817e\u5728\u7ebf\u66f4\u591a\u7684\u4f53\u73b0\u9a6c\u5411\u4e1c\u4e2a\u4eba\u5bf9\u4e92\u8054\u7f51\u672a\u6765\u7684\u6784\u60f3\uff0c\u6881\u6587\u6c5f\u4ed6\u4eec\u51e0\u4e2a\u5219\u662f\u53d7\u9a6c\u5411\u4e1c\u7684\u9080\u53c2\u5171\u540c\u53c2\u4e0e\u6280\u672f\u5f00\u53d1\u3002</p><p>\u3000\u3000\u53d7\u9650\u4e8e\u7cbe\u529b\u4e0e\u7ecf\u8d39\uff0c\u4ed6\u4eec\u8fd9\u6b21\u7684\u6269\u5bb9\u5347\u7ea7\uff0c\u4e5f\u53ea\u662f\u7ed9\u4e4b\u524d\u7684BBS\u7f51\u7ad9\u5efa\u7acb\u4fe1\u606f\u5206\u7c7b\u5217\u8868\u4e0e\u53ef\u5b9a\u5236\u7684\u6570\u636e\u5e93\uff0c\u5728\u8fd0\u8425\u65b9\u9762\u8fd8\u6ca1\u6709\u5177\u4f53\u7684\u89c4\u5212\uff0c\u5728\u5f20\u606a\u770b\u6765\uff0c\u6606\u817e\u5728\u7ebf\u9876\u591a\u53d1\u5c55\u6210\u8f83\u4e13\u4e1a\u7684\u4f53\u80b2\u7ade\u6280\u7f51\u7ad9\uff0c\u81f3\u5c11\u5728\u4e5d\u516d\u5e74\uff0c\u8fd9\u6837\u7684\u7f51\u7ad9\u6ca1\u8fd8\u6ca1\u6709\u503c\u5f97\u4ed6\u53bb\u6295\u8d44\u7684\u4ef7\u503c\uff0c\u4f46\u662f\u4e0e\u9a6c\u5411\u4e1c\u63a5\u89e6\u4e4b\u540e\uff0c\u624d\u77e5\u9053\u4ed6\u8fd9\u65f6\u5019\u5bf9\u4e8e\u4e92\u8054\u7f51\u4ea7\u4e1a\u5df2\u7ecf\u6709\u7740\u8f83\u4e3a\u5b8c\u6574\u7684\u6982\u5ff5\uff0c\u4f46\u662f\u8981\u6784\u5efa\u5b8c\u6574\u7684\u3001\u6280\u672f\u5148\u8fdb\u7684\u4e2d\u6587\u5e94\u7528\u5e73\u53f0\uff0c\u4e5f\u4e0d\u662f\u4ed6\u4eec\u51e0\u4e2a\u6e05\u534e\u5b66\u751f\u5229\u7528\u4e24\u4e09\u4e2a\u6708\u7684\u4e1a\u4f59\u65f6\u95f4\u5c31\u80fd\u5b8c\u6210\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u867d\u7136\u4e0d\u6e05\u695a\u90a3\u4e9b\u6280\u672f\u5c42\u9762\u7684\u4e1c\u897f\uff0c\u4f46\u662f\u4fb5\u6deb\u5341\u591a\u5e74\u7684\u7f51\u9f84\uff0c\u8ba9\u4ed6\u5bf9\u7b2c\u4e00\u4ee3\u3001\u7b2c\u4e8c\u4ee3\u4e92\u8054\u7f51\u7684\u4ea7\u4e1a\u7279\u5f81\u6709\u7740\u8fd9\u4e2a\u65f6\u4ee3\u7684\u4eba\u6240\u6ca1\u6709\u7684\u6e05\u9192\u8ba4\u8bc6\u3002</p><p>\u3000\u3000\u4e92\u8054\u7f51\u6280\u672f\u7684\u53d8\u9769\u4e0e\u4f7f\u7528\u5bf9\u7f51\u7ad9\u7684\u65b0\u751f\u4e0e\u53d1\u5c55\u8d77\u5230\u5173\u952e\u6027\u7684\u4f5c\u7528\uff0c\u56fd\u5185\u7b2c\u4e00\u4ee3\u4e92\u8054\u7f51\u4ea7\u4e1a\u7684\u6210\u529f\u6a21\u5f0f\u662f\u4ee5\u6280\u672f\u521b\u65b0\u4e3a\u4e3b\u5bfc\u7684\uff0c\u5229\u5982\u65b0\u6d6a\u6700\u521d\u5c31\u662f\u4ee5\u6280\u672f\u5e73\u53f0\u8d77\u5bb6\uff0c\u641c\u72d0\u4ee5\u641c\u7d22\u6280\u672f\u8d77\u5bb6\uff0c\u817e\u8baf\u4ee5\u5373\u65f6\u901a\u8baf\u6280\u672f\u8d77\u5bb6\uff0c\u76db\u5927\u4ee5\u7f51\u7edc\u6e38\u620f\u8d77\u5bb6\uff0c\u5728\u8fd9\u4e9b\u7f51\u7ad9\u7684\u521b\u9020\u9636\u6bb5\uff0c\u6280\u672f\u6027\u7684\u75d5\u8ff9\u76f8\u5f53\u4e4b\u91cd\u3002</p><p>\u3000\u3000\u6280\u672f\u521b\u65b0\u81ea\u4e0d\u7528\u8bf4\uff0c\u5728\u9a6c\u5411\u4e1c\u7684\u6784\u60f3\u91cc\uff0c\u6606\u817e\u5728\u7ebf\uff0c\u5e94\u8be5\u6210\u4e3a\u4e92\u8054\u7f51\u65b0\u5a92\u4f53\u7684\u4e00\u4e2a\u5e73\u53f0\uff0c\u5e76\u4e14\u6c47\u96c6\u5a31\u4e50\u3001\u4ea4\u4e92\u8bba\u575b\u3001\u90ae\u4ef6\u3001\u6536\u641c\u7b49\u589e\u503c\u670d\u52a1\u529f\u80fd\u3002\u5bf9\u65b0\u95fb\u4fe1\u606f\u7684\u91cd\u89c6\uff0c\u8fd9\u672c\u8eab\u5c31\u662f\u95e8\u6237\u7f51\u7ad9\u4e00\u4e2a\u91cd\u8981\u7279\u5f81\uff0c\u65e0\u8bba\u662f\u65e9\u671f\u7684\u878d\u8d44\u8fd8\u662f\u540e\u671f\u7684\u83b7\u5229\uff0c\u53ea\u6709\u4f9d\u8d56\u4e8e\u5de8\u5927\u7684\u70b9\u51fb\u6d41\u91cf\u4e0e\u5e9e\u5927\u7684\u7528\u6237\u7fa4\uff0c\u4e5f\u5145\u5206\u4f53\u73b0\u7b2c\u4e00\u6b21\u4e92\u8054\u7f51\u6d6a\u6f6e\u7684\u773c\u7403\u7ecf\u6d4e\u8272\u5f69\u3002</p><p>\u3000\u3000\u4e0d\u540c\u4e8e\u6881\u6587\u6c5f\u3001\u4e01\u6625\u5efa\u53ea\u5728\u6280\u672f\u4e0a\u6709\u4e13\u957f\u3001\u6574\u5929\u53ea\u4f1a\u5728\u56fe\u4e66\u9986\u4e0e\u5b9e\u9a8c\u5ba4\u91cc\u8dd1\uff0c\u9a6c\u5411\u4e1c\u5bf9\u7f51\u7ad9\u8fd0\u8425\u6709\u7740\u8fd9\u4e2a\u65f6\u4ee3\u76f8\u5f53\u6770\u51fa\u7684\u609f\u6027\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u5728\u540e\u6765\u4e92\u8054\u7f51\u5d1b\u8d77\u7684\u51e0\u4e2a\u4eba\u5f53\u4e2d\uff0c\u5f20\u606a\u6ca1\u6709\u770b\u5230\u9a6c\u5411\u4e1c\u7684\u8eab\u5f71\uff0c\u8fd9\u4e2a\u65f6\u4ee3\uff0c\u673a\u9047\u662f\u5f88\u91cd\u8981\u7684\u4e00\u4ef6\u4e8b\uff0c\u6709\u7740\u5f02\u8272\u7684\u624d\u534e\uff0c\u6216\u8bb8\u53ea\u610f\u5473\u7740\u4e00\u4efd\u76f8\u5f53\u4e0d\u9519\u7684\u5de5\u4f5c\uff0c\u6210\u4e3a\u4e3e\u4e16\u77a9\u76ee\u7684\u516c\u4f17\u4eba\u7269\uff0c\u8fd8\u9700\u8981\u4e00\u4e9b\u673a\u7f18\u624d\u884c\u3002</p><p>\u3000\u3000\u4ece\u51fa\u706b\u8f66\u7ad9\u5230\u7231\u8fbe\u7535\u5b50\u7684\u7814\u53d1\u4e2d\u5fc3\uff0c\u53ea\u4ea4\u8c08\u4e86\u4e00\u4e2a\u4e0b\u5348\u7684\u65f6\u95f4\uff0c\u5f20\u606a\u5c31\u51b3\u5b9a\u6295\u8d44\u6606\u817e\u5728\u7ebf\uff0c\u4ed6\u5bf9\u9a6c\u5411\u4e1c\u4ed6\u4eec\u8bf4\u9053\uff1a\u201c\u6211\u53ea\u5bf9\u4f60\u4eec\u6784\u60f3\u4e2d\u7684\u7f51\u7edc\u5e73\u53f0\u611f\u5174\u8da3\uff0c\u4f60\u4eec\u82e5\u662f\u6709\u5174\u8da3\u5c06\u6606\u817e\u5728\u7ebf\u53d1\u5c55\u6210\u4f60\u4eec\u6784\u60f3\u4e2d\u7684\u7f51\u7ad9\uff0c\u6211\u5012\u662f\u53ef\u4ee5\u6295\u8d44\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4ee5\u96c5\u864e\u4e3a\u76ee\u6807\uff1f\u201d\u9a6c\u5411\u4e1c\u7591\u60d1\u7684\u95ee\u3002</p><p>\u3000\u3000\u201c\u76ee\u524d\u4ee5\u96c5\u864e\u4e3a\u76ee\u6807\u2026\u2026\u201d\u5f20\u606a\u4ece\u5bb9\u6de1\u5b9a\u7684\u5750\u5728\u90a3\u91cc\uff0c\u4ed6\u5728\u6280\u672f\u4e0a\u662f\u5916\u884c\uff0c\u603b\u4e0d\u80fd\u8fd9\u65f6\u5019\u544a\u8bc9\u4ed6\u4eec\uff0c\u95e8\u6237\u7f51\u7ad9\u4ec5\u4ec5\u662f\u4e92\u8054\u7f51\u4ea7\u4e1a\u7684\u7b2c\u4e00\u4e2a\u53f0\u9636\uff0c\u968f\u7740\u4e92\u8054\u7f51\u6280\u672f\u7684\u53d1\u5c55\uff0c\u773c\u4e0b\u7684\u96c5\u864e\u8fd8\u6709\u5f88\u5927\u7684\u53d1\u5c55\u7a7a\u95f4\u3002</p><p>\u3000\u3000\u201c\u53ef\u662f\u2026\u2026\u201d\u9a6c\u5411\u4e1c\u6709\u4e9b\u72b9\u8c6b\u4e0d\u51b3\uff0c\u4ea4\u6d41\u7684\u65f6\u95f4\u8fd9\u4e48\u77ed\uff0c\u4ed6\u6000\u7591\u5f20\u606a\u6709\u6ca1\u6709\u5145\u5206\u610f\u8bc6\u5230\u505a\u4e00\u4e2a\u7efc\u5408\u6027\u7f51\u7edc\u5e73\u53f0\u7684\u8270\u5de8\u6027\uff0c\u201c\u8981\u505a\u6210\u90a3\u6837\u7684\u7f51\u7edc\u5e73\u53f0\uff0c\u5c31\u4e0d\u662f\u4e00\u822c\u7684\u5de5\u7a0b\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u7740\u8bf4\uff0c\u201c\u600e\u4e48\u4e86\uff0c\u5bf9\u7231\u8fbe\u7535\u5b50\u5c31\u6ca1\u6709\u4e00\u70b9\u671f\u5f85\uff1f\u6211\u561b\u597d\u6b79\u662f\u7231\u8fbe\u7535\u5b50\u7684\u4e3b\u8981\u6295\u8d44\u4eba\u4e4b\u4e00\u3002\u201d</p></article>"}}}]);
