(window.webpackJsonp=window.webpackJsonp||[]).push([[505],{528:function(p){p.exports={chapterTitle:null,title:"\u7b2c505\u7ae0 \u5e7f\u6cdb\u7684\u6743\u529b",content:"<article><h2>\u7b2c505\u7ae0 \u5e7f\u6cdb\u7684\u6743\u529b</h2><p>\u3000\u3000\u5929\u4e91\u5c71\u4e0a\u7684\u65c5\u6e38\u8d44\u6e90\u5927\u90fd\u5904\u4e8e\u201c\u91ce\u5f84\u901a\u5e7d\u5904\u201d\u7684\u672a\u5f00\u53d1\u72b6\u6001\uff0c\u5f20\u606a\u4ed6\u4eec\u5012\u672a\u89c9\u5f97\u6709\u4ec0\u4e48\u4e0d\u4fbf\uff0c\u5bfb\u5e7d\u63a2\u80dc\u66f4\u6709\u4e50\u8da3\u3002\u9526\u6e56\u4eca\u5e74\u4ee5\u7231\u8fbe\u7535\u5b50\u540d\u4e49\u8d44\u52a9\u7684\u6d77\u5dde\u5e02\u4e00\u4e2d\u5b66\u751f\u51ac\u4ee4\u8425\u6d3b\u52a8\u4e5f\u662f\u4e0e\u65b0\u829c\u65c5\u6e38\u5c40\u5408\u4f5c\u5230\u5929\u4e91\u5c71\u6765\u5bfb\u5e7d\u63a2\u80dc\uff0c\u51ac\u4ee4\u8425\u5230\u6625\u8282\u540e\u624d\u5f00\u8425\uff0c\u5b66\u6821\u8003\u8651\u5230\u5510\u5a67\u3001\u675c\u98de\u5979\u4eec\u8fd9\u4e9b\u9ad8\u4e09\u6bd5\u4e1a\u73ed\u7684\u5b66\u751f\u4f1a\u5e72\u90e8\u5b66\u4e60\u7d27\u5f20\uff0c\u5c31\u6ca1\u6709\u8ba9\u5979\u4eec\u53c2\u4e0e\u51ac\u4ee4\u8425\u7684\u524d\u671f\u7b79\u5212\u3002</p><p>\u3000\u3000\u675c\u98de\u5f53\u7136\u4e0d\u4f1a\u6e29\u987a\u7684\u542c\u4ece\u5b66\u6821\u7684\u5b89\u6392\uff0c\u4ed6\u5148\u8fc7\u6765\u6253\u524d\u54e8\uff0c\u8ba1\u5212\u518d\u5468\u7965\uff0c\u4eb2\u81ea\u8d70\u4e00\u8d9f\uff0c\u603b\u80fd\u53d1\u73b0\u8bb8\u591a\u4e0d\u8db3\u4e4b\u5904\uff0c\u624d\u6709\u5728\u674e\u829d\u82b3\u9762\u524d\u732e\u5b9d\u7684\u673a\u4f1a\uff1b\u8fd9\u5c0f\u5b50\u8ddf\u7740\u76db\u9752\u3001\u90b5\u81f3\u521a\u4ed6\u4eec\u6df7\u4e86\u4e24\u5e74\uff0c\u90fd\u6df7\u6cb9\u4e86\uff0c\u5f20\u606a\u4e5f\u61d2\u5f97\u6233\u7834\u4ed6\u3002</p><p>\u3000\u3000\u5510\u5a67\u8981\u4e0e\u5f20\u606a\u3001\u665a\u6674\u3001\u82b7\u5f64\u53bb\u5317\u4eac\u8fc7\u6625\u8282\uff0c\u90fd\u672a\u5fc5\u80fd\u8d76\u5f97\u4e0a\u51ac\u4ee4\u8425\u5f00\u8425\u3002</p><p>\u3000\u3000\u5f20\u606a\u4ed6\u4eec\u5728\u5c71\u4e0a\u4f4f\u4e86\u56db\u5929\uff0c\u518d\u4e0d\u4e0b\uff0c\u82b7\u5f64\u3001\u60dc\u7fbd\u3001\u60dc\u5bb9\u4e09\u4e2a\u5c0f\u4e2b\u5934\u90fd\u8981\u6210\u91ce\u4eba\u4e86\uff0c\u7adf\u6709\u4e00\u6b21\u7ed9\u60dc\u5bb9\u6002\u607f\u7740\u8eb2\u8ff7\u85cf\uff0c\u4e0d\u662f\u5979\u4eec\u4e09\u4e2a\u5c0f\u4e2b\u5934\u76f8\u4e92\u8eb2\uff0c\u662f\u4e09\u4e2a\u5c0f\u4e2b\u5934\u8ddf\u5f20\u606a\u5979\u4eec\u8eb2\uff0c\u8d70\u5931\u4e86\u534a\u5929\uff0c\u628a\u5927\u5bb6\u90fd\u5413\u4e86\u4e00\u8eab\u51b7\u6c57\uff0c\u8fd9\u624d\u814a\u6708\u4e8c\u5341\u516d\u63d0\u524d\u4e00\u5929\uff0c\u624d\u4e58\u6c5f\u8f6e\u79bb\u5f00\u65b0\u829c\u3002\u5f20\u77e5\u884c\u3001\u6881\u683c\u73cd\u5219\u8981\u5728\u65b0\u829c\u8fc7\u4ed6\u4eec\u7684\u7b2c\u4e00\u4e2a\u6625\u8282\uff0c\u5f20\u606a\u3001\u665a\u6674\u3001\u5510\u5a67\u5e26\u7740\u82b7\u5f64\u5728\u7701\u57ce\u4e0b\u4e86\u8239\uff0c\u51c6\u5907\u4ece\u5efa\u90ba\u4e58\u98de\u673a\u53bb\u5317\u4eac\u2014\u2014\u6ca1\u6709\u8ba9\u5085\u4fca\u6216\u9a6c\u6d77\u9f99\u968f\u884c\uff0c\u8ba9\u4ed6\u4eec\u56de\u6d77\u5dde\u8ddf\u5bb6\u4eba\u4e00\u8d77\u8fc7\u6625\u8282\uff0c\u5f20\u5ead\u4e5f\u5728\u7701\u57ce\u4e0b\u8239\uff0c\u4e0d\u8fc7\u5979\u7684\u5bb6\u5728\u7701\u57ce\u2014\u2014\u5176\u4ed6\u4eba\u5219\u7ee7\u7eed\u4e58\u6c5f\u8f6e\u8fd4\u56de\u6d77\u5dde\u3002</p><p>\u3000\u3000\u5728\u7701\u57ce\u4f4f\u4e86\u4e00\u591c\uff0c\u9664\u5915\u8fd9\u4e00\u5929\u624d\u5750\u98de\u673a\u62b5\u8fbe\u5317\u4eac\uff0c\u8bb8\u591a\u5355\u4f4d\u5230\u5e74\u5173\u90fd\u4f1a\u63d0\u524d\u653e\u5047\uff0c\u90a3\u4e9b\u5f02\u5730\u7684\u5458\u5de5\u66f4\u662f\u4f1a\u63d0\u524d\u4e94\u5230\u5341\u5929\u79bb\u5f00\u516c\u53f8\uff0c\u6574\u4e2a\u98de\u673a\u5c31\u6ca1\u6709\u591a\u5c11\u4eba\uff0c\u611f\u89c9\u4ed6\u4eec\u51e0\u4e2a\u5305\u4e0b\u98de\u673a\u4f3c\u7684\u3002\u4e0b\u98de\u673a\uff0c\u9002\u9022\u5317\u4eac\u4e0b\u5927\u96ea\uff0c\u6574\u4e2a\u57ce\u5e02\u90fd\u94f6\u88c5\u7d20\u88f9\uff0c\u4e0d\u518d\u662f\u5f80\u65e5\u7684\u7070\u6697\u5370\u8c61\u3002</p><p>\u3000\u3000\u867d\u7136\u4e0d\u6bd4\u5728\u4e1c\u6d77\u5f53\u5c01\u7586\u5927\u540f\uff0c\u5f90\u5b66\u5e73\u5728\u5317\u4eac\u6bd5\u7adf\u8fd8\u662f\u6b63\u90e8\u7ea7\u9ad8\u5b98\uff0c\u5f85\u9047\u89c4\u683c\u5012\u662f\u4e00\u70b9\u90fd\u6ca1\u6709\u964d\u4f4e\uff1b\u9ed1\u8272\u5965\u8fea\u6302\u7740\u56fd\u52a1\u9662\u7684\u8f66\u724c\uff0c\u53f8\u673a\u5c06\u8f66\u76f4\u63a5\u5f00\u5230\u673a\u573a\u51fa\u5165\u5883\u5927\u5385\u5916\u7684\u53f0\u9636\u4e0b\u7b49\u5019\u3002</p><p>\u3000\u3000\u673a\u573a\u5feb\u901f\u901a\u9053\u4e0a\u7684\u79ef\u96ea\u65e9\u5c31\u6e05\u5e72\u51c0\u4e86\uff0c\u96ea\u4e0a\u5348\u5c31\u505c\u4e86\uff0c\u592a\u9633\u9732\u51fa\u6765\uff0c\u53ea\u662f\u5076\u5c14\u4e00\u9635\u98ce\u6765\uff0c\u522e\u8d77\u6f2b\u5929\u7684\u96ea\u7c92\u98de\u821e\uff1b\u5317\u65b9\u7684\u96ea\u662f\u6bd4\u8f83\u5e72\uff0c\u8981\u5728\u6d77\u5dde\u4e0b\u8fd9\u4e48\u4e00\u573a\u96ea\uff0c\u5927\u98ce\u5439\u4e0a\u4e00\u4e24\u5929\uff0c\u624d\u80fd\u5c06\u96ea\u7c92\u5439\u8d77\u6765\u3002</p><p>\u3000\u3000\u5f90\u5b66\u5e73\u5230\u5317\u4eac\u540e\u4f4f\u5728\u590d\u5174\u95e8\u6728\u6a28\u5730\u4e00\u5904\u4e13\u95e8\u4e3a\u56fd\u5bb6\u673a\u5173\u9ad8\u7ea7\u5e72\u90e8\u5efa\u9020\u7684\u5c0f\u533a\u91cc\u3002\u4e09\u5c42\u7684\u82b1\u56ed\u6d0b\u697c\uff0c\u6709\u72ec\u7acb\u7684\u9662\u5b50\uff0c\u9664\u4e86\u5f90\u5b66\u5e73\u3001\u5468\u53d4\u60e0\u592b\u5987\u5916\uff0c\u53ea\u6709\u968f\u4ed6\u4eec\u4ece\u5efa\u90ba\u642c\u5230\u5317\u4eac\u7684\u5c0f\u5468\u4fdd\u59c6\u4f4f\u5728\u91cc\u9762\u3002</p><p>\u3000\u3000\u5f20\u606a\u7684\u5c0f\u8868\u54e5\u6881\u6587\u6c5f\u4e0e\u5973\u670b\u53cb\u7559\u5728\u5b66\u6821\u91cc\u6ca1\u6709\u56de\u897f\u5b89\u8fc7\u6625\u8282\uff0c\u5f90\u5b66\u5e73\u77e5\u9053\u540e\uff0c\u51c6\u5907\u6d3e\u8f66\u53bb\u63a5\uff0c\u5f20\u606a\u89c9\u5f97\u4e0d\u7528\u8fd9\u4e48\u9ebb\u70e6\uff0c\u6253\u7535\u8bdd\u8ba9\u5c0f\u8868\u54e5\u6881\u6587\u6c5f\u5e26\u7740\u5973\u670b\u53cb\u5750\u8f66\u4e00\u8d77\u8fc7\u6765\u5403\u5e74\u591c\u996d\u3002\u5c0f\u533a\u7684\u7ba1\u7406\u5341\u5206\u4e25\u683c\uff0c\u5f20\u606a\u4ed6\u4eec\u5750\u5f90\u5b66\u5e73\u7684\u8f66\u8fdb\u5c0f\u533a\uff0c\u53c8\u6709\u5f90\u5b66\u5e73\u7684\u53f8\u673a\u966a\u7740\uff0c\u624d\u6ca1\u6709\u63a5\u53d7\u4ec0\u4e48\u68c0\u67e5\uff0c\u6881\u6587\u6c5f\u5e26\u7740\u5973\u670b\u53cb\u8d76\u8fc7\u6765\u5403\u996d\uff0c\u4eba\u7ed9\u5835\u5728\u95e8\u5c97\u5904\uff0c\u8fd8\u9700\u8981\u4eba\u51fa\u53bb\u63a5\u4ed6\u4eec\u624d\u53ef\u4ee5\u8fdb\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u5e74\u4e2d\u5411\u6606\u817e\u5728\u7ebf\u6295\u5165\u6570\u767e\u4e07\u8d44\u91d1\u4e4b\u540e\uff0c\u6881\u6587\u6c5f\u4e0e\u9a6c\u5411\u4e1c\u7b49\u5176\u4ed6\u51e0\u540d\u6e05\u534e\u5728\u6821\u6216\u6bd5\u4e1a\u7684\u5b66\u751f\u90fd\u5728\u52aa\u529b\u642d\u8bbe\u4e00\u4e2a\u95e8\u6237\u7f51\u7ad9\u7684\u6846\u67b6\u5e76\u8d1f\u8d23\u7f51\u7ad9\u7684\u8fd0\u8425\u3002\u505a\u6280\u672f\u7684\u90fd\u4e0d\u5bb9\u6613\u7ed9\u5916\u754c\u7684\u6d88\u606f\u5206\u5fc3\uff0c\u6881\u6587\u6c5f\u5c5e\u4e8e\u90a3\u79cd\u53ea\u6709\u81ea\u5df1\u9047\u5230\u4e86\uff0c\u624d\u4f1a\u7ed9\u5413\u4e00\u8df3\u6216\u5403\u4e00\u60ca\u7684\u660e\u767d\u8fc7\u6765\u3002</p><p>\u3000\u3000\u4ed6\u4e0e\u4ed6\u5317\u4eac\u90ae\u7535\u5b66\u9662\u7684\u5973\u670b\u53cb\u8d76\u5230\u6728\u6a28\u5730\u7684\u8fd9\u5904\u5c0f\u533a\uff0c\u7ed9\u95e8\u5c97\u5904\u7684\u6b66\u88c5\u503c\u73ed\u6b66\u8b66\u62e6\u5728\u95e8\u5916\uff0c\u624d\u610f\u8bc6\u5230\u8fd9\u662f\u4e00\u5904\u9ad8\u7ea7\u5e72\u90e8\u805a\u5c45\u7684\u4f4f\u5b85\u533a\uff0c\u7b49\u5f20\u606a\u7531\u4eba\u5e26\u7740\u8fc7\u6765\u9886\u4ed6\u4eec\u8fdb\u53bb\uff0c\u4ed6\u60f3\u8d77\u8fd9\u91cc\u539f\u6765\u662f\u6e05\u534e\u540c\u5b66\u4e4b\u95f4\u4e00\u76f4\u8c08\u8bba\u7684\u201c\u90e8\u957f\u5c0f\u533a\u201d\u3002</p><p>\u3000\u3000\u6881\u6587\u6c5f\u8ddf\u5f20\u606a\u4ed6\u5c0f\u8205\u4e00\u6837\uff0c\u6709\u4e9b\u8bfb\u4e66\u4eba\u7684\u8fc2\u6c14\uff0c\u4eba\u60c5\u4e16\u6545\u6bd4\u8d77\u675c\u98de\u6765\u90fd\u8fdc\u4e0d\u5982\uff0c\u4e0d\u8fc7\u5f90\u5b66\u5e73\u559c\u6b22\u8fd9\u6837\u7684\u4eba\uff1b\u5f90\u5b66\u5e73\u89c1\u8fc7\u592a\u591a\u5904\u4e16\u5706\u6ed1\u7684\u4eba\u4e86\uff0c\u4e0d\u559c\u6b22\u90a3\u6837\u7684\u4eba\uff0c\u8981\u7559\u6881\u6587\u6c5f\u4e0e\u4ed6\u5973\u670b\u53cb\u591c\u91cc\u4e00\u8d77\u5b88\u5c81\uff0c\u623f\u95f4\u867d\u7136\u4e0d\u591a\uff0c\u4e0d\u8fc7\u6881\u6587\u6c5f\u7684\u5973\u670b\u53cb\u53ef\u4ee5\u4e0e\u5510\u5a67\u540c\u4e00\u623f\u95f4\uff0c\u6881\u6587\u6c5f\u53ef\u4ee5\u4e0e\u5f20\u606a\u540c\u4e00\u623f\u95f4\u3002</p><p>\u3000\u3000\u5510\u5a67\u4e0e\u82b7\u5f64\u5728\u9662\u5b50\u91cc\u5806\u96ea\u4eba\uff0c\u5f90\u5b66\u5e73\u4e3a\u82b7\u5f64\u8fc7\u6765\u7279\u610f\u82b1\u4e86\u4e9b\u5fc3\u601d\uff0c\u50cf\u5c0f\u533a\u91cc\u5176\u4ed6\u9662\u5b50\u4e2d\u7684\u79ef\u96ea\u90fd\u5df2\u6e05\u7a7a\uff0c\u60df\u6709\u8fd9\u5ea7\u9662\u5b50\u91cc\u79ef\u96ea\u90fd\u4fdd\u6301\u539f\u6837\uff0c\u4e0d\u8fc7\u7247\u523b\u4e4b\u95f4\u5c31\u7ed9\u82b7\u5f64\u8e29\u4e71\u4e86\uff0c\u53ef\u89c1\u5c0f\u4e2b\u5934\u7684\u5174\u594b\u52b2\u3002</p><p>\u3000\u3000\u665a\u6674\u3001\u8fd8\u6709\u6881\u6587\u6c5f\u7684\u5973\u670b\u53cb\u738b\u5f69\u73b2\u966a\u5468\u53d4\u60e0\u5728\u5305\u997a\u5b50\uff0c\u738b\u5f69\u73b2\u662f\u957f\u76f8\u6e05\u79c0\u7684\u5973\u5b69\u5b50\uff0c\u62ff\u6881\u6587\u6c5f\u81ea\u5df1\u7684\u8bdd\u8bf4\uff0c\u662f\u5317\u90ae\u4e5d\u4e94\u5c4a\u4e94\u5341\u7f8e\u5973\u4e4b\u4e00\uff0c\u8bdd\u8bf4\u5317\u90ae\u4e00\u5c4a\u672c\u79d1\u751f\u91cc\u7684\u5973\u751f\u4e0d\u8d85\u8fc7\u4e24\u767e\u4eba\uff0c\u54e6\uff0c\u5317\u90ae\u4e5f\u662f\u9526\u6e56\u91cd\u70b9\u62db\u8058\u5458\u5de5\u7684\u9662\u6821\uff0c\u5317\u90ae\u7684\u5973\u751f\u5916\u5f62\u90fd\u5904\u4e8e\u4ec0\u4e48\u6c34\u51c6\uff0c\u5f20\u606a\u5fc3\u91cc\u6709\u6570\u3002</p><p>\u3000\u3000\u738b\u5f69\u73b2\u8001\u5bb6\u662f\u897f\u5b89\u4eba\uff0c\u4e0d\u8fc7\u5979\u51fa\u751f\u5728\u5317\u4eac\uff0c\u7236\u6bcd\u90fd\u4e0d\u5728\u5317\u4eac\u8fc7\u6625\u8282\uff0c\u6240\u4ee5\u5979\u5c31\u968f\u6881\u6587\u6c5f\u5728\u5916\u6d41\u6d6a\uff0c\u5979\u7684\u5bb6\u5883\u76f8\u5f53\u4f18\u6e25\uff0c\u5979\u7236\u4eb2\u8fd8\u662f\u90ae\u653f\u90e8\u7684\u9ad8\u7ea7\u5b98\u5458\uff0c\u65c1\u4eba\u90fd\u5f88\u96be\u7406\u89e3\u738b\u5f69\u73b2\u4e3a\u4ec0\u4e48\u4f1a\u770b\u5230\u6709\u4e9b\u8fc2\u6c14\u7684\u6881\u6587\u6c5f\u3002</p><p>\u3000\u3000\u5f20\u606a\u524d\u4e16\u53ea\u89c1\u8fc7\u8fd9\u8868\u5ac2\u4e24\u9762\uff0c\u6881\u6587\u6c5f\u53bb\u7f8e\u56fd\u7559\u5b66\u540e\uff0c\u738b\u5f69\u73b2\u8fc7\u4e86\u56db\u5e74\u76f4\u5230\u5979\u4ece\u5317\u90ae\u7814\u7a76\u751f\u6bd5\u4e1a\u4e4b\u540e\u624d\u53bb\u7f8e\u56fd\u8ddf\u6881\u6587\u6c5f\u7ed3\u5a5a\uff0c\u5904\u4e8b\u4e56\u5de7\u4f46\u53c8\u5fc3\u5730\u7eaf\u51c0\u7684\u5973\u5b69\u5b50\uff1b\u603b\u4e4b\u518d\u8fc7\u5341\u5e74\uff0c\u8fd9\u6837\u7684\u5973\u5b69\u5b50\u5c31\u5f88\u7f55\u89c1\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u966a\u5f90\u5b66\u5e73\u4e0b\u56f4\u68cb\uff0c\u6881\u6587\u6c5f\u5750\u5728\u65c1\u8fb9\u89c2\u68cb\uff1b\u5f20\u606a\u7684\u68cb\u827a\u6bd4\u5f90\u5b66\u5e73\u7565\u9ad8\u4e00\u7b79\uff0c\u4f46\u6bd4\u4ed6\u8868\u54e5\u6881\u6587\u6c5f\u53c8\u4e0d\u5982\uff0c\u6e05\u534e\u3001\u5317\u5927\u91cc\u9690\u85cf\u7684\u4e1a\u4f59\u56f4\u68cb\u9ad8\u624b\u592a\u591a\uff0c\u800c\u4e14\u4ed6\u4eec\u5b66\u4e60\u4e4b\u540e\u5927\u591a\u4ee5\u56f4\u68cb\u53d6\u4e50\uff0c\u5f20\u606a\u4e0b\u7ec6\u68cb\uff0c\u591a\u534a\u4e0b\u4e0d\u8fc7\u4ed6\u4eec\u3002</p><p>\u3000\u3000\u5403\u8fc7\u665a\u996d\u4e4b\u540e\uff0c\u5728\u9662\u5b50\u91cc\u80fd\u770b\u5230\u5927\u6982\u662f\u5929\u5b89\u95e8\u65b9\u5411\u70b9\u653e\u7684\u70df\u706b\uff0c\u5929\u7a7a\u7ed9\u5448\u5f97\u4e94\u5f69\u6591\u6593\uff0c\u64ad\u653e\u6625\u8282\u6587\u827a\u665a\u4f1a\uff0c\u6881\u6587\u6c5f\u5750\u5230\u5ba2\u5385\u91cc\u770b\u6625\u665a\u53bb\u4e86\u3002\u5f20\u606a\u6ca1\u6709\u5174\u8da3\uff0c\u9664\u4e86\u7231\u8fbe\u7535\u5b50\u6324\u5165\u8fd9\u5c4a\u6625\u665a\u7684\u8d5e\u52a9\u5546\u540d\u5217\uff0c\u5176\u4ed6\u7684\u8282\u76ee\uff0c\u5f20\u606a\u90fd\u6709\u4e00\u5b9a\u7684\u5370\u8c61\uff0c\u53c8\u4e0d\u80fd\u62ff\u51fa\u6765\u70ab\u8000\uff0c\u5c31\u4e0e\u5f90\u5b66\u5e73\u8fd8\u662f\u5750\u5728\u4e66\u623f\u91cc\u8fb9\u4e0b\u68cb\u8fb9\u804a\u5929\u3002</p><p>\u3000\u3000\u8bf4\u5230\u5f90\u5b66\u5e73\u79bb\u5f00\u4e1c\u6d77\u540e\u4e00\u4e2a\u6708\u4e4b\u95f4\u7684\u6697\u4e2d\u7ea0\u845b\uff0c\u5f90\u5b66\u5e73\u5fae\u5fae\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u674e\u8fdc\u6e56\u8fd9\u4eba\u5e74\u8f7b\u3001\u6709\u80fd\u529b\uff0c\u505a\u4e8b\u4e5f\u6709\u9b44\u529b\uff0c\u53ea\u5f85\u4eba\u7a0d\u4e25\u5389\u4e86\u4e9b\u2026\u2026\u201d</p><p>\u3000\u3000\u6240\u8c13\u5f85\u4eba\u4e25\u5389\uff0c\u65e0\u975e\u8bf4\u4ed6\u6c14\u5ea6\u4e0d\u591f\u3002</p><p>\u3000\u3000\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u90a3\u6211\u5c31\u628a\u4ed6\u7684\u4e25\u5389\uff0c\u5f53\u6210\u662f\u5bf9\u9526\u6e56\u7684\u97ad\u7b56\u2026\u2026\u201d</p><p>\u3000\u3000\u5f90\u5b66\u5e73\u7b11\u4e86\u7b11\uff0c\u9526\u6e56\u867d\u7136\u89c4\u6a21\u8fd8\u4e0d\u591f\u5927\uff0c\u4f46\u4e5f\u4e0d\u662f\u4efb\u8c01\u90fd\u80fd\u634f\u7684\u8f6f\u67ff\u5b50\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u6211\u8fd9\u6b21\u8fc7\u6765\uff0c\u5012\u662f\u6709\u4e9b\u60f3\u542c\u5f90\u4f2f\u4f2f\u8bf4\u8bf4\u7ecf\u6d4e\u53d1\u5c55\u7814\u7a76\u4e2d\u5fc3\u6709\u4ec0\u4e48\u95f2\u60c5\u9038\u81f4\u7684\u4f20\u95fb\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u60f3\u542c\u4ec0\u4e48\u554a\uff1f\u201d\u5f90\u5b66\u5e73\u7b11\u7740\u95ee\uff0c\u5634\u91cc\u8fd9\u4e48\u95ee\u7740\uff0c\u8fd8\u6307\u7740\u68cb\u76d8\u63d0\u9192\u5f20\u522b\u5149\u987e\u4e86\u8bf4\u8bdd\u5fd8\u4e86\u843d\u5b50\u3002</p><p>\u3000\u3000\u201c\u5f53\u7136\u662f\u5de5\u4fe1\u4ea7\u4e1a\u4e0e\u6797\u4e1a\u3001\u9020\u7eb8\u4ea7\u4e1a\u65b9\u9762\u7684\u4f20\u95fb\uff1f\u201d\u5f20\u606a\u634f\u4e86\u4e00\u7c92\u9ed1\u5b50\u6273\u5934\uff0c\u201c\u6211\u8fd9\u4e9b\u5929\u7422\u78e8\u7740\uff0c\u5f90\u4f2f\u4f2f\u5230\u7ecf\u6d4e\u53d1\u5c55\u7814\u7a76\u4e2d\u5fc3\uff0c\u4f3c\u4e4e\u4e5f\u662f\u53ef\u8fdb\u4e5f\u53ef\u9000\u7684\u4e00\u6b65\u68cb\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u6b65\u68cb\u4e5f\u8ba9\u4eba\u63e3\u6469\u4e0d\u900f\uff0c\u201d\u5f90\u5b66\u5e73\u6307\u7740\u68cb\u76d8\u4e0a\u5f20\u606a\u521a\u843d\u4e0b\u7684\u68cb\u5b50\uff0c\u63e3\u6469\u4e0d\u900f\u5c31\u610f\u5473\u7740\u591a\u79cd\u53ef\u80fd\uff0c\u201c\u53ea\u662f\u5e0c\u671b\u6e3a\u832b\u4e9b\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u5012\u662f\u6015\u5f90\u5b66\u5e73\u610f\u5fd7\u6d88\u6c89\uff0c\u8fd9\u624d\u8bf4\u756a\u8bdd\u6765\u3002</p><p>\u3000\u3000\u56fd\u52a1\u9662\u7ecf\u6d4e\u53d1\u5c55\u7814\u7a76\u4e2d\u5fc3\u4f5c\u4e3a\u56fd\u52a1\u9662\u76f4\u5c5e\u7684\u653f\u7b56\u7814\u7a76\u4e0e\u54a8\u8be2\u673a\u6784\uff0c\u5e76\u975e\u4e00\u4e2a\u4e0e\u653f\u6cbb\u4e2d\u5fc3\u6743\u529b\u8131\u79bb\u592a\u8fdc\u7684\u90e8\u95e8\uff0c\u53ea\u662f\u5f90\u5b66\u5e73\u7684\u804c\u52a1\u5b89\u6392\u6709\u4e9b\u5c34\u5c2c\uff0c\u4f46\u4e5f\u975e\u5168\u65e0\u8fdb\u7684\u53ef\u80fd\u3002</p><p>\u3000\u3000\u5f90\u5b66\u5e73\u81ea\u5df1\u5012\u4e5f\u80fd\u660e\u767d\uff0c\u4f46\u662f\u4ed6\u53c8\u65e0\u6cd5\u770b\u5230\u4eca\u540e\u4e24\u5e74\u7684\u653f\u6cbb\u683c\u5c40\u53d8\u5316\uff0c\u770b\u5230\u7684\u5e0c\u671b\u8981\u6bd4\u5f20\u606a\u6e3a\u832b\u591a\u5c11\u3002</p><p>\u3000\u3000\u5f90\u5b66\u5e73\u5012\u4e0d\u662f\u81ea\u5df1\u7684\u5883\u9047\u5fe7\u8651\uff0c\u8bf4\u9053\uff1a\u201c\u5728\u4e2d\u5fc3\u5012\u662f\u6709\u5f88\u591a\u4e8b\u60c5\u8981\u505a\uff0c\u4e4b\u524d\u505a\u7701\u957f\uff0c\u5bf9\u4ea7\u4e1a\u7ecf\u6d4e\u6709\u4e9b\u7814\u7a76\uff0c\u4f46\u90fd\u4e0d\u53ca\u4e2d\u5fc3\u628a\u63e1\u5168\u56fd\u7684\u5b8f\u89c2\u9762\u90a3\u4e48\u900f\u5f7b\u3002\u4f60\u8bf4\u8981\u542c\u5de5\u4fe1\u4ea7\u4e1a\u3001\u6797\u4e1a\u3001\u9020\u7eb8\u4ea7\u4e1a\u65b9\u9762\u7684\u8f76\u95fb\uff0c\u5de5\u4fe1\u4ea7\u4e1a\uff0c\u4e13\u4e1a\u6027\u592a\u5f3a\uff0c\u6211\u90fd\u8fc7\u516d\u5341\u5c81\uff0c\u6ca1\u6709\u7cbe\u529b\u53bb\u7422\u78e8\u90a3\u4e48\u6df1\u5965\u7684\u4e13\u4e1a\u77e5\u8bc6\uff0c\u6797\u4e1a\u3001\u9020\u7eb8\u4ea7\u4e1a\uff0c\u6211\u5012\u53ef\u4ee5\u591a\u5173\u5fc3\u4e00\u4e0b\u7684\u3002\u73b0\u5728\u56fd\u5185\u5bf9\u5929\u7136\u6797\u7684\u4fdd\u62a4\u975e\u5e38\u4e0d\u5230\u4f4d\uff0c\u6797\u6743\u6539\u9769\u63d0\u4e86\u597d\u4e9b\u5e74\uff0c\u90fd\u672a\u89c1\u542f\u52a8\uff0c\u4e0e\u4e0b\u6e38\u4ee5\u6728\u6750\u4e3a\u539f\u6599\u7684\u4ea7\u4e1a\u4e4b\u95f4\u7684\u77db\u76fe\uff0c\u4e5f\u65e5\u76ca\u5c16\u9510\uff0c\u6797\u5730\u5206\u5e03\u9762\u79ef\u5e7f\u6cdb\u7684\u5730\u533a\uff0c\u7ecf\u6d4e\u53d1\u5c55\u4e25\u91cd\u6ede\u540e\u7684\u95ee\u9898\u4e5f\u4e0d\u5bb9\u5ffd\u89c6\u3002\u6211\u5728\u4e1c\u6d77\u4efb\u4e0a\uff0c\u5bf9\u6b64\u6709\u4e9b\u611f\u89e6\u4e0e\u60f3\u6cd5\uff0c\u4f46\u662f\u6ca1\u6709\u7ad9\u5230\u773c\u4e0b\u7684\u9ad8\u5ea6\u53bb\u5168\u76d8\u7684\u8003\u8651\u8fd9\u4e9b\u95ee\u9898\uff0c\u6b64\u65f6\u867d\u7136\u4e0d\u80fd\u505a\u592a\u5177\u4f53\u7684\u4e8b\u60c5\uff0c\u5728\u540e\u9762\u97ad\u7b56\u97ad\u7b56\u4e5f\u662f\u53ef\u4ee5\u7684\u3002\u201d\u53c8\u95ee\u5f20\u606a\uff0c\u201c\u65b0\u5149\u7eb8\u4e1a\u6709\u591a\u5927\u7684\u89c4\u5212\uff1f\u201d</p><p>\u3000\u3000\u201c\u65b0\u5149\u7eb8\u4e1a\u80fd\u505a\u5230\u591a\u5927\uff0c\u8fd8\u8981\u770b\u7231\u8fbe\u80fd\u505a\u5230\u591a\u5927\uff0c\u201d\u5f20\u606a\u56de\u7b54\u8bf4\u9053\uff0c\u201c\u9020\u7eb8\u4e1a\u662f\u8d44\u91d1\u5bc6\u96c6\u578b\u7684\u4ea7\u4e1a\uff0c\u9760\u81ea\u8eab\u7684\u5229\u6da6\u79ef\u7d2f\u505a\u5927\u89c4\u6a21\uff0c\u8fdb\u5c55\u4f1a\u5f88\u6162\u2026\u2026\u539f\u6599\u6797\u7684\u57f9\u80b2\u4e5f\u8fd8\u9700\u8981\u5f88\u957f\u7684\u5468\u671f\uff0c\u4e0d\u8fc7\u8fd1\u51e0\u5e74\u5185\uff0c\u539f\u6599\u591a\u4ee5\u8fdb\u53e3\u5e9f\u7eb8\u6d46\u3001\u6728\u6d46\u4e3a\u4e3b\uff0c\u4e0d\u4f1a\u4ece\u56fd\u5185\u8d2d\u8fdb\u4e00\u987f\u6728\u6d46\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u65b0\u5149\u7eb8\u4e1a\u662f\u81ea\u7f1a\u624b\u8db3\u4e0e\u5de8\u8c61\u8d77\u821e\uff0c\u5728\u522b\u4eba\u773c\u91cc\uff0c\u603b\u8981\u7b97\u7406\u60f3\u4e3b\u4e49\u8fc7\u5934\u4e86\u3002\u201d\u5f90\u5b66\u5e73\u7b11\u7740\u8bf4\uff0c\u867d\u7136\u5728\u522b\u4eba\u773c\u91cc\uff0c\u9526\u6e56\u7684\u6253\u7b97\u662f\u7406\u60f3\u4e3b\u4e49\u8fc7\u5934\u4e86\uff0c\u4ed6\u8fd8\u662f\u5f88\u6b23\u8d4f\u8fd9\u79cd\u505a\u6cd5\uff0c\u5173\u952e\u9526\u6e56\u6709\u5982\u6b64\u6253\u7b97\u5e76\u5b9e\u65bd\u7684\u5b9e\u529b\u3002</p><p>\u3000\u3000\u201c\u8fd9\u4e00\u70b9\u5e94\u8be5\u50cf\u5c0f\u65e5\u672c\u5b66\u4e60\uff0c\u5c0f\u65e5\u672c\u7684\u505a\u6cd5\uff0c\u65e0\u975e\u662f\u7528\u9ad8\u79d1\u6280\u7535\u5b50\u4ea7\u54c1\u53bb\u83b7\u53d6\u5229\u6da6\uff0c\u7136\u540e\u518d\u7528\u8fd9\u4e9b\u5229\u6da6\u53bb\u63a0\u593a\u522b\u56fd\u7684\u81ea\u7136\u8d44\u6e90\u3001\u4fdd\u62a4\u81ea\u5df1\u56fd\u5bb6\u7684\u81ea\u7136\u8d44\u6e90\u4e0d\u88ab\u7834\u574f\u7f62\u4e86\u3002\u9526\u6e56\u8fd9\u4e48\u505a\uff0c\u662f\u8682\u8681\u4eff\u6548\u5927\u8c61\uff0c\u867d\u7136\u6709\u87b3\u81c2\u5f53\u8f66\u7684\u53ef\u7b11\uff0c\u603b\u80fd\u8d77\u4e9b\u4f5c\u7528\uff0c\u201d\u5f20\u606a\u8bf4\u7684\u5f88\u5e73\u6de1\uff0c\u201c\u6211\u770b\u8fc7\u4e00\u4e2a\u6570\u636e\uff0c\u5c0f\u65e5\u672c\u6bcf\u5e74\u4f7f\u7528\u7684\u4e00\u51b3\u6027\u7b77\u5b50\u670996\uff05\u4ece\u6211\u56fd\u8fdb\u53e3\uff0c\u5927\u6982\u4e00\u5e74\u8981\u6d88\u8d39\u56fd\u5185250\u4e07\u68f5\u6811\u6728\uff0c\u800c\u5c0f\u65e5\u672c\u56fd\u5185\u7684\u68ee\u6797\u8986\u76d6\u7387\u662f\u6211\u56fd\u7684\u4e09\u500d\u8fd8\u591a\uff0c\u4e0d\u8fc7\u56fd\u5185\u8fd8\u5bf9\u8fd9\u7c7b\u4ea7\u54c1\u5b9e\u884c\u9000\u7a0e\u3001\u8d34\u7a0e\u7b49\u4f18\u60e0\u653f\u7b56\u9f13\u52b1\u51fa\u53e3\uff0c\u56fd\u5185\u7684\u68ee\u5de5\u4f01\u4e1a\u91c7\u4f10\u6797\u5730\uff0c\u8ffd\u6c42\u77ed\u671f\u5229\u6da6\uff0c\u5f88\u5c11\u5b9e\u65bd\u95f4\u4f10\uff0c\u5927\u591a\u662f\u4e00\u4f10\u5149\u5f0f\u7684\u5168\u4f10\uff0c\u8fd9\u4ec5\u4ec5\u662f\u68ee\u5de5\u4ea7\u4e1a\u5f88\u7a84\u7684\u4e00\u4e2a\u9762\u2026\u2026\u5176\u4ed6\u81ea\u7136\u8d44\u6e90\uff0c\u6682\u4e14\u4e0d\u8bf4\uff0c\u4f46\u662f\u6797\u4e1a\u53ca\u5176\u9644\u5c5e\u4ea7\u4e1a\uff0c\u90fd\u9700\u8981\u6df1\u523b\u7684\u53cd\u601d\u4e0e\u53d8\u9769\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u8bf4\u7684\u4e00\u6b21\u7b77\u5b50\uff0c\u6709\u5173\u90e8\u95e8\u90fd\u6709\u610f\u8bc6\u5230\uff0c\u8003\u8651\u5230\u56fd\u5185\u7684\u7ecf\u6d4e\u73b0\u72b6\uff0c\u5168\u9762\u7981\u6b62\u5f88\u96be\u5b9e\u65bd\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u7981\uff0c\u77ed\u671f\u5185\u5f53\u7136\u7981\u4e0d\u4e86\uff0c\u4e2d\u592e\u8981\u7981\uff0c\u5730\u65b9\u8981\u8ffd\u6c42\u77ed\u671f\u7ecf\u6d4e\u6548\u76ca\uff0c\u4e2d\u5c0f\u4f01\u4e1a\u4e5f\u4e0d\u4f1a\u6709\u592a\u5f3a\u70c8\u7684\u793e\u4f1a\u8d23\u4efb\u5fc3\uff0c\u6709\u77db\u76fe\uff0c\u8fd8\u662f\u89e3\u51b3\u4e0d\u4e86\uff1b\u90a3\u5c31\u9650\uff0c\u63d0\u9ad8\u751f\u4ea7\u6807\u51c6\uff0c\u63d0\u9ad8\u4f7f\u7528\u8005\u7684\u4f7f\u7528\u6210\u672c\uff0c\u6b64\u7c7b\u4ea7\u54c1\u5bf9\u5916\u51fa\u53e3\u66f4\u8981\u9650\u5236\uff0c\u975e\u4f46\u4e0d\u80fd\u9000\u7a0e\u3001\u8d34\u7a0e\u9f13\u52b1\u51fa\u53e3\uff0c\u8fd8\u8981\u63d0\u9ad8\u5173\u7a0e\u538b\u5236\u51fa\u53e3\uff0c\u51e0\u4e0b\u63aa\u65bd\u4e00\u6765\uff0c\u60c5\u51b5\u5c31\u80fd\u63a7\u5236\u5728\u53ef\u4ee5\u63a5\u53d7\u7684\u6c34\u5e73\u4e4b\u5185\u2026\u2026\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u201c\u6539\u9769\u5f00\u653e\u8fd9\u4e9b\u5e74\u6765\uff0c\u5012\u4e0d\u662f\u6211\u5728\u6279\u8bc4\u4e2d\u592e\u7684\u653f\u7b56\uff0c\u5e02\u573a\u624b\u6bb5\u3001\u7ecf\u6d4e\u6760\u6746\u8fd0\u7528\u8fd8\u4e0d\u591f\u7280\u5229\u2026\u2026\u201d</p><p>\u3000\u3000\u5f90\u5b66\u5e73\u8f7b\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u603b\u8981\u5faa\u5e8f\u6e10\u8fdb\u7684\uff0c\u8981\u505a\u7684\u4e8b\u60c5\u592a\u591a\uff0c\u8981\u80fd\u5728\u81ea\u5df1\u624b\u91cc\u5b8c\u6210\u4e00\u4e24\u4ef6\u4e8b\uff0c\u4e5f\u5c31\u4e0d\u7559\u9057\u61be\u4e86\u3002\u201d</p><p>\u3000\u3000\u8fd9\u8bdd\u91cc\u591a\u5c11\u900f\u9732\u51fa\u5bf9\u4ece\u4e1c\u6d77\u7701\u957f\u4efb\u4e0a\u79bb\u5f00\u6709\u4e9b\u4e0d\u7518\u5fc3\u7684\u610f\u5473\u3002</p><p>\u3000\u3000\u5f20\u606a\u80fd\u770b\u5230\u5f90\u5b66\u5e73\u4e5d\u516b\u5e74\u540e\u590d\u51fa\u6709\u5f88\u5927\u7684\u5e0c\u671b\uff0c\u4e5d\u516b\u5e74\uff0c\u5f90\u5b66\u5e73\u624d\u516d\u5341\u4e8c\u5c81\uff1b\u5173\u952e\u662f\u5f90\u5b66\u5e73\u4ece\u73b0\u5728\u8d77\u5c31\u8981\u5f00\u59cb\u4e3a\u4e5d\u516b\u5e74\u540e\u7684\u590d\u51fa\u505a\u51c6\u5907\uff0c\u653f\u6cbb\u4e0a\u3001\u5b66\u672f\u4e0a\u8981\u6d3b\u8dc3\uff0c\u4e0d\u80fd\u88ab\u4eba\u7ed9\u9057\u5fd8\u4e86\u3002</p><p>\u3000\u3000\u65b0\u5e74\u7684\u949f\u58f0\u54cd\u8fc7\uff0c\u542c\u5230\u5ba4\u5916\u6301\u7eed\u4e0d\u7edd\u7684\u7206\u7af9\u58f0\uff0c\u70df\u82b1\u5f69\u5149\u6620\u5728\u7a97\u6237\u4e0a\uff0c\u7535\u89c6\u91cc\u7684\u7206\u7af9\u70df\u82b1\u4e5f\u662f\u70ed\u95f9\u975e\u51e1\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u56fd\u5185\u603b\u4e60\u60ef\u62ff\u6625\u8282\u5f53\u6210\u65b0\u7684\u4e00\u5e74\u7684\u5f00\u59cb\uff0c\u8fd9\u4e00\u65e5\uff0c\u5176\u5b9e\u5df2\u7ecf\u662f\u4e5d\u4e03\u5e74\u7684\u4e8c\u6708\u4e03\u65e5\u4e86\u3002</p><p>\u3000\u3000\u5f90\u5b66\u5e73\u867d\u7136\u4e0d\u5982\u5728\u4e1c\u6d77\u65f6\u98ce\u5149\uff0c\u4f46\u8fd8\u662f\u8eab\u5c45\u9ad8\u4f4d\uff0c\u5373\u4f7f\u5230\u56fd\u52a1\u9662\u7ecf\u7814\u4e2d\u5fc3\u8fd8\u4e0d\u5230\u4e00\u4e2a\u6708\u7684\u65f6\u95f4\uff0c\u767b\u95e8\u62dc\u8bbf\u7684\u4eba\u5012\u4e5f\u4e0d\u5c11\u3002\u4e14\u4e0d\u7ba1\u6536\u4e0b\u793c\u54c1\u4e4b\u540e\u5982\u4f55\u5904\u7f6e\u2014\u2014\u4e8b\u540e\u9000\u56de\u6216\u4e0a\u7f34\u5ec9\u653f\u8d26\u6237\u90fd\u597d\u2014\u2014\u5934\u75bc\u4e5f\u597d\u3001\u5acc\u9ebb\u70e6\u3001\u5acc\u5435\u95f9\u4e5f\u597d\uff0c\u603b\u4e0d\u80fd\u95ed\u95e8\u8c22\u5ba2\uff0c\u4e0d\u7136\u8981\u88ab\u90e8\u4e0b\u4e0e\u540c\u50da\u89c6\u4e3a\u4e0d\u8fd1\u4eba\u60c5\uff0c\u8981\u88ab\u5b64\u7acb\uff0c\u8fde\u5de5\u4f5c\u90fd\u96be\u987a\u5229\u5f00\u5c55\u3002</p><p>\u3000\u3000\u5e74\u521d\u4e00\uff0c\u5f20\u606a\u4e0e\u5510\u5a67\u5728\u5f90\u5b66\u5e73\u5bb6\u5403\u8fc7\u65e9\u996d\uff0c\u5c31\u968f\u6881\u6587\u6c5f\u3001\u738b\u5f69\u73b2\u53bb\u8bbe\u4e8e\u6e05\u534e\u56ed\u4e1c\u95e8\u897f\u738b\u5e84\u7684\u6606\u817e\u5728\u7ebf\u7684\u516c\u53f8\u3002\u65b0\u5e74\u521d\u59cb\uff0c\u53ea\u6709\u603b\u7ecf\u7406\u9a6c\u5411\u4e1c\u4e0e\u4e24\u540d\u6280\u672f\u4eba\u5458\u5728\u516c\u53f8\u503c\u73ed\uff0c\u5f20\u606a\u4f5c\u4e3a\u516c\u53f8\u7684\u5927\u80a1\u4e1c\uff0c\u4eba\u5230\u4e86\u5317\u4eac\uff0c\u603b\u4e0d\u80fd\u4e0d\u53bb\u6170\u95ee\u4e00\u4e0b\u3002</p><p>\u3000\u3000\u8d76\u5230\u6e05\u534e\u56ed\u4e1c\u95e8\u897f\u738b\u5e84\u5927\u53a6\u7684\u6606\u817e\u4fe1\u606f\u901a\u4fe1\u6709\u9650\u8d23\u4efb\u516c\u53f8\uff0c\u9a6c\u5411\u4e1c\u90a3\u5c0f\u5b50\u6b63\u811a\u6572\u5728\u529e\u516c\u684c\u4e0a\uff0c\u5f88\u6ca1\u6709\u5174\u8c61\u5728\u7172\u7535\u8bdd\u7ca5\uff0c\u770b\u89c1\u5f20\u606a\u3001\u6881\u6587\u6c5f\u63a8\u95e8\u8fdb\u6765\uff0c\u614c\u5fd9\u4e0b\u5dee\u70b9\u6454\u5012\uff1b\u8fd8\u597d\uff0c\u6ca1\u6709\u5728\u6d4f\u89c8\u56fd\u5916\u8272\u60c5\u7f51\u7ad9\u3002</p><p>\u3000\u3000\u4e92\u8054\u7f51\u5728\u56fd\u5185\u4ece\u4e5d\u4e94\u5e74\u521d\u5f00\u59cb\u8bd5\u8fd0\u884c\uff0c\u76f4\u5230\u53bb\u5e74\u521d\u79cb\uff0c\u624d\u6709\u701b\u6d77\u5a01\u6210\u4e3a\u56fd\u5185\u9996\u5bb6\u6b63\u5f0f\u63d0\u4f9b\u4e92\u8054\u7f51\u63a5\u5165\u670d\u52a1\u7684\u670d\u52a1\u4f9b\u5e94\u5546\u3002</p><p>\u3000\u3000\u6240\u8c13\u7684\u4e92\u8054\u7f51\u63a5\u5165\u670d\u52a1\uff0c\u56fd\u4eba\u6700\u65e9\u7684\u5370\u8c61\u5219\u662f169\u3001163\u62e8\u53f7\u4e0a\u7f51\uff0c\u7f51\u901f\u8d85\u6162\u3001\u8d39\u7528\u8fd8\u8d85\u8d35\u3002</p><p>\u3000\u3000\u95e8\u6237\u7f51\u7ad9\u7684\u9053\u8def\u66f4\u662f\u9519\u7efc\u590d\u6742\uff0c\u5f20\u606a\u5373\u4f7f\u6e05\u695a\u672a\u6765\u7684\u53d1\u5c55\u65b9\u5411\uff0c\u4f46\u662f\u8981\u4ece\u4e00\u5207\u90fd\u8fd8\u662f\u8352\u829c\u7684\u8346\u68d8\u4e2d\u5f00\u5288\u51fa\u4e00\u6761\u9053\u8def\u6765\uff0c\u4e5f\u7edd\u975e\u6613\u4e8b\uff1b\u5f20\u671d\u9633\u3001\u4e01\u78ca\u3001\u738b\u5fd7\u4e1c\u7b49\u4eba\u7684\u6210\u529f\uff0c\u9664\u4e86\u65f6\u4ee3\u6240\u8d50\u4e88\u7684\u5de8\u5927\u6954\u673a\u4e4b\u5916\uff0c\u4e5f\u7edd\u975e\u90fd\u662f\u5076\u7136\u56e0\u7d20\u3002</p><p>\u3000\u3000\u9a6c\u5411\u4e1c\u4f5c\u4e3a\u6606\u817e\u5728\u7ebf\u6267\u884c\u5b98\u3001\u9996\u5e2d\u67b6\u6784\u8bbe\u8ba1\u5e08\uff0c\u4e5f\u662f\u975e\u5e38\u7684\u51fa\u8272\u3002\u5f20\u606a\u3001\u6881\u6587\u6c5f\u4ed6\u4eec\u63a8\u95e8\u8fdb\u529e\u516c\u5ba4\u4e4b\u95f4\uff0c\u4ed6\u6070\u597d\u5728\u8ddf\u4e00\u4e2a\u4ed6\u5728\u701b\u6d77\u5a01\u7684\u540c\u5b66\u804a\u5929\uff1b\u4ed6\u5230\u5e95\u4e0e\u6295\u8d44\u4eba\u5f20\u606a\u4e4b\u95f4\u7b97\u4e0d\u4e0a\u5341\u5206\u7684\u719f\u6089\uff0c\u5c06\u7535\u8bdd\u653e\u4e0b\uff0c\u7a0d\u7a0d\u89e3\u91ca\u4e86\u4e00\u4e0b\u3002</p><p>\u3000\u3000\u4e5d\u4e03\u5e74\uff0c\u701b\u6d77\u5a01\u5728\u56fd\u5185\u4fe1\u606f\u901a\u8baf\u4ea7\u4e1a\u91cc\u626e\u6f14\u4e86\u201c\u4e92\u8054\u7f51\u4f20\u6559\u58eb\u201d\u7684\u89d2\u8272\uff0c\u5f71\u54cd\u529b\u6781\u9ad8\uff0c\u50cf\u6881\u6587\u6c5f\u4e4b\u524d\u5c31\u505a\u597d\u201c\u82e5\u4e0d\u80fd\u51fa\u56fd\u5c31\u5148\u53bb\u701b\u6d77\u5a01\u5de5\u4f5c\u201d\u7684\u6253\u7b97\uff1b\u5f20\u606a\u4e0e\u5510\u5a67\u4e0a\u5348\u4e5f\u6ca1\u6709\u5730\u65b9\u6253\u53d1\u65f6\u95f4\uff0c\u5c31\u5728\u516c\u53f8\u91cc\u4e0e\u9a6c\u5411\u4e1c\u3001\u6881\u6587\u6c5f\u804a\u5929\uff0c\u738b\u5f69\u73b2\u5219\u6559\u5510\u5a67\u5229\u7528\u516c\u53f8\u7684\u4e92\u8054\u7f51\u63a5\u9a73\u767b\u5f55\u56fd\u5916\u7f51\u7ad9\u3002</p><p>\u3000\u3000\u9a6c\u5411\u4e1c\u3001\u6881\u6587\u6c5f\u7b49\u4eba\uff0c\u5bf9\u701b\u6d77\u5a01\u90fd\u5341\u5206\u7684\u63a8\u5d07\uff0c\u800c\u4e14\u701b\u6d77\u5a01\u8fd9\u51e0\u5929\u5c31\u8981\u901a\u8fc7\u79df\u7528\u90ae\u7535\u90e8\u7684\u7ebf\u8def\uff0c\u901a\u8fc7DDN\u4e13\u7ebf\u548c\u5e7f\u7535\u536b\u661f\u4f20\u8f93\u5b9e\u73b0\u5168\u56fd\u8303\u56f4\u7684\u7f51\u7edc\u63a5\u5165\u670d\u52a1\uff08\u6b64\u524d\u53ea\u6709\u90ae\u7535\u90e8163\u901a\u4fe1\u7f51\u63d0\u9ad8\u7f51\u7edc\u63a5\u5165\u670d\u52a1\uff0c\u7b2c\u4e00\u4ee3\u7f51\u6c11\u5927\u591a\u901a\u8fc7163\u62e8\u53f7\u4e0a\u7f51\uff0c\u670d\u52a1\u8d28\u91cf\u8fd8\u76f8\u5f53\u7684\u5dee\u52b2\uff09\uff0c\u8fd9\u5bf9\u56fd\u5185\u8fd8\u5904\u4e8e\u521a\u521a\u8d77\u6b65\u7684\u4e92\u8054\u7f51\u4e8b\u4e1a\uff0c\u610f\u4e49\u662f\u975e\u540c\u5c0f\u53ef\u7684\uff0c\u7b80\u503c\u5c31\u662f\u4e92\u8054\u7f51\u8ff7\u96fe\u4e2d\u4eae\u8d77\u7684\u660e\u4eae\u706f\u5854\u3002</p><p>\u3000\u3000\u9a6c\u5411\u4e1c\u3001\u6881\u6587\u6c5f\u7b49\u4eba\u8c08\u8d77\u8fd9\u4e2a\u8bdd\u9898\u8d77\u6765\uff0c\u90fd\u5341\u5206\u5174\u594b\u3002</p><p>\u3000\u3000\u5f20\u606a\u5374\u6de1\u6de1\u7684\u8bf4\u9053\uff1a\u201c\u701b\u6d77\u5a01\u8fc8\u51fa\u7684\u8fd9\u4e00\u6b65\uff0c\u610f\u4e49\u91cd\u5927\uff0c\u5bf9\u56fd\u5185\u7684\u4e92\u8054\u7f51\u4ea7\u4e1a\u8d21\u732e\u4e5f\u5927\uff0c\u4f46\u662f\u5bf9\u701b\u6d77\u5a01\u81ea\u8eab\u5374\u662f\u81f4\u547d\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff0c\u201d\u9a6c\u5411\u4e1c\u4e5f\u9887\u4e3a\u4e0d\u89e3\uff0c\u201c\u8d62\u6d77\u5a01\u63d0\u51fa\u2018\u4e2d\u56fd\u4eba\u79bb\u4fe1\u606f\u9ad8\u901f\u8def\u8fd8\u6709\u591a\u8fdc\u2019\u7684\u53e3\u53f7\uff0c\u96be\u9053\u4e0d\u6b63\u5207\u5408\u56fd\u4eba\u5bf9\u4fe1\u606f\u4e0e\u77e5\u8bc6\u7684\u6e34\u671b\u5417\uff1f\u201d</p><p>\u3000\u3000\u9a6c\u5411\u4e1c\u867d\u7136\u8db3\u591f\u4f18\u79c0\u4e86\uff0c\u4f46\u5e76\u975e\u6ca1\u6709\u65f6\u4ee3\u7684\u5c40\u9650\u3002\u4ed6\u4eec\u8fd9\u4e00\u4ee3\u9700\u8981\u5728\u4fe1\u606f\u4ea7\u4e1a\u7684\u8ff7\u96fe\u4e0e\u8346\u68d8\u4e2d\u6478\u7d22\u6323\u624e\uff0c\u6ce8\u5b9a\u6700\u7ec8\u53ea\u6709\u5c11\u6570\u4eba\u80fd\u627e\u5230\u6b63\u786e\u7684\u65b9\u5411\u4e0e\u51fa\u8def\u3002\u6210\u529f\u7684\u4eba\u5e76\u4e0d\u662f\u6bd4\u5931\u8d25\u7684\u4eba\u66f4\u4f18\u79c0\u3001\u66f4\u6709\u773c\u5149\uff0c\u8fd0\u6c14\u6709\u65f6\u5019\u771f\u662f\u5f88\u91cd\u8981\u7684\u56e0\u7d20\u3002</p><p>\u3000\u3000\u597d\u5427\uff0c\u5bf9\u4e8e\u5f20\u606a\u8bf4\u6765\uff0c\u4e0d\u5b58\u5728\u65b9\u5411\u6027\u7684\u95ee\u9898\uff0c\u5f20\u606a\u81f3\u5c11\u53ef\u4ee5\u5728\u65b9\u5411\u6027\u4e0a\u7ed9\u4e88\u9a6c\u5411\u4e1c\u3001\u6881\u6587\u6c5f\u4ed6\u4eec\u8db3\u591f\u7684\u6307\u5bfc\uff0c\u4ed6\u4eec\u9700\u8981\u5728\u8fd9\u4e2a\u65b9\u5411\u4e4b\u4e0b\uff0c\u5c06\u4e8b\u4e1a\u7684\u6846\u67b6\u7ed9\u642d\u8bbe\u8d77\u6765\uff0c\u5e76\u4e14\u8981\u79ef\u7d2f\u8db3\u591f\u591a\u7684\u6280\u672f\u4e0e\u7ba1\u7406\u7ecf\u9a8c\u3002</p><p>\u3000\u3000\u5f20\u606a\u7ed9\u4ed6\u4eec\u5206\u6790\u701b\u6d77\u5a01\u5931\u8d25\u7684\u5173\u952e\u56e0\u7d20\u85cf\u5728\u4f55\u5904\uff0c\u8fd9\u5176\u5b9e\u5e76\u4e0d\u9700\u8981\u4ec0\u4e48\u9884\u8a00\uff0c\u4ed6\u8bf4\u9053\uff1a\u201c\u701b\u6d77\u5a01\u7684\u53e3\u53f7\u662f\u5f88\u9f13\u52a8\u4eba\u5fc3\uff0c\u4f46\u662f\u4e0d\u73b0\u5b9e\u3002\u8981\u6210\u4e3a\u7f51\u7edc\u63a5\u5165\u670d\u52a1\u5546\uff0c\u6709\u4e09\u4e2a\u6761\u4ef6\uff0c\u6709\u94b1\u3001\u6709\u6743\u3001\u8fd8\u8981\u6709\u7f51\u7edc\u63a5\u5165\u670d\u52a1\u7684\u7ebf\u8def\u3002\u76ee\u524d\u53ea\u6709\u90ae\u7535\u90e8\u80fd\u63d0\u4f9b\u7f51\u7edc\u63a5\u5165\u670d\u52a1\uff0c\u701b\u6d77\u5a01\u8981\u641e\u8fd9\u4e2a\uff0c\u5c31\u662f\u8ddf\u90ae\u7535\u90e8\u76f4\u63a5\u7ade\u4e89\u3002\u701b\u6d77\u5a01\u7684\u7ebf\u8def\u5927\u591a\u662f\u79df\u90ae\u7535\u90e8\u7684\uff0c\u4e1a\u52a1\u8fd8\u53d7\u90ae\u7535\u90e8\u7ba1\u8f96\uff0c\u4ed6\u4eec\u51ed\u4ec0\u4e48\u8ddf\u90ae\u7535\u90e8\u7ade\u4e89\uff1f\u8fd9\u5c31\u662f\u56fd\u5185\u7684\u73b0\u5b9e\uff0c\u5341\u5e74\u5185\uff0c\u8981\u8ddf\u56fd\u5bb6\u5784\u65ad\u90e8\u95e8\u76f4\u63a5\u7ade\u4e89\u5c31\u662f\u5962\u60f3\uff01\u5c31\u7b97\u6211\u4eec\u4e13\u5fc3\u505aICP\u670d\u52a1\u5546\uff0c\u8fd8\u8981\u5343\u65b9\u767e\u8ba1\u8ddf\u90ae\u7535\u90e8\u6253\u597d\u5173\u7cfb\uff0c\u628a\u4ed6\u4eec\u5f53\u7237\u7237\u4f9b\u7740\uff0c\u800c\u4e14\u4eca\u540e\u5341\u5e74\u4e4b\u5185\uff0c\u6211\u4eec\u8981\u6301\u7eed\u4e0d\u61c8\u7684\u63d0\u4f9b\u514d\u8d39\u7684\u7f51\u7edc\u670d\u52a1\uff0c\u4e0d\u8981\u6709\u4ece\u4e2d\u76f4\u63a5\u8d62\u5229\u7684\u5962\u60f3\u2026\u2026\u201d</p><p>\u3000\u3000\u95e8\u6237\u7f51\u7ad9\u5c31\u662f\u4e00\u4e2a\u70e7\u94b1\u7684\u73a9\u827a\u513f\uff0c\u6ca1\u6709\u5341\u4ebf\u516b\u4ebf\u7684\u94b1\u70e7\u8fdb\u53bb\uff0c\u505a\u4e0d\u8d77\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0d\u4f1a\u5728\u4e24\u4e09\u5e74\u95f4\u6301\u7eed\u4e0d\u65ad\u5f80\u91cc\u9762\u7838\u8fd9\u4e48\u591a\u94b1\uff0c\u6606\u817e\u5728\u7ebf\u8981\u505a\u5927\u505a\u5f3a\uff0c\u5411\u6d77\u5916\u98ce\u6295\u878d\u8d44\u90a3\u662f\u4e00\u5b9a\u7684\u3002\u4e5f\u5fc5\u987b\u5411\u6d77\u5916\u98ce\u6295\u878d\u8d44\uff0c\u8fd9\u4e5f\u662f\u4e4b\u540e\u65e0\u8bba\u662f\u5411\u6d77\u5916\u6295\u8d44\u673a\u6784\u8f6c\u8ba9\u90e8\u5206\u80a1\u6743\u8fd8\u662f\u516c\u5f00\u4e0a\u5e02\u5957\u73b0\u7684\u5fc5\u8981\u6761\u4ef6\uff0c\u90a3\u65f6\u624d\u80fd\u5b9e\u73b0\u6295\u8d44\u76c8\u5229\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u5f20\u606a\u6295\u8d44\u6606\u817e\u5728\u7ebf\uff0c\u5e76\u975e\u770b\u91cd\u4e09\u56db\u5e74\u4e4b\u540e\u5341\u500d\u6216\u8005\u6570\u5341\u500d\u7684\u6295\u8d44\u6536\u76ca\uff0c\u800c\u662f\u770b\u5230\u4e92\u8054\u7f51\u5728\u4f20\u7edf\u5a92\u4f53\u4e4b\u5916\u6700\u4e3b\u8981\u7684\u65b0\u5174\u5a92\u4f53\uff0c\u800c\u4e14\u8fd9\u4e2a\u65b0\u5174\u5a92\u4f53\u7684\u5f71\u54cd\u529b\u4f1a\u8d8a\u6765\u8d8a\u91cd\u8981\uff0c\u4e5f\u6ce8\u5b9a\u4f1a\u8d85\u8d8a\u4f20\u7edf\u7684\u5a92\u4f53\u6e20\u9053\uff0c\u800c\u4e14\u662f\u53d7\u5b98\u65b9\u63a7\u5236\u4e0e\u7ea6\u675f\u529b\u6700\u5c0f\u7684\u5a92\u4f53\u6e20\u9053\u3002</p><p>\u3000\u3000\u5728\u5f53\u4eca\u4e16\u754c\uff0c\u5a92\u4f53\u6240\u8fdb\u884c\u7684\u8206\u8bba\u758f\u5bfc\u6709\u7740\u6f5c\u5728\u7684\u3001\u5e7f\u6cdb\u7684\u5f71\u54cd\u529b\uff1b\u5e7f\u6cdb\u7684\u5f71\u54cd\u529b\u5373\u610f\u5473\u7740\u5e7f\u6cdb\u7684\u6743\u529b\uff0c\u8fd9\u624d\u662f\u5f20\u606a\u6240\u5962\u671b\u7684\u4e1c\u897f\u3002</p></article>"}}}]);
