(window.webpackJsonp=window.webpackJsonp||[]).push([[510],{533:function(p){p.exports={chapterTitle:null,title:"\u7b2c510\u7ae0 \u56fd\u4ea7\u624b\u673a",content:"<article><h2>\u7b2c510\u7ae0 \u56fd\u4ea7\u624b\u673a</h2><p>\u3000\u3000\u5fb7\u5dde\u4eea\u5668\u5728\u82af\u7247\u96c6\u6210\u4e0e\u529f\u8017\u65b9\u9762\u7684\u6280\u672f\u6210\u5c31\u662f\u4ee4\u4eba\u53f9\u4e3a\u89c2\u6b62\u7684\uff0c\u5f20\u606a\u5f53\u5373\u8868\u793a\u5fb7\u5dde\u4eea\u5668\u82e5\u662f\u5728\u8fd9\u65b9\u9762\u6709\u4e9b\u81ea\u4ee5\u4e3a\u8fc7\u65f6\u3001\u4f46\u662f\u76f8\u5bf9\u7b2c\u4e09\u4e16\u754c\u56fd\u5bb6\u4e0e\u5730\u533a\u6765\u8bf4\u8fd8\u76f8\u5f53\u5148\u8fdb\u7684\u6280\u672f\uff0c\u9526\u6e56\u90fd\u53ef\u4ee5\u8003\u8651\u638f\u94b1\u8d2d\u4e70\u3002</p><p>\u3000\u3000\u5728ESS\u6b63\u5f0f\u7ed9\u9526\u6e56\u5e76\u8d2d\u4e4b\u540e\uff0c\u6210\u6c49\u748b\u3001\u8096\u664b\u6210\u52a0\u76df\u9526\u6e56\uff0c\u5c06\u8fd1\u4e00\u4e2a\u6708\u7684\u65f6\u95f4\uff0c\u4ed6\u4eec\u4e24\u4eba\u5c31\u5c06\u9526\u6e56\u8584\u5f31\u7684\u6280\u672f\u5e95\u5b50\u6478\u900f\u4e86\uff0c\u9526\u6e56\u5f00\u51fa\u6765\u7684MP3\u539f\u578b\u673a\uff0c\u7528\u4e24\u679a\u4e03\u53f7\u7535\u6c60\u53ea\u80fd\u575a\u6301\u4e00\u4e2a\u534a\u5c0f\u65f6\u7684\u64ad\u653e\u65f6\u95f4\uff0c\u8fd9\u5f53\u7136\u4e0d\u4ec5\u4ec5\u662f\u7535\u6c60\u8d28\u91cf\u7684\u95ee\u9898\u3002</p><p>\u3000\u3000\u5373\u4f7f\u7528\u4e0a\u65e0\u8bb0\u5fc6\u6548\u5e94\u3001\u5bb9\u91cf\u4f53\u79ef\u6bd4\u6700\u5927\u7684\u9502\u7535\u2014\u2014\u4e5d\u4e03\u5e74\u91c7\u7528\u9502\u79bb\u5b50\u7535\u6c60\u6210\u672c\u4f1a\u5f88\u9ad8\uff0c\u53cd\u6b63MP3\u4f1a\u5148\u5728\u65e5\u97e9\u6b27\u7f8e\u5e02\u573a\u63a8\u5e7f\uff0c\u6210\u672c\u9ad8\u4e00\u4e9b\u5012\u4e5f\u65e0\u59a8\u2014\u2014\u64ad\u653e\u65f6\u95f4\u4e5f\u4e0d\u5c3d\u4eba\u610f\uff0c\u5f53\u7136\uff0c\u4e0d\u5c3d\u4eba\u610f\u4e4b\u5904\u4e5f\u8fdc\u4e0d\u6b62\u8fd9\u4e48\u4e00\u70b9\uff0c\u539f\u578b\u673a\u6280\u672f\u79bb\u5c06\u4ea7\u54c1\u63a8\u5411\u5e02\u573a\u7684\u6210\u719f\u6280\u672f\u8fd8\u6709\u76f8\u5f53\u8fdc\u7684\u8ddd\u79bb\u3002</p><p>\u3000\u3000\u9526\u6e56\u5e76\u8d2dESS\u4e4b\u540e\uff0c\u5c31\u8981\u5c06ESS\u5f53\u6210\u6280\u672f\u7814\u53d1\u7684\u4e3b\u529b\uff0c\u9664\u4e86\u82b1\u8d39\u4e00\u767e\u4e07\u7f8e\u91d1\u6536\u8d2d\u5dee\u4e0d\u591a\u5feb\u8981\u8d44\u4e0d\u62b5\u503a\u7684ESS\u4e4b\u5916\uff0c\u8fd8\u5c06\u9996\u6b21\u6ce8\u5165\u4e00\u5343\u4e07\u7f8e\u5143\u7684\u8d44\u91d1\u7528\u4e8e\u7814\u53d1\uff0c\u9526\u6e56\u8fd8\u4f1a\u5c06\u4e3b\u4efb\u5de5\u7a0b\u5e08\u4e01\u6625\u5efa\u9886\u5bfc\u7684\u6280\u672f\u653b\u5173\u5c0f\u7ec4\u5e76\u5165ESS\u2014\u2014\u8fd9\u4e2a\u6280\u672f\u653b\u5173\u5c0f\u7ec4\u6b64\u65f6\u51e0\u4e4e\u96c6\u4e2d\u4e86\u9526\u6e56\u8fd9\u6bb5\u65f6\u95f4\u4ee5\u6765\u7f51\u7f57\u5230\u7684\u6700\u4f18\u79c0\u7684\u6280\u672f\u9aa8\u5e72\u2014\u2014\u6210\u7acb\u9635\u5bb9\u66f4\u52a0\u5e9e\u5927\u7684\u6280\u672f\u653b\u5173\u5c0f\u7ec4\u3002</p><p>\u3000\u3000\u4f46\u662f\u8981\u5728\u534a\u5e74\u65f6\u95f4\u91cc\u8981\u5c06MP3\u6240\u9700\u8981\u7684\u5173\u952e\u6280\u672f\u505a\u5230\u76f8\u5bf9\u5b8c\u7f8e\uff0c\u6210\u6c49\u748b\u5934\u4e0a\u9876\u7740\u7684\u538b\u529b\u8fd8\u662f\u5de8\u5927\u7684\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0cMP3\u64ad\u653e\u5668\u8fd9\u6b3e\u4ea7\u54c1\u6700\u7ec8\u8981\u5b8c\u7f8e\u65e0\u7455\uff0c\u8fd8\u8981\u4ea7\u54c1\u8bbe\u8ba1\u7b49\u8bf8\u591a\u65b9\u9762\u7684\u52aa\u529b\u3002</p><p>\u3000\u3000\u9526\u6e56\u7684\u51b3\u5fc3\u4e5f\u662f\u5de8\u5927\u7684\uff0c\u9664\u4e86\u7845\u8c37\u7684ESS\u4e4b\u5916\uff0c\u9526\u6e56\u8fd8\u5c06\u5728\u65e5\u672c\u7b51\u6ce2\u5e02\u6210\u7acb\u4e00\u5bb6\u6280\u672f\u7814\u53d1\u5b9e\u9a8c\u57fa\u5730\uff0c\u8fd8\u5c06\u5728\u97e9\u56fd\u6c49\u57ce\u3001\u65e5\u672c\u4e1c\u4eac\u3001\u9999\u6e2f\u6210\u7acb\u4e09\u5bb6\u5de5\u7a0b\u5e94\u7528\u4e0e\u5de5\u4e1a\u8bbe\u8ba1\u4e2d\u5fc3\uff0c\u5305\u62ec\u5728\u6d77\u5dde\u7684\u9526\u6e56\u5de5\u7a0b\u5e94\u7528\u4e0e\u6280\u672f\u7814\u53d1\u672c\u90e8\uff0c\u9526\u6e56\u524d\u671f\u7684\u6280\u672f\u6269\u5f20\u5c31\u5305\u62ec\u4e09\u4e2a\u6280\u672f\u7814\u53d1\u4e2d\u5fc3\u3001\u56db\u4e2a\u5de5\u7a0b\u5e94\u7528\u7814\u53d1\u4e2d\u5fc3\u3002</p><p>\u3000\u3000\u652f\u6491\u9526\u6e56\u8fdb\u884c\u5982\u6b64\u89c4\u6a21\u6280\u672f\u6269\u5f20\u7684\u57fa\u7840\u5c31\u662f\u7231\u8fbe\u7535\u5b50\u6bcf\u4e2a\u6708\u9ad8\u8fbe\u8fd1\u4e00\u4ebf\u5143\u7684\u9ad8\u989d\u5229\u6da6\uff0c\u8fd8\u5c06\u5305\u62ec\u9526\u6e56\u4ece\u7b2c\u4e09\u4ee3\u89e3\u7801\u82af\u7247\u5e02\u573a\u4e2d\u5206\u5f97\u7684\u90a3\u4e00\u90e8\u5206\u5784\u65ad\u5229\u6da6\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u8bf4\u53ca\u6d88\u8d39\u7535\u5b50\u4ea7\u54c1\u7684\u529f\u8017\u4e0e\u4f7f\u7528\u65f6\u95f4\u95ee\u9898\uff0c\u5f20\u606a\u4e5f\u5f88\u6e05\u695a\uff0c\u65e5\u540e\u56fd\u4ea7\u624b\u673a\u6700\u521d\u8ba9\u4eba\u57a2\u75c5\u7684\u4e5f\u662f\u5f85\u673a\u65f6\u957f\u95ee\u9898\u3002</p><p>\u3000\u3000\u6d77\u5916\u5382\u5546\u751f\u4ea7\u51fa\u6765\u7684\u624b\u673a\u6700\u957f\u80fd\u5f85\u673a\u4e00\u4e2a\u6708\uff0c\u56fd\u4ea7\u624b\u673a\u90fd\u575a\u6301\u4e0d\u5230\u4e00\u663c\u591c\uff0c\u5982\u4f55\u80fd\u6d88\u8d39\u8005\u4e0d\u628a\u56fd\u4ea7\u624b\u673a\u5212\u5165\u4f4e\u6863\u8d27\u7684\u884c\u5217\uff1f\u4e0d\u4ec5\u4ec5\u624b\u673a\u5982\u6b64\uff0c\u5176\u4ed6\u8bf8\u591a\u6d88\u8d39\u7535\u5b50\u4ea7\u54c1\uff0c\u90fd\u5b58\u5728\u76f8\u5173\u7684\u6280\u672f\u95ee\u9898\u3002</p><p>\u3000\u3000\u8fd9\u5c31\u6d89\u53ca\u5230\u82af\u7247\u6a21\u5757\u96c6\u6210\u3001\u7535\u6e90\u529f\u8017\u7ba1\u7406\u7b49\u4e00\u7cfb\u5217\u590d\u6742\u7684\u6280\u672f\uff0c\u5f20\u606a\u4e0d\u6e05\u695a\u5177\u4f53\u7684\u6280\u672f\u6784\u67b6\uff0c\u4f46\u662f\u51c6\u786e\u65e0\u8bef\u7684\u77e5\u9053\u672a\u6765\u7535\u5b50\u4ea7\u54c1\u7684\u53d1\u5c55\u65b9\u5411\uff0c\u5177\u4f53\u7684\u6280\u672f\u95ee\u9898\u81ea\u7136\u8981\u4ea4\u7ed9\u4e13\u4e1a\u4eba\u58eb\u53bb\u5904\u7406\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u8fd9\u4e5f\u7531\u4e8e\u56fd\u5185\u5382\u5546\u7f3a\u4e4f\u4ea7\u54c1\u7814\u53d1\u7684\u80fd\u529b\uff0c\u4e0d\u8981\u8bf4\u6838\u5fc3\u7684\u6280\u672f\u4e86\uff0c\u5c31\u662f\u4ea7\u54c1\u8bbe\u8ba1\u90fd\u4e25\u91cd\u4f9d\u8d56\u4e8e\u56fd\u5916\u516c\u53f8\uff0c\u8fd9\u79cd\u51e0\u4e4e\u5b8c\u5168\u4f9d\u8d56\u6d77\u5916\u7535\u5b50\u5382\u5546\u4e0e\u8bbe\u8ba1\u516c\u53f8\u7684\u5c40\u9762\uff0c\u9020\u6210\u56fd\u4ea7\u7535\u5b50\u4ea7\u54c1\u540c\u8d28\u5316\u73b0\u8c61\u4e25\u91cd\uff0c\u5c31\u62ff\u65e5\u540e\u7684\u56fd\u4ea7\u624b\u673a\u4e3e\u4f8b\uff0c\u56fd\u5185\u5382\u5546\u7684\u624b\u673a\u6027\u80fd\u90fd\u5dee\u4e0d\u591a\uff0c\u6253\u5e7f\u544a\u505a\u5e02\u573a\u7684\u6c34\u5e73\u4e5f\u76f8\u5dee\u65e0\u51e0\uff0c\u6700\u540e\u7ade\u4e89\u7684\u624b\u6bb5\u5c31\u662f\u6bd4\u4ef7\u683c\u4e86\uff0c\u4ef7\u683c\u6218\u4e00\u5f00\u59cb\uff0c\u5229\u6da6\u7a7a\u95f4\u5c31\u4e0b\u964d\uff0c\u56fd\u5185\u5382\u5546\u4e3a\u4e86\u964d\u4f4e\u751f\u4ea7\u6210\u672c\u7ef4\u6301\u751f\u8ba1\uff0c\u53c8\u88ab\u8feb\u91c7\u8d2d\u4f4e\u8d28\u4f4e\u4ef7\u7684\u5143\u5668\u4ef6\u6765\uff0c\u53cd\u800c\u9677\u5165\u56fd\u4ea7\u7535\u5b50\u4ea7\u54c1\u8d8a\u6765\u8d8a\u4f4e\u6863\u7684\u602a\u5708\u3002</p><p>\u3000\u3000\u9526\u6e56\u80fd\u4e0d\u80fd\u5728\u56fd\u5185\u7535\u5b50\u5382\u5546\u4e2d\u72ec\u7ad6\u4e00\u5e1c\uff0c\u6446\u8131\u201c\u56fd\u4ea7\u8d27\u5c31\u662f\u4f4e\u6863\u4ea7\u54c1\u201d\u7684\u602a\u5708\uff0c\u8003\u9a8c\u4e5f\u662f\u5de8\u5927\u7684\u3002</p><p>\u3000\u3000\u4e3a\u4e86\u8fbe\u6210\u8fd9\u4e2a\u76ee\u6807\uff0c\u5f20\u606a\u4e0d\u4f1a\u8ba9\u9526\u6e56\u6d89\u8db3\u592a\u591a\u7684\u4ea7\u4e1a\uff0c\u4e0d\u4f1a\u8ba9\u8d44\u91d1\u4ece\u6280\u672f\u6295\u5165\u4e0a\u5206\u6563\u51fa\u53bb\uff0c\u5f53\u521d\u5c06\u4e16\u7eaa\u9910\u996e\u91cc\u7684\u80a1\u4efd\u534a\u5356\u534a\u9001\u7684\u7ed9\u4e86\u5c0f\u53d4\u3001\u5c06\u4e39\u4e95\u5df7\u7684\u7269\u4e1a\u6089\u6570\u8f6c\u5230\u8bb8\u601d\u540d\u4e0b\u3001\u4e16\u7eaa\u9526\u6e56\u4e5f\u7edd\u4e0d\u76f4\u63a5\u53c2\u4e0e\u3001\u4e09\u756a\u6570\u6b21\u62d2\u7edd\u53f6\u5efa\u658c\u9080\u8bf7\u53c2\u80a1\u76db\u946b\u7535\u5668\u7684\u8bf1\u60d1\uff0c\u76ee\u7684\u5c31\u5728\u4e8e\u6b64\uff0c\u8981\u5c06\u80fd\u591f\u7b79\u96c6\u5230\u7684\u8d44\u91d1\u4e2d\u7684\u7edd\u5927\u591a\u6570\u90fd\u96c6\u4e2d\u5728\u7535\u5b50\u4ea7\u4e1a\u91cc\uff0c\u751a\u81f3\u5728\u7535\u5b50\u4ea7\u4e1a3C\u6218\u7565\u4e2d\uff083C\u6307\u8ba1\u7b97\u673a\u3001\u901a\u8baf\u3001\u6d88\u8d39\u7535\u5b50\u4ea7\u54c1\uff09\uff0c\u4e5f\u53ea\u5173\u6ce8\u6d88\u8d39\u7535\u5b50\u4e00\u73af\u3002</p><p>\u3000\u3000MP3\u3001U\u76d8\u7684\u63a8\u5e7f\u6d89\u53ca\u5230\u8ba1\u7b97\u673a\u666e\u53ca\u7b49\u5173\u952e\u6027\u7684\u65e0\u6cd5\u7ed5\u8fc7\u7684\u5e02\u573a\u56e0\u7d20\uff0c\u6240\u4ee5\u524d\u671f\u53ea\u80fd\u5728\u6b27\u7f8e\u65e5\u97e9\u7b49\u53d1\u8fbe\u56fd\u5bb6\u4e0e\u5730\u533a\u63a8\u5e7f\uff0c\u5c5e\u4e8e\u6d77\u5916\u8fd0\u8425\u90e8\u7684\u8d23\u804c\uff0c\u4f46\u662f\u5728\u56fd\u5185\u5e02\u573a\u4ee5\u53ca\u4e1c\u5357\u4e9a\u65b0\u5174\u5e02\u573a\uff0c\u4e00\u65e6\u5c06\u789f\u673a\u4e1a\u52a1\u6253\u5305\u51fa\u552e\u7ed9\u5609\u4fe1\u5b9e\u4e1a\u4e4b\u540e\uff0c\u9526\u6e56\u5c31\u4f1a\u51fa\u73b0\u4e1a\u52a1\u7a7a\u767d\u3002\u867d\u8bf4\u5e74\u540e\u5c31\u4f1a\u9646\u7eed\u4e0a\u9a6c\u7535\u5b50\u8bcd\u5178\u3001\u638c\u4e0a\u5b66\u4e60\u673a\u7b49\u9879\u76ee\uff0c\u4f46\u662f\u8be5\u7c7b\u4ea7\u54c1\u7684\u5e02\u573a\u6bd4\u8f83\u5f71\u789f\u673a\u8d77\u6765\uff0c\u89c4\u6a21\u76f8\u5bf9\u5c0f\u5f97\u591a\uff0c\u4e5f\u4e0d\u4f1a\u662f\u9526\u6e56\u7684\u91cd\u70b9\uff1b\u9526\u6e56\u4e0b\u4e00\u6b65\u7684\u91cd\u70b9\u662f\u624b\u673a\u3002</p><p>\u3000\u3000\u4e5d\u4e03\u5e74\u521d\u59cb\uff0c\u624b\u673a\u5728\u56fd\u5185\u8fd8\u662f\u5962\u4f88\u54c1\uff0c\u7231\u8fbe\u4fe1\u3001\u6469\u6258\u7f57\u6258\u7b49\u624b\u673a\u7684\u4ef7\u683c\u8fd8\u5c45\u9ad8\u4e0d\u4e0b\uff0c\u90fd\u5728\u56db\u4e94\u5343\u5143\u4ee5\u4e0a\uff0c\u6700\u5173\u952e\u7684\u662f\u56fd\u5185\u7684\u901a\u8baf\u8d39\u7528\u66f4\u662f\u9ad8\u6602\u5230\u53ef\u6015\u7684\u7a0b\u5ea6\uff0c\u5f00\u53f7\u529e\u5361\u8d39\u7528\u624d\u521a\u521a\u964d\u5230\u4e24\u5343\u5143\u4ee5\u4e0b\uff0c\u4ee5\u540e\u7684\u6708\u79df\u8d39\u3001\u6bcf\u5206\u949f\u901a\u8bdd\u8d39\u4ee5\u53ca\u57ce\u5e02\u95f4\u7684\u6f2b\u6e38\u8d39\uff0c\u5c06\u56fd\u5185\u7edd\u5927\u591a\u6570\u7684\u6d88\u8d39\u8005\u90fd\u62d2\u4e4b\u624b\u673a\u6d88\u8d39\u7684\u95e8\u69db\u4e4b\u5916\u3002</p><p>\u3000\u3000\u7531\u4e8e\u79fb\u52a8\u901a\u8baf\u7684\u5784\u65ad\u5c40\u9762\u5728\u76f8\u5f53\u77ed\u7684\u65f6\u95f4\u5185\u4e0d\u53ef\u80fd\u7ed9\u6253\u7834\uff0c\u6240\u4ee5\u5916\u754c\u5bf9\u56fd\u5185\u7684\u624b\u673a\u5e02\u573a\u524d\u666f\u5e76\u4e0d\u62b1\u4e50\u89c2\u7684\u6001\u5ea6\uff0c\u5374\u4e0d\u77e5\u9053\u624b\u673a\u5e02\u573a\u6269\u5bb9\u6070\u6070\u662f\u4ece\u4e5d\u4e03\u5e74\u5f00\u59cb\u7684\uff0c\u800c\u4e14\u52bf\u6001\u76f8\u5f53\u7684\u731b\u70c8\uff0c\u4e5d\u4e03\u5e74\u5341\u6708\u4efd\u56fd\u5185\u79fb\u52a8\u7535\u8bdd\u5f00\u53f7\u7a81\u7834\u4e00\u5343\u4e07\u3002</p><p>\u3000\u3000\u5f20\u606a\u5bf9\u8fd9\u4e2a\u6570\u636e\u6709\u7740\u5927\u6982\u7684\u5370\u8c61\uff0c\u6bd4\u8f83\u4e5d\u516d\u5e74\u5e95\u7684\u6570\u636e\uff0c\u4e5f\u5c31\u662f\u8bf4\u4e5d\u4e03\u5e74\u4e00\u5230\u5341\u6708\u4efd\uff0c\u624b\u673a\u7528\u6237\u5c31\u8981\u5267\u70c8\u6269\u5bb9\u4e09\u500d\u4ee5\u4e0a\u3002</p><p>\u3000\u3000\u5f20\u606a\u65e0\u6cd5\u786e\u5b9a\u8fd9\u65f6\u5019\u6709\u6ca1\u6709\u56fd\u5185\u5382\u5546\u5bf9\u8fd9\u4e00\u5757\u5e02\u573a\u52a8\u8d77\u5fc3\u601d\uff0c\u4f46\u662f\u6709\u4e00\u70b9\u662f\u80af\u5b9a\u7684\uff0c\u56fd\u5185\u5382\u5546\u771f\u6b63\u7684\u884c\u52a8\u8fd8\u8981\u62d6\u5ef6\u5230\u4e24\u5e74\u4e4b\u540e\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u9526\u6e56\u4ee5\u201cIDEA\u7231\u8fbe\u201d\u7684\u540d\u4e49\u63d0\u524d\u5728\u5386\u53f2\u8fdb\u7a0b\u4e4b\u524d\u4e24\u5e74\u542f\u52a8\u56fd\u4ea7\u624b\u673a\u9879\u76ee\uff0c\u56fd\u5185\u5382\u5546\u4e5f\u4f1a\u8fc5\u901f\u8ddf\u4e0a\u7684\uff0c\u8fd9\u5c06\u8fdb\u4e00\u6b65\u6539\u53d8\u56fd\u5185\u624b\u673a\u5e02\u573a\u7684\u8fdb\u7a0b\u3002</p><p>\u3000\u3000\u5fb7\u5dde\u4eea\u5668\u662f\u5168\u7403\u6700\u5927\u7684\u624b\u673a\u82af\u7247\u5382\u5546\uff0c\u5f20\u606a\u6b64\u884c\u81ea\u7136\u4f1a\u91cd\u70b9\u5173\u6ce8\u5fb7\u5dde\u4eea\u5668\u7684\u624b\u673a\u82af\u7247\u90e8\u95e8\uff0c\u5fb7\u5dde\u4eea\u5668\u867d\u7136\u8fd8\u6ca1\u6709\u5c06\u4e2d\u56fd\u770b\u6210\u672a\u6765\u6700\u91cd\u8981\u7684\u5e02\u573a\u2014\u2014\u5b9e\u9645\u60c5\u51b5\u662f\u5fb7\u5dde\u4eea\u5668\u5c61\u5c61\u8f7b\u89c6\u65b0\u5174\u5e02\u573a\u7684\u6f5c\u529b\u2014\u2014\u4f46\u662f\u624b\u673a\u82af\u7247\u4e1a\u52a1\u53c8\u80fd\u53d1\u5c55\u4e00\u4e2a\u5ba2\u6237\uff0c\u81ea\u7136\u4e0d\u4f1a\u62d2\u4e4b\u95e8\u5916\u3002</p><p>\u3000\u3000\u4ece\u624b\u673a\u57fa\u5e26\u82af\u7247\u3001\u7535\u6e90\u7ba1\u7406\u82af\u7247\u7b49\u6838\u5fc3\u5143\u5668\u4ef6\u4ee5\u53ca\u624b\u673a\u7535\u6c60\u3001\u663e\u793a\u5c4f\u3001\u673a\u58f3\u6a21\u5177\u7b49\u96f6\u914d\u4ef6\uff0c\u4ee5\u53ca\u5b8c\u6574\u7684\u624b\u673a\u4ea7\u54c1\u8bbe\u8ba1\uff0c\u56fd\u9645\u4e0a\u90fd\u6709\u516c\u53f8\u63d0\u4f9b\u4e13\u4e1a\u7684\u670d\u52a1\u3002\u867d\u7136\u5fb7\u5dde\u4eea\u5668\u4e3b\u8981\u4f9b\u5e94\u624b\u673a\u57fa\u5e26\u82af\u7247\u4e0e\u7535\u6e90\u7ba1\u7406\u82af\u7247\uff0c\u4f46\u662f\u5176\u4ed6\u4e00\u4e9b\u5e94\u7528\u6280\u672f\u3001\u4ea7\u54c1\u6280\u672f\uff0c\u4e5f\u662f\u76f8\u5f53\u5168\u9762\uff0c\u5373\u4f7f\u6bd4\u8d77\u7231\u7acb\u4fe1\u3001\u8bfa\u57fa\u4e9a\u3001\u6469\u6258\u7f57\u62c9\u8fd9\u4e9b\u4e13\u4e1a\u516c\u53f8\u6709\u4e9b\u4e00\u5b9a\u7684\u5dee\u8ddd\uff0c\u4f46\u662f\u9526\u6e56\u8981\u505a\u624b\u673a\u9879\u76ee\uff0c\u603b\u8981\u6709\u4e00\u4e2a\u57fa\u7840\uff0c\u5fb7\u5dde\u4eea\u5668\u8fd8\u662f\u5f88\u9ad8\u5174\u5c06\u4e00\u4e9b\u5e94\u7528\u6280\u672f\u6253\u5305\u5356\u7ed9\u9526\u6e56\u7684\u3002</p><p>\u3000\u3000\u5728\u5b89\u5409\u4f2f\u7b49\u5fb7\u5dde\u4eea\u5668\u9ad8\u5c42\u4eba\u5458\u7684\u966a\u540c\u4e0b\uff0c\u5176\u5de5\u4f5c\u4eba\u5458\u975e\u5e38\u8be6\u7ec6\u7684\u5411\u5f20\u606a\u4e00\u884c\u4eba\u4ecb\u7ecd\u4e86\u5f53\u524d\u6700\u5148\u8fdb\u7684\u79fb\u52a8\u901a\u8baf\u6280\u672f\u4e0e\u672a\u6765\u7684\u79fb\u52a8\u901a\u8baf\u6280\u672f\u53d1\u5c55\u65b9\u5411\u3002</p><p>\u3000\u3000\u79fb\u52a8\u901a\u8bfb\u6280\u672f\u672a\u6765\u7684\u53d1\u5c55\u65b9\u5411\uff0c\u5f20\u606a\u81ea\u7136\u4e0d\u7528\u542c\u5fb7\u5dde\u4eea\u5668\u7684\u4e13\u4e1a\u4eba\u58eb\u5520\u53e8\uff0c\u81f3\u4e8e\u5f53\u524d\u79fb\u52a8\u901a\u8baf\u6280\u672f\u7684\u53d1\u5c55\u2014\u2014\u5f20\u606a\u60f3\u7740\u5c06\u9648\u4fe1\u751f\u63fd\u5165\u9ebe\u4e0b\uff0c\u4e13\u4e1a\u95ee\u9898\u5f53\u7136\u8981\u4ea4\u7ed9\u4e13\u4e1a\u4eba\u58eb\u5904\u7406\uff0c\u5f20\u606a\u4fbf\u76f4\u63a5\u900f\u9732\u6709\u610f\u5728\u9648\u4fe1\u751f\u4e0e\u5fb7\u5dde\u4eea\u5668\u7684\u8058\u7528\u534f\u8bae\u671f\u6ee1\u4e4b\u540e\u5c06\u5176\u8058\u5165\u9526\u6e56\u3002</p><p>\u3000\u3000\u5f20\u606a\u63d0\u4f9b\u7684\u8bf4\u8f9e\u662f\uff1a\u201c\u9526\u6e56\u7684\u76ee\u6807\u5c31\u662f\u8981\u505a\u5927\u4e2d\u534e\u533a\u6700\u5927\u7684\u6d88\u8d39\u7535\u5b50\u5546\uff0c\u5728\u8fd9\u4e2a\u8fc7\u7a0b\u4e2d\uff0c\u5fb7\u5dde\u4eea\u5668\u5bf9\u9526\u6e56\u7684\u4f5c\u7528\u8d8a\u6765\u8d8a\u5f3a\uff0c\u201d\u8a00\u5916\u4e4b\u610f\u5c31\u662f\u8bf4\u9526\u6e56\u5bf9\u5fb7\u5dde\u4eea\u5668\u7684\u6280\u672f\u8d44\u6e90\u4f9d\u8d56\u4f1a\u8d8a\u6765\u8d8a\u91cd\uff0c\u201c\u4e24\u5bb6\u516c\u53f8\u4e4b\u95f4\u7684\u5408\u4f5c\u4f1a\u8d8a\u6765\u8d8a\u5bc6\u5207\u201d\uff0c\u8fd9\u4e00\u70b9\u5012\u662f\u4e0d\u5047\uff0c\u73b0\u5728\u9526\u6e56\u4e0e\u5fb7\u5dde\u4eea\u5668\u8054\u5408\u65af\u9ad8\u67cf\u5206\u4eab\u5f71\u789f\u673a\u7684\u89e3\u7801\u82af\u7247\u5e02\u573a\uff0c\u89e3\u7801\u82af\u7247\u4e5d\u516d\u5e74\u7ed9\u5fb7\u5dde\u4eea\u5668\u8d21\u732e6\uff05\u7684\u51c0\u5229\u6da6\uff0c\u9884\u8ba1\u4e5d\u4e03\u5e74\u4f1a\u63d0\u9ad8\u52307\uff05\uff0c\u8fd9\u4e2a\u5c40\u9762\u8fd8\u662f\u7ef4\u6301\u51e0\u5e74\uff0c\u4e4b\u5916\u8fd8\u5305\u62ec\u5f71\u789f\u673a\u673a\u82af\u9879\u76ee\u4e0a\u7684\u5408\u4f5c\uff0c\u9884\u8ba1\u80fd\u7ed9\u5fb7\u5dde\u4eea\u5668\u8d21\u732e\u7ea60.5\uff05\u7684\u51c0\u5229\u6da6\uff0c\u5fb7\u5dde\u4eea\u5668\u7ed9\u9526\u6e56\u4ee3\u5de5\u751f\u4ea7\u789f\u673a\u7cfb\u7edf\u63a7\u5236\u7b49\u4ea7\u54c1\u82af\u7247\u6bcf\u5e74\u8fd8\u6709\u6570\u767e\u4e07\u7f8e\u5143\u7684\u5229\u6da6\u3002\u9526\u6e56\u7684\u89c4\u6a21\u505a\u5f97\u8d8a\u5927\uff0c\u4f5c\u4e3a\u4e0a\u6e38\u5143\u5668\u4ef6\u4e0e\u5e94\u7528\u6280\u672f\u7684\u4f9b\u5e94\u5546\uff0c\u5fb7\u5dde\u4eea\u5668\u4ece\u9526\u6e56\u83b7\u5f97\u7684\u5229\u76ca\u4e5f\u5c31\u8d8a\u591a\uff0c\u8fd9\u662f\u52ff\u5eb8\u7f6e\u7591\u7684\uff0c\u201c\u9700\u8981\u4e00\u4e2a\u5173\u952e\u7684\u4eba\u7269\u6765\u52a0\u5f3a\u4e24\u5bb6\u516c\u53f8\u4e4b\u95f4\u7684\u8054\u7cfb\u201d\uff0c\u5f20\u606a\u5fd8\u8bb0\u539f\u5148\u6cf0\u5fd2\u5149\u7535\u7814\u7a76\u9662\u7684\u9996\u5e2d\u4e3b\u4efb\u5de5\u7a0b\u5e08\u4e01\u78ca\u5b9e\u9645\u5df2\u7ecf\u7ed9\u4ed6\u6316\u8fc7\u53bb\u4e86\uff0c\u201c\u7279\u522b\u662f\u8fd1\u671f\u9526\u6e56\u5e0c\u671b\u4ece\u5fb7\u5dde\u4eea\u5668\u5f15\u8fdb\u534a\u5bfc\u4f53\u5c01\u88c5\u3001\u6210\u54c1\u6d4b\u8bd5\u4e0e\u79fb\u52a8\u7535\u8bdd\u4e24\u4e2a\u9879\u76ee\uff0c\u9700\u8981\u4e00\u4e2a\u719f\u6089\u7684\u4eba\u6765\u5c3d\u5feb\u63a8\u8fdb\u9879\u76ee\u7684\u8fdb\u7a0b\uff0c\u624d\u7b26\u5408\u4e24\u5bb6\u516c\u53f8\u7684\u5171\u540c\u5229\u76ca\u201d\u3002</p></article>"}}}]);
