(window.webpackJsonp=window.webpackJsonp||[]).push([[511],{533:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u56db\u5341\u4e09\u8282 \u6311\u6218",content:"<article><h2>\u7b2c\u56db\u5341\u4e09\u8282 \u6311\u6218</h2><p>\u7a81\u7136\u53d1\u751f\u7684\u53d8\u6545\uff0c\u743c\u6069\u548c\u51db\u90fd\u8fd8\u6ca1\u660e\u767d\u8fc7\u6765\u662f\u600e\u4e48\u4e00\u56de\u4e8b\uff0c\u53ea\u89c1\u6885\u83f2\u65af\u7684\u94f6\u5251\u7834\u7a7a\u98de\u65a9\uff0c\u5251\u8eab\u4e0a\u5723\u5149\u7efd\u653e\uff0c\u4eff\u4f5b\u4e00\u8f6e\u70bd\u65e5\u649e\u5165\u6811\u6797\u6df1\u5904\u3002\u51c4\u5389\u77ed\u4fc3\u7684\u60e8\u53eb\u58f0\u4ece\u4e2d\u4f20\u51fa\uff0c\u7136\u540e\u50cf\u88ab\u5361\u4f4f\u8116\u5b50\u822c\u560e\u7136\u800c\u6b62\uff0c\u5468\u56f4\u518d\u5ea6\u5f52\u4e8e\u6c89\u5bc2\u3002</p><p>\u201c\u6709\u654c\u4eba\u5417\uff0c\u827e\u5f25\u8587\uff1f\u201d\u51db\u8bf4\uff0c\u6307\u5c16\u5df2\u7ecf\u631f\u4f4f\u4e86\u4e00\u679a\u7ea2\u5b9d\u77f3\uff0c\u968f\u65f6\u51c6\u5907\u63b7\u51fa\u3002</p><p>\u6885\u83f2\u65af\u4e0d\u7b54\uff0c\u5f84\u76f4\u671d\u6811\u6797\u4e2d\u8d70\u53bb\uff0c\u743c\u6069\u548c\u51db\u8fde\u5fd9\u8ddf\u4e0a\u3002\u8d70\u4e86\u5341\u6765\u6b65\uff0c\u4ed6\u4eec\u770b\u89c1\u4e86\u94f6\u5251\uff0c\u5b83\u6b63\u50cf\u4e00\u6839\u957f\u9489\uff0c\u7a7f\u900f\u4e00\u4e2a\u7070\u8863\u4eba\u7684\u80f8\u819b\uff0c\u5c06\u4ed6\u6b7b\u6b7b\u9489\u5728\u80cc\u540e\u7684\u6811\u5e72\u4e0a\u3002\u6b7b\u8005\u770b\u4e0a\u53bb\u7ea6\u56db\u5341\u4f59\u5c81\uff0c\u76f8\u8c8c\u5bfb\u5e38\uff0c\u8138\u4e0a\u76b1\u7eb9\u5f88\u6df1\uff0c\u4eff\u4f5b\u9053\u9053\u6c9f\u58d1\uff0c\u7741\u5f97\u5927\u5927\u7684\u773c\u775b\u7070\u6697\u65e0\u795e\uff0c\u4ece\u670d\u9970\u4e0a\u6765\u5224\u65ad\uff0c\u4eff\u4f5b\u53ea\u662f\u4e2a\u5c45\u4f4f\u5728\u9644\u8fd1\u7684\u666e\u901a\u6751\u6c11\uff0c\u5e76\u975e\u9884\u6599\u4e2d\u7684\u5f3a\u654c\u3002</p><p>\u5947\u602a\u3002</p><p>\u770b\u8d77\u6765\u50cf\u662f\u8bef\u6740\u4e86\u4eba\uff0c\u4f46\u743c\u6069\u60f3\u90fd\u672a\u60f3\u5c31\u5426\u5b9a\u4e86\u8fd9\u79cd\u53ef\u80fd\u6027\uff0c\u6885\u83f2\u65af\u7edd\u975e\u90a3\u79cd\u5192\u5192\u5931\u5931\u7684\u5973\u5b69\u5b50\uff0c\u4e0d\u53ef\u80fd\u80e1\u4e71\u51fa\u624b\u3002\u5979\u65e2\u7136\u4e00\u5251\u65a9\u6740\u4e86\u6b64\u4eba\uff0c\u90a3\u4e48\u6b64\u4eba\u4fbf\u662f\u654c\u4eba\u65e0\u7591\uff0c\u81f3\u4e8e\u4e3a\u4f55\u4f1a\u662f\u8fd9\u79cd\u6beb\u4e0d\u8d77\u773c\u7684\u6a21\u6837\u2026\u2026\u5f88\u663e\u7136\uff0c\u8fd9\u8bf4\u660e\u5bf9\u65b9\u5f88\u72e1\u733e\uff0c\u5f88\u64c5\u957f\u4f2a\u88c5\uff0c\u4ec5\u6b64\u800c\u5df2\u3002</p><p>\u7136\u800c\u4ed6\u8fd9\u6b21\u4f3c\u4e4e\u731c\u9519\u4e86\u3002</p><p>\u201c\u5361\u90a6\u5927\u53d4\uff1f\u201d\u6885\u83f2\u65af\u8f7b\u547c\u4e00\u58f0\uff0c\u8138\u4e0a\u5fae\u5fae\u53d8\u8272\uff0c\u5fc3\u4e2d\u4e00\u65f6\u95f4\u4e5f\u662f\u60ca\u7591\u4e0d\u5b9a\u3002\u5979\u521a\u624d\u6b63\u51c6\u5907\u5750\u4e0b\u4f11\u606f\uff0c\u7a81\u7136\u6709\u4e00\u80a1\u5de8\u5927\u7684\u5a01\u80c1\u611f\u81ea\u8fd9\u4e2a\u65b9\u5411\u51f6\u731b\u8feb\u6765\uff0c\u5176\u4e2d\u8574\u542b\u7740\u65e0\u6bd4\u6e05\u6670\u7684\u4ea1\u7075\u6c14\u606f\uff0c\u5206\u660e\u662f\u6709\u67d0\u4e2a\u5f3a\u529b\u7684\u4ea1\u7075\u602a\u7269\u2014\u2014\u6216\u8005\u9ad8\u9636\u4ea1\u7075\u5e08\u6f5c\u4f0f\u5728\u6811\u6797\u4e2d\uff0c\u5373\u5c06\u53d1\u52a8\u5077\u88ad\u7684\u5148\u5146\uff0c\u6240\u4ee5\u5979\u624d\u4f1a\u4e0d\u5047\u601d\u7d22\u5730\u4e00\u5251\u65a9\u51fa\uff0c\u5148\u4e0b\u624b\u4e3a\u5f3a\u3002\u50cf\u5979\u8fd9\u79cd\u8eab\u7ecf\u767e\u6218\u7684\u6b66\u8005\uff0c\u81ea\u6709\u4e00\u79cd\u751f\u6b7b\u8fb9\u9645\u4e2d\u953b\u70bc\u51fa\u6765\u7684\u654f\u9510\u76f4\u89c9\uff0c\u5bf9\u5371\u9669\u548c\u654c\u610f\u6781\u5176\u654f\u611f\uff0c\u66f4\u4f55\u51b5\u5982\u4eca\u5df2\u7ecf\u6e10\u6e10\u6062\u590d\u4e86\u5723\u6b66\u58eb\u7684\u80fd\u529b\uff0c\u4e0d\u53ef\u80fd\u8fde\u4ea1\u7075\u90a3\u79cd\u7279\u6709\u7684\u9ed1\u6697\u6c14\u606f\u90fd\u4f1a\u611f\u5e94\u9519\u3002\u4f46\u773c\u524d\u8fd9\u4f4d\u88ab\u7a7f\u900f\u80f8\u819b\u7684\u6b7b\u8005\uff0c\u5374\u53ea\u662f\u4e2a\u4f4f\u5728\u86c7\u708e\u5c71\u8fb9\u7684\u5bfb\u5e38\u6751\u6c11\uff0c\u800c\u4e14\u8fd8\u7b97\u5979\u7684\u719f\u4eba\uff0c\u513f\u5b50\u5728\u7279\u52a0\u5c14\u9547\u4e0a\u5f00\u4e86\u4e2a\u5c0f\u5e97\u94fa\uff0c\u5e73\u5e38\u78b0\u89c1\u90fd\u6253\u62db\u547c\u7684\uff0c\u6b64\u65f6\u88ab\u9489\u5728\u6811\u4e0a\uff0c\u4e5f\u5c31\u662f\u4e2a\u6807\u51c6\u7684\u5c38\u4f53\uff0c\u548c\u4ec0\u4e48\u4ea1\u7075\u602a\u7269\u3001\u4ea1\u7075\u5e08\u90fd\u626f\u4e0d\u4e0a\u534a\u70b9\u5173\u7cfb\u3002</p><p>\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f</p><p>\u6885\u83f2\u65af\u4e00\u8a00\u4e0d\u53d1\u5730\u8d70\u4e0a\u524d\uff0c\u62d4\u51fa\u94f6\u5251\uff0c\u5931\u53bb\u652f\u6491\u7684\u5c38\u4f53\u6389\u843d\u5728\u5730\uff0c\u6eda\u70eb\u7684\u9c9c\u8840\u4ece\u4f24\u53e3\u4e2d\u55b7\u6e85\u51fa\u6765\uff0c\u663e\u793a\u6b7b\u8005\u521a\u521a\u65ad\u6c14\u7684\u4e8b\u5b9e\u3002\u5c11\u5973\u6301\u5251\u659c\u659c\u6307\u5730\uff0c\u51e0\u6ef4\u8840\u73e0\u987a\u7740\u5251\u810a\u6eda\u843d\u4e0b\u6765\uff0c\u6e17\u5165\u6ce5\u571f\u4e2d\uff0c\u5979\u9759\u9759\u7ad9\u7acb\u7740\uff0c\u7eb9\u4e1d\u4e0d\u52a8\uff0c\u4eff\u4f5b\u96d5\u50cf\uff0c\u7136\u540e\u731b\u7136\u4f4e\u559d\u4e00\u58f0\uff0c\u7167\u7740\u9762\u524d\u7684\u6811\u5e72\u4e00\u5251\u659c\u5288\u4e0b\u6765\u3002</p><p>\u5251\u5149\u4eff\u4f5b\u94f6\u8272\u95ea\u7535\uff0c\u707f\u70c2\u5730\u5212\u7834\u957f\u7a7a\uff0c\u53ea\u542c\u5f97\u5693\u5730\u4e00\u58f0\u8f7b\u54cd\uff0c\u90a3\u9897\u7c97\u582a\u5408\u62b1\u7684\u5927\u6811\u88ab\u6885\u83f2\u65af\u4e00\u5251\u5288\u6210\u4e86\u4e24\u6bb5\u3002\u4ead\u4ead\u82e5\u76d6\u7684\u6811\u51a0\u8f70\u7136\u5012\u4e0b\uff0c\u6e85\u8d77\u6f2b\u5929\u7684\u70df\u5c18\u788e\u6ce5\uff0c\u4e00\u9053\u5168\u8eab\u8426\u7ed5\u7740\u6de1\u6de1\u9ed1\u8272\u96fe\u6c14\u7684\u4eba\u5f71\u8fc5\u6377\u65e0\u6bd4\u5730\u4ece\u4e2d\u7a9c\u4e86\u51fa\u6765\u3002\u51db\u65e9\u6709\u51c6\u5907\uff0c\u7ea2\u5b9d\u77f3\u6296\u624b\u5f39\u51fa\uff0c\u201c\u5657\u201d\u5730\u5728\u7a7a\u4e2d\u5316\u4f5c\u4e5d\u9053\u718a\u718a\u71c3\u70e7\u7684\u706b\u7130\u7bad\u77e2\uff0c\u8fde\u73af\u4ea4\u9519\u6fc0\u5c04\uff0c\u5b8c\u7f8e\u5730\u5c01\u6b7b\u4e86\u9ed1\u5f71\u6240\u6709\u7684\u53bb\u8def\u3002</p><p>\u51db\u7684\u5b9d\u77f3\u9b54\u6cd5\u6e90\u4e8e\u9f99\u8109\u8840\u7edf\uff0c\u66f4\u591a\u662f\u4e00\u79cd\u5929\u8d4b\u800c\u975e\u80fd\u529b\uff0c\u81ea\u4ece\u5f97\u5230\u9f99\u795e\u8840\uff0c\u5b8c\u6210\u201c\u9f99\u5316\u201d\u540e\uff0c\u5979\u7684\u9020\u8be3\u660e\u663e\u53c8\u4e0a\u4e86\u65b0\u53f0\u9636\u3002\u8fd9\u4e00\u624b\u5e72\u8106\u5229\u843d\uff0c\u6362\u5728\u4ee5\u524d\u662f\u7edd\u5bf9\u6ca1\u6cd5\u505a\u5230\u8fd9\u6837\u6f02\u4eae\u7684\u3002\u773c\u770b\u5c31\u8981\u88ab\u5c04\u4e2d\uff0c\u9ed1\u5f71\u9661\u7136\u8eab\u5f62\u4e00\u77ee\uff0c\u5de6\u624b\u6309\u5730\u6b62\u4f4f\u539f\u672c\u7b14\u76f4\u7684\u524d\u51b2\u4e4b\u52bf\uff0c\u968f\u5373\u4eff\u4f5b\u72f8\u732b\u822c\u4e1c\u6298\u897f\u7ed5\uff0c\u8fde\u7ffb\u5e26\u6eda\uff0c\u5c45\u7136\u5c06\u4e5d\u9053\u706b\u7130\u7bad\u5c3d\u6570\u907f\u4e86\u8fc7\u53bb\u3002</p><p>\u201c\u77f3\u722a\uff01\u201d</p><p>\u743c\u6069\u770b\u51c6\u65f6\u673a\uff0c\u6084\u7136\u53d1\u52a8\u4e86\u6cd5\u672f\uff0c\u56db\u9053\u5ca9\u77f3\u624b\u81c2\u81ea\u6ce5\u571f\u4e2d\u95ea\u7535\u94bb\u51fa\uff0c\u6293\u5411\u76ee\u6807\u3002\u9ed1\u5f71\u521a\u521a\u907f\u5f00\u706b\u7130\u7bad\u7684\u8fde\u73af\u5c04\u51fb\uff0c\u7a0d\u4e00\u677e\u61c8\uff0c\u811a\u8155\u88ab\u77f3\u722a\u6263\u4e86\u4e2a\u6b63\u7740\u3002\u4ed6\u4e00\u6323\u4e4b\u4e0b\uff0c\u6ca1\u6709\u6446\u8131\uff0c\u6b63\u8981\u84c4\u529b\u518d\u6323\uff0c\u6885\u83f2\u65af\u5df2\u7136\u50cf\u731b\u517d\u822c\u76f4\u51b2\u8fc7\u6765\uff0c\u5f53\u5934\u4e00\u5251\u5288\u4e0b\u3002</p><p>\u53cc\u8db3\u88ab\u9501\uff0c\u907f\u65e0\u53ef\u907f\uff0c\u9ed1\u5f71\u53ea\u80fd\u786c\u63a5\u5723\u6b66\u58eb\u7684\u653b\u51fb\u3002\u4ed6\u53e3\u4e2d\u53d1\u51fa\u523a\u8033\u7684\u9ad8\u9891\u7387\u4f4e\u5578\uff0c\u4e00\u67c4\u78a7\u5e7d\u5e7d\u7684\u957f\u5200\u81ea\u4f53\u5185\u5f39\u51fa\uff0c\u9ed1\u5f71\u53cc\u624b\u63e1\u4f4f\u5200\u67c4\uff0c\u819d\u76d6\u5fae\u5f2f\uff0c\u53cd\u81c2\u4e0a\u6311\uff0c\u67b6\u4f4f\u4e86\u94f6\u5251\u3002</p><p>\u201c\u94ff\u201d\u5730\u4e00\u58f0\uff0c\u5200\u5251\u76f8\u4ea4\uff0c\u53d1\u51fa\u9707\u8033\u6b32\u804b\u7684\u91d1\u94c1\u5de8\u54cd\uff0c\u6885\u83f2\u65af\u63a5\u8fde\u540e\u9000\u4e09\u6b65\uff0c\u800c\u9ed1\u5f71\u5219\u76f4\u76f4\u5730\u6454\u98de\u51fa\u53bb\uff0c\u8dcc\u843d\u5728\u5341\u6b65\u4e4b\u5916\u7684\u5730\u9762\u4e0a\uff0c\u4f46\u539f\u672c\u6263\u4f4f\u8db3\u8155\u7684\u56db\u6761\u5ca9\u77f3\u624b\u81c2\u4e5f\u88ab\u5c3d\u6570\u9707\u65ad\u4e86\u3002\u6885\u83f2\u65af\u6b63\u5f85\u8ffd\u51fb\uff0c\u9ed1\u5f71\u4f38\u624b\u4e00\u6307\uff0c\u539f\u672c\u7b3c\u7f69\u5168\u8eab\u7684\u96fe\u6c14\u4eff\u4f5b\u88ab\u72c2\u98ce\u5439\u52a8\uff0c\u547c\u5578\u65cb\u8f6c\u7740\uff0c\u51dd\u805a\u6210\u4e00\u4e2a\u9ed1\u8272\u87ba\u65cb\u9525\u4f53\uff0c\u65e0\u6570\u5f20\u72f0\u72de\u7684\u4eba\u8138\u6b64\u8d77\u5f7c\u4f0f\u5730\u5728\u9525\u4f53\u8868\u9762\u6d6e\u73b0\uff0c\u51c4\u5389\u7684\u54ed\u6ce3\u548c\u547c\u53f7\u58f0\u4ece\u4e2d\u900f\u51fa\uff0c\u6c14\u52bf\u6c79\u6c79\u5730\u671d\u7740\u6885\u83f2\u65af\u7b14\u76f4\u649e\u6765\u3002</p><p>\u5530\uff01</p><p>\u8f89\u9633\u62a4\u7b26\u5728\u5c11\u5973\u7684\u9886\u53e3\u4e2d\u5267\u70c8\u9707\u52a8\u8d77\u6765\uff0c\u8000\u773c\u7684\u5149\u8292\u4ece\u4e2d\u5982\u6f6e\u6c34\u822c\u6d8c\u51fa\uff0c\u547c\u5578\u5e2d\u5377\uff0c\u77ac\u95f4\u5c06\u9ed1\u96fe\u51dd\u6210\u7684\u87ba\u65cb\u9525\u4f53\u5b8c\u5168\u84b8\u53d1\uff0c\u5e72\u5e72\u51c0\u51c0\u3002\u4f46\u501f\u6b64\u4e00\u963b\uff0c\u9ed1\u5f71\u7ffb\u8eab\u722c\u8d77\uff0c\u6025\u901f\u540e\u9000\uff0c\u6210\u529f\u5730\u5c06\u8ddd\u79bb\u518d\u5ea6\u62c9\u5f00\u3002\u56e0\u4e3a\u9ed1\u96fe\u6d88\u6563\uff0c\u73b0\u5728\u743c\u6069\u7b49\u4eba\u53ef\u4ee5\u6e05\u695a\u5730\u770b\u89c1\u4ed6\u7684\u771f\u5b9e\u9762\u76ee\uff0c\u90a3\u662f\u4e2a\u76f8\u8c8c\u9887\u4e3a\u79c0\u6c14\u2014\u2014\u751a\u81f3\u53ef\u4ee5\u8bf4\u6709\u4e9b\u5973\u6027\u5316\u2014\u2014\u7684\u9752\u5e74\u7537\u5b50\uff0c\u4e00\u8eab\u7d27\u8eab\u9ed1\u8863\uff0c\u957f\u53d1\u98d8\u9038\uff0c\u98ce\u91c7\u4fca\u6717\uff0c\u8138\u4e0a\u6302\u7740\u9633\u5149\u707f\u70c2\u7684\u7b11\u5bb9\uff0c\u5374\u53c8\u4f3c\u4e4e\u603b\u900f\u7740\u51e0\u4e1d\u9634\u68ee\u90aa\u6c14\u3002\u201c\u6682\u505c\uff01\u201d\u4ed6\u9ad8\u58f0\u8bf4\uff0c\u4e3e\u8d77\u53f3\u624b\u793a\u610f\uff0c\u201c\u6211\u4eec\u53ef\u4ee5\u8c08\u8c08\uff0c\u6211\u6709\u4e2a\u63d0\u8bae\u3002\u201d</p><p>\u201c\u53ef\u4ee5\uff0c\u201d\u6885\u83f2\u65af\u51b7\u51b7\u8bf4\uff0c\u201c\u7b49\u4f60\u6210\u4e3a\u4fd8\u864f\u4e4b\u540e\u3002\u201d</p><p>\u65e0\u89c6\u4e8e\u5bf9\u65b9\u7684\u63d0\u8bae\uff0c\u91d1\u53d1\u5c11\u5973\u6c89\u7a33\u5730\u6301\u5251\u903c\u8fd1\uff0c\u5979\u53ef\u4ece\u6765\u6ca1\u5174\u8da3\u5728\u6218\u6597\u4e2d\u542c\u654c\u4eba\u7f57\u55e6\u3002\u8981\u8c08\u6ca1\u95ee\u9898\uff0c\u5148\u5c06\u4f60\u6253\u5012\u518d\u8bf4\u3002\u7136\u800c\u9ed1\u8863\u7537\u5b50\u63a5\u4e0b\u6765\u7684\u8bdd\uff0c\u5374\u8ba9\u5979\u4e0d\u5f97\u4e0d\u505c\u4f4f\u4e86\u811a\u6b65\u3002</p><p>\u201c\u5728\u4f60\u6253\u5012\u6211\u4e4b\u524d\uff0c\u90a3\u6761\u9f99\u6216\u8bb8\u5df2\u7ecf\u88ab\u53d8\u6210\u9f99\u5deb\u5996\u4e86\u54e6\u3002\u201d</p><p>\u51b0\u8679\uff1f\u9f99\u5deb\u5996\uff1f</p><p>\u5c3d\u7ba1\u9ed1\u8863\u7537\u5b50\u6ca1\u6709\u660e\u786e\u6240\u6307\uff0c\u4f46\u4ed6\u7684\u610f\u601d\u662f\u5f88\u6e05\u695a\u7684\uff0c\u56e0\u4e3a\u8fd9\u5ea7\u5c71\u4e2d\u4e43\u81f3\u6574\u4e2a\u5854\u745f\u8c37\uff0c\u9664\u4e86\u51b0\u8679\u5c31\u518d\u6ca1\u7b2c\u4e8c\u6761\u9f99\u4e86\u3002\u6885\u83f2\u65af\u773c\u4e2d\u5149\u8292\u4e00\u95ea\uff0c\u505c\u4e0b\u811a\u6b65\uff0c\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d\u5979\u95ee\u3002</p><p>\u201c\u6211\u5949\u9f99\u5deb\u6559\u4e3b\u4e4b\u547d\u800c\u6765\uff0c\u76ee\u7684\u662f\u2018\u529d\u8bf4\u2019\u8fd9\u5ea7\u5c71\u4e2d\u7684\u5b9d\u77f3\u9f99\u53d8\u6210\u9f99\u5deb\u5996\uff0c\u201d\u9ed1\u8863\u7537\u5b50\u7684\u56de\u7b54\u975e\u5e38\u7b80\u6d01\u627c\u8981\uff0c\u76f4\u63a5\u9053\u660e\u4e86\u81ea\u5df1\u7684\u8eab\u4efd\u548c\u6765\u610f\uff0c\u201c\u6211\u6709\u4e09\u4f4d\u540c\u4f34\uff0c\u4ed6\u4eec\u90fd\u662f\u7b2c\u4e00\u6d41\u7684\u4ea1\u7075\u5e08\uff0c\u5176\u4e2d\u6709\u4e00\u4f4d\u662f\u90aa\u9f99\u4f8d\u8005\uff0c\u5728\u4e24\u5c0f\u65f6\u5df2\u7ecf\u524d\u8fdb\u5165\u4e86\u90a3\u6761\u9f99\u7684\u6d1e\u7a74\u3002\u201d</p><p>\u6885\u83f2\u65af\u7684\u8138\u8272\u5fae\u5fae\u53d8\u4e86\u3002</p><p>\u8428\u739b\u65af\u7279\u5c45\u7136\u6253\u51b0\u8679\u7684\u4e3b\u610f\uff0c\u60f3\u628a\u5b83\u53d8\u6210\u9f99\u5deb\u5996\uff0c\u8fd9\u4e2a\u6d88\u606f\u6765\u5f97\u592a\u8fc7\u7a81\u7136\uff0c\u5b9e\u5728\u6709\u4e9b\u51fa\u4e4e\u5979\u7684\u610f\u6599\u4e4b\u5916\uff0c\u4f46\u4ed4\u7ec6\u4e00\u60f3\u5374\u53c8\u662f\u60c5\u7406\u4e4b\u4e2d\u3002\u6628\u65e5\u4e0b\u5348\uff0c\u5927\u4e3b\u6559\u5728\u8c08\u8bdd\u4e2d\u5176\u5b9e\u4e5f\u5df2\u7ecf\u9690\u6666\u5730\u6697\u793a\u4e86\u8fd9\u79cd\u62c5\u5fe7\uff0c\u4ed6\u8bf4\u8428\u739b\u65af\u7279\u7684\u90e8\u4e0b\u5728\u5854\u745f\u8c37\u6d3b\u52a8\uff0c\u53ea\u6015\u5e76\u975e\u5355\u7eaf\u5730\u590d\u4ec7\uff0c\u800c\u662f\u522b\u6709\u56fe\u8c0b\uff0c\u672a\u5c1d\u5c31\u6ca1\u6709\u8fd9\u5c42\u610f\u601d\u5728\u5185\u3002\u4f46\u5f53\u65f6\u6885\u83f2\u65af\u6b63\u6c89\u6d78\u5728\u60c5\u7231\u7684\u559c\u60a6\u4e4b\u4e2d\uff0c\u5e76\u672a\u591a\u60f3\uff0c\u5ffd\u7565\u4e86\u8fc7\u53bb\u3002\u5ff5\u53ca\u6b64\u5904\uff0c\u5c11\u5973\u4e0d\u7981\u4e0b\u610f\u8bc6\u5730\u54ac\u7d27\u5634\u5507\uff0c\u6697\u81ea\u607c\u6068\u81ea\u5df1\u7684\u7c97\u5fc3\u5927\u610f\uff0c\u5c45\u7136\u4f1a\u72af\u4e0b\u8fd9\u6837\u4e25\u91cd\u7684\u758f\u5ffd\uff0c\u8fd9\u79cd\u4e8b\u60c5\u5728\u4ee5\u524d\u662f\u6839\u672c\u4e0d\u53ef\u80fd\u4f1a\u51fa\u73b0\u7684\u554a\u3002</p><p>\u4f46\u8f6c\u77ac\u4e4b\u95f4\uff0c\u5979\u4fbf\u5c06\u8fd9\u79cd\u81ea\u8d23\u60c5\u7eea\u5b8c\u5168\u629b\u5f00\uff0c\u8fc7\u53bb\u7684\u4e8b\u60c5\u5df2\u7ecf\u8fc7\u53bb\uff0c\u91cd\u8981\u7684\u662f\u5982\u4f55\u89e3\u51b3\u8feb\u5728\u7709\u776b\u7684\u5371\u673a\u3002\u5982\u679c\u773c\u524d\u8fd9\u540d\u9ed1\u8863\u7537\u5b50\u6240\u8a00\u5c5e\u5b9e\u7684\u8bdd\uff0c\u8428\u739b\u65af\u7279\u7684\u90e8\u4e0b\u5df2\u7ecf\u8fdb\u5165\u4e86\u51b0\u8679\u7684\u9f99\u7a74\u3002\u4e09\u540d\u4ea1\u7075\u5e08\uff0c\u6b63\u5e38\u60c5\u51b5\u4e0b\u7edd\u4e0d\u662f\u4e00\u53ea\u592a\u53e4\u5b9d\u77f3\u9f99\u7684\u5bf9\u624b\uff0c\u8fd9\u7b80\u76f4\u53ef\u4ee5\u8bf4\u662f\u81ea\u5bfb\u6b7b\u8def\u3002\u4f46\u51b0\u8679\u539f\u672c\u5c31\u5df2\u7ecf\u5bff\u5143\u5c06\u5c3d\uff0c\u662f\u4e2a\u5782\u5782\u5c06\u6b7b\u7684\u8001\u8005\uff0c\u5bf9\u65b9\u53c8\u662f\u6709\u5907\u800c\u6765\uff0c\u51fa\u5176\u4e0d\u610f\uff0c\u5982\u679c\u53d1\u751f\u51b2\u7a81\u7684\u8bdd\uff0c\u80dc\u8d1f\u8fd8\u771f\u96be\u4ee5\u9884\u6599\u3002</p><p>\u201c\u4e0d\u8fc7\uff0c\u73b0\u5728\u8d76\u8fc7\u53bb\uff0c\u6216\u8bb8\u8fd8\u6765\u5f97\u53ca\u54e6\u3002\u201d</p><p>\u9ed1\u8863\u7537\u5b50\u7684\u8138\u4e0a\u6d6e\u73b0\u51fa\u8f7b\u677e\u7684\u7b11\u610f\uff0c\u4ed6\u77e5\u9053\u81ea\u5df1\u5df2\u7ecf\u638c\u63e1\u4e86\u4e3b\u52a8\uff0c\u5c3d\u7ba1\u662f\u6682\u65f6\u7684\uff0c\u4f46\u4ed6\u6240\u5e0c\u671b\u7684\u4e5f\u5c31\u4ec5\u6b64\u800c\u5df2\u3002\u201c\u8f6c\u5316\u9f99\u5deb\u5996\u7684\u4eea\u5f0f\u662f\u5f88\u8d39\u65f6\u95f4\u7684\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6309\u7167\u4ed6\u4eec\u4ee5\u5f80\u7684\u6548\u7387\uff0c\u4e24\u5c0f\u65f6\u8db3\u591f\u641e\u5b9a\uff0c\u4e0d\u8fc7\u8fd9\u6b21\u9762\u5bf9\u7684\u662f\u65b0\u54c1\u79cd\uff0c\u8bf4\u4e0d\u5b9a\u4f1a\u591a\u803d\u6401\u803d\u6401\u2014\u2014\u4f46\u5230\u5e95\u4f1a\u803d\u6401\u591a\u4e45\uff0c\u90a3\u5c31\u4e0d\u597d\u8bf4\u4e86\u5462\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d\u6885\u83f2\u65af\u6de1\u6de1\u5e94\u4e86\u58f0\u3002</p><p>\u201c\u6211\u7684\u4efb\u52a1\u5c31\u662f\u5b88\u5728\u8fd9\u91cc\uff0c\u963b\u62e6\u4e00\u5207\u53ef\u80fd\u51fa\u73b0\u7684\u5e72\u6270\u8005\u3002\u6211\u672c\u6765\u4ee5\u4e3a\u8fd9\u662f\u4e2a\u8f7b\u677e\u5de5\u4f5c\uff0c\u6240\u4ee5\u81ea\u544a\u594b\u52c7\u63a5\u4e0b\u6765\u2014\u2014\u4f46\u770b\u6765\u4eca\u5929\u5e76\u4e0d\u662f\u6211\u7684\u5e78\u8fd0\u65e5\uff0c\u201d\u9ed1\u8863\u7537\u5b50\u7528\u60a0\u95f2\u7684\u8bed\u8c03\u8bf4\uff0c\u201c\u6211\u627f\u8ba4\uff0c\u6211\u4e0d\u662f\u4f60\u4eec\u7684\u5bf9\u624b\uff0c\u4f46\u4f60\u4eec\u60f3\u8981\u6740\u6389\u6211\u7684\u8bdd\uff0c\u53ea\u6015\u4e5f\u6ca1\u90a3\u4e48\u5bb9\u6613\u5462\u3002\u800c\u4e14\uff0c\u5982\u679c\u6211\u628a\u76ee\u6807\u5b9a\u4e3a\u2018\u5c3d\u53ef\u80fd\u62d6\u5ef6\u65f6\u95f4\u5c31\u597d\u2019\u7684\u8bdd\uff0c\u6211\u76f8\u4fe1\u81f3\u5c11\u53ef\u4ee5\u8ba9\u4f60\u4eec\u5728\u534a\u5c0f\u65f6\u5185\u65e0\u6cd5\u62b5\u8fbe\u90a3\u53ea\u9f99\u7684\u6d1e\u7a9f\u5427\u3002\u201d</p><p>\u8fd9\u4e2a\u9884\u6d4b\u5e76\u4e0d\u7b97\u5938\u5f20\uff0c\u751a\u81f3\u53ef\u4ee5\u8bf4\u662f\u4fdd\u5b88\u4f30\u8ba1\u3002\u867d\u7136\u521a\u624d\u88ab\u6253\u5f97\u8fde\u6eda\u5e26\u722c\uff0c\u6a21\u6837\u5341\u5206\u72fc\u72c8\uff0c\u4f46\u80fd\u591f\u5728\u6885\u83f2\u65af\u3001\u51db\u548c\u743c\u6069\u4e09\u4eba\u7684\u8054\u624b\u5939\u51fb\u4e4b\u4e0b\u9003\u8131\uff0c\u8fd8\u80fd\u7ad9\u5728\u8fd9\u91cc\u597d\u6574\u4ee5\u6687\u5730\u8bf4\u8bdd\uff0c\u8fd9\u672c\u8eab\u5c31\u8bf4\u660e\u4e86\u5176\u5b9e\u529b\u4e0d\u53ef\u5c0f\u89c6\u3002\u4e0d\u6c42\u80dc\u5229\uff0c\u53ea\u662f\u5343\u65b9\u767e\u8ba1\u62d6\u5ef6\u65f6\u95f4\u7684\u8bdd\uff0c\u534a\u5c0f\u65f6\u662f\u5b8c\u5168\u53ef\u80fd\u7684\u3002\u201c\u6240\u4ee5\u5462\uff1f\u201d\u6885\u83f2\u65af\u6c89\u7740\u5730\u95ee\uff0c\u201c\u4f60\u5230\u5e95\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d</p><p>\u201c\u6211\u53ea\u662f\u6709\u4e00\u4e2a\u5c0f\u5c0f\u7684\u63d0\u8bae\u800c\u5df2\uff0c\u201d\u9ed1\u8863\u7537\u5b50\u8f7b\u58f0\u8bf4\uff0c\u539f\u672c\u6f06\u9ed1\u7684\u77b3\u5b54\u4e2d\uff0c\u9690\u9690\u6cdb\u8d77\u4e86\u9c9c\u8840\u7684\u989c\u8272\uff0c\u663e\u5f97\u975e\u5e38\u8be1\u5f02\uff0c\u201c\u4f60\u4eec\u79bb\u5f00\uff0c\u6211\u4e0d\u963b\u62e6\uff0c\u4f46\u8fd9\u4e2a\u5bb6\u4f19\u2014\u2014\u201d\u4ed6\u6307\u4e86\u6307\u743c\u6069\uff0c\u201c\u8981\u7559\u4e0b\u6765\u966a\u6211\u6253\u4e00\u67b6\u3002\u5c31\u8fd9\u4e48\u7b80\u5355\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u51db\u4e0d\u7981\u8131\u53e3\u95ee\u3002</p><p>\u201c\u56e0\u4e3a\u6211\u770b\u4ed6\u4e0d\u987a\u773c\uff0c\u201d\u9ed1\u8863\u7537\u5b50\u8bf4\uff0c\u201c\u4ec5\u6b64\u800c\u5df2\u3002\u201d</p><p>\u8fd9\u5b9e\u5728\u662f\u4e2a\u597d\u7406\u7531\uff0c\u7b80\u6d01\u660e\u4e86\uff0c\u5e72\u8106\u5229\u843d\uff0c\u65e0\u61c8\u53ef\u51fb\uff0c\u628a\u51db\u4e00\u65f6\u95f4\u564e\u5f97\u8bf4\u4e0d\u51fa\u8bdd\u3002\u5012\u662f\u4e00\u76f4\u6ca1\u6709\u505a\u58f0\u7684\u743c\u6069\u7a81\u7136\u7b11\u4e86\u7b11\uff0c\u201c\u90a3\u597d\u5427\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6211\u4eec\u63a5\u53d7\u8fd9\u4e2a\u63d0\u8bae\u3002\u201d</p><p>\u201c\u743c\u6069\u2014\u2014\u201d</p><p>\u201c\u4f60\u548c\u51db\u5feb\u53bb\u627e\u51b0\u8679\u5427\uff0c\u201d\u743c\u6069\u6253\u65ad\u6885\u83f2\u65af\u7684\u8bdd\uff0c\u201c\u6211\u53ef\u4e0d\u60f3\u771f\u770b\u89c1\u4e00\u53ea\u9f99\u5deb\u5996\u554a\u3002\u81f3\u4e8e\u8fd9\u5bb6\u4f19\uff0c\u5c31\u4ea4\u7ed9\u6211\u6765\u6253\u53d1\u597d\u4e86\u3002\u201d</p><p>\u201c\u56e0\u4e3a\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6211\u4e5f\u6b63\u5de7\u770b\u4ed6\u5f88\u4e0d\u987a\u773c\u5462\u3002\u201d</p></article>"}}}]);
