(window.webpackJsonp=window.webpackJsonp||[]).push([[512],{534:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u56db\u5341\u56db\u8282 \u5929\u65f6",content:"<article><h2>\u7b2c\u56db\u5341\u56db\u8282 \u5929\u65f6</h2><p>\u6885\u83f2\u65af\u51dd\u89c6\u743c\u6069\u51e0\u79d2\u949f\uff0c\u8f7b\u8f7b\u70b9\u4e86\u70b9\u5934\u3002\u201c\u5c0f\u5fc3\uff0c\u201d\u5979\u63d0\u9192\uff0c\u201c\u8fd9\u5bb6\u4f19\u662f\u4e2a\u5438\u8840\u9b3c\u3002\u201d</p><p>\u201c\u6211\u77e5\u9053\u3002\u201d\u743c\u6069\u8bf4\u3002</p><p>\u770b\u89c1\u9ed1\u8863\u4eba\u4f53\u5185\u5f39\u51fa\u7684\u90a3\u67c4\u78a7\u5e7d\u957f\u5200\u65f6\uff0c\u743c\u6069\u5c31\u5df2\u7ecf\u77e5\u6653\u4e86\u5bf9\u65b9\u7684\u4ea1\u7075\u8eab\u4efd\u3002\u5728\u5e7d\u6697\u5730\u57df\u7684\u65f6\u5019\uff0c\u4ed6\u548c\u5fb7\u53e4\u62c9\u5173\u7cfb\u751a\u597d\uff0c\u95f2\u8c08\u804a\u5929\u4e2d\u542c\u8bf4\u4e86\u5f88\u591a\u6709\u5173\u5438\u8840\u9b3c\u7684\u4e8b\u60c5\uff0c\u751a\u81f3\u5305\u62ec\u4e00\u4e9b\u9690\u79d8\u3002\u4eba\u4eba\u90fd\u77e5\u9053\uff0c\u5438\u8840\u9b3c\u4ee5\u9c9c\u8840\u4e3a\u98df\uff0c\u5c31\u50cf\u4eba\u8981\u5403\u996d\u559d\u6c34\u2014\u2014\u4f46\u5438\u8840\u9b3c\u662f\u4ea1\u7075\uff0c\u5e76\u4e0d\u9700\u8981\u201c\u6d88\u5316\u201d\uff0c\u90a3\u4e48\u8fd9\u4e9b\u4e0d\u65ad\u6444\u5165\u7684\u8840\u6db2\u5230\u54ea\u91cc\u53bb\u4e86\u5462\uff1f\u4e43\u662f\u76f4\u63a5\u53d8\u6210\u4e86\u7eaf\u7cb9\u7684\u80fd\u91cf\u3002\u800c\u51e1\u662f\u8fbe\u5230\u201c\u6210\u5e74\u201d\u7684\u5438\u8840\u9b3c\uff0c\u4fbf\u80fd\u81ea\u52a8\u83b7\u5f97\u4e00\u9879\u540d\u4e3a\u201c\u8840\u5203\u201d\u7684\u80fd\u529b\uff0c\u5373\u662f\u53ef\u4ee5\u5c06\u5438\u98df\u7684\u8840\u6db2\u51dd\u805a\u5916\u5316\uff0c\u5177\u73b0\u6210\u4e00\u67c4\u78a7\u5e7d\u5e7d\u7684\u6b66\u5668\uff08\u7edd\u5927\u591a\u6570\u5438\u8840\u9b3c\u90fd\u9009\u62e9\u4e86\u5200\u6216\u5251\uff09\uff0c\u8fd9\u5df2\u7ecf\u53ef\u4ee5\u7b97\u662f\u5438\u8840\u9b3c\u7684\u6807\u5fd7\u4e4b\u4e00\u4e86\u3002</p><p>\u5c11\u5973\u4e0d\u518d\u591a\u8a00\uff0c\u707f\u70c2\u94f6\u5149\u5982\u7ec6\u7802\u822c\u81ea\u5979\u7684\u5de6\u624b\u6307\u7f1d\u95f4\u6d41\u6cfb\u800c\u51fa\uff0c\u5728\u7a7a\u6c14\u4e2d\u51dd\u805a\u6210\u4e00\u53ea\u80cc\u751f\u53cc\u7ffc\u7684\u72ec\u89d2\u517d\u3002\u201c\u6211\u4eec\u8d70\uff0c\u51db\u3002\u201d\u5979\u62db\u547c\u540c\u4f34\uff0c\u4e24\u4f4d\u5c11\u5973\u9a91\u4e0a\u9732\u4e1d\u96c5\uff0c\u817e\u7a7a\u5347\u8d77\uff0c\u671d\u7740\u5c71\u5dc5\u9ad8\u901f\u98de\u53bb\u3002\u9ed1\u8863\u7537\u5b50\u679c\u7136\u4e1d\u6beb\u6ca1\u6709\u501f\u673a\u653b\u51fb\u6216\u963b\u62e6\u7684\u610f\u601d\uff0c\u53cd\u800c\u7b11\u5bb9\u6ee1\u9762\u5730\u62ac\u8d77\u624b\uff0c\u6325\u52a8\u7740\uff0c\u9065\u9065\u793a\u610f\u9053\u522b\u3002</p><p>\u201c\u4f60\u751f\u6c14\u7684\u6837\u5b50\u8d85\u7ea7\u53ef\u7231\uff0c\u7ea2\u8272\u77ed\u88d9\u7684\u9a6c\u5c3e\u8fab\u5c0f\u59d1\u5a18\uff0c\u201d\u4ed6\u5927\u58f0\u8bf4\uff0c\u201c\u80fd\u544a\u8bc9\u6211\u4f60\u7684\u540d\u5b57\u5417\uff1f\u6211\u662f\u5361\u5e03\u5207\u8bfa\xb7\u970d\u6587\uff0c\u4e0b\u6b21\u6709\u673a\u4f1a\u518d\u9047\u5230\uff0c\u6211\u60f3\u8bf7\u4f60\u559d\u676f\u5976\u8336\u3002\u201d</p><p>\u201c\u6211\u559c\u6b22\u5976\u8336\uff0c\u201d\u51db\u6e05\u8106\u7684\u58f0\u97f3\u81ea\u9ad8\u7a7a\u4e2d\u4f20\u6765\uff0c\u201c\u4f46\u6211\u8ba8\u538c\u957f\u5f97\u50cf\u5973\u4eba\u7684\u7537\u4eba\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u81ea\u79f0\u970d\u6587\u7684\u5438\u8840\u9b3c\u4f38\u624b\u6478\u4e86\u6478\u81ea\u5df1\u7684\u8138\uff0c\u9887\u4e3a\u65e0\u5948\u5730\u671d\u743c\u6069\u770b\u8fc7\u6765\uff0c\u201c\u6211\u957f\u5f97\u50cf\u5973\u4eba\u5417\uff1f\u201d\u4ed6\u95ee\u3002</p><p>\u743c\u6069\u70b9\u5934\uff0c\u201c\u5f88\u50cf\uff0c\u975e\u5e38\u50cf\u3002\u201d</p><p>\u201c\u2026\u2026\u90a3\u4f60\u5462\uff1f\u201d\u9ed1\u8863\u4eba\u4e0d\u6ee1\uff0c\u201c\u4f60\u96be\u9053\u4e0d\u50cf\u5417\uff1f\u201d</p><p>\u201c\u6216\u8bb8\u5427\uff0c\u201d\u743c\u6069\u8038\u8038\u80a9\uff0c\u201c\u4f46\u5979\u4e0d\u8ba8\u538c\u6211\u2014\u2014\u8fd9\u5c31\u662f\u533a\u522b\u6240\u5728\u4e86\u3002\u201d</p><p>\u9ed1\u8863\u4eba\u7eb5\u58f0\u5927\u7b11\uff0c\u51e0\u79d2\u79cd\u540e\u53c8\u560e\u7136\u800c\u6b62\uff0c\u201c\u6240\u4ee5\u8bf4\uff0c\u6211\u6700\u8ba8\u538c\u4f60\u8fd9\u79cd\u5bb6\u4f19\u4e86\uff0c\u201d\u4ed6\u8bed\u6c14\u5e73\u9759\u5730\u8bf4\uff0c\u773c\u4e2d\u7684\u7b11\u610f\u53d8\u5f97\u51b0\u51b7\u5f7b\u9aa8\uff0c\u201c\u4eca\u5929\u5c31\u7ed9\u6211\u6b7b\u5728\u8fd9\u91cc\u5427\uff01\u201d</p><p>\u6700\u540e\u4e00\u4e2a\u97f3\u8282\u5410\u51fa\u53e3\uff0c\u9ed1\u8863\u4eba\u731b\u529b\u8e6c\u5730\uff0c\u8eab\u4f53\u50cf\u70ae\u5f39\u822c\u8fc5\u731b\u524d\u51b2\uff0c\u56e0\u4e3a\u901f\u5ea6\u592a\u5feb\u7684\u7f18\u6545\uff0c\u4ee5\u81f3\u4e8e\u5728\u7a7a\u6c14\u4e2d\u7559\u4e0b\u4e86\u4e00\u4e32\u6b8b\u5f71\u3002\u743c\u6069\u53ea\u89c9\u773c\u524d\u4e00\u82b1\uff0c\u8fd8\u6ca1\u7b49\u4f5c\u51fa\u53cd\u5e94\uff0c\u5bf9\u624b\u5df2\u7ecf\u903c\u8feb\u5230\u4e86\u9762\u524d\uff0c\u53f3\u624b\u63a2\u51fa\uff0c\u4e00\u722a\u7167\u7740\u4ed6\u7684\u54bd\u5589\u90e8\u4f4d\u6293\u8fc7\u6765\u3002</p><p>\u6b64\u524d\u786c\u63a5\u6885\u83f2\u65af\u4e00\u5251\uff0c\u5438\u8840\u9b3c\u7684\u78a7\u5e7d\u957f\u5200\u88ab\u9707\u5f97\u6e83\u6563\u65e0\u5f62\uff0c\u6b64\u65f6\u8d64\u624b\u7a7a\u62f3\uff0c\u4f46\u8fd9\u4e00\u722a\u6293\u51fa\uff0c\u51cc\u5389\u51f6\u72e0\uff0c\u4e94\u6307\u5982\u94a9\uff0c\u82cd\u767d\u6beb\u65e0\u8840\u8272\u7684\u6307\u7532\u4e0a\u53cd\u5c04\u7740\u51b0\u51b7\u6151\u4eba\u7684\u5149\u6cfd\uff0c\u5018\u82e5\u88ab\u6293\u4e2d\u7684\u8bdd\uff0c\u8fde\u6728\u5934\u90fd\u4f1a\u88ab\u8f7b\u6613\u6495\u88c2\uff0c\u66f4\u522b\u8bf4\u8840\u8089\u4e4b\u8eaf\u3002\u767e\u5fd9\u4e4b\u4e2d\uff0c\u743c\u6069\u4e0b\u610f\u8bc6\u5730\u5f80\u540e\u4e00\u4ef0\uff0c\u540c\u65f6\u62ac\u624b\u4e0a\u683c\u3002</p><p>\u4eff\u4f5b\u662f\u610f\u5916\uff0c\u4ed6\u5206\u660e\u662f\u624b\u5fd9\u811a\u4e71\u5730\u4e00\u6321\uff0c\u5374\u6070\u597d\u5c01\u6b7b\u4e86\u5bf9\u624b\u7684\u653b\u51fb\u3002\u5438\u8840\u9b3c\u4e00\u722a\u6293\u5728\u743c\u6069\u7684\u624b\u81c2\u4e0a\uff0c\u50cf\u662f\u78b0\u5230\u4e86\u5757\u575a\u786c\u94c1\u677f\uff0c\u5c16\u9510\u7684\u6307\u7532\u5212\u8fc7\uff0c\u53d1\u51fa\u4e00\u9635\u4ee4\u4eba\u7259\u9178\u7684\u6469\u64e6\u58f0\uff0c\u5374\u534a\u70b9\u672a\u80fd\u8d77\u5230\u771f\u6b63\u7684\u4f24\u5bb3\u4f5c\u7528\u3002\u4ed6\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u76ee\u5149\u6c47\u805a\uff0c\u539f\u672c\u6697\u7070\u8272\u7684\u77b3\u5b54\u9aa4\u7136\u95f4\u53d8\u6210\u4e86\u6f84\u91d1\uff0c\u671d\u7740\u743c\u6069\u6df1\u6df1\u51dd\u89c6\u8fc7\u6765\u3002</p><p>\u51e1\u662f\u5438\u8840\u9b3c\uff0c\u90fd\u6709\u4e9b\u5929\u8d4b\u7684\u9b45\u60d1\u50ac\u7720\u80fd\u529b\uff0c\u4f46\u5e76\u4e0d\u600e\u4e48\u5389\u5bb3\uff0c\u5bf9\u4ed8\u5bfb\u5e38\u4eba\u8fd8\u52c9\u5f3a\uff0c\u78b0\u4e0a\u5fc3\u667a\u575a\u6bc5\u4e4b\u8f88\uff0c\u6216\u8005\u6709\u7ecf\u9a8c\u7684\u5192\u9669\u8005\uff0c\u57fa\u672c\u4e0a\u5c31\u662f\u6446\u8bbe\u3002\u4f46\u970d\u6587\u5374\u662f\u4e2a\u7279\u4f8b\uff0c\u4ed6\u662f\u5929\u751f\u7684\u9b54\u773c\uff0c\u53c8\u7ecf\u8fc7\u591a\u91cd\u6cd5\u672f\u7684\u5f3a\u5316\uff0c\u5168\u529b\u65bd\u5c55\u7684\u8bdd\uff0c\u8db3\u4ee5\u77ac\u95f4\u5265\u593a\u8bad\u7ec3\u6709\u7d20\u7684\u58eb\u5175\u7684\u795e\u667a\uff0c\u8ba9\u4ed6\u5b8c\u5168\u542c\u547d\u884c\u4e8b\uff0c\u65e0\u89c6\u751f\u6b7b\uff0c\u54ea\u6015\u9762\u524d\u662f\u4e07\u4e08\u6df1\u6e0a\u90fd\u80fd\u6beb\u4e0d\u72b9\u8c6b\u5f80\u4e0b\u8df3\u3002\u800c\u4e14\u522b\u7684\u5438\u8840\u9b3c\u8981\u53d1\u52a8\u9b45\u60d1\u51dd\u89c6\uff0c\u5fc5\u987b\u5bf9\u65b9\u540c\u65f6\u4e5f\u770b\u7740\u81ea\u5df1\u7684\u773c\u775b\u624d\u884c\uff0c\u970d\u6587\u5374\u6ca1\u6709\u8fd9\u79cd\u9650\u5236\uff0c\u53ea\u8981\u613f\u610f\u4fbf\u80fd\u65bd\u5c55\u3002\u5728\u4ed6\u7684\u9884\u6599\u4e4b\u4e2d\uff0c\u743c\u6069\u53ea\u662f\u4e2a\u5deb\u5e08\uff0c\u5e76\u975e\u6885\u83f2\u65af\u90a3\u79cd\u5723\u6b66\u58eb\uff0c\u4e0d\u53ef\u80fd\u62b5\u6321\u5f97\u4f4f\u81ea\u5df1\u7684\u51dd\u89c6\u3002\u8c01\u77e5\u9b54\u773c\u53d1\u52a8\uff0c\u743c\u6069\u5374\u5168\u7136\u4e0d\u53d7\u534a\u70b9\u5f71\u54cd\uff0c\u53cd\u800c\u8d81\u673a\u5f80\u540e\u9000\u4e86\u4e00\u6b65\uff0c\u5de6\u624b\u7528\u529b\u5f80\u4e0b\u6325\uff0c\u7838\u51fa\u4e00\u9897\u900f\u660e\u7684\u73bb\u7483\u7403\u4f53\u3002</p><p>\u201c\u556a\uff01\u201d</p><p>\u73bb\u7483\u7403\u7838\u5728\u5730\u4e0a\uff0c\u6beb\u65e0\u60ac\u5ff5\u5730\u788e\u88c2\u6210\u6e23\uff0c\u5f3a\u70c8\u7684\u767d\u5149\u4ece\u4e2d\u7efd\u51fa\uff0c\u8f70\u7136\u5982\u6f6e\u3002\u8fd9\u662f\u95ea\u5149\u7403\uff0c\u8fd8\u662f\u4ee5\u524d\u9634\u9b42\u57ce\u8fdc\u5f81\u5e7d\u6697\u5730\u57df\u65f6\u914d\u53d1\u7684\uff0c\u5176\u4e2d\u50a8\u5b58\u4e86\u6781\u6548\u5f3a\u5316\u8fc7\u7684\u95ea\u5149\u7206\u6cd5\u672f\u3002\u5438\u8840\u9b3c\u53c8\u79f0\u201c\u591c\u4e4b\u5b50\u201d\uff0c\u662f\u6240\u6709\u4ea1\u7075\u4e2d\u6700\u754f\u5149\u7684\u5b58\u5728\uff0c\u867d\u7136\u5355\u7eaf\u7684\u5f3a\u5149\u4e0d\u662f\u9633\u5149\uff0c\u5bf9\u4ed6\u4eec\u6ca1\u6709\u771f\u6b63\u7684\u6740\u4f24\u4f5c\u7528\uff0c\u4f46\u9aa4\u7136\u5728\u773c\u524d\u7206\u5f00\uff0c\u8fd8\u662f\u4ee4\u9ed1\u8863\u4eba\u63aa\u624b\u4e0d\u53ca\uff0c\u4ed6\u5306\u5fd9\u62ac\u81c2\u906e\u4f4f\u773c\u775b\uff0c\u5f80\u540e\u4fbf\u9000\u3002\u521a\u521a\u9000\u51fa\u534a\u6b65\uff0c\u5c31\u542c\u5f97\u8033\u8fb9\u98ce\u58f0\u51cc\u5389\uff0c\u547c\u543c\u5982\u96f7\uff0c\u5e9e\u5927\u7684\u5438\u529b\u4ece\u524d\u540e\u5de6\u53f3\u56db\u4e2a\u65b9\u5411\u540c\u65f6\u88ad\u6765\uff0c\u51e0\u4e4e\u8981\u5c06\u4ed6\u7684\u8eab\u4f53\u626f\u788e\u3002\u4ed6\u52c9\u5f3a\u7741\u5f00\u53cc\u76ee\u770b\u53bb\uff0c\u53ea\u89c1\u56db\u9053\u731b\u70c8\u7684\u6c14\u65cb\u6b63\u5b9b\u5982\u9f99\u86c7\u822c\u62d4\u5730\u800c\u8d77\uff0c\u6276\u6447\u76f4\u4e0a\uff0c\u4eff\u4f5b\u56db\u5ea7\u94a2\u94c1\u5de8\u78e8\uff0c\u5e26\u8d77\u65e0\u6570\u7802\u77f3\u6ce5\u571f\uff0c\u9686\u9686\u8f6c\u52a8\u7740\u671d\u81ea\u5df1\u63a8\u79fb\u78be\u538b\u8fc7\u6765\u3002</p><p>\u201c\u4e03\u91cd\u5929\u5802\uff01\u201d</p><p>\u5438\u8840\u9b3c\u4f4e\u5578\u4e00\u58f0\uff0c\u6234\u5728\u5de6\u624b\u4e2d\u6307\u4e0a\u7684\u4e00\u679a\u9aa8\u6212\u4e0a\u9aa4\u7136\u6cdb\u8d77\u5fae\u5149\uff0c\u6570\u5341\u9762\u9ed1\u8272\u4e09\u89d2\u900f\u660e\u5149\u76fe\u4ece\u4e2d\u98de\u51fa\uff0c\u56db\u6563\u7eb7\u98de\uff0c\u76d8\u65cb\u73af\u7ed5\uff0c\u5c06\u4ed6\u5168\u8eab\u4e0a\u4e0b\u90fd\u7262\u7262\u62a4\u4f4f\u3002\u98d3\u98ce\u9ad8\u901f\u65cb\u8f6c\u7740\u903c\u8feb\u800c\u6765\uff0c\u529b\u9053\u4f55\u6b62\u5343\u94a7\uff0c\u4fbf\u662f\u5757\u7cbe\u94a2\u4e5f\u4f1a\u88ab\u751f\u751f\u538b\u6210\u8584\u7247\uff0c\u4f55\u51b5\u5438\u8840\u9b3c\u5230\u5e95\u8fd8\u662f\u8840\u8089\u4e4b\u8eaf\uff0c\u773c\u770b\u5c31\u8981\u88ab\u538b\u5f97\u7c89\u8eab\u788e\u9aa8\uff0c\u7136\u800c\u90a3\u6570\u5341\u9762\u5149\u76fe\u5f7c\u6b64\u8054\u7ed3\uff0c\u4e92\u76f8\u906e\u853d\uff0c\u6784\u5efa\u8d77\u4e86\u4e00\u9053\u575a\u5b9e\u5c4f\u969c\u3002\u98d3\u98ce\u51b2\u649e\u63a8\u6324\uff0c\u51f6\u731b\u547c\u5578\uff0c\u5149\u76fe\u9010\u6e10\u627f\u53d7\u4e0d\u4f4f\u538b\u529b\uff0c\u7eb7\u7eb7\u88ab\u78be\u538b\u6210\u7c89\u788e\uff0c\u4f46\u5e76\u672a\u5c31\u6b64\u6d88\u6563\uff0c\u800c\u662f\u5728\u4e0b\u4e00\u77ac\u95f4\u53c8\u81ea\u52a8\u91cd\u7ec4\uff0c\u7262\u4e0d\u53ef\u7834\u3002</p><p>\u201c\u64cd\u63a7\u5929\u6c14\uff1f\u5567\uff0c\u597d\u50cf\u8fd8\u989d\u5916\u5f3a\u5316\u8fc7\uff0c\u771f\u4e0d\u7b80\u5355\u554a\u3002\u201d</p><p>\u53e3\u4e2d\u867d\u7136\u79f0\u8d5e\uff0c\u4f46\u5438\u8840\u9b3c\u7684\u8bed\u6c14\u4e2d\u5e76\u6ca1\u6709\u591a\u5c11\u7d27\u5f20\u3002\u8fd9\u679a\u540d\u4e3a\u201c\u4e03\u91cd\u5929\u5802\u201d\u7684\u6212\u6307\u662f\u4ed6\u521a\u521a\u52a0\u5165\u6697\u591c\u9762\u5177\u65f6\uff0c\u591c\u5e55\u8bae\u4f1a\u6240\u8d60\u9001\u7684\u5b9d\u7269\uff0c\u4ee5\u4e0a\u53e4\u9ed1\u9f99\u5df4\u52a0\u9042\u6258\u4e9a\uff08\u540e\u6765\u53d8\u6210\u9f99\u5deb\u5996\uff09\u7684\u5de6\u524d\u722a\u73af\u6307\u7b2c\u4e8c\u8282\u6307\u9aa8\u6253\u78e8\u800c\u6210\uff0c\u7ecf\u8fc7\u4e86\u56db\u4f4d\u5927\u5deb\u5e08\u7684\u5f3a\u5316\u548c\u795d\u798f\uff0c\u6ca1\u6709\u4efb\u4f55\u653b\u51fb\u80fd\u529b\uff0c\u53ea\u80fd\u5355\u7eaf\u7528\u4e8e\u9632\u5fa1\u2014\u2014\u4f46\u8fd9\u5c31\u5df2\u7ecf\u8ba9\u5b83\u8dfb\u8eab\u4e8e\u795e\u5668\u4e4b\u5217\u3002\u636e\u8bf4\u6700\u5f3a\u5f62\u6001\u53d1\u52a8\u65f6\uff0c\u4e03\u5341\u4e8c\u9762\u5149\u76fe\u540c\u65f6\u5f20\u5f00\uff0c\u5c31\u8fde\u795e\u660e\u7684\u653b\u51fb\u90fd\u80fd\u6321\u4e0b\uff0c\u4ee5\u970d\u6587\u76ee\u524d\u7684\u9020\u8be3\uff0c\u53ea\u80fd\u53d1\u6325\u6212\u6307\u5176\u4e94\u6210\u5de6\u53f3\u7684\u529b\u91cf\uff0c\u521b\u9020\u51fa\u4e09\u5341\u516d\u9762\u5149\u76fe\uff0c\u4f46\u5e94\u4ed8\u743c\u6069\u7528\u96f7\u9706\u795e\u529b\u5f3a\u5316\u7684\u201c\u64cd\u63a7\u5929\u6c14\u201d\u6cd5\u672f\uff0c\u4e5f\u5df2\u7ecf\u662f\u7ef0\u7ef0\u6709\u4f59\u4e86\u3002</p><p>\u7ecf\u8fc7\u4e86\u516d\u4e03\u79d2\u949f\u7684\u62c9\u952f\u6218\uff0c\u539f\u672c\u6c14\u52bf\u6c79\u6c79\u7684\u98d3\u98ce\u529b\u9053\u660e\u663e\u5f00\u59cb\u524a\u5f31\uff0c\u800c\u5438\u8840\u9b3c\u8eab\u5468\u7684\u9ed1\u8272\u5149\u76fe\u7ec4\u6210\u7684\u9632\u7ebf\u5219\u9010\u6b65\u5916\u6269\uff0c\u9732\u51fa\u53cd\u51fb\u7684\u52bf\u6001\u3002\u970d\u6587\u521a\u521a\u8212\u4e86\u53e3\u6c14\uff0c\u7a81\u89c9\u4e00\u9635\u8000\u773c\u7535\u5149\u5728\u773c\u524d\u9519\u4e71\u95ea\u8fc7\uff0c\u4ed6\u5fae\u5fae\u4e00\u6014\uff0c\u5b9a\u775b\u7ec6\u770b\uff0c\u5374\u89c1\u662f\u98d3\u98ce\u4e2d\u9aa4\u7136\u51fa\u73b0\u4e86\u65e0\u6570\u9053\u7ec6\u82e5\u6e38\u4e1d\u3001\u873f\u8712\u5982\u86c7\u7684\u94f6\u8272\u707f\u70c2\u95ea\u7535\uff0c\u5f7c\u6b64\u7f20\u7ed5\u7740\uff0c\u770b\u8d77\u6765\u7eda\u4e3d\u4e4b\u6781\u3002</p><p>\u7136\u540e\u5b83\u4eec\u540c\u65f6\u5d29\u5f00\u4e86\u3002</p><p>\u98d3\u98ce\u7830\u7136\u70b8\u88c2\uff0c\u731b\u70c8\u7684\u6c14\u6d41\u4eff\u4f5b\u65e0\u6570\u53ea\u5de8\u9524\uff0c\u671d\u7740\u56db\u9762\u516b\u65b9\u6563\u4e71\u6269\u6563\u51fa\u53bb\uff0c\u58f0\u52bf\u731b\u70c8\u81f3\u6781\uff0c\u4f46\u653b\u51fb\u7684\u65b9\u5411\u5374\u662f\u5f80\u5916\u800c\u975e\u5411\u5185\uff0c\u56db\u5468\u7684\u6811\u6728\u3001\u5c71\u77f3\u88ab\u9707\u5f97\u6298\u65ad\u7ffb\u6eda\u3001\u7c89\u788e\u6e83\u706d\uff0c\u8fd1\u5728\u54ab\u5c3a\u7684\u970d\u6587\u538b\u6839\u6ca1\u6709\u53d7\u5230\u534a\u70b9\u653b\u51fb\u3002\u5438\u8840\u9b3c\u7709\u5934\u5fae\u76b1\uff0c\u6b63\u4e0d\u89e3\u743c\u6069\u6b64\u4e3e\u662f\u4f55\u610f\uff0c\u9aa4\u7136\u95f4\u60f3\u8d77\u4e00\u4ef6\u4e8b\uff0c\u987f\u65f6\u9762\u8272\u5927\u53d8\uff0c\u8eab\u4f53\u4e00\u5f13\uff0c\u6025\u6025\u5c31\u8981\u5f80\u540e\u9000\u53bb\u3002</p><p>\u4ed6\u811a\u4e0b\u521a\u4e00\u53d1\u529b\uff0c\u7a81\u89c9\u5730\u9762\u4e0d\u77e5\u4f55\u65f6\u53d8\u5f97\u677e\u8f6f\u65e0\u6bd4\uff0c\u4eff\u4f5b\u6d41\u6c99\uff0c\u53c8\u4f3c\u6ce5\u6cbc\uff0c\u6084\u65e0\u58f0\u606f\u95f4\u534a\u622a\u5c0f\u817f\u4fbf\u5df2\u7ecf\u9677\u4e86\u4e0b\u53bb\u3002\u5438\u8840\u9b3c\u4e5f\u662f\u8eab\u7ecf\u767e\u6218\u7684\u4eba\u7269\uff0c\u867d\u60ca\u4e0d\u4e71\uff0c\u53f3\u624b\u5feb\u901f\u5730\u659c\u659c\u4e00\u6325\uff0c\u4eff\u4f5b\u6709\u96fe\u6c14\u4ece\u638c\u5fc3\u4e2d\u91ca\u653e\u51fa\uff0c\u8eab\u4f53\u7684\u4e0b\u6c89\u4e4b\u52bf\u4fbf\u7acb\u523b\u6b62\u4f4f\uff0c\u8f6c\u800c\u5f00\u59cb\u5feb\u901f\u5f80\u4e0a\u5347\u3002</p><p>\u77f3\u722a\uff01</p><p>\u743c\u6069\u6545\u6280\u91cd\u65bd\uff0c\u56db\u9053\u5ca9\u77f3\u624b\u81c2\u518d\u5ea6\u4ece\u571f\u4e2d\u7a9c\u51fa\uff0c\u8fc5\u96f7\u95ea\u7535\u822c\u6263\u4f4f\u4e86\u970d\u6587\u7684\u53cc\u817f\u3002\u5438\u8840\u9b3c\u773c\u4e2d\u51f6\u5149\u5927\u76db\uff0c\u5507\u8fb9\u66b4\u9732\u51fa\u4e24\u9897\u767d\u68ee\u68ee\u7684\u5c16\u9510\u7360\u7259\uff0c\u53c8\u4e00\u67c4\u65b0\u7684\u78a7\u5e7d\u957f\u5200\u81ea\u4f53\u5185\u500f\u5ffd\u5f39\u51fa\uff0c\u4ed6\u53cd\u624b\u63e1\u4f4f\u5200\u67c4\uff0c\u53cd\u638c\u65cb\u8f6c\uff0c\u5982\u5207\u8150\u6728\u822c\u5c06\u56db\u53ea\u5ca9\u77f3\u624b\u81c2\u5c3d\u6570\u65a9\u65ad\uff0c\u501f\u7740\u53cd\u51b2\u4e4b\u529b\u8eab\u4f53\u66f4\u52a0\u901f\u5f39\u8d77\u3002</p><p>\u7136\u800c\u5c31\u5728\u6b64\u65f6\uff0c\u707f\u70c2\u7684\u91d1\u8272\u9633\u5149\u7a7f\u7834\u70df\u5c18\uff0c\u76f4\u76f4\u7167\u5c04\u4e0b\u6765\u3002</p><p>\u4ed6\u4eec\u8eab\u5904\u7684\u4f4d\u7f6e\u662f\u5c71\u8170\u7684\u4e00\u7247\u6811\u6797\u4e2d\uff0c\u6811\u836b\u6d53\u5bc6\uff0c\u5149\u7ebf\u751a\u6697\u3002\u800c\u6b64\u524d\u98d3\u98ce\u7684\u7206\u70b8\uff0c\u6ca1\u6709\u4f24\u5bb3\u5438\u8840\u9b3c\uff0c\u5374\u5c06\u5468\u56f4\u7684\u6811\u6728\u5168\u90fd\u9707\u5012\u5728\u5730\uff0c\u987f\u65f6\u4fbf\u5f62\u6210\u4e86\u5927\u7247\u7684\u7a7a\u5730\uff0c\u539f\u672c\u88ab\u6811\u836b\u906e\u4f4f\u7684\u9633\u5149\u987f\u65f6\u4fbf\u7167\u5c04\u4e0b\u6765\u2014\u2014\u8fd9\u4fbf\u662f\u743c\u6069\u7684\u771f\u6b63\u76ee\u7684\u3002\u4ece\u4e00\u5f00\u59cb\uff0c\u4ed6\u5c31\u6ca1\u6709\u6307\u671b\u7528\u98d3\u98ce\u6765\u76f4\u63a5\u4f24\u5bb3\u5230\u5438\u8840\u9b3c\uff0c\u800c\u662f\u6253\u7740\u521b\u9020\u201c\u5730\u5229\u201d\u4ee5\u501f\u52a9\u201c\u5929\u65f6\u201d\u7684\u4e3b\u610f\u3002</p><p>\u5bf9\u4e8e\u5438\u8840\u9b3c\u6765\u8bf4\uff0c\u9633\u5149\u6bd4\u4ec0\u4e48\u9b54\u6cd5\u4ec0\u4e48\u5200\u5251\u90fd\u8981\u6050\u6016\u5f97\u591a\u3002\u9633\u5149\u4e0d\u4f46\u80fd\u591f\u76f4\u63a5\u707c\u4f24\u4ed6\u4eec\u7684\u76ae\u80a4\uff0c\u711a\u70e7\u4ed6\u4eec\u7684\u8840\u8089\uff0c\u800c\u4e14\u4f1a\u5168\u9762\u538b\u5236\u751a\u81f3\u5c01\u5370\u5438\u8840\u9b3c\u7684\u5404\u79cd\u5f02\u80fd\uff0c\u5176\u4e2d\u5c31\u5305\u62ec\u90a3\u5f3a\u608d\u5230\u53d8\u6001\u7684\u81ea\u6108\u6062\u590d\u80fd\u529b\u2014\u2014\u6b64\u5916\u8fd8\u4f1a\u8ba9\u4ed6\u4eec\u53d8\u5f97\u683c\u5916\u865a\u5f31\u3002\u53ea\u6709\u8fbe\u5230\u4eb2\u738b\u7ea7\u522b\u7684\u201c\u65e5\u884c\u8005\u201d\uff0c\u624d\u80fd\u591f\u76f4\u63a5\u66b4\u9732\u5728\u9633\u5149\u7167\u5c04\u4e4b\u4e0b\uff0c\u5b8c\u5168\u514d\u75ab\u4f24\u5bb3\uff0c\u800c\u773c\u524d\u7684\u8fd9\u540d\u5438\u8840\u9b3c\u867d\u7136\u5df2\u7ecf\u5f88\u5f3a\uff0c\u5374\u663e\u7136\u8fd8\u6ca1\u6709\u5230\u8fd9\u79cd\u7a0b\u5ea6\u3002</p><p>\u6b63\u5982\u743c\u6069\u6240\u6599\uff0c\u88ab\u9633\u5149\u76f4\u63a5\u7167\u4e2d\uff0c\u5438\u8840\u9b3c\u7684\u8eab\u4f53\u8868\u9762\u5239\u90a3\u95f4\u4fbf\u817e\u8d77\u4e86\u5927\u5927\u5c0f\u5c0f\u51e0\u5341\u56e2\u706b\u7130\u6765\uff0c\u4eff\u4f5b\u6574\u4e2a\u4eba\u90fd\u5728\u718a\u718a\u71c3\u70e7\u3002\u4f46\u5728\u4e0b\u4e00\u77ac\u95f4\uff0c\u6d53\u91cd\u7684\u9ed1\u96fe\u4fbf\u4ece\u5438\u8840\u9b3c\u7684\u4f53\u5185\u6d8c\u51fa\uff0c\u5c06\u4ed6\u56e2\u56e2\u5305\u88f9\u5728\u5185\uff0c\u6781\u5927\u5730\u524a\u5f31\u4e86\u9633\u5149\u7684\u7167\u5c04\u3002\u743c\u6069\u76ee\u5149\u4e00\u95ea\uff0c\u773c\u524d\u7684\u60c5\u5f62\u6709\u4e9b\u51fa\u4e4e\u610f\u6599\uff0c\u53ea\u4e0d\u8fc7\uff0c\u4ed6\u4e5f\u5e76\u6ca1\u6709\u5f53\u771f\u628a\u5e0c\u671b\u5168\u90e8\u5bc4\u6258\u5728\u592a\u9633\u4e0a\u3002</p><p>\u4e24\u679a\u68cb\u5b50\u8131\u624b\u63b7\u51fa\uff0c\u5316\u4f5c\u5353\u5c14\u6218\u58eb\u9b54\u50cf\uff0c\u4e24\u67c4\u957f\u5251\u8fc5\u731b\u65e0\u6bd4\u5730\u4ea4\u9519\u6325\u65a9\uff0c\u5c06\u76ee\u6807\u780d\u6210\u4e86\u56db\u6bb5\u3002</p></article>"}}}]);
