(window.webpackJsonp=window.webpackJsonp||[]).push([[514],{536:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u56db\u5341\u516d\u8282 \u4e4c\u9e26",content:"<article><h2>\u7b2c\u56db\u5341\u516d\u8282 \u4e4c\u9e26</h2><p>\u4ee5\u4eba\u7c7b\u4e3a\u6807\u51c6\u6765\u8861\u91cf\uff0c\u9ed1\u6697\u7cbe\u7075\u662f\u201c\u5a07\u5c0f\u73b2\u73d1\u201d\u7684\u79cd\u65cf\u3002\u7ef4\u5eb7\u5c3c\u4e9a\u7684\u8eab\u9ad8\uff0c\u5728\u540c\u65cf\u4e2d\u5df2\u7ecf\u5c5e\u4e8e\u4e2d\u7b49\u504f\u4e0a\uff0c\u548c\u743c\u6069\u7ad9\u5728\u4e00\u8d77\u7684\u65f6\u5019\u5374\u8fd8\u4e0d\u5230\u4ed6\u7684\u80a9\u8180\uff0c\u4e24\u4eba\u9ad8\u5ea6\u76f8\u5dee\u63a5\u8fd1\u56db\u5341\u516c\u5206\uff0c\u5982\u679c\u7ef4\u5eb7\u5c3c\u4e9a\u4e0d\u7a7f\u9ad8\u8ddf\u978b\u7684\u8bdd\uff0c\u60f3\u63a5\u543b\u90fd\u4e0d\u592a\u5bb9\u6613\u3002\u8475\u9732\u8eab\u9ad8\u548c\u6885\u83f2\u65af\u76f8\u5f53\uff0c\u4fbf\u88ab\u79f0\u4e3a\u201c\u5353\u5c14\u4e2d\u7684\u5de8\u4eba\u7f8e\u5973\u201d\uff0c\u5c5e\u4e8e\u5f02\u7c7b\u4e2d\u7684\u5f02\u7c7b\u3002\u4f46\u6b64\u65f6\u51fa\u73b0\u5728\u8eab\u65c1\u7684\u201c\u5353\u5c14\u5973\u796d\u53f8\u201d\u5e76\u975e\u771f\u4eba\uff0c\u800c\u662f\u9b54\u50cf\uff0c\u867d\u7136\u662f\u7279\u6b8a\u6784\u9020\uff0c\u4e0d\u50cf\u666e\u901a\u9b54\u50cf\u90a3\u6837\u5e9e\u5927\u65e0\u6bd4\uff0c\u6bd4\u743c\u6069\u90fd\u8fd8\u662f\u8981\u9ad8\u51fa\u5927\u534a\u622a\u3002\u4e24\u5c0a\u9b54\u50cf\u4e00\u5de6\u4e00\u53f3\u5939\u51fb\uff0c\u8000\u773c\u5230\u6781\u81f4\u7684\u767d\u70bd\u5149\u8292\u81ea\u5723\u5fbd\u4e2d\u6c79\u6d8c\u800c\u51fa\uff0c\u4ea4\u7ec7\u878d\u6c47\u6210\u4e00\u5f20\u5bc6\u4e0d\u900f\u98ce\u7684\u5929\u7f57\u5730\u7f51\uff0c\u4ee5\u94fa\u5929\u76d6\u5730\u7684\u67b6\u52bf\u5c45\u9ad8\u4e34\u4e0b\u7b3c\u7f69\u800c\u6765\u3002</p><p>\u4ee5\u6570\u5341\u540d\u8d27\u771f\u4ef7\u5b9e\u7684\u5353\u5c14\u5973\u796d\u53f8\u7075\u9b42\u586b\u5145\u9a71\u52a8\u7684\u9b54\u50cf\uff0c\u81ea\u4f4d\u5217\u6df1\u6e0a\u4e09\u5927\u9b54\u541b\u4e4b\u9996\u7684\u72c4\u9b54\u9ad8\u6839\u5904\u501f\u5f97\u529b\u91cf\uff0c\u5c06\u6700\u5f3a\u529b\u795e\u672f\u8f6c\u6362\u4e3a\u7eaf\u7cb9\u7684\u6b63\u80fd\u91cf\u76f4\u63a5\u91ca\u653e\uff0c\u6b63\u662f\u4ea1\u7075\u7684\u5929\u7136\u514b\u661f\u3002\u5438\u8840\u9b3c\u6b32\u5f85\u95ea\u907f\uff0c\u5374\u88ab\u743c\u6069\u6b7b\u6b7b\u64d2\u62b1\uff0c\u4e00\u65f6\u95f4\u65e0\u6cd5\u6323\u8131\uff0c\u201c\u56db\u81c2\u7329\u7329\u4e4b\u795d\u798f\u201d\u5e76\u4e0d\u5355\u7eaf\u662f\u8ba9\u4eba\u591a\u51fa\u4e24\u6761\u624b\u81c2\uff0c\u968f\u4e4b\u800c\u6765\u7684\u662f\u529b\u6c14\u4e5f\u51ed\u7a7a\u589e\u5927\u4e86\u4e00\u500d\uff0c\u743c\u6069\u7d27\u63a5\u7740\u53c8\u542f\u52a8\u4e86\u201c\u86ee\u725b\u4e4b\u529b\u201d\uff0c\u867d\u7136\u548c\u5438\u8840\u9b3c\u76f8\u6bd4\u4ecd\u6709\u5dee\u8ddd\uff0c\u4f46\u4ed6\u5df2\u7ecf\u62a2\u5f97\u5148\u673a\uff0c\u77ed\u6682\u538b\u5236\u51e0\u79d2\u949f\u8fd8\u662f\u80fd\u529e\u5230\u7684\u3002\u5018\u82e5\u662f\u5728\u9ed1\u6697\u6216\u9634\u5f71\u4e2d\uff0c\u5438\u8840\u9b3c\u76f4\u63a5\u5c06\u8eaf\u4f53\u96fe\u5316\u4fbf\u80fd\u8f7b\u6613\u8131\u8eab\uff0c\u4f46\u6b64\u523b\u662f\u5728\u707f\u70c2\u9633\u5149\u4e4b\u4e0b\uff0c\u9760\u7740\u8840\u6c14\u51dd\u7ed3\u6210\u7684\u900f\u660e\u62a4\u7532\u624d\u5f97\u4ee5\u4e0d\u53d7\u4f24\u5bb3\uff0c\u4e00\u65e6\u96fe\u5316\u7684\u8bdd\uff0c\u9633\u5149\u7684\u4f24\u5bb3\u52a0\u500d\uff0c\u53ea\u6015\u4e0d\u7b49\u9000\u56de\u5230\u6811\u836b\u4e0b\uff0c\u4ed6\u5c31\u76f4\u63a5\u84b8\u53d1\u5f97\u4e00\u5e72\u4e8c\u51c0\u4e86\u3002</p><p>\u7535\u5149\u706b\u77f3\u4e4b\u95f4\uff0c\u5438\u8840\u9b3c\u539f\u672c\u7070\u8910\u8272\u53cc\u773c\u9661\u7136\u53d8\u5f97\u8840\u7ea2\u4e00\u7247\uff0c\u8eaf\u4f53\u201c\u7830\u201d\u5730\u81ea\u884c\u70b8\u88c2\u5f00\u6765\uff0c\u5374\u6ca1\u6709\u51fa\u73b0\u8840\u8089\u6a2a\u98de\u7684\u6050\u6016\u666f\u8c61\uff0c\u800c\u662f\u5316\u4f5c\u51e0\u5341\u53ea\u9ed1\u8272\u8759\u8760\uff0c\u671d\u56db\u9762\u516b\u65b9\u5206\u6563\u9003\u53bb\u3002\u707f\u70c2\u7684\u5723\u5149\u8f70\u7136\u7167\u4e0b\uff0c\u547c\u5730\u4e00\u58f0\uff0c\u4eff\u4f5b\u98d3\u98ce\u731b\u70c8\u522e\u8fc7\uff0c\u53c8\u50cf\u662f\u6c6a\u6d0b\u8086\u8650\u5e2d\u5377\uff0c\u7edd\u5927\u591a\u6570\u8759\u8760\u77ac\u95f4\u4fbf\u5316\u4f5c\u7070\u70ec\uff0c\u4f46\u56e0\u4e3a\u603b\u4f53\u6570\u91cf\u592a\u591a\uff0c\u4ecd\u7136\u6709\u4e00\u53ea\u5e78\u8fd0\u7684\u6f0f\u7f51\u4e4b\u9c7c\u6210\u529f\u9003\u8131\uff0c\u75be\u82e5\u5229\u7bad\u5730\u9003\u79bb\u5f53\u573a\uff0c\u9041\u5165\u6811\u6797\u95f4\u7684\u6c89\u6c89\u9ed1\u6697\u4e4b\u4e2d\u3002</p><p>\u201c\u5636\uff01\u201d</p><p>\u743c\u6069\u5012\u5438\u4e00\u53e3\u51b7\u6c14\uff0c\u53ea\u89c9\u8138\u4e0a\u706b\u8fa3\u8fa3\u5730\u75bc\u75db\uff0c\u4f38\u624b\u6478\u53bb\uff0c\u6307\u95f4\u6cbe\u6ee1\u4e86\u6e29\u70ed\u7684\u8840\u7ea2\u6db2\u4f53\uff0c\u662f\u521a\u624d\u90a3\u53ea\u8759\u8760\u5728\u9003\u9041\u65f6\uff0c\u6070\u597d\u5212\u7834\u4e86\u4ed6\u7684\u9762\u988a\u3002\u4f24\u53e3\u4f3c\u4e4e\u5e76\u4e0d\u6df1\uff0c\u4f46\u5374\u975e\u5e38\u75bc\u75db\uff0c\u5e78\u597d\u8840\u5f88\u5feb\u4fbf\u81ea\u884c\u6b62\u4f4f\uff0c\u4e0d\u518d\u6d41\u6dcc\uff0c\u53ea\u662f\u88ab\u98ce\u4e00\u5439\uff0c\u8840\u6db2\u51dd\u7ed3\uff0c\u5f04\u5f97\u5de6\u534a\u8fb9\u8138\u8fde\u540c\u8116\u5b50\u4e0a\u90fd\u662f\u9ecf\u9ecf\u7a20\u7a20\u7684\uff0c\u96be\u53d7\u4e4b\u6781\uff0c\u4e00\u65f6\u95f4\u4e5f\u987e\u4e0d\u4e0a\u53bb\u6e05\u6d17\u3002\u4ed6\u8f6c\u8fc7\u8eab\uff0c\u76ef\u7740\u8759\u8760\u9003\u8131\u7684\u65b9\u5411\uff0c\u4e24\u5c0a\u5973\u796d\u53f8\u9b54\u50cf\u4e00\u5de6\u4e00\u53f3\u5730\u62a4\u536b\u7740\uff0c\u767d\u8272\u7684\u5149\u8292\u518d\u5ea6\u81ea\u8718\u86db\u5723\u8f89\u4e2d\u53d1\u51fa\uff0c\u8d8a\u6765\u8d8a\u4eae\uff0c\u84c4\u52bf\u5f85\u53d1\u3002</p><p>\u7136\u800c\u654c\u4eba\u5df2\u7ecf\u4e0d\u89c1\u4e86\u3002</p><p>\u743c\u6069\u7b49\u5f85\u4e86\u5927\u7ea6\u4e09\u5341\u79d2\u949f\uff0c\u6811\u6797\u4e2d\u59cb\u7ec8\u6ca1\u6709\u534a\u70b9\u52a8\u9759\uff0c\u5438\u8840\u9b3c\u4eff\u4f5b\u5f7b\u5e95\u6d88\u5931\u4e86\u822c\uff0c\u518d\u4e0d\u51fa\u73b0\u3002\u201c\u5c45\u7136\u5c31\u8fd9\u6837\u9003\u4e86\uff1f\u201d\u4ed6\u7565\u7565\u6709\u4e9b\u610f\u5916\uff0c\u4f46\u8fd9\u7ed3\u679c\u4e5f\u6b63\u4e2d\u4e0b\u6000\u3002</p><p>\u4ece\u4e00\u5f00\u59cb\uff0c\u743c\u6069\u7684\u6253\u7b97\u5c31\u662f\u901f\u6218\u901f\u51b3\uff0c\u81f3\u4e8e\u201c\u5e72\u6389\u5438\u8840\u9b3c\u201d\u8fd9\u4e2a\u9009\u9879\u5219\u5e76\u4e0d\u5728\u8ba1\u5212\u4e4b\u4e2d\u3002\u6b64\u65f6\u5df2\u7ecf\u662f\u5c06\u8fd1\u4e0b\u5348\u4e09\u70b9\uff0c\u867d\u7136\u9633\u5149\u8fd8\u662f\u5f88\u660e\u4eae\uff0c\u4f46\u6bd5\u7adf\u5df2\u7ecf\u8fc7\u4e86\u6b63\u5348\u5dc5\u5cf0\uff0c\u6b63\u5411\u8fdf\u66ae\u800c\u53bb\uff0c\u6bcf\u591a\u803d\u6401\u4e00\u523b\uff0c\u5bf9\u5438\u8840\u9b3c\u7684\u538b\u5236\u4fbf\u524a\u5f31\u4e00\u5206\uff0c\u5bf9\u743c\u6069\u81ea\u7136\u4e0d\u5229\u3002\u800c\u4e14\u6885\u83f2\u65af\u548c\u51db\u5df2\u7ecf\u8fdb\u5165\u51b0\u8679\u7684\u9f99\u7a9f\u4e2d\uff0c\u4e5f\u4e0d\u77e5\u60c5\u5f62\u5982\u4f55\uff0c\u743c\u6069\u4e0d\u514d\u62c5\u5fc3\uff0c\u6bd5\u7adf\u5979\u4eec\u8981\u9762\u5bf9\u7684\u662f\u9f99\u5deb\u6559\u7684\u7cbe\u82f1\u5206\u5b50\uff0c\u4e0d\u662f\u5bfb\u5e38\u4e4b\u8f88\u2014\u2014\u751a\u81f3\u6709\u53ef\u80fd\u8fd8\u5305\u62ec\u4e00\u53ea\u65b0\u664b\u9f99\u5deb\u5996\uff0c\u6240\u4ee5\u66f4\u8981\u65e9\u70b9\u7ed3\u675f\u8fd9\u8fb9\u7684\u6218\u6597\uff0c\u5c3d\u5feb\u524d\u53bb\u5e2e\u5fd9\u3002</p><p>\u5bf9\u624b\u662f\u957f\u8001\u7ea7\u5438\u8840\u9b3c\uff0c\u62e5\u6709\u5f3a\u608d\u5230\u53d8\u6001\u7684\u81ea\u6108\u80fd\u529b\uff0c\u8fd1\u4e4e\u4e0d\u6b7b\u4e4b\u8eab\uff0c\u743c\u6069\u53c8\u4e0d\u662f\u51db\u90a3\u79cd\u80fd\u591f\u75af\u72c2\u8f93\u51fa\u5de8\u5927\u4f24\u5bb3\u7684\u8d85\u70ae\u53f0\u578b\u5deb\u5e08\uff0c\u60f3\u901f\u6218\u901f\u51b3\uff0c\u96be\u5ea6\u5b9e\u5728\u662f\u975e\u5e38\u9ad8\u7684\uff0c\u4f46\u4ed6\u7ec8\u7a76\u8fd8\u662f\u505a\u5230\u4e86\uff1a\u5f15\u8bf1\u5438\u8840\u9b3c\u4e0a\u524d\u8fd1\u6218\uff0c\u5148\u7528\u4e24\u5c0a\u5353\u5c14\u6218\u58eb\u9b54\u50cf\u8fdb\u884c\u653b\u51fb\uff0c\u8ba9\u5bf9\u624b\u4ee5\u4e3a\u201c\u6280\u6b62\u6b64\u8033\u201d\uff0c\u6389\u4ee5\u8f7b\u5fc3\uff0c\u7136\u540e\u501f\u7740\u5728\u5730\u4e0a\u7ffb\u6eda\u8eb2\u95ea\u7684\u673a\u4f1a\uff0c\u5c06\u65e9\u5c31\u6697\u4e2d\u51c6\u5907\u597d\u7684\u201c\u5973\u796d\u53f8\u201d\u68cb\u5b50\u6254\u5728\u6ce5\u571f\u4e2d\uff0c\u6700\u540e\u5bfb\u673a\u538b\u5236\u4f4f\u5bf9\u624b\uff0c\u542f\u52a8\u8428\u74e6\u68cb\u9b54\u50cf\uff0c\u5bf9\u5438\u8840\u9b3c\u8fdb\u884c\u5723\u5149\u8f70\u51fb\u2014\u2014\u6574\u4e2a\u8ba1\u5212\u8fdb\u884c\u5f97\u975e\u5e38\u987a\u5229\uff0c\u4e00\u5207\u5c3d\u5982\u6240\u6599\uff0c\u53ea\u5728\u7ed3\u5c3e\u51fa\u4e86\u70b9\u95ee\u9898\uff1a\u5438\u8840\u9b3c\u5e76\u6ca1\u6709\u5982\u9884\u6599\u7684\u90a3\u6837\u88ab\u5f53\u573a\u8f70\u6740\uff0c\u800c\u662f\u5316\u8eab\u8759\u8760\u9003\u8131\u4e86\u3002</p><p>\u201c\u65e0\u6240\u8c13\uff0c\u521a\u624d\u6328\u4e86\u90a3\u4e00\u4e0b\uff0c\u4e0d\u6b7b\u4e5f\u91cd\u4f24\u4e86\u5427\u3002\u201d</p><p>\u5316\u8760\u662f\u5438\u8840\u9b3c\u7684\u62db\u724c\u80fd\u529b\u4e4b\u4e00\uff0c\u51e1\u662f\u6210\u5e74\u4f4d\u9636\u4ee5\u4e0a\u8005\u5747\u81ea\u52a8\u62e5\u6709\uff0c\u529b\u91cf\u8d8a\u5f3a\u5219\u80fd\u591f\u53d8\u6210\u7684\u8759\u8760\u6570\u91cf\u5c31\u8d8a\u591a\uff0c\u800c\u4e14\u6bcf\u4e00\u53ea\u90fd\u76f8\u5f53\u4e8e\u4e00\u4e2a\u72ec\u7acb\u5206\u8eab\u3002\u53ea\u8981\u6ca1\u6709\u5c06\u6240\u6709\u8759\u8760\u90fd\u6d88\u706d\uff0c\u5438\u8840\u9b3c\u5c31\u6ca1\u6709\u88ab\u771f\u6b63\u6467\u6bc1\u2014\u2014\u4f46\u5e76\u4e0d\u7b49\u4e8e\u4e0d\u53d7\u4f24\u5bb3\u3002\u521a\u624d\u970d\u6587\u53d8\u5316\u51fa\u7684\u8759\u8760\u5927\u7ea6\u6709\u4e09\u56db\u5341\u53ea\uff0c\u53ea\u6709\u4e00\u53ea\u9003\u8131\uff0c\u5176\u4f59\u90fd\u88ab\u5723\u5149\u6467\u6bc1\uff0c\u53d7\u521b\u4e4b\u91cd\u53ef\u60f3\u800c\u77e5\uff0c\u731c\u6d4b\u8d77\u6765\uff0c\u5dee\u4e0d\u591a\u5df2\u7ecf\u662f\u201c\u6fd2\u6b7b\u201d\u72b6\u6001\u4e86\u3002</p><p>\u743c\u6069\u6361\u8d77\u88ab\u9707\u8131\u624b\u7684\u98ce\u4e4b\u5203\uff0c\u53c8\u5c06\u4e24\u5c0a\u5973\u796d\u53f8\u9b54\u50cf\u6062\u590d\u6210\u68cb\u5b50\uff0c\u6536\u5165\u6000\u4e2d\uff0c\u53e6\u5916\u8fd8\u6709\u4e24\u5c0a\u6218\u58eb\u9b54\u50cf\uff0c\u6b64\u523b\u6b63\u6df1\u9677\u5728\u6ce5\u6cbc\u4e4b\u4e2d\uff0c\u56e0\u4e3a\u5931\u53bb\u63a7\u5236\uff0c\u5e94\u8be5\u5df2\u7ecf\u53d8\u56de\u4e86\u68cb\u5b50\u5f62\u6001\uff0c\u4e00\u65f6\u95f4\u65e0\u6687\u53bb\u7ba1\uff0c\u7b49\u56de\u6765\u518d\u6162\u6162\u6316\u5427\u3002\u65cb\u98ce\u4ece\u811a\u4e0b\u805a\u96c6\uff0c\u8d77\u521d\u5fae\u5f31\uff0c\u8f6c\u773c\u53d8\u5f97\u731b\u70c8\uff0c\u5c06\u4ed6\u7684\u8eab\u4f53\u6258\u8d77\uff0c\u5feb\u901f\u5f80\u4e0a\u98de\u53bb\u3002</p><p>\u4e09\u5206\u949f\u540e\uff0c\u4ed6\u63a5\u8fd1\u4e86\u5c71\u9876\uff0c\u6b63\u8981\u5bfb\u627e\u90a3\u53ea\u5b9d\u77f3\u9f99\u7684\u9f99\u7a9f\u6d1e\u53e3\uff0c\u731b\u7136\u95f4\u8033\u8fb9\u4f20\u6765\u9707\u8033\u6b32\u804b\u7684\u7206\u70b8\u58f0\u3002</p><p>\u8f70\u8f70\u8f70\u8f70\u8f70\u8f70\u8f70\uff01\u4eff\u4f5b\u5343\u4e07\u5428\u70b8\u836f\u540c\u65f6\u5f15\u7206\uff0c\u539f\u672c\u5e73\u9759\u77d7\u7acb\u7684\u86c7\u708e\u5c71\u731b\u70c8\u5730\u98a4\u52a8\u6447\u6643\u8d77\u6765\uff0c\u60ca\u5929\u52a8\u5730\u7684\u529b\u91cf\u81ea\u5c71\u4f53\u5185\u90e8\u6c79\u6d8c\u900f\u51fa\uff0c\u5c06\u5706\u9525\u5f62\u7684\u5cf0\u5ce6\u7c89\u788e\u6210\u4e86\u65e0\u6570\u5757\uff0c\u5927\u5982\u78e8\u76d8\u7684\u5ca9\u77f3\u7eb7\u7eb7\u5760\u843d\uff0c\u4eff\u4f5b\u66b4\u96e8\u3002\u5728\u8fd9\u9686\u9686\u7206\u70b8\u58f0\u4e2d\uff0c\u53c8\u4f20\u6765\u4e00\u58f0\u82cd\u8001\u800c\u60a0\u957f\u3001\u76f4\u8fbe\u4e5d\u9704\u4e91\u4e0a\u7684\u9f99\u541f\uff0c\u743c\u6069\u773c\u89d2\u4f59\u5149\u77a5\u53bb\uff0c\u6b63\u89c1\u4e00\u56e2\u707f\u70c2\u8679\u5f69\u81ea\u5c71\u4f53\u5185\u90e8\u7535\u5c04\u800c\u8d77\uff0c\u5f53\u4e2d\u9690\u9690\u73b0\u51fa\u72ec\u89d2\u56db\u7ffc\u7684\u5de8\u9f99\u4e4b\u5f62\uff0c\u671d\u7740\u897f\u5317\u65b9\u5411\u75be\u901f\u98de\u53bb\u3002</p><p>\u7136\u540e\u5ca9\u77f3\u4e4b\u96e8\u94fa\u5929\u76d6\u5730\u5730\u503e\u6cfb\u4e0b\u6765\uff0c\u5c06\u4ed6\u5f7b\u5e95\u6df9\u6ca1\u3002</p><p>\u203b\u203b\u203b</p><p>\u5728\u743c\u6069\u88ab\u5ca9\u77f3\u96e8\u541e\u566c\u7684\u540c\u65f6\uff0c\u5c71\u8170\u7684\u6811\u6797\u7a7a\u5730\u4e2d\uff0c\u7a81\u5140\u5730\u522e\u8d77\u4e86\u4e00\u9635\u98ce\u3002</p><p>\u98ce\u81ea\u5730\u5e95\u800c\u8d77\uff0c\u901f\u5ea6\u4e0d\u5feb\uff0c\u529b\u9053\u4e5f\u4e0d\u5f3a\uff0c\u5374\u5728\u51e0\u79d2\u949f\u91cc\u5c31\u5f62\u6210\u4e86\u8089\u773c\u6e05\u6670\u53ef\u89c1\u7684\u87ba\u65cb\u6c14\u65cb\u3002\u5730\u9762\u4e0a\u7684\u9ed1\u8272\u7070\u70ec\u2014\u2014\u90a3\u662f\u8759\u8760\u4eec\u88ab\u5723\u5149\u7167\u5c04\u70e7\u5316\u4e4b\u540e\u7684\u201c\u6b8b\u9ab8\u201d\u2014\u2014\u4eff\u4f5b\u88ab\u4e00\u53cc\u65e0\u5f62\u7684\u624b\u62e8\u52a8\u7740\uff0c\u7eb7\u7eb7\u81ea\u52a8\u98de\u8d77\uff0c\u6295\u5165\u5176\u4e2d\u3002\u6c14\u65cb\u81ea\u8eab\u8d8a\u65cb\u8d8a\u6025\uff0c\u5374\u5448\u4e00\u6761\u7ebf\u7b14\u76f4\u524d\u884c\uff0c\u79bb\u5f00\u7a7a\u5730\uff0c\u79fb\u52a8\u5230\u6811\u836b\u4e0b\u3002\u4e00\u53ea\u53cc\u773c\u7ea2\u5149\u95ea\u70c1\u7684\u9ed1\u8272\u8759\u8760\u81ea\u9ed1\u6697\u4e2d\u98de\u51fa\uff0c\u6295\u5165\u6c14\u65cb\uff0c\u7d27\u63a5\u7740\u7a7a\u6c14\u7684\u6d41\u52a8\u4fbf\u5b8c\u5168\u505c\u6ede\u4e86\u3002\u98ce\u6084\u7136\u6563\u53bb\uff0c\u663e\u9732\u51fa\u4e00\u4e2a\u5168\u8eab\u8d64\u88f8\u7684\u7537\u5b50\u8eab\u5f71\uff0c\u6b63\u662f\u660e\u660e\u5df2\u7ecf\u88ab\u743c\u6069\u6740\u6b7b\u7684\u5438\u8840\u9b3c\u970d\u6587\u3002</p><p>\u201c\u54e6\u54df\u54e6\u54df\uff0c\u8fd9\u4e0d\u662f\u6211\u4eb2\u7231\u7684\u5b66\u751f\u5361\u5e03\u5207\u8bfa\u561b\uff1f\u201d\u5145\u6ee1\u63f6\u63c4\u7684\u8bed\u6c14\uff0c\u4f46\u660e\u663e\u5e76\u65e0\u4ec0\u4e48\u6076\u610f\u7684\u58f0\u97f3\u7a81\u7136\u4ece\u5934\u9876\u4f20\u6765\uff0c\u4f34\u968f\u7740\u4f4e\u4f4e\u7684\u7a83\u7b11\uff0c\u201c\u4f5c\u4e3a\u4e00\u53ea\u5438\u8840\u9b3c\uff0c\u5149\u5929\u5316\u65e5\u4e4b\u4e0b\u88f8\u5954\uff0c\u4f60\u7684\u7231\u597d\u771f\u662f\u522b\u51fa\u5fc3\u88c1\u554a\u3002\u201d</p><p>\u201c\u90a3\u4e5f\u6bd4\u4f60\u8fd9\u4e2a\u5077\u7aa5\u72c2\u597d\u5f97\u591a\u3002\u201d\u5438\u8840\u9b3c\u5934\u4e5f\u4e0d\u62ac\u5730\u53cd\u5507\u76f8\u8ba5\uff0c\u5149\u7ebf\u5728\u4ed6\u7684\u76ae\u80a4\u8868\u9762\u8f7b\u5fae\u5730\u626d\u66f2\u6298\u5c04\uff0c\u53d8\u5e7b\u51fa\u4e00\u5957\u9ed1\u8272\u7684\u8863\u670d\uff0c\u906e\u853d\u4f4f\u8eab\u4f53\u3002\u7d27\u63a5\u7740\uff0c\u4e24\u6839\u8513\u85e4\u4ece\u8896\u53e3\u4e2d\u5ef6\u4f38\u51fa\u6765\uff0c\u5b83\u4eec\u7c97\u82e5\u5b69\u7ae5\u624b\u81c2\uff0c\u957f\u6ee1\u5012\u523a\uff0c\u989c\u8272\u9ed1\u6c89\u6c89\u7684\uff0c\u8868\u76ae\u5e72\u67af\u76b4\u88c2\uff0c\u50cf\u662f\u65e9\u5df2\u67af\u840e\uff0c\u884c\u52a8\u5374\u5341\u5206\u7075\u5de7\uff0c\u4eff\u4f5b\u87d2\u86c7\uff0c\u98de\u5feb\u5730\u8d34\u5730\u722c\u884c\u51fa\u6811\u6797\uff0c\u94bb\u8fdb\u521a\u624d\u5438\u8840\u9b3c\u7528\u9b54\u6cd5\u5851\u6210\u7684\u6ce5\u6cbc\u4e2d\u3002\u51e0\u79d2\u949f\u540e\uff0c\u8513\u85e4\u53c8\u91cd\u65b0\u94bb\u51fa\uff0c\u5feb\u901f\u722c\u56de\u5230\u5438\u8840\u9b3c\u8eab\u65c1\uff0c\u9ad8\u9ad8\u6602\u8d77\uff0c\u6258\u7740\u4e24\u679a\u9ed1\u66dc\u77f3\u5236\u6210\u7684\u8428\u74e6\u68cb\u5b50\u3002</p><p>\u5438\u8840\u9b3c\u4f38\u624b\u53d6\u8fc7\u68cb\u5b50\uff0c\u653e\u5728\u638c\u5fc3\u7ffb\u6765\u8986\u53bb\u770b\u4e86\u51e0\u904d\uff0c\u201c\u8fd9\u662f\u4ec0\u4e48\uff1f\u201d\u4ed6\u95ee\u3002</p><p>\u201c\u8428\u74e6\u68cb\uff0c\u201d\u5934\u9876\u4f20\u6765\u7684\u58f0\u97f3\u4e0d\u7d27\u4e0d\u6162\u5730\u56de\u7b54\uff0c\u201c\u90a3\u4e9b\u9ed1\u76ae\u7cbe\u7075\u4eec\u559c\u6b22\u73a9\u7684\u4e00\u79cd\u6218\u68cb\u3002\u201d</p><p>\u201c\u5e9f\u8bdd\uff01\u6211\u5f53\u7136\u77e5\u9053\u8fd9\u662f\u8428\u74e6\u68cb\uff0c\u201d\u5438\u8840\u9b3c\u6ca1\u597d\u6c14\u5730\u8bf4\uff0c\u201c\u4f46\u5b83\u4eec\u4f1a\u53d8\u6210\u9b54\u50cf\u2014\u2014\u201d</p><p>\u6251\u5566\u5566\u4e00\u58f0\u54cd\uff0c\u6253\u65ad\u4e86\u5438\u8840\u9b3c\u7684\u8bdd\uff0c\u9ed1\u8272\u7684\u9e1f\u4ece\u5934\u9876\u98de\u4e0b\u6765\uff0c\u655b\u7fc5\u843d\u5728\u4e00\u6839\u679d\u6860\u7684\u672b\u7aef\uff0c\u968f\u98ce\u4e0a\u4e0b\u8f7b\u6446\u3002\u90a3\u662f\u53ea\u4e4c\u9e26\uff0c\u6a21\u6837\u770b\u8d77\u6765\u5f88\u666e\u901a\uff0c\u4e0e\u5bfb\u5e38\u540c\u7c7b\u65e0\u5f02\uff0c\u4f46\u4ed4\u7ec6\u89c2\u5bdf\u7684\u8bdd\u5c31\u4f1a\u53d1\u73b0\u5b83\u5176\u5b9e\u4e0d\u662f\u6d3b\u7269\uff0c\u800c\u662f\u7528\u6728\u5934\u96d5\u6210\u7684\uff0c\u5237\u4e0a\u9ed1\u8272\u989c\u6599\uff0c\u7fbd\u6bdb\u867d\u7136\u6829\u6829\u5982\u771f\uff0c\u5374\u662f\u7eb9\u98ce\u4e0d\u52a8\uff0c\u773c\u73e0\u5012\u662f\u8f6c\u5f97\u5f88\u5feb\u3002\u201c\u4ed6\u6709\u80fd\u591f\u53d8\u6210\u9b54\u50cf\u7684\u68cb\u5b50\uff0c\u8fd9\u70b9\u6211\u63d0\u9192\u8fc7\u4f60\u7684\uff0c\u201d\u5b83\u8bf4\uff0c\u201c\u4f60\u5927\u610f\u4e86\u3002\u201d</p><p>\u4e4c\u9e26\u8bf4\u8bdd\u7684\u65f6\u5019\uff0c\u5634\u4e1d\u6beb\u6ca1\u6709\u5f20\u5f00\uff0c\u58f0\u97f3\u662f\u76f4\u63a5\u4ece\u8179\u8154\u4e2d\u53d1\u51fa\u7684\uff0c\u6240\u4ee5\u542c\u8d77\u6765\u9887\u4e3a\u542b\u6df7\u6c89\u95f7\uff0c\u660e\u663e\u5df2\u7ecf\u53d8\u5f62\u8d70\u6837\u3002\u5c3d\u7ba1\u5982\u6b64\uff0c\u5982\u679c\u743c\u6069\u6216\u8005\u6885\u83f2\u65af\u5728\u573a\u7684\u8bdd\uff0c\u80af\u5b9a\u80fd\u591f\u8f7b\u6613\u8fa8\u8ba4\u51fa\u8bf4\u8bdd\u8005\u7684\u8eab\u4efd\u3002\u5438\u8840\u9b3c\u54fc\u4e86\u4e00\u58f0\uff0c\u201c\u4f46\u4f60\u53ef\u4ece\u6765\u6ca1\u544a\u8bc9\u6211\u8fd9\u4e9b\u9b54\u50cf\u8fd8\u80fd\u50cf\u7267\u5e08\u4e00\u6837\u91ca\u653e\u795e\u672f\u3002\u201d</p><p>\u201c\u62b1\u6b49\uff0c\u8fd9\u70b9\u6211\u4e5f\u662f\u521a\u521a\u5f97\u77e5\u3002\u201d</p><p>\u5438\u8840\u9b3c\u6b32\u8a00\u53c8\u6b62\uff0c\u6700\u7ec8\u8fd8\u662f\u51b3\u5b9a\u653e\u5f03\u5728\u8fd9\u4e2a\u8bdd\u9898\u4e0a\u7ee7\u7eed\u7ea0\u7f20\u3002\u201c\u6211\u62ff\u5230\u4e86\u4ed6\u7684\u8840\uff0c\u201d\u4ed6\u7b80\u5355\u5730\u8bf4\uff0c\u62ac\u8d77\u53f3\u624b\uff0c\u98df\u6307\u7684\u6307\u5c16\u4e0a\u60ac\u6d6e\u7740\u4e00\u9897\u5c0f\u5c0f\u7684\u8840\u73e0\uff0c\u6ef4\u6e9c\u6e9c\u5730\u65cb\u8f6c\u4e0d\u505c\uff0c\u201c\u4f60\u60f3\u77e5\u9053\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u4ed6\u7684\u6240\u6709\u8bb0\u5fc6\u3002\u201d\u4e4c\u9e26\u56de\u7b54\u3002</p><p>\u201c\u6240\u6709\uff1f\u201d\u5438\u8840\u9b3c\u60ca\u8bb6\u5730\u53cd\u95ee\u3002</p><p>\u4ed6\u662f\u9ad8\u9636\u8840\u65cf\uff0c\u80fd\u591f\u4ece\u8840\u6db2\u4e2d\u8bfb\u53d6\u751f\u7269\u7684\u8bb0\u5fc6\uff0c\u4f46\u8fd9\u79cd\u80fd\u529b\u5e76\u4e0d\u662f\u6ca1\u6709\u9650\u5236\uff0c\u66f4\u4e0d\u662f\u5168\u65e0\u4ee3\u4ef7\u7684\u3002\u4e8b\u5b9e\u4e0a\uff0c\u8bfb\u53d6\u4e00\u4e2a\u4eba\u7684\u5168\u90e8\u8bb0\u5fc6\uff0c\u8fd9\u79cd\u5938\u5f20\u7684\u4e8b\u60c5\u5728\u4ed6\u51e0\u767e\u5e74\u7684\u751f\u547d\u5386\u7a0b\u4e2d\u8fd8\u4ece\u6ca1\u5c1d\u8bd5\u8fc7\u5462\u3002</p><p>\u201c\u6240\u6709\u3002\u201d\u4e4c\u9e26\u786e\u8ba4\u3002</p><p>\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u5438\u8840\u9b3c\u4e0d\u89e3\uff0c\u201c\u6709\u8fd9\u4e2a\u5fc5\u8981\u5417\uff1f\u4ed6\u53ea\u4e0d\u8fc7\u2026\u2026\u201d\u4ed6\u8038\u4e86\u8038\u80a9\uff0c\u201c\u53ea\u4e0d\u8fc7\u662f\u4e2a\u5bfb\u5e38\u4e4b\u8f88\u7f62\u4e86\u3002\u201d</p><p>\u6c89\u95f7\u7684\u7b11\u58f0\u4ece\u4e4c\u9e26\u7684\u8179\u8154\u4e2d\u53d1\u51fa\u6765\uff0c\u900f\u7740\u8ba9\u4eba\u6349\u6478\u4e0d\u900f\u7684\u610f\u5473\u3002\u201c\u5bfb\u5e38\u4e4b\u8f88\uff1f\u201d\u5b83\u8bf4\uff0c\u201c\u80fd\u591f\u8ba9\u51ef\u745f\u7433\u5bfb\u627e\u6570\u5343\u5e74\u7684\u4eba\uff0c\u65e0\u8bba\u5982\u4f55\u4e5f\u4e0d\u80fd\u7528\u2018\u5bfb\u5e38\u2019\u8fd9\u4e2a\u8bcd\u6765\u8bc4\u4ef7\u5427\u3002\u201d</p></article>"}}}]);
