(window.webpackJsonp=window.webpackJsonp||[]).push([[515],{537:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u56db\u5341\u4e03\u8282 \u68a6\u9b47\u4e0e\u5e0c\u671b",content:"<article><h2>\u7b2c\u56db\u5341\u4e03\u8282 \u68a6\u9b47\u4e0e\u5e0c\u671b</h2><p>\u72ed\u7a84\u7684\u77f3\u9636\u50cf\u86c7\u822c\u873f\u8712\u76d8\u65cb\u7740\uff0c\u4e00\u5708\u4e00\u5708\u5730\u5411\u4e0b\u5ef6\u4f38\uff0c\u901a\u5411\u5730\u5e95\u7684\u6700\u6df1\u5904\uff0c\u90a3\u91cc\u662f\u65e0\u8fb9\u65e0\u9645\u7684\u9ed1\u6697\u3002\u9774\u5e95\u8e29\u8e0f\u5728\u575a\u786c\u7c97\u7cd9\u7684\u5730\u9762\u4e0a\uff0c\u53d1\u51fa\u8f7b\u5fae\u6469\u64e6\u58f0\uff0c\u88ab\u4e24\u4fa7\u7684\u77f3\u58c1\u4ea4\u9519\u53cd\u5f39\u56de\u6765\uff0c\u5728\u5bc2\u9759\u4e2d\u663e\u5f97\u683c\u5916\u6e05\u6670\u3002\u4e00\u80a1\u9634\u5bd2\u7684\u6c14\u6d41\u81ea\u524d\u65b9\u6d8c\u6765\uff0c\u5c11\u5973\u60c5\u4e0d\u81ea\u7981\u5730\u6253\u4e86\u4e2a\u51b7\u98a4\uff0c\u808c\u80a4\u4e0a\u6cdb\u8d77\u7ec6\u5c0f\u7684\u5bd2\u6817\uff0c\u5979\u6212\u5907\u7740\uff0c\u501f\u52a9\u94f6\u5251\u4e0a\u7684\u5fae\u5f31\u53cd\u5149\u7f13\u7f13\u524d\u884c\u3002\u4e00\u6bb5\u65f6\u95f4\u4e4b\u540e\u2014\u2014\u6216\u8bb8\u662f\u5341\u51e0\u5206\u949f\uff0c\u6216\u8bb8\u66f4\u4e45\uff0c\u5c11\u5973\u7ec8\u4e8e\u62b5\u8fbe\u8fd9\u6761\u5730\u4e0b\u752c\u9053\u7684\u5c3d\u5934\uff0c\u77f3\u9636\u6d88\u5931\u4e86\uff0c\u5979\u770b\u89c1\u4e00\u6247\u9ed1\u8272\u7684\u95e8\uff0c\u865a\u63a9\u7740\uff0c\u6a58\u9ec4\u8272\u7684\u5149\u4ece\u7f1d\u9699\u91cc\u900f\u51fa\uff0c\u4f34\u968f\u7740\u9690\u9690\u7ea6\u7ea6\u7684\u52a8\u9759\u3002</p><p>\u201c\u90a3\u6247\u95e8\u4e4b\u540e\uff0c\u662f\u4f60\u6240\u6709\u7684\u68a6\u9b47\uff0c\u548c\u5168\u90e8\u7684\u5149\u660e\u3002\u201d</p><p>\u4eff\u4f5b\u6709\u58f0\u97f3\u5728\u8111\u6d77\u91cc\u5982\u662f\u8bf4\uff0c\u4f46\u5c11\u5973\u5e76\u6ca1\u6709\u7406\u776c\uff0c\u5979\u5c4f\u606f\u51dd\u795e\uff0c\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u8d70\u4e0a\u524d\u3002\u95e8\u662f\u80e1\u6843\u6728\u6750\u8d28\uff0c\u7eb9\u7406\u7ec6\u5bc6\uff0c\u5747\u5300\u5730\u5237\u7740\u6cb9\u6f06\uff0c\u56db\u4e2a\u89d2\u4e0a\u96d5\u523b\u7740\u96f7\u9706\u95ea\u7535\u7684\u56fe\u6848\uff0c\u800c\u5728\u95e8\u7684\u4e2d\u95f4\u90e8\u5206\u5219\u753b\u7740\u4e00\u53ea\u5de8\u5927\u7684\u725b\u5934\u3002\u5728\u725b\u5934\u7684\u7709\u5fc3\u90e8\u4f4d\uff0c\u534a\u7741\u534a\u95ed\u7684\u6f20\u7136\u53cc\u773c\u4e4b\u95f4\uff0c\u6709\u67d0\u4e2a\u7070\u767d\u8272\u3001\u8fd1\u4f3c\u692d\u5706\u5f62\u7684\u7269\u4f53\u5d4c\u5728\u5176\u4e2d\uff0c\u6cdb\u7740\u51b0\u51b7\u7684\u5149\u6cfd\u3002\u5c11\u5973\u5c48\u8d77\u5de6\u817f\uff0c\u7f13\u7f13\u534a\u8e72\uff0c\u8ba9\u89c6\u7ebf\u4e0e\u90a3\u4e2a\u7070\u767d\u8272\u7269\u4f53\u5904\u4e8e\u540c\u4e00\u6c34\u5e73\u7ebf\u4e0a\uff0c\u7136\u540e\u5979\u770b\u6e05\u695a\u4e86\uff1a\u90a3\u662f\u4e00\u53ea\u4eba\u7c7b\u7684\u5934\u9aa8\uff0c\u53e3\u4e2d\u5374\u53ea\u6709\u4e0a\u4e0b\u5341\u4e8c\u9897\u7259\u9f7f\uff0c\u6bcf\u9897\u7259\u9f7f\u7684\u4e2d\u5fc3\u90e8\u4f4d\u94bb\u5b54\uff0c\u7f00\u7740\u7ec6\u788e\u7684\u661f\u84dd\u5f69\u94bb\u3002</p><p>\u8fd9\u4e2a\u6807\u5fd7\u2026\u2026\u4e3a\u4ec0\u4e48\u89c9\u5f97\u5f88\u719f\u6089\uff1f\u50cf\u662f\u66fe\u7ecf\u89c1\u8fc7\uff0c\u800c\u4e14\u8fd8\u4e0d\u6b62\u4e00\u6b21\u3002\u5c11\u5973\u79c0\u7709\u8e59\u8d77\uff0c\u52aa\u529b\u601d\u7d22\u7740\uff0c\u5728\u8bb0\u5fc6\u4e2d\u641c\u5bfb\u76f8\u5173\u4fe1\u606f\uff0c\u6700\u7ec8\u5374\u4e00\u65e0\u6240\u83b7\u3002\u96be\u9053\u662f\u9519\u89c9\u5417\uff1f\u5979\u5947\u602a\u5730\u81ea\u5fd6\uff0c\u51b3\u5b9a\u6682\u65f6\u5148\u5c06\u5b83\u653e\u5728\u4e00\u8fb9\u3002</p><p>\u56e0\u4e3a\u8ddd\u79bb\u7f29\u77ed\uff0c\u95e8\u540e\u7684\u52a8\u9759\u5df2\u7ecf\u80fd\u591f\u542c\u5f97\u66f4\u6e05\u6670\uff0c\u90a3\u662f\u79cd\u5947\u602a\u7684\u58f0\u97f3\uff0c\u65ad\u65ad\u7eed\u7eed\uff0c\u5ffd\u9ad8\u5ffd\u4f4e\uff0c\u50cf\u662f\u7537\u6027\u75b2\u60eb\u4e0d\u582a\u7684\u7c97\u91cd\u5598\u606f\uff0c\u53c8\u50cf\u662f\u5973\u5b50\u5982\u6ce3\u5982\u8bc9\u7684\u54c0\u5a49\u547b\u541f\uff0c\u5939\u6742\u7740\u6e05\u8106\u7684\u76ae\u8089\u649e\u51fb\u58f0\uff0c\u542b\u6df7\u6742\u4e71\uff0c\u5374\u4ee4\u5c11\u5973\u83ab\u540d\u5730\u5fc3\u8df3\u52a0\u901f\uff0c\u8138\u988a\u4eff\u4f5b\u88ab\u706b\u70e7\u8fc7\u822c\uff0c\u5239\u90a3\u95f4\u53d8\u5f97\u901a\u7ea2\u3002\u5979\u5b9a\u4e86\u5b9a\u795e\uff0c\u4e0b\u610f\u8bc6\u5730\u6d3b\u52a8\u624b\u6307\uff0c\u8fd9\u624d\u53d1\u73b0\u81ea\u5df1\u63e1\u5251\u7684\u53f3\u624b\u56e0\u4e3a\u592a\u8fc7\u7528\u529b\uff0c\u638c\u5fc3\u5df2\u7ecf\u4e0d\u77e5\u4e0d\u89c9\u95f4\u6c81\u51fa\u4e86\u5fae\u6c57\u3002</p><p>\u201c\u89c1\u9b3c\uff01\u201d</p><p>\u5c11\u5973\u8f7b\u8f7b\u5550\u4e86\u53e3\uff0c\u5bf9\u81ea\u5df1\u7684\u8868\u73b0\u975e\u5e38\u4e0d\u6ee1\u610f\uff0c\u4f46\u73b0\u5728\u5e76\u4e0d\u662f\u81ea\u6211\u68c0\u8ba8\u7684\u65f6\u5019\u3002\u5979\u7f13\u7f13\u8c03\u5300\u547c\u5438\uff0c\u8ba9\u5fc3\u8df3\u6062\u590d\u6b63\u5e38\uff0c\u7136\u540e\u4f38\u624b\u6309\u5728\u865a\u63a9\u7684\u95e8\u4e0a\uff0c\u7a0d\u7a0d\u7528\u529b\uff0c\u5c06\u7f1d\u9699\u6084\u65e0\u58f0\u606f\u5730\u6269\u5927\uff0c\u7136\u540e\u9519\u8eab\u95ea\u5165\u3002</p><p>\u7136\u540e\u5c11\u5973\u88ab\u773c\u524d\u7684\u666f\u8c61\u60ca\u5446\u4e86\u3002</p><p>\u5979\u770b\u89c1\u4e86\u4e00\u95f4\u91d1\u78a7\u8f89\u714c\u7684\u5730\u4e0b\u5ba4\uff0c\u56db\u9762\u5899\u58c1\u4e0a\u7684\u706b\u70ac\u5c06\u5b83\u7167\u5f97\u660e\u4eae\u5802\u5802\uff0c\u4eff\u4f5b\u767d\u663c\u3002\u6c49\u767d\u7389\u77f3\u94fa\u6210\u7684\u5730\u9762\u4e0a\uff0c\u6697\u7ea2\u8272\u7ebf\u6761\u7eb5\u6a2a\u4ea4\u9519\uff0c\u72f0\u72de\u626d\u66f2\uff0c\u63cf\u7ed8\u51fa\u4e00\u53ea\u5de8\u5927\u7684\u9ab7\u9ac5\u5934\u9aa8\u56fe\u6848\uff0c\u800c\u5728\u5934\u9aa8\u56fe\u6848\u7684\u4e2d\u5fc3\uff0c\u4e5f\u5373\u539f\u672c\u9f3b\u6881\u5e94\u8be5\u6240\u5728\u7684\u4f4d\u7f6e\uff0c\u662f\u4e00\u5ea7\u4e03\u8292\u661f\u5f62\u796d\u575b\u3002\u8fd1\u767e\u540d\u5168\u8eab\u8d64\u88f8\u7684\u4eba\u7c7b\u2014\u2014\u5305\u62ec\u7537\u4eba\u548c\u5973\u4eba\u2014\u2014\u73af\u7ed5\u5728\u796d\u575b\u7684\u5468\u56f4\uff0c\u6b63\u5728\u6fc0\u60c5\u4ea4\u5abe\uff0c\u4e91\u96e8\u6b22\u597d\uff0c\u4e0d\u65ad\u53d8\u6362\u7740\u5404\u79cd\u59ff\u52bf\uff0c\u5c11\u5973\u5728\u95e8\u5916\u542c\u5230\u7684\u5947\u5f02\u52a8\u9759\u4fbf\u662f\u4ed6\u4eec\u6240\u53d1\u51fa\u3002</p><p>\u8fd9\u662f\u4ec0\u4e48\u2026\u2026\u5730\u4e0b\u4e71\u4ea4\u6d3e\u5bf9\u5417\uff1f</p><p>\u9ed1\u8272\u3001\u9ec4\u8272\u548c\u767d\u8272\u7684\u8089\u4f53\u9519\u4e71\u5730\u7ede\u7f20\u5728\u4e00\u8d77\uff0c\u56e0\u4e3a\u5267\u70c8\u8fd0\u52a8\u7684\u7f18\u6545\uff0c\u76ae\u80a4\u8868\u9762\u8986\u76d6\u4e0a\u4e00\u5c42\u4eae\u6676\u6676\u7684\u6c57\u6db2\uff0c\u5728\u706f\u5149\u4e0b\u662f\u5982\u6b64\u7684\u70ab\u76ee\u3002\u7a7a\u6c14\u4e2d\u5f25\u6f2b\u7740\u602a\u5f02\u7684\u6df7\u5408\u5473\u9053\uff0c\u50cf\u662f\u9e9d\u9999\uff0c\u53c8\u5e26\u7740\u8165\u81bb\u3002\u6216\u8bb8\u662f\u592a\u8fc7\u6295\u5165\u7684\u7f18\u6545\u5427\uff0c\u5bf9\u4e8e\u201c\u623f\u95f4\u91cc\u7a81\u7136\u591a\u4e86\u4e00\u4e2a\u964c\u751f\u4eba\u201d\u8fd9\u4e2a\u4e8b\u5b9e\uff0c\u6240\u6709\u4eba\u90fd\u6ca1\u6709\u505a\u51fa\u4efb\u4f55\u53cd\u5e94\uff0c\u5373\u4fbf\u662f\u773c\u5149\u63a0\u8fc7\uff0c\u4e5f\u5b9b\u82e5\u89c6\u800c\u4e0d\u89c1\u3002\u5c11\u5973\u9759\u9759\u5730\u7ad9\u5728\u95e8\u8fb9\uff0c\u6c89\u601d\u4e86\u7247\u523b\uff0c\u7a7f\u8fc7\u62e5\u6324\u7684\u4eba\u7fa4\uff0c\u671d\u7740\u796d\u575b\u8d70\u8fc7\u6765\u3002</p><p>\u5979\u8d70\u5f97\u5f88\u6162\uff0c\u4f46\u6b65\u4f10\u6c89\u7a33\uff0c\u6beb\u65e0\u72b9\u8c6b\u8fdf\u7591\uff0c\u50cf\u662f\u4e0b\u5b9a\u4e86\u67d0\u79cd\u51b3\u5fc3\u3002\u4eff\u4f5b\u662f\u4e0e\u6b64\u547c\u5e94\uff0c\u539f\u672c\u5e73\u9759\u7684\u796d\u575b\u4e0a\u7a7a\u6084\u65e0\u58f0\u606f\u5730\u51fa\u73b0\u4e86\u9ed1\u8272\u96fe\u6c14\uff0c\u626d\u66f2\u7740\uff0c\u53d8\u6210\u6f29\u6da1\u5f62\u72b6\uff0c\u7d27\u63a5\u7740\uff0c\u4e00\u4e2a\u4eba\u5f71\u4ece\u6f29\u6da1\u4e2d\u8d70\u51fa\u6765\u3002</p><p>\u90a3\u662f\u4f4d\u7f8e\u4e3d\u65e0\u6bd4\u7684\u5973\u5b50\uff0c\u6de1\u91d1\u8272\u957f\u53d1\u677e\u677e\u633d\u6210\u53d1\u9afb\uff0c\u6df1\u78a7\u8272\u773c\u7738\u4e2d\u95ea\u70c1\u7740\u4ece\u5bb9\u4e0d\u8feb\u7684\u7b11\u610f\uff0c\u4f18\u96c5\u5730\u7ad9\u5728\u796d\u575b\u4e0a\uff0c\u4fef\u89c6\u7740\uff0c\u4eff\u4f5b\u541b\u4e34\u81e3\u6c11\u7684\u5973\u738b\u3002\u548c\u5730\u4e0b\u5ba4\u91cc\u7684\u5176\u4ed6\u4eba\u4e00\u6837\uff0c\u5979\u5168\u8eab\u8d64\u88f8\uff0c\u4e00\u4e1d\u4e0d\u6302\uff0c\u80f8\u90e8\u5e76\u4e0d\u5927\uff0c\u7cbe\u81f4\u800c\u5c0f\u5de7\uff0c\u76c8\u76c8\u4e00\u624b\u53ef\u63e1\uff0c\u8170\u80a2\u7ea4\u7ec6\uff0c\u4fee\u957f\u7b14\u633a\u7684\u53cc\u817f\u7d27\u5e76\u7740\uff0c\u6324\u538b\u5f97\u5c0f\u8179\u4e0b\u65b9\u7684\u4e09\u89d2\u5730\u5e26\u66f4\u52a0\u9971\u6ee1\u8bf1\u4eba\uff0c\u6d45\u6d45\u7684\u91d1\u8272\u7ed2\u6bdb\u8986\u76d6\u7740\uff0c\u5c06\u6700\u795e\u79d8\u7684\u82b1\u56ed\u9690\u85cf\u5728\u5176\u4e2d\u3002\u5c11\u5973\u4e0d\u6562\u7f6e\u4fe1\u5730\u770b\u7740\u5979\uff0c\u6709\u90a3\u4e48\u4e00\u77ac\u95f4\uff0c\u5979\u51e0\u4e4e\u4ea7\u751f\u9519\u89c9\uff0c\u4ee5\u4e3a\u81ea\u5df1\u6b63\u9762\u5bf9\u4e00\u9762\u955c\u5b50\uff0c\u56e0\u4e3a\u773c\u524d\u7684\u91d1\u53d1\u5973\u5b50\u548c\u5979\u81ea\u5df1\u662f\u5982\u6b64\u7684\u8096\u4f3c\uff0c\u65e0\u8bba\u9762\u5bb9\u3001\u8eab\u6750\uff0c\u90fd\u6d3b\u8131\u8131\u662f\u4e00\u4e2a\u6a21\u5b50\u523b\u51fa\u6765\u4f3c\u7684\u3002</p><p>\u4e0d\u8fc7\uff0c\u5982\u679c\u4ed4\u7ec6\u5206\u8fa8\u7684\u8bdd\uff0c\u5176\u5b9e\u8fd8\u662f\u80fd\u591f\u770b\u51fa\u5dee\u5f02\u7684\u3002\u867d\u7136\u76f8\u8c8c\u662f\u8fd1\u4e4e\u4e00\u6a21\u4e00\u6837\uff0c\u4f46\u6c14\u8d28\u8fd8\u662f\u6709\u7740\u660e\u663e\u7684\u4e0d\u540c\u3002\u796d\u575b\u4e0b\u7684\u5c11\u5973\uff0c\u8fd8\u5e26\u7740\u660e\u663e\u7684\u7a1a\u6c14\uff0c\u50cf\u662f\u9752\u6da9\u7684\u82f9\u679c\uff0c\u867d\u7136\u6e05\u9999\u6021\u4eba\uff0c\u4f46\u8fd8\u6ca1\u6709\u771f\u6b63\u5730\u201c\u6210\u719f\u201d\uff0c\u800c\u7ad9\u5728\u796d\u575b\u4e0a\u7684\u5973\u5b50\u5219\u662f\u5df2\u7ecf\u719f\u900f\uff0c\u5728\u77eb\u5065\u82f1\u6b66\u4e4b\u5916\u53c8\u591a\u4e86\u4e00\u4efd\u4e30\u8174\u5a07\u5a9a\uff0c\u843d\u843d\u5927\u65b9\uff0c\u98ce\u60c5\u4e07\u79cd\u3002\u731c\u6d4b\u8d77\u6765\uff0c\u4e24\u4eba\u7684\u5e74\u9f84\u5dee\u8ddd\u7ea6\u6709\u5341\u5c81\u5de6\u53f3\u5427\u3002</p><p>\u5c11\u5973\u6014\u6014\u5730\u770b\u7740\u796d\u575b\u4e0a\u7684\u91d1\u53d1\u5973\u5b50\uff0c\u5ffd\u7136\u95f4\u4fbf\u660e\u767d\u4e86\u8fc7\u6765\u3002\u8bb0\u5fc6\u7684\u95f8\u95e8\u8f70\u7136\u6253\u5f00\uff0c\u88ab\u81ea\u5df1\u523b\u610f\u5c18\u5c01\u7684\u753b\u9762\u5982\u6f6e\u6c34\u822c\u6d8c\u5165\u610f\u8bc6\u3002\u201c\u5988\u5988\uff01\u201d\u5979\u8f7b\u58f0\u547c\u5524\uff0c\u773c\u6cea\u4e0d\u7531\u81ea\u4e3b\u5730\u6ed1\u8fc7\u8138\u988a\uff0c\u843d\u5728\u5730\u9762\u4e0a\uff0c\u6e85\u8d77\u65e0\u6570\u6676\u74e3\u3002</p><p>\u5979\u5411\u524d\u8d70\u53bb\uff0c\u60f3\u8981\u8d70\u4e0a\u796d\u575b\uff0c\u6295\u5165\u6bcd\u4eb2\u7684\u6000\u62b1\u3002\u4f46\u521a\u521a\u8e0f\u51fa\u4e24\u6b65\uff0c\u4e00\u58f0\u4f4e\u6c89\u7684\u5636\u543c\u81ea\u91d1\u53d1\u5973\u5b50\u8eab\u540e\u7684\u6f29\u6da1\u4e2d\u4f20\u6765\uff0c\u5176\u4e2d\u6ee1\u8574\u7684\u72c2\u66b4\u6c14\u606f\u8ba9\u5c11\u5973\u5239\u90a3\u95f4\u8138\u8272\u82cd\u767d\uff0c\u4e0b\u610f\u8bc6\u5730\u505c\u4f4f\u811a\u6b65\uff0c\u53cc\u624b\u63e1\u7d27\u94f6\u5251\uff0c\u5168\u795e\u6212\u5907\u3002\u4f5c\u4e3a\u8eab\u7ecf\u767e\u6218\u7684\u5723\u6b66\u58eb\uff0c\u5979\u80fd\u591f\u6e05\u695a\u611f\u5e94\u5f97\u5230\uff0c\u90a3\u7247\u6f29\u6da1\u4e4b\u4e2d\uff0c\u9690\u85cf\u7740\u67d0\u4e2a\u529b\u91cf\u5e9e\u5927\u3001\u5371\u9669\u65e0\u6bd4\uff0c\u800c\u4e14\u51f6\u6076\u81f3\u6781\u7684\u602a\u517d\uff0c\u5373\u4fbf\u662f\u5de8\u9f99\u4e5f\u672a\u5fc5\u80fd\u591f\u4e0e\u4e4b\u76f8\u5bf9\u6297\u5427\u3002\u5728\u5979\u4ee5\u5f80\u7684\u7ecf\u5386\u4e2d\uff0c\u4ece\u672a\u906d\u9047\u8fc7\u8fd9\u6837\u5f3a\u5927\u7684\u654c\u4eba\u3002</p><p>\u9ed1\u8272\u7684\u5f71\u5b50\u4ece\u6f29\u6da1\u4e2d\u722c\u884c\u51fa\u6765\uff0c\u5728\u5730\u9762\u4e0a\u626d\u66f2\u805a\u5408\u7740\uff0c\u6700\u7ec8\u53d8\u6210\u4eba\u5f62\u3002\u90a3\u662f\u4e00\u4e2a\u8d64\u88f8\u7684\u4e2d\u5e74\u7537\u5b50\uff0c\u76ae\u80a4\u5f88\u9ed1\uff0c\u4e24\u988a\u7684\u76ae\u8089\u4eff\u4f5b\u5df2\u7ecf\u5e72\u67af\uff0c\u6df1\u6df1\u5185\u9677\uff0c\u8ba9\u9762\u90e8\u770b\u8d77\u6765\u5c31\u50cf\u662f\u4e2a\u9ab7\u9ac5\u5934\uff0c\u8116\u9888\u4ee5\u4e0b\u7684\u90e8\u5206\u5374\u662f\u808c\u8089\u53d1\u8fbe\uff0c\u5757\u5757\u5792\u8d77\uff0c\u4eff\u4f5b\u94a2\u94c1\u3002\u624b\u81c2\u6781\u957f\uff0c\u53cc\u817f\u5374\u7c97\u58ee\u800c\u77ed\uff0c\u6bd4\u4f8b\u975e\u5e38\u4e4b\u4e0d\u534f\u8c03\uff0c\u4eff\u4f5b\u733f\u7334\u3002\u5728\u4ed6\u7684\u80ef\u4e0b\uff0c\u4e00\u6839\u9ed1\u8272\u7684\u8089\u830e\u6740\u6c14\u817e\u817e\u5730\u77d7\u7acb\u7740\uff0c\u5448\u56db\u5341\u4e94\u5ea6\u89d2\u5411\u4e0a\u7fd8\u8d77\uff0c\u659c\u659c\u6307\u5411\u5929\u82b1\u677f\u3002</p><p>\u201c\u5f88\u5c0f\u561b\u3002\u201d\u5c11\u5973\u4e0d\u65e0\u6076\u610f\u5730\u5728\u5fc3\u4e2d\u4e0b\u4e86\u4e2a\u8bc4\u8bed\u3002</p><p>\u4f46\u796d\u575b\u4e0a\u7684\u4e24\u4e2a\u4eba\u663e\u7136\u90fd\u6ca1\u6709\u5728\u610f\u5c11\u5973\uff0c\u751a\u81f3\u50cf\u662f\u538b\u6839\u6ca1\u53d1\u73b0\u8fd9\u4e2a\u4eba\u7684\u5b58\u5728\u3002\u7537\u5b50\u8d70\u5230\u91d1\u53d1\u5973\u5b50\u8eab\u540e\uff0c\u53cc\u624b\u6309\u5728\u5979\u7684\u80a9\u4e0a\uff0c\u7a0d\u7a0d\u7528\u529b\u4e0b\u538b\uff0c\u91d1\u53d1\u5973\u5b50\u56de\u8fc7\u5934\u751c\u751c\u5730\u7b11\u4e86\u7b11\uff0c\u4e56\u5de7\u5730\u987a\u52bf\u8dea\u5012\uff0c\u5c06\u81c0\u90e8\u9ad8\u9ad8\u7fd8\u8d77\u3002\u7537\u5b50\u633a\u8d77\u8089\u830e\uff0c\u5bf9\u51c6\u4f4d\u7f6e\uff0c\u4e1d\u6beb\u4e0d\u4f5c\u524d\u620f\uff0c\u4fbf\u786c\u751f\u751f\u5730\u63d2\u4e86\u8fdb\u53bb\u3002\u91d1\u53d1\u5973\u5b50\u53d1\u51fa\u4e00\u58f0\u75db\u53eb\uff0c\u7f8e\u4e3d\u7684\u9762\u5bb9\u65e0\u6cd5\u6291\u5236\u5730\u626d\u66f2\u8d77\u6765\uff0c\u4f46\u968f\u5373\u6b7b\u6b7b\u54ac\u7d27\u5634\u5507\uff0c\u9ed8\u9ed8\u627f\u53d7\uff0c\u4e00\u8a00\u4e0d\u53d1\u3002</p><p>\u7537\u5b50\u6beb\u65e0\u601c\u9999\u60dc\u7389\u4e4b\u610f\uff0c\u5927\u8038\u5927\u5f04\uff0c\u6bcf\u6b21\u90fd\u662f\u76f4\u63d2\u5230\u5e95\uff0c\u7136\u540e\u5b8c\u5168\u9000\u51fa\uff0c\u5468\u800c\u590d\u59cb\uff0c\u52a8\u4f5c\u673a\u68b0\u800c\u5355\u8c03\uff0c\u8138\u4e0a\u4e5f\u5168\u65e0\u534a\u70b9\u6109\u60a6\u795e\u60c5\uff0c\u4eff\u4f5b\u4e0d\u662f\u5728\u505a\u4e00\u4ef6\u5feb\u4e50\u7f8e\u5999\u7684\u4e8b\u60c5\uff0c\u800c\u662f\u5b8c\u6210\u4efb\u52a1\u4f3c\u7684\u3002\u56e0\u4e3a\u5e45\u5ea6\u592a\u5927\uff0c\u6bcf\u4e00\u6b21\u8fdb\u51fa\u5973\u5b50\u7684\u4f53\u8154\u90fd\u53d1\u51fa\u5657\u3001\u5657\u7684\u6deb\u9761\u58f0\u54cd\u3002\u796d\u575b\u4e0b\u7684\u5c11\u5973\u76ee\u77aa\u53e3\u5446\u5730\u770b\u7740\u773c\u524d\u53d1\u751f\u7684\u60c5\u5f62\uff0c\u4e00\u65f6\u95f4\u624b\u8db3\u65e0\u63aa\uff0c\u4e0d\u77e5\u9053\u8be5\u505a\u4ec0\u4e48\u597d\u2014\u2014\u7136\u540e\u5979\u53d1\u73b0\u7537\u5b50\u7684\u4f53\u578b\u5728\u4e0d\u77e5\u4e0d\u89c9\u95f4\u53d1\u751f\u53d8\u5316\uff1a\u4ed6\u7684\u773c\u7736\u4e2d\uff0c\u539f\u672c\u9ed1\u767d\u5206\u660e\u7684\u773c\u73e0\u53d8\u6210\u4e86\u8840\u7ea2\u8272\uff0c\u989d\u5934\u90e8\u4f4d\u7684\u76ae\u8089\u88ab\u706b\u7130\u707c\u70e7\u4e00\u822c\u7126\u9ed1\uff0c\u4e24\u9897\u7ec6\u7ec6\u7684\u5c16\u89d2\u4ece\u4e2d\u751f\u957f\u51fa\u6765\uff0c\u624b\u8db3\u56db\u80a2\u53d8\u6210\u8fd1\u4f3c\u731b\u517d\u7684\u5229\u722a\u3002\u5728\u4ed6\u5f13\u8d77\u7684\u540e\u80cc\u4e0a\uff0c\u4e24\u6392\u950b\u5229\u7684\u9ed1\u8272\u9aa8\u523a\u6b63\u81ea\u810a\u690e\u4e2d\u4f9d\u6b21\u5f39\u8d77\uff0c\u53d1\u51fa\u5494\u3001\u5494\u3001\u5494\u3001\u5494\u7684\u58f0\u97f3\uff0c\u50cf\u662f\u94a2\u5200\u51fa\u9798\uff0c\u5bd2\u5149\u51db\u51db\u3002\u800c\u539f\u672c\u88ab\u5c11\u5973\u8bc4\u4ef7\u4e3a\u201c\u5f88\u5c0f\u201d\u7684\u9ed1\u8272\u8089\u830e\uff0c\u4e5f\u4e0d\u77e5\u4f55\u65f6\u5df2\u7ecf\u53d8\u5f97\u7c97\u58ee\u4e86\u4e00\u500d\uff0c\u8868\u9762\u51f8\u8d77\u65e0\u6570\u7ec6\u5c0f\u7684\u8089\u7624\u548c\u5012\u523a\uff0c\u6bcf\u6b21\u62bd\u51fa\u90fd\u5c06\u91d1\u53d1\u5973\u5b50\u5ae9\u81a3\u5185\u58c1\u7684\u7c89\u8272\u871c\u8089\u5e26\u5f97\u7ffb\u51fa\u6765\uff0c\u4e73\u767d\u8272\u788e\u6cab\u6df7\u5408\u7740\u9c9c\u7ea2\u8840\u4e1d\u56db\u5904\u98de\u6e85\uff0c\u5c06\u5730\u9762\u4e0a\u5f04\u5f97\u6591\u6591\u70b9\u70b9\u3002</p><p>\u201c\u653e\u5f00\u5979\uff0c\u602a\u7269\uff01\u201d</p><p>\u5c11\u5973\u6012\u559d\uff0c\u5954\u8dd1\u7740\u8dc3\u4e0a\u796d\u575b\uff0c\u7167\u7740\u5df2\u7ecf\u53d8\u5f62\u6210\u602a\u517d\u7684\u7537\u5b50\u5f53\u5934\u4e00\u5251\u65a9\u4e0b\u3002\u7537\u5b50\u4e0d\u95ea\u4e0d\u907f\uff0c\u719f\u89c6\u65e0\u7779\uff0c\u4ecd\u7136\u4e13\u6ce8\u4e8e\u5728\u8eab\u524d\u5973\u5b50\u7684\u4f53\u5185\u51b2\u523a\u62bd\u63d2\u3002\u950b\u5229\u7684\u5251\u5203\u89e6\u53ca\u8116\u9888\u7684\u76ae\u80a4\uff0c\u4e0b\u4e00\u77ac\u95f4\u4ed6\u7684\u8111\u888b\u5c31\u4f1a\u88ab\u5b8c\u5168\u780d\u4e0b\u6765\u2014\u2014\u7136\u800c\u5c31\u5728\u8fd9\u4e00\u5239\u90a3\uff0c\u5c11\u5973\u7684\u773c\u524d\u9661\u7136\u5149\u534e\u6d41\u52a8\uff0c\u9519\u4e71\u8ff7\u79bb\uff0c\u811a\u4e0b\u5929\u65cb\u5730\u8f6c\uff0c\u767b\u65f6\u4fbf\u5931\u53bb\u4e86\u610f\u8bc6\u3002</p><p>\u203b\u203b\u203b</p><p>\u827e\u5f25\u8587\uff01</p><p>\u827e\u5f25\u8587\uff01</p><p>\u827e\u5f25\u8587\uff01</p><p>\u9065\u8fdc\u5904\u4f20\u6765\u7684\u4f4e\u58f0\u547c\u5524\uff0c\u8ba9\u5c11\u5973\u4ece\u6c89\u7761\u4e2d\u82cf\u9192\uff0c\u7f13\u7f13\u7741\u5f00\u773c\u775b\uff0c\u5374\u53d1\u73b0\u5468\u906d\u4e00\u7247\u9ed1\u6697\u3002\u5979\u9ed8\u9ed8\u5730\u6ce8\u89c6\u7740\u524d\u65b9\uff0c\u51e0\u79d2\u949f\u540e\uff0c\u773c\u775b\u4fbf\u5f00\u59cb\u9002\u5e94\u4e86\u8fd9\u79cd\u73af\u5883\uff0c\u80fd\u591f\u52c9\u5f3a\u89c6\u7269\u4e86\u3002</p><p>\u4f9d\u7136\u662f\u5728\u90a3\u95f4\u5730\u4e0b\u5ba4\uff0c\u4f46\u5899\u58c1\u4e0a\u7684\u706b\u70ac\u5df2\u7ecf\u5168\u90e8\u7184\u706d\uff0c\u6027\u7231\u72c2\u6b22\u7684\u4eba\u4eec\u4e5f\u90fd\u6d88\u5931\u5f97\u65e0\u5f71\u65e0\u8e2a\uff0c\u53ea\u5269\u4e0b\u81ea\u5df1\u3002\u6885\u83f2\u65af\u534a\u8dea\u5728\u796d\u575b\u4e0a\uff0c\u94f6\u5251\u201c\u7737\u604b\u201d\u4f9d\u7136\u63e1\u5728\u624b\u4e2d\uff0c\u8fd9\u8ba9\u5979\u5b89\u5fc3\u4e86\u4e0d\u5c11\u3002\u5979\u7ad9\u8d77\u8eab\uff0c\u73af\u89c6\u56db\u5468\uff0c\u4f46\u4e00\u65e0\u6240\u83b7\u3002\u6b64\u5904\u4f3c\u4e4e\u5df2\u7ecf\u88ab\u5e9f\u5f03\u591a\u65f6\uff0c\u7a7a\u6c14\u4e2d\u900f\u7740\u6de1\u6de1\u7684\u9709\u5473\uff0c\u6e29\u5ea6\u5f88\u4f4e\uff0c\u51bb\u5f97\u624b\u8db3\u90fd\u6709\u4e9b\u9ebb\u6728\u4e0d\u7075\u3002</p><p>\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u6bcd\u4eb2\u5462\uff1f\u4fb5\u72af\u6bcd\u4eb2\u7684\u90a3\u53ea\u602a\u517d\u5462\uff1f\u4ed6\u4eec\u90fd\u53bb\u4e86\u54ea\u91cc\uff1f</p><p>\u5c11\u5973\u6c89\u601d\u7740\uff0c\u4e00\u65f6\u95f4\u4e0d\u5f97\u5176\u89e3\uff0c\u4f46\u6b64\u5904\u786e\u5b9e\u5df2\u7ecf\u7a7a\u65e0\u4e00\u4eba\u3002\u201c\u5148\u79bb\u5f00\u518d\u8bf4\u5427\u3002\u201d\u5979\u60f3\uff0c\u6b63\u51c6\u5907\u8d70\u4e0b\u796d\u575b\uff0c\u731b\u7136\u89c9\u5f97\u811a\u4e0b\u4e00\u9635\u51b0\u51c9\u3002\u5c11\u5973\u4f4e\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u5730\u9762\u4e0d\u77e5\u4f55\u65f6\u5df2\u7ecf\u88c2\u5f00\u4e86\u5927\u5927\u5c0f\u5c0f\u65e0\u6570\u7684\u7f1d\u9699\uff0c\u6697\u7ea2\u8272\u7684\u6db2\u4f53\u4ece\u4e2d\u6c69\u6c69\u6d8c\u51fa\uff0c\u900f\u7740\u523a\u9f3b\u7684\u8165\u6c14\u3002</p><p>\u201c\u8840\uff1f\u201d</p><p>\u786e\u5b9e\u662f\u8840\u6db2\uff0c\u8165\u8fa3\u3001\u7c98\u7a20\uff0c\u800c\u4e14\u4eff\u4f5b\u65e0\u7a77\u65e0\u5c3d\uff0c\u53ea\u4e00\u77ac\u95f4\u4fbf\u5c06\u6574\u4e2a\u5730\u4e0b\u5ba4\u6df9\u6ca1\uff0c\u800c\u4e14\u6c34\u4f4d\u4e0a\u5347\u901f\u5ea6\u6781\u5feb\uff0c\u8d77\u521d\u53ea\u6f2b\u8fc7\u811a\u9762\uff0c\u7728\u773c\u95f4\u4fbf\u8fc7\u8db3\u80eb\uff0c\u518d\u8fc7\u534a\u79d2\u949f\uff0c\u4fbf\u5df2\u7ecf\u6df9\u5230\u4e86\u819d\u76d6\u2014\u2014\u6240\u5e78\u5230\u6b64\u4e3a\u6b62\uff0c\u6ca1\u6709\u7ee7\u7eed\u4e0a\u6da8\u3002\u5c11\u5973\u4e0d\u8a00\u4e0d\u8bed\uff0c\u6301\u5251\u9759\u9759\u7ad9\u7acb\uff0c\u7b49\u5f85\u7740\u8fdb\u4e00\u6b65\u7684\u53d8\u5316\u3002</p><p>\u201c\u5657\u201d\u5730\u4e00\u58f0\uff0c\u78a7\u7eff\u7684\u706b\u5149\u5728\u524d\u65b9\u4e0d\u8fdc\u5904\u7684\u8840\u6c34\u9762\u4e0a\u71c3\u8d77\uff0c\u7167\u4eae\u4e86\u5468\u56f4\u5c0f\u5c0f\u7684\u533a\u57df\uff0c\u63a5\u7740\u4e00\u4e2a\u5706\u7403\u72b6\u7684\u767d\u8272\u7269\u4f53\u81ea\u8840\u6c34\u4e2d\u9aa8\u788c\u6eda\u52a8\u7740\u5347\u8d77\uff0c\u60ac\u6d6e\u5728\u7a7a\u4e2d\u3002\u5c11\u5973\u96c6\u4e2d\u76ee\u529b\uff0c\u501f\u52a9\u706b\u5149\u7ec8\u4e8e\u770b\u6e05\u4e86\u90a3\u4e2a\u5706\u7403\u662f\u4ec0\u4e48\u3002</p><p>\u7136\u540e\u5979\u5413\u51fa\u4e86\u4e00\u8eab\u51b7\u6c57\u3002</p><p>\u90a3\u662f\u4e00\u4e2a\u4eba\u5934\uff0c\u800c\u4e14\u662f\u4e2a\u5a74\u513f\u7684\u5934\u9885\uff0c\u8fde\u63a5\u7740\u534a\u622a\u65ad\u9888\uff0c\u50cf\u662f\u88ab\u4eba\u7528\u5200\u5251\u5e73\u5e73\u780d\u65ad\u4e0b\u6765\u7684\u3002\u773c\u7736\u4e2d\u6ca1\u6709\u773c\u73e0\uff0c\u53ea\u662f\u4e24\u5904\u9ed1\u7a9f\uff0c\u5374\u6709\u8840\u6cea\u4e0d\u65ad\u4ece\u4e2d\u6d8c\u51fa\uff0c\u5728\u7c89\u5ae9\u7684\u8138\u86cb\u4e0a\u7559\u4e0b\u4e24\u9053\u75d5\u8ff9\u3002\u5c3d\u7ba1\u5982\u6b64\uff0c\u5b83\u4f3c\u4e4e\u4ecd\u7136\u770b\u5f97\u89c1\uff0c\u6b63\u54a7\u5f00\u5634\uff0c\u671d\u5c11\u5973\u563b\u563b\u800c\u7b11\uff0c\u4e24\u679a\u5c16\u5c16\u7684\u72ac\u9f7f\u81ea\u5507\u89d2\u9732\u51fa\uff0c\u663e\u5f97\u683c\u5916\u72f0\u72de\u53ef\u6016\u3002</p><p>\u201c\u665a\u4e0a\u597d\uff0c\u827e\u5f25\u8587\u3002\u201d\u5b83\u8bf4\u3002</p><p>\u5c11\u5973\u6df1\u6df1\u5438\u4e86\u53e3\u6c14\uff0c\u52c9\u5f3a\u8ba9\u81ea\u5df1\u9547\u5b9a\u4e0b\u6765\uff0c\u201c\u4f60\u2026\u2026\u4f60\u662f\u8c01\uff1f\u201d</p><p>\u201c\u6211\u662f\u8c01\uff1f\u201d\u5a74\u513f\u7684\u5934\u9885\u53d1\u51fa\u683c\u683c\u683c\u683c\u7684\u7b11\u58f0\uff0c\u201c\u9519\u4e86\uff0c\u827e\u5f25\u8587\uff0c\u4f60\u5e94\u8be5\u95ee\u2018\u6211\u4eec\u662f\u8c01\u2019\u3002\u201d</p><p>\u201c\u6211\u4eec\uff1f\u201d</p><p>\u201c\u662f\u7684\uff0c\u6211\u4eec\uff0c\u201d\u5a74\u513f\u7684\u5934\u9885\u8bf4\uff0c\u201c\u5305\u62ec\u6211\uff0c\u5305\u62ec\u4f60\u81ea\u5df1\uff0c\u4e5f\u5305\u62ec\u4ed6\u4eec\uff0c\u201d\u968f\u7740\u5b83\u7684\u8bf4\u8bdd\uff0c\u4e00\u56e2\u63a5\u4e00\u56e2\u7684\u78a7\u7eff\u706b\u5149\u5728\u5730\u4e0b\u5ba4\u4e2d\u71c3\u8d77\uff0c\u66f4\u591a\u7684\u5a74\u513f\u5934\u9885\u4ece\u8840\u6c34\u4e2d\u98de\u51fa\uff0c\u60ac\u6d6e\u5728\u7a7a\u4e2d\u3002\u5b83\u4eec\u4e00\u5171\u6709\u4e03\u4e2a\uff0c\u5c06\u5c11\u5973\u56e2\u56e2\u73af\u7ed5\uff0c\u201c\u6211\u4eec\u662f\u4f60\u7684\u540c\u80de\uff0c\u662f\u4f60\u7684\u5144\u5f1f\u59d0\u59b9\uff0c\u201d\u5a74\u513f\u5934\u9885\u4eec\u9f50\u58f0\u8bf4\uff0c\u55d3\u97f3\u5c16\u9510\uff0c\u5f7c\u6b64\u5e94\u548c\uff0c\u9707\u5f97\u5c11\u5973\u7684\u8033\u819c\u9635\u9635\u8f70\u9e23\uff0c\u201c\u6211\u4eec\u56e0\u4f60\u800c\u6b7b\u53bb\uff0c\u4e5f\u5fc5\u56e0\u4f60\u800c\u91cd\u751f\uff1b\u6211\u4eec\u77ed\u6682\u5ea6\u8fc7\u6b64\u4e16\uff0c\u7ec8\u5c06\u56de\u5f52\u4e0d\u673d\uff0c\u201d\u5b83\u4eec\u5728\u8840\u6c34\u8868\u9762\u6f02\u6d6e\u7740\uff0c\u7f13\u7f13\u79fb\u52a8\u903c\u8fd1\uff0c\u201c\u63a5\u53d7\u6211\u4eec\u7684\u62e5\u62b1\uff0c\u548c\u6211\u4eec\u878d\u4e3a\u4e00\u4f53\uff0c\u8fd9\u5c31\u662f\u65b0\u751f\uff0c\u8fd9\u5c31\u662f\u6c38\u6052\u2014\u2014\u201d</p><p>\u201c\u6211\u62d2\u7edd\uff01\u201d\u5c11\u5973\u6253\u65ad\u4e86\u5a74\u513f\u5934\u9885\u7684\u9f50\u58f0\u5408\u5531\uff0c\u5979\u9ad8\u4e3e\u8d77\u94f6\u5251\uff0c\u201c\u6eda\u5f00\uff01\u201d</p><p>\u201c\u8fd9\u662f\u6211\u4eec\u7684\u5bbf\u547d\uff0c\u4e0e\u751f\u4ff1\u6765\uff0c\u4f60\u65e0\u6cd5\u62d2\u7edd\u3002\u201d\u4e00\u4e2a\u5a74\u513f\u5934\u9885\u8bf4\u3002</p><p>\u201c\u6211\u62d2\u7edd\uff01\u201d\u5c11\u5973\u65a9\u9489\u622a\u94c1\u5730\u91cd\u590d\u3002</p><p>\u4e03\u4e2a\u5a74\u513f\u5934\u9885\u53d1\u51fa\u683c\u683c\u7684\u602a\u7b11\uff0c\u201c\u4f60\u65e0\u6cd5\u62d2\u7edd\uff0c\u201d\u5b83\u4eec\u5f02\u53e3\u540c\u58f0\u5730\u8bf4\uff0c\u201c\u4f60\u5fc5\u5c06\u52a0\u5165\u6211\u4eec\uff0c\u56e0\u4e3a\u8fd9\u662f\u8eab\u4e3a\u6740\u622e\u4e4b\u5b50\u7684\u5bbf\u547d\u201d\u3002</p><p>\u201c\u53bb\u4f60\u7684\u89c1\u9b3c\u5bbf\u547d\uff01\u201d\u5c11\u5973\u5927\u558a\uff0c\u4e00\u5251\u6325\u51fa\u3002</p><p>\u707f\u70c2\u7684\u5723\u5149\u81ea\u5251\u5203\u4e2d\u5954\u6d8c\u800c\u51fa\uff0c\u649e\u5411\u5176\u4e2d\u4e00\u4e2a\u5a74\u513f\u5934\u9885\uff0c\u4f46\u5c31\u5728\u5373\u5c06\u51fb\u4e2d\u7684\u90a3\u4e00\u5239\u90a3\uff0c\u5934\u9885\u81ea\u5df1\u7206\u70b8\u5f00\u6765\uff0c\u4e00\u53ea\u4ec5\u6709\u732b\u822c\u5927\u5c0f\u3001\u80cc\u751f\u9aa8\u523a\u7684\u72f0\u72de\u602a\u517d\u4ece\u4e2d\u8dc3\u51fa\uff0c\u907f\u8fc7\u5723\u5149\u3002\u7d27\u63a5\u7740\uff0c\u5176\u4ed6\u516d\u4e2a\u5a74\u513f\u5934\u9885\u4f9d\u6b21\u7206\u70b8\uff0c\u73b0\u51fa\u516d\u53ea\u4e00\u6a21\u4e00\u6837\u7684\u602a\u517d\u3002\u4e03\u53ea\u602a\u517d\u5e76\u6ca1\u6709\u8054\u5408\u8d77\u6765\u653b\u51fb\u5c11\u5973\uff0c\u53cd\u800c\u5728\u51fa\u73b0\u7684\u7b2c\u4e00\u77ac\u95f4\u4fbf\u6295\u5165\u4e86\u81ea\u76f8\u6b8b\u6740\u7684\u6218\u56e2\uff0c\u6bcf\u53ea\u602a\u517d\u6740\u6b7b\u4e00\u53ea\u5bf9\u624b\uff0c\u81ea\u8eab\u5c31\u4f1a\u53d8\u5f97\u81a8\u80c0\u4e00\u500d\u3002\u77ed\u77ed\u5341\u79d2\u949f\u540e\uff0c\u4e03\u53ea\u602a\u517d\u5df2\u7ecf\u53ea\u4f59\u4e0b\u6700\u540e\u4e00\u53ea\u80dc\u5229\u8005\uff0c\u5b83\u5df2\u7ecf\u5927\u5982\u86ee\u725b\uff0c\u53cc\u773c\u8840\u7ea2\uff0c\u5229\u9f7f\u68ee\u68ee\u5982\u5251\uff0c\u56db\u80a2\u722a\u5b50\u7a33\u7a33\u6309\u5728\u8840\u6c34\u9762\u4e0a\uff0c\u968f\u6ce2\u8d77\u4f0f\uff0c\u51dd\u89c6\u7740\u5c11\u5973\u3002</p><p>\u201c\u670d\u4ece\u6211\uff0c\u7136\u540e\u4f60\u5c06\u83b7\u5f97\u6c38\u6052\uff0c\u201d\u602a\u517d\u8bf4\uff0c\u4ece\u53e3\u4e2d\u5410\u51fa\u5bd2\u6c14\uff0c\u201c\u8fd9\u662f\u4f60\u6bcd\u4eb2\u7684\u5fc3\u613f\u3002\u201d</p><p>\u602a\u517d\u7684\u540e\u534a\u53e5\u8bdd\u8ba9\u5c11\u5973\u7a0d\u7a0d\u9000\u7f29\uff0c\u4f46\u968f\u5373\u575a\u51b3\u5730\u6447\u4e86\u6447\u5934\u3002\u201c\u4f11\u60f3\uff0c\u201d\u5979\u7b80\u77ed\u5730\u8bf4\uff0c\u201c\u4e0d\u662f\u4f60\u6b7b\uff0c\u5c31\u662f\u6211\u4ea1\u3002\u201d</p><p>\u201c\u90a3\u4f60\u5c31\u53bb\u6b7b\u5427\uff01\u201d</p><p>\u602a\u517d\u5389\u58f0\u5486\u54ee\uff0c\u817e\u7a7a\u8dc3\u8d77\uff0c\u671d\u5c11\u5973\u6251\u4e86\u8fc7\u6765\u3002\u5c11\u5973\u6325\u5251\u6a2a\u65a9\uff0c\u780d\u4e2d\u602a\u517d\u7684\u5de6\u524d\u722a\uff0c\u5374\u88ab\u968f\u624b\u4e00\u62e8\u4fbf\u6321\u5f00\uff0c\u5979\u5e73\u65f6\u7684\u5de8\u5927\u529b\u6c14\u4eff\u4f5b\u5239\u90a3\u95f4\u6d88\u5931\u5f97\u65e0\u5f71\u65e0\u8e2a\uff0c\u8fde\u63e1\u5251\u90fd\u4e0d\u7a33\u3002\u201c\u4f60\u7684\u529b\u91cf\u6765\u6e90\u4e8e\u6211\uff0c\u5973\u5b69\uff0c\u201d\u602a\u517d\u4f4e\u543c\uff0c\u201c\u6ca1\u6709\u5b83\uff0c\u4f60\u8fd8\u80fd\u62ff\u4ec0\u4e48\u548c\u6211\u5bf9\u6297\uff1f\u201d</p><p>\u5b83\u5f20\u5f00\u8840\u76c6\u5927\u53e3\uff0c\u6b63\u8981\u54ac\u4e0b\uff0c\u9661\u7136\u95f4\u4e00\u9053\u707f\u70c2\u523a\u76ee\u7684\u7d2b\u8272\u5149\u77e2\u4ece\u5929\u800c\u964d\uff0c\u6253\u5728\u602a\u517d\u8eab\u4e0a\uff0c\u5c06\u5b83\u5168\u8eab\u5192\u70df\u5730\u7b14\u76f4\u9707\u98de\u51fa\u53bb\u3002\u5149\u77e2\u51fb\u9000\u602a\u517d\uff0c\u968f\u5373\u6563\u6210\u65e0\u6570\u788e\u7247\uff0c\u81ea\u52a8\u98de\u821e\u73af\u7ed5\u7740\uff0c\u91cd\u65b0\u7ec4\u5408\u6210\u4eba\u5f62\uff0c\u6321\u5728\u6885\u83f2\u65af\u9762\u524d\u3002\u90a3\u662f\u4f4d\u77ed\u53d1\u7684\u9ed1\u8863\u5c11\u5973\uff0c\u5a07\u5c0f\u8eab\u6750\u4e2d\u900f\u7740\u51db\u51db\u7684\u5a01\u4e25\u548c\u538b\u8feb\u611f\uff0c\u53f3\u624b\u63e1\u7740\u5de8\u9570\uff0c\u5728\u5979\u7684\u8eab\u4f53\u5468\u56f4\uff0c\u4e00\u6761\u901a\u4f53\u900f\u660e\u3001\u957f\u7740\u53cc\u7ffc\u7684\u91d1\u8272\u5f02\u86c7\u6b63\u865a\u7a7a\u6e38\u8d70\uff0c\u76d8\u65cb\u73af\u7ed5\u3002</p><p>\u201c\u662f\u4f60\uff1f\u201d</p><p>\u6885\u83f2\u65af\u8ba4\u51fa\u4e86\u6765\u4eba\uff0c\u6b63\u662f\u6628\u65e5\u5728\u5deb\u5996\u957f\u8001\u7684\u5b85\u9662\u4e2d\u4ea4\u624b\u8fc7\u7684\u5bf9\u8c61\uff0c\u90a3\u4f4d\u795e\u79d8\u7684\u9ed1\u8863\u5c11\u5973\uff0c\u80fd\u591f\u64cd\u63a7\u65f6\u95f4\u4e4b\u529b\u7684\u5f3a\u654c\u3002\u5979\u5947\u602a\u5bf9\u65b9\u600e\u4e48\u4f1a\u7a81\u7136\u51fa\u73b0\u5728\u8fd9\u91cc\uff0c\u4f46\u6b64\u65f6\u663e\u7136\u65e0\u6687\u591a\u8bf4\u3002\u602a\u517d\u6447\u6447\u6643\u6643\u5730\u722c\u8d77\uff0c\u7ad9\u5728\u8840\u6c34\u6ce2\u9762\u4e0a\uff0c\u4f4e\u4f4e\u5486\u54ee\u7740\uff0c\u77aa\u7740\u8fd9\u4e2a\u7a81\u5982\u5176\u6765\u7684\u6363\u4e71\u8005\u3002\u201c\u4f60\u662f\u8c01\uff1f\u201d\u5b83\u95ee\u3002</p><p>\u201c\u4e0e\u4f60\u65e0\u5173\u3002\u201d\u9ed1\u8863\u5c11\u5973\u51b7\u6f20\u5730\u56de\u7b54\u3002</p><p>\u201c\u90a3\u4f60\u4e3a\u4f55\u800c\u6765\uff1f\u201d\u602a\u517d\u53c8\u95ee\u3002</p><p>\u201c\u4f60\u4e0d\u9700\u8981\u77e5\u9053\u3002\u201d\u9ed1\u8863\u5c11\u5973\u8bf4\u3002</p><p>\u602a\u517d\u6012\u543c\u7740\u6251\u4e0a\u524d\u6765\u3002\u9ed1\u8863\u5c11\u5973\u51b7\u51b7\u4e00\u7b11\uff0c\u770b\u7740\u5b83\u81ea\u9ad8\u7a7a\u6251\u4e0b\uff0c\u4e0d\u95ea\u4e0d\u907f\uff0c\u53ea\u662f\u4e3e\u8d77\u624b\u4e2d\u7684\u5de8\u9570\uff0c\u81ea\u4e0a\u800c\u4e0b\u95ea\u7535\u822c\u7b14\u76f4\u7ad6\u65a9\u3002\u602a\u517d\u867d\u7136\u4f53\u578b\u5e9e\u5927\uff0c\u52a8\u4f5c\u5374\u6781\u7075\u5de7\uff0c\u51cc\u7a7a\u9519\u8eab\uff0c\u5728\u5343\u94a7\u4e00\u53d1\u4e4b\u9645\u582a\u582a\u907f\u5f00\u5de8\u9570\u7684\u65a9\u51fb\u3002\u7136\u800c\u5b83\u521a\u521a\u843d\u4e0b\u6c34\u9762\uff0c\u6b63\u5f85\u518d\u6b21\u6251\u51fb\uff0c\u5c31\u542c\u5f97\u81ea\u5df1\u4f53\u5185\u4f20\u6765\u4e00\u8fde\u4e32\u201c\u5580\u5580\u5580\u5580\u5580\u5580\u201d\u7684\u6e05\u8106\u54cd\u52a8\uff0c\u8fd8\u6ca1\u7b49\u610f\u8bc6\u53cd\u5e94\u8fc7\u6765\uff0c\u6574\u4e2a\u8eab\u8eaf\u4fbf\u5982\u6d41\u6c99\u822c\u5206\u5d29\u79bb\u6790\uff0c\u788e\u6210\u4e86\u65e0\u6570\u5757\u3002</p><p>\u9ed1\u8863\u5c11\u5973\u6536\u8d77\u5de8\u9570\uff0c\u8f6c\u8fc7\u8eab\u6765\u770b\u5411\u6885\u83f2\u65af\uff0c\u7136\u540e\u4f38\u51fa\u53f3\u624b\u3002\u201c\u63e1\u7d27\uff0c\u201d\u5979\u6de1\u6de1\u8bf4\uff0c\u201c\u5728\u6211\u8bb8\u53ef\u4e4b\u524d\uff0c\u4e0d\u8981\u677e\u5f00\u3002\u201d</p><p>\u201c\u4f60\u2026\u2026\u4e3a\u4ec0\u4e48\u2026\u2026\u201d</p><p>\u6885\u83f2\u65af\u6ee1\u5fc3\u7591\u60d1\uff0c\u7136\u800c\u9ed1\u8863\u5c11\u5973\u663e\u7136\u4e0d\u60f3\u591a\u89e3\u91ca\u3002\u5979\u76f4\u63a5\u4e00\u628a\u6293\u4f4f\u6885\u83f2\u65af\u7684\u5de6\u624b\u624b\u8155\uff0c\u7136\u540e\u5bf9\u7740\u8eab\u5468\u76d8\u65cb\u7684\u91d1\u86c7\u70b9\u4e86\u70b9\u5934\u3002\u91d1\u86c7\u53cc\u7ffc\u4e00\u8212\uff0c\u51cc\u7a7a\u6e38\u8d70\uff0c\u5239\u90a3\u95f4\u5c06\u4e24\u4f4d\u5c11\u5973\u5168\u90fd\u73af\u7ed5\u5728\u5185\u3002\u6885\u83f2\u65af\u53ea\u89c9\u773c\u524d\u4e00\u9635\u70ab\u76ee\u7d2b\u5149\u95ea\u8fc7\uff0c\u4e0d\u7531\u5f97\u53cc\u773c\u7d27\u95ed\uff0c\u5f53\u5979\u518d\u7741\u5f00\u65f6\uff0c\u6b63\u770b\u89c1\u743c\u6069\u5173\u5207\u7684\u9762\u5bb9\u3002</p><p>\u201c\u4f60\u7ec8\u4e8e\u9192\u4e86\uff0c\u827e\u5f25\u8587\uff0c\u201d\u4ed6\u5c06\u5c11\u5973\u7d27\u7d27\u62b1\u5728\u6000\u4e2d\uff0c\u201c\u628a\u6211\u5413\u574f\u4e86\u3002\u201d</p></article>"}}}]);
