(window.webpackJsonp=window.webpackJsonp||[]).push([[516],{539:function(p){p.exports={chapterTitle:null,title:"\u7b2c516\u7ae0 \u95e8\u4e0b\u5ba2",content:"<article><h2>\u7b2c516\u7ae0 \u95e8\u4e0b\u5ba2</h2><p>\u3000\u3000\u5609\u4fe1\u5b9e\u4e1a\u4e0a\u5348\u505c\u724c\u53d1\u5e03\u654f\u611f\u6027\u6d88\u606f\uff0c\u4e0b\u5348\u5609\u4fe1\u5b9e\u4e1a\u7684\u80a1\u7968\u6062\u590d\u6b63\u5e38\u4ea4\u6613\uff0c\u77ed\u65f6\u95f4\u7684\u8fdf\u7591\u4e4b\u540e\uff0c\u80a1\u4ef7\u4e95\u55b7\u5f0f\u4e0a\u7a9c\uff0c\u7531\u4e8e\u5609\u4fe1\u5b9e\u4e1a\u4e0a\u5348\u516c\u544a\u7684\u6d88\u606f\u7528\u8bed\u542b\u6df7\uff0c\u5e76\u6ca1\u6709\u660e\u786e\u6d89\u53ca\u5230\u8d44\u4ea7\u6536\u8d2d\u7684\u5185\u5bb9\uff0c\u80a1\u4ef7\u4e95\u55b7\u540e\u53c8\u8fc5\u901f\u56de\u843d\uff0c\u4f46\u6bd4\u505c\u724c\u524d\u7684\u80a1\u4ef7\u6709\u4e0d\u5c0f\u5e45\u5ea6\u7684\u4e0a\u6da8\u3002</p><p>\u3000\u3000\u9999\u6e2f\u8054\u5408\u8bc1\u5238\u4ea4\u6613\u6240\u7684\u80a1\u5e02\u6295\u8d44\u5206\u6790\u5bb6\u901a\u5e38\u53ef\u4ee5\u5206\u4e3a\u4e24\u7c7b\uff0c\u4e00\u79cd\u559c\u6b22\u80a1\u7968\u5927\u8d77\u5927\u843d\uff0c\u8fd9\u6837\u4ed6\u4eec\u53ef\u4ee5\u505a\u77ed\u7ebf\u6295\u8d44\uff0c\u53e6\u4e00\u79cd\u559c\u6b22\u6295\u8d44\u5e73\u7a33\u589e\u957f\u3001\u5c06\u6765\u6709\u6f5c\u529b\u7684\u80a1\u7968\u3002</p><p>\u3000\u3000\u5609\u4fe1\u5b9e\u4e1a\u6ca1\u6709\u660e\u786e\u516c\u5e03\u6536\u8d2d\u7231\u8fbe\u7535\u5b50\u789f\u673a\u4e1a\u52a1\u7684\u6d88\u606f\uff0c\u800c\u4e14\u5609\u4fe1\u5b9e\u4e1a\u7684\u603b\u76d8\u5b50\u624d\u4e0d\u5341\u4e00\u4e8c\u4ebf\uff0c\u60f3\u6536\u8d2d\u5e74\u9500\u552e\u989d\u8fbe\u56db\u5341\u5e74\u3001\u76c8\u5229\u8fbe\u5341\u4ebf\u7684\u7231\u8fbe\u7535\u5b50\uff0c\u6709\u7740\u5f88\u5927\u7684\u4e0d\u786e\u5b9a\u6027\u3002\u5bf9\u4e8e\u559c\u6b22\u5e73\u7a33\u6295\u8d44\u3001\u957f\u7ebf\u6301\u6709\u7684\u80a1\u5e02\u6295\u8d44\u5206\u6790\u5bb6\u6765\u8bf4\uff0c\u6b64\u65f6\u4ecb\u5165\u5609\u4fe1\u5b9e\u4e1a\u5c31\u6709\u592a\u5927\u7684\u98ce\u9669\uff1b\u5bf9\u4e8e\u559c\u6b22\u640f\u98ce\u9669\u3001\u77ed\u7ebf\u6295\u8d44\u7684\u80a1\u5e02\u6295\u8d44\u5206\u6790\u5bb6\u800c\u8a00\uff0c\u5374\u7279\u522b\u949f\u610f\u5609\u4fe1\u5b9e\u4e1a\u7684\u80a1\u7968\u3002</p><p>\u3000\u3000\u867d\u7136\u5609\u4fe1\u5b9e\u4e1a\u7684\u80a1\u4ef7\u6ca1\u6709\u5728\u8bd5\u6c34\u6027\u8d28\u7684\u6d88\u606f\u516c\u5e03\u540e\u7acb\u5373\u589e\u6da8\u591a\u5c11\uff0c\u4f46\u662f\u80a1\u6027\u5374\u7ed9\u6fc0\u6d3b\uff0c\u4ea4\u6613\u91d1\u989d\u6210\u500d\u589e\u6da8\uff0c\u65e0\u7591\u662f\u5e02\u573a\u7ed9\u4e88\u7684\u6b63\u9762\u56de\u5e94\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u9999\u6e2f\u6df1\u6c34\u6d89\u9e2d\u5bee\u8857\uff0c\u8fd9\u6761\u65e9\u5e74\u56e0\u4e3a\u76d6\u6ee1\u9e2d\u5bee\u800c\u5f97\u540d\u7684\u5730\u65b9\u662f\u9999\u6e2f\u7535\u5b50\u4ea7\u54c1\u7684\u96c6\u6563\u5730\uff0c\u4e5d\u4e03\u5e74\uff0c\u9999\u6e2f\u5404\u5bb6\u5927\u89c4\u6a21\u7684\u7535\u5668\u884c\u90fd\u5728\u4e2d\u5fc3\u533a\u5f00\u8bbe\u5927\u5356\u573a\uff0c\u751a\u81f3\u4fdd\u7559\u4f20\u7edf\u4f5c\u574a\u5f0f\u7684\u9e2d\u5bee\u8857\u90fd\u5df2\u6709\u6ca1\u843d\u7684\u8ff9\u8c61\uff0c\u4f46\u662f\u5bf9\u4e8e\u5185\u5730\u4ee5\u53ca\u4e1c\u5357\u4e9a\u5230\u9999\u6e2f\u65c5\u6e38\u7684\u534e\u88d4\u6e38\u5ba2\uff0c\u9e2d\u5bee\u8857\u4ecd\u7136\u662f\u6dd8\u91d1\u6dd8\u5b9d\u7684\u5730\u70b9\uff0c\u6df1\u6c34\u6d89\u9e2d\u5bee\u8857\u4ecd\u7136\u662f\u7535\u5b50\u4ea7\u54c1\u5411\u4e1c\u5357\u4e9a\u5730\u533a\u3001\u5185\u5730\u5219\u5411\u73e0\u6c5f\u4e09\u89d2\u6d32\u5730\u533a\u5e45\u5c04\u7684\u4e2d\u5fc3\u4e4b\u4e00\u3002</p><p>\u3000\u3000\u56e0\u6b64\uff0c\u79d1\u738b\u7535\u5668\u9009\u62e9\u9e2d\u5bee\u8857\u4e0a\u6700\u5927\u7684\u6cf0\u6797\u7535\u5668\u884c\u4f5c\u4e3a\u6253\u5f00\u4e1c\u5357\u4e9a\u789f\u673a\u5e02\u573a\u7684\u7b2c\u4e00\u6b65\u3002</p><p>\u3000\u3000\u7cbe\u5fc3\u7b79\u5907\u7684\u6d3b\u52a8\u5f88\u6210\u529f\uff0c\u5927\u534a\u6761\u9e2d\u5bee\u8857\u4e0a\u7684\u6e38\u5ba2\u90fd\u7ed9\u5438\u5f15\u8fc7\u6765\uff0c\u4ee5\u81f4\u9644\u8fd1\u51e0\u6761\u8857\u7684\u8b66\u5bdf\u90fd\u8dd1\u8fc7\u6765\u5e2e\u5fd9\u7ef4\u6301\u79e9\u5e8f\u3002</p><p>\u3000\u3000\u79d1\u738b\u8bbe\u4e8e\u9999\u6e2f\u7684\u6d77\u5916\u5206\u516c\u53f8\u7531\u845b\u836b\u5747\u8d1f\u8d23\uff0c\u4f46\u662f\u8fd9\u6d89\u53ca\u5230\u79d1\u738b\u6253\u5f00\u4e1c\u5357\u4e9a\u5e02\u573a\u7684\u7b2c\u4e00\u6b65\uff0c\u8c22\u5251\u5357\u4e0e\u9648\u9759\u4eb2\u81ea\u5230\u9999\u6e2f\u6765\u7ed9\u845b\u836b\u5747\u52a9\u9635\uff0c\u5171\u540c\u7b56\u5212\u4e86\u6b64\u6b21\u6d3b\u52a8\u3002</p><p>\u3000\u3000\u4e3a\u4e86\u5438\u5f15\u6e38\u5ba2\u7684\u76ee\u5149\uff0c\u751a\u81f3\u8fd8\u79df\u7528\u4e86\u4e24\u53ea\u98de\u8247\u60ac\u6d6e\u5728\u9999\u6e2f\u6df1\u6c34\u6d89\u7684\u4e0a\u7a7a\u505a\u5ba3\u4f20\u3002</p><p>\u3000\u3000\u5f88\u591a\u4eba\u4e03\u516b\u5e74\u540e\u6216\u8bb8\u4f1a\u610f\u8bc6\u5230\u6d77\u5916\u8d2d\u4e70\u201cmade in china\u201d\u7684\u4ea7\u54c1\u662f\u4ef6\u5f88\u611a\u8822\u7684\u884c\u4e3a\uff0c\u4f46\u662f\u4e5d\u4e03\u5e74\uff0c\u5f88\u591a\u5185\u5730\u7684\u6e38\u5ba2\u542c\u522b\u4eba\u8bf4\u9999\u6e2f\u7684\u7535\u5668\u5f88\u4fbf\u5b9c\uff0c\u770b\u5230\u79d1\u738b\u5728\u9e2d\u5bee\u8857\u641e\u6d3b\u52a8\uff0c\u90fd\u62e5\u6324\u8fc7\u6765\u62a2\u8d2d\uff0c\u4ed6\u4eec\u5f53\u4e2d\u7edd\u5927\u591a\u6570\u8fd8\u4e0d\u8ba4\u5f97\u201cmade in china\u201d\u4ee3\u8868\u4ec0\u4e48\u610f\u601d\uff0c\u5f53\u7136\u5f88\u591a\u4e1c\u5357\u4e9a\u7684\u534e\u4fa8\u670b\u53cb\u4e5f\u5e76\u4e0d\u4ee5\u4e3a\u201cmade in china\u201d\u6709\u4ec0\u4e48\u4e0d\u597d\u3002</p><p>\u3000\u3000\u5750\u5728\u6cf0\u6797\u7535\u5668\u884c\u9e2d\u5bee\u8857\u5206\u5e97\u7ecf\u7406\u5ba4\u7684\u8c22\u5251\u5357\u3001\u845b\u836b\u5747\u542c\u5230\u4ece\u5927\u5802\u62a5\u51fa\u6765\u7684\u9500\u91cf\u6570\u636e\u8282\u8282\u6500\u5347\uff0c\u60f3\u5230\u9ec4\u660f\u65f6\u5206\u5230\u9e2d\u5bee\u8857\u7684\u6e38\u5ba2\u4f1a\u66f4\u591a\uff0c\u90fd\u96be\u6291\u5174\u594b\u4e4b\u60c5\u3002</p><p>\u3000\u3000\u845b\u836b\u5747\u5e73\u65f6\u7565\u663e\u67ad\u623e\u7684\u773c\u7709\uff0c\u6b64\u65f6\u4e5f\u610f\u6c14\u8e0c\u8e87\u8d77\u6765\uff0c\u4ed6\u5f88\u8212\u670d\u7684\u5750\u5728\u539f\u662f\u6cf0\u6797\u7535\u5668\u9e2d\u5bee\u8857\u5206\u5e97\u7ecf\u7406\u4e13\u7528\u7684\u9ad8\u80cc\u9ed1\u76ae\u6905\u4e0a\uff0c\u7709\u5f00\u773c\u7b11\u7684\u5bf9\u8c22\u5251\u5357\u8bf4\u9053\uff1a\u201c\u770b\u770b\uff0c\u4eca\u5929\u9500\u552e\u7834\u5343\u90fd\u6ca1\u6709\u95ee\u9898\uff0c\u5185\u5730\u5230\u9999\u6e2f\u8fd8\u4e0d\u662f\u5f88\u65b9\u4fbf\uff0c\u4f46\u662f\u5230\u9e2d\u5bee\u8857\u6dd8\u8d27\u7684\u4e1c\u5357\u4e9a\u6e38\u5ba2\u6700\u591a\uff0c\u6211\u4eec\u4e0d\u5c31\u770b\u4e2d\u8fd9\u70b9\u5417\uff1f\u79d1\u738b\u7684\u789f\u673a\u4ece\u8fd9\u91cc\u4e0d\u4ec5\u662f\u5411\u9999\u6e2f\u6269\u6563\uff0c\u800c\u662f\u5411\u6574\u4e2a\u4e1c\u5357\u4e9a\u5730\u533a\u6269\u6563\u2026\u2026\u201d</p><p>\u3000\u3000\u9e2d\u5bee\u8857\u5411\u4e1c\u5357\u4e9a\u5730\u533a\u7684\u5e45\u5c04\u5f71\u54cd\uff0c\u5374\u662f\u4ed6\u4eec\u6700\u770b\u91cd\u7684\uff0c\u6240\u4ee5\u4e0d\u60dc\u5728\u9999\u6e2f\u5927\u5f20\u65d7\u9f13\u3002</p><p>\u3000\u3000\u9648\u9759\u5750\u5728\u4e00\u4fa7\uff0c\u819d\u76d6\u5e76\u8d77\u6765\u6b6a\u5230\u4e00\u4fa7\uff0c\u4e0d\u5f97\u4e0d\u627f\u8ba4\u845b\u836b\u5747\u5728\u5546\u4e1a\u7ecf\u8425\u8fd8\u662f\u6709\u5929\u8d4b\u7684\uff0c\u8981\u662f\u8eab\u4e0a\u7684\u7ea8\u7ed4\u6c14\u606f\u53bb\u6389\u51e0\u5206\uff0c\u6216\u8bb8\u4f1a\u66f4\u52a0\u51fa\u8272\u3002\u4e0d\u8fc7\u5979\u6ca1\u6709\u53c2\u4e0e\u4ed6\u4eec\u7684\u8c08\u8bdd\uff0c\u773c\u775b\u76ef\u7740\u7ecf\u7406\u5ba4\u89d2\u843d\u91cc\u7684\u4e00\u53f0\u7535\u89c6\u673a\uff0c\u7535\u89c6\u673a\u6b63\u64ad\u653e\u7740\u9999\u6e2f\u65e0\u7ebf\u7fe1\u7fe0\u53f0\u7684\u8d22\u7ecf\u65b0\u95fb\uff0c\u7fe1\u7fe0\u53f0\u7684\u8d22\u7ecf\u8bb0\u8005\u6b63\u91c7\u8bbf\u4e00\u540d\u8d44\u6df1\u80a1\u8bc4\u4eba\u5bf9\u5609\u4fe1\u5b9e\u4e1a\u4eca\u5929\u516c\u544a\u7684\u6d88\u606f\u4ee5\u53ca\u4e0b\u5348\u7684\u80a1\u4ef7\u8d70\u5411\u53d1\u8868\u770b\u6cd5\u3002</p><p>\u3000\u3000\u4e0a\u5348\u65f6\u4e4d\u542c\u5230\u5609\u4fe1\u5b9e\u4e1a\u7684\u516c\u544a\u8fd8\u771f\u7ed9\u5413\u4e86\u4e00\u8df3\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u7684\u5fc3\u60c5\u4e5f\u7684\u786e\u4e0d\u9519\uff0c\u8fd9\u6837\u7684\u597d\u5fc3\u60c5\u5e76\u6ca1\u6709\u56e0\u4e3a\u542c\u5230\u7231\u8fbe\u7535\u5b50\u6709\u53ef\u80fd\u501f\u52a9\u5609\u4fe1\u5b9e\u4e1a\u5b9e\u73b0\u9999\u6e2f\u4e0a\u5e02\u7684\u6d88\u606f\u800c\u51cf\u6de1\u591a\u5c11\uff0c\u201c\u4e0d\u80fd\u8ba4\u6e05\u4e0e\u7231\u8fbe\u7535\u5b50\u4e4b\u95f4\u7684\u5dee\u8ddd\u5c31\u65e0\u6cd5\u8d85\u8d8a\u5b83\u201d\uff0c\u8fd9\u662f\u8c22\u6c49\u9756\u8ddf\u4ed6\u8bf4\u7684\u8bdd\uff0c\u79d1\u738b\u5c31\u662f\u4e3a\u4e86\u907f\u5f00\u7231\u8fbe\u5728\u56fd\u5185\u5e02\u573a\u7ed9\u4ed6\u4eec\u7684\u538b\u529b\uff0c\u624d\u8054\u5408\u845b\u5bb6\u5609\u4fe1\u7535\u5b50\u540c\u65f6\u5f00\u8f9f\u4e1c\u5357\u4e9a\u5e02\u573a\uff0c\u8981\u662f\u8fd8\u4e3a\u8fd9\u6837\u7684\u95ee\u9898\u70e6\u607c\uff0c\u5c31\u592a\u4e0d\u6210\u719f\u4e86\u3002</p><p>\u3000\u3000\u542c\u5230\u845b\u836b\u5747\u7684\u8bdd\uff0c\u8c22\u5251\u5357\u53ea\u662f\u7b11\u4e86\u7b11\uff0c\u968f\u53e3\u5e94\u4e86\u4e00\u58f0\uff1a\u201c\u63a5\u4e0b\u6765\u4e1c\u5357\u4e9a\u90a3\u8fb9\u7684\u62c5\u5b50\u8fd8\u662f\u5f88\u91cd\u7684\uff0c\u6211\u4eec\u5c31\u90fd\u63a8\u7ed9\u836b\u5747\u4f60\u4e86\u2026\u2026\u201d\u6ce8\u610f\u529b\u5e76\u6ca1\u6709\u5b8c\u5168\u4ece\u7535\u89c6\u673a\u5c4f\u5e55\u4e0a\u79fb\u5f00\u3002</p><p>\u3000\u3000\u845b\u836b\u5747\u89c1\u8c22\u5251\u5357\u4e0e\u9648\u9759\u7684\u6ce8\u610f\u529b\u90fd\u7ed9\u7535\u89c6\u6b63\u64ad\u653e\u7684\u8d22\u7ecf\u8bbf\u8c08\u7ed9\u5438\u5f15\u4f4f\uff0c\u60f3\u8d77\u90a3\u4e2a\u4ed6\u53ea\u63a5\u89e6\u8fc7\u4e24\u4e09\u6b21\u3001\u5374\u6ca1\u6709\u7559\u4e0b\u4ec0\u4e48\u597d\u5370\u8c61\u3001\u5e74\u8f7b\u5f97\u8fc7\u5206\u7684\u5c0f\u5b50\uff0c\u55c5\u4e86\u55c5\u9f3b\u5b50\uff0c\u4ed6\u53c8\u4e0d\u80fd\u8f7b\u7387\u6d41\u9732\u51fa\u8f7b\u89c6\u4e4b\u610f\uff0c\u5f20\u606a\u8fd9\u5c0f\u5b50\u4e8c\u5341\u5c81\u4e0d\u5230\u5c31\u80fd\u505a\u51fa\u8fd9\u4e9b\u4e8b\u60c5\uff0c\u603b\u662f\u6709\u8fc7\u4eba\u4e4b\u5904\uff0c\u8981\u662f\u8f7b\u7387\u7684\u5c31\u9119\u89c6\uff0c\u5927\u6982\u8981\u7ed9\u9648\u9759\u8fd9\u4e2a\u7f8e\u4eba\u7559\u4e0b\u8f7b\u6d6e\u7684\u5370\u8c61\u4e86\u3002</p><p>\u3000\u3000\u845b\u836b\u5747\u77a5\u773c\u770b\u4e86\u770b\u9648\u9759\u4fa7\u9762\uff0c\u96ea\u80a4\u5149\u6da6\u3001\u66f2\u7ebf\u4fee\u957f\uff0c\u80f8\u524d\u633a\u7acb\u7684\u4e73\u5cf0\u5c06\u8584\u6bdb\u7ebf\u8863\u9ad8\u9ad8\u9876\u8d77\uff0c\u8ba9\u4eba\u770b\u4e86\u7981\u4e0d\u4f4f\u7684\u8981\u5fc3\u751f\u90aa\u5ff5\u3001\u70ed\u6d41\u5f80\u53cc\u817f\u4e2d\u95f4\u6d8c\u3002</p><p>\u3000\u3000\u845b\u836b\u5747\u4e5f\u4e0d\u662f\u4e0d\u4f1a\u63a9\u9970\u81ea\u5df1\u7684\u6b32\u671b\uff0c\u4ed6\u6b64\u65f6\u7684\u8eab\u4efd\u4e0e\u5730\u4f4d\uff0c\u8eab\u8fb9\u4e5f\u5c11\u4e0d\u4e86\u56f4\u7ed5\u5927\u7fa4\u7684\u4f73\u4e3d\u7f8e\u4eba\uff0c\u6b63\u56e0\u4e3a\u5973\u4eba\u592a\u591a\uff0c\u6b32\u671b\u5c31\u663e\u5f97\u6cdb\u6ee5\uff0c\u770b\u5230\u7f8e\u4eba\u5bb9\u6613\u5fc3\u751f\u4e9b\u90aa\u5ff5\u4e5f\u6b63\u5e38\u5f97\u5f88\uff0c\u5012\u4e5f\u4e0d\u541d\u556c\u5728\u7f8e\u4eba\u9762\u524d\u5356\u5f04\u4e00\u4e0b\uff1a\u201c\u7231\u8fbe\u8981\u501f\u58f3\u4e0a\u5e02\uff0c\u53ea\u6709\u4e24\u6761\u9014\u5f84\u53ef\u8d70\u3002\u7b2c\u4e00\u6761\u9014\u5f84\u662f\u7531\u5609\u4fe1\u5b9e\u4e1a\u5411\u7231\u8fbe\u7535\u5b50\u7684\u80a1\u4e1c\u5b9a\u5411\u589e\u53d1\u80a1\u7968\uff0c\u7528\u80a1\u7968\u6765\u6536\u8d2d\u7231\u8fbe\u7535\u5b50\u7684\u8d44\u4ea7\u3002\u7231\u8fbe\u7535\u5b50\u53bb\u5e74\u7684\u9500\u552e\u989d\u5dee\u4e0d\u591a\u6709\u56db\u5341\u4ebf\uff0c\u5b9a\u5411\u589e\u53d1\u7684\u80a1\u7968\u4e00\u5b9a\u4f1a\u4e0d\u662f\u5c0f\u6570\u76ee\uff0c\u4e0e\u9999\u6e2f\u8bc1\u5238\u8054\u5408\u4ea4\u6613\u6240\u6709\u5173\u2018\u516c\u4f17\u6301\u80a1\u4e0d\u5f97\u4f4e\u4e8e25\uff05\u2019\u7684\u89c4\u5b9a\u8fdd\u80cc\u90a3\u662f\u80af\u5b9a\u7684\uff0c\u6240\u4ee5\u8fd9\u6761\u8def\uff0c\u4ed6\u4eec\u8d70\u4e0d\u901a\u3002\u7b2c\u4e8c\u6761\u9014\u5f84\u662f\u7531\u5609\u4fe1\u5b9e\u4e1a\u5728\u80a1\u5e02\u5411\u6295\u8d44\u673a\u6784\u4e0e\u6295\u8d44\u4eba\u516c\u5f00\u589e\u53d1\u80a1\u7968\uff0c\u52df\u96c6\u8d44\u91d1\u5bf9\u7231\u8fbe\u7535\u5b50\u7684\u80a1\u6743\u8fdb\u884c\u73b0\u91d1\u6536\u8d2d\u3002\u540c\u6837\uff0c\u589e\u53d1\u80a1\u7968\u7684\u8d44\u91d1\u91cf\u4e00\u5b9a\u4f1a\u8fdc\u8fdc\u5927\u4e8e\u5609\u4fe1\u5b9e\u4e1a\u6b64\u65f6\u7684\u603b\u5e02\u503c\uff0c\u8d70\u8fd9\u6761\u8def\u6210\u529f\u7684\u53ef\u80fd\u6027\u4e5f\u5b9e\u5728\u5f88\u5c0f\u3002\u5728\u9999\u6e2f\uff0c\u6210\u529f\u7684\u86c7\u541e\u8c61\u7684\u5e76\u8d2d\u6848\u4f8b\u5e76\u4e0d\u662f\u6ca1\u6709\uff0c\u4f46\u4e5f\u5c48\u6307\u53ef\u6570\uff0c\u4ed6\u4eec\u8fd9\u662f\u5728\u8d4c\u8fd0\u6c14\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u6ca1\u6709\u7b2c\u4e09\u6761\u9014\u5f84\uff1f\u201d\u9648\u9759\u4fa7\u8fc7\u5934\u770b\u4e86\u845b\u836b\u5747\u4e00\u773c\u3002</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u845b\u836b\u5747\u60f3\u7ee7\u7eed\u5356\u5f04\uff0c\u9648\u9759\u5374\u8bf4\u9053\uff1a\u201c\u5b9a\u5411\u589e\u53d1\u4e0e\u516c\u5f00\u589e\u53d1\u3001\u80a1\u7968\u6536\u8d2d\u4e0e\u73b0\u91d1\u6536\u8d2d\u7ed3\u5408\u8d77\u6765\uff0c\u4e0d\u5c31\u662f\u7b2c\u4e09\u6761\u9014\u5f84\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5443\u2026\u2026\u201d\u845b\u836b\u5747\u6123\u4e86\u6123\uff0c\u8fd9\u65f6\u5019\u6070\u5de7\u7535\u89c6\u91cc\u7684\u90a3\u4e2a\u8d44\u6df1\u80a1\u8bc4\u4eba\u7ed9\u7231\u8fbe\u7535\u5b50\u63a8\u6d4b\u501f\u58f3\u4e0a\u5e02\u7684\u8def\u7ebf\uff0c\u6b63\u662f\u9648\u9759\u6240\u8bf4\u7684\u5b9a\u5411\u589e\u53d1\u4e0e\u516c\u5f00\u589e\u53d1\u76f8\u7ed3\u5408\u624d\u5177\u6709\u53ef\u80fd\u6027\u3002\u845b\u836b\u5747\u5012\u4e5f\u4e0d\u89c9\u5f97\u5c34\u5c2c\uff0c\u5012\u4e0d\u662f\u4ed6\u4e0d\u80fd\u60f3\u5230\u8fd9\u4e00\u70b9\uff0c\u521a\u624d\u4e5f\u53ea\u662f\u4ed3\u4fc3\u4e4b\u4e0b\u968f\u53e3\u8bf4\u8bf4\u800c\u5df2\uff0c\u6ca1\u60f3\u5230\u9648\u9759\u770b\u95ee\u9898\u4e5f\u8fd9\u4e48\u7280\u5229\uff0c\u4f38\u624b\u9876\u4e86\u9876\u8c22\u5251\u5357\u7684\u80f3\u818a\u8098\u513f\uff0c\u7b11\u7740\u8bf4\uff0c\u201c\u4f60\u5bb6\u5a46\u5a18\u771f\u662f\u5389\u5bb3\uff0c\u8ddf\u6211\u4eec\u5728\u6d77\u5916\u8fd9\u51e0\u5e74\uff0c\u4e66\u5012\u662f\u6ca1\u6709\u767d\u5ff5\uff0c\u597d\u4e9b\u5973\u5b69\u5b50\u542c\u5230\u8fd9\u79cd\u8d22\u7ecf\u65b0\u95fb\u90fd\u4f1a\u5934\u75bc\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u5bf9\u5973\u6027\u7684\u8f7b\u89c6\u662f\u845b\u5bb6\u4eba\u7684\u4e00\u8d2f\u4f20\u7edf\uff0c\u9648\u9759\u542c\u5230\u845b\u836b\u5747\u8fd9\u4e48\u5fc3\u91cc\u4e0d\u60a6\uff0c\u5012\u6ca1\u6709\u8868\u793a\u51fa\u6765\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8981\u8bf4\u8fd9\u8d44\u672c\u6e38\u620f\uff0c\u6211\u4eec\u5bb6\u8fd8\u771f\u6709\u4e00\u4e2a\u602a\u80ce\uff0c\u9ad8\u4e2d\u8fd8\u6ca1\u6709\u6bd5\u4e1a\uff0c\u96f6\u82b1\u94b1\u5c31\u6709\u4e0a\u5343\u4e07\u4e86\uff0c\u4e00\u76f4\u62b1\u6028\u6ca1\u6709\u5927\u5c55\u8eab\u624b\u7684\u673a\u4f1a\u2026\u2026\u201d</p><p>\u3000\u3000\u60f3\u8d77\u8c22\u5b50\u5609\uff0c\u9648\u9759\u839e\u513f\u4e00\u7b11\uff0c\u4e0d\u8fc7\u5979\u4ece\u8fd9\u65b0\u95fb\u91cc\u60f3\u8d77\u4e00\u4e9b\u95ee\u9898\uff0c\u5bf9\u8c22\u5251\u5357\u8bf4\u9053\uff1a\u201c\u7231\u8fbe\u7535\u5b50\u501f\u5609\u4fe1\u5b9e\u4e1a\u5f88\u65e9\u5c31\u8fdb\u5165\u4e1c\u5357\u4e9a\u5e02\u573a\u4e86\uff0c\u4e0d\u8fc7\u6bd4\u8d77\u4ed6\u4eec\u5728\u56fd\u5185\u7684\u52a8\u4f5c\uff0c\u5728\u4e1c\u5357\u4e9a\u7684\u53cd\u5e94\u4f3c\u4e4e\u592a\u8fdf\u949d\u4e86\u4e9b\uff0c\u662f\u4e0d\u662f\u4ed6\u4eec\u7684\u7cbe\u529b\u90fd\u653e\u5728\u8fd9\u4e0a\u9762\u4e86\uff1f\u201d</p><p>\u3000\u3000\u8c22\u5251\u5357\u773c\u775b\u5fae\u5fae\u772f\u8d77\u6765\uff0c\u4ed6\u4e5f\u4e00\u76f4\u5728\u8003\u8651\u8fd9\u4e2a\u95ee\u9898\uff0c\u53ea\u662f\u6ca1\u6709\u60f3\u900f\u5f7b\u3002</p><p>\u3000\u3000\u201c\u5e94\u8be5\u662f\u8fd9\u6837\uff0c\u201d\u845b\u836b\u5747\u8bf4\u9053\uff0c\u201c\u4ed6\u4eec\u592a\u7126\u6025\u8fdb\u5165\u8d44\u672c\u5e02\u573a\u5708\u94b1\u4e86\uff0c\u9999\u6e2f\u7684\u6295\u8d44\u673a\u6784\u4e0e\u6295\u8d44\u4eba\u5f88\u6ce8\u91cd\u8d44\u4ea7\u7684\u7a33\u5b9a\u56de\u62a5\uff0c\u4ed6\u4eec\u60f3\u8981\u501f\u58f3\u4e0a\u5e02\uff0c\u4ed6\u4eec\u7684\u7ecf\u8425\u7b56\u7565\uff0c\u5c31\u8981\u8fce\u5408\u6295\u8d44\u673a\u6784\u4e0e\u6295\u8d44\u4eba\u7684\u53e3\u5473\u2026\u2026\u4f55\u51b5\u5609\u4fe1\u5b9e\u4e1a\u7684\u5b9e\u529b\u592a\u5dee\u4e86\u4e00\u4e9b\uff0c\u8ddf\u5609\u4fe1\u7535\u5b50\u6839\u672c\u5c31\u6ca1\u6cd5\u6bd4\uff0c\u4ed6\u4eec\u8981\u501f\u52a9\u5609\u4fe1\u5b9e\u4e1a\u6765\u5f00\u53d1\u4e1c\u5357\u4e9a\u5e02\u573a\uff0c\u7684\u786e\u8981\u5192\u5f88\u5927\u7684\u98ce\u9669\u3002\u201d</p><p>\u3000\u3000\u201c\u5728\u4e1c\u5357\u4e9a\u7684\u52a8\u4f5c\u8fdf\u949d\uff0c\u53c8\u4e3b\u52a8\u4e0b\u8c03\u56fd\u5185\u5e02\u573a\u7684\u9500\u552e\u76ee\u6807\uff0c\u4ed6\u4eec\u7684\u5e02\u573a\u7b56\u7565\u7684\u786e\u4fdd\u5b88\u4e86\u4e00\u4e9b\uff0c\u201d\u8c22\u5251\u5357\u8bf4\u9053\uff0c\u201c\u4fdd\u5b88\u7684\u7231\u8fbe\u7535\u5b50\uff0c\u5bf9\u6211\u4eec\u603b\u662f\u6709\u5229\u7684\u3002\u201d</p><p>\u3000\u3000\u7684\u786e\uff0c\u8981\u4e0d\u662f\u7231\u8fbe\u7535\u5b50\u4e5d\u4e03\u5e74\u7684\u5e02\u573a\u7b56\u7565\u76f8\u5bf9\u4fdd\u5b88\uff0c\u79d1\u738b\u7684\u538b\u529b\u8981\u5927\u8bb8\u591a\u3002\u867d\u7136\u7231\u8fbe\u7535\u5b50\u60f3\u8d70\u501f\u58f3\u4e0a\u5e02\u8fd9\u6761\u8def\uff0c\u8ba9\u4ed6\u4eec\u8d70\u6210\u529f\uff0c\u9526\u6e56\u7684\u5b9e\u529b\u4f1a\u56e0\u6b64\u5927\u589e\uff0c\u4f46\u662f\u7231\u8fbe\u7535\u5b50\u6210\u4e3a\u4e0a\u5e02\u516c\u53f8\u7684\u8d44\u4ea7\uff0c\u5012\u4e0d\u7528\u62c5\u5fc3\u5f20\u606a\u8fd9\u4eba\u8fd8\u80fd\u51ed\u501f\u7231\u8fbe\u7535\u5b50\u8eb2\u5728\u80cc\u540e\u5077\u653e\u51b7\u7bad\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u4ec5\u4ec5\u662f\u542c\u5230\u8fd9\u6837\u7684\u6d88\u606f\uff0c\u8c22\u5251\u5357\u5373\u4f7f\u4e0d\u80fd\u4f1a\u611f\u53d7\u5230\u592a\u5927\u7684\u538b\u529b\uff0c\u4e5f\u81f3\u4e8e\u5fc3\u60c5\u6109\u5feb\uff1b\u7701\u91cc\u5df2\u7ecf\u5bf9\u82cf\u9038\u7fa4\u5230\u6d77\u5dde\u62c5\u4efb\u5e02\u59d4\u526f\u4e66\u8bb0\u7684\u51b3\u5b9a\u6b63\u5f0f\u53d1\u6587\u5230\u6d77\u5dde\uff0c\u8fd9\u624d\u662f\u8c22\u5251\u5357\u5fc3\u60c5\u6109\u5feb\u7684\u6700\u4e3b\u8981\u539f\u56e0\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u30002\u670828\u65e5\uff0c\u4e5f\u662f\u76db\u946b\u73af\u7403\u7535\u5668\u5efa\u90ba\u65b0\u5e02\u8857\u5e97\u5f00\u4e1a\u7684\u65e5\u5b50\uff0c\u53f6\u5efa\u658c\u4ece\u673a\u573a\u63a5\u5230\u5f20\u606a\uff0c\u5c31\u76f4\u5954\u65b0\u5e02\u8857\u3002</p><p>\u3000\u3000\u5f20\u606a\u662f\u5728\u8f66\u5b50\u8fc7\u57ce\u5357\u5feb\u901f\u9053\u65f6\u63a5\u5230\u8bb8\u745e\u5e73\u7684\u7535\u8bdd\uff0c\u5f97\u77e5\u7701\u91cc\u6b63\u5f0f\u53d1\u6587\u4efb\u547d\u82cf\u9038\u7fa4\u4e3a\u6d77\u5dde\u5e02\u59d4\u526f\u4e66\u8bb0\u7684\u6d88\u606f\uff0c\u6709\u4e9b\u7a81\u7136\uff0c\u7701\u59d4\u7ec4\u7ec7\u90e8\u5c31\u82cf\u9038\u7fa4\u7684\u4efb\u547d\u8ddf\u6d77\u5dde\u5e02\u59d4\u6c9f\u901a\u8fd8\u6ca1\u6709\u4e24\u5929\uff0c\u4efb\u547d\u5c31\u4e0b\u6765\u4e86\uff0c\u5f20\u606a\u4e4b\u524d\u8fd8\u9884\u8ba1\u80fd\u518d\u62d6\u4e24\u4e2a\u6708\u3002</p><p>\u3000\u3000\u63a5\u4e0b\u6765\u4f9d\u7167\u7a0b\u5e8f\uff0c\u5468\u5bcc\u660e\u5c31\u8981\u4e3b\u52a8\u8f9e\u804c\u5e02\u59d4\u4e66\u8bb0\u7684\u804c\u52a1\u3001\u5510\u5b66\u8c26\u4e5f\u8981\u5411\u5e02\u4eba\u5927\u4e3b\u52a8\u8f9e\u53bb\u5e02\u957f\u7684\u804c\u52a1\uff0c\u8981\u7ed9\u82cf\u9038\u7fa4\u632a\u4f4d\u5b50\u3002</p><p>\u3000\u3000\u5f20\u606a\u7684\u884c\u7a0b\uff0c\u5e02\u91cc\u8bb8\u745e\u5e73\u4f1a\u968f\u65f6\u638c\u63e1\uff0c\u6709\u4ec0\u4e48\u6d88\u606f\u53ef\u4ee5\u53ca\u65f6\u8054\u7edc\u3002</p><p>\u3000\u3000\u6839\u636e\u6587\u4ef6\u7684\u8981\u6c42\uff0c\u82cf\u9038\u7fa4\u4f1a\u5728\u4e94\u65e5\u4e4b\u5185\u5230\u6d77\u5dde\u8d74\u4efb\uff0c\u8fd9\u5927\u6982\u5c31\u662f\u4ed6\u4eec\u5e0c\u671b\u770b\u5230\u7684\u5468\u82cf\u8054\u5408\u5236\u8861\u5510\u5b66\u8c26\u7684\u5c40\u9762\u5427\uff0c\u5f20\u606a\u653e\u4e0b\u7535\u8bdd\uff0c\u770b\u7740\u8f66\u7a97\u5916\u521d\u6625\u51b7\u5bc2\u7684\u7530\u91ce\uff0c\u7530\u91cc\u7684\u6ce5\u571f\u7ed8\u51b7\u5f97\u53d1\u767d\u3002</p><p>\u3000\u3000\u201c\u8fd9\u4e48\u5feb\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u5c06\u6d88\u606f\u544a\u8bc9\u53f6\u5efa\u658c\uff0c\u53f6\u5efa\u658c\u4e5f\u611f\u5230\u60ca\u8bb6\u3002</p><p>\u3000\u3000\u201c\u674e\u4ee3\u7701\u957f\u7684\u4ee3\u5b57\u8fd8\u6ca1\u6709\u53bb\u6389\uff0c\u7701\u957f\u7684\u5a01\u529b\u5c31\u53d1\u6325\u51fa\u6765\u4e86\u2026\u2026\u201d\u5f20\u606a\u5c06\u89c6\u7ebf\u4ece\u8f66\u7a97\u5916\u6536\u56de\u6765\uff0c\u53c8\u611f\u6168\u7684\u8bf4\u4e86\u4e00\u53e5\uff0c\u201c\u6211\u4eec\u6216\u8bb8\u9009\u62e9\u4e86\u4e00\u6761\u6bd4\u8f83\u96be\u7684\u8def\u5728\u8d70\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u8981\u662f\u674e\u4ee3\u7701\u957f\u819d\u524d\u6e29\u987a\u7684\u5c0f\u7ef5\u7f8a\uff0c\u8def\u5c31\u6ca1\u8fd9\u4e48\u96be\u4e86\uff0c\u201d\u53f6\u5efa\u658c\u62cd\u4e86\u62cd\u8111\u95e8\uff0c\u53c8\u7b11\u7740\u8bf4\u9053\uff0c\u201c\u4f60\u8981\u662f\u80fd\u5230\u9676\u4e66\u8bb0\u95e8\u4e0b\u591a\u8d70\u52a8\uff0c\u4e5f\u884c\u554a\u2026\u2026\u201d</p><p>\u3000\u3000\u82cf\u9038\u7fa4\u7684\u4efb\u547d\u80fd\u8fd9\u4e48\u4e0b\u53bb\uff0c\u6ca1\u6709\u9676\u664b\u7684\u8ba4\u53ef\uff0c\u674e\u8fdc\u6e56\u4f7f\u518d\u5927\u7684\u52b2\u4e5f\u4e0d\u884c\u3002</p><p>\u3000\u3000\u91d1\u56fd\u6d77\u3001\u82cf\u9038\u7fa4\u4e24\u4e2a\u4eba\u7684\u4efb\u547d\uff0c\u9676\u664b\u8868\u73b0\u51fa\u6765\u7684\u59ff\u6001\uff0c\u5728\u5916\u4eba\u770b\u6765\u662f\u622a\u7136\u4e0d\u540c\u7684\uff0c\u4f46\u662f\u8fd9\u4e2a\u4e5f\u4e0d\u80fd\u7406\u89e3\uff0c\u9676\u664b\u8981\u7ef4\u6301\u4ed6\u7701\u59d4\u4e66\u8bb0\u7684\u6743\u5a01\uff0c\u4f46\u662f\u5f20\u606a\u3001\u5510\u5b66\u8c26\u53c8\u4e0d\u80fd\u7b97\u662f\u4ed6\u7684\u95e8\u4e0b\u5ba2\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u5012\u662f\u6b63\u7406\uff0c\u56fd\u5185\u7684\u653f\u6cbb\u6c1b\u56f4\u5982\u6b64\uff0c\u8d44\u672c\u4e3b\u4e49\u603b\u5f52\u662f\u8981\u7ed9\u6253\u5012\u7684\uff0c\u5373\u4f7f\u6253\u4e0d\u5012\uff0c\u4f46\u4e5f\u4e0d\u80fd\u963b\u6b62\u67d0\u4e9b\u5b98\u5458\u8111\u5b50\u91cc\u6709\u8fd9\u6837\u7684\u60f3\u6cd5\uff0c\u4ee5\u540e\u5934\u75bc\u7684\u4e8b\u60c5\u4f1a\u8d8a\u6765\u8d8a\u591a\uff0c\u7b49\u7740\u5427\u3002\u201d</p></article>"}}}]);
