(window.webpackJsonp=window.webpackJsonp||[]).push([[516],{538:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u56db\u5341\u516b\u8282 \u9f99\u5316\u7684\u9ebb\u70e6",content:"<article><h2>\u7b2c\u56db\u5341\u516b\u8282 \u9f99\u5316\u7684\u9ebb\u70e6</h2><p>\u743c\u6069\u51fb\u8d25\u5438\u8840\u9b3c\u540e\uff0c\u6025\u5306\u5306\u53bb\u5bfb\u627e\u6885\u83f2\u65af\u548c\u51db\uff0c\u521a\u98de\u5230\u5c71\u9876\u5c31\u88ab\u4e00\u9635\u4e71\u77f3\u96e8\u5f53\u5934\u7838\u4e0b\u3002\u5e78\u597d\u4f5c\u4e3a\u9ad8\u9636\u5deb\u5e08\uff0c\u4f20\u9001\u672f\u662f\u5fc5\u5907\u7684\u9b54\u6cd5\uff0c\u5343\u94a7\u4e00\u53d1\u4e4b\u9645\u77ac\u79fb\u5230\u4e94\u91cc\u5f00\u5916\uff0c\u9669\u9669\u8eb2\u8fc7\u4e86\u8fd9\u706d\u9876\u4e4b\u707e\u3002\u7206\u70b8\u5e73\u606f\u540e\uff0c\u70df\u5c18\u88ab\u98ce\u5439\u6563\uff0c\u743c\u6069\u60ca\u8bb6\u5730\u53d1\u73b0\u5c11\u4e86\u5c0f\u534a\u622a\u5c16\u7aef\u7684\u5c71\u4f53\u5185\u90e8\uff0c\u51fa\u73b0\u4e86\u4e00\u4e2a\u5de8\u5927\u7684\u6df1\u6d1e\u3002</p><p>\u201c\u539f\u6765\u8fd9\u5ea7\u5c71\u5cf0\u662f\u4e2d\u7a7a\u7684\u2026\u2026\u8fd9\u5c31\u662f\u51b0\u8679\u7684\u5de2\u7a74\u5427\u3002\u201d</p><p>\u743c\u6069\u98de\u5230\u5c71\u5cf0\u4e0a\u7a7a\uff0c\u5f80\u4e0b\u4fef\u77b0\uff0c\u5bfb\u627e\u6885\u83f2\u65af\u548c\u51db\u7684\u8eab\u5f71\u3002\u4ed6\u6ca1\u770b\u5230\u4e24\u4f4d\u5c11\u5973\uff0c\u5374\u53d1\u73b0\u4e86\u4e00\u53ea\u9cde\u7532\u707f\u70c2\u7684\u706b\u7ea2\u8272\u5de8\u9f99\uff0c\u6b63\u8db4\u4f0f\u5728\u6d1e\u5e95\uff0c\u770b\u59ff\u52bf\u50cf\u662f\u5728\u7528\u8eab\u4f53\u906e\u853d\u4fdd\u62a4\u7740\u4ec0\u4e48\u3002\u201c\u600e\u4e48\u4f1a\u6709\u7ea2\u9f99\uff1f\u201d\u743c\u6069\u6115\u7136\uff0c\u4e00\u65f6\u8111\u7b4b\u6253\u7ed3\uff0c\u6ca1\u8f6c\u8fc7\u5f2f\uff0c\u6070\u5728\u6b64\u65f6\uff0c\u90a3\u53ea\u7ea2\u9f99\u62ac\u8d77\u5934\u671d\u4e0a\u671b\u6765\uff0c\u6b63\u597d\u770b\u89c1\u4ed6\u3002</p><p>\u201c\u5feb\u8fc7\u6765\uff0c\u743c\u6069\uff01\u201d\u719f\u6089\u7684\u5c11\u5973\u55d3\u97f3\u81ea\u7ea2\u9f99\u53e3\u4e2d\u53d1\u51fa\uff0c\u201c\u827e\u5f25\u8587\u51fa\u4e8b\u4e86\uff01\u201d</p><p>\u743c\u6069\u8fd9\u624d\u53cd\u5e94\u8fc7\u6765\uff0c\u8fde\u5fd9\u5f80\u4e0b\u964d\u843d\u3002\u7ea2\u9f99\u632a\u5f00\u5e9e\u5927\u7684\u8eab\u8eaf\uff0c\u9732\u51fa\u88ab\u4fdd\u62a4\u5728\u8179\u90e8\u4e0b\u9762\u7684\u94f6\u7532\u5c11\u5973\uff0c\u5979\u53cc\u76ee\u7d27\u95ed\uff0c\u8138\u8272\u82cd\u767d\u5f97\u5413\u4eba\uff0c\u5927\u9897\u5927\u9897\u7684\u6c57\u73e0\u4ece\u989d\u5934\u5192\u51fa\uff0c\u5934\u53d1\u5df2\u7ecf\u5b8c\u5168\u6e7f\u900f\u3002\u743c\u6069\u5feb\u901f\u68c0\u67e5\uff0c\u53d1\u73b0\u8eab\u4e0a\u5e76\u6ca1\u6709\u4efb\u4f55\u4f24\u53e3\uff0c\u94f6\u7532\u6cbe\u7740\u51e0\u70b9\u8840\u8ff9\uff0c\u4e5f\u660e\u663e\u4e0d\u662f\u5979\u81ea\u5df1\u7684\uff0c\u6574\u4e2a\u4eba\u5374\u4e0d\u65ad\u5730\u8f7b\u5fae\u98a4\u6296\uff0c\u50cf\u662f\u5f97\u4e86\u91cd\u75c5\uff0c\u5728\u53d1\u9ad8\u70e7\uff0c\u4f46\u4f53\u8868\u6e29\u5ea6\u53c8\u5b8c\u5168\u6b63\u5e38\u3002\u201c\u5979\u600e\u4e48\u4e86\uff0c\u51db\uff1f\u201d\u743c\u6069\u60ca\u95ee\uff0c\u201c\u600e\u4e48\u56de\u4e8b\uff1f\u201d</p><p>\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\uff0c\u201d\u7ea2\u9f99\u4e00\u526f\u60ca\u614c\u5931\u63aa\u7684\u6a21\u6837\uff0c\u58f0\u97f3\u91cc\u90fd\u5e26\u4e86\u54ed\u8154\uff0c\u201c\u4e00\u76f4\u8fd8\u597d\u597d\u7684\uff0c\u7a81\u7136\u5c31\u2026\u2026\u5bf9\u65b9\u7684\u6cd5\u672f\u660e\u660e\u6ca1\u6253\u4e2d\u5979\u554a\u2026\u2026\u201d</p><p>\u743c\u6069\u6df1\u5438\u53e3\u6c14\uff0c\u5f3a\u8feb\u81ea\u5df1\u9547\u5b9a\u4e0b\u6765\uff0c\u4ed6\u522b\u65e0\u6240\u957f\uff0c\u5e73\u5fc3\u9759\u6c14\u4e34\u5371\u4e0d\u4e71\u7684\u672c\u4e8b\u8fd8\u6709\u51e0\u5206\u3002\u4e24\u5c0a\u5973\u796d\u53f8\u9b54\u50cf\u518d\u5ea6\u88ab\u53ec\u51fa\uff0c\u63a5\u8fde\u5bf9\u6885\u83f2\u65af\u91ca\u653e\u5404\u79cd\u795d\u798f\u672f\uff0c\u5e0c\u671b\u80fd\u591f\u5148\u7a33\u5b9a\u4f4f\u5c40\u52bf\u3002\u201c\u522b\u7740\u6025\uff0c\u51db\uff0c\u201d\u4ed6\u8bf4\uff0c\u5c3d\u91cf\u628a\u8bed\u901f\u653e\u6162\uff0c\u201c\u4f60\u628a\u6574\u4e2a\u60c5\u51b5\u8bf4\u4e00\u904d\u3002\u201d</p><p>\u201c\u54e6\uff0c\u597d\u3002\u201d</p><p>\u7ea2\u9f99\u6b63\u5f85\u8ff0\u8bf4\uff0c\u5374\u53c8\u88ab\u6253\u65ad\u4e86\uff0c\u201c\u2014\u2014\u90a3\u4e2a\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u51db\uff0c\u4f60\u80fd\u4e0d\u80fd\u5148\u53d8\u56de\u6765\uff0c\u8fd9\u6837\u5b50\u548c\u4f60\u8bf4\u8bdd\uff0c\u6211\u597d\u6709\u538b\u529b\u7684\u3002\u201d</p><p>\u51db\u5e74\u5c81\u5c1a\u5e7c\uff0c\u53d8\u6210\u7684\u7ea2\u9f99\u4e5f\u53ea\u662f\u4e2a\u201c\u5c11\u5e74\u201d\u3002\u9f99\u662f\u6240\u8c13\u80fd\u591f\u201c\u65e0\u9650\u8fdb\u5316\u201d\u7684\u751f\u7269\uff0c\u6ca1\u6709\u6210\u957f\u4e0a\u9650\u4e00\u8bf4\uff0c\u4f53\u578b\u4e5f\u662f\u968f\u7740\u5e74\u9f84\u800c\u9012\u589e\uff0c\u5dee\u522b\u5c42\u6b21\u6781\u5176\u660e\u663e\uff0c\u592a\u53e4\u9f99\u5927\u5982\u5c71\u5cb3\uff0c\u96cf\u9f99\u5374\u5c0f\u5982\u732b\u72d7\uff0c\u81f3\u4e8e\u4e00\u53ea\u5c11\u5e74\u7ea2\u9f99\uff0c\u4e5f\u5c31\u548c\u6210\u5e74\u5927\u8c61\u76f8\u5f53\uff0c\u4e0d\u7b97\u975e\u5e38\u79bb\u8c31\uff0c\u4f46\u743c\u6069\u8fd8\u662f\u5f88\u4e0d\u4e60\u60ef\u3002\u539f\u672c\u5a07\u4fcf\u53ef\u7231\u7684\u5973\u53cb\uff0c\u7a81\u7136\u53d8\u6210\u5e9e\u5927\u5a01\u731b\u7684\u7ea2\u9f99\uff0c\u4e0d\u7ba1\u54ea\u4e2a\u7537\u4eba\u90fd\u4f1a\u8868\u793a\u538b\u529b\u5f88\u5927\u5427\u3002</p><p>\u201c\u4e0d\u884c\uff01\u201d\u7ea2\u9f99\u4eff\u4f5b\u5413\u4e86\u4e00\u8df3\uff0c\u8fde\u8fde\u6447\u5934\u8868\u793a\u62d2\u7edd\u3002</p><p>\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u743c\u6069\u5947\u602a\u3002</p><p>\u7ea2\u9f99\u6b32\u8a00\u53c8\u6b62\uff0c\u7136\u540e\u53c8\u7a81\u7136\u6539\u4e86\u4e3b\u610f\uff0c\u201c\u597d\u5427\u597d\u5427\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u4f60\u8f6c\u8fc7\u8eab\u53bb\uff0c\u4e0d\u51c6\u5077\u770b\uff01\u201d</p><p>\u5077\u770b\uff1f\u5077\u770b\u4ec0\u4e48\uff1f</p><p>\u743c\u6069\u4e0d\u89e3\uff0c\u4f46\u8fd8\u662f\u4f9d\u8a00\u8f6c\u8fc7\u8eab\u53bb\uff0c\u773c\u89d2\u77a5\u89c1\u70ab\u4e3d\u7684\u8d64\u7ea2\u7130\u5149\u81ea\u80cc\u540e\u7206\u51fa\uff0c\u7d27\u63a5\u7740\u5c31\u542c\u89c1\u8f7b\u5fae\u7684\u7ab8\u7ab8\u7aa3\u7aa3\u58f0\uff0c\u50cf\u662f\u8f7b\u67d4\u7684\u5e03\u6599\u6ed1\u8fc7\u5c11\u5973\u808c\u80a4\u3002\u5341\u51e0\u79d2\u79cd\u540e\uff0c\u51db\u7684\u58f0\u97f3\u4f20\u6765\uff0c\u201c\u597d\u4e86\u3002\u201d\u5979\u8bf4\u3002</p><p>\u743c\u6069\u56de\u8eab\u4e00\u770b\uff0c\u51db\u5df2\u7ecf\u81ea\u7ea2\u9f99\u53d8\u56de\u4e86\u4eba\u7c7b\u5c11\u5973\u5f62\u6001\uff0c\u8863\u670d\u5374\u6362\u4e86\u3002\u65e9\u4e0a\u51fa\u95e8\u65f6\uff0c\u5979\u7a7f\u7684\u662f\u65e0\u8896\u7ea2\u8272\u886c\u886b\u548c\u9ed1\u8272\u8d85\u77ed\u88d9\uff0c\u900f\u7740\u5973\u6027\u7684\u5a07\u7f8e\u53ef\u7231\uff0c\u73b0\u5728\u5374\u662f\u84dd\u8272\u957f\u8896\u886c\u886b\u52a0\u767d\u8272\u957f\u88e4\uff0c\u518d\u52a0\u767d\u8272\u725b\u76ae\u77ed\u9774\uff0c\u7ebf\u6761\u7b80\u6d01\u660e\u5feb\uff0c\u6ca1\u6709\u4efb\u4f55\u82b1\u9970\uff0c\u6574\u5957\u8863\u670d\u4fee\u8eab\u8d34\u4f53\uff0c\u4fe8\u7136\u4e00\u4f4d\u82f1\u59ff\u98d2\u723d\u7684\u7537\u88c5\u4e3d\u4eba\u3002\u201c\u4f60\u6362\u8863\u670d\u505a\u4ec0\u4e48\uff1f\u201d\u743c\u6069\u521a\u60f3\u95ee\uff0c\u8bdd\u6ca1\u51fa\u53e3\u7ec8\u4e8e\u53cd\u5e94\u8fc7\u6765\uff0c\u660e\u767d\u51db\u521a\u624d\u4e3a\u4ec0\u4e48\u62d2\u7edd\u6062\u590d\u4eba\u5f62\u4e86\u3002</p><p>\u51db\u501f\u52a9\u9f99\u795e\u4e4b\u8840\u5b8c\u6210\u9f99\u5316\uff0c\u5f53\u5979\u4ece\u4eba\u5f62\u53d8\u6210\u9f99\u5f62\u65f6\uff0c\u4f53\u578b\u4f55\u6b62\u589e\u5927\u4e86\u5341\u500d\uff0c\u800c\u4e14\u56e0\u4e3a\u662f\u81ea\u7136\u53d8\u5f62\uff0c\u4e0d\u662f\u9b54\u6cd5\u53d8\u5f62\uff0c\u90a3\u4e9b\u9b54\u6cd5\u7269\u54c1\u53ef\u4ee5\u81ea\u52a8\u878d\u5165\u53d8\u5f62\u540e\u7684\u8eab\u4f53\uff0c\u4f46\u666e\u901a\u7684\u8863\u670d\u5374\u4e0d\u80fd\uff0c\u81ea\u7136\u5168\u90fd\u6491\u7206\u4e86\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c\u51db\u7684\u7ea2\u9f99\u5f62\u6001\u65f6\u5176\u5b9e\u662f\u88f8\u4f53\uff0c\u518d\u53d8\u56de\u4eba\u5f62\u81ea\u7136\u8fd8\u662f\u88f8\u4f53\u3002\u867d\u8bf4\u9762\u5bf9\u7684\u662f\u81ea\u5df1\u60c5\u4eba\uff08\u743c\u6069\u548c\u6885\u83f2\u65af\u90fd\u662f\uff09\uff0c\u4f46\u5149\u5929\u5316\u65e5\u4e4b\u4e0b\uff0c\u8352\u5c71\u91ce\u5cad\u4e4b\u4e2d\uff0c\u4ee5\u88f8\u4f53\u793a\u4eba\uff0c\u5c0f\u5973\u5deb\u8fd8\u662f\u4f1a\u5bb3\u7f9e\u7684\u3002\u5e78\u8fd0\u7684\u662f\u5979\u4e5f\u6709\u6b21\u5143\u888b\uff0c\u91cc\u9762\u5907\u6709\u8863\u670d\uff0c\u6d3e\u4e0a\u4e86\u7528\u573a\u3002</p><p>\u4e0d\u8fc7\u743c\u6069\u6709\u4e9b\u7591\u60d1\u3002\u867d\u7136\u90fd\u662f\u7f8e\u4e3d\u7684\u5c11\u5973\uff0c\u4f46\u51db\u7684\u7a7f\u8863\u98ce\u683c\u548c\u6885\u83f2\u65af\u5dee\u5f02\u5de8\u5927\uff0c\u6253\u5f00\u5979\u4eec\u7684\u8863\u6a71\u5c31\u77e5\u9053\uff0c\u524d\u8005\u66f4\u5973\u6027\u5316\uff0c\u857e\u4e1d\u82b1\u8fb9\u6ce1\u6ce1\u8896\u8fde\u8863\u88d9\u4e00\u5927\u5806\uff0c\u5f80\u5f80\u8fd8\u7f00\u7740\u5404\u79cd\u4eae\u6676\u6676\u7684\u9970\u7269\uff1b\u540e\u8005\u5927\u6982\u56e0\u4e3a\u804c\u4e1a\u5173\u7cfb\uff0c\u529b\u6c42\u7b80\u6d01\u5229\u843d\uff0c\u8863\u670d\u90fd\u504f\u4e2d\u6027\uff0c\u751a\u81f3\u76f4\u63a5\u5c31\u662f\u7537\u88c5\uff0c\u88d9\u5b50\u5219\u662f\u4e00\u4ef6\u90fd\u6ca1\u6709\u3002\u51db\u73b0\u5728\u7a7f\u7684\u8fd9\u4ef6\u8863\u670d\uff0c\u600e\u4e48\u770b\u600e\u4e48\u50cf\u6885\u83f2\u65af\u7684\uff0c\u6bd4\u5982\u886c\u886b\u7684\u80f8\u53e3\u90e8\u4f4d\u660e\u663e\u9884\u7559\u7a7a\u95f4\u4e0d\u8db3\uff0c\u6885\u83f2\u65af\u7a7f\u6b63\u597d\uff0c\u5bf9\u4e8e\u51db\u6765\u8bf4\u5c31\u663e\u5f97\u592a\u7d27\u4e86\uff0c\u8ba9\u4eba\u6000\u7591\u5979\u53ea\u8981\u505a\u4e2a\u6df1\u547c\u5438\uff0c\u7ebd\u6263\u5c31\u4f1a\u556a\u556a\u556a\u5730\u7ef7\u5f00\u2026\u2026</p><p>\u597d\u5427\uff0c\u8fd9\u4e2a\u95ee\u9898\u4ee5\u540e\u518d\u8bf4\u3002</p><p>\u53d8\u56de\u4eba\u5f62\u7684\u51db\u5f00\u59cb\u8bb2\u8ff0\u521a\u624d\u6240\u53d1\u751f\u7684\u4e8b\u60c5\u3002\u539f\u6765\u5728\u548c\u743c\u6069\u5206\u624b\u4e4b\u540e\uff0c\u5979\u548c\u6885\u83f2\u65af\u4e24\u4eba\u4e00\u8def\u98ce\u9a70\u7535\u63a3\u5730\u95ef\u5165\u9f99\u7a9f\uff0c\u53d1\u73b0\u9f99\u5deb\u6559\u5f92\u5df2\u7ecf\u6210\u529f\u5236\u4f4f\u4e86\u8870\u8001\u7684\u5b9d\u77f3\u9f99\uff0c\u6b63\u5728\u4e3a\u5b83\u8fdb\u884c\u9f99\u5deb\u5996\u7684\u8f6c\u5316\u4eea\u5f0f\uff0c\u773c\u770b\u5c31\u8981\u5b8c\u6210\u3002\u4e24\u4f4d\u5c11\u5973\u81ea\u7136\u4e0d\u80fd\u5750\u89c6\u8fd9\u79cd\u4e8b\u60c5\u53d1\u751f\uff0c\u7acb\u523b\u73b0\u8eab\u963b\u6b62\uff0c\u4e00\u573a\u6fc0\u6218\u4fbf\u6beb\u65e0\u60ac\u5ff5\u5730\u7206\u53d1\u4e86\u3002\u51b0\u8679\u867d\u7136\u8001\u8fc8\u5782\u6b7b\uff0c\u5927\u9650\u5c06\u81f3\uff0c\u7ec8\u7a76\u662f\u4e00\u53ea\u592a\u53e4\u5de8\u9f99\uff0c\u8428\u739b\u65af\u7279\u81ea\u7136\u4e0d\u6562\u6389\u4ee5\u8f7b\u5fc3\uff0c\u6b64\u6b21\u6d3e\u6765\u7684\u56db\u4eba\uff0c\u9664\u4e86\u5438\u8840\u9b3c\u970d\u6587\u662f\u6765\u81ea\u6697\u591c\u9762\u5177\u7684\u5916\u63f4\uff0c\u7559\u5728\u5916\u9762\u8b66\u6212\u653e\u98ce\uff0c\u8fdb\u9f99\u7a9f\u7684\u4e09\u4eba\u90fd\u662f\u9f99\u5deb\u6559\u4e2d\u7b2c\u4e00\u6d41\u7684\u4ea1\u7075\u5e08\u2014\u2014\u800c\u5728\u8fd9\u4e09\u540d\u4ea1\u7075\u5e08\u4e2d\uff0c\u4e3a\u9996\u7684\u662f\u4e00\u540d\u90aa\u9f99\u4f8d\u8005\u3002</p><p>\u6240\u8c13\u90aa\u9f99\u4f8d\u8005\uff0c\u5373\u662f\u201c\u4f8d\u5949\u9f99\u5deb\u5996\u7684\u4eba\u201d\uff08\u90aa\u9f99\u5728\u6b64\u5904\u7279\u6307\u9f99\u5deb\u5996\uff09\uff0c\u540d\u4e3a\u201c\u4f8d\u8005\u201d\uff0c\u542c\u8d77\u6765\u662f\u4ec6\u4ece\uff0c\u5b9e\u9645\u60c5\u51b5\u5374\u5b8c\u5168\u76f8\u53cd\uff0c\u90aa\u9f99\u4f8d\u8005\u4e0d\u4ec5\u4e0d\u662f\u9f99\u5deb\u5996\u7684\u4ec6\u4ece\uff0c\u751a\u81f3\u5728\u4e00\u5b9a\u610f\u4e49\u4e0a\u8fd8\u662f\u4e3b\u4eba\uff0c\u80fd\u591f\u6307\u6325\u3001\u9a71\u9063\u9f99\u5deb\u5996\u3002\u5f53\u5e74\u9f99\u5deb\u6559\u5168\u76db\u65f6\u671f\uff0c\u5171\u6709\u4e5d\u540d\u90aa\u9f99\u4f8d\u8005\uff0c\u4e2a\u4e2a\u90fd\u662f\u540d\u58f0\u663e\u8d6b\u7684\u5927\u5deb\u5e08\uff0c\u96c4\u8e1e\u4e00\u65b9\u7684\u5f3a\u8005\uff0c\u5728\u6559\u4f1a\u4e2d\u5730\u4f4d\u4ec5\u6b21\u4e8e\u6559\u4e3b\uff08\u8428\u739b\u65af\u7279\uff09\uff0c\u5b9e\u529b\u81ea\u7136\u4e0d\u53ef\u5c0f\u770b\u3002\u6885\u83f2\u65af\u548c\u51db\u4ee5\u4e8c\u654c\u4e09\uff0c\u7aed\u5c3d\u5168\u529b\uff0c\u6210\u529f\u65a9\u6740\u4e86\u4e24\u540d\u4ea1\u7075\u5e08\uff0c\u4f46\u6700\u7ec8\u8fd8\u662f\u665a\u4e86\u4e00\u6b65\uff0c\u672a\u80fd\u963b\u6b62\u90aa\u9f99\u4f8d\u8005\u5b8c\u6210\u4eea\u5f0f\uff0c\u5c06\u51b0\u8679\u8f6c\u5316\u6210\u9f99\u5deb\u5996\u3002</p><p>\u6218\u5c40\u767b\u65f6\u51fa\u73b0\u9006\u8f6c\uff0c\u90aa\u9f99\u4f8d\u8005\u6307\u6325\u7740\u65b0\u664b\u592a\u53e4\u9f99\u5deb\u5996\uff0c\u5bf9\u4e24\u4f4d\u5c11\u5973\u53d1\u8d77\u4e86\u94fa\u5929\u76d6\u5730\u7684\u51f6\u731b\u53cd\u51fb\uff0c\u5c06\u5979\u4eec\u6253\u5f97\u8282\u8282\u540e\u9000\uff0c\u9669\u8c61\u73af\u751f\u3002\u4e0d\u8fc7\u5341\u79d2\u949f\uff0c\u72ec\u89d2\u517d\u9732\u4e1d\u96c5\u4fbf\u4e3a\u4e86\u63a9\u62a4\u6885\u83f2\u65af\uff0c\u88ab\u90aa\u9f99\u4f8d\u8005\u7684\u4e00\u53d1\u8fde\u73af\u95ea\u7535\u70b8\u6210\u7c89\u788e\uff1b\u51db\u73b0\u51fa\u7ea2\u9f99\u4e4b\u8eab\uff0c\u5143\u7d20\u8f6c\u6362\u6cd5\u9635\u5168\u529b\u5f00\u542f\uff0c\u4f46\u4f9d\u65e7\u65e0\u6cd5\u5bf9\u6297\u5df2\u7ecf\u53d8\u6210\u9f99\u5deb\u5996\u7684\u51b0\u8679\u3002\u773c\u89c1\u5f62\u52bf\u5371\u6025\uff0c\u6885\u83f2\u65af\u518d\u5ea6\u4f7f\u7528\u51a5\u6b65\uff0c\u4e00\u51fb\u91cd\u521b\u4e86\u90aa\u9f99\u4f8d\u8005\uff0c\u8feb\u4f7f\u5bf9\u65b9\u5e26\u7740\u9f99\u5deb\u5996\u9003\u9041\u9000\u8d70\uff0c\u51db\u6b32\u5f85\u8ffd\u51fb\uff0c\u5374\u770b\u89c1\u6885\u83f2\u65af\u6beb\u65e0\u9884\u5146\u5730\u7a81\u7136\u5012\u4e86\u4e0b\u6765\u3002</p><p>\u201c\u51a5\u6b65\uff1f\u201d</p><p>\u5728\u51db\u7684\u8bb2\u8ff0\u4e2d\uff0c\u743c\u6069\u654f\u9510\u5730\u628a\u63e1\u5230\u8fd9\u4e2a\u5173\u952e\u70b9\u3002\u6885\u83f2\u65af\u7684\u8eab\u4e0a\u6ca1\u6709\u53d1\u73b0\u4f24\u75d5\uff0c\u5979\u662f\u5723\u6b66\u58eb\uff0c\u5fc3\u667a\u575a\u6bc5\u5982\u94a2\u94c1\uff0c\u8fde\u7075\u5438\u602a\u90fd\u5948\u4f55\u4e0d\u5f97\uff0c\u4e5f\u4e0d\u592a\u53ef\u80fd\u88ab\u7cbe\u795e\u9b54\u6cd5\u4f24\u5bb3\uff0c\u770b\u8d77\u6765\u786e\u5b9e\u662f\u5982\u51db\u6240\u8a00\uff0c\u201c\u6beb\u65e0\u9884\u5146\u201d\u5730\u5012\u4e0b\u3002\u4f46\u4e16\u754c\u4e0a\u6ca1\u6709\u65e0\u7f18\u65e0\u6545\u7684\u4e8b\u60c5\uff0c\u4efb\u4f55\u53d8\u6545\u90fd\u5fc5\u5b9a\u6709\u5176\u8bf1\u56e0\uff0c\u76f4\u89c9\u544a\u8bc9\u743c\u6069\uff0c\u95ee\u9898\u5f88\u6709\u53ef\u80fd\u5c31\u51fa\u5728\u8fd9\u4e2a\u201c\u51a5\u6b65\u201d\u4e0a\u3002</p><p>\u201c\u600e\u4e48\u603b\u89c9\u5f97\u50cf\u662f\u5728\u54ea\u91cc\u542c\u8bf4\u8fc7\u8fd9\u4e2a\u8bcd\u2026\u2026\u5bf9\u4e86\uff0c\u662f\u524d\u5929\u838e\u73de\u514b\u66fe\u7ecf\u63d0\u5230\u3002\u201d</p><p>\u524d\u5929\u5728\u6765\u5854\u745f\u8c37\u7684\u8def\u4e0a\uff0c\u906d\u9047\u4e86\u4e00\u540d\u9f99\u5deb\u6559\u7684\u82cd\u767d\u9886\u4e3b\uff0c\u53d1\u751f\u4e86\u6fc0\u6218\u3002\u6218\u540e\u838e\u73de\u514b\u5c31\u63d0\u5230\u4e86\u201c\u51a5\u6b65\u201d\uff0c\u542c\u5979\u8bed\u6c14\u5e94\u8be5\u662f\u6885\u83f2\u65af\u5728\u6740\u6b7b\u82cd\u767d\u9886\u4e3b\u65f6\u6240\u7528\u7684\u67d0\u79cd\u5f3a\u529b\u6280\u80fd\uff0c\u4f46\u743c\u6069\u5e76\u6ca1\u6709\u770b\u89c1\uff0c\u4e8b\u540e\u4e5f\u6ca1\u591a\u95ee\uff0c\u539f\u672c\u90fd\u5feb\u5fd8\u4e86\uff0c\u88ab\u51db\u8fd9\u4e00\u8bf4\u624d\u53c8\u60f3\u8d77\u3002</p><p>\u201c\u51a5\u6b65\u662f\u4ec0\u4e48\uff1f\u201d\u4ed6\u95ee\u51db\u3002</p><p>\u51db\u5bf9\u6b64\u4e5f\u4e0d\u662f\u5341\u5206\u6e05\u695a\uff0c\u53ea\u77e5\u9053\u662f\u827e\u5f25\u8587\u65b0\u638c\u63e1\u7684\u4e00\u9879\u80fd\u529b\uff0c\u80fd\u591f\u5728\u6218\u6597\u4e2d\u6084\u65e0\u58f0\u606f\u5730\u77ac\u79fb\u5230\u5bf9\u624b\u8eab\u540e\uff0c\u53d1\u52a8\u7834\u574f\u529b\u5de8\u5927\u7684\u653b\u51fb\uff0c\u975e\u5e38\u4e4b\u5389\u5bb3\uff0c\u4f46\u4e5f\u975e\u5e38\u8017\u8d39\u7cbe\u529b\uff0c\u6bcf\u4f7f\u7528\u4e00\u6b21\u4e4b\u540e\u90fd\u4f1a\u4e25\u91cd\u865a\u8131\uff0c\u6240\u4ee5\u6885\u83f2\u65af\u5e73\u5e38\u4ece\u4e0d\u8f7b\u6613\u53d1\u52a8\uff0c\u800c\u4e14\u4e5f\u4f3c\u4e4e\u4e0d\u592a\u613f\u610f\u591a\u63d0\u53ca\u3002</p><p>\u90a3\u4e48\uff0c\u6885\u83f2\u65af\u73b0\u5728\u8fd9\u79cd\u72b6\u51b5\uff0c\u662f\u56e0\u4e3a\u53d1\u52a8\u51a5\u6b65\u7684\u540e\u9057\u75c7\u5417\uff1f</p><p>\u201c\u4e0d\u4f1a\u554a\uff0c\u53d1\u52a8\u51a5\u6b65\u7684\u540e\u679c\u53ea\u662f\u4e25\u91cd\u8017\u8d39\u7cbe\u529b\uff0c\u4f1a\u75b2\u5026\u865a\u8131\u800c\u5df2\uff0c\u795e\u667a\u8fd8\u662f\u6e05\u9192\u7684\uff0c\u4f11\u606f\u4e00\u6bb5\u65f6\u95f4\u5c31\u597d\u4e86\uff0c\u201d\u51db\u89e3\u91ca\uff0c\u201c\u4ece\u6765\u6ca1\u51fa\u73b0\u8fc7\u8fd9\u79cd\u72b6\u51b5\u3002\u201d</p><p>\u201c\u5514\u3002\u201d</p><p>\u867d\u7136\u51db\u5982\u6b64\u8bf4\uff0c\u743c\u6069\u8fd8\u662f\u89c9\u5f97\u6885\u83f2\u65af\u7a81\u7136\u6655\u5012\u548c\u53d1\u52a8\u51a5\u6b65\u4e4b\u95f4\u5fc5\u5b9a\u6709\u6240\u5173\u8054\uff0c\u8fd9\u7b97\u662f\u76f4\u89c9\u5427\uff0c\u4f46\u731c\u6d4b\u5f52\u731c\u6d4b\uff0c\u4f9d\u7136\u6ca1\u6709\u627e\u5230\u89e3\u51b3\u95ee\u9898\u7684\u529e\u6cd5\u3002\u743c\u6069\u4f4e\u5934\u770b\u5411\u6000\u4e2d\u7684\u91d1\u53d1\u5c11\u5973\uff0c\u53d1\u73b0\u5979\u7684\u8138\u8272\u8d8a\u53d1\u82cd\u767d\uff0c\u5168\u8eab\u5fae\u5fae\u98a4\u6296\uff0c\u50cf\u662f\u5728\u505a\u4e00\u573a\u6050\u6016\u7684\u5669\u68a6\uff0c\u65e0\u6cd5\u9192\u6765\u3002\u5728\u4ed6\u548c\u51db\u4ea4\u8c08\u671f\u95f4\uff0c\u4e24\u4f4d\u5353\u5c14\u5973\u796d\u53f8\u9b54\u50cf\u5df2\u7ecf\u5c06\u6240\u6709\u7684\u6cbb\u7597\u795d\u798f\u795e\u672f\u90fd\u91ca\u653e\u51fa\u6765\uff0c\u5374\u6ca1\u6709\u4ea7\u751f\u534a\u70b9\u6210\u6548\u3002</p><p>\u201c\u5c3d\u5feb\u8d76\u56de\u7279\u52a0\u5c14\u9547\uff0c\u770b\u770b\u5927\u4e3b\u6559\u6216\u8005\u90a3\u4e2a\u8001\u5deb\u5996\u957f\u8001\u6709\u6ca1\u6709\u4ec0\u4e48\u529e\u6cd5\uff0c\u201d</p><p>\u8f6c\u5ff5\u4e4b\u95f4\uff0c\u743c\u6069\u505a\u4e86\u51b3\u5b9a\u3002\u8981\u8bba\u6740\u4eba\uff0c\u4ed6\u5012\u8fd8\u7b97\u662f\u6709\u4e9b\u7ecf\u9a8c\uff0c\u4f46\u8981\u8bba\u6551\u4eba\u5c31\u662f\u4e2a\u5f7b\u5e95\u7684\u5916\u884c\uff0c\u65e2\u7136\u81ea\u5df1\u5df2\u7ecf\u675f\u624b\u65e0\u7b56\uff0c\u90a3\u8fd8\u662f\u8d76\u7d27\u53bb\u627e\u4e13\u5bb6\u5427\u3002\u201c\u51db\uff0c\u4f60\u6709\u6ca1\u6709\u51c6\u5907\u4f20\u9001\u672f\uff1f\u201d\u4ed6\u95ee\u3002</p><p>\u201c\u6ca1\u6709\u3002\u201d\u51db\u6447\u5934\u3002</p><p>\u743c\u6069\u76b1\u8d77\u7709\u5934\uff0c\u6885\u83f2\u65af\u7684\u60c5\u51b5\u4e0d\u5999\uff0c\u803d\u8bef\u4e0d\u8d77\u65f6\u95f4\u3002\u4f46\u6b64\u5730\u8ddd\u79bb\u7279\u52a0\u5c14\u9547\u975e\u5e38\u8fdc\uff0c\u81ea\u5df1\u7684\u4f20\u9001\u672f\u5df2\u7ecf\u7528\u5b8c\uff0c\u5929\u754c\u72ec\u89d2\u517d\u4e5f\u88ab\u6467\u6bc1\uff0c\u8981\u600e\u6837\u624d\u80fd\u5feb\u901f\u8d76\u56de\u53bb\u5462\uff1f\u6709\u4e86\uff0c\u72ec\u89d2\u517d\u867d\u7136\u6ca1\u6709\u4e86\uff0c\u8fd9\u91cc\u4e0d\u662f\u8fd8\u6709\u6761\u7ea2\u9f99\u4e48\u2026\u2026</p><p>\u53d1\u73b0\u743c\u6069\u7684\u773c\u5149\u671d\u81ea\u5df1\u79fb\u8fc7\u6765\uff0c\u51db\u8d77\u521d\u4e0d\u89e3\uff0c\u968f\u5373\u4fbf\u660e\u767d\u4e86\u4ed6\u7684\u7528\u610f\u3002\u5c0f\u5973\u5deb\u6241\u4e86\u6241\u5634\uff0c\u4f3c\u4e4e\u6709\u4e9b\u4e0d\u60c5\u613f\uff0c\u4f46\u8fd8\u662f\u70b9\u5934\u7b54\u5e94\u3002\u201c\u9000\u540e\u70b9\u3002\u201d\u5979\u8bf4\uff0c\u6df1\u6df1\u5438\u4e86\u53e3\u6c14\uff0c\u6b63\u5f85\u53d8\u8eab\u5316\u9f99\uff0c\u731b\u7136\u95f4\u4e00\u4e2a\u51b7\u51b7\u7684\u58f0\u97f3\u81ea\u8eab\u540e\u4e0d\u8fdc\u5904\u54cd\u8d77\u3002</p><p>\u201c\u6765\u4e0d\u53ca\u4e86\u3002\u201d</p><p>\u4e24\u4eba\u90fd\u662f\u4e00\u6014\uff0c\u5faa\u58f0\u671b\u53bb\uff0c\u53ea\u89c1\u4e00\u4f4d\u9ed1\u8863\u5c11\u5973\u6b63\u9759\u9759\u5730\u7ad9\u5728\u89d2\u843d\u7684\u9634\u5f71\u4e2d\uff0c\u80cc\u501a\u7740\u77f3\u58c1\uff0c\u53cc\u624b\u73af\u62b1\u80f8\u524d\uff0c\u5634\u89d2\u5fae\u5fae\u4e0a\u7fd8\uff0c\u5e26\u7740\u4e00\u4e1d\u8ba5\u8bbd\u7b11\u610f\u3002\u743c\u6069\u770b\u6e05\u5979\u7684\u9762\u5bb9\uff0c\u968f\u5373\u5927\u5403\u4e00\u60ca\uff0c\u4e0b\u610f\u8bc6\u5730\u540e\u9000\u534a\u6b65\uff0c\u4e3e\u8d77\u6cd5\u6756\u6307\u5411\u5979\u3002\u51db\u4e0d\u8ba4\u8bc6\u9ed1\u8863\u5c11\u5973\uff0c\u4f46\u89c1\u743c\u6069\u7684\u53cd\u5e94\uff0c\u4e0d\u5047\u601d\u7d22\u5730\u4e5f\u6446\u51fa\u4e86\u6212\u5907\u59ff\u6001\u3002</p><p>\u201c\u5982\u679c\u4f60\u559c\u6b22\u6d6a\u8d39\u65f6\u95f4\uff0c\u6211\u65e0\u6240\u8c13\uff0c\u201d\u9ed1\u8863\u5c11\u5973\u8bf4\uff0c\u5979\u7684\u773c\u775b\u4e00\u76f4\u770b\u7740\u743c\u6069\uff0c\u51db\u5bf9\u4e8e\u5979\u800c\u8a00\u4eff\u4f5b\u6839\u672c\u4e0d\u5b58\u5728\uff0c\u201c\u53cd\u6b63\u518d\u8fc7\u4e09\u5206\u949f\uff0c\u5979\u5c31\u4f1a\u6d88\u5931\u4e86\u3002\u201d</p><p>\u201c\u6d88\u5931\uff1f\u201d\u743c\u6069\u4e0d\u89e3\uff0c\u201c\u4ec0\u4e48\u610f\u601d\uff1f\u201d</p><p>\u201c\u5979\u7684\u7075\u9b42\u91cc\uff0c\u6709\u79cd\u5145\u6ee1\u8840\u8165\u5473\u9053\u7684\u9ed1\u6697\u529b\u91cf\u6b63\u5728\u82cf\u9192\uff0c\u201d\u9ed1\u8863\u5c11\u5973\u8bed\u6c14\u5e73\u6de1\u5730\u8bf4\uff0c\u201c\u6b64\u65f6\u5979\u7684\u81ea\u6211\u610f\u8bc6\uff0c\u6b63\u5728\u661f\u754c\u4e2d\u548c\u90a3\u79cd\u529b\u91cf\u6218\u6597\u3002\u5982\u679c\u6ca1\u6709\u5176\u4ed6\u53d8\u6570\uff0c\u4e24\u5206\u56db\u5341\u4e8c\u79d2\u4e4b\u540e\u5979\u7684\u81ea\u6211\u610f\u8bc6\u5c31\u4f1a\u5f7b\u5e95\u6cef\u706d\uff0c\u7075\u9b42\u88ab\u90a3\u79cd\u529b\u91cf\u5b8c\u5168\u541e\u566c\uff0c\u4e0d\u590d\u5b58\u5728\u4e8e\u6b64\u4e16\u754c\u2014\u2014\u8fd9\u5c31\u662f\u6d88\u5931\u7684\u610f\u601d\u3002\u201d</p><p>\u4e24\u5206\u56db\u5341\u4e8c\u79d2\uff1f\u7075\u9b42\u88ab\u541e\u566c\uff1f</p><p>\u743c\u6069\u548c\u51db\u90fd\u662f\u9762\u8272\u5927\u53d8\uff0c\u4ed6\u4eec\u6e05\u695a\u6885\u83f2\u65af\u7684\u90aa\u795e\u8840\u8109\uff0c\u4e5f\u77e5\u9053\u6740\u622e\u4e4b\u795e\u5df4\u5c14\u501f\u5979\u800c\u590d\u6d3b\u7684\u4f01\u56fe\uff0c\u9ed1\u8863\u5c11\u5973\u6240\u8a00\u6b63\u4e0e\u4e4b\u76f8\u7b26\u3002\u4e8b\u5b9e\u4e0a\uff0c\u4ece\u5f97\u77e5\u201c\u51a5\u6b65\u201d\u4e4b\u65f6\u8d77\uff0c\u743c\u6069\u5c31\u5df2\u7ecf\u5728\u9690\u9690\u6000\u7591\u6b64\u4e8b\u4e0e\u5c11\u5973\u4f53\u5185\u7684\u90aa\u795e\u8840\u8109\u6709\u5173\uff0c\u56e0\u4e3a\u8fd9\u79cd\u77ac\u79fb\u5230\u654c\u4eba\u80cc\u540e\u53d1\u52a8\u5077\u88ad\u7684\u6280\u80fd\uff0c\u900f\u7740\u9c9c\u660e\u7684\u201c\u523a\u5ba2\u201d\u98ce\u683c\uff0c\u4e0d\u53ef\u80fd\u662f\u63d0\u5c14\u6240\u8d50\u4e88\u7684\u795e\u5723\u80fd\u529b\uff0c\u800c\u6740\u622e\u4e4b\u795e\u5df4\u5c14\uff0c\u6b63\u662f\u4ee5\u51e1\u4eba\u523a\u5ba2\u4e4b\u8eab\u5c01\u795e\u3002</p><p>\u201c\u90a3\u2026\u2026\u90a3\u4f60\u80fd\u6551\u5979\u5417\uff1f\u201d\u51db\u7126\u6025\u5730\u95ee\uff0c\u201c\u4f60\u80af\u5b9a\u6709\u529e\u6cd5\u5bf9\u5427\uff1f\u201d</p><p>\u5018\u82e5\u9ed1\u8863\u5c11\u5973\u6240\u8a00\u5c5e\u5b9e\uff0c\u6885\u83f2\u65af\u4fbf\u9762\u4e34\u751f\u6b7b\u5371\u673a\uff0c\u800c\u4e14\u8981\u8d76\u56de\u7279\u52a0\u5c14\u9547\u4e5f\u5df2\u7ecf\u6765\u4e0d\u53ca\u3002\u5c31\u7b97\u8d76\u56de\u53bb\uff0c\u8fd9\u79cd\u7075\u9b42\u5c42\u9762\u7684\u6218\u6597\uff0c\u63d0\u5c14\u6559\u4f1a\u7684\u7267\u5e08\u4eec\u53ea\u6015\u4e5f\u540c\u6837\u675f\u624b\u65e0\u7b56\u3002\u4f46\u9ed1\u8863\u5c11\u5973\u65e2\u7136\u4e3b\u52a8\u73b0\u8eab\uff0c\u8bf4\u8d77\u6b64\u4e8b\uff0c\u6309\u7167\u5e38\u7406\u63a8\u6d4b\uff0c\u5979\u5f88\u53ef\u80fd\u5c31\u6709\u633d\u6551\u7684\u65b9\u6cd5\u5427\u3002</p><p>\u201c\u8981\u6551\u5979\u5f88\u7b80\u5355\uff0c\u201d\u9ed1\u8863\u5c11\u5973\u8bf4\uff0c\u201c\u53c2\u6218\uff0c\u7136\u540e\u6467\u6bc1\u90a3\u79cd\u9ed1\u6697\u529b\u91cf\u5c31\u884c\u3002\u201d</p><p>\u2026\u2026\u542c\u8d77\u6765\u5f88\u5bf9\uff0c\u95ee\u9898\u662f\u600e\u4e48\u505a\uff1f\u65e2\u7136\u6885\u83f2\u65af\u7684\u81ea\u6211\u610f\u8bc6\u6b63\u4e0e\u5df4\u5c14\u5728\u661f\u754c\u4e2d\u6218\u6597\uff0c\u90a3\u5982\u4f55\u53c2\u5165\u5176\u4e2d\uff1f\u4e14\u4e0d\u8bf4\u743c\u6069\u548c\u51db\u7684\u9020\u8be3\u90fd\u8fdc\u8fdc\u4e0d\u8db3\u4ee5\u524d\u5f80\u661f\u754c\uff0c\u5c31\u7b97\u80fd\u53bb\uff0c\u661f\u754c\u4e4b\u5e7f\u9614\u65e0\u8fb9\u65e0\u57a0\uff0c\u53c8\u600e\u4e48\u627e\u5230\u4ed6\u4eec\uff1f</p><p>\u9ed1\u8863\u5c11\u5973\u770b\u7740\u743c\u6069\uff0c\u6c89\u9ed8\u7247\u523b\uff0c\u7136\u540e\u5fae\u5fae\u53f9\u4e86\u53e3\u6c14\uff0c\u4ece\u9634\u5f71\u4e2d\u8d70\u51fa\u6765\u3002\u51db\u7d27\u5f20\u5730\u770b\u7740\uff0c\u89c1\u5979\u8d70\u5230\u743c\u6069\u9762\u524d\uff0c\u6162\u6162\u62ac\u8d77\u53f3\u624b\uff0c\u7d2b\u8272\u706b\u7130\u4ece\u98df\u6307\u6307\u5c16\u6d8c\u51fa\uff0c\u6ce8\u5165\u743c\u6069\u7709\u5fc3\u90e8\u4f4d\u3002\u201c\u501f\u7528\u4e00\u4e0b\u3002\u201d\u5979\u8f7b\u58f0\u8bf4\u3002</p><p>\u80cc\u751f\u53cc\u7ffc\u7684\u91d1\u9cde\u9f99\u86c7\u81ea\u865a\u7a7a\u4e2d\u6e38\u51fa\uff0c\u76d8\u65cb\u7f20\u7ed5\u7740\u9ed1\u8863\u5c11\u5973\u7684\u8eab\u4f53\uff0c\u610f\u6001\u4eb2\u5bc6\uff0c\u4eff\u4f5b\u591a\u5e74\u672a\u89c1\u7684\u6545\u4eba\u3002\u9ed1\u8863\u5c11\u5973\u51b2\u5b83\u5fae\u5fae\u7b11\u4e86\u7b11\uff0c\u4f38\u624b\u629a\u6478\u5b83\u7684\u5934\u90e8\u3002\u4e0b\u4e00\u77ac\u95f4\uff0c\u5979\u4eec\u6d88\u5931\u4e0d\u89c1\u3002</p></article>"}}}]);
