(window.webpackJsonp=window.webpackJsonp||[]).push([[520],{543:function(p){p.exports={chapterTitle:null,title:"\u7b2c520\u7ae0 \u62db\u5b89",content:"<article><h2>\u7b2c520\u7ae0 \u62db\u5b89</h2><p>\u3000\u3000\u53bb\u5e74\u4e09\u6708\u4efd\uff0c\u7701\u6797\u4e1a\u7cfb\u7edf\u6345\u51fa\u5929\u7136\u6797\u8986\u76d6\u7387\u6570\u636e\u4e25\u91cd\u9020\u5047\u7684\u5927\u4e71\u5b50\uff0c\u7ed9\u5f90\u5b66\u5e73\u634b\u4e0b\u53bb\u4e00\u6279\u4eba\uff0c\u521a\u53bb\u6797\u4e1a\u5385\u9540\u91d1\u7684\u539f\u6797\u4e1a\u5385\u5385\u957f\u4e5f\u56e0\u6b64\u9eef\u7136\u79bb\u5f00\u4e1c\u6d77\u5b98\u573a\uff0c\u8be5\u634b\u7684\u4eba\u634b\u4e86\uff0c\u4e5f\u67e5\u4e86\u597d\u51e0\u8d77\u5927\u6848\u5b50\uff0c\u6293\u4e86\u4e00\u6279\u4eba\u8fdb\u53bb\uff0c\u4f46\u662f\u4e25\u91cd\u7684\u4e71\u780d\u6ee5\u4f10\u4f7f\u5f97\u5c0f\u6c5f\u4e0a\u6e38\u7684\u5929\u7136\u6797\u751f\u6001\u4f53\u7cfb\u5904\u4e8e\u4e00\u4e2a\u975e\u5e38\u8106\u5f31\u7684\u72b6\u6001\uff0c\u8fd9\u4e2a\u540e\u679c\u8fd8\u662f\u8981\u5168\u7701\u4eba\u6c11\u6765\u80cc\u8d1f\u3002</p><p>\u3000\u3000\u4e3a\u6b64\uff0c\u5f90\u5b66\u5e73\u4e25\u4ee4\u7981\u6b62\u4e00\u5207\u5929\u7136\u6797\u780d\u4f10\u884c\u4e3a\uff0c\u5404\u56fd\u6709\u6797\u573a\u3001\u68ee\u5de5\u4f01\u4e1a\u7acb\u5373\u8f6c\u5236\u91cd\u7ec4\uff0c\u52a0\u5f3a\u6728\u6750\u539f\u6599\u7684\u8fdb\u53e3\u5de5\u4f5c\uff0c\u7701\u91cc\u62ff\u51fa\u4e00\u90e8\u5206\u94b1\u8fdb\u884c\u8d22\u653f\u8865\u8d34\u3002</p><p>\u3000\u3000\u5728\u8fd9\u4e2a\u80cc\u666f\u4e0b\uff0c\u65b0\u5149\u7eb8\u4e1a\u629b\u51fa\u89c4\u6a21\u5e9e\u5927\u7684\u901f\u4e30\u6797\u8425\u6797\u8ba1\u5212\uff0c\u7acb\u5373\u63a5\u6536\u51e0\u4e4e\u5168\u90e8\u7684\u4e0b\u5c97\u5206\u6d41\u7684\u6797\u4e1a\u5de5\u4eba\uff0c\u4f7f\u5f97\u5f90\u5b66\u5e73\u6574\u6cbb\u7701\u6797\u4e1a\u7cfb\u7edf\u7684\u4e25\u5389\u63aa\u65bd\u80fd\u591f\u987a\u5229\u5b9e\u65bd\u4e0b\u53bb\u3002</p><p>\u3000\u3000\u65b0\u5149\u7eb8\u4e1a\u62ff\u51fa\u6570\u4ebf\u8d44\u91d1\u5728\u5c0f\u6c5f\u4e2d\u4e0a\u6e38\u5404\u53bf\u5168\u9762\u94fa\u5f00\u5f0f\u7684\u8425\u9020\u901f\u4e30\u6797\u7684\u540c\u65f6\uff0c\u8fd8\u629b\u51fa\u4e0e\u4e4b\u914d\u5957\u7684\u5236\u6d46\u9879\u76ee\u89c4\u5212\u3002</p><p>\u3000\u3000\u4e1c\u6d77\u7701\u8425\u6797\u9879\u76ee\u89c4\u6a21\u7ea6\u5341\u4ebf\uff0c\u5236\u6d46\u9879\u76ee\u7684\u89c4\u6a21\u66f4\u52a0\u5e9e\u5927\uff0c\u4f7f\u5f97\u603b\u4f53\u89c4\u6a21\u9ad8\u8fbe\u516d\u5341\u4ebf\u3002</p><p>\u3000\u3000\u4e00\u5e74\u4e4b\u524d\uff0c\u65b0\u5149\u7eb8\u4e1a\u629b\u51fa\u8fd9\u4e24\u4e2a\u9879\u76ee\u65f6\uff0c\u8c22\u6c49\u9756\u7b49\u4eba\u90fd\u55e4\u4e4b\u4ee5\u9f3b\uff0c\u4e5d\u516d\u5e74\uff0c\u5168\u7701\u9020\u7eb8\u4ea7\u4e1a\u603b\u8d44\u4ea7\u89c4\u6a21\u8fd8\u6ca1\u6709\u516d\u5341\u4ebf\uff0c\u8ba4\u4e3a\u53ea\u6709\u90a3\u4e9b\u7ecf\u6d4e\u843d\u540e\u7684\u7701\u897f\u5317\u90e8\u5730\u533a\uff0c\u624d\u4f1a\u7ed9\u5f20\u606a\u4e0e\u5f90\u5b66\u5e73\u8054\u5408\u9ad8\u4e3e\u7684\u8fd9\u4e2a\u5e4c\u5b50\u552c\u4f4f\u2026\u2026</p><p>\u3000\u3000\u542c\u5230\u9ad8\u771f\u8bf4\u674e\u8fdc\u6e56\u6765\u8d74\u5bb4\u4e4b\u524d\u5728\u770b\u65b0\u5149\u7eb8\u4e1a\u7684\u5236\u6d46\u9879\u76ee\u8d44\u6599\uff0c\u8c22\u6c49\u9756\u5fae\u5fae\u4e00\u6123\uff0c\u4e0e\u5468\u747e\u73ba\u8fdf\u7591\u7684\u5bf9\u671b\u4e86\u4e00\u773c\uff1a\u674e\u8fdc\u6e56\u4e3a\u4ec0\u4e48\u8981\u62c9\u9646\u6587\u592b\u6765\u51d1\u70ed\u95f9\uff1f\u539f\u6765\u5728\u674e\u8fdc\u6e56\u5fc3\u76ee\u91cc\u8ba4\u4e3a\u5f20\u606a\u624d\u662f\u5bf9\u4ed6\u6709\u5e2e\u52a9\u7684\u4eba\u554a\uff01\u4ed6\u662f\u8981\u501f\u52a9\u9646\u6587\u592b\u62c9\u8fd1\u4e0e\u90a3\u8fb9\u7684\u5173\u7cfb\uff01</p><p>\u3000\u3000\u516d\u5341\u4ebf\u7684\u9879\u76ee\uff0c\u6628\u65e5\u8fd8\u662f\u6c34\u4e2d\u6708\u3001\u96fe\u4e2d\u82b1\uff1b\u8fd9\u4e00\u5929\u4e00\u8fc7\uff0c\u5609\u4fe1\u5b9e\u4e1a\u516c\u544a\u4e00\u53d1\u5e03\u3001\u9526\u6e56\u62a5\u5907\u6750\u6599\u4e00\u4e0a\u4ea4\uff0c\u4f3c\u4e4e\u90fd\u80fd\u771f\u5207\u7684\u770b\u89c1\u7ec6\u5fae\u5904\u4e86\u3002</p><p>\u3000\u3000\u8c22\u6c49\u9756\u6697\u81ea\u4e4d\u820c\uff0c\u96be\u9053\u8bf4\u8fd9\u5c11\u5e74\u65e9\u5728\u4e00\u5e74\u524d\uff0c\u5c31\u8c0b\u5212\u7740\u8981\u5c06\u7231\u8fbe\u7535\u5b50\u6d77\u5916\u4e0a\u5e02\u5957\u73b0\uff1f\u96be\u9053\u4ed6\u5c31\u80fd\u4e00\u6b65\u4e0d\u9519\u7684\u6599\u51c6\u789f\u673a\u5e02\u573a\u4f1a\u5982\u6b64\u53d1\u5c55\u4e0b\u6765\u3001\u53d1\u5c55\u4eca\u5929\u8fd9\u4e00\u6b65\uff1f</p><p>\u3000\u3000\u8c22\u6c49\u9756\u5fc3\u91cc\u90fd\u6709\u4e9b\u5fae\u5fae\u53d1\u5bd2\uff0c\u5f20\u606a\u8fd9\u5c11\u5e74\u6b65\u6b65\u5148\u673a\uff0c\u6b65\u6b65\u7ad9\u5728\u5236\u9ad8\u70b9\u4e0a\u3002</p><p>\u3000\u3000\u8fd9\u516d\u5341\u4ebf\u7684\u9879\u76ee\u7838\u4e0b\u53bb\uff0c\u4e0a\u4e0b\u6e38\u7684\u4ea7\u4e1a\u88e2\u5e26\u52a8\u8d77\u6765\uff0c\u589e\u52a0\u4e24\u767e\u4e2a\u4ebf\u7684\u56fd\u6c11\u751f\u4ea7\u603b\u503c\u662f\u8f7b\u800c\u6613\u4e3e\u7684\u4e8b\u60c5\uff0c\u90a3\u5c31\u53ef\u4ee5\u7ed9\u5168\u7701\u7684\u7ecf\u6d4e\u6570\u636e\u5b9e\u6253\u5b9e\u7684\u589e\u52a0\u56db\u4e94\u4e2a\u70b9\uff0c\u96be\u602a\u674e\u8fdc\u6e56\u8981\u4e3a\u6b64\u52a8\u5bb9\u3002</p><p>\u3000\u3000\u6b63\u6cf0\u96c6\u56e2\u80fd\u7ed9\u674e\u8fdc\u6e56\u4ec0\u4e48\uff1f</p><p>\u3000\u3000\u8fd9\u79cd\u7ea7\u522b\u7684\u653f\u6cbb\u4eba\u7269\u4f3c\u4e4e\u66f4\u770b\u91cd\u80fd\u4e0d\u80fd\u5b9e\u73b0\u81ea\u5df1\u7684\u653f\u6cbb\u62b1\u8d1f\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u9646\u6587\u592b\u65e2\u6ca1\u6709\u60f3\u5230\u674e\u8fdc\u6e56\u4f1a\u9080\u4ed6\u4e00\u8d77\u53bb\u7ed9\u82cf\u9038\u7fa4\u996f\u884c\uff0c\u4e5f\u6ca1\u6709\u60f3\u5230\u4ece\u9152\u5e97\u79bb\u5f00\u674e\u8fdc\u6e56\u8fd8\u4f1a\u201c\u987a\u8def\u201d\u9001\u4ed6\u56de\u5bb6\uff0c\u4ed6\u5bb6\u5e76\u4e0d\u5728\u674e\u8fdc\u6e56\u56de\u65b0\u6885\u82d1\u7684\u4e00\u6761\u8def\u4e0a\u3002</p><p>\u3000\u3000\u9646\u6587\u592b\u5fc3\u60f3\u674e\u8fdc\u6e56\u6216\u8bb8\u6709\u4ec0\u4e48\u4e8b\u8981\u501f\u7740\u9152\u684c\u7684\u6c14\u6c1b\u8ddf\u4ed6\u8bf4\uff0c\u6ca1\u6709\u63a8\u8f9e\u5c31\u94bb\u8fdb\u674e\u8fdc\u6e56\u7684\u8f66\u3002</p><p>\u3000\u3000\u79bb\u5bb6\u8d8a\u6765\u8d8a\u8fd1\uff0c\u674e\u8fdc\u6e56\u90fd\u6ca1\u6709\u8bf4\u4ec0\u4e48\uff0c\u9646\u6587\u592b\u4e5f\u6ca1\u6cd5\u5f00\u53e3\u95ee\u4ec0\u4e48\uff0c\u53ea\u662f\u6709\u4e00\u53e5\u6ca1\u4e00\u53e5\u804a\u7740\u542c\u4e0a\u53bb\u65e0\u5173\u7d27\u8981\u7684\u5bb6\u5e38\u8bdd\uff0c\u5012\u662f\u53f8\u673a\u5c06\u8f66\u5b50\u505c\u5728\u4ed6\u5bb6\u7684\u5c0f\u533a\u95e8\u5916\uff0c\u674e\u8fdc\u6e56\u624d\u95ee\u4e86\u9646\u6587\u592b\u4e00\u53e5\uff1a\u201c\u65b0\u5149\u7eb8\u4e1a\u7684\u90a3\u4e2a\u5236\u6d46\u9879\u76ee\uff0c\u5f90\u7701\u957f\u5728\u7684\u65f6\u5019\uff0c\u597d\u50cf\u4ec0\u4e48\u8fdb\u5c55\u90fd\u6ca1\u6709\u5417\uff1f\u201d</p><p>\u3000\u3000\u9646\u6587\u592b\u8fd8\u5728\u8003\u8651\u8ddf\u674e\u8fdc\u6e56\u600e\u4e48\u544a\u522b\u624d\u597d\uff0c\u624b\u6307\u6263\u7740\u8f66\u95e8\u9501\uff0c\u542c\u674e\u8fdc\u6e56\u8fd9\u4e48\u4e00\u95ee\uff0c\u90fd\u6709\u4e9b\u53d1\u8499\uff0c\u5236\u6d46\u9879\u76ee\u554a\uff1f\u4ed6\u98de\u5feb\u7684\u5c06\u65b0\u5149\u7eb8\u4e1a\u5236\u6d46\u9879\u76ee\u7684\u76f8\u5173\u8d44\u6599\u5728\u8111\u5b50\u91cc\u8fc7\u4e86\u4e00\u904d\uff0c\u56de\u7b54\u674e\u8fdc\u6e56\u8bf4\uff1a\u201c\u8bb0\u5f97\u5f53\u65f6\u63d0\u51fa\u4e00\u4e2a\u521d\u6b65\u7684\u610f\u5411\u51fa\u6765\uff0c\u4e4b\u540e\uff0c\u5012\u662f\u8425\u6797\u9879\u76ee\u8fc5\u901f\u5f00\u5c55\u8d77\u6765\uff0c\u65b0\u5149\u7eb8\u4e1a\u76ee\u524d\u5df2\u7ecf\u6295\u8fdb\u53bb\u4e24\u4e2a\u4ebf\u7684\u8d44\u91d1\uff0c\u4eca\u5e74\u7684\u8425\u6797\u8ba1\u5212\u7ec6\u6848\u90fd\u5df2\u7ecf\u51fa\u6765\uff0c\u4e0e\u5730\u65b9\u8be5\u7b7e\u7684\u534f\u8bae\u4e5f\u65e9\u5c31\u7b7e\u4e86\uff0c\u81f3\u5c11\u8fd8\u8981\u6709\u56db\u4e2a\u4ebf\u7684\u8d44\u91d1\u6295\u8fdb\u53bb\uff0c\u4e4b\u540e\u5c31\u662f\u5c01\u6797\u80b2\u6797\uff1b\u5236\u6d46\u9879\u76ee\u5012\u662f\u6ca1\u89c1\u518d\u63d0\u8fc7\uff0c\u597d\u50cf\u8bf4\u6761\u4ef6\u8fd8\u4e0d\u591f\u6210\u719f\u2026\u2026\u201d</p><p>\u3000\u3000\u9646\u6587\u592b\u770b\u89c1\u674e\u8fdc\u6e56\u9501\u7740\u7709\u5934\u5728\u601d\u8003\u4ec0\u4e48\uff0c\u4e5f\u770b\u4e0d\u51fa\u4ed6\u6709\u8ddf\u81ea\u5df1\u518d\u8c08\u4e0b\u53bb\u7684\u610f\u601d\uff0c\u4fa7\u7740\u8eab\u5b50\u7b49\u4e86\u4e00\u4f1a\u513f\uff0c\u674e\u8fdc\u6e56\u624d\u8bf4\u9053\uff1a\u201c\u8fd9\u91cc\u5c31\u662f\u4f60\u4f4f\u7684\u5c0f\u533a\uff0c\u662f\u4e0d\u662f\u8ba9\u5c0f\u66f9\u5f00\u8f66\u9001\u4f60\u8fdb\u53bb\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e0d\u7528\u4e86\uff0c\u5c0f\u533a\u91cc\u9762\u8f66\u7ed5\u5f2f\u4e0d\u65b9\u4fbf\uff0c\u7ed5\u8def\u8fc7\u6765\u5df2\u7ecf\u803d\u6401\u674e\u7701\u957f\u60a8\u65f6\u95f4\u4e86\u2026\u2026\u201d\u9646\u6587\u592b\u4e0b\u4e86\u8f66\u76ee\u9001\u5c0f\u8f66\u79bb\u5f00\uff0c\u624d\u5f80\u5c0f\u533a\u91cc\u8d70\u53bb\uff0c\u6ee1\u5fc3\u7591\u7aa6\u3002</p><p>\u3000\u3000\u9646\u6587\u592b\u5bf9\u5236\u6d46\u9879\u76ee\u7684\u60c5\u51b5\u7684\u786e\u4e0d\u6e05\u695a\uff0c\u4ed6\u4e0e\u5f90\u5b66\u5e73\u3001\u5f20\u606a\u7684\u5173\u7cfb\uff0c\u8fdc\u4e0d\u5982\u674e\u4e49\u6c5f\u8ddf\u4ed6\u4eec\u6765\u5f97\u4eb2\u70ed\uff0c\u4eca\u5929\u4e0e\u8c22\u6c49\u9756\u3001\u82cf\u9038\u7fa4\u3001\u5468\u747e\u73ba\u7b49\u4eba\u5728\u4e00\u8d77\uff0c\u7ed9\u5f20\u606a\u649e\u89c1\uff0c\u5012\u4e0d\u6653\u5f97\u5f20\u606a\u5fc3\u91cc\u4f1a\u6709\u4ec0\u4e48\u60f3\u6cd5\uff0c\u8981\u662f\u674e\u7701\u957f\u6709\u4ec0\u4e48\u660e\u786e\u7684\u8868\u793a\uff0c\u4e5f\u7ba1\u4e0d\u4e86\u5f20\u606a\u90a3\u8fb9\u6709\u4ec0\u4e48\u60f3\u6cd5\u4e86\u3002</p><p>\u3000\u3000\u5728\u5939\u7f1d\u751f\u5b58\u603b\u662f\u8270\u8f9b\u82e6\u95f7\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u9646\u6587\u592b\u7b2c\u4e8c\u5929\u5230\u7701\u653f\u5e9c\uff0c\u53bb\u67e5\u9605\u65b0\u5149\u7eb8\u4e1a\u5236\u6d46\u9879\u76ee\u7684\u6750\u6599\uff0c\u88ab\u544a\u4e4b\u5b58\u6863\u7684\u6750\u6599\u7ed9\u674e\u7701\u957f\u501f\u53bb\u4e86\u3002</p><p>\u3000\u3000\u9646\u6587\u592b\u6628\u5929\u591c\u91cc\u7ffb\u6765\u8986\u53bb\u60f3\u4e86\u597d\u4e45\uff0c\u60f3\u5230\u4e00\u79cd\u53ef\u80fd\u6027\uff0c\u51cc\u6668\u624d\u8ff7\u8ff7\u7cca\u7cca\u7684\u7761\u7740\uff0c\u867d\u7136\u53ea\u7761\u4e86\u51e0\u4e2a\u5c0f\u65f6\uff0c\u7cbe\u795e\u5374\u5f88\u4ea2\u594b\uff0c\u8fd9\u65f6\u5019\uff0c\u60f3\u6cd5\u66f4\u52a0\u6e05\u6670\u4e86\u3002\u4ed6\u56de\u5230\u529e\u516c\u5ba4\uff0c\u62ff\u51fa\u4fdd\u5bc6\u7535\u8bdd\u672c\uff0c\u4e0a\u9762\u5f55\u6709\u5f20\u606a\u7684\u8054\u7cfb\u7535\u8bdd\uff0c\u53ea\u6015\u5f20\u606a\u672c\u4eba\u4e5f\u4e0d\u6e05\u695a\uff0c\u7701\u5e38\u59d4\u6210\u5458\u90fd\u6709\u4ed6\u7684\u8054\u7cfb\u65b9\u5f0f\u3002</p><p>\u3000\u3000\u6bd5\u7adf\u4e4b\u524d\u6ca1\u6709\u4e0e\u5f20\u606a\u76f4\u63a5\u8054\u7cfb\u8fc7\uff0c\u8fdc\u4e0d\u53ca\u674e\u4e49\u6c5f\u4e0e\u5f20\u606a\u90a3\u4e48\u4eb2\u70ed\uff0c\u674e\u4e49\u6c5f\u53c8\u8c03\u53bb\u6d77\u5dde\u4e0b\u9762\u7684\u57ce\u5357\u533a\u5f53\u533a\u957f\u4e86\uff0c\u9646\u6587\u592b\u5728\u8003\u8651\uff0c\u662f\u4e0d\u662f\u8ddf\u674e\u4e49\u6c5f\u5148\u901a\u4e00\u4e0b\u6c14\u6bd4\u8f83\u597d\u3002</p><p>\u3000\u3000\u8fd9\u4ef6\u4e8b\u544a\u4e4b\u5f20\u606a\uff0c\u90a3\u60f3\u5fc5\u6628\u665a\u53ef\u80fd\u6709\u7684\u8bef\u4f1a\u4e5f\u5c31\u4f1a\u51b0\u6d88\u96ea\u878d\u3002</p><p>\u3000\u3000\u674e\u4e49\u6c5f\u5e74\u524d\u5230\u6d77\u5dde\u8d74\u4efb\uff0c\u73b0\u5728\u8fd8\u5728\u719f\u6089\u5de5\u4f5c\u9636\u6bb5\uff0c\u5b8b\u57f9\u660e\u7559\u4e0b\u7684\u597d\u5e95\u5b50\uff0c\u533a\u59d4\u4e66\u8bb0\u6234\u6587\u8fdb\u4e0e\u533a\u91cc\u7684\u5176\u4ed6\u5b98\u5458\u5f88\u6e05\u695a\u4ed6\u4e0e\u5e02\u957f\u5510\u5b66\u8c26\u3001\u524d\u4efb\u5b8b\u57f9\u660e\u7b49\u4eba\u7684\u4eb2\u5bc6\u5173\u7cfb\uff0c\u5de5\u4f5c\u4e0a\u90fd\u5f88\u914d\u5408\uff0c\u5de5\u4f5c\u4e0a\u624b\u8d77\u6765\u5c31\u5feb\u3002</p><p>\u3000\u3000\u674e\u4e49\u6c5f\u59bb\u5b50\u4e0e\u5b69\u5b50\u90fd\u8fd8\u7559\u5728\u7701\u57ce\uff0c\u6625\u8282\u4ed6\u56de\u7701\u57ce\uff0c\u4e0e\u9646\u6587\u592b\u78b0\u8fc7\u4e00\u6b21\u9762\uff0c\u8d76\u5f00\u8f66\u5230\u533a\u653f\u5e9c\u5c31\u63a5\u5230\u9646\u6587\u592b\u7684\u7535\u8bdd\u8fd8\u6709\u4e9b\u610f\u5916\u3002</p><p>\u3000\u3000\u201c\u674e\u7701\u957f\u95ee\u8d77\u65b0\u5149\u7eb8\u4e1a\u7684\u5236\u6d46\u9879\u76ee\uff1f\u201d\u674e\u4e49\u6c5f\u63a5\u5230\u9646\u6587\u592b\u7684\u7535\u8bdd\uff0c\u7981\u4e0d\u4f4f\u5174\u594b\u8d77\u6765\uff0c\u4ed6\u4e4b\u524d\u6709\u5728\u62c5\u5fc3\u82cf\u9038\u7fa4\u5230\u6d77\u5dde\u4e4b\u540e\uff0c\u8981\u662f\u4e0e\u5468\u5bcc\u660e\u8054\u5408\u8d77\u6765\u94b3\u5236\u5510\u5b66\u8c26\uff0c\u5c31\u7b97\u5510\u5b66\u8c26\u987a\u5229\u5f53\u4e0a\u5e02\u59d4\u4e66\u8bb0\u4e5f\u5f88\u96be\u53d7\uff0c\u4e0d\u8981\u8bf4\u4ed6\u4eec\u8fd9\u4e9b\u5728\u4e0b\u9762\u7684\u55bd\u55bd\u4e86\uff0c\u201c\u597d\uff0c\u6211\u9a6c\u4e0a\u7ed9\u5f20\u606a\u6253\u7535\u8bdd\uff0c\u544a\u8bc9\u4ed6\u8fd9\u4ef6\u4e8b\u3002\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u63a5\u5230\u674e\u4e49\u6c5f\u7684\u7535\u8bdd\uff0c\u4ed6\u4e0e\u8521\u7eef\u5a1f\u3001\u5468\u6e38\u6b63\u5728\u4e0b\u5c5e\u7684\u6797\u4e1a\u516c\u53f8\u542c\u5206\u7ba1\u8425\u6797\u9879\u76ee\u7684\u526f\u603b\u5b8b\u5fd7\u4f51\u6c47\u62a5\u6625\u590f\u5b63\u7684\u8425\u6797\u89c4\u5212\u3002</p><p>\u3000\u3000\u674e\u4e49\u6c5f\u7684\u58f0\u97f3\u5f88\u5174\u594b\uff1a\u201c\u674e\u7701\u957f\u5230\u5e95\u662f\u660e\u767d\u8c01\u5bf9\u4ed6\u624d\u6700\u6709\u7528\u3002\u4ed6\u521a\u5750\u4e0a\u7701\u957f\u7684\u4f4d\u5b50\uff0c\u6700\u7d27\u8981\u7684\u4e0d\u662f\u8ddf\u8c01\u52fe\u5fc3\u6597\u89d2\uff0c\u4e5f\u4e0d\u662f\u8c01\u5df4\u7ed3\u5949\u627f\u51e0\u53e5\u5c31\u80fd\u8ff7\u60d1\u5f97\u4e86\u4ed6\u8fd9\u53f7\u4eba\u7269\u7684\uff0c\u8c01\u80fd\u5e2e\u4ed6\u5728\u5de5\u4f5c\u4e0a\uff0c\u51c6\u786e\u7684\u8bf4\uff0c\u662f\u5728\u5168\u7701\u7ecf\u6d4e\u5de5\u4f5c\u4e0a\u5c3d\u5feb\u80fd\u6253\u5f00\u65b0\u5c40\u9762\uff0c\u8c01\u624d\u662f\u5bf9\u4ed6\u6709\u7528\u7684\u4eba\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u5927\u6e05\u65e9\u8fc7\u6765\u542c\u5b8b\u5fd7\u4f51\u6c47\u62a5\u4e86\u4e00\u5927\u5806\u6570\u636e\uff0c\u542c\u674e\u4e49\u6c5f\u5728\u7535\u8bdd\u6fc0\u52a8\u7684\u8bf4\u4e86\u4e00\u901a\uff0c\u4e00\u65f6\u8fd8\u6ca1\u6709\u7406\u6e05\u4ed6\u7684\u60f3\u6cd5\uff0c\u5c31\u201c\u55ef\u201d\u3001\u201c\u55ef\u201d\u7684\u8ba9\u674e\u4e49\u6c5f\u90fd\u8bf4\u51fa\u6765\u3002</p><p>\u3000\u3000\u201c\u674e\u7701\u957f\u8fd9\u51e0\u5929\u5728\u7814\u7a76\u65b0\u5149\u7eb8\u4e1a\u5236\u6d46\u9879\u76ee\u7684\u8d44\u6599\uff0c\u6628\u5929\u62c9\u9646\u79d8\u4e66\u957f\u53bb\u5efa\u90ba\u9152\u5e97\uff0c\u4e5f\u53ea\u662f\u60f3\u4ece\u9646\u79d8\u4e66\u957f\u90a3\u91cc\u4e86\u89e3\u66f4\u591a\u5173\u4e8e\u5236\u6d46\u9879\u76ee\u7684\u60c5\u51b5\uff0c\u201d\u674e\u4e49\u6c5f\u4e5f\u4e0d\u5fd8\u5e2e\u9646\u6587\u592b\u5728\u5f20\u606a\u9762\u524d\u89e3\u91ca\u4e00\u4e0b\uff0c\u201c\u5f90\u7701\u957f\u79bb\u5f00\u524d\uff0c\u7ed9\u7701\u91cc\u641e\u5b9a\u8054\u5408\u94a2\u94c1\u7684\u90a3\u4e2a\u5927\u9879\u76ee\uff0c\u4ed6\u603b\u4e0d\u597d\u610f\u601d\u5f52\u529f\u5230\u81ea\u5df1\u7684\u540d\u4e0b\uff0c\u8054\u5408\u94a2\u94c1\u90a3\u4e2a\u9879\u76ee\u505a\u4e0b\u6765\uff0c\u5168\u7701\u7ecf\u6d4e\u81f3\u5c11\u80fd\u62c9\u5230\u4e94\u516d\u4e2a\u70b9\uff1b\u5236\u6d46\u9879\u76ee\u89c4\u6a21\u5c0f\u4e00\u4e9b\uff0c\u4f46\u4e5f\u4e0d\u5dee\u591a\u5c11\uff0c\u5173\u952e\u5236\u6d46\u9879\u76ee\u4e4b\u524d\u8fd8\u53ea\u662f\u6709\u521d\u6b65\u7684\u610f\u5411\u800c\u5df2\uff0c\u4ec0\u4e48\u51c6\u5907\u5de5\u4f5c\u90fd\u6ca1\u6709\u505a\uff0c\u674e\u7701\u957f\u5c31\u662f\u770b\u91cd\u8fd9\u70b9\uff0c\u8fd9\u4e2a\u9879\u76ee\u8981\u662f\u505a\u6210\u4e86\uff0c\u4ed6\u603b\u662f\u53ef\u4ee5\u62ff\u51fa\u53bb\u70ab\u8000\u7684\uff0c\u5b9e\u5728\u4e0d\u884c\uff0c\u9526\u6e56\u53ef\u4ee5\u518d\u52a0\u5927\u89c4\u6a21\uff0c\u62c9\u52a8\u7701\u5185\u56fd\u6c11\u751f\u4ea7\u603b\u503c\u5411\u516d\u4e2a\u70b9\u770b\u9f50\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u5f20\u606a\u603b\u7b97\u542c\u660e\u767d\u4e86\uff0c\u674e\u8fdc\u6e56\u505a\u4ec0\u4e48\u4e8b\u60c5\u9996\u5148\u8003\u8651\u7684\u8fd8\u662f\u4ed6\u4e2a\u4eba\u7684\u653f\u6cbb\u62b1\u8d1f\uff0c\u521a\u4ee3\u7406\u4e0a\u7701\u957f\u5c31\u60f3\u8981\u6293\u51e0\u4e2a\u5927\u9879\u76ee\uff0c\u65b0\u5149\u7eb8\u4e1a\u65e9\u5c31\u63d0\u51fa\u6765\u7684\u5236\u6d46\u9879\u76ee\u4e5f\u5728\u4ed6\u7684\u89c6\u91ce\u4e4b\u5185\uff0c\u8fd9\u624d\u901a\u8fc7\u9646\u6587\u592b\u3001\u674e\u4e49\u6c5f\u66f2\u6298\u7684\u5411\u8fd9\u8fb9\u4f20\u8fbe\u8fd9\u5c42\u610f\u601d\uff0c\u7b97\u662f\u5bf9\u81ea\u5df1\u7684\u62db\u5b89\u5427\u3002\u542c\u5230\u674e\u4e49\u6c5f\u5728\u7535\u8bdd\u91cc\u7684\u58f0\u97f3\u8fd9\u4e48\u5174\u594b\uff0c\u5f20\u606a\u5012\u662f\u80fd\u7406\u89e3\uff0c\u7709\u5934\u5374\u8e59\u4e86\u8d77\u6765\uff0c\u5012\u4e0d\u662f\u60f3\u6cfc\u674e\u4e49\u6c5f\u7684\u51b7\u6c34\uff0c\u5374\u4e0d\u5f97\u4e0d\u63d0\u9192\u4ed6\uff0c\u201c\u5f53\u521d\u6211\u4eec\u629b\u51fa\u4e94\u5341\u4ebf\u5236\u6d46\u9879\u76ee\u610f\u5411\uff0c\u53ea\u662f\u4e00\u4e2a\u5927\u5e4c\u5b50\uff0c\u610f\u5728\u9f13\u52b1\u5c0f\u6c5f\u4e2d\u4e0a\u6e38\u7684\u5730\u65b9\u653f\u5e9c\u914d\u5408\u6211\u4eec\u5b9e\u65bd\u8425\u6797\u9879\u76ee\uff0c\u5b9e\u9645\u4e0a\u6211\u4eec\u5e76\u6ca1\u6709\u4ec0\u4e48\u4e0a\u5236\u6d46\u9879\u76ee\u7684\u8ba1\u5212\uff0c\u5373\u4f7f\u6709\uff0c\u4e5f\u4e0d\u662f\u8fd9\u4e24\u5e74\u2026\u2026\u201d</p></article>"}}}]);
