(window.webpackJsonp=window.webpackJsonp||[]).push([[522],{544:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e94\u5341\u56db\u8282 \u5fc3\u601d",content:"<article><h2>\u7b2c\u4e94\u5341\u56db\u8282 \u5fc3\u601d</h2><p>\u591c\u6e10\u6e10\u6df1\u4e86\uff0c\u5468\u906d\u5bc2\u9759\u65e0\u58f0\uff0c\u8fdc\u5904\u9547\u5b50\u91cc\u7684\u706f\u706b\u4e0d\u77e5\u4e0d\u89c9\u5df2\u7ecf\u5927\u591a\u7184\u706d\u3002\u4e00\u9635\u5bd2\u51b7\u7684\u591c\u98ce\u5439\u6765\uff0c\u8ba9\u6885\u83f2\u65af\u4e0b\u610f\u8bc6\u5730\u675f\u7d27\u6597\u7bf7\u7684\u9886\u53e3\u3002\u201c\u56de\u53bb\u5427\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5f88\u665a\u4e86\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u4ed6\u4eec\u6b63\u51c6\u5907\u5f80\u56de\u8d70\uff0c\u9aa4\u7136\u95f4\u542c\u5f97\u5929\u7a7a\u4e2d\u96f7\u9e23\u6eda\u6eda\uff0c\u9707\u8033\u6b32\u804b\uff0c\u62ac\u5934\u671b\u53bb\uff0c\u53ea\u89c1\u65e0\u6570\u9897\u8d64\u8272\u6d41\u661f\u81ea\u6781\u9ad8\u5929\u5b87\u4e2d\u63a5\u8fde\u4e0d\u65ad\u5730\u7eb7\u7eb7\u5760\u843d\u5982\u96e8\uff0c\u5212\u7834\u591c\u7a79\uff0c\u6700\u540e\u707f\u70c2\u5730\u71c3\u70e7\u6b86\u5c3d\u3002\u8fd9\u5e55\u573a\u666f\u65e2\u58ee\u89c2\u53c8\u70ab\u4e3d\uff0c\u7136\u800c\u743c\u6069\u548c\u6885\u83f2\u65af\u5f7c\u6b64\u5bf9\u89c6\uff0c\u4e0d\u77e5\u4f55\u6545\u5fc3\u5934\u90fd\u540c\u65f6\u5347\u8d77\u4e00\u9635\u5947\u602a\u7684\u9884\u611f\u3002</p><p>\u201c\u8d70\u5427\u3002\u201d\u6c89\u9ed8\u4e86\u7247\u523b\uff0c\u743c\u6069\u8bf4\u3002</p><p>\u4e24\u4eba\u7275\u7740\u624b\uff0c\u5e76\u80a9\u800c\u884c\u3002\u8d70\u4e86\u4e00\u4f1a\uff0c\u524d\u65b9\u5df2\u7ecf\u80fd\u591f\u9690\u7ea6\u770b\u89c1\u623f\u5b50\u3002\u201c\u743c\u6069\uff0c\u201d\u6885\u83f2\u65af\u7a81\u7136\u53eb\u4ed6\u7684\u540d\u5b57\uff0c\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u544a\u8bc9\u6211\u8fd9\u4e9b\u5462\uff1f\u201d</p><p>\u201c\u56e0\u4e3a\u4f60\u95ee\u5230\u4e86\u554a\u3002\u201d</p><p>\u201c\u4f46\u4f60\u5e76\u4e0d\u662f\u4e00\u5b9a\u8981\u544a\u8bc9\u6211\u6240\u6709\u7684\u4e8b\u60c5\u5427\uff0c\u201d\u5c11\u5973\u8bf4\uff0c\u201c\u4e8b\u5b9e\u4e0a\uff0c\u4f60\u5b8c\u5168\u53ef\u4ee5\u53ea\u8bf4\u4e00\u90e8\u5206\u3002\u201d</p><p>\u743c\u6069\u7b11\u4e86\u7b11\u3002</p><p>\u4ed6\u4e4b\u6240\u4ee5\u4f1a\u9009\u62e9\u8fd9\u4e2a\u65f6\u5019\uff0c\u5411\u6885\u83f2\u65af\u5766\u767d\u57cb\u85cf\u5728\u5fc3\u5e95\u7684\u79d8\u5bc6\uff0c\u800c\u4e14\u662f\u548c\u76d8\u6258\u51fa\uff0c\u6beb\u65e0\u9690\u7792\uff0c\u4e00\u65b9\u9762\u81ea\u7136\u662f\u673a\u7f18\u4f7f\u7136\uff0c\u987a\u52bf\u4e3a\u4e4b\uff0c\u4f46\u53e6\u4e00\u65b9\u9762\uff0c\u5374\u4e5f\u5e76\u4e0d\u662f\u4e00\u65f6\u51b2\u52a8\uff0c\u5fc3\u8840\u6765\u6f6e\uff0c\u800c\u662f\u7ecf\u8fc7\u614e\u91cd\u8003\u8651\u7684\u3002</p><p>\u8fd9\u4e9b\u4e8b\u60c5\u7ee7\u7eed\u9690\u7792\u4e0b\u53bb\uff0c\u610f\u4e49\u5176\u5b9e\u5df2\u7ecf\u4e0d\u5927\u3002\u6885\u83f2\u65af\u806a\u660e\u7edd\u9876\uff0c\u89c1\u95fb\u5e7f\u535a\uff0c\u53c8\u548c\u743c\u6069\u671d\u5915\u76f8\u5904\uff0c\u5979\u8fd9\u6837\u7684\u4eba\u7269\uff0c\u5982\u679c\u8bf4\u771f\u6ca1\u770b\u51fa\u4ec0\u4e48\u7aef\u502a\uff0c\u731c\u51fa\u4ec0\u4e48\u95e8\u9053\uff0c\u90a3\u662f\u7edd\u5bf9\u4e0d\u53ef\u80fd\u7684\u4e8b\u60c5\u3002\u8bda\u7136\uff0c\u743c\u6069\u6709\u5f88\u591a\u4e8b\u60c5\u6ca1\u6709\u544a\u8bc9\u8fc7\u5979\uff0c\u4f46\u5e76\u4e0d\u7b49\u4e8e\u5728\u5e73\u65f6\u7684\u8a00\u8c08\u4e3e\u6b62\u4e2d\u4e0d\u4f1a\u9732\u4ec0\u4e48\u53e3\u98ce\uff0c\u53cc\u65b9\u5173\u7cfb\u592a\u8fc7\u5bc6\u5207\uff0c\u5076\u7136\u8bf4\u6f0f\u5634\u662f\u5f88\u5bfb\u5e38\u7684\u4e8b\u60c5\uff1b\u743c\u6069\u7ecf\u5386\u4e00\u4e9b\u4e8b\u60c5\u65f6\uff0c\u5979\u5e76\u4e0d\u5728\u573a\uff0c\u4f46\u5e76\u4e0d\u7b49\u4e8e\u4e0d\u4f1a\u4ece\u5176\u4ed6\u6e20\u9053\u83b7\u77e5\u3002\u4f8b\u5982\u5728\u5e7d\u6697\u5730\u57df\u7684\u707e\u5384\u4e4b\u5730\u4e2d\uff0c\u743c\u6069\u9047\u89c1\u90a3\u540d\u51e4\u51f0\u7684\u4e8b\u60c5\uff0c\u5982\u679c\u6709\u4eba\u719f\u6089\u4f0a\u739b\u65af\u5361\u5386\u53f2\uff0c\u5f88\u5bb9\u6613\u5c31\u80fd\u4ece\u4e2d\u53d1\u73b0\u95ee\u9898\uff0c\u5f53\u65f6\u548c\u743c\u6069\u540c\u884c\u7684\u4eba\u6709\u51db\u3001\u838e\u73de\u514b\u548c\u8299\u8389\u5a05\u3002\u522b\u4eba\u5c31\u7f62\u4e86\uff0c\u51db\u96be\u9053\u8fd8\u4f1a\u5bf9\u6885\u83f2\u65af\u4fdd\u5bc6\u4e48\uff0c\u80af\u5b9a\u4f1a\u5f53\u505a\u8da3\u95fb\u8bb2\u7ed9\u597d\u53cb\u542c\u5427\u3002\u6885\u83f2\u65af\u662f\u5723\u6b66\u58eb\uff0c\u4e0d\u662f\u5386\u53f2\u5b66\u5bb6\uff0c\u5bf9\u4f0a\u739b\u65af\u5361\u7684\u5386\u53f2\u672a\u5fc5\u591a\u4e48\u719f\u6089\u2014\u2014\u7136\u800c\u522b\u5fd8\u4e86\uff0c\u5979\u8eab\u8fb9\u5c31\u6709\u4e24\u4f4d\u4f0a\u739b\u65af\u5361\u5386\u53f2\u65b9\u9762\u7684\u4e13\u5bb6\u3002\u5c24\u5176\u662f\u90a3\u4e2a\u5deb\u5996\u957f\u8001\uff0c\u660e\u663e\u5df2\u7ecf\u77e5\u9053\u4e86\u5f88\u591a\u4e8b\u60c5\uff0c\u4ee5\u4ed6\u548c\u6885\u83f2\u65af\u7684\u5173\u7cfb\uff0c\u96be\u9053\u53c8\u4f1a\u66ff\u743c\u6069\u9690\u7792\u4e48\u3002</p><p>\u6885\u83f2\u65af\u660e\u660e\u5df2\u7ecf\u770b\u51fa\u7709\u76ee\uff0c\u5374\u4e00\u76f4\u4e0d\u95ee\u4e0d\u63d0\uff0c\u4e00\u65b9\u9762\u662f\u5979\u6027\u683c\u4f7f\u7136\uff0c\u53e6\u4e00\u65b9\u9762\u4e5f\u672a\u5c1d\u4e0d\u662f\u6709\u7b49\u743c\u6069\u4e3b\u52a8\u4ea4\u4ee3\u7684\u610f\u601d\u3002\u800c\u4e14\u8bf4\u5230\u5e95\uff0c\u8fd9\u5c5e\u4e8e\u743c\u6069\u7684\u79c1\u4e8b\uff0c\u548c\u6885\u83f2\u65af\u539f\u672c\u5e76\u6ca1\u6709\u4ec0\u4e48\u5173\u7cfb\u3002\u6240\u4ee5\u5728\u6b63\u5e38\u60c5\u51b5\u4e0b\uff0c\u8fd9\u79cd\u5fc3\u7167\u4e0d\u5ba3\u7684\u5c40\u9762\u8fd8\u662f\u80fd\u591f\u7ee7\u7eed\u7ef4\u6301\u4e0b\u53bb\u7684\uff0c\u4f46\u51ef\u745f\u7433\u7684\u51fa\u73b0\uff0c\u5374\u8ba9\u5b83\u53d8\u5f97\u4e0d\u53ef\u80fd\u4e86\u3002\u6b63\u5982\u6885\u83f2\u65af\u81ea\u5df1\u6240\u8bf4\uff0c\u51ef\u745f\u7433\u7684\u51fa\u73b0\uff0c\u8ba9\u5979\u6e05\u695a\u611f\u53d7\u5230\u4e86\u5a01\u80c1\uff0c\u5979\u4e0d\u53ef\u80fd\u518d\u4fdd\u6301\u6c89\u9ed8\uff0c\u7ee7\u7eed\u6d88\u6781\u7b49\u5f85\u3002\u800c\u743c\u6069\u8981\u89e3\u91ca\u6e05\u695a\u51ef\u745f\u7433\u7684\u4e8b\u60c5\uff0c\u5c31\u65e0\u53ef\u907f\u514d\u5730\u8981\u7275\u626f\u5230\u81ea\u5df1\uff0c\u7275\u626f\u5230\u5730\u7403\uff0c\u7275\u626f\u5230\u4f0a\u739b\u65af\u5361\uff0c\u7b49\u7b49\u7b49\u7b49\u3002\u65e2\u7136\u5982\u6b64\uff0c\u8fd8\u4e0d\u5982\u7d22\u6027\u548c\u76d8\u6258\u51fa\uff0c\u5f7b\u5e95\u5766\u767d\uff0c\u4e89\u53d6\u4e3b\u52a8\u3002</p><p>\u800c\u4e14\u8fd9\u6837\u4e00\u6765\uff0c\u81ea\u5df1\u7b97\u662f\u5378\u4e0b\u4e86\u5fc3\u5934\u4e00\u4e2a\u6c89\u91cd\u5305\u88b1\uff0c\u4ece\u6b64\u4e0d\u5fc5\u518d\u5c0f\u5fc3\u7ffc\u7ffc\uff0c\u751f\u6015\u8bf4\u6f0f\u4ec0\u4e48\uff0c\u4ee5\u540e\u9047\u5230\u4e8b\u60c5\uff0c\u4e5f\u4e0d\u7528\u4e00\u4e2a\u4eba\u72ec\u81ea\u7422\u78e8\uff0c\u800c\u662f\u6709\u4e2a\u4eba\u53ef\u4ee5\u5171\u540c\u53c2\u8be6\uff0c\u8ba8\u8bba\u7814\u7a76\u2014\u2014\u6885\u83f2\u65af\u7684\u806a\u660e\u624d\u667a\uff0c\u505a\u53c2\u8c0b\u7ef0\u7ef0\u6709\u4f59\u3002\u65e0\u8bba\u4ece\u54ea\u4e2a\u65b9\u9762\u8bf4\uff0c\u90fd\u662f\u660e\u667a\u4e4b\u4e3e\u5427\u3002</p><p>\u5f53\u7136\uff0c\u743c\u6069\u5e76\u6ca1\u6709\u5bf9\u6885\u83f2\u65af\u8be6\u7ec6\u89e3\u91ca\u8fd9\u4e9b\uff0c\u4ed6\u77e5\u9053\u6885\u83f2\u65af\u60f3\u542c\u7684\u7b54\u6848\u4e5f\u538b\u6839\u4e0d\u662f\u8fd9\u4e9b\u3002\u201c\u6211\u5df2\u7ecf\u4e0d\u60f3\u518d\u5411\u4f60\u6709\u6240\u9690\u7792\uff0c\u201d\u4ed6\u7b80\u5355\u5730\u8bf4\uff0c\u201c\u6211\u505a\u4e0d\u5230\u3002\u201d</p><p>\u201c\u90a3\u73ca\u5609\u59d0\u59d0\u5462\uff1f\u201d\u6885\u83f2\u65af\u88c5\u4f5c\u82e5\u65e0\u5176\u4e8b\u7684\u6837\u5b50\u95ee\uff0c\u201c\u96be\u9053\u4f60\u5c31\u80fd\u5bf9\u5979\u9690\u7792\u5417\uff1f\u201d</p><p>\u201c\u73ca\u5609\u2026\u2026\u201d\u743c\u6069\u53f9\u4e86\u53e3\u6c14\uff0c\u201c\u6709\u4e9b\u4e8b\u60c5\uff0c\u73ca\u5609\u6682\u65f6\u4e0d\u9002\u5408\u77e5\u9053\u3002\u201d</p><p>\u5c11\u5973\u770b\u7740\u4ed6\uff0c\u7136\u540e\u5fae\u5fae\u4e00\u7b11\uff0c\u6ca1\u6709\u518d\u8bf4\u8bdd\u3002</p><p>\u56de\u5230\u4f4f\u5904\uff0c\u5df2\u7ecf\u662f\u5341\u70b9\u591a\u949f\u3002\u743c\u6069\u53d1\u73b0\u51db\u7684\u623f\u95f4\u5df2\u7ecf\u7184\u706f\uff0c\u91cc\u9762\u9759\u6084\u6084\u7684\uff0c\u5c0f\u5973\u5deb\u548c\u5979\u7684\u9189\u9152\u8001\u5e08\u5e94\u8be5\u90fd\u7761\u4e0b\u4e86\uff1b\u73ca\u5609\u7684\u623f\u95f4\u5374\u8fd8\u4eae\u7740\u706f\uff0c\u9690\u9690\u7ef0\u7ef0\u80fd\u770b\u89c1\u5979\u7684\u79c0\u4e3d\u8eab\u5f71\u6620\u5728\u7a97\u5e18\u4e0a\uff0c\u6b63\u5728\u4e13\u6ce8\u5730\u8bfb\u4e66\u3002\u201c\u73ca\u5609\u59d0\u59d0\u975e\u5e38\u52aa\u529b\u5462\u3002\u201d\u6885\u83f2\u65af\u8bf4\u3002</p><p>\u201c\u662f\u554a\u3002\u201d</p><p>\u201c\u6bd4\u4f60\u52aa\u529b\u591a\u4e86\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u743c\u6069\u6709\u4e9b\u8138\u7ea2\uff0c\u5e78\u597d\u6885\u83f2\u65af\u8f7b\u8f7b\u6572\u6253\u4e00\u53e5\u4fbf\u70b9\u5230\u4e3a\u6b62\u3002\u201c\u4eca\u5929\u665a\u4e0a\u5979\u65bd\u5c55\u51fa\u6765\u7684\u90a3\u9053\u6cd5\u672f\uff0c\u5c31\u662f\u661f\u9668\u672f\u5417\uff1f\u201d</p><p>\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6548\u679c\u662f\u4e00\u6a21\u4e00\u6837\uff0c\u4f46\u592a\u5f31\u4e86\u3002\u201d</p><p>\u201c\u5f88\u5f31\u5417\uff1f\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u8fd8\u597d\u5427\u3002\u201d</p><p>\u201c\u90a3\u662f\u4f60\u6ca1\u770b\u89c1\u5979\u4e0a\u6b21\u65bd\u5c55\u51fa\u6765\u7684\u5a01\u529b\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4e00\u51fb\u5c31\u706d\u6740\u4e86\u6d77\u795e\u3002\u76f8\u6bd4\u8d77\u6765\uff0c\u8fd9\u6b21\u5b9e\u5728\u662f\u5dee\u5f97\u592a\u8fdc\u592a\u8fdc\u4e86\uff0c\u5f31\u5f97\u4e0d\u6b63\u5e38\u3002\u201d</p><p>\u201c\u6211\u5012\u89c9\u5f97\uff0c\u4f60\u6240\u8bf4\u7684\u4e0a\u6b21\u624d\u662f\u4e0d\u6b63\u5e38\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u5b89\u535a\u91cc\u6bd5\u7adf\u662f\u4f4d\u795e\u660e\uff0c\u800c\u4e14\u662f\u4ee5\u751f\u547d\u529b\u5f3a\u97e7\u800c\u8457\u79f0\u7684\u795e\u660e\uff0c\u4e00\u51fb\u5c31\u5c06\u5979\u5f7b\u5e95\u6740\u6b7b\uff0c\u6b63\u5e38\u60c5\u51b5\u4e0b\uff0c\u518d\u5f3a\u5927\u7684\u6cd5\u672f\u4e5f\u505a\u4e0d\u5230\u5427\u3002\u201d</p><p>\u201c\u4f60\u8fd9\u4e48\u8bf4\u2026\u2026\u4e5f\u7b97\u6709\u9053\u7406\u5427\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4e0d\u8fc7\u8fd8\u662f\u89c9\u5f97\u59d0\u59d0\u4eca\u665a\u8fd9\u9053\u6cd5\u672f\u7684\u5a01\u529b\u592a\u5f31\u4e86\u3002\u201d</p><p>\u201c\u662f\u5417\uff1f\u5982\u679c\u4f60\u548c\u6b23\u5e03\u965b\u4e0b\u6613\u5730\u800c\u5904\uff0c\u4f60\u80fd\u63a5\u5f97\u4e0b\u6765\u5417\uff1f\u201d</p><p>\u201c\u81ea\u7136\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u8f7b\u800c\u6613\u4e3e\u3002\u201d</p><p>\u201c\u90a3\u5982\u679c\u662f\u4ee5\u4f60\u5728\u53bb\u4e0b\u5c42\u754c\u4e4b\u524d\u7684\u6c34\u51c6\u5462\uff1f\u201d\u6885\u83f2\u65af\u63a5\u7740\u95ee\uff0c\u201c\u8fd8\u80fd\u63a5\u4e0b\u5417\uff1f\u201d</p><p>\u743c\u6069\u5728\u4e0b\u5c42\u754c\u4e2d\u53c2\u52a0\u8840\u6218\uff0c\u867d\u7136\u5927\u591a\u6570\u65f6\u5019\u90fd\u5728\u540e\u65b9\uff0c\u4f46\u4e5f\u786e\u5b9e\u5386\u7ecf\u4e86\u65e0\u6570\u6b21\u640f\u6740\u62fc\u6597\uff0c\u5b9e\u529b\u63d0\u5347\u975e\u5e38\u660e\u663e\uff0c\u662f\u6545\u6885\u83f2\u65af\u6709\u6b64\u4e00\u95ee\u3002</p><p>\u201c\u5e94\u8be5\u4e5f\u6ca1\u95ee\u9898\u5427\uff0c\u201d\u743c\u6069\u6c89\u541f\u7740\uff0c\u8bed\u6c14\u4e0d\u662f\u90a3\u4e48\u786e\u5b9a\u4e86\uff0c\u201c\u6216\u8bb8\u4f1a\u53d7\u70b9\u8f7b\u4f24\u3002\u201d</p><p>\u201c\u90a3\u4e48\uff0c\u5982\u679c\u662f\u4f60\u521a\u9047\u5230\u6211\u65f6\u7684\u6c34\u51c6\u5462\uff1f\u201d\u6885\u83f2\u65af\u53c8\u95ee\u3002</p><p>\u201c\u90a3\u5c31\u6bd4\u8f83\u5371\u9669\u4e86\uff0c\u201d\u743c\u6069\u9ed8\u81ea\u8ba1\u7b97\u7247\u523b\uff0c\u201c\u80dc\u8d1f\u5728\u4e94\u4e94\u4e4b\u95f4\u5427\u3002\u201d</p><p>\u6885\u83f2\u65af\u8f7b\u8f7b\u70b9\u4e86\u70b9\u5934\uff0c\u201c\u8fd9\u4e48\u8bf4\uff0c\u73ca\u5609\u59d0\u59d0\u771f\u7684\u5f88\u4e86\u4e0d\u8d77\u5462\u3002\u201d\u5979\u8bf4\u3002</p><p>\u201c\u55ef\uff1f\u201d\u743c\u6069\u4e0d\u89e3\uff0c\u201c\u4f55\u4ee5\u89c1\u5f97\uff1f\u201d</p><p>\u201c\u5f53\u7136\u554a\uff0c\u4f60\u9047\u5230\u6211\u7684\u65f6\u5019\uff0c\u5df2\u7ecf\u662f\u9b54\u6cd5\u5b66\u9662\u6bd5\u4e1a\u7684\u6b63\u89c4\u5deb\u5e08\u4e86\u5427\uff0c\u73ca\u5609\u59d0\u59d0\u624d\u5b66\u4e86\u51e0\u5929\u5462\uff1f\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u77ed\u77ed\u51e0\u5929\u7684\u6210\u5c31\uff0c\u5c31\u8db3\u4ee5\u548c\u4f60\u4e94\u5e74\u7684\u82e6\u8bfb\u76f8\u5f53\u2014\u2014\u8fd9\u96be\u9053\u8fd8\u4e0d\u591f\u5f3a\u5417\uff1f\u201d</p><p>\u201c\u2026\u2026\u786e\u5b9e\u3002\u201d</p><p>\u88ab\u6885\u83f2\u65af\u4e00\u63d0\u9192\uff0c\u743c\u6069\u624d\u604d\u7136\u53d1\u89c9\uff0c\u73ca\u5609\u786e\u5b9e\u662f\u5df2\u7ecf\u6709\u7740\u60ca\u4eba\u7684\u8fdb\u6b65\uff0c\u800c\u81ea\u5df1\u5c45\u7136\u7ed9\u5ffd\u7565\u6389\u4e86\u3002\u9192\u609f\u6b64\u70b9\uff0c\u4ed6\u4e0d\u7531\u5f97\u65e2\u662f\u9ad8\u5174\uff0c\u53c8\u662f\u5fe7\u8651\uff0c\u5fc3\u60c5\u590d\u6742\uff0c\u4e00\u65f6\u95f4\u90fd\u4e0d\u77e5\u9053\u8be5\u8bf4\u4ec0\u4e48\u3002\u4ed6\u4e00\u76f4\u5e0c\u671b\u73ca\u5609\u80fd\u591f\u53d8\u5f97\u5f3a\u5927\u8d77\u6765\uff0c\u4e24\u4eba\u80fd\u591f\u66f4\u591a\u5730\u76f8\u4e92\u966a\u4f34\uff0c\u4e0d\u518d\u5206\u522b\uff1b\u4f46\u5fc3\u5e95\u9690\u85cf\u7684\u5fe7\u8651\uff0c\u5374\u8ba9\u4ed6\u89c9\u5f97\uff1a\u59d0\u59d0\u6bcf\u53d8\u5f3a\u4e00\u5206\uff0c\u5c31\u79bb\u81ea\u5df1\u8fdc\u4e86\u4e00\u6b65\u3002</p><p>\u6885\u83f2\u65af\u4f38\u51fa\u624b\uff0c\u8f7b\u8f7b\u63e1\u7740\u4ed6\u7684\u638c\u5fc3\uff0c\u201c\u522b\u60f3\u592a\u591a\u4e86\uff0c\u201d\u5979\u67d4\u58f0\u8bf4\uff0c\u201c\u65e9\u70b9\u966a\u6211\u4f11\u606f\u5427\u3002\u201d</p><p>\u201c\u55ef\uff0c\u201d\u743c\u6069\u6682\u65f6\u629b\u5f00\u6742\u5ff5\uff0c\u4e0d\u53bb\u518d\u60f3\uff0c\u201c\u4e0d\u8fc7\u6211\u80f3\u818a\u4e0d\u65b9\u4fbf\u5462\uff0c\u201d\u4ed6\u534a\u5f00\u73a9\u7b11\u5730\u8bf4\uff0c\u201c\u6240\u4ee5\u4eca\u665a\u4f60\u8981\u4e3b\u52a8\u70b9\u4e86\u3002\u201d</p><p>\u201c\u8ba8\u538c\uff01\u201d</p><p>\u5c11\u5973\u54ac\u7740\u5634\u5507\uff0c\u901a\u7ea2\u7684\u4fcf\u8138\u4e0a\u6ee1\u662f\u7f9e\u8272\uff0c\u8bed\u6c14\u5374\u662f\u8f6f\u7ef5\u7ef5\u7684\uff0c\u9690\u7136\u5df2\u7ecf\u9ed8\u8bb8\u3002\u53ea\u8981\u80fd\u8ba9\u4f60\u5f00\u5fc3\uff0c\u90a3\u4e48\u6211\u5c31\u4ec0\u4e48\u90fd\u613f\u610f\u505a\uff0c\u5979\u5728\u5fc3\u91cc\u8f7b\u58f0\u8bf4\uff0c\u56e0\u4e3a\uff0c\u6211\u559c\u6b22\u770b\u89c1\u4f60\u7b11\u7684\u6837\u5b50\u3002</p><p>\u203b\u203b\u203b</p><p>\u6fc0\u60c5\u6b22\u597d\u4e4b\u540e\uff0c\u5c11\u5973\u504e\u5728\u7537\u53cb\u6000\u4e2d\u6c89\u6c89\u7761\u53bb\uff0c\u6781\u5ea6\u7684\u75b2\u5026\u8ba9\u5979\u5f88\u5feb\u4fbf\u8fdb\u5165\u68a6\u4e61\u3002\u743c\u6069\u4e00\u65f6\u95f4\u5374\u65e0\u751a\u7761\u610f\uff0c\u6216\u8bb8\u56e0\u4e3a\u5b8c\u5168\u7531\u5973\u65b9\u4e3b\u52a8\u7684\u7f18\u6545\uff0c\u5728\u5c11\u5973\u706b\u70ed\u4f53\u8154\u4e2d\u7684\u51e0\u6b21\u53d1\u6cc4\uff0c\u4e0d\u4ec5\u6ca1\u6709\u6d88\u8017\u591a\u5c11\u7cbe\u529b\uff0c\u53cd\u800c\u8ba9\u4ed6\u7684\u795e\u667a\u53d8\u5f97\u66f4\u52a0\u6e05\u9192\u3002\u773c\u770b\u4e5f\u7761\u4e0d\u7740\uff0c\u4ed6\u7d22\u6027\u8eba\u5728\u5e8a\u4e0a\u770b\u7740\u5929\u82b1\u677f\uff0c\u9ed8\u9ed8\u5730\u60f3\u7740\u5fc3\u4e8b\u3002</p><p>\u6700\u8fd1\u8fd9\u6bb5\u65e5\u5b50\uff0c\u4ece\u8868\u9762\u4e0a\u770b\uff0c\u743c\u6069\u8fc7\u5f97\u9887\u4e3a\u6109\u5feb\uff0c\u9887\u4e3a\u60ec\u610f\uff0c\u653e\u4e0b\u4ee4\u4eba\u70e6\u5fc3\u7684\u5de5\u4f5c\uff0c\u79bb\u5f00\u9634\u68ee\u538b\u6291\u7684\u9634\u9b42\u57ce\uff0c\u51fa\u95e8\u65c5\u6e38\u5ea6\u5047\uff0c\u8eab\u65c1\u8fd8\u6709\u591a\u540d\u7f8e\u4eba\u76f8\u4f34\uff0c\u867d\u7136\u514d\u4e0d\u4e86\u7ecf\u5e38\u6253\u6253\u6740\u6740\uff0c\u4f46\u603b\u4f53\u6765\u8bf4\u8fd8\u662f\u5f88\u60a0\u95f2\u7684\u3002\u7136\u800c\u5728\u5185\u5fc3\u6df1\u5904\uff0c\u4ed6\u65e0\u65f6\u65e0\u523b\u4e0d\u6709\u4e00\u79cd\u60f6\u6050\u4e0d\u5b89\u611f\uff0c\u603b\u89c9\u5f97\u4e0d\u8e0f\u5b9e\uff1b\u603b\u6709\u90a3\u79cd\u5c71\u96e8\u6b32\u6765\u3001\u9ed1\u4e91\u538b\u57ce\u7684\u5371\u673a\u611f\uff0c\u50cf\u9634\u5f71\u4e00\u6837\u65f6\u523b\u7b3c\u7f69\u5728\u5fc3\u5934\u3002\u4eff\u4f5b\u773c\u524d\u8fd9\u7f8e\u597d\u7684\u4e00\u5207\uff0c\u968f\u65f6\u90fd\u4f1a\u50cf\u68a6\u5e7b\u822c\u60ca\u9192\uff0c\u50cf\u6ce1\u6cab\u822c\u788e\u53bb\uff0c\u6d88\u5931\u5f97\u65e0\u5f71\u65e0\u8e2a\u3002\u5728\u6f5c\u610f\u8bc6\u91cc\uff0c\u4f3c\u4e4e\u603b\u6709\u4e2a\u58f0\u97f3\u5728\u95ee\uff1a\u8fd9\u79cd\u60a0\u95f2\u81ea\u5728\u7684\u7f8e\u597d\u65f6\u5149\uff0c\u80fd\u591f\u4e00\u76f4\u6301\u7eed\u4e0b\u53bb\u4e48\uff0c\u8fd8\u80fd\u518d\u4eab\u53d7\u591a\u4e45\u5462\uff1f</p><p>\u743c\u6069\u4e0d\u77e5\u9053\u3002</p><p>\u81ea\u5df1\u7684\u5b58\u5728\uff0c\u65e9\u5df2\u88ab\u8bc1\u660e\u662f\u4e00\u679a\u7cbe\u5fc3\u51c6\u5907\u7684\u68cb\u5b50\uff0c\u7136\u800c\u9690\u85cf\u5728\u9ed1\u6697\u4e2d\u7684\u5e9e\u5927\u68cb\u5c40\uff0c\u81f3\u4eca\u5c1a\u672a\u7aa5\u5f97\u5168\u8c8c\u2014\u2014\u4e0d\uff0c\u51c6\u786e\u5730\u8bf4\uff0c\u662f\u8fde\u4e00\u4e2a\u8fb9\u89d2\u90fd\u8fd8\u6ca1\u770b\u900f\u5427\u3002\u9634\u9b42\u57ce\u7684\u57f9\u517b\u548c\u5b89\u6392\u3001\u591c\u5973\u58eb\u7684\u8bf1\u60d1\u548c\u7b3c\u7edc\u3001\u8fdc\u53e4\u5e1d\u56fd\u7684\u7075\u9b42\u5370\u8bb0\u3001\u59d0\u59d0\u73ca\u5609\u7684\u795e\u79d8\u8eab\u4e16\u3001\u7a81\u7136\u51fa\u73b0\u7684\u9ed1\u8863\u5c11\u5973\u2026\u2026\u6240\u6709\u7684\u8fd9\u4e00\u5207\uff0c\u90fd\u8ba9\u4ed6\u5982\u5760\u8ff7\u96fe\uff0c\u770b\u4e0d\u6e05\uff0c\u8fa8\u4e0d\u660e\uff0c\u4e0d\u77e5\u9053\u81ea\u5df1\u8eab\u5728\u4f55\u5904\uff0c\u66f4\u4e0d\u77e5\u9053\u81ea\u5df1\u5e94\u8be5\u5982\u4f55\u5e94\u5bf9\uff0c\u5982\u4f55\u624d\u80fd\u8d70\u51fa\u8ff7\u96fe\uff0c\u6446\u8131\u68cb\u5c40\u3002\u56e0\u4e3a\u4ed6\u751a\u81f3\u90fd\u4e0d\u77e5\u9053\uff0c\u54ea\u91cc\u624d\u662f\u524d\u65b9\u7684\u65b9\u5411\u3002</p><p>\u832b\u7136\u3002</p><p>\u743c\u6069\u552f\u4e00\u80fd\u591f\u786e\u5b9a\u7684\uff0c\u662f\u81ea\u5df1\u4e0d\u4f1a\u5c31\u8fd9\u6837\u8f7b\u6613\u5730\u8131\u8eab\u4e8b\u5916\u3002\u68cb\u624b\u4eec\u5e03\u4e0b\u68cb\u5b50\uff0c\u4e0d\u4f1a\u5c31\u6b64\u653e\u4efb\u81ea\u6d41\uff0c\u4e0d\u7406\u4e0d\u776c\uff0c\u6b64\u523b\u7684\u7965\u548c\u5e73\u9759\uff0c\u53ea\u662f\u66b4\u98ce\u96e8\u524d\u5915\u7684\u77ed\u6682\u5b89\u5b81\u7f62\u4e86\u3002\u4ed6\u611f\u89c9\u81ea\u5df1\u5c31\u50cf\u662f\u7ad9\u5728\u4e00\u5757\u8106\u5f31\u7684\u8352\u539f\u4e0a\uff0c\u4e0d\u77e5\u4f55\u65f6\u5c31\u4f1a\u6709\u6c79\u6d8c\u7684\u7194\u5ca9\u81ea\u5730\u5e95\u55b7\u53d1\uff0c\u6709\u731b\u70c8\u7684\u98d3\u98ce\u81ea\u811a\u4e0b\u5377\u8d77\uff0c\u5c06\u4ed6\u629b\u4e0a\u534a\u7a7a\uff0c\u4e5d\u9704\u4e91\u5916\uff0c\u7136\u540e\u6454\u5f97\u7c89\u8eab\u788e\u9aa8\u3002\u4ed6\u4e0d\u77e5\u9053\u8fd9\u4e00\u5929\u4ec0\u4e48\u65f6\u5019\u4f1a\u5230\u6765\uff0c\u6216\u8bb8\u8fd8\u5f88\u4e45\uff0c\u6216\u8bb8\u5c31\u662f\u660e\u65e5\uff0c\u81ea\u5df1\u5bf9\u6b64\u4e00\u5207\u672a\u77e5\uff0c\u53ea\u80fd\u5728\u6050\u60e7\u4e2d\u9ed8\u9ed8\u7b49\u5f85\u3002</p><p>\u8fd9\uff0c\u53ef\u4e0d\u662f\u6211\u60f3\u8981\u7684\u547d\u8fd0\u554a\uff01</p><p>\u743c\u6069\u8ba8\u538c\u8eab\u4e3a\u68cb\u5b50\uff0c\u8ba8\u538c\u53d7\u4eba\u6446\u5e03\u7684\u632b\u6298\u611f\uff0c\u8ba8\u538c\u8fd9\u79cd\u6d8c\u81ea\u5fc3\u5e95\u7684\u6df1\u6df1\u65e0\u529b\uff0c\u7136\u800c\u5374\u65e0\u6cd5\u6446\u8131\u3002\u4ed6\u5e0c\u671b\u638c\u63e1\u81ea\u5df1\u7684\u547d\u8fd0\uff0c\u4f46\u638c\u63e1\u81ea\u5df1\u547d\u8fd0\u7684\u524d\u63d0\uff0c\u662f\u62e5\u6709\u8db3\u591f\u7684\u529b\u91cf\u3002\u4ed6\u4e5f\u786e\u5b9e\u6210\u4e3a\u4e86\u4f18\u79c0\u7684\u5deb\u5e08\uff0c\u62e5\u6709\u4e86\u5728\u8fd9\u4e2a\u4e16\u754c\u4e0a\u76f8\u5bf9\u53ef\u89c2\u7684\u529b\u91cf\uff0c\u751a\u81f3\u4e00\u5ea6\u81ea\u89c9\u826f\u597d\uff0c\u7136\u800c\u5230\u4e86\u771f\u6b63\u7684\u5f3a\u8005\u9762\u524d\uff0c\u4f9d\u65e7\u662f\u4e0d\u582a\u4e00\u51fb\uff0c\u4ec0\u4e48\u90fd\u4e0d\u7b97\u3002\u4eca\u665a\u7684\u90a3\u573a\u6218\u6597\uff0c\u5c31\u662f\u6700\u597d\u7684\u8bc1\u660e\u3002</p><p>\u6216\u8bb8\uff0c\u81ea\u5df1\u8fd9\u6837\u803f\u803f\u4e8e\u6000\uff0c\u8fd9\u6837\u8bb0\u6068\u6b23\u5e03\u548c\u5965\u5609\u83b1\u65af\u7684\u771f\u6b63\u539f\u56e0\uff0c\u5e76\u975e\u53d7\u4f24\uff0c\u4e5f\u5e76\u975e\u4e22\u9762\u5b50\uff0c\u800c\u662f\u56e0\u4e3a\u7f9e\u607c\u5427\u3002\u7f9e\u6127\u4e8e\u81ea\u5df1\u7684\u65e0\u80fd\uff0c\u607c\u6012\u4e8e\u81ea\u5df1\u7684\u5dee\u52b2\uff0c\u5965\u5609\u83b1\u65af\u8bf4\u5f97\u5bf9\uff1a\u8eab\u4e3a\u4e00\u4e2a\u7537\u4eba\uff0c\u9047\u5230\u5371\u9669\u7684\u65f6\u5019\uff0c\u8fde\u81ea\u5df1\u5fc3\u7231\u7684\u5973\u4eba\u90fd\u6ca1\u6709\u80fd\u529b\u4fdd\u62a4\uff0c\u90a3\u8fd8\u6709\u6d3b\u5728\u4e16\u754c\u4e0a\u7684\u8d44\u683c\u4e48\u3002</p><p>\u53ef\u662f\uff0c\u81ea\u5df1\u5e94\u8be5\u600e\u4e48\u505a\u5462\uff1f</p><p>\u529b\u91cf\u8fd9\u79cd\u4e1c\u897f\uff0c\u4e0d\u4f1a\u51ed\u7a7a\u4ece\u5929\u4e0a\u6389\u4e0b\u6765\uff0c\u5f53\u7136\u4e5f\u4e0d\u4f1a\u771f\u7684\u901a\u8fc7\u5411\u6d41\u661f\u8bb8\u613f\u5c31\u80fd\u5b9e\u73b0\u3002\u6b63\u5e38\u60c5\u51b5\u4e0b\uff0c\u53ea\u80fd\u6309\u90e8\u5c31\u73ed\uff0c\u523b\u82e6\u953b\u70bc\uff0c\u4e00\u70b9\u70b9\u5730\u9010\u6e10\u79ef\u7d2f\u2014\u2014\u8fd9\u662f\u6b63\u9053\uff0c\u7136\u800c\u6b63\u9053\u603b\u662f\u610f\u5473\u7740\u6f2b\u957f\u7684\u65f6\u95f4\uff0c\u743c\u6069\u5374\u6070\u6070\u6ca1\u6709\u65f6\u95f4\u3002\u4e8b\u5b9e\u4e0a\uff0c\u4ed6\u4e0d\u5230\u4e8c\u5341\u5c81\u5c31\u5df2\u7ecf\u8e0f\u5165\u9ad8\u9636\u5deb\u5e08\u884c\u5217\uff0c\u6709\u4eca\u5929\u7684\u9020\u8be3\uff0c\u5df2\u7ecf\u662f\u5927\u5f00\u7279\u5f00\u4f5c\u5f0a\u5668\u4e86\u3002\u9664\u6b64\u4e4b\u5916\uff0c\u8981\u60f3\u83b7\u5f97\u529b\u91cf\uff0c\u800c\u4e14\u662f\u5feb\u901f\u83b7\u5f97\u529b\u91cf\uff0c\u6216\u8005\u662f\u4e0e\u90aa\u9b54\u8fbe\u6210\u5951\u7ea6\uff0c\u51fa\u5356\u7075\u9b42\uff1b\u6216\u8005\u4fbf\u662f\u501f\u52a9\u5916\u7269\uff0c\u4f8b\u5982\u4ec0\u4e48\u5b9d\u7269\u795e\u5668\u4e4b\u7c7b\u3002</p><p>\u4e03\u79d8\u5668\uff1f</p><p>\u81ea\u4ece\u5f97\u77e5\u81ea\u5df1\u7684\u4f0a\u739b\u65af\u5361\u7687\u5ba4\u8840\u8109\u65f6\u8d77\uff0c\u743c\u6069\u4fbf\u5f00\u59cb\u7559\u5fc3\u90a3\u4f20\u8bf4\u4e2d\u7684\u4e03\u79d8\u5668\uff0c\u671f\u671b\u80fd\u591f\u5f97\u77e5\u5b83\u4eec\u7684\u4e0b\u843d\uff0c\u6700\u597d\u80fd\u5f04\u4e00\u4e24\u4ef6\u5230\u624b\uff0c\u4ed6\u53bb\u70db\u5821\u67e5\u9605\u8d44\u6599\uff0c\u5411\u5965\u6c83\u8bf7\u6559\uff0c\u90fd\u662f\u4e3a\u4e86\u8fd9\u4e2a\u76ee\u7684\u3002\u6839\u636e\u6076\u9b54\u6b27\u51ef\u7684\u8bf4\u6cd5\uff0c\u4f3c\u4e4e\u53ea\u8981\u6709\u4e03\u79d8\u5668\u4e4b\u4e00\u5728\u624b\uff0c\u5c31\u53ef\u4ee5\u6a2a\u884c\u5929\u4e0b\uff0c\u5982\u679c\u4e03\u79d8\u5668\u9f50\u96c6\uff0c\u4fbf\u8fde\u6f2b\u5929\u795e\u660e\u4e5f\u53ef\u4ee5\u4e0d\u653e\u5728\u773c\u91cc\u3002\u743c\u6069\u81ea\u7136\u4e0d\u4f1a\u76f8\u4fe1\u8fd9\u79cd\u8bdd\uff0c\u4f46\u4e03\u79d8\u5668\u786e\u5b9e\u662f\u201c\u81f3\u9ad8\u201d\u7ea7\u7684\u795e\u5668\uff0c\u8fd9\u662f\u6240\u6709\u8d44\u6599\u90fd\u516c\u8ba4\u7684\u3002</p><p>\u81f3\u9ad8\u7ea7\u795e\u5668\uff0c\u5230\u5e95\u662f\u4e2a\u4ec0\u4e48\u6982\u5ff5\uff1f\u4e3e\u51e0\u4e2a\u7b80\u5355\u7684\u4f8b\u5b50\u5373\u53ef\u77e5\u3002\u5965\u6c83\u53f7\u79f0\u5b9d\u7269\u6536\u85cf\u5bb6\uff0c\u5b9d\u5e93\u91cc\u7684\u795e\u5668\u5927\u5927\u5c0f\u5c0f\u7ea6\u6709\u6570\u5341\u4ef6\uff0c\u4f46\u5374\u6ca1\u6709\u4e00\u4ef6\u591f\u5f97\u4e0a\u201c\u81f3\u9ad8\u201d\u7ea7\uff08\u9664\u4e86\u90a3\u51e0\u5f20\u8010\u745f\u5377\u8f74\u7684\u6b8b\u9875\uff0c\u7b97\u662f\u81f3\u9ad8\u795e\u5668\u7684\u201c\u788e\u7247\u201d\uff09\u3002\u201c\u8010\u745f\u5377\u8f74\u201d\u662f\u81f3\u9ad8\u795e\u5668\uff0c\u5960\u5b9a\u4e86\u8010\u745f\u745e\u5c14\u5e1d\u56fd\u7684\u9b54\u6cd5\u5b66\u57fa\u7840\uff0c\u5f00\u521b\u4e86\u5386\u53f2\u4e0a\u6700\u5149\u8f89\u707f\u70c2\u7684\u5deb\u5e08\u65f6\u4ee3\u3002\u601d\u601d\u624b\u4e2d\u7684\u201c\u5bd2\u51b0\u4e4b\u6212\u201d\u662f\u81f3\u9ad8\u795e\u5668\uff0c\u636e\u8bf4\u80fd\u591f\u8ba9\u6574\u4e2a\u4e16\u754c\u9677\u5165\u51b0\u5c01\uff0c\u8fd8\u80fd\u8ba9\u4eba\u6c38\u751f\u4e0d\u673d\uff0c\u9752\u6625\u6c38\u9a7b\u3002\u963f\u66fc\u7eb3\u5854\u4e09\u5723\u5668\u662f\u81f3\u9ad8\u795e\u5668\uff0c\u5176\u4e2d\u4e4b\u4e00\u7684\u201c\u70bd\u9633\u4e4b\u676f\u201d\uff0c\u4e00\u51fb\u5c31\u5c06\u9634\u9b42\u738b\u5b50\u5e03\u96f7\u7eb3\u65af\u7684\u534a\u4e2a\u8eab\u8eaf\u5316\u4f5c\u98de\u7070\u2014\u2014\u800c\u4e14\u5965\u6c83\u8bf4\u5f97\u5f88\u6e05\u695a\uff0c\u90a3\u70bd\u9633\u4e4b\u676f\u8fd8\u662f\u4e2a\u6b8b\u7f3a\u54c1\uff0c\u5c11\u4e86\u4e00\u4e2a\u90e8\u4ef6\uff0c\u5426\u5219\u738b\u5b50\u6bbf\u4e0b\u53ea\u6015\u5f53\u573a\u5c31\u6302\u4e86\u3002</p><p>\u5982\u679c\u81ea\u5df1\u4e5f\u80fd\u6301\u6709\u8fd9\u6837\u5f3a\u5927\u7684\u795e\u5668\uff0c\u5728\u9762\u5bf9\u8be1\u8c32\u83ab\u6d4b\u7684\u672a\u6765\u65f6\uff0c\u65e0\u7591\u4f1a\u589e\u5927\u5f88\u591a\u628a\u63e1\u5427\u3002</p><p>\u795e\u5668\u8fd9\u79cd\u4e1c\u897f\uff0c\u5927\u81f4\u4e0a\u4e5f\u53ef\u4ee5\u5206\u6b63\u90aa\u4e24\u79cd\uff0c\u524d\u8005\u5f80\u5f80\u504f\u4e8e\u9632\u5fa1\u3001\u795d\u798f\uff0c\u9488\u5bf9\u6027\u5f3a\uff0c\u6740\u4f24\u529b\u4e0d\u8db3\uff0c\u95e8\u69db\u9ad8\uff0c\u9650\u5236\u6761\u4ef6\u591a\uff0c\u4f46\u65e0\u526f\u4f5c\u7528\uff1b\u540e\u8005\u5f80\u5f80\u5a01\u529b\u5de8\u5927\uff0c\u7834\u574f\u6027\u6781\u5f3a\uff0c\u800c\u4e14\u95e8\u69db\u6781\u4f4e\uff0c\u4eba\u4eba\u53ef\u7528\uff0c\u4f46\u4f7f\u7528\u98ce\u9669\u6781\u9ad8\u3002\u524d\u8005\u7684\u5178\u578b\u4f8b\u5b50\uff0c\u4f8b\u5982\u6885\u83f2\u65af\u624b\u4e2d\u7684\u8f89\u9633\u62a4\u7b26\uff0c\u6740\u4f24\u529b\u53ea\u9488\u5bf9\u4ea1\u7075\uff0c\u800c\u4e14\u5fc5\u987b\u662f\u8fdc\u53e4\u592a\u9633\u795e\u963f\u66fc\u7eb3\u5854\uff08\u6216\u8005\u7ee7\u627f\u4e86\u963f\u66fc\u7eb3\u5854\u795e\u804c\u7684\u63d0\u5c14\u3001\u5170\u68ee\u5fb7\u5c14\uff09\u7684\u9ad8\u9636\u795e\u804c\u4eba\u5458\u624d\u80fd\u4f7f\u7528\uff0c\u5728\u5176\u4ed6\u4eba\u624b\u91cc\u5c31\u662f\u4e2a\u6446\u8bbe\uff1b\u540e\u8005\u7684\u5178\u578b\u4f8b\u5b50\uff0c\u4f8b\u5982\u4f20\u8bf4\u4e2d\u7684\u788e\u9b54\u6676\uff0c\u5bf9\u4f7f\u7528\u8005\u51e0\u4e4e\u6ca1\u6709\u201c\u8d44\u8d28\u201d\u4e0a\u7684\u8981\u6c42\uff0c\u8fde\u4e2a\u4e0d\u6210\u5668\u7684\u5deb\u5e08\u5b66\u5f92\u62ff\u5230\u4e86\uff0c\u90fd\u80fd\u591f\u5728\u5317\u5730\u5174\u98ce\u4f5c\u6d6a\uff0c\u547c\u98ce\u5524\u96e8\u2014\u2014\u4f46\u5386\u53f2\u4e0a\u83b7\u5f97\u8fd9\u79cd\u795e\u5668\u7684\u4eba\uff0c\u5341\u6709\u516b\u4e5d\u90fd\u6302\u4e86\uff0c\u5c11\u6570\u662f\u88ab\u6b63\u4e49\u82f1\u96c4\u5e72\u6389\uff0c\u5927\u591a\u6570\u5219\u662f\u88ab\u795e\u5668\u53cd\u566c\uff0c\u81ea\u5df1\u628a\u81ea\u5df1\u73a9\u6b7b\u4e86\u3002</p><p>\u603b\u7ed3\u8d77\u6765\u5c31\u662f\uff0c\u95e8\u69db\u8d8a\u9ad8\uff0c\u5b89\u5168\u6027\u8d8a\u9ad8\uff0c\u98ce\u9669\u8d8a\u5c0f\uff1b\u95e8\u69db\u8d8a\u4f4e\uff0c\u5b89\u5168\u6027\u8d8a\u5dee\uff0c\u53cd\u566c\u8d8a\u5f3a\uff0c\u8fd9\u4e5f\u662f\u7b26\u5408\u5e73\u8861\u4e4b\u9053\u7684\u3002\u5982\u679c\u662f\u788e\u9b54\u6676\u3001\u9b54\u6212\u90a3\u79cd\u795e\u5668\uff0c\u5f3a\u5219\u5f3a\u77e3\uff0c\u95e8\u69db\u53c8\u4f4e\uff0c\u6301\u6709\u8005\u5374\u662f\u5341\u4e2a\u5c31\u6709\u5341\u4e00\u4e2a\u6ca1\u597d\u4e0b\u573a\uff0c\u8fd9\u79cd\u4e1c\u897f\u743c\u6069\u662f\u4ece\u6765\u662f\u656c\u8c22\u4e0d\u654f\uff0c\u907f\u800c\u8fdc\u4e4b\u3002\u4ed6\u662f\u60f3\u53d8\u5f3a\uff0c\u4e0d\u662f\u60f3\u81ea\u6740\uff0c\u751f\u547d\u5982\u6b64\u7f8e\u597d\uff0c\u4ed6\u8fd8\u6ca1\u6d3b\u817b\u5462\u3002\u4f46\u95e8\u69db\u9ad8\u7684\u795e\u5668\uff0c\u5927\u591a\u53c8\u662f\u4e13\u4e3a\u90a3\u4e9b\u6709\u5d07\u9ad8\u7406\u5ff5\u575a\u5b9a\u4fe1\u4ef0\u5149\u660e\u4eba\u683c\u7684\u4eba\u8bbe\u8ba1\uff0c\u743c\u6069\u600e\u4e48\u770b\u81ea\u5df1\u90fd\u4e0d\u9002\u5408\u2026\u2026</p><p>\u552f\u4e00\u9002\u5408\u7684\uff0c\u5c31\u662f\u4e03\u79d8\u5668\u4e86\u3002</p><p>\u4e03\u79d8\u5668\u662f\u4f0a\u739b\u65af\u5361\u5e1d\u56fd\u7684\u4f20\u56fd\u81f3\u5b9d\uff0c\u8840\u8109\u9650\u5b9a\uff0c\u552f\u6709\u7687\u5ba4\u624d\u80fd\u4f7f\u7528\uff0c\u95e8\u69db\u4e0d\u53ef\u8c13\u4e0d\u9ad8\uff0c\u5b89\u5168\u6027\u6bcb\u5eb8\u7f6e\u7591\uff0c\u5951\u5408\u6027\u4e5f\u6bcb\u5eb8\u7f6e\u7591\u3002\u5957\u7528\u6e38\u620f\u8bf4\u6cd5\uff0c\u5c31\u662f\u91cf\u8eab\u6253\u9020\u7684\u4e13\u5c5e\u88c5\u5907\uff0c\u800c\u4e14\u8fd8\u662f\u7075\u9b42\u7ed1\u5b9a\u7684\u3002\u65e0\u8bba\u4ece\u54ea\u4e2a\u65b9\u9762\u770b\uff0c\u90fd\u662f\u6700\u5b8c\u7f8e\u7684\u9009\u62e9\u4e86\u5427\u3002</p><p>\u76f4\u5230\u4eca\u5929\u4e0b\u5348\u4e4b\u524d\uff0c\u743c\u6069\u90fd\u662f\u8fd9\u6837\u8ba4\u4e3a\u7684\uff0c\u4e5f\u662f\u8fd9\u6837\u61a7\u61ac\u548c\u5e0c\u671b\u7684\u2014\u2014\u7136\u800c\u548c\u51ef\u745f\u7433\u7684\u4f1a\u9762\uff0c\u5374\u751f\u751f\u5728\u4ed6\u5934\u9876\u6d47\u4e0b\u4e00\u76c6\u51b7\u6c34\u3002\u5f53\u51ef\u745f\u7433\u5411\u4ed6\u501f\u8d70\u90a3\u6761\u4e0d\u77e5\u9053\u4ece\u54ea\u91cc\u5192\u51fa\u6765\u7684\u91d1\u8272\u6e38\u9f99\u65f6\uff0c\u743c\u6069\u987a\u53e3\u95ee\u4e86\u53e5\u5b83\u662f\u4ec0\u4e48\uff0c\u5f97\u5230\u7684\u56de\u7b54\u662f\u201c\u7b2c\u4e03\u5668\u201d\u3002</p><p>\u5c31\u7b97\u518d\u8fdf\u949d\u7684\u4eba\uff0c\u4e5f\u4f1a\u628a\u8fd9\u4e2a\u8bcd\u548c\u201c\u7b2c\u4e03\u79d8\u5668\u201d\u8054\u7cfb\u8d77\u6765\u5427\u3002\u4e5f\u5373\u662f\u8bf4\uff0c\u4e03\u79d8\u5668\u4e4b\u4e00\uff0c\u5176\u5b9e\u65e9\u5c31\u5df2\u7ecf\u5728\u81ea\u5df1\u624b\u4e2d\uff0c\u7136\u800c\u81ea\u5df1\u538b\u6839\u6ca1\u6709\u5bdf\u89c9\uff0c\u66f4\u522b\u8bf4\u4f7f\u7528\u4e86\u3002\u8fd9\u7ed9\u743c\u6069\u9020\u6210\u7684\u6253\u51fb\uff0c\u53ef\u4e0d\u662f\u4e00\u822c\u7684\u5927\uff0c\u6253\u4e2a\u4e0d\u751a\u786e\u5207\u7684\u6bd4\u65b9\uff0c\u5c31\u50cf\u4f60\u5f88\u7a77\uff0c\u4e00\u76f4\u671f\u671b\u7740\u80fd\u4e70\u5f69\u7968\u4e2d\u4e94\u767e\u4e07\uff0c\u4f46\u4e00\u76f4\u672a\u80fd\u5982\u613f\uff0c\u4f60\u5bb6\u91cc\u6709\u4e2a\u74f6\u5b50\uff0c\u4e00\u76f4\u5f53\u505a\u6742\u7269\u6254\u5728\u89d2\u843d\u91cc\uff0c\u751a\u81f3\u81ea\u5df1\u90fd\u5fd8\u4e86\uff0c\u7a81\u7136\u67d0\u4e00\u65e5\u88ab\u4eba\u82b1\u5341\u5757\u94b1\u4e70\u8d70\u2014\u2014\u800c\u4e70\u5bb6\u6b64\u65f6\u544a\u8bc9\u4f60\uff0c\u8fd9\u4e2a\u74f6\u5b50\u662f\u53e4\u8463\uff0c\u4ef7\u503c\u5343\u4e07\u3002\u8fd9\u79cd\u5931\u843d\u611f\uff0c\u5f53\u771f\u662f\u65e0\u6cd5\u7528\u8bed\u8a00\u6765\u5f62\u5bb9\u3002</p><p>\u5f53\u7136\uff0c\u51ef\u745f\u7433\u662f\u8bf4\u201c\u6682\u501f\u201d\uff0c\u4e0d\u662f\u53d6\u8d70\u3002\u4f46\u66f4\u4e25\u91cd\u7684\u95ee\u9898\u662f\uff1a\u5982\u679c\u81ea\u5df1\u65e9\u5c31\u62e5\u6709\u7b2c\u4e03\u79d8\u5668\uff0c\u5374\u90fd\u4e0d\u77e5\u9053\u5b83\u7684\u5b58\u5728\u2014\u2014\u90a3\u5c31\u7b97\u6709\u4e00\u5929\uff0c\u81ea\u5df1\u771f\u7684\u5f97\u5230\u4e86\u5176\u4ed6\u79d8\u5668\uff0c\u96be\u9053\u5c31\u4e00\u5b9a\u80fd\u987a\u5229\u4f7f\u7528\u5417\uff1f\u4e03\u79d8\u5668\u5fc5\u987b\u4f0a\u739b\u65af\u5361\u7687\u5ba4\u624d\u80fd\u4f7f\u7528\uff0c\u4f46\u5e76\u4e0d\u7b49\u4e8e\u8bf4\uff0c\u53ea\u8981\u662f\u4f0a\u739b\u65af\u5361\u7687\u5ba4\uff0c\u5c31\u4e00\u5b9a\u80fd\u591f\u4f7f\u7528\u4e03\u79d8\u5668\u5427\u3002\u8fd9\u5176\u4e2d\uff0c\u662f\u5426\u8fd8\u6709\u522b\u7684\u9650\u5b9a\u6761\u4ef6\u5462\uff1f</p><p>\u5934\u75bc\u3002</p><p>\u601d\u7eea\u7eb7\u4e71\u4e2d\uff0c\u743c\u6069\u6e10\u6e10\u7761\u53bb\u3002</p></article>"}}}]);
