(window.webpackJsonp=window.webpackJsonp||[]).push([[522],{545:function(p){p.exports={chapterTitle:null,title:"\u7b2c522\u7ae0 \u58f0\u4e1c\u4e4b\u8ba1",content:"<article><h2>\u7b2c522\u7ae0 \u58f0\u4e1c\u4e4b\u8ba1</h2><p>\u3000\u3000\u6d77\u88d5\u516c\u53f8\u5df2\u7ecf\u8f83\u7eaf\u7cb9\u7684\u8f6c\u4e3a\u63a7\u80a1\u516c\u53f8\uff0c\u4e1a\u52a1\u90e8\u95e8\u5df2\u7ecf\u840e\u7f29\u5f97\u53ea\u5269\u4e0b\u7a7a\u58f3\u4e86\uff0c\u5373\u4f7f\u8d22\u52a1\u7ba1\u7406\u90e8\u95e8\uff0c\u4e5f\u9010\u6e10\u4e0e\u6d77\u5dde\u7684\u6d77\u88d5\u79c1\u7acb\u5b66\u6821\u7684\u8d22\u52a1\u90e8\u95e8\u6574\u5408\uff0c\u7701\u57ce\u8fd9\u8fb9\u7684\u529e\u516c\u573a\u6240\u4e0e\u4eba\u4e8b\u7cbe\u51cf\u4e0b\u6765\u7684\u5458\u5de5\u51e0\u4e4e\u90fd\u586b\u8fdb\u65b0\u5149\u7eb8\u4e1a\u5728\u7701\u57ce\u8bbe\u7acb\u7684\u6797\u4e1a\u516c\u53f8\u3002</p><p>\u3000\u3000\u4e0d\u65ad\u7684\u62db\u8058\u65b0\u5458\u5de5\uff0c\u53c8\u5927\u89c4\u6a21\u7684\u5438\u6536\u4ece\u56fd\u6709\u6797\u573a\u4e0e\u68ee\u5de5\u4f01\u4e1a\u4e0b\u5c97\u5206\u6d41\u7684\u804c\u5de5\u2014\u2014\u4e3b\u8981\u8fd8\u662f\u5438\u5f15\u4e0b\u5c97\u5206\u6d41\u7684\u6797\u4e1a\u5de5\u4eba\u2014\u2014\u6797\u4e1a\u516c\u53f8\u5728\u8fc7\u53bb\u4e0d\u5230\u4e00\u5e74\u7684\u65f6\u95f4\u91cc\u5c31\u8fc5\u901f\u81a8\u80c0\u8d77\u6765\uff0c\u6210\u4e3a\u9526\u6e56\u65d7\u4e0b\u5458\u5de5\u4eba\u6570\u6700\u4e3a\u5e9e\u5927\u7684\u4e00\u4e2a\u5206\u652f\u3002</p><p>\u3000\u3000\u8fd8\u597d\u5e73\u65f6\u7559\u5728\u603b\u90e8\u8d1f\u8d23\u884c\u653f\u4e0e\u8d22\u52a1\u7684\u5458\u5de5\u4e0d\u662f\u5f88\u591a\uff0c\u4e0e\u8fd9\u680b\u5199\u5b57\u697c\u91cc\u5176\u4ed6\u516c\u53f8\u7684\u5458\u5de5\uff0c\u4e2d\u5348\u7684\u5de5\u4f5c\u9910\u90fd\u4f1a\u5230\u9876\u5c42\u7684\u7efc\u5408\u9910\u5385\u7528\u9910\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u662f\u5165\u4e61\u968f\u4fd7\uff0c\u62ff\u516c\u53f8\u53d1\u7684\u5348\u9910\u5238\uff0c\u4e2d\u5348\u5c31\u5728\u5199\u5b57\u697c\u9876\u5c42\u7684\u9910\u5385\u7528\u5348\u9910\u3002</p><p>\u3000\u3000\u4e01\u69d0\u4e58\u8f66\u8fc7\u6765\uff0c\u6709\u4e9b\u4e8b\u60c5\u53ef\u4ee5\u5728\u8f66\u4e0a\u901a\u8fc7\u624b\u673a\u4ea4\u6d41\uff1b\u82cf\u6d25\u4e1c\u4e58\u98de\u673a\u4e4b\u524d\uff0c\u4e5f\u8fd8\u53ef\u4ee5\u7528\u7535\u8bdd\u6c9f\u901a\uff0c\u5dee\u4e0d\u591a\u4f9d\u7167\u8bb8\u9e3f\u4f2f\u58f0\u4e1c\u51fb\u897f\u7684\u601d\u8def\u601d\u8003\u89e3\u51b3\u65b9\u6848\uff0c\u4e3b\u8981\u8fd8\u662f\u4e0d\u80fd\u632b\u4f24\u674e\u4ee3\u7701\u957f\u7684\u79ef\u6781\u6027\u4e0e\u81ea\u5c0a\u5fc3\uff0c\u8bf4\u5230\u5e95\u4e5f\u6d89\u53ca\u5230\u66f4\u6df1\u5c42\u6b21\u7684\u653f\u6cbb\u6743\u5a01\u6027\u7684\u95ee\u9898\u3002</p><p>\u3000\u3000\u9664\u975e\u6839\u672c\u5c31\u4e0d\u7528\u754f\u60e7\u674e\u8fdc\u6e56\uff0c\u4e0d\u7136\u5c31\u8981\u8003\u8651\u5230\u4ed6\u4e3a\u4e86\u7ef4\u6301\u4ed6\u7701\u59d4\u526f\u4e66\u8bb0\u3001\u7701\u957f\u7684\u6743\u5a01\u53ef\u80fd\u505a\u51fa\u7684\u53cd\u5e94\u3002</p><p>\u3000\u3000\u4e01\u69d0\u4e0b\u5348\u4e24\u70b9\u949f\u4e58\u8f66\u8d76\u5230\u7701\u57ce\u4e0e\u5f20\u606a\u78b0\u9762\uff0c\u5f20\u606a\u8fd9\u65f6\u5019\u63a5\u5230\u5510\u5b66\u8c26\u7684\u7535\u8bdd\u3002</p><p>\u3000\u3000\u5f20\u606a\u6253\u7b97\u7b49\u60f3\u51fa\u4e00\u4e2a\u5927\u4f53\u7684\u89e3\u51b3\u601d\u8def\u4e4b\u540e\u518d\u8ddf\u5510\u5b66\u8c26\u3001\u5b8b\u57f9\u660e\u4ed6\u4eec\u63d0\u53ca\u8fd9\u4e8b\uff0c\u5012\u662f\u6ca1\u6709\u60f3\u5230\u674e\u4e49\u6c5f\u4f1a\u8d8a\u4fce\u4ee3\u5e96\uff0c\u5927\u6982\u662f\u674e\u4e49\u6c5f\u542c\u51fa\u81ea\u5df1\u6577\u884d\u7684\u8bed\u6c14\uff0c\u60f3\u7740\u901a\u8fc7\u5510\u5b66\u8c26\u6765\u7ed9\u81ea\u5df1\u65bd\u52a0\u538b\u529b\u3002</p><p>\u3000\u3000\u5f20\u606a\u5bf9\u5510\u5b66\u8c26\u5012\u6ca1\u6709\u4ec0\u4e48\u597d\u9690\u7792\u7684\uff0c\u5728\u7535\u8bdd\u91cc\u8ddf\u4ed6\u8be6\u7ec6\u89e3\u91ca\u4e86\u73b0\u65f6\u4e0a\u5236\u6d46\u9879\u76ee\u7684\u65e0\u6cd5\u514b\u670d\u7684\u73b0\u5b9e\u56f0\u96be\uff1a\u6728\u6d46\u4f9b\u5e94\u4f1a\u4e00\u76f4\u504f\u7d27\uff0c\u4ece\u5e02\u573a\u89d2\u5ea6\u4e0a\u5236\u6d46\u9879\u76ee\u662f\u5408\u9002\u7684\uff0c\u4f46\u662f\u5236\u6d46\u9879\u76ee\u4e0e\u9526\u6e56\u5176\u4ed6\u7684\u8ba1\u5212\u5728\u8d44\u91d1\u4e0e\u4eba\u529b\u5339\u914d\u4e0a\u6709\u51b2\u7a81\uff1b\u6ca1\u6709\u8db3\u591f\u591a\u7684\u539f\u6599\u6797\uff0c\u5f3a\u884c\u4e0a\u5236\u6d46\u9879\u76ee\uff0c\u4f1a\u8fdb\u4e00\u6b65\u52a0\u5267\u7701\u5185\u4e0e\u5468\u8fb9\u7701\u5e02\u7684\u751f\u6001\u73af\u5883\u538b\u529b\uff0c\u8fd8\u53ef\u80fd\u5bfc\u81f4\u56fd\u9645\u751f\u6001\u4e0e\u7eff\u8272\u548c\u5e73\u7ec4\u7ec7\u7684\u8d28\u96be\uff0c\u5176\u6076\u52a3\u5f71\u54cd\u5bf9\u9526\u6e56\u4f01\u4e1a\u5f62\u8c61\u7684\u6253\u51fb\u5c06\u662f\u707e\u96be\u6027\u7684\u3002</p><p>\u3000\u3000\u5510\u5b66\u8c26\u6c89\u541f\u7684\u7247\u523b\uff0c\u8bf4\u9053\uff1a\u201c\u8bf8\u4e8b\u90fd\u8981\u5411\u524d\u770b\uff0c\u987e\u5168\u5927\u5c40\uff0c\u987e\u5168\u771f\u6b63\u7684\u5927\u5c40\u662f\u6700\u91cd\u8981\u7684\uff0c\u4e00\u65f6\u7684\u56f0\u5883\u4e5f\u7b97\u4e0d\u4e86\u4ec0\u4e48\u3002\u8fd9\u4e48\u770b\u6765\uff0c\u4f60\u660e\u5929\u662f\u65e0\u6cd5\u56de\u6d77\u5dde\u7ed9\u82cf\u9038\u7fa4\u63a5\u98ce\u6d17\u5c18\u4e86\uff1f\u201d</p><p>\u3000\u3000\u5510\u5b66\u8c26\u80fd\u7406\u89e3\u5c31\u597d\u529e\uff0c\u5f20\u606a\u8fd8\u771f\u6015\u5510\u5b66\u8c26\u7ed9\u81ea\u5df1\u65bd\u52a0\u538b\u529b\uff0c\u53c8\u5728\u7535\u8bdd\u91cc\u8ddf\u4ed6\u8bf4\u4e86\u4e00\u4e9b\u53ef\u80fd\u91c7\u53d6\u7684\u8865\u6551\u63aa\u65bd\u3002</p><p>\u3000\u3000\u4e0b\u5348\u56db\u70b9\u949f\uff0c\u6362\u4e0a\u65b0\u7684\u624b\u673a\u7535\u677f\uff0c\u5f20\u606a\u62c9\u7740\u4e01\u69d0\u3001\u5085\u4fca\u5c31\u76f4\u63a5\u53bb\u673a\u573a\u622a\u82cf\u6d25\u4e1c\uff0c\u7b49\u5728\u673a\u573a\u4e0e\u82cf\u6d25\u4e1c\u6c47\u540e\u5c31\u76f4\u63a5\u4e58\u98de\u673a\u53bb\u5317\u4eac\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u674e\u8fdc\u6e56\u90fd\u4f20\u51fa\u8bdd\u6765\uff0c\u8fd9\u8fb9\u4e5f\u4e0d\u80fd\u4e00\u70b9\u53cd\u5e94\u90fd\u6ca1\u6709\u3002</p><p>\u3000\u3000\u65e2\u7136\u674e\u8fdc\u6e56\u4e0d\u662f\u76f4\u63a5\u627e\u5230\u9526\u6e56\uff0c\u800c\u662f\u901a\u8fc7\u9646\u6587\u592b\u3001\u674e\u4e49\u6c5f\u4f20\u8bdd\uff0c\u9526\u6e56\u4e5f\u4e0d\u9700\u8981\u76f4\u63a5\u627e\u674e\u8fdc\u6e56\u6c47\u62a5\uff1b\u7701\u57ce\u8fd9\u8fb9\u7531\u5468\u6e38\u8d1f\u8d23\uff0c\u8ba9\u4ed6\u4e0e\u5b8b\u5fd7\u4f51\u3001\u8bb8\u9e3f\u4f2f\u627e\u5206\u7ba1\u6797\u4e1a\u7684\u526f\u7701\u957f\u80e1\u5b66\u5175\u6c47\u62a5\u76f8\u5173\u5236\u6d46\u9879\u76ee\u7684\u5de5\u4f5c\u8fdb\u5c55\u5c31\u53ef\u4ee5\u4e86\uff0c\u4e5f\u4e0d\u4f1a\u4e00\u4e0b\u5b50\u5c06\u8bdd\u8bf4\u6b7b\u4e0d\u7559\u56de\u65cb\u7684\u4f59\u5730\uff0c\u65b0\u5149\u7eb8\u4e1a\u53ef\u4ee5\u5c06\u524d\u671f\u5de5\u4f5c\u5148\u5f00\u5c55\u8d77\u6765\uff1b\u524d\u671f\u7b79\u5907\u8ba1\u5212\u4f1a\u901a\u8fc7\u674e\u4e49\u6c5f\u3001\u9646\u6587\u592b\u53cd\u9988\u7ed9\u674e\u8fdc\u6e56\uff0c\u8868\u793a\u5bf9\u4ed6\u672c\u4eba\u7684\u5c0a\u91cd\u3002</p><p>\u3000\u3000\u201c\u62d6\u201d\u5b57\u8bc0\u80fd\u4e0d\u80fd\u594f\u6548\uff0c\u8fd9\u4e00\u5207\uff0c\u5173\u952e\u8fd8\u8981\u770b\u201c\u58f0\u4e1c\u201d\u65b9\u6848\u5b9e\u65bd\u7684\u6548\u679c\u5982\u4f55\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u9999\u6e2f\u4e0e\u5efa\u90ba\u4e4b\u95f4\uff0c\u6e2f\u9f99\u822a\u7a7a\u4e0e\u4e1c\u65b9\u822a\u7a7a\u6bcf\u5468\u5171\u6709\u4e8c\u5341\u4e00\u73ed\u98de\u673a\uff0c\u4e24\u5c0f\u65f6\u56db\u5341\u5206\u949f\u7684\u884c\u7a0b\uff0c\u8981\u662f\u4e0d\u7b97\u5f80\u8fd4\u673a\u573a\u4ee5\u53ca\u5728\u673a\u573a\u7b49\u5019\u7684\u65f6\u95f4\uff0c\u5efa\u90ba\u4e0e\u9999\u6e2f\u7684\u5f80\u6765\uff0c\u6bd4\u6d77\u5dde\u8fd8\u8981\u65b9\u4fbf\u3002</p><p>\u3000\u3000\u6e2f\u9f99\u822a\u7a7a\u7684\u98de\u673a\u521a\u5728\u5efa\u90ba\u5357\u90ca\u7684\u56fd\u9645\u673a\u573a\u964d\u843d\u4e0b\u6765\uff0c\u82cf\u6d25\u4e1c\u4e0e\u52a9\u7406\u521a\u6253\u5f00\u624b\u673a\u4eba\u8fd8\u6ca1\u6709\u4e0b\u98de\u673a\uff0c\u5c31\u63a5\u5230\u5f20\u606a\u4ece\u63a5\u673a\u5927\u5385\u91cc\u6253\u6765\u7684\u7535\u8bdd\uff0c\u8bf4\u662f\u8981\u9a6c\u4e0a\u6539\u4e58\u53bb\u5317\u4eac\u7684\u98de\u673a\u3002</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u6709\u4e09\u4e2a\u5c0f\u65f6\u6ca1\u8ddf\u5f20\u606a\u4ed6\u4eec\u901a\u4e0a\u7535\u8bdd\uff0c\u4e0d\u77e5\u9053\u4ed6\u4eec\u505a\u51fa\u4ec0\u4e48\u51b3\u5b9a\uff0c\u8fdb\u4e86\u63a5\u673a\u5927\u5385\u6025\u5fd9\u8ddf\u7740\u5f20\u606a\u4ed6\u4eec\u8f6c\u53bb\u5019\u673a\u5927\u5385\u62ff\u673a\u7968\u6362\u767b\u673a\u724c\uff0c\u76f4\u5230\u53c8\u5750\u4e0a\u98de\u673a\uff0c\u624d\u6709\u65f6\u95f4\u505c\u4e0b\u6b47\u4e00\u53e3\u6c14\uff0c\u53c8\u6025\u63a5\u7740\u95ee\u5f20\u606a\u4ed6\u4eec\u4e0b\u5348\u505a\u4ec0\u4e48\u51b3\u5b9a\u3002</p><p>\u3000\u3000\u4ed6\u4eec\u8d76\u65f6\u95f4\u6ca1\u6709\u8ba2\u5230\u5546\u52a1\u8231\u7684\u673a\u7968\uff0c\u7ecf\u6d4e\u8231\u6362\u767b\u673a\u724c\u53c8\u662f\u4f4d\u7f6e\u6700\u843d\u540e\u7684\u4e00\u6392\uff0c\u98de\u673a\u8d77\u98de\u540e\uff0c\u7a0d\u6709\u6c14\u6d41\u98de\u673a\u540e\u6bb5\u98a0\u7c38\u5c31\u6bd4\u8f83\u5389\u5bb3\uff0c\u5f20\u606a\u3001\u4e01\u69d0\u3001\u5085\u4fca\u90fd\u65e0\u6240\u8c13\uff0c\u82cf\u6d25\u4e1c\u4e0e\u4ed6\u7684\u52a9\u7406\u8fde\u7eed\u4e58\u673a\u5c31\u6709\u4e9b\u591f\u545b\u4e86\u3002\u843d\u5ea7\u540e\uff0c\u5f20\u606a\u8ddf\u7a7a\u4e58\u5c0f\u59d0\u8981\u4e86\u4e00\u676f\u5496\u5561\uff0c\u4ed6\u5c06\u5496\u5561\u676f\u6367\u5728\u624b\u91cc\uff0c\u4e5f\u6ca1\u6709\u6ce8\u610f\u5230\u90a3\u540d\u7a7a\u4e58\u5c0f\u59d0\u770b\u4ed6\u7684\u8868\u60c5\u989d\u5916\u4eb2\u5207\u3002</p><p>\u3000\u3000\u5f20\u606a\u8ddf\u82cf\u6d25\u4e1c\u4ecb\u7ecd\u4e0b\u5348\u4ed6\u4eec\u8ba8\u8bba\u7684\u60c5\u51b5\uff1a\u201c\u6211\u4eec\u4e0b\u5348\u7d27\u6025\u78cb\u5546\u4e86\u4e00\u4e0b\uff0c\u4e5f\u4e0e\u5728\u7f8e\u56fd\u7684\u9648\u4fe1\u751f\u3001\u8096\u664b\u6210\u3001\u6210\u6c49\u748b\u4e09\u4eba\u901a\u8fc7\u7535\u8bdd\u3002\u6211\u4eec\u8ba1\u5212\u4e2d\uff0c\u8981\u5728\u7f8e\u56fd\u7845\u8c37\u3001\u65e5\u672c\u7b51\u6ce2\u5e02\u4ee5\u53ca\u9526\u6e56\u5728\u6d77\u5dde\u7684\u672c\u90e8\u6210\u7acb\u4e09\u4e2a\u6280\u672f\u7814\u53d1\u4e2d\u5fc3\uff0c\u5728\u97e9\u56fd\u6c49\u57ce\u3001\u9999\u6e2f\u3001\u65e5\u672c\u4e1c\u4eac\u4ee5\u53ca\u9526\u6e56\u5728\u6d77\u5dde\u7684\u672c\u90e8\u6210\u7acb\u56db\u4e2a\u5de5\u7a0b\u5e94\u7528\u4e0e\u4ea7\u54c1\u8bbe\u8ba1\u4e2d\u5fc3\uff0c\u8fd9\u662f\u6211\u4eec\u6280\u672f\u6269\u5f20\u7684\u7b2c\u4e00\u6b65\u89c4\u5212\uff0c\u8fd9\u4e2a\u89c4\u5212\u662f\u4e0e\u624b\u673a\u9879\u76ee\u4ee5\u53ca\u6211\u4eec\u7684U-1\u3001M-1\u9879\u76ee\u7d27\u5bc6\u8054\u7cfb\u5728\u4e00\u8d77\u7684\uff0c\u8981\u4e3a\u8fd9\u4e09\u4e2a\u9879\u76ee\u63d0\u4f9b\u5145\u5206\u7684\u6280\u672f\u652f\u6301\u3002\u4f46\u662f\u73b0\u5b9e\u7684\u60c5\u51b5\uff0c\u6d77\u5dde\u6bd5\u7adf\u504f\u4e8e\u4e00\u9685\uff0c\u5728\u4eba\u529b\u8d44\u6e90\u3001\u6280\u672f\u8d44\u6e90\u4e0a\u53d7\u5230\u4e25\u91cd\u7684\u9650\u5236\u3002\u4ece\u7f8e\u56fd\u7845\u8c37\u3001\u97e9\u56fd\u6c49\u57ce\u3001\u65e5\u672c\u4e1c\u4eac\u3001\u7b51\u6ce2\u5e02\u4ee5\u53ca\u9999\u6e2f\u5230\u6d77\u5dde\uff0c\u5728\u4eba\u529b\u8d44\u6e90\u3001\u6280\u672f\u8d44\u6e90\u4e0a\u7684\u843d\u5dee\u5f88\u5927\uff0c\u9700\u8981\u4e00\u4e2a\u7f13\u51b2\u3001\u4e00\u4e2a\u8df3\u677f\u2026\u2026\u8003\u8651\u5230\u8fd9\u8df3\u677f\u4e0d\u4ec5\u9700\u8981\u80fd\u627f\u63a5\u3001\u6d88\u5316\u6b27\u7f8e\u3001\u65e5\u97e9\u6700\u5148\u8fdb\u7684\u6d88\u8d39\u7535\u5b50\u6280\u672f\uff0c\u8fd8\u9700\u8981\u80fd\u6301\u7eed\u7684\u5b75\u80b2\u4e0e\u5f00\u53d1\u76f8\u5173\u6280\u672f\uff0c\u5bf9\u8fd9\u4e2a\u5730\u533a\u7684\u9ad8\u79d1\u6280\u4eba\u529b\u4e0e\u6280\u672f\u8d44\u6e90\u7684\u8981\u6c42\u5c31\u76f8\u5173\u7684\u9ad8\uff0c\u56fd\u5185\u76ee\u524d\u6700\u9002\u5408\u7684\u5730\u533a\u5c31\u662f\u5317\u4eac\u4e2d\u5173\u6751\u2026\u2026\u201d</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u7b2c\u4e8c\u6b65\u4f1a\u8003\u8651\u5728\u5317\u4eac\u4e2d\u5173\u6751\u6210\u7acb\u5de5\u7a0b\u5e94\u7528\u4e2d\u5fc3\uff0c\u5728\u5efa\u90ba\u4e5f\u8981\u6210\u7acb\u4e00\u4e2a\uff0c\u600e\u4e48\u4e86\uff0c\u8fd9\u4e2a\u52a8\u4f5c\u8981\u63d0\u524d\uff1f\u201d</p><p>\u3000\u3000\u201c\u5bf9\uff0c\u662f\u8981\u63d0\u524d\uff0c\u4e0d\u4ec5\u8981\u63d0\u524d\uff0c\u8fd8\u8981\u5c06\u6280\u672f\u5b75\u80b2\u505a\u6210\u4ea7\u4e1a\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u5728\u539f\u5148\u8ba1\u5212\u4e2d\uff0c\u53ea\u662f\u5728\u7701\u57ce\u62db\u8058\u4e8c\u4e09\u5341\u4e2a\u5de5\u7a0b\u6280\u672f\u4eba\u5458\u6210\u7acb\u4e00\u4e2a\u5de5\u7a0b\u5e94\u7528\u4e2d\u5fc3\uff0c\u6211\u4eec\u4e0b\u5348\u8ba8\u8bba\u4e86\u4e00\u4e0b\uff0c\u89c9\u5f97\u5c06\u5728\u7701\u57ce\u8bbe\u7acb\u5de5\u7a0b\u5e94\u7528\u4e2d\u5fc3\u7684\u8ba1\u5212\u4fee\u6539\u4e00\u4e0b\uff0c\u62d3\u5c55\u6210\u5927\u578b\u7684\u79d1\u6280\u56ed\u8ba1\u5212\uff1b\u7701\u91cc\u4e0d\u662f\u4e5f\u4e00\u76f4\u5728\u641e\u9ad8\u65b0\u79d1\u6280\u4ea7\u4e1a\u6218\u7565\u5417\uff1f\u6211\u4eec\u5c31\u7528\u79d1\u6280\u56ed\u8ba1\u5212\u6765\u5f25\u8865\u5236\u6d46\u9879\u76ee\u3002\u4e3a\u6b64\uff0c\u6d77\u5dde\u79d1\u6280\u56ed\u9879\u76ee\u5c31\u6682\u7f13\u4e00\u4e0b\uff0c\u7b49\u4e24\u5e74\u518d\u63d0\u4e5f\u4e0d\u8fdf\uff1b\u9526\u6e56\u672c\u90e8\u7684\u5de5\u7a0b\u5e94\u7528\u4e2d\u5fc3\u3001\u6280\u672f\u7814\u53d1\u4e2d\u5fc3\u7684\u6269\u5f20\u8ba1\u5212\u90fd\u5e76\u5230\u7701\u57ce\u7684\u79d1\u6280\u56ed\u9879\u76ee\u91cc\u6765\u2026\u2026\u4f60\u89c9\u5f97\u8fd9\u6837\u5b89\u6392\u5982\u4f55\uff1f\u201d</p><p>\u3000\u3000\u201c\u8981\u6211\u8bf4\u5b9e\u8bdd\u554a\uff1f\u201d\u82cf\u6d25\u4e1c\u5f00\u73a9\u7b11\u95ee\u5f20\u606a\u3002</p><p>\u3000\u3000\u201c\u6709\u4ec0\u4e48\u8bdd\uff0c\u6211\u8fd8\u80fd\u4e0d\u8ba9\u4f60\u8bf4\u3002\u201d\u5f20\u606a\u7b11\u4e86\u8d77\u6765\u3002</p><p>\u3000\u3000\u201c\u6210\u7acb\u79d1\u6280\u56ed\uff0c\u5efa\u90ba\u7684\u6761\u4ef6\u6bd4\u6d77\u5dde\u8981\u6210\u719f\uff0c\u201d\u82cf\u6d25\u4e1c\u8bf4\u9053\uff0c\u201c\u6309\u8bf4\u5728\u56fd\u5185\u8981\u7b97\u5317\u4eac\u4e2d\u5173\u6751\u7684\u6280\u672f\u8d44\u6e90\u6700\u4e3a\u96c4\u539a\uff0c\u4f46\u662f\u9526\u6e56\u8981\u5728\u5317\u4eac\u641e\u4ec0\u4e48\u79d1\u6280\u56ed\uff0c\u4e0d\u4f1a\u53d7\u5230\u5317\u4eac\u5e02\u653f\u5e9c\u591a\u5927\u7684\u91cd\u89c6\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u70b9\u70b9\u5934\uff0c\u5730\u65b9\u652f\u6301\u529b\u5ea6\u662f\u6295\u8d44\u9879\u76ee\u65f6\u9700\u8981\u8003\u8651\u7684\u5173\u952e\u56e0\u7d20\u4e4b\u4e00\u3002\u4ece\u6280\u672f\u3001\u4eba\u529b\u8d44\u6e90\u4e0e\u5730\u65b9\u652f\u6301\u529b\u5ea6\u7684\u89d2\u5ea6\u6765\u8003\u8651\uff0c\u5efa\u90ba\u6070\u597d\u662f\u4e2a\u5e73\u8861\u70b9\u3002\u5f20\u606a\u4e4b\u524d\u8003\u8651\u5148\u5728\u6d77\u5dde\u6295\u8d44\u5efa\u79d1\u6280\u56ed\uff0c\u5c31\u662f\u60f3\u8981\u62c9\u6d77\u5dde\u4e00\u628a\u3002\u5f20\u606a\u505a\u51b3\u5b9a\u90fd\u5e26\u7740\u4e9b\u7406\u60f3\u4e3b\u4e49\uff0c\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u4ed6\u4eec\u90fd\u5fc3\u91cc\u6709\u6570\uff0c\u6709\u70b9\u5c0f\u7455\u75b5\uff0c\u4e5f\u6ca1\u4eba\u786c\u8981\u53cd\u5bf9\u4ed6\u3002</p><p>\u3000\u3000\u6ca1\u60f3\u5230\u73b0\u5b9e\u7684\u60c5\u51b5\uff0c\u8fd8\u662f\u8feb\u4f7f\u5f20\u606a\u505a\u51fa\u6700\u73b0\u5b9e\u7684\u9009\u62e9\u3002</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u95ee\u9053\uff1a\u201c\u90a3\u6211\u4eec\u7126\u6025\u53bb\u5317\u4eac\u505a\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u201c\u53bb\u5317\u4eac\u505a\u79c0\uff01\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u6211\u4eec\u8981\u5728\u5317\u4eac\u5c06\u58f0\u52bf\u7092\u8d77\u6765\u3002\u79d1\u6280\u56ed\u653e\u54ea\u91cc\u4e0d\u662f\u653e\uff0c\u9664\u5f00\u5317\u4eac\uff0c\u4e0a\u6d77\u3001\u6b66\u6c49\u3001\u6210\u90fd\u751a\u81f3\u5408\u80a5\u3001\u676d\u5dde\uff0c\u5728\u6d88\u8d39\u7535\u5b50\u6280\u672f\u9886\u57df\u90fd\u6709\u4e00\u5b9a\u7684\u6280\u672f\u4e0e\u4eba\u529b\u8d44\u6e90\uff0c\u5efa\u90ba\u4e0e\u8fd9\u51e0\u4e2a\u57ce\u5e02\u6bd4\u8d77\u6765\uff0c\u4e5f\u4e0d\u5360\u4ec0\u4e48\u4f18\u52bf\uff0c\u4e3a\u4ec0\u4e48\u8bf4\u6211\u4eec\u8981\u8146\u7740\u8138\u6c42\u5230\u7701\u91cc\u53bb\uff0c\u4e0d\u8ba9\u4ed6\u4eec\u6765\u6c42\u6211\u4eec\uff1f\u201d</p><p>\u3000\u3000\u201c\u58f0\u4e1c\u4e4b\u8ba1\uff1f\u201d\u82cf\u6d25\u4e1c\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u4e5f\u4e0d\u662f\u7eaf\u7cb9\u7684\u5927\u9020\u58f0\u52bf\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u624b\u673a\u9879\u76ee\u7684\u4e8b\u60c5\uff0c\u4f1a\u5b58\u5728\u4e00\u4e9b\u73b0\u5728\u7684\u56f0\u96be\uff0c\u90ae\u7535\u90e8\u53ef\u80fd\u4f1a\u503e\u5411\u4e8e\u5c06\u7b2c\u4e00\u5f20\u56fd\u4ea7\u624b\u673a\u6279\u6587\u7ed9\u4ed6\u4eec\u4e0b\u5c5e\u7684\u4f01\u4e1a\uff0c\u8fd9\u4e5f\u9700\u8981\u6211\u4eec\u505a\u4e9b\u989d\u5916\u7684\u5de5\u4f5c\uff0c\u5f88\u591a\u4e8b\u60c5\u53ef\u80fd\u6346\u7ed1\u5230\u4e00\u8d77\u53bb\u5b8c\u6210\u3002\u201d</p><p>\u3000\u3000\u624b\u673a\u9879\u76ee\u4e0e\u79d1\u6280\u56ed\u6346\u7ed1\u5728\u4e00\u8d77\u505a\u4e3a\u4e00\u4e2a\u9879\u76ee\uff0c\u89c4\u6a21\u8fd8\u662f\u8981\u6bd4\u5236\u6d46\u9879\u76ee\u5c11\u5f97\u591a\uff0c\u6240\u4ee5\u66f4\u9700\u8981\u505a\u79c0\uff0c\u5148\u5c06\u58f0\u52bf\u7092\u8d77\u6765\uff0c\u5f62\u6210\u66f4\u5927\u7684\u5f71\u54cd\u529b\uff0c\u8feb\u4f7f\u7701\u91cc\u505a\u51fa\u9009\u62e9\uff0c\u674e\u8fdc\u6e56\u603b\u4e0d\u80fd\u5f3a\u8feb\u7740\u9526\u6e56\u540c\u65f6\u4e0a\u4e24\u4e2a\u9879\u76ee\u3002</p><p>\u3000\u3000\u5728\u8fd9\u4e2a\u91cc\u9762\uff0c\u79d1\u6280\u56ed\u9879\u76ee\u662f\u5bf9\u5efa\u90ba\u5e02\u76f4\u63a5\u6709\u5229\u7684\uff0c\u800c\u5236\u6d46\u9879\u76ee\u7684\u9009\u5740\u53ea\u53ef\u80fd\u653e\u5728\u7701\u897f\u5317\u90e8\u67d0\u5e02\uff0c\u5efa\u90ba\u5e02\u5728\u7701\u91cc\u7684\u5730\u4f4d\uff0c\u53c8\u4e0d\u662f\u7701\u897f\u5317\u90e8\u5730\u533a\u80fd\u6bd4\u7684\uff0c\u5f20\u606a\u8fd9\u662f\u8981\u5c06\u5efa\u90ba\u5730\u65b9\u7684\u52bf\u529b\u8fd9\u6b21\u4e5f\u7ed1\u4e0a\u4ed6\u7684\u6218\u8f66\u3002</p></article>"}}}]);
