(window.webpackJsonp=window.webpackJsonp||[]).push([[523],{545:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e94\u5341\u4e94\u8282 \u743c\u6069\u7684\u4e8b\u4e1a",content:"<article><h2>\u7b2c\u4e94\u5341\u4e94\u8282 \u743c\u6069\u7684\u4e8b\u4e1a</h2><p>\u51e0\u58f0\u6e05\u8106\u7684\u9e1f\u9e23\u4ece\u9662\u4e2d\u6811\u4e0a\u4f20\u6765\uff0c\u60ca\u6270\u4e86\u5367\u5ba4\u4e2d\u7684\u9759\u8c27\uff0c\u5c06\u743c\u6069\u4ece\u7761\u68a6\u4e2d\u60ca\u9192\uff0c\u8ff7\u8ff7\u7cca\u7cca\u5730\u968f\u624b\u5f80\u8eab\u65c1\u4e00\u6478\uff0c\u53d1\u73b0\u7a7a\u8361\u8361\u7684\u6ca1\u4eba\u3002\u4ed6\u7a0d\u7a0d\u6e05\u9192\u4e86\u70b9\uff0c\u7741\u5f00\u773c\u775b\u5750\u8d77\u8eab\u6765\uff0c\u770b\u89c1\u7a88\u7a95\u7f8e\u4e3d\u7684\u5c11\u5973\u62ab\u7740\u4e00\u4ef6\u7c89\u7ed2\u7761\u888d\uff0c\u6b63\u5750\u5728\u7a97\u524d\uff0c\u5bf9\u955c\u68b3\u5986\u3002\u6d45\u767d\u8272\u6668\u5149\u81ea\u7a97\u5916\u900f\u5165\uff0c\u6620\u5728\u5979\u7684\u8138\u988a\u4e0a\uff0c\u6cdb\u7740\u67d4\u548c\u5fae\u5149\uff0c\u8d8a\u53d1\u663e\u5f97\u6574\u4e2a\u4eba\u82f1\u534e\u5185\u655b\uff0c\u6e29\u6da6\u5982\u7389\u3002</p><p>\u771f\u7f8e\u3002</p><p>\u743c\u6069\u9760\u5750\u5728\u5e8a\u5934\uff0c\u9759\u9759\u6b23\u8d4f\u7740\u773c\u524d\u7684\u7f8e\u666f\u3002\u5c11\u5973\u62ff\u7740\u4e00\u53ea\u8c61\u7259\u68b3\u5b50\uff0c\u4ed4\u7ec6\u5730\u5c06\u707f\u91d1\u8272\u79c0\u53d1\u68b3\u7406\u6574\u9f50\uff0c\u7136\u540e\u5206\u6210\u51e0\u7f15\u633d\u8d77\uff0c\u4f3c\u4e4e\u60f3\u8981\u76d8\u6210\u53d1\u9afb\uff0c\u4f46\u5979\u663e\u7136\u4e0d\u64c5\u6b64\u9053\uff0c\u7b28\u624b\u7b28\u811a\u7684\uff0c\u5c1d\u8bd5\u4e86\u534a\u5929\u90fd\u6ca1\u80fd\u6210\u529f\u3002\u4e00\u6c14\u4e4b\u4e0b\uff0c\u5979\u7d22\u6027\u5c06\u5934\u53d1\u653e\u4e0b\uff0c\u68b3\u4e86\u4e2a\u6700\u7b80\u5355\u7684\u9a6c\u5c3e\uff0c\u7528\u4e00\u6839\u767d\u8272\u7ec6\u4e1d\u5e26\u624e\u4f4f\uff0c\u5012\u4e5f\u663e\u5f97\u975e\u5e38\u4e4b\u5e72\u51c0\u5229\u843d\u3002\u7136\u800c\u5c11\u5973\u5bf9\u7740\u955c\u5b50\u770b\u4e86\u770b\uff0c\u4fa7\u8fc7\u8138\u7167\u4e86\u534a\u5929\uff0c\u7ec8\u7a76\u89c9\u5f97\u8fd8\u662f\u4e0d\u6ee1\u610f\uff0c\u4e0d\u7531\u5f97\u76b1\u8d77\u7709\u5934\uff0c\u52aa\u529b\u5730\u601d\u8003\u7740\uff0c\u8138\u4e0a\u5fae\u5fae\u6d41\u9732\u51fa\u82e6\u607c\u7684\u795e\u60c5\uff0c\u4e0d\u77e5\u9053\u8be5\u600e\u4e48\u505a\u3002</p><p>\u201c\u5230\u5e95\u8981\u7528\u54ea\u79cd\u53d1\u578b\u624d\u66f4\u6f02\u4eae\u5462\uff1f\u201d</p><p>\u56e0\u4e3a\u592a\u8fc7\u4e13\u5fc3\u81f4\u5fd7\u7684\u7f18\u6545\uff0c\u5e73\u65f6\u8b66\u89c9\u5982\u9e70\u7684\u5c11\u5973\u5e76\u6ca1\u6709\u6ce8\u610f\u5230\u7537\u53cb\u5728\u80cc\u540e\u9759\u9759\u6b23\u8d4f\u7684\u76ee\u5149\uff0c\u5979\u6b63\u6ee1\u8111\u5b50\u90fd\u5145\u585e\u7740\u590d\u6742\u7684\u53d1\u578b\u95ee\u9898\uff0c\u76f4\u5230\u743c\u6069\u770b\u5f97\u6709\u8da3\uff0c\u5fcd\u4e0d\u4f4f\u51fa\u58f0\u8be2\u95ee\u624d\u56de\u8fc7\u795e\u6765\u3002\u201c\u4f60\u5e72\u561b\u5462\uff1f\u201d\u743c\u6069\u542b\u7b11\u95ee\uff0c\u201c\u4e00\u5927\u6e05\u65e9\u7684\uff0c\u6298\u817e\u5934\u53d1\u505a\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u56e0\u4e3a\u771f\u7684\u5f88\u9ebb\u70e6\u554a\uff0c\u201d\u6885\u83f2\u65af\u54ac\u7740\u5634\u5507\uff0c\u62b1\u6028\u7740\uff0c\u201c\u5934\u53d1\u957f\u4e86\u5c31\u662f\u96be\u6253\u7406\u3002\u201d</p><p>\u5341\u516d\u4e03\u5c81\u7684\u5c11\u5973\uff0c\u6b63\u662f\u5973\u6027\u610f\u8bc6\u5b8c\u5168\u89c9\u9192\uff0c\u6700\u7231\u6253\u626e\u7684\u65f6\u5019\uff0c\u7136\u800c\u6885\u83f2\u65af\u662f\u4e2a\u4f8b\u5916\uff0c\u5979\u7d20\u6765\u5d07\u5c1a\u7b80\u6d01\u6734\u7d20\uff0c\u65b9\u4fbf\u5b9e\u7528\uff0c\u8863\u670d\u90fd\u662f\u6e05\u4e00\u8272\u7684\u886c\u886b\u957f\u88e4\uff0c\u5934\u53d1\u66f4\u662f\u7a0d\u5fae\u957f\u70b9\u5c31\u7acb\u523b\u526a\u65ad\uff0c\u514d\u5f97\u788d\u4e8b\u3002\u662f\u548c\u743c\u6069\u5728\u4e00\u8d77\u4e4b\u540e\uff0c\u5979\u624d\u5f00\u59cb\u7559\u957f\u53d1\uff0c\u4e24\u5e74\u65f6\u95f4\u4e0b\u6765\u4e5f\u5df2\u7ecf\u9887\u73b0\u6210\u6548\uff0c\u4f46\u968f\u4e4b\u800c\u6765\u7684\u68d8\u624b\u95ee\u9898\uff0c\u5c31\u662f\u5982\u4f55\u6253\u7406\u4e86\uff0c\u8fd9\u65b9\u9762\u5979\u53ef\u5b9e\u5728\u6ca1\u4ec0\u4e48\u7ecf\u9a8c\u3002</p><p>\u201c\u90a3\u5c31\u526a\u6389\u5427\uff0c\u201d\u743c\u6069\u968f\u53e3\u5efa\u8bae\uff0c\u201c\u8fd8\u50cf\u4ee5\u524d\u90a3\u6837\u5c31\u597d\u4e86\u554a\u3002\u201d</p><p>\u5c11\u5973\u6c14\u607c\u5730\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u201c\u4f60\u4e0d\u662f\u8bf4\u559c\u6b22\u5973\u5b69\u5b50\u7559\u957f\u53d1\u4e48\uff1f\u201d</p><p>\u201c\u5514\uff1f\u6211\u6709\u8fd9\u4e48\u8bf4\u8fc7\u5417\uff1f\u201d\u743c\u6069\u6115\u7136\uff0c\u201c\u4e0d\u8bb0\u5f97\u4e86\u3002\u201d</p><p>\u201c\u5f53\u7136\u8bf4\u8fc7\u554a\uff0c\u5c31\u662f\u90a3\u6b21\u6211\u4eec\u53bb\u963f\u683c\u62c9\u9686\u7684\u8def\u4e0a\uff0c\u201d\u5c11\u5973\u63d0\u9192\uff0c\u201c\u6211\u95ee\u4f60\uff1a\u559c\u6b22\u6211\u957f\u53d1\u8fd8\u662f\u77ed\u53d1\uff1b\u4f60\u56de\u7b54\u8bf4\uff1a\u957f\u53d1\uff0c\u8fd9\u6837\u624d\u50cf\u5973\u5b69\u5b50\uff0c\u4ee5\u524d\u77ed\u53d1\u7684\u6837\u5b50\u592a\u521a\u786c\u4e86\u2014\u2014\u8fd9\u4e9b\u8bdd\u6211\u53ef\u5168\u90fd\u8bb0\u5f97\u5462\uff0c\u522b\u60f3\u62b5\u8d56\u3002\u201d</p><p>\u201c\u2026\u2026\u4f60\u8bb0\u5fc6\u529b\u771f\u597d\u3002\u201d</p><p>\u88ab\u6885\u83f2\u65af\u63d0\u9192\uff0c\u743c\u6069\u603b\u7b97\u52c9\u5f3a\u60f3\u8d77\u6765\uff0c\u6a21\u6a21\u7cca\u7cca\u7684\u4f3c\u4e4e\u662f\u6709\u8fd9\u4e48\u56de\u4e8b\u3002\u5176\u5b9e\u5bf9\u4e8e\u5973\u5b69\u5b50\u5934\u53d1\u957f\u77ed\u7684\u95ee\u9898\uff0c\u4ed6\u5e76\u6ca1\u6709\u4ec0\u4e48\u7279\u522b\u5f3a\u70c8\u7684\u504f\u597d\uff0c\u957f\u53d1\u98d8\u9038\u81ea\u7136\u662f\u6e29\u5a49\u67d4\u7f8e\uff0c\u77ed\u53d1\u4e5f\u5f88\u4fcf\u4e3d\u53ef\u7231\uff0c\u6216\u8005\u82f1\u59ff\u98d2\u723d\uff0c\u522b\u5177\u98ce\u5473\u3002\u5c3d\u7ba1\u5982\u6b64\uff0c\u743c\u6069\u8fd8\u662f\u5fc3\u4e2d\u9690\u9690\u611f\u52a8\uff0c\u4ed6\u5f53\u65f6\u4e5f\u5c31\u662f\u968f\u53e3\u4e00\u8bf4\uff0c\u5e76\u975e\u5f88\u8ba4\u771f\uff0c\u6ca1\u60f3\u5230\u6885\u83f2\u65af\u5374\u771f\u5f80\u5fc3\u91cc\u53bb\u4e86\uff0c\u4e00\u76f4\u90fd\u8bb0\u7740\u3002</p><p>\u4ed6\u4ece\u5c11\u5973\u624b\u4e2d\u53d6\u8fc7\u68b3\u5b50\uff0c\u56de\u5fc6\u7740\u4ee5\u524d\u89c1\u8fc7\u7684\u90a3\u4e9b\u5404\u5f0f\u5404\u6837\u7684\u6f02\u4eae\u53d1\u578b\uff0c\u60f3\u5e2e\u5979\u4e5f\u7167\u6837\u5f04\u4e00\u4e2a\u3002\u4f46\u8fd9\u79cd\u4e8b\u60c5\u53c8\u54ea\u91cc\u662f\u90a3\u4e48\u5bb9\u6613\u7684\uff0c\u6298\u817e\u4e86\u534a\u5929\uff0c\u4f9d\u65e7\u8fd8\u662f\u5f92\u52b3\u65e0\u529f\uff0c\u6700\u7ec8\u53ea\u5f97\u65e0\u5948\u653e\u5f03\u3002\u201c\u7b97\u5566\u7b97\u5566\uff0c\u4f60\u628a\u6211\u5934\u53d1\u90fd\u5f04\u5f97\u4e00\u56e2\u7cdf\u4e86\uff0c\u201d\u6885\u83f2\u65af\u7b11\u7740\uff0c\u201c\u8fd8\u662f\u68b3\u4e2a\u9a6c\u5c3e\u5427\uff0c\u6700\u7b80\u5355\u3002\u201d</p><p>\u743c\u6069\u600f\u600f\u5730\u628a\u68b3\u5b50\u9012\u8fd8\u7ed9\u5c11\u5973\uff0c\u201c\u5973\u5b69\u5b50\u7684\u5934\u53d1\u771f\u96be\u5bf9\u4ed8\uff0c\u201d\u4ed6\u53d1\u8868\u8bc4\u8bba\uff0c\u201c\u6bd4\u6cd5\u5f8b\u548c\u9b54\u6cd5\u590d\u6742\u591a\u4e86\u3002\u201d</p><p>\u201c\u90a3\u4e5f\u672a\u5fc5\uff0c\u770b\u4eba\u7684\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u51db\u5c31\u5f88\u4f1a\u5f04\u5934\u53d1\uff0c\u80fd\u591f\u505a\u51fa\u4e0a\u767e\u79cd\u9020\u578b\u5462\u3002\u201d</p><p>\u201c\u662f\u5417\uff1f\u201d\u743c\u6069\u5012\u771f\u662f\u6ca1\u60f3\u5230\uff0c\u5728\u4ed6\u7684\u5370\u8c61\u91cc\uff0c\u51db\u81ea\u5df1\u7684\u53d1\u578b\u6c38\u8fdc\u90fd\u662f\u90a3\u5145\u6ee1\u50b2\u5a07\u610f\u5473\u7684\u53cc\u9a6c\u5c3e\uff0c\u4ece\u8ba4\u8bc6\u8d77\u5c31\u6ca1\u53d8\u8fc7\u5427\u3002\u5982\u679c\u771f\u5982\u6885\u83f2\u65af\u6240\u8a00\uff0c\u5979\u7684\u8fd9\u79cd\u4e13\u4e1a\u7d20\u8d28\u600e\u4e48\u4ece\u6ca1\u5728\u81ea\u5df1\u8eab\u4e0a\u5c55\u73b0\u51fa\u6765\u5462\u3002</p><p>\u201c\u55ef\uff0c\u5979\u6bcd\u4eb2\u4ece\u5c0f\u6559\u5979\u7684\uff0c\u5f88\u5389\u5bb3\u5427\u3002\u201d</p><p>\u201c\u90a3\u5979\u81ea\u5df1\u600e\u4e48\u2026\u2026\u201d</p><p>\u201c\u5979\u4ee5\u524d\u5f88\u559c\u6b22\u6362\u53d1\u578b\u7684\uff0c\u5dee\u4e0d\u591a\u5e73\u5747\u6bcf\u4e09\u5929\u6362\u4e00\u4e2a\uff0c\u201d\u6885\u83f2\u65af\u89e3\u91ca\uff0c\u201c\u4f46\u6362\u6765\u6362\u53bb\uff0c\u8fd8\u662f\u89c9\u5f97\u73b0\u5728\u8fd9\u6837\u6700\u6f02\u4eae\uff0c\u6240\u4ee5\u5c31\u61d2\u5f97\u6539\u4e86\u3002\u201d</p><p>\u201c\u90a3\u4f60\u548c\u5979\u5728\u4e00\u8d77\u7684\u65f6\u5019\uff0c\u6ca1\u6709\u5411\u5979\u5b66\u70b9\uff1f\u201d</p><p>\u201c\u5979\u662f\u6559\u8fc7\u6211\uff0c\u4f46\u6211\u5728\u8fd9\u65b9\u9762\u771f\u7684\u662f\u7b28\u5f97\u5f88\uff0c\u600e\u4e48\u5b66\u90fd\u5b66\u4e0d\u4f1a\uff0c\u5979\u4e00\u751f\u6c14\u5c31\u4e0d\u6559\u4e86\u2014\u2014\u5979\u8bf4\uff1a\u8fd8\u662f\u7b49\u5c06\u6765\u627e\u4e00\u4e2a\u8db3\u591f\u806a\u660e\u53c8\u8db3\u591f\u7231\u6211\u7684\u7537\u4eba\uff0c\u8ba9\u4ed6\u53bb\u5b66\u5427\u3002\u201d</p><p>\u201c\u2026\u2026\u8a00\u4e4b\u6709\u7406\u3002\u201d</p><p>\u5c11\u5973\u683c\u683c\u4e00\u7b11\uff0c\u91cd\u65b0\u68b3\u7406\u88ab\u743c\u6069\u5f04\u4e71\u7684\u5934\u53d1\uff0c\u5feb\u901f\u624e\u4e86\u4e2a\u5e72\u51c0\u5229\u843d\u7684\u9a6c\u5c3e\uff0c\u201c\u6211\u8981\u53bb\u4e00\u8d9f\u795e\u6bbf\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u4e2d\u5348\u4f30\u8ba1\u56de\u4e0d\u6765\uff0c\u4f60\u4e0d\u7528\u7b49\u6211\u4e86\u3002\u201d</p><p>\u201c\u6709\u4ec0\u4e48\u4e8b\u5417\uff1f\u8981\u8fd9\u4e48\u4e45\u3002\u201d</p><p>\u201c\u51b0\u8679\u7684\u4e8b\u60c5\uff0c\u6211\u5f97\u5411\u5927\u4e3b\u6559\u6c47\u62a5\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u795e\u8272\u95f4\u6d41\u9732\u51fa\u4e9b\u8bb8\u5fe7\u8651\uff0c\u201c\u4e00\u53ea\u592a\u53e4\u9f99\u5deb\u5996\u554a\u2026\u2026\u8fd8\u6709\u5361\u90a6\u5927\u53d4\u7684\u4e8b\u60c5\uff0c\u4e5f\u5f97\u5904\u7406\u3002\u201d</p><p>\u201c\u5361\u90a6\u5927\u53d4\uff1f\u201d\u743c\u6069\u83ab\u540d\u5176\u5999\uff0c\u201c\u8c01\u554a\uff1f\u201d</p><p>\u201c\u5c31\u662f\u6628\u5929\u88ab\u6211\u8bef\u6740\u7684\u90a3\u4e2a\u4eba\u3002\u201d</p><p>\u201c\u54e6\uff0c\u60f3\u8d77\u6765\u4e86\u3002\u53ef\u662f\u827e\u5f25\u8587\uff0c\u90a3\u4e0d\u7b97\u4f60\u7684\u8d23\u4efb\u5427\u3002\u201d</p><p>\u6628\u65e5\u5728\u86c7\u708e\u5c71\uff0c\u5438\u8840\u9b3c\u6697\u4e2d\u7aa5\u4f3a\u51c6\u5907\u5077\u88ad\uff0c\u88ab\u6885\u83f2\u65af\u5bdf\u89c9\uff0c\u4e00\u5251\u7834\u7a7a\u98de\u65a9\uff0c\u5374\u8bef\u6740\u4e86\u67d0\u4e2a\u6751\u6c11\u3002\u8fd9\u4ef6\u4e8b\u5f88\u8e4a\u8df7\uff0c\u743c\u6069\u4e8b\u540e\u63a8\u6d4b\u8d77\u6765\uff0c\u5341\u6709\u516b\u4e5d\u662f\u90a3\u4e2a\u5438\u8840\u9b3c\u7528\u4e86\u67d0\u79cd\u624b\u6cd5\uff0c\u7528\u6751\u6c11\u505a\u6321\u7bad\u724c\uff0c\u5bfc\u81f4\u8fd9\u79cd\u7ed3\u679c\u3002\u4f46\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u4eba\u786e\u5b9e\u662f\u6b7b\u5728\u6885\u83f2\u65af\u5251\u4e0b\uff0c\u8fc7\u5931\u4e5f\u597d\uff0c\u610f\u5916\u4e5f\u7f62\uff0c\u603b\u8981\u7ed9\u4e2a\u4ea4\u4ee3\u3002</p><p>\u6b63\u8bf4\u8bdd\u95f4\uff0c\u95e8\u88ab\u63a8\u5f00\u4e86\uff0c\u51db\u8e66\u8e66\u8df3\u8df3\u5730\u5f84\u76f4\u95ef\u8fdb\u6765\u3002\u201c\u65e9\uff0c\u827e\u5f25\u8587\uff0c\u743c\u6069\uff0c\u201d\u5979\u6d3b\u529b\u56db\u5c04\u5730\u6253\u62db\u547c\uff0c\u201c\u4eca\u5929\u5929\u6c14\u53ef\u771f\u597d\u554a\u2014\u2014\u827e\u5f25\u8587\uff0c\u4f60\u8981\u51fa\u95e8\uff1f\u201d</p><p>\u201c\u55ef\uff0c\u201d\u6885\u83f2\u65af\u6263\u597d\u886c\u886b\u7ebd\u6263\uff0c\u7a7f\u4e0a\u5916\u5957\uff0c\u201c\u6709\u4e8b\u4e48\uff1f\u201d</p><p>\u201c\u4f60\u5fd8\u4e86\u554a\uff0c\u4eca\u5929\u662f\u661f\u4e4b\u82b1\u7684\u5f00\u5e55\uff0c\u201d\u51db\u6485\u8d77\u5c0f\u5634\uff0c\u201c\u4e0d\u662f\u8bf4\u4eca\u5929\u966a\u6211\u4e00\u8d77\u53bb\u7684\u4e48\u3002\u201d</p><p>\u201c\u54e6\uff0c\u5dee\u70b9\u5fd8\u4e86\uff0c\u201d\u6885\u83f2\u65af\u63c9\u7740\u989d\u5934\uff0c\u201c\u62b1\u6b49\u62b1\u6b49\uff0c\u6211\u4eca\u5929\u5b9e\u5728\u6ca1\u7a7a\u3002\u4f60\u81ea\u5df1\u53bb\u5427\u3002\u201d</p><p>\u201c\u4e0d\u884c\u4e0d\u884c\uff0c\u6211\u4eec\u65e9\u5c31\u8bf4\u597d\u7684\uff0c\u201d\u51db\u6492\u5a07\uff0c\u201c\u6211\u4e00\u4e2a\u4eba\u51fa\u95e8\u4f1a\u8ff7\u8def\u7684\u5440\uff0c\u4e00\u5b9a\u8981\u6709\u4eba\u966a\u7740\u624d\u884c\u3002\u201d</p><p>\u201c\u53ef\u662f\u6211\u771f\u7684\u6709\u4e8b\u2014\u2014\u8fd9\u6837\u5427\uff0c\u8ba9\u4ed6\u966a\u4f60\u53bb\u597d\u4e86\u3002\u201d</p><p>\u201c\u4e5f\u884c\u3002\u201d</p><p>\u4e09\u8a00\u4e24\u8bed\u4e4b\u95f4\uff0c\u743c\u6069\u7684\u547d\u8fd0\u4fbf\u88ab\u51b3\u5b9a\uff0c\u800c\u5f53\u4ed6\u63d0\u51fa\u4e25\u6b63\u6297\u8bae\u65f6\uff0c\u5374\u5f97\u5230\u4e86\u76f4\u622a\u4e86\u5f53\u7684\u53cd\u9a73\u3002\u201c\u966a\u4f34\u5973\u53cb\uff0c\u8fd9\u662f\u7537\u4eba\u5e94\u5c3d\u7684\u4e49\u52a1\uff0c\u6709\u4ec0\u4e48\u597d\u62b1\u6028\u7684\u3002\u201d\u4e24\u4f4d\u5c11\u5973\u5982\u662f\u8bf4\u3002</p><p>\u201c\u4f46\u662f\u6211\u4e5f\u9700\u8981\u6709\u79c1\u4eba\u65f6\u95f4\u554a\uff0c\u201d\u743c\u6069\u7533\u8fa9\uff0c\u201c\u7537\u4eba\u4e0d\u4f46\u8981\u966a\u5973\u53cb\uff0c\u8fd8\u8981\u5fd9\u4e8b\u4e1a\u5462\u3002\u201d</p><p>\u201c\u4f60\u7684\u6240\u8c13\u4e8b\u4e1a\uff0c\u96be\u9053\u4e0d\u5c31\u662f\u52fe\u642d\u5973\u5b69\u5b50\u5417\uff1f\u201d</p><p>\u201c\u2026\u2026\u5582\uff0c\u51db\uff0c\u5927\u5bb6\u719f\u5f52\u719f\uff0c\u4f60\u4e71\u8bf4\u6211\u4e00\u6837\u544a\u4f60\u8bfd\u8c24\uff01\u201d</p><p>\u201c\u90a3\u4f60\u7684\u4e8b\u4e1a\u662f\u4ec0\u4e48\uff1f\u8bf4\u6765\u542c\u542c\uff1f\u201d</p><p>\u201c\u6211\u7684\u4e8b\u4e1a\u5c31\u662f\u2014\u2014\u8ba9\u4e16\u754c\u4e0a\u6240\u6709\u7f8e\u4e3d\u7684\u5973\u5b69\u5b50\u4eec\u90fd\u83b7\u5f97\u5e78\u798f\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\u5bb9\u6613\uff0c\u201d\u51db\u8bf4\uff0c\u201c\u4f60\u53ea\u8981\u8d76\u5feb\u81ea\u6740\u5c31\u53ef\u4ee5\u529e\u5230\u4e86\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u73a9\u7b11\u5f52\u73a9\u7b11\uff0c\u743c\u6069\u786e\u5b9e\u8fd8\u6ca1\u7a7a\u95f2\u5230\u65f6\u95f4\u591a\u5f97\u7528\u4e0d\u6389\u7684\u5730\u6b65\uff0c\u4ed6\u672c\u62df\u662f\u770b\u770b\u4e66\uff0c\u6e29\u4e60\u9b54\u6cd5\uff0c\u628a\u6700\u8fd1\u65b0\u5b66\u7684\u51e0\u4e2a\u6cd5\u672f\u5f3a\u5316\u8bad\u7ec3\u4e00\u4e0b\uff0c\u7136\u800c\u9762\u5bf9\u4e24\u540d\u7edd\u8272\u5c11\u5973\u7684\u62dc\u6258\u6073\u6c42\uff0c\u80fd\u591f\u786c\u4e0b\u5fc3\u6765\u62d2\u7edd\u7684\u7537\u4eba\u5b9e\u5728\u4e0d\u591a\u2014\u2014\u6216\u8bb8\u6709\uff0c\u4f46\u743c\u6069\u5e76\u975e\u5176\u4e2d\u4e4b\u4e00\u3002\u6240\u4ee5\u6700\u540e\u7684\u7ed3\u679c\uff0c\u662f\u743c\u6069\u53ea\u597d\u7b54\u5e94\u4e0b\u6765\uff0c\u966a\u51db\u53bb\u53c2\u52a0\u90a3\u4e2a\u4ec0\u4e48\u661f\u4e4b\u82b1\u7684\u5f00\u5e55\u5f0f\u3002</p><p>\u201c\u8d70\u5566\u8d70\u5566\uff0c\u522b\u90a3\u4e48\u65e0\u7cbe\u6253\u91c7\u7684\uff0c\u201d\u51db\u62c9\u7740\u4ed6\u7684\u624b\uff0c\u8feb\u4e0d\u53ca\u5f85\u7684\u6837\u5b50\uff0c\u201c\u80fd\u770b\u5230\u5f88\u591a\u5f88\u591a\u6f02\u4eae\u5973\u5b69\u5b50\u5462\uff0c\u4f60\u6700\u559c\u6b22\u4e86\u3002\u201d</p><p>\u201c\u5c0f\u5fc3\u70b9\uff0c\u51db\uff0c\u201d\u6885\u83f2\u65af\u63d0\u9192\u597d\u53cb\uff0c\u201c\u4ed6\u624b\u81c2\u53d7\u4f24\u4e86\uff0c\u8fd8\u6ca1\u75ca\u6108\u5462\u3002\u201d</p><p>\u201c\u54e6\uff0c\u77e5\u9053\u4e86\u3002\u201d</p><p>\u6885\u83f2\u65af\u6709\u4e8b\u5728\u8eab\uff0c\u5148\u884c\u79bb\u5f00\uff0c\u743c\u6069\u5219\u521a\u521a\u8d77\u5e8a\uff0c\u82b1\u4e86\u534a\u5c0f\u65f6\u624d\u6d17\u6f31\u5b8c\u6bd5\uff0c\u548c\u65e9\u5df2\u7ecf\u7b49\u5f97\u4e0d\u8010\u70e6\u7684\u51db\u51fa\u95e8\u3002\u5929\u6c14\u786e\u5b9e\u5f88\u597d\uff0c\u707f\u70c2\u7684\u9633\u5149\u3001\u548c\u7166\u7684\u5fae\u98ce\uff0c\u539f\u672c\u8fd8\u6709\u4e9b\u90c1\u7ed3\u7684\u5fc3\u60c5\u4e00\u4e0b\u5b50\u5c31\u53d8\u5f97\u8212\u7545\u8d77\u6765\u3002\u201c\u743c\u6069\uff0c\u4f60\u6628\u665a\u5bf9\u827e\u5f25\u8587\u505a\u4ec0\u4e48\u4e86\u554a\u3002\u201d\u8d70\u7740\u8d70\u7740\uff0c\u51db\u7a81\u7136\u95ee\u3002</p><p>\u201c\u4ec0\u4e48\u505a\u4ec0\u4e48\u4e86\uff1f\u201d\u743c\u6069\u83ab\u540d\u5176\u5999\u3002</p><p>\u201c\u603b\u89c9\u5f97\u5979\u4eca\u5929\u6709\u70b9\u2026\u2026\u602a\u602a\u7684\uff0c\u201d\u51db\u504f\u7740\u8111\u888b\uff0c\u601d\u7d22\u7740\uff0c\u201c\u6c14\u8d28\u8ddf\u5e73\u65f6\u6709\u70b9\u4e0d\u592a\u4e00\u6837\uff0c\u50cf\u662f\u6709\u4ec0\u4e48\u5730\u65b9\u6539\u53d8\u4e86\uff0c\u4f46\u53c8\u8bf4\u4e0d\u592a\u6e05\u695a\u3002\u201d</p><p>\u201c\u6211\u6ca1\u89c9\u5f97\u554a\uff0c\u662f\u4f60\u7684\u9519\u89c9\u5427\u3002\u201d</p><p>\u201c\u4e5f\u8bb8\u3002\u201d</p><p>\u743c\u6069\u548c\u51db\u8d70\u5230\u9547\u4e2d\u5fc3\u5e7f\u573a\uff0c\u53d1\u73b0\u90a3\u91cc\u5df2\u7ecf\u662f\u4eba\u5c71\u4eba\u6d77\uff0c\u70ed\u95f9\u975e\u51e1\uff0c\u653e\u773c\u671b\u53bb\uff0c\u904d\u5730\u90fd\u662f\u59ff\u5bb9\u4e0d\u4fd7\u7684\u7f8e\u8c8c\u5973\u5b50\uff0c\u6216\u8273\u4e3d\uff0c\u6216\u4fcf\u5a9a\uff0c\u6216\u9ad8\u6311\uff0c\u6216\u5a07\u5c0f\uff0c\u6216\u6210\u719f\uff0c\u6216\u9752\u6da9\uff0c\u4e89\u5947\u6597\u598d\uff0c\u5404\u5177\u98ce\u5473\uff0c\u786e\u5b9e\u4ee4\u4eba\u8d4f\u5fc3\u60a6\u76ee\uff0c\u53ea\u89c9\u4eff\u4f5b\u7f6e\u8eab\u5929\u5802\u3002</p><p>\u201c\u539f\u6765\u4e00\u4e2a\u5c0f\u5c0f\u7684\u5854\u745f\u8c37\uff0c\u5c45\u7136\u6709\u8fd9\u4e48\u591a\u7f8e\u4eba\u554a\u3002\u201d\u743c\u6069\u8d5e\u53f9\u4e4b\u4f59\uff0c\u4e0d\u514d\u4e5f\u6709\u4e9b\u7591\u60d1\u3002</p><p>\u201c\u7b28\u86cb\uff0c\u8fd9\u600e\u4e48\u53ef\u80fd\u554a\uff0c\u201d\u51db\u767d\u4e86\u4ed6\u4e00\u773c\uff0c\u201c\u8fd9\u4e9b\u4eba\u4e2d\u81f3\u5c11\u6709\u516b\u6210\u90fd\u662f\u4ece\u5916\u5730\u6765\u7684\u3002\u201d</p><p>\u539f\u6765\u5728\u4e00\u5f00\u59cb\uff0c\u201c\u661f\u4e4b\u82b1\u201d\u786e\u5b9e\u53ea\u662f\u5854\u745f\u8c37\u7684\u5185\u90e8\u6d3b\u52a8\uff0c\u53c2\u8d5b\u8005\u90fd\u662f\u672c\u5730\u4eba\uff0c\u89c4\u6a21\u975e\u5e38\u5c0f\uff0c\u800c\u4e14\u4e5f\u4e0d\u5b9a\u671f\uff1b\u4f46\u968f\u7740\u65f6\u95f4\u63a8\u79fb\uff0c\u540d\u58f0\u8d8a\u6765\u8d8a\u54cd\uff0c\u9010\u6e10\u5c31\u6709\u5916\u5730\u4eba\u4e5f\u6765\u6155\u540d\u53c2\u52a0\u3002\u5728\u6709\u5fc3\u4eba\u7684\u63a8\u52a8\u4e4b\u4e0b\uff0c\u6700\u7ec8\u5c31\u53d8\u6210\u4e86\u6574\u4e2a\u8c37\u5730\u7684\u5927\u578b\u8282\u65e5\u5e86\u5178\u4e4b\u4e00\u3002\u6bcf\u5e74\u5230\u8fd9\u4e2a\u65f6\u5019\uff0c\u6574\u4e2a\u8c37\u5730\u7684\u5973\u5b50\u4eec\u90fd\u4f1a\u4e91\u96c6\u4e8e\u6b64\uff0c\u4e89\u593a\u6700\u7f8e\u7684\u5934\u8854\uff0c\u5f71\u54cd\u529b\u53ef\u6bd4\u743c\u6069\u6240\u60f3\u8c61\u5f97\u8981\u5927\u591a\u4e86\u3002</p><p>\u201c\u4f46\u4f60\u4e5f\u4e0d\u662f\u8c37\u5730\u4eba\u554a\uff0c\u53ef\u4ee5\u53c2\u8d5b\u5417\uff1f\u201d\u743c\u6069\u95ee\u51db\u3002</p><p>\u201c\u53ef\u4ee5\u554a\uff0c\u6ca1\u9650\u5236\u7684\uff0c\u201d\u51db\u8bf4\uff0c\u201c\u53ea\u8981\u662f\u4eba\u5c31\u884c\uff0c\u7537\u4eba\u90fd\u53ef\u4ee5\u3002\u201d</p><p>\u201c\u2026\u2026\u4e0d\u4f1a\u5427\u3002\u201d</p><p>\u201c\u771f\u7684\uff0c\u827e\u5f25\u8587\u8bf4\u8fc7\uff0c\u5386\u53f2\u4e0a\u66fe\u7ecf\u6709\u4e00\u5c4a\uff0c\u6700\u540e\u7684\u51a0\u519b\u5c31\u662f\u4e2a\u7537\u4eba\u3002\u5f53\u65f6\u4ed6\u7a7f\u5973\u88c5\u4e0a\u573a\uff0c\u4e00\u9732\u9762\u5c31\u8273\u538b\u7fa4\u82b3\uff0c\u5149\u7167\u56db\u5ea7\uff0c\u628a\u8bc4\u59d4\u548c\u89c2\u4f17\u90fd\u60ca\u5446\u4e86\u2014\u2014\u54a6\uff0c\u743c\u6069\uff0c\u201d\u51db\u8bf4\u7740\u8bf4\u7740\uff0c\u7a81\u7136\u987f\u4f4f\uff0c\u7528\u4e00\u79cd\u5947\u602a\u7684\u773c\u795e\u6253\u91cf\u7740\u8eab\u65c1\u7684\u7537\u53cb\uff0c\u201c\u4f60\u5176\u5b9e\u4e5f\u5f88\u6709\u6f5c\u8d28\u554a\uff0c\u8981\u4e0d\u8981\u548c\u6211\u4e00\u8d77\u62a5\u540d\u2026\u2026\u201d</p><p>\u201c\u4f11\u60f3\uff01\u201d</p><p>\u5bdf\u89c9\u5230\u5c0f\u5973\u5deb\u7684\u90aa\u6076\u610f\u56fe\uff0c\u743c\u6069\u7acb\u523b\u65a9\u9489\u622a\u94c1\u5730\u8868\u793a\u62d2\u7edd\uff0c\u8fd9\u8ba9\u51db\u9887\u4e3a\u5931\u671b\u3002\u201c\u7b97\u5566\uff0c\u201d\u5979\u53f9\u6c14\uff0c\u201c\u4e0d\u5f3a\u6c42\uff0c\u4ee5\u4f60\u7684\u5ba1\u7f8e\u5c42\u6b21\uff0c\u786e\u5b9e\u65e0\u6cd5\u7406\u89e3\u8fd9\u4e48\u9ad8\u5c1a\u7684\u7f8e\u5b66\u8ffd\u6c42\u3002\u201d</p><p>\u201c\u2026\u2026\u9ad8\u5c1a\u4f60\u4e2a\u9b3c\u3002\u201d</p><p>\u4e24\u4eba\u8bf4\u8bdd\u58f0\u97f3\u5e76\u4e0d\u54cd\uff0c\u6240\u5904\u7684\u4f4d\u7f6e\u4e5f\u4e0d\u663e\u773c\uff0c\u751a\u81f3\u53ef\u4ee5\u8bf4\u504f\u50fb\uff1b\u4f46\u4ee5\u51db\u7684\u7edd\u8272\u59ff\u5bb9\uff0c\u5373\u4fbf\u5728\u4e00\u7fa4\u7f8e\u4eba\u4e2d\u4f9d\u65e7\u662f\u90a3\u6837\u5149\u5f69\u6d41\u6ea2\uff0c\u5f15\u4eba\u6ce8\u76ee\u3002\u5f88\u5feb\uff0c\u8d8a\u6765\u8d8a\u591a\u7684\u4eba\u6ce8\u610f\u5230\u5979\u2014\u2014\u987a\u4fbf\u770b\u89c1\u743c\u6069\uff0c\u7136\u540e\u7ec8\u4e8e\u6709\u672c\u5730\u9547\u6c11\u8ba4\u51fa\u4e86\u4ed6\u4eec\u3002</p><p>\u201c\u54c7\uff0c\u90a3\u4f4d\u5c0f\u59d0\u597d\u6f02\u4eae\u3002\u201d</p><p>\u201c\u662f\u5f88\u6f02\u4eae\u2026\u2026\u603b\u89c9\u5f97\u6709\u70b9\u773c\u719f\u554a\uff0c\u50cf\u662f\u4ee5\u524d\u89c1\u8fc7\u4f3c\u7684\u3002\u201d</p><p>\u201c\u5f53\u7136\u89c1\u8fc7\uff0c\u5979\u662f\u6885\u83f2\u65af\u5c0f\u59d0\u7684\u597d\u670b\u53cb\uff0c\u524d\u51e0\u5929\u4e00\u8d77\u4ece\u5916\u9762\u56de\u6765\u7684\uff0c\u597d\u50cf\u53eb\u4ec0\u4e48\u6765\u7740\uff0c\u51db\u5c0f\u59d0\uff1f\u201d</p><p>\u201c\u5bf9\u5bf9\uff0c\u4f60\u4e00\u8bf4\u6211\u60f3\u8d77\u6765\u4e86\uff0c\u5c31\u662f\u51db\u5c0f\u59d0\u2014\u2014\u90a3\u5979\u65c1\u8fb9\u7684\u7537\u4eba\u662f\u8c01\u554a\uff1f\u201d</p><p>\u201c\u597d\u50cf\u4e5f\u6709\u70b9\u773c\u719f\u2026\u2026\u597d\u50cf\u662f\u6885\u83f2\u65af\u5c0f\u59d0\u7684\u7537\u53cb\uff1f\u201d</p><p>\u201c\u6885\u83f2\u65af\u5c0f\u59d0\u7684\u7537\u53cb\u548c\u670b\u53cb\uff0c\u4e3a\u4ec0\u4e48\u8fd9\u4e48\u795e\u6001\u4eb2\u5bc6\uff1f\u8fd8\u624b\u7275\u7740\u624b\uff0c\u592a\u8fc7\u5206\u4e86\u5427\uff01\u201d</p><p>\u201c\u662f\u554a\u662f\u554a\uff0c\u811a\u8e29\u4e24\u6761\u8239\u7684\u5bb6\u4f19\uff0c\u6700\u53ef\u6076\u4e86\uff01\u201d</p><p>\u201c\u786e\u5b9e\u53ef\u6076\u2026\u2026\u53ef\u662f\u6211\u4e5f\u60f3\u53d8\u6210\u8fd9\u6837\u53ef\u6076\u7684\u4eba\u554a\uff01\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u8fd9\u4e9b\u7a83\u7a83\u79c1\u8bed\u8d8a\u6765\u8d8a\u591a\uff0c\u968f\u98ce\u98d8\u5165\u743c\u6069\u548c\u51db\u7684\u8033\u4e2d\uff0c\u7136\u800c\u4e24\u4eba\u90fd\u6ca1\u4ec0\u4e48\u53cd\u5e94\uff0c\u524d\u8005\u662f\u8138\u76ae\u539a\uff0c\u53ea\u5f53\u6ca1\u542c\u89c1\uff1b\u540e\u8005\u5219\u662f\u601d\u7ef4\u903b\u8f91\u8fe5\u5f02\u5e38\u4eba\uff0c\u538b\u6839\u4e0d\u5f53\u56de\u4e8b\uff0c\u6216\u8005\u8fd8\u89c9\u5f97\u633a\u6709\u8da3\u3002</p><p>\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u661f\u4e4b\u82b1\u7684\u5f00\u5e55\u5f0f\u5df2\u7ecf\u5f00\u59cb\uff0c\u4e3b\u6301\u4eba\u5c45\u7136\u662f\u90a3\u4f4d\u91d1\u53d1\u5723\u6b66\u58eb\u5e0c\u6b27\uff0c\u6b4c\u66e6\u96c5\u7684\u54e5\u54e5\uff0c\u5012\u662f\u8ba9\u743c\u6069\u9887\u4e3a\u610f\u5916\u3002\u4ed6\u5012\u4e5f\u6ca1\u8bf4\u4ec0\u4e48\u5e9f\u8bdd\uff0c\u7b80\u77ed\u81f4\u8f9e\u4e4b\u540e\uff0c\u4fbf\u662f\u6240\u6709\u53c2\u8d5b\u8005\u62a5\u540d\uff0c\u4f9d\u6b21\u4e0a\u53f0\u4eae\u76f8\uff0c\u603b\u4eba\u6570\u5927\u7ea6\u8fd1\u767e\u540d\uff0c\u770b\u5f97\u4eba\u773c\u82b1\u7f2d\u4e71\u3002\u6574\u4e2a\u8fc7\u7a0b\u6301\u7eed\u4e09\u5929\uff0c\u7b2c\u4e00\u5929\u662f\u5feb\u901f\u6dd8\u6c70\u5236\uff0c\u8981\u51b3\u51fa\u5341\u516d\u5f3a\uff0c\u7b2c\u4e8c\u5929\u5219\u8981\u51b3\u51fa\u56db\u5f3a\u4e2d\u7684\u4e09\u4e2a\u5e2d\u4f4d\uff0c\u7b2c\u4e09\u5929\u662f\u6700\u7ec8\u51b3\u8d5b\u3002\u6885\u83f2\u65af\u4f5c\u4e3a\u4e0a\u5c4a\u51a0\u519b\uff0c\u76f4\u63a5\u664b\u7ea7\u56db\u5f3a\uff0c\u9884\u5148\u5c31\u5360\u4e86\u4e00\u4e2a\u5e2d\u4f4d\uff0c\u6240\u4ee5\u4eca\u5929\u6839\u672c\u5c31\u6ca1\u6765\u3002\u6bd4\u8d5b\u5185\u5bb9\u5f88\u7b80\u5355\uff0c\u5305\u62ec\u8d70\u79c0\u3001\u624d\u827a\u8868\u6f14\u4e4b\u7c7b\uff0c\u603b\u4f53\u800c\u8a00\u5176\u5b9e\u548c\u5730\u7403\u4e0a\u7684\u9009\u7f8e\u6bd4\u8d5b\u5f88\u8fd1\u4f3c\uff0c\u4e5f\u6ca1\u4ec0\u4e48\u7279\u522b\u7684\u2014\u2014\u800c\u4e14\u5927\u5bb6\u7684\u8863\u670d\u90fd\u7a7f\u5f97\u6bd4\u8f83\u591a\uff0c\u8ba9\u743c\u6069\u9887\u89c9\u5931\u671b\u3002</p><p>\u51db\u6392\u5728\u7b2c\u4e8c\u5341\u4e09\u4f4d\uff0c\u4e00\u51fa\u573a\u4fbf\u5f15\u8d77\u8f70\u52a8\uff0c\u6beb\u65e0\u60ac\u5ff5\u5730\u6210\u529f\u664b\u7ea7\u3002\u5176\u4ed6\u53c2\u8d5b\u8005\u867d\u7136\u4e5f\u90fd\u662f\u5bb9\u8c8c\u79c0\u4e3d\u7684\u5973\u5b50\uff0c\u4f46\u548c\u5979\u7ad9\u5728\u4e00\u8d77\uff0c\u987f\u65f6\u4fbf\u663e\u5f97\u9eef\u6de1\u4e86\u8bb8\u591a\u3002\u743c\u6069\u770b\u4e86\u534a\u664c\uff0c\u9887\u89c9\u65e0\u804a\uff0c\u6b63\u597d\u51db\u4e5f\u5df2\u7ecf\u4e0b\u573a\uff0c\u4fbf\u51c6\u5907\u548c\u5979\u56de\u53bb\u3002\u6b64\u65f6\u8f6e\u5230\u6392\u5728\u51db\u4e4b\u540e\u7684\u7b2c\u4e8c\u5341\u56db\u4f4d\u53c2\u8d5b\u8005\u4e0a\u573a\uff0c\u743c\u6069\u968f\u610f\u77a5\u4e86\u4e00\u773c\u2014\u2014\u7136\u800c\u8fd9\u4e00\u77a5\u4e4b\u4e0b\uff0c\u76ee\u5149\u4fbf\u79fb\u4e0d\u5f00\u4e86\u3002</p><p>\u90a3\u662f\u4e00\u4f4d\u9ad8\u6311\u3001\u7ea4\u7ec6\u7684\u9ed1\u53d1\u5973\u5b50\uff0c\u7565\u663e\u5c16\u5c16\u7684\u8033\u6735\u8bf4\u660e\u5979\u7684\u7cbe\u7075\u8840\u7edf\uff0c\u8138\u8272\u82cd\u767d\uff0c\u5634\u5507\u4e5f\u6ca1\u4ec0\u4e48\u8840\u8272\uff0c\u4eff\u4f5b\u5927\u75c5\u521d\u6108\uff0c\u4f46\u5374\u81ea\u6709\u4e00\u79cd\u8bf4\u4e0d\u51fa\u7684\u5947\u5f02\u795e\u91c7\uff0c\u4eff\u4f5b\u6708\u5149\u4e0b\u7684\u6df1\u9083\u9634\u5f71\uff0c\u4eff\u4f5b\u5e7d\u6697\u4e2d\u7684\u79d8\u5bc6\u6df1\u6e0a\uff0c\u900f\u7740\u5371\u9669\u7684\u8bf1\u60d1\uff0c\u8ba9\u4eba\u5fcd\u4e0d\u4f4f\u88ab\u5438\u5f15\uff0c\u60f3\u8981\u9760\u8fd1\uff0c\u60f3\u8981\u4e00\u63a2\u7a76\u7adf\u3002\u800c\u6700\u7279\u522b\u7684\uff0c\u662f\u5979\u7684\u773c\u775b\uff0c\u59cb\u7ec8\u90fd\u662f\u7d27\u95ed\u7740\uff0c\u4ece\u4e0d\u7741\u5f00\u3002</p><p>\u5979\u662f\u4e2a\u76f2\u4eba\u3002</p></article>"}}}]);
