(window.webpackJsonp=window.webpackJsonp||[]).push([[526],{549:function(p){p.exports={chapterTitle:null,title:"\u7b2c526\u7ae0 \u4eac\u57ce\u7ea8\u7ed4",content:"<article><h2>\u7b2c526\u7ae0 \u4eac\u57ce\u7ea8\u7ed4</h2><p>\u3000\u3000\u59da\u575a\u4e0e\u8d75\u9e4f\u7ed9\u90a3\u4e2a\u5916\u8c8c\u6e05\u7eaf\u3001\u51fa\u624b\u91d1\u5361\u6ee1\u6293\u7684\u4e1c\u822a\u5973\u5b69\u5c0f\u4f73\u6293\u51fa\u53bb\u91c7\u8d2d\u9152\u6c34\u56de\u6765\uff0c\u53d1\u73b0\u5305\u53a2\u91cc\u591a\u4e86\u51e0\u4e2a\u4eba\u3002</p><p>\u3000\u3000\u59da\u6587\u76db\u4e0e\u4ed6\u7684\u670b\u53cb\u8fd8\u6709\u4e24\u4e2a\u5317\u5916\u7684\u6f02\u4eae\u5973\u5b69\u90fd\u79fb\u5230\u4ed6\u4eec\u5305\u53a2\u91cc\u6765\u3002</p><p>\u3000\u3000\u4f55\u8c13\u86c7\u9f20\u4e00\u7a9d\u3001\u4eba\u4ee5\u7fa4\u5206\uff1f</p><p>\u3000\u3000\u59da\u6587\u76db\u5e74\u7eaa\u624d\u4e8c\u5341\u4e03\u5c81\uff0c\u4ece\u5b66\u6821\u6bd5\u4e1a\u8fd8\u6ca1\u6709\u51e0\u5e74\uff0c\u884c\u653f\u7ea7\u522b\u5df2\u7ecf\u662f\u90ae\u7535\u90e8\u7535\u4fe1\u653f\u52a1\u53f8\u7684\u526f\u5904\u7ea7\u5b98\u5458\uff0c\u4ed6\u7684\u670b\u53cb\u9646\u5065\u4e5f\u662f\u8ba1\u59d4\u9ad8\u79d1\u6280\u53d1\u5c55\u53f8\u7684\u526f\u5904\u7ea7\u5b98\u5458\uff0c\u867d\u7136\u624b\u91cc\u90fd\u8fd8\u6ca1\u6709\u591a\u5927\u7684\u5b9e\u6743\uff0c\u8fd8\u5728\u90e8\u59d4\u91cc\u517b\u8d44\u5386\uff0c\u5c06\u4e24\u4e2a\u5317\u5916\u7684\u5973\u5b69\u5b50\u54c4\u5f97\u5b09\u7b11\u6012\u9a82\u98ce\u9a9a\u65e0\u7aef\u5374\u662f\u8f7b\u800c\u6613\u4e3e\u3002</p><p>\u3000\u3000\u8fd9\u4e24\u4e2a\u5317\u5916\u7684\u5973\u5b69\u5b50\uff0c\u8fd8\u662f\u59da\u6587\u76db\u5728\u82e6\u7b49\u6613\u99a8\u76f8\u4eb2\u672a\u679c\uff0c\u4e0e\u6613\u99a8\u7236\u6bcd\u5403\u8fc7\u665a\u996d\u4e4b\u540e\u7ea6\u9646\u5065\u5230\u9152\u5427\u559d\u9152\u9493\u51fa\u6765\u5531\u6b4c\u7684\u3002\u8981\u4e0d\u662f\u7ed9\u6613\u99a8\u649e\u5230\uff0c\u5531\u4e00\u4f1a\u513f\u6b4c\u518d\u719f\u6089\u719f\u6089\uff0c\u5c31\u8981\u62c9\u7740\u8fd9\u4e24\u4e2a\u5973\u5b69\u5b50\u53bb\u5bbe\u9986\u5f00\u623f\u95f4\u4e86\u3002\u59da\u6587\u76db\u5012\u4e0d\u4f1a\u5c06\u8fd9\u4e2a\u5ff5\u5934\u8d64\u88f8\u88f8\u7684\u5766\u9732\u51fa\u6765\uff0c\u800c\u662f\u7ed9\u6613\u99a8\u3001\u4f55\u6653\u96ef\u65e0\u60c5\u7684\u63ed\u7834\uff0c\u90a3\u4e24\u4e2a\u5317\u5916\u7684\u5973\u5b69\u5b50\u5c31\u5a07\u55d4\u563b\u7b11\u7684\u9a82\u4e86\u59da\u6587\u76db\u4e0e\u4ed6\u670b\u53cb\u51e0\u53e5\uff0c\u5012\u4e5f\u6ca1\u6709\u6389\u5934\u5c31\u8d70\u3002</p><p>\u3000\u3000\u8fd9\u79cd\u4f60\u60c5\u6211\u613f\u7684\u4e8b\u60c5\uff0c\u5f20\u606a\u4ece\u6765\u90fd\u4e0d\u4f1a\u9119\u89c6\u7684\uff0c\u4ed6\u4ee5\u524d\u7684\u54c1\u6027\u672a\u5fc5\u89c1\u5f97\u9ad8\u5c1a\u591a\u5c11\uff0c\u73b0\u5728\u4e5f\u672a\u5fc5\u89c1\u5f97\u5c31\u6d17\u5fc3\u9769\u9762\u4e86\u3002</p><p>\u3000\u3000\u6613\u99a8\u4e0e\u59da\u6587\u76db\u4e4b\u95f4\u662f\u4e0d\u5b58\u5728\u60c5\u7f18\u7684\uff0c\u6240\u8c13\u7684\u76f8\u4eb2\u90fd\u662f\u53d7\u53cc\u65b9\u5bb6\u957f\u631f\u8feb\u3001\u53c8\u65e0\u529b\u53cd\u6297\u7684\u7ed3\u679c\u3002</p><p>\u3000\u3000\u59da\u6587\u76db\u3001\u9646\u5065\u5373\u4f7f\u4e0d\u660e\u8bf4\uff0c\u5f20\u606a\u4e5f\u80fd\u770b\u51fa\u4ed6\u4eec\u51fa\u8eab\u9ad8\u5e72\u5bb6\u5ead\uff0c\u8fde\u7740\u6613\u99a8\u3001\u4f55\u6653\u96ef\u4ee5\u53ca\u76f8\u8c8c\u6e05\u5370\u7684\u5c0f\u4f73\uff0c\u5bb6\u5ead\u80cc\u666f\u90fd\u4e0d\u662f\u90a3\u4e48\u7b80\u5355\u3002</p><p>\u3000\u3000\u4e0e\u5730\u65b9\u4e0a\u7684\u8859\u5185\u4e56\u5f20\u8dcb\u6248\u4e0d\u540c\uff0c\u4eac\u57ce\u904d\u5730\u90fd\u662f\u738b\u5b50\u90e1\u4e3b\uff0c\u738b\u5b50\u90e1\u4e3b\u4e4b\u95f4\u8fd8\u5206\u6d3e\u7cfb\u6697\u6597\uff0c\u6240\u4ee5\u884c\u4e3a\u4e0a\u8981\u6536\u655b\u8bb8\u591a\uff0c\u8981\u8bf4\u4eac\u57ce\u6709\u8d35\u65cf\u7684\u8bdd\uff0c\u90a3\u4ed6\u4eec\u5c31\u662f\u7b2c\u4e8c\u3001\u7b2c\u4e09\u4ee3\u7684\u8d35\u65cf\u5b50\u5f1f\u3002</p><p>\u3000\u3000\u5e94\u8be5\u4e0e\u59da\u6587\u76db\u3001\u9646\u5065\u5df2\u7ecf\u8fdb\u5165\u793e\u4f1a\u5386\u7ec3\u7684\u51e0\u5e74\u4e5f\u6709\u5173\u7cfb\uff0c\u4ed6\u4eec\u4fe9\u4eba\u90fd\u5f88\u5065\u8c08\uff0c\u8fd9\u70b9\u8bf4\u662f\u5317\u4eac\u4eba\u7684\u7279\u70b9\u4e5f\u4e0d\u8fc7\uff0c\u4f46\u662f\u53e3\u98ce\u90fd\u5f88\u7d27\uff0c\u8c08\u8bdd\u91cc\u5f88\u5c11\u6d89\u53ca\u5230\u5f7c\u6b64\u7684\u5bb6\u5ead\u80cc\u666f\u3002\u6027\u683c\u4e5f\u7b97\u6e29\u548c\uff0c\u8fd9\u5f53\u7136\u4e0e\u5f20\u606a\u4e3e\u624b\u6295\u8db3\u4e4b\u95f4\u8868\u73b0\u51fa\u6765\u7684\u6c14\u5ea6\u4e5f\u6709\u5173\u7cfb\uff0c\u59da\u6587\u76db\u3001\u9646\u5065\u65e0\u6cd5\u5bf9\u5f20\u606a\u76db\u6c14\u51cc\u4eba\u3002\u50cf\u8d75\u9e4f\u3001\u59da\u575a\u8fdb\u6765\uff0c\u6c14\u5ea6\u5c31\u5dee\u4e86\u4e00\u4e9b\uff0c\u6c14\u5ea6\u8fd9\u4e1c\u897f\uff0c\u662f\u6700\u80fd\u4f53\u73b0\u4e00\u4e2a\u4eba\u8eab\u4efd\u4e0e\u793e\u4f1a\u5730\u4f4d\u7684\u8868\u73b0\u3002\u59da\u6587\u76db\u3001\u9646\u5065\u5373\u4f7f\u773c\u529b\u518d\u5dee\uff0c\u4e5f\u8981\u628a\u5f20\u606a\u5f52\u5165\u5230\u8859\u5185\u4e00\u7c7b\u4eba\u53bb\uff0c\u90a3\u57fa\u672c\u4e0a\u5c31\u8981\u5c5e\u4e8e\u540c\u7c7b\u4eba\u4e86\uff0c\u81ea\u7136\u5bb9\u6613\u4eb2\u8fd1\uff0c\u5f88\u5feb\u5c31\u70ed\u7edc\u8d77\u6765\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u8d75\u9e4f\u3001\u59da\u575a\u90fd\u8981\u7b97\u7231\u8fbe\u7535\u5b50\u7684\u9ad8\u7ea7\u804c\u5458\uff0c\u63d0\u5230\u7231\u8fbe\u7535\u5b50\uff0c\u9646\u5065\u6709\u5370\u8c61\uff1a\u201c\u554a\uff0c\u7231\u8fbe\u7535\u5b50\u8fd9\u6b21\u5dee\u70b9\u5c31\u662f\u6324\u51fa\u7535\u5b50\u4f01\u4e1a\u5341\u5f3a\u3002\u4f01\u4e1a\u5f88\u725b\u554a\uff0c\u542c\u8bf4\u7231\u8fbe\u7535\u5b50\u7684\u666e\u901a\u5458\u5de5\uff0c\u6bd4\u6211\u4eec\u90e8\u59d4\u7684\u5458\u5de5\u5de5\u8d44\u8fd8\u9ad8\uff0c\u8981\u4e0d\u4f60\u5e2e\u6211\u4ecb\u7ecd\u4ecb\u7ecd\uff0c\u6211\u53bb\u4f60\u4eec\u7231\u8fbe\u7535\u5b50\u6253\u5de5\u5f97\u4e86\uff1f\u201d</p><p>\u3000\u3000\u7231\u8fbe\u7535\u5b50\u789f\u673a\u4e1a\u52a1\u52c9\u5f3a\u4e0e\u8ba1\u59d4\u9ad8\u79d1\u6280\u672f\u53d1\u5c55\u53f8\u80fd\u626f\u4e0a\u5173\u7cfb\uff0c\u8981\u662f\u9646\u5065\u771f\u80fd\u653e\u4e0b\u4ed6\u7684\u5766\u8361\u5b98\u9014\uff0c\u5f20\u606a\u4e5f\u4e0d\u4f1a\u541d\u556c\u9ad8\u85aa\u7684\uff0c\u8fd9\u53ea\u662f\u5f00\u73a9\u7b11\uff0c\u6216\u8bb8\u9646\u5065\u672c\u4eba\u89c9\u5f97\u4eba\u5728\u90e8\u59d4\u4e0d\u591f\u81ea\u7531\uff0c\u867d\u8bf4\u4ed6\u5bb6\u5ead\u80cc\u666f\u8ba9\u4eba\u656c\u754f\uff0c\u4f46\u662f\u4e5f\u4e0d\u89c9\u5f97\u522b\u4eba\u5c31\u6bd4\u4ed6\u5dee\uff0c\u6bcf\u56de\u542c\u5230\u5730\u65b9\u4e0a\u7684\u8859\u5185\u4e3a\u975e\u4f5c\u6b79\u5c31\u5341\u5206\u7fa1\u6155\uff1b\u53ea\u4e0d\u8fc7\u4ed6\u672a\u6765\u7684\u4eba\u751f\u89c4\u5212\uff0c\u53c8\u6709\u591a\u5c11\u662f\u4ed6\u81ea\u5df1\u80fd\u505a\u5f97\u4e86\u4e3b\u7684\uff1f</p><p>\u3000\u3000\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u6d77\u5dde\u662f\u504f\u8f9f\u5730\u65b9\uff0c\u54ea\u6bd4\u5f97\u4e0a\u5728\u5317\u4eac\u5f53\u4eac\u57ce\u9614\u5c11\u6ecb\u6da6\uff1f\u4f60\u4eec\u8981\u4e0b\u53bb\uff0c\u4e5f\u8981\u7b49\u4e24\u4e09\u5e74\u5347\u5230\u53f8\u5c40\u7ea7\u5230\u5730\u65b9\u6302\u804c\u953b\u7ec3\u5f53\u4e2a\u5e02\u59d4\u526f\u4e66\u8bb0\u3001\u5e02\u957f\u4ec0\u4e48\u7684\uff0c\u6211\u5c31\u8981\u6765\u7fa1\u6155\u4f60\u4eec\u5a01\u98ce\u4e86\u3002\u201d</p><p>\u3000\u3000\u4e24\u4e09\u5e74\u71ac\u5230\u53f8\u5c40\u7ea7\uff0c\u81ea\u7136\u662f\u4e0d\u53ef\u80fd\uff0c\u5f20\u606a\u8fd8\u6ca1\u6709\u542c\u8bf4\u8fc7\u56fd\u5185\u6709\u4eba\u5728\u4e09\u5341\u5c81\u5de6\u53f3\u5347\u4e0a\u6b63\u5385\u7ea7\uff0c\u4e00\u822c\u8bf4\uff0c\u4ed6\u4eec\u5728\u90e8\u59d4\u5e73\u7a33\u517b\u7740\u8d44\u5386\uff0c\u56db\u5341\u5c81\u4e4b\u524d\u662f\u80fd\u8fbe\u5230\u8fd9\u4e2a\u76ee\u6807\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u4e48\u8bf4\u8bdd\uff0c\u5374\u60f9\u59da\u6587\u76db\u3001\u9646\u5065\u9ad8\u5174\u3002</p><p>\u3000\u3000\u59da\u6587\u76db\u7b11\u7740\u8bf4\uff1a\u201c\u54ea\u6709\u90a3\u4e48\u7b80\u5355\u7684\u4e8b\u60c5\uff1f\u90e8\u59d4\u91cc\uff0c\u4e00\u4e2a\u841d\u535c\u4e00\u4e2a\u5751\uff0c\u8fd9\u51e0\u5e74\u4e00\u76f4\u5728\u63d0\u5021\u673a\u6784\u7cbe\u51cf\uff0c\u56fd\u52a1\u9662\u4e3b\u4e8b\u7684\u90a3\u4f4d\u526f\u603b\u7406\uff0c\u8981\u4e0b\u72e0\u5fc3\u88c1\u64a4\u4e86\u51e0\u4e2a\u90e8\u95e8\uff0c\u90a3\u65f6\u841d\u535c\u6ca1\u5c11\uff0c\u5751\u5374\u5c11\u4e86\uff0c\u673a\u4f1a\u5c31\u66f4\u6e3a\u832b\u4e86\uff0c\u53f8\u5c40\u7ea7\u4e0d\u662f\u90a3\u4e48\u597d\u6df7\u7684\uff0c\u5012\u662f\u5730\u65b9\u6ecb\u6da6\uff0c\u8981\u6709\u5173\u7cfb\uff0c\u6211\u73b0\u5728\u5c31\u60f3\u5230\u5730\u65b9\u53bb\u2026\u2026\u201d</p><p>\u3000\u3000\u5b98\u804c\u5347\u8fc1\u662f\u6761\u8d8a\u8d70\u8d8a\u7a84\u7684\u5df7\u5b50\uff0c\u4ece\u526f\u5904\u7ea7\u5230\u53f8\u5c40\u7ea7\uff0c\u53ef\u4ee5\u8bf4\u662f\u8fd9\u6761\u7a84\u5df7\u5b50\u53d8\u5f62\u6700\u5927\u7684\u5587\u53ed\u53e3\uff0c\u4e0d\u6653\u5f97\u4f1a\u6709\u591a\u5c11\u4eba\u7ed9\u6324\u6389\u4e0b\u6765\u3002\u76f8\u5bf9\u8bf4\u6765\uff0c\u5730\u65b9\u6bd4\u90e8\u59d4\u8981\u5bbd\u677e\u4e00\u4e9b\uff0c\u6700\u4e3b\u8981\u7684\uff0c\u5728\u5730\u65b9\u505a\u5b98\u6bd4\u5728\u5929\u5b50\u811a\u4e0b\u517b\u8d44\u5386\u8981\u6ecb\u6da6\u5f97\u591a\u3002</p><p>\u3000\u3000\u59da\u575a\u53ea\u662f\u7231\u8fbe\u7535\u5b50\u5e02\u573a\u90e8\u4e0b\u5c5e\u7684\u5317\u4eac\u5206\u516c\u53f8\u7ecf\u7406\uff0c\u4e0d\u6e05\u695a\u9526\u6e56\u8981\u4e0a\u624b\u673a\u9879\u76ee\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u8d75\u9e4f\u4f5c\u4e3a\u82cf\u6d25\u4e1c\u7684\u52a9\u7406\uff0c\u662f\u82cf\u6d25\u4e1c\u6311\u9009\u51fa\u6765\u5927\u529b\u57f9\u517b\u7684\u4eba\uff0c\u5dee\u4e0d\u591a\u8fdb\u5165\u516c\u53f8\u51c6\u6838\u5fc3\u5c42\uff0c\u77e5\u9053\u4e0e\u90ae\u7535\u90e8\u7535\u4fe1\u653f\u52a1\u53f8\u4ee5\u53ca\u8ba1\u59d4\u9ad8\u79d1\u6280\u53d1\u5c55\u53f8\u641e\u597d\u5173\u7cfb\u6709\u5229\u4e8e\u9526\u6e56\u672a\u6765\u7684\u53d1\u5c55\u3002</p><p>\u3000\u3000\u867d\u8bf4\u59da\u6587\u76db\u3001\u9646\u5065\u5728\u90e8\u59d4\u91cc\u90fd\u6ca1\u6709\u4ec0\u4e48\u5b9e\u9645\u6743\u529b\uff0c\u4e0d\u8fc7\u6709\u5173\u7cfb\u603b\u6bd4\u6ca1\u5173\u7cfb\u597d\u3002\u5f20\u606a\u4e0d\u60f3\u5728\u5176\u4ed6\u5de5\u4f5c\u6ca1\u6709\u51c6\u5907\u59a5\u5f53\u7684\u60c5\u51b5\u4e0b\u5c31\u5728\u56fd\u5185\u6253\u8349\u60ca\u86c7\uff0c\u90a3\u6837\u4f1a\u53cd\u800c\u6dfb\u52a0\u989d\u5916\u7684\u963b\u529b\u3002\u5728\u4e0eTI\u8c08\u5224\u5f15\u8fdb\u624b\u673a\u4ea7\u54c1\u8bbe\u8ba1\u4e4b\u524d\uff0c\u5728\u6d88\u5316\u9526\u6e56\u65d7\u4e0b\u5b9e\u9a8c\u5ba4\u6d88\u5316\u76f8\u5173\u6280\u672f\u4e4b\u524d\uff0c\u5f20\u606a\u8fd8\u4e0d\u60f3\u6cc4\u6f0f\u9526\u6e56\u5c06\u4e0a\u624b\u673a\u9879\u76ee\u7684\u4fe1\u606f\uff0c\u77ed\u671f\u5c31\u4e0d\u4f1a\u76f4\u63a5\u4ee5\u9526\u6e56\u7684\u540d\u4e49\u5230\u90ae\u7535\u90e8\u53bb\u6d3b\u52a8\uff0c\u6b64\u65f6\u80fd\u4e0e\u59da\u6587\u76db\u3001\u9646\u5065\u8fc2\u56de\u6253\u597d\u5173\u7cfb\uff0c\u603b\u65b9\u4fbf\u65e5\u540e\u7684\u8fd0\u4f5c\u3002</p><p>\u3000\u3000\u5f20\u606a\u672a\u5fc5\u6709\u65f6\u95f4\u5728\u59da\u6587\u76db\u3001\u9646\u5065\u4e24\u4eba\u8eab\u4e0a\u4e0b\u5de5\u592b\uff0c\u7ed9\u8d75\u9e4f\u4e00\u4e2a\u4fe1\u53f7\uff0c\u8ba9\u4ed6\u6ce8\u610f\u6084\u65e0\u58f0\u606f\u7684\u505a\u597d\u516c\u5173\u5de5\u4f5c\uff0c\u82e5\u80fd\u901a\u8fc7\u59da\u6587\u76db\uff0c\u4e0d\u9732\u75d5\u8ff9\u7684\u4e0e\u7535\u4fe1\u653f\u52a1\u53f8\u7684\u4e3b\u8981\u5b98\u5458\u63a5\u89e6\u4e0a\uff0c\u90a3\u81ea\u7136\u662f\u6700\u597d\u7684\u7ed3\u679c\u3002</p><p>\u3000\u3000\u5728\u591c\u5e97\u5531\u6b4c\u5230\u51cc\u6668\u4e09\u70b9\u624d\u79bb\u5f00\uff0c\u4e58\u51fa\u79df\u8f66\u56de\u5bbe\u9986\uff0c\u59da\u575a\u5728\u5317\u4eac\u6709\u79df\u623f\u5b50\u4f4f\uff0c\u4ed6\u5c06\u5f20\u606a\u9001\u5230\u5bbe\u9986\uff0c\u5c31\u56de\u4f4f\u5904\u7761\u89c9\u53bb\u4e86\u3002</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u4e5f\u6ca1\u6709\u4f11\u606f\uff0c\u5728\u8ba8\u8bba\u8981\u5982\u4f55\u5728\u5317\u4eac\u8425\u9020\u6d69\u5927\u7684\u58f0\u52bf\u5c06\u674e\u8fdc\u6e56\u7684\u6ce8\u610f\u529b\u4ece\u5236\u6d46\u9879\u76ee\u4e0a\u5438\u5f15\u8fc7\u6765\u3002\u5f20\u606a\u8ddf\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u8bf4\u8d77KTV\u8ba4\u8bc6\u7684\u4e24\u540d\u9ad8\u5e72\u5b50\u5f1f\uff0c\u53c8\u5bf9\u82cf\u6d25\u4e1c\u7684\u79d8\u4e66\u8d75\u9e4f\u8bf4\uff1a\u201c\u67e5\u4e00\u4e0b\u6613\u99a8\u3001\u4f55\u6653\u96ef\u3001\u59da\u6587\u76db\u3001\u9646\u5065\u7684\u8eab\u4efd\u80cc\u666f\uff0c\u7279\u522b\u662f\u6613\u99a8\uff0c\u6211\u6000\u7591\u5979\u8001\u5b50\u662f\u90ae\u7535\u90e8\u526f\u5e02\u957f\u6613\u4e91\u98de\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u82b1\u4e86\u4e9b\u65f6\u95f4\u5c31\u542c\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u4ecb\u7ecd\u8bf4\u4e86\u4e00\u4e0b\u4ed6\u4eec\u521a\u624d\u8ba8\u8bba\u7684\u7ed3\u679c\uff0c\u5c31\u5148\u56de\u623f\u95f4\u53bb\u7761\u89c9\uff0c\u4ed6\u6c89\u6c89\u4e00\u89c9\u7761\u5230\u5341\u70b9\u624d\u8d77\u5e8a\uff0c\u5317\u4eac\u5206\u516c\u53f8\u7ecf\u7406\u59da\u575a\u65e9\u5c31\u51c6\u65f6\u6765\u62a5\u9053\uff0c\u8d75\u9e4f\u4e5f\u65e9\u5c31\u8d77\u5e8a\uff0c\u501f\u7740\u9152\u5e97\u63d0\u4f9b\u7684\u4f1a\u8bae\u5ba4\uff0c\u5df2\u7ecf\u5f00\u59cb\u5de5\u4f5c\u8d77\u6765\u3002</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u770b\u89c1\u5f20\u606a\u8d70\u8fdb\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5206\u7ba1\u7535\u4fe1\u653f\u52a1\u53f8\u3001\u79fb\u52a8\u901a\u8baf\u5c40\u7b49\u53f8\u5c40\u7684\u90ae\u7535\u90e8\u526f\u90e8\u957f\u6613\u4e91\u98de\u786e\u5b9e\u662f\u6628\u5929\u90a3\u4e2a\u6613\u99a8\u7684\u7236\u4eb2\uff0c\u8fd9\u4e2a\u673a\u4f1a\u6070\u662f\u597d\uff0c\u4e0d\u7136\u8fd8\u6101\u627e\u4e0d\u5230\u673a\u4f1a\u4e0e\u56fd\u5185\u901a\u8baf\u4ea7\u4e1a\u7684\u638c\u95e8\u4eba\u6253\u4ea4\u9053\uff0c\u606a\u5c11\u4f60\u8fd9\u6b21\u771f\u8981\u66ff\u516c\u53f8\u727a\u7272\u4e00\u4e0b\u5462\u3002\u201d</p><p>\u3000\u3000\u201c\u6ca1\u90a3\u4e48\u7b80\u5355\uff0c\u201d\u5f20\u606a\u6447\u4e86\u6447\u5934\uff0c\u6307\u4e86\u6307\u81ea\u5df1\u7684\u592a\u9633\u7a74\uff0c\u201c\u8111\u5b50\u7684\u4e1c\u897f\uff0c\u662f\u4e0d\u4f1a\u8ba9\u5176\u4ed6\u4eba\u6539\u53d8\u7684\uff0c\u8d8a\u662f\u6743\u9ad8\u4f4d\u91cd\uff0c\u601d\u60f3\u8d8a\u662f\u50f5\u5316\u2026\u2026\u201d\u4e8b\u5b9e\u4e0a\u56fd\u4ea7\u624b\u673a\u6279\u6587\u4e00\u62d6\u51e0\u5e74\u7edd\u4e0d\u4f1a\u56e0\u4e3a\u5176\u4ed6\u56fd\u5185\u5382\u5546\u7684\u5de5\u4f5c\u4e0d\u5230\u4f4d\uff0c\u5f20\u606a\u6ca1\u6709\u5962\u671b\u901a\u8fc7\u6613\u99a8\u8fd9\u5c42\u534a\u751f\u4e0d\u719f\u7684\u5173\u7cfb\u5c31\u80fd\u64ac\u5f00\u6613\u4e91\u98de\u575a\u95ed\u7684\u5fc3\u95e8\uff0c\u5f20\u606a\u62cd\u4e86\u62cd\u82cf\u6d25\u4e1c\u52a9\u7406\u8d75\u9e4f\u7684\u80a9\u8180\uff0c\u8bf4\u9053\uff0c\u201c\u8fd9\u4e9b\u5929\u5c31\u8981\u9ebb\u70e6\u4f60\u591a\u8ddf\u59da\u6587\u76db\u591a\u63a5\u89e6\u2026\u2026\u201d\u63a5\u89e6\u59da\u6587\u76db\uff0c\u5e76\u4e0d\u6307\u671b\u4ed6\u80fd\u76f4\u63a5\u5e2e\u4e0a\u5fd9\uff0c\u5173\u952e\u60f3\u901a\u8fc7\u4ed6\u4e86\u89e3\u53d1\u653e\u624b\u673a\u4ea7\u4e1a\u51c6\u5165\u6279\u6587\u7684\u4e3b\u7ba1\u5355\u4f4d\u90ae\u7535\u90e8\u7535\u4fe1\u653f\u52a1\u53f8\u7684\u52a8\u5411\u3002</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u7559\u4e0b\u6765\u53c2\u4e0e\u5177\u4f53\u7684\u4e8b\u60c5\uff0c\u4e0e\u5085\u4fca\u5750\u51fa\u79df\u8f66\u5230\u5f90\u5b66\u5e73\u5bb6\u5403\u4e2d\u996d\u3002</p><p>\u3000\u3000\u6628\u5929\u5230\u5317\u4eac\u5df2\u7ecf\u665a\u4e86\uff0c\u5c31\u4e0e\u5f90\u5b66\u5e73\u901a\u8fc7\u7535\u8bdd\uff0c\u6ca1\u6709\u5230\u4ed6\u5bb6\u6765\u6253\u6270\u4ed6\u4e0e\u5468\u6dd1\u60e0\u7684\u4f11\u606f\u3002\u4e2d\u5348\u5403\u996d\u65f6\uff0c\u5f20\u606a\u5c06\u674e\u8fdc\u6e56\u76ef\u4e0a\u5236\u6d46\u9879\u76ee\u7684\u60c5\u51b5\u8be6\u7ec6\u7684\u8ddf\u5f90\u5b66\u5e73\u8bf4\u4e86\u4e00\u904d\uff0c\u6839\u636e\u6628\u5929\u7701\u653f\u5e9c\u529e\u516c\u4f1a\u8bae\u6cc4\u9732\u51fa\u7684\u5185\u5bb9\uff0c\u674e\u8fdc\u6e56\u6709\u610f\u5c06\u5236\u6d46\u9879\u76ee\u5217\u5165\u7701\u59d4\u3001\u7701\u653f\u5e9c\u91cd\u70b9\u6276\u6301\u9879\u76ee\u3002\u7531\u4e8e\u5236\u6d46\u9879\u76ee\u524d\u671f\u7684\u51c6\u5907\u5de5\u4f5c\u8d44\u6599\u4e00\u6982\u6ca1\u6709\uff0c\u6709\u4eba\u63d0\u51fa\u5f02\u8bae\uff0c\u4e5f\u5c31\u6401\u7f6e\u4e0d\u8bae\uff0c\u8981\u662f\u5217\u5165\u7701\u653f\u5e9c\u91cd\u70b9\u6276\u6301\u9879\u76ee\uff0c\u60f3\u9000\u51fa\uff0c\u96be\u5ea6\u8fd8\u8981\u589e\u52a0\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u5f90\u5b66\u5e73\u4e5f\u5927\u611f\u5934\u75bc\uff0c\u6307\u7740\u81ea\u5df1\u8d77\u8936\u76b1\u7684\u8138\u4e0a\uff1a\u201c\u5f53\u5b98\u7684\uff0c\u597d\u50cf\u5b98\u5a01\u90fd\u96c6\u4e2d\u5728\u8fd9\u5f20\u8138\u4e0a\uff0c\u786e\u5b9e\u8981\u597d\u597d\u5e94\u4ed8\u624d\u884c\uff0c\u8fd9\u4e5f\u7b97\u662f\u6211\u79bb\u5f00\u4e1c\u6d77\u6ca1\u64e6\u5e72\u51c0\u7684\u5c41\u80a1\uff0c\u8981\u9020\u58f0\u52bf\uff0c\u6211\u8fd9\u8fb9\u5e94\u8be5\u80fd\u505a\u4ec0\u4e48\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u56fd\u52a1\u9662\u53d1\u5c55\u7814\u7a76\u4e2d\u5fc3\uff0c\u662f\u56fd\u52a1\u9662\u76f4\u5c5e\u7684\u7ecf\u6d4e\u653f\u7b56\u7814\u7a76\u4e0e\u54a8\u8be2\u673a\u6784\uff0c\u662f\u56fd\u5bb6\u667a\u56ca\u4e4b\u4e00\uff0c\u53d1\u5c55\u7814\u7a76\u4e2d\u5fc3\u7684\u7814\u7a76\u6210\u679c\u5c06\u76f4\u63a5\u5f71\u54cd\u56fd\u52a1\u9662\u7684\u76f8\u5173\u7ecf\u6d4e\u653f\u6cbb\u3002</p><p>\u3000\u3000\u5f90\u5b66\u5e73\u7b54\u5e94\u5e2e\u5fd9\uff0c\u53ef\u4ee5\u505a\u7684\u4e8b\u60c5\u81ea\u7136\u5f88\u591a\uff0c\u4e00\u662f\u538b\u5236\u56fd\u5185\u4e0a\u5236\u6d46\u9879\u76ee\u7684\u70ed\u60c5\uff0c\u5f15\u8d77\u56fd\u5bb6\u5bf9\u5929\u7136\u6797\u73b0\u72b6\u4e0e\u751f\u6001\u73af\u5883\u6301\u7eed\u6076\u5316\u7684\u5173\u6ce8\uff1b\u4e8c\u662f\u8fdb\u4e00\u6b65\u589e\u5f3a\u56fd\u5bb6\u5bf9\u9ad8\u79d1\u6280\u4ea7\u4e1a\u4e0e\u6280\u672f\u6295\u8d44\u7684\u91cd\u89c6\u3002</p><p>\u3000\u3000\u5229\u7528\u53d1\u5c55\u7814\u7a76\u4e2d\u5fc3\u7684\u6743\u5a01\u5bf9\u4e24\u4e2a\u4ea7\u4e1a\u4e00\u6291\u4e00\u626c\uff0c\u591a\u5c11\u5bf9\u674e\u8fdc\u6e56\u80fd\u65bd\u52a0\u4e9b\u5f71\u54cd\uff1b\u5f53\u7136\u5f20\u606a\u66f4\u5e0c\u671b\u53d1\u5c55\u7814\u7a76\u4e2d\u5fc3\u80fd\u52a0\u5f3a\u79fb\u52a8\u901a\u4fe1\u4ea7\u4e1a\u5bf9\u56fd\u5185\u7ecf\u6d4e\u4fc3\u8fdb\u7684\u7814\u7a76\uff0c\u4e5f\u4e3a\u4e0b\u4e00\u6b65\u4e0a\u624b\u673a\u9879\u76ee\u63d0\u524d\u9020\u52bf\u3002</p></article>"}}}]);
