(window.webpackJsonp=window.webpackJsonp||[]).push([[529],{551:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u516d\u5341\u4e00\u8282 \u9080\u7ea6",content:"<article><h2>\u7b2c\u516d\u5341\u4e00\u8282 \u9080\u7ea6</h2><p>\u5982\u679c\u6ca1\u6709\u5984\u60f3\uff0c\u4e16\u754c\u2026\u2026\u8be5\u600e\u4e48\u6837\u8fd8\u662f\u600e\u4e48\u6837\u3002</p><p>\u743c\u6069\u6700\u7ec8\u8fd8\u662f\u6ca1\u80fd\u8bf4\u670d\u6885\u83f2\u65af\uff0c\u597d\u5728\u4ed6\u4e5f\u4ece\u6765\u6ca1\u6307\u671b\u4e00\u8e74\u800c\u5c31\uff0c\u8fd9\u662f\u4e2a\u6f2b\u957f\u800c\u8270\u5de8\u7684\u8fc7\u7a0b\uff0c\u4ed6\u65e9\u5c31\u505a\u597d\u4e86\u6253\u6301\u4e45\u6218\u7684\u5fc3\u7406\u51c6\u5907\u3002\u8fd8\u662f\u90a3\u53e5\u8bdd\uff1a\u6162\u6162\u6765\uff0c\u4e0d\u7740\u6025\uff0c\u65f6\u95f4\u6709\u7684\u662f\uff0c\u6765\u65e5\u65b9\u957f\u561b\u3002</p><p>\u201c\u597d\u5566\u597d\u5566\uff0c\u201d\u6885\u83f2\u65af\u5b89\u6170\uff0c\u201c\u522b\u90a3\u4e48\u5782\u5934\u4e27\u6c14\u7684\u6837\u5b50\uff0c\u6211\u4f1a\u53e6\u5916\u8865\u507f\u4f60\u7684\u3002\u201d</p><p>\u542c\u5230\u8fd9\u53e5\u8bdd\uff0c\u743c\u6069\u987f\u65f6\u6709\u4e86\u7cbe\u795e\uff0c\u201c\u771f\u7684\uff1f\u201d\u4ed6\u60ca\u559c\u5730\u95ee\u3002</p><p>\u201c\u5f53\u7136\u662f\u771f\u7684\u2014\u2014\u4e0d\u662f\u65e9\u5c31\u8bf4\u8fc7\u4e86\u5417\uff0c\u201d\u5c11\u5973\u63d0\u9192\uff0c\u201c\u7b54\u5e94\u8981\u8865\u4f60\u4e00\u4ef6\u751f\u65e5\u793c\u7269\u7684\u3002\u201d</p><p>\u201c\u54e6\uff0c\u5bf9\uff0c\u201d\u743c\u6069\u60f3\u4e86\u8d77\u6765\uff0c\u201c\u90a3\u5230\u5e95\u662f\u4ec0\u4e48\u793c\u7269\u5462\uff1f\u201d</p><p>\u201c\u8fd9\u4e2a\u73b0\u5728\u4e0d\u80fd\u8bf4\uff0c\u5230\u65f6\u5019\u81ea\u7136\u5c31\u77e5\u9053\uff0c\u201d\u6885\u83f2\u65af\u683c\u683c\u7b11\u7740\uff0c\u201c\u73b0\u5728\u8bf4\u4e86\uff0c\u5230\u65f6\u5019\u5c31\u6ca1\u6709\u60ca\u559c\u4e86\u3002\u201d</p><p>\u201c\u90a3\u2018\u5230\u65f6\u5019\u2019\u5230\u5e95\u662f\u4ec0\u4e48\u65f6\u5019\u5462\uff1f\u201d</p><p>\u201c\u5f88\u5feb\uff0c\u201d\u5979\u542b\u7cca\u5730\u8bf4\uff0c\u201c\u4e0d\u4f1a\u8ba9\u4f60\u7b49\u592a\u4e45\u7684\u3002\u201d</p><p>\u201c\u2026\u2026\u597d\u5427\u3002\u201d</p><p>\u867d\u7136\u5f88\u597d\u5947\uff0c\u4f46\u743c\u6069\u8fd8\u662f\u6ca1\u6709\u518d\u8ffd\u95ee\uff0c\u56e0\u4e3a\u95ee\u4e5f\u6ca1\u6709\u7528\uff0c\u6885\u83f2\u65af\u65e2\u7136\u5df2\u7ecf\u8fd9\u4e48\u8bf4\u4e86\uff0c\u90a3\u5c31\u5fc5\u5b9a\u4e0d\u4f1a\u63d0\u524d\u900f\u9732\uff0c\u8fd8\u4e0d\u5982\u7701\u7701\u529b\u6c14\uff0c\u505a\u5927\u5bb6\u90fd\u7231\u505a\u7684\u4e8b\u60c5\u5427\u3002</p><p>\u4f5c\u4e3a\u536b\u5195\u8005\uff0c\u6885\u83f2\u65af\u76f4\u63a5\u5728\u661f\u4e4b\u82b1\u7684\u7b2c\u4e09\u65e5\u53c2\u4e0e\u56db\u5f3a\u534a\u51b3\u8d5b\uff0c\u524d\u4e24\u65e5\u90fd\u4e0d\u9700\u8981\u53bb\uff0c\u6559\u4f1a\u4e5f\u6ca1\u522b\u7684\u5de5\u4f5c\u5b89\u6392\uff0c\u5c31\u5728\u5bb6\u4e2d\u966a\u7740\u743c\u6069\u3002\u6216\u8bb8\u662f\u56e0\u4e3a\u5373\u5c06\u8981\u5206\u522b\u7684\u7f18\u6545\uff0c\u5c11\u5973\u6bd4\u5e73\u5e38\u6e29\u67d4\u4e86\u8bb8\u591a\uff0c\u5bf9\u743c\u6069\u4e58\u673a\u63d0\u51fa\u6765\u7684\u8981\u6c42\uff0c\u53ea\u8981\u4e0d\u662f\u592a\u8fc7\u5206\uff0c\u4e5f\u90fd\u5c3d\u53ef\u80fd\u5730\u6ee1\u8db3\u3002\u7ed3\u679c\u662f\u4ece\u7b2c\u4e00\u5929\u665a\u4e0a\u5f00\u59cb\uff0c\u76f4\u81f3\u7b2c\u4e8c\u5929\u534a\u591c\uff0c\u5979\u51e0\u4e4e\u90fd\u6ca1\u6709\u4e0b\u8fc7\u5e8a\uff0c\u9664\u4e86\u4e00\u65e5\u4e09\u9910\u3001\u5b66\u4e60\u3001\u7761\u7720\u548c\u77ed\u6682\u7684\u4f11\u606f\uff0c\u5176\u4f59\u65f6\u95f4\u90fd\u5728\u627f\u53d7\u743c\u6069\u65e0\u4f11\u65e0\u6b62\u7684\u201c\u653b\u51fb\u201d\u3002\u6700\u521d\u5979\u8fd8\u80fd\u52c9\u5f3a\u62db\u67b6\uff0c\u4f46\u9762\u5bf9\u743c\u6069\u90a3\u51fa\u5947\u5145\u6c9b\uff0c\u4eff\u4f5b\u6c38\u65e0\u6b62\u5883\u7684\u65fa\u76db\u7cbe\u529b\uff0c\u575a\u5f3a\u7684\u5c11\u5973\u4e5f\u4e56\u4e56\u8d25\u4e0b\u9635\u6765\uff0c\u53ea\u662f\u5929\u751f\u7684\u597d\u5f3a\u6027\u683c\u8ba9\u5979\u8fd8\u54ac\u7d27\u7259\u5173\uff0c\u575a\u6301\u7740\uff0c\u4e0d\u80af\u6c42\u9976\u3002</p><p>\u201c\u600e\u4e48\u56de\u4e8b\uff0c\u4f60\uff0c\u4f60\u96be\u9053\u5c31\u4e0d\u4f1a\u89c9\u5f97\u7d2f\u4e48\uff1f\u201d\u5979\u624b\u8db3\u762b\u8f6f\u5730\u8dea\u5728\u5e8a\u4e0a\uff0c\u53cc\u81c2\u5df2\u7ecf\u65e0\u529b\u652f\u6491\uff0c\u53ea\u80fd\u530d\u5310\u7740\uff0c\u6574\u4e2a\u4e0a\u534a\u8eab\u90fd\u8d34\u7740\u5e8a\u5355\uff0c\u4e00\u5bf9\u7ed3\u5b9e\u5c0f\u5de7\u7684\u9e3d\u4e73\u56e0\u4e3a\u6324\u538b\u800c\u53d8\u5f62\uff0c\u968f\u7740\u7537\u4eba\u5728\u81c0\u540e\u7684\u7528\u529b\u51b2\u649e\u4e0d\u65ad\u6765\u56de\u6643\u52a8\u3002\u6676\u83b9\u7684\u6c57\u73e0\u4ece\u5979\u7684\u9b13\u89d2\u6ef4\u843d\uff0c\u5c06\u6795\u5dfe\u6d78\u5f97\u900f\u6e7f\uff0c\u201c\u8fd9\u90fd\u4eca\u5929\u7b2c\u51e0\u6b21\u4e86\uff1f\u201d</p><p>\u201c\u5982\u679c\u4ee5\u4f60\u90a3\u8fb9\u4e3a\u8ba1\u7b97\u6807\u51c6\uff0c\u662f\u7b2c\u4e5d\u6b21\uff1b\u5982\u679c\u4ee5\u6211\u8fd9\u8fb9\u4e3a\u8ba1\u7b97\u6807\u51c6\uff0c\u662f\u7b2c\u56db\u6b21\uff0c\u201d\u743c\u6069\u4e00\u8fb9\u5728\u5c11\u5973\u4f53\u5185\u5feb\u901f\u51b2\u523a\u7740\uff0c\u4e00\u8fb9\u597d\u6574\u4ee5\u6687\u5730\u56de\u7b54\uff0c\u201c\u522b\u62c5\u5fc3\uff0c\u6700\u591a\u518d\u6765\u4e2a\u56db\u4e94\u6b21\u6211\u5c31\u4e0d\u884c\u4e86\u3002\u201d</p><p>\u201c\u4f60\u8fd9\u56db\u4e94\u6b21\u662f\u4ee5\u8c01\u4e3a\u6807\u51c6\uff1f\u201d</p><p>\u201c\u81ea\u7136\u662f\u6211\uff0c\u5982\u679c\u4ee5\u4f60\u4e3a\u6807\u51c6\uff0c\u5927\u6982\u8fd8\u6709\u5341\u6b21\u5de6\u53f3\u5427\u3002\u201d</p><p>\u201c\u2026\u2026\u6211\u8981\u7533\u8bf7\u4f11\u606f\u3002\u201d</p><p>\u201c\u7533\u8bf7\u9a73\u56de\uff0c\u201d\u743c\u6069\u4e00\u53e3\u5426\u51b3\uff0c\u201c\u4f5c\u4e3a\u5973\u4eba\uff0c\u6ca1\u6709\u8ba9\u7537\u53cb\u6ee1\u8db3\u5c31\u8981\u6c42\u4f11\u606f\uff0c\u8fd9\u662f\u975e\u5e38\u4e0d\u79f0\u804c\u7684\u8868\u73b0\u3002\u201d</p><p>\u201c\u53ef\u662f\u6211\u5df2\u7ecf\u591f\u4e86\uff0c\u6211\u89c9\u5f97\u518d\u6765\u4e00\u6b21\u6211\u5c31\u8981\u6b7b\u4e86\u3002\u201d</p><p>\u201c\u4e0d\u4f1a\u7684\uff0c\u201d\u743c\u6069\u5b89\u6170\uff0c\u201c\u4ece\u7406\u8bba\u4e0a\u8bf4\uff0c\u5973\u6027\u53ef\u4ee5\u65e0\u9650\u6b21\u9ad8\u6f6e\uff0c\u653e\u5fc3\u597d\u4e86\u3002\u201d</p><p>\u201c\u4f60\u90a3\u662f\u4ec0\u4e48\u6b6a\u7406\u90aa\u8bf4\uff01\u201d</p><p>\u7136\u800c\u743c\u6069\u5e76\u4e0d\u5728\u610f\u662f\u5426\u6b6a\u7406\u90aa\u8bf4\uff0c\u4ed6\u53ea\u662f\u66f4\u8fdb\u4e00\u6b65\u5730\u52a0\u5feb\u4e86\u901f\u5ea6\u3002\u7ecf\u8fc7\u65e0\u6570\u6b21\u7684\u5f00\u53d1\uff0c\u5c11\u5973\u7684\u83ca\u9053\u5df2\u7ecf\u53d8\u5f97\u6781\u5176\u654f\u611f\uff0c\u7d27\u7a84\u3001\u706b\u70ed\uff0c\u800c\u4e14\u5145\u6ee1\u60ca\u4eba\u7684\u5f39\u6027\u548c\u97e7\u52b2\uff0c\u5b8c\u5168\u53ef\u4ee5\u79f0\u5f97\u4e0a\u662f\u540d\u5668\uff0c\u8ba9\u4ed6\u6c89\u8ff7\u5176\u4e2d\uff0c\u5fd8\u4e4e\u6240\u4ee5\u3002\u201c\u827e\u5f25\u8587\uff0c\u201d\u4ed6\u968f\u53e3\u53d6\u7b11\u8eab\u4e0b\u7684\u5c11\u5973\uff0c\u201c\u4f60\u771f\u7684\u5f88\u6deb\u8361\u5462\uff0c\u8fde\u7528\u540e\u9762\u90fd\u80fd\u8fbe\u5230\u9ad8\u6f6e\u3002\u201d</p><p>\u201c\u8ba8\u2026\u2026\u8ba8\u538c\uff0c\u4e0d\u51c6\u8bf4\uff01\u201d</p><p>\u82f1\u6b66\u4fca\u4fcf\u7684\u91d1\u53d1\u5c11\u5973\uff0c\u8d1d\u9f7f\u7d27\u54ac\u6a31\u5507\uff0c\u6655\u7ea2\u4e24\u988a\u4e0a\u6d6e\u73b0\u51fa\u65e2\u7f9e\u4e14\u607c\u7684\u8868\u60c5\uff0c\u5b9e\u5728\u662f\u7f8e\u4e3d\u5f97\u65e0\u6cd5\u5f62\u5bb9\uff0c\u770b\u5f97\u743c\u6069\u6026\u7136\u5fc3\u52a8\uff0c\u6b63\u5728\u5979\u540e\u5ead\u4e2d\u8086\u610f\u4fb5\u72af\u7684\u6eda\u70eb\u5de8\u7269\u53d7\u6b64\u9f13\u821e\uff0c\u9661\u7136\u95f4\u4eff\u4f5b\u53c8\u81a8\u80c0\u7c97\u58ee\u4e86\u4e00\u5708\u3002\u6885\u83f2\u65af\u5fcd\u4e0d\u4f4f\u554a\u4e86\u58f0\uff0c\u5168\u8eab\u8f7b\u98a4\uff0c\u201c\u771f\u7684\u4e0d\u884c\u4e86\uff0c\u201d\u5979\u54c0\u6c42\uff0c\u201c\u8eab\u4f53\u597d\u50cf\u90fd\u8981\u88ab\u4f60\u6491\u88c2\u5f00\u4e86\u2026\u2026\u4eca\u5929\u5c31\u5230\u6b64\u4e3a\u6b62\u597d\u4e0d\u597d\uff1f\u201d</p><p>\u201c\u5230\u6b64\u4e3a\u6b62\uff1f\u53ef\u662f\u6211\u8fd8\u6ca1\u8981\u591f\u5462\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u7b11\u563b\u563b\u5730\u770b\u7740\u5bf9\u65b9\uff0c\u201c\u6216\u8005\u4f60\u5148\u4f11\u606f\u4e00\u4f1a\uff1f\u8ba9\u838e\u73de\u514b\u6765\u66ff\u4f60\uff1f\u53cd\u6b63\u5979\u8010\u529b\u597d\u3002\u201d</p><p>\u201c\u4e0d\u884c\uff01\u201d</p><p>\u4e0d\u5047\u601d\u7d22\u5730\uff0c\u6885\u83f2\u65af\u5426\u51b3\u4e86\u743c\u6069\u7684\u63d0\u8bae\u3002\u5f00\u73a9\u7b11\uff0c\u81ea\u5df1\u600e\u4e48\u80fd\u8f93\u7ed9\u90a3\u4e2a\u5973\u4eba\u3002\u52c9\u5f3a\u79ef\u7d2f\u8d77\u6700\u540e\u4e00\u70b9\u529b\u6c14\uff0c\u5979\u7d27\u7d27\u62b1\u4f4f\u8eab\u4e0a\u7684\u7537\u53cb\u3002\u201c\u6765\u5c31\u6765\uff0c\u201d\u5979\u54ac\u7740\u5634\u5507\uff0c\u201c\u8c01\u6015\u554a\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u7537\u5973\u5e8a\u4e0a\u5f81\u6218\uff0c\u5148\u5929\u7684\u751f\u7406\u6784\u9020\u5dee\u5f02\u5c31\u51b3\u5b9a\u4e86\u7537\u65b9\u5728\u4f53\u529b\u4e0a\u603b\u662f\u8981\u591a\u4ed8\u51fa\u5f88\u591a\uff0c\u4ee5\u8fd0\u52a8\u91cf\u6765\u8bf4\u743c\u6069\u51e0\u4e4e\u662f\u6885\u83f2\u65af\u7684\u5341\u500d\uff0c\u4f46\u5374\u4f9d\u7136\u884c\u6709\u4f59\u529b\uff0c\u660e\u663e\u5360\u636e\u4f18\u52bf\u3002\u4e4b\u6240\u4ee5\u4f1a\u5982\u6b64\uff0c\u5012\u5e76\u975e\u56e0\u4e3a\u4ed6\u771f\u6709\u8fd9\u4e48\u5f3a\u58ee\uff0c\u800c\u662f\u90a3\u6b21\u5728\u661f\u9668\u57ce\u53d8\u5316\u6740\u622e\u8005\u540e\u88ab\u6551\u9192\uff0c\u5728\u6b64\u8fc7\u7a0b\u4e2d\u5965\u5609\u83b1\u65af\u4e0d\u77e5\u9053\u505a\u4e86\u4ec0\u4e48\uff0c\u53cd\u6b63\u7ed3\u679c\u662f\u4ed6\u7684\u7cbe\u529b\u4ece\u6b64\u53d8\u5f97\u6781\u5ea6\u65fa\u76db\u5145\u6c9b\uff0c\u867d\u7136\u4e5f\u4f1a\u6d88\u8017\uff0c\u4f1a\u75b2\u60eb\uff0c\u4f46\u5f88\u5feb\u5c31\u80fd\u6062\u590d\u3002\u5c31\u50cf\u4ed6\u548c\u6885\u83f2\u65af\u6b22\u597d\uff0c\u4e4b\u6240\u4ee5\u80fd\u591f\u5360\u4e0a\u98ce\uff0c\u5e76\u975e\u662f\u4ed6\u591a\u4e48\u6301\u4e45\u5fcd\u8010\uff0c\u91d1\u67aa\u4e0d\u5012\uff0c\u800c\u662f\u56de\u6c14\u8d85\u5feb\uff0c\u767e\u6218\u4e0d\u75b2\uff0c\u8d8a\u6218\u8d8a\u52c7\uff0c\u6885\u83f2\u65af\u5374\u6ca1\u8fd9\u79cd\u80fd\u8010\uff0c\u800c\u4e14\u7ecf\u8fc7\u743c\u6069\u201c\u5f00\u53d1\u201d\u7684\u8eab\u4f53\u5df2\u7ecf\u8d8a\u6765\u8d8a\u654f\u611f\uff0c\u8d8a\u6765\u8d8a\u4e0d\u582a\u627f\u53d7\u7537\u6027\u7684\u653b\u51fb\uff0c\u7ed3\u679c\u81ea\u7136\u662f\u4e00\u8d25\u6d82\u5730\u3002\u53ef\u60dc\u743c\u6069\u539f\u672c\u8fd8\u5b58\u4e86\u4e2a\u5984\u60f3\uff0c\u5e0c\u671b\u6885\u83f2\u65af\u5728\u4e0d\u582a\u91cd\u8d1f\u4e4b\u4e0b\uff0c\u4f1a\u56e0\u6b64\u5141\u8bb8\u751a\u81f3\u4e3b\u52a8\u63d0\u51fa\u8ba9\u5176\u4ed6\u5973\u5b69\u5b50\u6765\u66ff\u5979\u201c\u5206\u62c5\u201d\uff0c\u7136\u800c\u4ed6\u8fd8\u662f\u4f4e\u4f30\u4e86\u5c11\u5973\u7684\u51b3\u5fc3\u548c\u610f\u5fd7\u3002</p><p>\u201c\u771f\u4e0d\u914d\u5408\uff0c\u770b\u6765\u6211\u7684\u4e3b\u89d2\u6a21\u677f\u8fd8\u4e0d\u591f\u5f3a\u554a\u3002\u201d</p><p>\u4e0d\u8fc7\u4e8b\u5b9e\u7ec8\u7a76\u8bc1\u660e\uff0c\u5206\u4eab\u662f\u4e00\u79cd\u7f8e\u5fb7\uff0c\u800c\u8d2a\u5fc3\u662f\u4f1a\u906d\u62a5\u5e94\u7684\u3002\u6885\u83f2\u65af\u7684\u901e\u5f3a\uff0c\u8ba9\u5979\u7b2c\u4e09\u5929\u65e9\u4e0a\u5dee\u70b9\u4e0b\u4e0d\u4e86\u5e8a\uff0c\u9669\u4e9b\u7f3a\u5e2d\u661f\u4e4b\u82b1\u7684\u6700\u7ec8\u6218\u3002\u636e\u5979\u4e8b\u540e\u8bf4\uff0c\u76f4\u5230\u4e0a\u53f0\u7684\u65f6\u5019\uff0c\u817f\u90fd\u8fd8\u6709\u4e9b\u53d1\u8f6f\uff0c\u8fd9\u663e\u7136\u4e5f\u5f88\u5927\u7a0b\u5ea6\u4e0a\u5f71\u54cd\u4e86\u5979\u5728\u6bd4\u8d5b\u4e2d\u7684\u8868\u73b0\uff0c\u6700\u540e\u53ea\u83b7\u5f97\u4e86\u7b2c\u4e09\u540d\uff0c\u4f5c\u4e3a\u536b\u5195\u8005\uff0c\u8fd9\u4e2a\u6210\u7ee9\u663e\u7136\u975e\u5e38\u4e0d\u7406\u60f3\u3002\u5979\u672c\u4eba\u867d\u7136\u4e0d\u770b\u91cd\u8fd9\u4e9b\uff0c\u4f46\u4f5c\u4e3a\u6559\u4f1a\u4ea4\u4ee3\u7684\u4efb\u52a1\u5374\u662f\u5931\u8d25\u4e86\u3002</p><p>\u201c\u5e78\u597d\u4e5f\u4e0d\u662f\u5f3a\u5236\u8981\u6c42\u6211\u5fc5\u987b\u83b7\u80dc\u3002\u201d\u5979\u81ea\u5632\u3002</p><p>\u661f\u4e4b\u82b1\u8fdb\u884c\u5230\u7b2c\u4e09\u5929\uff0c\u80fd\u591f\u7559\u4e0b\u7684\u56db\u5f3a\u90fd\u662f\u4e07\u4e2d\u9009\u4e00\u7684\u7f8e\u4eba\uff0c\u6b64\u65f6\u5bb9\u8c8c\u4e0a\u7684\u5dee\u8ddd\u5df2\u7ecf\u6781\u4e0d\u660e\u663e\uff0c\u66f4\u770b\u91cd\u7684\u662f\u6b4c\u5531\u3001\u821e\u8e48\u7b49\u827a\u672f\u624d\u534e\u3002\u51db\u4ee5\u4e00\u652f\u6781\u5177\u5a9a\u60d1\u7684\u70ed\u8fa3\u821e\u8e48\u5728\u534a\u51b3\u8d5b\u4e2d\u6253\u8d25\u6885\u83f2\u65af\uff0c\u6210\u529f\u664b\u7ea7\u51b3\u8d5b\uff0c\u4f46\u6700\u7ec8\u4e5f\u53ea\u5f97\u5230\u4e9a\u519b\u3002\u6218\u80dc\u5979\u6458\u53d6\u661f\u4e4b\u82b1\u5934\u8854\u7684\uff0c\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u90a3\u4f4d\u76f2\u5973\u838e\u7433\u5a1c\u3002</p><p>\u201c\u5979\u5531\u6b4c\u771f\u597d\u542c\u3002\u201d\u51db\u5fc3\u60a6\u8bda\u670d\u5730\u8bf4\u3002</p><p>\u6700\u540e\u51b3\u8d5b\u4e2d\uff0c\u838e\u7433\u5a1c\u8eab\u7a7f\u5bbd\u5927\u7684\u9ed1\u888d\uff0c\u6234\u7740\u4e00\u53ea\u9ed1\u8272\u7fbd\u6bdb\u9762\u5177\u767b\u4e0a\u821e\u53f0\u3002\u867d\u7136\u4ece\u9762\u5bb9\u5230\u8eab\u4f53\u5168\u90fd\u88ab\u906e\u5f97\u4e25\u4e25\u5b9e\u5b9e\uff0c\u5374\u53cd\u800c\u66f4\u589e\u52a0\u4e86\u90a3\u79cd\u6151\u4eba\u7684\u795e\u79d8\u611f\uff0c\u5145\u6ee1\u54c0\u4f24\u7684\u6b4c\u58f0\u81ea\u5979\u53e3\u4e2d\u548f\u51fa\uff0c\u6beb\u65e0\u60ac\u5ff5\u5730\u5f81\u670d\u4e86\u5728\u573a\u7684\u5168\u4f53\u89c2\u4f17\u3002\u6240\u6709\u8bc4\u59d4\u90fd\u4e00\u81f4\u7ed9\u4e86\u5979\u6700\u9ad8\u5206\uff0c\u636e\u8bf4\u8fd9\u5728\u661f\u4e4b\u82b1\u7684\u5386\u53f2\u4e0a\u90fd\u662f\u7f55\u89c1\u7684\u3002</p><p>\u201c\u5947\u602a\uff0c\u201d\u743c\u6069\u76b1\u7709\uff0c\u201c\u4e3a\u4ec0\u4e48\u6211\u89c9\u5f97\u5728\u5979\u8eab\u4e0a\u6709\u79cd\u5f88\u719f\u6089\u7684\u611f\u89c9\uff1f\u201d</p><p>\u4ed6\u5e76\u4e0d\u5728\u73b0\u573a\uff0c\u751a\u81f3\u4e0d\u5728\u9547\u4e2d\uff0c\u800c\u662f\u7559\u5728\u623f\u5b50\u91cc\uff0c\u60a0\u60a0\u95f2\u95f2\u5730\u8eba\u5728\u67d4\u8f6f\u7684\u6c99\u53d1\u4e0a\uff0c\u4e00\u8fb9\u559d\u7740\u679c\u6c41\uff0c\u4e00\u8fb9\u7528\u79d8\u6cd5\u773c\u914d\u5408\u671b\u8fdc\u955c\u89c2\u770b\u73b0\u573a\u76f4\u64ad\u3002\u5c3d\u7ba1\u5982\u6b64\uff0c\u743c\u6069\u4f9d\u7136\u80fd\u6e05\u695a\u5730\u611f\u89c9\u5230\u838e\u7433\u5a1c\u8eab\u4e0a\u90a3\u80a1\u5438\u5f15\u4eba\u7684\u795e\u79d8\u6c14\u8d28\uff0c\u6bd4\u521d\u89c1\u65f6\u66f4\u52a0\u7684\u201c\u6d53\u90c1\u201d\u5341\u500d\u3002\u5176\u5b9e\u4e25\u683c\u6765\u8bf4\uff0c\u56db\u5f3a\u4e4b\u4e2d\uff0c\u838e\u7433\u5a1c\u5728\u5bb9\u8c8c\u4e0a\u662f\u6700\u5c45\u52a3\u52bf\u7684\uff0c\u6bd5\u7adf\u5979\u53cc\u76ee\u5c3d\u76f2\uff0c\u800c\u201c\u62e5\u6709\u4e00\u53cc\u7f8e\u4e3d\u7684\u5927\u773c\u775b\u201d\u65e0\u7591\u662f\u7f8e\u4eba\u7684\u8bc4\u5224\u6807\u51c6\u4e4b\u4e00\u3002\u5979\u4e4b\u6240\u4ee5\u80fd\u591f\u4e00\u8def\u664b\u7ea7\u5e76\u6700\u7ec8\u593a\u51a0\uff0c\u5f88\u5927\u7a0b\u5ea6\u4e0a\u5c31\u662f\u56e0\u4e3a\u8fd9\u79cd\u201c\u6c14\u8d28\u201d\u9707\u6151\u5168\u573a\u7684\u7f18\u6545\u3002</p><p>\u4f46\u8fd9\u662f\u663e\u7136\u4e0d\u6b63\u5e38\u7684\u3002\u6c14\u8d28\u8fd9\u79cd\u4e1c\u897f\uff0c\u8bf4\u4e0d\u6e05\u9053\u4e0d\u660e\uff0c\u542b\u7cca\u98d8\u6e3a\u4f3c\u4e4e\u96be\u4ee5\u6349\u6478\uff0c\u5374\u53c8\u771f\u771f\u5b9e\u5b9e\u5b58\u5728\u3002\u5927\u81f4\u6765\u8bf4\uff0c\u90fd\u662f\u9700\u8981\u957f\u65f6\u95f4\u7684\u79ef\u7d2f\u3001\u57f9\u517b\u624d\u4f1a\u5f62\u6210\u7684\uff0c\u800c\u4e14\u662f\u76f8\u5bf9\u56fa\u5b9a\u7684\uff0c\u4e0d\u592a\u53ef\u80fd\u5728\u77ed\u77ed\u51e0\u5929\u5185\u53d1\u751f\u5982\u6b64\u660e\u663e\u7684\u53d8\u5316\u3002\u8fd9\u5176\u4e2d\u201c\u4eba\u4e3a\u201d\u7684\u75d5\u8ff9\uff0c\u5b9e\u5728\u662f\u592a\u8fc7\u660e\u663e\u4e86\u3002</p><p>\u8fd9\u4e2a\u5973\u4eba\u2026\u2026\u5f88\u5371\u9669\u3002</p><p>\u6beb\u65e0\u6765\u7531\u5730\uff0c\u76f4\u89c9\u8ba9\u743c\u6069\u505a\u51fa\u4e86\u8fd9\u4e2a\u5224\u65ad\u3002\u7406\u667a\u544a\u8bc9\u4ed6\u5e94\u8be5\u7acb\u523b\u8fdc\u79bb\uff0c\u7136\u800c\u8fd9\u79cd\u6c14\u8d28\u4f18\u96c5\u795e\u79d8\u3001\u5374\u53c8\u6563\u53d1\u7740\u51db\u51db\u5371\u9669\u611f\u7684\u7f8e\u4e3d\u5973\u5b50\uff0c\u4eff\u4f5b\u6df1\u9083\u7684\u9ed1\u8272\u591c\u5e55\uff0c\u6b63\u662f\u7537\u4eba\u6240\u96be\u4ee5\u6297\u62d2\u7684\u7c7b\u578b\uff0c\u4e8e\u4ed6\u66f4\u6709\u4e00\u79cd\u65e0\u6cd5\u8a00\u4f20\u7684\u7279\u522b\u8bf1\u60d1\u3002\u743c\u6069\u7684\u76ee\u5149\u604b\u604b\u4e0d\u820d\u5730\u5728\u5979\u8eab\u4f53\u4e0a\u6e38\u79fb\u7740\uff0c\u50cf\u662f\u88ab\u4ec0\u4e48\u529b\u91cf\u5438\u5f15\uff0c\u65e0\u6cd5\u6323\u8131\uff0c\u6c89\u8ff7\u5176\u4e2d\uff0c\u7ad9\u5728\u9547\u4e2d\u5e7f\u573a\u4e0a\u7684\u76f2\u76ee\u5973\u5b50\u4f3c\u6709\u6240\u89c9\uff0c\u7a81\u7136\u626c\u8d77\u8138\u6765\uff0c\u8fce\u7740\u9633\u5149\uff0c\u9732\u51fa\u4e00\u4e1d\u7b11\u5bb9\u3002</p><p>\u90a3\u7b11\u5bb9\u751c\u7f8e\u800c\u707f\u70c2\uff0c\u5374\u53c8\u5145\u6ee1\u7740\u96be\u4ee5\u5f62\u5bb9\u7684\u8be1\u79d8\u611f\uff0c\u743c\u6069\u6014\u4e86\u6014\uff0c\u968f\u5373\u610f\u8bc6\u5230\u5bf9\u65b9\u6050\u6015\u53d1\u73b0\u4e86\u81ea\u5df1\u3002\u4ed6\u4ece\u6765\u4e0d\u662f\u4ec0\u4e48\u541b\u5b50\uff0c\u4f46\u8fd9\u6837\u6697\u4e2d\u7aa5\u89c6\u964c\u751f\u5973\u5b50\uff0c\u7ec8\u7a76\u662f\u975e\u5e38\u5931\u793c\u7684\u884c\u4e3a\uff0c\u5982\u4eca\u8fd8\u88ab\u5bf9\u65b9\u6b63\u6293\u4e86\u4e2a\u73b0\u884c\uff0c\u4e0d\u514d\u6709\u4e9b\u8138\u4e0a\u5fae\u5fae\u53d1\u70ed\u3002\u4ed6\u7a0d\u7a0d\u8fdf\u7591\uff0c\u968f\u5373\u53d6\u6d88\u4e86\u79d8\u6cd5\u773c\uff0c\u8d70\u5230\u7a97\u524d\u62c9\u4e0a\u539a\u539a\u7684\u5e03\u5e18\uff0c\u5c06\u8fdc\u5904\u4f20\u6765\u7684\u55a7\u56a3\u70ed\u95f9\u963b\u9694\u5728\u5916\u3002\u505a\u5b8c\u8fd9\u4e9b\u540e\uff0c\u5deb\u5e08\u8d70\u56de\u4e66\u684c\u524d\uff0c\u6b63\u51c6\u5907\u7ee7\u7eed\u4eca\u5929\u7684\u9b54\u6cd5\u5b66\u4e60\uff0c\u773c\u89d2\u4f59\u5149\u4e2d\u5374\u5ffd\u7136\u77a5\u89c1\u53f0\u706f\u7684\u94f6\u8272\u5e95\u5ea7\u4e0a\uff0c\u4e0d\u77e5\u4f55\u65f6\u591a\u4e86\u4e00\u5f20\u6d45\u84dd\u8272\u7684\u4fe1\u7b3a\u3002</p><p>\u743c\u6069\u6361\u8d77\u4fe1\u7b3a\uff0c\u770b\u89c1\u4e00\u884c\u6587\u5b57\uff0c\u7b14\u8ff9\u5a1f\u79c0\uff0c\u663e\u662f\u51fa\u81ea\u5973\u5b50\u4e4b\u624b\uff0c\u5185\u5bb9\u4e5f\u5f88\u7b80\u5355\uff1a\u201c\u5170\u5c3c\u65af\u7279\u5148\u751f\u8c28\u542f\uff1a\u4eca\u591c\u5341\u70b9\uff0c\u897f\u5c71\u5cf0\u9876\u76f8\u4f1a\uff0c\u5e78\u52ff\u63a8\u5374\u201d\uff0c\u843d\u6b3e\u7b7e\u540d\u662f\u201c\u838e\u7433\u5a1c\u3002\u201d</p><p>\u838e\u7433\u5a1c\uff1f</p><p>\u743c\u6069\u6c89\u541f\u4e0d\u8bed\u3002\u51e0\u79d2\u949f\u540e\u7a81\u7136\u7b11\u4e86\u7b11\uff0c\u968f\u624b\u4e00\u63c9\uff0c\u5c06\u4fe1\u7b3a\u634f\u6210\u5c0f\u56e2\uff0c\u6254\u8fdb\u4e86\u5e9f\u7eb8\u7bd3\u4e2d\u3002</p><p>\u203b\u203b\u203b</p><p>\u7d20\u4e0d\u76f8\u8bc6\u7684\u795e\u79d8\u7f8e\u4eba\u4e3b\u52a8\u9080\u8bf7\uff0c\u591c\u6df1\u4eba\u9759\u65f6\u5728\u5c71\u9876\u5e7d\u4f1a\uff0c\u5bf9\u4e8e\u4efb\u4f55\u7537\u6027\u800c\u8a00\uff0c\u8fd9\u90fd\u5b9e\u5728\u662f\u96be\u4ee5\u6297\u62d2\u7684\u8bf1\u60d1\u3002\u5982\u679c\u8fd0\u6c14\u597d\u7684\u8bdd\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u80fd\u4e00\u4eb2\u82b3\u6cfd\uff0c\u53d1\u751f\u4ec0\u4e48\u8d85\u53cb\u8c0a\u5173\u7cfb\u3002\u5c3d\u7ba1\u5982\u6b64\uff0c\u743c\u6069\u5728\u77ed\u6682\u7684\u72b9\u8c6b\u4e4b\u540e\uff0c\u8fd8\u662f\u9009\u62e9\u4e86\u653e\u5f03\u3002</p><p>\u4ed6\u559c\u6b22\u7f8e\u5973\uff0c\u4f46\u5374\u5bb3\u6015\u9ebb\u70e6\u3002\u76f4\u89c9\u544a\u8bc9\u4ed6\uff0c\u8fd9\u4f4d\u76f2\u76ee\u5973\u5b50\u8eab\u4e0a\u9690\u85cf\u7740\u9ed1\u6697\u800c\u5371\u9669\u7684\u79d8\u5bc6\uff0c\u8fd8\u662f\u5c3d\u91cf\u5c11\u6253\u4ea4\u9053\u4e3a\u5999\u3002\u6885\u83f2\u65af\u9a6c\u4e0a\u5c31\u8981\u53bb\u9634\u5f71\u8c37\uff0c\u81ea\u5df1\u4e5f\u5373\u5c06\u79bb\u5f00\u6b64\u5730\uff0c\u6ca1\u5fc5\u8981\u591a\u62db\u60f9\u9ebb\u70e6\u2014\u2014\u81ea\u5df1\u8eab\u4e0a\u7684\u9ebb\u70e6\uff0c\u5b9e\u5728\u662f\u5df2\u7ecf\u591f\u591a\u4e86\u3002</p><p>\u505a\u4e86\u4e24\u4e2a\u6df1\u547c\u5438\uff0c\u5c06\u6742\u5ff5\u5b8c\u5168\u6448\u5f03\uff0c\u743c\u6069\u7ee7\u7eed\u4f4e\u5934\u770b\u4e66\uff0c\u6e29\u4e60\u9b54\u6cd5\u3002\u661f\u4e4b\u82b1\u6bd4\u8d5b\u7684\u8fd9\u4e09\u5929\u91cc\uff0c\u4ed6\u5e76\u6ca1\u6709\u5f53\u771f\u95f2\u7740\uff0c\u9664\u4e86\u548c\u6885\u83f2\u65af\u6b22\u597d\u4e4b\u5916\uff0c\u5269\u4f59\u65f6\u95f4\u51e0\u4e4e\u5168\u90fd\u6295\u5165\u5230\u5b66\u4e60\u548c\u953b\u70bc\u4e4b\u4e2d\uff0c\u201c\u5c3c\u67cf\u4e4b\u97ad\u7b1e\u201d\u3001\u201c\u4e03\u5f69\u55b7\u5c04\u201d\u3001\u201c\u6b7b\u4e91\u672f\u201d\u548c\u201c\u9178\u96e8\u66b4\u201d\u90fd\u5df2\u7ecf\u6089\u6570\u638c\u63e1\uff0c\u867d\u7136\u8bf4\u4e0d\u4e0a\u7cbe\u901a\uff0c\u65bd\u5c55\u8d77\u6765\u6548\u679c\u4e5f\u5df2\u7ecf\u5408\u683c\uff0c\u6b64\u5916\u8fd8\u5b66\u4e60\u4e86\u56db\u4e94\u4e2a\u5165\u95e8\u7ea7\u7684\u6cd5\u672f\uff0c\u4f8b\u5982\u201c\u94a2\u94c1\u53f7\u89d2\u201d\u3001\u201c\u504f\u659c\u76fe\u201d\u4e4b\u7c7b\uff0c\u5176\u6548\u7387\u7740\u5b9e\u9ad8\u5f97\u60ca\u4eba\u3002\u8fd9\u5c31\u662f\u9ad8\u9636\u5deb\u5e08\u7684\u4f18\u52bf\u6240\u5728\u4e86\uff0c\u4e00\u65e6\u51dd\u6210\u771f\u540d\uff0c\u4ed6\u4eec\u5bf9\u9b54\u6cd5\u7684\u7406\u89e3\u3001\u4e0e\u9b54\u7f51\u7684\u6c9f\u901a\u90fd\u4f1a\u9661\u7136\u63d0\u5347\uff0c\u8d85\u51fa\u539f\u672c\u7684\u5c42\u6b21\uff0c\u8e0f\u5165\u5168\u65b0\u9886\u57df\uff0c\u518d\u53bb\u5b66\u4e60\u5177\u4f53\u7684\u6cd5\u672f\u65f6\u5c31\u4f1a\u6709\u4e8b\u534a\u529f\u500d\u7684\u6548\u679c\uff0c\u800c\u4e14\u743c\u6069\u6240\u5b66\u7684\u8fd9\u4e9b\u90fd\u662f\u8f83\u4e3a\u4f4e\u9636\u7684\u6cd5\u672f\uff0c\u539f\u672c\u5c31\u5e26\u4e86\u201c\u8865\u4e60\u201c\u6027\u8d28\uff0c\u96be\u5ea6\u975e\u5e38\u4f4e\uff0c\u66f4\u52a0\u4e0d\u5728\u8bdd\u4e0b\u3002\u6b64\u5916\u8fd8\u6709\u4e00\u4e2a\u5f88\u91cd\u8981\u7684\u539f\u56e0\uff1a\u4ed6\u6bd5\u7adf\u662f\u79d1\u73ed\u51fa\u8eab\uff0c\u5728\u9634\u9b42\u57ce\u7684\u5deb\u5e08\u5b66\u9662\u4e2d\u5f85\u4e86\u6574\u6574\u4e94\u5e74\uff0c\u63a5\u53d7\u7684\u662f\u8fd9\u4e2a\u4e16\u754c\u4e0a\u6700\u7cfb\u7edf\u548c\u6700\u4e25\u8c28\u7684\u9b54\u6cd5\u6559\u80b2\u3002\u79d1\u73ed\u51fa\u8eab\u7684\u5bb6\u4f19\uff0c\u6216\u8bb8\u601d\u7ef4\u7aa0\u81fc\u592a\u6df1\uff0c\u5728\u521b\u9020\u529b\u4e0a\u6709\u6240\u6b20\u7f3a\uff0c\u4f46\u5728\u7a0b\u5f0f\u5316\u7684\u5b66\u4e60\u9886\u609f\u4e0a\uff0c\u662f\u7edd\u975e\u666e\u901a\u5deb\u5e08\u53ef\u6bd4\u7684\u3002</p><p>\u4eba\u4e00\u8ba4\u771f\u8d77\u6765\uff0c\u65f6\u95f4\u5c31\u8fc7\u5f97\u98de\u5feb\uff0c\u5f53\u4ed6\u518d\u5ea6\u62ac\u8d77\u5934\u65f6\uff0c\u53d1\u73b0\u5df2\u7ecf\u5230\u4e86\u508d\u665a\u65f6\u5206\uff0c\u7a97\u5916\u659c\u9633\u897f\u5782\uff0c\u708a\u70df\u5728\u66ae\u8272\u4e2d\u8885\u8885\u5347\u8d77\uff0c\u8fdc\u5904\u7684\u9547\u4e2d\u4e5f\u5b89\u9759\u4e0b\u6765\uff0c\u4e0d\u590d\u767d\u65e5\u7684\u55a7\u56a3\u70ed\u95f9\u3002\u743c\u6069\u5408\u4e0a\u4e66\u672c\uff0c\u63c9\u63c9\u9178\u6da9\u7684\u773c\u775b\uff0c\u7ad9\u8d77\u6765\u8212\u5c55\u8eab\u4f53\uff0c\u6b63\u51c6\u5907\u518d\u4f38\u4e24\u4e2a\u61d2\u8170\uff0c\u95e8\u88ab\u8f7b\u8f7b\u63a8\u5f00\uff0c\u6885\u83f2\u65af\u8d70\u4e86\u8fdb\u6765\u3002</p><p>\u201c\u603b\u7b97\u5b8c\u4e86\uff0c\u201d\u5979\u4e00\u8fb9\u8bf4\u7740\uff0c\u968f\u624b\u628a\u5916\u5957\u6254\u5728\u6905\u5b50\u4e0a\uff0c\u968f\u53e3\u62b1\u6028\u7740\uff0c\u201c\u5e0c\u671b\u4ee5\u540e\u518d\u4e5f\u4e0d\u7528\u53c2\u52a0\u8fd9\u79cd\u6bd4\u8d5b\u3002\u201d</p><p>\u201c\u90a3\u4e0d\u592a\u53ef\u80fd\uff0c\u201d\u743c\u6069\u6307\u51fa\u5979\u7684\u60f3\u6cd5\u5b8c\u5168\u4e0d\u5207\u5b9e\u9645\uff0c\u201c\u4f60\u4e0d\u53c2\u52a0\u4e86\uff0c\u4f60\u4eec\u6559\u4f1a\u600e\u4e48\u529e\uff1f\u4ece\u54ea\u91cc\u80fd\u518d\u627e\u4e00\u4e2a\u50cf\u4f60\u8fd9\u6837\u6f02\u4eae\u7684\u5973\u5b69\u5b50\u5462\u3002\u201d</p><p>\u201c\u90a3\u6211\u4e0d\u4e5f\u6ca1\u80fd\u62ff\u5230\u51a0\u519b\u4e48\uff0c\u201d\u6885\u83f2\u65af\u77a5\u4e86\u4ed6\u4e00\u773c\uff0c\u4eff\u4f5b\u4e0d\u7ecf\u610f\u5730\u8bf4\uff0c\u201c\u53ea\u662f\u7b2c\u4e09\u540d\u800c\u5df2\u5462\u3002\u201d</p><p>\u201c\u90a3\u5e2e\u8bc4\u59d4\u7684\u5ba1\u7f8e\u89c2\u6709\u95ee\u9898\u7f62\u4e86\uff0c\u201d\u743c\u6069\u7acb\u523b\u7528\u65a9\u9489\u622a\u94c1\u7684\u8bed\u6c14\u8bf4\uff0c\u201c\u5728\u6211\u5fc3\u91cc\uff0c\u4f60\u624d\u662f\u7b2c\u4e00\u3002\u201d</p><p>\u201c\u771f\u7684\uff1f\u201d\u6885\u83f2\u65af\u53cd\u95ee\u3002</p><p>\u201c\u771f\u7684\u3002\u201d</p><p>\u5c11\u5973\u504f\u8fc7\u5934\uff0c\u9759\u9759\u770b\u7740\u4ed6\uff0c\u8138\u4e0a\u7684\u795e\u60c5\u4f3c\u7b11\u975e\u7b11\uff0c\u8ba9\u743c\u6069\u4e0d\u4ec5\u5fc3\u91cc\u6709\u4e9b\u60f4\u60f4\u7136\uff0c\u6df1\u6015\u5bf9\u65b9\u63a5\u7740\u95ee\u51fa\u201c\u90a3\u6211\u548c\u73ca\u5609\u59d0\u59d0\u76f8\u6bd4\uff0c\u8c01\u662f\u7b2c\u4e00\u201d\u4e4b\u7c7b\u7684\u95ee\u9898\uff0c\u90a3\u53ef\u5c31\u5b8c\u86cb\u4e86\u3002\u5e78\u597d\uff0c\u6885\u83f2\u65af\u5e76\u6ca1\u6709\u8fd9\u4e48\u65e0\u804a\uff0c\u800c\u662f\u4e3b\u52a8\u6362\u4e86\u8bdd\u9898\u3002</p><p>\u201c\u665a\u4e0a\u7684\u5bb4\u4f1a\uff0c\u4f60\u8981\u548c\u6211\u4e00\u8d77\u53bb\u4e48\uff1f\u201d\u5979\u95ee\u3002</p><p>\u743c\u6069\u6447\u6447\u5934\u3002</p><p>\u201c\u4e0d\u53bb\uff1f\u201d\u6885\u83f2\u65af\u4f3c\u4e4e\u7565\u7565\u6709\u4e9b\u8be7\u5f02\u3002</p><p>\u201c\u55ef\u3002\u201d</p><p>\u6309\u7167\u5854\u745f\u8c37\u7684\u4f20\u7edf\uff0c\u5728\u661f\u4e4b\u82b1\u7ed3\u675f\u5f53\u665a\u4f1a\u4e3e\u884c\u76db\u5927\u7684\u7bdd\u706b\u665a\u4f1a\uff0c\u5168\u9547\u5c45\u6c11\u548c\u5916\u6765\u5ba2\u4eba\u90fd\u53ef\u4ee5\u53c2\u52a0\uff0c\u5927\u5bb6\u901a\u5bb5\u8fbe\u65e6\u7684\u72c2\u6b22\uff0c\u76f4\u81f3\u7b2c\u4e8c\u65e5\u9ece\u660e\u624d\u7ed3\u675f\u3002\u5728\u665a\u4f1a\u4e0a\uff0c\u6240\u6709\u672a\u5a5a\u3001\u5355\u8eab\u4e14\u5e74\u6ee1\u5341\u516d\u5c81\uff08\u5373\u6210\u5e74\uff09\u7684\u7537\u6027\u6216\u5973\u6027\u90fd\u53ef\u4ee5\u5411\u81ea\u5df1\u5fc3\u4eea\u7684\u7231\u6155\u5bf9\u8c61\u53d1\u51fa\u9080\u8bf7\uff0c\u5982\u679c\u5bf9\u65b9\u63a5\u53d7\u7684\u8bdd\uff0c\u4fbf\u53ef\u4ee5\u643a\u624b\u5171\u5ea6\u826f\u5bb5\uff0c\u5929\u4eae\u4e4b\u540e\u4fbf\u5206\u9053\u626c\u9573\uff0c\u53ea\u4f59\u56de\u5fc6\u2014\u2014\u7b80\u5355\u6765\u8bf4\u5c31\u662f\u4e00\u591c\u60c5\u3002\u5f53\u7136\uff0c\u5728\u661f\u4e4b\u82b1\u6bd4\u8d5b\u4e2d\u7efd\u653e\u5f02\u5f69\u7684\u90a3\u4e9b\u5973\u5b69\u4eec\uff0c\u65e0\u7591\u662f\u665a\u4f1a\u4e2d\u7684\u8000\u773c\u660e\u661f\uff0c\u662f\u6700\u53d7\u5173\u6ce8\u3001\u4f1a\u6536\u5230\u6700\u591a\u9080\u8bf7\u7684\u5bf9\u8c61\u3002\u636e\u8bf4\u8fd9\u79cd\u4e60\u4fd7\u7531\u6765\u5df2\u4e45\uff0c\u6700\u65e9\u53ef\u4ee5\u4e0a\u6eaf\u81f3\u5343\u5e74\u524d\u7684\u7cbe\u7075\u738b\u56fd\u65f6\u4ee3\uff0c\u4e00\u76f4\u6d41\u4f20\u81f3\u4eca\uff0c\u867d\u6709\u6cbf\u9769\uff0c\u4f46\u57fa\u672c\u4e0d\u53d8\u3002\u63d0\u5c14\u6559\u4f1a\u8fdb\u5165\u5854\u745f\u8c37\u4e4b\u540e\uff0c\u66fe\u7ecf\u6709\u9ad8\u9636\u795e\u804c\u4eba\u5458\u89c9\u5f97\u6b64\u4e3e\u6709\u4f24\u98ce\u5316\uff0c\u6709\u6096\u4f26\u7406\uff0c\u63d0\u8bae\u7acb\u6cd5\u7981\u6b62\uff0c\u4f46\u4ed6\u7684\u89c2\u70b9\u6ca1\u6709\u88ab\u666e\u904d\u63a5\u53d7\u3002\u5f53\u65f6\u5728\u6559\u4f1a\u5185\u90e8\u8ba8\u8bba\u65f6\uff0c\u5927\u591a\u6570\u4eba\u90fd\u8ba4\u4e3a\u201c\u6b64\u4e8b\u867d\u4e0d\u5b9c\u5021\u5bfc\uff0c\u4f46\u786e\u7cfb\u4e2a\u4eba\u81ea\u7531\uff0c\u5f7c\u6b64\u60c5\u613f\uff0c\u65e0\u635f\u516c\u76ca\uff0c\u6545\u65e0\u9700\u5f3a\u884c\u5e72\u6d89\u3002\u201d\u6700\u7ec8\u7684\u7ed3\u679c\u662f\u9881\u5e03\u4e86\u4e00\u9879\u9650\u5236\uff0c\u89c4\u5b9a\u5fc5\u987b\u662f\u201c\u5355\u8eab\u672a\u5a5a\u201d\u8005\u624d\u80fd\u9080\u8bf7\u4ed6\u4eba\u6216\u63a5\u53d7\u9080\u8bf7\uff0c\u4ee5\u514d\u7834\u574f\u5a5a\u59fb\u5bb6\u5ead\uff0c\u589e\u52a0\u793e\u4f1a\u77db\u76fe\u2014\u2014\u4ee5\u524d\u662f\u53ea\u6709\u201c\u6210\u5e74\u201d\u8fd9\u4e00\u9879\u9650\u5236\u7684\u3002</p><p>\u201c\u4e3a\u4ec0\u4e48\u4e0d\u53bb\uff1f\u201d\u5c11\u5973\u95ee\uff0c\u201c\u4f1a\u6709\u5f88\u591a\u7f8e\u5973\u5462\u3002\u201d</p><p>\u201c\u6211\u5bf9\u7f8e\u5973\u4e0d\u611f\u5174\u8da3\u3002\u201d\u743c\u6069\u4e49\u6b63\u8bcd\u4e25\u5730\u8bf4\u3002</p><p>\u201c\u5207\uff0c\u9a97\u4eba\uff01\u201d\u6885\u83f2\u65af\u4e00\u9488\u89c1\u8840\u5730\u63ed\u7a7f\u4e86\u743c\u6069\u7684\u8c0e\u8a00\uff0c\u201c\u4f60\u53ea\u662f\u5bf9\u6ca1\u6cd5\u52fe\u642d\u4e0a\u624b\u7684\u7f8e\u5973\u4e0d\u611f\u5174\u8da3\u5427\u3002\u201d</p><p>\u201c\u2026\u2026\u4f60\u53ef\u4e0d\u53ef\u4ee5\u4e0d\u8981\u8bf4\u5f97\u8fd9\u4e48\u76f4\u767d\u3002\u201d</p><p>\u743c\u6069\u6027\u683c\u4e0d\u7231\u70ed\u95f9\uff0c\u5bf9\u8fd9\u79cd\u4eba\u6570\u591a\u3001\u89c4\u6a21\u5927\u7684\u96c6\u4f1a\u6d3b\u52a8\u786e\u5b9e\u662f\u6709\u7740\u672c\u80fd\u7684\u6392\u65a5\u548c\u6297\u62d2\uff0c\u800c\u4e14\u6700\u91cd\u8981\u7684\u662f\uff0c\u4ed6\u53bb\u53c2\u52a0\u505a\u4ec0\u4e48\u5462\uff1f\u6709\u6885\u83f2\u65af\u548c\u51db\u4e24\u4eba\u5728\uff0c\u96be\u9053\u4ed6\u8fd8\u6562\u53bb\u52fe\u642d\u5176\u4ed6\u5973\u5b69\u5b50\u4e0d\u6210\uff1f\u53ea\u6015\u8fde\u642d\u8baa\u51e0\u53e5\u90fd\u4f1a\u88ab\u6390\uff0c\u8fd8\u4e0d\u5982\u7559\u5728\u5bb6\u91cc\u7761\u5927\u89c9\u5462\u3002</p><p>\u201c\u4e0d\u8fc7\u5c31\u7b97\u662f\u53ea\u80fd\u770b\u7740\uff0c\u9971\u9971\u773c\u798f\u4e5f\u4e0d\u9519\u554a\uff0c\u603b\u6bd4\u4e00\u4e2a\u4eba\u8eb2\u5728\u5bb6\u91cc\u597d\u5427\uff0c\u201d\u6885\u83f2\u65af\u6709\u4e9b\u6000\u7591\u5730\u770b\u7740\u81ea\u5df1\u7684\u7537\u53cb\uff0c\u201c\u8fd8\u662f\u8bf4\uff0c\u4f60\u6709\u522b\u7684\u7ea6\u4f1a\uff1f\u201d</p><p>\u827e\u5f25\u8587\uff0c\u592a\u806a\u660e\u5bf9\u5973\u5b69\u5b50\u800c\u8a00\u4e5f\u4e0d\u662f\u4ec0\u4e48\u597d\u4e8b\u3002</p><p>\u522b\u7684\u7ea6\u4f1a\u786e\u5b9e\u6709\uff0c\u4f46\u743c\u6069\u5e76\u672a\u6253\u7b97\u53bb\u8d74\u7ea6\uff0c\u4ed6\u53ea\u662f\u7d2f\u4e86\uff0c\u61d2\u5f97\u52a8\u5f39\u3002\u800c\u4e14\u2026\u2026\u4e3a\u4ec0\u4e48\u5927\u5bb6\u90fd\u89c9\u5f97\u4ed6\u662f\u4e3a\u4e86\u7f8e\u8272\u53ef\u4ee5\u4e0d\u987e\u4e00\u5207\u7684\u4eba\u5462\uff0c\u81ea\u5df1\u660e\u660e\u6ca1\u6709\u90a3\u4e48\u597d\u8272\u5982\u547d\u554a\u3002</p><p>\u201c\u6ca1\u6709\u5566\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6211\u4fdd\u8bc1\u4e56\u4e56\u5728\u5bb6\u7b49\u4f60\u4eec\u56de\u6765\u3002\u201d</p><p>\u201c\u90a3\u4f60\u5c31\u4e0d\u62c5\u5fc3\uff0c\u665a\u4f1a\u4e0a\u6709\u522b\u7684\u7537\u4eba\u6765\u9080\u8bf7\u6211\u548c\u51db\uff1f\u201d\u6885\u83f2\u65af\u6545\u610f\u95ee\uff0c\u201c\u6211\u6216\u8bb8\u7f62\u4e86\uff0c\u51db\u7684\u7231\u6155\u8005\u53ef\u662f\u4f1a\u6709\u5f88\u591a\u5f88\u591a\u54e6\u3002\u201d</p><p>\u201c\u5979\u6562\uff01\u201d</p><p>\u660e\u77e5\u6885\u83f2\u65af\u662f\u5728\u9017\u4ed6\uff0c\u743c\u6069\u4e5f\u5c31\u4ee5\u534a\u5f00\u73a9\u7b11\u7684\u8bed\u6c14\u56de\u7b54\u3002\u4e0d\u8fc7\u867d\u7136\u5634\u4e0a\u5f3a\u786c\uff0c\u5fc3\u5e95\u5012\u8fd8\u771f\u6709\u51e0\u5206\u60f4\u60f4\uff0c\u4ed6\u81ea\u7136\u4e0d\u62c5\u5fc3\u6885\u83f2\u65af\u4f1a\u63a5\u53d7\u522b\u7684\u7537\u5b50\u9080\u8bf7\uff0c\u4f46\u51db\u5c31\u4e0d\u597d\u8bf4\u4e86\uff0c\u5c0f\u5973\u5deb\u7231\u70ed\u95f9\uff0c\u597d\u65b0\u9c9c\uff0c\u6027\u683c\u98de\u626c\u8df3\u8131\uff0c\u548c\u743c\u6069\u4e4b\u95f4\u4e5f\u8c08\u4e0d\u4e0a\u591a\u6df1\u7684\u611f\u60c5\uff0c\u5018\u82e5\u78b0\u4e0a\u66f4\u6709\u9b45\u529b\u7684\u7537\u5b50\uff0c\u672a\u5fc5\u4e0d\u4f1a\u52a8\u5fc3\u554a\u3002</p><p>\u770b\u51fa\u743c\u6069\u7684\u5fc3\u601d\uff0c\u6885\u83f2\u65af\u5657\u55e4\u4e00\u7b11\uff0c\u201c\u597d\u5566\u597d\u5566\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u653e\u5fc3\u5427\uff0c\u6211\u4f1a\u66ff\u4f60\u770b\u7740\u5979\u7684\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\uff0c\u4f60\u600e\u4e48\u505a\uff1f\u201d</p><p>\u201c\u4e0d\u600e\u4e48\u505a\u554a\uff0c\u201d\u5c11\u5973\u8038\u8038\u80a9\uff0c\u201c\u6211\u4e00\u76f4\u5f85\u5728\u5979\u8eab\u8fb9\u5c31\u662f\u4e86\u3002\u201d</p><p>\u201c\u5514\uff1f\u201d\u743c\u6069\u4e0d\u89e3\uff0c\u201c\u90a3\u53c8\u600e\u4e48\u6837\u5462\uff1f\u201d</p><p>\u201c\u665a\u4f1a\u4e2d\u80fd\u591f\u4f5c\u4e3a\u9080\u8bf7\u5bf9\u8c61\u7684\uff0c\u5fc5\u987b\u662f\u672a\u5a5a\u800c\u4e14\u5355\u8eab\u8005\uff0c\u201d\u6885\u83f2\u65af\u89e3\u91ca\uff0c\u201c\u5982\u679c\u4e00\u4e2a\u5973\u5b69\u5b50\u5df2\u7ecf\u6709\u4f34\u4fa3\u4e86\uff0c\u5176\u4ed6\u4eba\u5c31\u4e0d\u80fd\u518d\u9080\u8bf7\u5979\u3002\u201d</p><p>\u201c\u539f\u6765\u5982\u6b64\u2014\u2014\u7b49\u4e00\u4e0b\uff0c\u5979\u7684\u4f34\u4fa3\u662f\u8c01\uff1f\u201d</p><p>\u5c11\u5973\u6307\u4e86\u6307\u81ea\u5df1\u3002</p><p>\u201c\u8fd9\u6837\u4e5f\u53ef\u4ee5\u5417\uff1f\u201d\u743c\u6069\u6115\u7136\uff0c\u201c\u4f60\u548c\u5979\u90fd\u662f\u5973\u5b69\u5b50\u2026\u2026\u201d</p><p>\u201c\u5f53\u7136\u53ef\u4ee5\uff0c\u201d\u5979\u683c\u683c\u7b11\u8d77\u6765\uff0c\u201c\u4f60\u4e0d\u77e5\u9053\u4e48\uff0c\u5728\u5854\u745f\u8c37\uff0c\u6216\u8005\u8bf4\u5728\u6574\u4e2a\u8c37\u5730\uff0c\u8fd9\u79cd\u4e8b\u60c5\u90fd\u662f\u633a\u5e38\u89c1\u7684\u3002\u201d</p><p>\u201c\u2026\u2026\u662f\u5462\uff0c\u6211\u5fd8\u4e86\u8fd9\u91cc\u4ee5\u524d\u662f\u7cbe\u7075\u65cf\u7684\u5730\u76d8\u3002\u201d</p><p>\u7cbe\u7075\u65cf\u4ee5\u7ea4\u7ec6\u3001\u7cbe\u81f4\u3001\u4fca\u79c0\u8457\u79f0\uff0c\u65e0\u8bba\u7537\u5973\u90fd\u662f\u5929\u751f\u7684\u7f8e\u4eba\uff0c\u5982\u679c\u4ee5\u4eba\u7c7b\u7684\u6807\u51c6\u8861\u91cf\uff0c\u5c31\u662f\u504f\u5973\u6027\u5316\uff0c\u5973\u6027\u66f4\u50cf\u5973\u6027\uff0c\u800c\u7537\u6027\u66f4\u50cf\u4f2a\u5a18\u3002\u6216\u8bb8\u4e5f\u6b63\u662f\u56e0\u4e3a\u8fd9\u4e2a\u7f18\u6545\uff0c\u7cbe\u7075\u65cf\u4e2d\u540c\u6027\u604b\u7684\u6bd4\u4f8b\u975e\u5e38\u9ad8\uff0c\u5728\u6709\u4e9b\u5730\u533a\u751a\u81f3\u851a\u4e3a\u98ce\u6c14\u3002\u8c37\u5730\u4ee5\u524d\u662f\u7cbe\u7075\u738b\u56fd\uff0c\u540e\u6765\u4eba\u7c7b\u5927\u91cf\u79fb\u6c11\u800c\u6765\uff0c\u4e24\u65cf\u6742\u5c45\u4ea4\u5f80\u6570\u767e\u5e74\uff0c\u591a\u6709\u901a\u5a5a\uff0c\u81f3\u4eca\u4e03\u6210\u4ee5\u4e0a\u7684\u8c37\u5730\u4eba\u90fd\u591a\u591a\u5c11\u5c11\u5e26\u70b9\u7cbe\u7075\u8840\u7edf\u3002\u201c\u5927\u64a4\u9000\u201d\u4e4b\u540e\uff0c\u7edd\u5927\u591a\u6570\u7cbe\u7075\u8fc1\u5f99\u5230\u6c38\u805a\u5c9b\uff0c\u8c37\u5730\u53d8\u6210\u4e86\u4eba\u7c7b\u7684\u805a\u5c45\u5730\uff0c\u4f46\u7cbe\u7075\u65cf\u7684\u5927\u91cf\u4f20\u7edf\u3001\u98ce\u4fd7\u90fd\u8fd8\u662f\u6d41\u4f20\u4e86\u4e0b\u6765\uff0c\u6bd4\u5982\u661f\u4e4b\u82b1\uff0c\u6bd4\u5982\u857e\u4e1d\u8fb9\u2026\u2026</p><p>\u201c\u90a3\u4f60\u4eec\u5141\u8bb8\u540c\u6027\u7ed3\u5a5a\u5417\uff1f\u201d\u743c\u6069\u9887\u6709\u4e9b\u597d\u5947\u5730\u95ee\u3002</p><p>\u201c\u4e0d\u5141\u8bb8\uff0c\u4e5f\u4e0d\u7981\u6b62\u3002\u201d</p><p>\u201c\u4f55\u89e3\uff1f\u201d</p><p>\u201c\u5c31\u662f\u8bf4\uff0c\u5854\u745f\u8c37\u7684\u6c11\u653f\u90e8\u95e8\u662f\u4e0d\u4f1a\u4e3a\u540c\u6027\u529e\u7406\u5a5a\u59fb\u767b\u8bb0\u7684\u3002\u4f46\u5982\u679c\u4f60\u771f\u60f3\u548c\u540c\u6027\u7ed3\u5a5a\u4e5f\u4e0d\u662f\u4e0d\u884c\uff0c\u53bb\u5f13\u8c37\u3001\u6218\u5f79\u8c37\u6216\u8005\u4f24\u75d5\u8c37\uff0c\u5b83\u4eec\u90fd\u662f\u5141\u8bb8\u540c\u6027\u7ed3\u5a5a\u7684\u3002\u800c\u4f60\u5728\u90a3\u4e9b\u5730\u65b9\u53d6\u5f97\u7684\u5a5a\u59fb\u767b\u8bb0\uff0c\u5728\u5854\u745f\u8c37\u4e5f\u662f\u88ab\u627f\u8ba4\u6709\u6548\u7684\u3002\u201d</p><p>\u201c\u539f\u6765\u5982\u6b64\u2026\u2026\u201d</p><p>\u5728\u5bb6\u4f11\u606f\u4e86\u5927\u7ea6\u5c0f\u65f6\uff0c\u6362\u4e86\u8eab\u8863\u670d\uff0c\u6885\u83f2\u65af\u518d\u5ea6\u51fa\u95e8\uff0c\u548c\u51db\u4e00\u8d77\u53bb\u53c2\u52a0\u665a\u4f1a\u3002\u5979\u5176\u5b9e\u4e5f\u4e0d\u7231\u70ed\u95f9\uff0c\u4f46\u4f5c\u4e3a\u83b7\u5956\u8005\u5fc5\u987b\u8981\u51fa\u573a\uff0c\u6ca1\u529e\u6cd5\uff0c\u800c\u4e14\u8fd8\u8981\u66ff\u743c\u6069\u770b\u7740\u51db\u5462\u3002</p><p>\u201c\u8bdd\u8bf4\uff0c\u4e3a\u4ec0\u4e48\u6211\u8981\u5e2e\u4f60\u505a\u8fd9\u79cd\u4e8b\u60c5\u554a\uff1f\u201d\u5979\u4e00\u8fb9\u7a7f\u4e0a\u5916\u5957\uff0c\u4e00\u8fb9\u534a\u771f\u534a\u5047\u5730\u62b1\u6028\uff0c\u201c\u4e0d\u89c9\u5f97\u6709\u54ea\u5730\u65b9\u4e0d\u5bf9\u52b2\u4e48\uff1f\u201d</p><p>\u201c\u5443\uff0c\u542c\u4f60\u8fd9\u4e48\u4e00\u8bf4\uff0c\u597d\u50cf\u662f\u6709\u70b9\u4e0d\u592a\u5bf9\u52b2\u2026\u2026\u201d</p><p>\u743c\u6069\u6709\u4e9b\u5c34\u5c2c\uff0c\u5e78\u597d\u8138\u76ae\u8fd8\u7b97\u539a\uff0c\u542b\u542b\u7cca\u7cca\u5e94\u4ed8\u8fc7\u53bb\uff0c\u6885\u83f2\u65af\u4e5f\u672a\u8ba4\u771f\u3002\u201c\u8d70\u5566\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u6211\u4eec\u4f1a\u65e9\u70b9\u56de\u6765\u7684\uff0c\u5927\u6982\u4e5d\u70b9\u949f\u5427\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u6885\u83f2\u65af\u79bb\u5f00\u540e\uff0c\u743c\u6069\u7ee7\u7eed\u770b\u4e66\u3002\u8fd9\u662f\u4e00\u4efd\u8bb2\u6388\u5982\u4f55\u5ef6\u957f\u5404\u79cd\u6301\u7eed\u6027\u6cd5\u672f\u4f5c\u7528\u65f6\u95f4\u7684\u8d44\u6599\uff0c\u4ee5\u524d\u5965\u6c83\u7ed9\u4ed6\u7684\uff0c\u662f\u80a5\u5deb\u5996\u81ea\u5df1\u603b\u7ed3\u51fa\u6765\u7684\u5fc3\u5f97\u4f53\u4f1a\uff0c\u867d\u7136\u53ea\u6709\u8584\u8584\u4e09\u9875\u7eb8\uff0c\u516d\u5343\u4f59\u5b57\uff0c\u5374\u8270\u6df1\u67af\u6da9\u5f97\u4eff\u4f5b\u5929\u4e66\uff0c\u5f88\u591a\u5730\u65b9\u90fd\u662f\u521d\u770b\u660e\u767d\uff0c\u518d\u770b\u53c8\u6a21\u7cca\uff0c\u4e91\u5c71\u96fe\u7f69\uff0c\u4f3c\u61c2\u975e\u61c2\uff0c\u770b\u5f97\u4ed6\u8111\u888b\u90fd\u9690\u9690\u4f5c\u75db\u3002\u9b54\u6cd5\u5b66\u4e00\u9053\uff0c\u7ec8\u7a76\u8fd8\u662f\u8981\u8bb2\u5e08\u627f\u7684\u3002\u5386\u53f2\u4e0a\u80fd\u591f\u6709\u6240\u6210\u5c31\u7684\u5deb\u5e08\uff0c\u4e00\u65b9\u9762\u81ea\u7136\u662f\u81ea\u5df1\u5929\u8d4b\u8fc7\u4eba\uff0c\u53e6\u4e00\u65b9\u9762\u4e5f\u5f80\u5f80\u90fd\u662f\u6709\u540d\u5e08\u6307\u70b9\uff0c\u771f\u6b63\u80fd\u591f\u5b8c\u5168\u81ea\u5b66\u6210\u624d\u8005\uff0c\u8fd1\u4e4e\u6ca1\u6709\u3002\u53ef\u60dc\u73b0\u5728\u5965\u6c83\u4e0d\u5728\u8eab\u8fb9\uff0c\u65e0\u6cd5\u8bf7\u6559\uff0c\u53ea\u80fd\u81ea\u5df1\u7422\u78e8\u4e86\u3002</p><p>\u5bf9\u4e86\uff0c\u4e0d\u662f\u8fd8\u6709\u4f4d\u5e7d\u7075\u5927\u5965\u672f\u5e08\u4e48\uff1f</p><p>\u743c\u6069\u6b63\u8003\u8651\u662f\u5426\u8981\u53bb\u8bf7\u6559\u5965\u5609\u83b1\u65af\uff0c\u623f\u95e8\u88ab\u8f7b\u8f7b\u6572\u4e86\u4e09\u4e0b\uff0c\u7136\u540e\u63a8\u5f00\uff0c\u838e\u73de\u514b\u8d70\u4e86\u8fdb\u6765\u3002</p></article>"}}}]);
