(window.webpackJsonp=window.webpackJsonp||[]).push([[53],{76:function(p){p.exports={chapterTitle:null,title:"\u7b2c1042\u7ae0 \u4e8c\u4e16\u7956\u5728\u4e1c\u5927\u7684\u4f20\u8bf4",content:"<article><h2>\u7b2c1042\u7ae0 \u4e8c\u4e16\u7956\u5728\u4e1c\u5927\u7684\u4f20\u8bf4</h2><p>\u3000\u3000\u201c\u4f60\u4eec\u8bf4\u8bf4\u770b\uff0c\u5929\u4e0b\u6709\u6211\u8fd9\u6837\u7684\u4e8c\u4e16\u7956\u5417\uff1f\u201d\u5f20\u77e5\u884c\u4e0e\u6c5f\u4e0a\u5143\u3001\u5218\u7235\u8d70\u8fc7\u6765\u65f6\uff0c\u5f20\u606a\u8170\u95f4\u7cfb\u7740\u56f4\u88d9\uff0c\u4e24\u53ea\u624b\u91cc\u62ff\u7740\u5228\u5b50\u6b63\u8e72\u5728\u5ba2\u5385\u524d\u7684\u8d70\u5eca\u91cc\u524a\u841d\u535c\u76ae\uff0c\u201c\u521a\u624d\u7eaa\u59d4\u6c5f\u4e66\u8bb0\u7684\u7231\u4eba\u8fc7\u6765\uff0c\u8fd8\u8ddf\u6211\u5988\u5938\u54b1\u5bb6\u7684\u4fdd\u59c6\u5207\u571f\u8c46\u4e1d\u7684\u6c34\u5e73\u771f\u662f\u9ad8\uff0c\u771f\u662f\u9ad8\u554a\u771f\u662f\u9ad8\u2014\u2014\u54e6\uff0c\u521a\u624d\u662f\u6211\u5207\u7684\u571f\u8c46\u4e1d\uff0c\u7b49\u4f1a\u513f\u8bf7\u4f60\u4eec\u597d\u597d\u7684\u54c1\u5c1d\u3002\u201d</p><p>\u3000\u3000\u6c5f\u4e0a\u5143\u3001\u5218\u7235\u54c8\u54c8\u5927\u7b11\uff0c\u6881\u683c\u73cd\u4ece\u5c4b\u91cc\u8d70\u51fa\u6765\uff0c\u4f38\u624b\u6390\u4f4f\u5f20\u606a\u7684\u8033\u6735\u62e7\u4e86\u4e00\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u5728\u6c5f\u4e3b\u4efb\u3001\u5218\u4e66\u8bb0\u9762\u524d\u80e1\u8bf4\u516b\u9053\u7684\u732e\u5b9d\uff0c\u8fd9\u6691\u5047\u90fd\u8fc7\u53bb\u5927\u534a\uff0c\u624d\u60f3\u8d77\u5230\u65b0\u829c\u770b\u4f60\u8001\u5a18\uff0c\u4f60\u4e0d\u662f\u6ca1\u826f\u5fc3\u7684\u4e8c\u4e16\u7956\u662f\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u987e\u6653\u6885\u5728\u540e\u9762\u5fae\u5fae\u800c\u7b11\uff0c\u7b11\u4e5f\u662f\u770b\u8d44\u683c\u7684\uff0c\u6c5f\u4e0a\u5143\u3001\u5218\u7235\u53ef\u4ee5\u54c8\u54c8\u5927\u7b11\uff0c\u4ee5\u793a\u4eb2\u8fd1\uff0c\u5979\u8981\u662f\u7b11\u5f97\u592a\u653e\u8086\uff0c\u5c31\u662f\u4e0d\u77e5\u5206\u5bf8\u3002\u5979\u770b\u5230\u8fc7\u5f88\u591a\u4e8c\u4e16\u7956\u5728\u957f\u8f88\u9762\u524d\u90fd\u4e56\u5de7\u4f36\u4fd0\u3001\u8ba8\u4eba\u559c\u597d\uff0c\u8fd9\u51e0\u4e4e\u662f\u5bb6\u5ead\u51fa\u8eab\u6240\u6d78\u67d3\u51fa\u6765\u7684\u5a9a\u4e0a\u672c\u80fd\uff0c\u5f53\u5e74\uff0c\u5979\u4e0e\u7f57\u5f52\u6e90\u7684\u513f\u5b50\u7f57\u6587\u4e5f\u63a5\u89e6\u8fc7\uff0c\u5728\u957f\u8f88\u9762\u524d\u4e0d\u6653\u5f97\u8868\u73b0\u51fa\u591a\u4e48\u7684\u5f6c\u5f6c\u6709\u793c\uff0c\u8c01\u80fd\u60f3\u5c31\u8fd9\u4e48\u4e2a\u5f6c\u5f6c\u6709\u793c\u7684\u5b98\u5ba6\u5b50\u5f1f\u4f1a\u8ddf\u4ed6\u8001\u5b50\u4e00\u6837\u6700\u540e\u90fd\u7ed9\u5224\u4e86\u6b7b\u7f13\uff1f\u53ea\u662f\u4e4b\u524d\u5f20\u606a\u6240\u8bf4\u7684\u90a3\u756a\u8bdd\u7ed9\u5979\u7684\u5370\u8c61\u6781\u6df1\uff0c\u5374\u53c8\u8bf4\u4e0d\u4e0a\u4ec0\u4e48\u611f\u89c9\uff0c\u6216\u8bb8\u4ed6\u90a3\u79cd\u8bf4\u8bdd\u7684\u8bed\u6c14\u8fc7\u4e8e\u6210\u719f\u4e86\u3002</p><p>\u3000\u3000\u8bf4\u53e5\u5b9e\u8bdd\uff0c\u987e\u6653\u6885\u4e5f\u6ca1\u6709\u60f3\u5230\u5f20\u77e5\u884c\u7684\u513f\u5b50\u4f1a\u6709\u597d\u53a8\u827a\uff0c\u5979\u4e0e\u5f20\u77e5\u884c\u7684\u79d8\u4e66\u6210\u4f59\u4e1c\u90fd\u6ca1\u6709\u8d44\u683c\u7559\u4e0b\u6765\u5403\u996d\uff0c\u5c06\u4e8b\u60c5\u6c47\u62a5\u5b8c\uff0c\u5c31\u544a\u8f9e\u79bb\u5f00\u3002</p><p>\u3000\u3000\u987e\u6653\u6885\u81ea\u5df1\u6ca1\u6709\u8f66\uff0c\u4e5f\u6ca1\u6709\u8d44\u683c\u914d\u8f66\u3001\u914d\u53f8\u673a\uff0c\u4e0d\u8fc7\u4e0a\u4e0b\u73ed\u603b\u4f1a\u6709\u987a\u8def\u8f66\u5750\uff0c\u4eca\u5929\u5c0f\u8f66\u73ed\u7684\u53f8\u673a\u90fd\u7ed9\u7559\u4e0b\u5b66\u4e60\u4ea4\u901a\u6cd5\u89c4\uff0c\u5979\u5c31\u5750\u6210\u4f59\u4e1c\u7684\u6851\u5854\u90a3\u56de\u5bb6\u53bb\u3002</p><p>\u3000\u3000\u7ecf\u8fc7\u5bcc\u8d35\u56ed\u522b\u5885\u533a\uff0c\u987e\u6653\u6885\u5f80\u91cc\u770b\u4e86\u4e00\u773c\uff0c\u5979\u77e5\u9053\u5f20\u77e5\u884c\u4e00\u5bb6\u5728\u642c\u8fdb\u5e02\u59d4\u5927\u9662\u524d\u5c31\u5750\u8fd9\u4e2a\u522b\u5885\u533a\u91cc\uff0c\u95ee\u6210\u4f59\u4e1c\uff1a\u201c\u5f20\u5e02\u957f\u5bb6\u5728\u5efa\u90ba\u4e5f\u6709\u623f\u5b50\u5427\uff1f\u201d\u5979\u521a\u624d\u5728\u5e02\u59d4\u5927\u9662\u770b\u5230\u90a3\u8f86\u505c\u5728\u6881\u683c\u73cd\u5e73\u65f6\u5f00\u7684\u798f\u7279\u65c1\u8fb9\u7684\u5954\u9a70S600\uff0c\u5fc3\u60f3\u81ea\u5df1\u5728\u65b0\u829c\u90fd\u6ca1\u6709\u770b\u5230\u8fc7\u8fd9\u6837\u7684\u8c6a\u534e\u8f66\uff0c\u90a3\u4e9b\u8bf4\u5f20\u5bb6\u5728\u6d77\u5dde\u662f\u8c6a\u5bcc\u7684\u4f20\u8a00\u5012\u662f\u4e0d\u5047\uff0c\u5f20\u77e5\u884c\u592b\u5987\u5728\u65b0\u829c\u8981\u6ce8\u610f\u5f71\u54cd\uff0c\u751f\u6d3b\u4e0d\u80fd\u592a\u5962\u534e\uff0c\u8fd9\u4e2a\u4e8c\u4e16\u7956\u5012\u6ca1\u6709\u4ec0\u4e48\u987e\u8651\uff0c\u5c3d\u60c5\u7684\u4eab\u53d7\uff0c\u53c8\u60f3\u5230\u4e4b\u524d\u5728\u8def\u4e0a\u4ed6\u6240\u8bf4\u7684\u8bdd\uff0c\u603b\u6709\u4e00\u79cd\u5f88\u602a\u5f02\u7684\u611f\u89c9\u3002</p><p>\u3000\u3000\u201c\u55ef\uff0c\u201d\u6210\u4f59\u4e1c\u662f\u5f20\u77e5\u884c\u7684\u79d8\u4e66\uff0c\u8fd9\u53ea\u662f\u6309\u7167\u7a0b\u5e8f\u5b89\u6392\u7684\uff0c\u5f20\u77e5\u884c\u5e76\u6ca1\u6709\u6307\u540d\u8981\u4ed6\u5f53\u79d8\u4e66\uff0c\u6240\u4ee5\u8bf4\u5173\u7cfb\u4eb2\u5bc6\u7a0b\u5ea6\u6bd4\u4e4b\u987e\u6653\u6885\u76f8\u5f53\uff0c\u4ed6\u4e5f\u6ca1\u6709\u76ae\u8089\u5c31\u8f7b\u4e86\u4e8c\u4e24\u3001\u81ea\u8ba4\u4e3a\u5728\u5e02\u653f\u5e9c\u7684\u5730\u4f4d\u5c31\u6bd4\u987e\u6653\u6885\u8fd8\u9ad8\uff0c\u987e\u6653\u6885\u5e73\u65e5\u91cc\u4e5f\u662f\u56f4\u7ed5\u5f20\u77e5\u884c\u8f6c\uff0c\u6709\u4e9b\u4e8b\u60c5\u4e5f\u6ca1\u6709\u4ec0\u4e48\u8bb3\u4e0d\u8bb3\u8a00\u7684\uff0c\u201c\u987e\u59d0\u4e0d\u662f\u8981\u9001\u4f60\u5bb6\u4e8e\u7af9\u53bb\u5efa\u90ba\u4e0a\u5b66\u5417\uff1f\u6709\u673a\u771f\u8981\u53bb\u62dc\u8bbf\u4e00\u4e0b\uff0c\u90a3\u624d\u53eb\u771f\u6b63\u7684\u6709\u94b1\u4eba\u5bb6\u3002\u6362\u6210\u662f\u6211\uff0c\u6307\u4e0d\u5b9a\u8fde\u5e02\u957f\u90fd\u4e0d\u60f3\u5f53\u4e86\uff0c\u5f20\u5e02\u957f\u6bcf\u5929\u90fd\u8fd9\u4e48\u8f9b\u82e6\uff0c\u6362\u6210\u522b\u4eba\u4e00\u5b9a\u7406\u89e3\u4e0d\u4e86\u2014\u2014\u987e\u59d0\u80fd\u5e38\u89c1\u8fc7\u5e26\u5f88\u6f02\u4eae\u5c0f\u9662\u5b50\u7684\u522b\u5885\u5427\uff0c\u90a3\u987e\u59d0\u6709\u6ca1\u6709\u89c1\u8fc7\u5e26\u7a7a\u4e2d\u82b1\u56ed\u7684\u9ad8\u5c42\u516c\u5bd3\uff1f\u201d</p><p>\u3000\u3000\u987e\u6653\u6885\u60f3\u8c61\u4e0d\u51fa\u7a7a\u4e2d\u82b1\u56ed\u662f\u4ec0\u4e48\u6982\u5ff5\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u65b0\u829c\u6709\u4ec0\u4e48\uff0c\u6211\u5c31\u89c1\u8bc6\u8fc7\u4ec0\u4e48\uff0c\u6211\u7684\u773c\u754c\u5c31\u90a3\u4e48\u70b9\u5bbd\uff0c\u54ea\u91cc\u89c1\u8fc7\u5e26\u7a7a\u4e2d\u82b1\u56ed\u7684\u9ad8\u5c42\u516c\u5bd3\u2014\u2014\u662f\u5728\u9876\u5c42\u7684\u9732\u53f0\u79cd\u4e9b\u82b1\u82b1\u8349\u8349\uff1f\u201d</p><p>\u3000\u3000\u201c\u662f\u9876\u697c\u7684\u9732\u53f0\u79cd\u4e9b\u82b1\u82b1\u8349\u8349\uff0c\u53e6\u5916\u780c\u51e0\u5ea7\u5047\u5c71\u3001\u9020\u6761\u5c0f\u6eaa\u3001\u5efa\u5ea7\u4ead\u5b50\uff0c\u4e5f\u5c31\u5927\u534a\u4ea9\u5730\u5427\uff01\u201d</p><p>\u3000\u3000\u201c\u5443\uff01\u201d\u987e\u6653\u6885\u5f53\u7136\u80fd\u77e5\u9053\u534a\u4ea9\u5730\u6709\u591a\u5927\uff0c\u5bcc\u8d35\u56ed\u6700\u597d\u7684\u522b\u5885\u5360\u5730\u4e5f\u5c31\u5927\u534a\u4ea9\u5730\uff0c\u5482\u5482\u820c\u6ca1\u6709\u8bf4\u4ec0\u4e48\uff0c\u5fc3\u60f3\u8fd8\u6709\u5047\u5c71\u3001\u6eaa\u6c34\uff0c\u90a3\u516c\u5bd3\u4f4f\u5b85\u7684\u5962\u534e\u7a0b\u5ea6\u8fd8\u771f\u4e0d\u662f\u5979\u6240\u80fd\u60f3\u8c61\u7684\uff0c\u5fc3\u60f3\u5e02\u957f\u5f20\u77e5\u884c\u5bb6\u91cc\u5c31\u5f20\u606a\u4e00\u4eba\u5728\u5efa\u90ba\uff0c\u90a3\u5b85\u5b50\u4e5f\u662f\u8fd9\u4e8c\u4e16\u7956\u4e00\u4e2a\u4eba\u4eab\u53d7\u5427\uff0c\u53c8\u60f3\u6216\u8bb8\u8fd8\u6709\u90a3\u4e0d\u65ad\u53d8\u6362\u7684\u6f02\u4eae\u5973\u5b69\u5b50\u3002</p><p>\u3000\u3000\u6210\u4f59\u4e1c\u4e5f\u5c31\u8ddf\u987e\u6653\u6885\u968f\u610f\u804a\u7740\uff0c\u8fd9\u4e9b\u4e8b\u60c5\u987e\u6653\u6885\u8fdf\u65e9\u4f1a\u77e5\u9053\uff0c\u81f3\u4e8e\u5176\u4ed6\u81ea\u5df1\u6240\u89c2\u5bdf\u5230\u7684\u4e8b\u60c5\uff0c\u5012\u4e0d\u80fd\u80e1\u4e71\u7684\u8bf4\uff0c\u4ed6\u7684\u524d\u7a0b\u5b8c\u5168\u5bc4\u6258\u5728\u5f20\u77e5\u884c\u7684\u8eab\u4e0a\u3002</p><p>\u3000\u3000\u5230\u65b0\u95f8\u5c0f\u533a\uff0c\u6210\u4f59\u4e1c\u505c\u4e0b\u6765\u8ba9\u987e\u6653\u6885\u4e0b\u8f66\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u987e\u59d0\u600e\u4e48\u8fd8\u4f4f\u8fd9\u91cc\uff0c\u5c31\u6ca1\u6709\u60f3\u8fc7\u6362\u623f\u5b50\uff1f\u201d</p><p>\u3000\u3000\u201c\u624d\u4f4f\u4e86\u56db\u5e74\u591a\u5c31\u6362\u623f\u5b50\uff0c\u591a\u53ef\u60dc\u554a\uff0c\u6211\u53ef\u6ca1\u6709\u8fd9\u7cbe\u529b\u6298\u817e\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u542c\u8bf4\u536b\u5a76\u4e5f\u4f4f\u8fd9\u91cc\uff0c\u987e\u59d0\u5728\u8fd9\u91cc\u89c1\u5230\u8fc7\uff1f\u201d</p><p>\u3000\u3000\u201c\u536b\u5a76\u554a\uff0c\u5728\u5c0f\u533a\u91cc\u4e5f\u53ea\u89c1\u8fc7\u51e0\u56de\uff0c\u6ca1\u6709\u5728\u5f20\u5e02\u957f\u5bb6\u89c1\u5f97\u591a\u3002\u201d</p><p>\u3000\u3000\u8bf4\u8d77\u5f20\u77e5\u884c\u5bb6\u7684\u7638\u817f\u4fdd\u59c6\u536b\u5a76\uff0c\u987e\u6653\u6885\u53c8\u60f3\u8d77\u4e09\u5e74\u524d\u53d1\u751f\u7684\u7f57\u5f52\u6e90\u6848\uff0c\u90a3\u53ef\u662f\u7ed9\u65b0\u829c\u6458\u6389\u4e00\u4e2a\u5927\u6bd2\u7624\uff0c\u5168\u5e02\u4eba\u6c11\u62cd\u624b\u79f0\u5feb\u3002</p><p>\u3000\u3000\u636e\u8bf4\u4e8b\u60c5\u7684\u76f4\u63a5\u5bfc\u706b\u7d22\u5c31\u662f\u56e0\u4e3a\u5f20\u606a\u8ddf\u7f57\u5f52\u6e90\u7684\u513f\u5b50\u7f57\u6587\u4e3a\u536b\u5a76\u5728\u5e02\u62db\u5f85\u5bbe\u9986\u5de5\u4f5c\u7684\u5973\u513f\u536b\u5170\u4e89\u98ce\u5403\u9163\u6240\u81f4\u3002</p><p>\u3000\u3000\u524d\u4e24\u5e74\uff0c\u987e\u6653\u6885\u8fd8\u5728\u5c0f\u533a\u91cc\u80fd\u7ecf\u5e38\u80fd\u770b\u5230\u90a3\u5973\u5b69\u5b50\uff0c\u6f02\u4eae\u5f97\u7d27\uff0c\u5230\u5e95\u6709\u6ca1\u6709\u7ed9\u7f57\u5f52\u6e90\u7684\u513f\u5b50\u4e0b\u836f\u8ff7\u5978\u5f97\u901e\uff0c\u5404\u6709\u5404\u7684\u8bf4\u6cd5\uff0c\u53ea\u4e0d\u8fc7\u7f57\u5f52\u6e90\u8ddf\u513f\u5b50\u90fd\u7ed9\u5224\u4e86\u6b7b\u7f13\u662f\u56e0\u4e3a\u53e6\u5916\u7684\u7f6a\u884c\uff0c\u540e\u6765\u536b\u5a76\u4e00\u76f4\u7ed9\u5f20\u77e5\u884c\u5bb6\u5f53\u4fdd\u59c6\uff0c\u52a0\u4e0a\u5f20\u77e5\u884c\u592b\u5987\u8fd8\u5c06\u536b\u5170\u8ba4\u4f5c\u5e72\u5973\u513f\uff0c\u6709\u4e9b\u4e8b\u60c5\u5012\u6ca1\u6709\u4eba\u6562\u968f\u610f\u8bae\u8bba\u3002\u518d\u540e\u6765\uff0c\u536b\u5170\u53c8\u56de\u5b66\u6821\u8bfb\u4e66\uff0c\u53c8\u8ddf\u4e8e\u7af9\u540c\u4e00\u5c4a\u8003\u8fdb\u4e1c\u5927\uff0c\u8fd8\u771f\u662f\u76f8\u5f53\u4e0d\u9519\u7684\u5973\u5b69\u5b50\u2014\u2014\u4e4b\u524d\u53d1\u751f\u90a3\u4e48\u591a\u7684\u4e8b\u60c5\uff0c\u4e5f\u53ea\u662f\u751f\u6d3b\u5728\u5e95\u5c42\u7684\u60b2\u54c0\u5427\u3002</p><p>\u3000\u3000\u987e\u6653\u6885\u8fd9\u624d\u60f3\u8d77\u6765\u8fd9\u4e2a\u590f\u5929\u5728\u5c0f\u533a\u91cc\u8fd8\u6ca1\u6709\u770b\u5230\u8fc7\u536b\u5170\uff0c\u8fd9\u6b21\u4e5f\u6ca1\u6709\u968f\u5f20\u77e5\u884c\u7684\u513f\u5b50\u56de\u65b0\u829c\u6765\uff0c\u8be5\u4e0d\u4f1a\u4e00\u76f4\u90fd\u7559\u5728\u5efa\u90ba\u5427\uff1f</p><p>\u3000\u3000\u987e\u6653\u6885\u66ff\u6210\u4f59\u4e1c\u5173\u4e0a\u8f66\u95e8\uff0c\u5c31\u5f80\u5c0f\u533a\u91cc\u8d70\u53bb\u3002</p><p>\u3000\u3000\u65b0\u95f8\u5c0f\u533a\u7b97\u662f\u65b0\u829c\u6bd4\u8f83\u65b0\u7684\u5c0f\u533a\uff0c\u56e0\u4e3a\u65b0\u5efa\u65f6\u6709\u4e9b\u50cf\u536b\u5170\u5bb6\u90a3\u4e48\u7684\u62c6\u8fc1\u6237\u5b89\u7f6e\u5728\u5c0f\u533a\uff0c\u7ed9\u5916\u4eba\u7684\u611f\u89c9\uff0c\u5c0f\u533a\u7684\u6863\u6b21\u5c31\u6709\u4e9b\u4f4e\uff0c\u5176\u5b9e\u5c0f\u533a\u7684\u73af\u5883\u8fd8\u7b97\u4e0d\u9519\uff0c\u987e\u6653\u6885\u4e00\u5bb6\u56db\u53e3\u4f4f\u4e09\u5ba4\u4e00\u5385\u7684\u623f\u5b50\uff0c\u867d\u8bf4\u623f\u578b\u683c\u5c40\u6bd4\u8f83\u7d27\u51d1\uff0c\u4f46\u5728\u65b0\u829c\u8fd8\u7b97\u662f\u4e0d\u9519\u7684\uff1b\u518d\u8bf4\u81ea\u5df1\u521a\u5f53\u4e0a\u5e02\u653f\u5e9c\u529e\u526f\u4e3b\u4efb\u5c31\u6025\u7740\u6362\u623f\u5b50\uff0c\u4f20\u51fa\u53bb\u4e5f\u4e0d\u597d\u542c\u3002</p><p>\u3000\u3000\u987e\u6653\u6885\u6253\u5f00\u95e8\uff0c\u770b\u5230\u4e08\u592b\u8ddf\u513f\u5b50\u4e24\u4eba\u6253\u7740\u8d64\u818a\u5750\u5728\u5ba2\u5385\u91cc\u4e0b\u68cb\uff0c\u62b1\u6028\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4e2a\u5927\u8001\u7237\uff0c\u5728\u5bb6\u91cc\u5c31\u4e0d\u80fd\u5148\u5c06\u665a\u996d\u505a\u8d77\u6765\uff1f\u59b9\u59b9\u4eba\u5462\uff1f\u201d</p><p>\u3000\u3000\u201c\u59b9\u59b9\u8d56\u5728\u9648\u5b81\u73a9\uff0c\u201d\u8138\u4e0a\u7a1a\u6c14\u672a\u6d88\u7684\u4e8e\u7af9\u62ac\u8d77\u5934\u770b\u4e86\u4ed6\u5988\u4e00\u773c\uff0c\u53c8\u8bf4\u9053\uff0c\u201c\u7238\u7238\u8bf4\u4e5f\u8981\u96be\u5f97\u4eab\u53d7\u4e00\u56de\u7ed9\u9886\u5bfc\u4f3a\u5019\u7684\u5e78\u798f\uff0c\u4f60\u4e0d\u662f\u8bf4\u4eca\u5929\u4f1a\u65e9\u56de\u5bb6\u505a\u996d\u7684\u5417\uff1f\u201d</p><p>\u3000\u3000\u987e\u6653\u6885\u56de\u623f\u95f4\u6362\u4e86\u4e00\u8eab\u666e\u901a\u7684\u5bb6\u5c45\u77ed\u886b\u51fa\u6765\uff0c\u8ddf\u4e08\u592b\u5520\u53e8\uff1a\u201c\u5f20\u5e02\u957f\u5bb6\u7684\u516c\u5b50\u5e26\u7740\u5973\u670b\u53cb\u56de\u65b0\u829c\uff0c\u6c5f\u4e3b\u4efb\u3001\u5218\u4e66\u8bb0\u665a\u4e0a\u90fd\u5728\u5f20\u5e02\u957f\u5bb6\u5403\u665a\u996d\uff0c\u6211\u624d\u80fd\u65e9\u56de\u5bb6\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u89c1\u8fc7\u5f20\u5e02\u957f\u4ed6\u513f\u5b50\uff0c\u6709\u4e8e\u7af9\u8bf4\u7684\u8fd9\u4e48\u5938\u5f20\uff1f\u201d\u4e8e\u536b\u7b11\u7740\u95ee\uff0c\u201c\u90fd\u8bf4\u9f99\u751f\u9f99\u3001\u51e4\u751f\u51e4\uff0c\u5f20\u5e02\u957f\u7684\u513f\u5b50\u4e0d\u53ef\u80fd\u50cf\u4e8e\u7af9\u8bf4\u7684\u8fd9\u4e48\u6ca1\u6709\u5bb6\u6559\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u6ca1\u6709\u8ddf\u4f60\u8bf4\u8fc7\u7f57\u5f52\u6e90\u7684\u513f\u5b50\uff1f\u8fd9\u4e9b\u4e8c\u4e16\u7956\u7684\u672c\u6027\uff0c\u6ca1\u6709\u706b\u773c\u91d1\u775b\uff0c\u4e00\u4e24\u6b21\u80fd\u770b\u900f\u5e95\u7ec6\uff1f\u201d\u987e\u6653\u6885\u62ff\u7740\u56f4\u88d9\u7cfb\u5728\u8170\u4e0a\uff0c\u51c6\u5907\u53bb\u53a8\u623f\u52a8\u624b\u505a\u665a\u996d\uff0c\u60f3\u4e86\u60f3\uff0c\u53c8\u8dd1\u56de\u6765\u544a\u8beb\u5979\u513f\u5b50\uff0c\u201c\u4f60\u7ed9\u6211\u62ce\u6e05\u695a\u4e86\uff0c\u90a3\u4e2a\u5c0f\u5b50\u4f60\u5c11\u53bb\u62db\u60f9\uff0c\u5c31\u5f53\u4f5c\u4e0d\u8ba4\u8bc6\uff0c\u4f60\u8ddf\u4f60\u7238\u90fd\u662f\u95f7\u5934\u846b\u82a6\uff0c\u6307\u4e0d\u5b9a\u7ed9\u4eba\u5bb6\u5356\u4e86\u8fd8\u5e2e\u4eba\u5bb6\u6570\u94b1\u2014\u2014\u90a3\u5c0f\u5b50\u5373\u4f7f\u7eaf\u6d01\u4e5f\u6709\u9650\uff0c\u5e26\u7740\u5973\u670b\u53cb\u6765\u65b0\u829c\u4e5f\u5c31\u7b97\u4e86\uff0c\u7adf\u7136\u8fd8\u5e26\u7740\u6f02\u4eae\u5973\u5b69\u5b50\u8fc7\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u90a3\u4e00\u5b9a\u662f\u6211\u4eec\u7684\u6821\u82b1\u9648\u5983\u84c9\uff01\u201d\u4e8e\u7af9\u60f3\u4e5f\u4e0d\u60f3\u7684\u5c31\u731c\u662f\u9648\u5983\u84c9\uff0c\u201c\u6211\u4eec\u5b66\u6821\u90fd\u516c\u8ba4\u9648\u5983\u84c9\u662f\u4ed6\u7684\u5973\u670b\u53cb\uff0c\u7ecf\u5e38\u770b\u5230\u4ed6\u4eec\u4e00\u8d77\u56fe\u4e66\u9986\u4e0a\u591c\u81ea\u4fee\u2026\u2026\u201d</p><p>\u3000\u3000\u987e\u6653\u6885\u5f20\u4e86\u5f20\u5634\uff0c\u62cd\u4e86\u62cd\u989d\u5934\uff0c\u5fc3\u60f3\u8fd9\u7b97\u4ec0\u4e48\u4e8b\u554a\uff0c\u8fd9\u5c0f\u5b50\u516c\u7136\u5e26\u4e24\u4e2a\u5973\u670b\u53cb\u5230\u65b0\u829c\u6765\u6e21\u5047\u554a\uff0c\u771f\u4e0d\u6653\u5f97\u4ed6\u600e\u6837\u624d\u80fd\u5c06\u8fd9\u4e24\u4e2a\u5973\u5b69\u5b50\u4e4b\u95f4\u7684\u77db\u76fe\u6446\u5e73\u7684\uff0c\u4e24\u4e2a\u5973\u5b69\u5b50\u90fd\u662f\u96be\u5f97\u4e00\u89c1\u7684\u82b1\u5bb9\u6708\u8c8c\uff0c\u4e00\u822c\u7537\u5b69\u5b50\u80fd\u6709\u5176\u4e2d\u4e00\u4e2a\u5f53\u5973\u670b\u53cb\u5927\u6982\u5c31\u77e5\u8db3\u4e86\uff0c\u4e5f\u53ea\u6709\u8fd9\u79cd\u4e8c\u4e16\u7956\u5f0f\u7684\u82b1\u82b1\u516c\u5b50\u624d\u4e0d\u4f1a\u73cd\u60dc\u8fd9\u6837\u7684\u5973\u5b69\u5b50\u2014\u2014\u5176\u4ed6\u4e8b\u60c5\u4e0d\u8bf4\uff0c\u8fd9\u5c0f\u5b50\u662f\u82b1\u82b1\u516c\u5b50\u90a3\u94c1\u5b9a\u7684\u4e86\u3002</p><p>\u3000\u3000\u987e\u6653\u6885\u5482\u4e86\u5482\u5634\uff0c\u8ddf\u4ed6\u8001\u516c\u8bf4\u9053\uff1a\u201c\u90a3\u4e2a\u5973\u5b69\u5b50\u4e5f\u8fd8\u771f\u662f\u6f02\u4eae\u2014\u2014\u770b\u4f60\u8fd8\u6709\u8138\u8bf4\u9f99\u751f\u9f99\u3001\u51e4\u751f\u51e4\u2014\u2014\u5f20\u5e02\u957f\u662f\u5f20\u5e02\u957f\uff0c\u8c01\u80fd\u80af\u5b9a\u8bf4\u5f20\u5e02\u957f\u7684\u513f\u5b50\u4e00\u5b9a\u54c1\u6027\u7aef\u826f\uff1f\u201d</p><p>\u3000\u3000\u4e8e\u536b\u6324\u7709\u5f04\u773c\u7684\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u54ea\u4e2a\u5c0f\u4f19\u5b50\u770b\u5230\u6f02\u4eae\u5973\u5b69\u5b50\u4e0d\u559c\u6b22\uff0c\u90fd\u8ddf\u4e8e\u7af9\u95f7\u846b\u82a6\u4f3c\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u4eec\u5b66\u6821\u672c\u90e8\u7684\u540c\u5b66\u90fd\u8bf4\u9648\u5983\u84c9\u662f\u4e1c\u5927\u7684\u6821\u82b1\uff0c\u6211\u770b\u5230\u8fc7\uff0c\u6f02\u4eae\u662f\u6f02\u4eae\uff0c\u4f46\u4e0d\u4e00\u5b9a\u5c31\u662f\u4e1c\u5927\u6700\u6f02\u4eae\u7684\uff0c\u4ed6\u4eec\u90fd\u6ca1\u6709\u770b\u5230\u5c0f\u5b81\uff0c\u7b49\u4e0b\u5b66\u671f\u5c0f\u5b81\u5230\u672c\u90e8\u540e\uff0c\u4ed6\u4eec\u5c31\u4f1a\u77e5\u9053\u8c01\u624d\u662f\u771f\u6b63\u7684\u6821\u82b1\u4e86\u3002\u201d\u4e8e\u7af9\u773c\u775b\u76ef\u7740\u68cb\u76d8\uff0c\u81ea\u987e\u81ea\u7684\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u4f60\u4e0d\u662f\u5c06\u5c0f\u5b81\u5f53\u59b9\u59b9\u5427\uff0c\u4e0d\u8981\u4e00\u4e2a\u52b2\u7684\u5c06\u5c0f\u5b81\u6302\u5634\u8fb9\u3002\u201d\u987e\u6653\u6885\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u6211\u54ea\u6709\uff1f\u53ea\u662f\u5ba2\u89c2\u8bc4\u4ef7\u4e00\u4e0b\uff0c\u201d\u4e8e\u7af9\u4e89\u8fa9\u9053\uff0c\u201c\u81ea\u5df1\u59b9\u59b9\u957f\u5f97\u6f02\u4eae\u5c31\u80fd\u8bf4\u554a\uff0c\u5173\u952e\u6211\u81ea\u5df1\u7684\u59b9\u59b9\u957f\u5f97\u50cf\u7238\u7238\uff0c\u4e00\u70b9\u90fd\u4e0d\u597d\u770b\u3002\u201d</p><p>\u3000\u3000\u201c\u54ea\u6709\u8fd9\u4e48\u8bf4\u81ea\u5df1\u59b9\u59b9\u7684\uff1f\u5c0f\u5fc3\u59b9\u59b9\u56de\u6765\u8ddf\u4f60\u95f9\u3002\u201d\u987e\u6653\u6885\u8bf4\u9053\uff0c\u60f3\u7740\u4e0d\u80fd\u7ee7\u7eed\u6253\u5c94\uff0c\u4e0d\u7136\u5c31\u8981\u5c06\u665a\u996d\u803d\u6401\u4e86\uff0c\u8d70\u5230\u53a8\u623f\uff0c\u60f3\u5230\u9648\u80b2\u5bb6\u7684\u5973\u513f\u8ddf\u968f\u5f20\u606a\u4e00\u8d77\u5230\u65b0\u829c\u6765\u7684\u5510\u5a67\u3001\u9648\u5983\u84c9\u6bd4\u8f83\uff0c\u8fd8\u771f\u96be\u8bf4\u54ea\u4e00\u4e2a\u66f4\u6f02\u4eae\u4e00\u4e9b\uff0c\u5404\u6709\u5404\u7684\u7279\u8d28\u3002\u5979\u4ece\u6765\u90fd\u4e0d\u5e0c\u671b\u513f\u5b50\u5c06\u6765\u627e\u7684\u5973\u670b\u53cb\u592a\u6f02\u4eae\uff0c\u5979\u6e05\u695a\u81ea\u5df1\u513f\u5b50\u6709\u4e9b\u592a\u5185\u5411\u4e86\uff0c\u8fd8\u662f\u666e\u901a\u7684\u5973\u5b69\u5b50\u9002\u5408\u4ed6\uff0c\u8fd9\u4e2a\u793e\u4f1a\u592a\u590d\u6742\uff0c\u518d\u8bf4\u9648\u80b2\u5bb6\u5973\u513f\u7684\u75c5\u80fd\u4e0d\u80fd\u6cbb\u8fd8\u662f\u95ee\u9898\uff0c\u5fc3\u60f3\u6216\u8bb8\u771f\u8981\u5c3d\u5feb\u4ece\u8fd9\u91cc\u642c\u51fa\u53bb\u3002\u60f3\u5230\u8fd9\u91cc\uff0c\u987e\u6653\u6885\u5c31\u5524\u4e08\u592b\u8981\u53a8\u623f\u6765\u5546\u91cf\u3002</p><p>\u3000\u3000\u201c\u4e8e\u536b\uff0c\u4f60\u8fc7\u6765\u4e00\u4e0b\u3002\u201d</p><p>\u3000\u3000\u201c\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d\u4e8e\u536b\u624b\u91cc\u8fd8\u62ff\u7740\u68cb\u5b50\uff0c\u5750\u7740\u4e0d\u52a8\uff0c\u95ee\u4ed6\u8001\u5a46\u6709\u4ec0\u4e48\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u201c\u65b0\u5efa\u7684\u6ee8\u6c5f\u5c0f\u533a\u5bf9\u5916\u9500\u552e\u623f\uff0c\u5bf9\u5e02\u59d4\u5e02\u653f\u5e9c\u7684\u5de5\u4f5c\u4eba\u5458\u6709\u4f18\u60e0\uff0c\u6211\u4eec\u662f\u4e0d\u662f\u627e\u4e2a\u65f6\u95f4\u53bb\u770b\u770b\uff1f\u201d\u987e\u6653\u6885\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u8fd9\u4e0d\u662f\u624d\u6362\u623f\u5b50\u6ca1\u591a\u4e45\uff1f\u8ddf\u9648\u5382\u957f\u4ed6\u4eec\u5f53\u90bb\u5c45\u86ee\u597d\u7684\uff0c\u4e3a\u4ec0\u4e48\u53c8\u8981\u642c\u5bb6\uff1f\u518d\u8bf4\u6ee8\u6c5f\u5c0f\u533a\u53ea\u5bf9\u5e02\u59d4\u5e02\u653f\u5e9c\u7684\u5de5\u4f5c\u4eba\u5458\u6709\u4f18\u60e0\uff0c\u8fd9\u4e0d\u662f\u660e\u6446\u7740\u7ed9\u56fd\u5bb6\u5e72\u90e8\u9001\u597d\u5904\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4e2a\u4e8b\u60c5\u4f60\u5c31\u4e0d\u8981\u7ba1\u4e86\uff0c\u6211\u4eec\u4e5f\u53ea\u80fd\u575a\u6301\u4e0d\u6536\u4eba\u5bb6\u94b1\u3002\u9022\u5e74\u8fc7\u8282\u4eba\u5bb6\u9001\u793c\u8fc7\u6765\uff0c\u5b9e\u5728\u9000\u4e0d\u4e86\u8fd8\u4e0d\u662f\u6362\u4e86\u94b1\u8ba9\u4f60\u6350\u51fa\u53bb\u2014\u2014\u6709\u4e9b\u4e8b\u60c5\u662f\u907f\u514d\u4e0d\u4e86\uff0c\u4f60\u8981\u5c06\u81ea\u5df1\u641e\u5f97\u6e05\u5ec9\u5982\u6c34\uff0c\u5728\u5e02\u653f\u5e9c\u91cc\u9762\u4ec0\u4e48\u5de5\u4f5c\u90fd\u505a\u4e0d\u4e0b\u53bb\uff0c\u73b0\u5728\u793e\u4f1a\u5c31\u662f\u8fd9\u79cd\u98ce\u6c14\uff0c\u5c31\u6b63\u800c\u516b\u7ecf\u7684\u8fc7\u6765\u6c42\u529e\u4e8b\uff0c\u4e5f\u8981\u5e26\u7740\u793c\u4e0a\u95e8\u2014\u2014\u4f60\u4e00\u76f4\u90fd\u5728\u5938\u5f20\u5e02\u957f\u6e05\u5ec9\uff0c\u5f20\u5e02\u957f\u4e0d\u4e5f\u8fd8\u6536\u793c\uff0c\u6536\u4e86\u793c\u4e0d\u4e5f\u8fd8\u662f\u6211\u4eb2\u81ea\u8dd1\u817f\u5e2e\u4ed6\u5356\u6389\u518d\u6350\u51fa\u53bb\uff1f\u201d\u987e\u6653\u6885\u8bf4\u9053\uff0c\u201c\u6ee8\u6c5f\u5c0f\u533a\u662f\u5bf9\u56fd\u5bb6\u5e72\u90e8\u4f18\u60e0\uff0c\u4f46\u662f\u518d\u4f18\u60e0\uff0c\u4ed6\u4eec\u4e5f\u6ca1\u6709\u4e8f\u672c\u5356\u7ed9\u6211\u4eec\uff0c\u6211\u4eec\u5c31\u62ff\u516c\u5f00\u7684\u4f18\u60e0\u4ef7\uff0c\u4e5f\u4e0d\u8d2a\u989d\u5916\u7684\u597d\u5904\uff0c\u8fd8\u6015\u522b\u4eba\u8bf4\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u5c31\u4e0d\u6015\u5f71\u54cd\u4e0d\u597d\uff1f\u201d</p><p>\u3000\u3000\u201c\u5f20\u5e02\u957f\u521a\u5230\u65b0\u829c\u8fd8\u76f4\u63a5\u4e70\u522b\u5885\u4f4f\uff0c\u4ed6\u5c31\u4e0d\u6015\u5f71\u54cd\u4e0d\u597d\uff1f\u201d\u987e\u6653\u6885\u8bf4\u9053\uff0c\u201c\u4e0d\u505a\u4e8f\u5fc3\u4e8b\uff0c\u6709\u4ec0\u4e48\u6015\u5f71\u54cd\u4e0d\u597d\u7684\uff1f\u201d\u987e\u6653\u6885\u5012\u662f\u60f3\u8d77\u9ec4\u660f\u65f6\u5f20\u606a\u8ddf\u5979\u8bf4\u7684\u90a3\u756a\u8bdd\uff0c\u8ddf\u5728\u5f20\u77e5\u884c\u8eab\u8fb9\uff0c\u5411\u4ee5\u5f80\u90a3\u6837\u8fc7\u5ea6\u7684\u7422\u78e8\u9886\u5bfc\u7684\u5fc3\u601d\u53cd\u800c\u4f1a\u65e0\u6cd5\u9002\u4ece\uff0c\u5fc3\u601d\u7b80\u5355\u4e00\u4e9b\u6216\u8bb8\u5c31\u627e\u5bf9\u5de5\u4f5c\u7684\u8109\u7edc\u4e86\u3002</p><p>\u3000\u3000\u201c\u6211\u8bf4\u4e0d\u8fc7\u4f60\uff0c\u6253\u7ed3\u5a5a\u5c31\u8ba4\u8bc6\u5230\u4f60\u5634\u5df4\u5b50\u5389\u5bb3\uff0c\u540e\u6094\u836f\u90fd\u4e70\u4e0d\u5230\uff0c\u201d\u4e8e\u536b\u7f29\u4e86\u7f29\u8116\u5b50\u7ee7\u7eed\u8ddf\u513f\u5b50\u4e0b\u68cb\uff0c\u201c\u4f60\u95ee\u95ee\u4e8e\u7af9\u8ddf\u59b9\u59b9\u613f\u4e0d\u613f\u610f\u642c\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u89c9\u5f97\u4f4f\u8fd9\u91cc\u633a\u597d\u7684\u3002\u201d\u4e8e\u7af9\u4e5f\u5b66\u4ed6\u7238\u7238\u7f29\u4e86\u7f29\u8116\u5b50\uff0c\u4e0d\u8fc7\u4ed6\u77e5\u9053\u8fd9\u79cd\u5927\u4e8b\uff0c\u4ed6\u8ddf\u4ed6\u7238\u7238\u90fd\u505a\u4e0d\u4e86\u4e3b\uff0c\u53cd\u6b63\u8981\u642c\u4e5f\u662f\u4e00\u65f6\u534a\u4f1a\u513f\u4e5f\u642c\u5f97\u4e86\u7684\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u95e8\u94c3\u54cd\u4e86\u8d77\u6765\uff0c\u4e8e\u7af9\u62ac\u5934\u8bf4\u9053\uff1a\u201c\u5c0f\u5b81\u6765\u4e86\uff0c\u59b9\u59b9\u53ea\u4f1a\u7528\u624b\u62cd\u95e8\u3002\u201d\u5fd9\u722c\u8d77\u6765\u53bb\u5f00\u95e8\u3002</p><p>\u3000\u3000\u987e\u6653\u6885\u4ece\u53a8\u623f\u95e8\u63a2\u51fa\u5934\uff0c\u770b\u7740\u9648\u80b2\u5bb6\u7684\u5973\u513f\u771f\u662f\u660e\u4e3d\u6e05\u8273\uff0c\u90a3\u53cc\u6e05\u6f88\u6d41\u5149\u7684\u773c\u73e0\u5b50\u8ba9\u4eba\u770b\u4e86\u5c31\u89c9\u5f97\u6709\u4e1d\u900f\u5fc3\u7684\u723d\u51c9\u6c14\u606f\uff0c\u53ea\u662f\u8eab\u6750\u7565\u7626\u524a\u4e86\u4e00\u4e9b\uff0c\u4e5f\u6108\u663e\u5f97\u6e05\u79c0\u8131\u4fd7\u3001\u56fd\u8272\u5929\u9999\uff0c\u8fd9\u6837\u7684\u5973\u5b69\u5b50\u518d\u957f\u5927\u4e9b\u5c31\u662f\u7978\u6c34\u2014\u2014\u81ea\u5df1\u5bb6\u7684\u9ec4\u6bdb\u4e2b\u5934\u8ddf\u9648\u80b2\u5bb6\u5973\u513f\u7ad9\u5728\u4e00\u8d77\u4e5f\u6709\u4e9b\u592a\u4e0d\u8d77\u773c\u4e86\u3002</p><p>\u3000\u3000\u201c\u987e\u59e8\u597d\uff0c\u542c\u4e8e\u7af9\u8bf4\u4eca\u5929\u665a\u996d\u662f\u987e\u59e8\u4eb2\u81ea\u505a\uff0c\u5c0f\u59b9\u62c9\u6211\u8fc7\u6765\u5403\u665a\u996d\uff0c\u6211\u5c31\u6ca1\u6709\u5ba2\u6c14\u2026\u2026\u201d\u9648\u5b81\u4fcf\u76ae\u7684\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u201c\u4f60\u7238\u4eba\u5462\uff1f\u4f60\u5988\u5988\u4eca\u5929\u4e0d\u662f\u4e0a\u4e2d\u73ed\u5417\uff0c\u4f60\u7238\u96be\u9053\u81ea\u5df1\u5728\u5bb6\u91cc\u505a\u996d\u5403\uff1f\u201d\u987e\u6653\u6885\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u6211\u7238\u8fd8\u7b49\u7740\u4e8e\u53d4\u6253\u7535\u8bdd\u8bf7\u4ed6\u5462\uff0c\u6211\u5148\u6765\u6253\u524d\u54e8\u3002\u201d\u9648\u5b81\u4fcf\u76ae\u7684\u7b11\u7740\u5c06\u5979\u7238\u7238\u5c31\u51fa\u5356\u4e86\u3002</p><p>\u3000\u3000\u987e\u6653\u6885\u6447\u5934\u800c\u7b11\uff0c\u60f3\u8d77\u81ea\u5df1\u4e0e\u9648\u80b2\u5c0f\u5b66\u3001\u521d\u4e2d\u3001\u4e2d\u4e13\u90fd\u662f\u540c\u5b66\uff0c\u6253\u5c0f\u4e5f\u7b97\u662f\u9752\u6885\u7af9\u9a6c\uff0c\u8981\u4e0d\u662f\u540e\u6765\u9047\u5230\u4e8e\u536b\uff0c\u5927\u6982\u4e5f\u4f1a\u968f\u7740\u5927\u4eba\u7684\u5fc3\u601d\u8ddf\u9648\u80b2\u7ed3\u5a5a\u5427\u3002\u60f3\u60f3\u4e5f\u771f\u662f\u5947\u602a\uff0c\u5728\u9648\u80b2\u5973\u513f\u7684\u5fc3\u91cc\uff0c\u4e8e\u7af9\u5927\u6982\u4e5f\u8ddf\u9648\u80b2\u5f53\u5e74\u5728\u81ea\u5df1\u5fc3\u91cc\u7684\u5730\u4f4d\u5dee\u4e0d\u591a\u5427\uff0c\u81ea\u5c0f\u9752\u6885\u7af9\u9a6c\uff0c\u4eb2\u8fd1\u4e5f\u6781\u4e3a\u4eb2\u8fd1\uff0c\u5374\u53c8\u8ddf\u7231\u60c5\u626f\u4e0d\u4e0a\u8fb9\u3002\u5fae\u5fae\u6447\u5934\u800c\u53f9\uff0c\u77e5\u9053\u5c31\u7b97\u642c\u51fa\u65b0\u95f8\u5c0f\u533a\uff0c\u6709\u4e9b\u4e8b\u60c5\u8fd8\u6539\u53d8\u4e0d\u4e86\u7684\uff0c\u4e00\u5207\u987a\u5176\u81ea\u7136\u5427\u3002</p><p>\u3000\u3000\u770b\u5230\u4e8e\u536b\u90fd\u7ad9\u8d77\u6765\u6253\u7535\u8bdd\u53bb\u4e86\uff0c\u987e\u6653\u6885\u53c8\u56de\u53a8\u623f\u91cc\u72ec\u81ea\u73a9\u7740\uff0c\u8fc7\u4e86\u4e00\u4f1a\u513f\uff0c\u95e8\u94c3\u53c8\u54cd\u4e86\u8d77\u6765\uff0c\u770b\u5230\u4e8e\u7af9\u7ad9\u8d77\u6765\u5f00\u95e8\uff0c\u987e\u6653\u6885\u5c31\u6ca1\u6709\u7406\u4f1a\u3002</p><p>\u3000\u3000\u987e\u6653\u6885\u624b\u811a\u9ebb\u5229\uff0c\u8fd9\u4e0e\u5979\u5e72\u7ec3\u6cfc\u8fa3\u7684\u6027\u5b50\u6709\u5173\uff0c\u4e5f\u4e0d\u559c\u6b22\u522b\u4eba\u8fdb\u53a8\u623f\u6765\u5e2e\u5979\u7684\u5fd9\uff0c\u5f88\u5feb\u5c31\u6574\u51fa\u4e00\u684c\u4e30\u76db\u7684\u83dc\u80b4\u3002</p><p>\u3000\u3000\u201c\u96be\u602a\u5218\u4e66\u8bb0\u51b3\u5b9a\u5c06\u4f60\u8c03\u53bb\u5e02\u653f\u5e9c\u529e\uff0c\u5c31\u8fd9\u505a\u83dc\u7684\u9ebb\u5229\u52b2\uff0c\u5c31\u8db3\u4ee5\u8ba9\u5e02\u653f\u5e9c\u529e\u7684\u5b98\u8001\u7237\u81ea\u53f9\u4e0d\u5982\u4e86\uff0c\u201d\u9648\u80b2\u76f8\u8c8c\u5112\u96c5\uff0c\u4e0e\u4e8e\u3001\u987e\u662f\u51e0\u5341\u5e74\u7684\u4ea4\u60c5\uff0c\u8bf4\u8bdd\u968f\u4fbf\uff0c\u95ee\u9053\uff0c\u201c\u542c\u8bf4\u79e6\u971c\u534e\u8981\u5f53\u526f\u5e02\u957f\uff0c\u5e02\u91cc\u662f\u4e0d\u662f\u6709\u8fd9\u79cd\u8bf4\u6cd5\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u4eec\u5929\u5929\u5728\u5e02\u653f\u5e9c\u91cc\u7684\u4eba\u4e5f\u53ea\u662f\u9053\u542c\u9014\u8bf4\uff0c\u8c01\u77e5\u9053\u6d88\u606f\u662f\u4ece\u54ea\u91cc\u4f20\u51fa\u6765\u7684\uff1f\u201d\u987e\u6653\u6885\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u79e6\u971c\u534e\u6700\u65e9\u5e2e\u7740\u5f20\u77e5\u884c\u5c06\u4e91\u6c60\u9152\u4e1a\u4e00\u8d77\u505a\u8d77\u6765\uff0c\u79e6\u971c\u534e\u5f53\u4e0a\u5e02\u8f7b\u5de5\u5c40\u5c40\u957f\uff0c\u636e\u8bf4\u5c31\u662f\u5f20\u77e5\u884c\u5f53\u4e0a\u5e38\u59d4\u540e\u4e0d\u4e45\u76f4\u63a5\u63d0\u540d\u7684\uff0c\u73b0\u5728\u5f20\u77e5\u884c\u5f53\u4e0a\u526f\u4e66\u8bb0\u3001\u5e02\u957f\u4e86\uff0c\u63d0\u79e6\u971c\u534e\u5f53\u526f\u5e02\u957f\u90a3\u662f\u518d\u987a\u7406\u6210\u7ae0\u4e0d\u8fc7\u7684\u4e8b\u60c5\u4e86\u2014\u2014\u5f20\u77e5\u884c\u60f3\u5728\u65b0\u829c\u505a\u6210\u4ec0\u4e48\u4e8b\u60c5\uff0c\u8fd8\u4e0d\u662f\u8981\u627e\u51e0\u4e2a\u5e2e\u624b\u624d\u884c\uff1f\u201d\u9648\u80b2\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u201c\u5f53\u521d\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u4e3b\u52a8\u53bb\u4e91\u6c60\u9152\u4e1a\uff0c\u4e0d\u7136\u4f60\u5c31\u662f\u526f\u5e02\u957f\u4e86\uff1f\u201d\u987e\u6653\u6885\u4e5f\u6ca1\u6709\u5426\u8ba4\u9648\u80b2\u7684\u731c\u6d4b\uff0c\u53ea\u662f\u8ddf\u4ed6\u5f00\u73a9\u7b11\u8bf4\u3002</p><p>\u3000\u3000\u201c\u4e09\u5e74\u524d\u5f20\u77e5\u884c\u5230\u65b0\u829c\u6765\uff0c\u5934\u4e0a\u76f4\u63a5\u5f00\u7740\u7701\u957f\u5f90\u5b66\u5e73\u7231\u5c06\u7684\u5149\u73af\u8fc7\u6765\u7684\uff0c\u4e00\u5230\u65b0\u829c\u6765\u5c31\u6380\u5f97\u65b0\u829c\u6ce2\u6f9c\u6c79\u6d8c\uff0c\u8c01\u80fd\u770b\u6e05\u4ed6\u7684\u6c34\u5e95\u2014\u2014\u4e09\u5e74\u524d\uff0c\u5c31\u7b97\u5f20\u77e5\u884c\u505a\u4e86\u597d\u4e8b\uff0c\u8c01\u80fd\u8bf4\u4ed6\u597d\uff1f\u8ddf\u7740\u4ed6\u662f\u7978\u662f\u798f\u90fd\u96be\u9884\u6599\uff0c\u73b0\u5728\u5f53\u7136\u662f\u53e6\u8bf4\u3002\u8bf4\u8d77\u6765\uff0c\u79e6\u971c\u534e\u8fd8\u662f\u6709\u51e0\u5206\u80fd\u8010\u7684\uff0c\u5f20\u77e5\u884c\u771f\u8981\u63d0\u62d4\u4ed6\u5f53\u526f\u5e02\u957f\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u4eba\u4f1a\u4e0d\u670d\u6c14\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u4e0d\u5c31\u6e05\u9ad8\u4e86\u4e9b\u5427\uff1f\u201d\u987e\u6653\u6885\u8fd8\u662f\u5f88\u968f\u610f\u7684\u5c31\u6233\u9648\u80b2\u7684\u75db\u5904\uff0c\u201c\u5728\u5f20\u77e5\u884c\u5230\u65b0\u829c\u4e4b\u524d\uff0c\u4f60\u5bf9\u65b0\u829c\u7684\u767d\u9152\u4ea7\u4e1a\u4e0d\u662f\u4e5f\u6709\u81ea\u5df1\u7684\u60f3\u6cd5\u5427\uff1f\u65e9\u8ddf\u4f60\u8bf4\u4e86\uff0c\u8981\u60f3\u505a\u6210\u4e8b\u60c5\uff0c\u6709\u4e9b\u6e05\u9ad8\u8fd8\u662f\u8981\u653e\u4e0b\u7684\u3002\u4e8b\u5b9e\u8bc1\u660e\u4ec0\u4e48\uff1f\u5f20\u5e02\u957f\u4e0d\u662f\u975e\u5e38\u5f97\u7fa4\u4f17\u7684\u62e5\u6234\uff1f\u4eca\u5929\u5e02\u653f\u5e9c\u529e\u516c\u4f1a\u8bae\uff0c\u5f20\u5e02\u957f\u5728\u4f1a\u4e0a\u5c31\u63d0\u51fa\u6765\uff0c\u5728\u7ecf\u6d4e\u53d1\u5c55\u4e0a\uff0c\u5404\u59d4\u5c40\u8981\u5c11\u52a8\u4e0d\u5207\u5b9e\u9645\u7684\u82b1\u82b1\u80a0\u5b50\uff0c\u80fd\u8ba9\u7fa4\u4f17\u90fd\u666e\u904d\u53d7\u60e0\u7684\u653f\u7b56\u624d\u662f\u597d\u7684\u653f\u7b56\uff0c\u4e0b\u4e00\u6b65\u5c31\u91cd\u70b9\u6253\u9020\u83dc\u56ed\u5b50\u4ea7\u4e1a\uff0c\u4ed6\u8bf4\u519c\u6237\u517b\u732a\u79cd\u83dc\u4e5f\u80fd\u53d1\u5bb6\u81f4\u5bcc\u2014\u2014\u6211\u60f3\u5f20\u5e02\u957f\u7684\u7ecf\u6d4e\u773c\u5149\u6bd4\u4f60\u5389\u5bb3\u5427\uff0c\u4f60\u4e09\u5e74\u524d\u600e\u4e48\u8bf4\u4eba\u5bb6\u6765\u7740\uff1f\u6211\u53ef\u8bb0\u7740\u5462\uff0c\u2018\u662f\u9aa1\u5b50\u662f\u9a6c\u8fd8\u8981\u4eba\u5bb6\u51fa\u6765\u6e9c\u4e09\u5e74\u518d\u8bf4\u2019\u3002\u201d</p><p>\u3000\u3000\u7ed9\u987e\u6653\u6885\u8fd9\u4e48\u8bf4\u7740\uff0c\u9648\u80b2\u4e5f\u4e0d\u607c\uff0c\u53ea\u662f\u6d8e\u8138\u800c\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4e5f\u53ea\u80fd\u8bf4\u5f20\u77e5\u884c\u521a\u8fc7\u6765\u65f6\u5c06\u5927\u5bb6\u90fd\u8ff7\u60d1\u4f4f\u4e86\uff0c\u4f60\u90a3\u65f6\u5019\u5c31\u80fd\u786e\u8ba4\u5f20\u77e5\u884c\u6e05\u5ec9\uff0c\u4e0d\u603b\u5f97\u6709\u7740\u8ba4\u8bc6\u7684\u8fc7\u7a0b\uff1f\u201d</p><p>\u3000\u3000\u4e8e\u536b\u63d2\u5634\u9053\uff1a\u201c\u4e0d\u8fc7\u6211\u4eec\u521a\u624d\u6838\u5b9e\u8fc7\uff0c\u5f20\u5e02\u957f\u7684\u513f\u5b50\u8fd8\u771f\u4e0d\u662f\u4e2a\u4e1c\u897f\uff0c\u4f60\u8ba9\u4f60\u5bb6\u5c0f\u5b81\u5728\u4e1c\u5927\u907f\u4eba\u5bb6\u8fdc\u4e9b\u3002\u201d</p><p>\u3000\u3000\u201c\u4ec0\u4e48\uff0c\u4f60\u4eec\u5728\u8bf4\u4ec0\u4e48\uff1f\u201d\u9648\u5b81\u6b6a\u5934\u8111\u888b\u3001\u5a07\u6da6\u5634\u5507\u5fae\u5658\u7740\uff0c\u6e05\u6f88\u7684\u79c0\u7738\u770b\u7740\u4e09\u4e2a\u5927\u4eba\uff0c\u201c\u600e\u4e48\u626f\u5230\u6211\u5934\u4e0a\u6765\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u65b0\u5e02\u957f\u7684\u513f\u5b50\u4e5f\u5728\u6211\u4eec\u4e1c\u5927\u4e0a\u5b66\uff0c\u8fd8\u662f\u56fd\u5546\u9662\u7684\uff0c\u4ed6\u4eec\u5728\u8bf4\u4ed6\u3002\u201d\u4e8e\u7af9\u62ac\u5934\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u662f\u5417\uff0c\u6211\u89c1\u8fc7\u5417\uff1f\u201d\u9648\u5b81\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u4ed6\u6bd4\u6211\u4eec\u5927\u4e00\u5c4a\uff0c\u5728\u672c\u90e8\uff0c\u53ea\u542c\u8bf4\u4ed6\u4ece\u6765\u90fd\u4e0d\u53bb\u6559\u5ba4\u4e0a\u8bfe\u7684\uff0c\u6211\u5728\u672c\u90e8\u4e00\u5e74\u4e5f\u90fd\u6ca1\u6709\u89c1\u8fc7\u4ed6\u662f\u4ec0\u4e48\u4eba\uff0c\u53ea\u662f\u542c\u8bf4\u8fc7\u4ed6\u7684\u4e00\u4e9b\u4e8b\u60c5\u7f62\u4e86\u2014\u2014\u542c\u8bf4\u4ed6\u5927\u4e00\u671f\u672b\u8003\u8bd5\u65f6\u4f5c\u5f0a\u7ed9\u4ed6\u4eec\u5e74\u7ea7\u4e3b\u4efb\u5f53\u573a\u6293\u4f4f\uff0c\u90a3\u4e2a\u5e74\u7ea7\u4e3b\u4efb\u6700\u540e\u56e0\u4e3a\u8fd9\u4e8b\u7ed9\u8d76\u51fa\u4e86\u4e1c\u5927\uff1b\u8fd8\u542c\u8bf4\u4ed6\u6709\u4e00\u6b21\u5f88\u96be\u5f97\u7684\u53bb\u6559\u5ba4\u91cc\u542c\u5b66\u6821\u8bb2\u5ea7\uff0c\u7ed3\u679c\u5728\u6559\u5ba4\u91cc\u8ddf\u8001\u5e08\u5bf9\u9a82\uff0c\u628a\u6211\u4eec\u56fd\u5546\u9662\u4e00\u4e2a\u5f88\u6709\u540d\u7684\u6559\u6388\u9a82\u5f97\u5410\u8840\uff0c\u73b0\u5728\u90fd\u8fd8\u8eba\u533b\u9662\u91cc\u6210\u4e86\u690d\u7269\u4eba\u2014\u2014\u4f60\u5728\u65b0\u6d66\u6821\u533a\u5c31\u6ca1\u6709\u542c\u8bf4\u8fc7\u8fd9\u4e9b\u4f20\u8bf4\uff1f\u8fd8\u6709\u4e00\u6b21\uff0c\u542c\u8bf4\u4ed6\u7684\u670b\u53cb\u7ed9\u6821\u5b66\u751f\u4f1a\u7684\u4eba\u6b3a\u8d1f\u4e86\uff0c\u4f60\u731c\u540e\u6765\u600e\u4e48\u6837\uff1f\u5b66\u6821\u7684\u5b66\u751f\u4f1a\u529e\u516c\u5ba4\u7ed9\u4ed6\u62c6\u4e86\u7cbe\u5149\uff0c\u8fd8\u5dee\u70b9\u7ed9\u4ed6\u4e00\u628a\u706b\u70e7\u6389\uff0c\u73b0\u5728\u6821\u5b66\u751f\u4f1a\u529e\u516c\u5ba4\u7684\u95e8\u8fd8\u7559\u7740\u7ed9\u4ed6\u8e39\u7834\u4e4b\u540e\u8865\u4e0a\u7684\u5370\u5b50\u2014\u2014\u542c\u8bf4\u4ed6\u770b\u4e0a\u5e08\u5927\u7684\u4e00\u4e2a\u5973\u5b69\uff0c\u90a3\u4e2a\u5973\u5b69\u4e4b\u524d\u5c31\u6709\u4e2a\u7537\u5b69\u5b50\u5728\u8ffd\uff0c\u4e0d\u6653\u5f97\u4ed6\u7528\u4e86\u4ec0\u4e48\u624b\u6bb5\uff0c\u90a3\u4e2a\u5973\u5b69\u5b50\u540e\u6765\u5c31\u8ddf\u4ed6\u4e86\uff0c\u4e4b\u524d\u90a3\u7537\u7684\u6c14\u4e0d\u8fc7\u5c31\u8bf7\u793e\u4f1a\u4e0a\u7684\u6df7\u6df7\u5bf9\u4ed8\u4ed6\u3002\u5c31\u5728\u672c\u90e8\u4e1c\u95e8\uff0c\u4ed6\u7684\u624b\u81c2\u7ed9\u51e0\u4e2a\u6df7\u6df7\u624e\u4e86\u4e00\u5200\uff0c\u4f60\u731c\u540e\u6765\u7684\u7ed3\u679c\u600e\u4e48\u6837\uff1f\u90a3\u7537\u7684\u7ed9\u5224\u4e86\u4e8c\u5341\u5e74\uff0c\u624e\u4ed6\u4e00\u5200\u7684\u9ed1\u9053\u5206\u5b50\u7ed9\u5224\u4e86\u6b7b\u7f13\u2014\u2014\u4f60\u8bf4\u8bf4\u770b\uff0c\u4e0d\u5c31\u4e89\u98ce\u5403\u918b\u6253\u67b6\u624b\u81c2\u7ed9\u5212\u7834\u4e86\u4e00\u4e0b\uff1f\u4e00\u4e2a\u5224\u4e8c\u5341\u5e74\u3001\u4e00\u4e2a\u5224\u6b7b\u7f13\uff0c\u4eba\u5bb6\u725b\u53c9\u5427\uff01\u201d\u4e8e\u7af9\u95ee\u9053\uff0c\u201c\u8ddf\u7559\u5b66\u751f\u6253\u67b6\u3001\u8ffd\u6f02\u4eae\u5973\u5b69\u5b50\u4e4b\u7c7b\u7684\u8fd9\u79cd\u4e8b\u591a\u5462\uff0c\u8bf4\u4e09\u5929\u4e09\u591c\u90fd\u8bf4\u4e0d\u5b8c\u2026\u2026\u201d</p><p>\u3000\u3000\u9648\u80b2\u5938\u5f20\u7684\u7741\u5927\u773c\u775b\uff0c\u671d\u987e\u6653\u6885\u3001\u4e8e\u536b\u5f20\u4e86\u5f20\u5634\uff0c\u90a3\u610f\u601d\u662f\u8bf4\u8fd9\u5c0f\u5b50\u8fd8\u662f\u4eba\u5417\uff1f\u53ea\u662f\u89c9\u5f97\u76f4\u63a5\u8fd9\u4e48\u8bf4\u5f20\u77e5\u884c\u7684\u513f\u5b50\u6709\u4e9b\u8fc7\u5206\uff0c\u8981\u4e0d\u662f\u4ece\u5c0f\u770b\u7740\u4e8e\u7af9\u957f\u5927\uff0c\u77e5\u9053\u8fd9\u5b69\u5b50\u4e0d\u4f1a\u778e\u8bf4\uff0c\u4ed6\u66f4\u613f\u610f\u76f8\u4fe1\u4ed6\u662f\u5728\u80e1\u8bf4\u516b\u9053\uff0c\u53ea\u95ee\u9053\uff1a\u201c\u4e1c\u5927\u5c31\u80fd\u4efb\u4ed6\u80e1\u6765\uff1f\u201d</p><p>\u3000\u3000\u987e\u6653\u6885\u5012\u662f\u89c9\u5f97\u5947\u602a\uff0c\u95ee\u5979\u513f\u5b50\uff1a\u201c\u4f60\u6ca1\u89c1\u8fc7\u5f20\u606a\uff1f\u201d</p><p>\u3000\u3000\u201c\u6ca1\u6709\u554a\uff1f\u201d\u4e8e\u7af9\u8bf4\u9053\uff0c\u201c\u4ed6\u51e0\u4e4e\u5c31\u6ca1\u6709\u5728\u6559\u5ba4\u91cc\u51fa\u73b0\u8fc7\uff0c\u53ea\u662f\u7ecf\u5e38\u966a\u6821\u82b1\u53bb\u56fe\u4e66\u9986\u7684\u9605\u89c8\u5ba4\u4e0a\u81ea\u4fee\uff0c\u6211\u6ca1\u4e8b\u53c8\u4e0d\u4f1a\u53bb\u9605\u89c8\u5ba4\u4e0a\u81ea\u4fee\u2026\u2026\u600e\u4e48\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u6ca1\u4ec0\u4e48\uff0c\u201d\u987e\u6653\u6885\u8bf4\u9053\uff0c\u201c\u542c\u4ed6\u8bf4\u89c1\u8fc7\u4f60\uff0c\u4f60\u4e5f\u5e94\u8be5\u89c1\u8fc7\u4ed6\uff0c\u53ea\u662f\u4e0d\u8ba4\u8bc6\u7f62\u4e86\u3002\u201d</p><p>\u3000\u3000\u9648\u80b2\u6478\u4e86\u6478\u9f3b\u5b50\uff0c\u5e02\u957f\u5f20\u77e5\u884c\u4ed6\u5728\u5927\u4f1a\u5c0f\u4f1a\u89c1\u8fc7\u51e0\u6b21\uff0c\u800c\u5f20\u77e5\u884c\u8fd9\u51e0\u5e74\u6765\u5728\u65b0\u829c\u7684\u98ce\u95fb\u6781\u597d\uff0c\u5b9e\u5728\u96be\u4ee5\u60f3\u8c61\u4ed6\u4f1a\u6709\u8fd9\u4e48\u4e2a\u6df7\u5e10\u513f\u5b50\u3002</p><p>\u3000\u3000\u201c\u6211\u4eec\u5973\u751f\u90a3\u8fb9\u4f20\u7684\u7248\u672c\u597d\u50cf\u6709\u4e9b\u4e0d\u4e00\u6837\u5462\u2026\u2026\u201d\u9648\u5b81\u6b6a\u7740\u8111\u888b\u7a81\u7136\u7684\u63d2\u4e86\u4e00\u53e5\u3002</p></article>"}}}]);
