(window.webpackJsonp=window.webpackJsonp||[]).push([[532],{555:function(p){p.exports={chapterTitle:null,title:"\u7b2c390\u7ae0 \u9752\u5929\u5f53\u4e0d\u5f97",content:"<article><h2>\u7b2c390\u7ae0 \u9752\u5929\u5f53\u4e0d\u5f97</h2><p>\u3000\u3000\u6c5f\u4e0a\u5143\u3001\u5218\u7235\u90fd\u4e0d\u662f\u4ec0\u4e48\u6c34\u5229\u4e13\u5bb6\uff0c\u4f46\u662f\u6c5f\u5824\u8bbe\u8ba1\u56fe\u7a3f\u62ff\u5728\u624b\u91cc\uff0c\u7b80\u5355\u7684\u6750\u6599\u5bf9\u6bd4\u8fd8\u662f\u77e5\u9053\u4e86\uff0c\u8fde\u7eed\u5728\u6c5f\u5824\u4e0a\u6df1\u94bb\u4e86\u516d\u5904\u53d6\u6837\uff0c\u4fbf\u662f\u5bf9\u6c34\u5229\u6ca1\u6709\u592a\u591a\u5e38\u8bc6\u7684\u4eba\u4e5f\u77e5\u9053\u7f57\u5f52\u6e90\u4ee3\u8868\u5e02\u91cc\u89c6\u5bdf\u540e\u5ba3\u79f0\u662f\u201c\u65b0\u829c\u57ce\u94c1\u8170\u201d\u7684\u57ce\u9632\u6c5f\u5824\u6839\u672c\u5c31\u662f\u8c46\u8150\u8170\uff0c\u7834\u5f00\u4e00\u622a\u539f\u672c\u5e94\u8be5\u662f\u94a2\u7b4b\u6df7\u51dd\u571f\u7ed3\u6784\u7684\u9632\u6d2a\u5899\uff0c\u7adf\u7136\u770b\u4e0d\u5230\u4e00\u6839\u7ec6\u94a2\u7b4b\u7684\u5b58\u5728\uff0c\u8981\u662f\u5c0f\u6c5f\u4e0a\u6e38\u5f62\u6210\u4e00\u6b21\u8f83\u5927\u7684\u6d2a\u5cf0\uff0c\u4e0d\u8981\u8bf4\u4e94\u5341\u5e74\u4e00\u9047\u4e86\uff0c\u90a3\u6015\u662f\u4e8c\u5341\u5e74\u4e00\u9047\uff0c\u4f9d\u8d56\u8fd9\u6837\u7684\u6c5f\u5824\u9632\u6d2a\uff0c\u53ea\u80fd\u662f\u9762\u4e34\u4e00\u573a\u706d\u9876\u4e4b\u707e\u3002</p><p>\u3000\u3000\u5230\u6700\u540e\uff0c\u6c5f\u4e0a\u5143\u4e0e\u5218\u7235\u7684\u8138\u8272\u90fd\u53d8\u4e86\uff0c\u4ed6\u4eec\u4e5f\u6ca1\u6709\u60f3\u5230\u9f99\u534e\u96c6\u56e2\u4f1a\u5982\u6b64\u7684\u5927\u80c6\uff0c\u5c06\u65b0\u829c\u5e02\u7684\u4fdd\u547d\u5de5\u7a0b\u9020\u6210\u8fd9\u6837\u7684\u8c46\u8150\u6e23\u5de5\u7a0b\u3002</p><p>\u3000\u3000\u8fd9\u4e9b\u5e74\u7531\u4e8e\u5c0f\u6c5f\u4e0a\u6e38\u5929\u7136\u6797\u6ee5\u780d\u6ee5\u4f10\u73b0\u8c61\u4e25\u91cd\uff0c\u4f7f\u5f97\u6d2a\u60a3\u9891\u53d1\uff0c\u65b0\u829c\u5e02\u539f\u6765\u4e94\u5341\u5e74\u4ee3\u4fee\u5efa\u7684\u6c5f\u5824\u5df2\u4e0d\u80fd\u5b8c\u5168\u65e5\u76ca\u4e25\u91cd\u7684\u9632\u6d2a\u9700\u8981\uff0c\u624d\u51b3\u5b9a\u4ece\u8d22\u653f\u91cc\u6324\u51fa\u94b1\u6765\u641e\u6c5f\u5824\u5de5\u7a0b\u3002\u4e0d\u8bf4\u8981\u8fd9\u6c5f\u5824\u80cc\u9762\u7684\u4e09\u5341\u4e07\u65b0\u829c\u5e02\u6c11\uff0c\u4e00\u65e6\u51b3\u5824\uff0c\u6c5f\u4e0a\u5143\u3001\u5218\u7235\u77e5\u9053\u81ea\u5df1\u9876\u4e0a\u7684\u4e4c\u7eb1\u5e3d\u7edd\u5bf9\u662f\u4fdd\u4e0d\u4f4f\uff0c\u8fd8\u4e0d\u77e5\u9053\u4eba\u5230\u665a\u5e74\u4f1a\u4e0d\u4f1a\u843d\u4e2a\u7262\u72f1\u4e4b\u707e\u3002</p><p>\u3000\u3000\u6c5f\u4e0a\u5143\u4e0e\u5218\u7235\u7b80\u5355\u5408\u8ba1\u4e86\u4e00\u4e0b\uff0c\u6709\u4e86\u51b3\u5b9a\uff0c\u624d\u5c06\u526f\u4e66\u8bb0\u3001\u5e38\u52a1\u526f\u5e02\u957f\u7f57\u5f52\u6e90\u3001\u7eaa\u59d4\u4e66\u8bb0\u5c1a\u798f\u6625\u3001\u5e02\u59d4\u79d8\u4e66\u957f\u94b1\u6587\u8d35\u7b49\u4eba\u53ec\u96c6\u8fc7\u6765\u3002</p><p>\u3000\u3000\u6ca1\u6709\u8981\u8ddf\u4ed6\u4eec\u5546\u91cf\u7684\u610f\u601d\uff0c\u6c5f\u4e0a\u5143\u76f4\u63a5\u5c06\u51b3\u5b9a\u8bf4\u51fa\u6765\uff1a\u201c\u5c31\u4eca\u5929\u7684\u521d\u6b65\u53d6\u6837\u8c03\u67e5\u6765\u770b\uff0c\u57ce\u9632\u6c5f\u5824\u7684\u95ee\u9898\u975e\u5e38\u4e25\u91cd\uff0c\u5df2\u7ecf\u4e25\u91cd\u4ee4\u4eba\u53d1\u6307\u7684\u5730\u6b65\uff0c\u8fd9\u6b21\uff0c\u5e02\u59d4\u7edd\u4e0d\u4f1a\u5fc3\u6148\u624b\u8f6f\u3002\u6211\u5efa\u8bae\u7acb\u5373\u7531\u5e02\u516c\u5b89\u5c40\u4e0e\u6b66\u8b66\u652f\u961f\u7684\u540c\u5fd7\u8054\u5408\u5bf9\u76f8\u5173\u8d23\u4efb\u4eba\u91c7\u53d6\u5f3a\u5236\u63aa\u65bd\uff0c\u7136\u540e\u8bf7\u6c42\u7701\u6c34\u5229\u5385\u6d3e\u51fa\u4e13\u5bb6\u534f\u52a9\u6211\u4eec\u4e00\u8d77\u8c03\u67e5\uff0c\u4e00\u5b9a\u8981\u5c06\u80cc\u540e\u7684\u95ee\u9898\u67e5\u6e05\u695a\u624d\u4f1a\u653e\u624b\u3002\u9274\u4e8e\u5c0f\u6c5f\u6c5b\u671f\u5c06\u81f3\uff0c\u539f\u5148\u5236\u5b9a\u7684\u9632\u6c5b\u8ba1\u5212\u5df2\u7ecf\u4e0d\u8db3\u7528\uff0c\u4ece\u73b0\u5728\u8d77\uff0c\u6211\u6c5f\u4e0a\u5143\u4eb2\u81ea\u62c5\u4efb\u9632\u6c5b\u6297\u6d2a\u6307\u6325\u90e8\u603b\u6307\u6325\uff0c\u5218\u5e02\u957f\u62c5\u4efb\u526f\u603b\u6307\u6325\uff0c\u539f\u6765\u62c5\u4efb\u603b\u6307\u6325\u7684\u7f57\u526f\u5e02\u957f\uff0c\u6211\u770b\u5c31\u8d1f\u8d23\u6307\u6325\u90e8\u5bf9\u5916\u754c\u7684\u5ba3\u4f20\u5de5\u4f5c\uff0c\u53e6\u5916\u5e02\u653f\u5e9c\u7684\u5206\u7ba1\u5de5\u4f5c\u4e5f\u8981\u8c03\u6574\u4e00\u4e0b\uff0c\u7f57\u526f\u5e02\u957f\u5206\u7ba1\u7684\u90a3\u4e00\u5757\uff0c\u6682\u65f6\u7531\u5176\u4ed6\u526f\u5e02\u957f\u5206\u5f53\uff0c\u7f57\u526f\u5e02\u957f\u4e13\u5fc3\u505a\u597d\u9632\u6c5b\u6307\u6325\u90e8\u5bf9\u5916\u5ba3\u4f20\u5de5\u4f5c\u3002\u57ce\u9632\u6c5f\u5824\u53d1\u751f\u8fd9\u4e48\u5927\u7684\u4e8b\u60c5\uff0c\u6c34\u5229\u5c40\u5c40\u957f\u8096\u5927\u4f1f\u6709\u9003\u4e0d\u8131\u7684\u8d23\u4efb\uff0c\u6211\u63d0\u8bae\u5e02\u59d4\u64a4\u9500\u8096\u5927\u4f1f\u7684\u515a\u5185\u4e00\u5207\u804c\u52a1\uff0c\u5e76\u63d0\u8bf7\u5e02\u4eba\u5927\u5e38\u59d4\u4f1a\u64a4\u6d88\u8096\u5927\u4f1f\u6c34\u5229\u5c40\u5c40\u957f\u3001\u5e02\u957f\u52a9\u7406\u7b49\u804c\u52a1\u2026\u2026\u6211\u7684\u610f\u89c1\u5c31\u662f\u8fd9\u6837\uff0c\u6709\u6ca1\u6709\u4e0d\u540c\u7684\u610f\u89c1\u6216\u8005\u8bf4\u662f\u66f4\u597d\u7684\u610f\u89c1\u3002\u201d\u4e4b\u524d\u8fd8\u5728\u60f3\u5f03\u8f66\u4fdd\u5e05\uff0c\u8fd9\u65f6\u770b\u5230\u95ee\u9898\u7adf\u662f\u5982\u6b64\u7684\u4e25\u91cd\uff0c\u6c5f\u4e0a\u5143\u3001\u5218\u7235\u770b\u7740\u7f57\u5f52\u6e90\uff0c\u6709\u5c06\u4ed6\u751f\u541e\u6d3b\u5265\u7684\u5fc3\u3002\u5218\u7235\u8865\u5145\u8bf4\u9053\uff1a\u201c\u4e8c\u5341\u4e09\u65e5\u5e02\u653f\u5e9c\u62db\u5f85\u5bbe\u9986\u53d1\u751f\u7684\u5f3a\u5236\u7325\u4eb5\u6848\u4ef6\uff0c\u4e5f\u8bf7\u5e02\u516c\u5b89\u5c40\u7acb\u5373\u5bf9\u6240\u6709\u5f53\u4e8b\u4eba\u91c7\u53d6\u5f3a\u5236\u63aa\u65bd\uff0c\u5e02\u516c\u5b89\u5c40\u5b5f\u6d69\u5df2\u7ecf\u4e0d\u9002\u5408\u5728\u5f53\u524d\u7684\u804c\u4f4d\u4e0a\u7ee7\u7eed\u8d1f\u9886\u5bfc\u8d23\u4efb\uff0c\u5efa\u8bae\u4ed6\u6682\u65f6\u5230\u9632\u6c5b\u6307\u6325\u90e8\u534f\u52a9\u5de5\u4f5c\u2026\u2026\u201d</p><p>\u3000\u3000\u5218\u7235\u8fd9\u4e2a\u8865\u5145\u65e0\u7591\u662f\u5bf9\u7f57\u5f52\u6e90\u81f4\u547d\u7684\u4e00\u51fb\uff0c\u4efb\u4f55\u4eba\u90fd\u80fd\u660e\u767d\u5218\u7235\u8fd9\u662f\u6700\u5f7b\u5e95\u7684\u843d\u4e95\u4e0b\u77f3\uff0c\u6c5f\u4e0a\u5143\u53ea\u662f\u524a\u7f57\u5f52\u6e90\u7684\u6743\uff0c\u77db\u5934\u6682\u65f6\u8fd8\u53ea\u9488\u5bf9\u5e02\u6c34\u5229\u7cfb\u7edf\uff0c\u5218\u7235\u5374\u6839\u672c\u4e0d\u5bb9\u7f57\u5f52\u6e90\u4ece\u5bb9\u517b\u8001\u3002</p><p>\u3000\u3000\u7f57\u5f52\u6e90\u8138\u8272\u60e8\u767d\uff0c\u6123\u662f\u6ca1\u6709\u6562\u53d1\u8868\u53cd\u5bf9\u610f\u89c1\u3002</p><p>\u3000\u3000\u8fd9\u8fb9\u8bae\u5b9a\uff0c\u6c5f\u4e0a\u5143\u4e0e\u5218\u7235\u8dd1\u8fc7\u53bb\u5c06\u5e02\u59d4\u51b3\u5b9a\u544a\u8bc9\u5f90\u5b66\u5e73\uff0c\u95ee\u4ed6\u7684\u610f\u89c1\u3002</p><p>\u3000\u3000\u5f90\u5b66\u5e73\u8bf4\u9053\uff1a\u201c\u6211\u5c0a\u91cd\u65b0\u829c\u5e02\u59d4\u7684\u51b3\u5b9a\uff0c\u6211\u4f1a\u56de\u53bb\u8ddf\u9676\u664b\u4e66\u8bb0\u6c47\u62a5\uff0c\u6211\u4e5f\u4f1a\u5728\u7701\u91cc\u770b\u7740\u4f60\u4eec\u3002\u201d</p><p>\u3000\u3000\u6c5f\u4e0a\u5143\u8bf4\u9053\uff1a\u201c\u7b49\u6c5b\u671f\u8fc7\u53bb\uff0c\u65b0\u829c\u5e02\u59d4\u4f1a\u7acb\u5373\u7ec4\u7ec7\u4e13\u5bb6\u7814\u7a76\u51fa\u4e00\u4e2a\u6574\u56fa\u65b9\u6848\uff0c\u786e\u4fdd\u57ce\u9632\u6c5f\u5824\u660e\u5e74\u6c5b\u671f\u4e4b\u524d\u6210\u4e3a\u65b0\u829c\u57ce\u771f\u6b63\u7684\u62a4\u57ce\u957f\u5824\u2026\u2026\u201d</p><p>\u3000\u3000\u5f90\u5b66\u5e73\u53c8\u6307\u7740\u4e00\u76f4\u5750\u5728\u4ed6\u8eab\u8fb9\u7684\u4e3e\u62a5\u4eba\u5218\u6839\u67f1\uff0c\u8bf4\u9053\uff1a\u201c\u4ed6\u7684\u95ee\u9898\uff0c\u65b0\u829c\u5e02\u59d4\u4e5f\u8981\u8ba4\u771f\u89e3\u51b3\u3002\u201d</p><p>\u3000\u3000\u6c5f\u4e0a\u5143\u63e1\u4f4f\u5218\u6839\u67f1\u7684\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4ee3\u8868\u65b0\u829c\u5e02\u59d4\u5411\u4f60\u9053\u8c26\uff0c\u8bf7\u4f60\u653e\u5fc3\uff0c\u65b0\u829c\u5e02\u59d4\u5bf9\u94f2\u9664\u7f6a\u6076\u7684\u51b3\u5fc3\u4e00\u76f4\u6ca1\u6709\u52a8\u6447\u8fc7\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u6487\u5634\u5750\u5728\u8f66\u53a2\u91cc\uff0c\u6ca1\u6709\u542d\u58f0\uff0c\u53ea\u5f53\u542c\u4e86\u4e00\u53e5\u7b11\u8bdd\uff1b\u5bf9\u5218\u7235\u5efa\u8bae\u7acb\u5373\u5bf9\u7f57\u5f52\u6e90\u7684\u513f\u5b50\u7f57\u6587\u91c7\u53d6\u5f3a\u5236\u63aa\u65bd\uff0c\u5012\u662f\u4e00\u559c\uff0c\u5218\u7235\u5bf9\u7f57\u5f52\u6e90\u7a77\u8ffd\u731b\u6253\uff0c\u80fd\u66ff\u4ed6\u7238\u7238\u5206\u62c5\u4e00\u4e0b\u538b\u529b\u3002</p><p>\u3000\u3000\u5bf9\u4e8e\u5218\u6839\u67f1\u6765\u8bf4\uff0c\u4ed6\u7684\u817f\u5df2\u7ecf\u6b8b\u5e9f\u4e86\uff0c\u81f3\u4e8e\u5f20\u800c\u7acb\u7b49\u4eba\u4f0f\u6cd5\u4ed6\u80fd\u83b7\u5f97\u591a\u5c11\u6c11\u4e8b\u8d54\u507f\u624d\u662f\u91cd\u8981\u7684\u3002</p><p>\u3000\u3000\u6c5f\u5824\u8fd9\u8fb9\u7684\u52a8\u9759\uff0c\u65e9\u5c31\u5438\u5f15\u9644\u8fd1\u7684\u7fa4\u4f17\u805a\u5230\u6c5f\u5824\u4e0b\u56f4\u89c2\uff0c\u90fd\u4e0d\u6653\u5f97\u8fd9\u91cc\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u3002</p><p>\u3000\u3000\u4e0b\u5348\u4e94\u70b9\u949f\uff0c\u5e02\u516c\u5b89\u5c40\u4e0e\u6b66\u8b66\u652f\u961f\u8054\u5408\u884c\u52a8\u8fc5\u901f\u63a7\u5236\u5f20\u800c\u7acb\u7b49\u9f99\u534e\u96c6\u56e2\u76f8\u5173\u8d1f\u8d23\u4eba\u4ee5\u53ca\u4e8c\u5341\u4e09\u65e5\u5e02\u653f\u5e9c\u62db\u5f85\u5bbe\u9986\u5f3a\u5236\u7325\u4eb5\u6848\u5f53\u4e8b\u4eba\u7f57\u6587\u3001\u5b5f\u660e\u7b49\u4eba\uff0c\u6c5f\u4e0a\u5143\u624d\u6709\u52c7\u6c14\u8fc7\u6765\u8bf7\u5f90\u5b66\u5e73\u4e0b\u6c5f\u5824\u4f11\u606f\uff0c\u5927\u5bb6\u5230\u73b0\u5728\u90fd\u6ca1\u6709\u5403\u4e2d\u996d\uff0c\u9965\u80a0\u8f98\u8f98\u597d\u591a\u5c0f\u65f6\u4e86\u3002</p><p>\u3000\u3000\u201c\u4e0d\u4e86\uff0c\u201d\u5f90\u5b66\u5e73\u8bf4\u9053\uff0c\u201c\u6211\u8fd8\u8981\u56de\u7701\u91cc\u53bb\u2026\u2026\u201d</p><p>\u3000\u3000\u5f90\u5b66\u5e73\u7684\u786c\u813e\u6c14\uff0c\u6c5f\u4e0a\u5143\u5012\u662f\u542c\u8bf4\u8fc7\uff0c\u518d\u8bf4\u4eca\u5929\u53d1\u751f\u8fd9\u6837\u7684\u4e8b\u60c5\uff0c\u4e5f\u6ca1\u6709\u80c6\u91cf\u5728\u65b0\u829c\u62db\u5f85\u5f90\u5b66\u5e73\u3002</p><p>\u3000\u3000\u5f20\u606a\u77e5\u9053\u5f90\u5b66\u5e73\u662f\u6025\u7740\u62ff\u8fd9\u4e8b\u505a\u7a81\u7834\u53e3\uff0c\u63a8\u52a8\u5168\u7701\u6c5f\u9632\u5de5\u7a0b\u6e05\u67e5\u5de5\u4f5c\uff0c\u8fde\u4e00\u5929\u65f6\u95f4\u90fd\u4e0d\u60f3\u803d\u6401\u3002</p><p>\u3000\u3000\u5f20\u606a\u7ed9\u9a6c\u6d77\u9f99\u6253\u7535\u8bdd\uff0c\u8ba9\u4ed6\u51c6\u5907\u4e9b\u5feb\u9910\u5230\u56fd\u9053\u6536\u8d39\u7ad9\u7b49\u4ed6\u4eec\uff0c\u4ed6\u4e0e\u6c5f\u4e0a\u5143\u3001\u5218\u7235\u9001\u5f90\u5b66\u5e73\u7b49\u4eba\u4e00\u76f4\u5230\u56fd\u9053\u6536\u8d39\u7ad9\u3002</p><p>\u3000\u3000\u56de\u5e02\u533a\u65f6\uff0c\u6c5f\u4e0a\u5143\u8ba9\u5f20\u606a\u5750\u4ed6\u7684\u8f66\uff0c\u8fd8\u5c06\u94b1\u6587\u8d35\u64b5\u5230\u522b\u7684\u8f66\u91cc\uff0c\u5bf9\u5f20\u606a\u8bf4\uff1a\u201c\u6709\u60c5\u51b5\u5176\u5b9e\u53ef\u4ee5\u5148\u8ddf\u6211\u77e5\u4f1a\u4e00\u58f0\u7684\uff0c\u8fd9\u4e48\u5927\u7684\u4e8b\u60c5\uff0c\u6211\u600e\u4e48\u4f1a\u4e0d\u652f\u6301\u4f60\u4eec\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e0d\u6653\u5f97\u6c5f\u4e0a\u5143\u770b\u51fa\u4ec0\u4e48\uff0c\u4ed6\u6f14\u7684\u8fd9\u51fa\u4e89\u98ce\u5403\u918b\u7684\u620f\uff0c\u53ea\u662f\u7ed9\u65b0\u829c\u4e2d\u5c42\u5b98\u5458\u770b\u7684\uff0c\u53ef\u6ca1\u6709\u6307\u671b\u80fd\u7792\u8fc7\u6c5f\u4e0a\u5143\uff0c\u5fc3\u91cc\u60f3\uff1a\u8981\u6ca1\u6709\u5f90\u5b66\u5e73\u4eca\u5929\u4eb2\u81ea\u5728\u8fd9\u91cc\u76ef\u7740\uff0c\u8fd9\u6c5f\u5824\u6216\u8bb8\u4f1a\u63a8\u5012\u91cd\u5efa\uff0c\u60f3\u63ea\u51fa\u8d23\u4efb\u4eba\uff0c\u90a3\u7b80\u76f4\u662f\u4f5c\u68a6\uff01</p><p>\u3000\u3000\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u4e00\u65f6\u5fc3\u6025\uff0c\u90fd\u6ca1\u6709\u4ed4\u7ec6\u8003\u8651\uff0c\u7ed9\u5218\u4e66\u8bb0\u60a8\u6dfb\u9ebb\u70e6\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u9ebb\u70e6\u5012\u662f\u672a\u5fc5\uff0c\u603b\u6bd4\u7b49\u8fd9\u6c5f\u5824\u57ae\u4e86\u518d\u53d1\u73b0\u95ee\u9898\u597d\uff0c\u201d\u6c5f\u4e0a\u5143\u8f7b\u8f7b\u4e00\u53f9\uff0c\u201c\u4e8b\u60c5\u662f\u86ee\u6025\u7684\uff0c\u624d\u8fc7\u53bb\u4e09\u5929\u800c\u5df2\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u77e5\u9053\u4ed6\u662f\u6697\u793a\u81ea\u5df1\u5927\u95f9\u5929\u4e91\u9152\u5e97\u4e0e\u7f57\u5f52\u6e90\u7236\u5b50\u6b63\u5f0f\u95f9\u7ffb\u8138\u624d\u8fc7\u53bb\u4e09\u5929\u5c31\u5bf9\u7f57\u5f52\u6e90\u4e88\u4ee5\u81f4\u547d\u4e00\u51fb\u663e\u7136\u662f\u65e9\u6709\u9488\u5bf9\u6027\u7684\u5728\u505a\u51c6\u5907\uff0c\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5e73\u65e5\u6ca1\u6709\u592a\u591a\u7684\u4e8b\u60c5\u505a\uff0c\u5b66\u4e60\u4e5f\u4e0d\u8ba4\u771f\uff0c\u4e1c\u901b\u897f\u901b\uff0c\u53d1\u73b0\u8fd9\u57ce\u9632\u6c5f\u5824\u5bf9\u4e8e\u60f3\u5e73\u5b89\u5f53\u5b98\u7684\u4eba\uff0c\u53ef\u662f\u4e00\u4e2a\u9690\u60a3\uff0c\u6316\u6389\u8fd9\u4e2a\u9690\u60a3\u4e0d\u96be\uff0c\u96be\u7684\u662f\u6316\u5230\u8fd9\u4e2a\u9690\u60a3\u8fd8\u4e0d\u81f3\u4e8e\u5728\u65b0\u829c\u7ad6\u4e00\u7fa4\u654c\u4eba\uff0c\u6211\u7238\u7238\u4ed6\u8fd9\u4eba\u4e5f\u662f\u6709\u591f\u8f9b\u82e6\u7684\u3002\u201d</p><p>\u3000\u3000\u6c5f\u4e0a\u5143\u60f3\u60f3\u81ea\u5df1\u90fd\u5feb\u516d\u5341\u5c81\u7684\u4eba\u4e86\uff0c\u8ddf\u4e00\u4e2a\u4e0d\u5230\u4e8c\u5341\u5c81\u7684\u534a\u5927\u5c11\u5e74\u5728\u8f66\u91cc\u73a9\u5fc3\u673a\uff0c\u591a\u5c11\u6709\u4e9b\u597d\u7b11\uff0c\u4f46\u662f\u5462\uff0c\u4ee5\u5f80\u5bf9\u5f20\u606a\u7684\u4e8b\u60c5\u53ea\u662f\u542c\u4eba\u8f6c\u8ff0\uff0c\u542c\u540e\u867d\u7136\u60ca\u8bb6\uff0c\u5012\u6ca1\u6709\u7279\u522b\u6df1\u7684\u611f\u89e6\uff0c\u5982\u4eca\u770b\u6765\uff0c\u8fd9\u513f\u5b50\u6bd4\u8001\u5b50\u8981\u96be\u7f20\u591a\u4e86\u3002</p><p>\u3000\u3000\u8f66\u8fc7\u57ce\u5173\u9547\uff0c\u542c\u89c1\u524d\u9762\u70ae\u7af9\u58f0\u5927\u4f5c\uff0c\u6c5f\u4e0a\u5143\u60f3\u627e\u8bdd\u9898\u7f13\u89e3\u4e00\u4e0b\u6c14\u6c1b\uff0c\u8bf4\u9053\uff1a\u201c\u4eca\u5929\u4ec0\u4e48\u597d\u65e5\u5b50\uff0c\u8fd9\u70ae\u7af9\u653e\u7684\u53ef\u771f\u4e0d\u5c11\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u4e0d\u77e5\u9053\u4eca\u5929\u7b97\u4ec0\u4e48\u826f\u8fb0\u5409\u65f6\uff0c\u8bf4\u9053\uff1a\u201c\u8981\u8bf4\u662f\u57ce\u9632\u6c5f\u5824\u7684\u4e8b\u60c5\uff0c\u4eca\u5929\u7684\u786e\u7b97\u5f97\u4e0a\u597d\u65e5\u5b50\u3002\u201d</p><p>\u3000\u3000\u8f66\u961f\u7ee7\u7eed\u524d\u884c\uff0c\u70ae\u7af9\u58f0\u65ad\u65ad\u7eed\u7eed\u4e00\u76f4\u6ca1\u6709\u505c\uff0c\u53cd\u800c\u8d8a\u6765\u8d8a\u6fc0\u70c8\u4e86\uff0c\u8857\u4e0a\u7684\u884c\u4eba\u4e5f\u6e10\u6e10\u591a\u4e86\uff0c\u90fd\u770b\u5230\u6c5f\u4e0a\u5143\u7684\u4e00\u53f7\u5c0f\u8f66\uff0c\u671d\u8fd9\u8fb9\u70ed\u70c8\u9f13\u638c\u8d77\u6765\uff0c\u9694\u7740\u8f66\u7a97\uff0c\u80fd\u542c\u89c1\u4eba\u4eec\u5728\u6b22\u547c\u3002</p><p>\u3000\u3000\u524d\u9762\u94b1\u6587\u8d35\u5750\u7684\u8f66\u7f13\u4e0b\u6765\uff0c\u4e0e\u8fd9\u8fb9\u5e76\u6392\uff0c\u9694\u7740\u8f66\u7a97\u8ddf\u6c5f\u4e0a\u5143\u901a\u7535\u8bdd\uff1a\u201c\u6c5f\u4e66\u8bb0\u60a8\u8ddf\u5218\u5e02\u957f\u4e3b\u6301\u5f7b\u67e5\u57ce\u9632\u6c5f\u5824\u7684\u4e8b\u60c5\u4ee5\u53ca\u5f20\u800c\u7acb\u4e0e\u7f57\u6587\u7b49\u4eba\u88ab\u6355\u7684\u6d88\u606f\uff0c\u5df2\u7ecf\u4f20\u904d\u5e02\u533a\u4e86\uff0c\u5230\u5904\u90fd\u8ddf\u8fc7\u8282\u4f3c\u7684\u5728\u5e86\u795d\uff0c\u8fd9\u4e9b\u70ae\u7af9\u90fd\u662f\u5e02\u6c11\u81ea\u53d1\u4e70\u6765\u70b9\u53d1\u7684\uff0c\u6c5f\u4e66\u8bb0\uff0c\u4f60\u662f\u4e0d\u662f\u4e0b\u8f66\u8ddf\u7fa4\u4f17\u8bf4\u51e0\u53e5\u8bdd\u3002\u201d</p><p>\u3000\u3000\u6c5f\u4e0a\u5143\u56de\u5934\u770b\u4e86\u770b\u5f20\u606a\uff0c\u4ed6\u8138\u4e0a\u5012\u6ca1\u6709\u7279\u522b\u7684\u8868\u60c5\uff0c\u56de\u5934\u671d\u94b1\u6587\u8d35\u6325\u4e86\u6325\u624b\uff1a\u201c\u7b97\u4e86\uff0c\u6848\u5b50\u8fd8\u6ca1\u6709\u5f00\u59cb\u67e5\u5462\uff0c\u6ca1\u6709\u8138\u7ed9\u65b0\u829c\u5e02\u6c11\u4ec0\u4e48\u4ea4\u5f85\u3002\u201d\u8981\u4e0d\u662f\u5f20\u606a\u5728\u8f66\u91cc\uff0c\u4ed6\u6216\u8bb8\u4f1a\u4e0b\u8f66\uff0c\u4f46\u662f\u60f3\u5230\u81ea\u5df1\u5c42\u5c42\u5305\u88f9\u7740\u7684\u5185\u5fc3\u6b32\u671b\u4f1a\u7ed9\u4e00\u4e2a\u534a\u5927\u5c11\u5e74\u770b\u900f\uff0c\u4ed6\u5bf9\u8fd9\u79cd\u653f\u6cbb\u79c0\u5c31\u6ca1\u6709\u4e1d\u6beb\u7684\u6b32\u671b\u4e86\u3002</p><p>\u3000\u3000\u8857\u4e0a\u6709\u6b22\u547c\u201c\u6c5f\u9752\u5929\u201d\uff0c\u4e5f\u6709\u6b22\u547c\u201c\u5218\u9752\u5929\u201d\u7684\uff0c\u6c5f\u4e0a\u5143\u81ea\u5632\u7684\u8bf4\uff1a\u201c\u73b0\u5728\u554a\uff0c\u9752\u5929\u53ef\u505a\u4e0d\u5f97\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u77e5\u9053\u6c5f\u4e0a\u5143\u4e00\u8bed\u53cc\u5173\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u2026\u2026\u665a\u6765\u517b\u8001\uff0c\u672a\u5fc5\u4e0d\u662f\u4e00\u79cd\u6170\u7c4d\u2026\u2026\u201d</p><p>\u3000\u3000\u6c5f\u4e0a\u5143\u8f7b\u8f7b\u5480\u56bc\u5f20\u606a\u7684\u8bdd\uff1a\u665a\u6765\u517b\u6765\uff0c\u672a\u5fc5\u4e0d\u662f\u4e00\u79cd\u6170\u85c9\u3002\u5728\u5b98\u573a\u53ae\u6df7\u592a\u4e45\u4e86\uff0c\u53ea\u6653\u5f97\u9752\u5929\u505a\u4e0d\u5f97\uff0c\u5374\u5fd8\u4e86\u665a\u5e74\u517b\u8001\uff0c\u672a\u514d\u4e0d\u662f\u4e00\u79cd\u6170\u85c9\u3002\u53d1\u751f\u8fd9\u6863\u5b50\u4e8b\uff0c\u60f3\u8981\u518d\u632a\u51fa\u65b0\u829c\u662f\u96be\u4e0a\u52a0\u96be\uff0c\u4e2d\u592e\u5bf9\u5e72\u90e8\u4efb\u547d\u7684\u5e74\u9650\u89c4\u5b9a\u4e5f\u65e5\u89c1\u4e25\u683c\u4e86\uff0c\u5c06\u65b0\u829c\u5f53\u6210\u81ea\u5df1\u7684\u517b\u8001\u4e4b\u5730\u672a\u5fc5\u4e0d\u597d\uff0c\u65e2\u7136\u90fd\u6709\u51b3\u5fc3\u517b\u8001\u4e86\uff0c\u8fd8\u6015\u524d\u7a0b\u7f81\u7eca\uff1f</p><p>\u3000\u3000\u6c5f\u4e0a\u5143\u5fae\u5fae\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u95ee\u5f20\u606a\uff1a\u201c\u4f60\u7236\u4eb2\u8bf7\u5047\u56de\u6d77\u5dde\u529e\u4e8b\uff0c\u4e8b\u60c5\u5dee\u4e0d\u591a\u529e\u5b8c\u4e86\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4e2a\u6211\u8fd8\u771f\u4e0d\u6e05\u695a\uff0c\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u770b\u5230\u533b\u5927\u9644\u5c5e\u533b\u9662\u7684\u7efc\u5408\u5927\u697c\u5c31\u5728\u524d\u9762\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u5c31\u5728\u533b\u9662\u95e8\u53e3\u4e0b\uff0c\u5c31\u4e0d\u966a\u6c5f\u4e66\u8bb0\u60a8\u4e86\u3002\u201d</p></article>"}}}]);
